** الطبرى
(فتح الباري ٣٥٥/١١)
** إسناده جيد','6','376',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2652,'<span data-type="title" id=toc-2658>٢٦٥٢ </span>- حديث صفوان بن عسال: سمعت رسول الله صلى الله عليه وسلم يقول: " إن بالمغرب بابا مفتوحا للتوبة مسيرة سبعين سنة لا يغلق حتى تطلع الشمس من نحوه ".
** ت ن جه
(فتح الباري ٣٥٥/١١)
** قال الحافظ فى " الفتح " ١١ / ٣٥٥: قال الترمذى: حسن صحيح، وصححه ابن خزيمة وابن حبان.','6','377',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2653,'<span data-type="title" id=toc-2659>٢٦٥٣ </span>- عن عائشة: إذا خرجت أول الآيات طرحت الأقلام وطويت الصحف وخلصت الحفظة وشهدت الأجساد على الأعمال.
** عبد الطبرى
(فتح الباري ٣٥٥/١١)
** إسناده صحيح
** قال الحافظ فى " الفتح " ١١ / ٣٥٥: وهو وإن كان موقوفا فحكمه الرفع.','6','378',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2654,'<span data-type="title" id=toc-2660>٢٦٥٤ </span>- عن ابن عمر قال: قال ملك الموت: يارب إن عبدك إبراهيم جزع من الموت، فقال: قل له الخليل إذا طال به العهد من خليله اشتاق إليه، فبلغه فقال: يارب قد اشتقت إلى لقائك فأعطاه ريحانة فشمها فقبض فيها.
** المبتدأ لأبى حذيفة إسحق بن بشر البخارى
(فتح الباري ٣٦١/١١)
** قال الحافظ فى " الفتح " ١١ / ٣٦١: فى المبتدأ لأبى حذيفة إسحق بن بشر البخارى، أحد الضعفاء بسند له عن ابن عمر.','6','379',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2655,'<span data-type="title" id=toc-2661>٢٦٥٥ </span>- عن عائشة: ثم يقول اللهم أعنى على سكرات الموت.
** ت ن جه
(فتح الباري ٣٦٢/١١)
** إسناده حسن
** قال الحافظ فى " الفتح " ١١ / ٣٦٢: عند أصحاب السنن سوى أبى داود.','6','380',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2656,'<span data-type="title" id=toc-2662>٢٦٥٦ </span>- حديث أبى هريرة فى قصة السؤال فى القبر وفيه: " ثم يفتح له باب من أبواب الجنة فيقال له: هذا مقعدك وما أعد الله لك فيها فيزداد غبطة وسرورا ثم يفتح له باب من أبواب النار فيقال: هذا مقعدك وما أعد الله لك فيها لو عصيته فيزداد غبطة وسرورا.... " الحديث وفيه فى حق الكافر: " ثم يفتح له باب من أبواب النار.. " وفيه: " فيزداد حسرة وثبورا.... " فى الموضعين وفيه " لو أطعته ".
** ابن أبى الدنيا طب
(فتح الباري ٣٦٦/١١)
** قال الحافظ فى " الفتح " ١١ / ٣٦٦: صححه ابن حبان.','6','381',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2657,'<span data-type="title" id=toc-2663>٢٦٥٧ </span>- حديث عبد الله بن عمرو بن العاص قال: جاء أعرابى إلى النبى صلى الله عليه
وسلم فقال ما الصور؟ ، قال: " قرن ينفخ فيه ".
** د ت ن
(فتح الباري ٣٦٨/١١)
** قال الحافظ فى " الفتح " ١١ / ٣٦٨: حسنه الترمذى وصححه ابن حبان والحاكم.','6','382',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2658,'<span data-type="title" id=toc-2664>٢٦٥٨ </span>- حديث أبى سعيد مرفوعا: " كيف أنعم وصاحب الصور قد ألتقم القرن واستمع الأذن حتى يؤمر بالنفخ ".
** ت
(فتح الباري ٣٦٨/١١)
** قال الحافظ فى " الفتح " ١١ / ٣٦٨: حسنه الترمذى.','6','383',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2659,'<span data-type="title" id=toc-2665>٢٦٥٩ </span>- حديث ابن عباس وفيه: جبريل عن يمينه وميكائيل عن يساره وهو صاحب الصور يعنى إسرافيل ب.
** حم هق
(فتح الباري ٣٦٨/١١)
** إسناده فيه مقال
** قال الحافظ فى " الفتح " ١١ / ٣٦٨: لأحمد والبيهقى من حديث ابن عباس وفى أسانيد كل منهما مقال.','6','384',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2660,'<span data-type="title" id=toc-2666>٢٦٦٠ </span>- عن أبى هريرة رفعه: " إن طرف صاحب الصور منذ وكل به مستعد أن ينظر نحو العرش مخافة أن يؤمر قبل أن يرتد إليه طرفه كأن عينيه كوكبان دريان ".
** ك
(فتح الباري ٣٦٨/١١)
** إسناده حسن','6','385',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2661,'<span data-type="title" id=toc-2667>٢٦٦١ </span>- عن عبد الله بن الحارث: كنا عند عائشة فقالت: ياكعب أخبرنى عن إسرافيل فذكر الحديث وفيه: وملك الصور جاث على إحدى ركبتيه وقد نصب الأخرى يلتقم الصور منحنيا ظهره شاخصا ببصره إلى إسرافيل وقد أمر إذا رأى إسرافيل قد ضم جناحيه أن ينفخ فى الصور، فقالت عائشة: سمعته من رسول الله صلى الله
عليه وسلم
** طس
(فتح الباري ٣٦٩/١١)
** قال الحافظ فى " الفتح " ١١ / ٣٦٩: رجاله ثقات إلا على بن زيد بن جدعان ففيه ضعف.','6','386',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2662,'<span data-type="title" id=toc-2668>٢٦٦٢ </span>- عن عبد الرحمن بن أبى عمرة قال: مامن صباح إلا وملكان موكلان بالصور.
** هناد بن السرى فى كتاب الزهد
(فتح الباري ٣٦٩/١١)
** إسناده صحيح موقوفا','6','387',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2663,'<span data-type="title" id=toc-2669>٢٦٦٣ </span>- عن أبى هريرة عن النبى صلى الله عليه وسلم أو عن عبد الله بن عمرو عن النبى
صلى الله عليه وسلم قال: " النافخان فى السماء الثانية رأس أحدهمابالمشرق ورجلاه بالمغرب _ أو قال بالعكس _ ينتظران متى يؤمران أن ينفخا فى الصور فينفخا ".
** حم
(فتح الباري ٣٦٩/١١)
** رجاله ثقات
** قال الحافظ فى " الفتح " ١١ / ٣٦٩: أخرجه الحاكم من حديث عبد الله بن عمر وبغير شك.','6','388',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2664,'<span data-type="title" id=toc-2670>٢٦٦٤ </span>- " ثم ينفخ فى الصور ثلاث نفخات نفخة الفزع ونفخة الصعق ونفخة القيام لرب العالمين ".
** الطبرى
(فتح الباري ٣٦٩/١١)
** إسناده ضعيف مضطرب','6','389',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2665,'<span data-type="title" id=toc-2671>٢٦٦٥ </span>- عن ابن مسعود: ثم يقوم ملك الصور بين السماء والأرض فينفخ فيه والصور قرن فلا يبقى لله خلق فى السماوات ولا فى الأرض إلا مات إلا من شاء ربك ثم يكون بين النفختين ماشاء الله أن يكون.
** هق
(فتح الباري ٣٧٠/١١)
** إسناده قوى','6','390',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2666,'<span data-type="title" id=toc-2672>٢٦٦٦ </span>- حديث أوس بن أوس الثقفى رفعه: " إن أفضل أيامكم يوم الجمعة فيه الصعقة وفيه النفخة.... " الحديث.
** حم د ن
(فتح الباري ٣٧٠/١١)
** قال الحافظ فى " الفتح " ١١ / ٣٧٠: صححه ابن خزيمة وابن حبان والحاكم.','6','391',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2667,'<span data-type="title" id=toc-2673>٢٦٦٧ </span>- من مرسل الحسن: بين النفختين أربعون سنة: الأولى يميت الله بها كل حى والأخرى يحيى الله بها كل ميت.
** مب
(فتح الباري ٣٧٠/١١)
** مرسل','6','392',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2668,'<span data-type="title" id=toc-2674>٢٦٦٨ </span>- من طريق أبى الزعراء: كنا عند عبد الله بن مسعود فذكر الدجال إلى أن قال: ثم يكون بين النفختين ماشاء الله أن يكون فليس فى بنى آدم خلق إلا فى الأرض منه شىء، قال: فيرسل الله ماء من تحت العرش فتنبت جسمانهم ولحمانهم من ذلك الماء كما تنبت الأرض من الرى.
** هق
(فتح الباري ٣٧٠/١١)
** رجاله ثقات موقوفا
** قال الحافظ فى " الفتح " ١١ / ٣٧٠: رواته ثقات إلا أنه موقوف.','6','393',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2669,'<span data-type="title" id=toc-2675>٢٦٦٩ </span>- حديث أبى هريرة عن النبى صلى الله عليه وسلم أنه سأل جبريل عن هذه الآية من الذين لم يشأ الله أن يصعقوا؟ ، قال: هم شهداء الله عز وجل.
** هق
(فتح الباري ٣٧١/١١)
** رجاله ثقات
** قال الحافظ فى " الفتح " ١١ / ٣٧١: صححه الحاكم ورواته ثقات ورجحه الطبرى.','6','394',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2670,'<span data-type="title" id=toc-2676>٢٦٧٠ </span>- " حديث أنس: فكان ممن استثنى الله ثلاثة جبريل وميكائيل وملك الموت ... " الحديث.
** هق ابن مردويه
(فتح الباري ٣٧١/١١)
** إسناده ضعيف','6','395',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2671,'<span data-type="title" id=toc-2677>٢٦٧١ </span>- حديث لقيط بن عامر مطولا وفيه: " يلبثون مالبثتم ثم تبعث الصائحة فلعمر إلهك ماتدع على ظهرها من أحد إلا مات حتى الملائكة الذين مع ربك ".
** عم
(فتح الباري ٣٧١/١١)','6','396',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2672,'<span data-type="title" id=toc-2678>٢٦٧٢ </span>- عن كعب الأحبار: إن الله تعالى يقول لأهل الجنة إذا دخلوها: إن لكل ضيف جزورا وإنى أجزركم اليوم حوتا وثورا فيجزر لأهل الجنة.... الحديث.
** مب
(فتح الباري ٣٧٥/١١)
** إسناده حسن','6','397',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2673,'<span data-type="title" id=toc-2679>٢٦٧٣ </span>- عن عبد الله بن مسعود فى قوله تعالى: (يوم تبدل الأرض غير الأرض) الآية قال: تبدل الأرض أرضا كأنها فضة لم يسفك فيها دم حرام ولم يعمل عليها خطيئة
** عب عبد الطبرى هب
(فتح الباري ٣٧٥/١١)
** رجاله رجال الصحيح','6','398',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2674,'<span data-type="title" id=toc-2680>٢٦٧٤ </span>- عن ابن مسعود: أرض بيضاء كأنها سبيكة فضة.
** الطبرى ك
(فتح الباري ٣٧٥/١١)
** رجاله موثقون','6','399',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2675,'<span data-type="title" id=toc-2681>٢٦٧٥ </span>- حديث معاوية بن حيدة جد بهز بن حكيم رفعه: " إنكم محشورون ونحا بيده نحو الشام رجالا وركبانا وتجرون على وجوهكم ".
** ت ن
(فتح الباري ٣٨٠/١١)
** إسناده قوى','6','400',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2676,'<span data-type="title" id=toc-2682>٢٦٧٦ </span>- حديث: " ستكون هجرة بعد هجرة وتنحاز الناس إلى مهاجر إبراهيم ولا يبقى فى الأرض إلا شرارها تلفظهم أرضوهم وتحشرهم النار مع القردة والخنازير تبيت معهم إذا باتوا وتقيل معهم إذا قالوا ".
** حم
(فتح الباري ٣٨٠/١١)
** إسناده لابأس به','6','401',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2677,'<span data-type="title" id=toc-2683>٢٦٧٧ </span>- حديث أبى سعيد: إنه لما حضره الموت دعا بثياب جدد فلبسها وقال: سمعت النبى صلى الله عليه وسلم يقول: " إن الميت يبعث فى ثيابه التى يموت فيها".
** د
(فتح الباري ٣٨٣/١١)
** قال الحافظ فى " الفتح " ١١ / ٣٨٣: صححه ابن حبان.','6','402',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2678,'<span data-type="title" id=toc-2684>٢٦٧٨ </span>- عن عمرو بن الأسود قال: رفنا أم معاذ بن جبل فأمر بها فكفنت فى ثياب جدد وقال: " أحسنوا أكفان موتاكم فإنهم يحشرون فيها ".
** ابن أبى الدنيا
(فتح الباري ٣ث٨٣/١١)
** إسناده حسن','6','403',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2679,'<span data-type="title" id=toc-2685>٢٦٧٩ </span>- من مرسل عبيد بن عمير: " يحشر الناس حفاة عراة فيقول الله تعالى: ألا أرى خليلى عريانا فيكسى إبراهيم ثوبا أبيض فهو أول من يكسى ".
** جعفر الفريابى
(فتح الباري ٣٨٤/١١)
** مرسل','6','404',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2680,'<span data-type="title" id=toc-2686>٢٦٨٠ </span>- حديث أبى بكرة رفعه: " ليردن على الحوضى رجال ممن صحبنى ورأنى ".
** حم طب
(فتح الباري ٣٨٥/١١)
** إسناده حسن','6','405',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2681,'<span data-type="title" id=toc-2687>٢٦٨١ </span>- من حديث أبى الدرداء: فقلت يارسول الله: أدع الله أن لا يجعلنى منهم، قال: " لست منهم ".
** طب
(فتح الباري ٣٨٥/١١)
** إسناده حسن','6','406',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2682,'<span data-type="title" id=toc-2688>٢٦٨٢ </span>- عن أبى سعيد: سمعت رسول الله صلى الله عليه وسلم فذكر حديثا فقال: " يا أيها الناس إنى فرطكم على الحوض فإذا جئتم قال رجل: يارسول الله أنا فلان بن فلان، وقال آخر: أنا فلان بن فلان، فأقول: أما النسب فقد عرفته ولعلكم أحدثتم بعدى ما أرتددتم به ".
** يع
(فتح الباري ٣٨٦/١١)
** إسناده حسن','6','407',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2683,'<span data-type="title" id=toc-2689>٢٦٨٣ </span>- حديث بريدة رفعه: " أهل الجنة عشرون ومائة صف، أمتى منها ثمانون صفا ".
** حم ت
(فتح الباري ٣٨٨/١١)
** قال الحافظ فى " الفتح " ١١ / ٣٨٨: صححه الترمذى.','6','408',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2684,'<span data-type="title" id=toc-2690>٢٦٨٤ </span>- من مرسل الحسن قال: " يقول الله لآدم: يا آدم أنت اليوم عدل بينى وبين ذريتك قم فانظر مايرفع إليك من أعمالهم ".
** ابن أبى الدنيا
(فتح الباري ٣٨٩/١١)
** مرسل','6','409',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2685,'<span data-type="title" id=toc-2691>٢٦٨٥ </span>- عن عمران بن حصين قال: كنا مع النبى صلى الله عليه وسلم فى سفر فرفع صوته بهاتين الآيتين: (يا أيها الناس اتقوا ربكم إن زلزلة الساعة شىء عظيم _ إلى _ شديد) فحث أصحابه المطى فقال: " هل تدرون أيى يوم ذاك "، قالوا: الله ورسوله أعلم، قال: " ذاك يوم ينادى الله آدم ".
** ت ك
(فتح الباري ٣٨٩/١١)
** قال الحافظ فى " الفتح " ١١ / ٣٨٩: صححه الترمذى والحاكم.','6','410',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2686,'<span data-type="title" id=toc-2692>٢٦٨٦ </span>- عن ابن عباس قال: الأسباب الأرحام.
** الطبرى
(فتح الباري ٣٩٣/١١)
** منقطع','6','411',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2687,'<span data-type="title" id=toc-2693>٢٦٨٧ </span>- عن أبى هريرة رضى الله عنه عن النبى صلى الله عليه وسلم قال: " يوم يقوم الناس لرب العالمين " قال: " مقدار نصف يوم من خمسين ألف سنة فيهون ذلك على المؤمن كتدلى الشمس إلى أن تغرب ".
** يع
(فتح الباري ٣٩٤/١١)
** قال الحافظ فى " الفتح " ١١ / ٣٩٤: صححه ابن حبان.','6','412',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2688,'<span data-type="title" id=toc-2694>٢٦٨٨ </span>- عن عبد الله بن عمرو بن العاص قال: يشتد كرب ذلك اليوم حتى يلجم الكافر العرق، قيل له: فأين المؤمنون؟ ، قال: على الكراس من ذهب ويظلل عليهم الغمام.
** هق فى البعث
(فتح الباري ٣٩٤/١١)
** إسناده حسن','6','413',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2689,'<span data-type="title" id=toc-2695>٢٦٨٩ </span>- عن أبى موسى قال: الشمس فوق رؤس الناس يوم القيامة وأعمالهم تظلهم.
** هق فى البعث
(فتح الباري ٣٩٤/١١)
** إسناده قوى','6','414',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2690,'<span data-type="title" id=toc-2696>٢٦٩٠ </span>- عن سلمان قال: تعطى الشمس يوم القيامة حر عشر سنين ثم تدنى من جماجم الناس حتى تكون قاب قوسين فيعرقون حتى يرشح العرق فى الأرض قامة ثم ترتفع حتى يغرغر الرجل.
** مب شب
(فتح الباري ٣٩٤/١١)
** إسناده جيد','6','415',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2691,'<span data-type="title" id=toc-2697>٢٦٩١ </span>- إن الرجل ليلجمه العرق يوم القيامة حتى يقول: يارب أرحنى ولو إلى النار.
** يع
(فتح الباري ٣٩٤/١١)
** قال الحافظ فى " الفتح " ١١ / ٣٩٤: صححها ابن حبان.','6','416',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2692,'<span data-type="title" id=toc-2698>٢٦٩٢ </span>- مرسل الحسن قال: بلغنى بأن رسول الله صلى الله عليه وسلم قال: " يحبس أهل الجنة بعد ما يجوزون الصراط، حتى يؤخذ لبعضهم من بعض ظلاماتهم فى الدنيا ويدخلون الجنة وليس فى قلوب بعضهم على بعض غل ".
** ابن أبى حاتم
(فتح الباري ٣٩٩/١١)
** إسناده صحيح مرسل','6','417',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2693,'<span data-type="title" id=toc-2699>٢٦٩٣ </span>- عن أنس رفعه: " من حوسب عذب ".
** ت
(فتح الباري ٤٠٢/١١)
** قال الحافظ فى " الفتح " ١١ / ٤٠٢: قال التر مذى: غريب أ. هـ. قلت: والراوى له عن همام على بن أبى بكر صدوق ربما أخطأ.','6','418',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2694,'<span data-type="title" id=toc-2700>٢٦٩٤ </span>- عن أبى هريرة رفعه: " تعرض الناس يوم القيامة ثلاث عرضات فأما عرضتان فجدال ومعاذير وعند ذلك تطير الصحف فى الأيدى فآخذ بيمينه وشماله ".
** ت جه حم
(فتح الباري ٤٠٣/١١)
** قال الحافظ فى " الفتح " ١١ / ٤٠٣: قال الترمذى: لا يصح لأن الحسن لم يسمع من أبى هريرة وقد رواه بعضهم عن على بن على الدفاعى عن الحسن عن أبى موسى أ. هـ. قال الحافظ فى " الفتح " ١١ / ٤٠٣: وهو عند ابن ماجه وأحمد من هذا الوجه مرفوعا وأخرجه البيهقى فى البعث بسند حسن عن عبد الله بن مسعود موقوفا.','6','419',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2695,'<span data-type="title" id=toc-2701>٢٦٩٥ </span>- من مرسل عبد الله بن باباه رفعه: " كأنى أراكم بالكوم جىء من دون جهنم ".
** هق فى البعث
(فتح الباري ٤٠٥/١١)
** إسناده رجاله ثقات مرسلا','6','420',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2696,'<span data-type="title" id=toc-2702>٢٦٩٦ </span>- أكر بن الحديث عند رسول الله صلى الله عليه وسلم ثم عدنا إليه فقال: " عرضت على الأنبياء الليلة بأممها فجعل النبى يحر ومعه الثلاثة والنبى يمر ومعه العصابة..... " فذكر الحديث.
** حم بز
(فتح الباري ٤٠٧/١١)
** إسناده صحيح','6','421',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2697,'<span data-type="title" id=toc-2703>٢٦٩٧ </span>- حديث رفاعة الجهنى قال: أقبلنا مع رسول الله صلى الله عليه وسلم.. فذكر حديثا وفيه: " وعدنى ربى أن يدخل الجنة من أمتى سبعين ألفا بغير حساب وإنى لأرجو أن لا يدخلوها حتى تبوؤا أنتم ومن صلح من أزواجكم وذرياتكم مساكن فى الجنة ".
** حم
(فتح الباري ٤٠٩/١١)
** قال الحافظ فى " الفتح " ١١ / ٤٠٩: صححه ابن خزيمة وابن حبان.','6','422',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2698,'<span data-type="title" id=toc-2704>٢٦٩٨ </span>- حديث أبى أمامة رفعه: " وعدنى ربى أن يدخل الجنة من أمتى سبعين ألفا مع كل ألف سبعين ألأفا لا حساب عليهم ولا عذاب، وثلاث حثيات من حثيات ربى ".
** ت طب حب
(فتح الباري ٤١٠/١١)
** قال الحافظ فى " الفتح " ١١ / ٤١٠: حسنه الترمذى.','6','423',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2699,'<span data-type="title" id=toc-2705>٢٦٩٩ </span>- حديث عتبة بن عبد: ثم يشفع كل ألف فى سبعين ألف ثم يحثى ربى ثلاث حثيات بكفيه، وفيه: فكبر عمر فقال النبى صلى الله عليه وسلم: " إن السبعين ألفا يشفعهم الله فى آبائهم وأمهاتهم وعشائرهم وإنى لأرجو أن يكون أدنى أمتى الحثيات ".
** حب طب
(فتح الباري ٤١٠/١١)
** إسناده جيد
** قال الحافظ فى " الفتح " ١١ / ٤١١: أخرجه الحافظ الضياء وقال: لا أعلم له علة أ. هـ. قلت: علته الإختلاف فى سنده.','6','424',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2700,'<span data-type="title" id=toc-2706>٢٧٠٠ </span>- حديث أبى بكر الصديق: أعطانى مع كل واحد من السبعين ألفا سبعين ألفا.
** حم يع
(فتح الباري ٤١١/١١)
** قال الحافظ فى " الفتح " ١١ / ٤١١: فى سنده راويان أحدهما ضعيف الحفظ والآخر لم يسم.','6','425',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2701,'<span data-type="title" id=toc-2707>٢٧٠١ </span>- حديث عائشة: فقدت رسول الله صلى الله عليه وسلم ذات يوم فاتبعته فإذا هو فى مشربة يصلى فرأيت على رأسه ثلاثة أنوار، فلما قضى صلاته قال: " رأيت الأنوار؟ "، قلت: نعم، قال: " إن أتيا أتانى من ربى فبشرنى أن الله يدخل الجنة من أمتى سبعين ألفا بغير حساب ولا عذاب ثم أتانى فبشرنى أن الله يرخل من أمتى مكان كل واحد من السبعين ألفا سبعين ألفا بغير حساب ولا عذاب ثم أتانى فبشرنى أن الله يدخل من أمتى مكان كل واحد من السبعين ألفا المضاعفة سبعين ألفا بغير حساب ولا عذاب، فقلت: يارب لا يبلغ هذا أمتى قال أكملهم لك من الأعراب ممن لا يصوم ولا يصلى ".
** الكلاباذى فى معانى الأخبار
(فتح الباري ٤١١/١١)
** إسناده واه','6','426',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2702,'<span data-type="title" id=toc-2708>٢٧٠٢ </span>- عن أنس رفعه: " إن الله وعدنى أن يدخل الجنة من أمتى أربعمائة ألف "، فقال أبو بكر: زدنا يارسول الله، فقال: " هكذا " _ وجمع كفيه _ فقال: زدنا، فقال: " وهكذا "، فقال عمر: حسبك أن الله إن شاء أدخل خلقه الجنة بكف واحدة، فقال النبى صلى الله عليه وسلم: " صدق عمر ".
** حم
(فتح الباري ٤١١/١١)
** قال الحافظ فى " الفتح " ١١ / ٤١١: سنده جيد لكن أختلف على قتادة فى سنده أختلافا كبيرا.','6','427',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2703,'<span data-type="title" id=toc-2709>٢٧٠٣ </span>- عن مجاهد أن رسول الله صلى الله عليه وسلم لما انصرف من غزاة بنى المصطلق، فساق قصة طويلة وفيها أن النبى صلى الله عليه وسلم قال: " أهل الجنة عشرون ومائة صف، ثمانون صفا منها أمتى وأربعون صفا سائر الأمم، ولى مع هؤلاء سبعون ألفا يدخلون الجنة بغير حساب "، قيل: من هم.... فذكر الحديث وفيه: فقال: " اللهم اجعل عكاشة منهم "، قال: فاستشهد بعد ذلك، ثم قام سعد بن عبادة الأنصارى فقال: يارسول الله ادع الله أن يجعلنى منهم.... الحديث.
** الخطيب فى المبهمات
(فتح الباري ٤١٢/١١)
** ضعيف
** قال الحافظ فى " الفتح " ١١ / ٤١٢: جاء من طريق واهية، أخرجه الخطيب فى المبهمات من طريق أبى حذيفة إسحق بن بشر البخارى أحد الضفعاء من طريقين له عن مجاهد، وهذا مع ضعفه وإرساله يستبعد من جهة جلالة سعد بن عبادة.','6','428',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2704,'<span data-type="title" id=toc-2710>٢٧٠٤ </span>- حديث أبى سعيد: فقام رجل آخر فقال: ادع الله أن يجعلنى منهم وقال فى آخره " سبقك بها عكاشة وصاحبه أما لو قلتم لقلت ولو قلت لوجبت ".
** شب بز يع
(فتح الباري ٤١٢/١١)
** قال الحافظ فى " الفتح " ١١ / ٤١٢: فى سنده عطية وهو ضعيف.','6','429',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2705,'<span data-type="title" id=toc-2711>٢٧٠٥ </span>- عن أبى هريرة قال: ضرس الكافر يوم القيامة أعظم من أحد يعظمون لتمتلىء منهم وليذوقوا العذاب.
** مب
(فتح الباري ٤٢٣/١١)
** إسناده صحيح
** قال الحافظ فى " الفتح " ١١ / ٤٢٣: لم يصرح برفعه لكن له حكم الرفع لأنه لا مجال للرأى فيه.','6','430',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2706,'<span data-type="title" id=toc-2712>٢٧٠٦ </span>- عن أبى هريرة: غلظ جلد الكافر وكثافة جلده اثنان وأربعون ذراعا بذراع الجبار.
** بز
(فتح الباري ٤٢٣/١١)
** إسناده صحيح','6','431',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2707,'<span data-type="title" id=toc-2713>٢٧٠٧ </span>- من مرسل عبيد بن عمير: " كثافة جلده سبعون ذراعا ".
** مب
(فتح الباري ٤٢٣/١١)
** إسناده صحيح مرسلا','6','432',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2708,'<span data-type="title" id=toc-2714>٢٧٠٨ </span>- حديث ابن عمر رفعه: " إن الكافر ليسحب لسانه الفرسخ والفرسخين يتوطؤه الناس ".
** ت
(فتح الباري ٤٢٤/١١)
** إسناده ضعيف','6','433',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2709,'<span data-type="title" id=toc-2715>٢٧٠٩ </span>- عن أنس قال: من كذب بالشفاعة فلا نصيب له فيها.
** ص
(فتح الباري ٤٢٦/١١)
** إسناده صحيح','6','434',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2710,'<span data-type="title" id=toc-2716>٢٧١٠ </span>- حديث ابن مسعود قال: يشفع نبيكم رابع أربعة جبريل ثم إبراهيم ثم موسى أو عيسى ثم نبيكم لا يشفع أحد من أكثر مما يشفع فيه.
** حم ن ك
(فتح الباري ٤٢٧/١١)
** قال الحافظ فى " الفتح " ١١ / ٤٢٧: هذا الحديث لم يصرح برفعه وقد ضعفه البخارى وقال: المشهور قوله صلى الله
عليه وسلم " أنا أول شافع ".','6','435',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2711,'<span data-type="title" id=toc-2717>٢٧١١ </span>- حديث ابن مسعود رفعه: " ما أحسن محسن من مسلم ولا كافر إلا أثابه الله "
قلنا: يارسول الله ما إثابة الكافر؟ ، قال: " المال والولد والصحة وأشباه ذلك "، قلنا: وما أثابته فى الآخرة؟ ، قال: " عذابا دون العذاب "، ثم قرأ: (أدخلوا آل فرعون أشد العذاب) .
** ابن مردويه هق
(فتح الباري ٤٣١/١١)
** إسناده ضعيف','6','436',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2712,'<span data-type="title" id=toc-2718>٢٧١٢ </span>- حديث حذيفة رفعه قال: " يجمع الناس فى صعيد واحد فيقال: يامحمد فأقول: لبيك وسعديك والخير فى يديك والمهدى من هديت وعبدك بين يديك وبك وإليك تباركت وتعاليت سبحانك لا ملجأ ولا منجا منك إلا إليك ". زاد عبد الرزاق: " سبحانك رب البيت فذلك قوله (عسى أن يبعثك ربك مقاما محمودا) .
** ن عب طب
(فتح الباري ٤٣٧/١١)
** قال الحافظ فى " الفتح " ١١ / ٤٣٧: قال ابن منده فى كتاب الإيمان: هذا حديث مجمع على صحة إسناده وثقه رواته.','6','437',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2713,'<span data-type="title" id=toc-2719>٢٧١٣ </span>- عن الكلبى قال: إذا دخل أهل الجنة الجنة وأهل النار النار بقيت زمرة من آخر زمر الجنة إذا خرج المؤمنون من الصراط بأعمالهم فيقول آخر زمرة من زمر النار لهم وقد بلغت النار منهم كل مبلغ، أما نحن فقد أخذنا بما فى قلوبنا من الشك والتكذيب فما نفعكم أنتم توحيدكم؟ ، قال: فيصرخون عند ذلك يدعون ربهم فيسمعهم أهل الجنة فيأتون آدم _ فذكر الحديث فى إتيانهم الأنبياء المذكورين _ قبل واحدا إلى محمد صلى الله عليه وسلم فينطلق فيأتى رب العزة فيسجد له حتى يأمره أن يرفع رأسه ثم يسأله ما تريد، وهو أعلم به، فيقول: رب أناس من عبادك أصحاب ذنوب لم يشركوا بك وأنت أعلم بهم فعيرهم أهل الشرك بعبادتهم إياك فيقول: وعزتى لأخرجنهم فيخرجهم قد احترقوا فينضح عليهم من الماء حتى ينبتوا ثم يدخلون الجنة فيسمون الجهنميين فيغبطه عند ذلك الأولون والآخرون نفذلك قوله (عسى أن يبعثك ربك مقاما محمودا) .
** تفسير يحيى بن سلام البصرى
(فتح الباري ٤٣٩/١١)
** قال الحافظ فى " الفتح " ١١ / ٤٣٩: يحيى بن سلام البصرى نزيل مصر ثم أفريقية _ وهو من طبقة يزيد بن هارون _ قد ضعفه الطارقطنى وقال أبو حاتم الرازى: صدوق، وقال أبو زرعة: ربما يهم وقال ابن عدى: يكتب حديثه مع ضعفه أ. هـ. لكن الكلبى ضعيف.','6','438',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2714,'<span data-type="title" id=toc-2720>٢٧١٤ </span>- حديث أبى سعيد: إن أدنى لولؤة عليها لتضىء مابين المشرق والمغرب.
** حم
(فتح الباري ٤٤٢/١١)
** قال الحافظ فى " الفتح " ١١ / ٤٤٢: صححه ابن حبان.','6','439',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2715,'<span data-type="title" id=toc-2721>٢٧١٥ </span>- عن أبى هريرة: فيفرج له فرجة قبل النار فينظر إليها فيقال له: أنظر إلى ما وقاك الله.
** جه
(فتح الباري ٤٤٢/١١)
** إسناده صحيح','6','440',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2716,'<span data-type="title" id=toc-2722>٢٧١٦ </span>- عن أبى هريرة: مامنكم من أحد إلا وله منزلان: منزل فى الجنة ومنزل فى النار فإذا مات ودخل النار ورث أهل الجنة منزله.
** جه حم
(فتح الباري ٤٤٢/١١)
** إسناده صحيح','6','441',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2717,'<span data-type="title" id=toc-2723>٢٧١٧ </span>- عن أبى هريرة: لقد ظننت أنك أول من يسألنى عن ذلك من أمتى وشفاعتى لمن شهد أن لا إله إلا الله مخلصا يصدق قلبه لسانه ولسانه قلبه.
** حم
(فتح الباري ٤٤٣/١١)
** قال الحافظ فى " الفتح " ١١ / ٤٤٣: صححه ابن حبان.','6','442',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2718,'<span data-type="title" id=toc-2724>٢٧١٨ </span>- حديث أبى سعيد أنه يخفف الوقوف عن المؤمن حتى يكون كصلاة مكتوبة.
** حم
(فتح الباري ٤٤٨/١١)
** إسناده حسن','6','443',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2719,'<span data-type="title" id=toc-2725>٢٧١٩ </span>- من مرسل عبد الله بن شفيق: فيجوز الرجل كالطرف وكالسهم وكالطائر السريع وكالفرس الجواد المضمر ويجوز الرجل يعدو عدوا ويمشى مشيا حتى يكون آخر من ينجو يحبو.
** مب
(فتح الباري ٤٥٣/١١)
** مرسل','6','444',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2720,'<span data-type="title" id=toc-2726>٢٧٢٠ </span>- من مرسل عبيد بن عمير: إن الصراط مثل السيف وبجنبتيه كلاليب، إنه ليؤخذ بالكلوب الواحد أكثر من ربيعة ومضر.
** مب
(فتح الباري ٤٥٤/١١)
** مرسل','6','445',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2721,'<span data-type="title" id=toc-2727>٢٧٢١ </span>- عن الفضيل بن عياض قال: بلغنا أن الصراط مسيرة خمسة عشر ألف سنة، خمسة آلاف صعود وخمسة آلاف هبوط وخمسة آلاف مستوى أدق من الشعرة وأحد من السيف على متن جهنم لا يجوز عليه إلا ضامر مهزول من خشية الله.
** كر
(فتح الباري ٤٥٤/١١)
** معضل','6','446',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2722,'<span data-type="title" id=toc-2728>٢٧٢٢ </span>- عن سعيد بن أبى هلال قال: بلغنا أن الصراط أدق من الشعر على بعض الناس ولبعض الناس مثل الوادى الواسع.
** مب ابن أبى الدنيا
(فتح الباري ٤٥٤/١١)
** مرسل أو معضل','6','447',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2723,'<span data-type="title" id=toc-2729>٢٧٢٣ </span>- من طريق غنيم بن قيس أحد التابعين قال: تمثل النار للناس، ثم يناديها مناد أمسكى أصحابك ودعى أصحابى، فتخسف بكل ولى لها فهى أعلم بهم من الرجل بولده ويخرج المؤمنون ندية ثيابهم.
** الطبرى
(فتح الباري ٤٥٤/١١)
** قال الحافظ فى " الفتح " ١١ / ٤٥٤: رجاله ثقات مع كونه مقطوعا.','6','448',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2724,'<span data-type="title" id=toc-2730>٢٧٢٤ </span>- حديث عبد الله بن عمرو رفعه: " يدخل من أهل القبلة النار من لا يحصى عددهم إلا الله بما عصوا الله واجترؤا على معصيته وخالفوا طاعته، فيؤذن لى فى الشفاعة فأثنى على الله ساجدا كما أثنى عليه قائما، فيقال لى: إرفع رأسك الحديث.
** طب
(فتح الباري ٤٥٥/١١)
** إسناده حسن','6','449',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2725,'<span data-type="title" id=toc-2731>٢٧٢٥ </span>- حديث سمرة رفعه: " إن لكل نبى حوضا ".
** ت
(فتح الباري ٤٦٧/١١)
** قال الحافظ فى " الفتح " ١١ / ٤٦٧: أشار _ الترمذى _ إلاأنه أختلف فى وصله وإرساله وإن المرسل أصح.','6','450',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2726,'<span data-type="title" id=toc-2732>٢٧٢٦ </span>- عن الحسن قال: قال رسول الله صلى الله عليه وسلم: " إن لكل نبى حوضا وهو قائم على حوضه بيده عصا يدعو فى عرف من أمته، إلا أنهم يتباهون أيهم أكثر تبعا وإنى لأرجو أن أكون أكثرهم تبعا ".
** ابن أبى الدنيا
(فتح الباري ٤٦٧/١١)
** إسناده صحيح مرسلا','6','451',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2727,'<span data-type="title" id=toc-2733>٢٧٢٧ </span>- حديث أبى سعيد رفعه: " وكل نبى يدعو أمته ولكل نبى حوض، فمنهم من يأتيه الفئام ومنهم من يأتيه العصبة، ومنهم من يأتيه الواحد ومنهم من يأتيه الإثنان ومنهم من لا يأتيه أحد وإنى لأكثر الأنبياء تبعا يوم القيامة ".
** ابن أبى الدنيا
(فتح الباري ٤٦٧/١١)
** إسناده لين','6','452',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2728,'<span data-type="title" id=toc-2734>٢٧٢٨ </span>- عن ثابت عن أنس: دخلت على ابن زياد وهم يذكرون الحوض فقال: هذا أنس فقلت: لقد كانت عجائز بالمدينة كثيرا ما يسألن ربهن أن يسقيهن من حوض نبيهن.
** يع
(فتح الباري ٤٦٨/١١)
** إسناده صحيح','6','453',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2729,'<span data-type="title" id=toc-2735>٢٧٢٩ </span>- عن أنس: ماحسبت أن أعيش حتى أرى مثلكم ينكر الحوض.
** فوائد العيسوى هق فى البعث
(فتح الباري ٤٦٨/١١)
** إسناده صحيح','6','454',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2730,'<span data-type="title" id=toc-2736>٢٧٣٠ </span>- من طريق يزيد الرقاشى عن أنس فى صفة الحوض: وسيأتيه قوم ذابلة شفاههم لا يطعمون منه قطرة من كذب به اليوم لم يصب الشرب منه يومئذ.
** هق
(فتح الباري ٤٦٨/١١)
** قال الحافظ فى " الفتح " ١١ / ٤٦٨: يزيد ضعيف لكن يقويه مامضى.','6','455',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2731,'<span data-type="title" id=toc-2737>٢٧٣١ </span>- حديث الصنابج بن الأعسر: إنى فرطكم على الحوض، وإنى مكاثر بكم.. الحديث
** حم جه
(فتح الباري ٤٦٨/١١)
** إسناده صحيح','6','456',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2732,'<span data-type="title" id=toc-2738>٢٧٣٢ </span>- عن أبى هريرة مرفوعا فى ذكر الحوض فقال فيه: " عرضه مثل مابينكم وبين جرباء وأذرح ".
** فوائد عبد الكريم بن الهيثم الديرعاقولى
(فتح الباري ٤٧٢/١١)
** إسناده حسن','6','457',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2733,'<span data-type="title" id=toc-2739>٢٧٣٣ </span>- من طريق أبى عبيدة قال: قال عبد الله رفعه: " إن النطفة تكون فى الرحم أربعين يوما على حالها لا تتغير ".
** حم
(فتح الباري ٤٨١/١١)
** إسناده ضعيف منقطع','6','458',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2734,'<span data-type="title" id=toc-2740>٢٧٣٤ </span>- حديث أنس: لا عليكم أن لا تعجبوا بعمل أحد حتى تنظروا بم يختم له، فإن العامل يعمل زمانا من عمره بعمل صالح لومات عليه دخل الجنة ثم يتحول فيعمل عملا سيئا.... الحديث.
** حم
(فتح الباري ٤٨٧/١١)
** قال الحافظ فى " الفتح " ١١ / ٤٨٧: صححه ابن حبان.','6','459',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2735,'<span data-type="title" id=toc-2741>٢٧٣٥ </span>- عن جابر رفعه: " إذا استهل الصبى ورث وصلى عليه ".
** ن
(فتح الباري ٤٨٩/١١)
** قال الحافظ فى " الفتح " ١١ / ٤٨٩: صححه ابن حبان والحاكم وقد ضعفه النووى فى شرح المهذب، والصواب أنه صحيح الإسناد لكن المرجح عند الحفاظ وقفه.','6','460',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2736,'<span data-type="title" id=toc-2742>٢٧٣٦ </span>- عن أبى الدرداء مرفوعا: " إن العبد لا يبلغ حقيقة الإيمان حتى يعلم أن ما أصابه لم يكن ليخطئه وما أخطأه لم يكن ليصيبه ".
** طب
(فتح الباري ٤٩٠/١١)
** إسناده حسن','6','461',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2737,'<span data-type="title" id=toc-2743>٢٧٣٧ </span>- حديث أبى الدرداء: كل امرىء مهيأ لما خلق له.
** حم
(فتح الباري ٤٩٣/١١)
** إسناده حسن','6','462',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2738,'<span data-type="title" id=toc-2744>٢٧٣٨ </span>- عن بشير بن كعب أحد كبار التابعين قال: سأل غلامان رسول الله صلى الله عليه
وسلم فيم العمل فيما جفت به الأقلام وجرت به المقادير أم شىء نستأنفه؟ ، قال: " بل فيما جفت به الأقلام "، قالا: ففيم العمل؟ ، قال: " أعملوا فكل ميسر لما هو عامل "، قالا: فالجد الآن.
** الفريابى
(فتح الباري ٤٩٧/١١)
** إسناده صحيح','6','463',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2739,'<span data-type="title" id=toc-2745>٢٧٣٩ </span>- حديث أنس: " إذا أراد الله بعبد خيرا استعمله "، قيل: كيف يستعمله؟ ، قال: " يوفضه لعمل صالح ثم يقبضه عليه ".
** ت
(فتح الباري ٤٩٩/١١)
** قال الحافظ فى " الفتح " ١١ / ٤٩٩: صححه الترمذى وأخرجه أحمد من هذا الوجه مطولا.','6','464',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2740,'<span data-type="title" id=toc-2746>٢٧٤٠ </span>- من حديث أبى هريرة: إذا قال العبد لا حول ولا قوة إلا بالله فيقول الله أسلم عبدى واستسلم.
** اك
(فتح الباري ٥٠١/١١)
** إسناده قوى','6','465',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2741,'<span data-type="title" id=toc-2747>٢٧٤١ </span>- عن أبى أيوب أن النبى صلى الله عليه وسلم ليلة أسرى به مر على إبراهيم على نبينا وعليه الصلاة والسلام فقال: يامحمد مر أمتك أن يكثروا من غراس الجنة، قال: " وما غراس الجنة؟ "، قال: لا حول ولا قوة إلا بالله.
** حم ت
(فتح الباري ٥٠١/١١)
** قال الحافظ فى " الفتح " ١١ / ٥٠١: صححه ابن حبان.','6','466',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2742,'<span data-type="title" id=toc-2748>٢٧٤٢ </span>- عن ابن عباس مرفوعا: " يحول بين المؤمن وبين الكفر ويحول بين الكافر وبين الهدى ".
** ابن مردويه
(فتح الباري ٥١٤/١١)
** إسناده ضعيف','6','467',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2743,'<span data-type="title" id=toc-2749>٢٧٤٣ </span>- قال عمر: حدثت قوما حديثا فقلت: لا وأبى فقال رجل من خلفى: لا تحلفوا بآبائكم فالتفت فإذا رسول الله صلى الله عليه وسلم يقول: " لو أن أحدكم حلف بالمسيح هلك والمسيح خير من آبائكم ".
** شب
(فتح الباري ٥٣١/١١)
** مرسل
** قال الحافظ فى " الفتح " ١١ / ٥٣١: هذا مرسل يتقوى بشواهده.','6','468',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2744,'<span data-type="title" id=toc-2750>٢٧٤٤ </span>- عن ابن عمر أنه سمع رجلا يقول: لا والكعبة، فقال: لا تحلف بغير الله فإنى سمعت رسول الله صلى الله عليه وسلم يقول: " من حلف بغير الله فقد كفر أو أشرك ".
** ت
(فتح الباري ٥٣١/١١)
** قال الحافظ فى " الفتح " ١١ / ٥٣١: قال الترمذى: حسن وصححه الحاكم.','6','469',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2745,'<span data-type="title" id=toc-2751>٢٧٤٥ </span>- عن ابن عمر: سمع النبى صلى الله عليه وسلم رجلا يحلف بأبيه فقال: " لا تحلفوا بآبائكم من حلف بالله فليصدق ومن حلف له بالله فليرض ومن لم يرض بالله فليس من الله ".
** جه
(فتح الباري ٥٣٥/١١)
** إسناده حسن','6','470',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2746,'<span data-type="title" id=toc-2752>٢٧٤٦ </span>- عن عبد الله بن بريدة عن أبيه رفعه: " من قال إنى برىء من الإسلام فإن كان كاذبا فهو كما قال وإن كان صادقا لم يعد إلى الإسلام سالما ".
** ن
(فتح الباري ٥٣٩/١١)
** قال الحافظ فى " الفتح " ١١ / ٥٣٩: صححه النسائى.','6','471',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2747,'<span data-type="title" id=toc-2753>٢٧٤٧ </span>- عن قتيلة أمرأة من جهينة: أن يهوديا أتى النبى صلى الله عليه وسلم فقال: إنكم تشركون تقولون ماشاء الله وشئت، وتقولون: والكعبة، فأمرهم النبى صلى الله عليه وسلم إذا أرادوا أن يحلفوا أن يقولوا: ورب الكعبة وأن يقولوا ماشاء الله ثم شئت.
** ن فى كتاب الإيمان والنذور
(فتح الباري ٥٤٠/١١)
** قال الحافظ فى " الفتح " ١١ / ٥٤٠: صححه النسائى.','6','472',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2748,'<span data-type="title" id=toc-2754>٢٧٤٨ </span>- حديث رفاعة بن عوانة: كانت يمين رسول الله صلى الله عليه وسلم التى يحلف بها أشهد عند الله والذى نفسى بيده.
** جه
(فتح الباري ٥٤٤/١١)
** قال الحافظ فى " الفتح " ١١ / ٥٤٤: فى سنده ضعيف وهو عبد الملك بن محمد الصنعانى.','6','473',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2749,'<span data-type="title" id=toc-2755>٢٧٤٩ </span>- عن المسعودى عن عوير قال: قال عبد الله: لا تحلفوا بحلف الشيطان أن يقول أحدكم: وعزة الله ولكن قولوا كما قال الله تعالى رب العزة.
** حل
(فتح الباري ٥٤٦/١١)
** قال الحافظ فى " الفتح " ١١ / ٥٤٦: فى المسعودى ضف وعون عن عبد الله منقطع.','6','474',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2750,'<span data-type="title" id=toc-2756>٢٧٥٠ </span>- من طريق الحسن البصرى مرفوعا فى قصة الرماة: وكان أحدهم إذا رمى حلف أنه أصاب فيظهر أنه أخطأ فقال النبى صلى الله عليه وسلم: " إيمان الرماة لغوا لا كفارة لها ولا عقوبة ".
** الطبرى
(فتح الباري ٥٤٧/١١)
** مرسل
** قال الحافظ فى " الفتح " ١١ / ٥٤٧: هذا لا يثبت لأنهم كانوا لا يعتمدون مراسيل الحسن لأنه كان يأخذ عن كل أحد.','6','475',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2751,'<span data-type="title" id=toc-2757>٢٧٥١ </span>- عن عائشة: أن رسول الله صلى الله عليه وسلم قال: " لغو اليمين هو كلام الرجل فى بيته كلا والله وبلى والله ".
** د
(فتح الباري ٥٤٨/١١)
** قال الحافظ فى " الفتح " ١١ / ٥٤٨: أشار أبو داود إلى أنه أختلف على عطاء وعلى إبراهيم فى رفعه ووقفه.','6','476',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2752,'<span data-type="title" id=toc-2758>٢٧٥٢ </span>- عن أبى هريرة أنه سمع رسول الله صلى الله عليه وسلم يقول: " ليس فيها كفارة يمين صبر يقتطع بها مالا بغير حق ".
** ابن شاهين
(فتح الباري ٥٥٧/١١)
** قال الحافظ فى " الفتح " ١١ / ٥٥٧: ظاهر سنده الصحة لكنه معلول لأن فيه عنعنة بقية فقد أخرجه أحمد من هذا الوجه فقال: فى هذا السند عن المتوكل أو أبى المتوكل فظهر أنه ليس هو الناجى الثقة بل آخر مجهول.','6','477',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2753,'<span data-type="title" id=toc-2759>٢٧٥٣ </span>- حديث عمرو بن شعيب عن أبيه عن جده مرفوعا: " لا نذر ولا يمين فيما لا يملك ابن آدم ".
** د ن
(فتح الباري ٥٦٥/١١)
** قال الحافظ فى " الفتح " ١١ / ٥٦٥: رواته لا بأس بهم لكن اختلف فى سنده على عمرو.','6','478',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2754,'<span data-type="title" id=toc-2760>٢٧٥٤ </span>- عن ابن عباس رفعه: " لا يمين فى غضب.... " الحديث.
** طس
(فتح الباري ٥٦٥/١١)
** إسناده ضعيف','6','479',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2755,'<span data-type="title" id=toc-2761>٢٧٥٥ </span>- حديث يوسف بن عبد الله بن سلام: رأيت النبى صلى الله عليه وسلم أخذ كسرة من خبز شعير فوضع عليها تمرة وقال: " هذه أدام هذه ".
** د ت
(فتح الباري ٥٧١/١١)
** إسناده حسن','6','480',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2756,'<span data-type="title" id=toc-2762>٢٧٥٦ </span>- عن ابن مسعود أنه جىء عنده بطعام فتنحى رجل فقال: إنى حرمته أن لا آكله فقال: أذن فكل وكفر عن يمينك ثم تلا هذه الآية إلى قوله (ولا تعتدوا) .
** الثورى فى جامعه
(فتح الباري ٥٧٥/١١)
** إسناده صحيح','6','481',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2757,'<span data-type="title" id=toc-2763>٢٧٥٧ </span>- البخيل من ذكرت عنده فلم يصل على.
** ن
(فتح الباري ٥٧٩/١١)
** قال الحافظ فى " الفتح " ١١ / ٥٧٩: صححه ابن حبان.','6','482',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2758,'<span data-type="title" id=toc-2764>٢٧٥٨ </span>- عن سعيد بن جبير قال مرة عن ابن عباس قال: إذا مات وعليه نذر قضى عنه وليه.
** شب
(فتح الباري ٥٨٤/١١)
** إسناده صحيح','6','483',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2759,'<span data-type="title" id=toc-2765>٢٧٥٩ </span>- عن ابن عباس قال: لا يصلى أحد عن أحد ولا يصوم أحد عن أحد.
** ابن عبد البر
(فتح الباري ٥٨٤/١١)
** قال الحافظ فى " الفتح " ١١ / ٥٨٤: أورده ابن عبد البر من طريقه موقوفا ثم قال: والنقل فى هذا عن ابن عباس مضطرب.','6','484',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2760,'<span data-type="title" id=toc-2766>٢٧٦٠ </span>- سئل ابن عباس عن رجل مات وعليه نذر فقال: يصام عنه النذر.
** شب
(فتح الباري ٥٨٤/١١)
** إسناده صحيح','6','485',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2761,'<span data-type="title" id=toc-2767>٢٧٦١ </span>- حديث عائشة: لا نذر فى معصية وكفارته كفارة يمين.
** د ت ن جه
(فتح الباري ٥٨٧/١١)
** قال الحافظ فى " الفتح " ١١ / ٥٨٧: أخرجه أصحاب السنن ورواته ثقات، لكنه معلول فإن الزهرى رواه عن أبى سلمة ثم بين أنه حمله عن سليمان بن أرقم عن يحيى بن أبى كثير عن أبى سلمة فدلسه بإسقاط اثنين، وحسن الظن بسليمان وهو عند غيره ضعيف باتفاقهم وحكى الترمذى عن البخارى أنه قال: لا يصح.','6','486',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2762,'<span data-type="title" id=toc-2768>٢٧٦٢ </span>- حديث ابن عباس رفعه: " من نذر نذرا لم يسمه فكفارته كفارة يمين " وفيه: " ومن نذر فى معصية فكفارته كفارة يمين ومن نذر نذرا لا يطيقه فكفارته كفارة يمين ".
** د
(فتح الباري ٥٨٧/١١)
** رجاله ثقات
** قال الحافظ فى " الفتح " ١١ / ٥٨٧: رواته ثقات.','6','487',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2763,'<span data-type="title" id=toc-2769>٢٧٦٣ </span>- عن أبى هريرة بينما رسول الله صلى الله عليه وسلم يسير فى جوف الليل إذ أبصر بخيال نفرت منه الإبل فإذا امرأه عريانة نافضة شعرها فقالت: نذرت أن أحج ماشية عريانة نافضة شعرى، فقال: " مرها فلتلبس ثيابها ولتهرق دما ".
** هق
(فتح الباري ٥٨٩/١١)
** إسناده ضعيف','6','488',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2764,'<span data-type="title" id=toc-2770>٢٧٦٤ </span>- عن عمران رفعه: " إذا نذر أحدكم أن يحج ماشيا فليهدر هديا وليركب ".
** هق
(فتح الباري ٥٨٩/١١)
** إسناده منقطع','6','489',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2765,'<span data-type="title" id=toc-2771>٢٧٦٥ </span>- من طريق كريمة بنت سيرين أنها سألت ابن عمر فقالت: جعلت على نفسى أن أصوم كل أربعاء واليوم أربعاء وهو يوم النحر فقال: أمر الله بوفاء النذر ونهى رسول الله عن صوم يوم النحر.
** حب فى الثقات
(فتح الباري ٥٩١/١١)
** رجاله ثقات','6','490',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2766,'<span data-type="title" id=toc-2772>٢٧٦٦ </span>- عن ابن عباس قال: كل شىء فى القرآن أو نحو قوله تعالى: (ففدية فى صيام أو صدقة أو نسك) فهو فيه مخير، وماكان (فمن لم يجد) فهو على الولاء أو على الترتيب.
** سفيان الثورى فى تفسيره
(فتح الباري ٥٩٤/١١)
** قال الحافظ فى " الفتح " ١١ / ٥٩٤: ليث ضعيف.','6','491',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2767,'<span data-type="title" id=toc-2773>٢٧٦٧ </span>- عن الزهرى أخبرنى أبو حسن مولى عبد الله بن الحارث وكان من أهل العلم والصلاح أنه سمع امرأة تقول لعبد الله بن نوفل تستفتيه فى غلام لها ابن زنية تعتقه فى رقبة كانت عليها فقال: لا أراه يجزئك سمعت عمر يقول: لأن أحمل على نعلين فى سبيل الله أحب إلى من أن أعتق ابن زنية.
**
(فتح الباري ٦٠٠/١١)
** إسناده صحيح','6','492',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2768,'<span data-type="title" id=toc-2774>٢٧٦٨ </span>- عن أبى هريرة قال: لأن أتبع بسوط فى سبيل الله أحب إلى من أن أعتق ولد زنية.
** شب
(فتح الباري ٦٠٠/١١)
** صحيح','6','493',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2769,'<span data-type="title" id=toc-2775>٢٧٦٩ </span>- عن ابن عمر أنه أعتق ولد زنا، وعنه: قد أمرنا الله أن نمن على من هو شر منه قال الله تعالى: (فإما منا بعد وإما فداء) .
** شب
(فتح الباري ٦٠١/١١)
** إسناده صحيح','6','494',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2770,'<span data-type="title" id=toc-2776>٢٧٧٠ </span>- عن أبى الخير عن عقبة بن عامر أنه سئل عن ذلك فمنع، قال أبو الخير: فسألنا فضالة بن عبيد فقال: يغفر الله لعقبة وهل هو إلا نسمة من النسم.
** ابن المنذر
(فتح الباري ٦٠١/١١)
** إسناده صحيح','6','495',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2771,'<span data-type="title" id=toc-2777>٢٧٧١ </span>- أن النبى صلى الله عليه وسلم قال: " والله لأغزون قريشا _ ثلاثا ثم سكت ثم قال _ إن شاء الله ".
** د
(فتح الباري ٦٠٣/١١)
** مرسل
** قال الحافظ فى " الفتح " ١١ / ٦٠٣: أخرجه أبو داود موصولا ومرسلا.','6','496',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2772,'<span data-type="title" id=toc-2778>٢٧٧٢ </span>- حديث معاذ رفعه: " إذا قال لامرأته: أنت طالق إن شاء الله لم تطلق وإن قال لعبده: أنت حر إن شاء الله فإنه حر ".
** لم
(فتح الباري ٦٠٤/١١)
** قال الحافظ فى " الفتح " ١١ / ٦٠٤: قال البيهقى: تفرد به حميد بن مالك وهو مجهول، واختلف عليه فى إسناده.','6','497',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2773,'<span data-type="title" id=toc-2779>٢٧٧٣ </span>- عن ابن عمر مرفوعا: " من حلف على يمين فقال: إن شاء الله فلا حنث عليه ".
** د ت ن جه
(فتح الباري ٦٠٥/١١)
** قال الحافظ فى " الفتح " ١١ / ٦٠٥: حسنه الترمذى وصححه الحاكم.','6','498',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2774,'<span data-type="title" id=toc-2780>٢٧٧٤ </span>- عن عكرمة قال: كان اسم عبد الرحمن بن سمرة عبد كلوب فسماه رسول الله صلى الله عليه وسلم عبد الرحمن فمر به وهو يتوضأ فقال: " تعال ياعبد الرحمن لا تطلب الإمارة.... " الحديث.
** طس
(فتح الباري ٦١٦/١١)
** قال الحافظ فى " الفتح " ١١ / ٦١٦: قال الطبرى: لم يروه عن عكرمة إلا عبد الله بن كيسان ولا عنه إلا ابنه إسحق تفرد به أبو الدرداء عبد العزيز بن منيب.أ. هـ. قلت: عبد الله بن كيسان ضعفه أبو حاتم الرازى وابنه إسحق لينه أبو أحمد الحاكم.','6','499',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2775,'<span data-type="title" id=toc-2781>٢٧٧٥ </span>- حديث يحيى بن عبيد الله عن أبيه عن أبى هريرة رفعه: " من حلف فرأى غيرها خيرا منها فليأت الذى هو خير فهو كفارته ".
** لم
(فتح الباري ٦١٧/١١)
** قال الحافظ فى " الفتح " ١١ / ٦١٧: يحيى ضعيف جدا.','6','500',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2776,'<span data-type="title" id=toc-2782>٢٧٧٦ </span>- حديث ابن مسعود رفعه: " تعلموا الفرائض وعلموها الناس فإنى امرؤ مقبوض وإن العلم سيقبض حتى يختلف الإثنان فى الفريضة فلا يجدان من يفصل بينهما ".
** حم ت ن
(فتح الباري ٥/١٢)
** قال الحافظ فى " الفتح " ١٢ / ٥: صححه الحاكم ورواته موثقون إلا أنه أختلف فيه على عوف الأعرابى اختلافا كثيرا، فقال الترمذى: إنه مضطرب والإختلاف عليه أنه جاء عنه من طريق ابن مسعود.','7','1',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2777,'<span data-type="title" id=toc-2783>٢٧٧٧ </span>- من طريق الحمانى عن عبد الرحمن بن أبى بكر عن أبيه رفعه: " تعلموا القرآن والفرائض وعلموها الناس أوشك أن يأتى على الناس زمان يختصم فيه الرجلان فى الفريضة فلا يجدان من يفصل بينهما ".
** طس
(فتح الباري ٥/١٢)
** قال الحافظ فى " الفتح " ١٢ / ٥: راشد مقبول لكن الراوى عنه مجهول.','7','2',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2778,'<span data-type="title" id=toc-2784>٢٧٧٨ </span>- عن أبى سعيد الخدرى: تعلموا الفرائض وعلموها الناس.
** قط
(فتح الباري ٥/١٢)
** قال الحافظ فى " الفتح " ١٢ / ٥: من طريق عطية وهو ضعيف.','7','3',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2779,'<span data-type="title" id=toc-2785>٢٧٧٩ </span>- عن عمر موقوفا: " تعلموا الفرائض كما تعلمون القرآن " وفى لفظ عنه: " تعلموا الفرائض فإنها من دينكم ".
** دى
(فتح الباري ٥/١٢)
** منقطع
** قال الحافظ فى " الفتح " ١٢ / ٥: رجالها ثقاتا إلا أن فى أسانيدها انقطاعا.','7','4',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2780,'<span data-type="title" id=toc-2786>٢٧٨٠ </span>- عن ابن مسعود موقوفا: " من قرأ القرآن فليتعلم الفرائض ".
** لم
(فتح الباري ٥/١٢)
** منقطع
** قال الحافظ فى " الفتح " ١٢ / ٥: رجالها ثقات إلا أن فى أسانيدها انقطاعا.','7','5',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2781,'<span data-type="title" id=toc-2787>٢٧٨١ </span>- عن الحسن رفعه مرسلا: " رحم الله أخى زكريا ماكان عليه من يرث ماله ".
** لم
(فتح الباري ٨/١٢)
** مرسل','7','6',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2782,'<span data-type="title" id=toc-2788>٢٧٨٢ </span>- عن الشعبى أن أبا بكر وابن عباس وابن الزبير كانوا يجعلون الجد أبا يرث ما يرث ويحجب مايحجب.
** يزيد بن هارون فى كتاب الفرائض
(فتح الباري ١٩/١٢)
** قال الحافظ فى " الفتح " ١٢ / ١٩: محمد بن سالم ضعيف والشعبى عن أبى بكر منقطع.','7','7',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2783,'<span data-type="title" id=toc-2789>٢٧٨٣ </span>- عن أبى سعيد الخدرى: أن أبا بكر الصديق جعل الجد أبا.
** دى
(فتح الباري ١٩/١٢)
** إسناده على شرط مسلم','7','8',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2784,'<span data-type="title" id=toc-2790>٢٧٨٤ </span>- عن عثمان بن عفان: أن أبا بكر كان يجعل الجد أبا.
** دى
(فتح الباري ١٩/١٢)
** إسناده صحيح','7','9',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2785,'<span data-type="title" id=toc-2791>٢٧٨٥ </span>- عن ابن عباس: أن أبا بكر كان يجعل الجد أبا.
** دى
(فتح الباري ١٩/١٢)
** إسناده صحيح','7','10',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2786,'<span data-type="title" id=toc-2792>٢٧٨٦ </span>- عن طاوس عنه أنه جعل الجد أبا.
** دى
(فتح الباري ١٩/١٢)
** إسناده صحيح','7','11',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2787,'<span data-type="title" id=toc-2793>٢٧٨٧ </span>- أفرضكم زيد.
** حم د ت ن جه
(فتح الباري ٢٠/١٢)
** حسن
** قال الحافظ فى " الفتح " ١٢ / ٢٠: أخرجه أحمد وأصحاب السنن صححه الترمذى وابن حبان والحاكم من رواية أبى
قلابة عن أنس وأعله بالإرسال ورجحه الدارقطنى والخطيب وغيرهما.','7','12',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2788,'<span data-type="title" id=toc-2794>٢٧٨٨ </span>- عن الشعبى قال: أول جد ورث فى الإسلام عمر فأخذ ماله، فأتاه على وزيد _ يعنى زيد بن ثابت _ فقالا: ليس لك ذلك إنما أنت كأحد الأخوين.
** دى
(فتح الباري ٢٠/١٢)
** إسناده صحيح','7','13',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2789,'<span data-type="title" id=toc-2795>٢٧٨٩ </span>- عن زيد بن ثابت أن عمر أتاه ... فذكر قصة فيها: إن مثل الجد كمثل شجرة نبتت على ساق واحدة فخرج منها غصن ثم خرج من الغصن غصن فإن قطعت الغصن رجع الماء إلى الساق وإن قطعت الثانى رجع المال إلى الأول، فخطب عمر الناس فقال: إن زيدا قال فى الجد قولا وقد أمضيته.
** قط
(فتح الباري ٢١/١٢)
** إسناده قوى','7','14',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2790,'<span data-type="title" id=toc-2796>٢٧٩٠ </span>- قال عمر: خذ من الجد ما اجتمع عليه الناس.
** دى
(فتح الباري ٢١/١٢)
** منقطع','7','15',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2791,'<span data-type="title" id=toc-2797>٢٧٩١ </span>- عن عبد الله بن عتبة وقبيصة بن ذؤيب أن عمر قضى أن الجد يقاسم الأخوة للأب والأم والأخوة للأب ماكانت المقاسمة خيرا له من الثلث فإن كثر الإخوة أعطى الجد الثلث.
** هق
(فتح الباري ٢١/١٢)
** إسناده صحيح','7','16',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2792,'<span data-type="title" id=toc-2798>٢٧٩٢ </span>- عن محمد بن سيرين: سألت عبيده عن الجد فقال: حفظت عن عمر فى الجد مائة قطية مختلفة.
** فوائد أبى جعفر الرازى
(فتح الباري ٣١/١٢)
** إسناده صحيح','7','17',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2793,'<span data-type="title" id=toc-2799>٢٧٩٣ </span>- عن الشعبى: كتب ابن عباس إلى على يسأله عن ستة أخوة وجد، فكتب إليه أن اجعله كأحدهم وامح كتابى.
** شب محمد بن نصر
(فتح الباري ٢١/١٢)
** إسناده صحيح','7','18',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2794,'<span data-type="title" id=toc-2800>٢٧٩٤ </span>- عن الشعبى قال: كتب ابن عباس إلى على _ وابن عباس بالبصرة _ إنى أتيت بجد وستى إخوة فكتب إليه على أن أعط الجد سبعا ولا تعطه أحدا بعده.
** دى
(فتح الباري ٢١/١٢)
** إسناده قوى','7','19',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2795,'<span data-type="title" id=toc-2801>٢٧٩٥ </span>- عن عبد الله بن سلمة: أن عليا كان يجعل الجد أخا حتى يكون سادسا.
** دى
(فتح الباري ٢١/١٢)
** إسناده صحيح','7','20',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2796,'<span data-type="title" id=toc-2802>٢٧٩٦ </span>- عن عبيد بن نضلة قال: كان عمر وابن مسعود يقاسمان الجد مع الإخوة مابينه وبين أن يكون السدس خيرا له من مقاسمة الإخوة.
** ص شب
(فتح الباري ٢١/١٢)
** إسناده صحيح','7','21',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2797,'<span data-type="title" id=toc-2803>٢٧٩٧ </span>- عن عبيدة بن عمرو قال: كان يعطى الجد مع الإخوة الثلث وكان عمر يعطيه السدس ثم كتب عمر إلى عبد الله: إنا نخاف أن نكون قد أجحفنا الجد فأعطه الثلث ثم قدم على هاهنا _ يعنى الكوفة _ فأعطاه السدس، قال عبيدة: فرأيهما من الجماعة أحب إلى من رأى أحدهما فى الفرقة.
** محمد بن نصر
(فتح الباري ٢٢/١٢)
** إسناده صحيح','7','22',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2798,'<span data-type="title" id=toc-2804>٢٧٩٨ </span>- من طريق العوفى عن ابن عباس قال: كان الرجل فى الجاهلية يلحق به الرجل فيكون تابعه، فإذا مات الرجل صار لأقاربه الميراث وبقى تابعه ليس له شىء فنزلت: (والذين عاقدت إيمانكم فآتوهم نصيبهم) فكانوا يعطونه من ميراثه ثم نزلت (وأولوا الأرحام بعضهم أولى ببعض فى كتاب الله) فنسخ ذلك.
** لم
(فتح الباري ٣٠/١٢)
** قال الحافظ فى " الفتح " ١٢ / ٣٠: العوفى ضعيف.','7','23',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2799,'<span data-type="title" id=toc-2805>٢٧٩٩ </span>- عن ابن مسعود أنه جعل العمة كالأب والخالة كالأم فقسم المال بينهما أثلاثا.
** لم
(فتح الباري ٣٠/١٢)
** إسناده صحيح','7','24',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2800,'<span data-type="title" id=toc-2806>٢٨٠٠ </span>- الخال وارث من لا وارث له.
** ت
(فتح الباري ٣٠/١٢)
** حسن','7','25',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2801,'<span data-type="title" id=toc-2807>٢٨٠١ </span>- من رواية مكحول مرسلا ومن رواية عمرو بن شعيب عن أبيه عن جده قال: جعل النبى صلى الله عليه وسلم ميراث ابن الملاعنة لأمه ولورثتها من بعدها.
** د
(فتح الباري ٣١/١٢)
** مرسل','7','26',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2802,'<span data-type="title" id=toc-2808>٢٨٠٢ </span>- عن واثلة رفعه: " تحوز المرأة ثلاثة مواريث: عتيقها ولقيطها وولدها الذى لاعنت عليه ".
** د ت ن جه
(فتح الباري ٣١/١٢)
** قال الحافظ فى " الفتح " ١٢ / ٣١: أخرجه أصحاب السنن الأربعة، قال البيهقى: ليس بثابت أ. هـ. قلت: وحسنه الترمذى وصححه الحاكم وليس فيه سوى عمر بن رؤبة مختلف فيه، قال البخارى: فيه نظر، ووثقه جماعة.','7','27',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2803,'<span data-type="title" id=toc-2809>٢٨٠٣ </span>- من طريق عثمان الجزرى عن مقسم: أن النبى صلى الله عليه وسلم دعا بأن لا يحول على عتبى الحول حتى يموت كافرا فمات قبل الحول.
** عب
(فتح الباري ٣٣/١٢)
** مرسل','7','28',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2804,'<span data-type="title" id=toc-2810>٢٨٠٤ </span>- عن عمرو بن شعيب عن أبيه عن جده قال: قام رجل فقال: يارسول الله إن فلانا ابنى عاهرت بأمه فى الجاهلية، فقال رسول الله صلى الله عليه وسلم: " لا دعوة فى الإسلام ذهب أمر الجاهلية الولد للفراش وللعاهر الحجر ".
** د
(فتح الباري ٣٤/١٢)
** إسناده حسن','7','29',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2805,'<span data-type="title" id=toc-2811>٢٨٠٥ </span>- حديث عبد الله بن الزبير: كانت لزمعة جارية يطؤها وكان يظن بآخر أنه يقع عليها فجاءت بولد يشبه الذى كان يظن به فمات زمعة، فذكرت ذلك للنبى صلى الله عليه وسلم فقال: " الولد للفراش واحتجبى منه ياسودة فليس لك بأخ ".
** ن
(فتح الباري ٣٧/١٢)
** حسن
** قال الحافظ فى " الفتح " ١٢ / ٣٧: رجال سنده رجال الصحيح إلا شيخ مجاهد وهو يوسف مولى آل الزبير وقد طعن البيهقى فى سنده فقال: فيه جرير وقد نسب فى آخر عمره إلى سوء الحفظ، وفيه يوسف وهو غير معروف.','7','30',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2806,'<span data-type="title" id=toc-2812>٢٨٠٦ </span>- عن ابن سيرين: أن سالما مولى أبى حذيفة الصحابى المشهور أعتقته امرأة من الأنصار سائبة وقالت له: وال من شئت، فوالى أبا حذيفة فلما اشتهر باليمامة دفع ميراثه للأنصارية أو لابنها.
** عب
(فتح الباري ٤١/١٢)
** إسناده صحيح','7','31',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2807,'<span data-type="title" id=toc-2813>٢٨٠٧ </span>- حديث عائشة مرفوعا: " من تولى إلى غير مواليه فليتبوأ مقعده من النار ".
** لم
(فتح الباري ٤٢/١٢)
** قال الحافظ فى " الفتح " ١٢ / ٤٢: صححه الحاكم.','7','32',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2808,'<span data-type="title" id=toc-2814>٢٨٠٨ </span>- من طريق سليمان بن على بن عبد الله بن عباس عن أبيه عن جده رفعه: " الولاء ليس بمنتقل ولا متحول ".
** طب
(فتح الباري ٤٤/١٢)
** قال الحافظ فى " الفتح " ١٢ / ٤٤: فى سنده المغيرة بن جميل وهو مجهول.','7','33',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2809,'<span data-type="title" id=toc-2815>٢٨٠٩ </span>- عن تميم الدارى قال: قلت: يارسول الله ما السنة فى الرجل على يدى رجل من المسلمين؟ ، قال: " هو أولى الناس بمحياه ومماته ".
** ابن أبى عاصم طب الباغندى فى مسند عمر بن عبد العزيز
(فتح الباري ٤٦/١٢)
** قال الحافظ فى " الفتح " ١٢ / ٤٦: قفال البخارى: قال بعضهم عن ابن موهب سمع تميما ولا يصح لقول النبى صلى الله
عليه وسلم: " الولاء لمن أعتق " وقال الشافعى: هذا الحديث ليس بثابت إنما يرويه عبد العزيز بن عمر عن ابن موهب، وابن موهب ليس بالمعروف ولا نعلمه لقى تميما ومثل هذا لا يثبت، وقال الخطابى: ضعف أحمد هذا الحديث.','7','34',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2810,'<span data-type="title" id=toc-2816>٢٨١٠ </span>- الإسلام يزيد ولا ينقص.
** د
(فتح الباري ٥٠/١٢)
** قال الحافظ فى " الفتح " ١٢ / ٥٠: صححه الحاكم من طريق يحيى بن يعمر عن أبى الأسود الرؤلى عنه، قال الحاكم: صحيح الإسناد، وتعقب بالإنقطاع بين أبى الأسود ومعاذ ولكن سماعه منه ممكن وقد زعم الجوزقانى أنه باطل وهى مجازفة.','7','35',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2811,'<span data-type="title" id=toc-2817>٢٨١١ </span>- عن معاذ: أنه كان يورث المسلم من الكافر بغير العكس.
** منيع
(فتح الباري ٥٠/١٢)
** إسناده قوى','7','36',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2812,'<span data-type="title" id=toc-2818>٢٨١٢ </span>- عن جابر مرفوعا: " لا يرث المسلم النصرانى إلا أن يكون عبده أو أمته ".
** ن ك
(فتح الباري ٥٣/١٢)
** قال الحافظ فى " الفتح " ١٢ / ٥٣: أعله ابن حزم بتدليس أبى الزبير وهو مردود فقد أخرجه عبد الرزاق عن ابن جريج عن أبى الزبير أنه سمع جابرا، فلا حجة فيه لكل من المسألتين لأنه ظاهره الموقوف.','7','37',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2813,'<span data-type="title" id=toc-2819>٢٨١٣ </span>- عن ابن عمر رفعه: " من انتقى من ولده ليفضحه فى الدنيا فضحه الله يوم القيامة.... " الحديث.
** لم
(فتح الباري ٥٣/١٢)
** قال الحافظ فى " الفتح " ١٢ / ٥٣: فى سنده الجراح والد وكيع مختلف فيه.','7','38',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2814,'<span data-type="title" id=toc-2820>٢٨١٤ </span>- عن ابن عمر: من انتفى من ولده نفليتبوأ مقعده من النار.
** عد
(فتح الباري ٥٤/١٢)
** قال الحافظ فى " الفتح " ١٢ / ٥٤: فى سنده محمد بن أبى الزعيزعة راويه عن نافع، قال أبو حاتم: منكر الحديث.','7','39',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2815,'<span data-type="title" id=toc-2821>٢٨١٥ </span>- حديث أبى هريرة: وأيما رجل جحد ولده وهو ينظر إليه احتجب الله منه.... الحديث.
** د ن
(فتح الباري ٥٤/١٢)
** قال الحافظ فى " الفتح " ١٢ / ٥٤: صححه ابن حبان والحاكم وفى سنده عبيد الله بن يوسف حجازى ماروى عنه سوى يزيد بن الهاد.','7','40',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2816,'<span data-type="title" id=toc-2822>٢٨١٦ </span>- عن سعيد بن المسيب: أن عمر كان قائفا.
** يزيد بن هارون فى الفرائض
(فتح الباري ٥٧/١٢)
** إسناده صحيح','7','41',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2817,'<span data-type="title" id=toc-2823>٢٨١٧ </span>- من طريق سعيد المقبرى: أنه سمع أباهريرة رفعه: " إذا زنى الرجل خرج منه الإيمان فكان عليه كالظلة فإذا أقلع رجع إليه الإيمان ".
** د ك
(فتح الباري ٦١/١٢)
** إسناده صحيح','7','42',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2818,'<span data-type="title" id=toc-2824>٢٨١٨ </span>- عن رجل من الصحابة رفعه: " من زنى خرج منه الإيمان فإن تاب تاب الله عليه ".
** طب
(فتح الباري ٦١/١٢)
** إسناده جيد','7','43',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2819,'<span data-type="title" id=toc-2825>٢٨١٩ </span>- حديث معاذ رفعه: " إنما نهيتكم عن نهبى العساكر فإما الفرسان فلا ".
** لم
(فتح الباري ٦٣/١٢)
** ضعيف
** قال الحافظ فى " الفتح " ١٢ / ٦٣: هو حديث ضعيف فى سنده ضعف وانقطاع.','7','44',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2820,'<span data-type="title" id=toc-2826>٢٨٢٠ </span>- عن قتادة: جلد بالجريد والنعال أربعين.
** د
(فتح الباري ٦٤/١٢)
** إسناده صحيح
** قال الحافظ فى " الفتح " ١٢ / ٦٤: علقه أبو داود بسند صحيح ووصله البيهقى.','7','45',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2821,'<span data-type="title" id=toc-2827>٢٨٢١ </span>- عن أبى سعيد: أتى النبى صلى الله عليه وسلم بنشوان فأمر به فنهذ بالأيدى وخفق بالنعال ... الحديث.
** ن
(فتح الباري ٦٧/١٢)
** إسناده صحيح','7','46',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2822,'<span data-type="title" id=toc-2828>٢٨٢٢ </span>- عن عبيد بن عمير أحد كبار التابعين: كان الذى يشرب الخمر فى عهد رسول الله صلى الله عليه وسلم وأبى بكر وبعض إمارة عمر يضربونه بأيديهم ونعالهم ويصكونه.
** عب
(فتح الباري ٦٧/١٢)
** إسناده صحيح','7','47',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2823,'<span data-type="title" id=toc-2829>٢٨٢٣ </span>- فى مرسل عبيد بن عمير: أن عمر جعله أربعين سوطا فلما رأهم لا يتناهون جعله ستين سوطا، فلما رأهم لا يتناهون جعله ثمانين سوطا وقال: هذا أدنى الحدود.
** عب
(فتح الباري ٦٩/١٢)
** مرسل','7','48',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2824,'<span data-type="title" id=toc-2830>٢٨٢٤ </span>- عن ثور بن يزيد: أن عمر استشار فى الخمر فقال له على بن أبى طالب: ترى أن تجعله ثمانين، فإنه إذا شرب سكر وإذا سكر هذى وإذا هذى افترى، فجلد عمر فى الخمر ثمانين.
** مما
(فتح الباري ٦٩/١٢)
** معضل
** تعقيب: قال الخطيب ١٢ / ٦٩: ثور بن يزيد هو الكلاعى وفى نسخة، ثور بن زيد وهو الديلى وقد روى مالك عن كليهما وكلاهما ثقة.','7','49',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2825,'<span data-type="title" id=toc-2831>٢٨٢٥ </span>- من طريق أبى جعفر محمد بن على بن الحسين أن عليا جلد الوليد بسوط له طرفان.
** طحا الطبرى
(فتح الباري ٧١/١٢)
** قال الحافظ فى " الفتح " ١٢ / ٧١: أن السند منقطع فإن أبا جعفر ولد بعد موت على بأكثر من عشرين سنة.','7','50',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2826,'<span data-type="title" id=toc-2832>٢٨٢٦ </span>- عن ابن عباس: أن رسول الله صلى الله عليه وسلم يوقت فى الخمر حدا، قال ابن عباس: وشرب رجل فكسر فانطلق به إلى النبى صلى الله عليه وسلم فلما حاذى دار العباس انفلت فدخل على العباس فالتزمه فذك ذلك للنبى صلى الله عليه وسلم فضحك ولم يأمر فيه بشىء.
** د ن
(فتح الباري ٧٢/١٢)
** إسناده قوى','7','51',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2827,'<span data-type="title" id=toc-2833>٢٨٢٧ </span>- عن أبى رافع عن عمر أنه أتى بشارب فقال لمطيع بن الأسود: إذا أصبحت غدا فاضربه فجاء عمر فوجده يضربه ضربا شديدا فقال: كم ضربته قال: ستين، قال: اقتص عنه بعشرين.
** أبو عبيدة فى غريب الحديث
(فتح الباري ٧٣/١٢)
** إسناده صحيح','7','52',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2828,'<span data-type="title" id=toc-2834>٢٨٢٨ </span>- عن أبى هريرة رفعه: " إذا سكر فاجلدوه ثم إذا سكر فاجلدوه ثم إذ سكر فاجلدوه ثم إذا سكر فاقتلوه ".
** د حم ن دى ابن المنذر
(فتح الباري ٧١/١٢)
** قال الحافظ فى " الفتح " ١٢ / ٧١: صححه ابن حبان.','7','53',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2829,'<span data-type="title" id=toc-2835>٢٨٢٩ </span>- حديث الشريد وهو ابن أوس الثقفى: إذا شرب فاضربوه ... وقال فى آخره: ثم إن عاد الرابعة فاقتلوه.
** حم دى طب
(فتح الباري ٧٩/١٢)
** قال الحافظ فى " الفتح " ١٢ / ٧٩: صححه الحاكم.','7','54',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2830,'<span data-type="title" id=toc-2836>٢٨٣٠ </span>- حديث أبى الرمداء: أن النبى صلى الله عليه وسلم أمر بالذى شرب الخمر فى الرابعة أن تضرب عنقه فضربت.
** طب ابن منده
(فتح الباري ٧٩/١٢)
** قال الحافظ فى " الفتح " ١٢ / ٧٩: فى سنده ابن لهيعة.','7','55',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2831,'<span data-type="title" id=toc-2837>٢٨٣١ </span>- عن عبد الله بن عمرو أنه قال: ائتونى برجل أقيم عليه الحد يعنى ثلاثا ثم سكر فإن لم أقتله فأنا كذاب.
** حارث جم
(فتح الباري ٨٠/١٢)
** منقطع
** قال الحافظ فى " الفتح " ١٢ / ٨٠ ك هذا منقطع لأن الحسن لم يسمع من عبد الله بن عمرو وكما جزم به المدينى وغيره فلا حجة فيه.','7','56',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2832,'<span data-type="title" id=toc-2838>٢٨٣٢ </span>- عن عمر بن الخطاب أنه جلد أبا محجن الثقفى فى الخمر ثمان مرار.
** عب
(فتح الباري ٨٠/١٢)
** إسناده لين','7','57',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2833,'<span data-type="title" id=toc-2839>٢٨٣٣ </span>- أن عمر جلد أب محجن فى الخمر أربع مرار ثم قال له: أنت خليع، فقال: أما إذا خلعتنى فلا أشربها أبدا.
** حماد بن سلمة فى مصنفه
(فتح الباري ٨١/١٢)
** رجاله ثقات','7','58',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2834,'<span data-type="title" id=toc-2840>٢٨٣٤ </span>- عن على أنه قطع يد سارق فى بيضة حديد ثمنها ربع دينار.
** شب
(فتح الباري ٨٣/١٢)
** منقطع
** قال الحافظ فى " الفتح " ١٢ / ٨٣: رجاله ثقات مع انقطاعه.','7','59',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2835,'<span data-type="title" id=toc-2841>٢٨٣٥ </span>- حديث خزيمة بن ثابت رفعه: " من أصاب دنياأقيم عليه حد ذلك الذنب فهو كفارته.
** حم
(فتح الباري ٨٤/١٢)
** إسناده حسن','7','60',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2836,'<span data-type="title" id=toc-2842>٢٨٣٦ </span>- حديث عمرو بن شعيب عن أبيه عن جده فيه: " فمن فعل شيئا فأقيم عليه الحد فهو كفارته ".
** لم
(فتح الباري ٨٤/١٢)
** إسناده صحيح','7','61',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2837,'<span data-type="title" id=toc-2843>٢٨٣٧ </span>- عن عائشة قالت: ققال رسول الله صلى الله عليه وسلم: " ظهور المسلمين حمى إلا فى حدود الله ".
** شيخ فى كتاب السرقة
(فتح الباري ٨٥/١٢)
** قال الحافظ فى " الفتح " ١٢ / ٨٥: فى محمد بن عبد العزيز ضعف.','7','62',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2838,'<span data-type="title" id=toc-2844>٢٨٣٨ </span>- حديث عصمة بن مالك الخطمى: ظهر المؤمن حمى إلا بحقه.
** طب
(فتح الباري ٨٥/١٢)
** قال الحافظ فى " الفتح " ١٢ / ٨٥: فى سنده الفضل بن المختار وهو ضعيف.','7','63',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2839,'<span data-type="title" id=toc-2845>٢٨٣٩ </span>- حديث أبى أمامة: من جرد ظهر مسلم بغير حق لقى الله وهو عليه غضبان.
** طب
(فتح الباري ٨٥/١٢)
** إسناده فيه مقال','7','64',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2840,'<span data-type="title" id=toc-2846>٢٨٤٠ </span>- مرسل حبيب بن أبى ثابت: أن النبى صلى الله عليه وسلم قال لأسامة لما شفع فيها: " لا تشفع فى حد فإن الحدود إذا انتهت إلى فليس لها مترك ".
** لم
(فتح الباري ٨٧/١٢)
** مرسل','7','65',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2841,'<span data-type="title" id=toc-2847>٢٨٤١ </span>- حديث عمرو بن شعيب عن أبيه عن جده رفعه: " تعافوا الحدود فيما بينكم فما بلغنى من حد فقد وجب ".
** د
(فتح الباري ٨٧/١٢)
** قال الحافظ فى " الفتح " ١٢ / ٨٧: صححه الحاكم وسنده إلى عمرو بن شعيب صحيح.','7','66',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2842,'<span data-type="title" id=toc-2848>٢٨٤٢ </span>- من طريق يحيى بن راشد قال: خرج علينا ابن عمر فقال: سمعت رسول الله صلى الله عليه وسلم يقول: " من حالت شفاعته دون حد من حدود الله فقد ضاد الله فى أمره ".
** د حم
(فتح الباري ٨٧/١٢)
** قال الحافظ فى " الفتح " ١٢ / ٨٧: صححه الحاكم.','7','67',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2843,'<span data-type="title" id=toc-2849>٢٨٤٣ </span>- عن عكرمة أن ابن عباس وعمارا والزبير أخذوا سارقا فخلوا سبيله فقلت لابن عباس: بئسما صنعتم حين خليتم سبيله، فقال: لا أم لك أما لو كنت أنت لسرك أن يخلى سبيلك.
** شب
(فتح الباري ٨٨/١٢)
** إسناده صحيح','7','68',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2844,'<span data-type="title" id=toc-2850>٢٨٤٤ </span>- عن عائشة بنت مسعود بن الأسود عن أبيها قال: لما سرقت المرأة تلك القطيفة من بيت رسول الله صلى الله عليه وسلم أعظمنا ذلك فجئنا إلى رسول الله صلى الله عليه وسلم نكلمه.
** جه
(فتح الباري ٨٩/١٢)
** إسناده حسن
** قال الحافظ فى " الفتح " ١٢ / ٨٩: صححه الحاكم.','7','69',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2845,'<span data-type="title" id=toc-2851>٢٨٤٥ </span>- أن امرأة جاءت امرأة فقالت: إن فلانة تستعيرك حليا، فأعارتها إياه فمكثت لا تراه فجاءت إلى التى استعارت لها فسألتها فقالت: ما استعرتك شيئا فرجعت إلى الأخرى فأنكرت فجاءت إلى النبى صلى الله عليه وسلم فدعاها فسألها فقالت: والذى بعثك بالحق ما إستعرت منها شيئا فقال: " اذهبوا إلى بيتها تجدوه تحت فراشها فأتوه فأخذوه وأمر بها فقطعت ".
** عب
(فتح الباري ٩٠/١٢)
** إسناده صحيح','7','70',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2846,'<span data-type="title" id=toc-2852>٢٨٤٦ </span>- من مرسل سعيد بن المسيب: أن امرأة من بنى مخزوم استعارت حليا على لسان أناس فجحدت فأمر بها النبى صلى الله عليه وسلم فقطعت.
** ن
(فتح الباري ٩٢/١٢)
** إسناده صحيح مرسلا','7','71',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2847,'<span data-type="title" id=toc-2853>٢٨٤٧ </span>- عن سعيد قال: أتى النبى صلى الله عليه وسلم بامرأة من بيت عظيم من بيوت قريش قد أتت أناسا فقالت: إن آل فلان يستعيرونكم كذا فأعاروها ثم أتوا أولئك فأنكروا، ثم أنكرت هى فقطعها النبى صلى الله عليه وسلم.
** عب
(فتح الباري ٩٢/١٢)
** إسناده صحيح مرسلا','7','72',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2848,'<span data-type="title" id=toc-2854>٢٨٤٨ </span>- فى مرسل الحسن بن محمد بن على قال: سرقت امرأة _ فذكر الحديث وفيه _ فجاء عمر بن أبى سلمة، فقال للنبى صلى الله عليه وسلم: أى أبة إنها عمتى، فقال: " لو كانت فاطمة بنت محمد لقطعت يدها ".
** عب
(فتح الباري ٩٥/١٢)
** مرسل','7','73',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2849,'<span data-type="title" id=toc-2855>٢٨٤٩ </span>- فى مرسل حبيب بن أبى ثابت: فاستشفعوا على النبى صلى الله عليه وسلم بغير واحد فكلموا أسامة.
** لم
(فتح الباري ٩٤/١٢)
** مرسل','7','74',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2850,'<span data-type="title" id=toc-2856>٢٨٥٠ </span>- من مرسل رجاء بن حيوة: أن النبى صلى الله عليه وسلم قطع فى المفصل.
** شب
(فتح الباري ٩٩/١٢)
** مرسل
** قال الحافظ فى " الفتح " ١٢ / ٩٩: أورده أبو الشيخ فى كتاب حد السرقة من وجه آخر عن رجاء عن عدى رفعه مثله.','7','75',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2851,'<span data-type="title" id=toc-2857>٢٨٥١ </span>- عن على: أنه قطع اليد من الأصابع الرجل من مشط القدم.
** عب
(فتح الباري ٩٩/١٢)
** منقطع
** قال الحافظ فى " الفتح " ١٢ / ٩٩: منقطع وإن كان رجال السند من رجال الصحيح.','7','76',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2852,'<span data-type="title" id=toc-2858>٢٨٥٢ </span>- حديث جابر قال: جىء بسارق إلى النبى صلى الله عليه وسلم فقال: " اقتلوه " فقالوا: يارسول الله إنما سرق، قال: " اقطعوه "، ثم جىء به الثانية فقال: " اقتلوه "، _ فذكر مثله إلى أن قال _: فأتى به الخامسة فقال: " اقتلوه "، قال جابر: فانطلقنا له فقتلناه ورميناه فى بئر.
** د ن
(فتح الباري ٩٩/١٢)
** قال الحافظ فى " الفتح " ١٢ / ٩٩: قال النسائى: هذاالحديث منكر ومصعب بن ثابت راويه ليس بالقوى.','7','77',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2853,'<span data-type="title" id=toc-2859>٢٨٥٣ </span>- عن القاسم بن محمد: أن أبا بكر قطع يد سارق فى الثالثة.
** عب
(فتح الباري ١٠٠/١٢)
** إسناده صحيح','7','78',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2854,'<span data-type="title" id=toc-2860>٢٨٥٤ </span>- من طريق سالم بن عبد الله: أن أبا بكر إنما قطع رجله وكان مقطوع اليد.
** عب
(فتح الباري ١٠٠/١٢)
** منقطع
** قال الحافظ فى " الفتح " ١٢ / ١٠٠: رجال السندين ثقات مع انقطاعهما.','7','79',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2855,'<span data-type="title" id=toc-2861>٢٨٥٥ </span>- عن عبد الرحمن بن عائذ: أن عمر أراد أن يقطع فى الثالثة فقال له على: اضربه واحبسه ففعل.
** عب
(فتح الباري ١٠٠/١٢)
** إسناده حسن','7','80',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2856,'<span data-type="title" id=toc-2862>٢٨٥٦ </span>- عن ابن عباس قال: قطع رسول الله صلى الله عليه وسلم رجلا فى مجن قيمته دينار أو عشرة دراهم.
** د حم ن ك
(فتح الباري ١٠٣/١٢)
** مضطرب','7','81',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2857,'<span data-type="title" id=toc-2863>٢٨٥٧ </span>- عن عطاء مرسلا: أن النبى صلى الله عليه وسلم قطع فى مجن قيمته دينار.
** طحا
(فتح الباري ١٠٣/١٢)
** مرسل','7','82',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2858,'<span data-type="title" id=toc-2864>٢٨٥٨ </span>- عن عمرو بن شعيب عن أبيه عن جده: " لا قطع فيما دون عشرة دراهم ".
** طحا
(فتح الباري ١٠٣/١٢)
** قال الحافظ فى " الفتح " ١٢ / ١٠٣: فيه حجاج بن أرطأة ضعيف مدلس.','7','83',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2859,'<span data-type="title" id=toc-2865>٢٨٥٩ </span>- حديث سعد: لا يقطع السارق إلا فى المجن.
** طحا ما
(فتح الباري ١٠٥/١٢)
** إسناده ضعيف','7','84',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2860,'<span data-type="title" id=toc-2866>٢٨٦٠ </span>- عن أنس: أن أبا بكر قطع فى شىء مايساوى درهمين.
** شب
(فتح الباري ١٠٦/١٢)
** إسناده قوى','7','85',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2861,'<span data-type="title" id=toc-2867>٢٨٦١ </span>- عن عمر أنه قال: إذا أخذ السارق ربع دينار قطع.
** ابن المنذر
(فتح الباري ١٠٧/١٢)
** إسناده منقطع','7','86',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2862,'<span data-type="title" id=toc-2868>٢٨٦٢ </span>- من مرسل سعيد بن المسيب: أن النبى صلى الله عليه وسلم قال لما بلغه ماصنعوا: " عطش الله من عطش آل محمد الليلة ".
** ابن وهب
(فتح الباري ١١١/١٢)
** مرسل','7','87',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2863,'<span data-type="title" id=toc-2869>٢٨٦٣ </span>- حديث البراء: لقيت خالى ومعه الراية فقال: بعثنى رسول الله صلى الله عليه
وسلم إلى رجل تزوج امرأة أبيه أن أضرب عنقه.
** حم د ت ن جه
(فتح الباري ١١٨/١٢)
** قال الحافظ فى " الفتح " ١٢ / ١١٨: أخرجه أحمد وأصحاب السنن وفى سنده أختلاف كثير.','7','88',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2864,'<span data-type="title" id=toc-2870>٢٨٦٤ </span>- عن ابن عباس: مر على بن أبى طالب بمجنونة بنى فلان قد زنت فأمر عمر برجمها فردها على وقال لعمر: أما تذكر أن رسول الله صلى الله عليه وسلم قال: " رفع القلم عن ثلاثة عن المجنون المغلوب على عقله، وعن الصبى حتى يحتلم، وعن النائم حتى يستيقظ "، قال: صدقت فخلى عنها.
** لم
(فتح الباري ١٢١/١٢)
** قال الحافظ فى " الفتح " ١٢ / ١٢١: سندها متصل لكن أعله النسائى بأن جرير بن حازم حدث بمصر بأحاديث غلط فيها.','7','89',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2865,'<span data-type="title" id=toc-2871>٢٨٦٥ </span>- عن سعيد: إن رجلا من أسمل قال لأبى بكر الصديق: إن الآخر زنى قال: فتب إلى الله واستتر بستر الله ثم أتى عمر كذلك فأتى كذلك فأتى رسول الله صلى الله عليه وسلم فأعرض عنه ثلاث مرات حتى إذا أكثر عليه بعث إلى أهله.
** ما ن
(فتح الباري ١٢٢/١٢)
** مرسل','7','90',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2866,'<span data-type="title" id=toc-2872>٢٨٦٦ </span>- حديث نعيم بن هزال: هلا تركتموه لعله يتوب فيتوب الله عليه.
** د
(فتح الباري ١٢٧/١٢)
** قال الحافظ فى " الفتح " ١٢ / ١٢٧: صححه الحاكم وحسنه الترمذى.','7','91',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2867,'<span data-type="title" id=toc-2873>٢٨٦٧ </span>- عن قبيصة بن جابر: خرجنا حجاجا فسنح لى ظبى فىميته بحجر فمات، فلما قدمنا مكة سألنا عمر فسأل عبد الرحمن بن عوف فحكما فيه بعنز، فقلت: إن أمير
المؤمنين لم يدر مايقول حتى سأل غيره، قال: فعلانى بالدرة فقال: أتقتل
الصيد فى الحرم وتسفه الحكم؟ ، قال الله تعالى: (يحكم به ذوا عدل منكم) وهذا عبد الرحمن بن عوف وأنا عمر.
** ص
(فتح الباري ١٣٢/١٢)
** إسناده صحيح
** قال الحافظ فى " الفتح " ١٢ / ١٣٢: ذكره مالك منقطعا.','7','92',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2868,'<span data-type="title" id=toc-2874>٢٨٦٨ </span>- فى رواية خالد الحذاء: إن ماعز بن مالك أتى النبى صلى الله عليه وسلم فقال إنه زنى فأعرض عنه فأعاد عليه مرارا، فسأله قومه: أمجنون هو؟ ، قالوا: ليس به بأس.
** طس
(فتح الباري ١٣٥/١٢)
** إسناده على شرط البخارى','7','93',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2869,'<span data-type="title" id=toc-2875>٢٨٦٩ </span>- حسن السؤال نصف العلم.
** سنى رياضة المتعلمين
(فتح الباري ١٣٨/١٢)
** إسناده ضعيف','7','94',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2870,'<span data-type="title" id=toc-2876>٢٨٧٠ </span>- عن ابن عباس: أن رجلا أقر بأنه زنى بامرأة فجلده النبى صلى الله عليه وسلم مائة، ثم سأل المرأة فقالت: كذب فجلده حد الفرية ثمانين.
** د ن
(فتح الباري ١٤١/١٢)
** قال الحافظ فى " الفتح " ١٢ / ١٤١: سكت عليه أبو داود وصححه الحاكم واستنكره النسائى.','7','95',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2871,'<span data-type="title" id=toc-2877>٢٨٧١ </span>- عن زيد بن أسلم أن عمر خطب الناس فقال: لا تشكوا من الرجم فإنه حق ولقد هممت أن أكتبه فى المصحف فسألت أبى بن كعب فقال: أليس أننى وأنت أستقرئها رسول الله صلى الله عليه وسلم قدمت فى صدرى وقلت أستقرئه آية الرجم وهم يتسافدون تسافد.
** فضائل القرآن لابن الضريس
(فتح الباري ١٤٣/١٢)
** رجاله ثقات','7','96',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2872,'<span data-type="title" id=toc-2878>٢٨٧٢ </span>- كان عمر أراد أن يرجم الحبلى فقال له معاذ: لا سبيل لك عليها حتى تضع مافى بطنها.
** شب
(فتح الباري ١٤٦/١٢)
** رجاله ثقات','7','97',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2873,'<span data-type="title" id=toc-2879>٢٨٧٣ </span>- فى مرسل سعيد بن المسيب أن عمر لما صدر من الحج دعا الله أن يقبضه إلأيه غير مضيع ولا مفرد وقال فى أخر القصة: فما انسلخ ذو الحجة حتى قتل عمر.
** ما
(فتح الباري ١٤٨/١٢)
** مرسل','7','98',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2874,'<span data-type="title" id=toc-2880>٢٨٧٤ </span>- من مرسل القاسم بن محمد قال: أجتمعت الأنصار إلى سعد بن عبادة فأتاهم أبو بكر وعمر وأبو عبيدة فقام الحباب بن المنذر وكان بدريا فقال: منا أمير ومنكم أمير فإنا والله ماننفس عليكم هذا الأمر ولكنا نخاف أن يليها أقوام قتلنا آبائهم وإخوتهم، فقال عمر: إذا كان ذلك فمت إن استطعت.
** سعد
(فتح الباري ١٥٣/١٢)
** إسناده صحيح مرسلا','7','99',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2875,'<span data-type="title" id=toc-2881>٢٨٧٥ </span>- عن ابن عباس عن عمر قال: قلت يامعشر الأنصار إن أولى الناس بنبى الله ثانى إثنين إذ هما فى الغار ثم أخذت بيده.
** الذهلى فى الزهريات
(فتح الباري ١٥٣/١٢)
** إسناده صحيح','7','100',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2876,'<span data-type="title" id=toc-2882>٢٨٧٦ </span>- عن زاين بن حبيش عنه أن عمر قال: يا معشر الأنصار، ألستم تعلمون أن رسول الله صلى الله عليه وسلم أمر أبا بكر أن يؤم بالناس، فأيكم تطيب نفسه أن يتقدم أبابكر، فقالوا: نعوذ بالله أن تتقدم أبابكر.
** حم ن
(فتح الباري ١٥٣/١٢)
** إسناده حسن','7','101',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2877,'<span data-type="title" id=toc-2883>٢٨٧٧ </span>- حديث أبى سعيد قال: قال أبو بكر: ألست أحق الناس بهذا الأمر؟ ، ألست أول من أسلم؟ ، ألست صاحب كذا.
** ت حب
(فتح الباري ١٥٣/١٢)
** قال الحافظ فى " الفتح " ١٢ / ١٥٣: حسنه الترمذى.','7','102',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2878,'<span data-type="title" id=toc-2884>٢٨٧٨ </span>- عن ابن عمر رضى الله عنهما: أن النبى صلى الله عليه وسلم ضرب وغرب وأن أبا بكر ضرب وغرب وأن عمر ضرب وغرب.
** ت ن
(فتح الباري ١٥٨/١٢)
** قال الحافظ فى " الفتح " ١٢ / ١٥٨: صححه ابن حبان.','7','103',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2879,'<span data-type="title" id=toc-2885>٢٨٧٩ </span>- حديث ابن عباس: ليس على الأمة حد حتى تحصن.
** طب
(فتح الباري ١٦١/١٢)
** إسناده حسن','7','104',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2880,'<span data-type="title" id=toc-2886>٢٨٨٠ </span>- عن ابن عمر: فى الأمة سإذا زنت ولا زوج لها يحدها سيدها، فإن كانت ذات زوج فأمرها إلى الإمام.
** عب
(فتح الباري ١٦٣/١٢)
** إسناده صحيح','7','105',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2881,'<span data-type="title" id=toc-2887>٢٨٨١ </span>- حديث أبى هريرة: فإنى أحكم بما فى التوراة.
** لم
(فتح الباري ١٧١/١٢)
** قال الحافظ فى " الفتح " ١٢ / ١٧١: فى سنده رجل مبهم.','7','106',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2882,'<span data-type="title" id=toc-2888>٢٨٨٢ </span>- عن هانىء بن حزام: أن رجلا وجد مع امرأته رجلا فقتلهما فكتب عمر كتابا فى العلانية، أن يقيدوه به وكتابا فى السر أن يعطوه الدية.
** عب
(فتح الباري ١٧٤/١٢)
** إسناده صحيح','7','107',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2883,'<span data-type="title" id=toc-2889>٢٨٨٣ </span>- فى رواية عبد الله بن بكر بن الحارث بن هشام رفعه: " لا يحل أن يجلد فوق عشرة أسواط إلا فى حد ".
** حارث
(فتح الباري ١٧٧/١٢)
** إسناده قوى مرسلا','7','108',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2884,'<span data-type="title" id=toc-2890>٢٨٨٤ </span>- عن ابن عباس: لو كنت راجما أحدا بغير بينة لرجمت فلانة، فقد ظهر فيها الريبة فى منطقها وهيئتها ومن يدخل عليها.
** جه
(فتح الباري ١٨١/١٢)
** إسناده صحيح','7','109',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2885,'<span data-type="title" id=toc-2891>٢٨٨٥ </span>- عن عمر أنه قال لرجل أقعد جاريته وقد اتهمها بالفاحشة على النار حتى احترق فرجها: هل رأيت ذلك عليها؟ ، قال: لا، قال: فاعترفت لك؟ ، قال: لا، قال: فضربه وقال: لولا أنى سمعت رسول الله صلى الله عليه وسلم يقول: " لا يقاد مملوك من مالكه "، لأقدتها منك.
** ك
(فتح الباري ١٨١/١٢)
** قال الحافظ فى " الفتح " ١٢ / ١٨١: قال الحاكم: صحيح الإسناد، وتعقبه الذهبى بأن فى إسناده عمرو بن عيسى شيخ الليث وفيه منكر الحديث، كذا قال فأوهم أن لغيره كلام وليس كذلك فإنه ذكره فى الميزان فقال: لا يعرف، لم يزد على ذلك ولا يلزم من ذلك القدح فيما رواه بل يتوقف فيه.','7','110',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2886,'<span data-type="title" id=toc-2892>٢٨٨٦ </span>- عن أبى هريرة وأبى سعيد قالا: قال رسول الله صلى الله عليه وسلم: " مامن عبد يصلى الخمس ويجتنب الكبائر السبع إلا فتحت له أبواب الجنة ... " الحديث.
** ن طب
(فتح الباري ١٨٢/١٢)
** قال الحافظ فى " الفتح " ١٢ / ١٨٢: صححه ابن حبان والحاكم.','7','111',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2887,'<span data-type="title" id=toc-2893>٢٨٨٧ </span>- عن النعمان بن مرة مرسلا: " الزنا والسرقة وشرب الخمر فواحش ".
** ما
(فتح الباري ١٨٣/١٢)
** مرسل','7','112',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2888,'<span data-type="title" id=toc-2894>٢٨٨٨ </span>- الإضرار فى الوصية من الكبائر.
** الطبرى
(فتح الباري ١٨٣/١٢)
** إسناده صحيح','7','113',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2889,'<span data-type="title" id=toc-2895>٢٨٨٩ </span>- عن أنس رفعه: " نظرت فى الذنوب فلم أر أعظم من سورة من القرآن أوتيها رجل فنسيها ".
** د ت
(فتح الباري ١٨٣/١٢)
** ضعيف','7','114',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2890,'<span data-type="title" id=toc-2896>٢٨٩٠ </span>- من أتى حائضا أو كاهنا فقد كفر.
** ت
(فتح الباري ١٨٣/١٢)
** ضعيف','7','115',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2891,'<span data-type="title" id=toc-2897>٢٨٩١ </span>- عن نافع: سئل ابن عمر عمن قذف أم ولد لأخر فقال: يضرب الحد صاغرا.
** عب
(فتح الباري ١٨٥/١٢)
** إسناده صحيح','7','116',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2892,'<span data-type="title" id=toc-2898>٢٨٩٢ </span>- قول الله تعالى: (ومن يقتل مؤمنا متعمدا فجزاؤه جهنم) أن هذه الآية لمانزلت قال المهاجرون والأنصار: وجبت حتى نزل (إن الله لا يغفر أن يشرك به ويغفر مادون ذلك لمن يشاء) .
** إسماعيل القاضى فى أحكام القرآن
(فتح الباري ١٨٨/١٢)
** إسناده حسن','7','117',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2893,'<span data-type="title" id=toc-2899>٢٨٩٣ </span>- عن ابن مسعود: فإذا أصاب دما حراما نزع منه الحياء
** طب
(فتح الباري ١٨٨/١٢)
** قال الحافظ فى " الفتح " ١٢ / ١٨٨: عن ابن مسعود بسند رجاله ثقات إلا أو فيه انقطاعا.','7','118',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2894,'<span data-type="title" id=toc-2900>٢٨٩٤ </span>- حديث عبد الله بن عمر: زوال الدنيا كلها أهون على الله من قتل رجل مسلم.
** ت
(فتح الباري ١٨٩/١٢)
** قال الحافظ فى " الفتح " ١٢ / ١٨٩: قال الترمذى: حديث حسن.','7','119',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2895,'<span data-type="title" id=toc-2901>٢٨٩٥ </span>- عن حبيب بعث برسول الله صلى الله عليه وسلم سرية فيها المقداد، فلما أتوهم وجدوهم تفرقوا وفيهم رجل له مال كثير لم يبرح فقال: أشهد أن لا إله إلا الله
فأهوى إليه فقتله ... الحديث وفيه: فذكروا ذلك لرسول الله صلى الله عليه
وسلم فقال: " يامقداد قتلت رجلا قال لا إله إلا الله فكيف لك بلا إله إلا
الله "، فأنزل الله (يا أيها الذين أمنوا إذا ضربتم فى سبيل الله فتبينوا) الآية، فقال النبى صلى الله عليه وسلم للمقداد: " كان رجلا مؤمنا يخفى
إيمانه ".
** بز قط فى الأفراد طب
(فتح الباري ١٩٠/١٢)
** قال الحافظ فى " الفتح " ١٢ / ١٩٠: قال الدارقطنى: تفرد به حبيب وتفرد به أبو بكر عنه أ. هـ. قلت: قد تابع أبابكر سفيان الثورى لكنه أرسله، أخرجه ابن أبى شيبة عن وكيع عنه وأخرجه الطبرى من طريق أبى إسحق الفزارى عن الثورى كذلك.','7','120',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2896,'<span data-type="title" id=toc-2902>٢٨٩٦ </span>- لا قود إلا بالسيف.
** بز عد
(فتح الباري ٢٠٠/١٢)
** ضعيف
** قال الحافظ فى " الفتح " ١٢ / ٢٠٠: ذكر البزار الإختلاف فيه مسح ضعيف إسناده، وقال ابن عدى: طرقه كلها ضعيفة.','7','121',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2897,'<span data-type="title" id=toc-2903>٢٨٩٧ </span>- حديث عبادة رفعه: " خمس صلوات كتبهن الله على العباد ... " الحديث وفيه " ومن لم يأت بهن فليس له عند الله عهد إن شاء عذبه وإن شاء أدخله الجنة ".
** ما د ت ن جه
(فتح الباري ٢٠٣/١٢)
** قال الحافظ فى " الفتح " ١٢ / ٢٠٣: أخرجه مالك وأصحاب السنن وصححه ابن حبان وابن السكن وغيرهما.','7','122',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2898,'<span data-type="title" id=toc-2904>٢٨٩٨ </span>- عن ابن عباس قال: جاء أصحاب الفيل حتى نزلوا الصفاح وهو موضع خارج مكة من جهة طريق اليمن، فأتاهم عبد المطلب فقال: إن هذا بيت الله لم يسبط عليه أحدا قالوا: لا نرجع حتى نهدمه فكانوا لا يقدمون فيلهم إلا تأخر فدعا الله الطير الأبابيل فأعطاها حجارة سوداء فلما حاذتهم رمتهم فما بقى منهم أحد إلا أخذته الحكة فكان لا يحك أحد منهم جلده إلا تساقط لحمه.
** ابن مردويه
(فتح الباري ٢٠٧/١٢)
** إسناده حسن','7','123',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2899,'<span data-type="title" id=toc-2905>٢٨٩٩ </span>- حديث جابر رفعه: " لا أعفو عمن قتل بعد أخذ الدية ".
** د
(فتح الباري ٢٠٩/١٢)
** إسناده منقطع','7','124',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2900,'<span data-type="title" id=toc-2906>٢٩٠٠ </span>- عن ابن مسعود قال: مامن رجل يهم بسيئة فتكتب عليه إلا أن رجلا لو هم بعد أبيه أن يقتل رجلا بالبيت الحرام إلا أذاقه الله من عذاب أليم.
** الثورى فى تفسيره
(فتح الباري ٢١٠/١٢)
** إسناده صحيح','7','125',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2901,'<span data-type="title" id=toc-2907>٢٩٠١ </span>- من مرسل قتادة: أن عروة بن مسعود لما دعا قومه إلى الإسلام فرمى بسهم فقتل عفا عن قاتله قبل أن يموت فأجاز النبى صلى الله عليه وسلم عفوه.
** شب
(فتح الباري ٢١٢/١٢)
** مرسل','7','126',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2902,'<span data-type="title" id=toc-2908>٢٩٠٢ </span>- من طريق النخعى قال: كان فيما جاء بهخ عروة البارقى إلى شريح من عند عمر قال: جرح الرجال والنساء سواء.
** ص
(فتح الباري ٢١٤/١٢)
** إسناده صحيح','7','127',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2903,'<span data-type="title" id=toc-2909>٢٩٠٣ </span>- من طريق عكرمة أن والد حذيفة قتل يوم أحد بعض المسلمين وهو يظن أنه من المشركين فوداه رسول الله صلى الله عليه وسلم.
** أبو العباس السراج فى تاريخه
(فتح الباري ٢١٨/١٢)
** قال الحافظ فى " الفتح " ١٢ / ٢١٨: رجاله ثقات مع إرساله.','7','128',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2904,'<span data-type="title" id=toc-2910>٢٩٠٤ </span>- حديث عمرو بن شعيب عن أبيه عن جده رفعه: " الأصابع سواء كلهن فيه عشر عشر من الإبل ".
** جه
(فتح الباري ٢٢٥/١٢)
** إسناده جيد
** قال الحافظ فى " الفتح " ١٢ / ٢٢٥: مزقه أبو داود حديثين.','7','129',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2905,'<span data-type="title" id=toc-2911>٢٩٠٥ </span>- عن أبى المهاجر عبد الله بن عميرة من بنى قيس بن ثعلبة قال: كان رجل يسابق الناس كل سنة بأيام، فلما قدم وجد مع وليدته سبعة رجال يشربون فأخذوه فقتلوه فذكر القصة فى إعترافهم وكتاب الأمير إلى عمر وفى جوابه أن: أضرب أعناقهم واقتلها معهم فلو أن أهل صنعاء اشتركوا فى دمه لقتلتهم.
** فوائد أبى الحسن بن زنجوية
(فتح الباري ٢٢٨/١٢)
** إسناده جيد','7','130',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2906,'<span data-type="title" id=toc-2912>٢٩٠٦ </span>- عن عبد الله بن عامر بن ربيعة قال: كنت مع عمر بطريق مكة فبال تحت شجرة فناداه رجل فضربه بالدرة فقال: عجلت على، فأعطاه المخفقة وقال: اقتص فأبى فقال: لتفعلن، قال: فإنى أغفرها.
** عب
(فتح الباري ٢٢٨/١٢)
** قال الحافظ فى " الفتح " ١٢ / ٢٢٨: أخرجه فى الموطأ عن عاصم بن عبيد الله عن عمر منقطعا.','7','131',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2907,'<span data-type="title" id=toc-2913>٢٩٠٧ </span>- عن عمرو بن شعيب عن أبيه عن جده: أن ابن محيصة الأصغر أصبح قتيلا على أبواب خيبر فقال رسول الله صلى الله عليه وسلم: " أقم شاهدين على من قتله أدفعه إليك برمته "، قال: يارسول الله أنى أصيب شاهدين وإنما أصبح قتيلا على أبوابهم، قال: " فتحلف خمسين قسامة "، قال: فكيف أحلف على مالا أعلم، قال
" تستحلف خمسين منهم "، قال: كيف وهم يهود.
** ن
(فتح الباري ٢٣٤/١٢)
** إسناده صحيح','7','132',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2908,'<span data-type="title" id=toc-2914>٢٩٠٨ </span>- من طريق عبد الرحمن بن بجيد قال: إن سهلا يعنى ابن أبى حثمة وهم فى الحديث أن رسول الله صلى الله عليه وسلم كتب إلى يهود: " إنه وجد بين أظهركم قتيل فدوه " فكتبوا يحلفون ماقتلناه ولا علمنا قاتلا، قال: " فوداه من عنده ".
** د
(فتح الباري ٢٣٧/١٢)
** قال الحافظ فى " الفتح " ١٢ / ٢٣٧: رده الشافعى بأنه مرسل.','7','133',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2909,'<span data-type="title" id=toc-2915>٢٩٠٩ </span>- من طريق مكحول حدثنى عمرو بن أبى خزاعة أنه قتل فيهم قتيل على عهد رسول الله صلى الله عليه وسلم فجعل القسامة على خزاعة بالله ماقتلنا ولا علمنا قاتلا فحلف كل منهم عن نفسه وعزم الدية.
** ابن منده فى الصحابة
(فتح الباري ٢٣٧/١٢)
** قال الحافظ فى " الفتح " ١٢ / ٢٣٧: عمرو مختلف فى صحبته.','7','134',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2910,'<span data-type="title" id=toc-2916>٢٩١٠ </span>- عن الشعبى قال: وجد قتيل بين حيين من العرب فقال عمر: قيسوا مابينهما فأيهما وجدتموه إليه أقرب فأحلفوهم خمسين يمينا وأعزموهم الدية.
** الثورى فى جامعه شب ص
(فتح الباري ٢٣٨/١٢)
** إسناده صحيح','7','135',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2911,'<span data-type="title" id=toc-2917>٢٩١١ </span>- حديث أبى سعيد: أن قتيلا وجد بين حيين فأمر النبى صلى الله عليه وسلم أن يقاس إلى أيهما أقرب فألقى دينه على الأقرب.
** حم
(فتح الباري ٢٣٨/١٢)
** إسناده ضعيف','7','136',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2912,'<span data-type="title" id=toc-2918>٢٩١٢ </span>- عن عبد الله الأشعرى فى غسل الأعقاب قال أبو صالح: فقلت لأبى عبد الله: من حدثك؟ ، قال: امراء الأجناد خالد بن الوليد ويزيد بن أبى سفيان وشرحبيل بن حسنة وعمرو بن العاص.
** جه
(فتح الباري ٢٤٠/١٢)
** قال الحافظ فى " الفتح " ١٢ / ٢٤٠: صححه ابن خزيمة.','7','137',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2913,'<span data-type="title" id=toc-2919>٢٩١٣ </span>- من رواية بشير بن نهيك عنه: من اطلع فى بيت قوم بغير إذنهم ففقؤا عينه فلا دية ولا قصاص.
** حم ابن أبى عاصم
(فتح الباري ٢٤٤/١٢)
** قال الحافظ فى " الفتح " ١٢ / ٢٤٤: صححه ابن حبان والبيهقى.','7','138',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2914,'<span data-type="title" id=toc-2920>٢٩١٤ </span>- من طريق أبى المليح فأرسله: إن حمل بن النابغة كانت له امرأتان مليكة وأم عفيف.
** حارث
(فتح الباري ٢٤٨/١٢)
** مرسل','7','139',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2915,'<span data-type="title" id=toc-2921>٢٩١٥ </span>- من مرسل سعيد بن المسيب: قضى فى الجنين يقتل من بطن أمه بغرة عبد أو وليدة.
** ما
(فتح الباري ٢٤٨/١٢)
** مرسل','7','140',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2916,'<span data-type="title" id=toc-2922>٢٩١٦ </span>- الرجل جبار.
** لم
(فتح الباري ٢٥٨/١٢)
** قال الحافظ فى " الفتح " ١٢ / ٢٥٨: راويه ضعيف.','7','141',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2917,'<span data-type="title" id=toc-2923>٢٩١٧ </span>- عن إبراهيم بن محمد بن أبى يحيى عن ربيعة عن ابن البيلمانى عن ابن عمر قال: قتل رسول الله صلى الله عليه وسلم مسلما بكافر وقال: " أنا أولى من وفى بذمته ".
** قط
(فتح الباري ٢٦٢/١٢)
** قال الحافظ فى " الفتح " ١٢ / ٢٦٢: قال الدارقطنى: إبراهيم ضعيف ولم يروه موصولا غيره والمشهور عن ابن
البيلمانى مرسلا، وقال البيهقى: أخطأ راويه عمار بن مطر على إبراهيم فى سنده، وإنما يرويه إبراهيم عن محمد بن المنكدر عن عبد الرحمن بن البيلمانى وهو منقطع وراويه غير ثقة.','7','142',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2918,'<span data-type="title" id=toc-2924>٢٩١٨ </span>- عن ابن عباس: كان رجل من الأنصار أسلم ثم ندم وأرسل إلى قومه فقالوا: يارسول الله هل له من توبة؟ ، فنزلت: (كيف يهدى الله قوما _ إلى قوله _ إلا الذين تابوا) فأسلم.
** ن حب
(فتح الباري ٢٦٨/١٢)
** قال الحافظ فى " الفتح " ١٢ / ٢٦٨: صححه ابن حبان.
** تعقيب: قال علاء عبده: الحديث بلفظه فى الإحسان فى تقريب صحيح ابن حبان (١٠ / ٣٢٩) رقم ٤٤٧٧ ولكن فيه: كان رجل من الأنصار أسلم ثم ارتد فلحق بالشرك ثم ندم وكذا فى النسائى.','7','143',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2919,'<span data-type="title" id=toc-2925>٢٩١٩ </span>- من طريق عبد الله بن شريك العامرى عن أبيه قال: قيل لعلى أن هنا قوما على باب المسجد يدعون أنك ربهم فدعاهم فقال لهم: ويلكم ماتقولون، قالوا: أنت ربنا وخالقنا ورازقنا فقال: ويلكم إنما أنا عبد مثلكم أكل الطعام كما تأكلون وأشرب كما تشربون إن أطعت الله أثابنى إن شاء وإن عصيته خشيت أن يعذبنى فاتقوا الله وارجعوا فأبوا، فلما كان الغد عذوا عليه فجاء قنبر فقال فدوا الله رجعوا يقولون ذلك الكلام، فقال: أدخلهم فقالوا: كذلك فلما كان الثالث قال: لئن قلتم ذلك لأقتلنكم بأخبث قتلة فأبوا إلا ذلك فقال: ياقنبر ائتنى بفعلة معهم مرورهم فخد لهم أخدودا وقال: إنى طارحكم فيها أو ترجعوا، فأبوا أن يرجعوا فقذف بهم فيها حتى إذا احترقوا قال: إنى إذا رأيت أمرا منكرا أوقدت نارى ودعوت قنبرا.
** أبو طاهر المخلص
(فتح الباري ٢٧٠/١٢)
** إسناده حسن','7','144',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2920,'<span data-type="title" id=toc-2926>٢٩٢٠ </span>- من طريق قتادة: أن عليا أتى بناس من الزط يعبدون وثنا فأحرقهم.
** شب
(فتح الباري ٢٧٠/١٢)
** إسناده منقطع','7','145',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2921,'<span data-type="title" id=toc-2927>٢٩٢١ </span>- حديث معاذ أن النبى صلى الله عليه وسلم لما أرسله إلى اليمن قال له: " أيما رجل ارتد عن الإسلام فدعه فإن عاد وإلا فاضرب عنقه وأيما امرأة ارتدت عن الإسلام فادعها فإن عادت وإلا فاضرب عنقها.
** لم
(فتح الباري ٢٧٢/١٢)
** إسناده حسن','7','146',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2922,'<span data-type="title" id=toc-2928>٢٩٢٢ </span>- من طريق بكير بن عبد الله بن الأشج أنه سأل نافعا كيف كان رأى ابن عمر فى الحرورية؟ ، قال: كان يراهم شرار خلق الله انطلقوا إلى آيات الكفار فجعلوها فى المؤمنين.
** الطبرى فى تهذيب الآثار
(فتح الباري ٢٨٦/١٢)
** إسناده صحيح','7','147',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2923,'<span data-type="title" id=toc-2929>٢٩٢٣ </span>- عن عائشة قالت: ذكر رسول الله صلى الله عليه وسلم الخوارج فقال: " هم شرار أمتى يقتلهم خيار أمتى ".
** بز
(فتح الباري ٢٨٦/١٢)
** إسناده حسن','7','148',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2924,'<span data-type="title" id=toc-2930>٢٩٢٤ </span>- من طريق الشعبى عنه قال: خطب على بنت أبى جهل.
** ك
(فتح الباري ٢٨٦/١٢)
** إسناده جيد مرسلا','7','149',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2925,'<span data-type="title" id=toc-2931>٢٩٢٥ </span>- عن عبد الله بن عمر: أتى ذو الخويصرة التميمى رسول الله صلى الله عليه وسلم
وهو يقاسم الغنائم بحنين فقال: يامحمد ... فذكر الحديث.
** محمد بن إسحق
(فتح الباري ٢٩١/١٢)
** إسناده حسن','7','150',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2926,'<span data-type="title" id=toc-2932>٢٩٢٦ </span>- عن حميد بن هلال قال: حدثنا رجل من عبد القيس قال: لحقت بأهل النهر فإنى مع طائفة منهم أسير إذا أتينا على قرية بيننا نهر فخرج رجل من القرية مروعا فقالوا: له لاروح عليك، وقطعوا إليه النهر فقالوا له: أنت ابن خباب صاحب النبى صلى الله عليه وسلم قال: نعم، قالوا: فحدثنا عن أبيك فحدثهم بحديث يكون فتنة فإن استطعت أن تكون عبد الله المقتول فكن، قال: فقدموه فضربوا عنقه ثم دعوا سريته وهى حبلى فبقروا عما فى بطنها.
** يعقوب بن سفيان
(فتح الباري ٢٩٧/١٢)
** إسناده صحيح','7','151',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2927,'<span data-type="title" id=toc-2933>٢٩٢٧ </span>- عن أبى سعيد قال: جاء أبو بكر إلى رسول الله صلى الله عليه وسلم فقال: يارسول الله إنى مررت بوادى كذا فإذا رجل حسن الهيئة متخشع يصلى فيه فقال: اذهب إليه فاقتله قال: فذهب إليه أبو بكر فلما رآه يصلى كره أن يقتله فرجع فقال النبى صلى الله عليه وسلم لعمر: " اذهب فاقتله " فذهب فرآه على تلك الحالة فرجع، فقال: " ياعلى اذهب إليه فاقتله فذهب إليه على فلم يره فقال النبى صلى الله عليه وسلم: " إن هذا وأصحابه يقرؤن القرآن لا يجاوز ترافيهم يمرقون من الدين كما يمرق السهم من الرمية ثم لا يعودون فيه فاقتلوهم هم شر البرية ".
** حم
(فتح الباري ٢٩٨/١٢)
** إسناده جيد','7','152',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2928,'<span data-type="title" id=toc-2934>٢٩٢٨ </span>- من مرسل الشعبى: ثم دعا رجالا فأعطاهم، فقام رجل فقال: إنك لتقسم ومانرى عدلا، قال: إذا لا يعدل أحد بعدى ثم دعا أبابكر فقال: اذهب فاقتله، فذهب فلم يجده فقال: لو قتلته لرجوت أن يكون أولهم وآخرهم.
** مغازى الأموى
(فتح الباري ٢٩٩/١٢)
** مرسل','7','153',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2929,'<span data-type="title" id=toc-2935>٢٩٢٩ </span>- عن ابن عباس وذكر عنده الخوارج وما يلقون عند قراءة القرآن فقال: يؤمنون بمحكمة ويهلكون عند متشابهة.
** الطبرى فى تهذيبه
(فتح الباري ٣٠٠/١٢)
** إسناده صحيح','7','154',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2930,'<span data-type="title" id=toc-2936>٢٩٣٠ </span>- عن عبد الله بن الحارث عن رجل من بنى نضر عن على وذكر الخوارج فقال: إن خالفوا إماما عدلا فقاتلوهم، وإن خالفوا إماما جائرا فلا تقاتلوهم فإن لهم مقالا.
** الطبرى
(فتح الباري ٣٠١/١٢)
** إسناده صحيح','7','155',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2931,'<span data-type="title" id=toc-2937>٢٩٣١ </span>- من طريق الفرزدق الشاعر أنه سمع أبا هريرة وأبا سعيد وسألهما فقال: إنى رجل من أهل المشرق وأن قوما يخرجون من قال: لا إله إلا الله ويؤمنون من سواهم فقالا لى: سمعنا النبى صلى الله عليه وسلم يقول: " من قتلهم فله أجر شهيد ومن قتلوه فله أجر شهيد ".
** طس
(فتح الباري ٣٠٢/١٢)
** إسناده جيد','7','156',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2932,'<span data-type="title" id=toc-2938>٢٩٣٢ </span>- من طريق أبى عبيدة بن محمد بن عمار بن ياسر قال: أخذ المشركون عمارا فعذبوه حتى قاربهم فى بعض ما أرادوا فشكى ذلك للنبى صلى الله عليه وسلم فقال له: " كيف تجد قلبك "، قال: مطمئنا بالإيمان، قال: " فإن عادوا فعد ".
** عب الطبرى عبد
(فتح الباري ٣١٢/١٢)
** رجاله ثقات مرسلا','7','157',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2933,'<span data-type="title" id=toc-2939>٢٩٣٣ </span>- من طريق ابن سيرين: أن رسول الله صلى الله عليه وسلم لقى عمار بن ياسر وهو يبكى فجعل يمسح الدموع عنه ويقول: " أخذك المشركون فغطوك فى الماء حتى قلت لهم كذا، إن عادوا فعد ".
** عب
(فتح الباري ٣١٢/١٢)
** رجاله ثقات مرسلا','7','158',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2934,'<span data-type="title" id=toc-2940>٢٩٣٤ </span>- عن ابن عباس قال: عذب المشركون عمارا حتى قال لهم كلاما تقية فاشتد عليه.... الحديث.
** ابن أبى حاتم
(فتح الباري ٣١٢/١٢)
** قال الحافظ فى " الفتح " ١٢ / ٣١٢: من طريق مسلم الأعور وهو ضعيف.','7','159',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2935,'<span data-type="title" id=toc-2941>٢٩٣٥ </span>- عن ابن عباس: أنه كان لا يرى طلاق المكره شيئا.
** عب
