INSERT INTO [b88] VALUES (2938,'<span data-type="title" id=toc-2944>٢٩٣٨ </span>- عن ابن عباس فى قصة شبرمة: حج عن نفسك ثم حج عن شبرمة.
** د
(فتح الباري ٣٢٧/١٢)
** إسناده صحيح
** قال الحافظ فى " الفتح " ١٢ / ٣٢٧: عند ابن ماجه فاجعل هذه عن نفسك ثم حج عن شبرمة.','7','163',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2939,'<span data-type="title" id=toc-2945>٢٩٣٩ </span>- عن ابن عباس قال: كان النبى صلى الله عليه وسلم يشرب عسلا عند سودة _ فذكر نحو حديث الباب وفى آخره _: فأنزلت (يا أيها النبى لم تحرم ما أحل الله لك) .
** طب ابن مردويه
(فتح الباري ٣٤٣/١٢)
** رجاله موثقون','7','164',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2940,'<span data-type="title" id=toc-2946>٢٩٤٠ </span>- حديث العداء بن خالد أنه اشترى من النبى صلى الله عليه وسلم عبدا أو أمة وكتب له العهدة (هذا ما اشترى العداء من محمد رسول الله صلى الله عليه وسلم عبدا أو أمة لا داء ولا غائلة ولا خبئة بيع المسلم للمسلم) .
** لم
(فتح الباري ٣٥٠/١٢)
** إسناده حسن','7','165',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2941,'<span data-type="title" id=toc-2947>٢٩٤١ </span>- عن سالم بن عبد الله بن عمر عن أبيه قال: لقى عمر عليا فقال: يا أباالحسن الرجل يرى الرؤيا فمنها مايصدق ومنها مايكذب، قال: نعم سمعت رسول الله صلى الله عليه وسلم يقول: " مامن عبد ولا أمة ينام فميتلىء نوما إلا تخرج بروحه إلى العرش فالذى لا يستيقظ دون العرش فتلك الرؤيا التى تصدق والذى يستيقظ دون العرش فتلك الرؤيا التى تكذب ".
** ك عق
(فتح الباري ٣٥٤/١٢)
** قال الحافظ فى " الفتح " ١٢ / ٣٥٤: قال الذهبى فى تلخيصه: هذا حديث منكر لم يصححه المؤلف، ولعل الآفة من الراوى عن ابن عجلان أ. هـ. قلت: هو أزهر بن عبد الله الأزدى الخرسانى ذكره العقيلى فى ترجمته وقال: إنه غير محفوظ.','7','166',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2942,'<span data-type="title" id=toc-2948>٢٩٤٢ </span>- إن رؤيا المؤمن كلام يكلم به العبد ربه فى المنام.
** نوادر الأصول للترمذى
(فتح الباري ٣٥٤/١٢)
** قال الحافظ فى " الفتح " ١٢ / ٣٥٤: الحديث المذكور فى نوادر الأصول للترمذى من حديث عبادة بن الصامت، اخرجه فى الأصل الثامن والسبعين وهو من رواية عن شيخه عمر بن أبى عمر، وهو واه وفى سنده جنيد.','7','167',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2943,'<span data-type="title" id=toc-2949>٢٩٤٣ </span>- من طريق أبى ميسرة مرسلا أنه صلى الله عليه وسلم قص على خديجة ما رأى فى المنام فقالت له: أبشر فإن الله لن يضيع بك إلا خيرا ثم أخرها بما وقع له من شق البطن وإعادته فقالت له أبشر إن هذا والله خير ثم استعلن له جبريل _ فذكر القصة _ فقال لها: " أرأيتك الذى كنت رأيت فى المنام فإنه جبريل استعلن لى بأن ربى أرسله إلى "، وأخبرها بما جاء به فقالت: أبشر فوالله لا يفعل الله بك إلا خيرا فاقبل الذى جاءك من الله فإنه حق، وأبشر فإنك رسول الله حقا.
** هق الدلائل
(فتح الباري ٣٥٩/١٢)
** مرسل','7','168',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2944,'<span data-type="title" id=toc-2950>٢٩٤٤ </span>- عن ابن عباس فى هذه الآية: تأويل رؤيا رسول الله صلى الله عليه وسلم فى عمرة القضاء.
** ابن مردويه فى التفسير
(فتح الباري ٣٦٢/١٢)
** إسناده ضعيف','7','169',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2945,'<span data-type="title" id=toc-2951>٢٩٤٥ </span>- فى قوله تعالى: (لهم البشرى فى الحياة الدنيا) هى الرؤيا الصالحة.
** ت جه
(فتح الباري ٣٧٥/١٢)
** قال الحافظ فى " الفتح " ١٢ / ٣٧٥: صححه الحاكم.','7','170',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2946,'<span data-type="title" id=toc-2952>٢٩٤٦ </span>- حديث أم كرز الكعبية قالت: سمعت النبى صلى الله عليه وسلم يقول: " ذهبت النبوة وبقيت المبشرات ".
** حم جه
(فتح الباري ٣٧٥/١٢)
** قال الحافظ فى " الفتح " ١٢ / ٣٧٥: صححه ابن خزيمة وابن حبان.','7','171',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2947,'<span data-type="title" id=toc-2953>٢٩٤٧ </span>- عن القاسم قال: اجتمع أبو هريرة وكعب فحدث أبو هريرة عن النبى صلى الله عليه وسلم أن لكل نبى دعوة مستجابة، فقال كعب: أفلا أخبرك عن إبراهيم؟ ،
لما رأى أنه يذبح ابنه إسحق قال الشيطان: إن لم أفتن هؤلاء عند هذه لم أفتنهم أبدا، فذهب إلى سارة فقال: أين ذهب إبراهيم بابنك؟ ، قالت: فى حاجته قال: كلا إنه ذهب به ليذبحه يزعم أن ربه أمره بذلك فقالت: أخشى أن لا يطيع ربه،
فجاء إسحق فأجابه بنحوه فواجه إبراهيم فلم يلتفت إليه فأيس أن يطيعوه.
** ابن أبى حاتم
(فتح الباري ٣٧٨/١٢)
** إسناده صحيح','7','172',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2948,'<span data-type="title" id=toc-2954>٢٩٤٨ </span>- عن ابن مسعود قال: لم يريا شيئا وإنما تحاكما ليجربا.
** الطبرى
(فتح الباري ٣٨١/١٢)
** إسناده ضعيف
** قال الحافظ فى " الفتح " ١٢ / ٣٨١: أخرج الحاكم بسند صحيح عن ابن مسعود نحوه وزاد، فلما ذكر لهما التأويل قالا: إنما كنا نلعب قال: قضى الأمر الآية.','7','173',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2949,'<span data-type="title" id=toc-2955>٢٩٤٩ </span>- قرأ ابن مسعود: (إنى أرانى أعصر عنبا) .
** ابن أبى حاتم
(فتح الباري ٣٨٢/١٢)
** إسناده حسن','7','174',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2950,'<span data-type="title" id=toc-2956>٢٩٥٠ </span>- عن عكرمة رفعه: " لقد عجبت من يوسف وكرمه وصبره حتى سئل عن البقرات العجاف والسمان، ولو كنت مكانه ما أجبت حتى أشترط أن يخرجونى، ولقد عجبت منه حين أتاه الرسول _ يعنى ليخرج إلى الملك _ فقال: ارجع إلى ربك ولو كنت مكانه ولبثت فىالسجن مالبث لأسرعت الإجابة ولبادرت الباب ولما ابتغيت العذر ".
** عب
(فتح الباري ٣٨٢/١٢)
** مرسل','7','175',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2951,'<span data-type="title" id=toc-2957>٢٩٥١ </span>- عن أبى هريرة قال: قال رسول الله صلى الله عليه وسلم: " من رآنى فى المنام فقد رآنى فإنى أرى فى كل صورة ".
** ابن أبى حاتم
(فتح الباري ٣٨٤/١٢)
** قال الحافظ فى " الفتح " ١٢ / ٣٨٤: فى سنده صالح مولى التوأمة وهو ضعيف لاختلاطه وهو من رواية من سمع منه بعد الإختلاط.','7','176',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2952,'<span data-type="title" id=toc-2958>٢٩٥٢ </span>- حديث أبى سعيد: أصدق الرؤيا بالأسحار.
** حم
(فتح الباري ٣٩٠/١٢)
** قال الحافظ فى " الفتح " ١٢ / ٣٩٠: صححه ابن حبان.','7','177',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2953,'<span data-type="title" id=toc-2959>٢٩٥٣ </span>- قوله صلى الله عليه وسلم لعثمان: " إن الله سيلبسك قميصا فلا تخلعه ".
** حم ت جه
(فتح الباري ٢٩٦/١٢)
** قال الحافظ فى " الفتح " ١٢ / ٣٩٦: صححه ابن ماجه.','7','178',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2954,'<span data-type="title" id=toc-2960>٢٩٥٤ </span>- حديث عبد الله بن عمرو بن العاص: سمعت رسول الله صلى الله عليه وسلم يقول: " بينا أنا نائم رأيت عمود الكتاب احتمل من تحت رأسى فأتبعته بصرى فإذا هو قد عهد به إلى الشام، ألا وإن الإيمان حين تقع الفتن بالشام ". وفى رواية: " فإذا وقعت الفتن فالأمن بالشام ".
** يعقوب بن سفيان طب
(فتح الباري ٤٠٢/١٢)
** قال الحافظ فى " الفتح " ١٢ / ٤٠٢: صححه الحاكم.','7','179',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2955,'<span data-type="title" id=toc-2961>٢٩٥٥ </span>- أخذوا عمود الكتاب فعمدوا به إلى الشام.
** عب
(فتح الباري ٤٠٢/١٢)
** منقطع
** قال الحافظ فى " الفتح " ١٢ / ٤٠٢: رجاله رجال الصحيح إلا أن فيه انقطاعا بين أبى قلابة وعبد الله بن عمرو.','7','180',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2956,'<span data-type="title" id=toc-2962>٢٩٥٦ </span>- عن أبى الدرداء رفعه: " بينا أنا نائم رأيت عمود الكتاب احتمل من تحت رأسى فظننت أنه مذهوب به فأتبعته بصرى فعمد له إلى الشام.... " الحديث.
** حم يعقوب بن سفيان طب
(فتح الباري ٤٠٣/١٢)
** إسناده صحيح','7','181',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2957,'<span data-type="title" id=toc-2963>٢٩٥٧ </span>- عن أبى أمامة قال: انتزع من تحت وسادتى، وزاد بعد قوله بصرى " فإذا هو نور ساطع حتى ظننت أنه قد هوى به فعمد إلى الشام وإنى أولت أن الفتن إذا وقعت أن الأمان بالشام ".
** يعقوب طب
(فتح الباري ٤٠٣/١٢)
** إسناده ضعيف','7','182',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2958,'<span data-type="title" id=toc-2964>٢٩٥٨ </span>- عن عبد الله بن حوالة أن رسول الله صلى الله عليه وسلم قال: " رأيت ليلة أسرى بى عمودا أبيض كأنه لواء تحمله الملائكة فقلت: ما تحملون قالوا: عمود الكتاب أمرنا أن نضعه بالشام، قال: وبينا أنا نائم رأيت عمود الكتاب اختلس من تحت وسادتى فظننت أن الله تخلى عن أهل الأرض فأتبعته بصرى فإذا هو نور ساطع حتى وضع بالشام ".
** طب
(فتح الباري ٤٠٣/١٢)
** إسناده حسن','7','183',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2959,'<span data-type="title" id=toc-2965>٢٩٥٩ </span>- حديث عوف بن مالك رفعه: " الرؤيا ثلاث منها أهاويل الشيطان ليحزن ابن آدم ومنها مايهم به الرجل فى يقظته فيراه فى منامه ومنها جزى من ستة وأربعين جزءا من النبوة ".
** جه
(فتح الباري ٤٠٧/١٢)
** إسناده حسن','7','184',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2960,'<span data-type="title" id=toc-2966>٢٩٦٠ </span>- عن مسروق قال: مر صهيب بأبى بكر فأعرض عنه، فسأله فقال: رأيت يدك مغلولة على باب أبى الحشر رجل من الأنصار، فقال أبو بكر: جمع لى دينى إلى يوم
الحشر.
** شب
(فتح الباري ٤٠٨/١٢)
** إسناده صحيح','7','185',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2961,'<span data-type="title" id=toc-2967>٢٩٦١ </span>- عن أبى هريرة قال: إذا تقارب الزمان لم تكد رؤيا المؤمن تكذب وأحب القيد وأكره الغل.
** بز
(فتح الباري ٤١٠/١٢)
** قال الحافظ فى " الفتح " ١٢ / ٤١٠: أخرج ابن ماجه من طريق أبى بكر الهذلى عن ابن سيرين حديث القيد موصولا مرفوعا ولكن الهذلى ضعيف.','7','186',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2962,'<span data-type="title" id=toc-2968>٢٩٦٢ </span>- حديث ابن عباس قال: لما مات عثمان بن مظعون قالت امرأته هنيئا لك الجنة.
** حم سعد
(فتح الباري ٤١١/١٢)
** قال الحافظ فى " الفتح " ١٢ / ٤١١: بسند فيه على بن زيد بن جدعان وفيه ضعف.','7','187',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2963,'<span data-type="title" id=toc-2969>٢٩٦٣ </span>- من مرسل زيد بن أسلم قال: سمع رسول الله صلى الله عليه وسلم عجوزا تقول فى جنازة عثمان بن مظعون وراء جنازته: هنيئا لك الجنة يا أبا السائب، وفيه: بحسبك أن تقولى كان يحب الله ورسوله.
** سعد
(فتح الباري ٤١١/١٢)
** إسناده حسن مرسلا','7','188',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2964,'<span data-type="title" id=toc-2970>٢٩٦٤ </span>- من مرسل أبى بردة بن أبى موسى قال: دخلت امرأة عثمان بن مظعون على نساء النبى صلى الله عليه وسلم فرأين هيئتها فقلن: مالك؟ ، فما فى قريش أغنى من بعلك، فقالت: أما ليلة فقائم ... الحديث.
** سعد
(فتح الباري ٤١١/١٢)
** مرسل','7','189',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2965,'<span data-type="title" id=toc-2971>٢٩٦٥ </span>- حديث فضالة بن عبيد رفعه: " كل ميت يختم على عمله إلا المرابط فى سبيل الله فإنه ينمى له عمله إلى يوم القيامة ويأمن من فتنة القبر ".
** د ت ن جه
(فتح الباري ٤١١/١٢)
** قال الحافظ فى " الفتح " ١٢ / ٤١١: ثبت فى السنن وصححه الترمذى وابن حبان والحاكم.','7','190',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2966,'<span data-type="title" id=toc-2972>٢٩٦٦ </span>- من مرسل الحسن رفعه: " رأيت كأن فى يدى سوارين من ذهب فكرهتهما فذهبا كسرى وقيصر ".
** شب
(فتح الباري ٤٢٥/١٢)
** مرسل','7','191',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2967,'<span data-type="title" id=toc-2973>٢٩٦٧ </span>- حديث على رفعه: " من كذب فى حلمه كلف يوم القيامة عقد شعيرة ".
** ت
(فتح الباري ٤٢٨/١٢)
** إسناده حسن
** قال الحافظ فى " الفتح " ١٢ / ٤٢٨: صححه الحاكم ولكنه من رواية عبد الأعلى بن عامر ضعفه أبو زرعة.','7','192',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2968,'<span data-type="title" id=toc-2974>٢٩٦٨ </span>- حديث أنس قال رسول الله صلى الله عليه وسلم فذكر حديثا وفيه: " والرؤيا لأول عابر ".
** لم
(فتح الباري ٤٣٢/١٢)
** ضعيف
** قال الحافظ فى " الفتح " ١٢ / ٤٣٢: فيه يزيد الرقاشى.','7','193',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2969,'<span data-type="title" id=toc-2975>٢٩٦٩ </span>- عن أبى رزين العقيلى رفعه: " الرؤيا على رجل طائر مالم تعبر فإذا عبرت وقعت ".
** د ت جه
(فتح الباري ٤٣٢/١٢)
** إسناده حسن
** قال الحافظ فى " الفتح " ١٢ / ٤٣٢: صححه الحاكم.','7','194',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2970,'<span data-type="title" id=toc-2976>٢٩٧٠ </span>- من مرسل أبى قلابة: الرؤيا تقع على مايعبر مثل ذلك مثل رجل رفع رجله فهو ينتظر متى يضعها.
** عب
(فتح الباري ٤٣٢/١٢)
** مرسل','7','195',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2971,'<span data-type="title" id=toc-2977>٢٩٧١ </span>- عن عائشة قالت: كانت امرأة من أهل المدينة لها زوج تاجر يختلف _ يعنى فى التجارة _ فاتت رسول الله صلى الله عليه وسلم فقالت: إن زوجى غائب وتركنى حاملا فرأيت فى المنام أن سارية بيتى انكسرت وإنى ولدت غلاما أعور، فقال: " خير يرجع زوجك إن شاء الله تعالى وتلدين غلاما بدا ... " فذكرت ذلك ثلاثا فجاءت ورسول الله صلى الله عليه وسلم غائب، فسألتها فأخبرتنى بالمنام، فقلت: لئن صدقت رؤياك ليموتن زوجك وتلدين غلاما فاجرا فقعدت تبكى فجاء رسول الله صلى الله عليه وسلم فقال: " مه يا عائشة إذا عبرتم للمسلم الرؤيا
فاعبروها على خير، فإن الرؤيا تكون على مايعبرها صاحبها ".
** دى
(فتح الباري ٤٣٢/١٢)
** إسناده حسن','7','196',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2972,'<span data-type="title" id=toc-2978>٢٩٧٢ </span>- عن عمر: أنه كتب إلى أبى موسى: فإذا رأى أحدكم رؤيا فقصها على أخيه فليقل: خير لنا وشر لأعدائنا.
** عب
(فتح الباري ٤٣٢/١٢)
** إسناده منقطع
** قال الحافظ فى " الفتح " ١٢ / ٤٣٢: رجاله ثقات ولكن سنده منقطع.','7','197',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2973,'<span data-type="title" id=toc-2979>٢٩٧٣ </span>- حديث ابن زمل الجهنى قال: كان النبى صلى الله عليه وسلم إذا صلى الصبح قال: هل رأى أحد منكم شيئا؟ ، قال ابن زمل: فقلت: أنا يارسول الله، قال: " خير
تلقاه وشرا تتوقاه وخير لنا وشر على أعدائنا والحمد لله رب العالمين اقصص رؤياك ".
** طب هق فى الدلائل
(فتح الباري ٤٣٢/١٢)
** إسناده ضعيف جدا','7','198',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2974,'<span data-type="title" id=toc-2980>٢٩٧٤ </span>- عن على قال: صلى بنا رسول الله صلى الله عليه وسلم يوما صلاة الفجر فجلس..... الحديث.
** ابن أبى حاتم
(فتح الباري ٤٤١/١٢)
** قال الحافظ فى " الفتح " ١٢ / ٤٤١: الراوى له عن زيد ضعيف.','7','199',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2975,'<span data-type="title" id=toc-2981>٢٩٧٥ </span>- عن أبى أمامة قال: خرج علينا رسول الله صلى الله عليه وسلم بعد صلاة الصبح فقال: " إنى رأيت الليلة رؤيا هى حق فاعقلوها.... " فذكر حديثا.
** طب
(فتح الباري ٤٤١/١٢)
** إسناده جيد','7','200',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2976,'<span data-type="title" id=toc-2982>٢٩٧٦ </span>- حديث عدى بن عميرة سمعت رسول الله صلى الله عليه وسلم يقول: " إن الله عز وجل لا يعذب العامة بعمل الخاصة حتى يروا المنكر بين ظهرانيهم وهم قادرون على أن ينكروه فإذا فعلوا ذلك عذب الله الخاصة والعامة ".
** حم
(فتح الباري ٤/١٣)
** إسناده حسن
** قال الحافظ فى " الفتح " ١٣ / ٤: عند أبى داود من حديث العرس بن عميرة وهو أخو عدى.','7','201',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2977,'<span data-type="title" id=toc-2983>٢٩٧٧ </span>- من فارق الجماعة شبرا فكأنما خلع ربقة الإسلام من عنقه.
** ت خز حب
(فتح الباري ٧/١٣)
** قال الحافظ فى " الفتح " ١٣ / ٧: أخرجه الترمذى وابن خزيمة وابن حبان وصححاه، وأخرجه البزار والطبرانى فى الأوسط من حديث ابن عباس وفى سنده خليد بن دعلج وفيه مقال.','7','202',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2978,'<span data-type="title" id=toc-2984>٢٩٧٨ </span>- حديث خالد بن الوليد: أن رجلا قال له: يا أبا سليمان اتق الله فإن الفتن ظهرت فقال: أما وابن الخطاب حى فلا، إنما تكون بعده فينظر الرجل فيفكر هل يجد مكانا لم ينزل به مثل مانزل بمكانه الذى هو به فى الفتنة والشر فلا يجد فتلك الأيام التى ذكرت رسول الله صلى الله عليه وسلم بين يدى الساعة أيام
الهرج.
** حم طب
(فتح الباري ١٥/١٣)
** إسناده حسن','7','203',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2979,'<span data-type="title" id=toc-2985>٢٩٧٩ </span>- عن حذيفة قال: يدرس الإسلام كما يدرس وشى الثوب حتى لا يدرى ماصيام ولا صلاة ولانسك ولا صدقة ويسرى على الكتاب من ليلة فلا يبقى فى الأرض منه آية ... الحديث.
** جه
(فتح الباري ١٦/١٣)
** إسناده قوى','7','204',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2980,'<span data-type="title" id=toc-2986>٢٩٨٠ </span>- عن عبد الله بن مسعود قال: ولينزعن القرآن من بين أظهركم يسرى عليه ليلا فيذهب فى أجواف الرجال فلا يبقى فى الأرض منه شىء.
** طب
(فتح الباري ١٦/١٣)
** إسناده صحيح','7','205',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2981,'<span data-type="title" id=toc-2987>٢٩٨١ </span>- عن ابن مسعود قال: ليس عام إلا والذى بعده شر منه.
** طب
(فتح الباري ٢٠/١٣)
** إسناده جيد','7','206',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2982,'<span data-type="title" id=toc-2988>٢٩٨٢ </span>- أمس خير من اليوم واليوم خير من غد وكذلك حتى تقوم الساعة.
** طب
(فتح الباري ٢٠/١٣)
** إسناده صحيح','7','207',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2983,'<span data-type="title" id=toc-2989>٢٩٨٣ </span>- عن عبد الله: لا يأتى عليكم عام إلا وهو شر من الذى قبله أما أنى لست أعنى عاما.
** دى
(فتح الباري ٢١/١٣)
** إسناده حسن','7','208',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2984,'<span data-type="title" id=toc-2990>٢٩٨٤ </span>- من أشار إلى أخيه بحديدة لعنته الملائكة.
** ت
(فتح الباري ٢٥/١٣)
** قال الحافظ فى " الفتح " ١٣ / ٢٥: أخرج الترمذى أصله موقوفا وقال: حسن غريب وكذا صححه أبو حاتم من هذا الوجه وقال من طريق ضمرة منكر.','7','209',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2985,'<span data-type="title" id=toc-2991>٢٩٨٥ </span>- عن جابر: نهى رسول الله صلى الله عليه وسلم أن يتعاطى السيف مسلولا.
** ت
(فتح الباري ٢٥/١٣)
** إسناده صحيح','7','210',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2986,'<span data-type="title" id=toc-2992>٢٩٨٦ </span>- عن أبى بكرة لعن الله من فعل هذا، إذا سل أحدكم سيفه فأراد أن يناوله أخاه فليغمده ثم يناوله إياه.
** حم طب
(فتح الباري ٢٥/١٣)
** إسناده جيد','7','211',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2987,'<span data-type="title" id=toc-2993>٢٩٨٧ </span>- عن حصين بن عبد الرحمن عن عمرو بن جاوان قال: قلت له أرأيت أعتزال الأحنف ماكان؟ قال: سمعت الأحنف قال: حججنا فإذا الناس يجتمعون فى وسط المسجد _ يعنى النبوى _ وفيهم على والزبير وطلحة وسعد إذ جاء عثمان ... فذكر قصة مناشدته لهم فى ذكر مناقبه، قال الأحنف: فلقيت طلحة والزبير فقلت: إنى لا أرى هذا الرجل _ يعنى عثمان _ إلا مقتولا فمن تأمرانى به؟ ، قال على: فقدمنا مكة فلقيت عائشة وقد بلغنا قتل عثمان، فقلت لها: من تأمرينى
به؟ ، قالت: على، قال: فرجعنا إلى المدينة فبايعت عليا ورجعت إلى البصرة
فبينما نحن كذلك إذ أتانى آت فقال: هذه عائشة وطلحة والزبير نزلوا بجانب
الخريبة يستنصرون بك، فأتيت عائشة فذكرتها بما قالت لى: ثم أتيت طلحة
والزبير فذكرتهما ... فذكر القصة وفيها قال: فقلت: والله لا أقاتلكم ومعكم أم المؤمنين وحوارى رسول الله صلى الله عليه وسلم ولا أقاتل رجلا
أمرتمونى ببيعته، فاعتزل القتال مع الفريقين.
** الطبرى
(فتح الباري ٣٤/١٣)
** إسناده صحيح','7','212',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2988,'<span data-type="title" id=toc-2994>٢٩٨٨ </span>- عن أبى هريرة قال: قال رسول الله صلى الله عليه وسلم: " كيف بك ياعبد الله بن عمرو إذا بقيت فى حثالة من الناس بقد مرجت عهودهم وأماناتهم واختلفوا فصاروا هكذا"، وشبك بين أصابعه، قال: فما تأمرنى؟ ، قال: " عليك بخاصتك ودع عنك عوامهم ".
** الطبرى
(فتح الباري ٣٨/١٣)
** قال الحافظ فى " الفتح " ١٣ / ٣٨: صححه ابن حبان.','7','213',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2989,'<span data-type="title" id=toc-2995>٢٩٨٩ </span>- حديث عبد الله بن عمرو: قالوا: كيف بنا يارسول الله قال: " تأخذون ماتعرفون ".
** طب
(فتح الباري ٣٩/١٣)
** إسناده صحيح','7','214',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2990,'<span data-type="title" id=toc-2996>٢٩٩٠ </span>- من طريق سعيد بن إياس بن سلمة أن أباه حدثه قال: قدم سلمة المدينة فلقيه بريدة بن الحصيب فقال: أرتددت عن هجرتك، فقال: معاذ الله، إنى فى أذن من رسول الله صلى الله عليه وسلم سمعته يقول: ابدوا يا أسلم _ أى القبيلة التى منها سلمة وبو برزة وبريدة المذكور _ قالوا: إنا نخاف أن يقدح ذلك فى هجرتنا قال: أنتم مهاجرون حيث كنتم.
** حم
(فتح الباري ٤١/١٣)
** إسناده حسن','7','215',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2991,'<span data-type="title" id=toc-2997>٢٩٩١ </span>- من رواية عمرو بن عبد الرحمن بن جرهد: سمعت رجلا يقول لجابر: من بقى من أصحاب رسول الله صلى الله عليه وسلم قال: أنس بن مالك وسلمة بن الأكوع، فقال الرجل: أما لمة فقد ارتد عن هجرته، فقال: لا تقل ذلك فإنى سمعت رسول الله صلى الله عليه وسلم يقول لأسلم: " ابدوا "، قالوا: إنا نخاف أن نرتد بعد هجرتنا، قال: " أنتم مهاجرون حيث كنتم ".
** حم
(فتح الباري ٤١/١٣)
** إسناده حسن','7','216',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2992,'<span data-type="title" id=toc-2998>٢٩٩٢ </span>- حديث على: لا تكروهوا الفتنة فى آخر الزمان فإنها تبير المنافقين.
** حل
(فتح الباري ٤٤/١٣)
** إسناده فيه ضعيف ومجهول','7','217',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2993,'<span data-type="title" id=toc-2999>٢٩٩٣ </span>- حديث شداد رفعه: " إذا وضع السيف من أمتى لم يرفع عنها إلى يوم القيامة ".
** الطبرى
(فتح الباري ٥٠/١٣)
** قال الحافظ فى " الفتح " ١٣ / ٥٠: صححه ابن حبان.','7','218',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2994,'<span data-type="title" id=toc-3000>٢٩٩٤ </span>- من طريق قيس بن أبى حازم قال: لما أقبلت عائشة فنزلت بعض مياه بنى عامر نبحت عليها الكلاب فقالت: أى ماء هذا؟ ، قالوا: الحوأب، قالت: ما أظننى إلا راجعة فقال لها بعض من كان معها: بل تقدمين فيراك المسلمون فيصلح الله ذات بينهم فقالت: إن النبى صلى الله عليه وسلم قال لنا ذات يوم: " كيف بإحداكن تنبح عليها كلاب الحوأب ".
** حم يع بز
(فتح الباري ٥٥/١٣)
** إسناده على شرط الصحيح
** قال الحافظ فى " الفتح " ١٣ / ٥٥: صححه ابن حبان والحاكم.','7','219',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2995,'<span data-type="title" id=toc-3001>٢٩٩٥ </span>- عن ابن عباس: أن رسول الله صلى الله عليه وسلم قال لنسائه: " أتيكنت صاحبة الجمل الأدبب تخرج تنبحها كلاب الحوأب يقتل عن يمينها وعن شمالها قتلى كثيرة وتنجو من بعد ماكادت ".
** بز
(فتح الباري ٥٥/١٣)
** رجاله ثقات','7','220',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2996,'<span data-type="title" id=toc-3002>٢٩٩٦ </span>- حديث ابن عباس قال: بلغ أصحاب على حين ساروا معه أن أهل البصرة اجتمعوا بطلحة والزبير فشق عليهم ووقع فى قلوبهم، فقال على: والذى لا إله غيره لنظرهرن على أهل البصرة ولنقتلن طلحة والزبير ... الحديث.
** طب
(فتح الباري ٥٥/١٣)
** قال الحافظ فى " الفتح " ١٣ / ٥٥: فى سنده إسماعيل بن عمرو البجلى فيه ضعف.','7','221',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2997,'<span data-type="title" id=toc-3003>٢٩٩٧ </span>- من طريق محمد بن قيس قال: ذكر لعائشة يوم الجمل فقالت: والناس يقولون يوم الجمل؟ ، قالوا: نعم، قالت: وددت أنى جلست كما جلس غيرى فكان أحب إلى من أن أكون ولدت من رسول الله صلى الله عليه وسلم عشرة كلهم مثل عبد الرحمن بن الحارث بن هشام.
** طب
(فتح الباري ٥٥/١٣)
** قال الحافظ فى " الفتح " ١٣ / ٥٥: فى سنده أبو معشر نجيح المدنى وفيه ضعف.','7','222',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2998,'<span data-type="title" id=toc-3004>٢٩٩٨ </span>- حديث أبى رافع أن رسول الله صلى الله عليه وسلم قال لعلى بن أبى طالب: " أنه سيكون بنيك وبين عائشة أمر "، قال: فأنا أشقاهم يارسول الله؟ ، قال: " لا ولكن إذا كان ذلك فارددها إلى مأمنها ".
** حم بز
(فتح الباري ٥٥/١٣)
** إسناده حسن','7','223',NULL,NULL,'0');
INSERT INTO [b88] VALUES (2999,'<span data-type="title" id=toc-3005>٢٩٩٩ </span>- عن عبد الرحمن بن أبزى قال: انتهى عبد الله بن بديل بن ورقاء الخزاعى إلى عائشة يوم الجمل وهى فى الهودج فقال: يا أم المؤمنين أتعلمين أنى أتيك عندما قتل عثمان فقلت ماتأمرينى، فقلت: الزم عليا؟ ، فسكتت فقال: اعقروا الجمل فعقروه، فنزلت أنا وأخوها محمد فاحتملنا هودجها فوضعناه بين يدى عليا فأمر بها فأدخلت بيتا.
** شب
(فتح الباري ٥٧/١٣)
** إسناده جيد','7','224',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3000,'<span data-type="title" id=toc-3006><span data-type="title" id=toc-3007>٣٠٠٠ </span></span>- عن زيد بن وهب قال: فكف على يده حتى بدءوه بالقتال فقاتلهم بعد الظهر فما غربت الشمس وحول الجمل أحد، فقال على: لا تتموا جريحا ولا تقتلوا مدبرا ومن أغلق بابه وألقى سلاحه فهو آمن.
** شب
(فتح الباري ٥٧/١٣)
** إسناده صحيح','7','225',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3001,'<span data-type="title" id=toc-3008>٣٠٠١ </span>- عن علقمة قال: قلت للأشتر: قد كنت كارها لقتل عثمان فكيف قاتلت يوم الجمل قال: إن هؤلاء بايعوا عليا ثم نكثوا عهده، وكان الزبير هو الذى حرك عائشة على الخروج فدعوت الله أن يكفينيه فلقينى كفه بكفه فما رضيت لشدة ساعدى أن قمت فى الركاب فضربته على رأسه ضربة فصرعته.
** الطبرى
(فتح الباري ٥٧/١٣)
** إسناده صحيح','7','226',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3002,'<span data-type="title" id=toc-3009>٣٠٠٢ </span>- عن زيد بن وهب قال: أقبل طلحة والزبير حتى نزلا البصرة فقبضا على عامل على عليها ابن حنيف وأقبل على حتى نزل بذى قار، فأرسل عبد الله بن عباس إلى الكوفة فابطؤا عليه فأرسل إليهم عمارا فخرجوا إليه.
** شب
(فتح الباري ٥٨/١٣)
** إسناده صحيح','7','227',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3003,'<span data-type="title" id=toc-3010>٣٠٠٣ </span>- عن أبى يزيد المدينى قال: قال عمار بن ياسر لعائشة لما فرغوا من الجمل: ما أبعد هذا المسير من العهد الذى عهد إليكم _ يشير إلى قوله تعالى (وقرن فى بيوتكن) _ فقالت: أبو اليقظان، قال: نعم، قالت: والله إنك ماعلمت لقوال بالحق، قال: الحمد لله الذى قضى لى على لسانك.
** الطبرى
(فتح الباري ٥١/١٣)
** إسناده صحيح','7','228',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3004,'<span data-type="title" id=toc-3011>٣٠٠٤ </span>- حديث أبى بكر الصديق: سمع رسول الله صلى الله عليه وسلم يقول: " إن الناس إذا رأوا المنكر فلم يغيروه أوشك أن يعمهم الله بعقاب ".
** د ت ن جه
(فتح الباري ٦٠/١٣)
** قال الحافظ فى " الفتح " ١٣ / ٦٠: أخرجه الأربعة وصححه ابن حبان.','7','229',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3005,'<span data-type="title" id=toc-3012>٣٠٠٥ </span>- عن الزهرى قال: جعل على على مقدمة أهل العراق قيس بن سعد بن عبادة وكانوا أربعين ألفا بايعوه على الموت فقتل على: فبايعوا الحسن بن على بالخلافة، وكان لا يحب القتال ولكن كان يريد أن يشترط على معاوية لنفسه، فعرف أن قيس بن سعد لا يطاوعه على الصلح فنزعه وأمر عبد الله بن عباس فاشترط لنفسه كما اشترط الحسن.
** الطبرى
(فتح الباري ٦٣/١٣)
** إسناده صحيح','7','230',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3006,'<span data-type="title" id=toc-3013>٣٠٠٦ </span>- عن أبى بصرة أنه سمع الحسن بن على يقول فى خطبته عند معاوية: إنى أشترط على معاوية لنفسى الخلافة بعده.
** محمد بن قدامة فى كتاب الخوارج
(فتح الباري ٦٥/١٣)
** إسناده قوى','7','231',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3007,'<span data-type="title" id=toc-3014>٣٠٠٧ </span>- عن الزهرى: كاتب الحسن بن على معاوية واشترط لنفسه فوصلت الصحيفة لمعاوية وقد أرسل إلى الحسن يسأله الصلح ومع الرسول صحيفة بيضاء مختوم فى أسفلها وكتب إلأيه أن اشترط ماشئت فهو لك، فاشترط الحسن أضعاف ماكان سأل أولا، فلما ألتقيا وبايعه الحسن سأله أن يعطيه ما أشترط فى السجل الذى ختم معاوية فى أسفله فتمسك معاوية إلا ماكان الحسن سأله أولا، واحتج بأنه أجاب سؤاله أول ماوقف عليه فاختلفا فى ذلك فلم ينفذ للحسن من الشرطين شىء.
** يعقوب بن سفيان
(فتح الباري ٦٥/١٣)
** إسناده صحيح','7','232',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3008,'<span data-type="title" id=toc-3015>٣٠٠٨ </span>- عن جويرية بن أسماء: سمعت أشياخ أهل المدينة يتحدثون أن معاوية لما أحتضر دعا يزيد فقال له: إن لك فى أهل المدينة يوما، فإن فعلوا فارمهم بمسلم بن عقبة فإنى عرفت نصيحته فلما ولى يزيد وفد عليه عبد الله بن حنظلة وجماعة فأكرمهم وأجازهم، فرجع فحرض الناس على يزيد وعابه ودعاهم إلى خلع يزيد فأجابوه، فبلغ يزيد فجهزظ إليهم مسلم بن عقبة، فاستقبلهم أهل المدينة بجموع كثيرة فهابهم أهل الشام وكرهوا قتالهم، فلما نشب القتال سعوا فى جوف المدينة
التكبير، وذلك أن بنى حارثة أدخلوا قوما فى الشاميين فى جانب الخندق فترك أهل المدينة القتال ودخلوا المدينة خوفا على أهلهم فكانت الهزيمة وقتل من قتل وبايع مسلم الناس على أنهم خوى ليزيد يحكم فى دمائهم وأموالهم وأهلهم بما شاء.
** أبو بكر بن أبى خثيمة
(فتح الباري ٧٠/١٣)
** إسناده صحيح','7','233',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3009,'<span data-type="title" id=toc-3016>٣٠٠٩ </span>- عن بن عباس قال: جاء تأويل هذه الآية على رأس ستين سنة (ولو دخلت عليهم فى أقطارها ثم سئلوا لفتنة لآتوها) .
** يعقوب بن سفيان فى تارخه
(فتح الباري ٧١/١٣)
** إسناده صحيح','7','234',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3010,'<span data-type="title" id=toc-3017>٣٠١٠ </span>- من مرسل أبى العالية: الآيات كلها فى ستة أشهر.
** حم
(فتح الباري ٧٧/١٣)
** مرسل','7','235',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3011,'<span data-type="title" id=toc-3018>٣٠١١ </span>- حديث أنس: إن أمام الدجال سنون خداعات يكذب فيها الصادق ويصدق فيها الكاذب ويخون فيها الأمين ويؤتمن فيها الخائن ويتكلم فيها الرويبضة.
** حم يع بز
(فتح الباري ٨٤/١٣)
** إسناده جيد','7','236',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3012,'<span data-type="title" id=toc-3019>٣٠١٢ </span>- حديث عبد الله بن عمرو: لا تقوم الساعة حتى يتساقد فى الطريق تسافد الحمر.
** بز طب
(فتح الباري ٨٤/١٣)
** قال الحافظ فى " الفتح " ١٣ / ٨٤: صححه ابن حبان والحاكم.','7','237',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3013,'<span data-type="title" id=toc-3020>٣٠١٣ </span>- حديث أنس: لا تقوم الساعة حتى لا يقال فى الأرض لا إله إلا الله.
** حم
(فتح الباري ٨٥/١٣)
** إسناده قوى','7','238',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3014,'<span data-type="title" id=toc-3021>٣٠١٤ </span>- عن عبد الله بن عمر: لا تقوم الساعة حتى يأخذ الله شرعته من أهل الأرض فيبقى عجاج لا يعرفون معروفا ولا ينكرون منكرا.
** حم
(فتح الباري ٨٥/١٣)
** إسناده جيد','7','239',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3015,'<span data-type="title" id=toc-3022>٣٠١٥ </span>- عن زيد بن وهب قال: كنا عند حذيفة فقال: كيف أنتم وقد خرج أهل دينكم يضرب بعضهم وجوه بعض بالسيف، قالوا: كيف تأمرنا؟ ، قال: انظروا العزقة التى تدعو إلى أمر على فالزمها فإنها على الحق.
** بز
(فتح الباري ٨٥/١٣)
** إسناده جيد','7','240',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3016,'<span data-type="title" id=toc-3023>٣٠١٦ </span>- عن الزهرى قال: لما بلغ معاوية غلبة على على أهل الجمل دعا إلى الطلب بدم عثمان فأجابه أهل الشام فسار إليه على فالتقيا بصفين.
** يعقوب بن سفيان
(فتح الباري ٨٥/١٣)
** إسناده جيد','7','241',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3017,'<span data-type="title" id=toc-3024>٣٠١٧ </span>- عن أبى مسلم الخولانى أنه قال لمعاوية: أنت تنازع عليا فى الخلافة أو أنت مثله؟ ، قال: لا، وإنى لأعلم أنه أفضل منى وأحق بالأمر ولكن ألستم تعلمون أن عثمان قتل مظلوما وأنا ابن عمه ووليه أطلب بدمه؟ ، فأتوا عليا فقولوا له يدفع لنا قتلة عثمان فأتوه فكلموه فقال: يدخل فى البيعة ويحاكمهم
إلى.
** كتاب صفين ليحيى بن سليمان الجعفى
(فتح الباري ٨٦/١٣)
** إسناده جيد','7','242',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3018,'<span data-type="title" id=toc-3025>٣٠١٨ </span>- عن أبى الرضا سمعت عمارا يوم صفين يقول: من سره أن يكتنفه الحور العين فليتقدم بين الصفين محتسبا.
** شب
(فتح الباري ٨٦/١٣)
** إسناده صحيح','7','243',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3019,'<span data-type="title" id=toc-3026>٣٠١٩ </span>- حديث ثوبان: وأنه سيكون فى أمتى كذابون ثلاثون كلهم يزعم أنه نبى وأنا خاتم النبيين لانبى بعدى.
** د ت
(فتح الباري ٨٦/١٣)
** قال الحافظ فى " الفتح " ١٣ / ٨٦: صححه ابن حبان.','7','244',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3020,'<span data-type="title" id=toc-3027>٣٠٢٠ </span>- من رواية عبد الله بن عمرو: لا تقوم الساعة حتى يخرج سبعون كذابا.
** طب
(فتح الباري ٨٧/١٣)
** إسناده ضعيف','7','245',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3021,'<span data-type="title" id=toc-3028>٣٠٢١ </span>- عن حذيفة: " سيكون فى أمتى كذابون دجالون سبعة وعشرون منهم أربعة نسوة وإنى خاتم النبيين لا نبى بعدى ".
** حم
(فتح الباري ٨٧/١٣)
** إسناده جيد','7','246',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3022,'<span data-type="title" id=toc-3029>٣٠٢٢ </span>- عن ابن مسعود قال: لما كان ليل أسرى برسول الله صلى الله عليه وسلم لقى إبراهيم وموسى وعيسى فتذاكروا الساعة فبدؤا بإبراهيم فسألوه عنها، فلم يكن عنده منها علم، ثم سألوا موسى فلم يكن عنده منها علم، فرد الحديث إلى عيسى فقال: قد عهد إلى فيما دون وجبتها فإما وجبتها فلا يعلمها إلا الله ... فذكر خروج الدجال، قال: فأنزل إليه فاقتله ثم ذكر خروج يأجوج ومأجوج ثم دعاءه بموتهم ثم بإرسال المطر فيلقى جيفهم فى البحر ثم تنسف الجبال وتمد الأرض مد الأديم، فعهد إلى إذا كان ذلك كانت الساعة من الناس كالحامل المتم لا يدرى أهلها متى يفجؤهم بولادتها ليلا كان أو نهارا.
** جه حم
(فتح الباري ٨٩/١٣)
** قال الحافظ فى " الفتح " ١٣ / ٨٩: صححه الحاكم.','7','247',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3023,'<span data-type="title" id=toc-3030>٣٠٢٣ </span>- من طريق سليمان بن شهاب قال: نزل على عبد الله بن المعتمر وكان صحابيا فحدثنى عن النبى صلى الله عليه وسلم أنه قال: " الدجال ليس به خفاء يجىء من قبل المشرق فيدعو إلى الدين فيتبع ويظهر فلا يزال حتى يقدم الكوفة فيظهر الدين ويعمل به فيتبع ويحث على ذلك ثم يدعى أنه نبى فيفزع من ذلك كل ذى لب ويفارقه فيمكث بعد ذلك فيقول: أنا الله فتغشى عينه وتقطع أذنه ويكتب بين عينيه كافر فلا يخفى على كل مسلم فيفارقه كل أحد من الخلق فى قلبه مثقال حبة من خردل من إيمان ".
** طب
(فتح الباري ٩١/١٣)
** إسناده ضعيف','7','248',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3024,'<span data-type="title" id=toc-3031>٣٠٢٤ </span>- عن أبى هريرة رفعه: " ثلاثة إذاخرجن لم ينفع نفسا إيمانها لم تكن آمنت من قبل: الدجال والدابة وطلوع الشمس من مغربها ".
** ت
(فتح الباري ٩١/١٣)
** قال الحافظ فى " الفتح " ١٣ / ٩١: صححه الترمذى.','7','249',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3025,'<span data-type="title" id=toc-3032>٣٠٢٥ </span>- عن حسان بن عطية قال: لا ينجو من فتنة الدجال إلا اثنا عشر ألف رجل وسبعة آلاف امرأة.
** حل
(فتح الباري ٩٢/١٣)
** إسناده حسن صحيح
** قال الحافظ فى " الفتح " ١٣ / ٩٢: هذا لا يقال من قبل الرأى فيحتمل أن يكون مرفوعا أرسله ويحتمل أن يكون أخذه عن بعض أهل الكتاب.','7','250',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3026,'<span data-type="title" id=toc-3033>٣٠٢٦ </span>- عن مجاهد قال: انطلقنا إلى رجل من الأنصار فقلت: حدثنا بما سمعت من رسول الله صلى الله عليه وسلم فى الدجال ولا تحدثنا عن غيره ... فذكر حديثا فيه " تمطر الأرض ولا ينبت الشجر ومعه جنة ونار فناره جنة وجنته نار ومعه جبل خبز ".
** حم هق فى البعث
(فتح الباري ٩٣/١٣)
** رجاله ثقات','7','251',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3027,'<span data-type="title" id=toc-3034>٣٠٢٧ </span>- عن أبى هريرة: سمعت أبا القاسم الصادق المصدوق يقول: " يخرج مسيح الضلالة فيبلغ ماشاء الله أن يبلغ من الأرض فى أربعين يوما فيلقى المؤمنون منه شدة شديدة ... " الحديث.
** بز
(فتح الباري ١٠٠/١٣)
** إسناده جيد','7','252',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3028,'<span data-type="title" id=toc-3035>٣٠٢٨ </span>- عن أبى سعيد رفعه فى صفة عين الدجال وفيه: " ومعه مثل الجنة والنار، وبين يديه رجلان نينذران أهل القرى كلما خرجا من قرية دخل أوائله ".
** منيع
(فتح الباري ١٠٢/١٣)
** إسناده ضعيف','7','253',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3029,'<span data-type="title" id=toc-3036>٣٠٢٩ </span>- حديث جنادة بن أبى أمية: أتينا رجل من الأنصار من الصحابة قال: قام فينا رسول الله صلى الله عليه وسلم فقال: " أنذركم المسيح.... " وفيه: " يمكث فى الأرض أربعين صباحا يبلغ سلطانه كل منهل لا يأتى أربعة مساجد: الكعبة ومسجد الأقصى والطور ".
** حم
(فتح الباري ١٠٥/١٣)
** رجاله ثقات','7','254',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3030,'<span data-type="title" id=toc-3037>٣٠٣٠ </span>- حديث حذيفة رفعه: " يأجوج أمة ومأجوج أمة كل أمة أربعمائة ألف لا يموت الرجل منهم حتى ينظر إلى ألف ذكر من صلبه كلهم قد حمل السلاح ".
** عد ابن أبى حاتم طس ابن مردويه
(فتح الباري ١٠٦/١٣)
** قال الحافظ فى " الفتح " ١٣ / ١٠٦: هو من رواية يحيى بن سعيد العطار عن محمد بن أسحق عن الأعمش والعطار ضعيف جدا ومحمد بن أسحق، قال ابن عدى: ليس هو صاحب المغازى بل هو العكاشى، قال: والحديث موضوع، قال ابن أبى حاتم: منكر.أه. قلت: لكن لبعضه شاهد صحيح.','7','255',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3031,'<span data-type="title" id=toc-3038>٣٠٣١ </span>- من طريق أبى هريرة رفعه: " ولد لنوح سام وحام ويافث، فولد لسام العرب وفارس والروم، وولد لحام القبط والبربر والسودان، وولد ليافث يأجوج ومأجوج والترك والصقاليه ".
** ابن أبى حاتم
(فتح الباري ١٠٧/١٣)
** إسناده ضعيف','7','256',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3032,'<span data-type="title" id=toc-3039>٣٠٣٢ </span>- عن أبى هريرة رفعه فى السد: " يحفرونه كل يوم حىى إذا كادوا يخرقونه قال الذى عليهم: ارجعوا فستخرقونه غدا فيعيده الله كأشد ماكان، حتى إذا بلغ مدتهم وأراد الله أن يبعثهم قال الذى عليهم ارجعوا فستخرقونه غدا إن شاء الله واستثنى، قال: فيرجعون فيجدونه كهيئة حين تركوه فيخرقونه فيخرجون على الناس.... " الحديث.
** ت حب ك
(فتح الباري ١٠٨/١٣)
** قال الحافظ فى " الفتح " ١٣ / ١٠٨: أخرجه الترمذى وحسنه، وابن حبان والحاكم وصححاه من طريق قتادة عن أبى
رافع عن أبى هريرة رفعه، أخرجه الترمذى والحاكم من رواية أبى عوانة وعبد بن حميد من رواية حماد بن سلمة وابن حبان من رواية سليمان التيمى كلهم عن
قتادة ورجاله رجال الصحيح إلا أن قتادة مدلس، وقد رواه بعضهم عنه فادخل
بينهما واسطة، أخرجه ابن مردويه لكن وقع التصريح فى رواية سليمان التيمى عن
قتادة بأن أبا رافع حدثه وهو فى صحيح ابن حبان.','7','257',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3033,'<span data-type="title" id=toc-3040>٣٠٣٣ </span>- حديث ابن عمر فى آخره: فأعدوا للمسألة جوابا، قالوا: وماجوابها؟ ، قال: أعمال البر.
** عد طس
(فتح الباري ١١٣/١٣)
** إسناده حسن','7','258',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3034,'<span data-type="title" id=toc-3041>٣٠٣٤ </span>- عن أنس: إن الله سائل كل راع عما استرعاه حفظ ذلك أو ضيعه.
** عد
(فتح الباري ١١٣/١٣)
** إسناده صحيح','7','259',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3035,'<span data-type="title" id=toc-3042>٣٠٣٥ </span>- حديث أبى بكر الصديق: " الأئمة من قريش ".
** حم
(فتح الباري ١١٤/١٣)
** إسناده منقطع
** بقال الحافظ فى " الفتح " ١٣ / ١١٤: رجاله رجال الصحيح لكن فى سنده انقطاع.','7','260',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3036,'<span data-type="title" id=toc-3043>٣٠٣٦ </span>- حديث ابن مسعود رفعه: " يامعشر قريش أنكم أهل هذا الأمر مالم تحدثوا فإذا غيرتم بعث الله عليكم من يلحاكم كما يلحى القضيب ".
** حم يع
(فتح الباري ١١٦/١٣)
** قال الحافظ فى " الفتح " ١٣ / ١١٦: رجاله ثقات إلا أنه من رواية عبيد الله بن عبد الله بن عتبة ابن مسعود عن عم أبيه عبد الله بن مسعود ولم يدركه.','7','261',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3037,'<span data-type="title" id=toc-3044>٣٠٣٧ </span>- عن أبى مسعود الأنصارى: لا يزال هذا الأمر فيكم وأنتم ولاته ... الحديث.
** حم
(فتح الباري ١١٦/١٣)
** قال الحافظ فى " الفتح " ١٣ / ١١٦: فى سماع عبيد الله من أبى مسعود نظر مبنى على الخلاف فى سنة وفاته.','7','262',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3038,'<span data-type="title" id=toc-3045>٣٠٣٨ </span>- من مرسل عطاء بن يسار: قال لقريش: أنتم أولى الناس بهذا الأمر ماكنتم الحق، إلا أن تعدلوا عنه فتلحون كما تلحى هذه الجريدة.
** شفع هق
(فتح الباري ١١٦/١٣)
** إسناده صحيح','7','263',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3039,'<span data-type="title" id=toc-3046>٣٠٣٩ </span>- حديث ثوبان رفعه: " استقيموا لقريش مااستقاموا لكم، فإن لم تستقيموا فضعوا سيوفكم على عواتقكم فأبيدوا خضراءهم فإن لم تفعلوا فكونوا زراعين أو أشقياء ".
** طيا طب
(فتح الباري ١١٦/١٣)
** منقطع
** قال الحافظ فى " الفتح " ١٣ / ١١٦: رجاله ثقات إلا أن فيه انقطاعا لأن راويه سالم بن أبى الجعد لم يسمع من ثوبان','7','264',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3040,'<span data-type="title" id=toc-3047>٣٠٤٠ </span>- حديث ذى مخبر وهو ابن أخر النجاشى عن النبى صلى الله عليه وسلم قال: " كان هذا الأمر فى حمير فنزعه الله منهم وصبره فى قريش وسيعود إليهم ".
** حم
(فتح الباري ١١٦/١٣)
** إسناده جيد','7','265',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3041,'<span data-type="title" id=toc-3048>٣٠٤١ </span>- عن عمر أنه قال: إن أدركنى أجلى وأبو عبيدة حى استخلفته.
** حم
(فتح الباري ١١٩/١٣)
** إسناده رجاله ثقات','7','266',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3042,'<span data-type="title" id=toc-3049>٣٠٤٢ </span>- حديث عبد الله بن أبى أوفى رفعه: " الله مع القاضى مالم يجر ".
** ابن المنذر جه ت
(فتح الباري ١٢٠/١٣)
** قال الحافظ فى " الفتح " ١٣ / ١١٩: استغربه الترمذى، وصححه ابن حبان والحاكم.','7','267',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3043,'<span data-type="title" id=toc-3050>٣٠٤٣ </span>- أن أبا بكر لما ولى الخلافة: ولى عمر القضاء.
** هق
(فتح الباري ١٢١/١٣)
** إسناده قوى','7','268',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3044,'<span data-type="title" id=toc-3051>٣٠٤٤ </span>- أن عمر استعمل عبد الله بن مسعود على القضاء.
** هق
(فتح الباري ١٢١/١٣)
** إسناده قوى','7','269',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3045,'<span data-type="title" id=toc-3052>٣٠٤٥ </span>- حديث عمران بن حصين والحكم بن عمرو الغفارى: لا طاعة فى معصية الله.
** حم بز
(فتح الباري ١٢٣/١٣)
** إسناده قوى','7','270',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3046,'<span data-type="title" id=toc-3053>٣٠٤٦ </span>- عن أنس رفعه: " من طلب القضاء واستعان عليه بالشفعاء وكل إلى نفسه، ومن أكره عليه أنزل الله عليه ملكا يسدده ".
** ابن المنذر ت د جه
(فتح الباري ١٢٤/١٣)
** قال الحافظ فى " الفتح " ١٣ / ١٢٤: أخرجه الترمذى من طريق أبى عوانة عن عبد الأعلى الثعلبى، وأخرجه هو وأبو داود وابن ماجه من طريق أبى عوانة ومن طريق إسرائيل عن عبد الأعلى فأسقط خثيمة من السند، قال الترمذى: ورواية أبى عوانة أصح، وقال فى رواية أبى عوانة حديث: حسن غريب، وأخرجه الحاكم من طريق إسرائيل وصححه وتعقب بأن ابن معين لين خثيمة وضعف عبد الأعلى وكذا قال الجمهور فى عبد الأعلى: ليس بقوى.','7','271',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3047,'<span data-type="title" id=toc-3054>٣٠٤٧ </span>- عن عوف بن مالك: أولها ملامة وثانيها ندامة وثالثها عذاب يوم القيامة إلا من عدل.
** بز طب
(فتح الباري ١٢٥/١٣)
** إسناده صحيح','7','272',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3048,'<span data-type="title" id=toc-3055>٣٠٤٨ </span>- عن أبى مريم الأسدى أنه قال لمعاوية: سمعت رسول الله صلى الله عليه وسلم يقول: " من ولاه الله من أمر الناس شيئا فاحتجب عن حاجتهم أحتجب الله عن حاجته يوم القيامة ".
** د ت
(فتح الباري ١٣٣/١٣)
** إسناده جيد','7','273',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3049,'<span data-type="title" id=toc-3056>٣٠٤٩ </span>- عن أبى سعيد رفعه: " لا يقض القاضى إلا وهو شبعان ريان ".
** هق
(فتح الباري ١٣٧/١٣)
** إسناده ضعيف','7','274',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3050,'<span data-type="title" id=toc-3057>٣٠٥٠ </span>- عن الشعبى قال: من سره أن يأخذ بالوثيقة من القضاء فليأخذ بقضاء عمر فإنه كان يستشير.
** يعقوب بن سفيان
(فتح الباري ١٤٩/١٣)
** إسناده جيد','7','275',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3051,'<span data-type="title" id=toc-3058>٣٠٥١ </span>- من طريق حارثة بن مضرب قال: قال عمر: إنى أنزلت نفسى من مال الله بمنزلة قيم اليتيم، إن استغنيت عنه تركت وإن افتقرت إليه أكلت بالمعروف.
** شب سعد
(فتح الباري ١٥١/١٣)
** إسناده صحيح','7','276',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3052,'<span data-type="title" id=toc-3059>٣٠٥٢ </span>- عن الأحنف قال: كنا بباب عمر _ فذكر قصة فيها _ فقال عمر: أنا أخبركم بما أستحل: ما أحج عليه وأعتمر وحلتى الشتاء والقيظ وقوتى وقوت عيالى كرجل من قريش ليس بأعلاهم ولا أسفلهم.
** الكرابيسى
(فتح الباري ١٥١/١٣)
** إسناده صحيح','7','277',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3053,'<span data-type="title" id=toc-3060>٣٠٥٣ </span>- من طريق طارق بن شهاب قال: أتى عمر بن الخطاب برجل فى حد فقال: أخرجاه من المسجد ثم اضرباه.
** شب عب
(فتح الباري ١٥٧/١٣)
** إسناده على شرط الشيخين','7','278',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3054,'<span data-type="title" id=toc-3061>٣٠٥٤ </span>- من طريق ابن معقل: أن رجلا جاء إلى عرم فسأءه فقال: ياقنبر أخرجه من المسجد فاقم عليه الحد.
** شب
(فتح الباري ١٥٧/١٣)
** إسناده فيه من فيه مقال
** تعقيب: أورد الحافظ هذا الأثر فى الفتح ١٣ / ١٥٧: بلفظ أن رجلا جاء إلى عمر فساءه إلخ ولكن الأثر عن على رضى الله عنه وقد أورده فى التعليق (٥ / ٢٩٧) فقال: وأما أثر على فقال ابن أبى شيبة: ثنا خالد عن أشعث عن فضيل عن ابن معقل: أن رجلا جاء إلى على فساره فقال: ياقنبر أخرجه من المسجد فأقم عليه الحد.','7','279',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3055,'<span data-type="title" id=toc-3062>٣٠٥٥ </span>- " جنبوا مساجدكم صبيانكم " الحديث.
** جه
(فتح الباري ١٥٧/١٣)
** إسناده ضعيف','7','280',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3056,'<span data-type="title" id=toc-3063>٣٠٥٦ </span>- حديث ابن عمر رفعه: " خصال لا تنبغى فى المسجد: لا يتخذ طريقا " ... الحديث وفيه: " ولا يضرب فيه حد ".
** جه
(فتح الباري ١٥٧/١٣)
** إسناده ضعيف','7','281',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3057,'<span data-type="title" id=toc-3064>٣٠٥٧ </span>- عن أبى عثمان النهدى: أن عثمان بن عفان أجاب عبدا للمغيرة بن شعبة دعاه وهو صائم فقال: أردت أن أجيب الداعى وأدعو بالبركة.
** زوائد البر والصلة لابن المبارك
(فتح الباري ١٦٤/١٣)
** إسناده صحيح','7','282',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3058,'<span data-type="title" id=toc-3065>٣٠٥٨ </span>- عن أبى هريرة رفعه: " ويل للأمراء ويل للعرفاء ".
** حم
(فتح الباري ١٦٩/١٣)
** قال الحافظ فى " الفتح " ١٣ / ١٦٩: صححه ابن خزيمة.','7','283',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3059,'<span data-type="title" id=toc-3066>٣٠٥٩ </span>- حديث على رفعه: " لا تقضى لأحد الخصمين حتى تسمع من الآخر ".
** د ت
(فتح الباري ١٧١/١٣)
** حسن','7','284',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3060,'<span data-type="title" id=toc-3067>٣٠٦٠ </span>- " كفى بك إثما أن لا تزال مخاصما ".
** طب
(فتح الباري ١٨١/١٣)
** إسناده ضعيف','7','285',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3061,'<span data-type="title" id=toc-3068>٣٠٦١ </span>- عن عبد الله بن الزبير أن النبى صلى الله عليه وسلم استكتب عبد الله بن الأرقم، فكان يكتب له إلى الملوك فبلغ من أمانته عنده أنه كان يأمره أن يكتب ويختم ولا يقرؤه، ثم استكتب زيد بن ثابت فكان يكتب الوحى ويكتب إلى الملوك وكان إذا غابا كتب جعفر بن أبى طالب وكتب له أحيانا جماعة من الصحابة.
** هق
(فتح الباري ١٨٤/١٣)
** إسناده حسن','7','286',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3062,'<span data-type="title" id=toc-3069>٣٠٦٢ </span>- عن زيد قال: أتى بى النبى صلى الله عليه وسلم مقدمة المدينة فأعجب بى فقيل له: هذا غلام من بنى النجار قد قرأ فيما أنزل الله عليك بضع عشرة سورة فأستقرئنى فقرأت (ق) فقال لى: " تعلم كتاب يهود، فإنى ما أمن يهودى على كتابى " فتعلمته فى نصف شهر حتى كتب له إلى يهود وأقرأ له إذا كتبوا إليه.
** تخ
(فتح الباري ١٨٦/١٣)
** قال الحافظ فى " الفتح " ١٣ / ١٨٦: وأخرجه أبو داود والترمذى من رواية عبد الرحمن بن أبى الزناد، قال الترمذى
حسن صحيح.','7','287',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3063,'<span data-type="title" id=toc-3070>٣٠٦٣ </span>- عن ابن عمر قال: دخل الرهط على عمر قبل أن ينزل به، فسمى السئة.. فذكر قصة إلى أن قال: فإنما الأمر إلى ستة: إلى عبد الرحمن وعثمان وعلى والزبير وطلحة وسعد.
** سعد
(فتح الباري ١٩٦/١٣)
** إسناده صحيح','7','288',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3064,'<span data-type="title" id=toc-3071>٣٠٦٤ </span>- عن أبى وائل قال: قلت لعبد الرحمن بن عوف كيف بايعتم عثمان وتركتم عليا فقال: ماذنبى بدأت بعلى فقلت له: أبايعلك على كتاب الله وسنة رسوله، وسيرة أبى بكر وعمر فقال: فيما استطعت وعرضتها على عثمان فقبل.
** عم
(فتح الباري ١٩٧/١٣)
** قال الحافظ فى " الفتح " ١٣ / ١٩٧: عن سفيان بن وكيع عن أبى بكر بن عياش عنه وسفيان بن وكيع ضعيف.','7','289',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3065,'<span data-type="title" id=toc-3072>٣٠٦٥ </span>- عن حذيفة قال: قال لى عمر: من ترى قومك يؤمرون بعدى، قال: قلت: قد نظر الناس إلى عثمان وشهروه لها.
** يعقوب بن شبة فى مسنده
(فتح الباري ١٩٨/١٣)
** إسناده صحيح
** قال الحافظ فى " الفتح " ١٣ / ١٩٨: من طريق صحيح إلى حذيفة.','7','290',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3066,'<span data-type="title" id=toc-3073>٣٠٦٦ </span>- عن حارثة بن مضرب حججت مع عمر فكان الحادى يحدو إن الأمير بعده عثمان بن عفان.
** البغوى فى معجمه خيثمة فى فضائل الصحابة
