INSERT INTO [b88] VALUES (3068,'<span data-type="title" id=toc-3075>٣٠٦٨ </span>- " من أعطى بيعة ثم نكثها لقى الله وليست معه يمينه ".
** طب
(فتح الباري ٢٠٥/١٣)
** إسناده جيد','7','293',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3069,'<span data-type="title" id=toc-3076>٣٠٦٩ </span>- عن قيس بن أبى حازم: رأيت عمر يجلس الناس ويقول: اسمعوا لخليفة رسول الله صلى الله عليه وسلم.
** الطبرى
(فتح الباري ٢٠٨/١٣)
** إسناده صحيح','7','294',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3070,'<span data-type="title" id=toc-3077>٣٠٧٠ </span>- حديث سفينة: الخلافة بعدى ثلاثون سنة.
** د ت ن جه
(فتح الباري ٢١٢/١٣)
** قال الحافظ فى " الفتح " ١٣ / ٢١٢: صححه ابن حبان.','7','295',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3071,'<span data-type="title" id=toc-3078>٣٠٧١ </span>- حديث ابن مسعود: أنه سئل كم يملك هذه الأمة من خليفة؟ ، فقال: سألت عنها رسول الله صلى الله عليه وسلم فقال: " اثنا عشر كعدة نقباء بنى إسرائيل ".
** حم بز
(فتح الباري ٢١٢/١٣)
** إسناده حسن','7','296',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3072,'<span data-type="title" id=toc-3079>٣٠٧٢ </span>- " مؤمن قوى خير وأحب " وفيه: " فقل قدر الله وما شاء صنع ".
** ن الطبرى
(فتح الباري ٢٢٧/١٣)
** قال الحافظ فى " الفتح " ١٣ / ٢٢٧: من طريق فضيل بن سليمان عن ابن عجلان فادخل بينه وبين الأعرج أبا الزناد قال النسائى: فضيل بن سليمان ليس بقوى.','7','297',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3073,'<span data-type="title" id=toc-3080>٣٠٧٣ </span>- حديث المقدام: ماملأ ابن آدم وعاء شرا من بطنه.
** د ت ن جه
(فتح الباري ٢٤٨/١٣)
** قال الحافظ فى " الفتح " ١٣ / ٢٤٨: صححه ابن حبان والحاكم.','7','298',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3074,'<span data-type="title" id=toc-3081>٣٠٧٤ </span>- حديث العرباض بن سارية: وعظنا رسول الله صلى الله عليه وسلم موعظة بليغة فذكره وفيه: " وإياكم ومحدثات الأمور فإن كل بدعة ضلالة ".
** حم د ت
(فتح الباري ٢٥٣/١٣)
** قال الحافظ فى " الفتح " ١٣ / ٢٥٣: صححه ابن ماجه وابن حبان والحاكم.','7','299',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3075,'<span data-type="title" id=toc-3082>٣٠٧٥ </span>- عن غضيف ابن الحارث قال: بعث إلى عبد الملك بن مروان فقال: إنا قد جمعنا الناس على رفع الأيدى على المنبر يوم الجمعة، وعلى القصص بعد الصبح والعصر، فقال: أما أنها أضل بدعكم عندى ولست بمجيبكم إلى شىء منهما لأن النبى صلى الله عليه وسلم قال: " ما أحدث قوم بدعة إلا رفع من السنة مثلها فتمسك بسنة خير م إحداث بدعة ".
** حم
(فتح الباري ٢٥٣/١٣)
** إسناده جيد','7','300',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3076,'<span data-type="title" id=toc-3083>٣٠٧٦ </span>- عن أبى هريرة رفعه: " لتدخلن الجنة إلا من أبى وشرد على الله شراد البعير "
** حم ك
(فتح الباري ٢٥٤/١٣)
** إسناده على شرط الشيخين','7','301',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3077,'<span data-type="title" id=toc-3084>٣٠٧٧ </span>- حديث ابن مسعود: أن النبى صلى الله عليه وسلم توسد فخذه فرقد، وكان إذا نام نفخ قال: فينا أنا قاعد إذا أنا برجل عليهم ثياب بيض، الله أعلم بما يهم من الجمال، فجلست طائفة منهم عند رأسه رسول الله صلى الله عليه وسلم، وطائفة منهم عند رجليه.
** ت
(فتح الباري ٢٥٥/١٣)
** قال الحافظ فى " الفتح " ١٣ / ٢٥٥: حسنه الترمذى وصححه ابن خزيمة.','7','302',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3078,'<span data-type="title" id=toc-3085>٣٠٧٨ </span>- عن أبى هريرة مرفوعا: " لو اعترض بنوا إسرائيل أدنى بقرة فذبحوها لكفتهم ولكن شددوا فشدد الله عليهم ".
** بز ابن أبى حاتم فى التفسير
(فتح الباري ٢٦١/١٣)
** قال الحافظ فى " الفتح " ١٣ / ٢٦١: فى السند عباد بن منصور وحديثه من قبيل الحسن وأورده الطبرى عن ابن عباس موقوفا وعن أبى العالية مقطوعا.','7','303',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3079,'<span data-type="title" id=toc-3086>٣٠٧٩ </span>- حديث أبى الدرداء رفعه: " ما حل الله فى كتابه فهو حلال، وما حرم فهو حرام وماسكت عنه فهو عفو، فاقبلوا من الله عافيته فإن الله لم يكن ينسى شيئا ".
** بز
(فتح الباري ٢٦٦/١٣)
** قال الحافظ فى " الفتح " ١٣ / ٢٦٦: قال البزار: سنده صالح وصححه الحاكم.','7','304',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3080,'<span data-type="title" id=toc-3087>٣٠٨٠ </span>- عن معاذ رفعه: " لا تعجلوا بالبلية قبل نزولها فإنكم إن تفعلوا لم يزل فى المسلمين من إذا قال سدد أو وفق وإن عجلتم تشتت بكم السبل ".
** مد
(فتح الباري ٢٦٧/١٣)
** مرسل
** قال الحافظ فى " الفتح " ١٣ / ٢٦٦: أخرج أبو داود فى المرسيل من رواية يحيى بن أبى كثير عن أبى سلمة مرفوعا، ومن طريق طاوس عن معاذ رفعه، وهما مرسلان يقوى بعض بعضا.','7','305',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3081,'<span data-type="title" id=toc-3088>٣٠٨١ </span>- عن إبراهيم النخعى قال: قرأ أبو بكر وفاكهة وأبا فقيل ما الأب؟ فقيل كذا وكذا فقال أبو بكر: إن هذا لهو التكلف أى أرض تقلنى أو أى سماء تظلنى إذا قلت فى كتاب الله بما لا أعلم.
** عبد
(فتح الباري ٢٧١/١٣)
** منقطع
** قال الحافظ فى " الفتح " ١٣ / ٢٧١: هذا منقطع بين النخعى والصديق.','7','306',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3082,'<span data-type="title" id=toc-3089>٣٠٨٢ </span>- من طريق إبراهيم التيمى: أن أبا بكر سئل عن الأب ماهو فقال: أى سماء تظلنى.
** عبد
(فتح الباري ٢٧١/١٣)
** منقطع','7','307',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3083,'<span data-type="title" id=toc-3090>٣٠٨٣ </span>- عن ابن عباس قال: الأب ماتنبته الأرض مما تأكله الدواب ولا يأكله الناس.
** الطبرى
(فتح الباري ٢٧١/١٣)
** إسناده صحيح','7','308',NULL,NULL,'0');
