INSERT INTO [b88] VALUES (3088,'<span data-type="title" id=toc-3095>٣٠٨٨ </span>- حديث أبى سعيد الخدرى: يقبض الله العلماء ويقبض العلم معهم، فتنشأ أحداث ينزو بعضهم على بعض نزو البعير على العير، ويكون الشيخ فيهم مستضعفا
** لم
(فتح الباري ٢٨٦/١٣)
** إسناده ضعيف','7','313',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3089,'<span data-type="title" id=toc-3096>٣٠٨٩ </span>- عن حذيفة رفعه: " يدرس الإسلام كما يدرس وشى الثوب ".
** حم
(فتح الباري ٢٨٧/١٣)
** قال الحافظ فى " الفتح " ١٣ / ٢٨٧: صححه الحاكم.','7','314',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3090,'<span data-type="title" id=toc-3097>٣٠٩٠ </span>- عن ابن مسعود: " فإن جاءه ماليس فى ذلك فليجتهد رأيه فإن الحلال بين والحرام بين فدع مايريبك إلا مالا يريبك ".
** شب
(فتح الباري ٢٨٨/١٣)
** إسناده صحيح','7','315',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3091,'<span data-type="title" id=toc-3098>٣٠٩١ </span>- قول على: لو كان الدين بالرأى لكان مسح أسفل الخف أولى من أعلاه.
** د
(فتح الباري ٢٨٩/١٣)
** إسناده حسن','7','316',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3092,'<span data-type="title" id=toc-3099>٣٠٩٢ </span>- حديث أبى هريرة: " لا يؤمن أحدكم حتى يكون هواه تبعا لما جئت به ".
** الحسن بن سفيان
(فتح الباري ٢٨٩/١٣)
** رجاله ثقات
** قال الحافظ فى " الفتح " ١٣ / ٢٨٩: صححه النووى فى آخر الأربعين.','7','317',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3093,'<span data-type="title" id=toc-3100>٣٠٩٣ </span>- عن حسان بن عطية أحد التابعين من ثقات الشاميين: كان جبريل ينزل على النبى صلى الله عليه وسلم بالسنة كما ينزل عليه بالقرآن.
** هق
(فتح الباري ٢٩١/١٣)
** إسناده صحيح','7','318',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3094,'<span data-type="title" id=toc-3101>٣٠٩٤ </span>- عن عمر: فساد الدين إذا جاء العلم من قبل الصغير استعصى عليه الكبير وصلاح الناس إذا جاء العلم من قبل الكبير تابعه عليه الصغير.
** قاسم بن أصبغ فى مصنفه
(فتح الباري ٣٠١/١٣)
** إسناده صحيح','7','319',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3095,'<span data-type="title" id=toc-3102>٣٠٩٥ </span>- حديث الحارث بن الحارث الأشعرى: فذكر حديثا طويلا وفيه: " وأنا أمركم بخمس أمرنى الله بهن: السمع والطاعة والجهاد والهجرة والجماعة فإن من فارق الجماعة قيد شبر فقد خلع ربقة الإسلم من عنقه ".
** ت
(فتح الباري ٣١٦/١٣)
** قال الحافظ فى " الفتح " ١٣ / ٣١٦: أخرجه الترمذى مصححا.','7','320',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3096,'<span data-type="title" id=toc-3103>٣٠٩٦ </span>- عن عمرو بن العاص من طريق ولده عبد الله بن عمرو عنه قال: جاء رجلان إلى رسول الله صلى الله عليه وسلم يختصمان فقال لعمرو: " اقض بينهما ياعمرو " قال: أنت أولى بذلك منى يارسول الله، قال: وإن كان قال: " فإذا قضيت بينهما فمالى "، فذكر نحوه لكن قال فى الإصابة: " فلك عشر حسنات ".
** لم
(فتح الباري ٣١٩/١٣)
** إسناده ضعيف
** قال الحافظ فى " الفتح " ١٣ / ٣١٩: وأخرج من حديث عقبة بن عامر نحوه بغير قصة بلفظ: فلك عشرة أجور " وفى سند كل منهما ضعف.','7','321',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3097,'<span data-type="title" id=toc-3104>٣٠٩٧ </span>- حديث البراء: ليس كلنا يسمع الحديث من النبى صلى الله عليه وسلم كانت لنا صنعة وأشغال، ولكن كان الناس لا يكذبون فيحدث الشاهد الغائب.
** هق فى المدخل
(فتح الباري ٣٢١/١٣)
** إسناده ضعيف','7','322',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3098,'<span data-type="title" id=toc-3105>٣٠٩٨ </span>- عن ابن عمر قال: لقيت ابن صياد يوما ومعه رجل من اليهود، فإذا عينه قد طفئت وهى خارجة مثل عين الجمل فلما رأيتها قلت: أنشدك الله ياابن صياد متى طفئت عينك؟ ، قال: لا أدرى والرحمن، قلت: كذبت لا تدرى وهى فى رأسك، قال: فمسحها ونخر ثلاثا فزعم اليهودى أنى ضربت بيدى صدرقه، وقلت له: اخسأ فلن تعد وقدرك، فذكرت ذلك لحفصة، فقالت حفصة: اجتنب هذا الرجل فإنما يتحدث أن الدجال يخرج عند غضبة يغضبها.
** عب
(فتح الباري ٣٢٥/١٣)
** إسناده صحيح','7','323',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3099,'<span data-type="title" id=toc-3106>٣٠٩٩ </span>- عن نافع قال: كان ابن عمر يقول: والله ما أشك أن المسيح الدجال هو ابن صياد.
** د
(فتح الباري ٣٢٥/١٣)
** إسناده صحيح','7','324',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3100,'<span data-type="title" id=toc-3107>٣١٠٠ </span>- حديث أبى بكرة قال: قال رسول الله صلى الله عليه وسلم: " يمكث أبو الدجال ثلاثين عاما لا يولد لهما، ثم يولد لهما غلام أعور أضر شىء وأقله نفعا " ونعت أباه وأمه، قال: فسمعنا بمولود ولد فى اليهود، فذهبت أنا والزبير
بن العوام فدخلنا على أبويه فإذا النعت فقلنا: هل لكما من ولد قالا: مكثنا
ثلاثين عاما لا يولد لنا ثم ولد لنا غلام أضر شىء وأقله نفعا.
** د
(فتح الباري ٣٢٦/١٣)
** قال الحافظ فى " الفتح " ١٣ / ٣٢٦: قال البيهقى: تفىد به على بن زيد بن جدعان وليس بالقوى.','7','325',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3101,'<span data-type="title" id=toc-3108>٣١٠١ </span>- عن حسان بن عبد الرحمن عن أبيه قال: لما افتتحنا أصبهان كان بين عسكرنا وبين اليهودية فرسخ فكنا نأتيها فنمتار منها فأتيتها يوما فإذا اليهود يزفنون ويضربون، فسألت صديقا لى منهم فقال: ملكنا الذى نستفتح به على العرب يدخل فبت عنده على سطح فصليت الغداة فلما طلعت الشمس إذا لرهج من قبل العسكر فنظرت، فإذا هو رجل عليه قبة من ريحان واليهود يزفنون ويضربون فنظرت فإذا هو ابن صياد فدخل المدينة فلم يعد حتى الساعة.
** أبو نعيم فى تاريخ أصبهان
(فتح الباري ٣٢٧/١٣)
** قال الحافظ فى " الفتح " ١٣ / ٣٢٨: عبد الرحمن بن حسن ماعرفته والباقون ثقات.','7','326',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3102,'<span data-type="title" id=toc-3109>٣١٠٢ </span>- عن جابر قال: فقدنا ابن صياد يوم الحرة.
** د
(فتح الباري ٣٢٨/١٣)
** إسناده صحيح','7','327',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3103,'<span data-type="title" id=toc-3110>٣١٠٣ </span>- من طريق كعب الأحبار: أن الدجال تلده أمه بقوص فى أرض مصر، قال: وبين مولده ومخرجه ثلاثون سنة، قال: ولم ينزل خبره فى التوراة والإنجيل وإنما هو فى بعض كتب الأنبياء.
** حل
(فتح الباري ٣٢٨/١٣)
** باطل
** قال الحافظ فى " الفتح " ١٣ / ٣٢٨: أحق بهذا الخبر أن يكون باطلا.','7','328',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3104,'<span data-type="title" id=toc-3111>٣١٠٤ </span>- رواية أبى سلمة عن جابر قال: قال رسول الله صلى الله عليه وسلم ذات يوم على المنبر: " أنه بينما الناس يسيرون فى البحر فنفد طعامهم فرفعت لهم جزيرة فخرجوا يريدون الخبر فلقيتهم الجساسة.... " فذكر الحديث وفيه سؤالهم عن نخل بيسان وفيه أن جابر اسهد أنه ابن صياد، فقلت: إنه مات قال: وإن مات قلت: فإنه أسلم، قال: وإن أسلم، قلت: فإنه دخل المدينة، قال: وإن دخل المدينة.
** د
(فتح الباري ٣٢٩/١٣)
** إسناده حسن','7','329',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3105,'<span data-type="title" id=toc-3112>٣١٠٥ </span>- حديث أبى ذر: لأن أحلف عشر مرار أن ابن صياد هو الدجال، أحب إلى من أن أحلف واحدة أنه ليس هو.
** حم
(فتح الباري ٣٢٩/١٣)
** إسناده صحيح','7','330',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3106,'<span data-type="title" id=toc-3113>٣١٠٦ </span>- قدمت على النبى صلى الله عليه وسلم يقول (من يعمل مثقال ذرة خيرا يره _ إلى آخر السورة) قال: " ما أبالى أن لا أسمع غيرها حسبى حسبى ".
** ن
(فتح الباري ٣٣١/١٣)
** قال الحافظ فى " الفتح " ١٣ / ٣٣١: صححه الحاكم.','7','331',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3107,'<span data-type="title" id=toc-3114>٣١٠٧ </span>- حديث جابر: أن عمر أتى النبى صلى الله عليه وسلم بكتاب أصابه من بعض أهل الكتاب، فقرأه عليه فغضب وقال: " لقد جئتكم بها بيضاء نقية لا تسألوهم عن شىء فيخبروكم بحث فتكذبوا به أو بباطل فتصدقوا به والذى نفسى بيده لو أن موسى كان حيا ماوسعه إلا أن يتبعنى ".
** حم شب بز
(فتح الباري ٣٣٤/١٣)
** قال الحافظ فى " الفتح " ١٣ / ٣٣٤: رجاله موثقون إلا أن فى مجالد ضعفا.','7','332',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3108,'<span data-type="title" id=toc-3115>٣١٠٨ </span>- من طريق عبد الله بن ثابت الأنصارى: أن عمر نسخ صحفية من التوراة فقال رسول الله صلى الله عليه وسلم: " لا تسألوا أهل الكتاب عن شىء ".
** بز
(فتح الباري ٣٣٤/١٣)
** قال الحافظ فى " الفتح " ١٣ / ٣٣٤: فى سنده جابر الجعفى وهو ضعيف.','7','333',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3109,'<span data-type="title" id=toc-3116>٣١٠٩ </span>- " لا تسألوا أهل الكتاب عن شىء فإنهم لن يهدوكم وقد ضلوا أن تكذبوا بحق أو تصدقوا بباطل ".
** سفيان الثورى
(فتح الباري ٣٣٤/١٣)
** إسناده حسن','7','334',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3110,'<span data-type="title" id=toc-3117>٣١١٠ </span>- حديث أبى هريرة: مارأيت أحدا أكثر مشورة لأصحابه من النبى صلى الله عليه وسلم
** لم
(فتح الباري ٣٤٠/١٣)
** منقطع
** قال الحافظ فى " الفتح " ١٣ / ٣٤٠: رجاله ثقات إلا أنه منقطع.','7','335',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3111,'<span data-type="title" id=toc-3118>٣١١١ </span>- حديث على قال: لما نزلت (يا أيها الذين آمنوا إذا ناجيتم الرسول) الآية قال لى النبى صلى الله عليه وسلم: " ماترى؟ دينار "، قلت: لا يطيقونه، قال: " فنصف دينار "، قلت: لا يطيقونه، قال: " فكم قلت: " شعيرة " فبى خفف الله عن هذه الأمة ".
** ت
(فتح الباري ٣٤٠/١٣)
** قال الحافظ فى " الفتح " ١٣ / ٣٤٠: حسنه الترمذى وصححه ابن حبان.','7','336',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3112,'<span data-type="title" id=toc-3119>٣١١٢ </span>- عن عبد الرحمن بن غنم: أن النبى صلى الله عليه وسلم قال لأبى بكر وعمر: " لو أنكما تتفقان على أمر واحد ماعصيتكما فى مشورة أبدا ".
** فضائل الصحابة لأسد بن موسى المعرفة ليعقوب بن سفيان
(فتح الباري ٣٤١/١٣)
** إسناده لابأس به
** قال الحافظ فى " الفتح " ١٣ / ٣٤١: عبد الرحمن بن غنم مختلف فى صحبته.','7','337',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3113,'<span data-type="title" id=toc-3120>٣١١٣ </span>- عن ابن عباس قال: تنفل رسول الله صلى الله عليه وسلم سيفه ذا الفقار يوم بدر وهو الذى رأى فيه الرؤيا يوم أحد، وذلك أن رسول الله صلى الله عليه
وسلم لما جاءه المشركون يوم أحد كان رأى رسول الله صلى الله عليه وسلم أن يقيم بالمدينة فيقاتلهم فيها فقال له ناس لم يكونوا شهدوا بدرا، أخرج بنا يارسول الله إليهم نقاتلهم بأحد، ونرجوا أن نصيب من الفضيلة ما أصاب أهل بدر فمازالوا برسول الله صلى الله عليه وسلم حتى لبس لأمته، فلما لبسها ندموا وقالوا: يارسول الله أقم فالرأى رأيك فقال: " ماينبغى لنبى أن يضع أداته بعد أن لبسها حتى يحكم الله بينه وبين عدوه "، وكان ذكر لهم قبل أن يلبس الأداة أنى رأيت أنى فى دريح حصينة فأولتها المدينة.
** طب
(فتح الباري ٣٤١/١٣)
** إسناده حسن
** قال الحافظ فى " الفتح " ١٣ / ٣٤١: صححه الحاكم.','7','338',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3114,'<span data-type="title" id=toc-3121>٣١١٤ </span>- أن النبى صلى الله عليه وسلم قال: " رأيت كأنى فى دريح حصينة، ورأيت بقرا تنخر فأولت الدرع الحصينة المدينة.... " الحديث.
** حم
(فتح الباري ٣٤٨/١٣)
** إسناده صحيح
** قال الحافظ فى " الفتح " ١٣ / ٣٤١: أخرجه أحمد والدارمى والنسائى من طريق حماد بن سلمة عن أبى الزبير عن جابر.','7','339',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3115,'<span data-type="title" id=toc-3122>٣١١٥ </span>- عن عائشة قالت: لما نزلت براءتى قام رسول الله صلى الله عليه وسلم على المنبر فدعا بهم وحدهم وفى لفظ فأمر برجلين وامرأة فضربوا حدهم.
** حم د ت ن جه
(فتح الباري ٣٤٢/١٣)
** قال الحافظ فى " الفتح " ١٣ / ٣٤٢: قال الترمذى: حسن لا نعرفه إلا من حديث ابن إسحق من هذا الوجه.','7','340',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3116,'<span data-type="title" id=toc-3123>٣١١٦ </span>- عن ميمون بن مهران قال: كان أبو بكر الصديق إذا ورد عليه أمر نظر فى كتاب الله فإن وجد فيه مايقضى به قضى بينهم، وإن علمه من سنة رسول الله صلى الله
عليه وسلم قضى به وإن لم يعلم خرج فسأل المسلمين عن السنة، فإن أعياه ذلك دعا رؤس المسلمين وعلماءهم واستشارهم، وأن عمر بن الخطاب كان يفعل ذلك.
** هق
(فتح الباري ٣٤٢/١٣)
** إسناده صحيح','7','341',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3117,'<span data-type="title" id=toc-3124>٣١١٧ </span>- من رواية سعيد بن المسيب: كان عمر يتعوذ بالله من معضلة ليس لها أبو الحسن _ يعنى على بن أبى طالب _ ومشارة عثمان الصحابة أول ماأستخلف فيما يفعل بعبيد الله بن عمر لما قتل الهرمزان وغيره ظنا منه أن لهم فى قتل أبيه مدخلا.
** سعد
(فتح الباري ٣٤٣/١٣)
** إسناده حسن','7','342',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3118,'<span data-type="title" id=toc-3125>٣١١٨ </span>- عن على: مافعل عثمان الذى فعل فى المصاحف إلا عن ملأ منا.
** أبو داود فى كتاب المصاحف
(فتح الباري ٣٤٣/١٣)
** إسناده حسن','7','343',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3119,'<span data-type="title" id=toc-3126>٣١١٩ </span>- من مرسل سعيد بن جبير بأن سعد بن معاذ لما سمع ماقيل فى أمر عائشة قال: سبحانك هذا بهتان عظيم.
** تفسير سنيد
(فتح الباري ٣٤٤/١٣)
** مرسل','7','344',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3120,'<span data-type="title" id=toc-3127>٣١٢٠ </span>- حديث بهز بن حكيم عن أبيه عن جده مرفوعا: " ومن منعها _ يعنى الزكاة _ فأنا أخذوها وشطر ماله عزمة من عزمات ربنا ... " الحديث.
** د ن
(فتح الباري ٣٥٥/١٣)
** قال الحافظ فى " الفتح " ١٣ / ٣٥٥: صححه ابن خزيمة والحاكم، وأما ابن حبان فقال فى ترجمة بهز بن حكيم: لولا هذاالحديث لأدخلته فى كتاب الثقات.','7','345',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3121,'<span data-type="title" id=toc-3128>٣١٢١ </span>- عن ابن عباس: أن اليهود أتوا النبى صلى الله عليه وسلم فقالوا: صف لنا ربك الذى تعبد، فأنزل الله عز وجل (قل هو الله أحد) فقال: " هذه صفة ربى عز وجل ".
** هق فى الأسماء والصفات
(فتح الباري ٣٥٦/١٣)
** إسناده حسن','7','346',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3122,'<span data-type="title" id=toc-3129>٣١٢٢ </span>- عن بن كعب قال: قال المشركون للنبى صلى الله عليه وسلم أنسب لنا ربك، فنزلت سورى الإخلاص ... الحديث وفيه: " أنه ليس شىء يولد إلا يموت وليس شىء يموت إلا يورث، والله لا يموت ولا يورث ولم يكن له شبه ولا عدل وليس كمثله شىء ".
** خز فى كتاب التوحيد
(فتح الباري ٣٥٦/١٣)
** قال الحافظ فى " الفتح " ١٣ / ٣٥٦: صححه الحاكم.','7','347',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3123,'<span data-type="title" id=toc-3130>٣١٢٣ </span>- عن ابن عباس أن المشركين سمعوا رسول الله صلى الله عليه وسلم يدعو ياالله يارحمن، فقالوا: كان محمد يأمرنا بدعاء إله واحد وهو يدعو إلهين فنزلت.
** ابن مردويه
(فتح الباري ٣٦٠/١٣)
** إسناده ضعيف','7','348',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3124,'<span data-type="title" id=toc-3131>٣١٢٤ </span>- عن أبى هريرة: رأيت رسول الله صلى الله عليه وسلم يقرؤها _ يعنى قوله تعالى (إن الله يأمركم أن تؤدوا الأمانات إلى أهلها.. إلى قوله سميعا بصيرا) _ ويضع إصبعيه، قال أبو يونس: وضع أبو هريرة إبهامه على أذنه والتى تليها على عينه.
** د
(فتح الباري ٣٧٣/١٣)
** إسناده قوى على شرط مسلم','7','349',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3125,'<span data-type="title" id=toc-3132>٣١٢٥ </span>- حديث عقبة بن عامر: سمعت رسول الله صلى الله عليه وسلم يقول على المنبر " إن ربنا سميع بصير "، وأشار إلى عينيه.
** لم
(فتح الباري ٣٧٣/١٣)
** إسناده حسن','7','350',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3126,'<span data-type="title" id=toc-3133>٣١٢٦ </span>- عن خويلة بنت مالك بن ثعلبة قالت: ظاهر منى زوجى أوس بن الصامت ... الحديث.
** د
(فتح الباري ٣٧٤/١٣)
** قال الحافظ فى " الفتح " ١٣ / ٣٧٤: صححه ابن حبان.','7','351',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3127,'<span data-type="title" id=toc-3134>٣١٢٧ </span>- فى مرسل محمد بن كعب القرظى: كانت خولة بنت ثعلبة تحت أوس بن الصامت فقال لها: أنت على كظهر أمى.
** طب
(فتح الباري ٣٧٤/١٣)
** مرسل','7','352',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3128,'<span data-type="title" id=toc-3135>٣١٢٨ </span>- من مرسل أبى العالية: كانت خولة بنت دليح تحت رجل من الأنصار سيىء الخلق فنازعته فقال: أنت على كظهر أمى.
** ابن مردويه
(فتح الباري ٣٧٤/١٣)
** مرسل','7','353',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3129,'<span data-type="title" id=toc-3136>٣١٢٩ </span>- عن الشعبى قال: المرأة التى جادلت فى زوجها هى خولة بنت الصامت وأمها معاذة أمة عبد الله بن أبى الذى نزل فيها (ولا تكرهوا فتياتكم على البغاء) .
** النقاش فى تفسيره
(فتح الباري ٣٧٤/١٣)
** إسناده ضعيف','7','354',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3130,'<span data-type="title" id=toc-3137>٣١٣٠ </span>- حديث ابن عباس: تفكروا فى كل شىء ولا تنكروا فى ذات الله.
** هق فى الأسماء والصفات
(فتح الباري ٣٨٣/١٣)
** إسناده جيد','7','355',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3131,'<span data-type="title" id=toc-3138>٣١٣١ </span>- حديث أبى الدرداء: لا تفقه كل الفقه حتى تمقت الناس فى ذات الله.
** هق فى الأسماء والصفات
(فتح الباري ٣٨٣/١٣)
** منقطع
** قال الحافظ فى " الفتح " ١٣ / ٣٨٣: رجاله ثقات إلا أنه منقطع.','7','356',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3132,'<span data-type="title" id=toc-3139>٣١٣٢ </span>- حديث ابن عباس: مر يهودى بالنبى صلى الله عليه وسلم فقال: " يايهودى حدثنا " فقال: كيف تقول يا أبا القاسم إذا وضع الله السموات على ذه والأرضين على ذه والماء على ذه والجبال على ذه وسائر الخلق على ذه وأشار أبو جعفر _ يعنى أحد رواته _ بخصره أولا ثم تابع حتى بلغ الإبهام.
** ت
(فتح الباري ٣٩٧/١٣)
** قال الحافظ فى " الفتح " ١٣ / ٣٩٧: قال الترمذى: حديث حسن غريب صحيح ووقع فى مرسل مسروق عند الهروى مرفوعا نحو هذه الزيادة.','7','357',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3133,'<span data-type="title" id=toc-3140>٣١٣٣ </span>- حديث أبى ذر: أن رسول الله صلى الله عليه وسلم قال: " يا أبا ذر ما السموات السبع مع الكرسى إلا كحلقة ملقاة بأرض فلاة وفضل العرس على الكرسى كفضل الفلاة على الحلقة ".
** لم
(فتح الباري ٤١١/١٣)
** قال الحافظ فى " الفتح " ١٣ / ٤١١: صححه ابن حبان وله شاهد عن مجاهد أخرجه سعيد بن منصور فى التفسير بسند صحيح عنه.','7','358',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3134,'<span data-type="title" id=toc-3141>٣١٣٤ </span>- عن أنس: قالت زينب: يارسول الله إنى لست كأحد من نسائك ليست منهم امرأة إلا زوجها أبوها أو أخوها أو أهلها غيرى.
** سعد
(فتح الباري ٤١٢/١٣)
** إسناده ضعيف','7','359',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3135,'<span data-type="title" id=toc-3142>٣١٣٥ </span>- فى مرسل الشعبى: قالت زينب: يارسول الله أنا أعظم نسائك عليك حقا أنا خيرهن منكما وأكرمهن سفيرا وأقربهن رحما فزوجنيك الرحمن من فوق عرشه وكان جبريل هو السفير بذلك وأنا ابنة عمتك وليس لك من نسائك قريبة غيرى.
** الطبرى أبو القاسم الطحاوى فى كتاب الحجة والتبيين
(فتح الباري ٤١٢/١٣)
** مرسل','7','360',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3136,'<span data-type="title" id=toc-3143>٣١٣٦ </span>- حديث أبى سعيد المرفوع: " ويقال لصاحب القرآن اقرأ وارق ورتل كما كنت ترتل فى الدنيا فإن منزلك عند آخر آية تقرؤها ".
** د
(فتح الباري ٤١٣/١٣)
** قال الحافظ فى " الفتح " ١٣ / ٤١٣: صححه الترمذى وابن حبان.','7','361',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3137,'<span data-type="title" id=toc-3144>٣١٣٧ </span>- حديث العباس بن عبد المطلب مرفوعا: " هل تدرون بعد مابين السماء والأرض " قلنا: لا، قال: " إحدى أو إثنتان أو ثلاث سبعون _ قال _ ومافوقها مثل ذلك حتى عد سبع سموات ثم فوق السماء السابعة البحر أسفله من أعلاه مثل مابين سماء إلى سماء ثم فوقه ثمانية أوعال مابين أظلافهن وركبهن مثل مابين سماء إلى سماء ثم العرش فوق ذلك بين أسفله وأعلاه مثل مابين سماء إلى سماء ثم الله فوق
ذلك ".
** د
(فتح الباري ٤١٣/١٣)
** قال الحافظ فى " الفتح " ١٣ / ٤١٣: صححه ابن خزيمة والحاكم.','7','362',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3138,'<span data-type="title" id=toc-3145>٣١٣٨ </span>- عن معمر عنه: فى قوله (وكان عرشه على الماء) قال: هذا بدء خلقه قبل أن يخلق السماء وعرشه من ياقوتة حمراء.
** عب
(فتح الباري ٤١٥/١٣)
** مرسل
** قال الحافظ فى " الفتح " ١٣ / ٤١٥: له شاهد عن سهل بن سعد مرفوع لكن سنده ضعيف.','7','363',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3139,'<span data-type="title" id=toc-3146>٣١٣٩ </span>- عن ابن عمر عن النبى صلى الله عليه وسلم قال: " إن أدنى أهل الجنة منزلة لمن ينظر فى ملكه ألف سنة وإن أفضلهم منزلة لمن ينظر فى وجه ربه عز وجل فى كل يوم مرتين "، قال: ثم تلا (وجوه يومئذ ناضرة) قال بالبياض والصفاء (إلى ربها ناظرة) قال: تنظر كل يوم فى وجه الله.
** عبد ت الطبرى
(فتح الباري ٤٢٤/١٣)
** قال الحافظ فى " الفتح " ١٣ / ٤٢٤: صححه الحاكم.','7','364',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3140,'<span data-type="title" id=toc-3147>٣١٤٠ </span>- عن ابن عباس فى قوله تعالى: (يوم يكشف عن ساق) قال: عن شدة فى الأمر والعرب تقول: قامت الحرب على ساق إذا اشتدت، وفيه: قدسن أصحابك ضرب الأعناق وقامت الحرب بنا على ساق، وزاد: إذا خفى عليكم شىء من القرآن فاتبعوه من الشعر.
** هق
(فتح الباري ٤٢٨/١٣)
** إسناده حسن
** قال الحافظ فى " الفتح " ١٣ / ٤٢٨: أسند البيهقى الأثر المذكور عن ابن عباس بسندين كل منهما حسن، وأسند البيهقى من وجه آخر صحيح عن ابن عباس قال: يريد يوم القيامة.','7','365',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3141,'<span data-type="title" id=toc-3148>٣١٤١ </span>- فى رواية حماد بن سلمة عن ثابت البنانى عن أبى بكر بن أبى موسى عن أبيه قال حماد: لا أعلمه إلا قد رفعه قال: " جنتان من ذهب للمقربين ومن دونهما جنتان من ورق لأصحاب اليمين ".
** الطبرى ابن أبى حاتم
(فتح الباري ٤٣١/١٣)
** رجاله ثقات','7','366',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3142,'<span data-type="title" id=toc-3149>٣١٤٢ </span>- حديث أبى هريرة: قلنا يارسول الله حدثنا عن الجنة مابناؤها؟ ، قال: " لبنة من ذهب ولبنة من فضة ".
** حم ت
(فتح الباري ٤٣٢/١٣)
** قال الحافظ فى " الفتح " ١٣ / ٤٣٢: صححه ابن حبان وله شاهد عن ابن عمر أخرجه الطبرانى وسنده حسن.','7','367',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3143,'<span data-type="title" id=toc-3150>٣١٤٣ </span>- عن ابن عباس فى قصة سؤال اليهود عن الروح ونزول قوله تعالى: (قل الروح من أمر ربى وما أوتيتم من العلم إلا قليلا) قالوا: كيف وقد أوتينا التوراة فنزلت (قل لو كان البحر مدادا لكلمات ربى..) الآية.
** ابن أبى حاتم
(فتح الباري ٤٤٥/١٣)
** إسناده صحيح','7','368',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3144,'<span data-type="title" id=toc-3151>٣١٤٤ </span>- عن ابن عباس فى قوله تعالى (إن تكفروا فإن الله غنى عنكم ولا يرضى لعباده الكفر) يعنى بعباده الكفار الذين أراد الله أن يطهر قلوبهم بقولهم لا إله إلا الله فأراد عباده المخلصين الذين قال فيهم (إن عبادى ليس لك عليهم سلطان) فحبب إليهم الإيمان وألزمهم كلمة التقوى شهادة أن لا إله إلا الله.
** الطبرى
(فتح الباري ٤٥٠/١٣)
** إسناده رجاله ثقات','7','369',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3145,'<span data-type="title" id=toc-3152>٣١٤٥ </span>- فى مرسل أبى قلابة: " البر لا يبلى والإثم لا ينسى والديان لا يموت وكن كما شئت كما تدين تدان ".
** هق فى الزهد
(فتح الباري ٤٥٨/١٣)
** رجاله ثقات مرسلا','7','370',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3146,'<span data-type="title" id=toc-3153>٣١٤٦ </span>- عن أبى هريرة: إذا ذهب ثلث الليل ... ذكر الحديث وزاد: فلا يزال بها حتى يطلع الفجر فيقول: هل من داع يستجاب له.
** ن خز
(فتح الباري ٤٦٨/١٣)
** قال الحافظ فى " الفتح " ١٣ / ٤٦٨: من رواية محمد بن إسحاق وفيه اختلاف.','7','371',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3147,'<span data-type="title" id=toc-3154>٣١٤٧ </span>- حديث ابن مسعود وفيه: " فإذ طلع الفجر صعد إلى العرش ".
** خز
(فتح الباري ٤٦٨/١٣)
** قال الحافظ فى " الفتح " ١٣ / ٤٦٨: من رواية إبراهيم الهجرى وفيه مقال.','7','372',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3148,'<span data-type="title" id=toc-3155>٣١٤٨ </span>- عن ابن مسعود قال: جاء رجل من بنى سليم إلى رسول الله صلى الله عليه وسلم فقال: علمنى.... فذكر الحديث وفيه: " فإذا انفجر الفجر صعد ".
** أبو إسماعيل الهروى
(فتح الباري ٤٦٨/١٣)
** قال الحافظ فى " الفتح " ١٣ / ٤٦٨: من رواية عون بن عبد الله بن عتبة بن مسعود عن عم أبيه ولم يسمع منه.','7','373',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3149,'<span data-type="title" id=toc-3156>٣١٤٩ </span>- حديث عبادة بن الصامت وفى آخره: " ثم يعلو ربنا على كرسيه ".
** لم
(فتح الباري ٤٦٨/١٣)
** قال الحافظ فى " الفتح " ١٣ / ٤٦٨: من رواية اسحق بن يحيى عن عبادة ولم يسمع منه','7','374',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3150,'<span data-type="title" id=toc-3157>٣١٥٠ </span>- حديث جابر وفيه: " ثم يعلو ربنا على كرسيه ".
** لم
(فتح الباري ٤٦٨/١٣)
** قال الحافظ فى " الفتح " ١٣ / ٤٦٨: من رواية محمد بن إسماعيل الجعفرى عن عبد الله بن سلمة بن أسلم وفيهما مقال.','7','375',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3151,'<span data-type="title" id=toc-3158>٣١٥١ </span>- حديث أبى الخطاب: أنه سأل النبى صلى الله عليه وسلم عن الوتر فذكر الوتر وفى آخره: " حتى إذا طلع الفجر ارتفع ".
** لم
(فتح الباري ٤٦٨/١٣)
** قال الحافظ فى " الفتح " ١٣ / ٤٦٨: من رواية ثوير بن أبى فاختة وهو ضعيف وهو ضعيف.','7','376',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3152,'<span data-type="title" id=toc-3159>٣١٥٢ </span>- حديث ابن مسعود: الندم توبة
** جه
(فتح الباري ٤٧١/١٣)
** حسن
** قال الحافظ فى " الفتح " ١٣ / ٤٧١: صححه الحاكم وأخرجه ابن حبان من حديث أنس وصححه.','7','377',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3153,'<span data-type="title" id=toc-3160>٣١٥٣ </span>- التائب من الذنب كمن لا ذنب له.
** جه طب
(فتح الباري ٤٧١/١٣)
** إسناده حسن','7','378',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3154,'<span data-type="title" id=toc-3161>٣١٥٤ </span>- حديث ابن مسعود: من قال: استغفر الله الذى لا إله إلا هو الحى القيوم وأتوب إليه غفرت ذنوبه وإن كان قد فر من الزحف.
** د ت
(فتح الباري ٤٧٢/١٣)
** قال الحافظ فى " الفتح " ١٣ / ٤٧٢: صححه الحاكم.','7','379',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3155,'<span data-type="title" id=toc-3162>٣١٥٥ </span>- حديث أبى بكر الصديق: فيأتون إبراهيم فيقول: انطلقوا إلى موسى فإن الله كلمه
تكليما.
** حم
(فتح الباري ٤٨٠/١٣)
** قال الحافظ فى " الفتح " ١٣ / ٤٨٠: صححه أبو عوانة.','7','380',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3156,'<span data-type="title" id=toc-3163>٣١٥٦ </span>- عن ابن عباس فى قوله تعالى: (ولقد رآه نزلة أخرى) قال: دنا منه ربه.
** الأموى فى مغازيه هق
(فتح الباري ٤٨٤/١٣)
** إسناده حسن','7','381',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3157,'<span data-type="title" id=toc-3164>٣١٥٧ </span>- عن ابن عباس قال: من إيمانهم إذا قيل لهم من خلق السموات ومن خلق الأرض ومن خلق الجبال قالوا: الله وهم به مشركون.
** الطبرى
(فتح الباري ٤٩٥/١٣)
** إسناده حسن','7','382',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3158,'<span data-type="title" id=toc-3165>٣١٥٨ </span>- عن على بن أبى طالب: الذى جاء بالصدق محمد صلى الله عليه وسلم والذى صدق به أبو بكر.
** الطبرى
(فتح الباري ٤٩٥/١٣)
** إسناده لين
** قال الحافظ فى " الفتح " ١٣ / ٤٩٥: من طريق لين إلى على بن أبى طالب.','7','383',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3159,'<span data-type="title" id=toc-3166>٣١٥٩ </span>- حديث حذيفة رفعه: " إن الله يصنع كل صانع وصنعته ".
** خ فى خلق أفعال العباد
(فتح الباري ٤٩٨/١٣)
** صحيح','7','384',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3160,'<span data-type="title" id=toc-3167>٣١٦٠ </span>- عن مغيث بن سمى قال: قال كعب: عليكم بالقرآن فإنه أحدث الكتب عهدا بالرحمن.
** ابن أبى حاتم
(فتح الباري ٤٩٩/١٣)
** إسناده حسن','7','385',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3161,'<span data-type="title" id=toc-3168>٣١٦١ </span>- عن أبى هريرة سمعت النبى صلى الله عليه وسلم يقول: " أفضل الأعمال عند الله إيمان لا شك فيه ... " الحديث.
** حم دى
(فتح الباري ٥٠٩/١٣)
** قال الحافظ فى " الفتح " ١٣ / ٥٠٩: ليس سنده على شرطه فى الصحيح وصححه ابن حبان.','7','386',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3162,'<span data-type="title" id=toc-3169>٣١٦٢ </span>- حديث جابر قال: نسخ عمر كتابا من التوراة بالعربية فجاء به إلى النبى صلى الله عليه وسلم فجعل يقرأ ووجه رسول الله صلى الله عليه وسلم يتغير فقال له رجل من الأنصار: ويحك ياابن الخطاب ألا ترى وجه رسول الله صلى الله عليه وسلم فقال رسول الله صلى الله عليه وسلم: " لا تسألوا أهل الكتاب عن شىء فإنهم لن يهدوكم وقد ضلوا، وإنكم إما أن تكذبوا بحق أو تصدقوا بباطل والله لو كان موسى بين أظهركم ماحل له إلا أن يتبعنى ".
** بز
(فتح الباري ٥٢٥/١٣)
** قال الحافظ فى " الفتح " ١٣ / ٥٢٥: فى سنده جابر الجعفى وهو ضعيف.','7','387',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3163,'<span data-type="title" id=toc-3170>٣١٦٣ </span>- عن جابر أن عمر أتى بكتاب أصابه من بعض كتب أهل الكتاب فقرأه على النبى صلى الله عليه وسلم فغضب فذكر نحوه دون قول الأنصارى وفيه: " والذى نفسى بيده لو أن موسى حيا ماوسعه إلا أن يتبعنى ".
** حم يع
(فتح الباري ٥٢٥/١٣)
** قال الحافظ فى " الفتح " ١٣ / ٥٢٥: فى سنده مجالد بن سعيد وهو لين.','7','388',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3164,'<span data-type="title" id=toc-3171>٣١٦٤ </span>- عن أبى الدرداء: جاء عمر بجوامع من التوراة فذكر نحوه وسمى الأنصارى الذى خاطب عمر عبد الله بن زيد الى رأى الأذان، وفيه: " لو كان موسى بين أظهركم ثم اتبعتموه وتركتمونى لضللتم ضلالا بعيدا ".
** طب
(فتح الباري ٥٢٥/١٣)
** قال الحافظ فى " الفتح " ١٣ / ٥٢٥: بسند فيه مجهول ومختلف فيه.','7','389',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3165,'<span data-type="title" id=toc-3172>٣١٦٥ </span>- من طريق خالد بن عرفطة قال: كنت عند عمر فجاءه رجل من عبد القيس فضربه بعصا معه فقال: مالى ياأمير المؤمنين؟ ، قال: أنت الذى نسخت كتاب دانيال، قال مرنى بأمرك، قال: انطلق فامحه فلئن بلغنى أنك قرأته أو أقرأته لأنهكتك عقوبة ثم قال: انطلقت فانتسخت كتابا من أهل الكتاب ثم جئت فقال لى رسول الله صلى
الله عليه وسلم " ماهذا "، قلت: كتاب انتسخته لنزداد به علما إلى علمنا فغضب حتى احمرت وجنتاه فذكر قصة فيها: " يا أيها الناس إنى قد أوتيت جوامع الكلم وخواتمه واختصر لى الكلام اختصارا ولد أتيتكم بها بيضاء نقية فلا تتهوكوا ".
** يع
(فتح الباري ٥٢٥/١٣)
** قال الحافظ فى " الفتح " ١٣ / ٥٢٥: فى سنده عبد الرحمن بن إسحق الواسطى وهو ضعيف.','7','390',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3166,'<span data-type="title" id=toc-3173>٣١٦٦ </span>- عن أبى الدرداء عن النبى صلى الله عليه وسلم قال: " مايوضع فى الميزان يوم القيامة أثقل من خلق حسن ".
** د ت
(فتح الباري ٥٣٩/١٣)
** قال الحافظ فى " الفتح " ١٣ / ٥٣٩: صححه ابن حبان.','7','391',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3167,'<span data-type="title" id=toc-3174>٣١٦٧ </span>- عن أبى هريرة قال: قال رسول الله صلى الله عليه وسلم: " من جلس فى مجلس وكثر فيه لغطه فقال قبل أن يقوم من مجلسه ذلك: سبحانك اللهم وبحمدك أشهد أن لا إله إلا أنت أستغفرك وأتوب إليك، إلا غفر له ماكان فى مجلسه ذلك ".
** ت نى حب طب فى الدعاء ك
(فتح الباري ٥٤٤/١٣)
** قال الحافظ فى " الفتح " ١٣ / ٥٤٤: هذا لفظ الترمذى وقال: حسن صحيح غريب لا نعرفه إلا من هذا الوجه، وفى الباب عن أبى برزة وعائشة، وقال الحاكم: هذا حديث صحيح على شرط مسلم إلا أن البخارى أعله برواية وهيب عن موسى بن عقبة عن سهيل عن أبيه عن كعب الأحبار كذا قال فى المستدرك ووهم فى ذلك، فليس فى هذا السند ذكر لوالد سهيل ولا كعب والصواب عن سهيل عن عون وكذا ذكره على الصواب فى علوم الحديث فإنه قد ساقه فيه من طريق البخارى عن محمد بن سلام عن مخلد بن يزيد عن ابن جريج بسنده ثم قال: قال البخارى هذا حديث مليح، ولا أعلم فى الدنيافى هذا الباب غير هذا الحديث إلا أنه معلول: حدثنا موسى بن إسماعيل حدثنا وهيب حدثنا موسى بن عقبة عن عون بن عبد الله، قوله: قال البخارى: هذا أولى فإنا لا نذكر لموسى بن عقبة سماعا من سهيل أ. هـ.','7','392',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3168,'<span data-type="title" id=toc-3175>٣١٦٨ </span>- عن عبد الله بن عمر بن الخطاب رضى الله عنهما عن النبى صلى الله عليه وسلم: " إن الله عز وجل يقبل توبة العرد ما لم يغرغر ".
** ت
(رياض الصالحين ١٥/١)
** قال الإمام النووى فى " الرياض " ١ / ١٥: قال الترمذى: حديث حسن.','7','393',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3169,'<span data-type="title" id=toc-3176>٣١٦٩ </span>- عن زر بن حبيش قال: أتيت صفوان بن عسال رضى الله عنه، أسأله عن المسح على الخفين فقال: ما جاء بك يا زر؟ ، فقلت: ابتغاء العلم، فقال: إن الملائكة تضع أجنحتها لطالب العلم، رضى بما يطلب، فقلت: إنه قد حك فى صدرى المسح على الخفين بعد الغائط والبول، وكنت امرأ من أصحاب النبى صلى الله عليه وسلم فجئت أسألك: هل سمعته يذكر فى ذلك شيئا؟ ، قال: نعم. كان يأمرنا إذا كنا سفرا _ أو مسافرين _ بأن لا ننزع خفافنا ثلاثة أيام ولياليهن إلا من الجنابة، لكن من غائط، وبول ونوم، فقلت: هل سمعته يذكر فى الهوى شيئا؟ ، قال: نعم كنا مع رسول الله صلى الله عليه وسلم فى سفر فبينا نحن عنده إذ ناداه أعرابى بصوت له جهورى: يا محمد، فأجابه رسول الله صلى الله عليه وسلم نحوا من صوته: " هاؤم "، فقلت له: ويحك اغضض من صوتك فإنك عند النبى صلى الله عليه وسلم وقد نهيت عن هذا فقال: والله لا اغضض قال الأعرابى: المرء يحب القوم ولما يلحق بهم؟ ، قال النبى صلى الله عليه وسلم " المرء مع من أحب يوم القيامة "، فما زال يحدثنا حتى ذكر بابا من المغرب مسيرة عرضه أو يسير الراكب فى عرضه أربعين أو سبعين عاما، قال سفيان أحد الرواة: " قبل الشام، خلقه الله تعالى يوم خلق السماوات والأرض مفتوحا للتوبة لا يغلق حتى
تطلع الشمس منه ".
** ت
(رياض الصالحين ١٥/١)
** قال الإمام النووى فى " الرياض " ١ / ١٦: قال الترمذى: حديث حسن صحيح.','7','394',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3170,'<span data-type="title" id=toc-3177>٣١٧٠ </span>- قال النبى صلى الله عليه وسلم: " إن عظم الجزاء مع عظم البلاء، وإن الله تعالى إذا أحب قوما ابتلاهم فمن رضى فله الرضا، ومن سخط فله السخط ".
** ت
(رياض الصالحين ٣٠/١)
** قال الإمام النووى فى " الرياض " ١ / ٣٠: قال الترمذى: حديث حسن.','7','395',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3171,'<span data-type="title" id=toc-3178>٣١٧١ </span>- عن معاذ بن أنس رضى الله عنه أن النبى صلى الله عليه وسلم قال: " من كظم غيظا وهو قادر على أن ينفذه، دعاه الله سبحانه وتعالى على رؤوس الخلائق يوم القيامة حتى يخيره من الحور العين ما شاء ".
** د ت
(رياض الصالحين ٣٢/١)
** قال الإمام النووى فى " الرياض " ١ / ٣٢: قال الترمذى: حديث حسن.','7','396',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3172,'<span data-type="title" id=toc-3179>٣١٧٢ </span>- عن أبى هريرة رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " ما يزال البلاء بالمؤمن والمؤمنة فى نفسه وولده وماله حتى يلقى الله تعالى وما عليه خطيئة ".
** ت
(رياض الصالحين ٣٢/١)
** قال الإمام النووى فى " الرياض " ١ / ٣٢: قال الترمذى: حديث حسن صحيح.','7','397',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3173,'<span data-type="title" id=toc-3180>٣١٧٣ </span>- عن الحسن بن على بن أبى طالب، رضى الله عنهما، قال: حفظت من رسول الله صلى الله عليه وسلم: " دع ما يريبك إلى ما لا يريبك، فإن الصدق طمأنينة والكذب ريبة ".
** ت
(رياض الصالحين ٣٤/١)
** قال الإمام النووى فى " الرياض " ١ / ٣٤: قال الترمذى: حديث صحيح.','7','398',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3174,'<span data-type="title" id=toc-3181>٣١٧٤ </span>- عن أبى ذر جندب بن جنادة، وأبى عبد الرحمن معاذ بن جبل، رضى الله عنهما عن رسول الله صلى الله عليه وسلم قال: " اتق الله حيثما كنت وأتبع السيئة الحسنة تمحها، وخالق الناس يخلق حسن ".
** ت
(رياض الصالحين ٣٧/١)
** قال الإمام النووى فى " الرياض " ١ / ٣٧: قال الترمذى: حديث حسن.','7','399',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3175,'<span data-type="title" id=toc-3182>٣١٧٥ </span>- عن ابن عباس رضى الله عنهما، قال: كنت خلف النبى صلى الله عليه وسلم يوما، فقال: " يا غلام إنى أعلمك كلمات، احفظ الله يحفظك، احفظ الله تجده تجاهك، إذا سألت فاسأل الله، وإذا استعنت فاستعن بالله، واعلم أن الأمة لو اجتمعت على أن ينفعوك بشىء، لم ينفعوك إلا بشىء قد كتبه الله لك، وإن اجتمعوا على أن يضروك بشىء، لم يضروك إلا بشىء قد كتبه الله عليك رفعت الأقلام وجفت الصحف ".
** ت
(رياض الصالحين ٣٧/١)
** قال الإمام النووى فى " الرياض " ١ / ٣٧: قال الترمذى: حديث حسن صحيح.','7','400',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3176,'<span data-type="title" id=toc-3183>٣١٧٦ </span>- عن أبى يعلى شداد بن أوس رضى الله عنه عن النبى صلى الله عليه وسلم قال: " الكيس من دان نفسه، وعمل لما بعد الموت، والعاجز من اتبع نفسه هواها وتمنى على الله ".
** ت
(رياض الصالحين ٤٠/١)
** قال الإمام النووى فى " الرياض " ١ / ٤٠: قال الترمذى: حديث حسن.','7','401',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3177,'<span data-type="title" id=toc-3184>٣١٧٧ </span>- عن أبى هريرة رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " من حسن إسلام المرء تركه ما لا يعنيه ".
** ت
(رياض الصالحين ٤٠/١)
** حسن','7','402',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3178,'<span data-type="title" id=toc-3185>٣١٧٨ </span>- عن أبى أمامة صدى بن عجلان الباهلى رضى الله عنه قال: سمعت رسول الله صلى الله عليه وسلم يخطب فى حجة الوداع، فقال: " اتقوا الله، وصلوا خمسكم، وصوموا شهركم، وأدوا زكاة أموالكم، وأطيعوا أمراءكم، تدخلوا جنة ربكم ".
** ت
(رياض الصالحين ٤٢/١)
** قال الإمام النووى فى " الرياض " ١ / ٤٢: قال الترمذى: حديث حسن صحيح.','7','403',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3179,'<span data-type="title" id=toc-3186>٣١٧٩ </span>- عن عمر رضى الله عنه قال: سمعت رسول الله صلى الله عليه وسلم يقول: " لو أنكم تتوكلون على الله حق توكله لرزقكم كما يرزق الطير، تغدوا خماصا وتروح بطانا ".
** ت
(رياض الصالحين ٤٥/١)
** قال الإمام النووى فى " الرياض " ١ / ٤٥: قال الترمذى حديث حسن.','7','404',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3180,'<span data-type="title" id=toc-3187>٣١٨٠ </span>- عن أنس رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " من قال _ يعنى إذا خرج من بيته _: بسم الله توكلت على الله، ولا حول ولا قوة إلا بالله يقال له: هديت وكفيت ووقيت وتنحى عنه الشيطان ".
** د ت ن
(رياض الصالحين ٤٦/١)
** قال الإمام النووى فى " الرياض " ١ / ٤٦: قال الترمذى: حديث حسن، زاد أبو داود: " فيقول _ يعنى الشيطان _ لشيطان أخر: كيف لك برجل قد هدى وكفى ووقى؟ ".','7','405',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3181,'<span data-type="title" id=toc-3188>٣١٨١ </span>- عن أم المؤمنين أم سلمة هند بنت أبى أمية حذيفة المخزومية رضى الله عنها أن النبى صلى الله عليه وسلم كان إذا خرج من بيته قال: " بسم الله توكلت على الله، اللهم إنى أعوذ بك أن أضل أو أضل، أو أزل أو أزل، أو أظلم أو أظلم، أو أجهل أو يجهل على ".
** د ت
(رياض الصالحين ٤٦/١)
** صحيح
** قال الإمام النووى فى " الرياض " ١ / ٤٦: قال الترمذى: حديث حسن صحيح وهذا لفظ أبى داود اه. قال الإمام النووى فى " الرياض " ١ / ٤٦: رواه أبو داود والترمذى وغيرهما بأسانيد صحيحة.','7','406',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3182,'<span data-type="title" id=toc-3189>٣١٨٢ </span>- عن أنس رضى الله عنه قال: كان أخوان على عهد النبى صلى الله عليه وسلم وكان أحدهما يأتى النبى صلى الله عليه وسلم والآخر يحترف، فشكا المحترف أخاه للنبى صلى الله عليه وسلم فقال: " لعلك ترزق به ".
** ت
(رياض الصالحين ٤٧/١)
** إسناده صحيح على شرط مسلم','7','407',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3183,'<span data-type="title" id=toc-3190>٣١٨٣ </span>- عن أبى هريرة رضى الله عنه، أن رسول الله صلى الله عليه وسلم قال: " بادروا بالأعمال سبعا، هل تنتظرون إلا فقرا منسيا، أو غنى مطغيا أو مرضا مفسدا أو هرما مفندا أو موتا مجهزا أو الدجال فشر غائب ينتظر أو الساعة فالساعة أدهى وأمر ".
** ت
(رياض الصالحين ٥٠/١)
** قال الإمام النووى فى " الرياض " ١ / ٥٠: قال الترمذى: حديث حسن.','7','408',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3184,'<span data-type="title" id=toc-3191>٣١٨٤ </span>- عن أبى صفوان عبد الله بن بسر الأسلمى رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " خير الناس من طال عمره وحسن عمله ".
** ت
(رياض الصالحين ٥٤/١)
** قال الإمام النووى فى " الرياض " ١ / ٥٤: قال الترمذى: حديث حسن.','7','409',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3185,'<span data-type="title" id=toc-3192>٣١٨٥ </span>- عن أبى نجيح العرباض بن سارية، رضى الله عنه قال: وعظنا رسول الله صلى الله عليه وسلم موعظة بليغة، وجلة منها القلوب وذرفت منها العيون، فقلنا يا رسول الله كأنها موعظة مودع فأوصنا قال: " أوصيكم بتقوى الله، والسمع والطاعة وإن تأمر عليكم عبد وإنه من يعش منكم فسيرى إختلافا كثيرا فعليكم بسنتى، وسنة الخلفاء الراشدين المهديين، عضوا عليها بالنواجذ، وإياكم ومحدثات الأمور فإن كل بدعة ضلالة ".
** د ت
(رياض الصالحين ٧٣/١)
** قال الإمام النووى فى " الرياض " ١ / ٧٣: قال الترمذى: حسن صحيح.','7','410',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3186,'<span data-type="title" id=toc-3193>٣١٨٦ </span>- عن حذيفة رضى الله عنه عن النبى صلى الله عليه وسلم قال: " والذى نفسى بيده لتأمرن بالمعروف، ولتنهون عن المنكر أو ليوشكن الله أن يبعث عليكم عقابا منه، ثم تدعونه فلا يستجاب لكم ".
** ت
(رياض الصالحين ٨٥/١)
** قال الإمام النووى فى " الرياض " ١ / ٨٥: قال الترمذى: حديث حسن.','7','411',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3187,'<span data-type="title" id=toc-3194>٣١٨٧ </span>- عن أبى سعيد الخدرى رضى الله عنه عن النبى صلى الله عليه وسلم قال: " أفضل الجهاد كلمة حق عند سلطان جائر ".
** د ت
(رياض الصالحين ٨٦/١)
** قال الإمام النووى فى " الرياض " ١ / ٨٦: قال الترمذى: حديث حسن.','7','412',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3188,'<span data-type="title" id=toc-3195>٣١٨٨ </span>- عن أبى عبد الله طارق بن شهاب البجلى الأحمسى رضى الله عنه، أن رجلا سأل النبى صلى الله عليه وسلم، وقد وضع رجله فى الغرز: أى الجهاد أفظل، قال: " كلمة حق عند سلطان جائر ".
** ن
(رياض الصالحين ٨٦/١)
** إسناده صحيح','7','413',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3189,'<span data-type="title" id=toc-3196>٣١٨٩ </span>- عن ابن مسعود رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " إن أول مادخل النقص على بنى إسرائيل أنه كان الرجل يلقى الرجل فيقول: يا هذا اتق الله ودع ما تصنع فإنه لا يحل لك، ثم يلقاه من الغد وهو على حاله فلا يمنعه ذلك أن يكون أكيله، وشريبه وقعيده، فلما فعلوا ذلك ضرب الله على قلوب بعضهم ببعض "، ثم قال: " لعن الذين كفروا من بنى إسرائيل على لسان داود وعيسى بن مريم، ذلك بما عصوا، وكانوا يعتدون، كانوا لا يتناهون عن منكر فعلوه لبئس ما كانوا يفعلون، ترى كثيرا منهم يتولون الذين كفروا لبئس ما قدمت لهم أنفسهم "، إلى قوله: " فاسقون "، ثم قال: " كلا والله لتأمرن بالمعروف، ولتنهون عن المنكر، ولتأخذن على يد الظالم ولتأطرنه على الحق أطرا، ولتقصرنه على الحق قصرا، أو ليضربن الله بقلوب بعضكم على بعض ثم ليلعنكم كما لعنهم ".
** د ت
(رياض الصالحين ٨٦/١)
** قال الإمام النووى فى " الرياض " ١ / ٨٦: قال الترمذى: حديث حسن.','7','414',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3190,'<span data-type="title" id=toc-3197>٣١٩٠ </span>- عن أبى بكر الصديق رضى الله عنه قال: يا أيها الناس إنكم لتقرؤون هذه الآية: " يا أيها الذين آمنوا عليكم أنفسكم لا يضركم من ضل إذا اهتديتم " وإنى سمعت رسول الله صلى الله عليه وسلم، يقول: " إن الناس إذا رأوا الظالم فلم يأخذوا على يديه أوشك أن يعمهم الله بعقاب منه ".
** د ت ن
(رياض الصالحين ٨٧/١)
** إسناده صحيح','7','415',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3191,'<span data-type="title" id=toc-3198>٣١٩١ </span>- عن أبى هريرة رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " المسلم أخزو المسلم لا يخونه ولا يكذبه ولا يخذله، كل المسلم على المسلم حرام، عرضه، وماله، ودمه، التقوى هاهنا، بحسب امرىء من الشر أن يحقر أخاه المسلم ".
** ت
(رياض الصالحين ٩٩/١)
** قال الإمام النووى فى " الرياض " ١ / ٩٩: قال الترمذى: حديث حسن.','7','416',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3192,'<span data-type="title" id=toc-3199>٣١٩٢ </span>- عن أبى شريح خويلد بن عمرو الخزاعى رضى الله عنه قال: قال النبى صلى الله عليه وسلم: " اللهم إنى أحرج حق الضعيفين اليتيم والمرأة ".
** ن
(رياض الصالحين ١١١/١)
** حسن
** قال الإمام النووى فى " الرياض " ١ / ١١١: رواه النسائى بإسناد جيد.','7','417',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3193,'<span data-type="title" id=toc-3200>٣١٩٣ </span>- عن أبى الدرداء عويمر رضى الله عنه قال: سمعت رسول الله صلى الله عليه وسلم " ابغونى الضعفاء، فإنما تنصرون، وترزقون بضعفائكم ".
** د
(رياض الصالحين ١١١/١)
** إسناده جيد','7','418',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3194,'<span data-type="title" id=toc-3201>٣١٩٤ </span>- عن عمرو بن الأحوص الجشمى رضى الله عنه أنه سمع النبى صلى الله عليه وسلم فى حجة الوداع يقول بعد أن حمد الله تعالى وأثنى عليه وذكر ووعظ ثم قال: " ألا واستوصوا بالنساء خيرا، فإنما هن عوان عندكم، ليس تملكون منهن شيئا غير ذلك، إلا أن يأتين بفاحشة مبينة، فإن فعلن فاهجروهن فى المضاجع، واضربوهن ضربا غير مبرح، فإن أطعنكم فلا تبغوا عليهم سبيلا، ألا إن لكم على
نسائكم حقا، ولنسائكم عليكم حقا، فحقكم عليهن أن لا يوطئن فرشكم من تكرهون ولا يأذن فى بيوتكم لمن تكرهون، ألا وحقهن عليكم أن تحسنوا إليهن فى كسوتهن
وطعامهن ".
** ت
(رياض الصالحين ١١٣/١)
** قال الإمام النووى فى " الرياض " ١ / ١١٣: قال الترمذى: حديث حسن صحيح.','7','419',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3195,'<span data-type="title" id=toc-3202>٣١٩٥ </span>- عن معاوية بن حيدة رضى الله عنه قال: قلت يا رسول الله ما حق زوجة أحدنا عليه؟ ، قال: " إن تطعمها إذا طعمت وتكسوها إذا اكتسيت ولا تضرب الوجه ولا تقبح ولا تهجر إلا فى البيت ".
** د
(رياض الصالحين ١١٣/١)
** حسن','7','420',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3196,'<span data-type="title" id=toc-3203>٣١٩٦ </span>- عن أبى هريرة رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " أكمل المؤمنين إيمانا أحسنهم خلقا، وخياركم خياركم لنسائهم ".
** ت
(رياض الصالحين ١١٤/١)
** قال الإمام النووى فى " الرياض " ١ / ١١٥: قال الترمذى: حديث حسن صحيح.','7','421',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3197,'<span data-type="title" id=toc-3204>٣١٩٧ </span>- عن إياس بن عبد الله بن أبى ذباب رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " لا تضربوا إماء الله "، فجاء عمر رضى الله عنه إلى رسول الله صلى الله عليه وسلم فقال: ذئرن النساء على أزواجهم، فرفخص فى ضربهن فأطاف بأل رسول الله صلى الله عليه وسلم نساء كثير يشكون أزواجهم، فقال رسول الله صلى الله عليه وسلم: " لقد أطاف بآل بيت محمد نساء كثيرات يشكون أزواجهم ليس
أولئك بخياركم ".
** د
(رياض الصالحين ١١٤/١)
** إسناد صحيح','7','422',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3198,'<span data-type="title" id=toc-3205>٣١٩٨ </span>- عن أبى على طلق بن على رضى الله عنه أن رسول الله صلى الله عليه وسلم قال: " إذا دعاالرجل زوجته لحاجته فلتأته، وإن كانت على التنور ".
** ت ن
(رياض الصالحين ١١٥/١)
** قال الإمام النووى فى " الرياض " ١ / ١١٥: قال الترمذى: حديث حسن صحيح.','7','423',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3199,'<span data-type="title" id=toc-3206>٣١٩٩ </span>- عن أبى هريرة رضى الله عنه عن النبى صلى الله عليه وسلم قال: " لو كنت أمرا أحدا أن يسجد لأحد لأمرت المرأة أن تسجد لزوجها ".
** ت
(رياض الصالحين ١١٥/١)
** قال الإمام النووى فى " الرياض " ١ / ١١٥: قال الترمذى: حديث حسن صحيح','7','424',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3200,'<span data-type="title" id=toc-3207>٣٢٠٠ </span>- عن أم سلمة رضى الله عنها قالت: قال رسول الله صلى الله عليه وسلم: " أيما امرأة ماتت، وزوجها عنها راض دخلت الجنة ".
** ت
(رياض الصالحين ١١٦/١)
** قال الإمام النووى فى " الرياض " ١ / ١١٦: قال الترمذى: حديث حسن.','7','425',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3201,'<span data-type="title" id=toc-3208>٣٢٠١ </span>- عن معاذ بن جبل رضى الله عنه عن النبى صلى الله عليه وسلم قال: " لا تؤذى امرأة زوجها فى الدنيا إلا قالت زوجته من الحور العين لا تؤذيه قاتلك الله فإنما هو عندك دخيل يوشك أن يفارقك إلينا ".
** ت
(رياض الصالحين ١١٦/١)
** قال الإمام النووى فى " الرياض " ١ / ١١٦: قال الترمذى: حديث حسن.','7','426',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3202,'<span data-type="title" id=toc-3209>٣٢٠٢ </span>- عن عبد الله بن عمرو بن العاص رضى الله عنهما قال: قال رسول الله صلى الله عليه وسلم: " كفى بالمرء إثما أن يضيع من يقوت ".
** د
(رياض الصالحين ١١٧/١)
** صحيح','7','427',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3203,'<span data-type="title" id=toc-3210>٣٢٠٣ </span>- عن عمرو بن شعيب عن أبيه عن جده رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " مروا أولادكم بالصلاة أبناء سبع سنين واضربوهم عليها وهم أبناء عشر وفرقوا بينهم فى المضاجع ".
** د
(رياض الصالحين ١٢٠/١)
** حسن
** قال الإمام النووى فى " الرياض " ١ / ١٢٠: رواه أبو داود بإسناد حسن.','7','428',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3204,'<span data-type="title" id=toc-3211>٣٢٠٤ </span>- عن أبى ثرية سبرة بن معبد الجهنى رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " علموا الصبى الصلاة لسبع سنين، واضربوه عليها ابن عشر سنين ".
** د ت
(رياض الصالحين ١٢٠/١)
** حسن
** قال الإمام النووى فى " الرياض " ١ / ١٢٠: قال الترمذى: حديث حسن.','7','429',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3205,'<span data-type="title" id=toc-3212>٣٢٠٥ </span>- عن عبد الله بن عمر برضى الله عنهما قال: قال رسول الله صلى الله عليه وسلم: " خير الأصحاب عند الله تعالى خيرهم لصاحبه، وخير الجيران عند الله تعالى خيرهم لجاره ".
** ت
(رياض الصالحين ١٢٢/١)
** قال الإمام النووى فى " الرياض " ١ / ١٢٢: قال الترمذى: حديث حسن.','7','430',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3206,'<span data-type="title" id=toc-3213>٣٢٠٦ </span>- عن سلمان بن عامر رضى الله عنه، عن النبى صلى الله عليه وسلم قال: " إذا أفطر أحدكم فليفطر على تمر، فإنه بركة، فإن لم يجد تمرا فالماء فإنه طهور ". وقال: " الصدقة على المسكين صدقة وعلى ذى الرحم ثنتان: صدقة وصلة ".
** ت
(رياض الصالحين ١٢٨/١)
** قال الإمام النووى فى " الرياض " ١ / ١٢٨: قال الترمذى: حديث حسن.','7','431',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3207,'<span data-type="title" id=toc-3214>٣٢٠٧ </span>- عن ابن عمر رضى الله عنهما قال: كانت تحتى امرأة وكنت أحبها وكان عمر يكرهها فقال لى: طلقها، فأبيت، فأتى عمر رضى الله عنه النبى صلى الله عليه وسلم فذكر ذلك، فقال النبى صلى الله عليه وسلم: " طلقها ".
** د ت
(رياض الصالحين ١٢٨/١)
** قال الإمام النووى فى " الرياض " ١ / ١٢٨: قال الترمذى: حديث حسن صحيح.','7','432',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3208,'<span data-type="title" id=toc-3215>٣٢٠٨ </span>- عن أبى الدرداء رضى الله عنه أن رجلا أتاه فقال: إن لى امرأة وإن أمى تأمرنى بطلاقها فقال: سمعت رسول الله صلى الله عليه وسلم يقول: " الوالد أوسط أبواب الجنة، فإن شئت فأضع ذلك الباب، أو احفظه ".
** ت
(رياض الصالحين ١٢٨/١)
** قال الإمام النووى فى " الرياض " ١ / ١٢٨: قال الترمذى: حديث حسن صحيح.','7','433',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3209,'<span data-type="title" id=toc-3216>٣٢٠٩ </span>- عن البراء بن عازب رضى الله عنهما عن النبى صلى الله عليه وسلم قال: " الخالة بمنزلة الأم ".
** ت
(رياض الصالحين ١٢٩/١)
** قال الإمام النووى فى " الرياض " ١ / ١٢٩: قال الترمذى: حديث حسن صحيح.','7','434',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3210,'<span data-type="title" id=toc-3217>٣٢١٠ </span>- عن أبى موسى رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " إن من إجلال الله تعالى إكرام ذى الشيبة المسلم، وحامل القرآن غير الغالى فيه، والجافى عنه وإكرام ذى السلطان المقسط ".
** د
(رياض الصالحين ١٣٦/١)
** حسن','7','435',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3211,'<span data-type="title" id=toc-3218>٣٢١١ </span>- عن عمرو بن شعيب عن أبيه عن جده رضى الله عنهم قال: قال رسول الله صلى الله عليه وسلم: " ليس منا من لم يرحم صغيرنا ويعرف شرف كبيرنا ".
** د ت
(رياض الصالحين ١٣٦/١)
** صحيح
** قال الإمام النووى فى " الرياض " ١ / ١٣٦: قال الترمذى: حديث حسن صحيح. وفى رواية أبى داود " حق كبيرنا ".','7','436',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3212,'<span data-type="title" id=toc-3219>٣٢١٢ </span>- عن أنس رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " ما أكرم شاب شيخا لسنه إلا قيض الله له من يكرمه عند سنه ".
** ت
(رياض الصالحين ١٣٧/١)
** قال الإمام النووى فى " الرياض " ١ / ١٣٧: قال الترمذى: حديث غريب.','7','437',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3213,'<span data-type="title" id=toc-3220>٣٢١٣ </span>- عن أبى هريرة رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " من عاد مريضا أو زار أخا له فى الله، ناداه مناد: بأن طبت، وطاب ممشاك وتبوأت من الجنة منزلا ".
** ت
(رياض الصالحين ١٣٨/١)
** قال الإمام النووى فى " الرياض " ١ / ١٣٨: قال الترمذى: حديث حسن.اه. قال الإمام النووى فى " الرياض " ١ / ١٣٨: وفى بعض النسخ غريب.','7','438',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3214,'<span data-type="title" id=toc-3221>٣٢١٤ </span>- عن أبى سعيد الخدرى رضى الله عنه عن النبى صلى الله عليه وسلم قال: " لا تصاحب إلا مؤمنا ولا يأكل طعامك إلا تقى ".
** د ت
(رياض الصالحين ١٣٩/١)
** إسناد لابأس به','7','439',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3215,'<span data-type="title" id=toc-3222>٣٢١٥ </span>- عن أبى هريرة رضى الله عنه، أن النبى صلى الله عليه وسلم، قال: " الرجل على دين خليله، فلينظر أحدكم من يخالل ".
** د ت
(رياض الصالحين ١٣٩/١)
** إسناده صحيح
** قال الإمام النووى فى " الرياض " ١ / ١٣٩: قال الترمذى: حديث حسن.','7','440',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3216,'<span data-type="title" id=toc-3223>٣٢١٦ </span>- عن عمر بن الخطاب رضى الله عنه قال: استأذنت النبى صلى الله عليه وسلم فى العمرة فأذن لى، وقال: " لا تنسنا يا أخى من دعائك "، فقال كلمة ما يسرنى أن لى بها الدنيا. وفى رواية قال: " أشركنا ياأخى فى دعائك ".
** د ت
(رياض الصالحين ١٤٢/١)
** صحيح
** قال الإمام النووى فى " الرياض " ١ / ١٤٢: قال الترمذى: حديث حسن صحيح.','7','441',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3217,'<span data-type="title" id=toc-3224>٣٢١٧ </span>- عن معاذ رضى الله عنه قال: سمعنا رسول الله صلى الله عليه وسلم يقول: " قال الله عز وجل: المتحابون فى جلالى، لهم منابر من نور يغبطهم النبيون والشهداء ".
** ت
(رياض الصالحين ١٤٤/١)
** قال الإمام النووى فى " الرياض " ١ / ١٤٤: قال الترمذى: حديث حسن صحيح.','7','442',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3218,'<span data-type="title" id=toc-3225>٣٢١٨ </span>- عن أبى إدريس الخولانى رحمه الله قال: دخلت مسجد دمشق، فإذا فتى براق الثناياو إذا الناس معه، فإذا اختلفوا فى شىء أسندوه إليه، وصدروا عن رأيه فسألت عنه، فقيل: هذا معاذ بن جبل رضى الله عنه، فلما كان من الغد هجرت فوجدته قد سبقنى بالتهجير ووجدته يصلى فانتظرته حتى قضى صلاته ثم جئته من قبل وجهه فسلمت عليه، ثم قلت: والله إنى لأحبك لله، فقال: آلله؟ ، فقلت: الله، فقال: آلله؟ ، فقلت: الله، فأخذنى بحبوة ردائى فجذبنى إليه فقال: أبشر فإنى سمعت رسول الله صلى الله عليه وسلم يقول: " قال الله تعالى بوجبت محبتى للمتحابين فى، والمتجالسين فى، والمتزاورين فى، والمتباذلين
فى ".
** ما
(رياض الصالحين ١٤٤/١)
** إسناده صحيح
** قال الإمام النووى فى " الرياض " ١ / ١٤٤: رواه مالك فى الموطأ بإسناده الصحيح.','7','443',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3219,'<span data-type="title" id=toc-3226>٣٢١٩ </span>- عن أبى كريمة المقداد بن معديكرب رضى الله عنه عن النبى صلى الله عليه وسلم قال: " إذا أحب الرجل أخاه، فليخبره أنه يحبه ".
** د ت
(رياض الصالحين ١٤٤/١)
** قال الإمام النووى فى " الرياض " ١ / ١٤٤: قال الترمذى: حديث حسن.','7','444',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3220,'<span data-type="title" id=toc-3227>٣٢٢٠ </span>- عن معاذ رضى الله عنه أن رسول الله صلى الله عليه وسلم أخذ بيده وقال: " يا معاذ والله إنى لأحبك، ثم أوصيك يا معاذ لا تدعن فى دبر كل صلاة تقول: اللهم أعنى على ذكرك وشكرك وحسن عبادتك ".
** د ن
(رياض الصالحين ١٤٤/١)
** صحيح
** قال الإمام النووى فى " الرياض " ١ / ١٤٥: رواه أبو داود والنسائى بإسناد صحيح.','7','445',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3221,'<span data-type="title" id=toc-3228>٣٢٢١ </span>- عن أنس رضى الله عنه أن رجلا كان عند النبى صلى الله عليه وسلم فمر رجل به فقال: يارسول الله إنى لأحب هذا، فقال له النبى صلى الله عليه وسلم: " أأعلمته؟ "، قال: لا، قال: " أعلمه "، فلحقه، فقال: إنى أحبك فى الله، فقال: أحبك الذى أحببتنى له.
** د
(رياض الصالحين ١٤٥/١)
** إسناده صحيح','7','446',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3222,'<span data-type="title" id=toc-3229>٣٢٢٢ </span>- عن أبى ذر رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " إنى أرى مالا ترون، أطت السماء وحق لها أن تئط، ما فيها موضع أربع أصابع إلا ملك واضع جبهته ساجدا لله تعالى، والله لو تعلمون ما أعلم، لضحكتم قليلا، ولبكيتم كثيرا، وما تلذذتم بالنساء على القرش، ولخرجتم إلى الصعدات تجأرون إلى الله تعالى ".
** ت
(رياض الصالحين ١٥٢/١)
** قال الإمام النووى فى " الرياض " ١ / ١٥٣: قال الترمذى: حديث حسن.','7','447',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3223,'<span data-type="title" id=toc-3230>٣٢٢٣ </span>- عن أبى برزة نضلة بن عبيد الأسلمى رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " لا تزول قدما عبد حتى يسأل عن عمره فيم أفناه وعن علمه فيم فعل فيه، وعن ماله من أين اكتسبه، وفيم أنفقه، وعن جسمه فيم أبلاه ".
** ت
(رياض الصالحين ١٥٣/١)
** قال الإمام النووى فى " الرياض " ١ / ١٥٣: قال الترمذى: حديث حسن صحيح.','7','448',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3224,'<span data-type="title" id=toc-3231>٣٢٢٤ </span>- عن أبى هريرة رضى الله عنه قال: قرأ رسول الله صلى الله عليه وسلم (يومئذ تحدث أخبارها) ثم قال: " أتدرون ما أخبارها؟ "، قالوا: الله ورسوله أعلم، قال: " فإن أخبارها أن تشهد على كل عبد أو أمة بما عمل على ظهرها، تقول: عملت كذا وكذا، فى يوم كذا وكذا، فهذه أخبارها ".
** ت
(رياض الصالحين ١٥٣/١)
** قال الإمام النووى فى " الرياض " ١ / ١٥٣: قال الترمذى: حديث حسن.','7','449',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3225,'<span data-type="title" id=toc-3232>٣٢٢٥ </span>- عن أبى سعيد الخدرى رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " كيف أنعم، وصاحب القرن قد التقم القرن، واستمع الإذن متى يؤمر بالنفخ فينفخ "، فكأن ذلك ثقل على أصحاب رسول الله صلى الله عليه وسلم، فقال لهم: " قولوا حسبنا الله ونعم الوكيل ".
** ت
(رياض الصالحين ١٥٣/١)
** قال الإمام النووى فى " الرياض " ١ / ١٥٤: قال الترمذى: حديث حسن.','7','450',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3226,'<span data-type="title" id=toc-3233>٣٢٢٦ </span>- عن أبى هريرة رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " من خاف أدلج، ومن أدلج بلغ المنزل، ألا إن سلعة الله غالية ألا إن سلعة الله الجنة ".
** ت
(رياض الصالحين ١٥٤/١)
** قال الإمام النووى فى " الرياض " ١ / ١٥٤: قال الترمذى: حديث حسن.
** تعقيب: قال الإمام النووى فى " الرياض " ١ / ١٥٤: قال الترمذى: حديث حسن.','7','451',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3227,'<span data-type="title" id=toc-3234>٣٢٢٧ </span>- عن أنس رضى الله عنه قال: سمعت رسول الله صلى الله عليه وسلم يقول: " قال الله تعالى: يا ابن آدم، إنك ما دعوتنى، ورجوتنى غفرت لك على ما كان منك ولا أبالى، يا ابن آدم، لو بلغت ذنوبك عنان السماء، ثم استغفرتنى غفرت لك، ياابن آدم، إنك لو أتيتنى بقراب الأرض خطايا، ثم لقيتنى لا تشرك بى شيئا، لأتيتك بقرابها مغفرة ".
** ت
(رياض الصالحين ١٦٦/١)
** قال الإمام النووى فى " الرياض " ١ / ١٦٦: قال الترمذى: حديث حسن.','7','452',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3228,'<span data-type="title" id=toc-3235>٣٢٢٨ </span>- عن أبى هريرة رضى الله عنه، قال: قال رسول الله صلى الله عليه وسلم: " لا يلج النار رجل بكى من خشية الله حتى يعود اللبن فى الضرع ولا يجتمع غبار فى سبيل الله ودخان جهنم ".
** ت
(رياض الصالحين ١٦٨/١)
** قال الإمام النووى فى " الرياض " ١ / ١٦٨: قال الترمذى: حديث حسن صحيح.','7','453',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3229,'<span data-type="title" id=toc-3236>٣٢٢٩ </span>- عن عبد الله بن الشخير رضى الله عنه قال: أتيت رسول الله صلى الله عليه وسلم وهو يصلى ولجوفه أزيز كأزيز المرجل من البكاء ".
** د تم
(رياض الصالحين ١٦٨/١)
** صحيح
** قال الإمام النووى فى " الرياض " ١ / ١٦٨: رواه أبو داود والترمذى فى الشمائل بإسناد صحيح.','7','454',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3230,'<span data-type="title" id=toc-3237>٣٢٣٠ </span>- عن أبى أمامة صدى بن عجلان الباهلى رضى الله عنه عن النبى صلى الله عليه وسلم قال: " ليس شىء أحب إلى الله تعالى من قطرتين، وأثرين قطرة دموع من خشية الله، وقطرة دم من تهراق فى سبيل الله، وأما الأثران: فأثر فى سبيل الله تعالى، وأثر فى فريضة من فرائض الله تعالى ".
** ت
(رياض الصالحين ١٦٩/١)
** قال الإمام النووى فى " الرياض " ١ / ١٧٠: قال الترمذى: حديث حسن.','7','455',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3231,'<span data-type="title" id=toc-3238>٣٢٣١ </span>- عن أبى العباس سهل بن سعد الساعدى رضى الله عنه قال: جاء رجل إلى النبى صلى الله عليه وسلم فقال: يا رسول الله دلنى على عمل إذا عملته أحبنى الله وأحبنى الناس، فقال: " ازهد فى الدنيا يحبك الله، وازهد فيما عند الناس يحبك الناس ".
** جه
(رياض الصالحين ١٧٥/١)
** حسن','7','456',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3232,'<span data-type="title" id=toc-3239>٣٢٣٢ </span>- عن سهل بن سعد الساعدى رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " لو كانت الدنيا تعدل عند الله جناح بعوضة ما سقى كافرا منها شربة ماء ".
** ت
(رياض الصالحين ١٧٦/١)
** قال الإمام النووى فى " الرياض " ١ / ١٧٦: قال الترمذى: حديث حسن صحيح.','7','457',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3233,'<span data-type="title" id=toc-3240>٣٢٣٣ </span>- عن أبى هريرة رضى الله عنه قال: سمعت رسول الله صلى الله عليه وسلم يقول: " ألا إن الدنيا ملعونة، ملعون ما فيها، إلا بذكر الله تعالى وما والاه وعالما ومتعلما ".
** ت
(رياض الصالحين ١٧٦/١)
** قال الإمام النووى فى " الرياض " ١ / ١٧٦: قال الترمذى: حديث حسن.','7','458',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3234,'<span data-type="title" id=toc-3241>٣٢٣٤ </span>- عن عبد الله بن مسعود رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " لا تتخذوا الضيعة فترغبوا فى الدنيا ".
** ت
(رياض الصالحين ١٧٦/١)
** قال الإمام النووى فى " الرياض " ١ / ١٧٦: قال الترمذى: حديث حسن.','7','459',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3235,'<span data-type="title" id=toc-3242>٣٢٣٥ </span>- عن عبد الله بن عمرو بن العاص رضى الله عنهما قال: مر علينا رسول الله صلى الله عليه وسلم، ونحن نعالج خضا لنا فقال: " ما هذا؟ "، فقلنا: قد وهى، فنحن نصلحه، فقال: " ما أرى الأمر إلا أعجل من ذلك ".
** د ت
(رياض الصالحين ١٧٦/١)
** قال الإمام النووى فى " الرياض " ١ / ١٧٦: قال الترمذى: حديث حسن صحيح.','7','460',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3236,'<span data-type="title" id=toc-3243>٣٢٣٦ </span>- عن كعب بن عياض رضى الله عنه قال: سمعت رسول الله صلى الله عليه وسلم يقول: " إن لكل أمة فتنة وفتنة أمتى المال ".
** ت
(رياض الصالحين ١٧٧/١)
** قال الإمام النووى فى " الرياض " ١ / ١٧٧: قال الترمذى: حديث حسن صحيح.','7','461',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3237,'<span data-type="title" id=toc-3244>٣٢٣٧ </span>- عن أبى عمرو ويقال: أبو عبد الله ويقال: أبو ليلى، عثمان بن عفان رضى الله عنه أن النبى صلى الله عليه وسلم قال: " ليس لابن آدم حق فى سوى هذه الخصال: بيت يسكنه، وثوب يوارى عورته، وجلف الخبز، والماء ".
** ت
(رياض الصالحين ١٧٧/١)
** قال الإمام النووى فى " الرياض " ١ / ١٧٧: قال الترمذى: حديث صحيح.','7','462',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3238,'<span data-type="title" id=toc-3245>٣٢٣٨ </span>- عن عبد الله بن مغفل رضى الله عنه قال: قال رجل للنبى صلى الله عليه وسلم: يا رسول الله والله إنى لأحبك، فقال: " أنظر ماذا تقول؟ " قال: والله إنى لأحبك _ ثلاث مرات _ فقال: " إن كنت تحبنى فأعد للفقر تجفافا، فإن الفقر أسرع إلى من يحبنى من السيل إلى منتهاه ".
** ت
(رياض الصالحين ١٧٧/١)
** قال الإمام النووى فى " الرياض " ١ / ١٧٧: قال الترمذى: حديث حسن.','7','463',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3239,'<span data-type="title" id=toc-3246>٣٢٣٩ </span>- عن طعب بن مالك رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " ما ذئبان جائعان أرسلا فى غنم، بأفسد لها من حرص المرء على المال، والشرف لدينه ".
** ت
(رياض الصالحين ١٧٨/١)
** قال الإمام النووى فى " الرياض " ١ / ١٧٨: قال الترمذى: حديث حسن صحيح.','7','464',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3240,'<span data-type="title" id=toc-3247>٣٢٤٠ </span>- عن عبد الله بن مسعود رضى الله عنه قال: نام رسول الله صلى الله عليه وسلم على حصيرة فقالم وقد أثر فى جنبه، قلنا: يا رسول الله لو اتخذنا لك وطاء فقال: " مالى وللدنيا؟ ، ما أنا فى الدنيا إلا كراكب استظل تحت شجرة، ثم راح وتركها ".
** ت
(رياض الصالحين ١٧٨/١)
** قال الإمام النووى فى " الرياض " ١ / ١٧٨: قال الترمذى: حديث حسن صحيح.','7','465',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3241,'<span data-type="title" id=toc-3248>٣٢٤١ </span>- عن أبى هريرة رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " يدخل الفقراء الجنة قبل الأغنياء بخمسمائة عام ".
** ت
(رياض الصالحين ١٧٨/١)
** قال الإمام النووى فى " الرياض " ١ / ١٧٨: قال الترمذى: حديث صحيح.','7','466',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3242,'<span data-type="title" id=toc-3249>٣٢٤٢ </span>- عن أبى أمامة رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " يا ابن آدم إنك أن تبذل الفضل خير لك، وأن بمسكه شر لك ولا تلام على كفاف وابدأ بمن تعول ".
** ت
(رياض الصالحين ١٨٤/١)
** قال الإمام النووى فى " الرياض " ١ / ١٨٦: قال الترمذى: حديث حسن صحيح.','7','467',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3243,'<span data-type="title" id=toc-3250>٣٢٤٣ </span>- عن عبيد الله بن محصن الأنصارى الخطمى رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " من أصبح منكم آمنا فى سربه، معافى فى جسده، عنده قوت يومه، فكأنما حيزت له الدنيا بحذافيرها ".
** ت
(رياض الصالحين ١٨٦/١)
** قال الإمام النووى فى " الرياض " ١ / ١٨٦: قال الترمذى: حديث حسن.','7','468',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3244,'<span data-type="title" id=toc-3251>٣٢٤٤ </span>- عن أبى محمد فضالة بن عبيد الأنصارى رضى الله عنه أنه سمع رسول الله صلى الله عليه وسلم يقول: " طوبى لمن هدى إلى الإسلام وكان عيشه كفافا وقنع ".
** ت
(رياض الصالحين ١٨٦/١)
** قال الإمام النووى فى " الرياض " ١ / ١٨٦: قال الترمذى: حديث حسن صحيح.','7','469',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3245,'<span data-type="title" id=toc-3252>٣٢٤٥ </span>- عن ابن عباس رضى الله عنهما قال: كان رسول الله صلى الله عليه وسلم يبيت الليالى المتتابعة طاويا وأهله لا يجدون عشاء وكان أكثر خبزهم خبز الشعير.
** ت
(رياض الصالحين ١٨٦/١)
** قال الإمام النووى فى " الرياض " ١ / ١٨٦: قال الترمذى: حديث حسن صحيح.','7','470',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3246,'<span data-type="title" id=toc-3253>٣٢٤٦ </span>- عن فضالة بن عبيد رضى الله عنه أن رسول الله صلى الله عليه وسلم كان إذا صلى بالناس يخر رجال من قامتهم فى الصلاة من الخصاصة _ وهم أصحاب الصفة _ حتى يقول الأعراب: هؤلاء مجانين، فإذا صلى رسول الله صلى الله عليه وسلم انصرف إليهم فقال: " لو تعلمون ما لكم عند الله تعالى، لأجبتم أن تزدادوا فاقة وحاجة ".
** ت
(رياض الصالحين ١٨٦/١)
** قال الإمام النووى فى " الرياض " ١ / ١٨٦: قال الترمذى: حديث صحيح.','7','471',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3247,'<span data-type="title" id=toc-3254>٣٢٤٧ </span>- عن أبى كريمة المقدام بن معديكرب رضى الله عنه قال: سمعت أن رسول الله صلى الله عليه وسلم يقول: " ما ملأ آدمى وعاء شرا من بطنه، بحسب ابن آدم أكلات يقمن صبه فإن كان لا محالة فثلث لطعامه، وثلث لشرابه، وثلث لنفسه ".
** ت
(رياض الصالحين ١٨٧/١)
** قال الإمام النووى فى " الرياض " ١ / ١٨٧: قال الترمذى: حديث حسن.','7','472',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3248,'<span data-type="title" id=toc-3255>٣٢٤٨ </span>- عن أسماء بنت يزيد رضى الله عنها قالت: كان كم قميص رسول الله صلى الله عليه وسلم إلى الرصغ.
** د ت
(رياض الصالحين ١٨٨/١)
** قال الإمام النووى فى " الرياض " ١ / ١٨٨: قال الترمذى: حديث حسن.','7','473',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3249,'<span data-type="title" id=toc-3256>٣٢٤٩ </span>- عن سمرة بن جندب رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " إن المسألة كد يكد بها الرجل وجهه إلا أن يسأل الرجل سلطانا أوفى أمر لا بد منه ".
** ت
(رياض الصالحين ١٩٥/١)
** قال الإمام النووى فى " الرياض " ١ / ١٩٥: قال الترمذى: حديث حسن صحيح.','7','474',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3250,'<span data-type="title" id=toc-3257>٣٢٥٠ </span>- عن ابن مسعود رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " من أصابته فاقة فأنزلها بالناس لم تسد فاقته، ومن أنزلها بالله، فيوشك الله له برزق عاجل أو آجل ".
** د ت
(رياض الصالحين ١٩٥/١)
** قال الإمام النووى فى " الرياض " ١ / ١٩٥: قال الترمذى: حديث حسن.','7','475',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3251,'<span data-type="title" id=toc-3258>٣٢٥١ </span>- عن ثوبان رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " من تكفل لى أن لايسأل الناس شيئا، وأتكفل له بالجنة؟ "، فقلت: أنا فكان لا يسأل أحدا شيئا.
** د
(رياض الصالحين ١٩٥/١)
** إسناده صحيح','7','476',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3252,'<span data-type="title" id=toc-3259>٣٢٥٢ </span>- عن أبى كبشة عمر بن سعد الأنمارى رضى الله عنه أنه سمع رسول الله صلى الله عليه وسلم يقول: " ثلاثة أقسم عليهن، وأحدثكم حديثا فاحفظوه: ما نقص مال عبد من صدقة، ولا ظلم عبد مظلمة صبر عليها إلا زاده الله عزا، ولا فتح عبد باب مسألة إلا فتح الله عليه باب فقر أو كلمة نحوها، أو كلمة نحوها، وأحدثكم حديثا فاحفظوه "، قال: " إنما الدنيا لأربعة نفر: عبد رزقه الله مالا وعلما، فهو يتقى فيه ربه، ويصل فيه رحمه ويعلم لله فيه حقا فهذا بأفضل المنازل، وعبد رزقه الله علما ولم يرزقه مالا فهو صادق النية يقول: لو أن لى مالا لعملت بعمل فلان فهو نيته فأجرهما سواء، وعبد رزقه الله مالا ولم يرزقه علما فهو يخبط فى ماله بغير علم، لا يتقى فيه ربه، ولا يصل فيه رحمه، ولا يعلم لله حقا، فهذا بأخبث المنازل،
وعبد لم يرزقه الله مالا ولا علما فهو يقول: لو أن لى مالا لعملت فيه بعمل فلان، فهو نيته فوزرهما سواء ".
** ت
(رياض الصالحين ٢٠٠/١)
** قال الإمام النووى فى " الرياض " ١ / ٢٠٠: قال الترمذى: حديث حسن صحيح.','7','477',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3253,'<span data-type="title" id=toc-3260>٣٢٥٣ </span>- عن عائشة رضى الله عنها أنهم ذبحوا شاة فقال النبى صلى الله عليه وسلم: " ما بقى منها؟ "، قالت: ما بقى منها إلا كتفها، قال: " بقى كلها غير كتفها ".
** ت
(رياض الصالحين ٢٠٠/١)
** قال الإمام النووى فى " الرياض " ١ / ٢٠٠: قال الترمذى: حديث صحيح.','7','478',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3254,'<span data-type="title" id=toc-3261>٣٢٥٤ </span>- عن أبى هريرة رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " أكثروا ذكر هاذم اللذات ".
** ت
(رياض الصالحين ٢٠٩/١)
** قال الإمام النووى فى " الرياض " ١ / ٢٠٩: قال الترمذى: حديث حسن.','7','479',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3255,'<span data-type="title" id=toc-3262>٣٢٥٥ </span>- عن أبى بن كعب رضى الله عنه كان رسول الله صلى الله عليه وسلم إذا ذهب ثلث الليل قام فقال: " يا أيها الناس اذكروا الله جاءت الراجفة تتبعها الرادفة جاء الموت بما فيه، جاء الموت بما فيه "، قلت: يا رسول الله إنى أكثر الصلاة عليك فكم أجعل لك من صلاتى؟ ، قال: " ما شئت "، قلت: الربع؟ ، قال: " ما شئت فإن زدت فهو خير لك "، قلت: فالنصف؟ ، قال: " ما شئت فإن زدت فهو خير لك " قلت: فالثلثين؟ ، قال: " ما شئت فإن زدت فهو خير لك "، قلت: أجعل لك صلاتى كلها؟ ، قال: " إذا تكفى همك، ويغفر لك ذنبك ".
** ت
(رياض الصالحين ٢٠٩/١)
** قال الإمام النووى فى " الرياض " ١ / ٢٠٩: قال الترمذى: حديث حسن.','7','480',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3256,'<span data-type="title" id=toc-3263>٣٢٥٦ </span>- عن ابن عباس رضى الله عنهما، قال: مر رسول الله صلى الله عليه وسلم بقبور بالمدينة فأقبل عليهم بوجهه فقال: " السلام عليكم يا أهل القبور يغفر الله لنا ولكم أنتم سلفنا ونحن بالأثر ".
** ت
(رياض الصالحين ٢١٠/١)
** قال الإمام النووى فى " الرياض " ١ / ٢١٠: قال الترمذى: حديث حسن.','7','481',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3257,'<span data-type="title" id=toc-3264>٣٢٥٧ </span>- عن وابصة بن معبد رضى الله عنه قال: أتيت رسول الله صلى الله عليه وسلم فقال: " جئت تسأل عن البر؟ "، قلت: نعم، فقال: " استفت قلبك البر ما اطمأنت إليه النفس، واطمأن إليه القلب، والإثم ما حاك فى النفس وتردد فى الصدر، وإن أفتاك الناس وأفتوك ".
** حم دى
(رياض الصالحين ٢١٢/١)
** حسن','7','482',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3258,'<span data-type="title" id=toc-3265>٣٢٥٨ </span>- عن الحسن بن على رضى الله عنهما قال: حفظت من رسول الله صلى الله عليه وسلم " دع ما يريبك إلى ما لا يريبك
** ت
(رياض الصالحين ٢١٣/١)
** قال الإمام النووى فى " الرياض " ١ / ٢١٣: قال الترمذى: حديث حسن صحيح.','7','483',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3259,'<span data-type="title" id=toc-3266>٣٢٥٩ </span>- عن عطية بن عروة السعدى الصحابى رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " لا يبلغ العبد أن يكون من المتقين، حتى يدع ما لا بأس به، حذرا لما به بأس ".
** ت
(رياض الصالحين ٢١٤/١)
** قال الإمام النووى فى " الرياض " ١ / ٢١٤: قال الترمذى: حديث حسن.','7','484',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3260,'<span data-type="title" id=toc-3267>٣٢٦٠ </span>- عن سلمة بن الأكوع رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " لا يزال الرجل يذهب بنفسه حتى يكتب فى الجبارين، فيصيبه ما أصابهم ".
** ت
(رياض الصالحين ٢٢٠/١)
** قال الإمام النووى فى " الرياض " ١ / ٢٢٠: قال الترمذى: حديث حسن.','7','485',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3261,'<span data-type="title" id=toc-3268>٣٢٦١ </span>- عن أبى الدرداء رضى الله عنه، أن النبى صلى الله عليه وسلم سقال: " ما من شىء أثقل فى ميزان المؤمن يوم القيامة من حسن الخلق، وإن الله يبغض الفاحش البذى ".
** ت
(رياض الصالحين ٢٢١/١)
** قال الإمام النووى فى " الرياض " ١ / ٢٢١: قال الترمذى: حديث حسن صحيح.','7','486',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3262,'<span data-type="title" id=toc-3269>٣٢٦٢ </span>- عن أبى هريرة رضى الله عنه قال: سئل رسول الله صلى الله عليه وسلم عن أكثر ما يدخل الناس الجنة؟ ، قال: "تقوى الله وحسن الخلق "، وسئل عن أكثر ما يدخل الناس النار؟ ، فقال: " الفم والفرج ".
** ت
(رياض الصالحين ٢٢١/١)
** قال الإمام النووى فى " الرياض " ١ / ٢٢١: قال الترمذى: حديث حسن صحيح.','7','487',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3263,'<span data-type="title" id=toc-3270>٣٢٦٣ </span>- عن أبى هريرة قال: قال رسول الله صلى الله عليه وسلم: " أكمل المؤمنين إيمانا أحسنهم خلقا، وخياركم خياركم لنسائهم ".
** ت
(رياض الصالحين ٢٢١/١)
** قال الإمام النووى فى " الرياض " ١ / ٢٢١: قال الترمذى: حديث حسن صحيح.','7','488',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3264,'<span data-type="title" id=toc-3271>٣٢٦٤ </span>- عن أبى أمامة الباهلى رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " أنا زعيم ببيت فى ربض الجنة لمن ترك المراء، وإن كان محقا وببيت فى وسط الجنة لمن ترك الكذب، وإن كان مازحا، وببيت فى أعلى الجنة لمن حسن خلقه ".
** د
(رياض الصالحين ٢٢٢/١)
** صحيح
** قال الإمام النووى فى " الرياض " ١ / ٢٢٢: رواه أبو داود بإسناد صحيح.','7','489',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3265,'<span data-type="title" id=toc-3272>٣٢٦٥ </span>- عن جابر رضى الله عنه أن رسول الله صلى الله عليه وسلم قال: " إن من أحبكم إلى وأقربكم منى مجلسا يوم القيامة أحاسنكم أخلاقا وإن أبغضكم إلى وأبعدكم منى يوم القيامة، الثرثارون والمتشدقون والمتفيقهون "، قالوا: يا رسول الله قد علمنا الثرثارون والمتشدقون فما المتفيقهون؟ ، قال: " المتكبرون ".
** ت
(رياض الصالحين ٢٢٢/١)
** قال الإمام النووى فى " الرياض " ١ / ٢٢٢: قال الترمذى: حديث حسن.','7','490',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3266,'<span data-type="title" id=toc-3273>٣٢٦٦ </span>- عن ابن مسعود رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " ألا أخبركم بمن يحرم على النار؟ ،_ أو بمن تحرم عليه النار؟ _ تحرم على كل قريب هين لين سهل ".
** ت
(رياض الصالحين /١)
** قال الإمام النووى فى " الرياض " ١ / ٢٢٤: قال الترمذى: حديث حسن.','7','491',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3267,'<span data-type="title" id=toc-3274>٣٢٦٧ </span>- عن أبى بكرة رضى الله عنه قال: سمعت رسول الله صلى الله عليه وسلم يقول: " من أهان السلطان أهانه الله ".
** ت
(رياض الصالحين ٢٣٣/١)
** قال الإمام النووى فى " الرياض " ١ / ٢٣٣: قال الترمذى: حديث حسن.','7','492',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3268,'<span data-type="title" id=toc-3275>٣٢٦٨ </span>- عن عائشة رضى الله عنها قالت: قال رسول الله صلى الله عليه وسلم: " إذا أراد الله بالأمير خيرا، جعل له وزير صدق، إن نسى ذكره وإن ذكر أعانه، وإذا أراد به غير ذلك جعل له وزير سوء، إن نسى لم يذكره وإن لم يذكر لم يعنه ".
** د
(رياض الصالحين ٢٣٤/١)
** إسناده جيد على شرط مسلم
** قال الإمام النووى فى " الرياض " ١ / ٢٣٤: رواه أبو داود بإسناد جيد على شرط مسلم.','7','493',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3269,'<span data-type="title" id=toc-3276>٣٢٦٩ </span>- عن العرباض بن سارية رضى الله عنه قال: وعظنا رسول الله صلى الله عليه وسلم موعظة وجلت منها القلوب وذرفت منها العيون........ فذكر الحديث.
** ت
(رياض الصالحين ٢٤٢/١)
** قال الإمام النووى فى " الرياض " ١ / ٢٤٢: قال الترمذى: حديث حسن صحيح.','7','494',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3270,'<span data-type="title" id=toc-3277>٣٢٧٠ </span>- عن سالم بن عبد الله بن عمر أن عبد الله بن عمر رضى الله عنهما كان يقول للرجل إذا أراد سفرا: ادن منى حتى أودعك كما كان رسول الله صلى الله عليه وسلم يودعنا فيقول: " استودع الله دينك وأمانتك وخواتيم عملك ".
** ت
(رياض الصالحين ٢٤٨/١)
** قال الإمام النووى فى " الرياض " ١ / ٢٤٩: قال الترمذى: حديث حسن صحيح.','7','495',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3271,'<span data-type="title" id=toc-3278>٣٢٧١ </span>- عن عبد الله بن يزيد الخطمى الصحابى رضى الله عنه قال: كان رسول الله صلى الله عليه وسلم إذا أراد أن يودع الجيش قال: " استودع الله دينكم وأمانتكم وخواتيم أعمالكم ".
** د
(رياض الصالحين ٢٤٩/١)
** صحيح
** قال الإمام النووى فى " الرياض " ١ / ٢٤٩: رواه أبو داود وغيره بإسناد صحيح.','7','496',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3272,'<span data-type="title" id=toc-3279>٣٢٧٢ </span>- عن أنس رضى الله عنه قال: جاء رجل إلى النبى صلى الله عليه وسلم فقال: يا رسول الله، إنى أريد سفرا، فزودنى، فقال: " زودك الله التقوى "، قال: زدنى، قال: " وغفر ذنبك "، قال: " ويسر لك الخير حيثما كنت ".
** ت
(رياض الصالحين ٢٤٩/١)
** قال الإمام النووى فى " الرياض " ١ / ٢٤٩: قال الترمذى: حديث حسن.','7','497',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3273,'<span data-type="title" id=toc-3280>٣٢٧٣ </span>- عن عائشة قالت: كانت يد رسول الله صلى الله عليه وسلم اليمنى لطهوره، وطعامه، وكانت اليسرى لخلائه، وما كان من أذى.
** د
(رياض الصالحين ٢٥١/١)
** صحيح
** قال الإمام النووى فى " الرياض " ١ / ٢٥١: رواه أبو داود وغيره بإسناد صحيح.','7','498',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3274,'<span data-type="title" id=toc-3281>٣٢٧٤ </span>- عن أبى هريرة رضى الله عنه أن رسول الله صلى الله عليه وسلم قال: " إذا لبستم وإذا توضأتم، فابدؤوا بأيامنكم ".
** د ت
(رياض الصالحين ٢٥١/١)
** صحيح
** قال الإمام النووى فى " الرياض " ١ / ٢٥١: رواه أبو داود والترمذى بإسناد صحيح.','7','499',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3275,'<span data-type="title" id=toc-3282>٣٢٧٥ </span>- عن عائشة رضى الله عنها قالت: قال رسول الله صلى الله عليه وسلم: " إذا أكل أحدكم فليذكر اسم الله تعالى، فإن نسى أن يذكر اسم الله تعالى فى أوله فليقل: بسم الله أوله وآخره ".
** د ت
(رياض الصالحين ٢٥٢/١)
** قال الإمام النووى فى " الرياض " ١ / ٢٥٢: قال الترمذى: حديث حسن صحيح.','7','500',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3276,'<span data-type="title" id=toc-3283>٣٢٧٦ </span>- عن عائشة رضى الله عنها قالت: كان رسول الله صلى الله عليه وسلم يأكل طعاما فى ستة من أصحابه، فجاء أعرابى فأكله بلقمتين، فقال رسول الله صلى الله عليه وسلم: " أما إنه لو سمى لكفاكم ".
** ت
(رياض الصالحين ٢٥٣/١)
** قال الإمام النووى فى " الرياض " ١ / ٢٥٣: قال الترمذى: حديث حسن صحيح.','8','1',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3277,'<span data-type="title" id=toc-3284>٣٢٧٧ </span>- عن معاذ بن أنس رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " من أكل طعاما فقال: الحمد لله الذى أطعمنى هذا، ورزقنيه من غير حول منى ولا قوة، غفر له ما تقدم من ذنبه ".
** د ت
(رياض الصالحين ٢٥٣/١)
** قال الإمام النووى فى " الرياض " ١ / ٢٥٣: قال الترمذى: حديث حسن.','8','2',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3278,'<span data-type="title" id=toc-3285>٣٢٧٨ </span>- عن ابن عباس رضى الله عنهما عن النبى صلى الله عليه وسلم قال: " البركة تنزل وسط الطعام، فكلوا من حافتيه ولا تأكلوا من وسطه ".
** د ت
(رياض الصالحين ٢٥٥/١)
** قال الإمام النووى فى " الرياض " ١ / ٢٥٥: قال الترمذى: حديث حسن صحيح.','8','3',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3279,'<span data-type="title" id=toc-3286>٣٢٧٩ </span>- عن عبد الله بن بسر رضى الله عنه قال: كان للنبى قصعة يقال لها الفراء يحملها أربعة رجال، فلما أضحوا وسجدوا الضحى أتى بتلك القصعة يعنى وقد ثرد فيها، فالتفوا عليها فلما كثروا جثا رسول الله صلى الله عليه وسلم، فقال أعرابى: ما هذه الجلسة؟ ، قال رسول الله صلى الله عليه وسلم: " إن الله جعلنى عبدا كريما لم يجعلنى جبارا عنيدا "، ثم قال رسول الله صلى الله عليه وسلم: " كلوا من حواليها، ودعوا ذروتها يبارك فيها ".
** د
(رياض الصالحين ٢٥٦/١)
** إسناده جيد','8','4',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3280,'<span data-type="title" id=toc-3287>٣٢٨٠ </span>- عن ابن عباس رضى الله عنهما قال: قال رسول الله صلى الله عليه وسلم: " لا تشربوا واحدا كشرب البعير، ولكن اشربوا مثنى وثلاث، وسموا إذا أنتم شربتم، واحمدوا إذا أنتم رفعتم ".
** ت
(رياض الصالحين ٢٥٨/١)
** قال الإمام النووى فى " الرياض " ١ / ٢٥٨: قال الترمذى: حديث حسن.','8','5',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3281,'<span data-type="title" id=toc-3288>٣٢٨١ </span>- عن أم ثابت كبشة بنت ثابت أخت حسان بن ثابت رضى الله عنهما قالت: دخل على رسول الله صلى الله عليه وسلم فشرب من فى قربة معلقة قائما، فقمت إلى فيها فقصفته.
** ت
(رياض الصالحين ٢٥٩/١)
** قال الإمام النووى فى " الرياض " ١ / ٢٦٠: قال الترمذى: حديث حسن صحيح.','8','6',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3282,'<span data-type="title" id=toc-3289>٣٢٨٢ </span>- عن أبى سعيد الخدرى رضى الله عنه أن النبى صلى الله عليه وسلم نهى عن النفخ فى الشرب فقال رجل: القذاة أراها فى الإناء؟ فقال: " أهرقها " قال: إنى لا أروى من نفس واحد؟ ، قال: " فأبن القدح إذا عن فيك ".
** ت
(رياض الصالحين ٢٦٠/١)
** قال الإمام النووى فى " الرياض " ١ / ٢٦٠: قال الترمذى: حديث حسن صحيح.','8','7',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3283,'<span data-type="title" id=toc-3290>٣٢٨٣ </span>- عن ابن عباس رضى الله عنهما أن النبى صلى الله عليه وسلم نهى أن يتنفس فى الإناء أو ينفخ فيه.
** ت
(رياض الصالحين ٢٦٠/١)
** قال الإمام النووى فى " الرياض " ١ / ٢٦٠: قال الترمذى: حديث حسن صحيح.','8','8',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3284,'<span data-type="title" id=toc-3291>٣٢٨٤ </span>- عن ابن عمر رضى الله عنهما قال: كنا نأكل على عهد رسول الله صلى الله عليه وسلم ونحن نمشى، ونشرب ونحن قيام.
** ت
(رياض الصالحين ٢٦١/١)
** قال الإمام النووى فى " الرياض " ١ / ٢٦١: قال الترمذى: حديث حسن صحيح.','8','9',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3285,'<span data-type="title" id=toc-3292>٣٢٨٥ </span>- عن عمرو بن شعيب عن أبيه عن جده رأيت رسول الله صلى الله عليه وسلم يشرب قائما وقاعدا.
** ت
(رياض الصالحين ٢٦١/١)
** قال الإمام النووى فى " الرياض " ١ / ٢٦١: قال الترمذى: حديث حسن صحيح.','8','10',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3286,'<span data-type="title" id=toc-3293>٣٢٨٦ </span>- عن أبى قتادة رضى الله عنه عن النبى صلى الله عليه وسلم قال: " ساقى القوم آخرهم شربا ".
** ت
(رياض الصالحين ٢٦١/١)
** قال الإمام النووى فى " الرياض " ١ / ٢٦١: قال الترمذى: حديث حسن صحيح.','8','11',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3287,'<span data-type="title" id=toc-3294>٣٢٨٧ </span>- عن ابن عباس رضى الله عنهما أن رسول الله صلى الله عليه وسلم قال: " البسوا من ثيابكم البياض فإنها من خير ثيابكم وكفنوا فيها موتاكم ".
** د ت
(رياض الصالحين ٢٦٣/١)
** قال الإمام النووى فى " الرياض " ١ / ٢٦٤: قال الترمذى: حديث حسن صحيح.','8','12',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3288,'<span data-type="title" id=toc-3295>٣٢٨٨ </span>- عن سمرة رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " البسوا البياض، فإنها أطهر وأطيب وكفنوا فيها موتاكم ".
** ن ك
(رياض الصالحين ٢٦٤/١)
** قال الإمام النووى فى " الرياض " ١ / ٢٦٤: قال الحاكم: حديث صحيح.','8','13',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3289,'<span data-type="title" id=toc-3296>٣٢٨٩ </span>- عن أبى رمشة رفاعة التيمى رضى الله عنه قال: رأيت رسول الله صلى الله عليه وسلم وعليه ثوبان أخضران.
** د ت
(رياض الصالحين ٢٦٤/١)
** إسناده صحيح','8','14',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3290,'<span data-type="title" id=toc-3297>٣٢٩٠ </span>- عن أم سلمة رضى الله عنها قالت: كان أحب الثياب إلى رسول الله صلى الله عليه وسلم القميص.
** د ت
(رياض الصالحين ٢٦٥/١)
** قال الإمام النووى فى " الرياض " ١ / ٢٦٥: قال الترمذى: حديث حسن.','8','15',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3291,'<span data-type="title" id=toc-3298>٣٢٩١ </span>- عن أسماء بنت يزيد الأنصارية رضى الله عنها قالت: كان كم قميص رسول الله صلى الله عليه وسلم إلى الرسغ.
** د ت
(رياض الصالحين ٢٦٦/١)
** قال الإمام النووى فى " الرياض " ١ / ٢٦٦: قال الترمذى: حديث حسن.','8','16',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3292,'<span data-type="title" id=toc-3299>٣٢٩٢ </span>- عن ابن عمر رضى الله عنهما عن النبى صلى الله عليه وسلم قال: " الأسبال فى الإزار، والقميص والعمامة، من جر شيئا خيلاء، لم ينظر الله إليه يوم القيامة ".
** د ن
(رياض الصالحين ٢٦٧/١)
** إسناده صحيح','8','17',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3293,'<span data-type="title" id=toc-3300>٣٢٩٣ </span>- عن أبى جرى جابر بن سليم رضى الله عنه، قال: رأيت رجلا يصدر الناس عن رأيه، لا يقول شيئا إلا صدروا عنه، قلت: من هذا؟ ، قالوا: رسول الله صلى الله عليه وسلم قلت: عليك السلام يا رسول الله _ مرتين _ قال: " لا تقل عليك السلام، عليك السلام تحية الموتى. قل: السلام عليك "، قال: قلت: أنت رسول الله؟ ، قال: " أنا رسول الله الذى إذا أصابك ضر فدعوته كشفه عنك، وإذا أصابك عام سنة فدعوته أنبتها لك، وإذا كنت بأرض قفرأو فلاة فضلت راحلتك فدعوته ردها عليك "، قال: قلت: اعهد إلى، قال: " لا تسبن أحدا، _ قال: فما سببت بعده حرا، ولا عبدا، ولا بعيرا ولا شاة _ ولا تحقرن من المعروف شيئا وأن تكلم أخاك وأنت منبسط إليه وجهك، إن ذلك من المعروف، وارفع إزارك إلى نصف الساق، فإن أبيت فإلى الكعبين، وإياك وإسبال الإزار فإنها مخيلة وإن الله لايحب المخيلة، وإن امرؤ شتمك وعيرك بما يعلم فيك فلا تعيره فيه، فإنما وبال ذلك عليه ".
** د ت
(رياض الصالحين ٢٦٧/١)
** إسناده صحيح
** قال الإمام النووى فى " الرياض " ١ / ٢٦٧: قال الترمذى: حديث حسن صحيح.','8','18',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3294,'<span data-type="title" id=toc-3301>٣٢٩٤ </span>- عن أبى هريرة رضى الله عنه قال: بينما رجل يصلى مسبل إزاره قال له رسول الله صلى الله عليه وسلم: " اذهب فتوضأ "، فذهب فتوضأ ثم جاء، فقال: " اذهب فتوضأ "، فقال له رجل: يا رسول الله، ما لك أمرته أن يتوضأ ثم سكت عنه؟ ، قال: " إنه كان يصلى وهو مسبل إزاره، وإن الله لا يقبل صلاة رجل مسبل ".
** د
(رياض الصالحين ٢٦٧/١)
** إسناده صحيح على شرط مسلم','8','19',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3295,'<span data-type="title" id=toc-3302>٣٢٩٥ </span>- عن قيس بن بشر التغلبى قال: أخبرنى أبى _ وكان جليسا لأبى الدرداء _ قال: كان بدمشق رجل من أصحاب النبى صلى الله عليه وسلم، يقال له سهل بن الحنظلية، وكان رجل متوحدا قلما يجالس الناس، إنما هو صلاة فإذا فرغ فإنما هو تسبيح وتكبير حتى يأتى أهله فمر بنا ونحن عند أبى الدرداء فقال له أبو الدرداء: كلمة تنفعنا، ولا تضرك، قال: بعث رسول الله صلى الله عليه وسلم سرية فقدمت فجاء رجل منهم فجلس فى المجلس الذى يجلس فيه رسول الله صلى الله عليه وسلم فقال لرجل إلى جنبه: لو رأيتنا حين التقينا نحن والعدو، فحمل فلان وطعن، فقال: خذها منى وأنا الغلام الغفارى كيف ترى فى قوله،قال: ما أرى بذلك بأسا، فتنازعا حتى سمع رسول الله صلى الله عليه وسلم فقال: " سبحان الله؟ لا بأس أن يؤجر ويحمد " فرأيت أباالدرداء سر بذلك، وجعل يرفع رأسه إليه ويقول: أنت سمعت ذلك من رسول الله صلى الله عليه وسلم؟ فيقول: نعم فما زال يعيد عليه حتى إنى لأقول ليبركن على ركبتيه. قال: فمر بنا يوما آخر فقال له أبو الدرداء: كلمة تنفعنا ولا تضرك، قال: قال لنا رسول الله صلى الله عليه وسلم: " المنفق على الخيل كالباسط يده بالصدقة لا يقبضها "، ثم مر بنا يوما آخر فقال له أبو الدرداء: كلمة تنفعنا ولا تضرك قال: قال رسول الله صلى الله عليه وسلم: " نعم الرجل خريم الأسدى لولا طول جمته وإسبال إزاره "، فبلغ خريما فعجل فأخذ شفرة فقطع بهاجمته إلى أذنيه، ورفع إزاره إلى أنصاف ساقيه، ثم مر بنا يوما آخر فقال له أبوالدرداء
كلمة تنفعنا ولا تضرك، قالم: سمعت رسول الله صلى الله عليه وسلم يقول: " إنكم
قادمون على إخوانكم فأصلحوا رحالكم، وأصلحوا لباسكم حتى تكونوا كأنكم شامة فى الناس، فإن الله لا يحب الفحش ولا التفحش ".
** د
(رياض الصالحين ٢٦٨/١)
** إسناده حسن
** قال الإمام النووى فى " الرياض " ١ / ٢٦٩: رواه أبو داود بإسناد حسن إلا قيس بن بشر فاختلفوا فى توثيقه وتضعيفه وقد روى له مسلم.','8','20',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3296,'<span data-type="title" id=toc-3303>٣٢٩٦ </span>- عن أبى سعيد الخدرى رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم " إزرة المسلم إلى نصف ساقه، ولا حرج _ أو لا جناح _ فيما بينه، وبين الكعبين، فما كان أسفل الكعبين فهو فى النار، ومن جر إزاره بطرا لم ينظر الله إليه ".
** د
(رياض الصالحين ٢٦٩/١)
** إسناده صحيح','8','21',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3297,'<span data-type="title" id=toc-3304>٣٢٩٧ </span>- عن ابن عمر رضى الله عنهما قال: قال رسول الله صلى الله عليه وسلم: " من جر ثوبه خيلاء لم ينظر الله إليه يوم القيامة "، فقالت أم سلمة: فكيف تصنع النساء بذيولهن، قال: " يرخين شبرا "، قالت: إذا انكشف أقدامهن، قال: " فيرخينه ذراعا لا يزدن ".
** د ت
(رياض الصالحين ٢٦٩/١)
** قال الإمام النووى فى " الرياض " ١ / ٢٦٩: قال الترمذى: حديث حسن صحيح.','8','22',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3298,'<span data-type="title" id=toc-3305>٣٢٩٨ </span>- عن معاذ بن أنس رضى الله عنه أن رسول الله صلى الله عليه وسلم قال: " من ترك اللباس تواضعا لله وهو يقدر عليه، دعاه الله يوم القيامة على رؤوس الخلائق حتى يخيره من أى حلل الإيمان شاء يلبسها ".
** ت
(رياض الصالحين ٢٦٩/١)
** قال الإمام النووى فى " الرياض " ١ / ٢٦٩: قال الترمذى: حديث حسن.','8','23',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3299,'<span data-type="title" id=toc-3306>٣٢٩٩ </span>- عن على رضى الله عنه قال: رأيت رسول الله صلى الله عليه وسلم أخذ حريرا فجعله فى يمينه، وذهبا، وجعله فى شماله، ثم قال: " إن هذين حرام على ذكور أمتى ".
** د
(رياض الصالحين ٢٧٠/١)
** إسناده حسن','8','24',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3300,'<span data-type="title" id=toc-3307>٣٣٠٠ </span>- عن عمرو بن شعيب عن أبيه عن جده رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " إن الله يحب أن يرى أثر نعمته على عبده ".
** ت
(رياض الصالحين ٢٧٠/١)
** قال الإمام النووى فى " الرياض " ١ / ٢٧٠: قال الترمذى: حديث حسن.','8','25',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3301,'<span data-type="title" id=toc-3308>٣٣٠١ </span>- عن أبى موسى الأشعرى رضى الله عنه أن رسول الله صلى الله عليه وسلم قال: " حرم لباس الحرير والذهب على ذكور أمتى، وأحل لإناثهم ".
** ت
(رياض الصالحين ٢٧٠/١)
** قال الإمام النووى فى " الرياض " ١ / ٢٧١: قال الترمذى: حديث حسن صحيح.','8','26',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3302,'<span data-type="title" id=toc-3309>٣٣٠٢ </span>- عن معاوية رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " لا تركبوا الخز ولا النمار ".
** د
(رياض الصالحين ٢٧١/١)
** حسن
** قال الإمام النووى فى " الرياض " ١ / ٢٧١: رواه أبو داود وغيره بإسناد حسن.','8','27',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3303,'<span data-type="title" id=toc-3310>٣٣٠٣ </span>- عن أبى المليح عن أبيه رضى الله عنه أن رسول الله صلى الله عليه وسلم نهى عن جلود السباع. وفى رواية: نهى عن جلود السباع أن تفترش.
** د ت ن
(رياض الصالحين ٢٧١/١)
** إسناده صحيح','8','28',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3304,'<span data-type="title" id=toc-3311>٣٣٠٤ </span>- عن أبى سعيد الخدرى رضى الله عنه قال: كان رسول الله صلى الله عليه وسلم إذا استجد ثوبا سماه باسمه _ عمامة رداء _ يقول: " اللهم لك الحمد أنت كسوتنيه، أسألك ما صنع له وأعوذ بك من شره وشر ما صنع له ".
** د ت
(رياض الصالحين ٢٧١/١)
** قال الإمام النووى فى " الرياض " ١ / ٢٧٢: قال الترمذى: حديث حسن.','8','29',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3305,'<span data-type="title" id=toc-3312>٣٣٠٥ </span>- عن يعيش بن طخفة الغفارى رضى الله عنه قال: قال أبى بينما أنا مضطجع فى المسجد على بطنى إذا رجل يحركنى برجله فقال: " إن هذه ضجعة يبغضها الله "، قال: فنظرت فإذا رسول الله صلى الله عليه وسلم.
** د
(رياض الصالحين ٢٧٣/١)
** إسناده صحيح','8','30',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3306,'<span data-type="title" id=toc-3313>٣٣٠٦ </span>- عن أبى هريرة رضى الله عنه عن رسول الله صلى الله عليه وسلم قال: " من قعد مقعدا لم يذكر الله تعالى فيه، كانت عليه من الله تعالى ترة، ومن اضطجع مضجعا لا يذكر الله تعالى فيه، كانت عليه من الله ترة ".
** د
(رياض الصالحين ٢٧٣/١)
** إسناده حسن','8','31',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3307,'<span data-type="title" id=toc-3314>٣٣٠٧ </span>- عن جابر بن سمرة رضى الله عنه قال: كان النبى صلى الله عليه وسلم إذا صلى الفجر تربع فى مجلسه حتى تطلع الشمس حسناء.
** د
(رياض الصالحين ٢٧٣/١)
** صحيح
** قال الإمام النووى فى " الرياض " ١ / ٢٧٣: رواه أبو داود وغيره بإسانيد صحيحة.','8','32',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3308,'<span data-type="title" id=toc-3315>٣٣٠٨ </span>- عن الشريد بن سويد رضى الله عنه قال: مر بى رسول الله صلى الله عليه وسلم أنا جالس هكذا وقد وضعت يدى اليسرى خلف ظهرى واتكأت على ألية يدى، فقال: " أتقعد قعدة المغضوب عليهم؟ ".
** د
(رياض الصالحين ٢٧٤/١)
** إسناده صحيح','8','33',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3309,'<span data-type="title" id=toc-3316>٣٣٠٩ </span>- عن جابر بن سمرة رضى الله عنهما، قال: كنا إذا أتينا النبى صلى الله عليه وسلم، جلس أحدنا حيث ينتهى.
** د ت
(رياض الصالحين ٢٧٤/١)
** قال الإمام النووى فى " الرياض " ١ / ٢٧٤: قال الترمذى: حديث حسن.','8','34',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3310,'<span data-type="title" id=toc-3317>٣٣١٠ </span>- عن عمرو بن شعيب عن أبيه عن جده رضى الله عنه أن رسول الله صلى الله عليه وسلم قال: " لا يحل لرجل أن يفرق بين اثنين إلا بإذنهما ". وفى رواية لأبى داود: " لا يجلس بين رجلين إلا بإذنهما ".
** د ت
(رياض الصالحين ٢٧٥/١)
** قال الإمام النووى فى " الرياض " ١ / ٢٧٥: قال الترمذى: حديث حسن.','8','35',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3311,'<span data-type="title" id=toc-3318>٣٣١١ </span>- عن حذيفة بن اليمان رضى الله عنه أن رسول الله صلى الله عليه وسلم لعن من جلس وسط الحلقة.
** د
(رياض الصالحين ٢٧٥ط/١)
** إسناده حسن','8','36',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3312,'<span data-type="title" id=toc-3319>٣٣١٢ </span>- عن أبى مجلز أن رجلا قعد وسط حلقة، فقال حذيفة ملعون على لسان محمد صلى الله عليه وسلم _ أو لعن الله على لسان محمد صلى الله عليه وسلم من جلس وسط الحلقة.
** ت
(رياض الصالحين ٢٧٥/١)
** قال الإمام النووى فى " الرياض " ١ / ٢٧٥: قال الترمذى: حديث حسن صحيح.','8','37',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3313,'<span data-type="title" id=toc-3320>٣٣١٣ </span>- عن أبى سعيد الخدرى رضى الله عنه قال: سمعت رسول الله صلى الله عليه وسلم يقول: " خير المجالس أوسعها ".
** د
(رياض الصالحين ٢٧٥/١)
** إسناده صحيح على شرط البخارى','8','38',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3314,'<span data-type="title" id=toc-3321>٣٣١٤ </span>- عن أبى هريرة رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " من جلس فى مجلس فكثر فيه لغطه، فقال قبل أن يقوم من مجلسه ذلك: سبحانك اللهم وبحمدك، أشهد أن لا إله إلا أنت، أستغفرك وأتوب إليك إلا غفر له ما كان فى مجلسه ذلك ".
** ت
(رياض الصالحين ٢٧٥/١)
** قال الإمام النووى فى " الرياض " ١ / ٢٧٥: قال الترمذى: حديث حسن صحيح.','8','39',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3315,'<span data-type="title" id=toc-3322>٣٣١٥ </span>- عن أبى برزة رضى الله عنه، قال: كان رسول الله صلى الله عليه وسلم يقول بأخرة إذا أراد أن يقوم من المجلس: " سبحانك اللهم وبحمدك، أشهد أن لا إله إلا أنت أستغفرك وأتوب إليك "، فقال رجل: يا رسول الله، إنك لتقول قولا ما كنت تقوله فيما مضى، قال: " ذلك كفارة لما يكون فى المجلس ".
** د
(رياض الصالحين ٢٧٥/١)
** قال الإمام النووى فى " الرياض " ١ / ٢٧٦: رواه الحاكم أبو عبد الله فىالمستدرك من رواية عائشة، وقال: صحيح الإسناد.','8','40',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3316,'<span data-type="title" id=toc-3323>٣٣١٦ </span>- عن ابن عمر رضى الله عنهما قال: قلما كان رسول الله صلى الله عليه وسلم يقوم من مجلس، حتى يدعو بهؤلاء الدعوات: " اللهم اقسم لنا من خشيتك ما تحول به بيننا وبين معصيتك ومن طاعتك ما تبلغنا به جنتك ومن اليقين ما تهون به علينا مصائب الدنيا، اللهم متعنا بأسماعنا وأبصارنا وقوتنا ما أحييتنا، اجعله الوارث منا، واجعل ثأرنا على من ظلمنا، وانصرنا على من عادانا، ولا تجعل مصيبتنا فى ديننا، ولا تجعل الدنيا أكبر همنا ولا مبلغ علمنا، ولا تسلط علينا من لا يرحمنا ".
** ت
(رياض الصالحين ٢٧٦/١)
** قال الإمام النووى فى " الرياض " ١ / ٢٧٦: قال الترمذى: حديث حسن.','8','41',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3317,'<span data-type="title" id=toc-3324>٣٣١٧ </span>- عن أبى هريرة رضى الله عنه، قال: قال رسول الله صلى الله عليه وسلم: " ما من قوم يقومون من مجلس لا يذكرون الله تعالى فيه، إلا قاموا عن عن مثل جيفة حمار وكان لهم حسرة ".
** د
(رياض الصالحين ٢٧٦/١)
** إسناده صحيح','8','42',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3318,'<span data-type="title" id=toc-3325>٣٣١٨ </span>- عن أبى هريرة رضى الله عنه، عن النبى صلى الله عليه وسلم قال: " ما جلس قوم مجلسا لم يذكروا الله تعالى فيه، ولم يصلوا على نبيهم فيه، إلا كان عليهم ترة، فإن شاء عذبهم وإن شاء غفر لهم ".
** ت
(رياض الصالحين ٢٧٦/١)
** قال الإمام النووى فى " الرياض " ١ / ٢٧٦: قال الترمذى: حديث حسن.','8','43',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3319,'<span data-type="title" id=toc-3326>٣٣١٩ </span>- عن أبى يوسف عبد الله بن سلام رضى الله عنه، قال: سمعت رسول الله صلى الله عليه وسلم يقول: " يا أيها الناس أفشوا السلام، وأطعموا الطعام وصلوا الأرحام، وصلوا والناس نيام، تدخلوا الجنة بسلام ".
** ت
(رياض الصالحين ٢٧٩/١)
** قال الإمام النووى فى " الرياض " ١ / ٢٧٩: قال الترمذى: حديث حسن صحيح.','8','44',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3320,'<span data-type="title" id=toc-3327>٣٣٢٠ </span>- عن الطفيل بن أبى بن كعب أنه كان يأتى عبد الله بن عمر. فيغدوا معه إلى السوق، قال: فإذا غدونا إلى السوق لم يمر عبد الله على سقاط ولا صاحب بيعة ولا مسكين ولا أحد إلا سلم عليه قال الطفيل: فجئت عبد الله بن عمر يوما، فاستتبعنى إلى السوق، فقلت له: ما تصنع بالسوق، وأنت لا تقف على البيع، ولا تسأل عن السلع ولا تسوم بها، ولا تجلس فى مجالس السوق؟ ، وأقول: اجلس بنا هاهنا نتحدث فقال: يا أبا بطن _ وكان الطفيل ذا بطن _، إنما نغدو من أجل السلام، نسلم على من لقيناه.
** ما
(رياض الصالحين ٢٧٩/١)
** إسناده صحيح','8','45',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3321,'<span data-type="title" id=toc-3328>٣٣٢١ </span>- عن عمران بن الحصين رضى الله عنهما، قال: جاء رجل إلى النبى صلى الله عليه وسلم فقال: السلام عليكم فرد عليه ثم جلس، فقال النبى صلى الله عليه وسلم: " عشر "، ثم جاء آخر، فقال: السلام عليكم، ورحمة الله، فرد عليه فجلس فقال: " عشرون "، ثم جاء آخر فقال: السلام عليكم ورحمة الله وبركاته، فرد عليه فجلس، فقال: " ثلاثون ".
** د ت
(رياض الصالحين ٢٨٠/١)
** قال الإمام النووى فى " الرياض " ١ / ٢٨٠: قال الترمذى: حديث حسن.','8','46',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3322,'<span data-type="title" id=toc-3329>٣٣٢٢ </span>- عن أسماء بنت يزيد رضى الله عنها أن رسول الله صلى الله عليه وسلم مر فى المسجد يوما، وعصبة من النساء قعود فألوى بيده بالتسليم. وفى رواية أبى داود: " فسلم علينا ".
** ت
(رياض الصالحين ٢٨٠/١)
** قال الإمام النووى فى " الرياض " ١ / ٢٨٠: قال الترمذى: حديث حسن.','8','47',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3323,'<span data-type="title" id=toc-3330>٣٣٢٣ </span>- عن أبى جرى الهجيمى رضى الله عنه قال: أتيت رسول الله صلى الله عليه وسلم فقلت: عليك السلام يا رسول الله، قال: لا تقل عليك السلام فإن عليك السلام تحية الموتى ".
** د ت
(رياض الصالحين ٢٨١/١)
** قال الإمام النووى فى " الرياض " ١ / ٢٨١: قال الترمذى: حديث حسن صحيح.','8','48',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3324,'<span data-type="title" id=toc-3331>٣٣٢٤ </span>- عن أبى أمامة صدى بن عجلان الباهلى رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " إن أولى الناس بالله من بدأهم بالسلام ".
** د
(رياض الصالحين ٢٨١/١)
** إسناده جيد','8','49',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3325,'<span data-type="title" id=toc-3332>٣٣٢٥ </span>- عن أبى أمامة رضى الله عنه، قيل: يا رسول الله، الرجلان يلتقيان أيهما يبدأ بالسلام، قال: " أولاهما بالله تعالى ".
** ت
(رياض الصالحين ٢٨١/١)
** قال الإمام النووى فى " الرياض " ١ / ٢٨١: قال الترمذى: حديث حسن.','8','50',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3326,'<span data-type="title" id=toc-3333>٣٣٢٦ </span>- عن أنس رضى الله عنه قال: قال لى رسول الله صلى الله عليه وسلم: " يا بنى إذا دخلت على أهلك فسلم، يكن بركة عليك وعلى أهل بيتك ".
** ت
(رياض الصالحين ٢٨٢/١)
** قال الإمام النووى فى " الرياض " ١ / ٢٨٢: قال الترمذى: حديث حسن صحيح.','8','51',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3327,'<span data-type="title" id=toc-3334>٣٣٢٧ </span>- عن أسماء بنت يزيد رضى الله عنها قالت: مر علينا النبى صلى الله عليه وسلم فى نسوة فسلم علينا. وفى رواية الترمذى: أن رسول الله صلى الله عليه وسلم مر فى المسجد يوما وعصبة من النساء قعود فألوى بيده بالتسليم ".
** د ت
(رياض الصالحين ٢٨٣/١)
** قال الإمام النووى فى " الرياض " ١ / ٢٨٣: قال الترمذى: حديث حسن.','8','52',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3328,'<span data-type="title" id=toc-3335>٣٣٢٨ </span>- عن أبى هريرة رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " إذا انتهى أحدكم إلى المجلس فليسلم، فإذا أراد أن يقوم فليسلم فليست الأولى بأحق من الآخرة ".
** د ت
(رياض الصالحين ٢٨٤/١)
** قال الإمام النووى فى " الرياض " ١ / ٢٨٤: قال الترمذى: حديث حسن.','8','53',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3329,'<span data-type="title" id=toc-3336>٣٣٢٩ </span>- عن ربعى بن حراش، قال: حدثنا رجل من بنى عامر أنه استأذن على النبى صلى الله عليه وسلم وهو فى بيت، فقال: أألج؟ ، فقال رسول الله صلى صلى الله عليه وسلم لخادمه: " اخرج لهذا فعلمه الإستئذان، فقل له: قل: السلام عليكم، أأدخل؟ "، فسمعه الرجل فقال: السلام عليكم أأدخل فأذن له النبى صلى الله عليه وسلم فدخل.
** د
(رياض الصالحين ٢٨٥/١)
** إسناده صحيح','8','54',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3330,'<span data-type="title" id=toc-3337>٣٣٣٠ </span>- عن كلدة بن الحنبل رضى الله عنه قال: أتيت النبى صلى الله عليه وسلم فدخلت عليه ولم أسلم، فقال النبى صلى الله عليه وسلم: " ارجع فقل السلام عليكم أأدخل ".
** د ت
(رياض الصالحين ٢٨٥/١)
** قال الإمام النووى فى " الرياض " ١ / ٢٨٥: قال الترمذى: حديث حسن.','8','55',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3331,'<span data-type="title" id=toc-3338>٣٣٣١ </span>- عن أبى هريرة رضى الله عنه قال: كان رسول الله صلى الله عليه وسلم إذا عطس وضع يده أو ثوبه على فيه، وخفض _ أو غض _ بها صوته. شك الراوى
** د ت
(رياض الصالحين ٢٨٧/١)
** قال الإمام النووى فى " الرياض " ١ / ٢٨٧: قال الترمذى: حديث حسن صحيح.','8','56',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3332,'<span data-type="title" id=toc-3339>٣٣٣٢ </span>- عن أبى موسى الأشعرى رضى الله عنه قال: كان اليهود يتعاطسون عند رسول الله صلى الله عليه وسلم يرجون أن يقول لهم: يرحمكم الله، فيقول: " يهديكم الله ويصلح بالكم ".
** د ت
(رياض الصالحين ٢٨٧/١)
** قال الإمام النووى فى " الرياض " ١ / ٢٨٧: قال الترمذى: حديث حسن صحيح.','8','57',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3333,'<span data-type="title" id=toc-3340>٣٣٣٣ </span>- عن أنس رضى الله عنه قال: قال رجل: يا رسول الله، الرجل منا يلقى أخاه أو صديقه، أينحنى له؟ ، قال: " لا "، قال: أفيلتزمه ويقبله قال: " لا "، قال: فيأخذ بيده ويصافحه؟ ، قال: " نعم ".
** ت
(رياض الصالحين ٢٨٧/١)
** قال الإمام النووى فى " الرياض " ١ / ٢٨٧: قال الترمذى: حسن.','8','58',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3334,'<span data-type="title" id=toc-3341>٣٣٣٤ </span>- عن صفوان بن عسال رضى الله عنه قال: قال يهودى لصاحبه: اذهب بنا إلى هذا النبى، فأتيا رسول الله صلى الله عليه وسلم، فسألاه عن تسع أيات بينات، فذكر الحديث إلى قوله: فقبلا يده ورجله، وقالا نشهد أنك نبى.
** ت
(رياض الصالحين ٢٨٨/١)
** إسناده صحيح
** قال الإمام النووى فى " الرياض " ١ / ٢٨٨: رواه الترمذى وغيره بأسانيد صحيحة.','8','59',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3335,'<span data-type="title" id=toc-3342>٣٣٣٥ </span>- عن عائشة رضى الله عنها قالت: قدم زيد بن حارثة المدينة ورسول الله صلى الله عليه وسلم فى بيتى، فأتاه فقرع الباب، فقام إليه النبى صلى الله عليه وسلم يجر ثوبه، فاعتنقه وقبله.
** ت
(رياض الصالحين ٢٨٨/١)
** قال الإمام النووى فى " الرياض " ١ / ٢٨٨: قال الترمذى: حديث حسن.','8','60',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3336,'<span data-type="title" id=toc-3343>٣٣٣٦ </span>- عن على رضى الله عنه قال: سمعت رسول الله صلى الله عليه وسلم يقول: " ما من مسلم يعود مسلما غدوة إلا صلى عليه سبعون ألف ملك حتى يمسى، وإن عاده عشية إلا صلى عليه سبعون ألف ملك حتى يصبح، وكان له خريف فى الجنة ".
** ت
(رياض الصالحين ٢٨٩/١)
** قال الإمام النووى فى " الرياض " ١ / ٢٩٠: قال الترمذى: حديث حسن.','8','61',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3337,'<span data-type="title" id=toc-3344>٣٣٣٧ </span>- عن ابن عباس رضى الله عنهما عن النبى صلى الله عليه وسلم قال: " من عاد مريضا لم يحضره أجله، فقال عنده سبع مرات: أسأل الله العظيم رب العرش العظيم أن يشفيك، إلا عافاه الله من ذلك المرض ".
** د ت
(رياض الصالحين ٢٩١/١)
** قال الإمام النووى فى " الرياض " ١ / ٢٩١: قال الترمذى: حديث حسن.اه. قال الحاكم: حديث صحيح على شرط البخارى.','8','62',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3338,'<span data-type="title" id=toc-3345>٣٣٣٨ </span>- عن أبى سعيد الخدرى، وأبى هريرة رضى الله عنهما، أنهما شهدا على رسول الله صلى الله عليه وسلم أنه، قال: " من قال: لا إله إلا الله والله أكبر صدقه ربه، فقال: لا إله إلا أنا وأنا أكبر، وإذا، قال: لا إله إلا الله وحده لا شريك له، قال: يقول: لا إله إلا أنا وحدى لاشريك لى، وإذا قال: لا إله إلا الله له الملك وله الحمد، قال: لا إله إلا أنا لى الحمد ولى الملك، وإذا قال: لا إله إلا الله ولا حول ولا قوة إلا بالله قال: لا إله إلا أنا ولا حول ولا قوة إلا بى "، وكان يقول: من قالها فى مرضه ثم مات لم تطعمه النار ".
** ت
(رياض الصالحين ٢٩١/١)
** قال الإمام النووى فى " الرياض " ١ / ٢٩٢: قال الترمذى: حديث حسن.','8','63',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3339,'<span data-type="title" id=toc-3346>٣٣٣٩ </span>- عن معاذ رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " من كان آخر كلامه لا إله إلا الله دخل الجنة ".
** د ك
(رياض الصالحين ٢٩٣/١)
** قال الإمام النووى فى " الرياض " ١ / ٢٩٣: قال الحاكم: صحيح الإسناد.','8','64',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3340,'<span data-type="title" id=toc-3347>٣٣٤٠ </span>- عن أبى موسى رضى الله عنه، أن رسول الله صلى الله عليه وسلم، قال: " إذا مات ولد العبد، قال الله تعالى لملائكته: قبضتم ولد عبدى، فيقولون: نعم، فيقول: قبضتم ثمرة فؤاده؟ ، فيقولون: نعم، فيقول: فماذا قال عبدى؟ ، فيقولون: حمدك واسترجع، فيقول الله تعالى: ابنوا لعبدى بيتا فى الجنة وسموه بيت الحمد ".
** ت
(رياض الصالحين ٢٩٥/١)
** قال الإمام النووى فى " الرياض " ١ / ٢٩٥: قال الترمذى: حديث حسن.','8','65',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3341,'<span data-type="title" id=toc-3348>٣٣٤١ </span>- عن أبى رافع أسلم مولى رسول الله صلى الله عليه وسلم أن رسول الله صلى الله عليه وسلم قال: " من عفس ميتا فكتم عليه، غفر الله له أربعين مرة ".
** ك
(رياض الصالحين ٢٩٦/١)
** قال الإمام النووى فى " الرياض " ١ / ٢٩٦: قال الحاكم: صحيح على شرط مسلم.','8','66',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3342,'<span data-type="title" id=toc-3349>٣٣٤٢ </span>- عن مرثد بن عبد الله اليزنى، قال: كان مالك بن هبيرة رضى الله عنه إذا صلى على الجنازة، فتقال الناس عليها، جزأهم عليها ثلاثة أجزاء، ثم قال: قال رسول الله صلى الله عليه وسلم: " من صلى عليه ثلاثة صفوف، فقد أوجب ".
** د ت
(رياض الصالحين ٢٩٨/١)
** قال الإمام النووى فى " الرياض " ١ / ٢٩٨: قال الترمذى: حديث حسن.','8','67',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3343,'<span data-type="title" id=toc-3350>٣٣٤٣ </span>- عن أبى هريرة وأبى قتادة وأبى إبراهيم الأشهلى عن أبيه _ وأبوه صحابى _ رضى الله عنهم، عن النبى صلى الله عليه وسلم أنه صلى على جنازة فقال: " اللهم اغفر لحينا وميتنا وصغيرنا وكبيرنا وذكرنا وأنثانا وشاهدنا وغائبنا، اللهم من أحببته منا فأحبه على الإسلام، ون توفيته منا فقوفه على الإيمان، اللهم لا تحرمنا أجره، ولا تفتنا بعده ".
** ت د
(رياض الصالحين ٢٩٩/١)
** قال الإمام النووى فى " الرياض " ١ / ٢٩٩: قال الحاكم: حديث أبى هريرة صحيح على شرط البخارى ومسلم.','8','68',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3344,'<span data-type="title" id=toc-3351>٣٣٤٤ </span>- عن عبد الله بن أبى أوفى رضى الله عنهما أنه كبر على جنازة ابنة له أربع تكبيرات، فقام بعد الرابعة كقدر ما بين التكبيرتين يستغفر لها ويدعو، ثم قال: كان رسول الله صلى الله عليه وسلم يصنع هكذا. وفى رواية: كبر أربعا فمكث ساعة حتى ظننت أنه سيكبر خمسا، ثم سلم عن يمينه وعن شماله، فلما انصرف قلنا له: ما هذا؟ ، فقال: إنى لا أزيدكم على ما رأيت رسول الله صلى الله عليه وسلم يصنع أو: هكذا صنع رسول الله صلى الله عليه وسلم.
** ك
(رياض الصالحين ٣٠٠/١)
** قال الإمام النووى فى " الرياض " ١ / ٣٠٠: قال الحاكم: حديث صحيح.','8','69',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3345,'<span data-type="title" id=toc-3352>٣٣٤٥ </span>- عن أبى هريرة رضى الله عنه عن النبى صلى الله عليه وسلم قال: " نفس المؤمن معلقة بدينه حتى يقضى عنه ".
** ت
(رياض الصالحين ٣٠١/١)
** قال الإمام النووى فى " الرياض " ١ / ٣٠١: قال الترمذى: حديث حسن.','8','70',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3346,'<span data-type="title" id=toc-3353>٣٣٤٦ </span>- عن صخر بن وداعة الغامدى الصحابى رضى الله عنه أن رسول الله صلى الله عليه وسلم قال: " اللهم بارك لأمتى فى بكورها "، وكان إذا بعث بعث سرية أو جيشا بعثهم من أول النهار، وكان صخر تاجرا فكان يبعث تجارته أول النهار، فأثرى وكثر ماله.
** د ت
(رياض الصالحين ٣٠٥/١)
** قال الإمام النووى فى " الرياض " ١ / ٣٠٥: قال الترمذى: حديث حسن.','8','71',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3347,'<span data-type="title" id=toc-3354>٣٣٤٧ </span>- عن عمرو بن شعيب عن أبيه عن جده رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " الراكب شيطان والراكبان شيطانان والثلاثة ركب ".
** د ت ن
(رياض الصالحين ٣٠٥/١)
** إسناده صحيح
** قال الإمام النووى فى " الرياض " ١ / ٣٠٥: قالالترمذى: حديث حسن.','8','72',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3348,'<span data-type="title" id=toc-3355>٣٣٤٨ </span>- عن أبى سعيد وأبى هريرة رضى الله عنهما قالا: قال رسول الله صلى الله عليه وسلم: " إذا خرج ثلاثة فى سفر فيؤمروا أحدهم ".
** د
(رياض الصالحين ٣٠٥/١)
** حسن
** قال الإمام النووى فى " الرياض " ١ / ٣٠٦: رواه أبو داود بإسناد حسن.','8','73',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3349,'<span data-type="title" id=toc-3356>٣٣٤٩ </span>- عن ابن عباس رضى الله عنهما أن النبى صلى الله عليه وسلم قال: " خير الصحابة أربعة، وخير السرايا أربعمائة، وخير الجيوش أربعة ألاف ولن يغلب اثنا عشر ألفا عن قلة ".
** د ت
(رياض الصالحين ٣٠٦/١)
** قال الإمام النووى فى " الرياض " ١ / ٣٠٦: قال الترمذى: حديث حسن.','8','74',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3350,'<span data-type="title" id=toc-3357>٣٣٥٠ </span>- عن أنس رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " عليكم بالدلجة فإن الأرض تطوى بالليل ".
** د
(رياض الصالحين ٣٠٧/١)
** إسناده حسن','8','75',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3351,'<span data-type="title" id=toc-3358>٣٣٥١ </span>- عن أبى ثعلبة الخشنى رضى الله عنه قال: كان الناس إذا نزلوا منزلا تفرقوا فى الشعاب والأودية، فقال رسول الله صلى الله عليه وسلم: " إن تفرقتم فى هذه الشعاب والأودية إنما ذلكم الشيطان "، فلم ينزلوا منزلا بعد ذلك منزلا إلا انضم بعضهم إلى بعض.
** د
(رياض الصالحين ٣٠٧/١)
** إسناده حسن','8','76',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3352,'<span data-type="title" id=toc-3359>٣٣٥٢ </span>- عن سهل بن عمرو _ وقيل سهيل بن عمرو _ الأنصارى المعروف بابن الحنظلية وهو من أهل بيعة الرضوان رضى الله عنه، قال: مر رسول الله صلى الله عليه وسلم ببعير قد لحق ظهره ببطنه، فقال: " اتقوا الله فى هذه البهائم المعجمة،، فاركبوها صالحة، وكلوها صالحة ".
** د
(رياض الصالحين ٣٠٧/١)
** إسناده صحيح','8','77',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3353,'<span data-type="title" id=toc-3360>٣٣٥٣ </span>- عن أنس رضى الله عنه قال: كنا إذا نزلنا منزلا لا نسبح حى نحل الرحال.
** د
(رياض الصالحين ٣٠٨/١)
** إسناده على شرط مسلم','8','78',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3354,'<span data-type="title" id=toc-3361>٣٣٥٤ </span>- عن جابر بن عبد الله رضى الله عنه قال: كان رسول الله صلى الله عليه وسلم يتخلف فى المسيرة فيزجى الضعيف، ويردف ويدعو له.
** د
(رياض الصالحين ٣٠٩/١)
** إسناده حسن','8','79',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3355,'<span data-type="title" id=toc-3362>٣٣٥٥ </span>- عن على بن ربيعة قال: شهدت على بن أبى طالب رضى الله عنه أتى بدابة ليركبها، فلما وضع رجله فى الركاب قال: بسم الله، فلما استوى على ظهرها قال: الحمد لله الذى سخر لنا هذا وما كنا له مقرنين وإنا إلى ربنا لمنقلبون ثم قال: الحمد لله ثلاث مرات، ثم قال: الله أكبر ثلاث مرات، ثم قال: سبحانك إنى ظلمت نفسى فاغفر لى إنه لا يغفر الذنوب إلا أنت، ثم ضحك، فقيل: يا أمير المؤمنين، من أى شىء ضحكت؟ ، قال: رأيت النبى صلى الله عليه وسلم فعل كما فعلت، ثم ضحك، فقلت: يا رسول الله من أى شىء ضحكت؟ ، قال: " إن ربك سبحانه يعجب من عبده إذا قال: اغفر لى ذنوبى يعلم أنه لا يغفر الذنوب غيرى ".
** د ت
(رياض الصالحين ٣١٠/١)
** قال الإمام النووى فى " الرياض " ١ / ٣١١: قال الترمذى: حديث حسن، وفى بعض النسخ حسن صحيح، وهذا لفظ أبى داود.','8','80',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3356,'<span data-type="title" id=toc-3363>٣٣٥٦ </span>- عن ابن عمر رضى الله عنهما، قال: كان النبى صلى الله عليه وسلم وجيوشه إذا علوا الثنايا كبروا، وإذا هبطوا سبحوا.
** د
(رياض الصالحين ٣١١/١)
** إسناده صحيح','8','81',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3357,'<span data-type="title" id=toc-3364>٣٣٥٧ </span>- عن أبى هريرة رضى الله عنه أن رجلا قال: يا رسول الله، إنى أريد أن أسافر فأوصنى، قال: " عليك بتقوى الله، والتكبير على كل شرف " فلما ولى الرجل قال: " اللهم اطو له البعد، وهون عليه السفر ".
** ت
(رياض الصالحين ٣١١/١)
** قال الإمام النووى فى " الرياض " ١ / ٣١٢: قال الترمذى: حديث حسن.','8','82',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3358,'<span data-type="title" id=toc-3365>٣٣٥٨ </span>- عن أبى هريرة رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " ثلاث دعوات مستجابات لا شك فيهن: دعوة المظلوم، ودعوة المسافر، ودعوة الوالد على ولده ".
** د ت
(رياض الصالحين ٣١٢/١)
** قال الإمام النووى فى " الرياض " ١ / ٣١٢: قال الترمذى: حديث حسن وليس فى رواية أبى داود " على ولده ".','8','83',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3359,'<span data-type="title" id=toc-3366>٣٣٥٩ </span>- عن أبى موسى الأشعرى رضى الله عنه أن رسول الله صلى الله عليه وسلم كان إذا خاف قوما قال: " اللهم إنا نجعلك فى نحورهم، ونعوذ بك من شرورهم ".
** د ن
(رياض الصالحين ٣١٢/١)
** إسناده صحيح','8','84',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3360,'<span data-type="title" id=toc-3367>٣٣٦٠ </span>- عن ابن مسعود رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " من قرأ حرفا من كتاب الله فله حسنة، والحسنة بعشر أمثالها، لا أقول: الم حرف، ولكن ألف حرف، ولام حرف، وميم حرف ".
** ت
(رياض الصالحين ٣١٦/١)
** قال الإمام النووى فى " الرياض " ١ / ٣١٦: قال الترمذى: حديث حسن صحيح.','8','85',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3361,'<span data-type="title" id=toc-3368>٣٣٦١ </span>- عن ابن عباس رضى الله عنهما قال: قال رسول الله صلى الله عليه وسلم: " إن الذى ليس فى جوفه شىء من القرآن كالبيت الخرب ".
** ت
(رياض الصالحين ٣١٦/١)
** قال الإمام النووى فى " الرياض " ١ / ٣١٦: قال الترمذى: حديث حسن صحيح.','8','86',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3362,'<span data-type="title" id=toc-3369>٣٣٦٢ </span>- عن عبد الله بن عمرو بن العاص رضى الله عنهما عن النبى صلى الله عليه وسلم قال: " يقال لصاحب القرآن: اقرأ وارتق ورتل كما كنت ترتل فى الدنيا، فإن منزلك عند آخر آية تقرؤها ".
** د ت
(رياض الصالحين ٣١٧/١)
** قال الإمام النووى فى " الرياض " ١ / ٣١٧: قال الترمذى: حسن صحيح.','8','87',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3363,'<span data-type="title" id=toc-3370>٣٣٦٣ </span>- عن أبى لبابة بشير بن عبد المنذر رضى الله عنه أن النبى صلى الله عليه وسلم قال: " من لم يتغن بالقرآن فليس منا ".
** د
(رياض الصالحين ٣١٨/١)
** إسناده جيد','8','88',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3364,'<span data-type="title" id=toc-3371>٣٣٦٤ </span>- عن أنس رضى الله عنه أن رجلا قال: يارسول الله إنى أحب هذه السورة (قل هو الله أحد) ، قال: " إن حبها أدخلك الجنة ".
** ت خت
(رياض الصالحين ٣١٩/١)
** قال الإمام النووى فى " الرياض " ١ / ٣١٩: قال الترمذى: حديث حسن.','8','89',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3365,'<span data-type="title" id=toc-3372>٣٣٦٥ </span>- عن أبى سعيدالخدرى رضى الله عنه قال: كان رسول الله صلى الله عليه وسلم يتعوذ من الجان، وعين الإنسان، حتى نزلت المعوذتان، فلما نزلتا أخذ بهما وترك ما سواهما.
** ت
(رياض الصالحين ٣٢٠/١)
** قال الإمام النووى فى " الرياض " ١ / ٣٢٠: قال الترمذى: حديث حسن.','8','90',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3366,'<span data-type="title" id=toc-3373>٣٣٦٦ </span>- عن أبى هريرة رضى اله عنه أن رسول الله صلى الله عليه وسلم قال: " من القرآن سورة ثلاثين آية شفعت لرجل حتى غفرت له، وهى (تبارك الذى بيده الملك) "، وفى رواية أبى داود " تشفع ".
** د ت
(رياض الصالحين ٣٢٠/١)
** قال الإمام النووى فى " الرياض " ١ / ٣٢٠: قال الترمذى: حديث حسن.','8','91',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3367,'<span data-type="title" id=toc-3374>٣٣٦٧ </span>- عن أنس رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " الدعاء لا يرد بين الأذان والإقامة ".
** د ت
(رياض الصالحين ٣٢٦/١)
** قال الإمام النووى فى " الرياض " ١ / ٣٢٦: قال الترمذى: حديث حسن.','8','92',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3368,'<span data-type="title" id=toc-3375>٣٣٦٨ </span>- عن أبى سعيد الخدرى رضى الله عنه عن النبى صلى الله عليه وسلم قال: " إذا رأيتم الرجل يعتاد المساجد فاشهدوا له بالإيمان، قال الله عز وجل: إنما يعمر مساجد الله من أمن بالله واليوم الآخر ".
** ت
(رياض الصالحين ٣٣٠/١)
** قال الإمام النووى فى " الرياض " ١ / ٣٣٠: قال الترمذى: حديث حسن.','8','93',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3369,'<span data-type="title" id=toc-3376>٣٣٦٩ </span>- عن عبد الله _ وقيل عمرو بن قيس _ المعروف بابن أم مكتوم المؤذن رضى الله عنه أنه قال: يا رسول الله إن المدينة كثيرة الهوام، والسباع فقال رسول الله صلى الله عليه وسلم: " تسمع حى على الصلاة، حى على الفلاح فحيهلا ".
** د
(رياض الصالحين ٣٣٢/١)
** إسناده حسن','8','94',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3370,'<span data-type="title" id=toc-3377>٣٣٧٠ </span>- عن أبى الدرداء رضى الله عنه قال: سمعت رسول الله صلى الله عليه وسلم يقول: " ما من ثلاثة فى قرية، ولا بدو لا تقام فيهم الصلاة إلا قد استحوذ عليهم الشيطان، فعليكم بالجماعة، فإنما يأكل الذئب من الغنم القاصية ".
** د
(رياض الصالحين ٣٣٣/١)
** إسناده حسن','8','95',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3371,'<span data-type="title" id=toc-3378>٣٣٧١ </span>- عن عثمان بن عفان رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " من شهد العشاء فى جماعة كان له قيام نصف ليلة، ومن صلى العشاء والفجر فى جماعة، كان له كقيام ليلة ".
** ت
(رياض الصالحين ٣٣٣/١)
** قال الإمام النووى فى " الرياض " ١ / ٣٣٣: قال الترمذى: حديث حسن صحيح.','8','96',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3372,'<span data-type="title" id=toc-3379>٣٣٧٢ </span>- عن بريدة رضى الله عنه عن النبى صلى الله عليه وسلم قال: " العهد الذى بيننا وبينهم الصلاة، فمن تركها فقد كفر ".
** ت
(رياض الصالحين ٣٣٤/١)
** قال الإمام النووى فى " الرياض " ١ / ٣٣٥: قال الترمذى: حديث حسن صحيح.','8','97',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3373,'<span data-type="title" id=toc-3380>٣٣٧٣ </span>- عن شقيق بن عبد الله التابعى المتفق على جلالته رحمه الله قال: كان أصحاب محمد صلى الله عليه وسلم لا يرون شيئا من الأعمال تركه كفر غير الصلاة.
** ت
(رياض الصالحين ٣٣٥/١)
** إسناده صحيح','8','98',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3374,'<span data-type="title" id=toc-3381>٣٣٧٤ </span>- عن أبى هريرة رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " إن أول ما يحاسب عليه العبد يوم القيامة من عمله، صلاته، فإن صلحت فقد أفلح وأنجح، وإن فسدت، فقد خاب وخر فإن انتقص من فريضته شيئا قال الرب عز وجل: انظروا هل لعبدى من تطوع، فيكمل منها ما انتقص من الفريضة، ثم يكون سائر أعماله على هذا ".
** ت
(رياض الصالحين ٣٣٥/١)
** قال الإمام النووى فى " الرياض " ١ / ٣٣٥: قال الترمذى: حديث حسن.','8','99',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3375,'<span data-type="title" id=toc-3382>٣٣٧٥ </span>- عن البراء بن عازب رضى الله عنهما قال: كان رسول الله صلى الله عليه وسلم يتخلل الصف من ناحية إلى ناحية، يمسح صدورنا ومناكبنا ويقول: " لا تختلفوا فتختلف قلوبكم "، وكان يقول: " إن الله وملائكته يصلون على الصف الأول ".
** د
(رياض الصالحين ٣٣٧/١)
** إسناده حسن','8','100',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3376,'<span data-type="title" id=toc-3383>٣٣٧٦ </span>- عن ابن عمر رضى الله عنهما أن رسول الله صلى الله عليه وسلم قال:: أقيموا الصفوف، وحاذوا بين المناكب، وسددوا الخلل، ولينوا بأيدى إخوانكم، ولا تذروا فرجات للشيطان، من وصل صفا وصله الله ومن قطع صفا قطعه الله ".
** د
(رياض الصالحين ٣٣٧/١)
** إسناده صحيح','8','101',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3377,'<span data-type="title" id=toc-3384>٣٣٧٧ </span>- عن أنس رضى الله عنه أن رسول الله صلى الله عليه وسلم قال: " رصوا صفوفكم، وقاربوا بينها، وحاذوا بالأعناق، فوالذى نفسى بيده إنى لأرى الشيطان يدخل من خلل الصف كنأنها الحذف ".
** د
(رياض الصالحين ٣٣٧/١)
** إسناده صحيح
** قال الإمام النووى فى " الرياض " ١ / ٣٣٧: رواه أبو داود بإسناد على شرط مسلم.','8','102',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3378,'<span data-type="title" id=toc-3385>٣٣٧٨ </span>- عن أنس أن رسول الله صلى الله عليه وسلم قال: " أتموا الصف المقدم، ثم الذى يليه فما كان من نقص فليكن فى الصف المؤخر ".
** د
(رياض الصالحين ٣٣٧/١)
** إسناده حسن','8','103',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3379,'<span data-type="title" id=toc-3386>٣٣٧٩ </span>- عن عائشة رضى الله عنها قالت: قال رسول الله صلى الله عليه وسلم: " إن الله وملائكته يصلون على ميامن الصفوف ".
** د
(رياض الصالحين ٣٣٨/١)
** إسناده على شرط مسلم
** قال الإمام النووى فى " الرياض " ١ / ٣٣٨: رواه أبو داود بإسناد على شرط مسلم وفيه رجل مختلف في توثيقه.','8','104',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3380,'<span data-type="title" id=toc-3387>٣٣٨٠ </span>- عن أبى عبد الله بلال بن رباح رضى الله عنه، مؤذن رسول الله صلى الله عليه وسلم أنه أتى رسول الله صلى الله عليه وسلم، ليؤذنه بصلاة الغداة فشغلت عائشة بلالا بأمر سألته عنه حتى أصبح جدا فقام بلال فآذنه بالصلاة بالصلاة، وتابع أذانه فلم يخرج رسول الله صلى الله عليه وسلم فلما خرج صلى بالناس فأخبره أن عائشة شغلته بأمر سألته عنه حتى أصبح جدا، وأنه أبطأ عليه بالخروج، فقال _ يعنى النبى صلى الله عليه وسلم _: " إنى كنت ركعت ركعتى الفجر "، فقال: يارسول الله إنك أصبحت جدا، قال: " لو أصبحت أكثر مما أصبحت لركعتها، وأحسنتهما وأجملتهما ".
** د
(رياض الصالحين ٣٣٩/١)
** إسناده حسن','8','105',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3381,'<span data-type="title" id=toc-3388>٣٣٨١ </span>- عن ابن عمر رضى الله عنهما، قال: رفقت النبى صلى الله عليه وسلم شهرا وكان يقرأ فى الركعتين قبل الفجر: (قل يا أيها الكافرون) ، و (قل هو الله أحد) .
** ت
(رياض الصالحين ٣٤١/١)
** قال الإمام النووى فى " الرياض " ١ / ٣٤١: قال الترمذى: حديث حسن.','8','106',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3382,'<span data-type="title" id=toc-3389>٣٣٨٢ </span>- عن أبى هريرة رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " إذا صلى أحدكم ركعتى الفجر، فليضطجع على يمينه ".
** د ت
(رياض الصالحين ٣٤١/١)
** إسناده صحيح
** قال الإمام النووى فى " الرياض " ١ / ٣٤٢: قال الترمذى: حديث حسن صحيح.','8','107',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3383,'<span data-type="title" id=toc-3390>٣٣٨٣ </span>- عن أم حبيبة رضى الله عنها قالت: قال رسول الله صلى الله عليه وسلم: " من حافظ على أربع ركعات قبل الظهر، وأربع بعدها، حرمه الله على النار ".
** د ت
(رياض الصالحين ٣٤٢/١)
** قال الإمام النووى فى " الرياض " ١ / ٣٤٢: قال الترمذى: حديث حسن صحيح.','8','108',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3384,'<span data-type="title" id=toc-3391>٣٣٨٤ </span>- عن عبد الله بن السائب رضى الله عنه أن رسول الله صلى الله عليه وسلم كان يصلى أربعا بعد أن تزول الشمس قبل الظهر، وقال: " إنها ساعة تفتح فيها أبواب السماء، فأحب أن يصعد لى فيها عمل صالح ".
** ت
(رياض الصالحين ٣٤٢/١)
** قال الإمام النووى فى " الرياض " ١ / ٣٤٢: قال الترمذى: حديث حسن.','8','109',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3385,'<span data-type="title" id=toc-3392>٣٣٨٥ </span>- عن عائشة رضى الله عنها أن النبى صلى الله عليه وسلم كان إذا لم يصل أربعا قبل الظهر صلاهن بعدها.
** ت
(رياض الصالحين ٣٤٢/١)
** قال الإمام النووى فى " الرياض " ١ / ٣٤٣: قال الترمذى: حديث حسن.','8','110',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3386,'<span data-type="title" id=toc-3393>٣٣٨٦ </span>- عن على بن أبى طالب رضى الله عنه قال: كان النبى صلى الله عليه وسلم يصلى قبل العصر أربع ركعات يفصل بينهن بالتسليم على الملائكة المقربين ومن تبعهم من المسلمين والمؤمنين.
** ت
(رياض الصالحين ٣٤٣/١)
** قال الإمام النووى فى " الرياض " ١ / ٣٤٣: قال الترمذى: حديث حسن.','8','111',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3387,'<span data-type="title" id=toc-3394>٣٣٨٧ </span>- عن ابن عمر رضى الله عنهما، عن النبى صلى الله عليه وسلم قال: " رحم الله امرأ صلى قبل العصر أربعا ".
** د ت
(رياض الصالحين ٣٤٣/١)
** قال الإمام النووى فى " الرياض " ١ / ٣٤٣: قال الترمذى: حديث حسن.','8','112',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3388,'<span data-type="title" id=toc-3395>٣٣٨٨ </span>- عن على بن أبى طالب رضى الله عنه، أن النبى صلى الله عليه وسلم كان يصلى قبل العصر ركعتين.
** د
(رياض الصالحين ٣٤٣/١)
** إسناده صحيح','8','217',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3389,'<span data-type="title" id=toc-3396>٣٣٨٩ </span>- عن على رضى الله عنه قال: الوتر ليس بحتم كصلاة المكتوبة ولكن سن رسول الله صلى الله عليه وسلم قال: " إن الله وتر يحب الوتر فأوتروا يا أهل القرآن ".
** د ت
(رياض الصالحين ٣٤٦/١)
** قال الإمام النووى فى " الرياض " ١ / ٣٤٦: قال الترمذى: حديث حسن.','8','218',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3390,'<span data-type="title" id=toc-3397>٣٣٩٠ </span>- عن ابن عمر رضى الله عنهما أن النبى صلى الله عليه وسلم قال: " بادروا الصبح بالوتر ".
** د ت
(رياض الصالحين ٣٤٦/١)
** قال الإمام النووى فى " الرياض " ١ / ٣٤٦: قال الترمذى: حديث حسن صحيح.','8','219',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3391,'<span data-type="title" id=toc-3398>٣٣٩١ </span>- عن سمرة رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " من توضأ يوم الجمعة فبها ونعمت، ومن اغتسل فالغسل أفضل ".
** د ت
(رياض الصالحين ٣٥٠/١)
** قال الإمام النووى فى " الرياض " ١ / ٣٥٠: قال الترمذى: حديث حسن.','8','220',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3392,'<span data-type="title" id=toc-3399>٣٣٩٢ </span>- عن أوس بن أوس رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " إن من أفضل أيامكم يوم الجمعة، فأكثروا على من الصلاة فيه، فإن صلاتكم معروضة على ".
** د
(رياض الصالحين ٣٥١/١)
** إسناده صحيح','8','221',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3393,'<span data-type="title" id=toc-3400>٣٣٩٣ </span>- عن عبد الله بن سلام رضى الله عنه أن النبى صلى الله عليه وسلم قال: " أيها الناس أفشوا السلام بينكم، وأطعموا الطعام، وصلوا بالليل والناس نيام، تدخلوا الجنة بسلام ".
** ت
(رياض الصالحين ٣٥٣/١)
** قال الإمام النووى فى " الرياض " ١ / ٣٥٣: قال الترمذى: حديث حسن صحيح.','8','222',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3394,'<span data-type="title" id=toc-3401>٣٣٩٤ </span>- عن أبى هريرة رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " رحم الله رجلا قام من الليل، فصلى وأيقظ امرأته، فإن أبت نضح فى وجهها الماء، رحم الله امرأة قامت من الليل فصلت، وأيقظت زوجها فإن أبى نضحت فى وجهه الماء ".
** د
(رياض الصالحين ٣٥٦/١)
** إسناده صحيح','8','223',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3395,'<span data-type="title" id=toc-3402>٣٣٩٥ </span>- عن أبى هريرة وأبى سعيد رضى الله عنهما، قالا: قال رسول الله صلى الله عليه وسلم: " إذا أيقظ الرجل أهله من الليل فصليا _ أو صلى _ ركعتين جميعا كتبا فى الذاكرين والذاكرات ".
** د
(رياض الصالحين ٣٥٦/١)
** إسناده صحيح','8','224',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3396,'<span data-type="title" id=toc-3403>٣٣٩٦ </span>- عن عائشة قالت: قلت: يارسول الله أرأيت إن علمت ألا ليلة ليلة القدر ما أقول فيها؟ ، قال: " قولى اللهم إنك عفو تحب العفو فاعف عنى ".
** ت
(رياض الصالحين ٣٥٨/١)
** قال الإمام النووى فى " الرياض " ١ / ٣٥٨: قال الترمذى: حديث حسن صحيح.','8','225',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3397,'<span data-type="title" id=toc-3404>٣٣٩٧ </span>- عن عائشة رضى الله عنها أن النبى صلى الله عليه وسلم قال: " السواك مطهرة للفم مرضاة للرب ".
** ن خز
(رياض الصالحين ٣٥٩/١)
** إسناده صحيح','8','226',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3398,'<span data-type="title" id=toc-3405>٣٣٩٨ </span>- عن ابن عباس رضى الله عنهما قال: قال رسول الله صلى الله عليه وسلم: " لا تصوموا قبل رمضان، صوموا لرؤيته وأفطروا لرؤيته، فإن حالت دونه غيابة فأكملوا ثلاثين يوما ".
** ت
(رياض الصالحين ٣٦٦/١)
** قال الإمام النووى فى " الرياض " ١ / ٣٦٦: قال الترمذى: حديث حسن صحيح.','8','227',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3399,'<span data-type="title" id=toc-3406>٣٣٩٩ </span>- عن أبى هريرة رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " إذا بقى نصف من شعبان فلا تصوموا ".
** ت
(رياض الصالحين ٣٦٦/١)
** قال الإمام النووى فى " الرياض " ١ / ٣٦٦: قال الترمذى: حديث حسن صحيح.','8','228',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3400,'<span data-type="title" id=toc-3407>٣٤٠٠ </span>- عن أبى اليقظان عمار بن ياسر رضى الله عنهما قال: من صام اليوم الذى يشك فيه فقد عصى أبا القاسم صلى الله عليه وسلم.
** د ت
(رياض الصالحين ٣٦٦/١)
** قال الإمام النووى فى " الرياض " ١ / ٣٦٦ _ ٣٦٧: قال الترمذى: حديث حسن صحيح.','8','229',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3401,'<span data-type="title" id=toc-3408>٣٤٠١ </span>- عن طلحة بن عبيد الله رضى الله عنه أن النبى صلى الله عليه وسلم كان إذا رأى الهلال قال: " اللهم أهله علينا بالأمن والإيمان والسلامة والإسلام ربى وربك الله، هلال رشد وخير ".
** ت
(رياض الصالحين ٣٦٧/١)
** قال الإمام النووى فى " الرياض " ١ / ٣٦٧: قال الترمذى: حديث حسن.','8','230',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3402,'<span data-type="title" id=toc-3409>٣٤٠٢ </span>- عن أبى هريرة رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " قال الله عز وجل: أحب عبادى إلى أعجلهم فطرا ".
** ت
(رياض الصالحين ٣٦٨/١)
** قال الإمام النووى فى " الرياض " ١ / ٣٦٨: قال الترمذى: حديث حسن.','8','231',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3403,'<span data-type="title" id=toc-3410>٣٤٠٣ </span>- عن سلمان بن عامر الضبى الصحابى رضى الله عنه عن النبى صلى الله عليه وسلم قال: " إذا أفطر أحدكم، فليفطر على تمر، فإن لم يجد فليفطر على ماء فإنه طهور ".
** د ت
(رياض الصالحين ٣٦٩/١)
** قال الإمام النووى فى " الرياض " ١ / ٣٦٩: قال الترمذى: حديث حسن صحيح.','8','232',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3404,'<span data-type="title" id=toc-3411>٣٤٠٤ </span>- عن أنس رضى الله عنه، قال: كان رسول الله صلى الله عليه وسلم يفطر قبل أن يصلى على رطبات، فإن لم تكن رطبات فتميرات، فإن لم تكن تميرات حسا حسوات من ماء ".
** د ت
(رياض الصالحين ٣٦٩/١)
** قال الإمام النووى فى " الرياض " ١ / ٣٦٩: قال الترمذى: حديث حسن.','8','233',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3405,'<span data-type="title" id=toc-3412>٣٤٠٥ </span>- عن لقيط بن صبرة رضى الله عنه قال: قلت: يارسول الله أخبرنى عن الوضوء؟ ، فقال: " أسبغ الوضوء، وخلل بين الأصابع، وبالغ فى الإستنشاق، إلا أن تكون صائما ".
** د ت
(رياض الصالحين ٣٧٠/١)
** قال الإمام النووى فى " الرياض " ١ / ٣٧٠: قال الترمذى: حديث حسن صحيح.','8','234',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3406,'<span data-type="title" id=toc-3413>٣٤٠٦ </span>- عن أبى هريرة رضى الله عنه عن رسول الله صلى الله عليه وسلم قال: " تعرض الأعمال يوم الإثنين والخميس، فأحب أن يعرض عملى وأنا صائم ".
** ت
(رياض الصالحين ٣٧٢/١)
** قال الإمام النووى فى " الرياض " ١ / ٣٧٢: قال الترمذى: حديث حسن.','8','235',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3407,'<span data-type="title" id=toc-3414>٣٤٠٧ </span>- عن عائشة رضى الله عنها، قالت: كان رسول الله صلى الله عليه وسلم يتحرى صوم الإثنين والخميس.
** ت
(رياض الصالحين ٣٧٢/١)
** قال الإمام النووى فى " الرياض " ١ / ٣٧٢: قال الترمذى: حديث حسن.','8','236',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3408,'<span data-type="title" id=toc-3415>٣٤٠٨ </span>- عن أبى ذر رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " إذا صمت من الشهر ثلاثا، فصم ثلاث عشرة، وأربع عشرة، وخمس عشرة ".
** ت
(رياض الصالحين ٣٧٣/١)
** قال الإمام النووى فى " الرياض " ١ / ٣٧٣: قال الترمذى: حديث حسن.','8','237',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3409,'<span data-type="title" id=toc-3416>٣٤٠٩ </span>- عن ابن عباس رضى الله عنهما، قال: كان رسول الله صلى الله عليه وسلم لا يفطر أيام البيض فى حضر ولا سفر.
** ن
(رياض الصالحين ٣٧٤/١)
** إسناده حسن','8','238',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3410,'<span data-type="title" id=toc-3417>٣٤١٠ </span>- عن زيد بن خالد الجهنى رضى الله عنه عن النبى صلى الله عليه وسلم قال: " من فطر صائما كان له مثل أجره، غير أنه لا ينقص من أجر الصائم شىء ".
** ت
(رياض الصالحين ٣٧٤/١)
** قال الإمام النووى فى " الرياض " ١ / ٣٧٤: قال الترمذى: حديث حسن صحيح.','8','239',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3411,'<span data-type="title" id=toc-3418>٣٤١١ </span>- عن أم عمارة الأنصارية، رضى الله عنها أن النبى صلى الله عليه وسلم دخل عليها، فقدمت إليه طعاما، فقال: " كلى "، فقالت: إنى صائمة فقال رسول الله صلى الله عليه وسلم: " إن الصائم تصلى عليه الملائكة إذا أكل عنده حتى يفرغوا "، وربما قال: " حتى يشبعوا ".
** ت
(رياض الصالحين ٣٧٤/١)
** قال الإمام النووى فى " الرياض " ١ / ٣٧٤: قال الترمذى: حديث حسن.','8','240',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3412,'<span data-type="title" id=toc-3419>٣٤١٢ </span>- عن أنس رضى الله عنه أن النبى صلى الله عليه وسلم جاء إلى سعد بن عبادة رضى الله عنه، فجاء بخبز وزيت فأكل، ثم قال النبى صلى الله عليه وسلم: " أفطر عندكم صائمون، وأكل طعامكم الأبرار وصلت عليكم الملائكة ".
** د
(رياض الصالحين ٣٧٤/١)
** إسناده صحيح','8','241',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3413,'<span data-type="title" id=toc-3420>٣٤١٣ </span>- عن لقيط بن عامر رضى الله عنه أنه أتى النبى صلى الله عليه وسلم فقال: إن أبى شيخ كبير لا يستطيع الحج، ولا العمرة، ولا الظعن؟ ، قال: " حج عن أبيك واعتمر ".
** د ت
(رياض الصالحين ٣٧٧/١)
** قال الإمام النووى فى " الرياض " ١ / ٣٧٧: قال الترمذى: حديث حسن صحيح.','8','242',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3414,'<span data-type="title" id=toc-3421>٣٤١٤ </span>- عن فضالة بن عبيد رضى الله عنه أن رسول الله صلى الله عليه وسلم قال: " كل ميت يختم على عمله إلا المرابط فى سبيل الله، فإنه ينمى له عمله إلى يوم القيامة ويؤمن من فتنة القبر ".
** د ت
(رياض الصالحين ٣٨٠/١)
** قال الإمام النووى فى " الرياض " ١ / ٣٨٠: قال الترمذى: حديث حسن صحيح.','8','243',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3415,'<span data-type="title" id=toc-3422>٣٤١٥ </span>- عن عثمان رضى الله عنه قال: سمعت رسول الله صلى الله عليه وسلم يقول: " رباط فى سبيل الله خير من ألف يوم فيما سواه من المنازل ".
** ت
(رياض الصالحين ٣٨٠/١)
** قال الإمام النووى فى " الرياض " ١ / ٣٨٠: قال الترمذى: حديث حسن صحيح.','8','244',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3416,'<span data-type="title" id=toc-3423>٣٤١٦ </span>- عن معاذ رضى الله عنه عن النبى صلى الله عليه وسلم قال: " من قاتل فى سبيل الله من رجل مسلم فواق ناقة وجبت له الجنة، ومن جرح جرحا فى سبيل الله أو نكب نكبة، فإنها تجىء يوم القيامة كأغزر ما كانت: لونها الزعفران وريحها المسك ".
** د ت
(رياض الصالحين ٣٨١/١)
** قال الإمام النووى فى " الرياض " ١ / ٣٨١: قال الترمذى: حديث حسن صحيح.','8','245',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3417,'<span data-type="title" id=toc-3424>٣٤١٧ </span>- عن أبى هريرة رضى الله عنه قال: مر رجل من أصحاب رسول الله صلى الله عليه وسلم بشعب فيه عيينة من ماء عذب، فأعجبته، فقال: لو اعتزلت الناس فأقمت فى هذا الشعب، ولن أفعل حتى أستأذن رسول الله صلى الله عليه وسلم فذكر ذلك لرسول الله صلى الله عليه وسلم، فقال: " لا تفعل فإن مقام أحدكم فى سبيل الله أفضل من صلاته فى بيته سبعين عاما، ألا تحبون أن يغفر الله لكم، ويدخلكم الجنة؟ ، اغزوا فى سبيل الله من قاتل فى سبيل الله فواق ناقة وجبت له الجنة ".
** ت
(رياض الصالحين ٣٨١/١)
** قال الإمام النووى فى " الرياض " ١ / ٣٨١: قال الترمذى: حديث حسن.','8','246',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3418,'<span data-type="title" id=toc-3425>٣٤١٨ </span>- عن أبى هريرة رضى الله عنه، قال: قال رسول الله صلى الله عليه وسلم: يقول: " لا يلج النار رجل بكى من خشية الله حتى يعود اللبن فى الضرع، ولا يجتمع على عبد غبار فى سبيل الله ودخان جهنم ".
** ت
(رياض الصالحين ٣٨٣/١)
** قال الإمام النووى فى " الرياض " ١ / ٣٨٣: قال الترمذى: حدييث حسن صحيح.','8','247',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3419,'<span data-type="title" id=toc-3426>٣٤١٩ </span>- عن ابن عباس رضى الله عنهما قال: سمعت رسول الله صلى الله عليه وسلم يقول: " عينان لا تمسهماالنار: عين بكت من خشية الله، وعين باتت تحرس فى سبيل الله ".
** ت
(رياض الصالحين ٣٨٣/١)
** قال الإمام النووى فى " الرياض " ١ / ٣٨٣: قال الترمذى: حديث حسن.','8','248',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3420,'<span data-type="title" id=toc-3427>٣٤٢٠ </span>- عن أبى أمامة رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " أفضل الصدقات ظل فسطاط فى سبيل الله، ومنيحة خادم فى سبيل الله أو طروقة فحل فى سبيل الله ".
** ت
(رياض الصالحين ٣٨٤/١)
** قال الإمام النووى فى " الرياض " ١ / ٣٨٤: قال الترمذى: حديث حسن صحيح.','8','249',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3421,'<span data-type="title" id=toc-3428>٣٤٢١ </span>- عن أبى هريرة رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " ما يجد الشهيد من مس القتل كما يجد أحدكم من مس القرصة ".
** ت
(رياض الصالحين ٣٨٨/١)
** قال الإمام النووى فى " الرياض " ١ / ٢٥٤: قال الترمذى: حديث حسن صحيح.','8','250',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3422,'<span data-type="title" id=toc-3429>٣٤٢٢ </span>- عن سهل بن سعد رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " ثنتان لا تردان، أو قلما تردان: الدعاء عند النداء وعند البأس حين يلحم بعضهم بعضا ".
** د
(رياض الصالحين ٣٨٨/١)
** إسناده صحيح','8','251',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3423,'<span data-type="title" id=toc-3430>٣٤٢٣ </span>- عن أنس رضى الله عنه قال: كان رسول الله صلى الله عليه وسلم إذا غزا قال: " اللهم أنت عضدى ونصيرى، بك أحول وبك أصول وبك أقاتل ".
** د ت
(رياض الصالحين ٣٨٩/١)
** قال الإمام النووى فى " الرياض " ١ / ٣٨٩: قال الترمذى: حديث حسن.','8','252',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3424,'<span data-type="title" id=toc-3431>٣٤٢٤ </span>- عن أبى موسى رضى الله عنه أن النبى صلى الله عليه وسلم كان إذا خاف قوما قال: " اللهم إنا نجعلك فى نحورهم ونعوذ بك من شرورهم ".
** د
(رياض الصالحين ٣٨٩/١)
** إسناده صحيح','8','253',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3425,'<span data-type="title" id=toc-3432>٣٤٢٥ </span>- عن عمرو بن عبسة رضى الله عنه قال: سمعت رسول الله صلى الله عليه وسلم، يقول: " من رمى بسهم فى سبيل الله فهو له عدل محررة ".
** د ت
(رياض الصالحين ٣٩٠/١)
** قال الإمام النووى فى " الرياض " ١ / ٣٩٠: قال الترمذى: حديث حسن صحيح.','8','254',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3426,'<span data-type="title" id=toc-3433>٣٤٢٦ </span>- عن أبى يحيى خريم بن فاتك رضى الله عنه، قال: قال رسول الله صلى الله عليه وسلم: " من أنفق نفقة فى سبيل الله كتب له سبعمائة ضعف ".
** ت
(رياض الصالحين ٣٩٠/١)
** قال الإمام النووى فى " الرياض " ١ / ٣٩١: قال الترمذى: حديث حسن.','8','255',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3427,'<span data-type="title" id=toc-3434>٣٤٢٧ </span>- عن أبى أمامة رضى الله عنه عن النبى صلى الله عليه وسلم قال: " من صام يوما فى سبيل الله جعل الله بينه، وبين النار خندقا كما بين السماء والأرض ".
** ت
(رياض الصالحين ٣٩١/١)
** قال الإمام النووى فى " الرياض " ١ / ٣٩١: قال الترمذى: حديث حسن صحيح.','8','256',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3428,'<span data-type="title" id=toc-3435>٣٤٢٨ </span>- عن أبى أمامة رضى الله عنه أن رجلا قال: يا رسول الله ائذن لى فى السياحة، فقال النبى صلى الله عليه وسلم: " إن سياحة أمتى الجهاد فى سبيل الله عز وجل ".
** د
(رياض الصالحين ٣٩٢/١)
** إسناده جيد','8','257',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3429,'<span data-type="title" id=toc-3436>٣٤٢٩ </span>- عن عبد الله بن عمرو بن العاص رضى الله عنهما عن النبى صلى الله عليه وسلم قال: " قفلة كغزوة ".
** د
(رياض الصالحين ٣٩٢/١)
** إسناده جيد','8','258',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3430,'<span data-type="title" id=toc-3437>٣٤٣٠ </span>- عن السائب بن يزيد رضى الله عنه قال: لما قدم النبى صلى الله عليه وسلم من غزوة تبوك تلقاه الناس، فتلقيته مع الصبيان على ثنية الوداع ".
** د
(رياض الصالحين ٣٩٢/١)
** إسناده صحيح','8','259',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3431,'<span data-type="title" id=toc-3438>٣٤٣١ </span>- عن أبى أمامة رضى الله عنه عن النبى صلى الله عليه وسلم قال: " من لم يغز أو يجهز غازيا أو يخلف غازيا فى أهله بخير، أصابه الله بقارعة قبل يوم القيامة ".
** د
(رياض الصالحين ٣٩٣/١)
** إسناده صحيح','8','260',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3432,'<span data-type="title" id=toc-3439>٣٤٣٢ </span>- عن أنس رضى الله عنه أن النبى صلى الله عليه وسلم قال: " جاهدوا المشركين بأموالكم وأنفسكم وألسنتكم ".
** د
(رياض الصالحين ٣٩٣/١)
** إسناده صحيح','8','261',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3433,'<span data-type="title" id=toc-3440>٣٤٣٣ </span>- عن أبى عمرو ويقال: أبو حكيم النعمان بن مقرن رضى الله عنه قال: شهدت رسول الله صلى الله عليه وسلم إذا لم يقاتل من أول النهار أخر القتال حتى تزول الشمس، وتهب الرياح وينزل النصر ".
** د ت
(رياض الصالحين ٣٩٣/١)
** قال الإمام النووى فى " الرياض " ١ / ٣٩٣: قال الترمذى: حديث حسن صحيح.','8','262',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3434,'<span data-type="title" id=toc-3441>٣٤٣٤ </span>- عن أبى الأعور سعيد بن زيد بن عمرو بن نفيل أحد العشرة المشهود لهم بالجنة رضى الله عنهم قال: سمعت رسول الله صلى الله عليه وسلم يقول: " من قتل دون ماله فهو شهيد ومن قتل دون دمه فهو شهيد، ومن قتل دون دينه فهو شهيد ومن قتل دون أهله فهو شهيد ".
** د ت
(رياض الصالحين ٣٩٤/١)
** قال الإمام النووى فى " الرياض " ١ / ٣٩٤: قال الترمذى: حديث حسن صحيح.','8','263',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3435,'<span data-type="title" id=toc-3442>٣٤٣٥ </span>- عن أبى هريرة رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " من أنظر معسرا أو وضع له، أظله الله يوم القيامة تحت ظل عرشه يوم لا ظل إلا ظله ".
** ت
(رياض الصالحين ٣٩٩/١)
** قال الإمام النووى فى " الرياض " ١ / ٣٩٩: قال الترمذى: حديث حسن صحيح.','8','264',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3436,'<span data-type="title" id=toc-3443>٣٤٣٦ </span>- عن أبى صفوان سويد بن قيس رضى الله عنه قال: جلبت أنا ومخرمة العبدى بزا من هجر، فجاءنا النبى صلى الله عليه وسلم، فساومنا بسراويل وعندى وزان يزن بالأجر، فقال النبى صلى الله عليه وسلم للوزان: " زن وأرجع ".
** د ت
(رياض الصالحين ٣٩٩/١)
** قال الإمام النووى فى " الرياض " ١ / ٣٩٩: قال الترمذى: حديث حسن صحيح.','8','265',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3437,'<span data-type="title" id=toc-3444>٣٤٣٧ </span>- عن أبى هريرة قال: سمعت رسول الله صلى الله عليه وسلم يقول: " الدنيا ملعونة، ملعون ما فيها، إلا بذكر الله تعالى، وما والاه، وعالما، أو متعلما ".
** ت
(رياض الصالحين ٤٠١/١)
** قال الإمام النووى فى " الرياض " ١ / ٤٠١: قال الترمذى: حديث حسن.','8','266',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3438,'<span data-type="title" id=toc-3445>٣٤٣٨ </span>- عن أبى سعيد الخدرى رضى الله عنه عن رسول الله صلى الله عليه وسلم قال: " لن يشبع مؤمن من خير حتى يكون منتهاه الجنة ".
** ت
(رياض الصالحين ٤٠١/١)
** قال الإمام النووى فى " الرياض " ١ / ٤٠١ _ ٤٠٢: قال الترمذى: حديث حسن.','8','267',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3439,'<span data-type="title" id=toc-3446>٣٤٣٩ </span>- عن أنس رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " من خرج فى طلب العلم، كان فى سبيل الله حتى يرجع ".
** ت
(رياض الصالحين ٤٠١/١)
** قال الإمام النووى فى " الرياض " ١ / ٤٠١: قال الترمذى: حديث حسن.','8','268',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3440,'<span data-type="title" id=toc-3447>٣٤٤٠ </span>- عن أبى أمامة رضى الله عنه، أن رسول الله صلى الله عليه وسلم قال: " فضل العالم على العابد كفضلى على أدناكم "، ثم قال رسول الله صلى الله عليه وسلم: " إن الله وملائكته وأهل السموات، والأرض حتى النملة فى جحرها وحتى الحوت ليصلون على معلمى الناس الخير ".
** ت
(رياض الصالحين ٤٠٢/١)
** قال الإمام النووى فى " الرياض " ١ / ٤٠٢: قال الترمذى: حديث حسن.','8','269',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3441,'<span data-type="title" id=toc-3448>٣٤٤١ </span>- عن ابن مسعود رضى الله عنه قال: سمعت رسول الله صلى الله عليه وسلم يقول: " نضر الله امرأ سمع منا شيئا، فبلغه كما سمعه فرب مبلغ أوعى من سامع ".
** ت
(رياض الصالحين ٤٠٢/١)
** قال الإمام النووى فى " الرياض " ١ / ٤٠٢: قال الترمذى: حديث حسن صحيح.','8','270',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3442,'<span data-type="title" id=toc-3449>٣٤٤٢ </span>- عن أبى هريرة رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " من سئل عن علم فكتمه، ألجم يوم القيامة بلجام من نار ".
** د ت
(رياض الصالحين ٤٠٢/١)
** قال الإمام النووى فى " الرياض " ١ / ٤٠٢: قال الترمذى: حديث حسن.','8','271',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3443,'<span data-type="title" id=toc-3450>٣٤٤٣ </span>- عن أبى هريرة رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " من تعلم علما يبتغى به وجه الله عز وجل لا يتعلمه إلا ليصيب به عرضا من الدنيا لم يجد عرف الجنة يوم القيامة ".
** د
(رياض الصالحين ٤٠٢/١)
** إسناده صحيح','8','272',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3444,'<span data-type="title" id=toc-3451>٣٤٤٤ </span>- عن أبى هريرة عن رسول الله صلى الله عليه وسلم قال: " كل أمر ذى بال لا يبدأ فيه بالحمد لله فهو أقطع ".
** د
(رياض الصالحين ٤٠٣/١)
** حسن','8','273',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3445,'<span data-type="title" id=toc-3452>٣٤٤٥ </span>- عن ابن مسعود رضى الله عنه أن رسول الله صلى الله عليه وسلم قال: " أولى الناس بى يوم القيامة أكثرهم على صلاة ".
** ت
(رياض الصالحين ٤٠٤/١)
** قال الإمام النووى فى " الرياض " ١ / ٤٠٤: قال الترمذى: حديث حسن.','8','274',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3446,'<span data-type="title" id=toc-3453>٣٤٤٦ </span>- عن أوس بن أوس رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " إن من أفضل أيامكم يوم الجمعة، فأكثروا على من الصلاة فيه، فإن صلاتكم معروضة على "، فقالوا: يا رسول الله، وكيف تعرض صلاتنا عليك وقد أرمت قال: يقول: بليت، قال: " إن الله عز وجل حرم على الأرض أجساد الأنبياء "
** د
(رياض الصالحين ٤٠٤/١)
** إسناده صحيح','8','275',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3447,'<span data-type="title" id=toc-3454>٣٤٤٧ </span>- عن أبى هريرة رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " رغم أنف رجل ذكرت عنده فلم يصل على ".
** ت
(رياض الصالحين ٤٠٥/١)
** قال الإمام النووى فى " الرياض " ١ / ٤٠٥: قال الترمذى: حديث حسن.','8','276',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3448,'<span data-type="title" id=toc-3455>٣٤٤٨ </span>- عن أبى هريرة رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " لا تجعلوا قبرى عيدا وصلوا على، فإن صلاتكم تبلغنى حيث كنتم ".
** د
(رياض الصالحين ٤٠٥/١)
** إسناده صحيح','8','277',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3449,'<span data-type="title" id=toc-3456>٣٤٤٩ </span>- عن أبى هريرة عن رسول الله صلى الله عليه وسلم قال: " ما من أحد يسلم على إلا رد الله على روحى حتى أرد عليه السلام ".
** د
(رياض الصالحين ٤٠٥/١)
** إسناده صحيح','8','278',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3450,'<span data-type="title" id=toc-3457>٣٤٥٠ </span>- عن على رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " البخيل من ذكرت عنده فلم يصل على ".
** ت
(رياض الصالحين ٤٠٥/١)
** قال الإمام النووى فى " الرياض " ١ / ٤٠٥: قال الترمذى: حديث حسن صحيح.','8','279',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3451,'<span data-type="title" id=toc-3458>٣٤٥١ </span>- عن فضالة بن عبيد رضى الله عنه، قال: سمع رسول الله صلى الله عليه وسلم رجلا يدعو فى صلاته لم يمجد الله تعالى ولم يصل على النبى صلى الله عليه وسلم فقال رسول الله صلى الله عليه وسلم: " عجل هذا "، ثم دعاه فقال له _ أو لغيره _: " إذا صلى أحدكم فليبدأ بتحميد ربه سبحانه، والثناء عليه ثم يصلى على النبى صلى الله عليه وسلم ثم يدعو بعد بما شاء ".
** د ت
(رياض الصالحين ٤٠٥/١)
** قال الإمام النووى فى " الرياض " ١ / ٤٠٥: قال الترمذى: حديث حسن صحيح.','8','280',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3452,'<span data-type="title" id=toc-3459>٣٤٥٢ </span>- عن جابر رضى الله عنه قال: سمعت رسول الله صلى الله عليه وسلم يقول: " أفضل الذكر لا إله إلا الله ".
** ت
(رياض الصالحين ٤١٣/١)
** قال الإمام النووى فى " الرياض " ١ / ٤١٣: قال الترمذى: حديث حسن.','8','281',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3453,'<span data-type="title" id=toc-3460>٣٤٥٣ </span>- عن عبد الله بن بسر رضى الله عنه أن رجلا قال: يارسول الله إن شرائع الإسلام قد كثرت على، فأخبرنى بشىء أتشبث به، قال: " لا يزال لسانك رطبا من ذكر الله ".
** ت
(رياض الصالحين ٤١٣/١)
** قال الإمام النووى فى " الرياض " ١ / ٤١٣: قال الترمذى: حديث حسن.','8','282',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3454,'<span data-type="title" id=toc-3461>٣٤٥٤ </span>- عن جابر رضى الله عنه عن النبى صلى الله عليه وسلم قال: " من قال: سبحان الله وبحمده غرست له نخلة فى الجنة ".
** ت
(رياض الصالحين ٤١٣/١)
** قال الإمام النووى فى " الرياض " ١ / ٤١٣: قال الترمذى: حديث حسن.','8','283',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3455,'<span data-type="title" id=toc-3462>٣٤٥٥ </span>- عن ابن مسعود رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " لقيت إبراهيم ليلة أسرى بى، فقال: يامحمد أقرىء أمتك السلام وأخبرهم أن الجنة طيبة التربة، عذبة الماء وأنها قيعان وأن غراسها سبحان الله والحمد لله ولا إله إلا الله والله أكبر ".
** ت
(رياض الصالحين ٤١٣/١)
** قال الإمام النووى فى " الرياض " ١ / ٤١٤: قال الترمذى: حديث حسن.','8','284',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3456,'<span data-type="title" id=toc-3463>٣٤٥٦ </span>- عن أبى الدرداء رضى الله عنه، قال: قال رسول الله صلى الله عليه وسلم: " ألا أنبئكم بخير أعمالكم، وأزكاها عند مليككم، وأرفعها فى درجاتكم وخير لكم من إنفاق الذهب والفضة، وخير لكم من أن تلقوا عدوكم فتضربوا أعناقهم، ويضربوا أعناقكم؟ "، قالوا: بلى، قال: " ذكر الله تعالى ".
** ت
(رياض الصالحين ٤١٤/١)
** قال الإمام النووى فى " الرياض " ١ / ٤١٤: قال الحاكم أبو عبد الله: إسناده صحيح.','8','285',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3457,'<span data-type="title" id=toc-3464>٣٤٥٧ </span>- عن سعد بن بى وقاص رضى الله عنه أنه دخل مع رسول الله صلى الله عليه وسلم على امرأة، وبين يديها نوى _ حصى _ تسبح به، فقال: " أخبرك بما هو أيسر عليك من هذا، _ أو أفضل _ "، فقال: " سبحان الله عدد ما خلق فى السماء، وسبحان الله عدد ما هو خالق، والله أكبر مثل ذلك، والحمد لله مثل ذلك ولا إله إلا الله مثل ذلك ولا حول ولا قوة إلا بالله مثل ذلك ".
** ت
(رياض الصالحين ٤١٤/١)
** قال الإمام النووى فى " الرياض " ١ / ٤١٤: قال الترمذى: حديث حسن.','8','286',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3458,'<span data-type="title" id=toc-3465>٣٤٥٨ </span>- عن أبى هريرة رضى الله عنه عن النبى صلى الله عليه وسلم أنه كان يقول إذا أصبح: " اللهم بك أصبحنا وبك أمسينا وبك نحيا وبك نموت وإليك النشور "، وإذا أمسى قال: " اللهم بك أمسينا، وبك نحيا وبك نموت وإليك النشور ".
** د ت
(رياض الصالحين ٤١٩/١)
** قال الإمام النووى فى " الرياض " ١ / ٤١٩: قال الترمذى: حديث حسن.','8','287',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3459,'<span data-type="title" id=toc-3466>٣٤٥٩ </span>- عن أبى هريرة أن أبابكرالصديق رضى الله عنه، قال: يا رسول الله مرنى بكلمات أقولهن إذا أصبحت وإذا أمسيت قال: " قل: اللهم فاطر السماوات والأرض عالم الغيب والشهادة، رب كل شىء ومليكه، أشهد أن لا إله إلا أنت أعوذ بك من شر نفسى، وشر الشيطان وشركه "، قال: " قلها إذا أصبحت وإذا أمسيت وإذا أخذت مضجعك ".
** د ت
(رياض الصالحين ٤١٩/١)
** قال الإمام النووى فى " الرياض " ١ / ٤١٩: قال الترمذى: حديث حسن صحيح.','8','288',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3460,'<span data-type="title" id=toc-3467>٣٤٦٠ </span>- عن عبد الله بن خبيب رضى الله عنه قال: قال لى رسول الله صلى الله عليه وسلم: " اقرأ: قل هو الله أحد، والمعوذتين حين تمسى وحين تصبح، ثلاث مرات تكفيك من كل شىء ".
** د ت
(رياض الصالحين ٤١٩/١)
** قال الإمام النووى فى " الرياض " ١ / ٤١٩: قال الترمذى: حديث حسن صحيح.','8','289',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3461,'<span data-type="title" id=toc-3468>٣٤٦١ </span>- عن عثمان بن عفان رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " ما من عبد يقول فى صباح كل يوم ومساء كل ليلة: بسم الله الذى لا يضر مع اسمه شىء فى الأرض ولا فى السماء وهو السميع العليم، ثلاث مرات إلا لم يضره شىء ".
** د ت
(رياض الصالحين ٤٢٠/١)
** قال الإمام النووى فى " الرياض " ١ / ٤٢٠: قال الترمذى: حديث حسن صحيح.','8','290',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3462,'<span data-type="title" id=toc-3469>٣٤٦٢ </span>- عن حذيفة رضى الله عنه، أن رسول الله صلى الله عليه وسلم كان إذا أراد أن يرقد وضع يده اليمنى تحت عضده ثم يقول: " اللهم قنى عذابك يوم تبعث عبادك ".
** ت
(رياض الصالحين ٤٢١/١)
** قال الترمذى: حديث حسن.قال الإمام النووى فى " الرياض " ١ / ٤٢١: قال الترمذى: حديث حسن. ورواه أبو داود من رواية حفصة رضى الله عنها وفيه أنه كان يقوله ثلاث مرات.','8','291',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3463,'<span data-type="title" id=toc-3470>٣٤٦٣ </span>- عن النعمان بن بشير رضى الله عنهما عن النبى صلى الله عليه وسلم قال: " الدعاء هو العبادة "
** د ت
(رياض الصالحين ٤٢٢/١)
** قال الإمام النووى فى " الرياض " ١ / ٤٢٢: قال الترمذى: حديث حسن صحيح.','8','292',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3464,'<span data-type="title" id=toc-3471>٣٤٦٤ </span>- عن عائشة رضى الله عنها قالت: كان رسول الله صلى الله عليه وسلم يستحب الجوامع من الدعاء، ويدع ما سوى ذلك.
** د
(رياض الصالحين ٤٢٢/١)
** إسناده جيد','8','293',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3465,'<span data-type="title" id=toc-3472>٣٤٦٥ </span>- عن عائشة رضى الله عنها أن النبى صلى الله عليه وسلم كان يدعو بهؤلاء الكلمات: " اللهم إنى أعوذ بك من فتنة النار، وعذاب النار، ومن شر الغنى والفقر "، وهذا لفظ أبى داود.
** د ت
(رياض الصالحين ٤٢٥/١)
** قال الإمام النووى فى " الرياض " ١ / ٤٢٥: قال الترمذى: حديث حسن اه.','8','294',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3466,'<span data-type="title" id=toc-3473>٣٤٦٦ </span>- عن زياد بن علاقة عن عمه، وهو قطبة بن مالك رضى الله عنه، قال: كان النبى صلى الله عليه وسلم يقول: " اللهم إنى أعوذ بك من منكرات الأخلاق والأعمال والأهواء ".
** ت
(رياض الصالحين ٤٢٥/١)
** قال الإمام النووى فى " الرياض " ١ / ٤٢٥: قال الترمذى: حديث حسن.','8','295',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3467,'<span data-type="title" id=toc-3474>٣٤٦٧ </span>- عن شكل بن حميد رضى الله عنه قال: قلت يارسول الله علمنى دعاء قال: " قل: اللهم إنى أعوذ بك من شر سمعى، ومن شر بصرى، ومن شر لسانى ومن شر قلبى، ومن شر منيى ".
** د ت
(رياض الصالحين ٤٢٥/١)
** قال الإمام النووى فى " الرياض " ١ / ٤٢٥: قال الترمذى: حديث حسن.','8','296',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3468,'<span data-type="title" id=toc-3475>٣٤٦٨ </span>- عن أنس رضى الله عنه أن النبى صلى الله عليه وسلم كان يقول: " اللهم إنى أعوذ بك من البرص، الجنون، الجذام، وسىء الأسقام ".
** د
(رياض الصالحين ٤٢٦/١)
** إسناده صحيح','8','297',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3469,'<span data-type="title" id=toc-3476>٣٤٦٩ </span>- عن أبى هريرة رضى الله عنه قال: كان رسول الله صلى الله عليه وسلم يقول: " اللهم إنى أعوذ بك من الجوع، فإنه بئس الضجيع، وأعوذ بك من الخيانة، فإنها بئست البطانة ".
** د
(رياض الصالحين ٤٢٦/١)
** إسناده صحيح','8','298',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3470,'<span data-type="title" id=toc-3477>٣٤٧٠ </span>- عن على رضى الله عنه أن مكاتبا جاءه فقال: إنى عجزت عن كتابتى، فأعنى، قال: ألا أعلمك كلمات علمنيهن رسول الله صلى الله عليه وسلم، لو كان عليك مثل جبل أحد ديناأداه الله عنك، قل: " اللهم اكفنى بحلالك عن حرامك واغننى بفضلك عمن سواك ".
** ت
(رياض الصالحين ٤٢٦/١)
** قال الإمام النووى فى " الرياض " ١ / ٤٢٦: قال الترمذى: حديث حسن.','8','299',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3471,'<span data-type="title" id=toc-3478>٣٤٧١ </span>- عن عمران بن الحصين رضى الله عنهما أن النبى صلى الله عليه وسلم علم أباه حصينا كلمتين يدعو بهما: " اللهم ألهمنى رشدى وأعذنى من شر نفسى ".
** ت
(رياض الصالحين ٤٢٦/١)
** قال الإمام النووى فى " الرياض " ١ / ٤٢٦: قال الترمذى: حديث حسن.','8','300',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3472,'<span data-type="title" id=toc-3479>٣٤٧٢ </span>- عن أبى الفضل العباس بن عبد المطلب رضى الله عنه قال: قلت: يارسول الله علمنى شيئا أسأله الله تعالى، قال: " سلوا الله العافية "، فمكثت أياما ثم جئت فقلت: يا رسول الله، علمنى شيئا أسأله الله تعالى قال لى: " يا عباس ياعم رسول الله سلوا الله العافية فى الدنيا والأخرة ".
** ت
(رياض الصالحين ٤٢٦/١)
** قال الإمام النووى فى " الرياض " ١ / ٤٢٦: قال الترمذى: حسن صحيح.','8','301',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3473,'<span data-type="title" id=toc-3480>٣٤٧٣ </span>- عن شهر بن حوشب قال: قلت لأم سلمة، رضى الله عنها: يا أم المؤمنين ما كان أكثر دعاء رسول الله صلى الله عليه وسلم، إذا كان عندك؟ ، قالت: كان أكثر دعائه: " يامقلب القلوب ثبت قلبى على دينك ".
** ت
(رياض الصالحين ٤٢٦/١)
** قال الإمام النووى فى " الرياض " ١ / ٤٢٧: قال الترمذى: حديث حسن.','8','302',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3474,'<span data-type="title" id=toc-3481>٣٤٧٤ </span>- عن أبى الدرداء رضى الله عنه، قال: قال رسول الله صلى الله عليه وسلم: " كان من دعاء داود صلى الله عليه وسلم: اللهم إهنى أسألك حبك، وحب من يحبك والعمل الذى يبلغنى حبك، اللهم اجعلى حبك أحب إلى من نفسى، وأهلى ومن الماء البارد ".
** ت
(رياض الصالحين ٤٢٧/١)
** قال الإمام النووى فى " الرياض " ١ / ٤٢٧: قال الترمذى: حديث حسن.','8','303',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3475,'<span data-type="title" id=toc-3482>٣٤٧٥ </span>- عن أنس رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " ألظوا بيا ذا الجلال والإكرام ".
** ت ن
(رياض الصالحين ٤٢٧/١)
** قال الإمام النووى فى " الرياض " ١ / ٤٢٧: قال الحاكم: صحيح الإسناد.','8','304',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3476,'<span data-type="title" id=toc-3483>٣٤٧٦ </span>- عن أبى أمامة رضى الله عنه، قال: دعا رسول الله صلى الله عليه وسلم بدعاء كثير لم نحفظ منه شيئا، قلنا: يارسول الله دعوت بدعاء كثير لم نحفظ منه شيئا، فقال: " ألا أدلكم على ما يجمع ذلك كله؟ "، تقول: " اللهم إنى أسألك من خير ما سألك منه نبيك محمد صلى الله عليه وسلم، وأعوذ بك من شر مااستعاذ منه نبيك محمد صلى الله عليه وسلم، وأنت المستعان، وعليك البلاغ ولا حول ولا قوة إلا بالله ".
** ت
(رياض الصالحين ٤٢٧/١)
** قال الإمام النووى فى " الرياض " ١ / ٤٢٧: قال الترمذى: حديث حسن.','8','305',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3477,'<span data-type="title" id=toc-3484>٣٤٧٧ </span>- عن ابن مسعود رضى الله عنه قال: كان من دعاء رسول الله صلى الله عليه وسلم: " اللهم إنى أسألك موجبات رحمتك، وعزائم مغفرتك، والسلامة من كل إثم والغنيمة من كل بر، والفوز بالجنة، والنجاة من النار ".
** ك
(رياض الصالحين ٤٢٧/١)
** قال الإمام النووى فى " الرياض " ١ / ٤٢٧: قال الحاكم: حديث صحيح على شرط مسلم.','8','306',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3478,'<span data-type="title" id=toc-3485>٣٤٧٨ </span>- عن أسمامة بن زيد رضى الله عنهما قال: قال رسول الله صلى الله عليه وسلم: " من صنع إليه معروف، فقال لفاعله: جزاك الله خيرا فقد أبلغ فى الثناء ".
** ت
(رياض الصالحين ٤٢٨/١)
** قال الإمام النووى فى " الرياض " ١ / ٤٢٨: قال الترمذى: حديث حسن صحيح.','8','307',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3479,'<span data-type="title" id=toc-3486>٣٤٧٩ </span>- عن أبى أمامة رضى الله عنه قال: قيل: لرسول الله صلى الله عليه وسلم أى الدعاء أسمع، قال: " جوف الليل الآخر، ودبر الصلوات المكتوبات ".
** ت
(رياض الصالحين ٤٢٩/١)
** قال الإمام النووى فى " الرياض " ١ / ٤٢٩: قال الترمذى: حديث حسن.','8','308',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3480,'<span data-type="title" id=toc-3487>٣٤٨٠ </span>- عن عبادة بن الصامت رضى الله عنه أن رسول الله صلى الله عليه وسلم قال: " ما على الأرض مسلم يدعو الله تعالى بدعوة إلا آتاه الله إياها، أو أو صرف بها عنه من السوء مثلها مالم يدع بإثم، أو قطيعة رحم "، فقال رجل من القوم: إذا نكثر، قال: " الله أكثر "، وفى رواية الحاكم زاد " أو يدخر له من الأجر مثلها ".
** ت ك
(رياض الصالحين ٤٢٩/١)
** قال الإمام النووى فى " الرياض " ١ / ٤٢٩: حديث حسن صحيح.','8','309',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3481,'<span data-type="title" id=toc-3488>٣٤٨١ </span>- عن أبى عبد الرحمن بلال بن الحارث المزنى رضى الله عنه، أن رسول الله صلى الله عليه وسلم قال: " إن الرجل ليتكلم بالكلمة من رضوان الله تعالى ماكان يظن أن تبلغ ما بلغت يكتب الله له بها رضوانه إلى يوم يلقاه، وإن الرجل ليتكلم بالكلمة من سخط الله ماكان يظن أن تبلغ ما بلغت يكتب الله له بها سخطه إلى يوم يلقاه ".
** ما ت
(رياض الصالحين ٤٣٧/١)
** قال الإمام النووى فى " الرياض " ١ / ٤٣٧: قال الترمذى: حديث حسن صحيح.','8','310',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3482,'<span data-type="title" id=toc-3489>٣٤٨٢ </span>- عن سفيان بن عبد الله رضى الله عنه قال: قلت يا رسول الله حدثنى بأمر أعتصم به قال: " قل ربى الله، ثم استقم "، قلت: يا رسول الله ما أخوف ما تخاف على؟ ، فأخذ بلسان نفسه، ثم قال: " هذا ".
** ت
(رياض الصالحين ٤٣٧/١)
** قال الإمام النووى فى " الرياض " ١ / ٤٣٧: قال الترمذى: حديث حسن صحيح.','8','311',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3483,'<span data-type="title" id=toc-3490>٣٤٨٣ </span>- عن أبى هريرة رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " من وقاه الله شر مابين لحييه، وشر ما بين رجليه دخل الجنة ".
** ت
(رياض الصالحين ٤٣٧/١)
** قال الإمام النووى فى " الرياض " ١ / ٤٣٧: قال الترمذى: حديث حسن.','8','312',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3484,'<span data-type="title" id=toc-3491>٣٤٨٤ </span>- عن عقبة بن عامر رضى الله عنه قال: قلت: يارسول الله ما النجاة؟ ، قال: " أمسك عليك لسانك، وليسعك بيتك، وابك على خطيئتك ".
** ت
(رياض الصالحين ٤٣٧/١)
** قال الإمام النووى فى " الرياض " ١ / ٤٣٧: قال الترمذى: حديث حسن.','8','313',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3485,'<span data-type="title" id=toc-3492>٣٤٨٥ </span>- عن معاذ رضى الله عنه، قال: قلت: يارسول الله أخبرنى بعمل يدخلنى الجنة ويباعدنى من النار؟ ، قال: " لقد سألت عن عظيم وإنه ليسير على من يسره الله تعالى عليه، تعبد الله لا تشرك به شيئا، وتقيم الصلاة، وتؤتى الزكاة، وتصوم رمضان، وتحج البيت "، ثم قال: " ألا أدلك على أبواب الخير؟ ، الصوم جنة، والصدقة تطفىء الخطيئة كما يطفىء الماء النار وصلاة الرجل من جوف الليل "، ثم تلا: " تتجافى جنوبهم عن المضاجع " حتى بلغ: " يعملون "، (السجدة: ١٦) ، ثم قال: " ألا أخبرك برأس الأمر وعموده وذروة سنامه "، قلت: بلى يارسول الله، قال: " رأس الأمر الإسلام وعموده الصلاة وذروة سنامه الجهاد "، ثم قال: " ألا أخبرك بملاك ذلك كله؟ "، قلت: بلى يا رسول الله، فأخذ بلسانه قال: " كف عليك هذا "، قلت: يا رسول الله وإنا لمؤاخذون بما نتكلم به؟ ، فقال: " ثكلتك أمك، وهل يكب الناس فى النار على وجوههم إلا حصائد ألسنتهم ".
** ت
(رياض الصالحين ٤٣٨/١)
** قال الإمام النووى فى " الرياض " ١ / ٤٣٨: قال الترمذى: حديث حسن صحيح.','8','314',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3486,'<span data-type="title" id=toc-3493>٣٤٨٦ </span>- عن عائشة رضى الله عنها قالت: قلت للنبى صلى الله عليه وسلم: حسبك من صفية كذا وكذا. قال بعض الرواة: تعنى قصيرة، فقال: " لقد قلت كلمة لو مزجت بماء البحر لمزجته "، قالت: وحكيت له إنسانا فقال: " ما أحب أنى حكيت إنسانا وإن لى كذا وكذا ".
** د ت
(رياض الصالحين ٤٣٩/١)
** قال الإمام النووى فى " الرياض " ١ / ٤٣٩: قال الترمذى: حديث حسن صحيح.','8','315',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3487,'<span data-type="title" id=toc-3494>٣٤٨٧ </span>- عن أبى الدرداء رضى الله عنه عن النبى صلى الله عليه وسلم قال: " من رد عن عرض أخيه رد الله عن وجهه النار يوم القيامة ".
** ت
(رياض الصالحين ٤٤٠/١)
** قال الإمام النووى فى " الرياض " ١ / ٤٤٠: قال الترمذى: حديث حسن.','8','316',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3488,'<span data-type="title" id=toc-3495>٣٤٨٨ </span>- عن سمرة بن جندب رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " لا تلاعنوا بلعنة الله، ولا بغضبه ولا بالنار ".
** د ت
(رياض الصالحين ٤٥٢/١)
** قال الإمام النووى فى " الرياض " ١ / ٤٥٢: قال أبو داود والترمذى: حديث حسن صحيح.','8','317',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3489,'<span data-type="title" id=toc-3496>٣٤٨٩ </span>- عن ابن مسعود رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " ليس المؤمن بالطعان، ولا اللعان، ولا الفاحش، ولا البذى ".
** ت
(رياض الصالحين ٤٥٢/١)
** قال الإمام النووى فى " الرياض " ١ / ٤٥٢: قال الترمذى: حديث حسن.','8','318',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3490,'<span data-type="title" id=toc-3497>٣٤٩٠ </span>- عن معاوية رضى الله عنه قال: سمعت رسول الله صلى الله عليه وسلم يقول: " إنك إن اتبعت عورات المسلمين أفسدتهم، أو كدت أن تفسدهم ".
** د
(رياض الصالحين ٤٥٨/١)
** صحيح
** قال الإمام النووى فى " الرياض " ١ / ٤٥٨: رواه أبو داود بإسناد صحيح.','8','319',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3491,'<span data-type="title" id=toc-3498>٣٤٩١ </span>- عن ابن مسعود رضى الله عنه أنه أتى برجل فقيل له: هذا فلان تقطر لحيته حمرا، فقال: إنا قد نهينا عن التجسس، ولكن إن يظهر لنا شىء نأخذ به.
** د
(رياض الصالحين ٤٥٨/١)
** حسن صحيح
** قال الإمام النووى فى " الرياض " ١ / ٤٥٨: رواه أبو داود بإسناد على شرط البخارى ومسلم.','8','320',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3492,'<span data-type="title" id=toc-3499>٣٤٩٢ </span>- عن واثلة بن الأسقع رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " لا تظهر الشماتة لأخيك فيرحمه الله ويبتليك ".
** ت
(رياض الصالحين ٤٦٠/١)
** قال الإمام النووى فى " الرياض " ١ / ٤٦٠: قال الترمذى: حديث حسن.','8','113',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3493,'<span data-type="title" id=toc-3500>٣٤٩٣ </span>- عن أبى هريرة رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " لا يحل لمسلم أن يهجر أخاه فوق ثلاث، فمن هجر فوق ثلاث، فمات دخل النار ".
** د
(رياض الصالحين ٤٦٤/١)
** إسناده على شرط البخارى ومسلم','8','114',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3494,'<span data-type="title" id=toc-3501>٣٤٩٤ </span>- عن أبى خراش حدرد بن أبى حدرد الأسلمى رضى الله عنه أنه سمع النبى صلى الله عليه وسلم يقول: " من هجر أخاه سنة فهو كسفك الدماء ".
** د
(رياض الصالحين ٤٦٤/١)
** إسناده صحيح','8','115',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3495,'<span data-type="title" id=toc-3502>٣٤٩٥ </span>- عن أبى هريرة رضى الله عنه أن رسول الله صلى الله عليه وسلم قال: " لا يحل لمؤمن أن يهجر مؤمنا فوق ثلاث، فإن مرت به ثلاث، فليلقه فليسلم عليه، فإن رد عليه السلام فقد اشتركا فى الأجر، وإن لم يرد عليه فقد باء بالإثم، وخرج المسلم من الهجرة ".
** د
(رياض الصالحين ٤٦٤/١)
** إسناده حسن','8','116',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3496,'<span data-type="title" id=toc-3503>٣٤٩٦ </span>- عن ابن مسعود رضى الله عنه قال: كنا مع رسول الله صلى الله عليه وسلم فى سمع فانطلق لحاجته فرأينا حمرة معها قرطان، فأخذنا فرخيها فجاءت الحمرة فجعلت تعرش فجاء النبى صلى الله عليه وسلم فقال: " من فجع هذه بولدها؟ ردوا ولدها إليها "، ورأى قرية نمل حرقناها، فقال: " من خرق هذه؟ "، قلنا: نحن، قال: " إنه لاينبغى أن يعذب بالنار إلا رب النار ".
** د
(رياض الصالحين ٤٦٨/١)
** إسناده صحيح','8','117',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3497,'<span data-type="title" id=toc-3504>٣٤٩٧ </span>- عن أبى هريرة رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " من تعلم علما يبتغى به وجه الله عز وجل لا يتعلمه إلا ليصيب به عرضا من الدنيا، لم يجد عرف الجنة يوم القيامة ".
** د
(رياض الصالحين ٤٧٢/١)
** إسناده صحيح','8','118',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3498,'<span data-type="title" id=toc-3505>٣٤٩٨ </span>- عن أم سلمة رضى الله عنها قالت: كنت عند رسول الله صلى الله عليه وسلم وعنده ميمونة، فأقبل ابن أم مكتوم، وذلك بعد أن أمرنا بالحجاب فقال النبى صلى الله عليه وسلم: " احتجبا منه "، فقلنا: يارسول الله أليس هو أعمى: لا يبصرنا، ولا يعرفنا؟ ، فقال النبى صلى الله عليه وسلم: " أفعميا وإن أنتما ألستما تبصرانه ".
** د ت
(رياض الصالحين ٤٧٤/١)
** قال الإمام النووى فى " الرياض " ١ / ٤٧٤: قال الترمذى: حديث حسن صحيح.','8','119',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3499,'<span data-type="title" id=toc-3506>٣٤٩٩ </span>- عن أبى هريرة رضى الله عنه قال: لعن رسول الله صلى الله عليه وسلم الرجل يلبس لبسة المرأة، المرأة تلبس لبسة الرجل ".
** د
(رياض الصالحين ٤٧٥/١)
** إسناده صحيح','8','120',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3500,'<span data-type="title" id=toc-3507><span data-type="title" id=toc-3508>٣٥٠٠ </span></span>- عن عمرو بن شعيب عن أبيه عن جده رضى الله عنه عن النبى صلى الله عليه وسلم: " لا تنتفو الشيب فإنه نور المسلم يوم القيامة ".
** د ت ن
(رياض الصالحين ٤٧٩/١)
** حسن
** قال الإمام النووى فى " الرياض " ١ / ٤٧٩: رواه أبو داود والترمذى والنسائى بأسانيد حسنة. قال الترمذى: هو حديث حسن.','8','121',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3501,'<span data-type="title" id=toc-3509>٣٥٠١ </span>- عن جابر رضى الله عنه أن رسول الله صلى الله عليه وسلم نهى أن ينتعل الرجل قائما.
** د
(رياض الصالحين ٤٨٠/١)
** إسناده حسن','8','122',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3502,'<span data-type="title" id=toc-3510>٣٥٠٢ </span>- عن أسيد بن أبى أسيد التابعى، عن امرأة من المبايعات قالت: كان فيما أخذ علينا رسول الله صلى الله عليه وسلم فى المعروف الذى أخذ علينا أن لا نعصيه فيه: أن لانخمش وجها، ولا ندعو ويلا، ولا نشق جيبا، وأن لا ننثر شطرا.
** د
(رياض الصالحين ٤٨٣/١)
** إسناده حسن','8','123',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3503,'<span data-type="title" id=toc-3511>٣٥٠٣ </span>- عن أبى موسى رضى الله عنه أن رسول الله صلى الله عليه وسلم قال: " ما من ميت يموت فيقوم باكيهم فيقول: وا جبلاه، وا سيداه، أو نحو ذلك إلا وكل به ملكان يلهزانه: أهكذا كنت؟ ".
** ت
(رياض الصالحين ٤٨٣/١)
** قال الإمام النووى فى " الرياض " ١ / ٤٨٣: قال الترمذى: حديث حسن.','8','124',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3504,'<span data-type="title" id=toc-3512>٣٥٠٤ </span>- عن قبيصة بن المخارق رضى الله عنه قال: سمعت رسول الله صلى الله عليه وسلم يقول: العيافة، والطيرة، والطرق من الجبت ".
** د
(رياض الصالحين ٤٨٤/١)
** إسناده حسن','8','125',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3505,'<span data-type="title" id=toc-3513>٣٥٠٥ </span>- عن ابن عباس رضى الله عنهما قال: قال رسول الله صلى الله عليه وسلم: " من اقتبس علما من النجوم، اقتبس شعبة من السحر زاد ما زاد ".
** د
(رياض الصالحين ٤٨٤/١)
** إسناده صحيح','8','126',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3506,'<span data-type="title" id=toc-3514>٣٥٠٦ </span>- عن بريدة رضى الله عنه أن النبى صلى الله عليه وسلم كان لا يتطير.
** د
(رياض الصالحين ٤٨٥/١)
** إسناده صحيح','8','127',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3507,'<span data-type="title" id=toc-3515>٣٥٠٧ </span>- عن عروة بن عامر رضى الله عنه، قال: ذكرت الطيرة عند رسول الله صلى الله عليه وسلم فقال: " أحسنها الفأل، ولا ترد مسلما فإذا رأى أحدكم ما يكره، فليقل: اللهم لا يأتى بالحسنات إلا أنت، ولا يدفع السيئات إلا أنت، ولا حول ولا قوة إلا بك ".
** د
(رياض الصالحين ٤٨٥/١)
** صحيح
** قال الإمام النووى فى " الرياض " ١ / ٤٦٨: رواه أبو داود بإسناده صحيح.','8','128',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3508,'<span data-type="title" id=toc-3516>٣٥٠٨ </span>- عن ابن عمر رضى الله عنهما قال: نهى رسول الله صلى الله عليه وسلم عن الجلالة فى الإبل أن يركب عليها.
** د
(رياض الصالحين ٤٨٩/١)
** إسناده صحيح','8','129',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3509,'<span data-type="title" id=toc-3517>٣٥٠٩ </span>- عن أبى هريرة رضى الله عنه أن رسول الله صلى الله عليه وسلم قال: " إذا رأيتم من يبيع أو يبتاع فى المسجد، فقولوا: لا أربح الله تجارتك، وإذا رأيتم من ينشد ضالة فقولوا: لا ردها الله عليك ".
** ت
(رياض الصالحين ٤٩٠/١)
** قال الإمام النووى فى " الرياض " ١ / ٤٩٠: قال الترمذى: حديث حسن.','8','130',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3510,'<span data-type="title" id=toc-3518>٣٥١٠ </span>- عن عمرو بن شعيب عن أبيه عن جده رضى الله عنه أن رسول الله صلى الله عليه وسلم نهى عن الشراء والبيع فى المسجد، وأن تنشد فيه ضالة أو ينشد فيه شعر ".
** د ت
(رياض الصالحين ٤٩٠/١)
** قال الإمام النووى فى " الرياض " ١ / ٤٩٠: قال الترمذى: حديث حسن.','8','131',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3511,'<span data-type="title" id=toc-3519>٣٥١١ </span>- عن معاذ بن أنس الجهنى رضى الله عنه أن النبى صلى الله عليه وسلم نهى عن الحبوة يوم الجمعة والإمام يخطب.
** د ت
(رياض الصالحين ٤٩١/١)
** قال الإمام النووى فى " الرياض " ١ / ٤٩٠: قال الترمذى وأبو داود: حديث حسن.','8','132',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3512,'<span data-type="title" id=toc-3520>٣٥١٢ </span>- عن بريدة رضى الله عنه أن رسول الله صلى الله عليه وسلم قال: " من حلف بالأمانة فليس منا ".
** د
(رياض الصالحين ٤٩٣/١)
** صحيح
** قال الإمام النووى فى " الرياض " ١ / ٤٩٣: رواه أبو داود بإسناد صحيح.','8','133',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3513,'<span data-type="title" id=toc-3521>٣٥١٣ </span>- عن ابن عمر رضى الله عنهما، أنه سمع رجلا يقول: لا والكعبة، قال ابن عمر: لا تحلف بغير الله، فإنى سمعت رسول الله صلى الله عليه وسلم يقول: " من حلف بغير الله فقد كفر أو أشرك ".
** ت
(رياض الصالحين ٤٩٣/١)
** قال الإمام النووى فى " الرياض " ١ / ٤٩٣: قال الترمذى: حديث حسن.','8','134',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3514,'<span data-type="title" id=toc-3522>٣٥١٤ </span>- عن ابن عمر رضى الله عنهما، قال: قال رسول الله صلى الله عليه وسلم: " من استعاذ بالله فأعيذوه، ومن سأل بالله فأعطوه ومن دعاكم فأجيبوه ومن صنع إليكم معروفا فكافئوه، فإن لم تجدوا ما تكافئونه، فادعوا له حتى تروا أنكم قد كافأتموه ".
** د ن
(رياض الصالحين ٤٩٦/١)
** صحيح
** قال الإمام النووى فى " الرياض " ١ / ٤٩٦: رواه أبو داود والنسائى بأسانيد الصحيحين.','8','135',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3515,'<span data-type="title" id=toc-3523>٣٥١٥ </span>- عن بريدة رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " لا تقولوا للمنافق سيد، فإنه إن يك سيدا، فقد اسخطتم ربكم عز وجل ".
** د
(رياض الصالحين ٤٩٦/١)
** إسناده صحيح','8','136',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3516,'<span data-type="title" id=toc-3524>٣٥١٦ </span>- عن أبى المنذر أبى بن كعب رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " لا تسبوا الريح، فإذا رأيتم ما تكرهون فقولوا: اللهم إنا نسألك من خير هذه الريح وخير ما فيها وخير ما أمرت به، ونعوذ بك من شر هذه الريح وشر ما فيها وشر ما أمرت به ".
** ت
(رياض الصالحين ٤٩٧/١)
** قال الإمام النووى فى " الرياض " ١ / ٤٩٧: قال الترمذى: حديث حسن صحيح.','8','137',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3517,'<span data-type="title" id=toc-3525>٣٥١٧ </span>- عن أبى هريرة رضى الله عنه قال: سمعت رسول الله صلى الله عليه وسلم يقول: " الريح من روح الله، تأتى بالرحمة، وتأتى بالعذاب، فإذا رأيتموها فلا تسبوها، وسلوا اله خيرها، واستعيذوا بالله من شرها ".
** د
(رياض الصالحين ٤٩٧/١)
** إسناده حسن','8','138',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3518,'<span data-type="title" id=toc-3526>٣٥١٨ </span>- عن زيد بن خالد الجهنى رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " لا تسبوا الديك فإنه يوقظ للصلاة ".
** د
(رياض الصالحين ٤٩٧/١)
** إسناده صحيح','8','139',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3519,'<span data-type="title" id=toc-3527>٣٥١٩ </span>- عن أنس رضى الله عنه، قال: قال رسول الله صلى الله عليه وسلم: " ما كان الفحش فى شىء إلا شانه وما كان الحياء فى شىء إلا زانه ".
** ت
(رياض الصالحين ٤٩٩/١)
** قال الإمام النووى فى " الرياض " ١ / ٤٩٩: قال الترمذى: حديث حسن.','8','140',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3520,'<span data-type="title" id=toc-3528>٣٥٢٠ </span>- عن عبد الله بن عمرو بن العاص رضى الله عنهما أن رسول الله صلى الله عليه وسلم قال: " إن الله يبغض البليغ من الرجال الذى يتخلل بلسانه كما تتخلل البقرة ".
** د ت
(رياض الصالحين ٤٩٩/١)
** قال الإمام النووى فى " الرياض " ١ / ٤٩٩: قال الترمذى: حديث حسن.','8','141',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3521,'<span data-type="title" id=toc-3529>٣٥٢١ </span>- عن حذيفة بن اليمان رضى الله عنه عن النبى صلى الله عليه وسلم قال: " لا تقولوا: ما شاء الله وشاء فلان، ولكن قولوا: ما شاء الله ثم ما شاء فلان ".
** د
(رياض الصالحين ٥٠١/١)
** إسناده صحيح','8','142',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3522,'<span data-type="title" id=toc-3530>٣٥٢٢ </span>- عن أنس رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " إياك والإلتفات فى الصلاة، فإن الإلتفات فى الصلاة هلكة فإن كان لابد، ففى التطوع لا فى الفريضة ".
** ت
(رياض الصالحين ٥٠٤/١)
** قال الإمام النووى فى " الرياض " ١ / ٥٠٤: قال الترمذى: حديث حسن صحيح.','8','143',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3523,'<span data-type="title" id=toc-3531>٣٥٢٣ </span>- عن جابر رضى الله عنه قال: نهى رسول الله صلى الله عليه وسلم أن يتعاطى السيف مسلولا.
** د ت
(رياض الصالحين ٥١١/١)
** قال الإمام النووى فى " الرياض " ١ / ٥١١: قال الترمذى: حديث حسن.','8','144',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3524,'<span data-type="title" id=toc-3532>٣٥٢٤ </span>- عن أنس بن سيرين قال: كنت مع أنس بن مالك رضى الله عنه عند نفر من المجوس، فجىء بفالوذج على إناء من فضة فلم يأكله، فقيل له: حوله، فحوله فى إناء من خلنج، وجىء به فأكله ".
** هق
(رياض الصالحين ٥١٦/١)
** إسناده حسن','8','145',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3525,'<span data-type="title" id=toc-3533>٣٥٢٥ </span>- عن على رضى الله عنه قال: حفظت عن رسول الله صلى الله عليه وسلم: " لا يتم بعد احتلام، ولا صمات يوم إلى الليل ".
** د
(رياض الصالحين ٥١٧/١)
** إسناده حسن','8','146',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3526,'<span data-type="title" id=toc-3534>٣٥٢٦ </span>- عن أبى ثعلبة الخشنى جرثوم بن ناشر رضى الله عنه، عن رسول الله صلى الله عليه وسلم قال: " إن الله تعالى فرض فرائض فلا تضيعوها وحد حدودا فى تعتدوها، وحرم أشياء فلا تنتهكوها، وسكت عن أشياء رحمة لكم غير نسيان فلا تبحثوا عنها ".
** قط
(رياض الصالحين ٥٢٨/١)
** حسن','8','147',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3527,'<span data-type="title" id=toc-3535>٣٥٢٧ </span>- عن ابن عمر رضى الله عنهما قال: كنا نعد لرسول الله صلى الله عليه وسلم فى المجلس الواحد مائة مرة: رب اغفر لى وتب على إنك أنت التواب الرحيم.
** د ت
(رياض الصالحين ٥٤٢/١)
** قال الإمام النووى فى " الرياض " ١ / ٥٤٢: قال الترمذى: حديث صحيح.','8','148',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3528,'<span data-type="title" id=toc-3536>٣٥٢٨ </span>- عن ابن مسعود رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " من قال: أستغفر الله الذى لا إله إلا هو الحي القيوم وأتوب إليه غفرت ذنوبه وإن كان فر من الزحف ".
** د ت ك
(رياض الصالحين ٥٤٢/١)
** قال الإمام النووى فى " الرياض " ١ / ٥٤٣: قال الحاكم: حديث صحيح على شرط البخارى ومسلم.','8','149',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3529,'<span data-type="title" id=toc-3537>٣٥٢٩ </span>- عن أبى سعيد الخدرى قال: كنا عنده وهو متكىء فذكرنا عليا ومعاوية فتناول رجل معاوية فاستوى أبو سعيد الخدرى جالسا ثم قال: كنا ننزل رفاقا مع رسول الله صلى الله عليه وسلم فكنا فى رفقة فيها أبو بكر فنزلنا على أهل أبيات وفيهم امرأة حبلى ومعنا رجل من أهل البادية، فقال للمرأة الحامل: أيسرك أن تلدى غلاما؟ ، قالت: نعم، قال: إن أعطيتنى شاة ولدت غلاما فأعطته فسجع لها أسجاعا ثم عمد إلى الشاة فذبحها وطبخها وأكل منها ومعنا أبو بكر فلما علم بالقصة قام فتقيأ كل شىء أكل قال: ثم رأيت ذلك البدوى أتى به عمر بن الخطاب وقد هب الأنصار فقال لهم عمر: لولا أن له صحبة من رسول الله صلى الله عليه وسلم ما أدرى ما نال فيها لكفيتكموه.
** أخبار الخوارج لمحمد بن قدامة المروزى
(الإصابة في تمييز الصحابة ١٣/١)
** رجاله ثقات','8','150',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3530,'<span data-type="title" id=toc-3538>٣٥٣٠ </span>- عن جابر قال: قال رسول الله صلى الله عليه وسلم: " إن الله اختار أصحابى على الثقلين سوى النبيين والمرسلين ".
** بز
(الإصابة في تمييز الصحابة ١٤/١)
** رجاله موثقون','8','151',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3531,'<span data-type="title" id=toc-3539>٣٥٣١ </span>- عن محمد بن إبراهيم التيمى عن أبيه قال: بعثنا رسول الله صلى الله عليه وسلم فى سرية _ الحديث.
** منده
(الإصابة في تمييز الصحابة ١٩/١)
** إسناده لا بأس به
** قال الحافظ فى " الإصابة " ١ / ١٩: أخرجه ابن مندة من طريق لا بأس بها.','8','152',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3532,'<span data-type="title" id=toc-3540>٣٥٣٢ </span>- عن يحيى بن عطاء بن إبراهيم عن أبيه عن جده أنه سمع النبى صلى الله عليه وسلم يعلم الناس بمنى، يقول: " قابلوا النعال ".
** البغوى الطبرانى
(الإصابة في تمييز الصحابة ١٩/١)
** قال الحافظ فى " الإصابة " ١ / ١٩: قال البغوى: لا أعلم له غيره اه. نقل الذهبى عن ابن عبد البر أنه قال: لايصح ذكره فى الصحابة، لأن حديثه مرسل معنى فهو تابعى اه. قلت: لفظ ابن عبد البر إسناد حديثه ليس بالقائم ولا تصح صحبته عندى، وحديثه مرسل اه. فإن عنى بالإرسال انقطاعا بين أحد رواته فذاك، وإلا فقد صرح بسماعه من النبى صلى الله عليه وسلم، فهو صحابى إن ثبت إسناد حديثه لكن مداره على عبد الله بن أسلم بن هرمز وهو ضعيف وشيخه مجهول، وقد اختلف فى سياقه على أبى عاصم، فقيل هكذا، وقيل: عن يحيى بن إبراهيم بن عطاء عن أبيه عن جده، حكاه ابن أبى حاتم وعلى هذا فالصحابى عطاء ورجحها ابن السكن، وأخرجها هو وابن شاهين من طريق عمرو بن على الفلاس عن أبى عاصم ورواه البغوى أيضا عن ابن الجنيد عن ابن أبى عاصم، فقال: إبراهيم بن يحيى بن عطاء وقيل: عن يحيى بن عبد الرحمن بن عطاء، وقيل: عن يحيى بن عبيد بن عطاء، رواه الطبرانى وترجم لعطاء فى الصحابة، كذلك ابن حبان وابن أبى عاصم ومطين وآخرون، ويقوى الرواية الأولى ما حكاه أبو العباس الدغولى قال: قلت لابن أبى حاتم الرازى: هل فى الصحابة أحد أسمه إبراهيم؟ ، قال: نعم، إبراهيم اسم قديم يسمى به رجل سمع من النبى صلى الله عليه وسلم، رواه المكيون عن عطاء بن إبراهيم عن أبيه والله أعلم.','8','153',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3533,'<span data-type="title" id=toc-3541>٣٥٣٣ </span>- عن جابر أن النبى صلى الله عليه وسلم كان يخطب على جذع فذكر الحديث فى اتخاذ المنبر، وفيه: فدعا رجلا، فقال: " مااسمك؟ "، قال: إبراهيم، قال: " خذ فى صنعته ".
** طس
(الإصابة في تمييز الصحابة ٢٠/١)
** قال الحافظ فى " الإصابة " ١ / ٢٠: فيه العلاء بن مسلمة الرواس وقد كذبوه.','8','154',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3534,'<span data-type="title" id=toc-3542>٣٥٣٤ </span>- عن إسماعيل بن إبراهيم الأشهلى عن أبيه قال: خرج النبى صلى الله عليه وسلم إلى بنى سلمة.
** ابن منده
(الإصابة في تمييز الصحابة ٢٠/١)
** قال الحافظ فى " الإصابة " ١ / ٢٠: قال ابن منده: إنه وهم، وقال أبو نعيم: هو وهم اه. ولم يبينا وجه الوهم فيه.','8','155',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3535,'<span data-type="title" id=toc-3543>٣٥٣٥ </span>- عن علقمة بن عبد الرحمن بن أبزى عن أبيه عن جده عن النبى صلى الله عليه وسلم أنه خطب الناس فأثنى على طوائف من المسلمين خيرا ثم قال: " ما بال أقوام لا يتعلمون من جيرانهم ولا يتفقهون "، الحديث.
** سحق ابن منده
(الإصابة في تمييز الصحابة ٢٢/١)
** إسناد صالح
** قال الحافظ فى " الإصابة " ١ / ٢٢: قال ابن السكن: ولا يروى إلا بهذا الإسناد وقال ابن منده: لا تصلح له صحبة ولا رؤية ثم أخرج حديثه عن ابن السكن واستغربه اه. رواه محمد بن إسحاق بن راهوية عن أبيه فقال فى إسناده: عن علقمة بن سعيد بن عبد الرحمن عن أبيه عن جده أورده الطبرانى فى ترجمة عبد الرحمن بن أبزى ورجح أبو نعيم هذه الرواية وقال: لا يصح لأبزى رواية ولا رؤية، واتصوب ابن الأثير كلامه اه. وكلام ابن السكن يرد عليه، والعمدة فى ذلك على البخارى فإليه المنتهى فى ذلك ورواية محمد بن إسحاق بن راهوية شاذة لأن علقمة أخو سعيد لا ابنه.','8','156',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3536,'<span data-type="title" id=toc-3544>٣٥٣٦ </span>- عن سهل بن سعد كان رجل يسمى أسود فسماه النبى صلى الله عليه وسلم أبيض.
** لم
(الإصابة في تمييز الصحابة ٢٣/١)
** قال الحافظ فى " الإصابة " ١ / ٢٣ _ ٢٤: قال الطبرانى: تفرد به ابن لهيعة.','8','157',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3537,'<span data-type="title" id=toc-3545>٣٥٣٧ </span>- حديث أبى بن عمارة أن النبى صلى الله عليه وسلم صلى فى بيته فسأله عن المسح على الخفين.
** د جه ك
(الإصابة في تمييز الصحابة ٢٥/١)
** إسناده ضعيف
** قال الحافظ فى " الإصابة " ١ / ٢٥: وذكر أبو حاتم أنه أخطأ والصواب أبو أبى بن أم حرام فالله أعلم وحكى البغوى: أنه أبى بن عبادة، وقال ابن حبان: صلى القبلتين غير أنى لست أعتمد على إسناد خبره.','8','158',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3538,'<span data-type="title" id=toc-3546>٣٥٣٨ </span>- عن أبى سعيد الخدرى أن رجلا من المسلمين قال: يا رسول الله أرأيت هذه الأمراض التى تصيبنا وما لنا فيها، قال: " كفارات "، فقال أبى بن كعب: يارسول الله وإن قلت؟ ، قال: " وإن شوكة فما فوقها فربما أبى أن لا يفارقه الوعك حتى يموت، وأن لا يشغل عن حج ولا عمرة، ولا جهاد ولا صلاة مكتوبة فى جماعة "، قال: فما مس إنسان جسده إلا وجد حره حتى مات.
** حم يع ابن أبى الدنيا
(الإصابة في تمييز الصحابة ٢٧/١)
** قال الحافظ فى " الإصابة " ١ / ٢٧: صححه ابن حبان ورواه الطبرانى من حديث أبى بن كعب بمعناه، وإسناده حسن.','8','159',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3539,'<span data-type="title" id=toc-3547>٣٥٣٩ </span>- عن أثوب مرفوعا: " الديك الأبيض خليلى ".
** قانع
(الإصابة في تمييز الصحابة ٢٩/١)
** قال الحافظ فى " الإصابة " ١ / ٢٩: ذكره الدارقطنى فى المؤتلف وقال: لايصح إسناده.','8','160',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3540,'<span data-type="title" id=toc-3548>٣٥٤٠ </span>- عن أحمر بن سواء السدوسى أنه كان له صنم يعبده فعمد إليه فألقاه فى بئر ثم أتى النبى صلى الله عليه وسلم فبايعه.
** ابن منده
(الإصابة في تمييز الصحابة ٣١/١)
** غريب
** قال الحافظ فى " الإصابة " ١ / ٣١: العلاء كوفى يجمع حديثه.','8','161',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3541,'<span data-type="title" id=toc-3549>٣٥٤١ </span>- عن شعبل بن أحمر: أن أحمر وفد إلى النبى صلى الله عليه وسلم، وكان وافد بنى تميم فكتب له النبى صلى الله عليه وسلم كتابا ولا بنه شعبل.
** ابن السكن أبو نعيم البغوى الطبرى
(الإصابة في تمييز الصحابة ٣١/١)
** قال الحافظ فى " الإصابة " ١ / ٣١: قال ابن السكن: إسناده مجهول، وقال أبو نعيم: غريب لا يعرف إلا من هذا الوجه.
** تعقيب: * انظر لفظ كتاب النبى صلى الله عليه وسلم فى (جامع المسانيد والسنن ج١ ص ١٨٦) (١٦٥) ، وقال ابن كثير: وفيه انقطاع ___ الخ ماساقه ابن كثير.','8','162',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3542,'<span data-type="title" id=toc-3550>٣٥٤٢ </span>- عن الأخضر بن أبى الأخضر عن النبى صلى الله عليه وسلم قال: " أنا أقاتل على تنزيل القرآن، وعلى يقاتل على تأويله ".
** ابن السكن
(الإصابة في تمييز الصحابة ٣٥/١)
** قال الحافظ فى " الإصابة " ١ / ٣٥: قال ابن السكن: فى إسناد حديثه نظر اه. وأشار الدارقطنى إلى أن جابر تفرد به وجابر رافض.','8','163',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3543,'<span data-type="title" id=toc-3551>٣٥٤٣ </span>- عن سعيد بن المسيب أن أبا سفيان وأبا جهل والأخنس اجتمعوا ليلا يسمعون القرآن سرا ... فذكر القصة، وفيها أن الأخنس أتى أبا سفيان، فقال: ما تقول، قال: أعرف وأنكر، قال أبو سفيان: فما تقول أنت؟ ، قال: أراه الحق.
** الذهبى فى الزهريات
(الإصابة في تمييز الصحابة ٣٦/١)
** إسناده صحيح','8','164',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3544,'<span data-type="title" id=toc-3552>٣٥٤٤ </span>- عن الأدرع قال: جئت ليلة أحرس النبى صلى الله عليه وسلم فإذا رجل ميت فخرج النبى صلى الله عليه وسلم فقيل: هذا عبد الله ذو البجادين، الحديث.
** جه
(الإصابة في تمييز الصحابة ٣٧/١)
** قال الحافظ فى " الإصابة " ١ / ٣٧: قال ابن منده غريب لا نعرفه إلا من هذا الوجه اه. فيه موسى بن عبيدة الربذى وهو ضعيف.','8','165',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3545,'<span data-type="title" id=toc-3553>٣٥٤٥ </span>- عن عبد الرحمن بن أذينه عن أبيه أن النبى صلى الله عليه وسلم قال: " من حلف على يمين فرأى غيرها خيرا منها فليأت الذى هو خير وليكفر عن يمينه ".
** طيا طب بغوى شاهين سكن أبو عروبة
(الإصابة في تمييز الصحابة ٣٨/١)
** قال الحافظ فى " الإصابة " ١ / ٣٨: قال البغوى: لا أعلم روى أذنيه غيره ولا أعلم رواه عن أبى إسحاق غير أبى الأحوص اه. وقال ابن السكن: يقال له صحبة ولا أعلم روى حديثه المرفوع غير أبى الأحوص وهو ثقة غير أنه لم يذكر فيه سماعه من النبى صلى الله عليه وسلم وأخرجه الترمذى فى العلل المنفرد عن قتيبة عن أبى الأحوص.','8','166',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3546,'<span data-type="title" id=toc-3554>٣٥٤٦ </span>- عن عثمان بن الأرقم بن أبى الأرقم عن أبيه، وكان من أصحاب النبى صلى الله عليه وسلم قال: " إن الذى يتخطى رقاب الناس يوم الجمعة ويفرق بين الإثنين بعد خروج الإمام كالجار قصيه فى النار ".
** حم ك
(الإصابة في تمييز الصحابة ٤١/١)
** قال الحافظ فى " الإصابة " ١ / ٤١: قال الدارقطنى فى الإفراد: تفرد به هشام بن زياد وهو أبو المقدام وقد ضعفوه.','8','167',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3547,'<span data-type="title" id=toc-3555>٣٥٤٧ </span>- عن عبد الرحمن بن أزهر عن أبيه أن رسول الله صلى الله عليه وسلم أتى بشارب وهو بحنين........ الحديث.
** طب د ن
(الإصابة في تمييز الصحابة ٤٣/١)
** قال الحافظ فى " الإصابة " ١ / ٤٣: وهذا وهم من الطبرانى أو من شيخه، فقد أخرجه أبو داود والنسائى عن ابن السرح بهذا الإسناد، عن الزهرى عن عبد الله بن عبد الرحمن بن أزهر عن أبيه فالحديث من مسند عبد الرحمن بن أزهر لا من مسند أزهر، وهكذا رواه صالح بن كيسان عن الزهرى عن عبد الرحمن بن أزهر نفسه، لم يقل عن أبيه وكذا رواه أبو سلمة بن عبد الرحمن ومحمد بن إبراهيم التيمى عن عبد الرحمن بن أزهر نفسه والله أعلم.','8','168',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3548,'<span data-type="title" id=toc-3556>٣٥٤٨ </span>- عن ابن عباس قال: اقتربت أنا ومحمد بن الحنفية من السقاية فشهد طلحة وعامر بن ربيعة وأزهر بن عبد عوف ومخرمة بن نوفل أن النبى صلى الله عليه وسلم دفعها إلى العباس يوم الفتح.
** بغوى
(الإصابة في تمييز الصحابة ٤٣/١)
** قال الحافظ فى " الإصابة " ١ / ٤٣: فى إسناده الواقدى.','8','169',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3549,'<span data-type="title" id=toc-3557>٣٥٤٩ </span>- عن أزهر بن منقر قال: رأيت النبى صلى الله عليه وسلم وصليت خلفه فسمعته يفتتح القراءة بالحمد لله ويسلم تسليمتين.
** منده
(الإصابة في تمييز الصحابة ٤٤/١)
** قال الحافظ فى " الإصابة " ١ / ٤٤: قال ابن مندة: غريب لا يعرف إلا من هذا الوجه اه. وفى إسناده على بن قرين، وقد كذبه ابن معين وموسى بن هارون وغيرهما.','8','170',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3550,'<span data-type="title" id=toc-3558>٣٥٥٠ </span>- عن ظهير بن رافع قال: ياابن أخى لقد نهانا رسول الله صلى الله عليه وسلم أن نكرى مما قلنا، قال: فسمعه رجل من بنى سليم يقال له: إساف بن انمار فشمت بنا، فقال: سعرا، فأجابه شاعرنا إساف بن نهيك أو نهيك بن اساف.
** منده الياوردى
(الإصابة في تمييز الصحابة ٤٤/١)
** قال الحافظ فى " الإصابة " ١ / ٤٤: قال ابن منده: غريب لانعرفه إلا من هذا الوجه اه.','8','171',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3551,'<span data-type="title" id=toc-3559>٣٥٥١ </span>- عن إسحاق صاحب النبى صلى الله عليه وسلم أن النبى صلى الله عليه وسلم نهى عن فتح التمرة وقشر الرطبة.
** أبو موسى عبدان
(الإصابة في تمييز الصحابة ٤٧/١)
** إسناده ضعيف
** قال الحافظ فى " الإصابة " ١ / ٤٧: فى إسناده ضعيف وانقطاع.','8','172',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3552,'<span data-type="title" id=toc-3560>٣٥٥٢ </span>- عن أسد بن أخى خديجة، عن النبى صلى الله عليه وسلم، قال: " لا تبع ما ليس عندك ".
** عق
(الإصابة في تمييز الصحابة ٤٨/١)
** قال الحافظ فى " الإصابة " ١ / ٤٨: ذكره العقيلى وقال: فى إسناده مقال اه.','8','173',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3553,'<span data-type="title" id=toc-3561>٣٥٥٣ </span>- عن أسد بن كرز قال: قال لى رسول الله صلى الله عليه وسلم: " يا أسد بن كرز لا تدخل الجنة بعمل ولكن برحمة الله ".
** الطبرى تخ سكن
(الإصابة في تمييز الصحابة ٤٩/١)
** إسناده حسن','8','174',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3554,'<span data-type="title" id=toc-3562>٣٥٥٤ </span>- عن أسد بن كرز سمع النبى صلى الله عليه وسلم يقول: " المريض تحات خطاياه ".
** عم يع بغوى
(الإصابة في تمييز الصحابة ٤٩/١)
** منقطع
** قال الحافظ فى " الإصابة " ١ / ٤٩: الحديث فيهانقطاع بين خالد وأسد.','8','175',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3555,'<span data-type="title" id=toc-3563>٣٥٥٥ </span>- عن قتادة بن النعمان قال: أهدى أسد بن كرز إلى رسول الله صلى الله عليه وسلم قوسا............ الحديث.
** منده
(الإصابة في تمييز الصحابة ٤٩/١)
** منقطع
** قال الحافظ فى " الإصابة " ١ / ٤٩: الحديث فيه انقطاع ين عاصم وقتادة.','8','176',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3556,'<span data-type="title" id=toc-3564>٣٥٥٦ </span>- عن أسعد بن عبد الله بن مالك قال: قال رسول الله صلى الله عليه وسلم: " أحب الدين إلى الله الحنيفة السمحة ".
** ك فى التاريخ غرائب أبى النرس أبو موسى ابن الأثير كر
(الإصابة في تمييز الصحابة ٥٢/١)
** قال الحافظ فى " الإصابة " ١ / ٥٢: ورويناه فى الغرائب لأبى النرسى وقد ذكره أبو موسى فى الذيل ومن طريقه ابن الأثير فأسقطا من بين الحاكم وجعفر وهو وهم فاحش، وقد أخرجه ابن عساكر فى ترجمة سليمان بن كثير الخزاعى وهو الصواب.','8','177',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3557,'<span data-type="title" id=toc-3565>٣٥٥٧ </span>- عن أسمر بن مضرس قال: أتيت النبى صلى الله عليه وسلم فبايعته فقال: " من سبق إلى ما لم يسبق إليه مسلم فهو له ".
** د
(الإصابة في تمييز الصحابة ٦٢/١)
** إسناده حسن','8','178',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3558,'<span data-type="title" id=toc-3566>٣٥٥٨ </span>- عن أسود بن أحدم المحاربى، أنه قدم بإبل له سمان إلى المدينة فى زمن محل فأتى بها النبى صلى الله عليه وسلم فقال له: " ما أردت بها؟ "، قال: خادما، فقال: " من عنده خادم "، فقال عثمان: عندى فأتاه بها فلما رآها، قال: مثلها أريد، قال: فخذها فقبض رسول الله صلى الله عليه وسلم ايله، فقال: يارسول الله أوصنى، قال: " لا تقل بلسانك إلا معروفا، ولا تبسط يدك إلا إلى خير ".
** طب بغوى سكن تخ ابن أبى الدنيا فى الصمت
(الإصابة في تمييز الصحابة ٦٣/١)
** قال الحافظ فى " الإصابة " ١ / ٦٣: قال البغوى: لا أعلم له غيره، ولم يحدث به غير أبى عبد الرحيم عن عبد الوهاب اه. قال الحافظ فى " الإصابة " ١ / ٦٣: قال البخارى: فى إسناده نظر.','8','179',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3559,'<span data-type="title" id=toc-3567>٣٥٥٩ </span>- عن الأسود بن النجترى بن خويلد أنه قال: يا رسول الله أعظم لأجرى أن أستغنى عن قوى؟ .
** تخ
(الإصابة في تمييز الصحابة ٦٤/١)
** رجاله ثقات
** قال الحافظ فى " الإصابة " ١ / ٦٤: رجاله ثقات مع إرساله.','8','180',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3560,'<span data-type="title" id=toc-3568>٣٥٦٠ </span>- عن أبى جميل عباد بن هشام وكان مؤذنا فى بحكب _ قرية من قرى نجارى _ قال: رأيت رجلا من أصحاب النبى صلى الله عليه وسلم يقال له: الأسود بن حازم بن صفوان وكنت آتيه مع أبى وأنا يومئذ ابن ست أو سبع سنين، فقال: شهدت غزوة الحديبية مع النبى صلى الله عليه وسلم وأنا ابن ثلاثين سنة.
** منده
(الإصابة في تمييز الصحابة ٦٤/١)
** إسناده ضعيف جدا','8','181',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3561,'<span data-type="title" id=toc-3569>٣٥٦١ </span>- عن إسماعيل بن النضر بن الأسود بن خطامة، من بنى كنانة عن أبيه عن جده، قال: خرج زهير بن خطامة وافدا حتى قدم على رسول الله صلى الله عليه وسلم فأسلم ثم، قال: إن لنا حمى فى الجاهلية فاحمه لنا، ثم ذكر إسلام الأسود بطوله.
** منده
(الإصابة في تمييز الصحابة ٦٥/١)
** إسناده مجهول','8','182',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3562,'<span data-type="title" id=toc-3570>٣٥٦٢ </span>- عن الأسود بن ربيعة بن الأسود البشكرى أن النبى صلى الله عليه وسلم لما فتح مكة قام خطيبا، فقال: " ألا إن دماء الجاهلية وغيرها تحت قدمى إلا السقاية والسدانة ".
** منده
(الإصابة في تمييز الصحابة ٦٧/١)
** إسناده مجهول','8','183',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3563,'<span data-type="title" id=toc-3571>٣٥٦٣ </span>- عن الأسود بن عمران قال: كنت رسول قومى إلى رسول الله صلى الله عليه وسلم لما دخلوا فى الإسلام ووافدهم.
** منده
(الإصابة في تمييز الصحابة ٧٠/١)
** قال الحافظ فى " الإصابة " ١ / ٧٠: قال ابن عبد البر: فى إسناده وحديثه مقال اه. وما فيه غير أبى المحجل وهو مجهول.','8','184',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3564,'<span data-type="title" id=toc-3572>٣٥٦٤ </span>- عن الأسود بن عويم قال: سألت رسول الله صلى الله عليه وسلم عن الجمع بين الحرة والأمة؟ ، فقال: " للحرة يومان وللأمة يوم ".
** منده
(الإصابة في تمييز الصحابة ٧٠/١)
** قال الحافظ فى " الإصابة " ١ / ٧٠: فى إسناده على بن قرين وقد كذبه ابن معين.','8','185',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3565,'<span data-type="title" id=toc-3573>٣٥٦٥ </span>- عن أحفاده عنه، قال: قدمت أنا وأخى الأسود على رسول الله صلى الله عليه وسلم فآمنا به وصدقناه، قال: وكان مزء والأسود قد خدما النبى صلى الله عليه وسلم وصحباه.
** منده
(الإصابة في تمييز الصحابة ٧١/١)
** قال الحافظ فى " الإصابة " ١ / ٧١: قال ابن منده: تفرد به إسحاق الرملى اه. وهم مجهولون.','8','186',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3566,'<span data-type="title" id=toc-3574>٣٥٦٦ </span>- عن الأسود بن وهب خال النبى صلى الله عليه وسلم أن رسول الله صلى الله عليه وسلم قال له: " ألا أنبئك بشىء عسى الله أن ينفعك به؟ ، " قال: بلى يا رسول الله، قال: " إن الربا أبواب،الباب منه عدل السبعين طوبا أدناها فجرة كاضطجاع الرحل مع أمه، وإن أرى الربى استطالة المرء فى عرض أخيه بغير حق ".
** منده
(الإصابة في تمييز الصحابة ٧١/١)
** قال الحافظ فى " الإصابة " ١ / ٧٢: ورواه ابن قانع فى معجمه من طريق أبى بكر بن الأعين عن عمرو بن أبى سلمة فقال: عن وهب بن الأسود خال رسول الله صلى الله عليه وسلم.. ولم يقل عن أبيه وأدخل بين صدقة وزيد الحكم الأيلى والحكم وصدقة ضعيفان.','8','187',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3567,'<span data-type="title" id=toc-3575>٣٥٦٧ </span>- عن عائشة أن الأسود بن وهب خال النبى صلى الله عليه وسلم استأذن عليه، فقال: يا خال ادخل فدخل فبسط له ردائه ... الحديث.
** منده ابن شاهين
(الإصابة في تمييز الصحابة ٧٢/١)','8','188',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3568,'<span data-type="title" id=toc-3576>٣٥٦٨ </span>- عن عبد الله بن أبى سلمة _ عن رجل من قومه _ يقال له: أسيد المزنى، قال: أتيت النبى صلى الله عليه وسلم، أريد أن أسأله وعنده رجل يسأله فأعرض عنه مرتين أو ثلاثا ثم قال: " من كان عنده أوقية ثم سأل فقد سأل الحافا ".
** سكن منده
(الإصابة في تمييز الصحابة ٧٤/١)
** قال الحافظ فى " الإصابة " ١ / ٧٤: قال ابن السكن: إسناده صالح ولم أقف على نسبه اه. وقال ابن منده: تفرد به ابن وهب.','8','189',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3569,'<span data-type="title" id=toc-3577>٣٥٦٩ </span>- عن أصبغ بن غياث: سمعت رسول الله صلى الله عليه وسلم يقول: " فيكم أيتها الأمة خلتان لم يكونا فى الأمم قبلكم ... الحديث.
** منده
(الإصابة في تمييز الصحابة ٨٢/١)
** قال الحافظ فى " الإصابة " ١ / ٨٢: جابر الجعفى أحد الضعفاء.','8','190',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3570,'<span data-type="title" id=toc-3578>٣٥٧٠ </span>- عن الأعرس بن عمرو قال: أتيت النبى صلى الله عليه وسلم بهدية فقبلها منى ودعا لنا فى مرعانا.
** منده
(الإصابة في تمييز الصحابة ٨٥/١)
** قال الحافظ فى " الإصابة " ١ / ٨٥: عبد الرحمن بن عمرو بن جيلة أحد المتروكين، وقال ابن منده: تفرد به ابن جيلة اه.','8','191',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3571,'<span data-type="title" id=toc-3579>٣٥٧١ </span>- عن الأعور بن بشامة ووردان بن مخرمة وربيعة بن رفيع العنبريين أنهم أتوا النبى صلى الله عليه وسلم وهو فى حجرته نائم إذا جاء عيينه بن حصن بسبى بنى العنبر، فقلنا: ما لنا يا رسول الله شيئا، وقد جئنا مسلمين؟ ، قال: " احلفوا أنكم جئتم مسلمين "، قال: فسكت أناو وردان وحلف ابن ربيعة ... الحديث.
** ابن عبدان فى الصحابة
(الإصابة في تمييز الصحابة ٨٦/١)
** قال الحافظ فى " الإصابة " ١ / ٨٦: فى إسناده من لا يعرف.','8','192',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3572,'<span data-type="title" id=toc-3580>٣٥٧٢ </span>- عن أفلح مولى رسول الله صلى الله عليه وسلم أنه قال: إن رسول الله صلى الله عليه وسلم، قال: " أخاف على أمتى من بعدى ضلالة الأهواء واتباع الشهوات "، قال: ونسيت الثالثة.
** منده الحكيم الترمذى ابن شاهين
(الإصابة في تمييز الصحابة ٩٠/١)
** قال الحافظ فى " الإصابة " ١ / ٩٠: ورواه الحكيم فى نوادره من هذا الوجه وسمى الثالثة: العجب، ورواه ابن شاهين، فسمى الثالثة: الغفلة بعد المعرفة، ومداره على يوسف بن خالد وهو السمتى، وهو متروك الحديث.','8','193',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3573,'<span data-type="title" id=toc-3581>٣٥٧٣ </span>- عن أم سلمة قالت: رأى رسول الله صلى الله عليه وسلم غلاما لنا يقال له: أفلح إذا سجد نفخ، فقال: " يا أفلح ترب وجهك ".
** ت
(الإصابة في تمييز الصحابة ٩١/١)
** قال الحافظ فى " الإصابة " ١ / ٩١: قال الترمذى: غريب وقال بعضهم عن أبى حمزة رباح، وميمون أبو حمزة ضعيف اه. وتابعه طلق بن غنام عن سعيد أبى عثمان الوراق عن أبى صالح به، وأخرج النسائى من طريق كريب عن أم سلمة، نحو هذا الحديث، فقال فيه: رأى غلاما لنا يقال له رباح، ويحتمل التعدد والله أعلم.','8','194',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3574,'<span data-type="title" id=toc-3582>٣٥٧٤ </span>- عن عبيدة بن عمرو السلمانى أن عيينة والأقرع استقطعا أبا بكر أرضا فقال لهما عمر: إنما كان النبى صلى الله عليه وسلم، يتألفكما على الإسلام فأما الأن فاجهدا جهدكما وقطع الكتاب.
** تخص يعقوب البسوبى بن سفيان سيف بن عمر فى الفتوح
(الإصابة في تمييز الصحابة ٩٢/١)
** إسناده صحيح
** قال الحافظ فى " الإصابة " ١ / ٩٢: قال ابن المدينى فى العلل: هذا منقطع لأن عبيدة لم يدرك القصة ولا روى عن عمر أنه سمعه منه قال: ولا يروى عن عمر بأحسن من هذا الإسناد، ورواه سيف بن عمر فى الفتوح مطولا.','8','195',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3575,'<span data-type="title" id=toc-3583>٣٥٧٥ </span>- عن أكتم بن أبى الجون الحزاعى قال: قلنا يا رسول الله إن فلانا جرىء فى القتال قال: " هو فى النار " الحديث.
** طب منده
(الإصابة في تمييز الصحابة ٩٦/١)
** إسناده حسن','8','196',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3576,'<span data-type="title" id=toc-3584>٣٥٧٦ </span>- عن أنس قال: قال رسول الله صلى الله عليه وسلم: " يا أكتم أعز مع غير قومك بحسن خلقك ".
** ابن أبى حاتم فى العلل العسكرى فى الأمثال بغوى منده
(الإصابة في تمييز الصحابة ٩٦/١)
** قال الحافظ فى " الإصابة " ١ / ٩٦ _ ٩٧: قال ابن أبى حاتم: سمعت أبى يقول: أبو سلمة العاملة متروك الحديث باطل اه. وأخرجه ابن منده من طريق أخرى عن أكتم نفسه، وأشار إليها ابن عبد البر والله أعلم.','8','197',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3577,'<span data-type="title" id=toc-3585>٣٥٧٧ </span>- عن أكيمة بن عبادة قال: رأيت رسول الله صلى الله عليه وسلم أكل كتفا وصلى ولم يتوضأ.
** سكن
(الإصابة في تمييز الصحابة ٩٧/١)
** إسناده مجهول
** قال الحافظ فى " الإصابة " ١ / ٩٧: قال ابن السكن: لم أسمعه إلا من ابن عقدة اه. عمر بن إبراهيم أحد المتروكين.','8','198',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3578,'<span data-type="title" id=toc-3586>٣٥٧٨ </span>- عن حمدى بن عميرة قال: كان بين امرىء القيس ورجل من حضر موت خصومة فارتفعا إلى النبى صلى الله عليه وسلم، فقال للحضرمى: " بينتك وإلا فيمينه "، فقال: يارسول الله إن حلف ذهب بأرضى، فقال: " من حلف على يمين كاذبة يقتطع بها حق أخيه، لقى الله وهو عليه غضبان "، فقال امرىء القيس: يا رسول الله فما لمن تركها وهو يعلم أنه محق؟ ، قال: " الجنة "، قال: فإنى أشهدك أنى قد تركتها ".
** ن حم بغوى
(الإصابة في تمييز الصحابة ١٠٠/١)
** إسناده صحيح','8','199',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3579,'<span data-type="title" id=toc-3587>٣٥٧٩ </span>- عن واثلة بن الأسقع قال: لعن رسول الله صلى الله عليه وسلم المخنثين وقال: " أخرجوهم من بيوتكم "، وأخرج النبى صلى الله عليه وسلم أنجشة، وأخرج عمر فلانا.
** طب
(الإصابة في تمييز الصحابة ١٠٧/١)
** إسناده لين
** تعقيب: * أخرجه الطبرانى فى الكبير (٢٠٥) ، وقال الهيثمى فى مجمع الزوائد (٨ / ١٠٤) : وفيه حماد مولى بنى أمية اه. وقال حمدى عبد المجيد السلفى ٢٢ / ٨٥: قال الأزدى متروك اه. وجناح وعنبسة ضعيفان. * وفى الباب عن ابن عباس، أخرجه الطبرانى فى الأوسط (مجمع البحرين ٢٤٥٩) .','8','200',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3580,'<span data-type="title" id=toc-3588>٣٥٨٠ </span>- عن سهل بن الحنظلية، أنهم ساروا مع النبى صلى الله عليه وسلم يوم حنين فاطنبوا السير حتى كان عشية، فحضرت صلاة الظهر..فذكر الحديث وفيه فقال رسول الله صلى الله عليه وسلم: " من يحرسنا الليلة؟ " فقال أنس بن أبى مرشد الغنوى: أنا يارسول الله، وفى آخر الحديث " هل نزلت الليلة؟ "، قال: لا إلا مصليه أو قاضى حاجة فقال: " قد أوجبت فلا عليك أن لا تعمل بعدها ".
** د ن بغوى طب منده
(الإصابة في تمييز الصحابة ١١٧/١)
** إسناده على شرط الصحيح','8','201',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3581,'<span data-type="title" id=toc-3589>٣٥٨١ </span>- عن أنيس بن الضحاك قال: قال رسول الله صلى الله عليه وسلم لأبى ذر: " يا أباذر البس الخشن الضيق حتى لا يجد العز والفخر فيك مسانما ".
** منده
(الإصابة في تمييز الصحابة ١٢١/١)
** قال الحافظ فى " الإصابة " ١ / ١٢١: قال ابن منده: غريب وفيه إرسال.','8','202',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3582,'<span data-type="title" id=toc-3590>٣٥٨٢ </span>- عن أهبان بن أوس أنه كان فى غنم له فشد الذئب على شاه منها فصاح عليه فأقعى على ذنبه، قال: فخاطبنى فقال: من لها يوم تشغل عنها.
** تخ
(الإصابة في تمييز الصحابة ١٢٥/١)
** قال الحافظ فى " الإصابة " ١ / ١٢٥: قال البخارى: إسناده ليس بالقوى اه. لأن فيه عبد الله بن عامر الأسلمى وهو ضعيف.','8','203',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3583,'<span data-type="title" id=toc-3591>٣٥٨٣ </span>- عن أوس بن الحدثان مرفوعا: " أخرجوا زكاة الفطر صاعا من طعام " الحديث.
** ابن أبى عاصم
(الإصابة في تمييز الصحابة ١٣٢/١)
** قال الحافظ فى " الإصابة " ١ / ١٣٢: عمر بن صهبان ضعيف، قال ابن منده: إنه خطأ.','8','204',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3584,'<span data-type="title" id=toc-3592>٣٥٨٤ </span>- عن أوس بن الحدثان مرفوعا: " من ترك الكذب وهو مبطل بنى له فى ربض الجنة ".
** منده
(الإصابة في تمييز الصحابة ١٣٢/١)
** قال الحافظ فى " الإصابة " ١ / ١٣٢: وقد اختلف فى إسناده على سلمة مع ضعفه.','8','205',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3585,'<span data-type="title" id=toc-3593>٣٥٨٥ </span>- عن جابر قال: كان ممن تخلف عن رسول الله صلى الله عليه وسلم فى تبوك ستة أبو لبابة، وأوس بن حدام، وثعلبة بن وديعة، وكعب بن مالك، مرارة بن الربيع، وهلال بن أمية، فجاء أبو لبابة، وأوس وثعلبة فربطوا أنفسهم بالسوارى وجاءوا بأموالهم، فقالوا: يا رسول الله خذها هذا الذى حبسنا عنك، فقال: " لا أحلهم حتى يكون قتال "، قال: فنزل القرآن: " وأخرون اعترفوا بذنوبهم " الآية.
** أبو الشيخ فى التفسير منده
(الإصابة في تمييز الصحابة ١٣٤/١)
** إسناده قوى','8','206',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3586,'<span data-type="title" id=toc-3594>٣٥٨٦ </span>- عن أوس بن خولى أن النبى صلى الله عليه وسلم قال له: " من تواضع لله رفعه الله ".
** منده
(الإصابة في تمييز الصحابة ١٣٤/١)
** قال الحافظ فى " الإصابة " ١ / ١٣٥: فى إسناده خارجة بن مصعب وهو ضعيف وفيه من لا يعرف أيضا.','8','207',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3587,'<span data-type="title" id=toc-3595>٣٥٨٧ </span>- عن ابن عباس قال: دخل أوس بن ساعدة الأنصارى على رسول الله صلى الله عليه وسلم فرأى فى وجهه الكراهة، فقال: يا رسول الله إن لى بنات وأنا أدعو عليهم بالموت فقال: " لا تدع "، الحديث.
** أبو موسى
(الإصابة في تمييز الصحابة ١٣٥/١)
** قال الحافظ فى " الإصابة " ١ / ١٣٥: إبراهيم بن حبان أحد الضعفاء المتروكين.','8','208',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3588,'<span data-type="title" id=toc-3596>٣٥٨٨ </span>- عن أنس بن مالك أن رسول الله صلى الله عليه وسلم قال: " بعثنى الله هدى ورحمة للعالمين وبعثنى لأمحو المزامير والمعازف "، فقال أوس بن سمعان: والذى بعثك بالحق لإنى لأجدها فى التوراة كذلك.
** منده
(الإصابة في تمييز الصحابة ١٣٦/١)
** قال الحافظ فى " الإصابة " ١ / ١٣٦: ابن يسار وهو أبو عقال أحد الضعفاء، قال ابن مندة: تفرد به عيسى بن أبى مريم عن إبراهيم.','8','209',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3589,'<span data-type="title" id=toc-3597>٣٥٨٩ </span>- عن ابن عباس قال: كان الرجل إذا قال لزوجته: أنت على كظهر أمى حرمت عليه، وكان أول من ظاهر فى الإسلام، رجل كان تحته بنت عم له يقال له: خويلة.
** بز منده شاهين
(الإصابة في تمييز الصحابة ١٣٧/١)
** قال الحافظ فى " الإصابة " ١ / ١٣٧: أخرجه البزار من طريق أبى حمزة الثمالى، وفيه ضعف، وقد رواه ابن شاهين وابن منده من هذا الوجه بلفظ: أول ظهار كان فى الإسلام من أوس بن الصامت كانت تحت بنت عم له، وأخرجه عبد الرزاق عن ابن عينية، عن ثابت الثمالى عن عكرمة مرسلا.','8','210',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3590,'<span data-type="title" id=toc-3598>٣٥٩٠ </span>- عن خويلة بنت مالك بن ثعلبة قالت: ظاهر منى زوجى أوس بن الصامت..الحديث.
** د
(الإصابة في تمييز الصحابة ١٣٧/١)
** إسناده حسن','8','211',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3591,'<span data-type="title" id=toc-3599>٣٥٩١ </span>- عن أوس بن عبد الله بن حجر الأسلمى أنه مر به رسول الله صلى الله عليه وسلم ومعه أبو بكر وهما متوجهان إلى المدينة بقحدوات بين الحجفة وهرشى وهما على جمل فحملهما على فحل إبله وبعث معهما غلاما يقال له مسعود فقال له: اسلك بهما حيث تعلم من محارم الطريق ولا تفارقهما.
** بغوى سكن منده طب
(الإصابة في تمييز الصحابة ١٣٨/١)
** قال الحافظ فى " الإصابة " ١ / ١٣٨: ورواه أبو العباس السراج فى تاريخه فذكره مرسلا، قال ابن عبد البر: مخرج حديثه عن ولده، وهو حديث حسن اه.','8','212',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3592,'<span data-type="title" id=toc-3600>٣٥٩٢ </span>- عن زيد بن أسلم قال: مر شأس بن قيس وكان يهوديا عظيم الكفر على نفر من الأوس، والخزرج يتحدثون، فغاظه ما رأى من تألفهم بعد العداوة فأمر شابا معه من يهود أن يجلس بينهم، فيذكرهم يوم بعاث ففعل، فتنازعوا وتشاجروا حتى وثب رجلان أوس بن قبطى من الأوس و، جبار بن صخر من الخزرج، فتقاولا وغضب الفريقان، وتواثبوا للقتال فبلغ ذلك رسول الله صلى الله عليه وسلم، فجاء حتى وعظهم، وأصلح بينهم فسمعوا وأطاعوا، فأنزل الله فى أوس وجبار ومن معهما " ياأيها الذين أمنوا إن تطيعوا فريقا من الذين أوتوا الكتاب يردوكم بعد إيمانكم كافرين "، وفى شأس بن قيس " يأهل الكتاب لم تصدون عن سبيل الله من آمن " الآية.
** ابن عبد البر أبو الشيخ فى التفسير
(الإصابة في تمييز الصحابة ١٣٩/١)
** إسناده مرسل
** قال الحافظ فى " الإصابة " ١ / ١٤٠: والحديث طويل أنا اختصرته وإسناده مرسل وفيه راو مبهم.','8','213',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3593,'<span data-type="title" id=toc-3601>٣٥٩٣ </span>- عن أوفى بن مولة قال: أتيت النبى صلى الله عليه وسلم فأقطعنى الغميم وشرط على: وأن ابن السبيل أول ريان، وأقطع ساعدة رجلا منا بئرا بالفلاة وأقطع إياس بن قتادة الجابية وهى دون اليمامة وكنا أتيناه جميعا.
** طب منده
(الإصابة في تمييز الصحابة ١٤٣/١)
** قال الحافظ فى " الإصابة " ١ / ١٤٣: قال ابن عبد البر: ليس إسناد حديثه بالقوى.','8','214',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3594,'<span data-type="title" id=toc-3602>٣٥٩٤ </span>- من طريق موسى بن جبير سمعت من حدثنى عن إياس الجهنى أنه كان يقول: قال معاذ: يا نبى الله أى الإيمان أفضل؟ ، قال: " تحب لله وتبغض لله وتعمل لسانك فى ذكر الله "، قال: وروى مصعب بن المقدام عن محمد بن إبراهيم المدنى عن أبى حازم: أنه جلس إلى إياس بن سهل الأنصارى فى مسجد بنى ساعدة، فقال لى: أقبل على أبا حازم أحدثط عن النبى صلى الله عليه وسلم.
** منده
(الإصابة في تمييز الصحابة ١٤٤/١)
** إسناده منقطع
** قال الحافظ فى " الإصابة " ١ / ١٤٤: الأسناد الأول منقطع، وفى الثانى محمد بن إبراهيم، وهو ابن أبى حميد أحد الضعفاء.','8','215',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3595,'<span data-type="title" id=toc-3603>٣٥٩٥ </span>- عن محمود بن البير قال: لما قدم أبو الحيسر أنس بن رافع مكة ومعه فتية من بنى عبد الأشهل فيهم إياس بن معاذ يلتمسون الحلف من قريش على قومهم من الخزرج سمع بهم رسول الله صلى الله عليه وسلم فأتاهم فجلس إليهم فقال لهم: " هل لكم إلى خير ممت جئتم له؟ "، قالوا: وماذاك؟ ، قال: " أنا رسول الله بعثنى إلى العباد أدعوهم إلى أن يعبدوه ولا يشركوا به شيئا " ثم ذكر لهم الإسلام وتلا عليهم القرآن، فقال إياس بن معاذ: ياقوم هذا والله خير مما جئتم له فأخذ أبو الحيسر حفنة من البطحاء فضرب وجهه بها وقال: دعنا منك فلعمرى لقد جئنا لغير هذا فسكت وقام وانصرفوا.
** ابن إسحاق فى المغازى
(الإصابة في تمييز الصحابة ١٤٦/١)
** صحيح
** قال الحافظ فى " الإصابة " ١ / ١٤٦: رواه جماعة عن ابن إسحاق هكذا وهو من صحيح حديثهما، لكن رواه زياد البكائى عن ابن إسحاق عن محمد بن عبد الرحمن بن عمرو بدل الحصين والأول أرجح أشار إلى ذلك البخارى فى تاريخه.','8','216',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3596,'<span data-type="title" id=toc-3604>٣٥٩٦ </span>- عن معاوية بن قرة عن أبيه أن النبى صلى الله عليه وسلم بعث أباه جد معاوية إلى رجل عرس بامرأة ابنه فضرب عنقه وخمس ماله.
** ن جه ابن أبى خثيمة سكن الباوردى
(الإصابة في تمييز الصحابة ١٤٧/١)
** إسناده حسن
** قال الحافظ فى " الإصابة " ١ / ١٤٧: قال ابن خثيمة عن يحيى بن معين: هذا حديث صحيح وكان ابن إدريس أسنده لقوم وأرسله لأخرين.','8','321',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3597,'<span data-type="title" id=toc-3605>٣٥٩٧ </span>- عن عبد الله بن الحارث أن أيمن وفتيه معه تعروا واجتلدوا فجعل النبى صلى الله عليه وسلم يقول: " لا من الله استحيوا ولا من رسوله استتروا " وأم أيمن تقول: يا رسول الله استغفر لهم فيأبى ما استغفر لهم.
** طب إبراهيم الحرجى
(الإصابة في تمييز الصحابة ١٥٠/١)','8','322',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3598,'<span data-type="title" id=toc-3606>٣٥٩٨ </span>- عن أنس لما ولد إبراهيم بن مارية جاريته كان يقع فى نفس النبى صلى الله عليه وسلم حتى أتاه جبريل عليه السلام فقال: السلام عليك يا أبا إبراهيم.قال الحافظ فى " الإصابة " ١ /
** منده
(الإصابة في تمييز الصحابة ١٥١/١)
** غريب
** قال الحافظ فى " الإصابة " ١ / ١٥١: هذا حديث غريب من حديث الزهرى.','8','323',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3599,'<span data-type="title" id=toc-3607>٣٥٩٩ </span>- عن عائشة قالت: لقد توفى إبراهيم بن النبى صلى الله عليه وسلم وهو ابن ثمانية عشر شهرا فلم يصل عليه ".
** بز يع حم
(الإصابة في تمييز الصحابة ١٥١/١)
** إسناده حسن
** قال الحافظ فى " الإصابة " ١ / ١٥١: صححه ابن حزم لكن قال أحمد فى رواية حنبل عنه حديث منكر، وقال الخطابى: حديث عائشة أحسن اتصالا من الراوية التى فيها أنه صلى عليه، قال: ولكن هو أولى، وقال ابن عبد البر: حديث عائشة لا يصح.','8','324',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3600,'<span data-type="title" id=toc-3608>٣٦٠٠ </span>- عن ابن عباس قال: لما مات إبراهيم بن النبى صلى الله عليه وسلم قال: " إن له مرضعا فى الجنة، فلو عاش لكان صديقا نبيا، ولو عاش لاعتقت أخواله من القبط وما أسترق قبطى.
** جه
(الإصابة في تمييز الصحابة ١٥١/١)
** قال الحافظ فى " الإصابة " ١ / ١٥١: فى سنده أبو شيبة الواسطى إبراهيم بن عثمان وهو ضعيف.','8','325',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3601,'<span data-type="title" id=toc-3609>٣٦٠١ </span>- عن أنس أن النبى صلى الله عليه وسلم صلى على ابنه إبراهيم وكبر عليه أربعا.
** سعد يع
(الإصابة في تمييز الصحابة ١٥١/١)
** قال الحافظ فى " الإصابة " ١ / ١٥١: من طريق عطاء بن عجلان وهو ضعيف. وقال الحافظ فى " الإصابة " ١ / ١٥١: وروى البزار من طريق أبى نضرة مثله، وفيه عبد الرحمن بن مالك بن معقل وهو ضعيف.','8','326',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3602,'<span data-type="title" id=toc-3610>٣٦٠٢ </span>- عن البراء قال: قد صلى رسول الله صلى الله عليه وسلم على ابنه إبراهيم ومات وهو ابن ستة عشر شهرا.
** حم شب عب
(الإصابة في تمييز الصحابة ١٥٢/١)
** قال الحافظ فى " الإصابة " ١ / ١٥٢: جابر الجعفى أحدالضعفاء، ورواه ابن أبى شيبة فى مصنفه فلم يذكر البراء وكذا عبد الرزاق.','8','327',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3603,'<span data-type="title" id=toc-3611>٣٦٠٣ </span>- عن أنس قال: لما توقى إبراهيم بن النبى صلى الله عليه وسلم وهو ابن ستة عشر شهرا فقال: " ادفنوه بالبقيع فإن له مرضعا تتم رضاعة فى الجنة ".
** منده
(الإصابة في تمييز الصحابة ١٥٢/١)
** قال الحافظ فى " الإصابة " ١ / ١٥٢: قال ابن منده: غريب لا نعرفه من حديث الثورى إلا من هذا الوجه.','8','328',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3604,'<span data-type="title" id=toc-3612>٣٦٠٤ </span>- عن أيوب بن بشير عن النبى صلى الله عليه وسلم قال: " أفضل الصدقة على ذى الرحم الكاشح ".
** ابن شاهين فى الصحابة
(الإصابة في تمييز الصحابة ١٥٩/١)
** مرسل
** قال الحافظ فى " الإصابة " ١ / ١٥٩: وهذا مرسل لا يقتضى له صحبة. وقال الحافظ فى " الإصابة " ١ / ١٥٩: وفى سند ابن شاهين المذكور من يضعف، وهذا الحديث أخرجه عبد الله بن أحمد فى زياداته، والطبرانى فى الكبير من طريق سفيان بن حسين عن الزهرى عن أيوب بن بشير عن حكيم بن حزام، فهذا أولى، مع أنه معلول لأنه اختلف فيه على أيوب بن بشير.','8','329',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3605,'<span data-type="title" id=toc-3613>٣٦٠٥ </span>- عن طارق بن شهاب يقول: خرجنا حجاجا فأوطأ رجل منا يقال له أربد بن عبد الله ظبيا، فأتينا عمر نسأله، فقال له عمر: احكم فيه، قال: أنت خير منى وأعلم، قال: إنما أمرتك أن تحكم قال: قد قلت فيه جدى قد جمع الماء والشجر: قال: ففيه ذلك.
** عب
(الإصابة في تمييز الصحابة ١٦٤/١)
** إسناده صحيح
** قال الحافظ فى " الإصابة " ١ / ١٦٤: رواه الأعمش عن سليمان بن ميسرة عن طارق ولم يسم الرجل.','8','330',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3606,'<span data-type="title" id=toc-3614>٣٦٠٦ </span>- عن إزاد مرد بن هرمز _ وكان قد أدرك الإسلام وكان من أساورة كسرى _ قال: بينا نحن على باب كسرى ننتظر الإذن فأبطأ علينا الإذن واشتد الحر وضجرنا _ فذكر القصة _ وفى آخرها فقلت: لا حول ولا قوة إلا بالله ما شاء الله كان وما لم يشأ لم يكن فلم يزل والله يحترق حتى صار رمادا.
** منده
(الإصابة في تمييز الصحابة ١٦٧/١)
** قال الحافظ فى " الإصابة " ١ / ١٦٧: قال ابن منده: غريب اه. عكرمة فيه ضعف.','8','331',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3607,'<span data-type="title" id=toc-3615>٣٦٠٧ </span>- عن أسير قال: فنادى منادى على: ياخيل الله اركبى وابشرى فصف الناس لهن فانتضى أويس سيفه حتى كسر جفنه فألقاه ثم جعل يقول: أيها الناس تموا تموا ليتمن وجوه ثم لا ينصرف حتى يرى الجنة فجعل يقول ذلك ويمشى إذ جاءته رقية فأصابت فؤاده فتردى مكانه كأنما مات منذ لحظة.
** ك
(الإصابة في تمييز الصحابة ١٩٠/١)
** إسناده صحيح','8','332',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3608,'<span data-type="title" id=toc-3616>٣٦٠٨ </span>- عن أبى مريم الحنفى أن عمر قرأ بعد الحارث فقال له أبو مريم الحنفى: إنك خرجت من الخلاء، فقال له: أمسيلمة أفتاك بهذا؟ .
** سعد تح
(الإصابة في تمييز الصحابة ١٩١/١)
** إسناده صحيح','8','333',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3609,'<span data-type="title" id=toc-3617>٣٦٠٩ </span>- عن أسد بن المقامس التركى عن النبى صلى الله عليه وسلم قال: " إن الله وملائكته يصلون على الصف الأول ".
** عمر النسفى فى تاريخ سمرقند
(الإصابة في تمييز الصحابة ١٩٦/١)
** موضوع
** قال الحافظ فى " الإصابة " ١ / ١٩٦: خبر مكذوب. قال الحافظ فى " الإصابة " ١ / ١٩٦: قال أبو سعد السمعانى: سلوا الله الثبات على الصدق فليس العجب من رواية بهرام عن الحامدى إنما العجب من رواية عمر النسفى هذا فى كتابه غير منكر عليه بل راوية من يظن أنه حديث اه. قلت: فهو فى باب رتن ومكلبة بن ملكان ونحوهما.','8','334',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3610,'<span data-type="title" id=toc-3618>٣٦١٠ </span>- عن إسماعيل بن أبى حكيم المدنى قال: سمعت رسول الله صلى الله عليه وسلم يقول: " إن الله يسمع قراءة (لم يكن) فيقول: أبشر عبدى ".
** منده
(الإصابة في تمييز الصحابة ١٩٧/١)
** قال الحافظ فى " الإصابة " ١ / ١٩٧: قال أبو نعيم: لم يذكر أحد فى الأئمة إسماعيل فى الصحابة وهو عندى إسناد منقطع اه.','8','335',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3611,'<span data-type="title" id=toc-3619>٣٦١١ </span>- عن إسماعيل بن عبد الرحمن أن رسول الله صلى الله عليه وسلم قال لعمار: " تقتلك الفئة الباغية "
** الباوردى فى الصحابة
(الإصابة في تمييز الصحابة ١٩٨/١)
** قال الحافظ فى " الإصابة " ١ / ١٩٨: إسماعيل بن عبد الرحمن الأنصارى: تابعى ذكره ابن حبان فى ثقاته. وقال الحافظ فى " الإصابة " ١ / ١٩٨: فى سنده ضرار بن صرد وهو ضعيف.','8','336',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3612,'<span data-type="title" id=toc-3620>٣٦١٢ </span>- عن أسيد بن أبى أسيد أن رسول الله صلى الله عليه وسلم تزوج امرأة من بنى الجوت قال: فبعثنى فجئتها فأنزلتها الشعب فذكر قصة المستعيذة.
** أبو موسى
(الإصابة في تمييز الصحابة ٢٠٠/١)
** قال الحافظ فى " الإصابة " ١ / ٢٠١: تعقبه أبو موسى بأن عمر بن الحكم إنما رواه عن أبى أسيد نفسه، وكذا أخرجه الحسن بن سفيان فى مسنده. قال الحافظ فى " الإصابة " ١ / ٢٠١: موسى بن عبيدة ضعيف، وكذلك محمد بن سنان فيحتمل أن يكون سقط من الإسناد قوله _ عن أبيه _ فإن أسيد بن أبى أسيد تابعى معروف، وقد أخرج البخارى حديث المستعيذة من طريق حمزة بن أبى أسيد عن أبيه أيضا.','8','337',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3613,'<span data-type="title" id=toc-3621>٣٦١٣ </span>- عن الأشج قال: خرجنا أربعمائة وخمسين رجلا للتجارة فأسلمت على يدى على فذهب بى إلى النبى صلى الله عليه وسلم وهو يقسم غنائم بدر.
** لم
(الإصابة في تمييز الصحابة ٢٠٢/١)
** موضوع
** قال الحافظ فى " الإصابة " ١ / ٢٠٢: الأشج _ جاء ذكره فى ____. خبر موضوع افتراه محمود بن على الطرازى أحد الكذابين بعد الخمسمائة.','8','338',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3614,'<span data-type="title" id=toc-3622>٣٦١٤ </span>- عن الشريد بن عمرو أن النبى صلى الله عليه وسلم استنشده من شعره فقال: " كاد أن يسلم ".
** لم
(الإصابة في تمييز الصحابة ٢١١/١)
** صحيح
** تعقيب: قال محمد مصطفى: قوله شعره أى: شعر أمية بن الصلت.','8','339',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3615,'<span data-type="title" id=toc-3623>٣٦١٥ </span>- عن عمرو بن عثمان بن يعلى بن مرة عن أبيه عن جده: أنهم كانوا مع النبى صلى الله عليه وسلم فى مسيرة فانتهوا إلى مضيق فحضرت الصلاة فمطروا..الحديث.
** ت ابن عبد البر
(الإصابة في تمييز الصحابة ٢١٥/١)
** إسناده لا بأس به
** قال الحافظ فى " الإصابة " ١ / ٢١٥: قال الترمذى: غريب اه.','8','340',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3616,'<span data-type="title" id=toc-3624>٣٦١٦ </span>- عن أيفع بن عبد الكلاعى على منبر حمص، قال: قال رسول الله صلى الله عليه وسلم: " إذا أدخل الله أهل الجنة الجنة وأهل النار النار، قال: يا أهل الجنة كم لبثتم فى الأرض عدد سنين؟ ،.. الحديث.
** الإسماعيلى فى الصحابة
(الإصابة في تمييز الصحابة ٢٢٢/١)
** رجاله ثقات
** قال الحافظ فى " الإصابة " ١ / ٢٢٢: وتابعه أبو يعلى عن الهيثم بن خارجة عن الوليد. رجال إسناده ثقات إلا أنه مرسل أو معضل، وقال الحافظ فى " الإصابة " ١ / ٢٢٢: أيفع بن عبد الكلاعى: تابعى صغير، ولا يصح لأيفع سماع من صحابى.','8','341',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3617,'<span data-type="title" id=toc-3625>٣٦١٧ </span>- عن أيمن الحبشى قال: " يقطع السارق ".
** تخ
(الإصابة في تمييز الصحابة ٢٢٣/١)
** مرسل
** قال الحافظ فى " الإصابة " ١ / ٢٢٣: قال الدارقطنى: أيمن راوى حديث السرقة تابعى لم يدرك النبى صلى الله عليه وسلم ولا الخلفاء بعده.','8','342',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3618,'<span data-type="title" id=toc-3626>٣٦١٨ </span>- عن صالح مولى التوأمة أن باقول مولى العاص بن أمية، صنع لرسول الله صلى الله عليه وسلم منبره من طرفاء ثلاث درجات.
** عب منده سكن
(الإصابة في تمييز الصحابة ٢٢٤/١)
** إسناده ضعيف
** قال الحافظ فى " الإصابة " ١ / ٢٢٤: هو مرسل، ورواه ابن السكن من طريق آخر. قال ابن السكن: ولم يقع لنا إلا من هذا الوجه وهو ضعيف.اه. وأخرجه أبو نعيم من طريق محمد بن اسماعيل المسمولى أحد الضعفاء.','8','343',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3619,'<span data-type="title" id=toc-3627>٣٦١٩ </span>- عن صالح مولى التوأمة حدثنى باقوم مولى سعيد بن العاص قال: صنعت لرسول الله صلى الله عليه وسلم، منبرا من طرفاء الغابة ثلاث درجات المقعد ودرجتين.
** نعيم
(الإصابة في تمييز الصحابة ٢٢٤/١)
** ضعيف','8','344',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3620,'<span data-type="title" id=toc-3628>٣٦٢٠ </span>- عن بحيرا الراهب يقول: سمعت رسول الله صلى الله عليه وسلم يقول: " إذا شرب الرجل كأسا من خمر ... " الحديث.
** عدى
(الإصابة في تمييز الصحابة ٢٢٩/١)
** إسناده ضعيف جدا
** قال الحافظ فى " الإصابة " ١ / ٢٢٩: قال ابن عدى: هذا حديث منكر، ولم أسمع لبحيرا بمسند غير هذا اه.','8','345',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3621,'<span data-type="title" id=toc-3629>٣٦٢١ </span>- عن بديل بن عمرو الخطمى قال: عرضت على رسول الله صلى الله عليه وسلم رقية الحية فأذن لى فيها ودعا فيها بالبركة.
** منده
(الإصابة في تمييز الصحابة ٢٣١/١)
** قال الحافظ فى " الإصابة " ١ / ٢٣١: قال ابن منده: غريب لا نعرفه إلا من هذا الوجه اه. وفى الإسناد من لا يعرف.','8','346',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3622,'<span data-type="title" id=toc-3630>٣٦٢٢ </span>- عن حزام بن هشام عن أبيه قال: قدم بديل بن كلثوم على رسول الله صلى الله عليه وسلم فأنشده: لاهم إنى ناشد محمدا.
** الباوردى
(الإصابة في تمييز الصحابة ٢٣١/١)
** إسناده منقطع','8','347',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3623,'<span data-type="title" id=toc-3631>٣٦٢٣ </span>- عن تميم الدارى فى هذه الآية: " يأيها الذين أمنوا شهادة بينكم إذا حضر أحدكم الموت حين الوصية " الآية، قال: يرى الناس منها غيرى وغير عدى بن بداء وكانا نصرانيين، يختلفان إلى الشام قبل الإسلام، فأتيا الشام لتجارتهما وقدم عليهما مولى لبنى سهم يقال له: بديل بن أبى مريم بتجارة ومعه جام من فضة.... الحديث.
** ت
(الإصابة في تمييز الصحابة ٢٣١/١)
** قال الحافظ فى " الإصابة " ١ / ٢٣٢: أبو النضر: هو محمد بن السائب الكلبى ضعيف، وأصل الحديث فى صحيح البخارى.','8','348',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3624,'<span data-type="title" id=toc-3632>٣٦٢٤ </span>- عن بديل " حليف لهم " قال: رأيت النبى صلى الله عليه وسلم يمسح على الخفين.
** منده
(الإصابة في تمييز الصحابة ٢٣٢/١)
** قال الحافظ فى " الإصابة " ١ / ٢٣٢: رشد بن سعد أحد الضعفاء.','8','349',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3625,'<span data-type="title" id=toc-3633>٣٦٢٥ </span>- عن بديل بن ورقاء أن النبى صلى الله عليه وسلم أمره أن يحبس السبايا والأموال بالجعرانة حتى يقدم عليه ففعل.
** تخ بغوى
(الإصابة في تمييز الصحابة ٢٣٣/١)
** إسناده حسن','8','350',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3626,'<span data-type="title" id=toc-3634>٣٦٢٦ </span>- عن يعلى بن الأشرف قال: أدركت عشرة من الصحابة منهم البراء بن حزم وعبد الله بن جراد، قالوا: أخذ منا النبى صلى الله عليه وسلم من المائة من الإبل جذعتين كذا.
** الباوردى
(الإصابة في تمييز الصحابة ٢٣٤/١)
** قال الحافظ فى " الإصابة " ١ / ٢٣٤: يعلى بن الأشرف أحد الضعفاء المتروكين.','8','351',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3627,'<span data-type="title" id=toc-3635>٣٦٢٧ </span>- عن أنس قال: دخلت على البراء بن مالك وهو يتغنى، فقلت له: قد أبدلك الله ماهو خير منه، فقال: أترهب أن أموت على فراشى، لا والله ما كان الله ليحرمنى ذلك، وقد قتلت مائة منفردا سوى من شاركت فيه.
** بغوى
(الإصابة في تمييز الصحابة ٢٣٦/١)
** إسناده صحيح','8','352',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3628,'<span data-type="title" id=toc-3636>٣٦٢٨ </span>- عن بزيعع مرفوعا فى تزيين أركان الجنة بالحسن والحسين وفيه: " لا يدخلك مراء ولا بخيل ".
** عبدان فى الصحابة
(الإصابة في تمييز الصحابة ٢٤٢/١)
** إسناده فيه مجاهيل
** قال الحافظ فى " الإصابة " ١ / ٢٤٢: قال أبو موسى: هذا غريب جدا اه. وقال ابن عبدان: لم يذكر بزيع سماعا فلا أدرى أهو مرسل أم لا.','8','353',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3629,'<span data-type="title" id=toc-3637>٣٦٢٩ </span>- عن جنادة بن أبى أمية، قال: كنا مع بسر بن أرطاة فى البحر، فأتى بسارق، فقال: سمعت رسول الله صلى الله عليه وسلم يقول: " لا تقطع الأيدى فى السفر ".
** د
(الإصابة في تمييز الصحابة ٢٤٣/١)
** إسناده قوى
** قال الحافظ فى " الإصابة " ١ / ٢٤٣: فى سنن أبى داود فإسناد مصرى قوى.','8','354',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3630,'<span data-type="title" id=toc-3638>٣٦٣٠ </span>- عن أبو وائل أن عمر استعمل بشر بن عاصم على صدقات هوازن فتخلف بشر فلقيه عمر، فقال: ما أخلفك؟ ، أما لنا عليك سمع وطاعة؟ ، قال: بلى، ولكن سمعت رسول الله صلى الله عليه وسلم يقول: " من ولى من أمر المسلمين شيئا أتى به يوم القيامة حتى يوقف على جسر جهنم ".
** بغوى
(الإصابة في تمييز الصحابة ٢٥١/١)
** قال الحافظ فى " الإصابة " ١ / ٢٥١: قال البغوى: لم يروه عن سيار غير سويد وفى حديثه لين اه. وقد وقع لنا من غير طريق سويد أخرجه ابن أبى شيبة.','8','355',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3631,'<span data-type="title" id=toc-3639>٣٦٣١ </span>- عن بشر بن عصمة _ صاحب النبى صلى الله عليه وسلم _ قال: قال رسول الله صلى الله عليه وسلم للأزد: " هم منى وأنا منهم ".
** طب
(الإصابة في تمييز الصحابة ٢٥٣/١)
** إسناده فيه ضعف','8','356',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3632,'<span data-type="title" id=toc-3640>٣٦٣٢ </span>- عن بشر بن قيس بن كلوة، أنه قدم على النبى صلى الله عليه وسلم ومعه ابنه رحيم وهما مقرونان فى سلسلة فى يمين كانت عليه، فقال: " يا بشر أقطعها فليست عليك يمين "، فقطعها وأسلم ومسح وجهه ودعا له بخير.
** ابن شاهين
(الإصابة في تمييز الصحابة ٢٥٦/١)
** إسناده ضعيف','8','357',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3633,'<span data-type="title" id=toc-3641>٣٦٣٣ </span>- عن بشر بن معاذ الأسدى، أنه صلى مع النبى صلى الله عليه وسلم هو وأبوه وكان غلاما ابن عشر سنين، وكان جبريل إمام النبى صلى الله عليه وسلم، والنبى ينظر إلى خيال جبريل شبه ظل سحابة إذا تحرك الخيال ركع النبى صلى الله عليه وسلم.
** أبو موسى فى الذيل
(الإصابة في تمييز الصحابة ٢٥٧/١)
** قال الحافظ فى " الإصابة " ١ / ٢٥٧: جابر بن عبد اله العقيلى كذاب، مشهور بالكذب. قال غنجار فى تاريخه: نفاه الأمير خالد بن أحمد بن نجار لأنه ادعى أنه سمع الحسن البصرى يقول: لما ولدت حملت إلى النبى صلى الله عليه وسلم.','8','358',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3634,'<span data-type="title" id=toc-3642>٣٦٣٤ </span>- عن بشر بن معاوية أنه قدم مع أبيه معاوية بن ثور على رسول الله صلى الله عليه وسلم فمسح رأس بشر ودعا له.. الحديث، وفيه فكانت فى وجهه مسحة النبى صلى الله عليه وسلم كالفرة وكان لا يمسح شيئا إلا برأ.
** منده
(الإصابة في تمييز الصحابة ٢٥٧/١)
** قال الحافظ فى " الإصابة " ١ / ٢٥٧: قال البغوى: عمران مجهول اه. قال ابن منده: لا نعرفه إلا من هذا الوجه اه. قلت: بل له طريق أخرى رواها أبو نعيم.','8','359',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3635,'<span data-type="title" id=toc-3643>٣٦٣٥ </span>- عن بشر بن معاوية بن ثور، وهو جد صاعد لأمه: أنهما وفدا على النبى صلى الله عليه وسلم فعلمهما يس والفاتحة والمعوذات وعلمهم الإبتداء بالبسملة فى الصلوات....... فذكر حديثا طويلا.
** نعيم
(الإصابة في تمييز الصحابة ٢٥٧/١)
** إسناده مجهول
** قال الحافظ فى " الإصابة " ١ / ٢٥٨: إسناده مجهول من صاعد فصاعدا. وله طريق أخرى أخرجها ابن شاهين.','8','360',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3636,'<span data-type="title" id=toc-3644>٣٦٣٦ </span>- عن معاوية بن بشر بن يزيد بن معاوية بن ثور قال: قدم بشر بن معاوية بن ثور على رسول الله صلى الله عليه وسلم فمسح على وجهه ودعا له.
** ابن شاهين
(الإصابة في تمييز الصحابة ٢٥٨/١)
** منقطع
** قال الحافظ فى " الإصابة " ١ / ٢٥٨: هذا فيه انقطاع.','8','361',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3637,'<span data-type="title" id=toc-3645>٣٦٣٧ </span>- عن ابن عباس مرفوعا: " أربعة سادوا فى الإسلام عدى بن حاتم، وبشر بن هلال، وسراقة بن مالك، وعروة بن مسعود ".
** عبدان فى الصحابة
(الإصابة في تمييز الصحابة ٢٥٨/١)
** إسناده مجهول','8','362',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3638,'<span data-type="title" id=toc-3646>٣٦٣٨ </span>- عن بشر أنه أسلم فرد عليه النبى صلى الله عليه وسلم ماله وولده ثم لقيه هو وابنه طلقا مقرنين بحبل، فقال له: " ما هذا؟ "، فقال: حلفت لئن رد الله على مالى، وولدى لأحجن بيت الله مقرونا، فقطعه وقال: " حجا فإن هذا من الشيطان ".
** طب منده
(الإصابة في تمييز الصحابة ٢٥٩/١)
** قال الحافظ فى " الإصابة " ١ / ٢٥٩: قال ابن منده: غريب، تفرد بالرواية عن بشر: ابنه خليفة وقد تقدم نحوه لبشر بن قيس، فما أدرى هما اثنان أو واحد.','8','363',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3639,'<span data-type="title" id=toc-3647>٣٦٣٩ </span>- عن أيوب بن بشير المعافرى عن أبيه، قال: كانت ثائرة فى بنى معاوية فخرج النبى صلى الله عليه وسلم يصلح بينهم وهو متكىء على رجل، قال: فبينما هم كذلك إذا التفت إلى قبر، فقال: " لا دريت...... الحديث.
** بز طب سكن
(الإصابة في تمييز الصحابة ٢٦٠/١)
** قال الحافظ فى " الإصابة " ١ / ٢٦٠: قال البغوى: لا أعلم له غير هذا الحديث. فيه عمرو بن صهبان وهو ضعيف. وقال ابن السكن: فيه نظر، ولم يذكر فى حديثه سماعا ولا حضورا.','8','364',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3640,'<span data-type="title" id=toc-3648>٣٦٤٠ </span>- عن بشير بن سعد عن النبى صلى الله عليه وسلم، قال: " منزلة المؤمن منزلة الرأس من الجسد ".
** طب _ ابن قانع
(الإصابة في تمييز الصحابة ٢٦٢/١)
** إسناده ضعيف','8','365',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3641,'<span data-type="title" id=toc-3649>٣٦٤١ </span>- عن بشير الثقفى قال: أتيت النبى صلى الله عليه وسلم، فقلت: إنى نذرت فى الجاهلية أن لا آكل لحم الجزور ولا أشرب الخمر، فقال: " أما لحوم الجزر فكلها، وأما الخمر فلا تشرب ".
** بغوى الإسماعيلى
(الإصابة في تمييز الصحابة ٢٦٦/١)
** قال الحافظ فى " الإصابة " ١ / ٢٦٦: أبو أعبة عبد الكريم بن أبى المخارق أحد الضعفاء.','8','366',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3642,'<span data-type="title" id=toc-3650>٣٦٤٢ </span>- عن بشير الحارثى الكعبى قال: وفدنى قومى بنو الحارث بن كعب إلى النبى صلى الله عليه وسلم، فقال: " من أين أقبلت؟ "، قلت: أنا وافد قومى إليك بالإسلام، قال: " مرحبا ما اسمك؟ "، قلت: اسمى أكبر، قال: " بل أنت بشير ".
** نى تخ سكن
(الإصابة في تمييز الصحابة ٢٦٦/١)
** قال الحافظ فى " الإصابة " ١ / ٢٦٦: قال ابن منده: غريب لانعرفه إلا من حديث أهل الجزيرة عن عصام.','2','440',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3643,'<span data-type="title" id=toc-3651>٣٦٤٣ </span>- عن أبى هريرة أن بشير الغفارى كان له مقعد من رسول الله صلى الله عليه وسلم لا يكاد يخطئه ... فذكر الحديث وفيه: أنه ابتاع بعيرا وأنه شرد فقال النبى صلى الله عليه وسلم: " إن الشرود يرد " وفيه: " فكيف بيوم مقداره خمسون ألف سنة يوم يقوم الناس لرب العالمين ".
** الحسن بن سفيان ابن شاهين ابن مردويه
(الإصابة في تمييز الصحابة ٢٦٦/١)
** قال الحافظ فى " الإصابة " ١ / ٢٦٧: عبد السلام بن عجلان ضعيف.','2','441',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3644,'<span data-type="title" id=toc-3652>٣٦٤٤ </span>- عن بكر بن عبد الله بن ربيع الأنصارى قال: قال رسول الله صلى الله عليه وسلم: " علموا أولادكم السباحة والرماية ... الحديث.
** منده
(الإصابة في تمييز الصحابة ٢٧٢/١)
** قال الحافظ فى " الإصابة " ١ / ٢٧٢: إسماعيل يضعف فى غير أهل بلده وهذا منه وشيخه غير معروف ولم يذكر أنه سمعه فأخشى أن يكون مرسلا.','2','442',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3645,'<span data-type="title" id=toc-3653>٣٦٤٥ </span>- عن يوسف بن مالك بن بهزاد عن جده قال: خطبنا رسول الله صلى الله عليه وسلم، فقال: " يا معشر الناس، احفظونى فى أبى بكر " الحديث.
** أبو موسى
(الإصابة في تمييز الصحابة ٢٧٦/١)
** قال الحافظ فى " الإصابة " ١ / ٢٧٦: قال ابن عبدان: لا يعرف إلا من هذا الوجه اه. فى إسناده جعفر بن عبد الواحد وهو الهاشمى وقد اتهموه بالكذب.','2','443',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3646,'<span data-type="title" id=toc-3654>٣٦٤٦ </span>- عن بهز قال: كان رسول الله صلى الله عليه وسلم يستاك عرضا.
** بغوى
(الإصابة في تمييز الصحابة ٢٧٦/١)
** قال الحافظ فى " الإصابة " ١ / ٢٧٦: قال البغوى: لا أعلم روى بهز إلا هذا وهو منكر اه. وقال الحافظ فى " الإصابة " ١ / ٢٧٧: وفى الجملة: هو كما قال ابن عبد البر: إسناده مضطرب ليس بالقائم.','2','444',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3647,'<span data-type="title" id=toc-3655>٣٦٤٧ </span>- عن أبى هريرة قال: دخل معاذ بن جبل على النبى صلى الله عليه وسلم فقال: إن بالباب شابا يبكى على شبابه وهو يستأذن فدخل، فقال: " ما يبكيك "، قال: إنى ركبت ذنوبا إن أخذت ببعضها خلدت فى جهنم...... فذكر الحديث فى اعترافه أنه كان ينبش القبور وفيه فجعل ينادى يا سيدى ومولاى هذا بهلول بن دويب مغلولا مسلسلا معترفا بذنوبه، قال: فذكره بطوله فى نحو ورقتين.
** أبو موسى المدينى
(الإصابة في تمييز الصحابة ٢٧٧/١)
** قال الحافظ فى " الإصابة " ١ / ٢٧٧: بهلول: جاء ذكره فى حديث لم يثبت، ذكر أبو موسى أنه روى بإسناد غير متصل. حكم عليه بعض الحفاظ بالوضع، ولكن ذكر أبو موسى أن أبا الشيخ أخرج عن الزهرى نحو منه مرسلا.','2','445',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3648,'<span data-type="title" id=toc-3656>٣٦٤٨ </span>- خطاب بن محمد بن بولى عن أبيه عن جده قال: قال: رسول الله صلى الله عليه وسلم: " إياكم والطعام الحار ".
** عبدان فى الصحابة أبو موسى
(الإصابة في تمييز الصحابة ٢٧٧/١)
** إسناده مجهول
** قال الحافظ فى " الإصابة " ١ / ٢٧٨: قال ابن قانع فى الصحابة: بولى والد عبد الله اه. فذكره ابن قانع فى الوحدة فصحفه وأخطأ فى إسناده، فإن الصواب عن عبد العزيز بن أبى حازم، عن أبيه عن عبد الله بن بولى، ليس فيه عن أبيه.','2','446',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3649,'<span data-type="title" id=toc-3657>٣٦٤٩ </span>- عن بيحر بن عامر يقول: أتينا رسول الله صلى الله عليه وسلم فأسلمنا وسألناه أن يضع عنا العتمة، فقلنا: إنا نشتغل بجلب إبلنا، فقال: " إنكم إن شاء الله ستحلبون وتصلون ".
** طب سكن
(الإصابة في تمييز الصحابة ٢٧٨/١)
** قال الحافظ فى " الإصابة " ١ / ٢٧٨: قال أبو نعيم: تفرد به يحيى بن راشد عن الرحال بن المنذر عن أبيه اه. قلت: يحيى ضعيف.','2','447',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3650,'<span data-type="title" id=toc-3658>٣٦٥٠ </span>- عن بشر بن محيف، قال: أتيت عمر بن الخطاب فقلت: أتيتك لأبايعك فقال: أليس قد بايعت أميرى؟ ، قلت: بلى، قال: فإذا بايعت أميرى فقد بايعتنى.
** سعد
(الإصابة في تمييز الصحابة ٢٨٧/١)
** إسناده صحيح','2','448',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3651,'<span data-type="title" id=toc-3659>٣٦٥١ </span>- عن بشر بن قيس قال: كنا عند عمر فى رمضان فأفطرنا ثم ظهر أن الشمس لم تغرب، فقال عمر: من أفطر فليقض يوما مكانه.
** عب
(الإصابة في تمييز الصحابة ٢٨٧/١)
** إسناده صحيح','2','449',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3652,'<span data-type="title" id=toc-3660>٣٦٥٢ </span>- عن ابن عباس أن أبا بكر الصديق، صحب النبى صلى الله عليه وسلم وهو ابن ثمان عشرة سنة والنبى صلى الله عليه وسلم ابن عشرين، وهم يريدون الشام فى تجارة حتى إذا نزل منزلا فيه سدرة قعد فى ظلها ومضى أبو بكر إلى راهب يقال له: بحيرا يسأله عن شىء، فقال له: من الرجل الذى فى ظل السدرة؟ ، قال: محمد بن عبد الله بن عبد المطلب، فقال: هذا والله نبى، ما استظل تحتها بعد عيسى بن مريم إلا محمد، ووقع فى قلب أبى بكر الصديق، فلما بعث نبى الله صلى الله عليه وسلم اتبعه.
** منده
(الإصابة في تمييز الصحابة ٢٩٤/١)
** قال الحافظ فى " الإصابة " ١ / ٢٩٤: عبد الغنى بن سعيد الثقفى أحد الضعفاء المتروكين.','2','450',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3653,'<span data-type="title" id=toc-3661>٣٦٥٣ </span>- عن بشير بن كعب قال: جاء غلامان شابان إلى رسول الله صلى الله عليه وسلم، فقالا: يارسول الله أنعمل فيما حفت به الأقلام.... الحديث.
** عبدان
(الإصابة في تمييز الصحابة ٣٠١/١)
** قال الحافظ فى " الإصابة " ١ / ٣٠١: قال أبو موسى: هو مرسل.','2','451',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3654,'<span data-type="title" id=toc-3662>٣٦٥٤ </span>- عن تميم بن جراشة، قال: قدمت فى وفد ثقيف على رسول الله صلى الله عليه وسلم فأسلمنا، وسألناه أن يكتب لنا كتابا فيه شروط.. الحديث.
** مطين
(الإصابة في تمييز الصحابة ٣٠٥/١)
** إسناده ضعيف','2','452',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3655,'<span data-type="title" id=toc-3663>٣٦٥٥ </span>- عن عبادة بن تمين المازنى عن أبيه، قال: رأيت رسول الله صلى الله عليه وسلم يتوضأ ويمسح الماء على رجليه.
** تخ حم شب عمر بغوى طب الباوردى
(الإصابة في تمييز الصحابة ٣٠٧/١)
** رجاله ثقات
** قال الحافظ فى " الإصابة " ١ / ٣٠٧: أغرب أبو عمر فقال: إنه ضعيف اه. وقال البغوى: لا أعلم روى عباد عن أبيه غير هذا وتبعه غيره على ذلك، وفيه نظر فقد أخرج له ابن منده حديثين آخرين أحدهما فى الشك فى الحديث وقد وهم فيه ابن لهيعة، وإنما يعرف عن عمه وثانيهما رويناه فى الأول من فوائد العيسوى.','2','453',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3656,'<span data-type="title" id=toc-3664>٣٦٥٦ </span>- عن أبى هاشم الجعفى قال: دخلنا مسجد قباء وقد أسفروا وكان النبى صلى الله عليه وسلم أمر معاذا أن يصلى بهم.... فذكر الحديث.
** منده
(الإصابة في تمييز الصحابة ٣٠٨/١)
** منقطع
** قال الحافظ فى " الإصابة " ١ / ٣٠٨: قال ابن منده: لا يعرف إلا من هذا الوجه اه. قلت: فيه انقطاع، وقد رواه عمر بن شبه من وجه آخر.','2','454',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3657,'<span data-type="title" id=toc-3665>٣٦٥٧ </span>- عن تميم بن بدير، قال: قال رسول الله صلى الله عليه وسلم: " يأيها الناس اتباعوا أنفسكم من الله من مال الله.. " الحديث.
** الباوردى سكن
(الإصابة في تمييز الصحابة ٣/٢)
** رجاله ثقات
** قال الحافظ فى " الإصابة " ٢ / ٣: تميم بن بدير العدوى، قال البزار: أدرك الجاهلية، وسمع من عمر بن الخطاب، وروى عن النبى صلى الله عليه وسلم مرسلا اه. قال ابن السكن: ليس فى حديثه ما يدل على صحبته، وقد أدخله فى المسند وذكره ابن حبان فى الثقات.','2','455',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3658,'<span data-type="title" id=toc-3666>٣٦٥٨ </span>- عن عروة قال: ثم أخذ الراية يعنى فى غزاة مؤتة ثابت بن أقرم بعد قتل ابن رواحة فدفعها إلى خالد بن الوليد.
** ابن إسحاق فى المغازى منده
(الإصابة في تمييز الصحابة ٦/٢)
** إسناده ضعيف','2','456',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3659,'<span data-type="title" id=toc-3667>٣٦٥٩ </span>- عن قتادة أن عم ثابت بن رفاعة أتى النبى صلى الله عليه وسلم فقال: يارسول الله إن ثابتا يتيم فى حجرى، فما يحل لى من ماله؟ ، قال: " أن تأكل بالمعروف من غير أن تقى مالك بماله "
** منده
(الإصابة في تمييز الصحابة ٩/٢)
** رجاله ثقات
** قال الحافظ فى " الإصابة " ٢ / ٩: هذا مرسل رجاله ثقات.','2','457',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3660,'<span data-type="title" id=toc-3668>٣٦٦٠ </span>- عن أبى هريرة رفعه: " نعم الرجل ثابت بن قيس ".
** ت
(الإصابة في تمييز الصحابة ١٥/٢)
** إسناده حسن','2','458',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3661,'<span data-type="title" id=toc-3669>٣٦٦١ </span>- عن ثعلبة بن الحكم، قال: كنا مع النبى صلى الله عليه وسلم فانتهب الناس غنما فنهى عنها.
** جه
(الإصابة في تمييز الصحابة ٢٠/٢)
** إسناده صحيح','2','459',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3662,'<span data-type="title" id=toc-3670>٣٦٦٢ </span>- عن جابر أن فتى من الأنصار يقال له: ثعلبة بن عبد الرحمن كان يخدم النبى صلى الله عليه وسلم فبعثه فى حاجة، فمر بباب رجل من الأنصار فرأى امرأة تغتسل فكرر النظر إليها ثم خاف أن ينزل الوحى فهرب على وجهه حتى أتى جبالا بين مكة والمدينة فولجها ففقده رسول الله صلى الله عليه وسلم أربعين يوما وهى الأيام التى قالوا ودعه ربه وقلاه ثم إن جبريل نزل عليه فقال: يامحمد إن الهارب يتعوذ بى من النار، فأرسل إليه عمر، فقال: انطلق أنت وسمان فائتيانى به، فلقيهما راع يقال له: دفافة، فقال: لعلكما تريدان الهارب من جهنم...... الحديث.
** شاهين نعيم منده
(الإصابة في تمييز الصحابة ٢٣/١)
** قال الحافظ فى " الإصابة " ٢ / ٢٣: قال ابن منده بعد أن رواه مختصرا: تفرد به منصور اه. قلت: فيه ضعف وشيخه أضعف منه، وفى هذا السياق ما يدل على وهن الخبر، لأن نزول " ما ودعك ربك وما قلى "، كان قبل الهجرة بلا خلاف.','2','460',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3663,'<span data-type="title" id=toc-3671>٣٦٦٣ </span>- عن ثعلبة بن أبى مالك القرظى أن النبى صلى الله عليه وسلم أتاه أهل بهزور فقضى أن الماء إذا بلغ الكعبين لم يحبس الأعلى.
** عاصم بغوى منده
(الإصابة في تمييز الصحابة ٢٤/٢)
** رجاله ثقات
** قال الحافظ فى " الإصابة " ٢ / ٢٤: ثعلبة بن أبى مالك القرظى، ذكره ابن حبان فى ثقات التابعين، وقال أبو حاتم: هو تابعى وحديثه مرسل اه. قلت: وحديثه عن عمر فى صحيح البخارى.','2','461',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3664,'<span data-type="title" id=toc-3672>٣٦٦٤ </span>- عن ابن الأشعث الصنعانى قال: لما بلغ ثمامة بن عدى _ وكان أميرا على صنعاء الشام وكانت له صحبة _ قتل عثمان بكى وطال بكاؤه فلما أفاق، قال: هذا حين انتزعت خلافة النبوة.
** تخ سعد
(الإصابة في تمييز الصحابة ٢٨/٢)
** إسناده صحيح
** قال الحافظ فى " الإصابة " ٢ / ٢٨: روى البخارى فى تاريخه وابن سعد بإسناد صحيح إلى أبى قلابة.','2','462',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3665,'<span data-type="title" id=toc-3673>٣٦٦٥ </span>- عن ثوبان الأنصارى قال: سمعت رسول الله صلى الله عليه وسلم يقول: " من رأيتموه ينشد شعرا فى المسجد فقولوا: فض الله فاك.. " الحديث.
** منده
(الإصابة في تمييز الصحابة ٢٩/٢)
** قال الحافظ فى " الإصابة " ٢ / ٢٩: ورواه من طريق أبى خثيمة الجعفى عن عباد بن كثير فلم يقل عن جده، وعباد فيه ضعف، وخالفه يزيد بن خصيفة، فقال عن محمد بن عبد الرحمن عن أبى هريرة وهو المحفوظ، أخرجه النسائى والترمذى.','2','463',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3666,'<span data-type="title" id=toc-3674>٣٦٦٦ </span>- عن أنس بن مالك قال: كنا إذاأردنا أن نسأل رسول الله صلى الله عليه وسلم أمرنا عليا أو سلمان أو ثابت بن معاذ، لأنهم كانوا أجرأ أصحابه عليه فلما نزلت (إذا جاء نصر الله والفتح) ، فذكر حديث فى فضل على فيه: " إنه أخى ووزيرى وخليفتى فى أهل بيتى وخير من أخلف بعدى ".
** خط فى المؤتلف
(الإصابة في تمييز الصحابة ٣٦/٢)
** إسناده ضعيف
** قال الحافظ فى " الإصابة " ٢ / ٣٦: قال الخطيب: مطير مجهول اه. قلت: أبو يحيى التميمى ضعيف جدا.','2','464',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3667,'<span data-type="title" id=toc-3675>٣٦٦٧ </span>- عن جابر بن الأزرق الغاضرى قال: أتيت رسول الله صلى الله عليه وسلم على راحلة ومتاع فدفعنى رجل، فقلت: جئت من أقطار اليمن لأسمع من النبى صلى الله عليه وسلم فأعى ثم أرجع فأحدث من ورائى وأنت تمنعنى قال: صدقت، ثم ركب رسول الله صلى الله عليه وسلم..... فذكر الحديث وفيه دعاؤه للمحلقين ثلاث مرات.
** منده
(الإصابة في تمييز الصحابة ٤٠/٢)
** قال الحافظ فى " الإصابة " ٢ / ٤٠: قال ابن منده: غريب لا يعرف إلا بهذا الإسناد.','2','465',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3668,'<span data-type="title" id=toc-3676>٣٦٦٨ </span>- عن جابر بن حابس أو عابس الصبرى قال: سمعت رسول الله صلى الله عليه وسلم: " من كذب على متعمدا فليتبوأ مقعده من النار ".
** طب
(الإصابة في تمييز الصحابة ٤١/٢)
** إسناده مجهول','2','466',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3669,'<span data-type="title" id=toc-3677>٣٦٦٩ </span>- عن جابر بن أبى سبرة قال: سمعت رسول الله صلى الله عليه وسلم يذكر الجهاد فقال: " إن الشيطان قعد لابن آدم بأطرقه........ الحديث.
** ك هب منده
(الإصابة في تمييز الصحابة ٤١/٢)
** قال الحافظ فى " الإصابة " ٢ / ٤٢: قال ابن منده: غريب تفرد به طارق اه. والمحفوظ فى هذا عن سالم بن أبى الجعر عن سبرة بن أبى فاكهة.','2','467',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3670,'<span data-type="title" id=toc-3678>٣٦٧٠ </span>- عن جابر قال: كنت أمنح أصحابى الماء يوم بدر.
** تخ
(الإصابة في تمييز الصحابة ٤٥/٢)
** إسناده صحيح','2','468',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3671,'<span data-type="title" id=toc-3679>٣٦٧١ </span>- عن جابر بن عبد الله الراسبى عن النبى صلى الله عليه وسلم قال: " من عفا عن قاتله دخل الجنة ".
** منده
(الإصابة في تمييز الصحابة ٤٦/٢)
** قال الحافظ فى " الإصابة " ٢ / ٤٦: قال ابن منده: هذا حديث غريب إن كان محفوظا اه. قال أبو نعيم: قوله الراسبى وهم، وإنما هو الأنصارى اه.','2','469',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3672,'<span data-type="title" id=toc-3680>٣٦٧٢ </span>- عن جابر بن عتيك مرفوعا: " أن من الغيرة ما يبغض الله ... " الحديث.
** د ن
(الإصابة في تمييز الصحابة ٤٧/٢)
** إسناده صحيح','2','470',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3673,'<span data-type="title" id=toc-3681>٣٦٧٣ </span>- عن عطاء قال: رأيت جابر بن عبد الله، وجابر بن عمير يرتميان فمل أحدهما فجلس، فقال له الآخر كسلت؟ ، قال: أما إنى سمعت رسول الله صلى الله عليه وسلم، يقول: " كل شىء ليس من ذكر الله فهو لعب إلا أربعة ".
** ن
(الإصابة في تمييز الصحابة ٤٩/٢)
** إسناده صحيح','2','471',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3674,'<span data-type="title" id=toc-3682>٣٦٧٤ </span>- عن محمد بن مسلم بن جاعل عن أبيه عن جده عن رسول الله صلى الله عليه وسلم، قال: " إن أحصاهم لهذا القرآن من أمتى منافقوهم ".
** منده
(الإصابة في تمييز الصحابة ٥٠/٢)
** قال الحافظ فى " الإصابة " ٢ / ٥٠: قال ابن منده: هذا حديث غريب لا نعرفه إلا من هذا الوجه.','2','472',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3675,'<span data-type="title" id=toc-3683>٣٦٧٥ </span>- عن جارية بن قدامة، قال: قلت: يارسول الله أوصنى وأقلل، قال: " لا تغضب ".
** حم منده
(الإصابة في تمييز الصحابة ٥٣/٢)
** قال الحافظ فى " الإصابة " ٢ / ٥٣: فيه اختلاف على هشام بن عروة. وصححه ابن حبان من طريقه.','2','473',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3676,'<span data-type="title" id=toc-3684>٣٦٧٦ </span>- عن الأسود بن هلال، قال: كان فينا أعرابى يؤذن بالحيرة يقال له: جبر، فقال: إن عثمان لن يموت، حتى يلى هذه الأمة، فقيل له: من أين تعلم "، فقال: لأنى صليت مع رسول الله صلى الله عليه وسلم الفجر فلما سلم استقبلنا بوجهه، فقال: " إن ناسا من أصحابى وزنوا الليلة "، فوزن أبو بكر فوزن، ثم وزن عمر فوزن، ثم وزن عثمان فوزن.
** قانع منده
(الإصابة في تمييز الصحابة ٥٨/٢)
** قال الحافظ فى " الإصابة " ٢ / ٥٨: قال ابن منده: هذا حديث غريب بهذا الإسناد.','2','474',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3677,'<span data-type="title" id=toc-3685>٣٦٧٧ </span>- عن جيلة بن حارثة قلت: يا رسول الله علمنى شيئا ينفعنى الله به قال: " إذا أخذت مضجعك فاقرأ (قل يأيها الكافرون) ".
** ن
(الإصابة في تمييز الصحابة ٦٢/٢)
** إسناده صحيح
** قال الحافظ فى " الإصابة " ٢ / ٦٢: حديث متصل صحيح الإسناد.','2','475',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3678,'<span data-type="title" id=toc-3686>٣٦٧٨ </span>- عن عائشة جاء حبيب بن الحارث فقال: يا رسول الله إنى رجل مقراف للذنوب، قال: " فتب إلى الله عز وجل........ الحديث.
** طب سكن
(الإصابة في تمييز الصحابة ٦٤/٢)
** قال الحافظ فى " الإصابة " ٢ / ٦٤: قال ابن السكن: لم يصح إسناد حديثه اه. قال ابن منده: غريب لا نعرفه إلا من هذا الوجه اه. قال الطبرانى فى الأوسط: لا يروى عن هشام إلا بهذا الإسناد، تفرد به عيسى بن إبراهيم عن سعيد بن عبد الله عن نوح عنه اه. وذكر عبد الغنى ابن سعيد فى المؤتلف أن أيوب بن ذكوان رواه عن هشام اه. قلت: وأيوب ونوح ضعيفان.','2','476',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3679,'<span data-type="title" id=toc-3687>٣٦٧٩ </span>- عن جبير بن نوفل قال: قال رسول الله صلى الله عليه وسلم: " ما تقرب عبد إلى الله بأفضل مما خرج منه _ يعنى القرآن _ ".
** مطين الباوردى منده
(الإصابة في تمييز الصحابة ٦٦/٢)
** قال الحافظ فى " الإصابة " ٢ / ٦٦: فى إسناده ليث بن أبى سليم. قال ابن منده: رواه بكر بن خنيس عن ليث عن زيد عن جبير بن نفير مرسلا.','2','477',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3680,'<span data-type="title" id=toc-3688>٣٦٨٠ </span>- عن جثامة بن قيس مرفوعا: " من صام يوما فى سبيل الله باعده الله عن النار مائة عام ".
** منده
(الإصابة في تمييز الصحابة ٦٧/٢)
** قال الحافظ فى " الإصابة " ٢ / ٦٧: فى الإسناد من لا يعرف.','2','478',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3681,'<span data-type="title" id=toc-3689>٣٦٨١ </span>- عن حجدم أنه أتى النبى صلى الله عليه وسلم فمسح على رأسه وقال: " بارك الله فى حجدم "، وكتب له كتابا فذكر الحديث بطوله.
** منده
(الإصابة في تمييز الصحابة ٦٨/٢)
** قال الحافظ فى " الإصابة " ٢ / ٦٨: قال ابن منده: هو حديث غريب اه. قلت: فى إسناده من لايعرف ثم هو من رواية النضر بن سلمة بن شاذان وهو متروك.','2','479',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3682,'<span data-type="title" id=toc-3690>٣٦٨٢ </span>- عن حكيم بن حجدم _ أراه عن أبيه _ قال: قال رسول الله صلى الله عليه وسلم: " من حلب شاته ورقع قميصه وخصف نعله وأكل مع خادمه وحمل من سوقه فقد برىء من الكبر ".
** منده
(الإصابة في تمييز الصحابة ٦٨/٢)
** إسناده ضعيف','2','480',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3683,'<span data-type="title" id=toc-3691>٣٦٨٣ </span>- أن النبى صلى الله عليه وسلم غير اسم جحش هذا كان اسمه بره فسماه النبى صلى الله عليه وسلم جحشا.
** قط
(الإصابة في تمييز الصحابة ٦٩/٢)
** إسناده واه','2','481',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3684,'<span data-type="title" id=toc-3692>٣٦٨٤ </span>- عن جدار، قال: غزونا مع رسول الله صلى الله عليه وسلم، فلقينا عدونا فقام فحمد الله وأثنى عليه ثم قال: " أيها الناس إنكم أصبحتم وعليكم من الله نعم فيما بين خضراء، وصفراء، وحمراء وفى البيوت مافيها ". فذكر الخطبة بطولها.
** بغوى منده
(الإصابة في تمييز الصحابة ٦٩/٢)
** قال الحافظ فى " الإصابة " ٢ / ٦٩: قال ابن منده: غريب اه. العباس ضعيف جدا، قال ابن معين: أما حديث جدار فليس بصحيح اه. قال ابن الجوزى عن النسائى: هذا حديث باطل. وقال الدارقطنى فى العلل: ليس بالمحفوظ والصواب قول منصور والأعمش.','2','482',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3685,'<span data-type="title" id=toc-3693>٣٦٨٥ </span>- عن جابر قال: حملنى خالى جد بن قيس وما أقدر أن أرمى بحجر فى السبعين راكبا من الأنصار الذين وفدوا على رسول الله صلى الله عليه وسلم.... فذكر الحديث فى بيعة العقبة.
** طب منده
(الإصابة في تمييز الصحابة ٧٠/٢)
** إسناده قوى
** قال الحافظ فى " الإصابة " ٢ / ٧٠: قال ابن منده: غريب من حديث معاوية بن عمار، تفرد به محمد بن عمران بن أبى ليلى.','2','483',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3686,'<span data-type="title" id=toc-3694>٣٦٨٦ </span>- عن الجراد بن عبس أو ابن عبس قال: قلنا: يارسول الله إن لنار كايا فكيف لنا أن نعذب؟ .
** منده
(الإصابة في تمييز الصحابة ٧٢/٢)
** قال الحافظ فى " الإصابة " ٢ / ٧٢: عبد الرحمن بن عمرو بن جبلة متروك.','2','484',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3687,'<span data-type="title" id=toc-3695>٣٦٨٧ </span>- عن جراد العقيلى قال: بعث رسول الله صلى الله عليه وسلم سرية فيها الأزد والأشعريون فغنموا وسلموا الحديث.
** منده
(الإصابة في تمييز الصحابة ٧٢/٢)
** قال الحافظ فى " الإصابة " ٢ / ٧٢: يعلى بن الأشرف متروك. قال أبو نعيم: إنما يعرف من حديث عبد الله بن جراد نفسه.','2','485',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3688,'<span data-type="title" id=toc-3696>٣٦٨٨ </span>- عن جرو السدوسى قال: أتينا النبى صلى الله عليه وسلم بتمر من تمر اليمامة فقال: " أى تمر هذا؟ "، الحديث.
** نعيم منده
(الإصابة في تمييز الصحابة ٧٣/٢)
** قال الحافظ فى " الإصابة " ٢ / ٧٣: قال ابن منده: هذا حديث غريب حسن المخرج اه. قلت: محمد بن جابر هو اليمامى ضعيف.','2','486',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3689,'<span data-type="title" id=toc-3697>٣٦٨٩ </span>- عن جرو بن عمرو أنه أتى النبى صلى الله عليه وسلم وكتب له كتابا " أن ليس عليكم حشر ولا عشر ".
** منده
(الإصابة في تمييز الصحابة ٧٣/٢)
** إسناده مجهول','2','487',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3690,'<span data-type="title" id=toc-3698>٣٦٩٠ </span>- عن جرير قال: لما بعث النبى صلى الله عليه وسلم أتيته، فقال: " ما جاء بك "، قلت: جئت لأسلم فألقى إلى كساءه وقال: " إذا أتاكم كريم قوم فاكرموه ".
** طس
(الإصابة في تمييز الصحابة ٧٦/٢)
** قال الحافظ فى " الإصابة " ٢ / ٧٦: حصين فيه ضعف.','2','488',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3691,'<span data-type="title" id=toc-3699>٣٦٩١ </span>- عن رجل من بنى حنيفة يقال له: جرى أن رجلا أتى النبى صلى الله عليه وسلم، فقال: يارسول الله إنى ربما أكون فى الصلاة فتقع يدى على فرجى فقال: " امض فى صلاتك ".
** منده
(الإصابة في تمييز الصحابة ٧٨/٢)
** قال الحافظ فى " الإصابة " ٢ / ٧٨: قال ابن منده: غريباه. قلت: وسلام ضعيف وإسماعيل كذلك.','2','489',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3692,'<span data-type="title" id=toc-3700>٣٦٩٢ </span>- عن جزء بن الجدرحان قال: وفد أخى قداد بن الجدرحان إلى رسول الله صلى الله عليه وسلم من اليمن بإيمانه وإيمان من أطاعه من أهل بيته وهم إذ ذاك ستمائة بيت عن أطاع الجدرحان وآمن بمحمد صلى الله عليه وسلم فلقيته سرية النبى صلى الله عليه وسلم، فقال لهم قداد: أنا مؤمن فلم يقبلوا منه وقتلوه، فبلغنى ذلك فخرجت إلى رسول الله صلى الله عليه وسلم فنزلت (يأيها الذين أمنوا إذا ضربتم فى سبيل الله فتبينوا) الآية، فأعطانى النبى صلى الله عليه وسلم دية أخى مائة ناقة حمراء وغزوت طيئا فأصبت منهم غنائم وسبيت أربعين امرأة فأتيت بهن المدينة فزوجهن رسول الله صلى الله عليه وسلم أصحابه.
** منده
(الإصابة في تمييز الصحابة ٧٩/٢)
** إسناده مجهول','2','490',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3693,'<span data-type="title" id=toc-3701>٣٦٩٣ </span>- عن جسر بن وهب قال: سمعت نبى الله صلى الله عليه وسلم يقول: " الخيل فى نواحيها الخيرإلى يوم القيامة ".
** قط فى المؤتلف
(الإصابة في تمييز الصحابة ٨١/٢)
** إسناده مجهول','2','491',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3694,'<span data-type="title" id=toc-3702>٣٦٩٤ </span>- عن ابن عمر، قال: جاء رجل إلى رسول الله صلى الله عليه وسلم فقال: يارسول الله أرأيت إن قاتلت بين يديك حتى أقتل يدخلنى ربى الجنة ولا يحقرنى؟ ، قال: " نعم "، قال: كيف وأن منتن الريح أسود اللون؟ ، وفيه أنه استشوه.
** شاهين
(الإصابة في تمييز الصحابة ٨٢/٢)
** إسناده ضعيف','2','492',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3695,'<span data-type="title" id=toc-3703>٣٦٩٥ </span>- عن أبى هريرة قال: ما احتذى النعال، ولا ركب المطايا، ولا وطىء التراب بعد رسول الله صلى الله عليه وسلم أفضل من جعفر بن أبى طالب.
** ت ن
(الإصابة في تمييز الصحابة ٨٦/٢)
** إسناده صحيح','2','493',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3696,'<span data-type="title" id=toc-3704>٣٦٩٦ </span>- عن ابن عمر، قال: كنا مع رسول الله صلى الله عليه وسلم فرفع رأسه إلى السماء فقال: " وعليكم السلام ورحمة الله وبركاته "، فقال الناس: يا رسول الله ما كنت تصنع هذا؟ ، قال: " مر بى جعفر بن أبى طالب فى ملأ من الملائكة فسلم على ".
** قط فى غرائب مالك
(الإصابة في تمييز الصحابة ٨٧/٢)
** إسناده ضعيف','2','494',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3697,'<span data-type="title" id=toc-3705>٣٦٩٧ </span>- عن جعونة بن زياد الشنى أنه سمع النبى صلى الله عليه وسلم يقول: " لا بد من العريف والعريف فى النار ".
** منده
(الإصابة في تمييز الصحابة ٨٨/٢)
** قال الحافظ فى " الإصابة " ٢ / ٨٨: عبد الرحمن بن عمرو بن جبلة أحد الضعفاء، وبقية رجاله مجهولون.','2','495',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3698,'<span data-type="title" id=toc-3706>٣٦٩٨ </span>- عن محمد بن إبراهيم التيمى قال: قيل يا رسول الله أعطيت عيينة بن حصن والأقرع بن حابس مائة مائة، وتركت جعيلا؟ ، فقال: " والذى نفسى بيده لجعيل بن سراقة خير من طلاع الأرض، مثل عيينة والأقرع، ولكنى أتألفهما وأركل جعيلا إلى إيمانه ".
** ابن إسحاق فى المغازى
(الإصابة في تمييز الصحابة ٨٩/٢)
** حسن مرسلا
** قال الحافظ فى " الإصابة " ٢ / ٨٩: هذا مرسل حسن، لكن له شاهد موصول.','2','496',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3699,'<span data-type="title" id=toc-3707>٣٦٩٩ </span>- عن أبى ذر أن رسول الله صلى الله عليه وسلم قال له: " كيف ترى جعيلا " قلت: مسكينا كشكله من الناس، قال: " وكيف ترى فلانا "، قلت: سيدا من السادات، قال: " لجعيل خير من ملء الأرض مثل هذا "، قال: قلت: يا رسول الله ففلان هكذا وتصنع به ما تصنع؟ ، قال: " إنه رأس قومه فأتألفهم ".
** الرويانى فى مسنده ابن عبد الحكم فى فتوح مصر
(الإصابة في تمييز الصحابة ٨٩/٢)
** إسناده صحيح
** قال الحافظ فى " الإصابة " ٢ / ٨٩: وأخرجه ابن حبان من وجه آخر، عن أبى ذر لكن لم يسم جعيلا، وأخرجه البخارى من حديث سهل بن سعد، فأبهم جعيلا وأبا ذر.','2','497',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3700,'<span data-type="title" id=toc-3708>٣٧٠٠ </span>- عن جفينة أن النبى صلى الله عليه وسلم كتب إليه كتابا فرقع به دلوه فقالت له ابنته: عمدت إلى كتاب سيد العرب فرقعت به دلوك؟ ، فهرب وأخذ كل قليل وكثير هو له ثم جاء بعد مسلما، فقال له النبى صلى الله عليه وسلم: " انظر ما وجدت من متاعك قبل قسمة السهام فخذه ".
** بغوى طب
(الإصابة في تمييز الصحابة ٩١/٢)
** قال الحافظ فى " الإصابة " ٢ / ٩٢: قال البغوى: منكر من حديث الفورى وأبو بكر الزاهرى ضعيف الحديث.','2','498',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3701,'<span data-type="title" id=toc-3709>٣٧٠١ </span>- ** منده
(الإصابة في تمييز الصحابة ٩٣/٢)
** عن الجلاس أنه أتى النبى صلى الله عليه وسلم فسأله عن الوضوء فقال: " واحدة تجزىء وثنتان "، قال: ورأيته توضأ ثلاثا ثلاثا.
** تعقيب: قال الحافظ فى " الإصابة " ٢ / ٩٣: قال ابن منده: غريب لا يعرف إلا من هذا الوجه اه. عبد الرحمن متروك الحديث.','2','499',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3702,'<span data-type="title" id=toc-3710>٣٧٠٢ </span>- عن جلاس بن عمرو قال: وفدت فى نفر من قومى من كنده على رسول الله صلى الله عليه وسلم فلما أردنا الرجوع، قلنا: أوصنا يا نبى الله، قال: " إن لكل ساع غاية، وغاية ابن آدم الموت.. "، الحديث.
** بغوى
(الإصابة في تمييز الصحابة ٩٣/٢)
** قال الحافظ فى " الإصابة " ٢ / ٩٣: على بن قرين ضعيف جدا، ومن فوقه لا يعرفون.','2','500',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3703,'<span data-type="title" id=toc-3711>٣٧٠٣ </span>- عن يزيد بن جمرة، قال: أتى أبى جمرة بن عوف إلى النبى صلى الله عليه وسلم هو وأخوه حريث ... الحديث.
** منده
(الإصابة في تمييز الصحابة ٩٥/٢)
** رجاله مجهولون','3','1',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3704,'<span data-type="title" id=toc-3712>٣٧٠٤ </span>- عن أم سلمة أنها كانت عند رسول الله صلى الله عليه وسلم فجاء جمهان الأعمى، فقال: استترى، قالت: يا رسول الله جمهان الأعمى، قال: " إنه يكره للنساء أن ينظرن إلى الرجال، كما يكره للرجال أن ينظروا إلى النساء ".
** لم
(الإصابة في تمييز الصحابة ٩٥/٢)
** قال الحافظ فى " الإصابة " ٢ / ٩٦: نصر بن طريف ضعيف.','3','2',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3705,'<span data-type="title" id=toc-3713>٣٧٠٥ </span>- عن خباب بن الكنانى قال: كنت بالفلاة إذ مر علينا جيش عرمرم فقيل: هذا رسول الله........ فذكر الحديث بطوله.
** منده
(الإصابة في تمييز الصحابة ٩٩/٢)
** إسناده ضعيف','3','3',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3706,'<span data-type="title" id=toc-3714>٣٧٠٦ </span>- عن أبى عبد الرحمن الصنعانى أن جنادة الأزدى أم قوما.... الحديث وفيه سمعت رسول الله صلى الله عليه وسلم يقول: " من أم قوما وهم له كارهون فإن صلاته لا تجاوز ترقوته ".
** طب
(الإصابة في تمييز الصحابة ٩٩/٢)
** إسناده ضعيف','3','4',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3707,'<span data-type="title" id=toc-3715>٣٧٠٧ </span>- عن جنادة بن زيد الحارثى، قال: وفدت على رسول الله صلى الله عليه وسلم فقلت: يارسول الله إنى وافد قومى من بلحارث من البحرين فادع الله يعيننا على عدونا، قال: فدعا وكتب لنا كتابا.
** سكن الباوردى
(الإصابة في تمييز الصحابة ١٠١/٢)
** إسناده ضعيف ومجهول','3','5',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3708,'<span data-type="title" id=toc-3716>٣٧٠٨ </span>- عن جنادة بن مالك عن النبى صلى الله عليه وسلم، قال: " ثلاث من فعل الجاهلية لا يدعهن أهل الإسلام: استسقاء بالكواكب، وطعن فى النسب، والنياجة على الميت ".
** سعد سكن طب تخ
(الإصابة في تمييز الصحابة ١٠٢/٢)
** قال الحافظ فى " الإصابة " ٢ / ١٠٢: قال البخارى: فى إسناده نظر.','3','6',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3709,'<span data-type="title" id=toc-3717>٣٧٠٩ </span>- عن جندب بن الحارث، قال: رأيت رسول الله صلى الله عليه وسلم وهو يفحج ما بين فخذى الحسين ويقبل زبيبته.
** المعافى بن زكريا فى الجليس
(الإصابة في تمييز الصحابة ١٠٣/٢)
** غريب','3','7',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3710,'<span data-type="title" id=toc-3718>٣٧١٠ </span>- عن جهجهاه الغفارى أنه قدم فى نفر من قومه يريدون الإسلام فحضروا مع رسول الله صلى الله عليه وسلم المغرب فلما أن سلم، قال: " ليأخذ كل رجل منكم بيد جليسه ... "، فذكر الحديث فى شربه قبل أن يسلم جلاب سبع شياه فلما أسلم لم يستتم حلب شياه الحديث.
** شب
(الإصابة في تمييز الصحابة ١١٢/٢)
** غريب
** قال الحافظ فى " الإصابة " ٢ / ١١٢: تفرد به موسى بن عبيدة عن عبيد.','3','8',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3711,'<span data-type="title" id=toc-3719>٣٧١١ </span>- عن جهر، قال: قرأت خلف النبى صلى الله عليه وسلم، فقال: " ياجهر أسمع ربك ولا تسمعنى ".
** طب قانع
(الإصابة في تمييز الصحابة ١١٣/٢)
** قال الحافظ فى " الإصابة " ٢ / ١١٣: الوقاصى ضعيف، وقد خالفه النعمان بن راشد.','3','9',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3712,'<span data-type="title" id=toc-3720>٣٧١٢ </span>- عن أبى هند الدارى أن النبى صلى الله عليه وسلم كتب له كتابا وفيه: شهد عباس بن عبد المطلب، وجهم بن قيس، وشرحبيل بن حسنة.
** منده
(الإصابة في تمييز الصحابة ١١٤/٢)
** إسناده ضعيف','3','10',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3713,'<span data-type="title" id=toc-3721>٣٧١٣ </span>- عن جهم البلوى، قال: وافانا رسول الله صلى الله عليه وسلم فسألنا من نحن؟ ، فقلنا: نحن بنو عبد مناف، فقال: " أنتم بنو عبد الله ".
** بغوى
(الإصابة في تمييز الصحابة ١١٥/٢)
** إسناده ضعيف
** قال الحافظ فى " الإصابة " ٢ / ١١٥: قال أبو حاتم: عبد العزيز بن عمران ضعيف لا يعتمد على روايته.','3','11',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3714,'<span data-type="title" id=toc-3722>٣٧١٤ </span>- عن جهم قال: سمعت رسول الله صلى الله عليه وسلم يقول: " إن حسنا وحسينا سيدا شباب أهل الجنة ".
** ابن أبى عزرة فى مسنده مند قانع
(الإصابة في تمييز الصحابة ١١٥/٢)
** إسناده ضعيف','3','12',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3715,'<span data-type="title" id=toc-3723>٣٧١٥ </span>- عن جودان العبدى، عن النبى صلى الله عليه وسلم: " من اعتذر إلى أخيه فلم يقبل منه كان عليه خطيئة صاحب مكس ".
** حب فى روضة العقلاء جه طب مد
(الإصابة في تمييز الصحابة ١١٧/٢)
** قال الحافظ فى " الإصابة " ٢ / ١١٧: قال ابن حبان: إن كان ابن جريج سمعه فهو حسن غريب اه. وقال ابن أبى حاتم: سألت أبى عنه فقال: جودان مجهول وليست له صحبة اه.','3','13',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3716,'<span data-type="title" id=toc-3724>٣٧١٦ </span>- عن جعدة بن هبيرة قال: ذكر عند النبى صلى الله عليه وسلم عبد لبنى المطلب يصلى ولا ينام ... الحديث.
** طب
(الإصابة في تمييز الصحابة ١١٩/٢)
** مرسل','3','14',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3717,'<span data-type="title" id=toc-3725>٣٧١٧ </span>- عن عبد الرحمن بن عبد القارى أن رسول الله صلى الله عليه وسلم بعث عمرو بن العاص إلى جيفر وعبد ابنى الجلندى أميرى عمان فمضى عمرو إليهما فأسلما وأسلم معهما بشر كثير ووضع الجزية على من لم يسلم.
** عبدان
(الإصابة في تمييز الصحابة ١٣١/٢)
** إسناده صحيح','8','367',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3718,'<span data-type="title" id=toc-3726>٣٧١٨ </span>- عن جعفر العبدى، قال: قال رسول الله صلى الله عليه وسلم: " ويل للميالين من أمتى ".
** على بن سعيد فى الصحابة
(الإصابة في تمييز الصحابة ١٣٨/٢)
** قال الحافظ فى " الإصابة " ٢ / ١٣٨: قال أبو موسى: إن كان هذا هو جعفر بن زيد العبدى _ فهو تابعى معروف _ وإلا فما أعرفه اه. قلت: هو هو، فقد ذكره البخارى فى التاريخ وذكر هذا الحديث فى ترجمته من طريق معتمر، وقال: هو مرسل.','8','368',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3719,'<span data-type="title" id=toc-3727>٣٧١٩ </span>- عن جون بن قتادة التيمى، قال: كنا مع النبى صلى الله عليه وسلم فى بعض أسفاره فمر بعض أصحابه بسقاء معلق فيه ماء وأراد أن يشرب فقال له صاحب السقاء: إنه جلد ميتة، فذكروا ذلك له، فقال: " اشربوا فإن دباغ الميتة طهورها ".
** منيع
(الإصابة في تمييز الصحابة ١٤٢/٢)
** قال الحافظ فى " الإصابة " ٢ / ١٤٣: قال أبو محمد بن حزم: هذا حديث صحيح وجون قد صحت صحبته اه. وتعقبه أبو بكر بن مفوز، فقال: هذا خطأ، فجون رجل تابعى مجهول لا يعرف روى منه إلا الحسن.','8','369',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3720,'<span data-type="title" id=toc-3728>٣٧٢٠ </span>- عن حابس بن ربيعة التميمى أنه سمع النبى صلى الله عليه وسلم يقول: " العين حق ".
** حم ت خز تخ خد
(الإصابة في تمييز الصحابة ١٤٤/٢)
** قال الحافظ فى " الإصابة " ٢ / ١٤٤: قال ابن السكن: اختلف على يحيى بن كثير فيه ولم نجده إلا من طريقه اه. وقال البغوى: لا أعلم له إلا هذا الحديث اه. قال ابن عبد البر: فى إسناد حديثه اضطراب اه.','8','370',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3721,'<span data-type="title" id=toc-3729>٣٧٢١ </span>- عن عبد الله بن عامر قال: دخل حابس بن سعد لمسجد فى السحر وكان قد أدرك النبى صلى الله عليه وسلم فرأى الناس يصلون فى صفة المسجد فقال: " مراؤون فأرغبوهم، إن الملائكة تصلى فى السحر فى مقدمة المسجد ".
** حم
(الإصابة في تمييز الصحابة ١٤٥/٢)
** إسناده صحيح موقوفا','8','371',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3722,'<span data-type="title" id=toc-3730>٣٧٢٢ </span>- عن الحارث بن الجنيد يقول: قال لى رسول الله صلى الله عليه وسلم: " إياكم والجدال فإن الجدال لا يدل على خير.. "، حديث.
** الإسماعيلى فى الصحابة
(الإصابة في تمييز الصحابة ١٤٩/٢)
** قال الحافظ فى " الإصابة " ٢ / ١٤٩: سنده فيه على بن قرين وعلى اتهموه.','8','372',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3723,'<span data-type="title" id=toc-3731>٣٧٢٣ </span>- روى النسائى من طريق الزهرى عن عروة عن عائشة عن النبى صلى الله عليه وسلم قال: " دخلت الجنة فسمعت قراءة، فقلت: من هذا: فقيل: حارثة بن النعمان فقال رسول الله صلى الله عليه وسلم: كذلكم البر، وكان برا بأمه ". وهو عند أحمد من طريق معمر عن الزهرى عن عروة أو غيره ولفظه: " كان أبر الناس بأمه ".
** ن حم
(الإصابة في تمييز الصحابة ١٩٠/٢)
** إسناده صحيح','8','373',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3724,'<span data-type="title" id=toc-3732>٣٧٢٤ </span>- عن عائشة قالت: خرجت يوم الخندق فسمعت حسا فالتفت فإذا أنا بسعد بن معاذ ومعه ابن أخيه الحارث بن أوس يحمل مجنة ... الحديث.
** حم
(الإصابة في تمييز الصحابة ١٤٨/٢)
** صحيح
** قال الحافظ فى " الإصابة " ٢ / ١٤٨: صححه ابن حبان.','8','374',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3725,'<span data-type="title" id=toc-3733>٣٧٢٥ </span>- عن الحارث بن أبى ضرار، أنه جاء إلى المدينة ومعه فداء ابنته بعد أن أسرت وتزوجها رسول الله صلى الله عليه وسلم، قال: فلما كان بالعقيق نظر إلى الإبل فرغب فى بعيرين منها فغيبهما فى شعبا ثم جاء فقال: يالحد هذا فداء ابنتى فقال: " فأين البعيران اللذان غيبتهما بالعقيق "، فقال الحارث: أشهد أن لا إله إلا الله، وأن محمدا رسول الله والله ما أطلع على ذلك إلا الله، قال: فأسلم وأسلم معه ابنان له، وناس من قومه.
** ابن اسحاق ابن عابد فى المغازى
(الإصابة في تمييز الصحابة ١٦٠/٢)
** منقطع','8','375',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3726,'<span data-type="title" id=toc-3734>٣٧٢٦ </span>- عن حبة وسواء ابنى خالد قالا: دخلنا على النبى صلى الله عليه وسلم وهو يعالج شيئا.
** جه
(الإصابة في تمييز الصحابة ٢٠٠/٢)
** إسناده حسن','8','376',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3727,'<span data-type="title" id=toc-3735>٣٧٢٧ </span>- عن صالح بن مسمار أن النبى صلى الله عليه وسلم قال: " يا حارث بن مالك كيف أصبحت؟ "، قال: أصبحت مؤمنا حقا، قال: " إن لكل قول حقيقة فما حقيقة إيمانك؟ " قال: عرفت نفسى عن الدنيا فأسهرت ليلى وأظمأت نهارى وكأنى أنظر أهل الجنة يتزاورون، وكأنى أسمع عواء أهل النار، فقال: " مؤمن نور الله قلبه ".
** مب عب
(الإصابة في تمييز الصحابة ١٧٤/٢)
** معضل','8','377',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3728,'<span data-type="title" id=toc-3736>٣٧٢٨ </span>- عن أنس أن النبى صلى الله عليه وسلم لقى الحارث يوما فقال: " كيف أصبحت يا حارث؟ "، قال: أصبحت مؤمنا حقا.... الحديث بطوله، وفى آخره، قال: " يا حارث عرفت فالزم ".
** هب
(الإصابة في تمييز الصحابة ١٧٥/٢)
** قال الحافظ فى " الإصابة " ٢ / ١٧٥: يوسف بن عطية الصفار ضعيف جدا، قال البيهقى: هذا منكر وقد خبط فيه يوسف فقال مرة: الحارث ومرة حارثة اه. وقال ابن المبارك: لا أعلم صالح بن مسمار اسند إلا حديثا واحدا، وهذا الحديث لا يثبت موصولا.','8','378',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3729,'<span data-type="title" id=toc-3737>٣٧٢٩ </span>- عن جابر قال: قال أبو اليسر كان لى على الحارث بن يزيد الجهنى مال فطال حبسه إياى.... الحديث.
** عبدان عبد الغنى بن سعيد فى المبهمان
(الإصابة في تمييز الصحابة ١٨٥/٢)
** منقطع رجاله ثقات
** قال الحافظ فى " الإصابة " ٢ / ١٨٥: أصله فى صحيح مسلم عن عبادة بن الوليد بن عبادة الحديث.','8','379',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3730,'<span data-type="title" id=toc-3738>٣٧٣٠ </span>- عن الحارث بن يزيد الجهنى قال: كان النبى صلى الله عليه وسلم ينهى أن يبال فى الماء المتجمع المستنقع.
** أبو موسى فى الذيل
(الإصابة في تمييز الصحابة ١٨٥/٢)
** إسناده ضعيف','8','380',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3731,'<span data-type="title" id=toc-3739>٣٧٣١ </span>- عن زيد بن حارثة أن النبى صلى الله عليه وسلم دعا أباه حارثة بن شراحبيل إلى الإسلام فأسلم.
** منده ك
(الإصابة في تمييز الصحابة ١٨٩/٢)
** رجاله مجهولون
** قال الحافظ فى " الإصابة " ٢ / ١٨٩: قال ابن مندة: غريب لا نعرفه إلا من هذا الوجه اه. ورويناه فى فوائد تمام فى نحو ورقتين ورجال إسناده مجهولون من يحيى إلى زيد بن الحسن بن أسامة.','8','381',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3732,'<span data-type="title" id=toc-3740>٣٧٣٢ </span>- عن الأحنف بن قيس، قال: بينما أنا أطوف بالبيت فى زمن عثمان إذ أخذ رجل من بنى ليث بيدى، فقال: ألا أبشرك؟ ، قلت: بلى، قال: أتذكر إذ بعثنى رسول الله صلى الله عليه وسلم إلى قومك فجعلت أعرض عليهم الإسلام وأدعوهم إليه؟ قلت: أنت إنك لتدعونا إلى خير وتأمر به وإنه ليدعو إلىالخير فبلغ ذلك النبى صلى الله عليه وسلم، فقال: " اللهم اغفر للأحنف "، فكان الأحنف يقول: فما شىء من عملى أرجى عندى من ذلك، _ يعنى دعوة النبى صلى الله عليه وسلم _.
** عاصم
(الإصابة في تمييز الصحابة ١٦٣/١)
** قال الحافظ فى " الإصابة " ٢ / ١٦٣: تفرد به على بن زيد وفيه ضعف.','8','382',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3733,'<span data-type="title" id=toc-3741>٣٧٣٣ </span>- عن جرير قال: خرجت إلى فارس، فقلت: ما شاء الله لا حول ولا قوة إلا بالله فسمعنى رجل، فقال: ما هذا الكلام الذى لم أسمعه من أحد منذ سمعته من السماء؟ فقلت: ما أنت وخبر السماء؟ قال: إنى كنت مع كسرى فأرسلنى فى بعض أموره فخرجت ثم قدمت، فإذا شيطان خلفنى فى أهلى على صورتى فبدا لى فقال: شارطنى على أن يكون لى يوم ولك يوم وإلا هلكت فرضيت بذلك فصار جليس يحدثنى وأحدثه، فقال لى ذات يوم: إنى ممن يسترق السمع والليلة نوبتى، قلت فهل لك أن أجىء معك، قال: نعم، فتهيأت ثم أتانى، فقال: خذ بمعرفتى وإياك أن تتركها فتهلك فأخذت بمعرفته فعرج حتى لمست السماء فإذا أنا بقائل يقول: ما شاء الله لاقوة إلا بالله فسقطوا لوجوههم، وسقطت فرجعت غلى أهلى فإذا أنا به
يدخل بعد أيام فجعلت أقول: ماشاء الله، لاحول ولا قوة إلا بالله، قال: فيذوب لذلك حتى يصير مثل الذباب، ثم قال لى: قد حفظته فانقطع عنا.
** منده
(الإصابة في تمييز الصحابة ١٦٧/١)
** قال الحافظ فى " الإصابة " ١ / ١٦٧: إبراهيم بن فهد أحد الضعفاء.','8','383',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3734,'<span data-type="title" id=toc-3742>٣٧٣٤ </span>- عن عبد الملك بن عمير نحوه وزاد: أنه قرب له بعيره فركب متوجها إلى النبى صلى الله عليه وسلم فمات فى الطريق، قال: ويقال نزلت فيه هذه الآية " ومن يخرج من بيته مهاجرا إلى الله ورسوله ثم يدركه الموت فقد وقع أجره على الله....... " الآية.
** الأموى فى المغازى
(الإصابة في تمييز الصحابة ١٨٠/١)
** قال الحافظ فى " الإصابة " ١ / ١٨٠: عبد الله بن زياد هو ابن سمعان أحد المتروكين. وقال الحافظ فى " الإصابة " ١ / ١٨١: وقد وجدت له شاهدا ذكره أبو حاتم السجستانى فى كتاب المعمرين.','8','384',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3735,'<span data-type="title" id=toc-3743>٣٧٣٥ </span>- عن عبد الله بن سلمة، قال: غزونا أذربيجان فى زمن عمر ومعنا أويس فلما رجعنا مرض فمات.
** عم
(الإصابة في تمييز الصحابة ١٩٠/١)
** قال الحافظ فى " الإصابة " ١ / ١٩٠: فى الإسناد الهيثم بن عدى وهو متروك.','8','385',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3736,'<span data-type="title" id=toc-3744>٣٧٣٦ </span>- عن بدر بن عبد الله المزنى قال: قلت يارسول الله: إنى رجل محارف لا ينمى لى مال ... فذكر حديثا.
** منده
(الإصابة في تمييز الصحابة ٢٣٠/١)
** قال الحافظ فى " الإصابة " ١ / ٢٢٩: عمرو بن الحصين متروك.','8','386',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3737,'<span data-type="title" id=toc-3745>٣٧٣٧ </span>- حديث مرفوع متنه: " مااسترذل الله عبدا إلا حرم العلم ".
** عبدان أبو موسى
(الإصابة في تمييز الصحابة ٢٦٥/١)
** إسناده ضعيف جدا','8','387',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3738,'<span data-type="title" id=toc-3746>٣٧٣٨ </span>- عن ابن عباس قال: كان رسول الله صلى الله عليه وسلم يعلم قنيا بمكة اسمه بلعام وكان أعجمى اللسان فكان المشركون يرون رسول الله صلى الله عليه وسلم يدخل عليه، ويخرج من عنده، فقالوا: إنما يتعلم من بلعام فأنزل الله تعالى " يقولون إنما يعلمه بشر لسان الذى يلحدون إليه " الآية.
** ابن أبى حاتم فى التفسير ابن مردوية
(الإصابة في تمييز الصحابة ٢٧٤/١)
** قال الحافظ فى " الإصابة " ١ / ٢٧٤: مسلم بن كيسان الأعور وهو ضعيف.','8','388',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3739,'<span data-type="title" id=toc-3747>٣٧٣٩ </span>- من طريق شعبة بن دخان بن التوأم عن أبيه عن جده عن النبى صلى الله عليه وسلم، قال: " إن هذا الشعر سجع من كلام العرب ".
** منده
(الإصابة في تمييز الصحابة ٣٠٩/١)
** قال الحافظ فى " الإصابة " ١ / ٣٠٩: قال ابن منده: إسناده مجهول وهو وهم.','8','389',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3740,'<span data-type="title" id=toc-3748>٣٧٤٠ </span>- عن تميم بن غيلان، قال: بعث رسول الله صلى الله عليه وسلم أبا سفيان بن حرب والمغيرة بن شعبة وخالد بن الوليد أو غيره، وأمرهم أن يكسروا طاغية ثقيف....... الحديث.
** بغوى شاهين قانع
(الإصابة في تمييز الصحابة ٣١١/١)
** قال الحافظ فى " الإصابة " ١ / ٣١١: قال ابن منده: لا نعرفه إلا من هذا الوجه، قال: هو مرسل.','8','390',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3741,'<span data-type="title" id=toc-3749>٣٧٤١ </span>- عن أبى أمامة أن ثعلبة بن حاطب الأنصارى قال: يارسول الله ادع الله أن يرزقنى مالا، فقال النبى صلى الله عليه وسلم: " قليل تؤدى شكره خير من كثير لا تطيقه ... " فذكر الحديث بطوله فى دعاء النبى صلى الله عليه وسلم له وكثرة ماله ومنعه الصدقة ونزول قول الله تعالى (ومنهم من عاهد الله لئن آتانا من فضله) الآية، وفيه أن النبى صلى الله عليه وسلم، مات ولم يقبض منه الصدقة ولا أبو بكر ولا عمر وأنه مات فى خلافة عثمان.
** الباوردى سكن شاهين
(الإصابة في تمييز الصحابة ١٩/٢)
** قال الحافظ فى " الإصابة " ٢ / ١٩: وفى كون صاحب هذه القصة _ إن صح الخبر ولا أظنه يصح _ هو البدرى المذكور قبله نظر وقد تأكدت المغايرة بينهما.','8','391',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3742,'<span data-type="title" id=toc-3750>٣٧٤٢ </span>- عن جابر بن عبد الله بن رئاب أن النبى صلى الله عليه وسلم قال: " مر بى ميكائيل فى نفر من الملائكة....... "، الحديث.
** بغوى سكن
(الإصابة في تمييز الصحابة ٤٤/٢)
** إسناده ضعيف
** قال الحافظ فى " الإصابة " ٢ / ٤٤: قال البغوى: الوازع ضعيف جدا، قال: ولا أعرف لجابر سندا غيره اه. قلت: بل له غيره.','8','392',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3743,'<span data-type="title" id=toc-3751>٣٧٤٣ </span>- عن همام عن الكلبى فى قوله تعالى (يمحو الله ما شاء ويثبت) ، قال: يمحو من الرزق، وقال: فقلت: من حدثك؟ ، قال: أبو صالح عن جابر بن رئاب عن النبى صلى الله عليه وسلم.
** شاهين مردويه
(الإصابة في تمييز الصحابة ٤٤/٢)
** إسناده ضعيف
** قال الحافظ فى " الإصابة " ٢ / ٤٤: جاء عن جابر بن عبد الله بن رئاب أحاديث من طرق ضعيفة.','8','393',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3744,'<span data-type="title" id=toc-3752>٣٧٤٤ </span>- عن عبد الله بن جابر العبدى قال: كنت مع الوفد الذين أتوا رسول الله صلى الله عليه وسلم من عبد القيس ولست منهم إنما كنت مع أبى فنهاهم رسول الله صلى الله عليه وسلم عن الشرب فى الأوعية.. الحديث وفيه أنه حج مع أبيه بعد النبى صلى الله عليه وسلم فأتى الحسن بن على فسلم عليه فرحب به فسأله رجل عن نبيذ الجر فرخص فيه، قال: فقال له أبى: أبعد ما نهى عنه رسول الله صلى الله عليه وسلم، قال: نعم قد كان بعدكم رخصة.
** حم فى الأشرية البغوى
(الإصابة في تمييز الصحابة ٤٦/٢)
** إسناده حسن','8','394',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3745,'<span data-type="title" id=toc-3753>٣٧٤٥ </span>- عن جرير بن الأرقط قال: رأيت النبى صلى الله عليه وسلم فى حجة الوداع فسمعته يقول: " أعطيت الشفاعة ".
** منده
(الإصابة في تمييز الصحابة ٧٦/٢)
** قال الحافظ فى " الإصابة " ٢ / ٧٦: رواه ابن منده من طريق يعلى بن الأشرق وهو متروك.','8','395',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3746,'<span data-type="title" id=toc-3754>٣٧٤٦ </span>- عن جنادة بن أمية الأزدى أنهم دخلوا على رسول الله صلى الله عليه وسلم ثمانية نفر هو ثامنهم فقرب إليهم طعاما يوم الجمعة ... الحديث فى النهى عن صيام يوم الجمعة.
** حم ن بغوى
(الإصابة في تمييز الصحابة ٩٩/٢)
** صحيح
** قال الحافظ فى " الإصابة " ٢ / ١٠٠: هذان الخبران الأولان صحيحان.','8','396',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3747,'<span data-type="title" id=toc-3755>٣٧٤٧ </span>- عن جنادة بن أمية أن رجالا من الصحابة قال بعضهم إن الهجرة قد انقطعت فاختلفوا فى ذلك فانطلقت إلى النبى صلى الله عليه وسلم، فقال: " إن الهجرة لا تنقطع ما كان الجهاد ".
** حم
(الإصابة في تمييز الصحابة ٩٩/٢)
** صحيح
** قال الحافظ فى " الإصابة " ٢ / ١٠٠: هذان الخبران الأولان صحيحان.','8','397',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3748,'<span data-type="title" id=toc-3756>٣٧٤٨ </span>- عن محمد بن السائب الكلبى عن أشياخ النخع قالوا: بعث النخع رجلين منهم إلى النبى صلى الله عليه وسلم وافدين بإسلامهم أرطاة بن شراحبيل بن كعب والجهيش واسمه الأرقم من بنى بكر عمرو بن عوف بن النخع فخرجا حتى قدما على رسول الله صلى الله عليه وسلم فعرض عليهما الإسلام فقبلا فبايعاه على قومهما وأعجب رسول الله صلى الله عليه وسلم شأنهما وحسن هيئتهما، فقال: " هل خلفتما ورائكما من فوقكما مثلكما؟ "، قالا: يارسول الله، قد خلفنا ورائنا من قومنا سبعين رجلا كلهم أفضل منا وكلهم يقطع الأمر وينفذ الأشياء ما يشاركوننا فى الأمر، إذا كان فدى لهما رسول الله صلى الله عليه وسلم ولقومهما بخير، وقال: " اللهم بارك فى النخع وعقد لأرطاة لواء.. " فذكر قصته.
** سعد
(الإصابة في تمييز الصحابة ١١٦/٢)
** قال الحافظ فى " الإصابة " ٢ / ١١٦: قال الذهبى فى التجريد يقال فيه الخزاعى ذكر فى حديث كأنه موضوع.','8','398',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3749,'<span data-type="title" id=toc-3757>٣٧٤٩ </span>- عن الحارث بن خزمة قال: بعث النبى صلى الله عليه وسلم يوم الإثنين.
** منده
(الإصابة في تمييز الصحابة ١٥٤/٢)
** إسناده ضعيف','8','399',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3750,'<span data-type="title" id=toc-3758>٣٧٥٠ </span>- عن الحارث بن قيس قال: سمعت رسول الله صلى الله عليه وسلم يوم الفتح يقول: " لا تغزى مكة بعد اليوم إلى يوم القيامة ".
** ت حب قط
(الإصابة في تمييز الصحابة ١٧٣/٢)
** قال الحافظ فى " الإصابة " ٢ / ١٧٣: صححه الترمذى وابن حبان.','8','400',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3751,'<span data-type="title" id=toc-3759>٣٧٥١ </span>- عن سعيد بن أبى عزيز، قال: قدم أبو عزيز جندب بن النعمان الأزدى على النبى صلى الله عليه وسلم فأسن إسلامه وجعله عريف قومه ثم هاجر إلى الشام فى خلافة عمر وسكن دمشق، وداره تعرف بدار النخلة ودفن فيها هو وابنه سعيد وابنه عمر بن سعيد،ثم تحول حفص بن عمرو بن سعيد إلى زملكان فسكنها.
** كر
(الإصابة في تمييز الصحابة ١٠٩/٢)
** إسناده غريب
** قال الحافظ فى " الإصابة " ٢ / ١٠٩: لا أعرف لرجاله ذكرا إلا فى هذا الخبر.','8','401',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3752,'<span data-type="title" id=toc-3760>٣٧٥٢ </span>- عن جندب بن نضلة أنه أتى النبى صلى الله عليه وسلم فقال: يارسول الله كنت شامرا راجزا وكان لى صاحب من الجن فأتانى فدهمنى وقال: هب فقد لاح سراج الدين يصادق مهذب أمين فارحل على ناجية أمون تمشى على الصحصح والحزون فانتبهت فدعورا فقلت ماذا، قال: وساطع الأرض وفارض لقد بعث محمد فى الطول والعرض نشأ ف الحرمان العظام وهاجر إلى طيبة الأمينة، قال: فسرت فإذا أنا بهاتف يقول: ياأيها الراكب المزجى مطيته نحو الرسول لقد وفقت للرشد فإذا هو صاحب الجنى ... فذكر القصة إلى أن، قال: فعرض عليه الرسول صلى الله
عليه وسلم الإسلام فأسلم.
** أبو سعيد النيسابورى فى شرف المصطفى
(الإصابة في تمييز الصحابة ١١١/٢)
** حسن','8','402',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3753,'<span data-type="title" id=toc-3761>٣٧٥٣ </span>- عن ابن عباس قال: قام رجل من الأزد يقال له: جندب بن زهير الغامرى إلى رسول الله صلى الله عليه وسلم، وقال: بأبى وأمى إنى لأرجع من عندك فلم تقر عينى بمال ولا ولد حتى أرجع فانظر إليك، فأنى لى بك فى عمار القيامة..... فذكر حديثا طويلا فى أهوال يوم القيامة.
** على بن سعد فى الطاعى والمعصية
(الإصابة في تمييز الصحابة ١٠٣/٢)
** قال الحافظ فى " الإصابة " ٢ / ١٠٣: مقاتل ضعيف.','8','403',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3754,'<span data-type="title" id=toc-3762>٣٧٥٤ </span>- عن حارثة بن النعمان قال مررت على رسول الله صلى الله عليه وسلم ومعه جبرائيل جالس فى المقاعد فسلمت عليه فلما رجعت، قال: " هل رأيت الذى كان معى؟ "، قلت: نعم، قال: " فإنه جبريل وقد رد عليك السلام ".
** حم طب
(الإصابة في تمييز الصحابة ١٩٠/٢)
** إسناده صحيح','8','404',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3755,'<span data-type="title" id=toc-3763>٣٧٥٥ </span>- عن الحباب بن المنذر قال: أشرت على رسول الله صلى الله عليه وسلم برأيين فقبل منى خرجت معه فى غزاة بدر فذكر نحو ما تقدم، قال: وخير عند موته فاستشار أصحابه فقالوا: لقيس معنا فاستشارنى، فقلت: اختر يارسول الله حيث أختار ربك فقبل ذلك منى.
** شاهين
(الإصابة في تمييز الصحابة ١٩٧/٢)
** إسناده ضعيف','8','405',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3756,'<span data-type="title" id=toc-3764>٣٧٥٦ </span>- قال على: من ههنا من أصحاب رسول الله صلى الله عليه وسلم؟ ، فقام اثنا عشر رجلا منهم قيس بن ثابت وحبيب بن بديل بن ورقاء، فشهدوا أنهم سمعوا سمعوا رسول الله صلى الله عليه وسلم يقول: " من كنت مولاه فعلى مولاه ".
** ابن عقدة فى كتاب الموالاة
(الإصابة في تمييز الصحابة /٢)
** إسناده ضعيف','8','406',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3757,'<span data-type="title" id=toc-3765>٣٧٥٧ </span>- عن تميم قال: سئل النبى صلى الله عليه وسلم عن سبأ أرجلا كان أو امرأة الحديث.
** ابن مردوية ابن أبى خثيمة
(الإصابة في تمييز الصحابة ٤/٢)
** قال الحافظ فى " الإصابة " ٢ / ٤: قال ابن منده: هكذا رواه عبد الوهاب بن عبدة عن أبى عمرو عن الليث عنه قال: وأبو عمرو مجهول وقد رواه موسى عن أبيه عن يزيد بن الحصين مرسلا ليس فيه تميم اه. قلت: أخرجه ابن مردوية من طريق زيد بن الحباب عن موسى كذلك لكن أخرجه ابن أبى خثيمة عن عبد الوهاب بن نجدة عن عثمان بن كثير عن الليث عن موسى بن على عن يزيد بن حصين عن تميم الدارى أن رجلا فذكره ففيه تعقب على ابن منده من وجهين أحدهما قوله: أن أباعمرو مجهول فقد عرف أنه عثمان بن كثير، ثانيهما: قوله: يقال أنه تميم الدارى ولا يصح فقد صرح ابن أبى خثيمة أنه تميم الدارى ولو أنه روى مرسلا لا يقدح فى كون تميم المذكور هو الدارى والله أعلم. والحديث معروف لفروة بن مسيك، أخرجه الترمذى وروى مثله عن ابن عباس أشار إليه الترمذى ووصله ابن مردوية.','8','407',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3758,'<span data-type="title" id=toc-3766>٣٧٥٨ </span>- عن حبيب بن الحارثو أبى الفادية قالا: خرجنا مهاجرين ومعنا أم أبى الفادية فأسلموا، فقلت: يارسول الله أوصنى، قال: " إياك ومايسوء الأذن ".
** منده نعيم
(الإصابة في تمييز الصحابة ٢٠١/٢)
** قال الحافظ فى " الإصابة " ٢ / ٢٠١: أخرجه أبو نعيم مرسلا، والعاص مجهول.','8','408',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3759,'<span data-type="title" id=toc-3767>٣٧٥٩ </span>- عن حبيب بن حبيب، قال: شهدت رسول الله صلى الله عليه وسلم قال لحسان ابن ثابت: " قل فى أبى بكر شيئا ".
** ك
(الإصابة في تمييز الصحابة ٢٠٢/٢)
** قال الحافظ فى " الإصابة " ٢ / ٢٠٢: الراوى عن غالب متروك، وقال ل العقيلى: غالب هذا إسناده مجهول اه.','8','409',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3760,'<span data-type="title" id=toc-3768>٣٧٦٠ </span>- عن محمد بن حبيب بن خراس عن أبيه أنه سمع النبى صلى الله عليه وسلم يقول: " المسلمون أخوة........ " الحديث.
** منده
(الإصابة في تمييز الصحابة ٢٠٣/٢)
** إسناده متروك','8','410',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3761,'<span data-type="title" id=toc-3769>٣٧٦١ </span>- عن عبد الله بن حبيب الكندى يقول عن أبيه: سألت النبى صلى الله عليه وسلم ما للمرأة من زوجها إذا مات؟ ، قال: " لها الربع إذا لم يكن لها ولد ".
** موسى الإسماعيلى
(الإصابة في تمييز الصحابة ٢٠٤/٢)
** قال الحافظ فى " الإصابة " ٢ / ٢٠٤: على بن قرين أحد المتروكين.','8','411',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3762,'<span data-type="title" id=toc-3770>٣٧٦٢ </span>- عن حبيب بن زيد أنه سأل النبى صلى الله عليه وسلم عن الوضوء، الحديث.
** الإسماعيلى
(الإصابة في تمييز الصحابة ٢٠٤/٢)
** قال الحافظ فى " الإصابة " ٢ / ٢٠٤: عبد الرحمن بن عمرو بن جبلة أحد المتروكين.
** تعقيب: * فى ترجمة عبد الرحمن بن عمرو بن جبلة. فى ميزان الإعتدال قال الذهبى: قال أبو حاتم: كان يكذب وضربت على حديثه اه وقال الدارقطنى: متروك يضع الحديث اه. وفى لسان الميزان قال ابن حجر: قال أبو القاسم البغوى فى معجم الصحابة: ضعيف الحديث جدا اه.','8','412',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3763,'<span data-type="title" id=toc-3771>٣٧٦٣ </span>- عن حبيب بن الضحاك أن رسول الله صلى الله عليه وسلم قال: " أتانى جبريل فقال: رأيت رحما معلقة بالعرش تدعو على من قطعها، قلت: كم بينهما؟ قال: خمسة عشر أبا ".
** نعيم
(الإصابة في تمييز الصحابة ٢٠٤/٢)
** إسناده مجهول
** قال الحافظ فى " الإصابة " ٢ / ٢٠٥: أظنه مرسلا.','8','413',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3764,'<span data-type="title" id=toc-3772>٣٧٦٤ </span>- عن حبيب بن عمرو _ وكان قد بايع النبى صلى الله عليه وسلم _ أنه كان إذا مر على قوم قال: السلام عليكم.
** عبدان
(الإصابة في تمييز الصحابة ٢٠٦/٢)
** رجاله ثقات','8','414',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3765,'<span data-type="title" id=toc-3773>٣٧٦٥ </span>- عن حبيب بن أبى مرضية أن النبى صلى الله عليه وسلم نزل منزلا بخيبر فقيل له: انتقل فإنه وبىء.
** عبدان
(الإصابة في تمييز الصحابة ٢٠٨/٢)
** قال الحافظ فى " الإصابة " ٢ / ٢٠٨: قال عبدان: لا يعرف له صحبة اه. قلت: لم يثق أبو موسى بسنده وقال فى التجريد: إنه منكر.','8','415',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3766,'<span data-type="title" id=toc-3774>٣٧٦٦ </span>- عن إبراهيم بن منبه بن الحجاج السلمى عن أبيه عن جده، قال: قال رسول الله صلى الله عليه وسلم: " من رأيتموه يذكر أبابكر وعمر بسوء فإنما يرتد عن الإسلام ".
** قانع
(الإصابة في تمييز الصحابة ٢١٦/٢)
** قال الحافظ فى " الإصابة " ٢ / ٢١٧: فى إسناده غير واحد من المجهولين.','8','416',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3767,'<span data-type="title" id=toc-3775>٣٧٦٧ </span>- عن أبى الأسود قال: دخل معاوية على عائشة معاتبته فى قتل حجر وأصحابه وقالت: سمعت رسول الله صلى الله عليه وسلم، يقول: " يقتل بعدى أناس يغضب الله لهم وأهل السماء ".
** يعقوب بن سفيان فى التاريخ
(الإصابة في تمييز الصحابة ٢١٨/٢)
** إسناده منقطع
** قال الحافظ فى " الإصابة " ٢ / ٢١٨: فى سنده انقطاع.','8','417',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3768,'<span data-type="title" id=toc-3776>٣٧٦٨ </span>- أن حجر بن عدى أصابته جنابة فقال للموكل به: أعطنى شرابى أتطهر به ولا تعطنى غدا شيئا، فقال: أخاف أن تموت عطشا فيقتلنى معاوية، قال: فدعا الله فانسكبت له سحابة بالماء فأخذ منها الذى احتاج إليه، فقال أصحابه: ادع الله أن يخلصنا، فقال: اللهم خر لنا، قال: فقتل هو وطائفة منهم.
** إبراهيم بن الجنيد فى كتاب الأولياء
(الإصابة في تمييز الصحابة ٢١٨/٢)
** إسناده منقطع','8','418',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3769,'<span data-type="title" id=toc-3777>٣٧٦٩ </span>- عن مخش بن حجير عن أبيه: أنه سمع النبى صلى الله عليه وسلم يقول فى حجة الوداع: " إن دماءكم وأمواكم وأعراضكم عليكم حرام.. " الحديث.
** طب منده
(الإصابة في تمييز الصحابة ٢٢٠/٢)
** إسناده صالح','8','419',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3770,'<span data-type="title" id=toc-3778>٣٧٧٠ </span>- عن أنس قال: كان معاذ يؤم قومه فدخل حرام وهو يريد أن يسقى نخله فصلى مع القوم فلما رأى معاذا يطول تجوز ولحق بنخله..... الحديث وفيه قوله صلى الله عليه وسلم: " أفتان أنت لا تطول بهم ".
** ن يع سكن
(الإصابة في تمييز الصحابة ٢٢٤/٢)
** صحيح','8','420',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3771,'<span data-type="title" id=toc-3779>٣٧٧١ </span>- عن ابن عمر، قال: كنت جالسا عند النبى صلى الله عليه وسلم فأتاه حرملة بن بن زيد الأنصارى فقال: يانبى الله الإيمان ههنا وأشار إلى لسانه والنفاق ههنا ووضع يده على صدره، فقال: " اللهم اجعل حرملة لسانا صادقا " الحديث.
** طب منده
(الإصابة في تمييز الصحابة ٢٢٧/٢)
** إسناده لابأس به
** قال الحافظ فى " الإصابة " ٢ / ٢٢٧: ورويناه فى فوائد هشام بن عمار رواية أحمد بن سليمان بن زبان من حديث أبى الدرداء نحوه.','8','421',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3772,'<span data-type="title" id=toc-3780>٣٧٧٢ </span>- عن حزامة بن نعيم، قال: أتيت النبى صلى الله عليه وسلم بتبوك فى جماعة وهو نازل فقال: " عرفوا عليكم عرفاء وأدوا زكاتكم فلا دين إلا بزكاة " فقال أبو يزيد اللقيطى: وما الزكاة يارسول الله؟ قال: " زكاة الرقاب وزكاة الأموال ".
** منده
(الإصابة في تمييز الصحابة ٢٣٤/٢)
** إسناده فيه من لايعرف','8','422',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3773,'<span data-type="title" id=toc-3781>٣٧٧٣ </span>- عن الحسحاس بن فضيل الحنظلى، قال: قال رسول الله صلى الله عليه وسلم: " ليس منكم أحد إلا وله منزلان أحدهما فى الجنة والأخر فى النار....." الحديث.
** أبو إسحاق بن ثابت فى تاريخ هراة
(الإصابة في تمييز الصحابة ٢٤١/٢)
** رجال إسناده مجاهيل
** قال الحافظ فى " الإصابة " ٢ / ٢٤١: هو من رواية خالد بن هياج وهو متروك.','8','423',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3774,'<span data-type="title" id=toc-3782>٣٧٧٤ </span>- روى السراج فى تاريخه عن عكرمة أن والد حذيفة بن اليمان قتل يوم أحد قتله رجل من المسلمين، وهو يظن أنه من المشركين فوداه رسول الله صلى الله عليه وسلم.
** السراج فى التاريخ
(الإصابة في تمييز الصحابة ٢٤٧/٢)
** رجاله ثقات
** قال الحافظ فى " الإصابة " ٢ / ٢٤٧: رجاله ثقات مع إرساله، وله شاهد أخرجه أبو إسحاق الغزارى فى كتاب السير.','8','424',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3775,'<span data-type="title" id=toc-3783>٣٧٧٥ </span>- عن حسيل بن عرفطة أن النبى صلى الله عليه وسلم قال: " إذا قمت فى الصلاة فقل بسم الله الرحمن الرحيم، الحمد لله رب العالمين، حتى تختمها...... "، الحديث.
** قط
(الإصابة في تمييز الصحابة ٢٤٨/٢)
** رجال الإسناد لا يعرفون','8','425',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3776,'<span data-type="title" id=toc-3784>٣٧٧٦ </span>- عن الحسين قال: سمعت رسول الله صلى الله عليه وسلم يقول: " ما من مسلم تصيبه مصيبة وإن قدم عهدها فيحدث لها استرجاعا إلا أعطاه الله ثواب ذلك ".
** جه يع
(الإصابة في تمييز الصحابة ٢٤٨/٢)
** إسناده ضعيف
** قال الحافظ فى " الإصابة " ٢ / ٢٤٩: فى إسناده ضعف.','8','426',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3777,'<span data-type="title" id=toc-3785>٣٧٧٧ </span>- عن الحسين بن على قال: أتيت عمر بن الخطاب وهو يخطب على المنبر فصعدت إليه فقلت: انزل عن منبر أبى واذهب إلى منبر أبيك، فقال عمر: لم يكن لأبى منبر فأجلسنى معه أقلب حصى بيدى، فلما نزل انظلق بى إلى منزله فقال لى: من علمك؟ ، قلت: والله ماعلمنى أحد، قال: بأبى لو جعلت تغشانا قال: فأتيته يوما وهو خال بمعاوية وابن عمر بالباب فرجع ابن عمر فرجعت معه فلقينى بعد فقال لى: لم أرك؟ ، قلت: ياأمير المؤمنين إنى جئت وأنت خال بمعاوية فرجعت مع ابن عمر فقال: أنت أحق من ابن عمر فإنما أنبت ما ترى فى رؤوسنا الله ثم أنتم.
** خط
(الإصابة في تمييز الصحابة ٢٤٩/٢)
** سنده صحيح','8','427',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3778,'<span data-type="title" id=toc-3786>٣٧٧٨ </span>- عن حصين بن جندب قال: كنا مع النبى صلى الله عليه وسلم فشكى إليه قوم فقالوا: إنا نمنا حتى طلعت الشمس فأمرهم أن يؤذنوا ويقيموا.
** منده
(الإصابة في تمييز الصحابة ٢٥٤/٢)
** إسناده فيه من لايعرف','8','428',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3779,'<span data-type="title" id=toc-3787>٣٧٧٩ </span>- عن عمران بن حصين أن حصينا أتى النبى صلى الله عليه وسلم قبل أن يسلم الحديث، وفيه ثم إن حصينا أسلم.
** حم ن
(الإصابة في تمييز الصحابة ٢٥٧/٢)
** إسناده صحيح','8','429',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3780,'<span data-type="title" id=toc-3788>٣٧٨٠ </span>- عن عمران بن حصين عن أبيه أنه أتى النبى صلى الله عليه وسلم فقال: يا محمد كان المطلب خيرا لقومك منك.... الحديث وفيه فلما أراد أن ينصرف قال: ما أقول؟ ، قال: " قل: اللهم قنى شر نفسى، واعزم لى على أرشد أمرى "، فانطلق ولم يكن أسلم ثم إنه أسلم فقال: يارسول الله فما أقول الأن حين أسلمت، قال: " قل اللهم قنى شر نفسى واعزم لى على أرشد أمرى اللهم اغفر لى ما أسررت وما أعلنت وما أخطأت وما عمدت وما علمت وما جهلت "، وفى رواية النسائى: فما أقول الأن وأنا مسلم.
** ن
(الإصابة في تمييز الصحابة ٢٥٧/٢)
** إسناده صحيح
** قال الحافظ فى " الإصابة " ٢ / ٢٥٧: سنده صحيح من الطريقين.','8','430',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3781,'<span data-type="title" id=toc-3789>٣٧٨١ </span>- عن حصين بن مشمت، أنه وفد إلى النبى صلى الله عليه وسلم فبايعه بيعة الإسلام وصدق إليه صدقة ماله وأقطعه النبى صلى الله عليه وسلم وشرط عليه أن لا يمنع ماءه ولا يمنع فضله.
** تخ عاصم الحسن بن سفيان شاهين طب الضياء فى المختارة
(الإصابة في تمييز الصحابة ٢٥٩/٢)
** قال الحافظ فى " الإصابة " ٢ / ٢٦٠: أكثر رواته غير معروفين لكن قد صححه ابن خزيمة.','8','431',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3782,'<span data-type="title" id=toc-3790>٣٧٨٢ </span>- عن حصين أنه سمع النبى صلى الله عليه وسلم يقول: " ما من والى عشرة إلا جاء يوم القيامة مغلولا معذبا أو مفقورا ".
** منده
(الإصابة في تمييز الصحابة ٢٦٣/٢)
** إسناده منقطع','8','432',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3783,'<span data-type="title" id=toc-3791>٣٧٨٣ </span>- عن أبى وائل قال: وفد بنو أسد فقال لهم النبى صلى الله عليه وسلم: " من أنتم؟ "، قالوا: نحن بنو الزنية أحلاس الخيل، قال: " بل أنتم بنو الرشدة "، فقالوا: لا ندع إسم أبينا.
** عمر بن سنة
(الإصابة في تمييز الصحابة ٢٦٤/٢)
** إسناده صحيح','8','433',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3784,'<span data-type="title" id=toc-3792>٣٧٨٤ </span>- عن جعير بن عبد الله بن الحكم قال: رأنى الحكم وأنا غلام أكل من ههنا ومن ههنا فقال: ياغلام هكذا يأكل الشيطان إن النبى صلى الله عليه وسلم كان إذا أكل لم يعد أصابعه مابين يديه.
** نعيم
(الإصابة في تمييز الصحابة ٢٦٩/٢)
** إسناده ضعيف','8','434',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3785,'<span data-type="title" id=toc-3793>٣٧٨٥ </span>- عن عبد الرحمن بن أبى بكر، قال: كان الحكم بن أبى العاص يجلس عند النبى صلى الله عليه وسلم فإذا تكلم اختلج فبصر به النبى صلى الله عليه وسلم فقال: " كن كذلك ". فما زال يختلج حتى مات.
** طب هق فى الدلائل
(الإصابة في تمييز الصحابة ٢٧٢/٢)
** إسناده فيه نظر
** قال الحافظ فى " الإصابة " ٢ / ٢٧٢: أخرجه البيهقى فى الدلائل من هذا الوجه وفيه ضرار بن صرد وهو منسوب للرفض.','8','435',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3786,'<span data-type="title" id=toc-3794>٣٧٨٦ </span>- لفظ المتن: " الإثنان فما فوقهما جماعة ".
** ابن أبى خثيمة
(الإصابة في تمييز الصحابة ٢٧٤/٢)
** منكر
** قال الحافظ فى " الإصابة " ٢ / ٢٧٤: الحكم بن عمير الثمالى: قال ابن أبى حاتم عن أبيه: روى عن النبى صلى الله عليه وسلم أحاديث منكرة يرويها عيسى بن إبراهيم _ وهو ضعيف _، عن موسى بن أبى حبيب _ وهو ضعيف _، عن عمه الحكم. قال الحافظ فى " الإصابة " ٢ / ٢٧٤: ووجدت له راويا غير موسى ووجدت لعيسى متابعا عن موسى أخرجه ابن السكن.','8','436',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3787,'<span data-type="title" id=toc-3795>٣٧٨٧ </span>- وفى الصحيح أنه صلى الله عليه وسلم قال: " من دخل دار حكيم بن حزام فهو آمن ".
** لم
(الإصابة في تمييز الصحابة ٢٧٨/٢)
** صحيح','8','437',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3788,'<span data-type="title" id=toc-3796>٣٧٨٨ </span>- عن أبى هريرة، قال: بينما النبى صلى الله عليه وسلم جالس فى عدة من أصحابه إذ أقبل شيخ كبير يتوكأ على عكازه فسلم على النبى صلى الله عليه وسلم وأصحابه فردوا عليه، فقال: " اجلس ياحماد فإنك على خبر " فسأله عن ذلك فقال: " إذا بلغ العبد أربعين أمنه الله من الخصال الثلاثة.. " الحديث بطوله.
** أبو موسى
(الإصابة في تمييز الصحابة ٢٨٢/٢)
** قال الحافظ فى " الإصابة " ٢ / ٢٨٢: القطان بن عمار بن ياسر أحد الضعفاء.','8','438',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3789,'<span data-type="title" id=toc-3797>٣٧٨٩ </span>- عن يزيد بن نعيم أن رجلا من أسلم يقال له عبيد، قال: وقع عمى على وليدة فحملت فعلام يقال له حمام، وذلك فى الجاهلية، فأتى النبى صلى الله عليه وسلم فكلمه فى ابنه، فقال له: " خذ ابنك " فأخذه فجاء مولى الوليدة فعرض رسول الله صلى الله عليه وسلم غلامين، فقال: خذ أحدهما أو دع للرجل ابنه فأخذ غلاما اسمه رافع وترك له ابنه فقال رسول الله صلى الله عليه وسلم: " أيما رجل عرف ابنه فأخذه ففكاك رقبة ".
** طب الباوردى بفى بن خالد والطبرى فى تهذيب الآثار
(الإصابة في تمييز الصحابة ٢٨٣/٢)
** إسناده حسن','8','439',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3790,'<span data-type="title" id=toc-3798>٣٧٩٠ </span>- عن حميد بن ثور أنه حين أسلم أتى النبى صلى الله عليه وسلم فقال: أصبح قلبى من تسليمى مقصدا، إن خطأ منها وإن تعمدا، فى أبيات يقول فيها: حتى أتيت المصطفى محمدا يتلوا من الله كتابا مرشدا.
** شاهين الخطابى فى الغريب
(الإصابة في تمييز الصحابة ٢٨٩/٢)
** عق الأزدى فى الضعفاء طب
** قال الحافظ فى " الإصابة " ٢ / ٢٩٠: يعنى ضعيف متروك.','8','440',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3791,'<span data-type="title" id=toc-3799>٣٧٩١ </span>- عن حميد بن عبد يغوث سمع النبى صلى الله عليه وسلم يقول: " أبو بكر أخر وأنا أخوه ".
** منده
(الإصابة في تمييز الصحابة ٢٩١/٢)
** قال الحافظ فى " الإصابة " ٢ / ٢٩١: عبد الرحمن ضعيف جدا.','8','441',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3792,'<span data-type="title" id=toc-3800>٣٧٩٢ </span>- عن حنطب بن الحارث قال: سمعت رسول الله صلى الله عليه وسلم يقول: " أبو بكر وعمر من الدين بمنزلة السمع والبصر ".
** الباوردى
(الإصابة في تمييز الصحابة ٢٩٥/٢)
** قال الحافظ فى " الإصابة " ٢ / ٢٩٥: قال أبو عمر: ليس له غيره اه. قلت: لكن اختلفا فى إسناده اختلافا كثيرا.','8','442',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3793,'<span data-type="title" id=toc-3801>٣٧٩٣ </span>- عن حذيفة، قال لحذيم: اجمع لى بنى فأوصاهم، فقال: إن ليتيمى الذى فى حجرى مائة من الإبل، فقال حذيم: ياأبت إنى سمعت بنيك يقولون: إنما نقر بهذا لتقر عين أبينا، فإذا مات رجعنا، فارتفعوا إلى رسول الله صلى الله عليه وسلم، فجاء حنيفة وحذيم ومن معهما، ومعهم حنظلة وهو غلام وهو وهو رديف أبيه حذيم، فقص حنيفة على النبى صلى الله عليه وسلم قصته قال: فغضب النبى صلى الله عليه وسلم فجثى على ركتبيه، وقال: " لا بل الصدقة: خمس وإلا فعشر، وإلا فعشرون وإلا فثلاثون، فإن أكثرت فأربعون " قال: فودعوه ومع اليتيم هراوة، فقال النبى صلى الله عليه وسلم: " عظمت هذه هراوة يتيم "، فقال حذيم: إن لى بنين ذوى لحى وإن هذا أصغرهم يعنى حنظلة فادع الله له فمسح رأسه وقال: " بارك الله فيك "، _ أو قال: بورك فيك _ قال الذيال: فلقد رأيت حنظلة يؤتى بالإنسان الوارم وجهه، فيتفل على يديه ويقول: بسم الله ويضع يده على رأسه موضع كف رسول الله صلى الله عليه وسلم فيمسحه ثم يمسح موضع الورم فيذهب الورم.
** طب
(الإصابة في تمييز الصحابة ٢٩٥/٢)
** إسناده منقطع
** قال الحافظ فى " الإصابة " ٢ / ٢٩٦: ورواه الحسن بن سفيان فى مسنده من وجه آخر، ورواه الطبرانى بطوله منقطعا، ورواه أبو يعلى من هذا الوجه وليس بتمامه.','8','443',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3794,'<span data-type="title" id=toc-3802>٣٧٩٤ </span>- عن جبلة بن سحيم: صليت خلف حنظلة الأنصارى إمام مسجد قباء من أصحاب النبى صلى الله عليه وسلم فقرأ سورة مريم فلما جاءت السجدة سجد.
** البخارى فى الصحابة
(الإصابة في تمييز الصحابة ٢٩٦/٢)
** إسناده صحيح','8','444',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3795,'<span data-type="title" id=toc-3803>٣٧٩٥ </span>- عن عروة قال:استأذن حنظلة بن أبى عامر وعبد الله بن أبى بن سلول رسول الله صلى الله عليه وسلم فى قتل أبويهما فنهاهما عن ذلك.
** شاهين
(الإصابة في تمييز الصحابة ٢٩٩/٢)
** إسناده حسن','8','445',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3796,'<span data-type="title" id=toc-3804>٣٧٩٦ </span>- عن حنظلة العثيمى، وكان من أصحاب النبى صلى الله عليه وسلم قال: " ما من قوم جلسوا مجلسا يذكرون الله إلا ناداهم مناد من السماء قوموا فقد غفرت لكم وبدلت سيئاتكم ".
** العسكرى
(الإصابة في تمييز الصحابة ٣٠٠/٢)
** إسناده ضعيف
** قال الحافظ فى " الإصابة " ٢ / ٣٠١: فى إسناده إلى قتادة ضعف.','8','446',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3797,'<span data-type="title" id=toc-3805>٣٧٩٧ </span>- عن حوشب قال: سمعت النبى صلى الله عليه وسلم يقول: " لو كان جريج فقيها عالما لعلم أن إجابة دعاء أمه أولى من عبادة ربه عز وجل ".
** الحسن بن سفيان الحكيم الترمذى فى النوادر
(الإصابة في تمييز الصحابة ٣٠٢/٢)
** قال الحافظ فى " الإصابة " ٢ / ٣٠٢: قال ابن مندة: غريب تفرد به الحكم بن الريان عن الليث اه.','8','447',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3798,'<span data-type="title" id=toc-3806>٣٧٩٨ </span>- عن علي أو يهوديا كان يقال له: حير نجره كان له على رسول الله صلى الله عليه وسلم دنانير فتقاضاه، فقال: " ما عندى ماأعطيك "، قال: إذا لا أفارقك حتى تعطينى فجلس ممعه فلامه أصحابه، فقال: " منعنى ربى أن أظلم معاهدا "، فلما دخل النهار أسلم اليهودى، وجعل شطر ماله فى سبيل الله، الحديث.
** ك أبو سعيد فى شرف المصطفى هق فى الدلائل
(الإصابة في تمييز الصحابة ٣٠٨/٢)
** قال الحافظ فى " الإصابة " ٢ / ٣٠٨: أبو على بن الأشعث أحد الضعفاء.','8','448',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3799,'<span data-type="title" id=toc-3807>٣٧٩٩ </span>- عن حبة بن جوين قال: لما كان يوم غديرخم دعا النبى صلى الله عليه وسلم الصلاة جامعة، فذكر الحديث: " من كنت مولاه فعلى مولاه "، قال: فأخذ بيد على حتى نظرت إلى آباطهما وأنا يومئذ مشرك.
** ابن عقده فى كتاب الموالاة
(الإصابة في تمييز الصحابة ٣١٩/٢)
** إسناده ضعيف جدا
** قال الحافظ فى " الإصابة " ٢ / ٣١٩: السند ضعيف وحبه اتفقوا على ضعفه إلا العجلى فوثقه ومشاه أحمد وقال: صالح جزره وسط.','8','449',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3800,'<span data-type="title" id=toc-3808>٣٨٠٠ </span>- عن حبة العرنى، قال: لما أمر رسول الله صلى الله عليه وسلم بسد الأبواب التى فى المسجد شق عليهم قال حبة: إنى لأنظر إلى حمزة بن عبد المطلب وهو تحت قطيفة حمراء وعيناه تذرفان وهو يقول: أخرجت عمك، الحديث.
** ابن مردويه
(الإصابة في تمييز الصحابة ٣٢٠/٢)
** إسناده ضعيف
** قال الحافظ فى " الإصابة " ٢ / ٣٢٠: الإسناد إلى أبان ضعيف، ومسلم الملائى ضعيف.','8','450',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3801,'<span data-type="title" id=toc-3809>٣٨٠١ </span>- عن أبى وائل، قال: رأى عمرو بن شرحبيل أنه أدخل الجنة فإذا قباب مضروبة فقلت: لمن هذه؟ ، قالوا: لذى الكلاع وهو شب، قلت: فأين عمارة؟ ، قال: أمامك، قلت: وكيف قتل بعضهم بعضا؟ ، قال: إنهم لغوا الله فوجدوه واسع المغفرة.
** يعقوب بن سفيان إبراهيم بن ديزيل فى كتاب صفين هق فى الدلائل
(الإصابة في تمييز الصحابة ١٩/٣)
** إسناده صحيح','8','451',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3802,'<span data-type="title" id=toc-3810>٣٨٠٢ </span>- عن الحارث بن بدل، قال: شهدت رسول الله صلى الله عليه وسلم يوم حنين فانهزم أصحابه، الحديث.
** بغوى مطين الباوردى شاهين
(الإصابة في تمييز الصحابة ٢٢/٣)
** قال الحافظ فى " الإصابة " ٣ / ٢٢: وهكذا رواه بكر بن بكار عن محمد بن عبد الله لكن، قال: الحارث بن سليم بن بدل وقال _ مرة _: عبد الله بن الحارث بن بدل، وقال الوليد بن مسلم: عن الشعبى، عن الحارث بن بدل عن رجل من قومه. وتابعه صدقة بن خالد، وقال القاسم بن يزيد الجرمى: عن الشعبى عن الحارث بن الحارث بن بدل عن سهيل الثقفى عن النبى صلى الله عليه وسلم. قال البغوى: وقد روى أن الحارث بن بدل رواه عن عمرو بن سفيان الثقفى، عن النبى صلى الله عليه وسلم، قال ابن عبد البر: لا يصح الحديث لكثرة اضطراب الشعبى فيه اه. قال أبو حاتم: الحارث مجهول: والشعبى لم يلق أحدا من الصحابة.','8','452',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3803,'<span data-type="title" id=toc-3811>٣٨٠٣ </span>- عن عبد الله بن أمية، أن رسول الله صلى الله عليه وسلم مر فى بعض مغازيه بناس من مزينة فتبعه عبد امرأة منهم.... الحديث، فى أمره العبد باستئذان سيدته.
** ك هق
(الإصابة في تمييز الصحابة ٢٦/٣)
** مرسل
** قال الحافظ فى " الإصابة " ٣ / ٢٦: قال الحاكم: صحيح الإسناد اه. وخفى عليه أن الحارث لا صحبة له، وأخرجه البيهقى عن الحاكم ولم ينبه على إرساله.','8','453',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3804,'<span data-type="title" id=toc-3812>٣٨٠٤ </span>- عن حبة بن مسلم، قال: قال رسول الله صلى الله عليه وسلم: " ملعون من لعب بالشطرنج ".
** ابن حزم عبدان
(الإصابة في تمييز الصحابة ٣٠/٣)
** قال الحافظ فى " الإصابة " ٣ / ٣١: قال ابن حزم: حبة مجهول والإسناد منقطع اه. وقال ابن القطان: حبة مجهول اه. قال: وقيل انه حبة بن سلمة أخو شقيق بن سلمة وهو لا يعرف أيضا اه.
** تعقيب: الضعيفة للألبانى (١١٤٥) : موضوع.','8','454',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3805,'<span data-type="title" id=toc-3813>٣٨٠٥ </span>- عن حسان بن أبى سنان، قال: قال رسول الله صلى الله عليه وسلم: " طالب العلم بين الجهال كالحى بين الأموات ".
** على بن سعيد العسكرى فى الصحابة
(الإصابة في تمييز الصحابة ٣٨/٣)
** مرسل
** قال الحافظ فى " الإصابة " ٣ / ٣٨: حسان بن أبى سنان أحد زهاد التابعين أرسل حديثا. قال ابن حبان فى الثقات: لا أعرف له حديثا مسندا.','8','455',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3806,'<span data-type="title" id=toc-3814>٣٨٠٦ </span>- عن حسان بن عبد الرحمن الضبعى قال: قال رسول الله صلى الله عليه وسلم: " لو اغتسلتم من المذى لكان أشد عليكم من الحيض ".
** العسكرى فى الصحابة
(الإصابة في تمييز الصحابة ٣٨/٣)
** مرسل
** قال الحافظ فى " الإصابة " ٣ / ٣٨: تابعى أرسل حديثا. قال البخارى وابن أبى حاتم وابن حبان: حديثه مرسل.','8','456',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3807,'<span data-type="title" id=toc-3815>٣٨٠٧ </span>- عن حطيم الحدانى قال: قال رسول الله صلى الله عليه وسلم: " بشر المشائين إلى المسجد فى الظلم بالنور التام يوم القيامة ".
** أبو موسى
(الإصابة في تمييز الصحابة ٤٠/٣)
** مرسل
** قال الحافظ فى " الإصابة " ٣ / ٤٠: حطيم الحدانى تابعى أرسل حديثا.','8','457',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3808,'<span data-type="title" id=toc-3816>٣٨٠٨ </span>- عن حفص بن أبى جبلة مولاهم، عن النبى صلى الله عليه وسلم فى قوله تعالى (يأيها الرسول كلوا من الطيبات) الآية، قال: " ذلك عيسى بن مريم يأكل من غزل أمه ".
** عبدان
(الإصابة في تمييز الصحابة ٤٠/٣)
** مرسل
** قال الحافظ فى " الإصابة " ٣ / ٤٠: حفص بن أبى جبلة تابعى أرسل حديثا.','8','458',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3809,'<span data-type="title" id=toc-3817>٣٨٠٩ </span>- عن حوشب قال: كان رسول الله صلى الله عليه وسلم يقول فى دعائه: " اللهم إنى أعوذ بك من دنيا تمنع خير الآخرة....... " الحديث.
** ابن أبى الدنيا
(الإصابة في تمييز الصحابة ٤٣/٣)
** مرسل
** قال الحافظ فى " الإصابة " ٣ / ٤٣: حوشب تابعى أرسل حديثا.','8','459',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3810,'<span data-type="title" id=toc-3818>٣٨١٠ </span>- عن الحسن البصرى مرسلا: كانوا يرجون فى حمى ليلة كفارة لما مضى من الذنوب.
** ابن أبى الدنيا
(الإصابة في تمييز الصحابة ٤٣/٣)
** مرسل','8','460',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3811,'<span data-type="title" id=toc-3819>٣٨١١ </span>- عن خارجة بن جزء العذرى قال: سمعت رجلا يقول يوم تبوك: يا رسول الله أتباضع أهل الجنة.. الحديث.
** سكن منده هب خط فى المؤتلف
(الإصابة في تمييز الصحابة ٤٦/٣)
** إسناده ضعيف
** قال الحافظ فى " الإصابة " ٣ / ٤٦: فى إسناده ضعف.','8','461',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3812,'<span data-type="title" id=toc-3820>٣٨١٢ </span>- عن خالد بن خلاد عن النبى صلى الله عليه وسلم أنه، قال: " من أخاف أهل المدينة أخافه الله وعليه لعنة الله وغضبه إلى يوم القيامة لا يقبل منه صرف ولا عدل ".
** المحاملى فى الأمالى
(الإصابة في تمييز الصحابة ٥٥/٣)
** قال الحافظ فى " الإصابة " ٣ / ٥٥: المعروف برواية هذا المتن السائب بن خلاد الأنصارى وموسى بن عبيدة ضعيف.','8','462',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3813,'<span data-type="title" id=toc-3821>٣٨١٣ </span>- عن خالد بن رافع أن رسول الله صلى الله عليه وسلم قال لابن مسعود: " لا تكثر همك ما يقدر يكن وما ترزق يأتك ".
** منده
(الإصابة في تمييز الصحابة ٥٥/٣)
** قال الحافظ فى " الإصابة " ٣ / ٥٥: الإضطراب فيه من عياش بن عباس فإنه ضعيف.','8','463',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3814,'<span data-type="title" id=toc-3822>٣٨١٤ </span>- عن خالد بن زيد بن حارثة قال: قال رسول الله صلى الله عليه وسلم: " برىء من الشح من أتى الزكاة، وقرى الضيف وأعطى فى النائبة ".
** يع طب
(الإصابة في تمييز الصحابة ٥٨/٣)
** إسناده حسن','8','464',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3815,'<span data-type="title" id=toc-3823>٣٨١٥ </span>- عن خالد بن يزيد المدنى _ وكانت له صحبة _، أن رسول الله صلى الله عليه وسلم، قال: " ما من أهل بيت يروح عليهم تالد من الغنم إلا صلت عليهم الملائكة ".
** نعيم
(الإصابة في تمييز الصحابة ٥٨/٣)
** إسناده واه جدا','8','465',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3816,'<span data-type="title" id=toc-3824>٣٨١٦ </span>- عن خالد بن عبيد الله بن الحجاج، أن رسول الله صلى الله عليه وسلم كان يدعو فيقول: " اللهم إنى أعوذ بك أن أظلم أو أظلم......... " الحديث.
** منده
(الإصابة في تمييز الصحابة ٦٤/٣)
** قال الحافظ فى " الإصابة " ٣ / ٦٤: قال ابن مندة: غريب.','8','466',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3817,'<span data-type="title" id=toc-3825>٣٨١٧ </span>- عن خالد بن عدى قال: سمعت رسول الله صلى الله عليه وسلم يقول: " من جاءه من أخيه معروف من غير إشراف ولا مسئلة، فليقبله ولا يرده، فإنما هو رزق ساقه الله تعالى إليه ".
** حم شب حارث يع طب
(الإصابة في تمييز الصحابة ٦٤/٣)
** إسناده صحيح
** قال الحافظ فى " الإصابة " ٣ / ٦٤: إسناده صحيح، السياق لأبى يعلى.','8','467',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3818,'<span data-type="title" id=toc-3826>٣٨١٨ </span>- عن خالد بن عمير قال: أتيت مكة والنبى صلى الله عليه وسلم بها فبعته رجل سراويل فوزن لى وأرجح.
** مسند الحسن بن سفيان
(الإصابة في تمييز الصحابة ٦٦/٣)
** رجاله ثقات
** قال الحافظ فى " الإصابة " ٣ / ٦٦: رجاله ثقات إلا أنه أختلف فيه على شعبة وعلى سماك، والمشهور أنه عن مخرفة العبدى.','8','468',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3819,'<span data-type="title" id=toc-3827>٣٨١٩ </span>- عن أبى هريرة، قال: نزلنا مع رسول الله صلى الله عليه وسلم منزلا فجعل الناس يمرون فيقول رسول الله صلى الله عليه وسلم: " من هذا "، فأقول: فلان، حتى مر خالد فقال: " من هذا؟ "، فقلت: خالد بن الوليد، فقال: " نعم عبد الله هذا سيف من سيوف الله ".
** ت
(الإصابة في تمييز الصحابة ٧١/٣)
** رجاله ثقات','8','469',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3820,'<span data-type="title" id=toc-3828>٣٨٢٠ </span>- عن خثيمة قال: أتى خالد بن الوليد رجل ومعن زق خمر، فقال: اللهم اجعله عسلا فصار عسلا. وفى رواية له من هذا الوجه: مر رجل بخالد، ومعه زق خمر، فقال: ما هذا؟ ، قال: خل، قال: جعله الله خلا، فنظروا فإذا هو خل، وقد كان خمرا.
** ابن أبى الدنيا
(الإصابة في تمييز الصحابة ٧٣/٣)
** إسناده صحيح','8','470',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3821,'<span data-type="title" id=toc-3829>٣٨٢١ </span>- عن خالد الخزاعى _ وكانت له صحبة وكان ممن بايع تحت الشجرة _، قال: جلس رسول الله صلى الله عليه وسلم يوما ... فذكر الحديث وفيه: " سألت الله ثلاثا فأعطانى اثنتين ومنعنى واحدة ".
** الحسن بن سفيان يع طب الطبرى
(الإصابة في تمييز الصحابة ٧٥/٣)
** رجاله ثقات','8','471',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3822,'<span data-type="title" id=toc-3830>٣٨٢٢ </span>- عن عبد الله بن السائب عن أبيه عن جده، قال: رأيت رسول الله صلى الله عليه وسلم متكئا على سرير يأكل قديدا ثم يشرب من فخارها.
** منده
(الإصابة في تمييز الصحابة ٧٧/٣)
** قال الحافظ فى " الإصابة " ٣ / ٧٧: قال ابن منده: غريب لا نعرفه إلا من هذا الوجه.','8','472',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3823,'<span data-type="title" id=toc-3831>٣٨٢٣ </span>- عن محمد بن عبد الله بن عطاء بن خباب عن أبيه عن جده قال: كنت جالسا عند أبى بكر الصديق فرأى طائرا، فقال: طوبى لهذا، قلت: أتقول هذا وأنت صديق رسول الله صلى الله عليه وسلم ... الحديث.
** منده
(الإصابة في تمييز الصحابة ٧٨/٣)
** قال الحافظ فى " الإصابة " ٣ / ٧٨: قال ابن منده: هذا حديث غريب لا نعرفه إلا من هذا الوجه اه.','8','473',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3824,'<span data-type="title" id=toc-3832>٣٨٢٤ </span>- عن أنس قال: اجتمع نساء النبى صلى الله عليه وسلم فجعل يقول الكلمة كما يقول الرجل عند أهله فقالت إحداهن: كأن هذا حديث خرافة، فقال: " أتدرين ما خرافة؟ ، إنه كان رجلا من بنى عذرة أصابته الجن، فكان فيهم حينا فرجع فجعل يحدث بأحاديث لا تكون فى الإنس فحدث أن رجلا من الجن كانت له أم فأمرته أن يتزوج......... " فذكر قصة طويلة.
** ابن أبى الدنيا فى ذم البغى
(الإصابة في تمييز الصحابة ٨٧/٣)
** قال الحافظ فى " الإصابة " ٣ / ٨٧: رجاله ثقات إلا الراوى له عن ثابت وهو سحيم بن معاوية يروى عنه عاصم بن على ما عرفته فليحرر رجاله.','8','474',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3825,'<span data-type="title" id=toc-3833>٣٨٢٥ </span>- عن الحارث بن الخزرج الأنصارى يقول: حدثنى أبى أنه سمع النبى صلى الله عليه وسلم ونظر إلى ملك الموت عند رأس رجل من الإنصار، فقال: " يا ملك الموت ارفق بصاحبى فإنه مؤمن "، فقال له: يامحمد طب نفسا وقر عينا فإنى بكل مؤمن رفيق.. الحديث بطوله.
** شاهين فى الجنائز منده بز عاصم طب قانع
(الإصابة في تمييز الصحابة ٩٣/٣)
** قال الحافظ فى " الإصابة " ٣ / ٩٣: عمرو بن شمر متروك.','8','475',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3826,'<span data-type="title" id=toc-3834>٣٨٢٦ </span>- عن الزهرى قال: كان خزيمة بن حكيم يأتى خديجة فى كل عام وكانت بينهما قرابة فأتاها فبعثته مع النبى صلى الله عليه وسلم ... فذكره مطولا.
** شاهين كر
(الإصابة في تمييز الصحابة ٩٦/٣)
** إسناده ضعيف جدا
** قال الحافظ فى " الإصابة " ٣ / ٩٧: ذكره ابن شاهين مطولا فى ورقتين وفيه غريب كثير وإسناده ضعيف جدا مع انقطاعه.','8','476',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3827,'<span data-type="title" id=toc-3835>٣٨٢٧ </span>- عن حبيب بن مخفنف قال: انتهيت إلى النبى صلى الله عليه وسلم يوم عرفة وهو يقول: " هل تعرفونها......... " الحديث.
** حم د ت ن جه
(الإصابة في تمييز الصحابة ٣٢/٣)
** قال الحافظ فى " الإصابة " ٣ / ٣٢: قال ابن عبد البر: روى حديثه عبد الكريم بن أبى المخارق ولا يصح، قال البغوى: عبد الكريم شيخ ابن جريج فيه هو وابن أبى المخارق أبو أمية المعلم البصرى وفى حديثه لين.','8','477',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3828,'<span data-type="title" id=toc-3836>٣٨٢٨ </span>- عن حجر العدوى أن النبى صلى الله عليه وسلم قال لعمر: " قد أخذنا زكاة العباس ".
** أبو موسى ت
(الإصابة في تمييز الصحابة ٣٥/٣)
** قال الحافظ فى " الإصابة " ٣ / ٣٥: وهم أبو موسى فيه وكأنه سقط فى نسخته عن على فظن حجرا صحابيا وإنما هو فى الترمذى عن حجر العدوى عن على وفى الإسناد مع ذلك علة غير هذه والله أعلم.','8','478',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3829,'<span data-type="title" id=toc-3837>٣٨٢٩ </span>- عن ياسين بن الحسن بن ياسين يقول: حججت سنة ست وأربعين ومائتين فذكر حديثا وفيه: فرأيت أعرابيا فى البادية اسمه حوط بن مرة بن علقمة فقلت له: هل سمعت من رسول الله صلى الله عليه وسلم شيئا؟ ، قال: نعم شهدت محمدا صلى الله عليه وسلم وقيل له: هل أتيت من طعام الجنة بشىء فقال: " نعم، أتانى جبريل بخبيصة من خبيص الجنة فأكلتها ".
** أبو عبد الرحمن السلمى فى كتاب الأطعمة
(الإصابة في تمييز الصحابة ٤٤/٣)
** موضوع
** قال الحافظ فى " الإصابة " ٣ / ٤٤: أحمد بن نصر الدارع أحدالكذابين.','8','479',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3830,'<span data-type="title" id=toc-3838>٣٨٣٠ </span>- عن خالد بن حكيم بن حزام قال: كان أبو عبيد أميرا بالشام فتناول بعض أهل الأرض فقام إليه خالد فكلمه فقالوا: أغضبت الأمير، فقال: أما إنى لم أرد أن أغضبه ولكنى سمعت رسول الله صلى الله عليه وسلم يقول: " أن أشد الناس عذابا يوم القيامة أشدهم عذابا للناس فى الدنيا ".
** عاصم بغوى
(الإصابة في تمييز الصحابة ٥٣/٣)
** معلول','8','480',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3831,'<span data-type="title" id=toc-3839>٣٨٣١ </span>- عن خالد بن سلمة أن النبى صلى الله عليه وسلم أعتق غلاما، فقال: " ولاؤه لك ".
** قانع
(الإصابة في تمييز الصحابة ٦٠/٣)
** قال الحافظ فى " الإصابة " ٣ / ٦٠: عمر بن الحسن الأشنانى وهو أحد الضعفاء','8','481',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3832,'<span data-type="title" id=toc-3840>٣٨٣٢ </span>- عن خزيمة بن معمر الأنصارى قال: رجمت امرأة فى عهد النبى صلى الله عليه وسلم، فقال النبى صلى الله عليه وسلم: " هو كفارة لذنوبها ".
** سكن شاهين
(الإصابة في تمييز الصحابة ٩٨/٣)
** قال الحافظ فى " الإصابة " ٣ / ٩٨: قال ابن السكن: فى حديثه نظر اه. قال ابن السكن: تفرد به المنكدر وهو ضعيف اه. قلت: وقد خالفه أسامة بن زيد، فرواه عن ابن المنكدر عن ابن خزيمة بن ثابت عن أبيه، وهذا أشبه وفيه اختلاف آخر.','8','482',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3833,'<span data-type="title" id=toc-3841>٣٨٣٣ </span>- عن الخشخاش بن الحارث قال: أتيت النبى صلى الله عليه وسلم ومعى ابن لى فقال: " ابنك هذا؟ "، قلت: نعم، " لا يجنى عليك ولا تجنى عليه ".
** حم جه
(الإصابة في تمييز الصحابة ٩٨/٣)
** إسناده لا بأس به','8','483',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3834,'<span data-type="title" id=toc-3842>٣٨٣٤ </span>- عن الخشخاش قال: قال رسول الله صلى الله عليه وسلم: " ليس أحد منكم إلا وله منزلان: أحدهما فى الجنة والآخر فى النار.... " الحديث.
** لم
(الإصابة في تمييز الصحابة ٩٩/٣)
** قال الحافظ فى " الإصابة " ٣ / ٩٩: خالد بن هياج أحد الضعفاء.','8','484',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3835,'<span data-type="title" id=toc-3843>٣٨٣٥ </span>- عن أنس قال: قال رسول الله صلى الله عليه وسلم: " إن الخضر فى البحر واليسع فى البر، يجتمعان كل ليلة عند الردم الذى بناه ذو القرنين بين الناس وبين يأجوج ومأجوج ويحجان ويعتمران كل عام ويشربان من زمزمكم شربة تكفيهما قابل ".
** حارث
(الإصابة في تمييز الصحابة ١٠٧/٣)
** قال الحافظ فى " الإصابة " ٣ / ١٠٧: عبد الرحيم وأبان متروكان.','8','485',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3836,'<span data-type="title" id=toc-3844>٣٨٣٦ </span>- عن كعب قال: الخضر على منبر من نور بين البحر الأعلى والبحر الأسفل وقد أمرت دواب البحر أن تسمع له وتطيع، وتعرض عليه الأرواح غدوة وعشية.
** عق
(الإصابة في تمييز الصحابة ١٠٧/٣)
** قال الحافظ فى " الإصابة " ٣ / ١٠٧: قال العقيلى: عبد الله بن المغيرة يحدث بما لا أصل له اه. قال ابن يونس: إنه منكر الحديث اه.','8','486',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3837,'<span data-type="title" id=toc-3845>٣٨٣٧ </span>- عن خضيف قال: أربعة من الأنبياء أحياء اثنان فى السماء عيسى وإدريس وإثنان فى الأرض الخضر والياس فأماالخضر فإنه فى البحر وأما صاحبه فإنه فى البر.
** شاهين
(الإصابة في تمييز الصحابة ١٠٧/٣)
** إسناده ضعيف','8','487',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3838,'<span data-type="title" id=toc-3846>٣٨٣٨ </span>- عن أبى أمامة الباهلى: أن رسول الله صلى الله عليه وسلم قال: " ألا أحدثكم عن الخضر؟ " قالوا: بلى يارسول الله، قال: " بينما هو ذات يوم يمشى فى سوق بنى إسرائيل أبصره رجل مكاتب، فقال: تصدق على بارك الله فيك، فقال الخضر: آمنت بالله ما شاء الله من أمر يكون، ما عندى من شىء أعطيكه فقال المسكين: أسألك بوجه الله لما تصدقت على فإنى نظرت السماحة فى وجهك ورجوت البركة عندك، فقال الخضر: آمنت بالله ما عندى شىء أعطيكه إلا أن تأخذنى فتبيعنى، فقال المسكين: وهل يستقيم هذا، فقال: نعم الحق أقول لقد سألتنى بأمر عظيم أما أنى لا أخيبك بوجه ربى، بعنى قال: فقدمه إلى السوق فباعه بأربعمائة درهم فمكث عند المشترى زمانا لا يستعمله فى شىء فقال له: أجد إنما اشتريتنى التماس خير عندى، فأوصنى بعمل، قال: أكره أن أشق عليك، إنك شيخ كبير، ضعيف، قال: ليس يشق على قال: نعم، فانقل هذه الحجارة، وكان لا ينقلها دون ستة نفر فى يوم، فخرج الرجل لبعض حاجته ثم انصرف وقد نقل الحجارة فى ساعة، فقال: أحسنت وأجملت وأطقت مالم أرك تطيقه، قال: ثم عرض للرجل سفر، فقال: إنى أحسبك أمينا فاخلفنى فى أهلى خلافة حسنة، قال: نعم وأوصنى بعمل، قال: إنى أكره أن أشق عليك، قال: ليس يشق على، قال: فاضرب من اللبن لنبتنى حتى أقدم عليك،
قال: ومر الرجل لسفره ثم رجع وقد شيد بناؤه، فقال: أسألك بوجه ما سببك وماأمرك؟ ، قال: سألتنى بوجه الله ووجه الله أوقعنى فى العبودية، فقال الخضر: سأخبرك من أنا، أناالخضر الذى سمعت به، سألنى مسكين صدقة، فلم يكن عندى شىء أعطيه فسألنى بوجه الله فأمكنته من رقبتى، فباعنى وأخبرك أنه من سأل بوجه الله فرد سائله وهو يقدر، وقف يوم القيامة وليس على وجهه جلد، ولا لحم إلا عظم يتقعقع، فقال الرجل: آمنت بالله شققت عليك يانبى الله، ولم أعلم، قال: لا بأس أحسنت وأتقنت، فقال الرجل: بأبى وأمى يانبى الله، احكم فى أهلى ومالى بما شئت أو اختر فأخلى سبيلك، قال: أحب أن تخلى سبيلى، فأعبد ربى، قال: فخلى سبيله، فقال الخضر: الحمد لله الذى أوقعنى فى العبودية ثم نجانى منها ".
** طب
(الإصابة في تمييز الصحابة ١١١/٣)
** قال الحافظ فى " الإصابة " ٣ / ١١٢: سند هذا الحديث حسن لولا عنعنة بقية.','8','488',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3839,'<span data-type="title" id=toc-3847>٣٨٣٩ </span>- عن كثير بن عبد الله بن عمر بن عوف عن أبيه عن جده أن رسول الله صلى الله عليه وسلم كان فى المسجد فسمع كلاما من ورائه فإذا هو بقائل يقول: اللهم أعنى على ما ينجينى مما خوفتنى، فقال رسول الله صلى الله عليه وسلم حين سمع ذلك: " ألا أنضم إليها أختها "، فقال الرجل: اللهم ارزقنى شوق الصالحين إلى ماشوقتهم إليه فقال النبى صلى الله عليه وسلم لأنس بن مالك " اذهب يا أنس إليه فقل له: يقول لك رسول الله صلى الله عليه وسلم تستغفر لى " فجاءه أنس فقال الرجل: يا أنس أنت رسول رسول الله إلى مارجع فاستثبت فقال له النبى صلى الله عليه وسلم: " قل له نعم "، فقال له: اذهب فقل له: إن الله فضلك على الأنبياء مثل ما فضل رمضان على الشهور وفضل أمتك على الأمم مثل ما فضل يوم الجمعة على سائر الأيام فذهب فإذا هو الخضر.
** عد
(الإصابة في تمييز الصحابة ١١٧/٣)
** قال الحافظ فى " الإصابة " ٣ / ١١٧: كثير بن عبد الله ضعفه الأئمة، لكن جاء من غير روايته، أخرجه الطبرانى فى الأوسط.','8','489',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3840,'<span data-type="title" id=toc-3848>٣٨٤٠ </span>- عن أنس بن مالك، قال: خرجت ليلة من الليالى أحمل مع النبى صلى الله عليه وسلم الطهور فسمع مناديا يناى فقال لى: " يا أنس صه "، قال: فسكت فاستمع فإذا هو يقول: " اللهم أعنى على ما ينجينى مما خوفتنى منه "، قال:، فقال رسول الله صلى الله عليه وسلم: " لو قال أختها معها "، فكأن الرجل لقن ما أراد النبى صلى الله عليه وسلم، فقال: وارزقنى شوق الصالحين إلى ماشوقتهم إليه، فقال النبى صلى الله عليه وسلم: " يا أنس ضع الطهور وائت هذا المنادى فقل له: ادع لرسول الله أن يعينه الله على ما ابتعثه به، وادع لأمته أن يأخذوا ما أتاهم به نبيهم بالحق "، فقال لى: ومن أرسلك؟ ، فكرهت أن أخبره ولم أستأمر رسول الله صلى الله عليه وسلم، فقلت له: رحمك الله ما يضرك من أرسلنى ادع بما قلت لك، فقال: لا، أو تخبرنى: من أرسلك؟ ، قال: فرجعت إلى رسول الله صلى الله عليه وسلم فقلت له: يارسول الله أبى أن يدعو بما قلت له حتى أخبره بمن أرسلنى، فقال: ارجع إليه، فقل له: أنا رسول رسول الله فرجعت إليه فقلت له: مرحبا برسول رسول الله أنا كنت أحق أن آتيه، اقرأ على رسول الله صلى الله عليه وسلم منى وقل له: يارسول الله الخضر يقرأ عليك السلام ورحمة الله، ويقول لك: يارسول الله إن الله قد فضلك على النبيين كما فضل شهر رمضان على سائر الشهور، وفضل أمتك على الأمم كما فضل يوم الجمعة
على سائر الأيام، قال: فلما وليت سمعته يقول: الهم اجعلنى من هذه الأمة المرشدة المرحومة المتوب عليها.
** كر طس
(الإصابة في تمييز الصحابة ١١٧/٣)
** قال الحافظ فى " الإصابة " ٣ / ١١٩: قال الطبرانى فى الأوسط: لم يروه عن أنس إلا عاصم، ولا عنه إلا وضاح تفرد به محمد بن سلام، قال أبو الحسين بن المنادى: هذا حديث واه بالوضاح وغيره وهو منكر الإسناد سقيم المتن.','8','490',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3841,'<span data-type="title" id=toc-3849>٣٨٤١ </span>- عن أنس، قال: خرج رسول الله صلى الله عليه وسلم ذات ليلة لحاجة، فخرجت خلفه فسمعنا قائلا، يقول: اللهم إنى أسألك شوق الصادقين إلى ماشوقتهم إليه فقال رسول الله صلى الله عليه وسلم: " يالها من دعوة لو أضاف إليها أختها " فسمعنا القائل وهو، يقول: اللهم إنىأسألك أن تعيننى بما ينجينى مما خوفتنى منه، فقال رسول الله صلى الله عليه وسلم: " وجبت ورب الكعبة، يا أنس ائت الرجل فاسأله أن يدعو لرسول الله صلى الله عليه وسلم أن يرزقه الله القبول من أمته، والمعانة على ماجاء به من الحق والتصديق "، قال أنس: فأتيت الرجل فقلت: ياعبد الله ادع لرسول الله، فقال لى: ومن أنت؟ فكرهت أن أخبره ولم أستأذن، وأبى أن يدعو حتى أخبره، فرجعت إلى رسول الله صلى الله عليه وسلم، فأخبرته، فقال لى: " أخبره "، فرجعت، فقلت له: أنا رسول رسول الله إليك، فقال: مرحبا برسول الله، وبرسول رسول الله، فدعا له، وقال: اقرأه منى السلام وقل له: أنا أخوك الخضر، وأنا كنت أحق أن أتيك، قال: فلما وليت سمعته يقول: اللهم اجعلنى من هذه الأمة المرحومة المتاب عليها.
** شاهين قط فى الأفراد
(الإصابة في تمييز الصحابة ١١٩/٣)
** قال الحافظ فى " الإصابة " ٣ / ١١٩ _ ١٢٠: محمد بن عبد الله هذا هو أبو سلمة الأنصارى وهو واهى الحديث جدا، وليس هو شيخ البخارى قاضى البصرة، ذاك ثقة وهو أقدم من أبى سلمة.','8','491',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3842,'<span data-type="title" id=toc-3850>٣٨٤٢ </span>- عن ابن عباس مرفوعا قال: " يلتقى الخضر والياس فى كل عام فى الموسم فيحلق كل واحد منهما رأس صاحبه ويتفرقان عن هؤلاء الكلمات: بسم الله ما شاء الله لا يسوق الخير إلا الله بسم الله ماشاء الله لا يصرف السوء إلا الله، بسم الله ما شاء الله ما كان من نعمة فمن الله، بسم الله ما شاء الله لا حول ولا قوة إلا بالله ".
** فوائد أبى إسحاق المزنى
(الإصابة في تمييز الصحابة ١٢٠/٣)
** قال الحافظ فى " الإصابة " ٣ / ١٢٠: قال الدارقطنى فى الأفراد: لم يحدث به عدا ابن جريج غير الحسن بن رزن، قال أبو جعفر العقيلى: لم يتابع عليه وهو مجهول وحديثه غير محفوظ، وقال أبو الحسن بن المبارك: هو حديث واه بالحن المذكور اه.','8','492',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3843,'<span data-type="title" id=toc-3851>٣٨٤٣ </span>- الحديث بلفظ: يجتمع البرى والبحرى إلياس والخضر كل عام بمكة، قال ابن عباس: بلغنا أنه يحلق أحدهما رأس صاحبه ويقول أحدهما للآخر قل بسم الله......... وزاد قال ابن عباس: قال رسول الله صلى الله عليه وسلم: " ما من عبد قالها فى كل يوم إلا أمن من الحرق والغرق والسرقة وكل شىء يكرهه حتى يمسى وكذلك حتى يصبح.
** ابن الجوزى
(الإصابة في تمييز الصحابة ١٢٠/٣)
** إسناده واه جدا
** قال الحافظ فى " الإصابة " ٣ / ١٢٠: قال ابن الجوزى: أحمد بن عمار متروك عند الدارقطنى ومهدى بن هلال مثله اه. وقال ابن حبان: مهدى بن هلال يروى الموضوعات.','8','493',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3844,'<span data-type="title" id=toc-3852>٣٨٤٤ </span>- عن عبد العزيز بن أبى رواد، قال: يجتمع الخضر وإلياس بيت المقدس فى شهر رمضان من أوله إلى آخره ويفطران على الكرفس وإقبال الموسم كل عام.
** عمز
(الإصابة في تمييز الصحابة ١٢١/٣)
** معضل','8','494',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3845,'<span data-type="title" id=toc-3853>٣٨٤٥ </span>- عن على، قال: كنت عند النبى صلى الله عليه وسلم فذكر عنده الأدهان، فقال: " فضل دهن البنفسج على سائر الأدهان كفضلنا أهل البيت على سائر الخلق "، قال: وكان النبى صلى الله عليه وسلم يدهن به ويستعط فذكر حديثا طويلا، فيه الكراث والبازوج والجرجرير، والهندباء والكمأة والكرفس واللحم والحيتان، وفيه: " الكمأة من الجنة ماواها شفاء للعين وفيهاشفاء من السم، وهما طعام الياس واليسع، يجتمعان كل عام بالموسم يشربان شربة من ماء زمزم فيكتفيان بها إلى قابل فيرد الله شبابهما فى كل مائة عام مرة وطعامهما الكمأة والكرفس ".
** فوائد أبى على الباشانى
(الإصابة في تمييز الصحابة ١٢١/٣)
** قال الحافظ فى " الإصابة " ٣ / ١٢١: قال ابن الجوزى: لايشك حديثى فى أن هذا الحديث موضوع والمتهم به عبد الرحمن بن حبيب، فقال ابن حبان: إنه كان يضع الحديث.','8','495',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3846,'<span data-type="title" id=toc-3854>٣٨٤٦ </span>- عن على، قال: يجتمع فى كل يوم عرفة جبرائيل وميكائيل وإسرافيل والخضر فيقول جبرائيل: ماشاء الله لاقوة إلا بالله فيرد عليه ميكائيل ماشاء الله كل نعمة فمن الله فيرد عليهما إسرافيل ماشاء الله الخير كله بيد الله فيرد عليهم الخضر ماشاء الله لا يرفع السوء إلا الله ثم يتفرقون ولا يجتمعون إلى قابل فى مثل ذلك اليوم.
** ابن الجوزى
(الإصابة في تمييز الصحابة ١٢١/٣)
** قال الحافظ فى " الإصابة " ٣ / ١٢١: عبيد بن إسحاق متروك الحديث.','8','496',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3847,'<span data-type="title" id=toc-3855>٣٨٤٧ </span>- عن واثلة بن الأسقع قال: غزونا مع رسول الله صلى الله عليه وسلم غزوة تبوك حتى إذا كنا ببلاد جذام، وقد كان أصابنا عطش، فإذا بين أيدينا آثار غيث فسرنا ميلا فإذا غدير حتى إذا ذهب ثلث الليل إذا نحن بمناد ينادى بصوت حزين اللهم اجعلنى من أمة محمد المرحومة المغفور لها المستجاب لها والمبارك عليها فقال رسول الله صلى الله عليه وسلم: " ياحذيفة وياأنس ادخلا إلى هذا الشعب فانظرا ما هذا الصوت؟ "، قال: فدخلنا، فإذا نحن برجل عليه ثياب بيض أشد بياضا من الثلج، وإذا وجهه ولحيته كذلك، وإذا هو أعلى جسما منا بذراعين أو ثلاثة، فسلمنا عليه، فرد عليناالسلام، ثم، قال: مرحبا أنتما رسل رسول
رسول الله؟ ، فقلنا: نعم، من أنت يرحمك الله؟ ، قال أناإلياس النبى خرجت أريد مكة فرأيت عسكركم، فقال لى جند من الملائكة على مقدمتهم جبريل، وعلى ساقهم ميكائيل: هذا أخوك رسول الله صلى الله عليه وسلم فسلم عليه وألقه، ارجعا إليه فاقرآه منى السلام وقولا له: لم يمنعنى من الدخول إلى عسكركم إلا
أنى تخوفت أن تذعر الإبل ويفزع المسلمون من طولى فإن خلقى ليس كخلقكم، قولا
له صلى الله عليه وسلم يأتينى، قال حذيفة وأنس: فصافحناه، فقال لأنس: ياخادم رسول الله، من هذا؟ ، قال: هذا حذيفة صاحب سر رسول الله، فرحب له ثم قال: والله إنه لفى السماء أشهر منه فى الأرض، يسميه أهل السماء صاحب سر رسول الله، قال حذيفة: هل تلقى الملائكة؟ ، قال: ما من يوم إلا وأنا ألقاهم يسلمون على وأسلم عليهم، قال: فأتينا النبى صلى الله عليه وسلم فخرج
معنا حتى أتينا الشعب فإذا ضوء إلياس وثيابه كالشمس فقال النبى صلى الله عليه وسلم: " على رسلكم "، فتقدمنا قدر خمسين ذراعا، فعانقه مليا ثم قعدا،
فرأينا شيئا يشبه الطير العظام قد أحدقت بهما، وهى بيض قد نشرت أجنحتها فحالت بيننا وبينهما، ثم صرخ بنارسول الله فقال: " ياحذيفة ويا أنس تقدما
"، فإذا بين أيديهما مائدة خضراء لم أر شيئا قط أحسن منها، قد غلب خضرتها بياضنا، فصارت وجوهنا خضراء وثيابنا خضراء، وإذا عليها جبن وتمر ورمان وموز وعنب ورطب وبقل ما خلا الكراث، فقال النبى صلى الله عليه وسلم: " كلوا بسم الله "، فقلنا: يارسول الله أمن طعام الدنيا هذا؟ قال: " لا " قال لنا: " هذا رزقى ولى فى كل أربعين يوما وليلة أكلة تأتينى بها الملائكة
فكان هذا تمام الأربعين، وهو شىء يقول الله له كن فيكون "، فقلنا: من أين وجهك؟ ، قال: من خلف رومية كنت فى جيش من الملائكة مع جيش من مسلمى الجن،
غزونا أمة من الكفار، قلنا: فكم مسافة ذلك الموضع الذى كنت فيه؟ قال: أربعة أشهر، وفارقتهم أنا منذ عشرة أيام، وأنا أريد مكة، أشرب منها فى كل
سنة شربة، وهى ريى وعصمتى إلى تمام الموسم من قابل، قلنا: وأى المواطن أكثر مثواك؟ ، قال: الشام وبيت المقدس والمغرب واليمن وليس من مسجد من
مساجد محمد إلا وأنا أدخله صغيرا وكبيرا، فقلنا: متى عهدك بالخضر؟ ، قال منذ سنة كنت قد التقيت أنا وهو بالموسم، وأنا ألقاه بالموسم، وقد كان قال
لى: إنك ستلقى محمدا قبلى فاقرأه منى السلام وعانقه وعانقنا وبكى وبكينا فنظرنا إليه حين هوى فى السماء، كأنه حمل حملا، فقلنا: يارسول الله، لقد رأينا عجبا إذ هوى إلى السماء، قال: " يكون بين جناحى ملك حتى ينتهى به حيث أراد ".
** شاهين
(الإصابة في تمييز الصحابة ١٢٢/٣)
** قال الحافظ فى " الإصابة " ٣ / ١٢٤: قال ابن الجوزى: لعل بقية سمع هذا من كذاب فدلسه عن الأوزاعى،','8','497',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3848,'<span data-type="title" id=toc-3856>٣٨٤٨ </span>- عن أنس، قال: غزونا مع رسول الله صلى الله عليه وسلم، حتى إذا كنا بفج الناقة بهذا الحجر، إذا نحن بصوت يقول: اللهم اجعلنى من أمة محمد المرحومة المغفور لها المتاب عليها،المستجاب منها، فقال لى رسول الله صلى الله عليه وسلم: " يا أنس انظر ماهذا الصوت؟ "، فدخلت الجبل، فإذا رجل أبيض الرأس
واللحية عليه ثياب بيض طوزله أكثر من ثلاثمائة ذراع، فلما نظر إلى قال: أنت رسول رسول الله؟ ، قلت: نعم قال: ارجع إليه فاقرأ عليه منى السلام، وقل له: هذا أخوك إلياس يريد يلقاك، فجاء النبى صلى الله عليه وسلم وأنا معه، حتى إذ كنت قريبا منه تقدم وتأخرت فتحدثا طويلا، فنزل عليهما شىء من السماء شبيه السفرة، فدعوانى فأكلت معهما، فإذا فيها كمأة ورمان وكرفس فلما أكلت قمت فتنحيت وجاءت سحابة، فاحتملته، أنظر إلى بياض ثيابه فيها تهوى به قبل الشام، فقلت للنبى صلى الله عليه وسلم: بأبى أنت وأمى هذا الطعام الذى أكلنا من السماء نزل عليك؟ ، قال: " سألته عنه فقال لى: أتانى به جبريل لى فى كل أربعين يوم أكلة وفى كل حول شربة من ماء زمزم وربما رأيته على الجب يمسك بالدلو فيشرب وربما سقان ".
** ابن أبى الدنيا
(الإصابة في تمييز الصحابة ١٢٤/٣)
** قال الحافظ فى " الإصابة " ٣ / ١٢٠: قال ابن الجوزى: يزيد وإسحاق لا يعرفان.','8','498',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3849,'<span data-type="title" id=toc-3857>٣٨٤٩ </span>- **
(الإصابة في تمييز الصحابة /)
**','8','499',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3850,'<span data-type="title" id=toc-3858>٣٨٥٠ </span>- عن أبى هريرة رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم فى البحر: " هو الطهور ماؤه الحل مييته ".
** د ت ن جه شب ما شفع حم
(بلوغ المرام ٢/١)
** قال الحافظ فى " البلوغ " ١ / ٢: صححه ابن خزيمة والترمذى.
** تعقيب: قال الفقى ١ / ٢: وأخرجه أيضا ابن حبان وابن الجارود فى المنتقى والحاكم فى المستدرك والدارقطنى والبيهقى فى السنة، وحكى الترمذى عن البخارى تصحيحه، وصححه أيضا ابن المنذر وابن منده والبغوى.','8','500',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3851,'<span data-type="title" id=toc-3859>٣٨٥١ </span>- عن أبى سعيد الخدرى رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم " إن الماء طهور لا ينجسه شىء "
** د ت ن
(بلوغ المرام ٢/١)
** قال الحافظ فى " البلوغ " ١ / ٢: صححه أحمد.
** تعقيب: قال الفقى ١ / ٢: قال الترمذى: حسن. وصححه أيضا ابن معين وابن حزم والحاكم.','9','1',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3852,'<span data-type="title" id=toc-3860>٣٨٥٢ </span>- عن أبى أمامة الباهلى رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم " إن الماء لا ينجسه شىء إلا ما غلب على ريحه وطعمه ولونه ".
** جه
(بلوغ المرام ٢/١)
** قال الحافظ فى " البلوغ " ١ / ٣: ضعفه أبو حاتم.
** تعقيب: قال الفقى ١ / ٣: لأنه من رواية رشدين بن سعد كان صالحا فى دينه مغفلا فى روايته فتركوه.','9','2',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3853,'<span data-type="title" id=toc-3861>٣٨٥٣ </span>- عن عبد الله بن عمر رضى الله عنهما قال: قال رسول الله صلى الله عليه وسلم " إذا كان الماء قلتين لم يحمل الخبث ". وفى لفظ " لم ينجس ".
** د ت ن جه
(بلوغ المرام ٣/١)
** قال الحافظ فى " البلوغ " ١ / ٣: صححه ابن خزيمة والحاكم وابن حبان.
** تعقيب: قال الفقى ١ / ٣: هذا حديث شاذ ومضطرب سندا ومتنا أما شذوذه فلأنه غير مشهور مع شده حاجة الأمة إليه أعظم من حاجتها إلى أنصبة الزكاة فكان الواجب نقله كنقل نجاسة البول وعدد الركعات وهذا لم يروى إلا عن ابن عمر ولم يروه عنه إلا عبيد الله وعبد الله فأين كبار الصحابه ابن عمر وأهل المدينة الذين مخرج السنة من عندهم وهم أحوج الناس إليها لقلة الماء عندهم؟ وأما علته فمن ثلاث وجوه: أولا: وقفه على ابن عمر كما رجحه المزى وابن تيميه والبيهقى. ثانيا: اضطراب سنده. ثالثا: اضطراب متنه. ولذا أعرض عنه أصحاب الصحاح وضعفه ابن عبد البر ومقدار القلتين لم يصح فيه عن النبى صلى الله عليه وسلم شىء أصلا. اه. من تهذيب السنن للحافظ ابن القيم. تغتسلين مع الصبح وتصلين قال: وهو أعجب الأمرين إلى.','9','3',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3854,'<span data-type="title" id=toc-3862>٣٨٥٤ </span>- عن رجل صحب النبى صلى الله عليه وسلم قال: " نهى رسول الله صلى الله عليه وسلم: أن تغتسل المرأة بفضل الرجل أو الرجل بفضل المرأة وليغترفا جميعا ".
** د ن
(بلوغ المرام ٣/١)
** إسناده صحيح
** تعقيب: قال الفقى ١ / ٤: قال ابن قدامة فى المحرر: وصححه الحميدى. وقال البيهقى: رجاله ثقات.','9','4',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3855,'<span data-type="title" id=toc-3863>٣٨٥٥ </span>- اغتسل بعض أزواج النبى صلى الله عليه وسلم فى جفنة، فجاء يغتسل منها فقالت: إنى كنت جنبا فقال: " إن الماء لا يجنب ".
** د ت ن جه
(بلوغ المرام ٤/١)
** قال الحافظ فى " البلوغ " ١ / ٤: صححه الترمذى وابن خزيمة.','9','5',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3856,'<span data-type="title" id=toc-3864>٣٨٥٦ </span>- عن أبى قتادة رضى الله عنه أن رسول الله صلى الله عليه وسلم قال فى الهرة: " إنها ليست بنجس إنما هى من الطوافين عليكم ".
** د ت ن جه
(بلوغ المرام ٤/١)
** قال الحافظ فى " البلوغ " ١ / ٤: صححه الترمذى وابن خزيمة.
** تعقيب: قال الفقى ١ / ٤: قال فى المحرر: وصححه ابن حبان والحاكم وغيرهم. وقال الدارقطنى: رواته ثقات معروفون. وقال الحاكم: هذا الحديث مما صححه مالك واحتج به فى الموطأ.','9','6',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3857,'<span data-type="title" id=toc-3865>٣٨٥٧ </span>- عن ابن عمر رضى الله عنهما قال: قال رسول الله صلى الله عليه وسلم: " أحلت لنا ميتتان ودمان فأما الميتتان: فالجراد والحوت وأما الدمان: فالطحال والكبد ".
** حم جه
(بلوغ المرام ٥/١)
** ضعيف
** قال الحافظ فى " البلوغ " ١ / ٥: فيه ضعف.
** تعقيب: قال الفقى ١ / ٥: لأنه من رواية عبد الرحمن بن زيد بن أسلم وهو منكر الحديث وقد صرح أبو زرعة والحاكم بوقفه.','9','7',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3858,'<span data-type="title" id=toc-3866>٣٨٥٨ </span>- عن أبى واقد الليثى رضى الله عنه قال: قال النبى صلى الله عليه وسلم: " ما قطع من البهيمة _ وهى حية _ فهو ميت ".
** د ن
(بلوغ المرام ٥/١)
** قال الحافظ فى " البلوغ " ١ / ٥: حسنه الترمذى واللفظ له.
** تعقيب: قال الفقى ١ / ٥: له ثلاثة طرق عن ثلاثة آخرين من الصحابة: أبى سعيد، ابن عمر، تميم الدارى.','9','8',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3859,'<span data-type="title" id=toc-3867>٣٨٥٩ </span>- عن سلمة بن المحبق رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " دباغ جلود الميتة طهورها ".
** لم
(بلوغ المرام ٦/١)
** قال الحافظ فى " البلوغ " ١ / ٦: صححه ابن حبان.
** تعقيب: قال الفقى ١ / ٦: وأخرجه أحمد وأبو داود والنسائى والبيهقى عن سلمة بألفاظ أخرى.','9','9',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3860,'<span data-type="title" id=toc-3868>٣٨٦٠ </span>- عن عمرو بن خارجة رضى الله عنه قال: " خطبنا رسول الله صلى الله عليه وسلم بمنى وهو على راحلته ولعابها يسيل على كتفى ".
** حم ت
(بلوغ المرام ٧/١)
** قال الحافظ فى " البلوغ " ١ / ٧: صححه الترمذى.','9','10',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3861,'<span data-type="title" id=toc-3869>٣٨٦١ </span>- عن أبى السمح رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " يغسل من بول الجارية ويرش من بول الغلام ".
** د ن
(بلوغ المرام ٨/١)
** قال الحافظ فى " البلوغ " ١ / ٨: صححه الحاكم.
** تعقيب: قال الفقى ١ / ٨: جاء مثله من حديث لبابة بنت الحرث وعلى بن أبى طالب.','9','11',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3862,'<span data-type="title" id=toc-3870>٣٨٦٢ </span>- عن أبى هريرة رضى الله عنه قال: قالت خولة: يا رسول الله فإن لم يذهب الدم؟
قال: " يكفيك الماء، ولا يضرك أثره ".
** ت
(بلوغ المرام ٨/١)
** إسناده ضعيف
** تعقيب: قال الفقى ١ / ٨: وضعفه لأنه من رواية ابن لهيعة وقال إبراهيم الحربى: لم نسمع بخولة إلا فى هذا الحديث.','9','12',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3863,'<span data-type="title" id=toc-3871>٣٨٦٣ </span>- عن أبى هريرة رضى الله عنه: عن رسول الله صلى الله عليه وسلم أنه قال: " لولا أن أشق على أمتى لأمرتهم بالسواك مع كل وضوء ".
** ماحم ن خت
(بلوغ المرام ٨/١)
** قال الحافظ فى " البلوغ " ١ / ٨: صححه ابن خزيمة.
** تعقيب: قال الفقى ١ / ٨: قال ابن منده: إسناده مجمع على صحته وفى معناه عدة أحاديث عن عدة صحابة.','9','13',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3864,'<span data-type="title" id=toc-3872>٣٨٦٤ </span>- عن على رضى الله عنه فى صفة وضوء النبى صلى الله عليه وسلم قال: ومسح برأسه واحدة.
** د
(بلوغ المرام ٩/١)
** قال الحافظ فى " البلوغ " ١ / ٩: أخرجه الترمذى والنسائى بإسناد صحيح بل قال الترمذى: إنه أصح شىء فى الباب.','9','14',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3865,'<span data-type="title" id=toc-3873>٣٨٦٥ </span>- عن عبد الله بن عمرو رضى الله عنهما _ فى صفة الوضوء _ قال: ثم مسح برأسه وأدخل إصبعيه السباحتين فى أذنيه ومسح بإبهاميه ظاهر أذنيه.
** د ن
(بلوغ المرام ٩/١)
** قال الحافظ فى " البلوغ " ١ / ٩: صححه ابن خزيمة.','9','15',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3866,'<span data-type="title" id=toc-3874>٣٨٦٦ </span>- عن لقيط بن صبرة رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " أسبغ الوضوء، وخلل بين الأصابع وبالغ فى الاستنشاق إلا أن تكون صائما ".
** د ت ن جه
(بلوغ المرام ١٠/١)
** قال الحافظ فى " البلوغ " ١ / ١٠: صححه ابن خزيمة.','9','16',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3867,'<span data-type="title" id=toc-3875>٣٨٦٧ </span>- عن عثمان رضى الله عنه أن النبى صلى الله عليه وسلم: " كان يخلل لحيته فى الوضوء ".
** ن
(بلوغ المرام ١٠/١)
** قال الحافظ فى " البلوغ " ١ / ١٠: صححه ابن خزيمة.
** تعقيب: قال الفقى ١ / ١٠: ضعفه ابن معين: قال الإمام أحمد ليس فى تخليل اللحية شىء وكل ما ورد فيها لا يخلو من إعلال وتضعيف.','9','17',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3868,'<span data-type="title" id=toc-3876>٣٨٦٨ </span>- عن عبد الله بن زيد قال: إن النبى صلى الله عليه وسلم " أتى بثلثى مد فجعل يدلك ذراعيه ".
** حم
(بلوغ المرام ١٠/١)
** قال الحافظ فى " البلوغ " ١ / ١٠: صححه ابن خزيمة.
** تعقيب: قال الفقى ١ / ١٠: وثلثا المد هو أقل ماروى أنه صلى الله عليه وسلم توضأ به. وأما حديث الثلث فلا أصل له. قال الفقى ١ / ١٠: وقد صحح أبو زرعة من حديث عائشة أنه صلى الله عليه وسلم كان يغتسل بالصاع ويتوضأ بالمد.','9','18',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3869,'<span data-type="title" id=toc-3877>٣٨٦٩ </span>- عن عبد الله بن زيد أنه رأى النبى صلى الله عليه وسلم " يأخذ لأذنيه ماء غير الماء الذى أخذه لرأسه ".
** هق
(بلوغ المرام ١١/١)
** قال الحافظ فى " البلوغ " ١ / ١١: هو عند مسلم من هذا الوجه بلفظ ومسح برأسه بماء غير فضل يديه وهو المحفوظ.
** تعقيب: قال الفقى ١ / ١١: قال الصنعانى لم يذكر المصنف فى التلخيص الحبير أنه أخرجه مسلم ولا رأيناه فيه. اه.','9','19',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3870,'<span data-type="title" id=toc-3878>٣٨٧٠ </span>- عن أبى هريرة رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " إذا توضأتم فابدأوا بميامنكم ".
** د ت ن جه
(بلوغ المرام ١١/١)
** قال الحافظ فى " البلوغ " ١ / ١١: صححه ابن خزيمة.
** تعقيب: قال الفقى ١ / ١١: وأخرجه أحمد وابن حبان والبيهقى وزاد فيه (وإذا لبستم) قال ابن دقيق العيد: هو حقيق بأن يصحح.','9','20',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3871,'<span data-type="title" id=toc-3879>٣٨٧١ </span>- عن جابر رضى الله عنه قال: كان النبى صلى الله عليه وسلم " إذا توضأ أدار الماء على مرفقيه ".
** قط
(بلوغ المرام ١٢/١)
** إسناده ضعيف
** تعقيب: قال الفقى ١ / ١٢: فى إسناده القاسم بن محمد بن عقيل متروك. ضعفه أحمد وابن معين وغيرهما.','9','21',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3872,'<span data-type="title" id=toc-3880>٣٨٧٢ </span>- عن أبى هريرة رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " لا وضوء لمن لم يذكر اسم الله عليه ".
** حم د جه
(بلوغ المرام ١٢/١)
** إسناده ضعيف
** قال الحافظ فى " البلوغ " ١ / ١٢: وللترمذى عن سعيد بن زيد، وأبى سعيد نحوه، قال أحمد: لا يثبت فيه شىء.
** تعقيب: قال الفقى ١ /١٢: لأنه من رواية يعقوب بن سلمة الليثى عن أبيه عن أبى هريرة ولا يعرف له سماع من أبيه. قال الفقى ١ / ١٢: قد روى من طرق لايخلو واحد منها عن مقال إلا أنه يقوى بعضها بعضا ولذا قال ابن أبى شيبة: ثبت لنا أن النبى صلى الله عليه وسلم قاله.','9','22',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3873,'<span data-type="title" id=toc-3881>٣٨٧٣ </span>- عن طلحة بن مصرف عن أبيه عن جده رضى الله عنه قال: رأيت رسول الله صلى الله عليه وسلم " يفصل بين المضمضة والاستنشاق ".
** د
(بلوغ المرام ١٢/١)
** إسناده ضعيف
** تعقيب: قال الفقى ١ / ١٢: لأنه من رواية ليث بن أبى سليم قال النووى: اتفق العلماء على ضعفه. ومصرف مجهول.','9','23',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3874,'<span data-type="title" id=toc-3882>٣٨٧٤ </span>- عن المغيرة بن شعبة: أن النبى صلى الله عليه وسلم: " مسح أعلى الخف وأسفله ".
** د ت جه
(بلوغ المرام ١٤/١)
** إسناده ضعيف
** قال الحافظ فى " البلوغ " ١ / ١٤: فى إسناده ضعف.
** تعقيب: قال الفقى ١ / ١٤: لأنه من رواية كاتب المغيرة وقد ضعفه أئمة الحديث.','9','24',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3875,'<span data-type="title" id=toc-3883>٣٨٧٥ </span>- عن على رضى الله عنه أنه قال: " لو كان الدين بالرأى لكان أسفل الخف أولى بالمسح من أعلاه وقد رأيت رسول الله صلى الله عليه وسلم يمسح على ظاهر خفيه ".
** د
(بلوغ المرام ١٤/١)
** إسناده حسن
** تعقيب: قال الفقى ١ / ١٤: قال الحافظ فى التلخيص: إنه حديث صحيح.','9','25',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3876,'<span data-type="title" id=toc-3884>٣٨٧٦ </span>- عن صفوان بن عسال قال: كان النبى صلى الله عليه وسلم " يأمرنا إذا كنا سفرا أن لاننزع خفافنا ثلاثة أيام ولياليهن إلا من جنابة ولكن من غائط وبول ونوم ".
** ن ت خز
(بلوغ المرام ١٤/١)
** قال الحافظ فى " البلوغ " ١ / ١٤: أخرجه الترمذى واللفظ له وابن خزيمة وصححاه.
** تعقيب: قال الفقى ١ / ١٤: قال البخارى: ليس فى توقيت المسح شىء أصح منه. وصححه الترمذى والخطابى.','9','26',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3877,'<span data-type="title" id=toc-3885>٣٨٧٧ </span>- عن ثوبان رضى الله عنه قال: " بعث رسول الله صلى الله عليه وسلم سرية
فأمرهم أن يمسحوا على العصائب يعنى العمائم والتساخين يعنى الخفاف ".
** حم د
(بلوغ المرام ١٤/١)
** قال الحافظ فى " البلوغ " ١ / ١٤: صححه الحاكم.','9','27',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3878,'<span data-type="title" id=toc-3886>٣٨٧٨ </span>- عن أنس مرفوعا: " إذا توضأ أحدكم ولبس خفيه فليمسح عليهما وليصل فيهما
ولايخلعهما إن شاء إلا من الجنابة ".
** قط ك
(بلوغ المرام ١٤/١)
** قال الحافظ فى " البلوغ " ١ / ١٥: أخرجه الحاكم وصححه. قال الحافظ فى " البلوغ " ١ / ١٤: وعن عمر رضى الله عنه موقوفا.','9','28',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3879,'<span data-type="title" id=toc-3887>٣٨٧٩ </span>- عن أبى بكرة رضى الله عنه: عن النبى صلى الله عليه وسلم " أنه رخص للمسافر ثلاثة أيام ولياليهن، وللمقيم يوما وليلة إذا تطهر ولبس خفيه أن يمسح عليهما ".
** قط
(بلوغ المرام ١٥/١)
** قال الحافظ فى " البلوغ " ١ / ١٥: صححه ابن خزيمة.','9','29',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3880,'<span data-type="title" id=toc-3888>٣٨٨٠ </span>- عن أبى بن عمارة رضى الله عنه أنه قال: يارسول الله أمسح على الخفين؟ قال:
نعم، قال: يوما؟ قال: نعم، قال: ويومين؟ قال: نعم، قال: وثلاثة
أيام؟ قال: نعم وما شئت.
** د
(بلوغ المرام ١٥/١)
** قال الحافظ فى " البلوغ " ١ / ١٥: أخرجه أبو داود وقال: ليس بالقوى.
** تعقيب: قال الفقى ١ / ١٥: قال المنذرى: وأخرجه ابن ماجه. وبمعنى قول أبى داود قال البخارى. وقال الأمام أحمد: رجاله لا يعرفون. وقال الدارقطنى، هذا إسناد لا يثبت.اه.
وقال ابن معين: إسناده مظلم.','9','30',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3881,'<span data-type="title" id=toc-3889>٣٨٨١ </span>- عن أنس بن مالك قال: كان أصحاب رسول الله صلى الله عليه وسلم على عهده " ينتظرون العشاء حتى تخفق رؤسهم ثم يصلون ولا يتوضئون ".
** د
(بلوغ المرام ١٥/١)
** قال الحافظ فى " البلوغ " ١ / ١٥: صححه الدارقطنى وأصله فى مسلم.','9','31',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3882,'<span data-type="title" id=toc-3890>٣٨٨٢ </span>- عن عائشة أن النبى صلى الله عليه وسلم: " قبل بعض نسائه ثم خرج إلى الصلاة ولم يتوضأ ".
** حم
(بلوغ المرام ١٦/١)
** قال الحافظ فى " البلوغ " ١ / ١٦: ضعفه البخارى.
** تعقيب: قال الفقى ١ / ١٦: قال فى المحرر: ورجاله مخرج لهم فى الصحيح. وقال النسائى ليس فى هذا الباب أحسن منه ولكنه مرسل. وقال المصنف: روى من عشرة أوجه عن عائشة.','9','32',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3883,'<span data-type="title" id=toc-3891>٣٨٨٣ </span>- عن طلق بن على رضى الله عنه قال: قال رجل مسست ذكرى أو قال الرجل يمس ذكره فى الصلاة أعليه الوضوء؟ فقال النبى صلى الله عليه وسلم: " لا إنما هو بضعة منك ".
** د ت ن جه حم
(بلوغ المرام ١٧/١)
** قال الحافظ فى " البلوغ " ١ / ١٧: صححه ابن حبان. وقال ابن المدينى: هو أحسن من حديث بسرة.','9','33',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3884,'<span data-type="title" id=toc-3892>٣٨٨٤ </span>- عن بسرة بنت صفوان رضى الله عنها: أن رسول الله صلى الله عليه وسلم قال: " من مس ذكره فليتوضأ ".
** د ت ن جه حم
(بلوغ المرام ١٧/١)
** قال الحافظ فى " البلوغ " ١ / ١٧: صححه الترمذى وابن حبان. وقال البخارى: هو أصح شىء فى هذا الباب.','9','34',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3885,'<span data-type="title" id=toc-3893>٣٨٨٥ </span>- عن أبى هريرة رضى الله عنه قال: قال النبى صلى الله عليه وسلم: " من غسل ميتا فليغتسل، ومن حمله فليتوضأ ".
** حم ن ت
(بلوغ المرام ١٧/١)
** قال الحافظ فى " البلوغ " ١ / ١٧: حسنه الترمذى. وقال أحمد: لا يصح فى هذا الباب شىء.','9','35',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3886,'<span data-type="title" id=toc-3894>٣٨٨٦ </span>- عن عبد الله بن أبى بكر رضى الله عنه: أن فى الكتاب الذى كتبه رسول الله صلى الله عليه وسلم لعمرو بن حزم " أن لا يمس القرآن إلا طاهر ".
** مان حب
(بلوغ المرام ١٧/١)
** قال الحافظ فى " البلوغ " ١ / ١٧: رواه مالك مرسلا وهو معلول.
** تعقيب: قال الفقى ١ / ١٧: قال ابن عبد البر: أشبه المتواتر لتلقى الناس له بالقبول.','9','36',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3887,'<span data-type="title" id=toc-3895>٣٨٨٧ </span>- عن أنس بن مالك رضى الله عنه أن النبى صلى الله عليه وسلم: " احتجم وصلى ولم يتوضأ ".
** قط
(بلوغ المرام ١٨/١)
** قال الحافظ فى " البلوغ " ١ / ١٨: لينه الدارقطنى.','9','37',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3888,'<span data-type="title" id=toc-3896>٣٨٨٨ </span>- عن معاوية قال: قال رسول الله صلى الله عليه وسلم: " العين وكاء السه فإذا نامت العينان استطلق الوكاء ".
** حم طب
(بلوغ المرام ١٨/١)
** إسناده ضعيف
** قال الحافظ فى " البلوغ " ١ / ١٨: رواه أحمد والطبرانى وزاد " ومن نام فليتوضأ " وهذه الزيادة فى هذا الحديث عند أبى داود من حديث على دون قوله (استطلق الوكاء) وفى كلا
الإسنادين ضعف.','9','38',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3889,'<span data-type="title" id=toc-3897>٣٨٨٩ </span>- عن ابن عباس رضى الله عنهما مرفوعا: " إنماالوضوء على من نام مضطجعا ".
** د
(بلوغ المرام ١٨/١)
** إسناده ضعيف
** قال الحافظ فى " البلوغ " ١ / ١٨: وفى إسناده ضعف.
** تعقيب: قال الفقى ١ / ١٨: قال أبو داود: إنه حديث منكر.','9','39',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3890,'<span data-type="title" id=toc-3898>٣٨٩٠ </span>- عن أنس بن مالك رضى الله عنه قال: " كان رسول الله صلى الله عليه وسلم إذا دخل الخلاء وضع خاتمه ".
** د ت ن جه
(بلوغ المرام ١٩/١)
** قال الحافظ فى " البلوغ " ١ / ١٩: هو معلول.
** تعقيب: قال الفقى ١ / ١٩: لأنه من رواية ابن جريج عن الزهرى عن أنس وابن جريج لم يسمعه من الزهرى وإنما سمعه من زياد بن سعد عن الزهرى بلفظ أخر أنه صلى الله عليه وسلم إتخذ خاتما من ورق ثم ألقاه.','9','40',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3891,'<span data-type="title" id=toc-3899>٣٨٩١ </span>- عن معاذ رضى الله عنه: عن النبى صلى الله عليه وسلم: " اتقوا الملاعن الثلاثة: البراز فى الموارد وقارعة الطريق والظل ".
** د
(بلوغ المرام ١٩/١)
** ضعيف
** قال الحافظ فى " البلوغ " ١ / ٢٠: ولأحمد عن ابن عباس " أو نقع ماء " وفيها ضعف.
** تعقيب: قال الفقى ١ / ٢٠: زيادة أبى داود روايتها منقطعة لأنها من رواية أبى سعيد الحميرى عن معاذ وهو لم يدركه وحديث أحمد فيه ابن لهيعة والراوى عن ابن عباس مجهول.','9','41',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3892,'<span data-type="title" id=toc-3900>٣٨٩٢ </span>- عن ابن عمر رضى الله عنهما: " نهى النبى صلى الله عليه وسلم عن قضاء الحاجة
تحت الأشجار المثمرة وضفة النهر الجارى ".
** طب
(بلوغ المرام ٢٠/١)
** إسناده ضعيف
** تعقيب: قال الفقى ١ / ٢٠: لأن فى سنده فرات بن السائب وهو متروك.','9','42',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3893,'<span data-type="title" id=toc-3901>٣٨٩٣ </span>- عن جابر قال: قال رسول الله صلى الله عليه وسلم: " إذا تغوط الرجلان فليتوار كل واحد منهما عن صاحبه ولا يتحدثا فإن الله يمقت على ذلك ".
** حم
(بلوغ المرام ٢٠/١)
** قال الحافظ فى " البلوغ " ١ / ٢٠: صححه ابن السكن وابن القطان وهو معلول.
** تعقيب: قال الفقى ١ / ٢٠: لأنه من رواية عكرمة بن عمارالبجلى وقد ضعف بعض الحفاظ حديثه ولا وجه للتضعيف بهذا فقد أخرج مسلم حديثه عن يحيى بن أبى كثير واستشهد بحديثه البخارى عن يحيى أيضا والحديث قد أخرجه أحمد وأبو داود وابن ماجه عن أبى سعيد.','9','43',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3894,'<span data-type="title" id=toc-3902>٣٨٩٤ </span>- عن عائشة أن النبى صلى الله عليه وسلم كان إذا خرج من الغائط قال: " غفرانك ".
** د ت ن جه حم
(بلوغ المرام ٢١/١)
** قال الحافظ فى " البلوغ " ١ / ٢١: صححه أبو حاتم والحاكم.','9','44',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3895,'<span data-type="title" id=toc-3903>٣٨٩٥ </span>- عن أبى هريرة رضى الله عنه: أن رسول الله صلى الله عليه وسلم " نهى أن يستنجى
بعظم أو روث وقال: إنهمالا يطهران ".
** قط
(بلوغ المرام ٢١/١)
** قال الحافظ فى " البلوغ " ١ / ٢١: صححه الدارقطنى.','9','45',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3896,'<span data-type="title" id=toc-3904>٣٨٩٦ </span>- " أكثر عذاب القبر من البول ".
** ك
(بلوغ المرام ٢١/١)
** إسناده صحيح
** تعقيب: قال الفقى ١ / ٢١: هذا كلام الحافظ ابن حجر هنا وقال فى التلخيص: وللحاكم وأحمد وابن ماجه " أكثر عذاب القبر من البول " وأعله أبو حاتم وقال: إن رفعه باطل.','9','46',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3897,'<span data-type="title" id=toc-3905>٣٨٩٧ </span>- عن سراقة بن مالك قال: علمنا رسول الله صلى الله عليه وسلم " فى الخلاء أن نقعد على اليسرى وننصب اليمنى ".
** هق
(بلوغ المرام ٢١/١)
** إسناده ضعيف
** تعقيب: قال الفقى ١ / ٢١: قال الحازمى: فى سنده من لا نعرفه ولا نعلم فى الباب غيره.','9','47',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3898,'<span data-type="title" id=toc-3906>٣٨٩٨ </span>- عن عيسى بن يزداد عن أبيه رضى الله عنهما قال: قال رسول الله صلى الله عليه وسلم: " إذا بال أحدكم فلينتر ذكره ثلاث مرات ".
** جه
(بلوغ المرام ٢٢/١)
** إسناده ضعيف
** تعقيب: قال الفقى ١ / ٢٢: قال أبو حاتم: حديث مرسل. وقال البخارى: لا صحبة له. وقال النووى فى شرح المهذب: اتفقوا على أنه ضعيف.','9','48',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3899,'<span data-type="title" id=toc-3907>٣٨٩٩ </span>- عن ابن عباس رضى الله عنهما: " أن النبى صلى الله عليه وسلم سأل أهل قباء فقال: إن الله يثنى عليكم، فقالوا: إنا نتبع الحجارة الماء ".
** بز
(بلوغ المرام ٢٢/١)
** إسناده ضعيف
** قال الحافظ فى " البلوغ " ١ / ٢٢: أصله فى أبى داود وصححه ابن خزيمة من حديث أبى هريرة رضى الله عنه بدون ذكر الحجارة.','9','49',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3900,'<span data-type="title" id=toc-3908>٣٩٠٠ </span>- عن عائشة رضى الله عنه قالت: " كان رسول الله صلى الله عليه وسلم يغتسل من
أربع من الجنابة، ويوم الجمعة، ومن الحجامة، ومن غسل الميت ".
** د
(بلوغ المرام ٢٣/١)
** قال الحافظ فى " البلوغ " ١ / ٢٣: صححه ابن خزيمة.','9','50',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3901,'<span data-type="title" id=toc-3909>٣٩٠١ </span>- عن سمرة بن جندب رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " من توضأ يوم الجمعة فبها ونعمت، ومن اغتسل فالغسل أفضل ".
** د ت ن جه حم
(بلوغ المرام ٢٣/١)
** قال الحافظ فى " البلوغ " ١ / ٢٣: حسنه الترمذى.
** تعقيب: قال الفقى ١ / ٢٣: هو من مراسيل الحسن البصرى عن سمرة وفيه خلاف عند العلماء وقد أعرض عنه الشيخان فلم يخرجاه.','9','51',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3902,'<span data-type="title" id=toc-3910>٣٩٠٢ </span>- عن على رضى الله عنه قال: " كان رسول الله صلى الله عليه وسلم يقرئنا
القرآن ما لم يكن جنبا ".
** د ت ن جه حم
(بلوغ المرام ٢٤/١)
** قال الحافظ فى " البلوغ " ١ / ٢٤: صححه الترمذى وحسنه ابن حبان.','9','52',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3903,'<span data-type="title" id=toc-3911>٣٩٠٣ </span>- عن عائشة رضى الله عنها قالت: " كان رسول الله صلى الله عليه وسلم ينام وهو جنب من غير أن يمس ماء ".
** د ت ن جه
(بلوغ المرام ٢٤/١)
** قال الحافظ فى "البلوغ " ١ / ٢٤: هو معلول.
** تعقيب: قال الفقى ١ / ٢٤: بين المصنف فى التلخيص أنه من رواية أبى إسحق السبيعى عن الأسود عن عائشة قال أحمد: إنه ليس بصحيح وقال أبو داود: إنه وهم لأن أباإسحق لم يسمعه من الأسود وقد صححه البيهقى وقال: إن أباإسحق سمعه من الأسود.','9','53',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3904,'<span data-type="title" id=toc-3912>٣٩٠٤ </span>- عن عائشة رضى الله عنها قالت: قال رسول الله صلى الله عليه وسلم: " إنى لا أحل المسجد لحائض ولا جنب ".
** د
(بلوغ المرام ٢٥/١)
** قال الحافظ فى " البلوغ " ١ / ٢٥: صححه ابن خزيمة.','9','54',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3905,'<span data-type="title" id=toc-3913>٣٩٠٥ </span>- عن أبى هريرة رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " إن تحت
كل شعرة جنابة فاغسلوا الشعر وأنقوا البشر ".
** د ت
(بلوغ المرام ٢٥/١)
** قال الحافظ فى " البلوغ " ١ / ٢٥: ضعفه أبو داود والترمذى ولأحمد عن عائشة رضى الله عنها نحوه وفيه راو مجهول.
** تعقيب: قال الفقى ١ / ٢٥: هو من رواية الحارث بن وجيه، قال أبو داود: حديثه منكر وهو ضعيف. وقال الشافعى: هذا الحديث ليس بثابت.','9','55',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3906,'<span data-type="title" id=toc-3914>٣٩٠٦ </span>- عن ابن عمر رضى الله عنهما قال: قال رسول الله صلى الله عليه وسلم: " التيمم ضربتان: ضربة للوجه، وضربة لليدين إلى المرفقين ".
** قط
(بلوغ المرام ٢٦/١)
** قال الحافظ فى " البلوغ " ١ / ٢٦: صحح الأئمة وقفه.','9','56',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3907,'<span data-type="title" id=toc-3915>٣٩٠٧ </span>- عن أبى هريرة رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " الصعيد
وضوء المسلم وإن لم يجد الماء عشر سنين فإذا وجد الماء فليتق الله وليمسه بشرته ".
** بز
(بلوغ المرام ٢٦/١)
** قال الحافظ فى " البلوغ " ١ / ٢٧: صححه ابن القطان ولكن صوب الدارقطنى إرساله وللترمذى عن أبى ذر نحوه وصححه.','9','57',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3908,'<span data-type="title" id=toc-3916>٣٩٠٨ </span>- عن ابن عباس رضى الله عنهما فى قوله عز وجل " وإن كنتم مرضى أو على سفر " قال: إذا كانت بالرجل الجراحة فى سبيل الله والقروح فيجنب فيخاف أن يموت إن اغتسل تيمم.
** قط بز
(بلوغ المرام ٢٧/١)
** قال الحافظ فى " البلوغ " ١ / ٢٧: رواه الدارقطنى موقوفا ورفعه البزار وصححه ابن خزيمة والحاكم.
** تعقيب: قال الفقى ١ / ٢٧: قال البزار: لا نعلم من رفعه عن عطاء من الثقات إلا جرير وقد قال ابن معين: إن جريرا سمع من عطاء بعد الإختلاط فلا يتم رفعه.','9','58',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3909,'<span data-type="title" id=toc-3917>٣٩٠٩ </span>- عن على رضى الله عنه قال: " انكسرت إحدى زندى فسألت رسول الله صلى الله عليه وسلم فأمرنى أن أمسح على الجبائر ".
** جه
(بلوغ المرام ٢٧/١)
** إسناده واه جدا
** تعقيب: قال الفقى ١ / ٢٧: لأنه من رواية عمرو بن خالد وهو كذاب.','9','59',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3910,'<span data-type="title" id=toc-3918>٣٩١٠ </span>- عن جابر رضى الله عنه فى الرجل الذى شج فاغتسل فمات " إنما كان يكفيه أن تيمم ويعصب على جرحه خرقة ثم يمسح عليها ويغسل سائر جسده ".
** د
(بلوغ المرام ٢٨/١)
** إسناده ضعيف
** قال الحافظ فى " البلوغ " ١ / ٢٨: رواه أبو داود بسند فيه ضعف وفيه اختلاف على رواته.
** تعقيب: قال الفقى ١ / ٢٨: لأنه من رواية الزبير بن خريق قال الدارقطنى: ليس بالقوى وقد رواه عن عطاء
عن جابر ورواه الأوزاعى عن عطاء عن ابن عباس.','9','60',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3911,'<span data-type="title" id=toc-3919>٣٩١١ </span>- عن ابن عباس رضى الله عنهما قال: " من السنة أن لا يصلى الرجل بالتيمم إلا صلاة واحدة ثم يتيمم للصلاة الأخرى ".
** قط
(بلوغ المرام ٢٨/١)
** إسناده ضعيف جدا
** تعقيب: قال الفقى ١ / ٢٨: لأنه من رواية الحسن بن عمارة وهو ضعيف جدا.','9','61',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3912,'<span data-type="title" id=toc-3920>٣٩١٢ </span>- عن عائشة رضى الله عنها أن فاطمة بنت أبى حبيش كانت تستحاض فقال لها رسول الله صلى الله عليه وسلم: " إن دم الحيض دم أسود يعرف فإذا كان ذلك فأمسكى عن الصلاة فإذا كان الآخر فتوضئى وصلى ".
** د ن
(بلوغ المرام ٢٨/١)
** قال الحافظ فى " البلوغ " ١ / ٢٨: صححه ابن حبان والحاكم واستنكره أبو حاتم.
** تعقيب: قال الفقى ١ / ٢٨: قال الصنعانى: لأنه من رواية عدى بن ثابت عن أبيه عن جده وجده لا يعرف. وقد ضعف أبو داود الحديث.اه. وهذا خطأ فإن هذا الحديث فى سنن أبى داود والنسائى من حديث عروة عن عائشة ومن حديث فاطمة بنت أبى حبيش على اختلاف بين الرواه على عروة. ولذلك ادعى ابن القطان انقطاعه، ورده ابن القيم بأن عروة رواه عنهما وقد أدرك كلتيهما وسمع منهما بلا ريب.','9','62',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3913,'<span data-type="title" id=toc-3921>٣٩١٣ </span>- عن حمنة بنت جحش قالت: كنت أستحاض حيضة كثيرة وشديدة فأتيت النبى صلى الله
عليه وسلم أستفتيه فقال: " إنما هى ركضة من الشيطان فتحيضى ستة أيام أو سبعة أيام ثم اغتسلى فإذا استنقأت فصلى أربعة وعشرين أو ثلاثة وعشرين وصومى وصلى فإن ذلك يجزئك وكذلك فافعلى كل شهر كما تحيض النساء فإن قويت على أن تؤخرى الظهر وتعجلى العصر ثم تغتسلين حين تطهرين وتصلى الظهر والعصر جميعا ثم تؤخرين المغرب وتعجلين العشاء ثم تغتسلين وتجمعين بين الصلاتين فافعلى وتغتسلين مع الصبح وتصلين قال: وهو أعجب الأمرين إلى ". تغتسلين مع الصبح وتصلين قال: وهو أعجب الأمرين إلى.
** حم د ت جه
(بلوغ المرام ٢٩/١)
** قال الحافظ فى " البلوغ " ١ / ٢٩: صححه الترمذى وحسنه البخارى.
** تعقيب: قال الفقى ١ / ٢٩: قد ضعف البيهقى رواية الغسل، وقال بعضهم: إنها منسوخة، وقال الخطابى: قد
ترك بعض العلماء القول بحديث حمنة لأن ابن عقيل راويه ليس بذاك.','9','63',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3914,'<span data-type="title" id=toc-3922>٣٩١٤ </span>- عن ابن عباس رضى الله عنهما عن رسول الله صلى الله عليه وسلم فى الذى يأتى امرأته وهى حائض قال: " يتصدق بدينار أو بنصف دينار ".
** حم د ت ن جه
(بلوغ المرام ٣٠/١)
** قال الحافظ فى " البلوغ " ١ / ٣٠: صححه الحاكم وابن القطان ورجح غيرهما وقفه.
** تعقيب: قال الفقى ١ / ٣٠: قال الحافظ ابن حجر: الاضطراب فى إسناد هذا الحديث ومتنه كثير جدا.','9','64',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3915,'<span data-type="title" id=toc-3923>٣٩١٥ </span>- عن معاذ بن جبل رضى الله عنه أنه سأل النبى صلى الله عليه وسلم: " ما يحل
للرجل من امرأته وهى حائض؟ فقال: ما فوق الإزار ".
** د
(بلوغ المرام ٣١/١)
** قال الحافظ فى " البلوغ " ١ / ٣١: ضعفه أبو داود.','9','65',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3916,'<span data-type="title" id=toc-3924>٣٩١٦ </span>- عن رافع بن خديج رضى الله عنه قال: " أصبحوا بالصبح فإنه أعظم لأجوركم ".
** حم د ت ن جه
(بلوغ المرام ٣٣/١)
** قال الحافظ فى " البلوغ " ١ / ٣٣: صححه الترمذى وابن حبان.','9','66',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3917,'<span data-type="title" id=toc-3925>٣٩١٧ </span>- عن جبير بن مطعم قال: قال رسول الله صلى الله عليه وسلم: " يابنى عبد مناف لا تمنعوا أحدا طاف بهذا البيت وصلى أية ساعة شاء من ليل أو نهار ".
** حم د ت ن جه
(بلوغ المرام ٣٤/١)
** قال الحافظ فى " البلوغ " ١ / ٣٤: صححه الترمذى وابن حبان.','9','67',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3918,'<span data-type="title" id=toc-3926>٣٩١٨ </span>- عن ابن عمر رضى الله عنهما أن النبى صلى الله عليه وسلم قال: " الشفق الحمرة ".
** قط
(بلوغ المرام ٣٤/١)
** قال الحافظ فى " البلوغ " ١ / ٣٤: صححه ابن خزيمة. وغيره وقفه على ابن عمر.','9','68',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3919,'<span data-type="title" id=toc-3927>٣٩١٩ </span>- عن ابن عباس رضىالله عنهما قال: قال رسول الله صلى الله عليه وسلم: " الفجر
فجران فجر يحرم الطعام وتحل فيه الصلاة وفجر تحرم فيه الصلاة _ أى صلاة الصبح _ ويحل فيه الطعام ".
** خز ك
(بلوغ المرام ٣٤/١)
** قال الحافظ فى " البلوغ " ١ /٣٤: صححه الحاكم وابن خزيمة.','9','69',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3920,'<span data-type="title" id=toc-3928>٣٩٢٠ </span>- عن ابن مسعود رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " أفضل الأعمال الصلاة فى أول وقتها ".
** ت ك
(بلوغ المرام ٣٥/١)
** قال الحافظ فى " البلوغ " ١ / ٣٥: صححه الحاكم والترمذى وأصله فى الصحيحين.','9','70',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3921,'<span data-type="title" id=toc-3929>٣٩٢١ </span>- عن أبى محذورة أن النبى صلى الله عليه وسلم قال: " أول الوقت رضوان الله
وأوسطه رحمة الله وأخره عفو الله ".
** قط
(بلوغ المرام ٣٥/١)
** إسناده ضعيف جدا
** قال الحافظ فى " البلوغ " ١ / ٣٥: وللترمذى من حديث ابن عمر نحوه دون الأوسط وهو ضعيف أيضا.
** تعقيب: قال الفقى ١ / ٣٥: لأنهما من رواية يعقوب بن الوليد المدنى، قال أحمد: كان من الكذابين الكبار.','9','71',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3922,'<span data-type="title" id=toc-3930>٣٩٢٢ </span>- عن عبد الله بن زيد بن عبد ربه قال: طاف بى _ وأنا نائم _ رجل فقال: تقول الله أكبر الله أكبر فذكر الأذان _ بتربيع التكبير بغير ترجيع والإقامة فرادى إلا قد قامت الصلاة _ قال: فلما أصبحت أتيت رسول الله صلى الله عليه وسلم فقال: إنها لرؤيا حق ... الحديث.
** حم د
(بلوغ المرام ٣٦/١)
** قال الحافظ فى " البلوغ " ١ / ٣٦: صححه الترمذى وابن خزيمة.','9','72',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3923,'<span data-type="title" id=toc-3931>٣٩٢٣ </span>- عن أبى جحيفة رضى الله عنه قال: رأيت بلالا يؤذن وأتتبع فاه ههنا وههنا وإصبعاه فى أذنيه.
** حم ت
(بلوغ المرام ٣٩/١)
** قال الحافظ فى " البلوغ " ١ / ٣٩: صححه الترمذى.','9','73',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3924,'<span data-type="title" id=toc-3932>٣٩٢٤ </span>- عن ابن عمر رضى الله عنهما أن بلالا أذن قبل الفجر فأمره النبى صلى الله عليه وسلم أن يرجع فينادى: " ألا إن العبد نام ".
** د
(بلوغ المرام ٣٩/١)
** قال الحافظ فى " البلوغ " ١ / ٣٩: ضعفه أبو داود.
** تعقيب: قال الفقى ١ / ٤٠: قال أبو داود: هذا حديث لم يروه عن أيوب إلا حماد بن سلمة، وقال الترمذى: هذا حديث غير محفوظ، وكذلك قال ابن المدينى: أخطأ فيه حماد.','9','74',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3925,'<span data-type="title" id=toc-3933>٣٩٢٥ </span>- عن عثمان بن أبى العاص رضى الله عنه قال: يا رسول الله اجعلنى إمام قومى فقال: " أنت إمامهم واقتد بأضعفهم واتخذ مؤذنالا يأخذ على أذانه أجرا ".
** حم د ت ن جه
(بلوغ المرام ٤٠/١)
** قال الحافظ فى " البلوغ " ١ / ٤٠: حسنه الترمذى وصححه الحاكم.','9','75',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3926,'<span data-type="title" id=toc-3934>٣٩٢٦ </span>- عن جابر رضى الله عنه أن رسول الله صلى الله عليه وسلم قال لبلال: " إذا أذنت فترسل وإذا أقمت فاحدر واجعل بين أذانك وإقامتك مقدار ما يفرغ الآكل من أكله ... " الحديث.
** ت
(بلوغ المرام ٤٠/١)
** قال الحافظ فى " البلوغ " ١ / ٤٠: ضعفه الترمذى.
** تعقيب: قال الفقى ١ / ٤٠: قال الترمذى: لا نعرفه إلا من حديث عبد المنعم بن لقيم البصرى صاحب السقاء وإسناده مجهول.','9','76',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3927,'<span data-type="title" id=toc-3935>٣٩٢٧ </span>- عن أبى هريرة رضى الله عنه أن النبى صلى الله عليه وسلم قال: " لايؤذن إلا متوضىء ".
** ت
(بلوغ المرام ٤٠/١)
** قال الحافظ فى " البلوغ " ١ / ٤٠: ضعفه الترمذى.
** تعقيب: قال الفقى ١ / ٤٠: لأنه من رواية الزهرى عن أبى هريرة والزهرى لم يلق أبا هريرة والراوى عن الزهرى ضعيف.','9','77',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3928,'<span data-type="title" id=toc-3936>٣٩٢٨ </span>- عن زياد بن الحارث قال: قال رسول الله صلى الله عليه وسلم: " من أذن فهو يقيم ".
** ت
(بلوغ المرام ٤٠/١)
** قال الحافظ فى " البلوغ " ١ / ٤٠: ضعفه الترمذى.
** تعقيب: قال الفقى ١ / ٤٠: لأنه من رواية عبد الرحمن بن زياد بن أنعم الإفريقى، ضعفه يحيى القطان وغيره.','9','78',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3929,'<span data-type="title" id=toc-3937>٣٩٢٩ </span>- عن عبد الله بن زيد أنه قال: أنا رأيته _ يعنى الأذان _ وأنا كنت أريده قال: " فأقم أنت ".
** د
(بلوغ المرام ٤٠/١)
** ضعيف
** قال الحافظ فى: البلوغ: ١ / ٤٠: فيه ضعف.
** تعقيب: قال الفقى ١ / ٤٠: قال الزيلعى فى نصب الراية: هو من رواية أبى أسامة عن أبى العميس عن عبد الله بن زيد. قال البيهقى: قال الحاكم: هذا فى متنه ضعف فإن أبا أسامة أتى فيه بشىء لم يروه أحد وهو أن بلالا أذن وعبد الله بن زيد أقام.','9','79',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3930,'<span data-type="title" id=toc-3938>٣٩٣٠ </span>- عن أبى هريرة قال: قال رسول الله صلى الله عليه وسلم: " المؤذن أملك بالأذان
والإمام أملك بالإقامة ".
** عد
(بلوغ المرام ٤١/١)
** قال الحافظ فى " البلوغ " ١ / ٤١: ضعفه ابن عدى، وللبيهقى نحوه عن على رضى الله عنه من قوله.
** تعقيب: قال الفقى ١ / ٤١: قال البيهقى: ليس بمحفوظ.','9','80',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3931,'<span data-type="title" id=toc-3939>٣٩٣١ </span>- عن أنس رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " لايرد الدعاء بين الأذان والإقامة ".
** ن
(بلوغ المرام ٤١/١)
** قال الحافظ فى " البلوغ " ١ / ٤١: صححه ابن خزيمة.
** تعقيب: قال الفقى ١ / ٤١: ورواه أبو داود والترمذى.','9','81',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3932,'<span data-type="title" id=toc-3940>٣٩٣٢ </span>- عن على بن طلق قال: قال رسول الله صلى الله عليه وسلم: " إذا فسا أحدكم فى الصلاة فلينصرف وليتوضأ وليعد الصلاة ".
** حم د ت ن جه
(بلوغ المرام ٤١/١)
** قال الحافظ فى " البلوغ " ١ / ٤١: صححه ابن حبان.','9','82',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3933,'<span data-type="title" id=toc-3941>٣٩٣٣ </span>- عن عائشة رضى الله عنها أن النبى صلى الله عليه وسلم قال: " لا يقبل الله صلاة حائض إلا بخمار ".
** حم د ت جه
(بلوغ المرام ٤٢/١)
** قال الحافظ فى " البلوغ " ١ / ٤٢: صححه ابن خزيمة.','9','83',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3934,'<span data-type="title" id=toc-3942>٣٩٣٤ </span>- عن عامر بن ربيعة قال: كنا مع النبى صلى الله عليه وسلم فى ليلة مظلمة فأشكلت علينا القبلة فصلينا فلما طلعت الشمس إذا نحن صلينا إلى غير القبلة فنزلت " فأينما تولوا فثم وجه الله ".
** ت
(بلوغ المرام ٤٢/١)
** قال الحافظ فى " البلوغ " ١ / ٤٢: ضعفه الترمذى.
** تعقيب: قال الفقى ١ / ٤٢: لأنه فيه أشعث بن سعد السمان وهو ضعيف.','9','84',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3935,'<span data-type="title" id=toc-3943>٣٩٣٥ </span>- عن أبى هريرة رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " مابين المشرق والمغرب قبلة ".
** ت
(بلوغ المرام ٤٢/١)
** قال الحافظ فى " البلوغ " ١ / ٤٢: قواه البخارى.
** تعقيب: قال الفقى ١ / ٤٢: قال الترمذى: حسن صحيح، وقد روى عن غير واحد من الصحابة منهم عمر، وعلى وابن عباس.أه.','9','85',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3936,'<span data-type="title" id=toc-3944>٣٩٣٦ </span>- عن أنس رضى الله عنه: كان النبى صلى الله عليه وسلم: " إذا سافر فأراد أن يتطوع استقبل بناقته القبلة فكبر ثم صلى حيث كان وجه ركابه ".
** د
(بلوغ المرام ٤٣/١)
** إسناده حسن','9','86',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3937,'<span data-type="title" id=toc-3945>٣٩٣٧ </span>- عن أبى سعيد الخدرى رضى الله عنه: أن النبى صلى الله عليه وسلم قال: " الأرض كلها مسجد إلا المقبرة والحمام ".
** ت
(بلوغ المرام ٤٣/١)
** قال الحافظ فى " البلوغ " ١ / ٤٣: له علة.
** تعقيب: قال الفقى ١ / ٤٣: قال الحافظ فى الفتح: رجاله ثقات لكن اختلف فى وصله وإرساله وحكم مع ذلك بصحته الحاكم وابن حبان.اه.','9','87',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3938,'<span data-type="title" id=toc-3946>٣٩٣٨ </span>- عن ابن عمر رضى الله عنهما أن النبى صلى الله عليه وسلم: نهى أن يصلى فى سبع مواطن " المزبلة، المجزرة، والمقبرة، وقارعة الطريق، والحمام،
ومعاطن الإبل، وفوق ظهر بيت الله تعالى ".
** ت
(بلوغ المرام ٤٤/١)
** قال الحافظ فى " البلوغ " ١ / ٤٤: ضعفه الترمذى.
** تعقيب: قال الفقى ١ / ٤٤: لأنه من رواية زيد بن جبيرة الأنصارى، قال البخارى وغيره: منكر الحديث
وقد ذكر الحافظ الذهبى فى الميزان له عدة أحاديث منكرة وساق منها هذا.','9','88',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3939,'<span data-type="title" id=toc-3947>٣٩٣٩ </span>- عن أبى سعيد قال: قال رسول الله صلى الله عليه وسلم: " اذا جاء أحدكم المسجد فلينظر فإن رأى فى نعليه أذى أو قذرا فليمسحه وليصل فيهما ".
** د
(بلوغ المرام ٤٤/١)
** قال الحافظ فى " البلوغ " ١ / ٤٤: صححه ابن خزيمة.','9','89',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3940,'<span data-type="title" id=toc-3948>٣٩٤٠ </span>- عن أبى هريرة رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " اذا وطىء أحدكم الأذى بخفيه فطهورهما التراب ".
** د
(بلوغ المرام ٤٤/١)
** قال الحافظ فى " البلوغ " ١ / ٤٤: صححه ابن حبان.
** تعقيب: قال الفقى ١ / ٤٤: قال النووى: إسناده صحيح.','9','90',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3941,'<span data-type="title" id=toc-3949>٣٩٤١ </span>- عن مطرف بن عبد الله بن الشخير عن أبيه قال: " رأيت رسول الله صلى الله عليه
وسلم يصلى وفى صدره أزيز كأزيز المرجل من البكاء ".
** حم د ت ن
(بلوغ المرام ٤٥/١)
** قال الحافظ فى " البلوغ " ١ / ٤٥: صححه ابن حبان.','9','91',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3942,'<span data-type="title" id=toc-3950>٣٩٤٢ </span>- عن ابن عمر رضى الله عنهما قال: قلت لبلال: كيف رأيت النبى صلى الله عليه وسلم يرد عليهم حين يسلمون عليه وهو يصلى قال: " يقول هكذا وبسط كفه ".
** د ت
(بلوغ المرام ٤٥/١)
** قال الحافظ فى " البلوغ " ١ / ٤٥: صححه الترمذى.
** تعقيب: قال الفقى ١ / ٤٧: وقد أخرج مسلم عن جابر، والحاكم عن صهيب مثل ذلك.','9','92',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3943,'<span data-type="title" id=toc-3951>٣٩٤٣ </span>- عن أبى هريرة رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " اقتلوا الأسودين فى الصلاة: الحية والعقرب ".
** د ت ن جه
(بلوغ المرام ٤٦/١)
** قال الحافظ فى " البلوغ " ١ / ٤٦: صححه ابن حبان.','9','93',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3944,'<span data-type="title" id=toc-3952>٣٩٤٤ </span>- عن أبى هريرة أن رسول الله صلى الله عليه وسلم قال: " إذا صلى أحدكم فليجعل تلقاء وجهه شيئا، فإن لم يجد فلينصب عصا فإن لم يكن فليخط خطا ثم لا يضره من مر بين يديه ".
** حم جه
(بلوغ المرام ٤٧/١)
** حسن
** قال الحافظ فى " البلوغ " ١ / ٤٧: صححه ابن حبان ولم يصب من زعم أنه مضطرب بل هو حسن.
** تعقيب: قال الفقى ١ / ٤٧: أورده ابن الصلاح فى مقدمته مثالا للمضطرب وقد رده ابن حجر بقوله ولم يصب..
الخ، والحديث قد صححه أحمد وابن المدينى.','9','94',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3945,'<span data-type="title" id=toc-3953>٣٩٤٥ </span>- عن أبى سعيد الخدرى رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " لا يقطع الصلاة شىء وادرأوا ما استطعتم ".
** د
(بلوغ المرام ٤٧/١)
** إسناده ضعيف
** قال الحافظ فى " البلوغ " ١ / ٤٧: فى سنده ضعف.
** تعقيب: قال الفقى ١ / ٤٧: لأنه من رواية سعيد بن عمير الهمدانى تكلم فيه غير واحد، وأخرج له مسلم مقرونا بغيره.','9','95',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3946,'<span data-type="title" id=toc-3954>٣٩٤٦ </span>- عن أبى ذر رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " إذا قام أحدكم فى الصلاة فلا يمسح الحصى فإن الرحمة تواجهه ".
** حم د ت ن جه
(بلوغ المرام ٤٨/١)
** إسناده صحيح
** قال الحافظ فى " البلوغ " ١ / ٤٨: وزاد أحمد " واحدة أو دع ".
** تعقيب: قال الفقى ١ / ٤٨: رواه مسلم وابن ماجه عن أبى أبى هريرة.','9','96',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3947,'<span data-type="title" id=toc-3955>٣٩٤٧ </span>- " إياك والإلتفات فى الصلاة فإنه هلكة فإن كان لابد ففى التطوع ".
** ت
(بلوغ المرام ٤٨/١)
** قال الحافظ فى " البلوغ " ١ / ٤٨: صححه الترمذى.','9','97',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3948,'<span data-type="title" id=toc-3956>٣٩٤٨ </span>- عن عائشة رضى الله عنها قالت: أمر رسول الله صلى الله عليه وسلم " ببناء المساجد فى الدور وأن تنظف وتطيب ".
** حم د ت
(بلوغ المرام ٤٩/١)
** قال الحافظ فى " البلوغ " ١ / ٥٠: صحح الترمذى إرساله.
** تعقيب: قال الفقى ١ / ٥٠: قد رواه أبو داود مسندا ورجاله ثقات وزيادة الثقة مقبولة.','9','98',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3949,'<span data-type="title" id=toc-3957>٣٩٤٩ </span>- عن أبى هريرة أن رسول الله صلى الله عليه وسلم قال: " إذا رأيتم من يبيع أو يبتاع فى المسجد فقولوا له لا أربح الله تجارتك ".
** ن ت
(بلوغ المرام ٥٠/١)
** قال الحافظ فى " البلوغ " ١ / ٥٠: حسنه الترمذى.','9','99',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3950,'<span data-type="title" id=toc-3958>٣٩٥٠ </span>- عن حكيم بن حزام رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " لا تقام الحدود فى المساجد ولا يستقاد فيها ".
** حم د
(بلوغ المرام ٥٠/١)
** إسناده ضعيف
** تعقيب: قال الفقى ١ / ٥٠: قال الحافظ ابن حجر فى التلخيص: لا بأس باسناده. ورواه أحمد والحاكم والدارقطنى وابن السكن.','9','100',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3951,'<span data-type="title" id=toc-3959>٣٩٥١ </span>- عن أنس قال: قال رسول الله صلى الله عليه وسلم: " لا تقوم الساعة حتى يتباهى الناس فى المساجد ".
** حم د ن جه
(بلوغ المرام ٥١/١)
** قال الحافظ فى " البلوغ " ١ / ٥١: صححه ابن خزيمة.
** تعقيب: قال الفقى ١ / ٥١: رواه البخارى تعليقا.','9','101',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3952,'<span data-type="title" id=toc-3960>٣٩٥٢ </span>- عن ابن عباس رضى الله عنهما قال: قال رسول الله صلى الله عليه وسلم: " ما أمرت بتشييد المساجد ".
** د
(بلوغ المرام ٥١/١)
** قال الحافظ فى " البلوغ " ١ / ٥١: صححه ابن حبان.','9','102',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3953,'<span data-type="title" id=toc-3961>٣٩٥٣ </span>- عن أنس قال: قال رسول الله صلى الله عليه وسلم: " عرضت على أجور أمتى حتى القزاة يخرجها الرجل من المسجد ".
** د ت
(بلوغ المرام ٥٢/١)
** قال الحافظ فى " البلوغ " ١ / ٥٢: استغربه الترمذى وصححه ابن خزيمة.','9','103',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3954,'<span data-type="title" id=toc-3962>٣٩٥٤ </span>- عن عمر رضى الله عنه أنه كان يقول: " سبحانك اللهم وبحمدك وتبارك اسمك وتعالى جدك ولا إله غيرك ".
** م
(بلوغ المرام ٥٤/١)
** إسناده منقطع (موقوفا)
** قال الحافظ فى " البلوغ " ١ / ٥٤: رواه مسلم بسند منقطع ورواه الدارقطنى موصولا وموقوفا.
** تعقيب: قال الفقى ١ / ٥٤: لأن مسلم سمعه من غيره وليس هو على شرطه فإن عبدة بن أبى لبابة راويه عن عمر لم يدرك عمر بل ولم يسمع من ابن عمر انما رواه راوية وقد رواه أبو داود
والحاكم عن عائشة عن النبى صلى الله عليه وسلم مرفوعا.','9','104',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3955,'<span data-type="title" id=toc-3963>٣٩٥٥ </span>- عن عائشة رضى الله عنها قالت: كان رسول الله صلى الله عليه وسلم يستفتح الصلاة بالتكبير والقراءة بالحمد لله رب العالمين، وكان إذا ركع لم يشخص رأسه ولم يصوبه ولكن بين ذلك، وكان إذا رفع من الركوع لم يسجد حتى يستوى قائما، وكان إذا رفع رأسه من السجدة لم يسجد حتى يستوى جالسا، وكان يقول فى كل ركعتين التحية، وكان يفرش رجله اليسرى وينصب اليمنى وكان ينهى عن عقبة الشيطان، وينهى أن يفترش الرجل ذراعيه إفتراش السبع وكان يختم صلاته بالتسليم.
** م
(بلوغ المرام ٥٤/١)
** قال الحافظ فى " البلوغ " ١ / ٥٥: له علة.
** تعقيب: قال الفقى ١ / ٥٥: لأنه من رواية أبى الجوزاء عن عائشة، قال ابن عبد البر: هو مرسل أبو الجوزاء لم يسمع عائشة، وأعل أيضا بأن مسلما أخرجه من طريق الأوزاعى مكاتبة.','9','105',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3956,'<span data-type="title" id=toc-3964>٣٩٥٦ </span>- عن أبى هريرة رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " إذا قرأتم الفاتحة فاقرؤا بسم الله الرحمن الرحيم فإنها أحدى آياتها ".
** قط
(بلوغ المرام ٥٧/١)
** قال الحافظ فى " البلوغ " ١ / ٥٧: صوب الدارقطنى وقفه.','9','106',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3957,'<span data-type="title" id=toc-3965>٣٩٥٧ </span>- عن أبى هريرة قال: " كان رسول الله صلى الله عليه وسلم إذا فرغ من قراءة أم القرأن رفع صوته وقال " آمين ".
** قط ك
(بلوغ المرام ٥٧/١)
** قال الحافظ فى " البلوغ " ١ / ٥٧: حسنه الدارقطنى وصححه الحاكم.','9','107',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3958,'<span data-type="title" id=toc-3966>٣٩٥٨ </span>- عن عبد الله بن أبى أوفى رضى الله عنه قال: جاء رجل الى النبى صلى الله عليه وسلم فقال: " انى لا أستطيع أن آخذ من القرآن شيئا فعلمنى ما يجزئنى منه
فقال: " قل سبحان الله والحمد لله ولا إله إلا الله والله أكبر ولا حول ولا قوة إلا بالله العلى العظيم ... " الحديث.
** حم د ن
(بلوغ المرام ٥٧/١)
** قال الحافظ فى " البلوغ " ١ / ٥٧: صححه ابن حبان والدارقطنى والحاكم.','9','108',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3959,'<span data-type="title" id=toc-3967>٣٩٥٩ </span>- عن سليمان بن يسار قال: " كان فلان يطيل الأوليين من الظهر ويخفف العصر ويقرأ فى المغرب بقصار المفصل وفى العشاء بوسطه وفى الصبح بطواله فقال أبو هريرة: ما صليت وراء أحد أشبه صلاة برسول الله صلى الله عليه وسلم من هذا ".
** ن
(بلوغ المرام ٥٨/١)
** إسناده صحيح (موقوفا)','9','109',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3960,'<span data-type="title" id=toc-3968>٣٩٦٠ </span>- عن حذيفة رضى الله عنه قال: " صليت مع النبى صلى الله عليه وسلم فما مرت به آية رحمة إلا وقف عندها يسأل ولاآية عذاب الا تعوذ منها ".
** حم د ت ن جه
(بلوغ المرام ٥٩/١)
** قال الحافظ فى " البلوغ " ١ / ٥٩: حسنه الترمذى.','9','110',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3961,'<span data-type="title" id=toc-3969>٣٩٦١ </span>- عن عائشة رضى الله عنها قالت: " رأيت رسول الله صلى الله عليه وسلم يصلى متربعا ".
** ن
(بلوغ المرام ٦٠/١)
** قال الحافظ فى " البلوغ " ١ / ٦٠: صححه ابن خزيمة. قال الحافظ فى " البلوغ " ١ / ٨٨: صححه الحاكم.','9','111',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3962,'<span data-type="title" id=toc-3970>٣٩٦٢ </span>- عن ابن عباس رضى الله عنهما: أن النبى صلى الله عليه وسلم كان يقول بين السجدتين " اللهم اغفر لى وارحمنى واهدنى وعافنى وارزقنى ".
** د ت جه
(بلوغ المرام ٦٠/١)
** قال الحافظ فى " البلوغ " ١ / ٦٠: صححه الحاكم.','9','112',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3963,'<span data-type="title" id=toc-3971>٣٩٦٣ </span>- عن أنس رضى الله عنه: " أن النبى صلى الله عليه وسلم كان لا يقنت الا إذا دعا لقوم أو دعا على قوم ".
** لم
(بلوغ المرام ٦١/١)
** قال الحافظ فى " البلوغ " ١ / ٦١: صححه ابن خزيمة.','9','113',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3964,'<span data-type="title" id=toc-3972>٣٩٦٤ </span>- عن ابن عباس قال: " كان رسول الله صلى الله عليه وسلم يعلمنا دعاء ندعو به فى القنوت من صلاة الصبح ".
** هق
(بلوغ المرام ٦٢/١)
** إسناده ضعيف
** قال الحافظ فى " البلوغ " ١ / ٦٢: فى سنده ضعف.','9','114',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3965,'<span data-type="title" id=toc-3973>٣٩٦٥ </span>- عن فضالة بن عبيد رضى الله عنه قال: سمع رسول الله صلى الله عليه وسلم رجلا يدعو فى صلاته ولم يحمد الله ولم يصل على النبى صلى الله عليه وسلم فقال: " عجل هذا " ثم دعاه فقال: " إذا صلى أحدكم فليبدأ بتحميد ربه والثناء عليه ثم يصلى على النبى صلى الله عليه وسلم ثم يدعو بما شاء ".
** حم د ت ن
(بلوغ المرام ٦٤/١)
** قال الحافظ فى " البلوغ " ١ / ٦٤: صححه الترمذى وابن حبان والحاكم.','9','115',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3966,'<span data-type="title" id=toc-3974>٣٩٦٦ </span>- عن وائل بن حجر رضى الله عنه قال: " صليت مع النبى صلى الله عليه وسلم فكان يسلم عن يمينه السلام عليكم ورحمة الله وبركاته وعن شماله السلام عليكم ورحمة الله وبركاته ".
** د
(بلوغ المرام ٦٥/١)
** إسناده صحيح','9','116',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3967,'<span data-type="title" id=toc-3975>٣٩٦٧ </span>- عن معاذ بن جبل: أن رسول الله صلى الله عليه وسلم قال له: أوصيك يا معاذ لا تدعن دبر كل صلاة أن تقول " اللهم أعنى على ذكرك وشكرك وحسن عبادتك ".
** حم د ن
(بلوغ المرام ٦٦/١)
** إسناده قوى','9','117',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3968,'<span data-type="title" id=toc-3976>٣٩٦٨ </span>- عن أبى أمامة رضى الله عنه قال: " قال رسول الله صلى الله عليه وسلم " من قرأ
آية الكرسى دبر كل صلاة مكتوبة لم يمنعه من دخول الجنة إلا الموت ".
** ن
(بلوغ المرام ٦٦/١)
** قال الحافظ فى " البلوغ " ١ / ٦٦: صححه ابن حبان. وزاد فيه الطبرانى " وقل هو الله أحد ".','9','118',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3969,'<span data-type="title" id=toc-3977>٣٩٦٩ </span>- عن جابر رضى الله عنه أن النبى صلى الله عليه وسلم قال لمريض - صلى على وسادة، فرمى بها - وقال: " صل على الأرض إن استطعت وإلا فأوم إيماء واجعل سجودك أخفض من ركوعك ".
** هق
(بلوغ المرام ٦٧/١)
** إسناده قوى
** قال الحافظ فى " البلوغ " ١ / ٦٧: صحح أبو حاتم وقفه.','9','119',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3970,'<span data-type="title" id=toc-3978>٣٩٧٠ </span>- عن عمران بن حصين رضى الله عنه: " أن النبى صلى الله عليه وسلم صلى بهم فسها فسجد سجدتين ثم تشهد ثم سلم ".
** د ت ك
(بلوغ المرام ٦٨/١)
** قال الحافظ فى " البلوغ " ١ / ٦٨: حسنه الترمذى وصححه الحاكم.','9','120',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3971,'<span data-type="title" id=toc-3979>٣٩٧١ </span>- عن عبد الله بن جعفر مرفوعا " من شك فى صلاته فليسجد سجدتين بعد ما يسلم ".
** حم د ن
(بلوغ المرام ٦٩/١)
** قال الحافظ فى " البلوغ " ١ / ٦٩: صححه ابن خزيمة.','9','121',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3972,'<span data-type="title" id=toc-3980>٣٩٧٢ </span>- عن المغيرة بن شعبة أن رسول الله صلى الله عليه وسلم قال: " إذا شك أحدكم
فقام فى الركعتين فاستتم قائما فليمض ولا يعود وليسجد سجدتين فإن لم يستتم
قائما فليجلس ولاسهو عليه ".
** د جه قط
(بلوغ المرام ٦٩/١)
** إسناده ضعيف
** تعقيب: قال الفقى ١ / ٦٩: لأن مدار طرقه على جابر الجعفى وهو ضعيف.','9','122',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3973,'<span data-type="title" id=toc-3981>٣٩٧٣ </span>- عن عمر رضى الله عنه عن النبى صلى الله عليه وسلم قال: " ليس على من خلف الإمام سهو، فإن سهاالإمام فعليه وعلى من خلفه ".
** ت هق
(بلوغ المرام ٦٩/١)
** إسناده ضعيف
** تعقيب: قال الفقى ١ / ٦٩: لأنه بجميع طرقه من رواية خارجة بن مصعب وهو ضعيف.','9','123',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3974,'<span data-type="title" id=toc-3982>٣٩٧٤ </span>- عن ثوبان عن النبى صلى الله عليه وسلم أنه قال: " لكل سهو سجدتان بعد ما يسلم ".
** د جه
(بلوغ المرام ٦٩/١)
** إسناده ضعيف
** تعقيب: قال الفقى ١ / ٧٠: فى تضعيفه نظر لأنه من رواية اسماعيل بن عياش عن الشاميين وهو إنما يضعف إذا روى عن غيرهم.','9','124',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3975,'<span data-type="title" id=toc-3983>٣٩٧٥ </span>- عن خالد بن معدان رضى الله عنه قال: " فضلت سورة الحج بسجدتين ".
** مد
(بلوغ المرام ٧٠/١)
** قال الحافظ فى " البلوغ " ١ / ٧٠: رواه أحمد والترمذى موصولا من حديث عقبة بن عامر وزاد " فمن لم يسجدها فلا يقرأها " وسنده ضعيف.
** تعقيب: قال الفقى ١ / ٧٠: هو موجود أيضا من حديث عقبة بن عامر بلفظ: قلت: يا رسول الله فى سورة الحج سجدتان؟ قال: " نعم ومن لم يسجدهما فلا يقرأها ". لأنه فيه ابن لهيعة قيل إنه تفرد به.','9','125',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3976,'<span data-type="title" id=toc-3984>٣٩٧٦ </span>- عن ابن عمر رضى الله عنهما قال: " كان النبى صلى الله عليه وسلم يقرأ علينا القرآن فإذا مر بالسجدة كبر وسجد وسجدنا معه ".
** د
(بلوغ المرام ٧١/١)
** إسناده لين
** قال الحافظ فى " البلوغ " ١ / ٧١: رواه أبو داود بسند فيه لين.
** تعقيب: قال الفقى ١ / ٧١: لأنه من رواية عبد الله بن عمر بن نافع بن عبد الله بن عمر العمرى وهو ضعيف وأخرجه الحاكم من رواية أخيه عبيد الله وهو ثقة.','9','126',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3977,'<span data-type="title" id=toc-3985>٣٩٧٧ </span>- عن عبد الرحمن بن عوف رضى الله عنه قال: سجد النبى صلى الله عليه وسلم فأطال السجود ثم رفع رأسه فقال: " إن جبريل أتانى فبشرنى فسجدت لله شكرا ".
** حم
(بلوغ المرام ٧١/١)
** قال الحافظ فى " البلوغ " ١ / ٧١: صححه الحاكم.','9','127',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3978,'<span data-type="title" id=toc-3986>٣٩٧٨ </span>- عن ابن عمر رضى الله عنهما قال: قال رسول الله صلى الله عليه وسلم: " رحم
الله امرأ صلى أربعا قبل العصر ".
** حم د ت خز
(بلوغ المرام ٧٢/١)
** قال الحافظ فى " البلوغ " ١ / ٧٢: حسنه الترمذى، صححه ابن خزيمة.
** تعقيب: قال الفقى ١ / ٧٣: فى إسناده محمد بن مهران متكلم فيه. قال الفقى ١ / ٧٣: قال العلامة ابن القيم فى زاد المعاد: وسمعت شيخ الأسلام ابن تيمية رحمه الله ينكر هذا الحديث ويدفعه جدا ويقول: إنه موضوع، ويذكر عن أبى إسحاق الجوزجانى إنكاره.','9','128',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3979,'<span data-type="title" id=toc-3987>٣٩٧٩ </span>- عن أبى هريرة رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " إذا
صلى أحدكم الركعتين قبل صلاة الصبح فليضطجع على جنبه الأيمن ".
** حم د ت
(بلوغ المرام ٧٣/١)
** قال الحافظ فى " البلوغ " ١ / ٧٤: صححه الترمذى.','9','129',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3980,'<span data-type="title" id=toc-3988>٣٩٨٠ </span>- " صلاة الليل والنهار مثنى مثنى ".
** حم د ت ن جه
(بلوغ المرام ٧٤/١)
** قال الحافظ فى " البلوغ " ١ / ٧٤: صححه ابن حبان. وقال النسائى: هذا خطأ.
** تعقيب: قال الفقى ١ / ٧٤: زيادة النهار من رواية على بن عبد الله البارقى الأزدى ضعفه ابن معين وقد خالفه جماعة من أصحاب ابن عمر فلم يذكروها وقد صححها ابن خزيمة وابن حبان والحاكم فى المستدرك وقال: رواتها ثقات. قال الفقى ١ / ٧٤: وقال البيهقى: هذا حديث صحيح. وعلى البارقى احتج به مسلم والزيادة من الثقة مقبولة وقد صححه البخارى لما سئل عنه وقد رواه ابن سيرين عن ابن عمر مرفوعا بإسناد رجاله ثقات.','9','130',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3981,'<span data-type="title" id=toc-3989>٣٩٨١ </span>- عن أبى أيوب الأنصارى أن رسول الله صلى الله عليه وسلم قال: " الوتر حق على كل مسلم، من أحب أن يوتر بخمس فليفعل، ومن أحب أن يوتر بثلاث فليفعل ومن أحب أن يوتر بواحدة فليفعل ".
** د ن جه
(بلوغ المرام ٧٤/١)
** قال الحافظ فى " البلوغ " ١ / ٧٤: صححه ابن حبان ورجح النسائى وقفه.
** تعقيب: قال الفقى ١ / ٧٤: وكذا صحح أبو حاتم والذهبى والدارقطنى فى العلل والبيهقى وغير واحد وقفه قال المصنف فى التلخيص: وهو الصواب.','9','131',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3982,'<span data-type="title" id=toc-3990>٣٩٨٢ </span>- عن على بن أبى طالب رضى الله عنه قال: " ليس الوتر بحتم كهيئة المكتوبة ولكن سنة سنها رسول الله صلى الله عليه وسلم ".
** ت ن ك
(بلوغ المرام ٧٥/١)
** قال الحافظ فى " البلوغ " ١ / ٧٥: حسنه الترمذى وصححه الحاكم.','9','132',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3983,'<span data-type="title" id=toc-3991>٣٩٨٣ </span>- عن خارجة بن حذافة رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " إن الله أمدكم بصلاة هى خير لكم من حمر النعم "، قلنا: وما هى يارسول
الله قال: " الوتر ما بين صلاة العشاء الى طلوع الفجر ".
** حم د ت جه
(بلوغ المرام ٧٥/١)
** قال الحافظ فى " البلوغ " ١ / ٧٥: صححه الحاكم.
** تعقيب: قال الفقى ١ / ٧٥: قال الترمذى حديث غريب لا نعرفه إلا من حديث يزيد بن أبى حبيب وقد وهم بعض المحدثين فى هذا الحديث.','9','133',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3984,'<span data-type="title" id=toc-3992>٣٩٨٤ </span>- عن عبد الله بن بريدة رضى الله عنه عن أبيه قال: قال رسول الله صلى الله عليه وسلم: " الوتر حق فمن لم يوتر فليس منا ".
** د
(بلوغ المرام ٧٥/١)
** إسناده لين
** قال الحافظ فى " البلوغ " ١ / ٧٦: أخرجه أبو داود بسند لين وصححه الحاكم وله شاهد ضعيف عن أبى هريرة رضى الله عنه عند أحمد.
** تعقيب: قال الفقى ١ / ٧٦: لأنه فيه عبد الله بن عبد الله العتكى ضعفه البخارى والنسائى وقال ابن معين إنه موقوف. قال الفقى ١ / ٧٦: فى مسند أحمد بلفظ " من لم يوتر فليس منا " وفى سنده الخليل بن مرة منكر الحديث وسنده منقطع كما قاله أحمد.','9','134',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3985,'<span data-type="title" id=toc-3993>٣٩٨٥ </span>- عن على قال: قال رسول الله صلى الله عليه وسلم: " أوتروا ياأهل القرآن فإن الله وتر يحب الوتر ".
** حم د ت ن جه
(بلوغ المرام ٧٦/١)
** قال الحافظ فى " البلوغ " ١ / ٧٦: صححه ابن خزيمة.','9','135',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3986,'<span data-type="title" id=toc-3994>٣٩٨٦ </span>- عن طلق بن على قال: سمعت رسول الله صلى الله عليه وسلم يقول: " لا وتران فى ليلة ".
** حم د ت ن
(بلوغ المرام ٧٧/١)
** قال الحافظ فى " البلوغ " ١ / ٧٧: صححه ابن حبان.','9','136',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3987,'<span data-type="title" id=toc-3995>٣٩٨٧ </span>- عن أنس رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " من صلى الضحى اثنتى عشرة ركعة بنى الله له قصرا فى الجنة ".
** ت
(بلوغ المرام ٧٨/١)
** قال الحافظ فى " البلوغ " ١ / ٧٨: إستغربه الترمذى.
** تعقيب: قال الفقى ١ / ٧٨: قال الحافظ فى التلخيص: إسناده ضعيف.','9','137',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3988,'<span data-type="title" id=toc-3996>٣٩٨٨ </span>- عن ابن عباس عن النبى صلى الله عليه وسلم قال: " من سمع النداء فلم يأت فلا صلاة له إلا من عذر ".
** جه قط حب ك
(بلوغ المرام ٨٠/١)
** إسناده على شرط مسلم
** قال الحافظ فى " البلوغ " ١ / ٨٠: رجح بعضهم وقفه.','9','138',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3989,'<span data-type="title" id=toc-3997>٣٩٨٩ </span>- عن يزيد بن الأسود أنه صلى مع رسول الله صلى الله عليه وسلم صلاة الصبح فلما صلى رسول الله صلى الله عليه وسلم إذا هو برجلين لم يصليا فدعا بهما ترعد فرائصهما فقال لهما: " ما منعكما أن تصليا معنا؟ " قالا: قد صلينا فى رحالنا قال: " فلا تفعلا إذا صليتما فى رحالكما ثم أدركتما الإمام ولم يصل فصليا معه فإنها لكم نافلة ".
** حم د ت ن
(بلوغ المرام ٨٠/١)
** قال الحافظ فى " البلوغ " ١ / ٨٠: صححه ابن حبان والترمذى.','9','139',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3990,'<span data-type="title" id=toc-3998>٣٩٩٠ </span>- عن جابر رضى الله عنه: " ولا تؤمن امرأة رجلا، ولا أعرابى مهاجرا، ولا فاجر مؤمنا ".
** جه
(بلوغ المرام ٨٢/١)
** إسناده واه
** تعقيب: قال الفقى ١ / ٨٢: فيه عبد الله بن محمد العدوى عن على بن زيد بن جدعان والعدوى اتهمه وكيع بوضع الحديث وشيخه على بن زيد ضعيف.','9','140',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3991,'<span data-type="title" id=toc-3999>٣٩٩١ </span>- عن أنس رضى الله عنه أن النبى صلى الله عليه وسلم قال: "رصوا صفوفكم وقاربوا بينها وحاذوا الأعناق ".
** د ن
(بلوغ المرام ٨٢/١)
** قال الحافظ فى " البلوغ " ١ / ٨٣: صححه ابن حبان.','9','141',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3992,'<span data-type="title" id=toc-4000>٣٩٩٢ </span>- عن وابصة بن معبد رضى الله عنه: " أن رسول الله صلى الله عليه وسلم رأى رجلا يصلى خلف الصف وحده فأمره أن يعيد الصلاة ".
** حم د ت
(بلوغ المرام ٨٣/١)
** قال الحافظ فى " البلوغ " ١ / ٨٣: صححه ابن حبان. وقال الحافظ فى البلوغ ١ / ٨٤: وزاد الطبرانى فى حديث وابصة " ألا دخلت معهم أو اجتررت رجلا ".
** تعقيب: قال الفقى ١ / ٨٤: أخرجه أيضاالدارقطنى وقال ابن عبد البر إنه مضطرب الإسناد ولا يثبته جماعة من أهل الحديث وقال ابن سيد الناس: ليس الأضطراب الذى وقع فيه مما يضره. قال الفقى ١ / ٨٤: فى حديث وابصة عند الطبرانى: فيه السرى بن اسماعيل وهو متروك.','9','142',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3993,'<span data-type="title" id=toc-4001>٣٩٩٣ </span>- عن أبى بن كعب رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " صلاة الرجل مع الرجل أزكى من صلاته وحده، وصلاته مع الرجلين أزكى من صلاته مع الرجل، وماكان أكثر فهو أحب الى الله ".
** د ن
(بلوغ المرام ٨٤/١)
** قال الحافظ فى " البلوغ " ١ / ٨٤: صححه ابن حبان.','9','143',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3994,'<span data-type="title" id=toc-4002>٣٩٩٤ </span>- عن أم ورقة رضى الله عنها " أن النبى صلى الله عليه وسلم أمرها " أن تؤم أهل دارها ".
** د
(بلوغ المرام ٨٤/١)
** قال الحافظ فى " البلوغ " ١ / ٨٤: صححه ابن خزيمة.','9','144',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3995,'<span data-type="title" id=toc-4003>٣٩٩٥ </span>- عن ابن عمر رضى الله عنهما قال: قال رسول الله صلى الله عليه وسلم: " صلوا على من قال لا إله إلا الله، وصلوا خلف من قال لا إله إلا الله ".
** قط
(بلوغ المرام ٨٥/١)
** إسناده ضعيف','9','145',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3996,'<span data-type="title" id=toc-4004>٣٩٩٦ </span>- عن على بن أبى طالب رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " إذا أتى أحدكم الصلاة والإمام على حال فليصنع كما يصنع الإمام ".
** ت
(بلوغ المرام ٨٥/١)
** إسناده ضعيف
** تعقيب: قال الفقى ١ / ٨٥: أخرجه الترمذى من حديث معاذ وعلى وفيه ضعف وانقطاع وقال: لا نعلم أحدا أسنده إلا من هذا الوجه، وقد أخرجه أبو داود من حديث عبد الرحمن بن أبى ليلى
قال: حدثنا أصحابنا أى من الصحابة، لأن عبد الرحمن وإن كان لم يسمع من معاذ فقد سمع من غيره وبهذا تندفع دعوى الانقطاع.','9','146',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3997,'<span data-type="title" id=toc-4005>٣٩٩٧ </span>- عن عائشة رضى الله عنها: " أن النبى صلى الله عليه وسلم كان يقصر فى السفر ويتم ويصوم ويفطر ".
** قط
(بلوغ المرام ٨٥/١)
** رواته ثقات
** قال الحافظ فى " البلوغ " ١ / ٨٦: رواته ثقات إلا أنه معلول والمحفوظ عن عائشة من فعلها وقالت إنه لا يشق على أخرجه البيهقى.
** تعقيب: قال الفقى ١ / ٨٦: استنكره أحمد.','9','147',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3998,'<span data-type="title" id=toc-4006>٣٩٩٨ </span>- عن ابن عمر رضى الله عنهما قال: قال رسول الله صلى الله عليه وسلم: " إن الله تعالى يحب أن تؤتى رخصه كما يكره أن تؤتى معصيته ".
** حم
(بلوغ المرام ٨٦/١)
** قال الحافظ فى " البلوغ " ١ / ٨٦: صححه ابن خزيمة وابن حبان وفى رواية " كما يحب أن تؤتى عزائمه ".','9','148',NULL,NULL,'0');
INSERT INTO [b88] VALUES (3999,'<span data-type="title" id=toc-4007>٣٩٩٩ </span>- عن جابر رضى الله عنه: " أقام بتبوك عشرين يوما يقصر الصلاة ".
** د
(بلوغ المرام ٨٧/١)
** رواته ثقات
** قال الحافظ فى " البلوغ " ١ / ٨٧: رواته ثقات إلا أنه أختلف فى وصله.','9','149',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4000,'<span data-type="title" id=toc-4008><span data-type="title" id=toc-4009>٤٠٠٠ </span></span>- عن ابن عباس رضى الله عنهما قال: قال رسول الله صلى الله عليه وسلم: " لا تقصروا الصلاة فى أقل من أربعة برد من مكة الى عسفان ".
** قط
(بلوغ المرام ٨٨/١)
** إسناده ضعيف
** قال الحافظ فى " البلوغ " ١ / ٨٨: الصحيح أنه موقوف، كذا أخرجه ابن خزيمة.
** تعقيب: قال الفقى ١ / ٨٨: لأنه من رواية عبد الوهاب بن مجاهد نسبه الثورى إلى الكذب، وقال الأزدى: لا
تحل الرواية عنه.اه.','9','150',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4001,'<span data-type="title" id=toc-4010>٤٠٠١ </span>- عن جابر رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " خير أمتى الذين إذا أساؤا استغفروا، وإذا سافروا قصروا وأفطروا ".
** طس
(بلوغ المرام ٨٨/١)
** إسناده ضعيف
** قال الحافظ فى " البلوغ " ١ / ٨٨: هو فى مرسل سعيد بن المسيب عند البيهقى مختصرا.','9','151',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4002,'<span data-type="title" id=toc-4011>٤٠٠٢ </span>- عن ابن عمر رضى الله عنهما قال: قال رسول الله صلى الله عليه وسلم: " من أدرك ركعة من صلاة الجمعة وغيرها فليضف إليها أخرى وقد تمت صلاة ".
** ن جه قط
(بلوغ المرام ٩٠/١)
** إسناده صحيح
** قال الحافظ فى " البلوغ " ١ / ٩٠: قوى أبو حاتم إرساله.','9','152',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4003,'<span data-type="title" id=toc-4012>٤٠٠٣ </span>- عن ابن عباس رضى الله عنهما قال: قال رسول الله صلى الله عليه وسلم: " من
تكلم يوم الجمعة والإمام يخطب فهو كمثل الحمار يحمل أسفارا والذى يقول له
أنصت ليست له جمعة ".
** حم
(بلوغ المرام ٩١/١)
** إسناده لا بأس به','9','153',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4004,'<span data-type="title" id=toc-4013>٤٠٠٤ </span>- عن زيد بن أرقم رضى الله تعالى عنه قال: " صلى النبى صلى الله عليه وسلم
العيد ثم رخص فى الجمعة ثم قال: من شاء أن يصلى فليصل ".
** حم د ن جه
(بلوغ المرام ٩٢/١)
** قال الحافظ فى " البلوغ " ١ / ٩٢: صححه ابن خزيمة.','9','154',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4005,'<span data-type="title" id=toc-4014>٤٠٠٥ </span>- عن جابر رضى الله عنه قال: " مضت السنة أن فى كل أربعين فصاعدا جمعة ".
** قط
(بلوغ المرام ٩٣/١)
** إسناده ضعيف
** تعقيب: قال الفقى ١ / ٩٣: فى إسناده عبد العزيز بن عبد الرحمن، قال أحمد: اضرب على أحاديثه فإنها كذب
أو موضوعة.','9','155',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4006,'<span data-type="title" id=toc-4015>٤٠٠٦ </span>- عن سمرة بن جندب: " أن النبى صلى الله عليه وسلم كان يستغفر للمؤمنين
والمؤمنات كل جمعة ".
** بز
(بلوغ المرام ٩٣/١)
** إسناده لين
** تعقيب: قال الفقى ١ / ٩٣: لأنه من رواية يوسف بن خالد البستى وهو ضعيف.','9','156',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4007,'<span data-type="title" id=toc-4016>٤٠٠٧ </span>- عن طارق بن شهاب أن رسول الله صلى الله عليه وسلم قال: " الجمعة حق واجب على
كل مسلم فى جماعة إلا أربعة مملوك وامرأة وصبى ومريض ".
** د
(بلوغ المرام ٩٤/١)
** قال الحافظ فى " البلوغ " ١ / ٩٤: قال أبو داود لم يسمع طارق من النبى صلى الله عليه وسلم، وأخرجه الحاكم من طريق طارق المذكور عن أبى موسى.','9','157',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4008,'<span data-type="title" id=toc-4017>٤٠٠٨ </span>- عن ابن عمر رضى الله عنهما قال: قال رسول الله صلى الله عليه وسلم:
" ليس على مسافر جمعة ".
** طب
(بلوغ المرام ٩٤/١)
** إسناده ضعيف','9','158',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4009,'<span data-type="title" id=toc-4018>٤٠٠٩ </span>- عن عبد الله بن مسعود رضى الله عنه قال: كان رسول الله صلى الله عليه وسلم: " إذا استوى على المنبر استقبلناه بوجوهنا ".
** ت
(بلوغ المرام ٩٤/١)
** إسناده ضعيف
** تعقيب: قال الفقى ١ / ٩٤: لأنه تفرد به محمد بن الفضل بن عطية وهو ضعيف. وقال الترمذى ذاهب الحديث ولا يصح فى هذا الباب شىء.','9','159',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4010,'<span data-type="title" id=toc-4019>٤٠١٠ </span>- عن حذيفة رضى الله عنه: " أن النبى صلى الله عليه وسلم صلى صلاة الخوف
بهؤلاء ركعة، وبهؤلاء ركعة ولم يقضوا ".
** حم د ن
(بلوغ المرام ٩٦/١)
** قال الحافظ فى " البلوغ " ١ / ٩٧: صححه ابن حبان.','9','160',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4011,'<span data-type="title" id=toc-4020>٤٠١١ </span>- عن ابن عمر رضى الله عنهما قال: قال رسول الله صلى الله عليه وسلم: " صلاة الخوف ركعة على أى وجه كان ".
** بز
(بلوغ المرام ٩٧/١)
** إسناده ضعيف
** تعقيب: قال الفقى ١ / ٩٧: قال المصنف: صححه ابن حبان وغيره.','9','161',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4012,'<span data-type="title" id=toc-4021>٤٠١٢ </span>- عن ابن عمر مرفوعا قال: " ليس فى صلاة الخوف سهو ".
** قط
(بلوغ المرام ٩٧/١)
** إسناده ضعيف','9','162',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4013,'<span data-type="title" id=toc-4022>٤٠١٣ </span>- عن أبى عمير بن أنس بن مالك عن عمومة له من الصحابة: " أن ركبا جاءوا فشهدوا
أنهم رأوا الهلال بالأمس فأمرهم النبى صلى الله عليه وسلم أن يفطروا وإذا
أصبحوا أن يغدوا الى مصلاهم ".
** حم د
(بلوغ المرام ٩٧/١)
** إسناده صحيح','9','163',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4014,'<span data-type="title" id=toc-4023>٤٠١٤ </span>- عن أبن بريدة عن أبيه رضى الله عنه٤ما قال: " كان رسول الله صلى الله عليه
وسلم لا يخرج يوم الفطر حتى يطعم، ولا يطعم يوم الأضحى حتى يصلى ".
** حم ت
(بلوغ المرام ٩٨/١)
** قال الحافظ فى " البلوغ " ١ / ٩٨: صححه ابن حبان.
** تعقيب: قال الفقى ١ / ٩٨: رواه البيهقى وزاد: " وكان إذا رجع أكل من كبد أضحيته ".','9','164',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4015,'<span data-type="title" id=toc-4024>٤٠١٥ </span>- عن أبى سعيد رضى الله عنه قال: " كان النبى صلى الله عليه وسلم لا يصلى
قبل العيد شيئا فإذا رجع الى منزله صلى ركعتين ".
** جه
(بلوغ المرام ٩٨/١)
** إسناده حسن','9','165',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4016,'<span data-type="title" id=toc-4025>٤٠١٦ </span>- عن عمرو بن شعيب عن أبيه عن جده رضى الله عنهم قال: قال نبى الله صلى الله
عليه وسلم: " التكبير فى الفطر سبع فى الأولى وخمس فى الأخرى والقراءة
بعدهما كلتيهما ".
** د
(بلوغ المرام ٩٩/١)
** قال الحافظ فى " البلوغ " ١ / ٩٩: نقل الترمذى عن البخارى تصحيحه.','9','166',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4017,'<span data-type="title" id=toc-4026>٤٠١٧ </span>- عن أنس رضى الله عنه قال: قدم رسول الله صلى الله عليه وسلم المدينة
ولهم يومان يلعبون فيهما فقال: " لقد أبدلكم الله بهما خيرا منهما يوم الأضحى ويوم الفطر ".
** د ن
(بلوغ المرام ٩٩/١)
** إسناده صحيح','9','167',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4018,'<span data-type="title" id=toc-4027>٤٠١٨ </span>- عن على رضى الله عنه قال: " من السنة أن يخرج الى العيد ماشيا ".
** ت
(بلوغ المرام ١٠٠/١)
** قال الحافظ فى " البلوغ " ١ / ١٠٠: حسنه الترمذى.','9','168',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4019,'<span data-type="title" id=toc-4028>٤٠١٩ </span>- عن أبى هريرة رضى الله عنه: " أنهم أصابهم مطر فى يوم عيد فصلى بهم النبى صلى
الله عليه وسلم صلاة العيد فى المسجد.
** د
(بلوغ المرام ١٠٠/١)
** إسناده لين
** تعقيب: قال الفقى ١ / ١٠٠: لأن فى إسناده رجل مجهول.','9','169',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4020,'<span data-type="title" id=toc-4029>٤٠٢٠ </span>- عن ابن عباس رضى الله عنهما: خرج النبى صلى الله عليه وسلم: " متواضعا متبذلا متخشعا، مترسلا، متضرعا، فصلى ركعتين لم يخطب خطبتكم هذه ".
** حم د ت ن جه
(بلوغ المرام ١٠٢/١)
** قال الحافظ فى " البلوغ " ١ / ١٠٢: صححه الترمذى وأبو عوانه وابن حبان.','9','170',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4021,'<span data-type="title" id=toc-4030>٤٠٢١ </span>- عن عائشة رضى الله عنها قالت: شكا الناس إلى رسول الله صلى الله عليه وسلم قحوط المطر، فأمر بمنبر فوضع له بالمصلى ووعد الناس يوما يخرجون فيه فخرج حين بدا حاجب الشمس فقعد على المنبر فكبر وحمد الله ثم قال: " إنكم شكوتم جدب دياركم وقد أمركم الله أن تدعوه، ووعدكم أن يستجيب لكم ثم قال: الحمد لله رب العالمين الرحمن الرحيم مالك يوم الدين،لا إله إلا الله يفعل ما يريد اللهم أنت الله لا إله إلا أنت أنت الغنى ونحن الفقراء، أنزل علينا الغيث وأجعل ماأنزلت علينا قوة وبلاغا إلى حين "، ثم رفع يديه فلم يزل حتى رئى بياض إبطيه ثم حول إلى الناس ظهره، وقلب ردائه، وهو رافع يديه، ثم أقبل على الناس ونزل فصلى ركعتين، فأنشأ الله تعالى سحابة فرعدت وبرقت ثم أمطرت.
** د
(بلوغ المرام ١٠٢/١)
** قال الحافظ فى " البلوغ " ١ / ١٠٣: قال أبو داود: غريب وإسناده جيد.','9','171',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4022,'<span data-type="title" id=toc-4031>٤٠٢٢ </span>- عن أبى هريرة رضى الله عنه أن رسول الله صلى الله عليه وسلم قال: " خرج سليمان عليه السلام يستسقى فرأى نملة مستلقية على ظهرها رافعة قوائمها إلى السماء تقول: اللهم إنا خلق من خلقك ليس بنا غنى عن سقياك فقال: " ارجعوا فقد سقيتم بدعوة غيركم ".
** حم
(بلوغ المرام ١٠٤/١)
** قال الحافظ فى: " البلوغ " ١ / ١٠٤: صححه الحاكم.','9','172',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4023,'<span data-type="title" id=toc-4032>٤٠٢٣ </span>- عن أبى موسى رضى الله عنه أن رسول الله صلى الله عليه وسلم قال: " أحل الذهب والحرير لإناث أمتى وحرم على ذكورها ".
** حم ن ت
(بلوغ المرام ١٠٦/١)
** قال الحافظ فى " البلوغ " ١ / ١٠٦: صححه الترمذى.
** تعقيب: قال الفقى ١ / ١٠٦: هو من حديث سعيد بن أبى هند عن أبى موسى وأعله أبو حاتم بأنه لم يلقه. وكذا قال ابن حبان فى صحيحه: معلول لا يصح.','9','173',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4024,'<span data-type="title" id=toc-4033>٤٠٢٤ </span>- عن أبى هريرة رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: أكثروا ذكر هاذم اللذات: الموت ".
** ت ن
(بلوغ المرام ١٠٧/١)
** قال الحافظ فى " البلوغ " ١ / ١٠٧: صححه ابن حبان.','9','174',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4025,'<span data-type="title" id=toc-4034>٤٠٢٥ </span>- عن بريدة رضى الله عنه أن النبى صلى الله عليه وسلم قال: " المؤمن يموت بعرق الجبين ".
** د ت ن
(بلوغ المرام ١٠٧/١)
** قال الحافظ فى " البلوغ " ١ / ١٠٧: صححه ابن حبان.','9','175',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4026,'<span data-type="title" id=toc-4035>٤٠٢٦ </span>- عن معقل بن يسار أن النبى صلى الله عليه وسلم قال: " اقرءوا على موتاكم يس ".
** د ن
(بلوغ المرام ١٠٧/١)
** قال الحافظ فى " البلوغ " ١ / ١٠٧: صححه ابن حبان.
** تعقيب: قال الفقى ١ / ١٠٧: هو حديث سليمان التيمى عن أبى عثمان _ وليس بالنهدى _ وأعله ابن القطان بالاضطراب والوقف وبجهالة حال أبى عثمان وأبيه. ونقل عن الدارقطنى أنه قال: هذا حديث مضطرب الإسناد ومجهول المتن ولا يصح. اه.','9','176',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4027,'<span data-type="title" id=toc-4036>٤٠٢٧ </span>- عن أبى هريرة عن النبى صلى الله عليه وسلم قال: " نفس المؤمن معلقة بدينه حتى يقضى عنه ".
** حم ت
(بلوغ المرام ١٠٨/١)
** فال الحافظ فى " البلوغ " ١ / ١٠٨: حسنه الترمذى.','9','177',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4028,'<span data-type="title" id=toc-4037>٤٠٢٨ </span>- عن ابن عباس رضى الله عنهما أن النبى صلى الله عليه وسلم قال: " ألبسوا من ثيابكم البياض فإنها من خير ثيابكم وكفنوا فيها موتاكم ".
** حم د ت جه
(بلوغ المرام ١٠٩/١)
** قال الحافظ فى " البلوغ " ١ / ١٠٩: صححه الترمذى.','9','178',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4029,'<span data-type="title" id=toc-4038>٤٠٢٩ </span>- عن عائشة رضى الله عنها أن النبى صلى الله عليه وسلم قال لها: " لو مت قبلى لغسلتك ".
** حم جه
(بلوغ المرام ١١٠/١)
** قال الحافظ فى " البلوغ " ١ / ١١٠: صححه ابن حبان.','9','179',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4030,'<span data-type="title" id=toc-4039>٤٠٣٠ </span>- عن حذيفة رضى الله عنه: " أن النبى صلى الله عليه وسلم كان ينهى عن النعى ".
** حم ت
(بلوغ المرام ١١١/١)
** قال الحافظ فى " البلوغ " ١ / ١١١: حسنه الترمذى.','9','180',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4031,'<span data-type="title" id=toc-4040>٤٠٣١ </span>- عن جابر رضى الله عنه قال: " كان رسول الله صلى الله عليه وسلم يكبر على جنائزنا أربعا ويقرأ بفاتحة الكتاب فى التكبيرة الأولى ".
** شفع
(بلوغ المرام ١١٢/١)
** إسناده ضعيف
** تعقيب: قال الفقى ١ / ١١٢: لأنه عن إبراهيم بن محمد بن عبد الله بن عقيل وقد ضعفوا ابن عقيل.','9','181',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4032,'<span data-type="title" id=toc-4041>٤٠٣٢ </span>- عن أبى هريرة رضى الله عنه أن النبى صلى الله عليه وسلم قال: " إذا صليتم على الميت فأخلصوا له فى الدعاء ".
** د
(بلوغ المرام ١١٣/١)
** قال الحافظ فى " البلوغ " ١ / ١١٣: صححه ابن حبان.','9','182',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4033,'<span data-type="title" id=toc-4042>٤٠٣٣ </span>- عن سالم عن أبيه رضى الله عنهما أنه رأى النبى صلى الله عليه وسلم وأبا بكر وعمر يمشون أمام الجنازة.
** حم د ت ن جه
(بلوغ المرام ١١٤/١)
** قال الحافظ فى " البلوغ " ١ / ١١٤: صححه ابن حبان. وأعله النسائى وطائفة بالإرسال.
** تعقيب: قال الفقى ١ / ١١٤: قال الترمذى: أهل الحديث يرون المرسل أصح. وقد ذكر الدارقطنى فى العلل اختلافا كثيرا فيه عن الزهرى قال: والصحيح قول من قال عن الزهرى عن سالم عن أبيه أنه كان يمش _ موقوفا عليه _. وقال البيهقى: الموصول أرجح لأنه من رواية ابن عيينة وهو ثقة حافظ.','9','183',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4034,'<span data-type="title" id=toc-4043>٤٠٣٤ </span>- عن ابن عمر عن النبى صلى الله عليه وسلم قال: " إذا وضعتم موتاكم فى القبور فقولوا: " بسم الله وعلى ملة رسول الله ".
** حم د ن
(بلوغ المرام ١١٤/١)
** قال الحافظ فى " البلوغ " ١ / ١١٤: صححه ابن حبان. وأعله الدارقطنى بالوقف.
** تعقيب: قال الفقى ١ / ١١٤: رجح النسائى وقفه على ابن عمر إلا أن له شواهد مرفوعة.','9','184',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4035,'<span data-type="title" id=toc-4044>٤٠٣٥ </span>- عن عائشة ضى الله عنها أن رسول الله صلى الله عليه وسلم قال: " كسر عظم الميت ككسره حيا ".
** د
(بلوغ المرام ١١٤/١)
** إسناده على شرط مسلم','9','185',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4036,'<span data-type="title" id=toc-4045>٤٠٣٦ </span>- عن عثمان قال: كان رسول الله صلى الله عليه وسلم إذا فرغ من دفن الميت وقف عليه وقال: " استغفروا لأخيكم واسألوا له التثبيت فإنه الآن يسأل ".
** د
(بلوغ المرام ١١٥/١)
** قال الحافظ فى " البلوغ " ١ / ١١٥: صححه الحاكم.','9','186',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4037,'<span data-type="title" id=toc-4046>٤٠٣٧ </span>- عن أبى هريرة رضى الله عنه: " أن رسول الله صلى الله عليه وسلم لعن زائرات القبور ".
** ت
(بلوغ المرام ١١٦/١)
** قال الحافظ فى " البلوغ " ١ / ١١٦: صححه ابن حبان.','9','187',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4038,'<span data-type="title" id=toc-4047>٤٠٣٨ </span>- عن ابن عباس قال: مر رسول الله صلى الله عليه وسلم بقبور المدينة فأقبل عليهم بوجهه فقال " السلام عليكم ياأهل القبور، يغفر الله لنا ولكم أنتم سلفنا ونحن بالأثر ".
** ت
(بلوغ المرام ١١٧/١)
** قال الحافظ فى " البلوغ " ١ / ١١٧: قال الترمذى: حسن.','9','188',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4039,'<span data-type="title" id=toc-4048>٤٠٣٩ </span>- عن معاذ بن جبل أن النبى صلى الله عليه وسلم بعثه إلى اليمن فأمره أن يأخذ من كل ثلاثين بقرة تبيعا أو تبيعة، ومن كل حالم دينارا أو عدله معافريا.
** حم د ت ن جه
(بلوغ المرام ١٢٠/١)
** قال الحافظ فى " البلوغ " ١ / ١٢٠: حسنه الترمذى وأشار إلى الإختلاف فى وصله، وصححه ابن حبان والحاكم.','9','189',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4040,'<span data-type="title" id=toc-4049>٤٠٤٠ </span>- عن بهز بن حكيم عن أبيه عن جده رضى الله عنهم قال: قال رسول الله صلى الله عليه وسلم: " فى كل سائمة إبل: فى أربعين بنت لبون، لا تفرق إبلها عن حسابها من أعطاها مؤتجرا بها فله أجرها، ومن منعها فإنا آخذوها وشطر ماله، عزمة من عزمات ربنا، لا يحل لآل محمد منها شىء ".
** حم د ن
(بلوغ المرام ١٢٠/١)
** قال الحافظ فى " البلوغ " ١ / ١٢٠: صححه الحاكم وعلق الشافعى القول به على ثبوته.
** تعقيب: قال الفقى ١ / ١٢٠: قال الشافعى: لا يثبته أهل العلم بالحديث. وقال ابن حبان: كان بهز يخطىء كثيرا ولولا هذا الحديث لأدخلته فى الثقات.','9','190',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4041,'<span data-type="title" id=toc-4050>٤٠٤١ </span>- عن على قال: قال رسول الله صلى الله عليه وسلم: " إذا كانت لك مائتا درهم _ وحال عليها الحول _ ففيها خمسة دراهم وليس عليه شىء حتى يكون لك عشرون دينارا أو حال عليها الحول ففيها نصف دينار فما زاد فبحساب ذلك وليس فى مال زكاة عليه حتى يحول عليه الحول ".
** د
(بلوغ المرام ١٢١/١)
** حسن
** قال الحافظ فى " البلوغ " ١ / ١٢١: اختلف فى رفعه.
** تعقيب: قال الفقى ١ / ١٢١: ورواه أيضا أحمد والترمذى والنسائى وهو مروى عن على من طريق عاصم بن ضمرة ومن طريق الحارث الأعور عن على. قال البخارى: وكلاهما عندى صحيح.','9','191',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4042,'<span data-type="title" id=toc-4051>٤٠٤٢ </span>- عن عبد الله بن عمرو أن رسول الله صلى الله عليه وسلم قال: " من ولى يتيما له مال، فليتجر له ولا يتركه حتى تأكله الصدقة ".
** ت قط
(بلوغ المرام ١٢١/١)
** إسناده ضعيف
** قال الحافظ فى " البلوغ " ١ / ١٢١: له شاهد مرسل عند الشافعى.
** تعقيب: قال الفقى ١ / ١٢١: فى سند رواية الترمذى المثنى بن الصباح وفى سند رواية الدارقطنى مندل بن على وهما ضعيفان.','9','192',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4043,'<span data-type="title" id=toc-4052>٤٠٤٣ </span>- عن معاذ قال: فأما القثاء والبطيخ والرمان والقصب فقد عفا عنه رسول الله صلى الله عليه وسلم.
** قط
(بلوغ المرام ١٢٣/١)
** إسناده ضعيف','9','193',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4044,'<span data-type="title" id=toc-4053>٤٠٤٤ </span>- عن سهل بن أبى حثمة قال: " أمرنا رسول الله صلى الله عليه وسلم إذا خرصتم فخذوا ودعوا الثلث فإن لم تدعوا الثلث فدعوا الربع ".
** حم د ت ن
(بلوغ المرام ١٢٣/١)
** قال الحافظ فى " البلوغ " ١ / ١٢٣: صححه ابن حبان والحاكم.','9','194',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4045,'<span data-type="title" id=toc-4054>٤٠٤٥ </span>- عن عتاب بن أسيد قال: " أمر رسول الله صلى الله عليه وسلم أن يخرص العنب كما يخرص النخل وتؤخذ زكاته زبيبا ".
** حم د ت ن جه
(بلوغ المرام ١٢٣/١)
** قال الحافظ فى " البلوغ " ١ /١٢٣: فيه انقطاع.','9','195',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4046,'<span data-type="title" id=toc-4055>٤٠٤٦ </span>- عن عمرو بن شعيب عن أبيه عن جده أن امرأة أتت النبى صلى الله عليه وسلم ومعها ابنة لها وفى يد ابنتها مسكتان من ذهب فقال لها: " أتعطين زكاة هذا؟ "، قالت: لا، قال: " أيسرك أن يسورك الله بهما يوم القيامة سوارين من نار فألقتهما ".
** د ت ن
(بلوغ المرام ١٢٣/١)
** إسناده قوى
** قال الحافظ فى " البلوغ " ١ / ١٢٣: صححه الحاكم من حديث عائشة.','9','196',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4047,'<span data-type="title" id=toc-4056>٤٠٤٧ </span>- عن أم سلمة أنها كانت تلبس أوضاحا من ذهب فقالت: يا رسول الله أكنز هو؟ قال: " إذا أديت زكاته فليس بكنز ".
** د قط
(بلوغ المرام ١٢٣/١)
** قال الحافظ فى " البلوغ " ١ / ١٢٤: صححه الحاكم.','9','197',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4048,'<span data-type="title" id=toc-4057>٤٠٤٨ </span>- عن سمرة بن جندب قال: " كان رسول الله صلى الله عليه وسلم يأمرناأن نخرج
الصدقة من الذى نعده للبيع ".
** د
(بلوغ المرام ١٢٤/١)
** إسناده لين
** تعقيب: قال الفقى ١ / ١٢٤: لأنه من رواية سليمان بن سمرة وهو مجهول.','9','198',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4049,'<span data-type="title" id=toc-4058>٤٠٤٩ </span>- عن عمرو بن شعيب عن أبيه عن جده أن رسول الله صلى الله عليه وسلم قال _ فى كنز وجد فى خربة _: " إن وجدته فى قرية مسكونة فعرفه وإن وجدته فى قرية غير مسكونه ففيه وفى الركاز الخمس ".
** جه
(بلوغ المرام ١٢٤/١)
** إسناده حسن
** تعقيب: قال الفقى ١ / ١٢٤: رواه الشافعى وأبو داود والحاكم والبيهقى.','9','199',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4050,'<span data-type="title" id=toc-4059>٤٠٥٠ </span>- " أغنوهم عن الطواف فى هذا اليوم ".
** عد قط
(بلوغ المرام ١٢٥/١)
** إسناده ضعيف
** تعقيب: قال الفقى ١ / ١٢٥: لأنه من رواية محمد بن عمر الواقدى. قال كاتبه كان عالما بالمغازى والسير والفتوح. قال البخارى: متروك، وكذبه أحمد.','9','200',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4051,'<span data-type="title" id=toc-4060>٤٠٥١ </span>- عن ابن عباس قال: " فرض رسول الله صلى الله عليه وسلم زكاة الفطر طهرة للصائم من اللغو والرفث وطعمة للمساكين، فمن أداها قبل الصلاة فهى زكاة مقبولة ومن أداها بعد الصلاة فهى صدقة من الصدقات ".
** د جه
(بلوغ المرام ١٢٥/١)
** قال الحافظ فى " البلوغ " ١ / ١٢٥: صححه الحاكم.
** تعقيب: قال الفقى ١ / ١٢٥: ورواه الدارقطنى.','9','201',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4052,'<span data-type="title" id=toc-4061>٤٠٥٢ </span>- عن أبى سعيد الخدرى عن النبى صلى الله عليه وسلم قال: " أيما مسلم كسا مسلما ثوبا على عرى كساه الله من خضر الجنة، وأيما مسلم أطعم على جوع أطعمه الله من ثمار الجنة، وأيما مسلم سقى مسلما على ظمأ سقاه الله من الرحيق المختوم ".
** د
(بلوغ المرام ١٢٦/١)
** إسناده لين
** قال الحافظ فى " البلوغ " ١ / ١٢٦: فى إسناده لين.
** تعقيب: قال الفقى ١ / ١٢٦: قال المنذرى: فى إسناده أبو خالد الدالانى أثنى عليه غير واحد وتكلم فيه غير واحد.','9','202',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4053,'<span data-type="title" id=toc-4062>٤٠٥٣ </span>- عن أبى هريرة قال: قيل يا رسول الله أى الصدقة أفضل؟ قال: " جهد المقل وابدأ بمن تعول ".
** حم د
(بلوغ المرام ١٢٦/١)
** قال الحافظ فى " البلوغ " ١ / ١٢٦: صححه ابن خزيمة وابن حبان والحاكم.','9','203',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4054,'<span data-type="title" id=toc-4063>٤٠٥٤ </span>- عن أبى هريرة قال: قال رسول الله صلى الله عليه وسلم: " تصدقوا "، فقال رجل: يا رسول الله عندى دينار، قال: " تصدق به على نفسك "، قال: عندى آخر، قال: " تصدق به على ولدك "، قال: عندى آخر، قال: " تصدق به على زوجتك "، قال: عندى آخر، قال: " تصدق به على خادمك "، قال: عندى آخر قال: " أنت أبصر به ".
** د ن
(بلوغ المرام ١٢٧/١)
** قال الحافظ فى " البلوغ " ١ / ١٢٧: صححه ابن حبان والحاكم.','9','204',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4055,'<span data-type="title" id=toc-4064>٤٠٥٥ </span>- عن سمرة بن جندب قال: قال رسول الله صلى الله عليه وسلم: " المسألة كد يكد بها الرجل وجهه إلا أن يسأل الرجل سلطانا أو فى أمر لابد منه ".
** ت
(بلوغ المرام ١٢٨/١)
** قال الحافظ فى " البلوغ " ١ / ١٢٨: صححه الترمذى.','9','205',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4056,'<span data-type="title" id=toc-4065>٤٠٥٦ </span>- عن أبى سعيد الخدرى قال: قال رسول الله صلى الله عليه وسلم: " لا تحل الصدقة لغنى إلا لخمسة: العامل عليها، أو رجل اشتراها بماله، أو غاز فى سبيل الله، أو مسكين تصدق عليه منها فأهدى منها لغنى ".
** حم د جه ك
(بلوغ المرام ١٢٨/١)
** قال الحافظ فى " البلوغ " ١ / ١٢٨: صححه الحاكم وأعل بالإرسال.','9','206',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4057,'<span data-type="title" id=toc-4066>٤٠٥٧ </span>- عن عبيد الله بن عدى بن الخيار رضى الله عنه: أن رجلين حدثاه أنهما أتيا رسول الله صلى الله عليه وسلم يسألانه الصدقة، فقلب فيهما النظر فرآهما
جلدين فقال: " إن شئتما أعطيتكما ولا حظ فيها لغنى ولا لقوى مكتسب ".
** حم
(بلوغ المرام ١٢٩/١)
** قال الحافظ فى " البلوغ " ١ / ١٢٩: قواه أبو داود والنسائى.
** تعقيب: قال الفقى ١ / ١٢٩: قال الإمام أحمد: هو أجودها إسنادا.','9','207',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4058,'<span data-type="title" id=toc-4067>٤٠٥٨ </span>- عن عمار بن ياسر قال: " من صام اليوم الذى يشك فيه فقد عصى أباالقاسم صلى
الله عليه وسلم ".
** خت حم د ت ن جه
(بلوغ المرام ١٣١/١)
** قال الحافظ فى " البلوغ " ١ / ١٣١: صححه ابن خزيمة وابن حبان.','9','208',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4059,'<span data-type="title" id=toc-4068>٤٠٥٩ </span>- عن ابن عمر قال: " ترائى الناس الهلال، فأخبرت النبى صلى الله عليه وسلم أنى رأيته، فصام وأمر الناس بصيامه ".
** د ك حب
(بلوغ المرام ١٣١/١)
** قال الحافظ فى " البلوغ " ١ / ١٣١: صححه الحاكم وابن حبان.','9','209',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4060,'<span data-type="title" id=toc-4069>٤٠٦٠ </span>- عن ابن عباس رضى الله عنهما أن أعرابيا جاء إلى النبى صلى الله عليه وسلم فقال: إنى رأيت الهلال، فقال: " أتشهد أن لا إله إلا الله "، قال: نعم قال: " أتشهد أن محمدا رسول الله "، قال: نعم، قال: " فأذن فى الناس يابلال أن يصوموا غدا ".
** حم د ت ن جه
(بلوغ المرام ١٣١/١)
** قال الحافظ فى " البلوغ " ١ / ١٣١: صححه ابن خزيمة وابن حبان. ورجح النسائى إرساله.','9','210',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4061,'<span data-type="title" id=toc-4070>٤٠٦١ </span>- عن حفصة أم المؤمنين: أن النبى صلى الله عليه وسلم قال: " من لم يبيت الصيام قبل الفجر فلا صيام له ".
** حم د ت ن جه
(بلوغ المرام ١٣١/١)
** قال الحافظ فى " البلوغ " ١ / ١٣٢: مال الترمذى والنسائى إلى ترجيح وقفه، وصححه مرفوعاابن خزيمة وابن حبان. وللدارقطنى: " لا صيام لمن لم يفرضه من الليل ".','9','211',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4062,'<span data-type="title" id=toc-4071>٤٠٦٢ </span>- عن سليمان بن عامر الضبى عن النبى صلى الله عليه وسلم قال: " إذا أفطر أحدكم فليفطر على تمر، فإن لم يجد فليفطر على ماء فإنه طهور ".
** حم د ت ن جه
(بلوغ المرام ١٣٣/١)
** قال الحافظ فى " البلوغ " ١ / ١٣٣: صححه ابن خزيمة وابن حبان والحاكم.','9','212',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4063,'<span data-type="title" id=toc-4072>٤٠٦٣ </span>- عن شداد بن أوس أن النبى صلى الله عليه وسلم أتى على رجل بالبقيع وهو يحتجم فى رمضان، فقال: " أفطر الحاجم والمحجوم ".
** حم د ن جه
(بلوغ المرام ١٣٤/١)
** قال الحافظ فى " البلوغ " ١ / ١٣٤: صححه أحمد وابن خزيمة وابن حبان.
** تعقيب: قال الفقى ١ / ١٣٤: قال السيوطى فى الجامع الصغير متواتر، وقال الإمام أحمد: فيه غير حديث ثابت. وقال اسحاق: قد ثبت هذا من خمسة أوجه عن النبى صلى الله عليه وسلم.','9','213',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4064,'<span data-type="title" id=toc-4073>٤٠٦٤ </span>- عن أنس بن مالك قال: أول ما كرهت الحجامة للصائم أن جعفر بن أبى طالب احتجم وهو صائم فمر به النبى صلى الله عليه وسلم فقال: " أفطر هذان " ثم رخص النبى صلى الله عليه وسلم بعد فى الحجامة للصائم، وكان أنس يحتجم وهو صائم.
** قط
(بلوغ المرام ١٣٤/١)
** قال الحافظ فى " البلوغ " ١ / ١٣٤: قواه الدارقطنى.','9','214',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4065,'<span data-type="title" id=toc-4074>٤٠٦٥ </span>- عن عائشة: " أن النبى صلى الله عليه وسلم اكتحل فى رمضان وهو صائم ".
** جه
(بلوغ المرام ١٣٤/١)
** إسناده ضعيف
** قال الحافظ فى " البلوغ " ١ / ١٣٤: قال الترمذى: لا يصح فى هذا الباب شىء.','9','215',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4066,'<span data-type="title" id=toc-4075>٤٠٦٦ </span>- " من أفطر فى رمضان ناسيا فلا قضاء عليه ولا كفارة ".
** ك
(بلوغ المرام ١٣٥/١)
** صحيح','9','216',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4067,'<span data-type="title" id=toc-4076>٤٠٦٧ </span>- عن أبى هريرة رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " من ذرعه القىء فلا قضاء عليه، ومن استقاء فعليه القضاء ".
** حم د ت ن جه
(بلوغ المرام ١٣٥/١)
** قال الحافظ فى " البلوغ " ١ / ١٣٥: أعله أحمد، وقواه الدارقطنى.
** تعقيب: قال الفقى ١ / ١٣٥: قال الترمذى: حسن غريب لا نعرفه من حديث هشام عن ابن سيرين عن أبى هريرة عن النبى صلى الله عليه وسلم إلا من حديث عيسى بن يونس ولا يصح إسناده. قال البخارى: لا أراه محفوظا.','9','217',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4068,'<span data-type="title" id=toc-4077>٤٠٦٨ </span>- عن ابن عباس قال: " رخص للشيخ الكبير أن يفطر ويطعم عن كل يوم مسكينا ولا قضاء عليه ".
** قط ك
(بلوغ المرام ١٣٦/١)
** قال الحافظ فى " البلوغ " ١ / ١٣٦: صححه الدارقطنى والحاكم.','9','218',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4069,'<span data-type="title" id=toc-4078>٤٠٦٩ </span>- عن أبى ذر قال: " أمرنا رسول الله صلى الله عليه وسلم أن نصوم من الشهر ثلاثة أيام: ثلاث عشرة، وأربع عشرة، وخمس عشرة ".
** ن ت
(بلوغ المرام ١٣٨/١)
** قال الحافظ فى " البلوغ " ١ / ١٣٨: صححه ابن حبان.
** تعقيب: قال الفقى ١ / ١٣٨: أخرجه أحمد والنسائى وابن حبان من حديث أبى هريرة. وأصحاب السنن من حديث قتادة بن ملحان، والنسائى وأصحاب السنن من حديث جرير. وصححه ابن خزيمة من حديث ابن مسعود.','9','219',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4070,'<span data-type="title" id=toc-4079>٤٠٧٠ </span>- عن أبى هريرة أن رسول الله صلى الله عليه وسلم قال: " إذا انتصف شعبان فلا تصوموا ".
** حم د ت ن جه
(بلوغ المرام ١٣٩/١)
** قال الحافظ فى " البلوغ " ١ / ١٣٩: استنكره أحمد.
** تعقيب: قال الفقى ١ / ١٣٩: لأنه من رواية العلاء بن عبد الرحمن ولكنه من رجال مسلم. وقال المصنف فى التقريب: صدوق. وصححه ابن حبان وغيره.','9','220',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4071,'<span data-type="title" id=toc-4080>٤٠٧١ </span>- عن الصماء بنت بسر أن رسول الله صلى الله عليه وسلم قال: " لا تصوموا يوم السبت إلا فيما افترض عليكم فإن لم يجد أحدكم إلا لحاء عنب أو عود شجرة فليمضغها ".
** حم د ت ن جه
(بلوغ المرام ١٣٩/١)
** رجاله ثقات
** قال الحافظ فى " البلوغ " ١ / ١٣٩: رجاله ثقات، إلا أنه مضطرب، وقد أنكره مالك. وقال أبو داود: هو منسوخ.
** تعقيب: قال الفقى ١ / ١٣٩: لأنه رواه عبد الله بن بسر عن أخته الصماء وقيل عنه عن أبيه بسر وقيل عن الصماء عن عائشة. قال أبو داود عن مالك بن أنس: هو كذب.','9','221',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4072,'<span data-type="title" id=toc-4081>٤٠٧٢ </span>- عن أم سلمة أن رسول الله صلى الله عليه وسلم كان أكثر ما يصوم من الأيام يوم السبت والأحد وكان يقول: " إنهما يوما عيد للمشركين
وأناأريد أن أخالفهم ".
** ن خز
(بلوغ المرام ١٣٩/١)
** قال الحافظ فى " البلوغ " ١ / ١٣٩: صححه ابن خزيمة وهذا لفظه.','9','222',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4073,'<span data-type="title" id=toc-4082>٤٠٧٣ </span>- عن أبى هريرة: " أن النبى صلى الله عليه وسلم نهى عن صوم يوم عرفة بعرفة ".
** حم د ن جه
(بلوغ المرام ١٣٩/١)
** قال الحافظ فى " البلوغ " ١ / ١٣٩: صححه ابن خزيمة والحاكم. واستنكره العقيلى.
** تعقيب: قال الفقى ١ / ١٣٩: لأن فى إسناده مهديا الهجرى ضعفه العقيلى وقال: لا يتابع عليه.
والراوى عنه مختلف فيه.','9','223',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4074,'<span data-type="title" id=toc-4083>٤٠٧٤ </span>- عن عائشة قالت: " السنة على المعتكف أن لا يعود مريضا ولا يشهد جنازة ولا يمس امرأة ولا يباشرها ولا يخرج لحاجة إلا لما لابد له منه ولا اعتكاف إلا بصوم ولا اعتكاف إلا فى مسجد جامع ".
** د
(بلوغ المرام ١٤١/١)
** رجاله لا بأس بهم
** قال الحافظ فى " البلوغ " ١ / ١٤١: لا بأس برجاله إلا أن الراجح وقف آخره.','9','224',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4075,'<span data-type="title" id=toc-4084>٤٠٧٥ </span>- عن عائشة قالت: قلت يا رسول الله: أرأيت إن علمت أى ليلة ليلة القدر، ما أقول فيها؟ قال: قولى " اللهم إنك عفو تحب العفو فاعف عنى ".
** حم ت ن جه
(بلوغ المرام ١٤١/١)
** قال الحافظ فى " البلوغ " ١ / ١٤١: صححه الترمذى والحاكم.','9','225',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4076,'<span data-type="title" id=toc-4085>٤٠٧٦ </span>- عن عائشة قالت: قلت يا رسول الله على النساء جهاد؟ قال: " نعم عليهن جهاد لا قتال فيه: الحج والعمرة ".
** حم جه
(بلوغ المرام ١٤٢/١)
** إسناده صحيح
** قال الحافظ فى " البلوغ " ١ / ١٤٢: أصله فى الصحيح.','9','226',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4077,'<span data-type="title" id=toc-4086>٤٠٧٧ </span>- عن جابر مرفوعا: " الحج والعمرة فريضتان ".
** عد
(بلوغ المرام ١٤٢/١)
** ضعيف
** تعقيب: قال الفقى ١ / ١٤٢: لأن فى إسناده أبا عصمة وهو ضعيف. وقال ابن حزم إنه كذب.','9','227',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4078,'<span data-type="title" id=toc-4087>٤٠٧٨ </span>- عن أنس قال: قيل يا رسول الله: ماالسبيل؟ قال: " الزاد والراحلة ".
** قط
(بلوغ المرام ١٤٣/١)
** قال الحافظ فى " البلوغ " ١ / ١٤٣: صححه الحاكم. والراجح ارساله، وأخرجه الترمذى من حديث ابن عمر وفى إسناده ضعف.
** تعقيب: قال الفقى ١ / ١٤٣ _ فى تعليقه على حديث ابن عمر عند الترمذى _: فى إسناده إبراهيم بن يزيد الخوزى. ضعفه أحمد والنسائى. وفى الباب أحاديث أخر عن بعض الصحابة تعضد هذا، والمرسل الذى قبله.','9','228',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4079,'<span data-type="title" id=toc-4088>٤٠٧٩ </span>- عن ابن عباس رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " أيما
صبى حج ثم بلغ الحنث فعليه أن يحج حجة أخرى، وأيما عبد حج ثم أعتق فعليه حجة
أخرى ".
** شب هق
(بلوغ المرام ١٤٣/١)
** رجاله ثقات
** قال الحافظ فى " البلوغ " ١ / ١٤٤: رجاله ثقات إلا أنه اختلف فى رفعه والمحفوظ أنه موقوف.
** تعقيب: قال الفقى ١ / ١٤٤: قال ابن خزيمة: الصحيح أنه موقوف. وللمحدثين كلام كثير فى رفعه ووقفه.','9','229',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4080,'<span data-type="title" id=toc-4089>٤٠٨٠ </span>- عن ابن عباس أن النبى صلى الله عليه وسلم سمع رجلا يقول لبيك عن شبرمة قال: " من شبرمة "، قال: أخ لى أو قريب لى، فقال: " حججت عن نفسك " قال: لا، قال: " حج عن نفسك ثم حج عن شبرمة ".
** د جه
(بلوغ المرام ١٤٤/١)
** قال الحافظ فى " البلوغ " ١ / ١٤٤: صححه ابن حبان. والراجح عند أحمد وقفه.
** تعقيب: قال الفقى ١ / ١٤٤: قال المصنف فى التلخيص: ورواه الدارقطنى والبيهقى وقال: إسناده صحيح وليس فى هذا الباب أصح منه. وقال ابن تيمية: إن أحمد حكم فى رواية ابنه صالح عنه أنه مرفوع، فيكون قد اطلع على ثقة رفعه، قال: وقد رفعه جماعة.','9','230',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4081,'<span data-type="title" id=toc-4090>٤٠٨١ </span>- عن خلاد بن السائب عن أبيه أن رسول الله صلى الله عليه وسلم قال: " أتانى جبريل فأمرنى أن آمر أصحابى أن يرفعوا أصواتهم بالإهلال ".
** حم د ت ن جه
(بلوغ المرام ١٤٦/١)
** قال الحافظ فى " البلوغ " ١ / ١٤٦: صححه الترمذى وابن حبان.','9','231',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4082,'<span data-type="title" id=toc-4091>٤٠٨٢ </span>- عن زيد بن ثابت: " أن النبى صلى الله عليه وسلم تجرد لإهلاله واغتسل ".
** ت
(بلوغ المرام ١٤٦/١)
** قال الحافظ فى " البلوغ " ١ / ١٤٦: حسنه الترمذى.','9','232',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4083,'<span data-type="title" id=toc-4092>٤٠٨٣ </span>- عن خزيمة بن ثابت: " أن النبى صلى الله عليه وسلم كان إذا فرغ من تلبيته فى حج أو عمرة سأل الله رضوانه والجنة واستعاذ برحمته من النار ".
** شفع
(بلوغ المرام ١٥١/١)
** إسناده ضعيف
** تعقيب: قال الفقى ١ / ١٥١: فى إسناده صالح بن محمد بن زائدة أبو واقد الليثى: ضعيف.','9','233',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4084,'<span data-type="title" id=toc-4093>٤٠٨٤ </span>- عن يعلى بن أمية قال: " طاف رسول الله صلى الله عليه وسلم مضطبعا ببرد
أخضر ".
** حم د ت جه
(بلوغ المرام ١٥٣/١)
** قال الحافظ فى " البلوغ " ١ / ١٥٣: صححه الترمذى.','9','234',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4085,'<span data-type="title" id=toc-4094>٤٠٨٥ </span>- عن ابن عباس قال: قال لنا رسول الله صلى الله عليه وسلم: " لا ترموا الجمرة حتى تطلع الشمس ".
** حم د ت جه
(بلوغ المرام ١٥٣/١)
** قال الحافظ فى " البلوغ " ١ / ١٥٣: فيه انقطاع.','9','235',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4086,'<span data-type="title" id=toc-4095>٤٠٨٦ </span>- عن عائشة قالت: " أرسل النبى صلى الله عليه وسلم بأم سلمة ليلة النحر فرمت الجمرة قبل الفجر ثم مضت فأفاضت ".
** د
(بلوغ المرام ١٥٣/١)
** إسناده على شرط مسلم','9','236',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4087,'<span data-type="title" id=toc-4096>٤٠٨٧ </span>- عن عروة بن مضرس قال: قال رسول الله صلى الله عليه وسلم: " من شهد صلاتنا هذه _ يعنى بالمزدلفة _ فوقف معنا حتى ندفع، وقد وقف بعرفة قبل ذلك ليلاأو نهارا فقد تم حجة وقضى تفثه ".
** حم د ت ن جه
(بلوغ المرام ١٥٤/١)
** قال الحافظ فى " البلوغ " ١ / ١٥٤: صححه الترمذى وابن خزيمة.','9','237',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4088,'<span data-type="title" id=toc-4097>٤٠٨٨ </span>- عن عائشة قالت: قال رسول الله صلى الله عليه وسلم: " إذا رميتم وحلقتم فقد حل لكم الطيب وكل شىء إلا النساء ".
** حم د
(بلوغ المرام ١٥٥/١)
** إسناده ضعيف
** قال الحافظ فى " البلوغ " ١ / ١٥٥: فى إسناده ضعف.','9','238',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4089,'<span data-type="title" id=toc-4098>٤٠٨٩ </span>- عن ابن عباس أن النبى صلى الله عليه وسلم قال: " ليس على النساء حلق وإنما يقصرون ".
** د
(بلوغ المرام ١٥٥/١)
** إسناده حسن','9','239',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4090,'<span data-type="title" id=toc-4099>٤٠٩٠ </span>- عن عاصم بن عدى: " أن رسول الله صلى الله عليه وسلم رخص لرساء الإبل فى البيتوتة عن منى يرمون يوم النحر، ثم يرمون الغد ومن بعد الغد، ليومين ثم يرمون يوم النفر ".
** حم د ت ن جه
(بلوغ المرام ١٥٦/١)
** قال الحافظ فى " البلوغ " ١ / ١٥٦: صححه االترمذى وابن حبان.','9','240',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4091,'<span data-type="title" id=toc-4100>٤٠٩١ </span>- عن سراء بنت نبهان رضى الله عنها قالت: خطبنا رسول الله صلى الله عليه وسلم
يوم الرؤوس، فقال: " أليس هذا أوسط أيام التشريق ".... الحديث
** د
(بلوغ المرام ١٥٦/١)
** إسناده حسن','9','241',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4092,'<span data-type="title" id=toc-4101>٤٠٩٢ </span>- عن ابن عباس: " أن النبى صلى الله عليه وسلم لم يرمل فى السبع الذى أفاض
فيه ".
** حم د ن جه
(بلوغ المرام ١٥٧/١)
** قال الحافظ فى " البلوغ " ١ / ١٥٧: صححه الحاكم.','9','242',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4093,'<span data-type="title" id=toc-4102>٤٠٩٣ </span>- عن ابن الزبير قال: قال رسول الله صلى الله عليه وسلم: " صلاة فى مسجدى هذا أفضل من ألف صلاة فيما سواه إلا المسجد الحرام وصلاة فى المسجد الحرام أفضل من صلاة فى مسجدى هذا بمائة صلاة ".
** حم
(بلوغ المرام ١٥٧/١)
** قال الحافظ فى " البلوغ " ١ / ١٥٧: صححه ابن حبان.','9','243',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4094,'<span data-type="title" id=toc-4103>٤٠٩٤ </span>- عن عكرمة عن الحجاج بن عمرو الأنصارى قال: قال رسول الله صلى الله عليه وسلم: " من كسر أو عرج فقد حل وعليه الحج من قابل ". قال عكرمة: فسألت ابن عباس وأبا هريرة عن ذلك فقالا: صدق.
** حم د ت ن جه
(بلوغ المرام ١٥٨/١)
** قال الحافظ فى " البلوغ " ١ / ١٥٨: حسنه الترمذى.','9','244',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4095,'<span data-type="title" id=toc-4104>٤٠٩٥ </span>- عن ابن مسعود قال: سمعت رسول الله صلى الله عليه وسلم يقول: " إذا اختلف المتبايعان وليس بينهما بينة، فالقول ما يقول رب
السلعة أو يتتاركان ".
** حم د ت ن جه
(بلوغ المرام ١٥٩/١)
** قال الحافظ فى " البلوغ " ١ / ١٥٩: صححه الحاكم.','9','245',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4096,'<span data-type="title" id=toc-4105>٤٠٩٦ </span>- عن أبى هريرة رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " إذا وقعت فأرة فى السمن فإن كان جامدا فألقوها وما حولها وإن كان مائعا فلا تقربوه ".
** حم د
(بلوغ المرام ١٦٠/١)
** قال الحافظ فى " البلوغ " ١ / ١٦٠: حكم عليه البخارى وأبو حاتم بالوهم.','9','246',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4097,'<span data-type="title" id=toc-4106>٤٠٩٧ </span>- عن ابن عمر رضى الله عنهما قال: نهى عمر عن بيع أمهات الأولاد فقال: " لا تباع، ولا توهب، ولا تورث، يستمتع بها ما بدا له فإذا مات فهى حرة ".
** ما هق
(بلوغ المرام ١٦١/١)
** قال الحافظ فى " البلوغ " ١ / ١٦١: قال البيهقى: رفعه بعض الرواة فوهم.','9','247',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4098,'<span data-type="title" id=toc-4107>٤٠٩٨ </span>- عن جابر رضى الله عنه قال: كنا نبيع سرارينا أمهات الأولاد والنبى صلى الله
عليه وسلم حى لا يرى بذلك بأسا.
** ن جه قط حب
(بلوغ المرام ١٦١/١)
** قال الحافظ فى " البلوغ " ١ / ١٦١: صححه ابن حبان.','9','248',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4099,'<span data-type="title" id=toc-4108>٤٠٩٩ </span>- عن أبى هريرة رضى الله عنه قال: نهى رسول الله صلى الله عليه وسلم " عن بيعتين فى بيعة ".
** حم ن
(بلوغ المرام ١٦٢/١)
** قال الحافظ فى " البلوغ " ١ / ١٦٢: صححه الترمذى وابن حبان.
** تعقيب: قال الفقى ١ / ١٦٢: ورواه أيضا مالك فى بلاغاته _ وهى صحيحة _ والشافعى، ورجال أحمد
رجال الصحيح.','9','249',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4100,'<span data-type="title" id=toc-4109>٤١٠٠ </span>- عن عمرو بي شعيب عن أبيه عن جده رضى الله عنهما قال: قال رسول الله صلى الله
عليه وسلم: " لا يحل سلف وبيع ولا شرطان فى بيع، ولا ربح مالم يضمن ولا
بيع ما ليس عندك ".
** حم د ت ن جه
(بلوغ المرام ١٦٢/١)
** قال الحافظ فى " البلوغ " ١ / ١٦٢: صححه الترمذى وابن خزيمة والحاكم، وأخرجه فى علوم الحديث من رواية أبى حنيفة عن عمرو المذكور بلفظ: نهى عن بيع وشرط. ومن هذا الوجه أخرجه الطبرانى فى الأوسط، وهو غريب.','9','250',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4101,'<span data-type="title" id=toc-4110>٤١٠١ </span>- عن ابن عمر رضى الله عنهما قال: ابتعت زيتا فى السوق، فلما استوجبته لقينى رجلا فأعطانى به ربحا حسنا فأردت أن أضرب على يد الرجل فأخذ رجل من خلفى بذراعى فالتفت فإذا هو زيد بن ثابت، فقال: لا تبعه حتى تحوزه إلى رحلك فإن رسول الله صلى الله عليه وسلم نهى أن تباع السلع حيث تبتاع حتى يحوزها التجار إلى رحالهم.
** حم د
(بلوغ المرام ١٦٣/١)
** قال الحافظ فى " البلوغ " ١ / ١٦٣: صححه ابن حبان والحاكم.','9','251',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4102,'<span data-type="title" id=toc-4111>٤١٠٢ </span>- عن ابن عمر قال: قلت: يا رسول الله إنى أبيع الإبل بالبقيع فأبيع بالدنانير وآخذ الدراهم، وأبيع بالدراهم وآخذ الدنانير، آخذ هذا من هذه وأعطى هذه
من هذا، فقال رسول الله صلى الله عليه وسلم: " لا بأس أن تأخذها بسعر يومها
مالم تفترقا وبينكما شىء ".
** حم د ت ن جه
(بلوغ المرام ١٦٣/١)
** قال الحافظ فى " البلوغ " ١ / ١٦٣: صححه الحاكم.','9','252',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4103,'<span data-type="title" id=toc-4112>٤١٠٣ </span>- عن جابر رضى الله عنه أن النبى صلى الله عليه وسلم: " نهى عن المحاقلة والمزابنة والمخابرة وعن الثنيا، إلا أن تعلم ".
** حم د ت ن
(بلوغ المرام ١٦٤/١)
** قال الحافظ فى " البلوغ " ١ / ١٦٤: صححه الترمذى.','9','253',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4104,'<span data-type="title" id=toc-4113>٤١٠٤ </span>- عن أبى أيوب الأنصارى رضى الله عنه قال: سمعت رسول الله صلى الله عليه وسلم
يقول: " من فرق بين والدة وولدها فرق الله بينه وبين أحبته يوم القيامة ".
** حم
(بلوغ المرام ١٦٥/١)
** قال الحافظ فى " البلوغ " ١ / ١٦٥: صححه الترمذى والحاكم. لكن فى إسناده مقال وله شاهد.
** تعقيب: قال الفقى ١ / ١٥٦: فى إسناده حسين بن عبد الله المعافرى مختلف فيه، وشاهده من حديث أبى موسى عند ابن ماجه والدارقطنى بإسناد لا بأس به بلفظ: " لعن رسول الله صلى الله عليه وسلم من فرق بين الوالد وولده وبين الأخ وأخيه "، وعن عبادة بن الصامت عند الحاكم والدارقطنى بإسناد ضعيف " لا يفرق بين الأم وولدها " قيل: إلى متى؟ قال: " حتى يبلغ الغلام وتحيض الجارية ".','9','254',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4105,'<span data-type="title" id=toc-4114>٤١٠٥ </span>- عن على بن أبى طالب رضى الله عنه قال: أمرنى رسول الله صلى الله عليه وسلم
أن أبيع غلامين أخوين، فبعتهما، ففرقت بينهما فذكرت ذلك للنبى صلى الله عليه
وسلم فقال: " أدركهما فارتجعهما ولا تبعهما إلا جميعا ".
** حم
(بلوغ المرام ١٦٥/١)
** رجاله ثقات
** قال الحافظ فى " البلوغ " ١/ ١٦٥: صححه ابن خزيمة وابن الجارود وابن حبان والحاكم والطبرانى وابن القطان.','9','255',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4106,'<span data-type="title" id=toc-4115>٤١٠٦ </span>- عن أنس بن مالك قال: غلا السعر فى المدينة على عهد رسول الله صلى الله عليه وسلم، فقال الناس: يارسول الله غلا السعر فسعر لنا، فقال رسول الله صلى الله عليه وسلم: " إن الله هو المسعر، القابض، الباسط الرازق، وإنى لأرجو أن ألقى الله تعالى وليس أحد منكم يطلبنى بمظلمة ولا مال ".
** حم د ت جه
(بلوغ المرام ١٦٥/١)
** قال الحافظ فى " البلوغ " ١ / ١٦٦: صححه ابن حبان.
** تعقيب: قال الفقى ١ / ١٦٦: قال الحافظ فى التلخيص: إسناده على شرط مسلم.','9','256',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4107,'<span data-type="title" id=toc-4116>٤١٠٧ </span>- عن عبد الله بن بريدة عن أبيه قال: قال رسول الله صلى الله عليه وسلم: " من حبس العنب أيام القطاف حتى يبيعه ممن يتخذه خمرا فقد تقحم النار على بصيرة ".
** طب
(بلوغ المرام ١٦٧/١)
** إسناده حسن','9','257',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4108,'<span data-type="title" id=toc-4117>٤١٠٨ </span>- عن عائشة قالت: قال رسول الله صلى الله عليه وسلم: " الخراج بالضمان ".
** حم د ت ن جه
(بلوغ المرام ١٦٧/١)
** قال الحافظ فى " البلوغ " ١ / ١٦٧: ضعفه البخارى وأبو داود. صححه الترمذى وابن خزيمة وابن الجارود وابن حبان
والحاكم وابن القطان.
** تعقيب: قال الفقى ١ / ١٦٧: لهذا الحديث عند أبى داود ثلاث طرق: اثنان منها رجالهما رجال الصحيح والثالث قال فيها أبو داود: إسناده ليس بذاك ولعله لأن فيها مسلم
بن خالد الزنجى: شيخ الشافعى وهو متفق على الاحتجاج به.','9','258',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4109,'<span data-type="title" id=toc-4118>٤١٠٩ </span>- عن أبى سعيد الخدرى رضى الله عنه أن النبى صلى الله عليه وسلم: " نهى عن
شراء ما فى بطون الأنعام حتى تضع وعن بيع ما فى ضروعها وعن شراء العبد وهو آبق وعن شراء المغانم حتى تقسم، وعن شراء الصدقات حتى تقبض وعن ضربة الغائص ".
** جه بز قط
(بلوغ المرام ١٦٧/١)
** إسناده ضعيف
** تعقيب: قال الفقى ١ / ١٦٨: فى إسناده شهر بن حوشب تكلم فيه ابن عدى والنسائى. وقوى أمره البخارى وحسن حديثه هو وأحمد والترمذى. وله شواهد عن أبى هريرة عند أحمد وأبى داود وعن ابن عباس عند البيهقى والدارقطنى فتعدد طرقه يشد بعضها بعضا.','9','259',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4110,'<span data-type="title" id=toc-4119>٤١١٠ </span>- عن ابن عباس قال: نهى رسول الله صلى الله عليه وسلم: " أن تباع ثمرة حتى تطعم، ولا يباع صوف على ظهر ولا لبن فى ضرع ".
** طس قط
(بلوغ المرام ١٦٨/١)
** قال الحافظ فى " البلوغ " ١ / ١٦٨: أخرجه أبو داود فى المراسيل لعكرمة، وأخرجه أيضا موقوفا على ابن عباس باسناد قوى، ورجحه البيهقى.
** تعقيب: قال الفقى ١ / ١٦٨ _ تعليقا على قوله " رجحه البيهقى " _: أى رجح وقفه، وله شاهد عند أبى بكر بن عاصم عن عمران بن حصين مرفوعا بإسناد يحتج.','9','260',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4111,'<span data-type="title" id=toc-4120>٤١١١ </span>- عن أبى هريرة رضى الله عنه أن النبى صلى الله عليه وسلم: " نهى عن بيع
المضامين والملاقيح ".
** بز
(بلوغ المرام ١٦٨/١)
** إسناده ضعيف
** قال الحافظ فى " البلوغ " ١ / ١٦٨: وفى إسناده ضعف.
** تعقيب: قال الفقى ١ / ١٦٨: فى إسناده صالح بن أبى الأخضر ضعفه ابن عدى وله شاهد عن ابن عمر عند عبد الرازق بسند قوى.','9','261',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4112,'<span data-type="title" id=toc-4121>٤١١٢ </span>- عن أبى هريرة رضى الله تعالى عنه قال: قال رسول الله صلى الله عليه وسلم:
" من أقال مسلما بيعته أقال الله عثرته ".
** د جه
(بلوغ المرام ١٦٨/١)
** قال الحافظ فى " البلوغ " ١ / ١٦٨: صححه ابن حبان والحاكم.','9','262',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4113,'<span data-type="title" id=toc-4122>٤١١٣ </span>- عن عبد الله بن مسعود رضى الله عنه عن النبى صلى الله عليه وسلم قال:
" الربا ثلاثة وسبعون بابا أيسرها مثل أن ينكح الرجل أمه وإن أربى الربا عرض الرجل المسلم ".
** جه ك
(بلوغ المرام ١٧٠/١)
** قال الحافظ فى " البلوغ " ١ / ١٧٠: رواه ابن ماجه مختصرا، والحاكم بتمامه وصححه.','9','263',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4114,'<span data-type="title" id=toc-4123>٤١١٤ </span>- عن سمرة بن جندب: " أن النبى صلى الله عليه وسلم نهى عن بيع الحيوان بالحيوان نسيئة ".
** حم د ت ن جه
(بلوغ المرام ١٧١/١)
** قال الحافظ فى " البلوغ " ١ / ١٧١: صححه الترمذى وابن الجارود.
** تعقيب: قال الفقى ١ / ١٧١: هو من رواية الحسن البصرى عن سمرة وهو مختلف فى سماعه منه. قال الأثرم: قال أبو عبد الله: لا يصح سماعه منه. وأحاديث المنع كلها ليس منها حديث يعتمد عليه. اه.','9','264',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4115,'<span data-type="title" id=toc-4124>٤١١٥ </span>- عن ابن عمر رضى الله عنهما قال: سمعت رسول الله صلى الله عليه وسلم يقول:
" إذا تبايعتم بالعينة وأخذتم أذناب البقر ورضيتم بالزرع وتركتم الجهاد سلط الله عليكم ذلا لا ينزعه حتى ترجعوا إلى دينكم ".
** د
(بلوغ المرام ١٧٢/١)
** إسناده فيه مقال
** قال الحافظ فى " البلوغ " ١ / ١٧٢: رواه أبو داود من رواية نافع عنه وفى إسناده مقال، ولأحمد نحوه من رواية عطاء ورجاله ثقات، وصححه ابن القطان.','9','265',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4116,'<span data-type="title" id=toc-4125>٤١١٦ </span>- عن أبى أمامة رضى الله عنه عن النبى صلى الله عليه وسلم قال: " من شفع لأخيه شفاعة فأهدى له هدية فقبلها فقد أتى بابا عظيما من أبواب الربا ".
** حم د
(بلوغ المرام ١٧٢/١)
** إسناده فيه مقال','9','266',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4117,'<span data-type="title" id=toc-4126>٤١١٧ </span>- عن عبد الله بن عمرو بن العاص رضى الله عنهما قال: " لعن رسول الله صلى الله
عليه وسلم الراشى والمرتشى ".
** د ت
(بلوغ المرام ١٧٢/١)
** قال الحافظ فى " البلوغ " ١ / ١٧٢: صححه الترمذى.','9','267',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4118,'<span data-type="title" id=toc-4127>٤١١٨ </span>- عن عبد الله بن عمرو: " أن النبى صلى الله عليه وسلم أمره أن يجهز جيشا فنفدت الإبل فأمره أن يأخذ على قلائص الصدقة، قال: فكنت أخذ البعير بالبعيرين إلى إبل الصدقة ".
** ك هق
(بلوغ المرام ١٧٢/١)
** رجاله ثقات
** تعقيب: قال الفقى ١ / ١٧٢: قال يحيى بن معين: هو حديث مشهور.','9','268',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4119,'<span data-type="title" id=toc-4128>٤١١٩ </span>- عن سعد بن أبى وقاص رضى الله عنه قال: سمعت رسول الله صلى الله عليه وسلم
يسأل عن اشتراء الرطب بالتمر، فقال: " أينقص الرطب إذا يبس؟ قالوا: نعم
فنهى عن ذلك ".
** حم د ت ن جه
(بلوغ المرام ١٧٣/١)
** قال الحافظ فى " البلوغ " ١ / ١٧٣: صححه ابن المدينى والترمذى وابن حبان والحاكم.','9','269',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4120,'<span data-type="title" id=toc-4129>٤١٢٠ </span>- عن ابن عمر: " أن النبى صلى الله عليه وسلم نهى عن بيع الكالىء بالكالىء يعنى الدين بالدين ".
** بز سحق
(بلوغ المرام ١٧٣/١)
** إسناده ضعيف
** تعقيب: قال الفقى ١ / ١٧٣: هو من رواية الدراوردى عن موسى بن عبيدة الربذى. قال أحمد: لا تحل الرواية عنه ولا أعرف هذا الحديث عن غيره.','9','270',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4121,'<span data-type="title" id=toc-4130>٤١٢١ </span>- عن أنس بن مالك رضى الله تعالى عنه: " أن رسول الله صلى الله عليه وسلم
نهى عن بيع العنب حتى يسود وعن بيع الحب حتى يشتد ".
** حم د ت جه
(بلوغ المرام ١٧٤/١)
** قال الحافظ فى " البلوغ " ١ / ١٧٤: صححه ابن حبان والحاكم.','9','271',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4122,'<span data-type="title" id=toc-4131>٤١٢٢ </span>- عن عائشة رضى الله عنها قالت: قلت: " يا رسول الله إن فلانا قدم له بز من
الشام فلو بعثت إليه فأخذت منه ثوبين نسيئة إلى ميسرة، فبعث إليه فامتنع ".
** ك هق
(بلوغ المرام ١٧٥/١)
** رجاله ثقات','9','272',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4123,'<span data-type="title" id=toc-4132>٤١٢٣ </span>- عن أبى هريرة قال: قال رسول الله صلى الله عليه وسلم: " لا يغلق الرهن من صاحبه الذى رهنه، له غنمه، وعليه غرمه ".
** قط ك
(بلوغ المرام ١٧٥/١)
** رجاله ثقات
** قال الحافظ فى " البلوغ " ١ / ١٧٦: رجاله ثقات إلا أن المحفوظ عند أبى داود وغيره إرساله.','9','273',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4124,'<span data-type="title" id=toc-4133>٤١٢٤ </span>- عن على قال: قال رسول الله صلى الله عليه وسلم: " كل قرض جر منفعة فهو ربا ".
** حارث
(بلوغ المرام ١٧٦/١)
** إسناده ساقط
** قال الحافظ فى " البلوغ " ١ / ١٧٦: رواه الحارث بن أبى أسامة وإسناده ساقط. وله شاهد ضعيف عن فضالة بن عبيد عند البيهقى. وآخر موقوف عن عبد الله بن سلام عند البخارى.
** تعقيب: قال الفقى ١ / ١٧٦: فى إسناده سوار بن مصعب، قال النسائى: متروك، وقال البخارى: منكر الحديث.','9','274',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4125,'<span data-type="title" id=toc-4134>٤١٢٥ </span>- عن أبى بكر بن عبد الرحمن مرسلا: " أيما رجل باع متاعا فأفلس الذى ابتاعه ولم يقض الذى باعه من ثمنه شيئا فوجد متاعه بعينه فهو أحق به وإن مات
المشترى فصاحب المتاع أسوة الغرماء ".
** د ما
(بلوغ المرام ١٧٦/١)
** قال الحافظ فى " البلوغ " ١ / ١٧٦: رواه أبو داود ومالك من رواية أبى بكر بن عبد الرحمن مرسلا. ووصله البيهقى وضعفه تبعا لأبى داود.
** تعقيب: قال الفقى ١ / ١٧٧: لأنه من رواية اسماعيل بن عياش لكنه لا يضعف إلا إذا روى عن غير الشاميين وهو هنا روى عن الحارث الزبيدى وهو شامى فهو صالح للإحتجاج به فيعضد المرسل ويقويه.','9','275',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4126,'<span data-type="title" id=toc-4135>٤١٢٦ </span>- عن عمر بن خلدة قال: أتينا أبا هريرة فى صاحب لنا قد أفلس، فقال: لأقضين فيكم بقضاء رسول الله صلى الله عليه وسلم: " من أفلس أو مات فوجد رجل متاعه بعينه فهو أحق به ".
** ك د جه
(بلوغ المرام ١٧٧/١)
** قال الحافظ فى " البلوغ " ١ / ١٧٧: صححه الحاكم. وضعفه أبو داود وضعف أيضا الزيادة فى ذكر الموت.
** تعقيب: قال الفقى ١ / ١٧٧: لأن فى إسناده أبا المعتمر، قال أبو داود: من هو؟ أى لا يعرف لكن قال أبو حاتم: أبو المعتمر بن عمرو بن أبى رافع روى عنه ابن أبى ذئب محمد بن عبد الرحمن بن المغيرة وهو ثقة فاضل فقيه. قال الشافعى: رواية عمرو بن خلدة أولى من رواية أبى بكر بن عبد الرحمن المرسلة.','9','276',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4127,'<span data-type="title" id=toc-4136>٤١٢٧ </span>- عن عمرو بن الشريد عن أبيه قال: قال رسول الله صلى الله عليه وسلم: " لى الواجد يحل عرضه وعقوبته ".
** د ن خت
(بلوغ المرام ١٧٧/١)
** قال الحافظ فى " البلوغ " ١ / ١٧٧: صححه ابن حبان.','9','277',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4128,'<span data-type="title" id=toc-4137>٤١٢٨ </span>- عن ابن كعب بن مالك عن أبيه: " أن رسول الله صلى الله عليه وسلم حجر على معاذ ماله وباعه فى دين كان عليه ".
** قط
(بلوغ المرام ١٧٨/١)
** قال الحافظ فى " البلوغ " ١ / ١٧٨: صححه الحاكم وأخرجه أبو داود مرسلا ورجح إرساله.','9','278',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4129,'<span data-type="title" id=toc-4138>٤١٢٩ </span>- عن عطية القرظى رضى الله تعالى عنه قال: " عرضنا على النبى صلى الله عليه
وسلم يوم قريظة فكان من أنبت قتل ومن لم ينبت خلى سبيله فكنت ممن لم ينبت
فخلى سبيلى ".
** د ت ن جه
(بلوغ المرام ١٧٨/١)
** قال الحافظ فى " البلوغ "١ / ١٧٨: صححه ابن حبان والحاكم وقال: على شرط الشيخين.','9','279',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4130,'<span data-type="title" id=toc-4139>٤١٣٠ </span>- عن عمرو بن شعيب عن أبيه عن جده رضى الله عنهما أن رسول الله صلى الله عليه وسلم قال: " لا يجوز لامرأة عطية إلا بإذن زوجها ". وفى لفظ: " لا يجوز للمرأة أمر فى مالها إذا ملك زوجها عصمتها ".
** حم د ن جه
(بلوغ المرام ١٧٨/١)
** قال الحافظ فى " البلوغ " ١ / ١٧٨: صححه الحاكم.','9','280',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4131,'<span data-type="title" id=toc-4140>٤١٣١ </span>- عن عمرو بن عوف المزنى رضى الله تعالى عنه أن رسول الله صلى الله عليه وسلم قال: " الصلح جائز بين المسلمين إلا صلحا حرم حلالا أو أحل حراما والمسلمون على شروطهم إلا شرطا حرم حلالا أو أحل حراما ".
** ت
(بلوغ المرام ١٧٩/١)
** قال الحافظ فى " البلوغ " ١ / ١٧٩: صححه الترمذى وأنكروا عليه لأن راوية كثير بن عبد الله بن عمرو بن عوف ضعيف وكأنه اعتبره بكثرة طرقه. وقد صححه ابن حبان من حديث أبى هريرة رضى الله تعالى عنه.
** تعقيب: قال الفقى ١ / ١٧٩: قال فيه الشافعى وأبو داود: هو ركن من أركان الكذب. وقال ابن حبان له عن أبيه نسخة موضوعة. وقال الذهبى: أما الترمذى فروى من حديثه " الصلح جائز..
.. الخ وصححه فلهذا لا يعتمد العلماء على تصحيحه. وقال الحافظ: ووقف هذا الحديث على عمر أشهر يعنى كتابه إلى أبى موسى المشهور فى القضاء. رواه البيهقى فى المعرفة، وقد رواه الدارقطنى من حديث أبى هريرة مختصرا وقال: هذا صحيح الإسناد وأخرجه الحاكم فى المستدرك وقال: صحيح على شرطهما.','9','281',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4132,'<span data-type="title" id=toc-4141>٤١٣٢ </span>- عن جابر رضى الله تعالى عنه قال: توفى رجل منا فغسلناه وحنطناه وكفناه ثم أتينا به رسول الله صلى الله عليه وسلم فقلنا: تصلى عليه؟ فخطا خطى ثم قال: " أعليه دين "، فقلنا: ديناران، فانصرف فتحملهما أبو قتادة فأتيناه، فقال أبو قتادة: الديناران على، فقال رسول الله صلى الله عليه وسلم: " حق الغريم
وبرىء منهما الميت "، قال: نعم، فصلى عليه.
** حم د ن
(بلوغ المرام ١٨٠/١)
** قال الحافظ فى " البلوغ " ١ / ١٨٠: صححه ابن حبان والحاكم.','9','282',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4133,'<span data-type="title" id=toc-4142>٤١٣٣ </span>- عن عمرو بن شعيب عن أبيه عن جده قال: قال رسول الله صلى الله عليه وسلم: " لا كفالة فى حد ".
** هق
(بلوغ المرام ١٨٠/١)
** إسناده ضعيف
** تعقيب: قال الفقى ١ / ١٨٠: قال البيهقى: إنه منكر، وفى الباب آثار كلها لا تخلو عن مقال.','9','283',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4134,'<span data-type="title" id=toc-4143>٤١٣٤ </span>- عن أبى هريرة رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " قال الله تعالى: أنا ثالث الشريكين مالم يخن أحدهما صاحبه فإذا خان خرجت من بينهما ".
** د
(بلوغ المرام ١٨١/١)
** قال الحافظ فى "البلوغ " ١ / ١٨١: صححه الحاكم.','9','284',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4135,'<span data-type="title" id=toc-4144>٤١٣٥ </span>- عن جابر بن عبد الله رضى الله تعالى عنهما قال: أردت الخروج إلى خيبر فأتيت النبى صلى الله عليه وسلم فقال: " إذا أتيت وكيلى بخيبر فخذ منه خمسة عشر وسقا ".
** د
(بلوغ المرام ١٨١/١)
** قال الحافظ فى " البلوغ " ١ / ١٨١: صححه أبو داود.','9','285',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4136,'<span data-type="title" id=toc-4145>٤١٣٦ </span>- عن أبى ذر رضى الله عنه قال: قال لى النبى صلى الله عليه وسلم: " قل الحق ولو كان مرا ".
** لم
(بلوغ المرام ١٨٢/١)
** قال الحافظ فى " البلوغ " ١ / ١٨٢: صححه ابن حبان من حديث طويل.','9','286',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4137,'<span data-type="title" id=toc-4146>٤١٣٧ </span>- عن سمرة بن جندب رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " على اليد ما أخذت حتى تؤديه ".
** حم د ت ن جه
(بلوغ المرام ١٨٢/١)
** قال الحافظ فى " البلوغ " ١ / ١٨٢: صححه الحاكم.
** تعقيب: قال الفقى ١ / ١٨٢: هو من رواية الحسن عن سمرة.','9','287',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4138,'<span data-type="title" id=toc-4147>٤١٣٨ </span>- عن أبى هريرة رضى الله تعالى عنه قال: قال رسول الله صلى الله عليه وسلم:
" أد الأمانة إلى من ائتمنك ولا تخن من خانك ".
** ت د
(بلوغ المرام ١٨٢/١)
** قال الحافظ فى " البلوغ " ١ / ١٨٣: حسنه أبو داود، وصححه الحاكم، واستنكره أبو حاتم الرازى، وأخرجه جماعة من الحفاظ وهو شامل للعارية.
** تعقيب: قال الفقى ١ / ١٨٣: إنما استنكره أبو حاتم لأنه روى من طرق فى أحدها طلق بن غنم، وفى الآخر أيوب بن سويد، وفى الآخر من لا يعرف. ولذا قال ابن الجوزى: لا يصح من جميع طرقه شىء.','9','288',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4139,'<span data-type="title" id=toc-4148>٤١٣٩ </span>- عن يعلى بن أمية رضى الله عنه قال: قال لى رسول الله صلى الله عليه وسلم: " إذا أتتك رسلى فأعطهم ثلاثين درعا "، قلت: يارسول الله أعاريه مضمونة أو عارية مؤداه؟ قال: بل عارية مؤداة ".
** حم د ن
(بلوغ المرام ١٨٣/١)
** قال الحافظ فى " البلوغ ١ / ١٨٣: صححه ابن حبان.','9','289',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4140,'<span data-type="title" id=toc-4149>٤١٤٠ </span>- عن صفوان بن أمية رضى الله عنه أن النبى صلى الله عليه وسلم استعار منه
دروعا يوم حنين، فقال: أغصب يا محمد؟ قال: " بل عارية مضمونة ".
** د حم ن
(بلوغ المرام ١٨٣/١)
** قال الحافظ فى " البلوغ " ١ / ١٨٣: صححه الحاكم وأخرج له شاهدا ضعيفا عن ابن عباس رضى الله عنهما.','9','290',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4141,'<span data-type="title" id=toc-4150>٤١٤١ </span>- عن رافع بن خديج رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم:
" من زرع فى أرض قوم بغير إذنهم فليس له من الزرع شىء وله نفقته ".
** حم د ت جه
(بلوغ المرام ١٨٤/١)
** قال الحافظ فى " البلوغ " ١ / ١٨٤: حسنه الترمذى، ويقال أن البخارى ضعفه.
** تعقيب: قال الفقى ١ / ١٨٤: نقل الترمذى عن البخارى تحسين هذا الحديث.','9','291',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4142,'<span data-type="title" id=toc-4151>٤١٤٢ </span>- عن عروة بن الزبير رضى الله عنهما قال: قال رجل من أصحاب رسول الله صلى الله
عليه وسلم: إن رجلين اختصماإلى رسول الله صلى الله عليه وسلم فى أرض، غرس
أحدهما فيها نخلا والأرض للأخر، فقضى رسول الله صلى الله عليه وسلم بالأرض
لصاحبها وأمر صاحب النخل أن يخرج نخلة، وقال: " ليس لعرق ظالم حق ".
** د
(بلوغ المرام ١٨٤/١)
** إسناده حسن
** قال الحافظ فى " البلوغ " ١ / ١٨٤: وآخره عند أصحاب السنن من رواية عروة عن سعيد بن زيد واختلف فى وصله وإرساله وفى تعيين صحابيه.','9','292',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4143,'<span data-type="title" id=toc-4152>٤١٤٣ </span>- " قضى النبى صلى الله عليه وسلم بالشفعة فى كل شىء ".
** طحا
(بلوغ المرام ١٨٥/١)
** رجاله ثقات','9','293',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4144,'<span data-type="title" id=toc-4153>٤١٤٤ </span>- عن أنس بن مالك قال: قال رسول الله صلى الله عليه وسلم: " جار الدار أحق بالدار ".
** ن
(بلوغ المرام ١٨٥/١)
** قال الحافظ فى " البلوغ " ١ / ١٨٥: صححه ابن حبان وله علة.
** تعقيب: قال الفقى ١ / ١٨٥: لأنه رواه جماعة من الحفاظ عن أنس وأخرون عن الحسن عن سمرة وقالوا هذا هو المحفوظ لكن صحح ابن القطان الطريقين وإن كان فى سماع الحسن من سمرة خلاف.','9','294',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4145,'<span data-type="title" id=toc-4154>٤١٤٥ </span>- عن جابر رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " الجار أحق بشفعة جاره ينتظر بها _ وإن كان غائبا _ إذا كان طريقهما واحدا ".
** حم د ت ن جه
(بلوغ المرام ١٨٥/١)
** رجاله ثقات','9','295',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4146,'<span data-type="title" id=toc-4155>٤١٤٦ </span>- عن ابن عمر عن النبى صلى الله عليه وسلم قال: " الشفعة كحل العقال ".
** جه بز
(بلوغ المرام ١٨٥/١)
** إسناده ضعيف
** قال الحافظ فى " البلوغ " ١ / ١٨٥: زاد البزار " ولا شفعة لغائب ".
** تعقيب: قال الفقى ١ / ١٨٥: فى إسناده محمد بن عبد الرحمن البيلمانى يحدث عن أبيه نسخة كلها موضوعة لا يجوز الإحتجاج به لذا قال ابن حبان: لا أصل له.','9','296',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4147,'<span data-type="title" id=toc-4156>٤١٤٧ </span>- عن صهيب رضى الله عنه أن النبى صلى الله عليه وسلم قال: " ثلاث فيهن البركة
البيع إلى أجل، والمقارضة، وخلط البر بالشعير للبيت لا للبيع ".
** جه
(بلوغ المرام ١٨٦/١)
** إسناده ضعيف
** تعقيب: قال الفقى ١ / ١٨٦: فى إسناده نصر بن القاسم عن عبد الرحيم بن داود وهما مجهولان.','9','297',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4148,'<span data-type="title" id=toc-4157>٤١٤٨ </span>- عن حكيم بن حزام رضى الله عنه: " أنه كان يشترط على الرجل إذا أعطاه مالا
مقارضة: أن لا تجعل مالى فى كبد رطبة ولا تحمله فى بحر ولا تنزل به فى
بطن مسيل فإن فعلت شيئا من ذلك فقد ضمنت مالى ".
** قط
(بلوغ المرام ١٨٦/١)
** رجاله ثقات
** تعقيب: قال الفقى ١ / ١٨٦: رواه البيهقى أيضا وقوى الحافظ ابن حجر إسناده.','9','298',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4149,'<span data-type="title" id=toc-4158>٤١٤٩ </span>- عن العلاء بن عبد الرحمن بن يعقوب عن أبيه عن جده: " أنه عمل فى مال لعثمان على أن الربح بينهما ".
** ما
(بلوغ المرام ١٨٦/١)
** صحيح موقوفا','9','299',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4150,'<span data-type="title" id=toc-4159>٤١٥٠ </span>- عن ابن عمر رضى الله عنهما قال: قال رسول الله صلى الله عليه وسلم:
" أعطوا الأجير أجره قبل أن يجف عرقه ".
** جه
(بلوغ المرام ١٨٨/١)
** ضعيف
** قال الحافظ فى " البلوغ " ١ / ١٨٩: وفى الباب عن أبى هريرة رضى الله عنه عند أبى يعلى والبيهقى، وجابر عند الطبرانى وكلها ضعاف.','9','300',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4151,'<span data-type="title" id=toc-4160>٤١٥١ </span>- عن أبى سعيد الخدرى رضى الله عنه أن النبى صلى الله عليه وسلم قال:
" من استأجر أجيرا فليسم له أجرته ".
** عب
(بلوغ المرام ١٨٩/١)
** قال الحافظ فى " البلوغ " ١ / ١٨٩: رواه عبد الرازق وفيه انقطاع، ووصله البيهقى من طريق أبى حنيفة.','9','301',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4152,'<span data-type="title" id=toc-4161>٤١٥٢ </span>- عن سعيد بن زيد رضى الله عنه عن النبى صلى الله عليه وسلم قال:
" من أحيا أرضا ميتة فهى له ".
** د ت ن
(بلوغ المرام ١٨٩/١)
** قال الحافظ فى " البلوغ " ١ / ١٨٩: حسنه الترمذى وقال: روى مرسلا. وهو كما قال، واختلف فى صحابيه فقيل جابر وقيل عائشة وقيل عبد الله بن عمر والراجح الأول.
** تعقيب: قال الفقى ١ / ١٨٩: قال الترمذى: وقد رواه بعضهم عن هشام بن عروة عن أبيه عن النبى صلى الله
عليه وسلم مرسلا.','9','302',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4153,'<span data-type="title" id=toc-4162>٤١٥٣ </span>- عن سمرة بن جندب رضى الله تعالى عنه قال: قال رسول الله صلى الله عليه وسلم:
" من أحاط حائطا على الأرض فهى له ".
** د
(بلوغ المرام ١٩٠/١)
** قال الحافظ فى " البلوغ " ١ / ١٩٠: صححه ابن الجارود.
** تعقيب: قال الفقى ١ / ١٩٠: هو من رواية الحسن البصرى عن سمرة وفى سماعه منه خلاف.','9','303',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4154,'<span data-type="title" id=toc-4163>٤١٥٤ </span>- عن عبد الله بن مغفل أن النبى صلى الله عليه وسلم قال: " من حفر بئرا فله
أربعون ذراعا عطنا لماشيته ".
** جه
(بلوغ المرام ١٩٠/١)
** إسناده ضعيف
** تعقيب: قال الفقى ١ / ١٩٠: مداره على إسماعيل بن مسلم المكى كان كثير الإختلاط لكن له شواهد عن أبى هريرة عند أحمد، وعن سعيد بن المسيب مرسلا عند الدارقطنى وعند الحاكم موصولا وقد يقوى بعضها بعضا.','9','304',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4155,'<span data-type="title" id=toc-4164>٤١٥٥ </span>- عن علقمة بن وائل عن أبيه: " أن النبى صلى الله عليه وسلم أقطعه أرضا
بحضر موت ".
** د ت
(بلوغ المرام ١٩٠/١)
** قال الحافظ فى " البلوغ " ١ / ١٩٠: صححه ابن حبان.','9','305',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4156,'<span data-type="title" id=toc-4165>٤١٥٦ </span>- عن ابن عمر رضى الله عنهما أن النبى صلى الله عليه وسلم أقطع الزبير حضر
فرسه فأجرى الفرس حتى قام ثم رمى بسوطه، فقال: " أعطوه حيث بلغ السوط ".
** د
(بلوغ المرام ١٩٠/١)
** ضعيف
** قال الحافظ فى " البلوغ " ١ / ١٩٠: فيه ضعف.
** تعقيب: قال الفقى ١ / ١٩٠: لأنه من رواية عبد الله بن عمر بن حفص بن عاصم بن عمر بن الخطاب وهو ضعيف.','9','306',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4157,'<span data-type="title" id=toc-4166>٤١٥٧ </span>- عن رجل من الصحابة رضى الله عنه قال: غزوت مع النبى صلى الله عليه وسلم فسمعته يقول: " الناس شركاء فى ثلاثة: فى الكلأ والماء والنار ".
** حم د
(بلوغ المرام ١٩٠/١)
** رجاله ثقات','9','307',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4158,'<span data-type="title" id=toc-4167>٤١٥٨ </span>- عن ابن عمر وابن عباس عن النبى صلى الله عليه وسلم قال: " لا يحل لرجل مسلم
أن يعطى العطية ثم يرجع فيها إلا الوالد فيما يعطى ولده ".
** حم د ت ن جه
(بلوغ المرام ١٩٢/١)
** قال الحافظ فى " البلوغ " ١ / ١٩٢: صححه الترمذى وابن حبان والحاكم.','9','308',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4159,'<span data-type="title" id=toc-4168>٤١٥٩ </span>- عن ابن عباس رضى الله تعالى عنهما قال: وهب رجل لرسول الله صلى الله عليه
وسلم ناقة فأثابه عليها، فقال: رضيت؟ قال: لا، فزاده، فقال: رضيت؟
قال: لا، فزاده، فقال: رضيت؟ قال: نعم.
** حم
(بلوغ المرام ١٩٣/١)
** قال الحافظ فى " البلوغ " ١ / ١٩٣: صححه ابن حبان.','9','309',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4160,'<span data-type="title" id=toc-4169>٤١٦٠ </span>- عن أبى هريرة عن النبى صلى الله عليه وسلم قال: " تهادوا تحابوا ".
** خد يع
(بلوغ المرام ١٩٤/١)
** إسناده حسن','9','310',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4161,'<span data-type="title" id=toc-4170>٤١٦١ </span>- عن أنس رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " تهادوا فإن
الهدية تسل السخيمة ".
** بز
(بلوغ المرام ١٩٤/١)
** إسناده ضعيف','9','311',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4162,'<span data-type="title" id=toc-4171>٤١٦٢ </span>- عن ابن عمر رضى الله عنهما عن النبى صلى الله عليه وسلم قال: " من وهب هبة
فهو أحق بها ما لم يثب عليها ".
** ك
(بلوغ المرام ١٩٤/١)
** قال الحافظ فى " البلوغ " ١ / ١٩٤: صححه الحاكم والمحفوظ من رواية ابن عمر عن عمر قوله.','9','312',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4163,'<span data-type="title" id=toc-4172>٤١٦٣ </span>- عن عياض بن حمار رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم:
" من وجد لقطة فليشهد ذوى عدل وليحفظ عفاصها ووكاءها ثم لا يكتم ولا يغيب فإن جاء ربها فهو أحق بها وإلا فهو مال الله يؤتيه من يشاء ".
** حم د ن جه
(بلوغ المرام ١٩٥/١)
** قال الحافظ فى " البلوغ " ١ / ١٩٥: صححه ابن خزيمة وابن الجارود وابن حبان.','9','313',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4164,'<span data-type="title" id=toc-4173>٤١٦٤ </span>- عن عمران بن حصين رضى الله عنه قال: جاء رجل إلى النبى صلى الله عليه وسلم
فقال: إن ابن ابنى مات فما لى من ميراثه؟ فقال: " لك السدس "، فلما ولى
دعاه، فقال: " لك سدس آخر "، فلما ولى دعاه، فقال: " إن السدس الآخر
طعمة ".
** حم د ت ن جه
(بلوغ المرام ١٩٦/١)
** قال الحافظ فى " البلوغ " ١ / ١٩٦: صححه الترمذى وهو من رواية الحسن البصرى عن عمران وقيل إنه لم يسمع منه.','9','314',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4165,'<span data-type="title" id=toc-4174>٤١٦٥ </span>- عن أبى بريدة عن أبيه رضى الله عنهما: " أن النبى صلى الله عليه وسلم جعل
للجدة السدس إذا لم يكن دونها أم ".
** د ن
(بلوغ المرام ١٩٦/١)
** قال الحافظ فى " البلوغ " ١ / ١٩٦: صححه ابن خزيمة وابن الجارود وقواه ابن عدى.','9','315',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4166,'<span data-type="title" id=toc-4175>٤١٦٦ </span>- عن المقدام بن معديكرب قال: قال رسول الله صلى الله عليه وسلم:
" الخال وارث من لا وارث له ".
** حم د ن جه
(بلوغ المرام ١٩٦/١)
** قال الحافظ فى " البلوغ ١ / ١٩٦: حسنه أبو زرعة الرازى وصححه الحاكم وابن حبان.
** تعقيب: قال الفقى ١ / ١٩٦: قال المنذرى: اختلف فى هذا الحديث، وقال البيهقى: كان ابن معين يضعفه ويقول: ليس فيه حديث قوى، وقد ردوا هذا الحديث بوجوه كثيرة غير هذا وقد ردها كلها العلامة ابن القيم فى تهذيب السنن وقواه ورجح العمل به.','9','316',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4167,'<span data-type="title" id=toc-4176>٤١٦٧ </span>- عن أبى أمامة بن سهل رضى الله عنه قال: كتب عمر إلى أبى عبيده أن رسول الله صلى الله عليه وسلم قال: " الله ورسوله مولى من لا مولى له والخال وارث من لا وارث له ".
** حم ت ن جه
(بلوغ المرام ١٩٧/١)
** قال الحافظ فى " البلوغ " ١ / ١٩٧: حسنه الترمذى وصححه ابن حبان.
** تعقيب: قال الفقى ١ / ١٩٧: وروى من حديث عائشة.','3','15',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4168,'<span data-type="title" id=toc-4177>٤١٦٨ </span>- عن جابر رضى الله عنه عن النبى صلى الله عليه وسلم قال:
" إذا استهل المولود ورث ".
** د
(بلوغ المرام ١٩٧/١)
** قال الحافظ فى " البلوغ " ١ / ١٩٧: صححه ابن حبان.','3','16',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4169,'<span data-type="title" id=toc-4178>٤١٦٩ </span>- عن عمرو بن شعيب عن أبيه عن جده قال: قال رسول الله صلى الله عليه وسلم: " ليس للقاتل من الميراث شىء ".
** ن قط
(بلوغ المرام ١٩٧/١)
** قال الحافظ فى " البلوغ " ١ / ١٩٧: قواه ابن عبد البر، وأعله النسائى، والصواب وقفه على عمرو.','3','17',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4170,'<span data-type="title" id=toc-4179>٤١٧٠ </span>- عن عمر بن الخطاب رضى الله عنه قال: سمعت رسول الله صلى الله عليه وسلم
يقول: " ما أحرز الوالد أو الولد فهو لعصبته من كان ".
** د ن جه
(بلوغ المرام ١٩٧/١)
** قال الحافظ فى " البلوغ " ١ / ١٩٧: صححه ابن المدينى وابن عبد البر.','3','18',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4171,'<span data-type="title" id=toc-4180>٤١٧١ </span>- عن عبد الله بن عمر رضى الله تعالى عنهما قال: قال رسول الله صلى الله عليه
وسلم: " الولاء لحمة كلحمة النسب لا يباع ولا يوهب ".
** ك
(بلوغ المرام ١٩٧/١)
** قال الحافظ فى " البلوغ " ١ / ١٩٧: رواه الحاكم من طريق الشافعى عن محمد بن الحسن عن أبى يوسف وصححه ابن حبان وأعله البيهقى. قال الحافظ فى " البلوغ " ١ / ٢٩٤: صححه ابن حبان والحاكم وأصله فى الصحيحين بغير هذا اللفظ.','3','19',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4172,'<span data-type="title" id=toc-4181>٤١٧٢ </span>- عن أنس رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم:
" أفرضكم زيد بن ثابت ".
** حم ت ن جه
(بلوغ المرام ١٩٧/١)
** قال الحافظ فى " البلوغ " ١ / ١٩٨: صححه الترمذى وابن حبان والحاكم وأعل بالإرسال.
** تعقيب: قال الفقى ١ / ١٩٨: قال الحافظ فى التلخيص: سماع أبى قلابة من أنس صحيح إلا أنه لم يسمع منه هذا، وقد رجح الدارقطنى والبيهقى والخطيب أن الموصول منه ذكر أبى عبيدة والباقى مرسل.','3','20',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4173,'<span data-type="title" id=toc-4182>٤١٧٣ </span>- عن أبى أمامة الباهلى رضى الله تعالى عنه قال: سمعت رسول الله صلى الله عليه
وسلم يقول: " إن الله أعطى كل ذى حق حقه فلا وصية لوارث ".
** حم د ت جه
(بلوغ المرام ١٩٩/١)
** قال الحافظ فى " البلوغ ١ / ١٩٩: حسنه أحمد والترمذى وقواه ابن خزيمة وابن الجارود ورواه الدارقطنى من حديث ابن عباس رضى الله عنهما وزاد فى آخره " إلا أن يشاء الورثة " وإسناده حسن.','3','21',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4174,'<span data-type="title" id=toc-4183>٤١٧٤ </span>- عن معاذ بن جبل رضى الله تعالى عنه قال: قال النبى صلى الله عليه وسلم:
" إن الله تصدق عليكم بثلث أموالكم عند وفاتكم زيادة فى حسناتكم ".
** قط
(بلوغ المرام ١٩٩/١)
** ضعيف
** قال الحافظ فى " البلوغ " ١ / ٢٠٠: أخرجه أحمد والبزار من حديث أبى الدرداء، وابن ماجه من حديث أبى هريرة رضى الله عنه وكلها ضعيفة لكن قد يقوى بعضها بعضا والله أعلم.','3','22',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4175,'<span data-type="title" id=toc-4184>٤١٧٥ </span>- عن عمرو بن شعيب عن أبيه عن جده عن النبى صلى الله عليه وسلم قال: " من أودع وديعه فليس عليه ضمان ".
** جه
(بلوغ المرام ٢٠٠/١)
** إسناده ضعيف
** تعقيب: قال الفقى ١ / ٢٠٠: لأن فى سنده المثنى بن الصباح متروك.','3','23',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4176,'<span data-type="title" id=toc-4185>٤١٧٦ </span>- عن أنس قال: كان رسول الله صلى الله عليه وسلم يأمرنا بالباءة وينهى عن التبتل نهيا شديدا، ويقول: " تزوجوا الولود الودود فإنى مكاثر بكم الأنبياء يوم القيامة ".
** حم
(بلوغ المرام ٢٠١/١)
** قال الحافظ فى " البلوغ " ١ / ٢٠١: صححه ابن حبان وله شاهد عند أبى داود والنسائى وابن حبان من حديث معقل بن يسار.','3','24',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4177,'<span data-type="title" id=toc-4186>٤١٧٧ </span>- عن أبى هريرة أن النبى صلى الله عليه وسلم كان إذا رفأ إنسانا إذا تزوج قال: " بارك الله لك وبارك عليك وجمع بينكما فى خير ".
** حم د ت ن جه
(بلوغ المرام ٢٠١/١)
** قال الحافظ فى " البلوغ " ١ / ٢٠١: صححه الترمذى وابن خزيمة وابن حبان.','3','25',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4178,'<span data-type="title" id=toc-4187>٤١٧٨ </span>- عن عبد الله بن مسعود رضى الله عنه قال: علمنا رسول الله صلى الله عليه وسلم التشهد فى الحاجة: " إن الحمد لله نحمده ونستعينه ونستغفره ونعوذ بالله من شرور أنفسنا وسيئات أعمالنا من يهد الله فلا مضل له ومن يضلل فلا هادى له وأشهد أن لا إله إلا الله وأشهد أن محمدا عبده ورسوله " ويقرأ ثلاث آيات.
** حم د ت ن جه
(بلوغ المرام ٢٠١/١)
** قال الحافظ فى " البلوغ " ١ / ٢٠٢: حسنه الترمذى والحاكم.','3','26',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4179,'<span data-type="title" id=toc-4188>٤١٧٩ </span>- عن جابر قال: قال رسول الله صلى الله عليه وسلم: " إذا خطب أحدكم المرأة فإن استطاع أن ينظر إلى ما يدعوه إلى نكاحها فليفعل ".
** حم د
(بلوغ المرام ٢٠٢/١)
** رجاله ثقات
** قال الحافظ فى " البلوغ " ١ / ٢٠٢: رجاله ثقات وصححه الحاكم. وله شاهد عند الترمذى والنسائى عن المغيرة. وعند ابن ماجه وابن حبان من حديث محمد بن مسلمة.','3','27',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4180,'<span data-type="title" id=toc-4189>٤١٨٠ </span>- عن عامر بن عبد الله بن الزبير عن أبيه رضى الله عنهم أن رسول الله صلى الله
عليه وسلم قال: " أعلنوا النكاح ".
** حم
(بلوغ المرام ٢٠٤/١)
** قال الحافظ فى " البلوغ " ١ / ٢٠٤: صححه الحاكم.
** تعقيب: قال الفقى ١ / ٢٠٤: ورواه ابن ماجه عن عائشة وفيه زيادة " واضربوا عليه بالغربال " وهو عند الترمذى بلفظ " الدف ". قال الحافظ فى الفتح: سنده ضعيف. وللطبرانى عن السائب بن يزيد " أشيدوا النكاح ".','3','28',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4181,'<span data-type="title" id=toc-4190>٤١٨١ </span>- عن أبى بردة بن أبى موسى عن أبيه رضى الله تعالى عنهما قال: قال رسول الله
صلى الله عليه وسلم: " لا نكاح إلا بولى ".
** حم د ت ن جه
(بلوغ المرام ٢٠٤/١)
** قال الحافظ فى " البلوغ " ١ / ٢٠٤: صححه ابن المدينى والترمذى وابن حبان وأعل بالإرسال.
** تعقيب: قال الفقى ١ / ٢٠٤: رواه شعبة والثورى عن أبى اسحاق السبيعى عن أبى بردة عن النبى صلى الله عليه وسلم مرسلا، ورواه إسرائيل وغيره موصولا. قال ابن القيم فى " تهذيب السنن ": قال ابن المدينى: حديث اسرائيل صحيح. وسئل عنه البخارى فقال: الزيادة من الثقة مقبولة، وإسرائيل ثقة. فإن كان شعبة والثورى ارسلاه فإن ذلك لا يضر الحديث. ثم ذكر صحته من عدة وجوه.','3','29',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4182,'<span data-type="title" id=toc-4191>٤١٨٢ </span>- عن عائشة قالت: قال رسول الله صلى الله عليه وسلم: " أيما امرأة نكحت بغير
إذن وليها فنكاحها باطل فإن دخل بها فلها المهر بما استحل من فرجها فإن
اشتجروا فالسلطان ولى من لا ولى له ".
** د ت جه
(بلوغ المرام ٢٠٤/١)
** قال الحافظ فى " البلوغ ١ / ٢٠٤: صححه أبو عوانة وابن حبان والحاكم.','3','30',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4183,'<span data-type="title" id=toc-4192>٤١٨٣ </span>- عن ابن عباس أن النبى صلى الله عليه وسلم قال: " ليس للولى مع الثيب أمر
واليتيمة تستأمر ".
** د ن
(بلوغ المرام ٢٠٥/١)
** قال الحافظ فى " البلوغ " ١ / ٢٠٥: صححه ابن حبان.','3','31',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4184,'<span data-type="title" id=toc-4193>٤١٨٤ </span>- عن أبى هريرة رضى الله تعالى عنه قال: قال رسول الله صلى الله عليه وسلم:
" لا تزوج المرأة المرأة ولا تزوج المرأة نفسها ".
** جه قط
(بلوغ المرام ٢٠٥/١)
** رجاله ثقات','3','32',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4185,'<span data-type="title" id=toc-4194>٤١٨٥ </span>- عن ابن عباس رضى الله عنهما: " أن جارية بكرا أتت النبى صلى الله عليه وسلم
فذكرت أن أباها زوجها وهى كارهة فخيرها رسول الله صلى الله عليه وسلم ".
** حم د جه
(بلوغ المرام ٢٠٥/١)
** قال الحافظ فى " البلوغ " ١ / ٢٠٦: أعل بالإرسال.
** تعقيب: قال الفقى ١ / ٢٠٦: قال ابن القيم: رواية هذا الحديث مرسلة ليست بعلة فإنه قد روى مسندا ومرسلا.','3','33',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4186,'<span data-type="title" id=toc-4195>٤١٨٦ </span>- عن الحسن عن سمرة رضى الله عنه عن النبى صلى الله عليه وسلم قال:
" أيما امرأة زوجها وليان فهى للأول منهما ".
** حم د ت ن جه
(بلوغ المرام ٢٠٦/١)
** قال الحافظ فى " البلوغ " ١ / ٢٠٦: حسنه الترمذى.','3','34',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4187,'<span data-type="title" id=toc-4196>٤١٨٧ </span>- عن جابر رضى الله تعالى عنه قال: قال رسول الله صلى الله عليه وسلم:
" أيما عبد تزوج بغير إذن مواليه أو أهله فهو عاهر ".
** حم د ت
(بلوغ المرام ٢٠٦/١)
** قال الحافظ فى " البلوغ " ١ / ٢٠٦: صححه الترمذى وكذلك ابن حبان.','3','35',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4188,'<span data-type="title" id=toc-4197>٤١٨٨ </span>- عن ابن مسعود رضى الله عنه: " لعن رسول الله صلى الله عليه وسلم المحلل
والمحلل له ".
** حم ن ت
(بلوغ المرام ٢٠٨/١)
** قال الحافظ فى " البلوغ " ١ / ٢٠٨: صححه الترمذى وفى الباب عن على، أخرجه الأربعة إلا النسائى.','3','36',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4189,'<span data-type="title" id=toc-4198>٤١٨٩ </span>- عن أبى هريرة رضى الله تعالى عنه قال: قال رسول الله صلى الله عليه وسلم:
" لا ينكح الزانى المجلود إلا مثله ".
** حم د
(بلوغ المرام ٢٠٨/١)
** رجاله ثقات','3','37',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4190,'<span data-type="title" id=toc-4199>٤١٩٠ </span>- عن ابن عمر قال: قال رسول الله صلى الله عليه وسلم: " العرب بعضهم أكفاء
بعض والموالى بعضهم أكفاء بعض إلا حائكا أو حجاما ".
** ك
(بلوغ المرام ٢٠٩/١)
** إسناده فيه راو لم يسم
** قال الحافظ فى " البلوغ " ١ / ٢٠٩: فى إسناده راو لم يسم واستنكره أبو حاتم، وله شاهد عند البزار من حديث معاذ بن جبل بسند منقطع.
** تعقيب: قال الفقى ١ / ٢٠٩: قال أبو حاتم: هذا لا أصل له وقال فى موضع آخر: حديث باطل. وقال ابن عبد البر: هذا منكر موضوع. وله طرق كلها واهية.','3','38',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4191,'<span data-type="title" id=toc-4200>٤١٩١ </span>- عن أبى هريرة رضى الله تعالى عنه أن النبى صلى الله عليه وسلم قال:
" يا بنى بياضة أنكحوا أبا هند وانكحوا إليه _ وكان حجاما _ ".
** د ك
(بلوغ المرام ٢٠٩/١)
** إسناده جيد','3','39',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4192,'<span data-type="title" id=toc-4201>٤١٩٢ </span>- عن الضحاك بن فيروز الديلمى عن أبيه قال: قلت يا رسول الله " إنى أسلمت وتحتى أختان فقال رسول الله صلى الله عليه وسلم طلق أيتهما شئت ".
** حم د ت جه
(بلوغ المرام ٢١٠/١)
** قال الحافظ فى " البلوغ " ١ / ٢١٠: صححه ابن حبان والدارقطنى والبيهقى وأعله البخارى.
** تعقيب: قال الفقى ١ / ٢١٠: أعله لأنه من رواية وهب الجيشانى عن الضحاك بن فيروز عن أبيه ولا يعرف سماع بعضهم من بعض.','3','40',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4193,'<span data-type="title" id=toc-4202>٤١٩٣ </span>- عن سالم عن أبيه: " أن غيلان بن سلمة أسلم وله عشر نسوة فأسلمن معه فأمره النبى صلى الله عليه وسلم أن يتخير منهن أربعا ".
** حم ت
(بلوغ المرام ٢١٠/١)
** قال الحافظ فى " البلوغ " ١ / ٢١٠: صححه ابن حبان والحاكم وأعله البخارى وأبو زرعة وأبو حاتم.
** تعقيب: قال الفقى ١ / ٢١٠: قال ابن كثير فى الإرشاد: رواه الشافعى وأحمد والترمذى وابن ماجه وهذا إسناد على شرط الشيخين إلا أن الترمذى قال: سمعت البخارى يقول: هذا حديث غير محفوظ.','3','41',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4194,'<span data-type="title" id=toc-4203>٤١٩٤ </span>- عن ابن عباس قال: " رد النبى صلى الله عليه وسلم ابنته زينب على أبى العاص بن الربيع بعد ست سنين بالنكاح الأول ولم يحدث نكاحا ".
** حم د ت جه
(بلوغ المرام ٢١٠/١)
** قال الحافظ فى " البلوغ " ١ / ٢١٠: صححه أحمد والحاكم.','3','42',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4195,'<span data-type="title" id=toc-4204>٤١٩٥ </span>- عن عمرو بن شعيب عن أبيه عن جده: " أن النبى صلى الله عليه وسلم رد ابنته زينب على أبى العاص بنكاح جديد ".
** لم
(بلوغ المرام ٢١١/١)
** قال الحافظ فى " البلوغ " ١ / ٢١١: قال الترمذى: حديث ابن عباس أجود إسنادا والعمل على حديث عمرو بن شعيب.
** تعقيب: قال الفقى ١ / ٢١١: قال أحمد والدارقطنى: هذا حديث ضعيف والحديث الصحيح هو الذى روى أنه أقرهما على النكاح الأول.','3','43',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4196,'<span data-type="title" id=toc-4205>٤١٩٦ </span>- عن ابن عباس قال: أسلمت امرأة فتزوجت فجاء زوجها فقال: " يا رسول الله إنى كنت أسلمت وعلمت بإسلامى فانتزعها رسول الله صلى الله عليه وسلم من زوجها الآخر وردها إلى زوجها الأول ".
** حم د جه
(بلوغ المرام ٢١١/١)
** قال الحافظ فى " البلوغ " ١ / ٢١١: صححه ابن حبان والحاكم.','3','44',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4197,'<span data-type="title" id=toc-4206>٤١٩٧ </span>- عن زيد بن كعب بن عجرة عن أبيه قال: تزوج رسول الله صلى الله عليه وسلم
العالية من بنى غفار فلما دخلت عليه رأى بكشحها بياضا فقال النبى صلى الله
عليه وسلم: " البسى ثيابك والحقى بأهلك وأمر لها بالصداق ".
** ك
(بلوغ المرام ٢١١/١)
** قال الحافظ فى " البلوغ " ١ / ٢١١: فى إسناده جميل بن زيد وهو مجهول واختلف عليه فى شيخه اختلافا كثيرا.
** تعقيب: قال الفقى ١ / ٢١١: _ فى جميل بن زيد _ قال الذهبى فى الميزان: قال ابن معين: ليس بثقة. قال البخارى: لم يصح حديثه.','3','45',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4198,'<span data-type="title" id=toc-4207>٤١٩٨ </span>- عن سعيد بن المسيب أن عمر بن الخطاب قال: " أيما رجل تزوج امرأة فدخل بها فوجدها برصاء أو مجنونة أو مجذومة فله الصداق بمسيسه إياها وهو له على من غره منها ".
** ما ص شب
(بلوغ المرام ٢١١/١)
** رجاله ثقات','3','46',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4199,'<span data-type="title" id=toc-4208>٤١٩٩ </span>- عن سعيد بن المسيب قال: " قضى عمر رضى الله عنه فى العنين أن يؤجل سنة ".
** لم
(بلوغ المرام ٢١٢/١)
** رجاله ثقات','3','47',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4200,'<span data-type="title" id=toc-4209>٤٢٠٠ </span>- عن أبى هريرة رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم:
" ملعون من أتى امرأة فى دبرها ".
** د ن
(بلوغ المرام ٢١٢/١)
** رجاله ثقات
** قال الحافظ فى " البلوغ " ١ / ٢١٢: رجاله ثقات لكن أعل بالإرسال.','3','48',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4201,'<span data-type="title" id=toc-4210>٤٢٠١ </span>- عن ابن عباس رضى الله عنهما قال: قال رسول الله صلى الله عليه وسلم:
" لا ينظر الله إلى رجل أتى رجلا أو امرأة فى دبرها ".
** ت ن حب
(بلوغ المرام ٢١٢/١)
** قال الحافظ فى " البلوغ " ١ / ٢١٢: أعل بالوقف.','3','49',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4202,'<span data-type="title" id=toc-4211>٤٢٠٢ </span>- عن حكيم بن معاوية عن أبيه رضى الله عنه قال: قلت يا رسول الله ما حق زوج
أحدنا عليه؟ قال: " تطعمها إذا أكلت وتكسوها إذا اكتسيت ولا تضرب الوجه ولا تهجر إلا فلا البيت ".
** حم د ن جه
(بلوغ المرام ٢١٣/١)
** قال الحافظ فى " البلوغ " ١ / ٢١٢: علق البخارى بعضه وصححه ابن حبان والحاكم.','3','50',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4203,'<span data-type="title" id=toc-4212>٤٢٠٣ </span>- عن أبى سعيد الخدرى رضى الله عنه أن رجلا قال: يا رسول الله إن لى جارية وأنا أعزل عنها وأنا أكره أن تحمل وأنا أريد ما يريد الرجال وإن اليهود تحدث أن العزل المؤودة الصغرى قال: " كذبت اليهود لو أراد الله أن يخلقه مااستطعت أن تصرفه ".
** حم د ن طحا
(بلوغ المرام ٢١٤/١)
** رجاله ثقات
** تعقيب: قال الفقى ١ / ٢١٤: قال ابن القيم بعد أن ساقه بسند أبى داود: وحسبك بهذا الإسناد صحة، فكلهم ثقات حفاظ.','3','51',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4204,'<span data-type="title" id=toc-4213>٤٢٠٤ </span>- عن ابن عباس قال: لما تزوج على فاطمة قال له رسول الله صلى الله عليه وسلم: " أعطها شيئا قال ما عندى شىء، قال فأين درعك الحطمية ".
** د ن
(بلوغ المرام ٢١٥/١)
** قال الحافظ فى " البلوغ " ١ / ٢١٥: صححه الحاكم.','3','52',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4205,'<span data-type="title" id=toc-4214>٤٢٠٥ </span>- عن علقمة عن ابن مسعود: أنه سئل عن رجل تزوج امرأة ولم يفرض لها صداقا ولم يدخل بها حتى مات، فقال ابن مسعود: لها مثل صداق نسائهالا وكس ولا
شطط وعليها العدة ولها الميراث، فقام معقل بن سنان الأشجعى فقال: قضى رسول الله صلى الله عليه وسلم فى بروع بنت واشق _ امرأة منا _ مثل ما قضيت ففرح بها ابن مسعود.
** حم د ت ن جه
(بلوغ المرام ٢١٦/١)
** قال الحافظ فى " البلوغ " ١ / ٢١٦: صححه الترمذى وحسنه جماعة.','3','53',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4206,'<span data-type="title" id=toc-4215>٤٢٠٦ </span>- عن جابر بن عبد الله رضى الله عنهما أن النبى صلى الله عليه وسلم قال:
" من أعطى فى صداق امرأة سويقا أو تمرا فقد استحل ".
** د
(بلوغ المرام ٢١٦/١)
** قال الحافظ فى " البلوغ " ١/ ٢١٦: أخرجه أبو داود وأشار إلى ترجيح وقفه.','3','54',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4207,'<span data-type="title" id=toc-4216>٤٢٠٧ </span>- عن عبد الله بن عامر بن ربيعة عن أبيه رضى الله عنه: " أن النبى صلى الله
عليه وسلم أجاز نكاح امرأة على نعلين ".
** ت
(بلوغ المرام ٢١٦/١)
** قال الحافظ فى " البلوغ " ١ / ٢١٦: صححه الترمذى وخولف فى ذلك.','3','55',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4208,'<span data-type="title" id=toc-4217>٤٢٠٨ </span>- عن على رضى الله عنه قال: " لا يكون المهر أقل من عشرة دراهم ".
** قط
(بلوغ المرام ٢١٧/١)
** إسناده فيه مقال
** قال الحافظ فى " البلوغ " ١ / ٢١٧: أخرجه الدارقطنى موقوفا وفى سنده مقال.
** تعقيب: قال الفقى ١ / ٢١٧: فى إسناده مبشر بن عبيد. قال أحمد: كان يضع الحديث.','3','56',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4209,'<span data-type="title" id=toc-4218>٤٢٠٩ </span>- عن عقبة بن عامر رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم:
" خير الصداق أيسره ".
** د
(بلوغ المرام ٢١٧/١)
** قال الحافظ فى " البلوغ " ١ / ٢١٧: صححه الحاكم.','3','57',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4210,'<span data-type="title" id=toc-4219>٤٢١٠ </span>- عن عائشة رضى الله عنها أن عمرة بنت الجون تعوذت من رسول الله صلى الله عليه
وسلم حين أدخلت عليه _ تعنى لما تزوجها _ فقال: " لقد عذت بمعاذ "، فطلقها
وأمر أسامة فمتعها بثلاثة أثواب.
** جه
(بلوغ المرام ٢١٧/١)
** إسناده فيه راو متروك
** قال الحافظ فى " البلوغ "١ / ٢١٧: فى إسناده راو متروك. وأصل القصة فى الصحيح من حديث أبى أسيد الساعدى.','3','58',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4211,'<span data-type="title" id=toc-4220>٤٢١١ </span>- عن ابن مسعود قال: قال رسول الله صلى الله عليه وسلم: " طعام الوليمة أول
يوم حق، وطعام اليوم الثانى سنة، وطعام الثالث سمعة، ومن سمع سمع الله
به ".
** ت
(بلوغ المرام ٢١٨/١)
** رجاله رجال الصحيح
** قال الحافظ فى " البلوغ " ١ /٢١٨: استغربه الترمذى. وله شاهد عن أنس عند ابن ماجه.
** تعقيب: قال الفقى ١ / ٢١٨: رواه أحمد وأبو داود عن قتادة عن الحسن عن عبد الله بن عثمان الثقفى عن رجل من ثقيف يقال إن له معروفا وأثنى عليه. قال قتادة: إن لم يكن زهير بن عثمان فلا أدرى ما اسمه.','3','59',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4212,'<span data-type="title" id=toc-4221>٤٢١٢ </span>- عن رجل من أصحاب النبى صلى الله عليه وسلم قال: " إذا اجتمع داعيان فأجب أقربهما بابا فإن سبق أحدهما فأجب الذى سبق ".
** د
(بلوغ المرام ٢١٩/١)
** إسناده ضعيف
** تعقيب: قال الفقى ١ / ٢١٩: فى إسناده أبو خالد يزيد بن عبد الرحمن الدالانى وثقه أبو حاتم الرازى.
وقال أحمد وابن معين: لا بأس به. وقال ابن عدى: فى إسناده لين إلا أنه يكتب حديثه. والحديث رواه أيضا أحمد وزاد " فإن أقربهما بابا أقربهما جوارا.','3','60',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4213,'<span data-type="title" id=toc-4222>٤٢١٣ </span>- عن ابن عباس أن النبى صلى الله عليه وسلم أتى بقصعة من ثريد فقال: " كلوا من جوانبها ولا تأكلوا من وسطها فإن البركة تنزل فى وسطها ".
** د ت ن جه
(بلوغ المرام ٢١٩/١)
** إسناده صحيح','3','61',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4214,'<span data-type="title" id=toc-4223>٤٢١٤ </span>- عن عائشة رضى الله عنها قالت: كان رسول الله صلى الله عليه وسلم يقسم
لنسائه فيعدل ويقول: " اللهم هذا قسمى فيما أملك فلا تلمنى فيما تملك ولا أملك ".
** د ت ن جه
(بلوغ المرام ٢٢٠/١)
** قال الحافظ فى " البلوغ " ١ / ٢٢٠: صححه ابن حبان والحاكم ولكن رجح الترمذى إرساله.','3','62',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4215,'<span data-type="title" id=toc-4224>٤٢١٥ </span>- عن أبى هريرة رضى الله عنه أن النبى صلى الله عليه وسلم قال: " من كانت له
امرأتان فمال إلى إحداهما جاء يوم القيامة وشقه مائل ".
** حم د ت ن جه
(بلوغ المرام ٢٢٠/١)
** إسناده صحيح','3','63',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4216,'<span data-type="title" id=toc-4225>٤٢١٦ </span>- عن عروة رضى الله عنه قال: قالت عائشة رضى الله عنها: ياابن أختى " كان رسول الله صلى الله عليه وسلم لا يفضل بعضنا على بعض فى القسم من مكثه عندنا وكان قل يوم إلا وهو يطوف علينا جميعا فيدنو من كل امرأة من غير مسيس حتى يبلغ الذى هو يومها فيبيت عندها ".
** حم د
(بلوغ المرام ٢٢١/١)
** قال الحافظ فى " البلوغ " ١/ ٢٢١: صححه الحاكم.','3','64',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4217,'<span data-type="title" id=toc-4226>٤٢١٧ </span>- " أن امرأة ثابت بن قيس اختلعت منه، فجعل النبى صلى الله عليه وسلم عدتها حيضة ".
** د ت
(بلوغ المرام ٢٢٢/١)
** قال الحافظ فى " البلوغ " ١ / ٢٢٢: حسنه الترمذى.','3','65',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4218,'<span data-type="title" id=toc-4227>٤٢١٨ </span>- عن ابن عمر رضى الله عنهما قال: قال رسول الله صلى الله عليه وسلم:
" أبغض الحلال عند الله الطلاق ".
** د جه
(بلوغ المرام ٢٢٣/١)
** قال الحافظ فى " البلوغ " ١ / ٢٢٣: صححه الحاكم، ورجح أبو حاتم إرساله.','3','66',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4219,'<span data-type="title" id=toc-4228>٤٢١٩ </span>- عن محمود بن لبيد قال: أخبر رسول الله صلى الله عليه وسلم عن رجل طلق امرأته ثلاث تطليقات جميعا، فقام غضبان ثم قال: " أيلعب بكتاب الله وأنا بين أظهركم "، حتى قام رجل فقال: يا رسول الله ألا أقتله؟ .
** ن
(بلوغ المرام ٢٢٤/١)
** رواته موثقون
** قال الحافظ فى " البلوغ " ١ / ٢٢٤: رواته موثقون.','3','67',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4220,'<span data-type="title" id=toc-4229>٤٢٢٠ </span>- عن ابن عباس رضى الله عنهما قال: طلق أبو ركانة أم ركانة فقال له رسول الله صلى الله عليه وسلم: " راجع امرأتك " فقال: إنى طلقتها ثلاثا قال: " قد علمت راجعها ". وفى لفظ لأحمد: طلق أبو ركانة امرأته فى مجلس واحد ثلاثا فحزن عليها فقال له رسول الله صلى الله عليه وسلم: " فإنها واحدة ".
** د حم
(بلوغ المرام ٢٢٥/١)
** قال الحافظ فى " البلوغ " ١ / ٢٢٥: وفى سندهما ابن إسحاق وفيه مقال.
** تعقيب: قال الفقى ١ / ٢٢٥: ابن إسحاق إنما يتهم بالتدليس إذا عنعن فقط وإلا فهو إمام ثقة.','3','68',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4221,'<span data-type="title" id=toc-4230>٤٢٢١ </span>- عن أبى هريرة رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم:
" ثلاث جدهن جد وهزلهن جد: النكاح، والطلاق، والرجعة ".
** د ت جه
(بلوغ المرام ٢٢٦/١)
** قال الحافظ فى " البلوغ " ١ / ٢٢٦: صححه الحاكم. وفى رواية لابن عدى من وجه آخر ضعيف: " الطلاق والعتاق والنكاح ".','3','69',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4222,'<span data-type="title" id=toc-4231>٤٢٢٢ </span>- عن عبادة بن الصامت مرفوعا " لا يجوز اللعب فى ثلاث: الطلاق والنكاح والعتق فمن قالهن فقد وجبن ".
** حارث
(بلوغ المرام ٢٢٦/١)
** إسناده ضعيف','3','70',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4223,'<span data-type="title" id=toc-4232>٤٢٢٣ </span>- عن ابن عباس رضى الله تعالى عنهما عن النبى صلى الله عليه وسلم قال: " إن الله تعالى وضع عن أمتى الخطأ والنسيان ومااستكرهوا عليه ".
** جه ك
(بلوغ المرام ٢٢٦/١)
** قال الحافظ فى " البلوغ " ١ / ٢٢٦: قال أبو حاتم: لا يثبت.','3','71',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4224,'<span data-type="title" id=toc-4233>٤٢٢٤ </span>- عن جابر رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم:
" لا طلاق إلا بعد نكاح ولاعتق إلا بعد ملك ".
** يع
(بلوغ المرام ٢٢٧/١)
** قال الحافظ فى " البلوغ " ١ / ٢٢٧: صححه الحاكم وهو معلول. وأخرج ابن ماجه عن المسور بن مخرمة مثله، وإسناده حسن لكنه معلول أيضا.
** تعقيب: قال الفقى ١ / ٢٢٧: قال الحاكم: أنا متعجب من الشيخين كيف أهملاه؟ لقد صح على شرطهما من حديث عائشة وجابر وابن عمر ومعاذ. وقال ابن معين: لا يصح عن النبى صلى الله عليه وسلم. وقال ابن عبد البر: روى من وجوه إلا أنها عند أهل العلم معلولة وقال الدارقطنى: الصحيح مرسل ليس فيه جابر. وقال الفقى ١ / ٢٢٧ _ فى التعليق على رواية ابن ماجه _: لأنه اختلف فيه على الزهرى.','3','72',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4225,'<span data-type="title" id=toc-4234>٤٢٢٥ </span>- عن عمرو بن شعيب عن أبيه عن جده رضى الله عنهما قال: قال رسول الله صلى الله
عليه وسلم: " لا نذر لابن آدم فيمالا يملك، ولا عتق له فيما لا يملك، ولا
طلاق له فيما لا يملك ".
** د ت
(بلوغ المرام ٢٢٨/١)
** قال الحافظ فى " البلوغ " ١ / ٢٢٨: صححه الترمذى ونقل عن البخارى أنه أصح ما ورد فيه.
** تعقيب: قال الفقى ١ / ٢٢٨: قال البيهقى: هو أصح حديث وأشهره.','3','73',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4226,'<span data-type="title" id=toc-4235>٤٢٢٦ </span>- عن عائشة رضى الله عنها عن النبى صلى الله عليه وسلم قال: " رفع القلم عن
ثلاثة: عن النائم حتى يستيقظ، وعن الصغير حتى يكبر، وعن المجنون حتى
يعقل أو يفيق ".
** حم د ن جه حب
(بلوغ المرام ٢٢٨/١)
** قال الحافظ فى " البلوغ " ١ / ٢٢٨: صححه الحاكم.
** تعقيب: قال الفقى ١ / ٢٢٨: ورواه البخارى معلقا عن على فى قصة عمر حين أراد أن يرجم المجنونة.','3','74',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4227,'<span data-type="title" id=toc-4236>٤٢٢٧ </span>- عن عمران بن حصين رضى الله عنه أنه سئل عن الرجل يطلق ثم يتراجع ولا يشهد؟
فقال: " أشهد على طلاقها وعلى رجعتها ".
** د
(بلوغ المرام ٢٢٨/١)
** إسناده صحيح
** قال الحافظ فى " البلوغ " ١ / ٢٢٨: رواه أبو داود هكذا موقوفا. وأخرجه البيهقى بلفظ: " أن عمران بن حصين سئل عمن راجع امرأته، ولم يشهد فقال فى غير سنة؟ فليشهد الآن ". وزاد الطبرانى فى رواية: ويستغفر الله','3','75',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4228,'<span data-type="title" id=toc-4237>٤٢٢٨ </span>- عن عائشة رضى الله عنها قالت: " آلى رسول الله صلى الله عليه وسلم من نسائه وحرم فجعل الحرام حلالا وجعل لليمين كفارة ".
** ت
(بلوغ المرام ٢٢٩/١)
** رواته ثقات
** تعقيب: قال الفقى ١ / ٢٢٩ ك قال الترمذى: روى عن الشعبى عن مسروق عن عائشة وروى عن الشعبى عن النبى صلى الله عليه وسلم مرسلا وهو أصح. اهـ.','3','76',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4229,'<span data-type="title" id=toc-4238>٤٢٢٩ </span>- عن ابن عباس رضى الله عنه أن رجلا ظاهر من امرأته ثم وقع عليها فأتى النبى صلى الله عليه وسلم فقال: إنى وقعت عليها قبل أن أكفر، قال: " فلا تقربها حتى تفعل ماأمرك الله به ".
** د ت ن جه
(بلوغ المرام ٢٣٠/١)
** قال الحافظ فى " البلوغ " ١ / ٢٣٠: صححه الترمذى ورجح النسائى إرساله. ورواه البزار من وحه آخر عن ابن عباس رضى الله تعالى عنهما وزاد فيه " كفر ولا تعد ".','3','77',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4230,'<span data-type="title" id=toc-4239>٤٢٣٠ </span>- عن سلمة بن صخر قال: دخل رمضان فخفت أن أصيب امرأتى فظاهرت منها فانكشف لى شىء منها ليلة فوقعت عليها، فقال لى رسول الله صلى الله عليه وسلم:
" حرر رقبة " فقلت: ماأملك إلا رقبتى، قال: " فصم شهرين متتابعين "، قلت: وهل أصبت الذى أصبت إلا من الصيام؟ قال: " أطعم فرقا من تمر ستين مسكينا ".
** حم د ت جه
(بلوغ المرام ٢٣٠/١)
** قال الحافظ فى " البلوغ " ١ / ٢٣٠: صححه ابن خزيمة وابن الجارود.','3','78',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4231,'<span data-type="title" id=toc-4240>٤٢٣١ </span>- عن ابن عباس رضى الله عنهما أن رسول الله صلى الله عليه وسلم أمر رجلا أن يضع يده عند الخامسة على فيه، وقال: " إنها موجبة ".
** د ن
(بلوغ المرام ٢٣٢/١)
** رجاله ثقات','3','79',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4232,'<span data-type="title" id=toc-4241>٤٢٣٢ </span>- عن ابن عباس أن رجلا جاء إلى النبى صلى الله عليه وسلم فقال: إن امرأتى لا ترد يد لامس، قال: " غربها " قال: أخاف أن تتبعها نفسى، قال: " فاستمتع بها ".
** د ت بز
(بلوغ المرام ٢٣٢/١)
** رجاله ثقات
** قال الحافظ فى " البلوغ " ١ / ٢٣٣: رجاله ثقات وأخرجه النسائى من وجه آخر عن ابن عباس رضى الله عنهما بلفظ قال: " طلقها " قال: لا أصبر عنها، قال: " فأمسكها ".','3','80',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4233,'<span data-type="title" id=toc-4242>٤٢٣٣ </span>- عن أبى هريرة أنه سمع رسول الله صلى الله عليه وسلم يقول حين نزلت آية المتلاعنين: " أيما امرأة أدخلت على قوم من ليس منهم فليست من الله فى شىء ولم يدخلها الله جنته وأيما رجل جحد ولده _ وهو ينظر إليه _ احتجب الله عنه وفضحه على رؤوس الأولين والآخرين ".
** د ن جه
(بلوغ المرام ٢٣٣/١)
** قال الحافظ فى " البلوغ " ١ / ٢٣٣: صححه ابن حبان.','3','81',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4234,'<span data-type="title" id=toc-4243>٤٢٣٤ </span>- عن عمر رضى الله عنه قال: " من أقر بولده طرفة عين فليس له أن ينفيه ".
** هق
(بلوغ المرام ٢٣٣/١)
** حسن','3','82',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4235,'<span data-type="title" id=toc-4244>٤٢٣٥ </span>- عن عائشة رضى الله عنها قالت: " أمرت بريدة أن تعتد بثلاث حيض ".
** جه
(بلوغ المرام ٢٣٤/١)
** رواته ثقات
** قال الحافظ فى " البلوغ " ١ / ٢٣٤: رواته ثقات لكنه معلول.','3','83',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4236,'<span data-type="title" id=toc-4245>٤٢٣٦ </span>- عن أم سلمة رضى الله عنها قالت: جعلت على عينى صبرا بعد أن توفى أبو سلمة
فقال رسول الله صلى الله عليه وسلم: " إنه يشب الوجه فلا تجعليه إلا بالليل وانزعيه بالنهار ولا تمتشطى بالطيب ولا بالحناء فإنه خضاب "، قلت: بأى
شىء أمتشط؟ قال: " بالسدر ".
** د ن
(بلوغ المرام ٢٣٥/١)
** إسناده حسن','3','84',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4237,'<span data-type="title" id=toc-4246>٤٢٣٧ </span>- عن فريعة بنت مالك أن زوجها خرج فى طلب أعبد له فقتلوه قالت: فسألت رسول الله صلى الله عليه وسلم أن أرجع إلى أهلى فإن زوجى لم يترك لى مسكنا يملكه ولا نفقة، فقال: " نعم " فلما كنت فى الحجرة نادانى،
فقال: " امكثى فى بيتك حتى يبلغ الكتاب أجله " قالت: فاعتددت فيه أربعة أشهر وعشرا قالت: فقضى به بعد ذلك عثمان.
** حم د ت ن جه
(بلوغ المرام ٢٣٥/١)
** قال الحافظ فى " البلوغ " ١ / ٢٣٦: صححه الترمذى والذهبى وابن حبان وغيرهم.','3','85',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4238,'<span data-type="title" id=toc-4247>٤٢٣٨ </span>- عن عمرو بن العاص رضى الله عنه قال: " لا تلبسوا علينا سنة نبينا: عدة أم الولد إذا توفى عنها سيدها أربعة أشهر وعشر ".
** حم د جه
(بلوغ المرام ٢٣٦/١)
** قال الحافظ فى " البلوغ " ١ / ٢٣٦: صححه الحاكم. وأعله الدارقطنى بالإنقطاع.','3','86',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4239,'<span data-type="title" id=toc-4248>٤٢٣٩ </span>- عن عائشة قالت: " إنما الأقراء الأطهار ".
** ما
(بلوغ المرام ٢٣٦/١)
** إسناده صحيح
** قال الحافظ فى " البلوغ " ١ / ٢٣٦: أخرجه مالك فى قصة بسند صحيح.','3','87',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4240,'<span data-type="title" id=toc-4249>٤٢٤٠ </span>- عن ابن عمر قال: " طلاق الأمة تطليقتان وعدتها حيضتان ".
** قط
(بلوغ المرام ٢٣٦/١)
** قال الحافظ فى " البلوغ " ١ / ٢٣٦: رواه الدارقطنى وأخرجه مرفوعا وضعفه. وأخرجه أبو داود والترمذى وابن ماجه من حديث عائشة رضى الله عنها، وصححه الحاكم وخالفوه فاتفقوا على ضعفه.','3','88',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4241,'<span data-type="title" id=toc-4250>٤٢٤١ </span>- عن روفيع بن ثابت رضى الله عنه عن النبى صلى الله عليه وسلم قال:
" لا يحل لامرىء يؤمن بالله واليوم الآخر أن يسقى ماءه زرع غيره ".
** د ت
(بلوغ المرام ٢٣٧/١)
** قال الحافظ فى " البلوغ " ١ / ٢٣٧: صححه ابن حبان، وحسنه البزار.','3','89',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4242,'<span data-type="title" id=toc-4251>٤٢٤٢ </span>- عن المغيرة بن شعبة قال: قال رسول الله صلى الله عليه وسلم: " امرأة المفقود امرأته حتى يأتيها البيان ".
** قط
(بلوغ المرام ٢٣٧/١)
** إسناده ضعيف','9','317',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4243,'<span data-type="title" id=toc-4252>٤٢٤٣ </span>- عن أبى سعيد رضى الله عنه أن النبى صلى الله عليه وسلم قال فى سبايا أوطاس: " لا توطأ حامل حتى تضع ولا غير ذات حمل حتى تحيض حيضة ".
** د
(بلوغ المرام ٢٣٧/١)
** قال الحافظ فى " البلوغ " ١ / ٢٣٧: صححه الحاكم وله شاهد عن ابن عباس رضى الله عنهما فى الدارقطنى.','9','318',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4244,'<span data-type="title" id=toc-4253>٤٢٤٤ </span>- عن أم سلمة رضى الله عنها قالت: قال رسول الله صلى الله عليه وسلم: " لا يحرم من الرضاع إلا ما فتق الأمعاء وكان قبل الفطام ".
** ت
(بلوغ المرام ٢٣٩/١)
** قال الحافظ فى " البلوغ " ١ / ٢٣٩: صححه الترمذى والحاكم.','9','319',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4245,'<span data-type="title" id=toc-4254>٤٢٤٥ </span>- عن ابن عباس رضى الله عنهما قال: " لا رضاع إلا فى الحولين ".
** قط عد
(بلوغ المرام ٢٤٠/١)
** قال الحافظ فى " البلوغ " ١ / ٢٤٠: رواه الدارقطنى وابن عدى مرفوعا وموقوفا ورجحا الموقوف.','9','320',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4246,'<span data-type="title" id=toc-4255>٤٢٤٦ </span>- عن طارق المحاربى رضى الله عنه قال: قدمنا المدينة فإذا رسول الله صلى الله عليه وسلم قائم على المنبر يخطب الناس ويقول: " يد المعطى
العليا وابدأ بمن تعول أمك وأباك وأختك وأخاك ثم أدناك فأدناك ".
** ن
(بلوغ المرام ٢٤٠/١)
** قال الحافظ فى " البلوغ " ١ / ٢٤٠: صححه ابن حبان والدارقطنى.','9','321',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4247,'<span data-type="title" id=toc-4256>٤٢٤٧ </span>- عن جابر يرفعه فى الحامل المتوفى عنها زوجها قال: " لا نفقة لها ".
** هق
(بلوغ المرام ٢٤١/١)
** رجاله ثقات
** قال الححافظ فى " البلوغ " ١ / ٢٤١: أخرجه البيهقى ورجاله ثقات لكن قال: المحفوظ وقفه.','9','322',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4248,'<span data-type="title" id=toc-4257>٤٢٤٨ </span>- عن أبى هريرة رضى الله تعالى عنه قال: قال رسول الله صلى الله عليه وسلم:
" اليد العليا خير من اليد السفلى ويبدأ أحدكم بمن يعول تقول المرأة أطعمنى أو طلقنى ".
** قط
(بلوغ المرام ٢٤١/١)
** إسناده حسن','9','323',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4249,'<span data-type="title" id=toc-4258>٤٢٤٩ </span>- عن سعيد بن المسيب: " فى الرجل لا يجد ما ينفق على أهله قال يفرق بينهما ".
** ص
(بلوغ المرام ٢٤٢/١)
** مرسل قوى
** قال الحافظ فى " البلوغ " ١ / ٢٤١: أخرجه سعيد بن منصور عن سفيان عن أبى الزناد عنه قال: قلت لسعيد بن المسيب: سنة؟ فقال: سنة، وهذا مرسل قوى.','9','324',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4250,'<span data-type="title" id=toc-4259>٤٢٥٠ </span>- عن عمر " أنه كتب إلى أمراء الأجناد فى رجال غابوا عن نسائهم أن يأخذوهم بأن ينفقوا أو يطلقوا فإن طلقوا بعثوا بنفقة ما حبسوا ".
** شفع هق
(بلوغ المرام ٢٤٢/١)
** إسناده حسن','9','325',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4251,'<span data-type="title" id=toc-4260>٤٢٥١ </span>- عن بهز بن حكيم عن أبيه عن جده قال: قلت يا رسول الله: من أبر؟ قال:
" أمك "، قلت: ثم من؟ قال: " أمك "، قلت: ثم من؟ قال: " أمك "، قلت: ثم من؟ قال: " أباك ثم الأقرب فالأقرب ".
** د ت
(بلوغ المرام ٢٤٢/١)
** قال الحافظ فى " البلوغ " ١ / ٢٤٢: حسنه الترمذى.','9','326',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4252,'<span data-type="title" id=toc-4261>٤٢٥٢ </span>- عن عبد الله بن عمرو أن امرأة قالت: يا رسول الله إن ابنى كان بطنى له وعاء
وثديى له ثقاء وحجرى له حواء وإن أباه طلقنى وأراد أن ينتزعه منى، فقال
لها رسول الله صلى الله عليه وسلم: " أنت أحق به مالم تنكحى ".
** حم د
(بلوغ المرام ٢٤٢/١)
** قال الحافظ فى " البلوغ " ١ / ٢٤٣: صححه الحاكم.','9','327',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4253,'<span data-type="title" id=toc-4262>٤٢٥٣ </span>- عن أبى هريرة أن امرأة قالت يا رسول الله: إن زوجى يريد أن يذهب بابنى وقد
نفعنى وسقانى من بئر أبى عتبة، فجاء زوجها فقال النبى صلى الله عليه وسلم:
" يا غلام هذا أبوك وهذه أمك، فخذ بيد أيهما شئت " فأخذ بيد أمه، فانطلقت
به.
** حم د ت ن جه
(بلوغ المرام ٢٤٣/١)
** قال الحافظ فى " البلوغ " ١ / ٢٤٣: صححه الترمذى.','9','328',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4254,'<span data-type="title" id=toc-4263>٤٢٥٤ </span>- عن رافع بن سنان أنه أسلم وأبت امرأته أن تسلم، فأقعد النبى صلى الله عليه وسلم الأم ناحية والأب ناحية وأقعد الصبى بينهما فمال إلى أمه،
فقال: " اللهم اهده " فمال إلى أبيه فأخذه.
** د ن
(بلوغ المرام ٢٤٣/١)
** قال الحافظ فى " البلوغ " ١ / ٢٤٣: صححه الحاكم.','9','329',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4255,'<span data-type="title" id=toc-4264>٤٢٥٥ </span>- عن عائشة عن رسول الله صلى الله عليه وسلم قال: " لا يحل قتل مسلم إلا بإحدى
ثلاث خصال: زان محصن فيرجم، ورجل يقتل مسلما متعمدا فيقتل، ورجل يخرج من
الإسلام فيحارب الله ورسوله فيقتل أو يصلب أو ينفى من الأرض ".
** د ن
(بلوغ المرام ٢٤٤/١)
** قال الحافظ فى " البلوغ " ١ / ٢٤٤: صححه الحاكم.','9','330',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4256,'<span data-type="title" id=toc-4265>٤٢٥٦ </span>- عن سمرة رضى الله تعالى عنه قال: قال رسول الله صلى الله عليه وسلم:
" من قتل عبده قتلناه، ومن جدع عبده جدعناه ".
** حم د ت ن جه
(بلوغ المرام ٢٤٤/١)
** قال الحافظ فى " البلوغ " ١ / ٢٤٥: حسنه الترمذى وهو من رواية الحسن البصرى عن سمرة وقد اختلف فى سماعه منه. وفى رواية أبى داود والنسائى بزيادة " ومن خصى عبده خصيناه ". وصحح الحاكم هذه الزيادة.','9','331',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4257,'<span data-type="title" id=toc-4266>٤٢٥٧ </span>- عن عمر بن الخطاب رضى الله عنه قال: سمعت رسول الله صلى الله عليه وسلم
يقول: " لا يقاد الوالد بالولد ".
** حم ت جه
(بلوغ المرام ٢٤٥/١)
** قال الحافظ فى " البلوغ " ١ / ٢٤٥: صححه ابن الجارود والبيهقى. وقال الترمذى: إنه مضطرب.
** تعقيب: قال الفقى ١ / ٢٤٥: وقال الترمذى: وقد روى هذا الحديث عن عمرو بن شعيب مرسل وهذا الحديث فيه
اضطراب.','9','332',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4258,'<span data-type="title" id=toc-4267>٤٢٥٨ </span>- عن على رضى الله تعالى عنه وقال فيه: " المؤمنون تتكافأ دماؤهم ويسعى بذمتهم أدناهم وهم يد على من سواهم ولا يقتل مؤمن بكافر ولا ذو عهد فى عهده ".
** حم د ن
(بلوغ المرام ٢٤٥/١)
** قال الحافظ فى " البلوغ " ١ / ٢٤٦: صححه الحاكم.','9','333',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4259,'<span data-type="title" id=toc-4268>٤٢٥٩ </span>- عن عمران بن حصين رضى الله عنه أن غلاما لأناس فقراء قطع أذن غلام لأناس أغنياء فأتوا النبى صلى الله عليه وسلم فلم يجعل لهم شيئا.
** حم د ت ن
(بلوغ المرام ٢٤٦/١)
** إسناده صحيح','9','334',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4260,'<span data-type="title" id=toc-4269>٤٢٦٠ </span>- عن عمرو بن شعيب عن أبيه عن جده أن رجلا طعن رجلا بقرن فى ركبته فجاء إلى
النبى صلى الله عليه وسلم، فقال: أقدنى، فقال: " حتى تبرأ " ثم جاء إليه
فقال: أقدنى، فأقاده، ثم جاء إليه فقال: يا رسول الله عرجت، فقال: " قد نهيتك فعصيتنى فأبعدك الله وبطل عرجك " ثم نهى رسول الله صلى الله عليه وسلم أن يقتص من جرح حتى يبرأ صاحبه.
** حم قط
(بلوغ المرام ٢٤٦/١)
** قال الحافظ فى " البلوغ " ١ / ٢٤٦: أعل بالإرسال.
** تعقيب: قال الفقى ١ / ٢٤٦: لأنه من رواية عمرو بن شعيب عن أبيه عن جده وفى معناه أحاديث تزيده قوة.','9','335',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4261,'<span data-type="title" id=toc-4270>٤٢٦١ </span>- حديث ابن عباس أن عمر رضى الله عنه سأل من شهد قضاء رسول الله صلى الله
عليه وسلم فى الجنين؟ قال: فقام حمل بن النابغة فقال: كنت بين يدى
امرأتين فضربت إحداهما الأخرى.... فذكره مختصرا
** د ن
(بلوغ المرام ٢٤٧/١)
** قال الحافظ فى " البلوغ " ١ / ٢٤٧: صححه ابن حبان والحاكم.','9','336',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4262,'<span data-type="title" id=toc-4271>٤٢٦٢ </span>- عن ابن عباس رضى الله عنهما قال: قال رسول الله صلى الله عليه وسلم:
" من قتل فى عميا أو رميا بحجر أو سوط أو عصا فعقله عقل الخطأ ومن قتل عمدا فهو قود ومن حال دونه فعليه لعنة الله ".
** د ن جه
(بلوغ المرام ٢٤٨/١)
** إسناده قوى','9','337',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4263,'<span data-type="title" id=toc-4272>٤٢٦٣ </span>- عن ابن عمر رضى الله عنهما عن النبى صلى الله عليه وسلم قال: " إذا أمسك
الرجل الرجل وقتله الآخر يقتل الذى قتل ويحبس الذى أمسك ".
** قط
(بلوغ المرام ٢٤٨/١)
** رجاله ثقات
** قال الحافظ فى " البلوغ " ١ / ٢٤٨: رواه الدارقطنى موصولا، وصححه ابن القطان ورجاله ثقات، إلا أن البيهقى رجح المرسل.','9','338',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4264,'<span data-type="title" id=toc-4273>٤٢٦٤ </span>- عن عبد الرحمن بن البيلمانى أن النبى صلى الله عليه وسلم قتل مسلما بمعاهد
وقال: " أنا أولى من وفى بذمته ".
** عب
(بلوغ المرام ٢٤٨/١)
** قال الحافظ فى " البلوغ " ١ / ٢٤٨: أخرجه عبد الرازق هكذا مرسلا، ووصله الدارقطنى بذكر ابن عمر فيه وإسناد الموصول واه.','9','339',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4265,'<span data-type="title" id=toc-4274>٤٢٦٥ </span>- عن أبى بكر بن محمد بن عمرو بن حزم عن أبيه عن جده أن النبى صلى الله عليه
وسلم كتب إلى أهل اليمن فذكر الحديث وفيه: " أن من اعتبط مؤمنا قتلا عن بينه فإنه قود إلا أن يرضى أولياء المقتول،
وإن فى النفس الدية مائة من الإبل، وفى الأنف إذا أوعب جدعه الدية وفى
العينين الدية، وفى اللسان الدية، وفى الشفتين الدية، وفى الذكر الدية،
وفى البيضتين الدية، وفى الصلب الدية، وفى الرجل الواحدة نصف الدية،
وفى المأمومة ثلث الدية، وفى الجائفة ثلث الدية، وفى المنقلة خمس عشرة
من الإبل، وفى كل إصبع من أصابع اليد والرجل عشر من الإبل وفى السن خمس
من الإبل، وفى الموضحة خمس من الإبل، وإن الرجل يقتل بالمرأة وعلى أهل
الذهب ألف دينار ". الذهب ألف دينار ".
** مد ن خز جا حب حم
(بلوغ المرام ٢٤٩/١)
** قال الحافظ فى " البلوغ " ١ / ٢٤٩: اختلفوا فى صحته.
** تعقيب: قال الفقى ١ / ٢٤٩: قال الشافعى فى الرسالة: لم يقبلوا هذا الحديث حتى ثبت عندهم أنه كتاب النبى صلى الله عليه وسلم. وقال ابن عبد البر: هذا الحديث مشهور عند أهل السنة، مرفوع مافيه عند أهل العلم معرفة يستغنى بشهرتها عن الإسناد لأنه أشبه المتواتر لتلقى الناس له بالقبول. اه.','9','340',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4266,'<span data-type="title" id=toc-4275>٤٢٦٦ </span>- عن ابن مسعود عن النبى صلى الله عليه وسلم قال: " دية الخطأ أخماسا عشرون
حقه، وعشرون جذعة وعشرون بنات مخاض وعشرون بنات لبون، وعشرون بنى
لبون ".
** قط
(بلوغ المرام ٢٤٩/١)
** قال الحافظ فى " البلوغ " ١ / ٢٤٩: أخرجه الأربعة بلفظ " وعشرون بنى مخاص " بدل لبون، وإسناد الأول أقوى وأخرجه ابن شيبة من وجه آخر موقوفا وهو أصح من المرفوع.','9','341',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4267,'<span data-type="title" id=toc-4276>٤٢٦٧ </span>- عن ابن عمر رضى الله عنهما عن النبى صلى الله عليه وسلم قال: " إن أعتى الناس على الله ثلاثة: من قتل فى حرم الله، أو قتل غير قاتله، أو قتل لذحل الجاهلية ".
** حب
(بلوغ المرام ٢٥٠/١)
** قال الحافظ فى " البلوغ " ١ / ٢٥٠: أخرجه ابن حبان فى حديث صححه.
** تعقيب: قال الفقى ١ / ٢٥٠: وأخرجه أحمد.','9','342',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4268,'<span data-type="title" id=toc-4277>٤٢٦٨ </span>- عن عبد الله بن عمرو بن العاص رضى الله عنهما أن رسول الله صلى الله عليه
وسلم قال: " ألاإن دية الخطأ وشبه العمد ماكان بالسوط والعصاة مائة من
الإبل منها أربعون فى بطونها أولادها ".
** د ن جه
(بلوغ المرام ٢٥٠/١)
** قال الحافظ فى " البلوغ " ١ / ٢٥٠: صححه ابن حبان.','9','343',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4269,'<span data-type="title" id=toc-4278>٤٢٦٩ </span>- عن عمرو بن شعيب عن أبيه عن جده رضى الله عنهم رفعه " من تطبب ولم يكن بالطب
معروفا فأصاب نفسا فما دونها فهو ضامن ".
** قط د ن
(بلوغ المرام ٢٥٠/١)
** قال الحافظ فى " البلوغ " ١ / ٢٥٠: صححه الحاكم وهو عند أبى داود والنسائى وغيرهما إلا أن من أرسله أقوى ممن وصله.
** تعقيب: قال الفقى ١ / ٢٥٠: قال الدارقطنى: لم يسنده عن ابن جريج غير الوليد بن مسلم، وقال أبو داود:
لم يروه إلا الوليد لا ندرى أصحيح هو أم لا؟ .','9','344',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4270,'<span data-type="title" id=toc-4279>٤٢٧٠ </span>- عن عمرو بن شعيب عن أبيه عن جده أن النبى صلى الله عليه وسلم قال: " فى
المواضح خمس، خمس، من الإبل ". وزاد أحمد " والأصابع سواء، كلهن عشر، عشر، من الإبل ".
** حم د ت ن جه
(بلوغ المرام ٢٥١/١)
** قال الحافظ فى " البلوغ " ١ / ٢٥١: صححه ابن خزيمة وابن الجارود.','9','345',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4271,'<span data-type="title" id=toc-4280>٤٢٧١ </span>- عن عمرو بن شعيب عن أبيه عن جده قال: قال رسول الله صلى الله عليه وسلم:
" عقل أهل الذمة نصف عقل المسلمين ".
** حم د ت ن جه
(بلوغ المرام ٢٥١/١)
** قال الحافظ فى " البلوغ " ١ / ٢٥١: ولفظ أبى داود " دية العاهد نصف دية الحر ". وللنسائى " عقل المرأة مثل عقل الرجل حتى يبلغ الثلث من ديتها ". وصححه ابن خزيمة.','9','346',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4272,'<span data-type="title" id=toc-4281>٤٢٧٢ </span>- عن عمرو بن شعيب عن أبيه عن جده قال: قال رسول الله صلى الله عليه وسلم:
" عقل شبه العمد مغلظ مثل عقل العمد ولا يقتل صاحبه وذلك أن ينزو الشيطان فتكون دماء بين الناس فى غير ضغينة ولا حمل سلاح ".
** قط
(بلوغ المرام ٢٥١/١)
** قال الحافظ فى " البلوغ " ١ / ٢٥١: أخرجه الدارقطنى وضعفه.
** تعقيب: قال الفقى ١ / ٢٥١: فى إسناده محمد راشد المكحولى متكلم فيه.','9','347',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4273,'<span data-type="title" id=toc-4282>٤٢٧٣ </span>- عن ابن عباس رضى الله عنهما قال: قتل رجل رجلا على عهد رسول الله صلى الله عليه وسلم فجعل النبى صلى الله عليه وسلم ديته اثنى عشر ألفا.
** د ت ن جه
(بلوغ المرام ٢٥١/١)
** قال الحافظ فى " البلوغ " ١ / ٢٥١: رجح النسائى وأبو حاتم إرساله.','9','348',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4274,'<span data-type="title" id=toc-4283>٤٢٧٤ </span>- عن أبى رمثة قال: أتيت النبى صلى الله عليه وسلم ومعى ابنى فقال: " من هذا " فقلت: ابنى وأشهد به، فقال: " أما إنه لا يجنى عليك
ولا تجنى عليه ".
** ن د
(بلوغ المرام ٢٥١/١)
** قال الحافظ فى " البلوغ: ١ / ٢٥٢: صححه ابن خزيمة وابن الجارود.','9','349',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4275,'<span data-type="title" id=toc-4284>٤٢٧٥ </span>- عن ابن عمر رضى الله عنهما قال: قال رسول الله صلى الله عليه وسلم:
" هل تدرى ياابن أم عبد كيف حكم الله فيمن بغى من هذه الأمة؟ " قال: الله ورسوله أعلم، قال: " لا يجهز على جريحها ولا يقتل أسيرها ولا يطلب هاربها
ولا يقسم فيؤها ".
** بز ك
(بلوغ المرام ٢٥٣/١)
** قال الحافظ فى " البلوغ " ١ / ٢٥٤: رواه الحاكم وصححه فوهم لأن فى إسناده كوثر بن حكيم وهو متروك. وصح عن على من طرق نحوه موقوفا أخرجه ابن أبى شيبة والحاكم.
** تعقيب: قال الفقى ١ / ٢٥٣: كوثر بن حكيم لم يوثقه أحد من الأمة. وقال ابن عدى: هذا حديث غير محفوظ.','9','350',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4276,'<span data-type="title" id=toc-4285>٤٢٧٦ </span>- عن عبد الله بن عمر قال: قال رسول الله صلى الله عليه وسلم:
" من قتل دون ماله فهو شهيد ".
** د ن ت
(بلوغ المرام ٢٥٤/١)
** قال الحافظ فى " البلوغ " ١ / ٢٥٤: صححه الترمذى.','9','351',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4277,'<span data-type="title" id=toc-4286>٤٢٧٧ </span>- عن البراء بن عازب رضى الله عنه قال: قضى رسول الله صلى الله عليه وسلم أن
حفظ الحوائط بالنهار على أهلها وأن حفظ الماشية بالليل على أهلها، وأن على
أهل الماشية ماأصابت ماشيتهم بالليل.
** حم د ن جه
(بلوغ المرام ٢٥٤/١)
** قال الحافظ فى " البلوغ " ١ / ٢٥٥: صححه ابن حبان وفى إسناده اختلاف.','9','352',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4278,'<span data-type="title" id=toc-4287>٤٢٧٨ </span>- عن ابن عباس رضى الله تعالى عنه أن أعمى كانت له أم ولد تشتم النبى صلى الله عليه وسلم وتقع فيه فينهاها فلا تنتهى فلما كان ذات ليلة أخذ المعول فجعله فى بطنها واتكأ عليها فقتلها فبلغ ذلك النبى صلى الله عليه وسلم فقال:
ألا اشهدوا فإن دمها هدر ".
** د
(بلوغ المرام ٢٥٥/١)
** رواته ثقات','9','353',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4279,'<span data-type="title" id=toc-4288>٤٢٧٩ </span>- عن سعيد بن سعد بن عبادة قال: كان فى أبياتنا رويجل ضعيف فخبث بأمة من
إمائهم، فذكر ذلك سعد لرسول الله صلى الله عليه وسلم فقال: " اضربوه حده " فقالوا: يا رسول الله إنه أضعف من ذلك، فقال: " خذوا عثكالا فيه مائة
شمراخ ثم اضربوه به ضربة واحدة ففعلوا ".
** حم ن جه
(بلوغ المرام ٢٥٨/١)
** إسناده حسن
** قال الحافظ فى " البلوغ " ١ / ٢٥٩: اختلف فى وصله وإرساله.','9','354',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4280,'<span data-type="title" id=toc-4289>٤٢٨٠ </span>- عن ابن عباس رضى الله عنهما أن النبى صلى الله عليه وسلم قال: " من وجدتموه
يعمل عمل قوم لوط فاقتلوا الفاعل والمفعول به ومن وجدتموه وقع على بهيمة
فاقتلوه واقتلوا البهيمة ".
** حم د ت ن جه
(بلوغ المرام ٢٥٩/١)
** رجاله موثقون
** قال الحافظ فى " البلوغ " ١ / ٢٥٩: رجاله موثقون إلا أن فيه اختلافا.','9','355',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4281,'<span data-type="title" id=toc-4290>٤٢٨١ </span>- عن ابن عمر رضى الله تعالى عنهما أن النبى صلى الله عليه وسلم ضرب وغرب
وأن أبا بكر ضرب وغرب وأن عمر ضرب وغرب.
** ت
(بلوغ المرام ٢٥٩/١)
** رجاله ثقات
** قال الحافظ فى " البلوغ " ١ / ٢٥٩: رجاله ثقات إلا أنه اختلف في وقفه ورفعه.','9','356',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4282,'<span data-type="title" id=toc-4291>٤٢٨٢ </span>- عن أبى هريرة قال: قال رسول الله صلى الله عليه وسلم: " ادفعوا الحدود ما
وجدتم لها مدفعا ".
** جه
(بلوغ المرام ٢٥٩/١)
** إسناده ضعيف','9','357',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4283,'<span data-type="title" id=toc-4292>٤٢٨٣ </span>- عن عائشة رضى الله عنها بلفظ " ادرءوا الحدود عن المسلمين ما استطعتم ".
** ت ك
(بلوغ المرام ٢٥٩/١)
** ضعيف
** تعقيب: قال الفقى ١ / ٢٥٩: وكذلك أخرجه البيهقى وهو عندهم من طريق الزهرى عن عروة عن عائشة وفيه زيادة " فان كان له مخرج فخلوا سبيله، فإن الإمام أن يخطىء فى العفو خير من أن يخطىء فى العقوبة " وفى إسناده يزيد بن زياد الدمشقى. قال فيه البخارى: منكر الحديث، وقال النسائى: متروك، ورواه وكيع عن الزهرى موقوفا. قال الترمذى: وهو أصح. وقد روى عن غير واحد من الصحابة أنهم قالوا ذلك.','9','358',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4284,'<span data-type="title" id=toc-4293>٤٢٨٤ </span>- عن أنس بن مالك قال: أول لعان كان فى الإسلام أن شريك بن سحماء قذفه هلال بن أمية بامرأته، فقال له النبى صلى الله عليه وسلم: " البينة
وإلا فحد فى ظهرك ". الحديث
** يع
(بلوغ المرام ٢٦٠/١)
** رجاله ثقات','9','359',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4285,'<span data-type="title" id=toc-4294>٤٢٨٥ </span>- عن جابر رضى الله عنه عن النبى صلى الله عليه وسلم قال: " ليس على خائن
ولا مختلس ولا منتهب قطع ".
** حم د ت ن جه
(بلوغ المرام ٢٦١/١)
** قال الحافظ فى " البلوغ " ١ / ٢٦٢: صححه الترمذى وابن حبان.','9','360',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4286,'<span data-type="title" id=toc-4295>٤٢٨٦ </span>- عن رافع بن خديج رضى الله عنه قال: سمعت رسول الله صلى الله عليه وسلم
يقول: " لا قطع فى ثمر ولا كثر ".
** حم د ت ن جه
(بلوغ المرام ٢٦٢/١)
** قال الحافظ فى " البلوغ " ١ / ٢٦٢: صححه الترمذى وابن حبان.','9','361',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4287,'<span data-type="title" id=toc-4296>٤٢٨٧ </span>- عن أبى أمية المخزومى رضى الله عنه قال: أتى رسول الله صلى الله عليه وسلم
بلص قد اعترف اعترافا ولم يوجد معه متاع، فقال له رسول الله صلى الله عليه
وسلم: " ماإخالك سرقت " قال: بلى، فأعاد عليه مرتين أو ثلاثا فأمر به فقطع
وجىء به، فقال: " أستغفر الله وتب إليه " فقال: استغفر الله وأتوب إليه
فقال: " اللهم تب عليه _ ثلاثا ".
** د حم ن
(بلوغ المرام ٢٦٢/١)
** رجاله ثقات
** قال الحافظ فى " البلوغ " ١ / ٢٦٢: رجاله ثقات وأخرجه الحاكم من حديث أبى هريرة رضى الله عنه فساقه بمعناه وقال فيه " اذهبوا به فاقطعوه ثم احموه " وأخرجه البزار أيضا وقال: لا بأس باسناده.
** تعقيب: قال الفقى ١ / ٢٦٢: قال الخطابى: فى إسناده مقال والحديث إذا رواه مجهول لم يكن حجة. قال المنذرى: كأنه يشير إلا أن أباالمنذر مولى أبى ذر لم يرو عنه إلا اسحاق بن عبد الله ابن أبى طلحة.','9','362',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4288,'<span data-type="title" id=toc-4297>٤٢٨٨ </span>- عن عبد الرحمن بن عوف رضى الله تعالى عنه أن رسول الله صلى الله عليه وسلم
قال: " لا يغرم السارق إذا أقيم عليه الحد ".
** ن
(بلوغ المرام ٢٦٢/١)
** قال الحافظ فى " البلوغ " ١ / ٢٦٢: رواه النسائى وبين أنه منقطع. وقال أبو حاتم: هو منكر.','9','363',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4289,'<span data-type="title" id=toc-4298>٤٢٨٩ </span>- عن عبد الله بن عمرو بن العاص عن رسول الله صلى الله عليه وسلم أنه سئل عن
الثمر المعلق فقال: " من أصاب بفيه من ذى حاجة غير متخذ خبنة فلا شىء عليه،
ومن خرج بشىء منه فعليه الغرامة والعقوبة، ومن خرج بشىء منه بعد أن يؤويه
الجرين فبلغ ثمن الجن فعليه القطع ".
** د ن
(بلوغ المرام ٢٦٢/١)
** قال الحافظ فى " البلوغ " ١ / ٢٦٣: صححه الحاكم.','9','364',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4290,'<span data-type="title" id=toc-4299>٤٢٩٠ </span>- عن صفوان بن امية أن النبى صلى الله عليه وسلم قال: لما أمر بقطع الذى سرق
ردائه فشفع فيه " هلا كان ذلك قبل أن تأتينى به؟ ".
** حم د ت ن جه
(بلوغ المرام ٢٦٣/١)
** قال الحافظ فى " البلوغ " ١ / ٢٦٣: صححه ابن الجارود والحاكم.','9','365',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4291,'<span data-type="title" id=toc-4300>٤٢٩١ </span>- عن جابر رضى الله عنه قال: جىء بسارق إلى النبى صلى الله عليه وسلم فقال:
" اقتلوه " فقالوا: إنما سرق يا رسول الله، قال: " اقطعوه " فقطع ثم جىء
به الثانية، فقال: " اقتلوه " فذكر مثله ثم جىء به الثالثة فذكر مثله ثم
جىء به الرابعة كذلك ثم جىء به الخامسة، فقال: " اقتلوه ".
** د ن
(بلوغ المرام ٢٦٣/١)
** قال الحافظ فى " البلوغ " ١ / ٢٦٣: استنكره النسائى وأخرج من حديث الحارث بن حاطب نحوه.
** تعقيب: قال الفقى ١ / ٢٦٣: فى إسناده مصعب بن ثابت بن عبد الله بن الزبير: ضعفه ابن معين وأحمد وقال أبو حاتم: لا يحتج به، ولم يوثقه أحد. ولذا قال ابن عبد البر حديث القتل للسارق منكر لا أصل له.','9','366',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4292,'<span data-type="title" id=toc-4301>٤٢٩٢ </span>- عن جابر أن رسول الله صلى الله عليه وسلم قال: " ما أسكر كثيره فقليله حرام ".
** حم د ت ن جه
(بلوغ المرام ٢٦٥/١)
** قال الحافظ فى " البلوغ " ١ / ٢٦٥: صححه ابن حبان.','9','367',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4293,'<span data-type="title" id=toc-4302>٤٢٩٣ </span>- عن أم سلمة عن النبى صلى الله عليه وسلم قال: " إن الله لم يجعل شفاءكم فيما
حرم عليكم ".
** هق
(بلوغ المرام ٢٦٥/١)
** قال الحافظ فى " البلوغ " ١ / ٢٦٥: صححه ابن حبان.','9','368',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4294,'<span data-type="title" id=toc-4303>٤٢٩٤ </span>- عن سعيد بن زيد رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم:
" من قتل دون ماله فهو شهيد ".
** د ت ن جه
(بلوغ المرام ٢٦٦/١)
** قال الحافظ فى " البلوغ " ١ / ٢٦٦: صححه الترمذى.','9','369',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4295,'<span data-type="title" id=toc-4304>٤٢٩٥ </span>- عن أنس أن النبى صلى الله عليه وسلم قال: " جاهدوا المشركين بأموالكم
وأنفسكم وألسنتكم ".
** حم ن
(بلوغ المرام ٢٦٦/١)
** قال الحافظ فى " البلوغ " ١ / ٢٦٦: صححه الحاكم.','9','370',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4296,'<span data-type="title" id=toc-4305>٤٢٩٦ </span>- عن جرير رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم:
" أنا برىء من كل مسلم يقيم بين المشركين ".
** د ت ن
(بلوغ المرام ٢٦٧/١)
** إسناده صحيح
** قال الحافظ فى " البلوغ " ١ / ٢٦٧: رجح البخارى إرساله.','9','371',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4297,'<span data-type="title" id=toc-4306>٤٢٩٧ </span>- عن عبد الله بن السعدى قال: قال رسول الله صلى الله عليه وسلم:
" لا تنقطع الهجرة ما قوتل العدو ".
** ن
(بلوغ المرام ٢٦٧/١)
** قال الحافظ فى " البلوغ " ١ / ٢٦٧: صححه ابن حبان.','9','372',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4298,'<span data-type="title" id=toc-4307>٤٢٩٨ </span>- عن معقل بن النعمان بن مقرن قال: شهدت رسول الله صلى الله عليه وسلم
إذا لم يقاتل أول النهار أخر القتال حتى تزول الشمس وتهب الرياح وينزل
النصر.
** حم د ت ن
(بلوغ المرام ٢٦٨/١)
** قال الحافظ فى " البلوغ " ١ / ٢٦٨: صححه الحاكم، وأصله فى البخارى.','9','373',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4299,'<span data-type="title" id=toc-4308>٤٢٩٩ </span>- عن سمرة قال: قال رسول الله صلى الله عليه وسلم: " اقتلوا شيوخ المشركين
واستبقوا شرخهم ".
** د
(بلوغ المرام ٢٦٩/١)
** قال الحافظ فى " البلوغ " ١ / ٢٦٩: صححه الترمذى.','9','374',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4300,'<span data-type="title" id=toc-4309>٤٣٠٠ </span>- عن أبى أيوب رضى الله عنه قال: إنما أنزلت هذه الآية فينا معشر الأنصار يعنى قوله تعالى " ولا تلقوا بأيديكم إلى التهلكة " قاله ردا على من أنكر على من حمل على صف الروم حتى دخل فيهم.
** د ت ن
(بلوغ المرام ٢٦٩/١)
** قال الحافظ فى " البلوغ " ١ / ٢٦٩: صححه الترمذى وابن حبان والحاكم.','9','375',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4301,'<span data-type="title" id=toc-4310>٤٣٠١ </span>- عن عبادة بن الصامت قال: قال رسول الله صلى الله عليه وسلم: " لا تغلوا فإن
الغلول نار وعار على أصحابه فى الدنيا والآخرة ".
** حم ن
(بلوغ المرام ٢٧٠/١)
** قال الحافظ فى " البلوغ " ١ / ٢٧٠: صححه ابن حبان.','9','376',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4302,'<span data-type="title" id=toc-4311>٤٣٠٢ </span>- عن مكحول أن النبى صلى الله عليه وسلم نصب المنجنيق على أهل الطائف.
** مد
(بلوغ المرام ٢٧٠/١)
** رجاله ثقات
** قال الحافظ فى " البلوغ " ١ / ٢٧٠: أخرجه أبو داود فى المراسيل ورجاله ثقات، ووصله العقيلى بإسناد ضعيف عن على رضى الله عنه.','9','377',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4303,'<span data-type="title" id=toc-4312>٤٣٠٣ </span>- عن سعيد بن جبير أن رسول الله صلى الله عليه وسلم قتل يوم بدر ثلاثة صبرا.
** مد
(بلوغ المرام ٢٧١/١)
** رجاله ثقات','9','378',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4304,'<span data-type="title" id=toc-4313>٤٣٠٤ </span>- عن عمران بن حصين رضى الله عنه أن رسول الله صلى الله عليه وسلم فدى رجلين
من المسلمين برجل مشرك.
** ت
(بلوغ المرام ٢٧١/١)
** قال الحافظ فى " البلوغ " ١ / ٢٧١: صححه الترمذى، وأصله عند مسلم.','9','379',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4305,'<span data-type="title" id=toc-4314>٤٣٠٥ </span>- عن صخر بن العيلة أن النبى صلى الله عليه وسلم قال: " إن القوم إذا أسلموا
أحرزوا دماءهم وأموالهم ".
** د
(بلوغ المرام ٢٧١/١)
** رجاله موثقون','9','380',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4306,'<span data-type="title" id=toc-4315>٤٣٠٦ </span>- عن معن بن يزيد قال: سمعت رسول الله صلى الله عليه وسلم يقول:
" لا نفل إلا بعد الخمس ".
** حم د
(بلوغ المرام ٢٧٢/١)
** قال الحافظ فى " البلوغ " ١ / ٢٧٢: صححه الطحاوى.','9','381',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4307,'<span data-type="title" id=toc-4316>٤٣٠٧ </span>- عن حبيب بن مسلمة رضى الله عنه قال: شهدت رسول الله صلى الله عليه وسلم
نفل الربع فى البدءة والثلث فى الرجعة.
** د
(بلوغ المرام ٢٧٢/١)
** قال الحافظ فى " البلوغ " ١ / ٢٧٢: صححه ابن الجارود وابن حبان والحاكم.','9','382',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4308,'<span data-type="title" id=toc-4317>٤٣٠٨ </span>- عن عبد الله بن أبى أوفى رضى الله عنه قال: أصبنا طعاما يوم خيبر فكان
الرجل يجىء فيؤخذ منه مقدار مايكفيه ثم ينصرف.
** د
(بلوغ المرام ٢٧٢/١)
** قال الحافظ فى " البلوغ " ١ / ٢٧٢: صححه ابن الجارود والحاكم.','9','383',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4309,'<span data-type="title" id=toc-4318>٤٣٠٩ </span>- عن رويفع بن ثابت رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم:
" من كان يؤمن بالله واليوم الآخر فلا يركب دابة من فىء المسلمين حتى إذا أعجفها ردها فيه ولا يلبس ثوبا من فىء المسلمين حتى إذا أخلقه رده فيه ".
** د دى
(بلوغ المرام ٢٧٣/١)
** رجاله لا بأس بهم','9','384',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4310,'<span data-type="title" id=toc-4319>٤٣١٠ </span>- عن أبى عبيدة بن الجراح قال: سمعت رسول الله صلى الله عليه وسلم يقول:
" يجير على المسلمين بعضهم ".
** شب حم
(بلوغ المرام ٢٧٣/١)
** إسناده ضعيف
** قال الحافظ فى " البلوغ " ١ / ٢٧٣: فى إسناده ضعف.','9','385',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4311,'<span data-type="title" id=toc-4320>٤٣١١ </span>- عن معاذ بن جبل رضى الله عنه قال: غزونا مع رسول الله صلى الله عليه وسلم
خيبر فأصبنا فيها غنما فقسم فينا رسول الله صلى الله عليه وسلم طائفة وجعل
بقيتها فى المغنم ".
** د
(بلوغ المرام ٢٧٤/١)
** رجاله لا بأس بهم','9','386',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4312,'<span data-type="title" id=toc-4321>٤٣١٢ </span>- عن أبى رافع قال: قال النبى صلى الله عليه وسلم: " إنى لا أخيس بالعهد
ولا أحبس الرسل ".
** د ن
(بلوغ المرام ٢٧٤/١)
** قال الحافظ فى " البلوغ ١ / ٢٧٤: صححه ابن حبان.','9','387',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4313,'<span data-type="title" id=toc-4322>٤٣١٣ </span>- عن معاذ بن جبل قال: " بعثنى النبى صلى الله عليه وسلم إلى اليمن فأمرنى أن آخذ من كل حالم دينارا أو عدله معافريا ".
** د ت ن
(بلوغ المرام ٢٧٤/١)
** قال الحافظ فى " البلوغ " ١ / ٢٧٥: صححه ابن حبان والحاكم.','9','388',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4314,'<span data-type="title" id=toc-4323>٤٣١٤ </span>- عن ابن عمر رضى الله عنهما أن النبى صلى الله عليه وسلم سابق بين الخيل
وفضل القرح فى الغاية ".
** حم د
(بلوغ المرام ٢٧٦/١)
** قال الحافظ فى " البلوغ " ١ / ٢٧٦: صححه ابن حبان.','9','389',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4315,'<span data-type="title" id=toc-4324>٤٣١٥ </span>- عن أبى هريرة قال: قال رسول الله صلى الله عليه وسلم: " لا سبق إلا فى خف أو نصل أو حافر ".
** حم د ت ن
(بلوغ المرام ٢٧٦/١)
** قال الحافظ فى " البلوغ ١ / ٢٧٦: صححه ابن حبان.','9','390',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4316,'<span data-type="title" id=toc-4325>٤٣١٦ </span>- عن أبى هريرة رضى الله عنه عن النبى صلى الله عليه وسلم قال: " من أدخل
فرسا بين فرسين _ وهو لا يأمن أن يسبق _ فلا بأس به فإن أمن فهو قمار ".
** حم د
(بلوغ المرام ٢٧٦/١)
** إسناده ضعيف','9','391',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4317,'<span data-type="title" id=toc-4326>٤٣١٧ </span>- عن ابن عباس رضى الله عنهما قال: نهى رسول الله صلى الله عليه وسلم عن قتل
أربع من الدواب: النملة والنحلة، والهدهد، والصرد.
** حم د
(بلوغ المرام ٢٧٧/١)
** قال الحافظ فى " البلوغ " ١ / ٢٧٧: صححه ابن حبان.','9','392',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4318,'<span data-type="title" id=toc-4327>٤٣١٨ </span>- عن ابن أبى عمار قلت لجابر: الضبع صيد هى؟ قال: نعم، قلت: قاله رسول الله صلى الله عليه وسلم؟ قال: نعم ".
** حم د ت ن جه
(بلوغ المرام ٢٧٧/١)
** قال الحافظ فى " البلوغ " ١ / ٢٧٧: صححه البخارى وابن حبان.
** تعقيب: قال الفقى ١ / ٢٧٧: ابن أبى عمار هو عبد الرحمن المكى، وثقه أبو زرعة والنسائى، ولم يتكلم فيه أحد.','9','393',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4319,'<span data-type="title" id=toc-4328>٤٣١٩ </span>- عن ابن عمر رضى الله عنهما أنه سئل عن القنفذ فقال: " قل لا أجد فيما أوحى
إلى محرما.. الآية "، فقال شيخ عنده: سمعت أبا هريرة يقول: ذكر عند النبى
صلى الله عليه وسلم فقال: " إنها خبيثة من الخبائث " فقال ابن عمر: إن كان
رسول الله صلى الله عليه وسلم قال هذا فهو كما قال.
** حم د
(بلوغ المرام ٢٧٧/١)
** إسناده ضعيف','9','394',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4320,'<span data-type="title" id=toc-4329>٤٣٢٠ </span>- عن ابن عمر رضى الله عنهما قال: نهى رسول الله صلى الله عليه وسلم عن
الجلالة وألبانها.
** د ت جه
(بلوغ المرام ٢٧٨/١)
** قال الحافظ فى " البلوغ " ١ / ٢٧٨: حسنه الترمذى.','9','395',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4321,'<span data-type="title" id=toc-4330>٤٣٢١ </span>- عن عبد الرحمن بن عثمان القرشى رضى الله عنه أن طبيبا سأل رسول الله صلى
الله عليه وسلم عن الضفدع يجعلها فى دواء فنهى عن قتلها.
** حم د ن
(بلوغ المرام ٢٧٨/١)
** قال الحافظ فى " البلوغ " ١ / ٢٧٨: صححه الحاكم.','9','396',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4322,'<span data-type="title" id=toc-4331>٤٣٢٢ </span>- عن أبى سعيد الخدرى رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم:
" ذكاة الجنين ذكاة أمه ".
** حم
(بلوغ المرام ٢٨٠/١)
** قال الحافظ فى " البلوغ " ١ / ٢٨٠: صححه ابن حبان.','9','397',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4323,'<span data-type="title" id=toc-4332>٤٣٢٣ </span>- عن ابن عباس رضى الله عنهما أن النبى صلى الله عليه وسلم قال:
" المسلم يكفيه اسمه، فإن نسى أن يسمى حين يذبح فليسم ثم يأكل ".
** قط
(بلوغ المرام ٢٨٠/١)
** قال الحافظ فى " البلوغ " ١ / ٢٨٠: أخرجه الدارقطنى وفيه راو فى حفظه ضعف، وفى إسناده محمد بن يزيد بن سنان
وهو صدوق ضعيف الحفظ، وأخرجه عبد الرازق بإسناد صحيح إلى ابن عباس موقوفا
عليه.','9','398',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4324,'<span data-type="title" id=toc-4333>٤٣٢٤ </span>- " ذبيحة المسلم حلال، ذكر اسم الله عليها أم لم يذكر ".
** مد
(بلوغ المرام ٢٨٠/١)
** رجاله موثقون','9','399',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4325,'<span data-type="title" id=toc-4334>٤٣٢٥ </span>- عن أبى هريرة رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم:
" من كان له سعة ولم يضح فلا يقربن مصلانا ".
** حم جه
(بلوغ المرام ٢٨١/١)
** قال الحافظ فى " البلوغ " ١ / ٢٨١: صححه الحاكم. ورجح الأئمة غيره وقفه.','9','400',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4326,'<span data-type="title" id=toc-4335>٤٣٢٦ </span>- عن البراء بن عازب رضى الله عنه قال: قام فينا رسول الله صلى الله عليه
وسلم فقال: " أربع لا تجوز فى الضحايا العوراء البين عورها، والمريضة
البين مرضها، والعرجاء البين ضلعها، والكبيرة التى لا تنقى ".
** حم د ت ن جه
(بلوغ المرام ٢٨١/١)
** قال الحافظ فى " البلوغ " ١ / ٢٨١: صححه الترمذى وابن حبان.','9','401',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4327,'<span data-type="title" id=toc-4336>٤٣٢٧ </span>- عن على رضى الله عنه قال: أمرنا رسول الله صلى الله عليه وسلم أن نستشرف
العين والأذن ولا نضحى بعوراء ولا مقابلة ولا مدابرة ولا خرقاء ولا
ثرماء.
** حم د ت ن جه
(بلوغ المرام ٢٨٢/١)
** قال الحافظ فى " البلوغ "١ / ٢٨٢: صححه الترمذى وابن حبان والحاكم.','9','402',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4328,'<span data-type="title" id=toc-4337>٤٣٢٨ </span>- عن ابن عباس رضى الله عنهما أن النبى صلى الله عليه وسلم عق عن الحسن
والحسين كبشا كبشا.
** د
(بلوغ المرام ٢٨٢/١)
** قال الحافظ فى " البلوغ " ١ / ٢٨٢: صححه ابن خزيمة وابن الجارود وعبد الحق. لكن رجح أبو حاتم إرساله. وأخرج ابن حبان من حديث أنس نحوه.
** تعقيب: قال الفقى ١ / ٢٨٢: وقد أخرجه البيهقى وابن حبان والحاكم عن عائشة بزيادة " يوم السابع وسماهما وأمر أن يماط عن رأسيهما الأذى ". وصححه ابن السكن من حديث جابر بأتم من هذا، ورواه أحمد والنسائى من حديث بريدة وسنده صحيح.','9','403',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4329,'<span data-type="title" id=toc-4338>٤٣٢٩ </span>- عن عائشة أن رسول الله صلى الله عليه وسلم أمرهم أن يعق عن الغلام شاتان
مكافئتان وعن الجارية شاه.
** ت
(بلوغ المرام ٢٨٢/١)
** قال الحافظ فى " البلوغ " ١ / ٢٨٣: صححه الترمذى. وأخرج أحمد والأربعة عن أم كرز الكعبية نحوه.','9','404',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4330,'<span data-type="title" id=toc-4339>٤٣٣٠ </span>- عن سمرة رضى الله عنه أن رسول الله صلى الله عليه وسلم قال: " كل غلام مرتهن
بعقيقته تذبح عنه يوم سابعه ويحلق ويسمى ".
** حم د ت ن جه
(بلوغ المرام ٢٨٣/١)
** قال الحافظ فى " البلوغ " ١ / ٢٨٣: صححه الترمذى.','9','405',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4331,'<span data-type="title" id=toc-4340>٤٣٣١ </span>- عن ابن عمر رضى الله عنهما أن رسول الله صلى الله عليه وسلم قال:
" من حلف على يمين فقال: إن شاء الله، فلا حنث عليه ".
** حم د ت ن جه
(بلوغ المرام ٢٨٤/١)
** قال الحافظ فى " البلوغ " ١ / ٢٨٤: صححه ابن حبان.','9','406',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4332,'<span data-type="title" id=toc-4341>٤٣٣٢ </span>- عن أسامة بن زيد رضى الله عنهما قال: قال رسول الله صلى الله عليه وسلم:
" من صنع إليه معروف فقال لفاعله: جزاك الله خيرا فقد أبلغ فى الثناء ".
** ت
(بلوغ المرام ٢٨٤/١)
** قال الحافظ فى " البلوغ " ١ / ٢٨٤: صححه ابن حبان.','9','407',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4333,'<span data-type="title" id=toc-4342>٤٣٣٣ </span>- عن ابن عباس رضى الله عنهما مرفوعا " من نذر نذرا لم يسم فكفارته كفارة يمين
ومن نذر نذرا فى معصية فكفارته كفارة يمين، ومن نذر نذرا لا يطيقه فكفارته
كفارة يمين ".
** د
(بلوغ المرام ٢٨٥/١)
** إسناده صحيح
** قال الحافظ فى " البلوغ " ١ / ٢٨٥: إسناده صحيح إلا أن الحفاظ رجحوا وقفه.','9','408',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4334,'<span data-type="title" id=toc-4343>٤٣٣٤ </span>- عن ثابت بن الضحاك قال: نذر رجل على عهد رسول الله صلى الله عليه وسلم أن
ينحر إبلا ببوانة فأتى رسول الله صلى الله عليه وسلم فسأله، فقال: " هل كان
فيها وثن يعبد؟ " قال: لا، قال: " فهل كان فيها عيد من أعيادهم؟ " فقال:
لا، فقال: " أوف بنذرك فإنه لا وفاء لنذر فى معصية الله ولا فى قطيعة رحم
ولا فيما لا يملك ابن آدم ".
** د طب
(بلوغ المرام ٢٨٥/١)
** إسناده صحيح
** قال الحافظ فى " البلوغ " ١ / ٢٨٧: صحيح الإسناد وله شاهد من حديث كردم عند أحمد.','9','409',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4335,'<span data-type="title" id=toc-4344>٤٣٣٥ </span>- عن جابر رضى الله تعالى عنه أن رجلا قال يوم الفتح: يا رسول الله إنى نذرت
إن فتح الله عليك مكة أن أصلى فى بيت المقدس، فقال: " صل هاهنا " فسأله،
فقال: " صل هاهنا " فسأله، فقال: " فشأنك إذا ".
** حم د
(بلوغ المرام ٢٨٧/١)
** قال الحافظ فى " البلوغ " ١ / ٢٨٧: صححه الحاكم.','9','410',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4336,'<span data-type="title" id=toc-4345>٤٣٣٦ </span>- عن بريدة رضى الله تعالى عنه قال: قال رسول الله صلى الله عليه وسلم:
" القضاة ثلاثة: إثنان فى النار وواحد فى الجنة، رجل عرف الحق فقضى به فهو فى الجنة، ورجل عرف الحق فلم يقض به وجار فى الحكم فهو فى النار، ورجل لم يعرف الحق فقضى للناس على جهل فهو فى النار ".
** د ت ن جه
(بلوغ المرام ٢٨٧/١)
** قال الحافظ فى " البلوغ " ١ / ٢٨٧: صححه الحاكم.','9','411',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4337,'<span data-type="title" id=toc-4346>٤٣٣٧ </span>- عن أبى هريرة رضى الله تعالى عنهما قال: قال رسول الله صلى الله عليه وسلم:
" من ولى القضاء فقد ذبح بغير سكين ".
** حم د ت ن جه
(بلوغ المرام ٢٨٧/١)
** قال الحافظ فى " البلوغ " ١ / ٢٨٨: صححه ابن خزيمة وابن حبان.','9','412',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4338,'<span data-type="title" id=toc-4347>٤٣٣٨ </span>- عن على رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " إذا تقاضى إليك رجلان فلا تقض للأول حتى تسمع كلام الآخر فسوف تدرى كيف
تقضى " قال على: فما زلت قاضيا بعد.
** حم د ت
(بلوغ المرام ٢٨٨/١)
** قال الحافظ فى " البلوغ " ١ / ٢٨٨: حسنه الترمذى، وقواه ابن المدينى، وصححه ابن حبان، وله شاهد عند الحاكم
من حديث ابن عباس.','9','413',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4339,'<span data-type="title" id=toc-4348>٤٣٣٩ </span>- عن أبى هريرة رضى الله عنه قال: لعن رسول الله صلى الله عليه وسلم الراشى والمرتشى فى الحكم.
** حم د ت ن جه
(بلوغ المرام ٢٨٩/١)
** قال الحافظ فى " البلوغ " ١ / ٢٨٩: حسنه الترمذى، وصححه ابن حبان، وله شاهد من حديث عبد الله بن عمرو عند الأربعة إلا النسائى.','9','414',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4340,'<span data-type="title" id=toc-4349>٤٣٤٠ </span>- عن عبد الله بن الزبير قضى رسول الله صلى الله عليه وسلم أن الخصمين يقعدان
بين يدى الحاكم.
** د
(بلوغ المرام ٢٨٩/١)
** قال الحافظ فى " البلوغ " ١ / ٢٨٩: صححه الحاكم.
** تعقيب: قال الفقى ١ / ٢٨٩: هو من رواية مصعب بن ثابت بن عبد الله بن الزبير وفيه كلام، قال أبو حاتم: كثير الغلط.','9','415',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4341,'<span data-type="title" id=toc-4350>٤٣٤١ </span>- عن ابن عباس رضى الله عنهما أن النبى صلى الله عليه وسلم قال: " ترى الشمس؟ " قال: نعم، قال: " على مثلها فاشهد أودع ".
** عد
(بلوغ المرام ٢٩٠/١)
** إسناده ضعيف
** قال الحافظ فى " البلوغ " ١ / ٢٩٠: صححه الحاكم فأخطأ.','9','416',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4342,'<span data-type="title" id=toc-4351>٤٣٤٢ </span>- " البينة على المدعى واليمين على من أنكر ".
** هق
(بلوغ المرام ٢٩١/١)
** إسناده صحيح','9','417',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4343,'<span data-type="title" id=toc-4352>٤٣٤٣ </span>- عن أبى موسى رضى الله تعالى عنه أن رجلين اختصما فى دابة وليس لواحد منهم بينة فقضى بهارسول الله صلى الله عليه وسلم بينهما نصفين.
** حم د ن
(بلوغ المرام ٢٩١/١)
** قال الحافظ فى " البلوغ " ١ / ٢٩٢: رواه النسائى وهذا لفظه وقال: إسناده جيد','9','418',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4344,'<span data-type="title" id=toc-4353>٤٣٤٤ </span>- عن جابر رضى الله عنه أن رسول الله صلى الله عليه وسلم قال: " من حلف على
منبرى هذا بيمين آثمة تبوأ مقعده من النار ".
** حم د ن
(بلوغ المرام ٢٩٢/١)
** قال الحافظ فى " البلوغ " ١ / ٢٩٢: صححه ابن حبان.','9','419',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4345,'<span data-type="title" id=toc-4354>٤٣٤٥ </span>- عن جابر رضى الله عنه أن رجلين اختصما فى ناقة فقال كل واحد منهما:
نتجت هذه الناقة عندى وأقاما بينة، فقضى رسول الله صلى الله عليه وسلم
لمن هى فى يده.
** قط
(بلوغ المرام ٢٩٢/١)
** إسناده ضعيف
** قال الحافظ فى " البلوغ " ١ / ٢٩٢: وفى إسنادهما ضعف.','9','420',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4346,'<span data-type="title" id=toc-4355>٤٣٤٦ </span>- عن ابن عمر رضى الله تعالى عنهما أن النبى صلى الله عليه وسلم رد اليمين
على طالب الحق.
** قط
(بلوغ المرام ٢٩٢/١)
** إسناده ضعيف
** قال الحافظ فى " البلوغ " ١ / ٢٩٢: فى إسنادهما ضعف.','9','421',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4347,'<span data-type="title" id=toc-4356>٤٣٤٧ </span>- عن عمرو بن شعيب عن أبيه عن جده عن النبى صلى الله عليه وسلم قال: " المكاتب عبد ما بقى عليه من مكاتبته درهم ".
** د
(بلوغ المرام ٢٩٤/١)
** إسناده حسن
** قال الحافظ فى " البلوغ " ١ / ٢٩٥: أخرجه أبو داود بإسناد حسن. وأصله عند أحمد والثلاثة، وصححه الحاكم.
** تعقيب: قال الفقى ١ / ٢٩٥: قال الترمذى: غريب.','9','422',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4348,'<span data-type="title" id=toc-4357>٤٣٤٨ </span>- عن أم سلمة رضى الله عنها قالت: قال رسول الله صلى الله عليه وسلم:
" إذا كان لإحداكن مكاتب وكان عنده مايؤدى فلتحتجب منه ".
** حم د ت ن جه
(بلوغ المرام ٢٩٥/١)
** قال الحافظ فى " البلوغ " ١ / ٢٩٥: صححه الترمذى.','9','423',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4349,'<span data-type="title" id=toc-4358>٤٣٤٩ </span>- عن ابن عباس قال: قال رسول الله صلى الله عليه وسلم:
" أيما أمة ولدت من سيدها فهى حرة بعد موته ".
** جه ك
(بلوغ المرام ٢٩٥/١)
** إسناده ضعيف
** قال الحافظ فى " البلوغ " ١ / ٢٩٦: رجح جماعة وقفه على عمر رضى الله عنه.','9','424',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4350,'<span data-type="title" id=toc-4359>٤٣٥٠ </span>- عن سهل بن حنيف رضى الله عنه أن رسول الله صلى الله عليه وسلم قال:
" من أعان مجاهدا فى سبيل الله أو غارما فى عسرته أو مكاتبا فى رقبته أظله الله يوم لا ظل إلا ظله ".
** حم
(بلوغ المرام ٢٩٦/١)
** قال الحافظ فى " البلوغ " ١ / ٢٩٦: صححه الحاكم.','9','425',NULL,NULL,'0');
