** حسن
** قال الحافظ فى " البلوغ " ١ / ٣١٢: أخرجه الترمذى وله شواهد منها: حديث ابن عباس عند أبى داود وغيره ومجموعها يقضى بأنه حديث حسن.','9','465',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4391,'<span data-type="title" id=toc-4400>٤٣٩١ </span>- عن ابن مسعود رضى الله تعالى عنه قال: قال رسول الله صلى الله عليه وسلم: " إن أولى الناس بى يوم القيامة أكثرهم على صلاة ".
** ت
(بلوغ المرام ٣١٢/١)
** قال الحافظ فى " البلوغ " ١ / ٣١٢: صححه ابن حبان.','9','466',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4392,'<span data-type="title" id=toc-4401>٤٣٩٢ </span>- عن ابن عمر رضى الله عنهما قال: لم يكن رسول الله صلى الله عليه وسلم يدع هؤلاء الكلمات حين يمسى وحين يصبح: " اللهم إنى أسألك العافية فى دينى ودنياى وأهلى ومالى، اللهم استر عوراتى وآمن روعاتى واحفظنى من بين يدى ومن خلفى وعن يمينى وعن شمالى ومن فوقى وأعوذ بك أن أغتال من تحتى ".
** ن جه
(بلوغ المرام ٣١٢/١)
** قال الحافظ فى " البلوغ " ١ / ٣١٢: صححه الحاكم.','9','467',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4393,'<span data-type="title" id=toc-4402>٤٣٩٣ </span>- عن عبد الله بن عمر رضى الله عنهما قال: كان رسول الله صلى الله عليه وسلم يقول: " اللهم إنى أعوذ بك من غلبة الدين وغلبة العدو وشماتة الأعداء ".
** ن
(بلوغ المرام ٣١٣/١)
** قال الحافظ فى " البلوغ " ١ / ٣١٣: صححه الحاكم.','9','468',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4394,'<span data-type="title" id=toc-4403>٤٣٩٤ </span>- عن بريدة رضى الله عنه قال: سمع النبى صلى الله عليه وسلم رجلا يقول: اللهم إنى أسألك بأنى أشهد أنك أنت الله لا إله إلا أنت الأحد الصمد الذى لم يلد ولم يولد ولم يكن له كفوا أحد، فقال رسول الله صلى الله عليه وسلم: " لقد سأل الله باسمه الذى إذا سئل به أعطى وإذا دعى به أجاب ".
** د ت ن جه
(بلوغ المرام ٣١٣/١)
** قال الحافظ فى " البلوغ " ١ / ٣١٣: صححه ابن حبان.','9','469',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4395,'<span data-type="title" id=toc-4404>٤٣٩٥ </span>- من حديث أبى هريرة رضى الله عنه (" اللهم انفعتى بما علمتنى وعلمنى ما ينفعنى ") وقال فى آخره: " وزدنى علما، الحمد لله على كل حال، وأعوذ بالله من أهل النار ".
** ت
(بلوغ المرام ٣١٤/١)
** إسناده حسن','9','470',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4396,'<span data-type="title" id=toc-4405>٤٣٩٦ </span>- عن عائشة أن النبى صلى الله عليه وسلم علمها هذا الدعاء: " اللهم إنى أسألك من الخير كله عاجله وآجله ما علمت منه وما لم أعلم وأعوذ بك من الشر كله عاجله وآجله ما علمت منه وما لم أعلم. اللهم إنى أسألك من خير ما سألك عبدك ونبيك وأعوذ بك من شر ما عاذ منه عبدك ونبيك اللهم إنى أسألك الجنة وما قرب إليها من قول أو عمل وأسألك أن تجعل كل قضاء قضيته لى خيرا ".
** جه
(بلوغ المرام ٣١٤/١)
** قال الحافظ فى " البلوغ " ١ / ٣١٤: صححه ابن حبان والحاكم.','9','471',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4397,'<span data-type="title" id=toc-4406>٤٣٩٧ </span>- عن رفاعة بن رافع رضى الله عنه ان النبى صلى الله عليه وسلم سئل: أى الكسب أطيب؟ قال: " عمل الرجل بيده، وكل بيع مبرور ".
** بز
(بلوغ المرام ١٥٨/١)
** قال الحافظ فى " البلوغ " ١ / ١٥٨: صححه الحاكم.','9','472',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4398,'<span data-type="title" id=toc-4407>٤٣٩٨ </span>- عن رجل من الأنصار أنه جاء بأمة سوداء فقال: يا رسول الله إن على عتق رقبة مؤمنة فإن كنت ترى هذه مؤمنة أعتقها فقال: " أتشهدين أن لا إله إلا الله؟ " قالت: نعم، قال: " أتشهدين أن محمدا رسول الله؟ " قالت: نعم، قال: " أتؤمنين بالبعث؟ " قالت: نعم، قال: " اعتقها ".
** تخز عب
(العلو ١٧/١)
** صحيح','9','473',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4399,'<span data-type="title" id=toc-4408>٤٣٩٩ </span>- عن أبى هريرة رضى الله عنه قال: جاء رجل إلى رسول الله صلى الله عليه وسلم بجارية أعجمية فقال: يا رسول الله إن على عتق رقبة مؤمنة أفأعتق هذه؟ فقال لها: " أين الله " فأشارت إلى السماء، قال: " فمن أنا؟ " فأشارت إلى رسول الله ثم إلى السماء، قال: " اعتقها فإنها مؤمنة ".
** حم مسند أبى هريرة للقاضى البريى
(العلو ١٧/١)
** إسناده حسن','9','474',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4400,'<span data-type="title" id=toc-4409>٤٤٠٠ </span>- عن ابن عباس قال: جاء رجل إلى النبى صلى الله عليه وسلم ومعه جارية سوداء أعجمية فقال: على رقبة فهل تجزىء هذه عنى؟ ، فقال: " أين الله؟ " فأشارت بيدها إلى السماء، فقال: " من أنا " قالت: أنت رسول الله، قال: " اعتقها فإنها مؤمنة ".
** لم
(العلو ١٨/١)
** قال الحافظ الذهبى فى " العلو " ١ / ١٨: هذا محفوظ عن أبى معاوية لكن شيخه قد ضعف.','9','475',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4401,'<span data-type="title" id=toc-4410>٤٤٠١ </span>- عن محمد بن الشريد أن أمه أوصته أن يعتق عنها رقبة مؤمنة، فقال: يا رسول الله إن أمى أوصت بكذا وهذه جارية سوداء نوبية أتجزىء عنى؟ قال: " ائتنى بها " فقال لها: " أين الله " قالت: فى السماء، قال: من أنا؟ قالت: أنت رسول الله، قال: " فأعتقها فإنها مؤمنة ".
** لم
(العلو ١٨/١)
** إسناده ليس بالقائم
** قال الحافظ الذهبى فى " العلو " ١ / ١٨: ويروى نحوه عن محمد بن الشريد بن سويد الثقفى عن أبى هريرة مرفوعا وقيل صوابه عمر بن الرشيد. فالله أعلم.','9','476',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4402,'<span data-type="title" id=toc-4411>٤٤٠٢ </span>- عن أبى رزين العقيلى قال: قلت يا رسول الله أين كان ربنا قبل أن يخلق السموات والأرض؟ ، قال: " كان فى عماء ما فوقه هواء وما تحته هواء ثم خلق العرش ثم استوى عليه ".
** ت جه سحق
(العلو ١٩/١)
** إسناده حسن','9','477',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4403,'<span data-type="title" id=toc-4412>٤٤٠٣ </span>- عن عبد الله بن عمرو بن العاص أن رسول الله صلى الله عليه وسلم قال: " الراحمون يرحمهم الرحمن، ارحموا من فى الأرض يرحمكم من فى السماء ".
** د ت
(العلو ٢٠/١)
** قال الحافظ الذهبى فى " العلو " ١ / ٢٠: صححه الترمذى، تفرد به سفيان.','9','478',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4404,'<span data-type="title" id=toc-4413>٤٤٠٤ </span>- عن جرير: سمع النبى صلى الله عليه وسلم يقول: " من لم يرحم من فى الأرض لم يرحمه من فى السماء ".
** لم
(العلو ٢٠/١)
** رواته ثقات','9','479',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4405,'<span data-type="title" id=toc-4414>٤٤٠٥ </span>- عن عبد الله بن مسعود: ارحم من فى الأرض يرحمك من فى السماء.
** لم
(العلو ٢٠/١)
** مرسل
** قال الحافظ الذهبى فى " العلو " ١ / ٢٠: رواه عمار بن رزيق عن أبى إسحاق مرفوعا والواقف أصح مع أن رواية أبى عبيدة عن والده فيها إرسال.','9','480',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4406,'<span data-type="title" id=toc-4415>٤٤٠٦ </span>- عن أبى هريرة رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " لما ألقى إبراهيم عليه السلام فى النار قال: اللهم إنك واحد فى السماء وأنا فى الأرض واحد أعبدك ".
** لم
(العلو ٢١/١)
** إسناده حسن
** قال الحافظ الذهبى فى " العلو " ١ / ٢١: هذا حديث حسن الإسناد رواه جماعة عن إسحاق.','9','481',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4407,'<span data-type="title" id=toc-4416>٤٤٠٧ </span>- عن عبادة بن الصامت أن رسول الله صلى الله عليه وسلم كان يقول: " من توضأ فأحسن الوضوء ثم قام إلى الصلاة فأتم ركوعها وسجودها والقراءة فيها قالت: حفظك الله كما حفظتنى ثم صعد بها إلى السماء ولها نور وضوء وفتحت لها أبواب السماء حتى ينتهى بها إلى الله عز وجل فتشفع لصاحبها ".
** لم
(العلو ٢١/١)
** قال الحافظ الذهبى فى " العلو " ١ / ٢١: الحديث سمعه محمد بن أسلم الطوسى وغيره منه ورواه مروان ابن معاوية عن الأحوص أحد الضعفاء وهاه ابن معين.','9','482',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4408,'<span data-type="title" id=toc-4417>٤٤٠٨ </span>- عن أبى هريرة عن النبى صلى الله عليه وسلم قال: " إن الميت يحضره الملائكة فإذا كان الرجل الصالح قالوا: أخرجى أيتهاالنفس الطيبة كانت فى الجسد الطيب
أبشرى بروح وريحان ورب غير غضبان فلا يزال يقال لها ذلك حتى تخرج ثم يعرج
بها إلى السماء فيستفتح لها فيقال من هذا؟ فيقال: فلان فيقال مرحبا بالنفس
الطيبة فلا يزال يقال لها ذلك حتى ينتهى بها إلى السماء التى فيها الله تعالى
.... ". وذكر الحديث.
** حم ك
(العلو ٢٢/١)
** قال الحافظ الذهبى فى " العلو " ١ / ٢٢: رواه الحاكم فى المستدرك وقال هو على شرط البخارى ومسلم. ورواه أئمة عن ابن أبى ذئب.','9','483',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4409,'<span data-type="title" id=toc-4418>٤٤٠٩ </span>- عن ابن عمر قال: كنا جلوسا ذات يوم بفناء رسول الله صلى الله عليه وسلم إذ مرت امرأة من بناته، فقال أبو سفيان: ما مثل محمد فى بنى هاشم إلا
كمثل الريحانة فى وسط الزبل، فسمعت فأبلغته رسول الله صلى الله عليه وسلم
فخرج فصعد على منبره وقال: " ما بال أقوال تبلغنى عن أقوام؟ إن الله خلق
سماوات سبعا فاختار العليا فسكنها وأسكن سماواته من شاء من خلقه ثم اختار
خلقه فاختار بنى آدم، فاختار العرب، فاختار مضر، فاختار قريشا، فاختار بنى هاشم، فاختارنى فلم أزل خيارا من خيار، فمن أحب قريشا فبحبى أحبهم ومن أبغض العرب فببغضى أبغضهم ".
** تخز
(العلو ٢٢/١)
** حديث منكر
** قال الحافظ الذهبى فى " العلو " ١ / ٢٣: تابعه حماد بن واقد وغيره عن محمد بن ذكوان أحد الضعفاء، وبعضهم يقول فيه عبد الله بن دينار بدل عمرو بن دينار.','9','484',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4410,'<span data-type="title" id=toc-4419>٤٤١٠ </span>- عن جابر قال: قال رسول الله صلى الله عليه وسلم: " بينا أهل الجنة فى نعيمهم إذ سطع لهم نور فرفعوا رؤسهم فإذا الرب جل جلاله قد أشرف عليهم من فوقهم فقال: " السلام عليكم ياأهل الجنة " فذلك قوله عز وجل (سلام قولا من رب رحيم) ".
** جه
(العلو ٢٣/١)
** إسناده ضعيف','9','485',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4411,'<span data-type="title" id=toc-4420>٤٤١١ </span>- عن عمران بن خالد بن طليق قال حدثنى أبى عن أبيه عن جده قال: اختلفت قريش إلى حصين والد عمران، فقالوا: إن هذا الرجل يذكر آلهتنا فنحب أن تكلمه وتعظه، فمشوا معه إلى قريب من باب النبى صلى الله عليه وسلم
فجلسوا ودخل حصين فلما رأه رسول الله صلى الله عليه وسلم قال: " أوسعوا
للشيخ " فقال: ما هذا الذى يبلغنا عنك؟ إنك تشتم آلهتنا وتذكرهم؟ وقد كان أبوك جفنة وخبزا، فقال: " إن أبى وأباك فى النار يا حصين، كم تعبد إلها اليوم؟ " قال: سبعة فى الأرض وإلها فى السماء، قال: " فإذا أصابك الضيق فمن تدعوا؟ " قال: الذى فى السماء، قال: " فإذا هلك المال فمن تدعوا؟ " قال: الذى فى السماء ".
** تخز ابن قدامة فى العلو
(العلو ٢٣/١)
** قال الحافظ الذهبى فى " العلو " ١ / ٢٤: عمران ضعيف.','9','486',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4412,'<span data-type="title" id=toc-4421>٤٤١٢ </span>- عن عمران بن حصين قال: قال النبى صلى الله عليه وسلم لأبى: " كم تعبد اليوم إلها؟ " فقال: ستة فى الأرض وواحد فى السماء، قال: " فأيهم لرغبتك ورهبتك؟ " قال: الذى فى السماء، قال: " يا حصين أماإنك لو أسلمت علمتك كلمتين ينفعانك " فلما أسلم قال: يا رسول الله علمنى الكلمتين
قال: " قل اللهم ألهمنى رشدى وأعذنى من شر نفسى ".
** لم
(العلو ٢٤/١)
** قال الحافظ الذهبى فى " العلو " ١ / ٢٤: شبيب ضعيف.','9','487',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4413,'<span data-type="title" id=toc-4422>٤٤١٣ </span>- عن الحسن عن أنس قال: قال رسول الله صلى الله عليه وسلم: " ما من حافظين يرفعان إلى الله ما حفظا يرى فى أول الصحيفة خيرا وفى آخرها خيرا إلا قال: الله عز وجل لملائكته أشهدكم أنى قد غفرت لعبدى مابين طرفى الصحيفة ".
** لم
(العلو ٢٥/١)
** قال الحافظ الذهبى فى " العلو " ١ / ٢٥: تفرد به تمام أحد الضعفاء.','9','488',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4414,'<span data-type="title" id=toc-4423>٤٤١٤ </span>- عن أبى هريرة رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " إن الله تعالى إذا جمع الأولين والآخرين يوم القيامة جاء الرب تبارك وتعالى إلى المؤمنين فوقف عليهم على كور _ فقالوا لعقبة: ما الكور؟ قال: المكان المرتفع _ فيقول: هل تعرفون ربكم؟ قالوا: إن عرفنا نفسه
عرفناه، فيتجلى لهم ضاحكا فى وجوههم فيخرون له سجدا ".
** تخز
(العلو ٢٥/١)
** حسن
** قال الحافظ الذهبى فى " العلو " ١ / ٢٥: أخرجه ابن خزيمة فى كتاب التوحيد عن الفلاس عن الحنفى وعنده " على كوم ".','9','489',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4415,'<span data-type="title" id=toc-4424>٤٤١٥ </span>- عن عدى بن عميرة قال: كان بأرضنا حبر من اليهود يقال له ابن شهلة فالتقيت أنا وهو، فقال: إنى أجد فى كتاب الله إن أصحاب الفردوس قوم يعبدون ربهم على وجوههم، لا والله ما أعلم هذه الصفة إلا فينا معشر يهود وأجد نبيا يخرج من اليمن لا نراه يخرج إلا منا، قال عدى: فوالله ما لبثت حتى بلغنا أن رجلا من بنى هاشم قد تنبأ فذكرت حديث ابن شهلة فخرجت إليه فإذا هو ومن تبعه يسجدون على وجوههم ويزعمون أن إلههم فى السماء.
** لم
(العلو ٢٥/١)
** غريب','9','490',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4416,'<span data-type="title" id=toc-4425>٤٤١٦ </span>- عن عائشة قالت: ما رفع رسول الله صلى الله عليه وسلم رأسه إلى السماء إلا قال: " يا مصرف القلوب ثبت قلبى على طاعتك ".
** لم
(العلو ٢٦/١)
** قال الحافظ الذهبى فى " العلو " ١ / ٢٦: صالح ضعيف.','9','491',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4417,'<span data-type="title" id=toc-4426>٤٤١٧ </span>- عن معاوية بن الحكم السلمى قال: كانت لى غنم ترعى بالعذيب بالعريض أتعهدها وفيها جارية لى سوداء فجئتها يوما ففقدت شاة من خيار الغنم فقلت: أين الفلانية؟ ، قالت: أكلها الذئب، فأسفت وأنا من بنى آدم فضربت وجهها ثم ندمت على ما صنعت فذكرت ذلك لرسول الله صلى الله عليه وسلم فقال: " أضربت وجهها " وعظم ذلك تعظيما شديدا، فقلت: يا رسول الله إن من توبتى أن أعتقها، قال: " فائتنى بها قبل أن تعتقها "، فجئته بها، فقال لها: " من ربك؟ " قالت: الله، قال: " وأين هو؟ " قالت: فى السماء، قال: " فمن أنا؟ " قالت: أنت رسول الله، قال: " أعتقها فإنها مؤمنة ".
** لم
(العلو ٢٦/١)
** صحيح','9','492',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4418,'<span data-type="title" id=toc-4427>٤٤١٨ </span>- عن أبى حجاج الثمالى قال: قال رسول الله صلى الله عليه وسلم: " يقول القبر للميت حين يوضع فيه: ويحك ياابن آدم، ما غرك بى إذ كنت تمر بى ألم تعلم أنى بيت الظلمة والفتنة والوحدة والدود، فإن كان مصلى أجاب عنه مجيب القبر فيقول: أرأيت إن كان يأمر بالمعروف وينهى عن المنكر؟ فيقول: إذا أعود عليه خضرا، ويعود جسمه نورا، ويصعد بروحه إلى رب العالمين ".
** لم
(العلو ٢٧/١)
** غريب
** قال الحافظ الذهبى فى " العلو " ١ / ٢٧: ابن أبى مريم ضعيف من قبل حفظه.','9','493',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4419,'<span data-type="title" id=toc-4428>٤٤١٩ </span>- عن أبى الدرداء سمعت رسول الله صلى الله عليه وسلم يقول: " من اشتكى منكم أو اشتكى أخ له فليقل: " ربنا الله الذى فى السماء تقدس اسمك أمرك فى السماء والأرض كما رحمتك فى السماء اغفر لنا حوبنا وخطايانا أنت رب الطيبين، أنزل رحمة من رحمتك وشفاء من شفائك على هذا الوجع فيبرأ ".
** د
(العلو ٢٧/١)
** قال الحافظ الذهبى فى " العلو " ١ / ٢٧: زيادة لين الحديث.','9','494',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4420,'<span data-type="title" id=toc-4429>٤٤٢٠ </span>- عن ابن عمر قال: قال رسول الله صلى الله عليه وسلم: " اتقوا دعوة المظلوم فإنها تصعد إلى الله كأنها شرارة ".
** لم
(العلو ٢٧/١)
** غريب وإسناده جيد','9','495',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4421,'<span data-type="title" id=toc-4430>٤٤٢١ </span>- عن أبى هريرة قال: لما خطب على فاطمة من رسول الله صلى الله عليه وسلم فقال لها: " أى بنية إن ابن عمك عليا قد خطبك فما تقولين؟ " فبكت، ثم قالت: كأنك إنماادخرتنى لفقير قريش، فقال: " والذى بعثنى بالحق ما تكلمت فى هذا حتى أذن الله فيه من السماء " فقالت فاطمة: رضيت بما رضى الله لى ".
** لم
(العلو ٢٧/١)
** منكر
** قال الحافظ الذهبى فى " العلو " ١ / ٢٨: هذا حديث منكر لعل محمد بن كثير افتراه فإنه متهم فإن الأوزاعى ما نطق به قط ولم أرو هذا ونحوه إلا للتزييف والكشف والفراء ليس بثقة.','9','496',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4422,'<span data-type="title" id=toc-4431>٤٤٢٢ </span>- عن أنس قال: قال رسول الله صلى الله عليه وسلم: " أتانى جبرائيل عليه السلام وفى يده مرآة بيضاء فيها نكتة سوداء، فقلت: ما هذا ياجبرائيل؟ قال: هذه الجمعة يعرضها عليك ربك عز وجل لتكون لك عيدا ولقومك من بعدك تكون أنت الأول وتكون اليهود والنصارى من بعدك، قلت: ما لنا فيها؟ قال: لكم فيها خير، فيها ساعة من دعا الله فيها بخير هو قسم له أعطاه إياه، أو ليس بقسم إلا ذخر له ماهو أعظم منه، قلت: ما هذه النكتة السوداء فيها؟ ، قال: هى الساعة تقوم يوم الجمعة وهو سيد الأيام عندنا ونحن ندعوه يوم المزيد فى الآخرة، قلت: وما يوم المزيد؟ ، قال: إن ربك اتخذ فى الجنة واديا أفيح من مسك أبيض، فإذا كان يوم الجمعة نزل تبارك وتعالى من عليين على كرسيه ثم حف الكرسى بمنابر من نور، ثم جاء النبيون حتى يجلسوا عليها ثم حف المنابر بكراسى من ذهب، ثم جاء الصديقون والشهداء حتى يجلسوا عليها، ثم جاء أهل الجنة حتى يجلسوا على الكثيب فيتجلى لهم ربهم عز وجل حتى ينظروا إلى وجهه، ثم يقول أنا الذى صدقتكم وعدى وأتممت عليكم نعمتى، وهذا محل كرامتى، فيسألونه ويسألونه حتى تنتهى رغبتهم، فيفتح لهم عند ذلك مالا عين رأت، ولا أذن سمعت، ولا خطر على قلب بشر، إلى أوان منصرف الناس من يوم الجمعة، ثم يصعد على كرسيه ويصعد معه الصديقون والشهداء، ويرجع أهل الغرف إلى غرفهم درة بيضاء لا فصم فيها ولا نظم أو ياقوتة حمراء أو زبرجدة خضراء، فيها غرفها وأبوابها بها مطردة فيها أنهار متذللة فيها أثمارها فيها أزواجها وخدمها فليسوا إلى شىء أحوج منهم إلى يوم الجمعة ليزدادوا من كرامته عز وجل وليزدادوا نظرا إلى وجهه فلذلك دعى يوم المزيد ".
** عبد الله بن أحمد فى كتاب السنة
(العلو ٢٨/١)
** مشهور
** قال الحافظ الذهبى فى " العلو " ١ / ٢٩: هذا حديث مشهور وافر الطرق.','9','497',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4423,'<span data-type="title" id=toc-4432>٤٤٢٣ </span>- عن أنس بن مالك عن النبى صلى الله عليه وسلم قال: " أتانى جبرائيل عليه السلام وفى يده كالمرآة البيضاء، فقلت: يا جبرائيل ماهذه؟ ، قال: هذه الجمعة بعثنى الله بها إليك وهو عندنا يوم المزيد، إن ربك اتخذ فى الجنة واديا أفيح من مسك أبيض فإذا كان يوم الجمعة نزل على كرسيه ونزل معه النبيون والصديقون ثم حفت بالكراسى منابر من ذهب مكللة بالزبرجد واللؤلؤ والياقوت فيجلس عليها النبيون والصديقون، ونزل أهل الغرف على الكثب من المسك الأبيض فيتجلى لهم ربهم فينظرون إلى وجهه ثم ارتفع على كرسيه وارتفع أهل الغرف إلى غرفهم ".
** لم
(العلو ٢٩/١)
** قال الحافظ الذهبى فى " العلو " ١ / ٢٩: صالح ضعيف تفرد به عنه القاضى أبو يوسف.','9','498',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4424,'<span data-type="title" id=toc-4433>٤٤٢٤ </span>- قال أنس بن مالك: أتى جبريل بمرآة بيضاء فيها نكتة سوداء إلى النبى صلى الله عليه وسلم، فقال: " ما هذه؟ " قال: هذه الجمعة فضلت بها أنت وأمتك والناس لكم فيها تبع: اليهود والنصارى، لكم فيها خير وفيها ساعة لا يوافها عبد مؤمن يدعو بخير إلا استجيب له وهو عندنا يو م المزيد، فقال النبى صلى الله عليه وسلم: " وما يوم المزيد " قال: ربك اتخذ فى الجنة واديا فيه كثب من مسك فإذا كان يوم القيامة أنزل الله من شاء من الملائكة وحوله الشهداء والصديقون فيجلسون من ورائهم على تلك الكثب، فيقول الله تعالى: أنا ربكم قد صدقتكم وعدى فسلونى أعطكم، فيقولون: ربنا نسألك الرضا، فيقول: رضيت عنكم ولكم ما شئتم ولدى مزيد، فهم يحبون يوم الجمعة لما يعطيهم ربهم من الخير وهو اليوم الذى استوى فيه ربك على العرش وفيه خلق آدم وفيه تقوم الساعة ".
** شفع قط القاضى أبو أحمد العسال فى كتاب المعرفة
(العلو ٣٠/١)
** قال الحافظ الذهبى فى " العلو " ١ / ٣٠: إبراهيم وموسى ضعفاء. وقال الحافظ الذهبى فى " العلو " ١ / ٣١: له طرق يعضد بعضها بعضا.
** تعقيب: * ساق الحافظ الذهبى هذه الطرق التى يعضد بعضها بعضا.','9','499',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4425,'<span data-type="title" id=toc-4434>٤٤٢٥ </span>- قال أنس بن مالك رضى الله عنه: أتى رسول الله صلى الله عليه وسلم رسول الله جبرائيل وفى يده كالمرأة _ الحديث بطوله وفيه _: " فإذا كان يوم الجمعة من أيام الآخرة يهبط الرب عز وجل عن عرشه إلى كرسيه وحف الكرسى بمنابر من نور فجلس النبيون ".
** لم
(العلو ٣١/١)
** غريب
** قال الحافظ الذهبى فى " العلو " ١ / ٣١: غريب تفرد به الوليد.','9','500',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4426,'<span data-type="title" id=toc-4435>٤٤٢٦ </span>- عن أبى سعيد الخدرى قال: قال رسول الله صلى الله عليه وسلم: " يقبل الله صدقة العبد من كسب طيب ولا يقبل الله إلا طيبا، ولا يصعد إليه إلا الطيب، فيأخذ التمرة فيربيها حتى يجعلها مثل الجبل ".
** لم
(العلو ٣١/١)
** صحيح','10','1',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4427,'<span data-type="title" id=toc-4436>٤٤٢٧ </span>- عن محمد بن مالك أن سعد بن معاذ لما حكم فى بنى قريظة قال له رسول الله صلى الله عليه وسلم: " لقد حكمت فيهم حكما حكم الله به من فوق سبعة أرقعة ".
** لم
(العلو ٣٢/١)
** مرسل','10','2',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4428,'<span data-type="title" id=toc-4437>٤٤٢٨ </span>- عن ثابت عن أنس وفيه: " فآتى باب الجنة فيفتح لى فآتى ربى تبارك وتعالى
وهو على كرسيه أو سريره فأخر ساجدا ". وذكر الحديث
** أبو أحمد العسال فى كتاب المعرفة
(العلو ٣٢/١)
** إسناده قوى','10','3',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4429,'<span data-type="title" id=toc-4438>٤٤٢٩ </span>- عن أنس عن النبى صلى الله عليه وسلم قال: " فأدخل على ربى وهو على عرشه
تبارك وتعالى ".
** ابن قدامة فى العلو
(العلو ٣٢/١)
** قال الحافظ الذهبى فى " العلو " ١ / ٣٢: زائدة ضعيفة والمتن بنحوه فى الصحيح للبخارى من حديث قتادة عن أنس عن النبى صلى الله عليه وسلم قال " فأستأذن على ربى فى داره فيؤذن لى عليه ".','10','4',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4430,'<span data-type="title" id=toc-4439>٤٤٣٠ </span>- عن سعد بن أبى وقاص: أن النبى صلى الله عليه وسلم قال لسعد بن معاذ: " لقد حكمت فيهم بحكم الملك من فوق سبع سماوات ".
** ن
(العلو ٣٢/١)
** صحيح
** قال الحافظ الذهبى فى " العلو " ١ / ٣٢: أخرجه النسائى من طريق أبى عامر عبد الملك بن عمر العقدى عن محمد بن صالح التمار وهو صدوق.','10','5',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4431,'<span data-type="title" id=toc-4440>٤٤٣١ </span>- عن ابن عباس قال: قال رسول الله صلى الله عليه وسلم: " ما من عبد يقول لا إله إلا الله وحده لا شريك له، له الملك وله الحمد، يحيى ويميت وهو على كل شىء قدير، إلا خرقت السماوات حتى تفضى إلى الله عز وجل ".
** لم
(العلو ٣٢/١)
** إسناده ليس بالقوى
** قال الحافظ الذهبى فى " العلو " ١ / ٣٢: ليس إسناده بالقوى من قبل إسماعيل بن قيس بن سعد بن زيد بن ثابت فإنه ضعيف.','10','6',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4432,'<span data-type="title" id=toc-4441>٤٤٣٢ </span>- قال أبو جرى جابر بن سليم: ركبت قعودا لى وأتيت مكة فى طلبه فأنخت بباب مسجد فإذا هو جالس صلى الله عليه وسلم وهو محتب ببردة لها طرائق حمر، فقلت: السلام عليك يا رسول الله، قال: " وعليك " قلت: إنا معشر أهل البادية قوم بنا الجفاء فعلمنى كلمات ينفعنى الله بهن، قال: " أدن "
ثلاثا، فقال: " أعد على " فقلت: إنا معشر أهل البادية قوم بنا الجفاء
فعلمنى كلمات ينفعنى الله بهن، قال: " اتق الله، ولا تحقرن من المعروف شيئا، ولو أن تصب من دلوك فى إناء المستقى، وإذا لقيت أخالك فالقه بوجه
منبسط، وإياك وإسبال الإزار فإنه من المخيلة وإن الله لا يحب المخيلة، وإن امرؤ سبك بما يعلم فيك فلا تسبه بما تعلم فيه فإن الله يجعل لك أجرا،
ويجعل عليه وزرا، ولا تسبن شيئا مما خولك الله " قال أبو جرى: فوالذى
ذهب بنفس محمد صلى الله عليه وسلم ما سببت لى شاة ولا بعيرا، فقال رجل: يا رسول الله ذكرت إسبال الإزار وقد يكون بالرجل القرح أو الشىء يستحى منه
قال: " لا بأس إلى نصف الساق أو إلى الكعبين إن رجلا ممن كان قبلكم لبس
بردين فتبختر فيهما فنظر الله إليه من فوق عرشه فمقته فأمر الأرض فأخذته،
فهو يتجلجل فى الأرض فاحذروا وقائع الله عز وجل ".
** د ت
(العلو ٣٦/١)
** إسناده لين
** قال الحافظ الذهبى فى " العلو " ١ / ٣٦: عبد السلام هو ابن عجلان وللحديث طرق وخرجه أبو داود وبعضه الترمذى.','10','7',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4433,'<span data-type="title" id=toc-4442>٤٤٣٣ </span>- عن أبى هريرة قال: قال رسول الله صلى الله عليه وسلم: " ما قال عبد لا إله إلا الله مخلص إلا صعدت لا يردها حجاب فإذا وصلت إلى الله نظر إلى قائلها وحق على الله أن لا ينظر إلى موحد إلارحمه ".
** ت ابن قدامة فى العلو
(العلو ٣٦/١)
** غريب
** قال الحافظ الذهبى فى " العلو " ١ / ٣٦: رواه الترمذى بنحوه من طريق الوليد بن القاسم وحسنه.','10','8',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4434,'<span data-type="title" id=toc-4443>٤٤٣٤ </span>- عن أبى هريرة أن رسول الله صلى الله عليه وسلم قال: " إذا أحب الله عبدا نادى جبرائيل فقال: إنى أحب عبدى فأحبوه، فينوه به جبرائيل حملة العرش
فتسمع أهل السماء لغط حملة العرش فيحبه أهل السماء السابعة ثم سماء سماء
حتى ينزل إلى السماء الدنيا ثم يهبط إلى الأرض فيحبه أهل الأرض ".
** لم
(العلو ٣٧/١)
** محفوظ ثابت
** قال الحافظ الذهبى فى " العلو " ١ / ٣٧: حديث محفوظ ثابت لا أستحضر إسناده.','10','9',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4435,'<span data-type="title" id=toc-4444>٤٤٣٥ </span>- عن جبير عن أبيه عن جده قال: أتى رسول الله صلى الله عليه وسلم أعرابى فقال: يا رسول الله جهدت الأنفس وضاع العيال وهلكت الأنعام ونهكت
الأموال فاستسق الله لنا فإنا لنستشفع بالله عليك وبك على الله، فقال: " ويحك أتدرى ما تقول؟ إنه لا يستشفع بالله على أحد من خلقه، شأن الله أعظم من ذلك، ويحك أتدرى ماالله؟ إن عرشه لعلى سمواته وأرضه هكذا _ قال: وأرانا وهب بيده هكذا _ وقال مثل القبة، وإنه ليئط به أطيط الرحل بالراكب ".
** تخز طب ابن منده قط عبدة
(العلو ٣٨/١)
** غريب جدا فرد
** قال الحافظ الذهبى فى " العلو " ١ / ٣٨: هذا حديث غريب جدا فرد، وابن اسحاق حجة فى المغازى إذا أسند، وله مناكير وعجائب.','10','10',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4436,'<span data-type="title" id=toc-4445>٤٤٣٦ </span>- عن الشعبى قال: كانت زينب تقول للنبى صلى الله عليه وسلم: أنا أعظم نسائك عليك حقا، وأنا خيرهن منكحا، وتقول: زوجنيك الرحمن من فوق عرشه، وكان جبرائيل هو السفير بذلك، وأنا ابنة عمتك وليس لك من نسائك قريبة غيرى.
** لم
(العلو ٤٠/١)
** مرسل','10','11',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4437,'<span data-type="title" id=toc-4446>٤٤٣٧ </span>- أن حسان بن ثابت أنشد النبى صلى الله عليه وسلم: شهدت بإذن الله أن محمدا رسول الذى فوق السموات من عل وأن أبا يحيى ويحيى كلاهما له عمل فى دينه متقبل وأن أخا الأحقاف إذ قام فيهم يقول بذات الله فيهم ويعدل
** خط
(العلو ٤٠/١)
** مرسل','10','12',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4438,'<span data-type="title" id=toc-4447>٤٤٣٨ </span>- أن عبد الله بن رواحة مشى ليلة إلى أمة له فنالها، فرأته امرأته فلامته، فجحدها، فقالت له: إن كنت صادقا فاقرأ القرآن فإن الجنب لا يقرأ القرآن، فقال: شهدت بأن وعد الله حق وأن النار مثوى الكافرين وأن العرش فوق الماء طاف وفوق العرش رب العالمين فقالت امرأته: صدقت وكذبت عينى، وكانت لا تحفظ القرآن.
** ابن عبد البر فى الإستيعاب
(العلو ٤١/١)
** منقطع
** قال الحافظ الذهبى فى " العلو " ١ / ٤١: قال ابن عبد البر فى كتاب الاستيعاب: روينا من وجوه صحاح أن عبد الله بن رواحة.... قال الحافظ الذهبى فى " العلو " ١ / ٤٢: روى من وجوه مرسلة منها يحيى بن أيوب المصرى حدثنا عمارة ابن غزية عن قدامة بن محمد بن إبراهيم الحاطبى فذكره.. فهو منقطع.','10','13',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4439,'<span data-type="title" id=toc-4448>٤٤٣٩ </span>- قد أنشد شعر أمية بن أبى الصلت عند النبى صلى الله عليه وسلم فقال: " آمن شعره وكفر قلبه " وهو: مجدوا الله فهو للمجد أهل ربنا فى السماء أمسى كبيرا بالبناء الأعلى الذى سبق الخلق وسوى فوق السماء سريرا شرجعا ما يناله بصر العين ترى دونه الملائك صورا
** لم
(العلو ٤٢/١)
** إسناده منقطع','10','14',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4440,'<span data-type="title" id=toc-4449>٤٤٤٠ </span>- عن جابر وابن عباس قالا: قال على: يا رسول الله إذا أنت قبضت من يغسلك؟ وفيم نكفنك؟ ومن يصلى عليك؟ ومن يدخلك القبر؟ فقال: " يا على أماالغسل فغسلنى أنت وابن عباس يصب الماء وجبريل ثالثكما فإذا أنتم فرغتم من غسلى فكفنونى فى ثلاث أثواب جدد، وجبريل يأتينى بحنوط من الجنة، فإذا أنتم وضعتمونى على السرير فضعونى فى المسجد واخرجوا عنى فإن أول من يصلى على رب العالمين من فوق عرشه ثم جبريل وميكائيل وإسرافيل ثم الملائكة زمرا ثم ادخلوا فقوموا صفوفا لا يتقدم على أحد " فقبض رسول الله صلى الله عليه وسلم، ثم أدخلوه المسجد ووضعوه فى المسجد وخرج الناس عنه فأول من صلى عليه الرب من فوق عرشه ".
الحديث.
** حل
(العلو ٤٣/١)
** موضوع
** قال الحافظ الذهبى فى " العلو " ١ / ٤٣: هذا حديث موضوع وأراه من افتراء عبد المنعم وإنما رويته لهتك حاله.','10','15',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4441,'<span data-type="title" id=toc-4450>٤٤٤١ </span>- عن أنس بن مالك قال: قال رسول الله صلى الله عليه وسلم: " أخبرنى جبريل عن الله عز وجل أن الله تعالى قال: وعزتى وجلالى ووحدانيتى وفاقة خلقى
إلى وإستوائى على عرشى وارتفاع مكانى إنى لأستحيى من عبدى وأمتى يشيبان فى الإسلام أن أعذبهما، فرأيت رسول الله صلى الله عليه وسلم يبكى، فقلت: ما يبكيك؟ فقال: " بكيت لمن يستحى الله منه ولا يستحى من الله ".
** حل
(العلو ٤٣/١)
** موضوع
** قال الحافظ الذهبى فى " العلو " ١ / ٤٣: أخرجه أبو نعيم الحافظ فى الحلية وعداده فى الموضوعات. وهذا الأنصارى ليس بثقة.','10','16',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4442,'<span data-type="title" id=toc-4451>٤٤٤٢ </span>- عن أنس قال: قال رسول الله صلى الله عليه وسلم: " أوحى الله تعالى إلى نبى من الأنبياء: ما بال عبادى، يدخلون بيوتى بقلوب غير طاهرة وأيد غير نقية، أبى يفترون؟ وإياى يخدعون؟ وعزتى وجلالى وعلوى فى ارتفاعى لأبلينهم ببلية أترك الحليم فيهم حيران، لا ينجو منهم إلا من دعاء كدعاء الغريق ".
** طب
(العلو ٤٣/١)
** لا يصح
** قال الحافظ الذهبى فى " العلو " ١ / ٤٤: لا يصح هذا ولكنه محتمل.','10','17',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4443,'<span data-type="title" id=toc-4452>٤٤٤٣ </span>- عن ابن عباس قال: قال رسول الله صلى الله عليه وسلم: " إن العبد ليشرف على حاجة من حاجات الدنيا فيذكره الله من فوق سبع سموات فيقول: ملائكتى إن عبدى هذا قد أشرف على حاجة فإن فتحتها له بابا من أبواب النار ولكن أزويها عنه فيصبح العبد عاضا على أنامله يقول: من سبقنى؟ من دهانى؟ وماهى إلا رحمة رحمه الله تعالى بها ".
** لم
(العلو ٤٤/١)
** قال الحافظ الذهبى فى " العلو " ١ / ٤٤: صالح تالف ولا يحتمل شعبة هذا.','10','18',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4444,'<span data-type="title" id=toc-4453>٤٤٤٤ </span>- عن أبى هريرة رضى الله عنه قال: ما طرف صاحب الصور مذ وكل به مستعدا ينظر نحو العرش مخافة أن يؤمر قبل أن يرتد إليه طرفه كأن عينيه كوكبان دريان ".
** ك
(العلو ٤٤/١)
** قال الحافظ الذهبى فى " العلو " ١ / ٤٤: أخرجه الحاكم وصححه.','10','19',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4445,'<span data-type="title" id=toc-4454>٤٤٤٥ </span>- عن على بن أبى طالب قال: حدثنى رسول الله صلى الله عليه وسلم ويده على كتفى قال: " حدثنى الصادق الناطق رسول رب العالمين وأمينه على وحيه جبرائيل ويده
على كتفى، سمعت إسرافيل سمعت القلم سمعت اللوح يقول: سمعت الله من فوق العرش
يقول للشىء كن فيكون فلا يبلغ الكاف النون، حتى ما يكون ما يكون ".
** ابن قدامة فى العلو
(العلو ٤٥/١)
** باطل
** قال الحافظ الذهبى فى " العلو " ١ / ٤٥: هذا حديث باطل ما حدث به هلال أبدا وأحمد المكى كذاب رويته للتحذير منه.','10','20',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4446,'<span data-type="title" id=toc-4455>٤٤٤٦ </span>- عن ابن عباس أن رسول الله صلى الله عليه وسلم قال: " مررت ليلة أسرى بى برائحة طيبة فقلت: ما هذه الرائحة يا جبرائيل، قال: هذه ماشطة بنت فرعون
كانت تمشطها فوقع المشط من يدها، فقالت: بسم الله، قالت ابنة فرعون: أبى؟
قالت: ربى ورب أبيك، قالت: أقول له إذا، قالت: قولى له، قال لها:
أو لك رب غيرى؟ قالت: ربى وربك الذى فى السماء، فأحمى لها بقرة من نحاس
فقالت: إن لى إليك حاجة، قال: وما حاجتك؟ قالت: أن تجمع عظامى وعظام
ولدى، قال: ذلك لك لما لك علينا من حق، فألقى ولدها فى البقرة واحدا واحدا
فكان آخرهم صبى، فقال: ياأمه اصبرى فإنك على الحق " قال ابن عباس: فأربعة
تكلموا وهم صبيان ابن ماشطة فرعون، وصبى جريج وعيسى بن مريم، والرابع لا
أحفظه ".
** يع
(العلو ٤٦/١)
** إسناده حسن
** قال الحافظ الذهبى فى " العلو " ١ / ٤٦: هذا حديث حسن الإسناد.','10','21',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4447,'<span data-type="title" id=toc-4456>٤٤٤٧ </span>- عن أبى هريرة قال: قال رسول الله صلى الله عليه وسلم: _ يعنى يقول الله عز وجل _: " أنا عند ظن عبدى بى وأنا معه حين يذكرنى، فإن ذكرنى فى نفسه ذكرته فى نفسى، وإن ذكرنى فى ملأ ذكرته فى ملأ خير منهم، وإن اقترب إلى شبرا اقتربت إليه ذراعا، وإن اقترب إلى ذراعا أقتربت إليه باعا، وإن أتانى ماشيا أتيته هرولا ".
** ابن قدامة فى العلو
(العلو ٤٧/١)
** صحيح','10','22',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4448,'<span data-type="title" id=toc-4457>٤٤٤٨ </span>- عن أسماء بنت أبى بكر الصديق قالت: قال رسول الله صلى الله عليه وسلم: " لما كان ليلة أسرى بى انتهيت إلى سدرة المنتهى ". وذكر الحديث
** لم
(العلو ٤٧/١)
** إسناده صالح','10','23',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4449,'<span data-type="title" id=toc-4458>٤٤٤٩ </span>- عن عبد الله بن عمرو بن العاص قال: إذا مكثت النطفة فى الرحم أربعين ليلة جاء ملك فاحتلجها ثم عرج بها إلى الرحمن تبارك وتعالى، فيقول: اخلق ياأحسن الخالقين فيقضى الله فيها ما يشاء ويهبط بها الملك.... وذكر الحديث.
** لم
(العلو ٤٨/١)
** قال الحافظ الذهبى فى " العلو " ١ / ٤٨: فى إسناده ابن لهيعة.','10','24',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4450,'<span data-type="title" id=toc-4459>٤٤٥٠ </span>- عن عبد الله بن عمرو قال: قال رسول الله صلى الله عليه وسلم: " الرحم معلقة بالعرش وليس الواصل بالمكافى ولكن إذا قطعه ذو رحمة وصله ".
** لم
(العلو ٤٨/١)
** إسناده قوى','10','25',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4451,'<span data-type="title" id=toc-4460>٤٤٥١ </span>- عن أبى أمامة قال: قال أبو أيوب: نزل على رسول الله صلى الله عليه وسلم شهرا فارتقبت عمله فرأيته إذا زالت الشمس فلو كان فى يده عمل الدنيا رفضه وإن كان نائما كأنما يوقظ، فيقوم فيغتسل أو يتوضأ ثم يركع أربع ركعات يتمهن ويحسنهن يتمكن فيهن، فسألته عن ذلك، فقال: " إن أبواب السماء وأبواب الجنان تفتح فى تلك الساعة فلا ترتج حتى تصلى هذه الصلاة فأحب أن يصعد منى إلى ربى تلك الساعة خيرا ".
** آدم بن أبى إياس فى كتاب الثواب
(العلو ٤٨/١)
** إسناده ضعيف
** قال الحافظ الذهبى فى " العلو " ١ / ٤٩: إسناده ضعيف من عبيد الله بن زحر.','10','26',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4452,'<span data-type="title" id=toc-4461>٤٤٥٢ </span>- عن العباس بن عبد المطلب: كنت جالسا بالبطحاء فى عصابة فيها رسول الله صلى الله عليه وسلم إذا مرت عليهم سحابة فنظر إليها، فقال: " هل تدرون ما اسم هذه؟ ... " الحديث بطوله.
** ت الضياء فى المختارة الغيلانيات
(العلو ٥٠/١)
** قال الحافظ الذهبى فى " العلو " ١ / ٥٠: قد حسنه الترمذى.','10','27',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4453,'<span data-type="title" id=toc-4462>٤٤٥٣ </span>- عن العباس قال: كنا عند النبى صلى الله عليه وسلم فمرت سحابة فقال: " ما هذا " قلنا: السحاب، قال: " والمزن " قلنا: والمزن، قال: " والعنان " قلنا: والعنان، قال: " أتدرون كم بين الأرض والسماء؟ " قلنا: الله ورسوله أعلم، قال: " أحد أو اثنتان أو ثلاث وسبعون سنة _ ثم عد سبع سماوات كذلك _ ثم فوق ذلك بحر بين أعلاه وأسفله كما بين سماء إلى سماء وفوق ذلك ثمانية أوعال بين أظلافهن وركبهن ما بين سماء إلى سماء، والعرش فوق ذلك والله فوق العرش ".
** ابن منده فى التوحيد
(العلو ٥٠/١)
** قال الحافظ الذهبى فى " العلو " ١ / ٥٠: أخرجه الحافظ أبو عبد الله بن منده فى كتاب التوحيد. تفرد سماك عن عبد الله وعبد الله فيه جهالة. ويحيى بن العلاء متروك الحديث. وقد رواه إبراهيم بن طهمان عن سماك وإبراهيم ثقة.','10','28',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4454,'<span data-type="title" id=toc-4463>٤٤٥٤ </span>- عن الحسين بن عبد الرحمن بن عوف عن أبيه عن النبى صلى الله عليه وسلم قال: " ثلاثة تحت العرش يوم القيامة: القرآن يحاج العباد والأمانة والرحم ".
** لم
(العلو ٥١/١)
** منكر','10','29',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4455,'<span data-type="title" id=toc-4464>٤٤٥٥ </span>- عن معاذ بن جبل قال: قال رسول الله صلى الله عليه وسلم: " كلمتان من قالهما لم يكن ناهية دون العرش والأخرى تملأ ما بين السماء والأرض لا إله إلا الله والله أكبر ".
** لم
(العلو ٥١/١)
** قال الحافظ الذهبى فى " العلو " ١ / ٥١: حديث فى إسناده ابن لهيعة.','10','30',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4456,'<span data-type="title" id=toc-4465>٤٤٥٦ </span>- عن البراء قال: خرجنا مع رسول الله صلى الله عليه وسلم فى جنازة........ فذكر الحديث بطوله، وقال فى الروح: " حتى ينتهى بها إلى السماء السابعة فيقول الله تعالى: أعيدوه ".
** لم
(العلو ٥٢/١)
** إسناده صالح','10','31',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4457,'<span data-type="title" id=toc-4466>٤٤٥٧ </span>- عن سلمان الفارسى قال: قال رسول الله صلى الله عليه وسلم: " إن ربكم حيى كريم يستحى من عبده إذا رفع يديه إليه يدعوه أن يردهما صفرا ليس فيهما شىء ".
** لم
(العلو ٥٢/١)
** مشهور
** قال الحافظ الذهبى فى " العلو " ١ /٥٢: رواه عن النبى صلى الله عليه وسلم على بن أبى طالب وابن عمرو وأنس وغيرهم.','10','32',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4458,'<span data-type="title" id=toc-4467>٤٤٥٨ </span>- حديث قتادة بن النعمان سمع النبى صلى الله عليه وسلم يقول: " لما فرغ الله من خلقه استوى على عرشه ".
** أبو بكر الخلال فى كتاب السنة
(العلو ٥٢/١)
** رواته ثقات','10','33',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4459,'<span data-type="title" id=toc-4468>٤٤٥٩ </span>- عن أبى هريرة رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: _ عن الله تعالى _: " أنا أغنى الشركاء عن الشرك، لا يصعد إلى من الرياء شىء ".
** لم
(العلو ٥٢/١)
** قال الحافظ الذهبى فى " العلو " ١ / ٥٢: قيس بن الربيع هو ردىء الحفظ.','10','34',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4460,'<span data-type="title" id=toc-4469>٤٤٦٠ </span>- عن أبى هريرة رضى الله عنه عن النبى صلى الله عليه وسلم قال: " رب يمين لا يصعد إلى الله تعالى فى هذه البقعة فرأيت فيها النحاسين ".
** لم
(العلو ٥٢/١)
** منكر','10','35',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4461,'<span data-type="title" id=toc-4470>٤٤٦١ </span>- عن عمر بن عبد الملك قال: خطبنا على رضى الله عنه فقال: إن رسول الله صلى
الله عليه وسلم حدثنى عن ربه عز وجل فقال: " وعزتى وجلالى وارتفاعى فوق
عرشى، مامن أهل قرية ولا بيت ولا رجل ببادية كانوا على ما أكرهت من معصيتى
فتحولوا عنها إلى ما أحببت من طاعتى، إلا تحولت لهم مما يكرهون من عذابى إلى
ما يحبون من رحمتى ".
** العسال فى كتاب المعروف أبو جعفر بن أبى شيبة فى كتاب العرش
(العلو ٥٣/١)
** إسناده ضعيف','10','36',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4462,'<span data-type="title" id=toc-4471>٤٤٦٢ </span>- عن عبادة بن الصامت أن النبى صلى الله عليه وسلم قال: " إن الله تعالى رفعنى يوم القيامة فى أعلى غرفة من الجنة ليس فوقى إلا حملة العرش ".
** لم
(العلو ٥٣/١)
** إسناده ضعيف','10','37',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4463,'<span data-type="title" id=toc-4472>٤٤٦٣ </span>- عن عبادة بن الصامت قال: قال رسول الله صلى الله عليه وسلم: " ينزل الله كل ليلة إلى سماء الدنيا حين يبقى ثلث الليل الآخر فيقول: ألا عبد يدعونى فأستجب له؟ ألا ظالم لنفسه يدعونى فأفكه؟ فيكون كذلك إلى مطلع الفجر ويعلو
على كرسيه ".
** لم
(العلو ٥٣/١)
** قال الحافظ الذهبى فى " العلو " ١ / ٥٣: إسحاق ضعيف لم يدرك جد أبيه.','10','38',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4464,'<span data-type="title" id=toc-4473>٤٤٦٤ </span>- عن جابر عن رسول الله صلى الله عليه وسلم قال: " إن الملك يرفع العمل للعبد يرى أن فى يديه منه سرورا حتى ينتهى إلى الميقات الذى وصف الله له فيضع العمل فيه، فيناديه الجبار عز وجل من فوقه: ارم ما معك، فيقول: ما رفعت إليك إلا حقا، فيقول صدقت ارم به ".
** العسال
(العلو ٥٣/١)
** منكر
** قال الحافظ الذهبى فى " العلو " ١ / ٥٤: هذا حديث منكر لا يثبت مثله ونجم لا أعرفه.','10','39',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4465,'<span data-type="title" id=toc-4474>٤٤٦٥ </span>- حديث أبى عبيدة بن عبد الله بن مسعود عن أبيه عن النبى صلى الله عليه وسلم قال: " يجمع الله الأولين والآخرين لميقات يوم معلوم أربعين سنة شاخصة أبصارهم إلى السماء ينتظرون فصل القضاء فينزل الله تعالى من العرش إلى الكرسى فى ظلل من الغمام ".
** لم
(العلو ٥٤/١)
** إسناده فيه انقطاع محتمل
** قال الحافظ الذهبى فى " العلو " ١ / ٥٤: فيه انقطاع محتمل.','10','40',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4466,'<span data-type="title" id=toc-4475>٤٤٦٦ </span>- عن ابن مسعود قال: قال رجل يا رسول الله ماالمقام المحمود؟ قال: " ذاك يوم ينزل الله على عرشه.... " وذكر الحديث.
** شيخ فى كتاب العظمة
(العلو ٥٤/١)
** قال الحافظ الذهبى فى " العلو " ١ / ٥٤: عثمان ضعيف.','10','41',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4467,'<span data-type="title" id=toc-4476>٤٤٦٧ </span>- عن سهل بن سعد قال: قال رسول الله صلى الله عليه وسلم: " دون الله سبعون ألف حجاب من نور وظلمة، ما يسمع من نفس شىء من حسن تلك الحجب إلا زهقت نفسه ".
** البيهقى فى الصفات
(العلو ٥٤/١)
** قال الحافظ الذهبى فى " العلو " ١ / ٥٤: موسى بن عبيدة أحد الضعفاء.','10','42',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4468,'<span data-type="title" id=toc-4477>٤٤٦٨ </span>- عن معاذ بن جبل قال: قال رسول الله صلى الله عليه وسلم: " إن الله ليكره فى السماء أن يخطىء أبو بكر فى الأرض ".
** حارث
(العلو ٥٥/١)
** غير صحيح
** قال الحافظ الذهبى فى " العلو " ١ / ٥٥: أبو الحارث مجهول، وبكر واه، وشيخه المصلوب تالف، والخبر غير صحيح وعلى باغض الصديق اللعنة، أخرجه الحارث فى مسنده.','10','43',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4469,'<span data-type="title" id=toc-4478>٤٤٦٩ </span>- قال ثابت البنانى: كان داود عليه السلام يطيل الصلاة ثم يركع ثم يرفع رأسه إلى السماء ثم يقول: إليك رفعت رأسى ياعامر السماء نظر العبيد إلى أربابها يا ساكن السماء ".
** لم
(العلو ٥٥/١)
** إسناده صالح','10','44',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4470,'<span data-type="title" id=toc-4479>٤٤٧٠ </span>- عن الحسن قال: سمع يونس عليه السلام تسبيح الحصى وتسبيح الحيتان فجعل يسبح ويهلل ويقدس ويقول: سيدى فى السماء مسكنك، وفى الأرض قدرتك وعجائبك، سيدى من الجبال أهبطتنى، وفى البلاد سيرتنى، وفى الظلمات الثلاث حبستنى، فلما كان فى تمام أربعين يوما وأصابه الغم: فنادى فى الظلمات أن لا إله إلا أنت سبحانك إنى كنت من الظالمين ".
** لم
(العلو ٥٥/١)
** قال الحافظ الذهبى فى " العلو " ١ / ٥٦: أبو حذيفة كذاب.','10','45',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4471,'<span data-type="title" id=toc-4480>٤٤٧١ </span>- عن تميم الدارى سألنا رسول الله صلى الله عليه وسلم عن المعانقة فقال: " أول
من عانق خليل الله إبراهيم سمع صوتا يقدس الله فقصده فإذا هو برجل أهلب طوله
ثمانية عشر ذراعا يقدس الله عز وجل، فقال له إبراهيم: يا شيخ من ربك قال:
الذى فى السماء، قال: من رب من فى السماء؟ قال: الذى فى السماء هو رب من فى السماء ومن فى الأرض.... " وذكر الحديث.
** لم
(العلو ٥٦/١)
** باطل
** قال الحافظ الذهبى فى " العلو " ١ / ٥٦: حديث باطل طويل.','10','46',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4472,'<span data-type="title" id=toc-4481>٤٤٧٢ </span>- عن على بن الحسين أخبرنى رجل من أهل العلم أن رسول الله صلى الله عليه وسلم قال: " تمد الأرض لعظمة الرحمن يوم القيامة مد الأديم ثم لا يكون لبشر منها
إلا موضع قدميه ثم ادعى أول الناس فأخر ساجدا ثم يؤذن لى، فأقول: أى رب إن هذا جبرائيل، قال: وهو عن يمين الرحمن..... " الحديث.
** لم
(العلو ٥٦/١)
** مرسل قوى','10','47',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4473,'<span data-type="title" id=toc-4482>٤٤٧٣ </span>- عن جابر قال: بلغنى حديث فى القصاص وصاحبه بمصر، فاشتريت بعيرا وسرت إليه فأتيته فقال: سمعت رسول الله صلى الله عليه وسلم يقول: " إن الله
يبعثكم حفاة عراة غرلا بهما، ثم ينادى بصوت رفيع غير فظيع وهو قائم على عرشه يسمع القريب والبعيد يقول: أنا الديان لا ظلم اليوم ".
** لم
(العلو ٥٦/١)
** موضوع
** قال الحافظ الذهبى فى " العلو " ١ / ٥٦: حديث المبتدأ إسحاق بن بشير وهو كذاب. قال الحافظ الذهبى فى " العلو " ١ / ٥٦: فهذا شبه موضوع.','10','48',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4474,'<span data-type="title" id=toc-4483>٤٤٧٤ </span>- عن عبد الله بن عمر: والعرش ياقوته حمراء.
** لم
(العلو ٥٨/١)
** قال الحافظ الذهبى فى " العلو " ١ / ٥٨: موسى واه. قال الحافظ الذهبى فى " العلو " ١ / ٥٨: قال أبو أسامة عن اسماعيل بن أبى خالد قال: أخبرت أن العرش ياقوته حمراء، هذا ثابت عن هذا التابعى الإمام.','10','49',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4475,'<span data-type="title" id=toc-4484>٤٤٧٥ </span>- عن حسان بن عطية قال: حملة العرش ثمانية يتجاوبون بصوت حسن رخيم فيقول أربعة منهم: سبحانك وبحمدك على حلمك بعد علمك، ويقول أربعة: سبحانك وبحمدك على عفوك بعد قدرتك ".
** لم
(العلو ٥٨/١)
** إسناده قوى','10','50',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4476,'<span data-type="title" id=toc-4485>٤٤٧٦ </span>- عن أبى هريرة وأبى سعيد عن النبى صلى الله عليه وسلم قال: " مجالس الذكر تنزل عليهم السكينة، وتحفهم الملائكة وتغشاهم الرحمة ويذكرهم الله على عرشه ".
** لم
(العلو ٥٨/١)
** قال الحافظ الذهبى فى " العلو " ١ / ٥٨: الجارود واه والحديث له أصل لكن لفظ الصحيحين عن أبى هريرة " وذكرهم الله فى من عنده ".','10','51',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4477,'<span data-type="title" id=toc-4486>٤٤٧٧ </span>- عن أبى أمامة رضى الله عنه يقول: ما من عبد يسبح تسبيحة إلا يسبح ماخلق الله من شىء وإن من شىء إلا يسبح بحمده وما من عبد يكبر تكبيرة إلا ملأت مابين السماء والأرض، ومامن عبد يحمد تحميدة، إلا خفف الله عن كل ذات حمل حملها، ومامن عبد يهلل تهليلة فينهنهها شىء دون العرش.
** لم
(العلو ٥٩/١)
** قال الحافظ الذهبى فى " العلو " ١ / ٥٩: ابن بسر ضعيف.','10','52',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4478,'<span data-type="title" id=toc-4487>٤٤٧٨ </span>- عن معاذ قال: سمعت رسول الله صلى الله عليه وسلم يقول: " كلمتان إحداهما ليست لها ناهية دون العرش والأخرى تملأ مابين المساء والأرض إله إلا الله والله أكبر ". فبكى ابن عمر وقال: الكلمتان تغفلهما وتألفهما.
** حميد بن زنجويه فى كتاب الترغيب
(العلو ٥٩/١)
** قال الحافظ الذهبى فى " العلو " ١ / ٥٩: ابن لهيعة بحر من بحور العلم لكنه سىء الحفظ لين.','10','53',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4479,'<span data-type="title" id=toc-4488>٤٤٧٩ </span>- عن على رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " إن فاتحة الكتاب وآية الكرسى والآيتين من آل عمران (شهد الله) و (قل اللهم مالك الملك) مابينهن وبين الله حجاب، لما أراد أن ينزلهن تعلقن بالعرش فقلن يارب تهبطنا إلى من يعصيك؟ " وذكر الحديث.
** لم
(العلو ٦٠/١)
** منكر
** قال الحافظ الذهبى فى " العلو " ١ / ٦٠: هذا حديث مشهور تفرد به الحارث بن عمير وبمثل هذا الحديث المنكر نالوا منه.','10','54',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4480,'<span data-type="title" id=toc-4489>٤٤٨٠ </span>- قال عبد الله رضى الله عنه: سارعوا إلى الجمع فى الدنيا فإن الله تعالى ينزل لأهل الجنة كل جمعة فى كثيب من كافور أبيض فيكونون فى القرب منه على قدر تسارعهم إلى الجمع فى الدنيا.
** لم
(العلو ٦٠/١)
** حسن','10','55',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4481,'<span data-type="title" id=toc-4490>٤٤٨١ </span>- عن الحسن عن أبى هريرة قال: قال رسول الله صلى الله عليه وسلم: " تدرون ما
هذه التى فوقكم؟ " قالوا: الله ورسوله أعلم، قال: " فإنها الرقيع سقف
محفوظ وموج مكفوف، هل تدرون كم بينكم وبينها؟ " قالوا: الله ورسوله أعلم
قال: " بينكم وبينها مسيرة خمسمائة عام وبينها وبين السماء الأخرى مثل ذلك
_ حتى عد سبع سماوات _ وغلظ كل سماء مسيرة خمسمائة عام _ ثم قال: _ هل تدرون
ما فوق ذلك؟ " قالوا: الله ورسوله أعلم، قال: " فإن فوق ذلك العرش
وبينه وبين السماء السابعة مسيرة خمسمائة عام _ قال: _ هل تدرون ما هذه
التى تحتكم؟ " قالوا: الله ورسوله أعلم، قال: " فإنها الأرض وبينها
وبين الأرض التى تليها مسيرة خمسمائة عام حتى عد سبع أرضين وغلظ كل أرض
خمسمائة عام ثم قال: والذى نفسى بيده لو أنكم دليتم بحبل إلى الأرض السابعة
لهبط على الله _ ثم قرأ رسول الله صلى الله عليه وسلم _ " هو الأول والآخر
والظاهر والباطن ".
** هق حم ت
(العلو ٦٠/١)
** رواته ثقات
** قال الحافظ الذهبى فى " العلو " ١ / ٦٠: الحسن مدلس والمتن منكر.','10','56',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4482,'<span data-type="title" id=toc-4491>٤٤٨٢ </span>- عن ابن عباس فى قوله " خلق سبع سماوات ومن فى الأرض مثلهن ". قال: فى كل أرض نحو إبراهيم صلى الله عليه وسلم.
** البيهقى فى الصفات
(العلو ٦١/١)
** رواته ثقات','10','57',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4483,'<span data-type="title" id=toc-4492>٤٤٨٣ </span>- عن ابن عباس " ومن الأرض مثلهن " قال: سبع أرضين وفى كل أرض نبى كنبيكم وآدم كآدمكم ونوح كنوح وإبراهيم كإبراهيم وعيسى كعيسى ".
** البيهقى فى الصفات
(العلو ٦١/١)
** قال الحافظ الذهبى فى " العلو " ١ / ٦١: شريك وعطاء فيهما لين لا يبلغ بهما رد حديثهما وهذه بلية تحير السامع كتبتها
استطرادا للتعجب وهو من قبيل اسمع واسكت.','10','58',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4484,'<span data-type="title" id=toc-4493>٤٤٨٤ </span>- عن ابن مسعود فى قوله تعالى " وسع كرسيه السماوات والأرض " قال: دخلت السماوات السبع والأرضون السبع فى الكرسى وذكر قوله " وسع كرسيه ".
** لم
(العلو ٦١/١)
** قال الحافظ الذهبى فى " العلو " ١ / ٦١: الحكم متروك الحديث.','10','59',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4485,'<span data-type="title" id=toc-4494>٤٤٨٥ </span>- عن ابن عباس قال: " الكرسى موضع القدمين والعرش لا يقدر أحد قدره ".
** الذهبى فى العلو
(العلو ٦١/١)
** رواته ثقات','10','60',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4486,'<span data-type="title" id=toc-4495>٤٤٨٦ </span>- عن ابن عمر قال: " لما قبض رسول الله صلى الله عليه وسلم قال أبو بكر رضى الله عنه: أيها الناس إن كان محمد إلهكم الذى تعبدون فإنه قد مات وإن كان إلهكم الذى تعبدون فى السماء فإن إلهكم الذى فى السماء فإن إلهكم لم يمت ثم تلا " وما محمد إلا رسول قد خلت من قبله الرسل ".
** تخ تعليقا
(العلو ٦٢/١)
** صحيح
** قال الحافظ الذهبى فى " العلو " ١ / ٦٢: هذا حديث صحيح قد أخرجه البخارى فى تاريخه تعليقا لفضيل بن غزوان أخبرتا به ابن علوان أنبأنا ابن قدامة فى كتاب إثبات صفة العلو لله تأليفه.','10','61',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4487,'<span data-type="title" id=toc-4496>٤٤٨٧ </span>- عن قيس قال: " لما قدم عمر رضى اله عنه الشام أستقبله الناس وهو على بعيره فقالوا: ياأمير المؤمنين لو ركبت بروذنا يلقاك عظماء الناس ووجوههم فقال عمر رضى الله عنه ألا أريكم ههنا إنما الأمر من ههنا، فأشار بيده إلى السماء ".
** لم
(العلو ٦٢/١)
** إسناده كالشمس','10','62',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4488,'<span data-type="title" id=toc-4497>٤٤٨٨ </span>- " لقيت عمر امرأة يقال لها خولة بنت ثعلبة فقال عمر: هذه امرأة سمع الله شكواها من فوق سبع سماوات ".
** لم
(العلو ٦٣/١)
** إسناده صالح فيه انقطاع
** قال الحافظ الذهبى فى " العلو " ١ / ٦٣: أبو يزيد لم يلحق عمر.','10','63',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4489,'<span data-type="title" id=toc-4498>٤٤٨٩ </span>- عن عبد الرحمن بن عوف: " أنه لماأخذ البيعة يوم الشورى لعثمان وبايع الناس رفع رأسه إلى سقف المسجد وقال اللهم اشهد وذكرالقصة.
** لم
(العلو ٦٣/١)
** إسناده لا يصح','10','64',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4490,'<span data-type="title" id=toc-4499>٤٤٩٠ </span>- عن ابن مسعود قال: " العرش فوق الماء والله فوق العرش لا يخفى عليه شىء من أعمالكم ".
** عبد الله بن أحمد فى السنة أبو بكر بن المنذر العسال طب شيخ اللالكائى الكلمنكى هق
(العلو ٦٤/١)
** إسناده صحيح','10','65',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4491,'<span data-type="title" id=toc-4500>٤٤٩١ </span>- عن ابن مسعود قال: " من قال سبحان االله والحمد لله والله أكبر تلقاهن ملك فعرج بهن إلى الله عز وجل فلا يمر يملأ من الملائكة إلا استغفروا حتى يحيى بهن وجه الرحمن عز وجل ".
** العسال
(العلو ٦٤/١)
** إسناده صحيح','10','66',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4492,'<span data-type="title" id=toc-4501>٤٤٩٢ </span>- عن ابن مسعود قال: " إن العبد ليهم بالأمر من التجارة والإمارة حتى إذا تيسر له نظر لله إليه من فوق سبع سماوات فيقول للملائكة اصرفوه عنه فإنه إن يسرته له أدخلته النار ".
** اللالكائى
(العلو ٦٤/١)
** إسناده قوى','10','67',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4493,'<span data-type="title" id=toc-4502>٤٤٩٣ </span>- عن ابن مسعود قال: " إن الله تعالى يبرز لأهل جنته فى كل جمعة فى كثيف من كافور أبيض فيحدث لهم من الكرامة مالم يروا مثله ويكونون فى الدنو منه كمسارعتهم إلى الجمع ".
** ابن بطة فى الإبانة الكبرى
(العلو ٦٥/١)
** إسناده جيد','10','68',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4494,'<span data-type="title" id=toc-4503>٤٤٩٤ </span>- عن أم سلمة فى قوله " الرحمن على العرش استوى " قالت: الكيف غير معقول والإستواء غير مجهول والإقرار به ايمان والجحود به كفر ".
** ابن قدامة فى العلو
(العلو ٦٥/١)
** لا يصح
** قال الحافظ الذهبى فى " العلو " ١ / ٦٥: هذا القول محفوظ عن جماعة كربيعة الرأى، ومالك الإمام، وأبى جعفر الترمذى، فأما عن أم سلمة فلا يصح لأن أبا كنانة ليس بثقة وأبو عمير لاأعرفه.','10','69',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4495,'<span data-type="title" id=toc-4504>٤٤٩٥ </span>- عن النعمان بن سعد قال: كنت بالكوفة فى دار الإمارة دار على بن أبى طالب رضى الله عنه إذ دخل علينا نوف فقال يا أمير المؤمنين: بالباب أربعون رجلا من اليهود. فقال على: على بهم فلما وقفوا بين يديه قالوا: صف لنا ربك الذى فى السماء كيف هو؟ وكيف كان؟ ومتى كان؟ وعلى أى شىء هو؟ فاستوى على رضى الله عنه جالسا فقال: يامعشر اليهود اسمعوا منى ولا تبالوا أن لا تسألوا
أحدا غيرى: إن ربى عز وجل هو الأول لم يبد مما، ولا ممازج مع ما، ولا حال وهما، ولا شيخ ينقضى ". الحديث
** ابن قدامة فى العلو
(العلو ٦٥/١)
** منكر وإسناده غير ثابت
** قال الحافظ الذهبى فى " العلو " ١ / ٦٦: هذا حديث منكر وإسناده غير ثابت. لكنه صح إلى عبد الوارث.','10','70',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4496,'<span data-type="title" id=toc-4505>٤٤٩٦ </span>- عن عبد الله بن عمرو: " قالت الملائكة ياربنا مناالملائكة المقربون ومنا حملة العرش ومنا الكرام الكاتبون ونحن نسبح الليل والنهار ولا نسأم ولا نفتر خلقت بنى آدم فجعلت لهم الدنيا فاجعل لنا الآخرة قال: ثم عادوا فأجهدوا المسألة فقالوا مثل ذلك فقال جل جلاله: لن أجعل صالح ذرية من خلقت بيدى كمن قلت له كن فكان ".
** الدارمى فى كتاب النقض على المريسى
(العلو ٦٦/١)
** إسناده صالح','10','71',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4497,'<span data-type="title" id=toc-4506>٤٤٩٧ </span>- قال عبد الله بن عمر: " خلق الله أربعة أشياء بيده: العرش والقلم وآدم وجنة عدن ثم قال لسائر الخلق كن فكان ".
** لم
(العلو ٦٦/١)
** إسناده جيد','10','72',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4498,'<span data-type="title" id=toc-4507>٤٤٩٨ </span>- عن أبى ذر قال: كنت مع النبى صلى الله عليه وسلم على حمار عليه برذعة أو قطيفة وذلك عند غروب الشمس فقال لى: " يا أبا ذر هل تدرى أين تغيب هذه؟ قلت: الله ورسوله أعلم قال: فإنها تغرب فى عين حمئة تنطلق حتى تخر ساجدة لربها تحت العرش فإذا حان وقت خروجهاأذن لها فتخرج فتطلع، فإذا أراد الله أن يطلعها من حيث تغرب حبسها فتقول يارب إن مسيرى بعيد فيقول لها اطلعى من حيث غربت ".
** حم
(العلو ٦٧/١)
** إسناده حسن','10','73',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4499,'<span data-type="title" id=toc-4508>٤٤٩٩ </span>- عن سلمان قال: " سبعة يظلهم الله فى ظل عرشه ".
** لم
(العلو ٦٧/١)
** ضعيف
** قال الحافظ الذهبى فى " العلو " ١ / ٦٧: هذا موقوف ضعيف الإسناد.','10','74',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4500,'<span data-type="title" id=toc-4509><span data-type="title" id=toc-4510>٤٥٠٠ </span></span>- قال العرباض بن سارية عن النبى صلى الله عليه وسلم قال: " يقول الله عز وجل: المتحابون بجلالى فى ظل عرشى يوم لا ظل إلاظلى ".
** لم
(العلو ٦٨/١)
** إسناده حسن','10','75',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4501,'<span data-type="title" id=toc-4511>٤٥٠١ </span>- عن أبى الزبير عن جابر قال: لما رجعت مهاجرات البحر إلى رسول الله صلى الله عليه وسلم قال: " ألا تحدثون بأعجب شىء رأيتم بأرض الحبشة؟ فقال فتية منهم يا رسول الله: بينا نحن جلوس إذ مرت علينا عجوز من عجائزهم تحمل قلة من ماء فمرت بفتى منهم فجعل احدى يديه بين كتفيها ثم رفعها على ركبتها فانكسرت قلتها فلما ارتفعت التفتت فقالت: سوف تعلم يا غدر إذا وضع الكرسى وجمع الأولين والآخرين وتكلمت الأيدى والأرجل بما كانوا يكسبون أتعلم أمرى وأمرك عنده غدا فقال رسول الله صلى الله عليه وسلم صدقت كيف يقدس الله قوما لا يؤخذ لضعيفهم من قويهم ".
** لم
(العلو ٦٨/١)
** إسناده صالح','10','76',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4502,'<span data-type="title" id=toc-4512>٤٥٠٢ </span>- عن عبادة بن الصامت أن رسول الله صلى الله عليه وسلم قال: " الجنة مائة درجة ما بين كل درجتين كما بين السماء والأرض والفردوس أعلاها درجة ومن فوقها العرش فإذا سألتم الله فاسألوه الفردوس ".
** لم
(العلو ٦٨/١)
** رواته ثقات','10','77',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4503,'<span data-type="title" id=toc-4513>٤٥٠٣ </span>- عن معاذ بن جبل: سمعت رسول الله صلى الله عليه وسلم يقول: " الجنة مائة درجة " نحوه. وقال: " العرش على الفردوس ومنها تفجر أنهار الجنة ".
** لم
(العلو ٦٩/١)
** منقطع معلل
** قال الحافظ الذهبى فى " العلو " ١ / ٦٩: هذا منقطع معلل بما قبله.
** تعقيب: قال محمد مصطفى: قوله " بماقبله " يعنى حديث عبادة بن الصامت مرفوعا " الجنة مائة درجة.الخ " ص ٦٨ _ ٦٩.','10','78',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4504,'<span data-type="title" id=toc-4514>٤٥٠٤ </span>- عن أسماء بنت قيس قالت: اما توفى سعد بن معاذ صاحت أمه فقال النبى صلى الله عليه وسلم: " ألا يرقأ دمعك ويذهب حزنك؟ فإن ابنك أول من ضحك الله إليه واهتز له العرش ".
** لم
(العلو ٧٠/١)
** مرسل
** قال الحافظ الذهبى فى " العلو " ١ / ٧١: أسماء تابعية وهذا مرسل.','10','79',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4505,'<span data-type="title" id=toc-4515>٤٥٠٥ </span>- عن أنس قال رسول الله صلى الله عليه وسلم: وجنازة سعد موضوعة " اهتز لها عرش الرحمن ".
** لم
(العلو ٧١/١)
** صحيح','10','80',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4506,'<span data-type="title" id=toc-4516>٤٥٠٦ </span>- عن أبى سعيد أن النبى صلى الله عليه وسلم قال: " اهتز العرش لموت سعد بن معاذ ".
** لم
(العلو ٧١/١)
** صحيح','10','81',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4507,'<span data-type="title" id=toc-4517>٤٥٠٧ </span>- عن عائشة قالت: سمعت أسيد بن حضير يقول: سمعت رسول الله صلى الله عليه وسلم يقول: " لقد اهتز العرش لوفاة سعد ".
** لم
(العلو ٧١/١)
** إسناده حسن','10','82',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4508,'<span data-type="title" id=toc-4518>٤٥٠٨ </span>- عن رميثة سمعت النبى صلى الله عليه وسلم: " ولو أشاء أن أقبل الخاتم من قربى لفعلت وهو يقول: اهتز عرش الرحمن يريد بذلك سعد بن معاذ ".
** لم
(العلو ٧١/١)
** إسناده صالح
** قال الحافظ الذهبى فى " العلو " ١ / ٧١: صححه ابن منده.','10','83',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4509,'<span data-type="title" id=toc-4519>٤٥٠٩ </span>- عن ابن عمر مرفوعا: " اهتز العرش لحب لقاء الله سعدا ".
** لم
(العلو ٧١/١)
** متواتر
** قال الحافظ الذهبى فى " العلو " ١ / ٧١: وفى الباب عن سعد بن أبى وقاص وابن عمرو حذيفة وأبى هريرة وأسماء بنت يزيد ومعيقب فهذا متواتر أشهد بأن رسول الله صلى الله عليه وسلم قاله.','10','84',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4510,'<span data-type="title" id=toc-4520>٤٥١٠ </span>- عن ابن عباس: مامن شىء كان فى بنى إسرائيل إلا سيكون فى هذه الأمة مثله وإن رجلا من بنى إسرائيل كانت له امرأة جميلة فأولع به رجل يخبره عنها أنها كذا وكذا بالفحش قال كيف أصنع ولها على دين؟ قال: أنا أسلفك ما عليك فطلقها ثم تزوجها ذلك الرجل بعد، فلما تزوجها أخذه بحقه فاشتد عليه فقال: اتق الله فإنك لم تزل بى حتى فعلت ما فعلت، فلم يقلع عنه حتى أجره نفسه، فبينما هو ذات يوم أكلا طعاما فجعل يصب عليهم الماء فذكر مكانها منه قبل اليوم وأنه الأن يصب عليهم الماء فبكى فاهتز العرش فقال تعالى: إن رحمتى سبقت غضبى ".
** كتاب العرش لأبى جعفر محمد بن عثمان الحافظ
(العلو ٧٢/١)
** إسناده متصل
** قال الحافظ الذهبى فى " العلو " ١ / ٧٢: إسنادها متصل لكن لا أعرف التابعى.','10','85',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4511,'<span data-type="title" id=toc-4521>٤٥١١ </span>- عن أبى سعيد أن رسول الله صلى الله عليه وسلم قال: " إن الشيطان قال: وعزتك لا أبرح أغوى عبادك ما دامت أرواحهم فى أجسادهم قال: وعزتى وجلالى وارتفاعى مكانى لا أزال أغفر لهم مااستغفرونى ".
** لم
(العلو ٧٢/١)
** قال الحافظ الذهبى فى " العلو " ١ / ٧٢: فيه دراج وهو واه.','10','86',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4512,'<span data-type="title" id=toc-4522>٤٥١٢ </span>- عن جابر بن عبد الله قال: قال رسول الله صلى الله عليه وسلم: " إن أقرب الخلق إلى الله تعالى جبريل وإسرافيل وميكائيل وإنهم من الله تعالى بمسيرة خمسين ألف سنة ".
** ابن منده فى الصفات شيخ الإسلام فى الفاروق
(العلو ٧٢/١)
** إسناده لين
** قال الحافظ الذهبى فى " العلو " ١ / ٧٢: إسناده لين لأن الأحوص ليس بمعتمد.','10','87',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4513,'<span data-type="title" id=toc-4523>٤٥١٣ </span>- عنن أنس قال: قال رسول الله صلى الله عليه وسلم: " إذا نزل الله إلى سماء الدنيا نزل على عرشه ".
** الفاروق لشيخ الإسلام
(العلو ٧٢/١)
** إسناده ساقط
** قال الحافظ الذهبى فى " العلو " ١ / ٧٣: هذا إسناد ساقط وبشر لا ندرى من هو.','10','88',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4514,'<span data-type="title" id=toc-4524>٤٥١٤ </span>- ولفظه " إذا إراد أن ينزل على عرشه نزل بذاته ".
** ابن منده فى الصفات
(العلو ٧٣/١)
** قال الحافظ الذهبى فى " العلو " ١ / ٧٣: قال ابن منده: لعل هذا موضوع.','10','89',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4515,'<span data-type="title" id=toc-4525>٤٥١٥ </span>- عن عبد الله عن النبى صلى الله عليه وسلم قال: " يجمع الله الأولين والآخرين ليمقات يوم معلوم أربعين سنة شاخصة أبصارهم إلى السماء ينتظرون فصل القظاء، وينزل الله فى ظلل من الغمام من العرش إلى الكرسى ". الحديث بطوله.
** ابن وارة عبد الله بن أحمد أبو أمية الطرطوسى
(العلو ٧٣/١)
** إسناده حسن','10','90',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4516,'<span data-type="title" id=toc-4526>٤٥١٦ </span>- عن عبد الله بن مسعود قال: " بينا أنا عند رسول الله صلى الله عليه وسلم أقرأ عليه حتى بلغت " عسى أن يبلغك ربك مقاما محمودا " قال: يجلسنى على العرش ".
** لم
(العلو ٧٤/١)
** منكر
** قال الحافظ الذهبى فى " العلو " ١ / ٧٥: هذا حديث منكر لايفرح به وسلمة هذا متروك الحديث وأشعث لم يلحق ابن مسعود','10','91',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4517,'<span data-type="title" id=toc-4527>٤٥١٧ </span>- عن عبد الله بن سلام قال: " إذا كان يوم القيامة جىء بنبيكم صلى الله عليه وسلم فأقعد بين يدى الله على كرسيه ".
** لم
(العلو ٧٥/١)
** إسناده لا يثبت
** قال الحافظ الذهبى فى " العلو " ١ / ٧٥: هذا موقوف ولا يثبت إسناده.','10','92',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4518,'<span data-type="title" id=toc-4528>٤٥١٨ </span>- عن أبى هريرة أن النبى صلى الله عليه وسلم أخذ بيدى فقال " إن الله خلق السماوات والأرضين وما بينهما فى ستة أيام ثم استوى على العرش يوم السابع وخلق التربة يوم السبت والجبال يوم الأحد والشجر يوم الأثنين والشر يوم الثلاثاء والنور يوم الأربعاء والدواب يوم الخميس، وآدم يوم الجمعة فى آخر ساعة من النهار بعد العصر خلقه من أديم الأرض بأحمرها وأسودها وطيبها وخبيثها من أجل ذلك جعل الله من آدم الطيب والخبيث ".
** ن
(العلو ٧٥/١)
** غريب
** قال الحافظ الذهبى فى " العلو " ١ / ٧٥: الأخضر وثقه ابن معين وقال أبو حاتم: يكتب حديثه ولينه الأزدى وحديثه فى السنن الأربعة وهذا الحديث غريب من أفراده.','10','93',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4519,'<span data-type="title" id=toc-4529>٤٥١٩ </span>- عن ابن عباس " أن اليهود أتت النبى صلى الله عليه وسلم فسألته عن خلق السماوات والأرض فقال: " خلق الله الأرض يوم الأحد والأثنين وخلق الجبال يوم الثلاثاء وخلق يوم الأربعاء الشجر والماء والمدائن والعمران والخراب قال الله تعالى " أئنكم لتكفرون بالذى خلق الأرض فى يومين إلى قوله وقدر فيها أقواتها فى أربعة أيام " وخلق يوم الخميس السماء، وخلق يوم الجمعة النجوم والشمس والقمر والملائكة إلى ثلاث ساعات بقين فخلق فى أول ساعة الآجال، وفى الثانية ألقى الأفة على كل شىء مما ينتفع به الناس وفى الثالثة خلق آدم وأسكنه الجنة وأمر ابليس بالسجود له وأخرجه منها فى آخر ساعة ثم قالت اليهود: ثم ماذايا محمد؟ قال: ثم استوى على العرش قالوا قد أصبت لو أتممت قالوا: ثم استراح فغضب النبى صلى الله عليه وسلم فنزلت " ومامسنا من لغوب فاصبر على مايقولون ".
** لم
(العلو ٧٥/١)
** قال الحافظ الذهبى فى " العلو " ١ / ٧٦: صححه الحاكم وأنى ذلك والبقال قد ضعفه ابن معين والناس.','10','94',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4520,'<span data-type="title" id=toc-4530>٤٥٢٠ </span>- عن معاذ بن جبل: سمعت رسول الله صلى الله عليه وسلم يقول: " المتحابون فى الله يظلهم الله فى ظل عرشه يوم لا ظل إلا ظله ".
** لم
(العلو ٧٦/١)
** قال الحافظ الذهبى فى " العلو " ١ / ٧٦: الصحيح أن أبا إدريس لم يشافه معاذ بن جبل.','10','95',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4521,'<span data-type="title" id=toc-4531>٤٥٢١ </span>- عن عمر قال: قال رسول الله صلى الله عليه وسلم: " إن اليتيم إذا بكى اهتز عرش الرحمن لبكائه فيقول الله لملائكته: من أبكى عبدى وأنا أخذت أباه وواريته فى التراب؟ فيقولون ربنا أعلم به فيقول: اشهدوا لمن أرضاه أرضيته يوم القيامة ".
** لم
(العلو ٧٧/١)
** إسناده ضعيف','10','96',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4522,'<span data-type="title" id=toc-4532>٤٥٢٢ </span>- عن أنس قال: " تلا رسول الله صلى الله عليه وسلم نارا وقودها الناس والحجارة، وبين يديه رجل أسود فهتف بالبكاء فنزل جبرائيل فقال من هذا قال: رجل من الحبشة وأثنى عليه قال: فإن الله تعالى يقول وعزتى وجلالى وارتفاعى فوق عرشى لا تبكين عن عبد فى الدنيا من خشيتى إلا أكثرت ضحكها فى الجنة ".
** لم
(العلو ٧٧/١)
** موضوع
** قال الحافظ الذهبى فى " العلو " ١ / ٧٧: هذا حديث فى نقدى موضوع والقرشى ليس بثقة والكوفى لا أعرفه فلعله آفته.','10','97',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4523,'<span data-type="title" id=toc-4533>٤٥٢٣ </span>- عن جابر مرفوعا: " أذن لى أن أحدث عن مالك من حملة العرش مابين شحمة أذنه إلى عاتقه مسيرة سبع مائة سنة ".
** لم
(العلو ٧٨/١)
** إسناده صحيح','10','98',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4524,'<span data-type="title" id=toc-4534>٤٥٢٤ </span>- عن أنس عن النبى صلى الله عليه وسلم: " أذن لى فى الحديث عن مالك، إن قدميه لعلى الأرض السابعة ثم خرج فى الهوى مابين السماء والأرض حتى انتهى إلى إن كان العرش على هامته لو أن الطير سخرت فى مابين أصل عنقه إلى منتهى رأسه خفقت فيه سبعمائة عام قبل أن تقطعه ".
** لم
(العلو ٧٨/١)
** إسناده واه','10','99',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4525,'<span data-type="title" id=toc-4535>٤٥٢٥ </span>- عن أبى أمامة قال: كان من أشد الناس تكذيبا لرسول الله صلى الله عليه وسلم وأكثرهم ردا عليه اليهود فسألوه أى البقاع شر؟ فقال: حتى أسأل صاحبى جبرائيل، فجاءه فسأله فقال: حتى أسأل ربى قال: فسأل ربه فقال: شر البقاع أسواقها، وخير البقاع مساجدها. فهبط جبرائيل فقال: يامحمد لقد دنوت من الله عز وجل دنوا ما دنوت مثله قط فكان بينى وبينه سبعون حجابا من نور فقال إن شر البقاع أسواقها وخير البقاع مساجدها ".
** لم
(العلو ٧٨/١)
** إسناده ليس بالقوى','10','100',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4526,'<span data-type="title" id=toc-4536>٤٥٢٦ </span>- عن ابن عمر " قال رجل يا رسول الله؟ أى البقاع خير؟ قال: لا أدرى فأتاه جبرائيل فسأله فقال: لا أدرى قال: سل ربك قال: ما تسأله عن شىء فانتفض انتفاضة كاد يصعق منها محمد صلى الله عليه وسلم فلما صعد جبرائيل قال الله عز وجل: سألك محمد أى البقاع خير؟ حدثه أن خير البقاع المساجد وأن شر البقاع الأسواق ".
** لم
(العلو ٧٨/١)
** غريب صالح الإسناد','10','101',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4527,'<span data-type="title" id=toc-4537>٤٥٢٧ </span>- عن أبى هريرة عن النبى صلى الله عليه وسلم قال: " ينزل ربنا عز وجل كل ليلة إذا مضى ثلث الليل الأول فيقول أنا الملك من ذا الذى يسألنى فأعطيه من ذا الذى يدعونى فأستجيب له، من ذا الذى يستغفرنى فأغفر له؟ فلا يزال كذلك ".
** لم
(العلو ٧٩/١)
** إسناده قوى
** قال الحافظ الذهبى فى " العلو " ١ / ٧٩: إسناده قوى، وقد الفت أحاديث النزول فى جزء، وذلك متواتر أقطع به.','10','102',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4528,'<span data-type="title" id=toc-4538>٤٥٢٨ </span>- عن عبد الله بن عمرو قال: سمعت النبى صلى الله عليه وسلم يقول: " كفى بالمرء إثما أن يضيع من يقوت " ثم أنشأ عبد الله يحدث عن الشمس قال: " إنها إذا غربت صعدت إلى السماء فسلمت وسجدت واستأذنت فيؤذن لها وباتت تجرى فهى كذلك حتى يأتى عليها ليلة فتسلم فلا يقبل منها وتسلم فلا يرد عليها وتستأذن فلا يؤذن لها فتتلمس من يشفع لها فلا تجد، فتقول: إن المشرق بعيد فلا يؤذن لها، فإذا طلع الفجر قيل لها اطلعى من مكانك فذلك حين لا ينفع نفسا إيمانها ".
** لم
(العلو ٧٩/١)
** قال الحافظ الذهبى فى " العلو " ١ / ٧٩: قال ابن منده: إسناده صحيح.','10','103',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4529,'<span data-type="title" id=toc-4539>٤٥٢٩ </span>- عن أبى مالك الأشعرى أنهم بينما هم عند رسول الله صلى الله عليه وسلم: " فذكر قوما ليسوا بأنبياء ولا شهداء يغبطهم النبيون بمقعدهم وقربهم من الله يوم القيامة قم قال هم عباد الله من بلدان شتى وقبائل شتى من شعوب القبائل لم يكن بينهم أرحام يتواصلون بها دنيا يتباذلونها تحابوا بروح الله يجعل الله لهم منابر من نور ويجعل وجوههم نورا يوم القيامة قدام الرحمن يفزع الناس ولا يفزعون ويخاف الناس ولا يخافون ".
** حميد بن زنجويه فى الترغيب
(العلو ٨٠/١)
** إسناده صالح','10','104',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4530,'<span data-type="title" id=toc-4540>٤٥٣٠ </span>- عن أنس قال: قال رسول الله صلى الله عليه وسلم: " إن الله من على فيما من: إنى أعطيتك فاتحة الكتاب وهى كنز من كنوز عرشى قسمتها بينى وبينك نصفين ".
** لم
(العلو ٨٠/١)
** قال الحافظ الذهبى فى " العلو " ١ / ٨٠: صالح ضعيف الحديث.','10','105',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4531,'<span data-type="title" id=toc-4541>٤٥٣١ </span>- عن شهر بن حوشب أن رجلا قدم حمص فلقى رجلا فحدثه أنه سمع رسول الله صلى الله
عليه وسلم يقول: " المتحابون فى الله فى ظل العرش يوم القيامة ".
** لم
(العلو ٨٠/١)
** قال الحافظ الذهبى فى " العلو " ١ / ٨٠: حجاج هذا يقال له زق العسل جائز الحديث ليس بالحجة.','10','106',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4532,'<span data-type="title" id=toc-4542>٤٥٣٢ </span>- عن أبى ذر: قلت يا رسول الله هل رأيت ربك؟ فقال " نور أنى أراه ".
** تخز
(العلو ٨١/١)
** قال الحافظ الذهبى فى " العلو " ١ / ٨١: عد ابن خزيمة هذا منكر.','10','107',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4533,'<span data-type="title" id=toc-4543>٤٥٣٣ </span>- عن ابن عباس قال: قال رسول الله صلى الله عليه وسلم: " رأيت ربى عز وجل ".
** حم
(العلو ٨١/١)
** إسناده قوى','10','108',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4534,'<span data-type="title" id=toc-4544>٤٥٣٤ </span>- عن سليم بن عامر أن أبا أمامة حدثه قال: خرج علينا رسول الله صلى الله عليه وسلم بعد صلاة الصبح فقال: " إنى رأيت رؤيا حق فاعقلوها أتانى رجل فأخذ بيدى فاستتبعنى حتى أتى جبلا وعرا فقال لى أرقه قلت لا أستطيع فقال إنى سأسهله لك فجعلت كلما رفعت قدمى وضعتها على درجة حتى استوينا على سواء الجبل فانطلقنا فإذا نحن برجال ونساء مشققة أشداقهم قلت: ماهؤلاء قال هؤلاء يقولون مالا يفعلون فذكر خبرا طويلا يقول فيه: ثم رفعت رأسى فإذا ثلاثة نفر تحت العرش قلت ماهؤلاء؟ قال أبوك إبراهيم وموسى وعيسى وهم ينتظرونك ".
** لم
(العلو ٨٢/١)
** إسناده جيد
** قال الحافظ الذهبى فى " العلو " ١ / ٨٢: رواه أبو اسماعيل الترمذى عن كاتب الليثى وهو ملى بمعرفته إن شاء الله.','10','109',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4535,'<span data-type="title" id=toc-4545>٤٥٣٥ </span>- عن أبى ذر الغفارى رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم " إن الآيتين من آخر سورة البقرة أو تيتهن من تحت العرش لم يؤتها نبى من قبلى ".
** لم
(العلو ٨٤/١)
** رواته ثقات','10','110',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4536,'<span data-type="title" id=toc-4546>٤٥٣٦ </span>- عن عقبة بن عامر قال: قال رسول الله صلى الله عليه وسلم: " من قرأ الآيتين من أخر سورة البقرة فإن الله أعطانيها من تحت العرش ".
** لم
(العلو ٨٤/١)
** إسناده صالح','10','111',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4537,'<span data-type="title" id=toc-4547>٤٥٣٧ </span>- عن معقل بن يسار قال: قال رسول الله صلى الله عليه وسلم: " ألا إنى أعطيت سورة البقرة من الذكر الأول وأعطيت طه ويس من ألواح موسى وأعطيت الفاتحة وخواتيم سورة البقرة من تحت العرش وأعطيت المفصل نافلة ".
** لم
(العلو ٨٤/١)
** منكر
** قال الحافظ الذهبى فى " العلو " ١ / ٨٤: عبيد الله متروك الحديث.','10','112',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4538,'<span data-type="title" id=toc-4548>٤٥٣٨ </span>- عن أبى أمامة قال: قال رسول الله صلى الله عليه وسلم: " أربع آيات أنزلت من تحت العرش لم ينزل منهن شىء غير هن أم الكتاب وآية الكرسى وخاتمة البقرة
والكوثر ".
** لم
(العلو ٨٥/١)
** قال الحافظ الذهبى فى " العلو " ١ / ٨٥: لم يصح هذا.','10','113',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4539,'<span data-type="title" id=toc-4549>٤٥٣٩ </span>- عن أبى هريرة رضى الله عنه مرفوعا: " من أنظر معسرا أو وضع عنه أظله الله تحت عرشه يوم لا ظل إلا ظله ".
** لم
(العلو ٨٥/١)
** إسناده صالح','10','114',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4540,'<span data-type="title" id=toc-4550>٤٥٤٠ </span>- أن نافع بن زيد الحميرى وفد على النبى صلى الله عليه وسلم فى نفر من حمير فقالوا: أتيناك لنتفقه فى الدين ونسأل عن أول هذا الأمر فقال: " كان الله وليس شىء غيره وكان عرشه على الماء ثم خلق القلم فقال اكتب ماهو كائن ثم خلق السماوات والأرض وما بينهما واستوى على عرشه ".
** ابن شاهين فى كتاب الصحابة
(العلو ٨٥/١)
** إسناده واه','10','115',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4541,'<span data-type="title" id=toc-4551>٤٥٤١ </span>- عن ابن عباس رضى الله عنه قال: " إنما مثل السماوات والأرض فيما وراءهن من الهواء حيث لا سماء ولا أرض كمثل فسطاط فى صحرائكم ترى ذلك الفسطاط أخذ من الصحراء ".
** لم
(العلو ٨٦/١)
** قال الحافظ الذهبى فى " العلو " ١ / ٨٦: طلحة ضعفوه.','10','116',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4542,'<span data-type="title" id=toc-4552>٤٥٤٢ </span>- عن ابن عباس رضى الله عنه سمع النبى صلى الله عليه وسلم يقول: " إن لله ملكا لو قيل له التقم السماوات والأرض لفعل ".
** طب
(العلو ٨٦/١)
** منكر','10','117',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4543,'<span data-type="title" id=toc-4553>٤٥٤٣ </span>- عن ابن عباس رضى الله عنه قال: " خلق الله اللوح المحفوظ كمسيرة خمسمائة عام فقال للقلم قبل أن يخلق الخلق وهو على العرش أكتب علمى فى خلقى فجرى بما هو كائن إلى يوم القيامة ".
** لم
(العلو ٨٦/١)
** قال الحافظ الذهبى فى " العلو " ١ / ٨٦: إسناده لولا ابن لهيعة جيد.','10','118',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4544,'<span data-type="title" id=toc-4554>٤٥٤٤ </span>- عن ابن عباس رضى الله عنه فى قوله " ثم لأتينهم من بين أيديهم " قال: لم يستطع أن يقول من فوقهم، علم أن الله تعالى من فوقهم ".
** لم
(العلو ٨٦/١)
** قال الحافظ الذهبى فى " العلو " ١ / ٨٦: إبراهيم بن حكيم بن أبان أحد الضعفاء.','10','119',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4545,'<span data-type="title" id=toc-4555>٤٥٤٥ </span>- عن أبى أمامة الباهلى عن النبى صلى الله عليه وسلم قال: " أربعة لعنهم الله تعالى فوق عرشه وأمنت الملائكة الذى يحصر نفسه عن النساء فلا يتزوج ولا يتسرى لئلا يولد له، والرجل يتشبه بالنساء والمرأة تتشبه بالرجال ومضل المساكين ".
** الفاروق للهروى
(العلو ٨٧/١)
** منكر
** قال الحافظ الذهبى فى " العلو " ١ / ٨٧: أخرجه صاحب الفاروق وهو حديث منكر وخالد مغمور كحماد.','10','120',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4546,'<span data-type="title" id=toc-4556>٤٥٤٦ </span>- عن أبى هريرة رضى الله عنه أن يهوديا قال: يامحمد ما يصنع الرب اليوم؟ قال: " هو على عرشه يدبر الأمر يفصل الآيات ".
** لم
(العلو ٨٧/١)
** منكر
** قال الحافظ الذهبى فى " العلو " ١ / ٨٧: هذا منكر ومعناه حق لكن عبد المنعم كذبه أحمد وغيره.','10','121',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4547,'<span data-type="title" id=toc-4557>٤٥٤٧ </span>- عن ابن عباس رضى الله عنه قال: " إذا تكلم الله بالوحى سمع أهل السماوات له صوتا كصوت الحديد على الصفا فيخرون له سجدا ".
** لم
(العلو ٨٧/١)
** قال الحافظ الذهبى فى " العلو " ١ / ٨٧: يزيد ليس بالحافظ.','10','122',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4548,'<span data-type="title" id=toc-4558>٤٥٤٨ </span>- عن ابن عباس: " ينادى مناد بين يدى الساعة: أتتكم الساعة يسمعه الأحياء والأموات ثم ينزل إلى سماء الدنيا ".
** لم
(العلو ٨٧/١)
** رواته ثقات','10','123',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4549,'<span data-type="title" id=toc-4559>٤٥٤٩ </span>- عن ابن عباس رضى الله عنه: " أن بورك من فى النار " وقال عز وجل ومن حولها قال: " الملائكة ".
** لم
(العلو ٨٧/١)
** إسناده صالح','10','124',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4550,'<span data-type="title" id=toc-4560>٤٥٥٠ </span>- عن عبد الله بن عمرو: " لماأراد الله أن يخلق ما خلق إذ كان عرشه على الماء وإذ لا أرض ولا سماء، خلق الريح فسلطها على الماء حتى اضطرب وآثار ركامه فأخرج من الماء دخانا وطينا وزبدا، فأمر الدخان فعلا وسما ونما فخلق منه السماوات وخلق من الطين الأرض ومن الزبد الجبال ".
** لم
(العلو ٨٧/١)
** إسناده ضعيف','10','125',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4551,'<span data-type="title" id=toc-4561>٤٥٥١ </span>- عن أبى ريحانة قال: قال رسول الله صلى الله عليه وسلم: " إن ابليس اتخذ عرشا على الماء مثل عرش الرحمن عز وجل ووكل لكل رجل شيطانين وأمهلهما سنة فإن فتناه وإلا قطع أيديهما وأرجلهما وصلبهما ثم بعث إليه شيطانين ".
** تفسير سنيد بن داود
(العلو ٨٨/١)
** غريب منكر
** قال الحافظ الذهبى فى " العلو " ١ / ٨٨: قال الحافظ ابن منده: تفرد به أبو بكر. قال الحافظ الذهبى فى " العلو " ١ / ٨٨: هو حديث غريب منكر لا يعرف إلا بهذا الإسناد.','10','126',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4552,'<span data-type="title" id=toc-4562>٤٥٥٢ </span>- " أمر رسول الله صلى الله عليه وسلم فصف المهاجرين والأنصار صفين ثم أخذ بيد العباس وعلى فمر بين الصفين فضحك فقال على: بأبى وأمى من ايش ضحكت فقال: هبط جبرائيل فأخبرنى أن الله باهى بك ياعلى وبك يا عباس وبى حملة العرش وباهى بالمهاجرين والأنصار أهل السماء العليا ".
** العبس فى كتاب العرش
(العلو ٨٨/١)
** موضوع
** قال الحافظ الذهبى فى " العلو " ١ / ٨٨: هذا حديث موضوع فى نقدى فلا أدرى من آفته، وسفيان ابن بشر مشهور ما رأيت فيه جرحا فليضعف برواية مثل هذا.','10','127',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4553,'<span data-type="title" id=toc-4563>٤٥٥٣ </span>- عن ابن عباس رضى الله عنه قال: " قالت امرأة العزيز ليوسف إنى كثيرة الدر والياقوت فأعطيك ذلك حتى تنفق فى مرضاة سيدك فى السماء ".
** لم
(العلو ٨٨/١)
** قال الحافظ الذهبى فى " العلو " ١ / ٨٨: جويبر بن سعيد واه. وقال الحافظ الذهبى فى " العلو " ١ / ٨٨: إسناده قوى عن جويبر.','10','128',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4554,'<span data-type="title" id=toc-4564>٤٥٥٤ </span>- عن أبى ذر رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " ما بين السماء والأرض مسيرة خمسمائة عام ومسيرة ما بينهما إلى التى تليها خمسمائة عام كذلك إلى السماء السابعة والأرضين مثل جميع ذلك وما بين السماء السابعة والعرش مثل جميع ذلك، ولو حضرتم لصاحبكم فيها لوجدتموه " يعنى علمه
** لم
(العلو ٨٩/١)
** منكر
** قال الحافظ الذهبى فى " العلو " ١/ ٨٩: أبو نصر هذا مجهول وماكان الأعمش شافهه به، وهو عند محاضر بن المورع عن الأعمش عن عمرو بن مرة عن أبى نضرة والأول أشهر ولكل حال فهو خبر منكر.','10','129',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4555,'<span data-type="title" id=toc-4565>٤٥٥٥ </span>- عن أبى الدرداء: " أنه أتاه رجل فذكر أن أباه احتبس بوله وأصابه الأسر بحصاة البول فعلمه رقيه سمعها من رسول الله صلى الله عليه وسلم " ربنا الله الذى فىالسماء تقدس اسمك أمرك فى السماء والأرض كما رحمتك فى السماء اجعل رحمتك فى الأرض واغفر لنا حوبنا وخطايانا أنت رب الطيبين فأنزل رحمة من رحمتك وشفاء من شفائك على هذا الوجع فيبرأ " وأمره أن يرقيه بها فرقاه فبرىء ".
** د
(العلو ٨٩/١)
** قال الحافظ الذهبى فى " العلو " ١ / ٨٩: زيادة لين.','10','130',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4556,'<span data-type="title" id=toc-4566>٤٥٥٦ </span>- عن أبى ذر رضى الله عنه: قلت يارسول الله أى أية أعظم؟ قال: آية الكرسى ماالسماوات السبع فى الكرسى إلا كحلقة ملقاة فى أرض فلاة وفضل العرش على الكرسى كفضل الفلاة على تلك الحلقة ".
** لم
(العلو ٨٩/١)
** منكر
** قال الحافظ الذهبى فى " العلو " ١ / ٩٠: رواه عن محمد بن مرزوق بن بكير وأحسب العبشمى هو الأموى صدوق وإلا فهو آخر والخبر منكر.','10','131',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4557,'<span data-type="title" id=toc-4567>٤٥٥٧ </span>- عن سليمان بن حبيب المحازى قال: نزلنا حمص قافلين من الروم فإذا بعبد الله بن زكريا ومكحول، فانطلقنا إلى أبى أمامة فإذا هو شيخ هرم، فلما تكلم إذا رجل يبلغ حاجته ويزيد فوعظنا وقال: إياكم والظلم، فإن الله جل جلاله يجلس يوم القيامة على القنطرة الوسطى بين الجنة والنار فيقول: وعزتى وو جلالى لا يجوز بى ظلم ظالم ".
** لم
(العلو ٩٠/١)
** منكر وإسناده وسط
** قال الحافظ الذهبى فى " العلو " ١ / ٩١: الحديث منكر وإسناده وسط.','10','132',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4558,'<span data-type="title" id=toc-4568>٤٥٥٨ </span>- عن عبد الله بن سلام قال: " بدأ الله خلق الأرض فخلق سبع أرضين يوم الأحد والإثنين وقدر فيها أقواتها يوم الثلاثاء والأربعاء واستوى إلى السماء فخلقهن فى يومين ". وذكر الحديث
** لم
(العلو ٩١/١)
** إسناده صحيح','10','133',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4559,'<span data-type="title" id=toc-4569>٤٥٥٩ </span>- عن عبد الرحمن بن زيد بن أسلم عن أبيه أن رسول الله صلى الله عليه وسلم قال: " ما السماوات السبع فى الكرسى إلا كدراهم سبعة ألقيت فى ترس ".
** لم
(العلو ٩١/١)
** مرسل
** قال الحافظ الذهبى فى " العلو " ١ / ٩١: هذا مرسل وعبد الرحمن ضعيف.','10','134',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4560,'<span data-type="title" id=toc-4570>٤٥٦٠ </span>- عن أبى ذر رضى الله عنه أن النبى صلى الله عليه وسلم قال: " يا أبا ذر ما السماوات عند الكرسى إلا كحلقة ملقاة بأرض فلاة وفضل العرش على الكرسى كفضل الفلاة على الحلقة ".
** لم
(العلو ٩٥/١)
** قال الحافظ الذهبى فى " العلو " ١ / ٩٥: إبراهيم ليس بشىء وقد وثق.','10','135',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4561,'<span data-type="title" id=toc-4571>٤٥٦١ </span>- عن ابن عباس رضى الله عنه فى قوله " عسى أن يبعثك ربك مقاما محمودا " قال: يقعده على العرش.
** لم
(العلو ٩٩/١)
** إسناده ساقط
** قال الحافظ الذهبى فى " العلو " ١ / ٩٩: إسناده ساقط وعمر هذا الرازى متروك وفيه جويبر. قال الحافظ الذهبى فى " العلو " ١ / ٩٩: هذا مشهور من قول مجاهد ويروى مرفوعا وهو باطل.','10','136',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4562,'<span data-type="title" id=toc-4572>٤٥٦٢ </span>- **
(الأذكار /)
**','10','137',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4563,'<span data-type="title" id=toc-4573>٤٥٦٣ </span>- عن جابر بن عبد الله رضى الله عنهما قال: سمعت رسول الله صلى الله عليه وسلم يقول: " أفضل الذكر لا إله إلا الله ".
** ت جه
(الأذكار ١٣/١)
** قال الإمام النووى فى " الأذكار " ١ / ١٣: قال الترمذى: حديث حسن.','10','138',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4564,'<span data-type="title" id=toc-4574>٤٥٦٤ </span>- عن سعد بن أبى وقاص رضى الله عنه: " أنه دخل مع رسول الله صلى الله عليه وسلم على امرأة وبين يديها نوى أو حصى تسبح به، فقال: ألا أخبرك بما هو أيسر عليك من هذا أو أفضل؟ فقال: سبحان الله عدد ما خلق فى السماء، وسبحان الله عدد ما خلق فى الأرض، وسبحان الله عدد ما بين ذلك، وسبحان الله عدد ما هو خالق، والله أكبر مثل ذلك، والحمد لله مثل ذلك، ولا إله
إلا الله مثل ذلك، ولا حول ولا قوة إلا بالله مثل ذلك ".
** د ت
(الأذكار ١٣/١)
** قال الإمام النووى فى " الأذكار " ١ / ١٤: قال الترمذى: حديث حسن.
** تعقيب: قال عبد القادر الأرناؤوط ١ / ١٤: ورواه أيضا ابن حبان فى صحيحه، كلهم من حديث عمرو بن الحارث عن سعيد بن أبى هلال عن خزيمة عن عائشة بنت سعد بن أبى وقاص عن أبيها سعد، وخزيمة غير منسوب عن عائشة بنت سعد لا يعرف كما قال الحافظ فى التقريب، ومع ذلك فقد حسنه الترمذى وصححه الحاكم ووافقه الذهبى، ولعل تحسين الترمذى له برواية أخرى عنده فى الدعوات من حديث هاشم بن سعد الكوفى عن كنانة مولى صفية عن صفية. وقال الترمذى: هذا حديث غريب لا نعرفه من حديث صفية إلا من هذا الوجه من حديث هاشم بن سعيد، وليس إسناده بمعروف، قال: وفى الباب عن ابن عباس.اهـ. وثبت من حديث ابن عباس عن جويرية، ولكن ليس فيه ذكر للحصى.','10','139',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4565,'<span data-type="title" id=toc-4575>٤٥٦٥ </span>- عن يسيرة الصحابية المهاجرة رضى الله عنها: " أن النبى صلى الله عليه وسلم " أمرهن أن يراعين بالتكبير والتقديس والتهليل وأن يعقدن بالأنامل مسؤولات مستنطقات ".
** د ت
(الأذكار ١٤/١)
** إسناده حسن','10','140',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4566,'<span data-type="title" id=toc-4576>٤٥٦٦ </span>- عن عبد الله بن عمر رضى الله عنهما قال: " رأيت رسول الله صلى الله عليه وسلم
يعقد التسبيح، وفى رواية بيمينه ".
** د ت ن
(الأذكار ١٤/١)
** إسناده حسن','10','141',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4567,'<span data-type="title" id=toc-4577>٤٥٦٧ </span>- عن عبد الله بن بسر الصحابى رضى الله عنه: " أن رجلا قال يا رسول الله إن شرائع الإسلام قد كثرت على فأخبرنى بشىء أتشبث به فقال: لا يزال لسانك رطبا من ذكر الله تعالى ".
** ت
(الأذكار ١٤/١)
** قال الإمام النووى فى " الأذكار " ١ / ١٤: قال الترمذى: حديث حسن.','10','142',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4568,'<span data-type="title" id=toc-4578>٤٥٦٨ </span>- عن أبى الدراء رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " ألا أنبئكم بخير أعمالكم وأزكاها عند مليككم وأرفعها فى درجاتكم وخير لكم من إنفاق الذهب والورق وخير لكم من أن تلقوا عدوكم فتضربوا أعناقهم ويضربوا أعناقكم؟ قالوا: بلى. قال: ذكر الله تعالى ".
** ت جه
(الأذكار ١٥/١)
** قال الإمام النووى فى " الأذكار " ١ / ١٥: قال الحاكم: هذا حديث صحيح الإسناد.','10','143',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4569,'<span data-type="title" id=toc-4579>٤٥٦٩ </span>- عن ابن مسعود رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " لقيت إبراهيم صلى الله عليه وسلم ليلة أسرى بى فقال: يا محمد أقرىء أمتك السلام وأخبرهم أن الجنة طيبة التربة عذبة الماء وأنها قيعان وأن غراسها: سبحان الله والحمد لله ولا إله إلا الله والله أكبر ".
** ت
(الأذكار ١٥/١)
** قال الإمام النووى فى " الأذكار " ١ / ١٥: قال الترمذى: حديث حسن.','10','144',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4570,'<span data-type="title" id=toc-4580>٤٥٧٠ </span>- عن جابر رضى الله عنه عن النبى صلى الله عليه وسلم قال: " من قال سبحان الله وبحمده غرست له نخلة فى الجنة ".
** ت
(الأذكار ١٥/١)
** قال الإمام النووى فى " الأذكار " ١ / ١٥: قال الترمذى: حديث حسن.','10','145',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4571,'<span data-type="title" id=toc-4581>٤٥٧١ </span>- عن أبى ذر رضى الله عنه قال: " قلت يا رسول الله أى الكلام أحب إلى الله تعالى؟ قال: ما اصطفى الله تعالى لملائكته: سبحان ربى وبحمده، سبحان ربى وبحمده ".
** ت
(الأذكار ١٥/١)
** قال الإمام النووى فى " الأذكار " ١ / ١٥: قال الترمذى: حديث حسن صحيح.','10','146',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4572,'<span data-type="title" id=toc-4582>٤٥٧٢ </span>- عن أبى هريرة رضى الله عنه عن النبى صلى الله عليه وسلم قال: " إذا استيقظ أحدكم فليقل الحمد لله الذى رد على روحى وعافانى فى جسدى وأذن لى بذكره ".
** سنى
(الأذكار ١٦/١)
** إسناده صحيح','10','147',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4573,'<span data-type="title" id=toc-4583>٤٥٧٣ </span>- عن أبى سعيد االخدرى رضى الله عنه قال: " كان رسول الله صلى الله عليه وسلم إذا استجد ثوبا سماه باسمه عمامة أو قميصا أو رداء ثم يقول: اللهم لك الحمد أنت كسوتنيه أسألك خيره وخير ما صنع له وأعوذ بك من شره وشر ما صنع له ".
** د ت ن
(الأذكار ١٧/١)
** صحيح
** قال الإمام النووى فى " الأذكار " ١ / ١٧: قال الترمذى: هذا حديث حسن.','10','148',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4574,'<span data-type="title" id=toc-4584>٤٥٧٤ </span>- عن عائشة رضى الله عنها قالت: " كانت يد رسول الله صلى الله عليه وسلم اليمنى لطهوره وطعامه، وكانت اليسرى لخلائه وماكان من أذى ".
** د
(الأذكار ١٨/١)
** إسناده صحيح','10','149',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4575,'<span data-type="title" id=toc-4585>٤٥٧٥ </span>- عن أبى هريرة رضى الله عنه عن رسول الله صلى الله عليه وسلم قال: " إذا لبستم وإذا توضأتم فابدؤوا بأيامنكم ".
** د ت جه هق
(الأذكار ١٨/١)
** حسن','10','150',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4576,'<span data-type="title" id=toc-4586>٤٥٧٦ </span>- عن أم سلمة رضى الله عنها وأسمها هند: " أن النبى صلى الله عليه وسلم كان إذا خرج من بيته قال: بسم الله توكلت على الله اللهم إنى أعوذ بك أن أضل أو أضل أو أزل أو أزل أو أظلم أو أظلم أو أجهل أو يجهل على ".
** د ت جه ن
(الأذكار ١٨/١)
** صحيح
** قال الإمام النووى فى " الأذكار " ١ / ١٨: قال الترمذى: حديث صحيح.','10','151',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4577,'<span data-type="title" id=toc-4587>٤٥٧٧ </span>- عن أنس رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " من قال - يعنى إذا خرج من بيته - بسم الله توكلت على الله ولا حول ولا قوة إلا
بالله يقال له هديت وكفيت ووقيت وتنحى عنه الشيطان ".
** د ت ن
(الأذكار ١٨/١)
** قال الإمام النووى فى " الأذكار " ١ / ١٩: قال الترمذى: حديث حسن.','10','152',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4578,'<span data-type="title" id=toc-4588>٤٥٧٨ </span>- عن أنس رضى الله عنه قال: قال لى رسول الله صلى الله عليه وسلم: " يابنى إذا دخلت على أهلك فسلم يكن بركة عليك وعلى أهل بيتك ".
** ت
(الأذكار ١٩/١)
** قال الإمام النووى فى " الأذكار " ١ / ١٩: قال الترمذى: حديث حسن صحيح.','10','153',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4579,'<span data-type="title" id=toc-4589>٤٥٧٩ </span>- عن أبى مالك الأشعرى رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم " إذا ولج الرجل بيته فليقل: اللهم إنى أسألك خير المولج وخير المخرج بسم الله ولجنا وبسم الله خرجنا وعلى الله ربنا توكلنا ثم ليسلم على أهله ".
** د
(الأذكار ١٩/١)
** قال الإمام النووى فى " الأذكار " ١ / ١٩: لم يضعفه أبو داود.
** تعقيب: قال عبد القادر الأرناؤوط ١ / ١٩: وهو حديث حسن.','10','154',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4580,'<span data-type="title" id=toc-4590>٤٥٨٠ </span>- عن أبى أمامة الباهلى عن رسول الله صلى الله عليه وسلم قال: " ثلاثة كلهم ضامن على الله عز وجل رجل خرج غازيا فى سبيل الله عز وجل فهو ضامن على الله عز وجل حتى يتوفاه فيدخله الجنة أو يرده بما نال من أجر وغنيمة ورجل راح إلى المسجد فهو ضامن على الله تعالى حتى يتوفاه فيدخله الجنة أو يرده بما نال من أجر وغنيمة ورجل دخل بيته بسلام فهو ضامن على الله سبحانه وتعالى ".
** د
(الأذكار ١٩/١)
** حسن','10','155',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4581,'<span data-type="title" id=toc-4591>٤٥٨١ </span>- عن عبد الله بن عمرو بن العاص رضى الله عنهما قال: " كان رسول الله صلى الله عليه وسلم إذا رجع من النهار إلى بيته يقول: الحمد لله الذى كفانى وآوانى، والحمد لله الذى أطعمنى وسقانى، والحمد لله الذى من على أسألك أن تجيرنى من النار ".
** سنى
(الأذكار ٢٠/١)
** إسناده ضعيف','10','156',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4582,'<span data-type="title" id=toc-4592>٤٥٨٢ </span>- عن على رضى الله عنه أن النبى صلى الله عليه وسلم قال: " ستر مابين أعين الجن وعورات بنى آدم إذا دخل الكنيف أن يقول: بسم الله ".
** ت
(الأذكار ٢١/١)
** قال الإمام النووى فى " الأذكار " ١ /٢١: قال الترمذى: إسناده ليس بالقوى.
** تعقيب: قال عبد القادر الأرناؤوط ١ / ٢١: لكن للحديث شواهد بمعناه.','10','157',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4583,'<span data-type="title" id=toc-4593>٤٥٨٣ </span>- عن المهاجر بن قنفذ رضى الله عنه قال: أتيت النبى صلى الله عليه وسلم وهو يبول فسلمت عليه فلم يرد حتى توضأ ثم اعتذر إلى وقال: إنى كرهت أن أذكر الله تعالى إلا على طهر ". أو قال على طهارة.
** د ن جه
(الأذكار ٢٢/١)
** صحيح','10','158',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4584,'<span data-type="title" id=toc-4594>٤٥٨٤ </span>- ثبت فى الصحيح أن رسول الله صلى الله عليه وسلم كان يقول: " غفرانك ".
** د ت
(الأذكار ٢٢/١)
** صحيح','10','159',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4585,'<span data-type="title" id=toc-4595>٤٥٨٥ </span>- عن أبى هريرة رضى الله عنه: عن النبى صلى الله عليه وسلم: " لا وضوء لمن لم يذكر اسم الله عليه ".
** د
(الأذكار ٢٢/١)
** ضعيف
** قال الإمام النووى فى " الأذكار " ١ / ٢٢: جاء فى التسمية أحاديث ضعيفة، ثبت عن أحمد بن حنبل رحمه الله أنه قال: لا أعلم فى التسمية فى الوضوء حديثا ثابتا، فمن الأحاديث حديث أبى هريرة. وقال الإمام النووى فى " الأذكار " ١ / ٢٢: ورويناه فى رواية سعيد بن زيد وأبى سعيد وعائشة وأنس بن مالك وسهل بن سعد رضى الله عنهم رويناها كلها فى " سنن البيهقى " وغيره، وضعفها كلها
البيهقى وغيره.
** تعقيب: قال عبد القادر الأرناؤوط ١ / ٢٢: قال الحافظ المنذرى فى " الترغيب والترهيب ": ولا شك أن الأحاديث التى
وردت فى التسمية وإن كان لا يسلم شىء منها عن مقال، فإنها تتعاضد بكثرة
طرقها وتكتسب قوة والله أعلم.اهـ. وكذلك قال العز بن جماعة: إن له طرقا
تقويه.','10','160',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4586,'<span data-type="title" id=toc-4596>٤٥٨٦ </span>- عن ابن عمر رضى الله عنهما: أن النبى صلى الله عليه وسلم قال: " من توضأ ثم قال: أشهد أن لا إله إلا الله وحده لا شريك له، وأشهد أن محمدا عبده ورسوله قبل أن يتكلم غفر له ما بين الوضؤين ".
** قط
(الأذكار ٢٣/١)
** إسناده ضعيف','10','161',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4587,'<span data-type="title" id=toc-4597>٤٥٨٧ </span>- عن أنس رضى الله عنه عن النبى صلى الله عليه وسلم قال: " من توضأ فأحسن الوضوء ثم قال ثلاث مرات: أشهد أن لا إله إلا الله وحده لا شريك له وأشهد أن محمدا عبده ورسوله فتحت له ثمانية أبواب الجنة من أيها شاء دخل ".
** حم جه سنى
(الأذكار ٢٣/١)
** إسناده ضعيف
** قال الإمام النووى فى " الأذكار " ١ / ٢٣: وروينا تكرير الشهادة: أن لا إله إلا الله ثلاث مرات فى كتاب ابن السنى من رواية عثمان بن عفان رضى الله عنه بإسناد ضعيف.','10','162',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4588,'<span data-type="title" id=toc-4598>٤٥٨٨ </span>- عن أبى موسى الأشعرى رضى الله عنه قال: أتيت رسول الله صلى الله عليه وسلم بوضوء فتوضأ فسمعته يدعو ويقول: " اللهم اغفر لى ذنبى ووسع لى فى
دارى وبارك لى فى رزقى " فقلت: يا نبى الله سمعتك تدعو بكذا وكذا قال: " وهل تركن من شىء؟ ".
** ن سنى
(الأذكار ٢٤/١)
** إسناده صحيح','10','163',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4589,'<span data-type="title" id=toc-4599>٤٥٨٩ </span>- عن بلال رضى الله عنه قال: كان رسول الله صلى الله عليه وسلم إذا خرج إلى الصلاة قال: " بسم الله آمنت بالله توكلت على الله لا حول ولا قوة إلا بالله اللهم بحق السائلين عليك وبحق مخرجى هذا فإنى لم أخرجه أشرا ولا بطرا ولا رياء ولا سمعة خرجت ابتغاء مرضاتك واتقاء سخطك أسألك أن تعيذنى من النار وأن تدخلنى الجنة ".
** سنى
(الأذكار ٢٥/١)
** ضعيف
** قال الإمام النووى فى " الأذكار " ١ / ٢٥: أحد رواته الوازع بن نافع العقيلى وهو متفق على ضعفه وإنه منكر الحديث وروينا فى كتاب ابن السنى معناه من رواية عطية العوفى عن أبى سعيد الخدرى رضى الله عنه عن رسول الله صلى الله عليه وسلم وعطية أيضا ضعيف.
** تعقيب: قال عبد القادر أرناؤوط ١ / ٢٥: وهو كما قال وقد أبعد المصنف رحمه الله فالحديث قد رواه ابن ماجه رقم " ٧٧٨ " فى المساجد والجماعات وأحمد فى المسند ٣ / ٢١ من حديث فضيل بن مرزوق عن عطية بن سعد العوفى عن أبى سعيد الخدرى وإسناده ضعيف. وقد حسنه الحافظ فى تخريج الإذكار، ونسبه لأحمد وابن ماجه وابن خزيمة فى كتاب التوحيد وأبى نعيم الأصبهانى قال: فى كتاب الصلاة لأبى نعيم عن فضيل بن عطية قال: حدثنى ... فذكره لكن لم يرفعه فقد أمن بذلك تدليس عطية العوفى. وقال الحافظ: وقد عجبت للشيخ - يعنى النووى - كيف اقتصر على سوق
رواية بلال دون أبى سعيد وعزو رواية أبى سعيد لابن السنى دون ابن ماجه.','10','164',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4590,'<span data-type="title" id=toc-4600>٤٥٩٠ </span>- عن عبد الله بن عمرو بن العاص رضى الله عنهما: عن النبى صلى الله عليه وسلم أنه كان إذا دخل المسجد يقول: " أعوذ بالله العظيم وبوجهه الكريم وسلطانه القديم من الشيطان الرجيم " قال: " فإذا قال ذلك قال الشيطان: حفظ منى سائر اليوم ".
** د
(الأذكار ٢٦/١)
** حسن إسناده جيد','10','165',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4591,'<span data-type="title" id=toc-4601>٤٥٩١ </span>- عن أبى هريرة رضى الله عنه أن رسول الله صلى الله عليه وسلم قال: " إذا رأيتم من يبيع أو يبتاع فى المسجد فقولوا:لا أربح الله تجارتك وإذا رأيتم من ينشد فيه ضالة فقولوا: لا رد الله عليك ".
** ت
(الأذكار ٢٧/١)
** قال الإمام النووى فى " الأذكار " ١ / ٢٧: قال الترمذى: حديث حسن.','10','166',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4592,'<span data-type="title" id=toc-4602>٤٥٩٢ </span>- عن عائشة رضى الله عنها: أن رسول الله صلى الله عليه وسلم: كان إذا سمع المؤذن يتشهد، قال " وأنا وأنا ".
** د
(الأذكار ٣١/١)
** إسناده صحيح','10','167',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4593,'<span data-type="title" id=toc-4603>٤٥٩٣ </span>- عن أنس رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " لا يرد الدعاء بين الأذان والإقامة ".
** د ت ن سنى
(الأذكار ٣٢/١)
** قال الإمام النووى فى " الأذكار " ١ / ٣٢: قال الترمذى: حديث حسن صحيح.','10','168',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4594,'<span data-type="title" id=toc-4604>٤٥٩٤ </span>- عن عبد الله بن عمرو بن العاص رضى الله عنهما: أن رجلا قال: يا رسول الله إن المؤذنين يفضلوننا؟ فقال رسول الله صلى الله عليه وسلم: " قل كما يقولون فإذا انتهيت فسل تعطه ".
** د
(الأذكار ٣٢/١)
** قال الإمام النووى فى " الأذكار " ١ / ٣٢: لم يضعفه أبو داود.
** تعقيب: قال عبد القادر الأرناؤوط ١ / ٣٢: وهو حديث حسن، حسنه الحافظ وغيره.','10','169',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4595,'<span data-type="title" id=toc-4605>٤٥٩٥ </span>- عن سهل بن سعد رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " ثنتان لا تردان أو قل ما تردان: الدعاء عند النداء وعند اليأس حين يلجم بعضهم بعضا ".
** د
(الأذكار ٣٢/١)
** إسناده صحيح','10','170',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4596,'<span data-type="title" id=toc-4606>٤٥٩٦ </span>- قال رسول الله صلى الله عليه وسلم: " اطلبوا استجابة الدعاء عند التقاء الجيوش وإقامة الصلاة ونزول الغيث ".
** شفع الأم
(الأذكار ٣٣/١)
** مرسل
** تعقيب: قال عبد القادر الأرناؤوط ١/ ٣٣: رواه الشافعى فى الأم فى آخر الإستسقاء عن مكحول أن ... رسول الله صلى الله عليه وسلم ... إلخ، وهو مرسل أو معضل، لأن جل رواية مكحول عن التابعين، قال الحافظ في تخريج الأذكار: وله شاهد عن عطاء بن أبى رباح قال: تفتح السماء عند ثلاث خلال فتحروا فيها الدعاء، فذكر مثل مرسل مكحول أخرجه سعيد بن منصور فى سننه، وهو مقطوع جيد، له حكم المرسل، لأن مثله لا يقال بالرأى.','10','171',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4597,'<span data-type="title" id=toc-4607>٤٥٩٧ </span>- عن عائشة رضى الله عنها: كان النبى صلى الله عليه وسلم إذا افتتح الصلاة قال: " سبحانك اللهم وبحمدك وتبارك اسمك وتعالى جدك ولا إله غيرك ".
** ت د جه ن هق
(الأذكار ٣٥/١)
** إسناده ضعيف
** قال الإمام النووى فى " الأذكار " ١ / ٣٥: ضعفه أبو داود والترمذى والبيهقى وغيرهم. ورواه أبو داود والترمذى والنسائى وابن ماجه والبيهقى من رواية أبى سعيد الخدرى وضعفه. وقال البيهقى: وروى الأستفتاح بسبحانك اللهم وبحمدك عن ابن مسعود مرفوعا وعن أنس مرفوعا وكلها ضعيفة. قال: وأصح ما روى فيها
عن عمر بن الخطاب.
** تعقيب: قال عبد القادر الأرناؤوط ١ / ٣٥: ولكن بمجموعها يقوى الحديث، وقد حسنه الحافظ بن حجر فى تخريج الأذكار فقال بعد تخريج الحديث باسناده من طرق: حديث حسن. أخرجه أبو داود والترمذى والنسائى وابن ماجه والبيهقى. قال عبد القادر الأرناؤوط ١ / ٣٥: وصححه الحاكم ووافقه الذهبى.','10','172',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4598,'<span data-type="title" id=toc-4608>٤٥٩٨ </span>- عن عمر بن الخطاب رضى الله عنه: " أنه كبر ثم قال: سبحانك اللهم وبحمدك تبارك اسمك وتعالى جدك ولا إله غيرك ".
** هق
(الأذكار ٣٥/١)
** قال الإمام النووى فى " الأذكار " ١ / ٣٥: قال البيهقى: وأصح ما روى فيه عن عمر بن الخطاب رضى الله عنه.','10','173',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4599,'<span data-type="title" id=toc-4609>٤٥٩٩ </span>- عن على رضى الله عنه قال: كان النبى صلى الله عليه وسلم إذا استفتح الصلاة قال: " لا إله إلا أنت سبحانك ظلمت نفسى وعملت سوءا فاغفر لى إنه لا يغفر الذنوب إلا أنت وجهت وجهى ٠٠٠٠٠ الخ ".
** هق
(الأذكار ٣٦/١)
** ضعيف
** قال الإمام النووى فى " الأذكار " ١ / ٣٦: الحارث الأعور متفق على ضعفه، وكان الشعبى يقول: الحارث كذاب.
** تعقيب: قال عبد القادر الأرناؤوط ١ / ٣٦: كان الشعبى يكذبه فى رأيه، لا فى حديثه.','10','174',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4600,'<span data-type="title" id=toc-4610>٤٦٠٠ </span>- قال صلى الله عليه وسلم: " لا تجزى صلاة لا يقرأ فيها بفاتحة الكتاب ".
** خز حب
(الأذكار ٣٨/١)
** صحيح
** قال الإمام النووى فى " الأذكار " ١ / ٣٨: رواه ابن خزيمة وأبو حاتم فى صحيحيهما بالاسناد الصحيح وحكما بصحته.','10','175',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4601,'<span data-type="title" id=toc-4611>٤٦٠١ </span>- عن عوف بن مالك رضى الله عنه قال: " قمت مع رسول الله صلى الله عليه وسلم فقام فقرأ (سورة البقرة) لا يمر بآية رحمة إلا وقف وسأل ولايمر بآية عذاب
إلا وقف وتعوذ، قال: ثم ركع بقدر قيامه، يقول فى ركوعه: سبحان ذى الجبروت
والملكوت والكبرياء والعظمة، ثم قال فى سجوده مثل ذلك ".
** د ن تم
(الأذكار ٤٣/١)
** صحيح
** قال الإمام النووى فى " الأذكار " ١ / ٤٣: رواه الترمذى فى كتاب الشمائل بأسانيد صحيحة.
** تعقيب: * قال ابن حجر فى نتائج الأفكار ٢ / ٧٣: فيه نظر.','10','176',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4602,'<span data-type="title" id=toc-4612>٤٦٠٢ </span>- عن عائشة رضى الله عنها قالت: كان رسول الله صلى الله عليه وسلم يقول فى سجود القرآن: " سجد وجهى للذى خلقه وشق سمعه وبصره بحوله وقوته ".
** د ت ن ك
(الأذكار ٤٦/١)
** قال الإمام النووى فى " الأذكار " ١ / ٤٧: قال الترمذى حديث صحيح. وزاد الحاكم فتبارك الله أحسن الخالقين قال: وهذه زيادة صحيحة على شرط الصحيحين. وأما قوله اللهم اجعلها لى ذخرا ٠٠الخ فرواه الترمذى مرفوعا من رواية ابن عباس رضى الله عنهما بإسناد حسن، وقال الحاكم حديث صحيح.','10','177',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4603,'<span data-type="title" id=toc-4613>٤٦٠٣ </span>- عن ابن عباس فى حديث مبيته عند خالته ميمونة رضى الله عنها وصلاة النبى صلى الله عليه وسلم فى الليل فذكره قال: وكان إذا رفع رأسه من السجدة قال: " رب اغفر لى وارحمنى واجبرنى وارفعنى وارزقنى واهدنى ".
** هق د
(الأذكار ٤٧/١)
** إسناده حسن
** قال الإمام النووى فى " الأذكار " ١ / ٤٧: وفى رواية أبو داود " وعافنى ".
** تعقيب: يراجع نتائج الأفكار ٢ / ١١٨','10','178',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4604,'<span data-type="title" id=toc-4614>٤٦٠٤ </span>- عن أنس رضى الله عنه: " أن رسول الله صلى الله عليه وسلم لم يزل يقنت فى الصبح حتى فارق الدنيا ".
** ك / الأربعين
(الأذكار ٤٨/١)
** قال الإمام النووى فى " الأذكار " ١ / ٤٨: قال الحاكم: حديث صحيح.
** تعقيب: قال عبد القادر الأرناؤوط ١ / ٤٨: وأخرجه الحاكم أيضا فى كتاب القنوت. وقال عبد القادر الأرناؤوط ١ / ٤٨: أيضا صححه الحاكم على طريقته فى تصحيح ما هو حس٤ن عند غيره، فالصواب أن الحديث حسن.','10','179',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4605,'<span data-type="title" id=toc-4615>٤٦٠٥ </span>- عن الحسن بن على رضى الله عنهما قال: علمنى رسول الله صلى الله عليه وسلم كلمات أقولهن فى الوتر " اللهم اهدنى فيمن هديت وعافنى فيمن عافيت وتولنى فيمن توليت وبارك لى فيما أعطيت وقنى شر ماقضيت فإنك تقضى ولا يقضى عليك وإنه لا يذل من واليت تباركت ربنا وتعاليت ".
** د ت ن جه هق
(الأذكار ٤٨/١)
** صحيح
** قال الإمام النووى فى " الأذكار " ١ / ٤٩: قال الترمذى: هذا حديث حسن. قال الإمام النووى فى " الأذكار " ١ / ٤٩: ويستحب أن يقول عقيب هذا الدعاء اللهم صلى على محمد وعلى آل محمد وسلم فقد جاء فى رواية النسائى فى هذا الحديث بإسناد حسن: " وصلى الله على النبى ".
** تعقيب: قال عبد القادر الأرناؤوط ١ / ٤٩: تعليقا على زيادة النسائى: قال الحافظ فى تخريج الأذكار (٢ / ١٤٦) : هذاالحديث أصله حسن وروى من طرق متعددة عن الحسن لكن هذه الزيادة فى هذا السند غريبة لا تثبت وإن سنده لا يخلو إما عن راو مجهول أو انقطاع فى السند. قال عبد القادر الأرناؤوط: وقد بالغ المصنف رحمه الله فقال فى شرح المهذب إنه سند صحيح أو حسن وكذا فى الخلاصة.','10','180',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4606,'<span data-type="title" id=toc-4616>٤٦٠٦ </span>- عن ثوبان رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " لا يؤم عبد قوما فيخص نفسه بدعوة دونهم فإن فعل فقد خانهم ".
** د ت
(الأذكار ٥٠/١)
** قال الإمام النووى فى " الأذكار " ١ / ٥٠: قال الترمذى: حديث حسن.','10','181',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4607,'<span data-type="title" id=toc-4617>٤٦٠٧ </span>- عن القاسم قال: علمتنى عائشة رضى الله عنها قالت: هذا تشهد رسول الله صلى الله عليه وسلم " التحيات لله والصلوات والطيبات، السلام عليك أيها النبى ورحمة الله وبركاته، السلام علينا وعلى عباد الله الصالحين، أشهد أن لا إله إلا الله وأشهد أن محمدا عبده ورسوله ".
** هق
(الأذكار ٥١/١)
** إسناده جيد
** تعقيب: قال عبد القادر الأرناؤوط ١ / ٥١: قال الحافظ فى تخريج الأذكار ٢ / ١٦٤: فى سنده محمد بن صالح بن دينار وهو مختلف فيه فوثقه أحمد وأبو داود وغيرهما وقال أبو حاتم الرازى ليس بقوى وكذلك لينه الدارقطنى وأما ابنه صالح فلم أجد له ذكرا بجرح ولا تعديل ولا ترجمة فى كتاب الرجال كالبخارى وابن أبى حاتم وابن حبان وابن عدى وهو فى درجة المستور فلم أعرف مستند الشيخ - يعنى النووى - فى وصف هذا الإسناد بالجودة وقد قال البيهقى بعد تخريجه: الصحيح عن عائشة موقوفا فأشار إلى شذوذ الزيادة والعلم عند الله.','10','182',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4608,'<span data-type="title" id=toc-4618>٤٦٠٨ </span>- عن عبد الرحمن بن عبد القارى أنه سمع عمر بن الخطاب رضى الله عنه وهو على المنبر وهو يعلم الناس التشهد يقول: " قولوا التحيات لله الزاكيات لله الطيبات لله السلام عليك أيها النبى ورحمة الله وبركاته السلام علينا وعلى عباد الله الصالحين أشهد أن لاإله إلا الله وحده لا شريك له وأشهد أن محمدا عبده ورسوله ".
** ما هق
(الأذكار ٥٢/١)
** إسناده صحيح
** تعقيب: قال عبد القادر الأرناؤوط ١ / ٥٢: هذا وإن كان موقوفا فهو فى حكم المرفوع لأن ذلك محال يقال بالرأى.','10','183',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4609,'<span data-type="title" id=toc-4619>٤٦٠٩ </span>- عن عائشة رضى الله عنها أنها كانت تقول إذا تشهدت: " التحيات الطيبات الصلوات الزاكيات لله أشهد أن لا إله إلا الله وأن محمدا عبده ورسوله السلام عليك أيها النبى ورحمة الله وبركاته السلام علينا وعلى عباد الله الصالحين ".
** ما هق
(الأذكار ٥٢/١)
** إسناده صحيح','10','184',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4610,'<span data-type="title" id=toc-4620>٤٦١٠ </span>- عن مالك عن نافع: عن ابن عمر رضى الله عنهما أنه كان يتشهد فيقول " بسم الله التحيات لله، الصلوات لله، الزاكيات لله، السلام على النبى ورحمة الله وبركاته، السلام علينا وعلى عباد الله الصالحين شهدت أن لا إله إلا الله، شهدت أن محمدا رسول الله ".
** ما هق
(الأذكار ٥٢/١)
** إسناده صحيح','10','185',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4611,'<span data-type="title" id=toc-4621>٤٦١١ </span>- عن ابن مسعود رضى الله تعالى عنه قال: " من السنة أن يخفى التشهد ".
** د ت هق
(الأذكار ٥٤/١)
** قال الإمام النووى فى " الأذكار " ١ / ٥٤: قال الترمذى: حديث حسن. قال الحاكم: صحيح اهـ. وإذا قال الصحابى: من السنة كذا، كان بمعنى قوله قال رسول الله صلى الله عليه وسلم.','10','186',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4612,'<span data-type="title" id=toc-4622>٤٦١٢ </span>- عن أبى صالح ذكوان عن بعض أصحاب النبى صلى الله عليه وسلم قال: قال النبى صلى الله عليه وسلم لرجل: " كيف تقول فى الصلاة؟ قال: أتشهد وأقول: اللهم إنى أسألك الجنة وأعوذ بك من النار أما إنى لا أحسن دندنتك ولا دندنة معاذ فقال النبى صلى الله عليه وسلم: حولها ندندن ".
** د
(الأذكار ٥٦/١)
** إسناده صحيح','10','187',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4613,'<span data-type="title" id=toc-4623>٤٦١٣ </span>- عن أبى أمامة رضى الله عنه قال: قيل لرسول الله صلى الله عليه وسلم أى الدعاء أسمع؟ قال: " جوف الليل الآخر ودبر الصلوات المكتوبات ".
** ت
(الأذكار ٥٧/١)
** قال الإمام النووى فى " الأذكار " ١ / ٥٧: قال الترمذى: حديث حسن.','10','188',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4614,'<span data-type="title" id=toc-4624>٤٦١٤ </span>- عن عبد الله بن عمرو رضى الله عنهما عن النبى صلى الله عليه وسلم قال: خصلتان أو خلتان لا يحافظ عليهما عبد مسلم إلا دخل الجنة، هما يسير ومن يعمل بهما قليل يسبح الله تعالى دبر كل صلاة عشرا ويحمد عشرا ويكبر عشرا فذلك خمسون
ومائة باللسان وألف وخمسمائة فى الميزان ويكبر أربعا وثلاثين إذا أخذ مضجعه ويحمد ثلاثا وثلاثين ويسبح ثلاثا وثلاثين فذلكم مائة باللسان وألف بالميزان قال: فلقد رأيت رسول الله صلى الله عليه وسلم يعقدها بيده قالوا:
يا رسول الله كيف هما يسير ومن يعمل بهما قليل قال: يأتى أحدكم يعنى الشيطان
- الشيطان - فى منامه فينومه قبل أن يقوله ويأتيه فى صلاته فيذكره حاجة قبل
أن يقولها ".
** د ت ن
(الأذكار ٥٩/١)
** إسناده صحيح
** قال الإمام النووى فى " الأذكار " ١ / ٥٩: إسناده صحيح إلا أن ففيه عطاء بن السائب وفيه اختلاف لسبب اختلاطه وقد أشار أيوب السختيانى إلى صحة حديثه هذا.
** تعقيب: قال عبد القادر الأرناؤوط ١ / ٥٩: قال بن علان فى شرح الأذكار: قال الحافظ فى تخريج الأذكار ٢ / ٢٦٧ وقول الشيخ - يعنى النووى - إلا أن فيه عطاء بن السائب ٠٠٠الخ، لا أثر له فإن شعبة والثورى وحماد بن زيد سمعوا من عطاء قبل الإختلاف وقد اتفقوا على أن الثقة إذا تميز ما حدث قبل اختلاطه مما بعده قبل، وهذا من ذلك ويؤيده قوله: وأشار أيوب ٠٠٠ الخ.','10','189',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4615,'<span data-type="title" id=toc-4625>٤٦١٥ </span>- عن معاذ بن جبل رضى الله عنه أن رسول الله صلى الله عليه وسلم أخذ بيده وقال: " يا معاذ والله إنى لا حبك ثم قال: أوصيك يا معاذ لا تدعن فى دبر كل صلاة
تقول: اللهم أعنى على ذكرك وشكرك وحسن عبادتك ".
** د ن
(الأذكار ٦٠/١)
** إسناده صحيح','10','190',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4616,'<span data-type="title" id=toc-4626>٤٦١٦ </span>- عن فضالة بن عبيد قال: قال رسول الله صلى الله عليه وسلم: " إذا صلى أحدكم فليبدأ بتحميد الله تعالى والثناء عليه ثم يصلى على النبى صلى الله عليه وسلم ثم يدعو بما شاء ".
** سنى
(الأذكار ٦١/١)
** إسناده ضعيف
** تعقيب: قال عبد لقادر الأرناؤوط ١ / ٦١: قال الحافظ ابن حجر فى تخريج الأذكار كما فى شرح الأذكار: هذا بالنسبة لسند ابن السنى وإلا فقد أخرج الخبر أبو داود وصححه والترمذى وابن خزيمة وابن حبان والحاكم وقال: هو على شرط مسلم وفى موضع هو على شرطهما أى الشيخين ولا أعرف له علة. وقال الحافظ بعد تخريجه من طريقين: هذا حديث صحيح أخرجه أحمد وإسحاق فى مسنديهما وأبو داود والترمذى وابن خزيمة وابن حبان والحاكم. وللحديث قصة رواها من ذكر، وهى قول فضالة بن عبيد: إن النبى صلى الله عليه وسلم رأى رجلا ٠٠٠الخ، وأخرجه ابن السنى مقتصرا على الحديث دون القصة
قال الحافظ: وليس فى سنده من يوصف بالضعف إلا ابن لهيعة وكأن المصنف ضعفه بسببه وابن لهيعة لم ينفرد به بل رواه غيره كما ترى، وعجيب من اقتصاره
- يعنى النووى - على تضعيف هذا السند دون غيره من الأحاديث التر أوردها قبل
من كتاب ابن السنى مع أن أكثرها ضعيف، وهذا صحيح المتن رواته ثقات مخرج
لهم فى الصحيح إلا واحدا، اتفقوا على ضعفه وقد ذكر المصنف فى المجموع
الحديث وقال: رواه أبو داود وابن ماجه وابن حبان والحاكم. قال الترمذى: حسن صحيح وقال الحاكم: صحيح على شرط مسلم فكأنه - يعنى النووى
- لم يستحضر ذلك هنا.','10','191',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4617,'<span data-type="title" id=toc-4627>٤٦١٧ </span>- عن أنس رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " من صلى الفجر فى جماعة ثم قعد يذكر الله تعالى حتى تطلع الشمس ثم صلى ركعتين كانت كأجر حجة وعمرة تامة تامة تامة ".
** ت
(الأذكار ٦١/١)
** قال الإمام النووى فى " الأذكار " ١ / ٦١: قال الترمذى: حديث حسن.','10','192',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4618,'<span data-type="title" id=toc-4628>٤٦١٨ </span>- عن أبى ذر رضى الله عنه أن رسول الله صلى الله عليه وسلم قال: " من قال فى دبر صلاة الصبح وهو ثان رجليه قبل أن يتكلم: لا إله إلا الله وحده لا شريك له له الملك وله الحمد يحيى ويميت وهو على كل شىء قدير عشر مرات كتب له عشر حسنات ومحى عنه عشر سيئات ورفع له عشر درجات وكان يومه ذلك فى حرز من كل مكروه وحرس من الشيطان ولم ينبغ لذنب أن يدركه فى ذلك اليوم إلا الشرك بالله تعالى ".
** ت
(الأذكار ٦١/١)
** قال الإمام النووى فى " الأذكار " ١ / ٦١: قال الترمذى: حسن وفى بعض النسخ: صحيح.
** تعقيب: قال عبد القادر الأرناؤوط ١ / ٦١: الحديث حسن دون التقيد بقوله وهو ثان رجليه وقد حسنه الحافظ فى تخريج الإحياء ورواه ابن حبان من حديث أبى أيوب مقيدا بدبر الصلاة، وليس فيه ثنى الرجلين.','10','193',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4619,'<span data-type="title" id=toc-4629>٤٦١٩ </span>- عن عبد الله بن خبيب رضى الله عنه قال: " خرجنا فى ليلة مطيرة وظلمة شديدة نطلب النبى صلى الله عليه وسلم ليصلى لنا فأدركناه فقال: قل. فلم أقل شيئا
ثم قال: قل، فلم أقل شيئا ثم قال قل فقلت: يا رسول الله ما أقول؟ قال قل هو الله أحد والمعوذتين حين تمسى وحين تصبح ثلاث مرات تكفيك من كل شىء ".
** د ت ن
(الأذكار ٦٣/١)
** إسناده صحيح
** قال الإمام النووى فى " الأذكار " ١ / ٦٣: قال الترمذى: حديث حسن صحيح.','10','194',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4620,'<span data-type="title" id=toc-4630>٤٦٢٠ </span>- عن أبى هريرة رضى الله عنه عن النبى صلى الله عليه وسلم " أنه كان يقول إذا أصبح: اللهم بك أصبحنا وبك أمسينا وبك نحيا وبك نموت وإليك النشور. وإذا أمسى قال: اللهم بك أمسينا وبك نحيا وبك نموت وإليك النشور ".
** د ت جه
(الأذكار ٦٣/١)
** إسناده صحيح
** قال الإمام النووى فى " الأذكار " ١ / ٦٣: قال الترمذى: حديث حسن.','10','195',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4621,'<span data-type="title" id=toc-4631>٤٦٢١ </span>- عن أبى هريرة رضى الله عنه: أن أبا بكر الصديق رضى الله عنه قال: يارسول الله مرنى بكلمات أقولهن إذا أصبحت وإذا أمسيت قال: " قل اللهم فاطر السموات
والأرض عالم الغيب والشهادة رب كل شىء ومليكه أشهد أن لا إله إلا أنت أعوذ بك من شر نفسى وشر الشيطان وشركه قال: قلها إذا أصبحت وإذا أمسيت وإذا أخذت مضجعك ".
** ت د
(الأذكار ٦٤/١)
** إسناده صحيح
** قال الإمام النووى فى " الأذكار " ١ / ٦٤: قال الترمذى: حديث حسن صحيح.','10','196',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4622,'<span data-type="title" id=toc-4632>٤٦٢٢ </span>- عن عثمان بن عفان رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم " ما عبد يقول فى صباح كل يوم ومساء كل ليلة: بسم الله الذى لا يضر مع اسمه شىء فى الأرض ولا فى السماء وهو السميع العليم ثلاث مرات لم يضره شىء ".
** د ت
(الأذكار ٦٥/١)
** قال الإمام النووى فى " الأذكار " ١ / ٦٥: قال الترمذى: هذا حديث حسن صحيح.','10','197',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4623,'<span data-type="title" id=toc-4633>٤٦٢٣ </span>- عن ثوبان رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " من قال حين يمسى: رضيت بالله ربا وبالإسلام دينا وبمحمد صلى الله عليه وسلم نبيا كان حقا على الله تعالى أن يرضيه ".
** د ن ت ك
(الأذكار ٦٥/١)
** إسناده جيد
** قال الإمام النووى فى " الأذكار " ١ / ٦٥: فى إسناده سعيد بن المرذبان أبو سعد البقال الكوفى مولى حذيفة ابن اليمان وهو ضعيف باتفاق الحفاظ وقد قال الترمذى: هذا حديث حسن صحيح غريب من هذا الوجه فلعله صح عنده من وجه آخر وقد رواه أبو داود والنسائى بأسانيد جيدة عن رجل خدم النبى صلى الله عليه وسلم بلفظه فثبت أصل الحديث ولله الحمد وقد رواه الحافظ أبو عبد الله فى " المستدرك على الصحيحين " وقال حديث صحيح
الإسناد.
** تعقيب: قال عبد القادر الأرناؤوط تعليقا على قوله ضعيف باتفاق الحفاظ ١ / ٦٥: لم يتفقوا على ضعفه وإنما ضعفه الجمهور.','10','198',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4624,'<span data-type="title" id=toc-4634>٤٦٢٤ </span>- عن أنس رضى الله عنه أن رسول الله صلى الله عليه وسلم قال: " من قال حين يصبح أو يمسى: اللهم إنى أصبحت أشهدك وأشهد حملة عرشك وجميع خلقك أنك أنت الله الذى لا إله إلا أنت وأن محمدا عبدك ورسولك أعتق الله ربعه من النار فمن قالها مرتين أعتق الله نصفه من النار ومن قالها ثلاثا أعتق الله تعالى ثلاثة أرباعه فإن قالها أربعا أعتقه الله تعالى من النار ".
** د
(الأذكار ٦٥/١)
** إسناده جيد
** قال الإمام النووى فى " الأذكار " ١ / ٦٥: روينا فى سنن أبى داود باسناد جيد لم يضعفه عن أنس رضى الله عنه.
** تعقيب: قال عبد القادر الأرناؤوط ١ / ٦٥: قال الحافظ فى تخريج الأذكار: فى وصف هذا الإسناد بأنه جيد نظر، ولعل أبو داود إنما سكت عنه لمجيئه من وجه آخر عن أنس، ومن أجله قلت: إنه حسن. اهـ.','10','199',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4625,'<span data-type="title" id=toc-4635>٤٦٢٥ </span>- عن عبد الله بن غنام البياض الصحابى رضى الله عنه أن رسول الله صلى الله عليه
وسلم قال: " من قال حين يصبح اللهم ما أصبح بى من نعمة فمنك وحدك لا شريك لك، لك الحمد ولك الشكر فقد أدى شكر يومه ومن قال مثل ذلك حين يمسى فقد أدى شكر ليلته ".
** د
(الأذكار ٦٦/١)
** إسناده جيد
** قال الإمام النووى فى " الأذكار " ١ / ٦٦: لم يضعفه أبو داود.
** تعقيب: قال عبد القادر الأرناؤوط ١ / ٦٦: حديث حسن.','10','200',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4626,'<span data-type="title" id=toc-4636>٤٦٢٦ </span>- عن ابن عمر رضى الله عنهما قال: لم يكن النبى صلى الله عليه وسلم يدع هؤلاء الدعوات حين يمسى وحين يصبح: " اللهم إنى أسألك العافية فى الدنيا والآخرة، اللهم إنى أسألك العفو والعافية فى دينى ودنياى وأهلى ومالى اللهم استر عوراتى وآمن روعاتى اللهم احفظنى من بين يدى ومن خلفى وعن يمينى وعن شمالى ومن فوقى وأعوذ بعظمتك أن أغتال من تحتى ".
** د ن جه
(الأذكار ٦٦/١)
** إسناده صحيح
** قال الإمام النووى فى " الأذكار " ١ / ٦٦: قال الحاكم أبو عبد الله: هذا حديث صحيح الإسناد.
** تعقيب: قال عبد القادر الأرناؤوط ١ / ٦٦: قال الحافظ: وقول الشيخ - يعنى النووى - بالأسانيد الصحيحة يوهم أن له طرقا
عن ابن عمر وليس كذلك. وقال عبد القادر الأرناؤوط ١ / ٦٦: تعليقا على تصحيح الحاكم: ووافقه الذهبى وهو حديث صحيح.','10','201',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4627,'<span data-type="title" id=toc-4637>٤٦٢٧ </span>- عن على رضى الله عنه عن رسول الله صلى الله عليه وسلم: أنه كان يقول عند مضجعه: " اللهم إنى أعوذ بوجهك الكريم وبكلماتك التامة من شر ما أنت آخذ بناصيته، اللهم أنت تكشف المغرم والمأثم، اللهم لا يهزم جندك ولا يخلف وعدك ولا ينفع ذا الجد منك الجد سبحانك وبحمدك ".
** د ن
(الأذكار ٦٦/١)
** إسناده صحيح
** تعقيب: قال عبد القادر الأرناؤوط ١ / ٦٦: بل هو حديث حسن فان فى سنده علتان تحطه عن مرتبة الصحيح كماقال الحافظ فى تخريجه.','10','202',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4628,'<span data-type="title" id=toc-4638>٤٦٢٨ </span>- عن أبى عياش رضى الله عنه أن رسول الله صلى الله عليه وسلم قال: " من قال إذا أصبح لا إله إلا الله وحده لا شريك له له الملك وله الحمد وهو على كل شىء قدير كان له عدل رقبة من ولد إسماعيل صلى الله عليه وسلم وكتب له عشر حسنات وحط عنه عشر سيئات ورفع له عشر درجات وكان فى حرز من الشيطان حتى يمسى وإن قالها إذا أمسى كان له مثل ذلك حتى يصبح ".
** د جه
(الأذكار ٦٧/١)
** إسناده جيد
** تعقيب: قال عبد القادر الأرناؤوط ١ / ٦٧: قال الحافظ فى تخريج الأذكار وفى قول الشيخ - يعنى النووى - بأسانيد نظر فانه
ليس له عند أبى داود وابن ماجه إلا سند حماد إلى منتهاه. وقال عبد القادر الأرناؤوط ١ / ٦٧: وهو حديث صحيح.','10','203',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4629,'<span data-type="title" id=toc-4639>٤٦٢٩ </span>- عن أبى مالك الأشعرى رضى الله عنه أن رسول الله صلى الله عليه وسلم قال: " إذا أصبح أحدكم فليقل: أصبحنا وأصبح الملك لله رب العالمين اللهم إنى أسألك خير هذا اليوم فتحه ونصره ونوره وبركته وهداه وأعوذ بك من شر ما فيه وشر ما بعده ثم إذا أمسى فليقل مثل ذلك ".
** د
(الأذكار ٦٧/١)
** قال الإمام النووى فى " الأذكار " ١ / ٦٧: روينا فى سنن أبى داود بإسناد لم يضعفه.
** تعقيب: قال عبد القادر الأرناؤوط ١ / ٦٧: لم يضعفه أبو داود فى سننه، وقد ضعفه خارجها كما قال الحافظ والحديث حسن بشواهده.','10','204',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4630,'<span data-type="title" id=toc-4640>٤٦٣٠ </span>- عن ابن عباس رضى الله عنهما عن رسول الله صلى الله عليه وسلم أنه قال: " من قال حين يصبح: (فسبحان الله حين تمسون وحين تصبحون وله الحمد فى السموات والأرض وعشيا وحين تظهرون يخرج الحى من الميت ويخرج الميت من الحى ويحيى الأرض بعد موتها وكذلك تخرجون) ، أدرك مافاته فى يومه ذلك ومن قالهن حين يمسى أدرك مافاته فى ليلته ".
** د
(الأذكار ٦٧/١)
** قال الإمام النووى فى " الأذكار " ١ / ٦٧: لم يضعفه أبو داود، وقد ضعفه البخارى فى " تاريخه الكبير " وفى كتابه "
كتاب الضعفاء ".
** تعقيب: قال عبد القادر الأرناؤوط ١ / ٦٧: لكن للحديث شواهد بمعناه.','10','205',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4631,'<span data-type="title" id=toc-4641>٤٦٣١ </span>- عن عبد الله بن أبزى رضى الله عنه قال: كان رسول الله صلى الله عليه وسلم إذا أصبح قال: " أصبحنا على فطرة الإسلام وكلمة الإخلاص ودين نبينا محمد
صلى الله عليه وسلم وملة أبينا إبراهيم صلى الله عليه وسلم حنيفا مسلما وما كان من المشركين ".
** سنى
(الأذكار ٦٨/١)
** إسناده صحيح','10','206',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4632,'<span data-type="title" id=toc-4642>٤٦٣٢ </span>- عن معقل بن يسار رضى الله عنه عن النبى صلى الله عليه وسلم قال: " من قال حين يصبح ثلاث مرات: أعوذ بالله السميع العليم من الشيطان الرجيم وقرأ ثلاث آيات من سورة الحشر وكل الله تعالى به سبعين ألف ملك يصلون عليه حتى يمسى وإن مات فى ذلك اليوم مات شهيدا ومن قالها حين يمسى كان بتلك المنزلة ".
** ت سنى
(الأذكار ٦٨/١)
** إسناده ضعيف
** تعقيب: قال عبد القادر الأرناؤوط ١ / ٦٩: وفى سنده خالد بن طهمان وهو صدوق اختلط قبل موته بعشر سنين. قال الترمذى: هذا حديث غريب.','10','207',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4633,'<span data-type="title" id=toc-4643>٤٦٣٣ </span>- عن ابن عباس رضى الله عنهما أن رجلا شكا إلى رسول الله صلى الله عليه وسلم أنه تصيبه الآفات فقال له رسول الله صلى الله عليه وسلم: " قل إذا أصبحت بسم الله على نفسى وأهلى ومالى فإنه لا يذهب لك شىء فقالهن الرجل فذهبت عنه الآفات ".
** سنى
(الأذكار ٦٩/١)
** إسناده ضعيف','10','208',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4634,'<span data-type="title" id=toc-4644>٤٦٣٤ </span>- عن أبى هريرة رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " من قرأ (حم المؤمن) إلى: (إليه المصير) وآية الكرسى حين يصبح حفظ بهما حتى يمسى ومن قرأهما حين يمسى حفظ بهما حتى يصبح ".
** ت سنى
(الأذكار ٧٠/١)
** إسناده ضعيف
** تعقيب: قال عبد القادر الأرناؤوط ١ / ٧٠: فيه عبد الرحمن ابن أبى مليكه وهو ضعيف.','10','209',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4635,'<span data-type="title" id=toc-4645>٤٦٣٥ </span>- عن أبى سعيد الخدرى رضى الله عنه: كان رسول الله صلى الله عليه وسلم إذا طلعت عليه الشمس قال: " الحمد لله الذى جللنا اليوم عافيته وجاء بالشمس من
مطلعها اللهم أصبحت أشهد لك بما شهدت به لنفسك وشهدت به ملائكتك وحملة عرشك وجميع خلقك أنك أنت الله لا إله إلا أنت القائم بالقسط لا إله إلا أنت العزيز
الحكيم أكتب شهادتى بعد شهادة ملائكتك وأولى العلم اللهم أنت السلام ومنك
السلام وإليك السلام أسألك ياذا الجلال والإكرام أن تستجيب لنا دعوتنا وأن تعطينا رغبتنا وأن تغنينا عمن أغنيته عنا من خلقك اللهم أصلح لى دينى الذى هو
عصمة أمرى وأصلح لى دنياى التى فيها معيشتى وأصلح لى آخرتى التى إليها منقلبى ".
** سنى
(الأذكار ٧١/١)
** إسناده ضعيف','10','210',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4636,'<span data-type="title" id=toc-4646>٤٦٣٦ </span>- عن عبد الله بن السائب رضى الله عنه: " أن رسول الله صلى الله عليه وسلم كان يصلى أربعا بعد أن تزول الشمس قبل الظهر وقال إنها ساعة تفتح فيها أبواب السماء فأحب أن يصعد لى فيها عمل صالح ".
** ت
(الأذكار ٧٢/١)
** قال الإمام النووى فى " الأذكار " ١ / ٧٢: قال الترمذى: حديث حسن.','10','211',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4637,'<span data-type="title" id=toc-4647>٤٦٣٧ </span>- عن أنس رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " لأن أجلس مع قوم يذكرون الله عز وجل من صلاة العصر إلى إن تغرب الشمس أحب إلى من أن أعتق ثمانية من ولد إسماعيل ".
** سنى
(الأذكار ٧٢/١)
** إسناده ضعيف
** تعقيب: قال عبد القادر الأرناؤوط ١ / ٧٢: لكن للحديث شواهد بمعناه يقوى بها منها ما رواه أبو داود من حديث أنس وهو حديث حسن وبنحوه رواه أحمد فى المسند عن أبى أمامة.','10','212',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4638,'<span data-type="title" id=toc-4648>٤٦٣٨ </span>- عن عمارة بن شبيب قال: قال رسول الله صلى الله عليه وسلم: " من قال لا إله إلا الله وحده لا شريك له له الملك وله الحمد يحيى ويميت وهو على كل شىء قدير عشر مرات على أثر المغرب بعث الله تعالى له مسلحة يتكفلونه من الشيطان متى يصبح وكتب الله له بها عشر حسنات موجبات ومحا عنه عشر سيئات
موبقات وكانت له بعدل عشر رقاب مؤمنات ".
** ت
(الأذكار ٧٣/١)
** قال الإمام النووى فى " الأذكار:" ١ / ٧٣: قال الترمذى: لا نعرف لعمارة بن شبيب سماعا من النبى صلى الله عليه وسلم اهـ. قلت وقد رواه النسائى فى كتاب " عمل اليوم والليلة " من طريقين أحدهما
هكذا والثانى عن عمارة عن رجل من الأنصار، قال الحافظ أبو القاسم ابن عساكر: هذا الثانى هو الصواب.','10','213',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4639,'<span data-type="title" id=toc-4649>٤٦٣٩ </span>- عن أبى بن كعب رضى الله عنه قال: كان رسول الله صلى الله عليه وسلم إذا سلم فى الوتر قال: " سبحان الملك القدوس " وفى رواية النسائى وابن السنى " سبحان الملك القدوس " ثلاث مرات.
** د ن
(الأذكار ٧٤/١)
** أسناده صحيح','10','214',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4640,'<span data-type="title" id=toc-4650>٤٦٤٠ </span>- عن على رضى الله عنه: أن النبى صلى الله عليه وسلم كان يقول فى آخر وتره: " اللهم إنى أعوذ برضاك من سخطك وأعوذ بمعافاتك من عقوبتك وأعوذ بك منك
لا أحصى ثناء عليك أنت كما أثنيت على نفسك ".
** د ت ن
(الأذكار ٧٤/١)
** قال الإمام النووى فى " الأذكار " ١ / ٧٤: قال الترمذى: حديث حسن.','10','215',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4641,'<span data-type="title" id=toc-4651>٤٦٤١ </span>- عن على رضى الله عنه عن رسول الله صلى الله عليه وسلم أنه كان يقول عند مضجعه: " اللهم إنى أعوذ بوجهك الكريم وكلماتك التامة من شر ماأنت آخذ بناصيته اللهم أنت تكشف المغرم والمأثم اللهم لا يهزم جندك ولا يخلف وعدك ولا ينفع ذا الجد منك الجد سبحانك اللهم وبحمدك ".
** د ن
(الأذكار ٧٦/١)
** إسناده صحيح','10','216',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4642,'<span data-type="title" id=toc-4652>٤٦٤٢ </span>- عن أبى الأزهر - ويقال أبو زهير - الأنمارى رضى الله عنه: أن رسول الله
صلى الله عليه وسلم كان إذا أخذ مضجعه من الليل قال: " بسم الله وضعت جنبى
اللهم اغفر لى ذنبى وأخسىء شيطانى وفك رهانى واجعلنى فى الندى الأعلى ".
** د
(الأذكار ٧٦/١)
** إسناده حسن','10','217',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4643,'<span data-type="title" id=toc-4653>٤٦٤٣ </span>- عن عرباض بن سارية رضى الله عنه أن النبى صلى الله عليه وسلم " كان يقرأ المسبحات قبل أن يرقد ".
** د ت
(الأذكار ٧٧/١)
** قال الإمام النووى فى " الأذكار " ١ / ٧٧: قال الترمذى: حديث حسن.','10','218',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4644,'<span data-type="title" id=toc-4654>٤٦٤٤ </span>- عن عائشة رضى الله عنها قالت: " كان النبى صلى الله عليه وسلم لا ينام حتى يقرأ (بنى إسرائيل) و (الزمر) ".
** لم
(الأذكار ٧٧/١)
** قال الإمام النووى فى " الأذكار " ١ / ٧٧: قال الترمذى: حديث حسن.','10','219',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4645,'<span data-type="title" id=toc-4655>٤٦٤٥ </span>- عن ابن عمر رضى الله عنهما أن النبى صلى الله عليه وسلم كان يقول إذا أخذ مضجعه: " الحمد لله الذى كفانى وأوانى وأطعمنى وسقانى والذى من على فأفضل والذى أعطانى فأجزل الحمد لله على كل حال اللهم رب كل شىء ومليكه وإله كل شىء أعوذ بك من النار ".
** د
(الأذكار ٧٧/١)
** إسناده صحيح','10','220',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4646,'<span data-type="title" id=toc-4656>٤٦٤٦ </span>- عن رجل من أسلم من أصحاب النبى صلى الله عليه وسلم قال: " كنت جالسا عند رسول الله صلى الله عليه وسلم فجاء رجل من أصحابه فقال: يا رسول الله لدغت الليلة فلم أنم حتى أصبحت قال: " ماذا؟ " قال: عقرب قال: " أما إنك لو قلت
حين أمسيت أعوذ بكلمات الله التامات من شر ما خلق لم يضرك شىء إن شاء الله تعالى ".
** د
(الأذكار ٧٧/١)
** إسناده صحيح','10','221',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4647,'<span data-type="title" id=toc-4657>٤٦٤٧ </span>- عن أبى هريرة رضى الله عنه: " اللهم فاطر السموات والأرض عالم الغيب والشهادة رب كل شىء ومليكه أشهد أن لا إله إلا أنت أعوذ بك من شر نفسى وشر الشيطان وشركه قلها إذا أصبحت وإذا أمسيت وإذا اضجعت ".
** د ت
(الأذكار ٧٨/١)
** إسناده صحيح
** قال الإمام النووى فى " الأذكار " ١ / ٧٨: حديث أبى هريرة رضى الله عنه قدمناه فى باب ما يقول عند الصباح والمساء فى قصة أبى بكر الصديق ١ / ٦٤.','10','222',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4648,'<span data-type="title" id=toc-4658>٤٦٤٨ </span>- عن شداد بن أوس رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم " ما من مسلم يأوى إلى فراشه فيقرأ سورة من كتاب الله تعالى حين يأخذ مضجعه إلا وكل الله عز وجل به ملكا لا يدع شيئا يقربه يؤذيه يهب متى هب ".
** ت سنى
(الأذكار ٧٨/١)
** إسناده ضعيف
** تعقيب: قال عبد القادر الأرناؤوط ١ / ٧٨: قال ابن حجر فى تخريج الأذكار: حديث حسن ثم ذكر لأصل الحديث طريقا وقال بعد إيرادها: هذه طرق يقوى بعضها بعضا يمتنع معها إطلاق القول بضعف الحديث قال: وإنما صححه ابن حبان والحاكم لأن طريقهما عدم التفرقة بين الصحيح والحسن.','10','223',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4649,'<span data-type="title" id=toc-4659>٤٦٤٩ </span>- عن على رضى الله عنه قال: " ما كنت أرى أحدا يعقل ينام قبل أن يقرأ الآيات الثلاث الأواخر من سورة البقرة ".
** أبو بكر بن أبى داود
(الأذكار ٧٩/١)
** إسناده صحيح على شرط البخارى ومسلم
** تعقيب: قال عبد القادر الأرناؤوط ١ / ٧٩: قال الحافظ فى تخريج الأذكار: أخرجه أبو بكر عبد الله بن أبى داود فى كتاب " شريعة القارى " من طريقين الأول صحيحة كما قال الشيخ ٠٠٠الخ.','10','224',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4650,'<span data-type="title" id=toc-4660>٤٦٥٠ </span>- عن إبراهيم النخعى قال: " كانوا يعلمونهم إذا أووا إلى فراشهم أن يقرؤوا المعوذتين " وفى روايه: " كانوا يستحبون أن يقرؤوا هؤلاء السور فى كل ليلة ثلاث مرات قل هو الله أحد والمعوذتين ".
** لم
(الأذكار ٨٠/١)
** إسناده صحيح على شرط مسلم
** تعقيب: قال عبد القادر الأرناؤوط ١ / ٧٨: قال الحافظ فى تخريج الأذكار: الأثر عن النخعى أخرجه ابن أبى داود بسندين كلاهما صحيح أخرج الشيخان لجميع رواتهما نعجب من اختصار الشيخ - يعنى النووى - على شرط مسلم.','10','225',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4651,'<span data-type="title" id=toc-4661>٤٦٥١ </span>- عن أبى هريرة رضى الله عنه عن رسول الله صلى الله عليه وسلم قال: " من قعد مقعدا لم يذكر الله تعالى فيه كانت عليه من الله تره ومن اضجع مضجعا لا يذكر الله تعالى فيه كانت عليه من الله تعالى تره ".
** د
(الأذكار ٨٠/١)
** إسناده جيد','10','226',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4652,'<span data-type="title" id=toc-4662>٤٦٥٢ </span>- عن عائشة رضى الله تعالى عنها أن رسول الله صلى الله عليه وسلم: كان إذا استيقظ من الليل قال: " لا إله إلا أنت سبحانك استغفرك لذنبى وأسألك رحمتك اللهم زدنى علما ولا تزغ قلبى بعد إذ هديتنى وهب لى من لدنك رحمة إنك أنت الوهاب ".
** د
(الأذكار ٨١/١)
** قال الإمام النووى فى " الأذكار "١ / ٨١: فى سنن أبى داود باسناد لم يضعفه.
** تعقيب: قال عبد القادر الأرناؤوط ١ / ٨١: فى سنده عبد الله بن الوليد بن قيس التجيبى وهو لين الحديث كما قال الحافظ فى التقريب ولكن له شواهد بمعناه يقوى بها.','10','227',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4653,'<span data-type="title" id=toc-4663>٤٦٥٣ </span>- عن أبى هريرة رضى الله عنه أنه سمع رسول الله صلى الله عليه وسلم يقول: " إذا رد الله عز وجل إلى العبد المسلم نفسه من الليل فسبحه واستغفره ودعاه تقبل منه ".
** سنى
(الأذكار ٨١/١)
** إسناده ضعيف','10','228',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4654,'<span data-type="title" id=toc-4664>٤٦٥٤ </span>- عن أبى هريرة رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " إذا قام أحدكم عن فراشه من الليل ثم عاد إليه فلينفضه بصنفة إزاره ثلاث مرات فإنه لا يدرى ما خلفه عليه فإذا اضطجع فليقل: باسمك اللهم وضعت جنبى وبك أرفعه
إن أمسكت نفسى فارحمها وإن رددتها فاحفظها بما تحفظ به عبادك الصالحين ".
** ت جه سنى
(الأذكار ٨١/١)
** إسناده جيد
** قال الإمام النووى فى " الأذكار " ١ / ٨١: قال الترمذى: حديث حسن.','10','229',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4655,'<span data-type="title" id=toc-4665>٤٦٥٥ </span>- عن محمد بن يحيى بن حبان أن خالد بن الوليد رضى الله عنه أصابه أرق فشكا ذلك إلى النبى صلى الله عليه وسلم فأمره أن يتعوذ عند منامه بكلمات الله التامات من غضبه ومن شر عباده ومن همزات الشياطين وأن يحضرون ".
** لم
(الأذكار ٨٢/١)
** مرسل
** قال الإمام النووى فى " الأذكار " ١ / ٨٢: محمد بن يحيى تابعى.','10','230',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4656,'<span data-type="title" id=toc-4666>٤٦٥٦ </span>- عن عمرو بن شعيب عن أبيه عن جده: أن رسول الله صلى الله عليه وسلم كان يعلمهم من الفزع كلمات: " أعوذ بكلمات الله التامة من غضبه وشر عباده ومن همزات الشياطين وأن يحضرون " قال: وكان عبد الله بن عمرو يعلمهم من بينه ومن لم يعقل كتبه فعلقه عليه ".
** ت د سنى
(الأذكار ٨٢/١)
** قال الإمام النووى فى " الأذكار " ١ / ٨٢: قال الترمذى: حديث حسن.','10','231',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4657,'<span data-type="title" id=toc-4667>٤٦٥٧ </span>- عن عمرو بن عبسة رضى الله عنه: " أنه سمع النبى صلى الله عليه وسلم يقول أقرب ما يكون الرب من العبد فى جوف الليل الآخر فإن استطعت أن تكون ممن يذكر الله تعالى فى تلك الساعة فكن ".
** د ت
(الأذكار ٨٤/١)
** قال الإمام النووى فى " الأذكار " ١/ ٨٤: قال الترمذى: حديث حسن صحيح.','10','232',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4658,'<span data-type="title" id=toc-4668>٤٦٥٨ </span>- عن أبى هريرة رضى الله عنه: أن رسول الله صلى الله عليه وسلم قال: " إن لله تسعا وتسعين اسما مائة إلا واحدا من أحصاها دخل الجنة إنه وتر يحب الوتر هو الله الذى لا إله إلا هو الرحمن الرحيم..... الحديث بذكر الأسماء ".
** ت
(الأذكار ٨٥/١)
** حسن
** قال الإمام النووى فى " الأذكار " ١ / ٨٥: هذا حديث رواه البخارى ومسلم إلى قوله " يحب الوتر " وما بعده حديث حسن رواه
الترمذى وغيره.
** تعقيب: قال عبد القادر الأرناؤوط ١ / ٨٥: حسنه المصنف رحمه الله تعالى وذكره ابن حبان فى صحيحه وقد قال الترمذى ٣٥٠٢: هذا حديث غريب. حدثنا به غير واحد عن صفوان بن صالح ولا نعرفه إلا من حديث صفوان بن صالح وهو ثقة عند أهل الحديث وقد روى هذا الحديث من غير وجه عن أبى هريرة عن النبى صلى الله عليه وسلم ولا نعلم فى كبير شىء من الروايات ذكر الأسماء إلا فى هذا الحديث باسناد غير هذا عن أبى هريرة عن رسول الله صلى الله عليه وسلم وذكر فيه الأسماء وليس له إسناد صحيح وأنظر جامع الأصول ٤ / ١٧٤.','10','233',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4659,'<span data-type="title" id=toc-4669>٤٦٥٩ </span>- عن عبد الله بن عمرو بن العاص رضى الله عنهما قال: قال رسول الله صلى الله عليه وسلم: " لا يفقه من قرأ القرآن فى أقل من ثلاث ".
** د ت ن
(الأذكار ٨٦/١)
** إسناده صحيح','10','234',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4660,'<span data-type="title" id=toc-4670>٤٦٦٠ </span>- عن سعد بن أبى وقاص رضى الله عنه قال: " إذا وافق ختم القرآن أول الليل صلت عليه الملائكة حتى يصبح وإن وافق ختمه آخر الليل صلت عليه الملائكة حتى يمسى ".
** د ى
(الأذكار ٨٧/١)
** قال الإمام النووى فى " الأذكار " ١ / ٨٧: قال الدارمى: هذا حسن عن سعد.
** تعقيب: قال عبد القادر الأرناؤوط ١ / ٨٧: قال ابن علان فى شرح الأذكار: نازعه الحافظ فى تحسينه بأنه فى سنده ليث بن أبى سليم وهو ضعيف الحفظ ومحمد بن حميد مختلف فيه قال: وكأنه حسنه لشواهده السابقة وغيرها، أو لم يرد الحسن الأصطلاحى.','10','235',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4661,'<span data-type="title" id=toc-4671>٤٦٦١ </span>- عن قتادة التابعى الجليل قال: " كان أنس بن مالك رضى الله عنه إذا ختم القرآن جمع أهله ودعا ".
** ابن أبى داود
(الأذكار ٨٨/١)
** إسناده صحيح','10','236',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4662,'<span data-type="title" id=toc-4672>٤٦٦٢ </span>- عن أنس رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " عرضت على أجور أمتى حتى القذاة يخرجها الرجل من المسجد وعرضت على ذنوب أمتى فلم أر ذنبا أعظم من سورة من القرآن أو آية أوتيها الرجل ثم نسيها ".
** د ت
(الأذكار ٨٩/١)
** قال الإمام النووى فى " الأذكار " ١ / ٨٩: تكلم الترمذى فيه.
** تعقيب: قال عبد القادر الأرناؤوط ١ / ٨٩: قال الترمذى فيه: هذا حديث غريب اهـ. ولكن للحديث شواهد بالمعنى يرتقى بها إلى درجة الحسن.','10','237',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4663,'<span data-type="title" id=toc-4673>٤٦٦٣ </span>- " كل أمر ذى بال لا يبدأ فيه ببسم الله الرحمن الرحيم فهو أقطع ".
** كتاب الأربعين للحافظ الرهاوى
(الأذكار ٩٤/١)
** حسن
** قال الإمام النووى فى " الأذكار " ١ / ٩٤: وقد روى موصولا كما ذكرنا وروى مرسلا ورواية الموصول جيدة الإسناد.
** تعقيب: قال عبد القادر الأرناؤوط ١ / ٩٤: أخرجه ابن السنى فى عمل " اليوم والليلة " عن أبى هريرة وإسناده ضعيف. ورواه بنحوه الحاكم والترمذى والبيهقى فى " شعب الإيمان " عن ابن عباس رضى الله عنهما وفى سنده يمان بن المقبرة وهو ضعيف.','10','238',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4664,'<span data-type="title" id=toc-4674>٤٦٦٤ </span>- عن أبى موسى الأشعرى رضى الله عنه أن رسول الله صلى الله عليه وسلم قال: " إذا مات ولد العبد قال الله تعالى لملائكته: قبضتم ولد عبدى؟ فيقولون نعم فيقو ل: قبضتم ثمرة فؤاده؟ فيقولون: نعم فيقول: فماذا قال عبدى فيقولون: حمدك واسترجع فيقول الله تعالى: ابنوا لعبدى بيتا فى الجنة وسموه بيت الحمد ".
** ت
(الأذكار ٩٥/١)
** قال الإمام النووى فى " الأذكار " ١ / ٩٦: قال الترمذى: حديث حسن.','10','239',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4665,'<span data-type="title" id=toc-4675>٤٦٦٥ </span>- عن عبد الله بن مسعود رضى الله عنه أن رسول الله صلى الله عليه وسلم قال: " أولى الناس بى يوم القيامة أكثرهم على صلاة ".
** ت
(الأذكار ٩٦/١)
** قال الإمام النووى فى " الأذكار " ١ / ٩٧: قال الترمذى: حديث حسن.','10','240',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4666,'<span data-type="title" id=toc-4676>٤٦٦٦ </span>- عن أوس بن أوس رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " إن من أفضل أيامكم يوم الجمعة فأكثروا على من الصلاة فيه فإن صلاتكم معروضة على فقالوا: يا رسول الله وكيف تعرض صلاتنا عليك وقد أرمت؟ قال يقول بليت قال: إن الله حرم على الأرض أجساد الأنبياء ".
** د ن جه
(الأذكار ٩٧/١)
** إسناده صحيح
** تعقيب: قال عبد القادر الأرناؤوط ١ / ٩٧: وهو حديث صحيح.','10','241',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4667,'<span data-type="title" id=toc-4677>٤٦٦٧ </span>- عن أبى هريرة رضى الله عنه قال رسول الله صلى الله عليه وسلم: " لا تجعلوا قبرى عيدا وصلوا على فإن صلاتكم تبلغنى حيث كنتم ".
** د
(الأذكار ٩٧/١)
** إسناده صحيح
** تعقيب: قال عبد القادر الأرناؤوط ١ / ٩٧: قال الحافظ فى تخريج الأذكار: حديث حسن.','10','242',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4668,'<span data-type="title" id=toc-4678>٤٦٦٨ </span>- عن أبى هريرة رضى الله عنه أن رسول الله صلى الله عليه وسلم قال: " مامن أحد يسلم على إلا رد على روحى حتى أرد عليه السلام ".
** د
(الأذكار ٩٧/١)
** إسناده صحيح
** تعقيب: قال عبد القادر الأرناؤوط ١ / ٩٧: قال الحافظ فى تخريج الأذكار: وسنده حسن.','10','243',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4669,'<span data-type="title" id=toc-4679>٤٦٦٩ </span>- عن أبى هريرة رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " رغم أنف رجل ذكرت عنده فلم يصل على ".
** ت
(الأذكار ٩٨/١)
** قال الإمام النووى فى " الأذكار " ١ / ٩٨: قال الترمذى: حديث حسن.','10','244',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4670,'<span data-type="title" id=toc-4680>٤٦٧٠ </span>- عن أنس رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " من ذكرت عنده فليصل على فإنه من صلى على مرة صلى الله عز وجل عليه عشرا ".
** ابن السنى
(الأذكار ٩٨/١)
** إسناده جيد
** تعقيب: قال عبد القادر الأرناؤوط ١ / ٩٨: قال ابن علان فى شرح الأذكار: قال الحافظ: أخرجه النسائى آخر فضائل القرآن وقال الأرناؤوط ١ / ٩٨ أيضا: قال الحافظ: ووصف السند بالجودة كأنه بالنظر إلى رجاله بأنهم موثقون لكن فى السند انقطاع يعنى بين أبى إسحاق السبيعى وأنس بن مالك رضى الله عنه اه. أقول: للحديث شواهد بمعناه يقوى بها.','10','245',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4671,'<span data-type="title" id=toc-4681>٤٦٧١ </span>- عن جابر بن عبد الله رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم " من ذكرت عنده فلم يصل على فقد شقى ".
** سنى
(الأذكار ٩٨/١)
** إسناده ضعيف
** تعقيب: قال عبد القادر الأرناؤوط ١ / ٩٨: وفى إسناده الفضل بن المنتشر وهو ضعيف. قال الحافظ: وللحديث طريق أخرى أخرجها الطبرانى مختصرة من حديث جابر بن عبد الله ان النبى صلى الله عليه
وسلم قال: قال لى جبريل: من ذكرت عنده فلم يصل عليك فقد شقى. اهـ. وقد جاء الحديث من طرق بلفظ: من ذكرت عنده فلم يصل على خطىء طريق
الجنة. وهو حديث حسن بطرقه.','10','246',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4672,'<span data-type="title" id=toc-4682>٤٦٧٢ </span>- عن فضالة بن عبيد رضى الله عنه قال: سمع رسول الله صلى الله عليه وسلم رجلا يدعو فى صلاته لم يمجد الله تعالى ولم يصل على النبى صلى الله عليه وسلم فقال رسول الله صلى الله عليه وسلم: " عجل هذا ثم دعاه فقال له أو لغيره: إذا صلى أحدكم فليبدأ بتحميد ربه سبحانه والثناء عليه ثم يصلى على النبى صلى
الله عليه وسلم ثم يدعو بعد بما شاء ".
** د ت ن
(الأذكار ٩٩/١)
** قال الإمام النووى فى " الأذكار " ١ / ٩٩: قال الترمذى: حديث حسن صحيح.','10','247',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4673,'<span data-type="title" id=toc-4683>٤٦٧٣ </span>- عن أبى بكر رضى الله عنه: " أن النبى صلى الله عليه وسلم كان إذا أراد الأمر قال: اللهم خر لى واختر لى ".
** ت
(الأذكار ١٠١/١)
** إسناده ضعيف
** قال الإمام النووى فى " الأذكار " ١ / ١٠١: ضعفه الترمذى وغيره.','10','248',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4674,'<span data-type="title" id=toc-4684>٤٦٧٤ </span>- عن أنس رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " يا أنس إذا هممت بأمر فاستخر ربك فيه سبع مرات ثم انظر إلى الذى سبق إلى قلبك فإن الخير فيه ".
** سنى
(الأذكار ١٠١/١)
** إسناده غريب
** قال الإمام النووى فى " الأذكار " ١ / ١٠٢: إسناده غريب، فيه من لا أعرفهم.','10','249',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4675,'<span data-type="title" id=toc-4685>٤٦٧٥ </span>- عن أنس رضى الله عنه عن النبى صلى الله عليه وسلم: إنه كان إذا كربه أمر قال: " يا حى يا قيوم برحمتك أستغيث ".
** ت
(الأذكار ١٠٢/١)
** قال الإمام النووى فى " الأذكار " ١ / ١٠٢: قال الحاكم: هذا حديث صحيح الإسناد.','10','250',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4676,'<span data-type="title" id=toc-4686>٤٦٧٦ </span>- عن أبى موسى الأشعرى رضى الله عنه: أن النبى صلى الله عليه وسلم كان إذا خاف قوما قال: " اللهم إنا نجعلك فى نحورهم، ونعوذ بك من شرورهم ".
** د ن
(الأذكار ١٠٤/١)
** إسناده صحيح','10','251',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4677,'<span data-type="title" id=toc-4687>٤٦٧٧ </span>- عن على رضى الله عنه: " أن مكاتبا جاءه فقال: إنى عجزت عن كتابتى فأعنى قال: ألا أعلمك كلمات علمنيهم رسول الله صلى الله عليه وسلم ولو كان عليك مثل جبل دينا أداه الله عنك؟ قال: قل اللهم اكفنى بحلالك عن حرامك وأغننى عمن سواك ".
** ت
(الأذكار ١٠٧/١)
** قال الإمام النووى فى " الأذكار " ١ / ١٠٥: قال الترمذى: حديث حسن.','10','252',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4678,'<span data-type="title" id=toc-4688>٤٦٧٨ </span>- عن أبى زميل قال: " قلت: لابن عباس: ماشىء أج٤ده فى صدرى؟ قال: ما هو قلت والله لا أتكلم به فقال لى: أشىء فى شك وضحك وقال: مانجا منه أحد حتى أنزل الله تعالى " فإن كنت فى شك مما أنزلنا إليك " الآية يونس (٩٤)
فقال لى: إذا وجدت فى نفسك شيئا فقل (هو الأول والآخر والظاهر والباطن وهو بكل شىء عليم ".
** د
(الأذكار ١٠٨/١)
** إسناده جيد
** تعقيب: قال عبد القادر الأرناؤوط ١ / ١٠٨: فى سنده النضر بن محمد وهو ثقة له أفراد وعكرمة بن عمار العجلى وهو صدوق يغلط وقال ابن علان فى شرح الأذكار: قال الحافظ: هذا المتن شاذ وقد ثبت عن ابن عباس من رواية سعيد بن جبير ومن رواية مجاهد وغيرهما عنه: " ماشك النبى صلى الله عليه وسلم ولا سأل " أخرجه عيد بن حميد
والطبرانى وابن أبى حاتم بأسانيد صحيحة. وجاء من وجه آخر مرفوعا من لفظه صلى الله عليه وسلم قال: " لا أشك ولا أسأل " أخرجوه من رواية سعيد ومعمر وغيرهما عن قتادة. قال: ذكر لنا وفى لفظ بلغنا فذكره وسنده صحيح.','10','253',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4679,'<span data-type="title" id=toc-4689>٤٦٧٩ </span>- عن خارجة بن الصلت عن عمه قال: أتيت النبى صلى الله عليه وسلم فأسلمت ثم رجعت فمررت على قوم عندهم رجل مجنون موثق بالحديد فقال أهله: إنا حدثنا أن صاحبك هذا قد جاء بخير فهل عندك شىء تداويه؟ فرقيته بفاتحة الكتاب فبرأ فأعطونى مائة شاة فأتيت النبى صلى الله عليه وسلم فأخبرته فقال ": هل إلا هذا " وفى رواية هل قلت غير هذا؟ قلت: لا قال: خذها فلعمرى لمن أكل
برقية باطل لقد أكلت برقية حق ".
** د
(الأذكار ١١٠/١)
** إسناده صحيح
** تعقيب: قال عبد القادر الأرناؤوط ١ / ١١٠: قال الحافظ: حديث حسن.','10','254',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4680,'<span data-type="title" id=toc-4690>٤٦٨٠ </span>- عن أبى هريرة رضى الله عنه: عن رسول الله صلى الله عليه وسلم " أكثروا ذكر هاذم اللذات ". يعنى الموت.
** ت ن جه
(الأذكار ١١٢/١)
** إسناده صحيح
** قال الإمام النووى فى " الأذكار " ١ / ١١١: قال الترمذى: حديث حسن.','10','255',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4681,'<span data-type="title" id=toc-4691>٤٦٨١ </span>- عن ابن عباس رضى الله عنهما عن النبى صلى الله عليه وسلم قال: " من عاد مريضا لم يحضر أجله فقال عنده سبع مرات: أسأل الله العظيم رب العرش العظيم أن يشفيك إلا عافاه الله سبحانه وتعالى من ذلك المرض ".
** د ت
(الأذكار ١١٤/١)
** إسناده صحيح
** قال الإمام النووى فى " الأذكار " ١ / ١١٤: قال الترمذى: حديث حسن. قال الحاكم أبو عبد الله فى كتابه " المستدرك على الصحيحين ": هذا حديث صحيح
على شرط البخارى.
** تعقيب: قال عبد القادر الأرناؤوط ١ / ١١٤: قال ابن علان فى شرح الأذكار: قال الحافظ بعد تخريجه للحديث: هذا حديث حسن وأخرجه أحمد وقال الترمذى: حسن غريب.','10','256',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4682,'<span data-type="title" id=toc-4692>٤٦٨٢ </span>- عن عبد الله بن عمرو بن العاص رضى الله عنهما قال: قال النبى صلى الله عليه وسلم: " إذا جاء الرجل يعود مريضا فليقل: اللهم اشف عبدك ينكأ لك عدوا أو يمشى لك إلى صلاة ".
** د
(الأذكار ١١٤/١)
** قال الإمام النووى فى " الأذكار " ١ / ١١٤: لم يضعفه أبو داود.
** تعقيب: قال عبد القادر الأرناؤوط ١ / ١١٤ / وهو حديث حسن، حسنه الحافظ وغيره.','10','257',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4683,'<span data-type="title" id=toc-4693>٤٦٨٣ </span>- عن على رضى الله عنه قال: كنت شاكيا فمر بى رسول الله صلى الله عليه وسلم وأنا أقول اللهم إن كان أجلى قد حضر فأرحنى وإن كان متأخرا فارفعه عنى وإن كان بلاء فصبرنى فقال رسول الله صلى الله عليه وسلم: كيف قلت فأعاد عليه ما قاله فضربه برجله وقال: اللهم عافه أو اشفه شك شعبة قال: فما اشتكيت وجعى بعد ".
** ت
(الأذكار ١١٤/١)
** قال الإمام النووى فى " الأذكار " ١ / ١١٤: قال الترمذى: حديث حسن صحيح.','10','258',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4684,'<span data-type="title" id=toc-4694>٤٦٨٤ </span>- عن أبى سعيد الخدرى وأبى هريرة رضى الله عنهما أنهما شهدا على رسول الله صلى الله عليه وسلم أنه قال: " من قال لا إله إلا الله والله أكبر، صدقه ربه فقال: لا إله إلا أنا وأنا أكبر وإذا قال: لا إله إلا الله وحده لا شريك له قال: يقول لا إله إلا أنا وحدى لا شريك لى وإذا قال: لا إله إلا الله له الملك وله الحمد قال: لا إله إلا أنا لى الملك ولى الحمد وإذا قال: لاإله إلا الله ولا حول ولا قوة إلا بالله قال: لا إله إلا أنا ولا حول ولا قوة
إلا بى وكان يقول: من قالها فى مرضه ثم مات لم تطعمه النار ".
** ت جه
(الأذكار ١١٤/١)
** قال الإمام النووى فى " الأذكار " ١ / ١١٥: قال الترمذى: حديث حسن.','10','259',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4685,'<span data-type="title" id=toc-4695>٤٦٨٥ </span>- عن أبى أمامة رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " تمام عيادة المريض أن يضع أحدكم يده على جبهته أو على يده فيسأله كيف هو؟ ". هذا لفظ الترمذى. وفى رواية ابن السنى " من تمام العيادة أن تضع يدك على المريض فتقول: كيف أصبحت أو كيف أمسيت ".
** ت سنى
(الأذكار ١١٥/١)
** قال الإمام النووى فى " الأذكار " ١ / ١١٥: قال الترمذى: ليس إسناده بذاك.
** تعقيب: قال عبد القادر الأرناؤوط ١ / ١١٥: إسناده ضعيف.','10','260',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4686,'<span data-type="title" id=toc-4696>٤٦٨٦ </span>- عن أبى سعيد الخدرى رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " إذا دخلتم على مريض فنفسوا له فى أجله فإن ذلك لا يرد شيئا ويطيب نفسه "
** ت جه
(الأذكار ١١٨/١)
** إسناده ضعيف','10','261',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4687,'<span data-type="title" id=toc-4697>٤٦٨٧ </span>- عن أنس رضى الله عنه قال: دخل النبى صلى الله عليه وسلم على رجل يعوده فقال: " هل تشتهى شيئا؟ تشتهى كعكا؟ قال نعم فطلبه له ".
** جه سنى
(الأذكار ١١٨/١)
** إسناده ضعيف
** تعقيب: قال عبد القادر الأرناؤوط ١ / ١١٨: إسناده ضعيف. وذكر ابن ماجه قبل حديث أنس حديثا لا بن عباس بهذا المعنى وسنده أصح من هذا فى سنده صفوان بن هبيرة وهو لين الحديث. وقال عبد القادر الأرناؤوط ١ / ١١٩: قال الحافظ: وللحديث شاهد عن عمر أخرجه ابن أبى الدنيا فى كتاب المرض والكفارات لكنه موقوف.','10','262',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4688,'<span data-type="title" id=toc-4698>٤٦٨٨ </span>- عن عقبة بن عامر رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم " لا تكرهوا مرضاكم على الطعام فإن الله يطعمهم ويسقيهم ".
** ت جه
(الأذكار ١١٩/١)
** قال الإمام النووى فى " الأذكار " ١ / ١١٩: قال الترمذى: حديث حسن.
** تعقيب: قال عبد القادر الأرناؤوط ١ / ١١٩: هو حديث حسن لشواهده.','10','263',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4689,'<span data-type="title" id=toc-4699>٤٦٨٩ </span>- عن ميمون بن مهران عن عمر بن الخطاب رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " إذا دخلت على مريض فمره فليدع لك فإن دعاءه كدعاء الملائكة ".
** جه سنى
(الأذكار ١١٩/١)
** إسناده صحيح أو حسن
** قال الإمام النووى فى " الأذكار " ١ / ١١٩: لكن ميمون بن مهران لم يدرك عمر.
** تعقيب: قال عبد القادر الأرناؤوط ١ / ١١٩: إسناده منقطع، قال الحافظ فلا يكون صحيحا ولو اعتضد لكان حسنا لكن لم نجد له شاهدا يصلح للإعتبار.','10','264',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4690,'<span data-type="title" id=toc-4700>٤٦٩٠ </span>- عن معاذ بن جبل رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم " من كان آخر كلامه لا إله إلا الله دخل الجنة ".
** د
(الأذكار ١٢١/١)
** مشهور
** قال الإمام النووى فى " الأذكار " ١ / ١٢١: قال الحاكم أبو عبد الله فى كتابه " المستدرك ": هذا حديث صحيح الإسناد.
** تعقيب: قال عبد القادر الأرناؤوط ١ / ١٢١: هذا من الحاكم على قاعدته فى تصحيح الحسن وإلا فالحديث حسن.','10','265',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4691,'<span data-type="title" id=toc-4701>٤٦٩١ </span>- عن معقل بن يسار الصحابى رضى الله عنه أن النبى صلى الله عليه وسلم قال: " اقرؤوا يس على موتاكم ".
** د جه
(الأذكار ١٢٢/١)
** إسناده ضعيف
** قال الإمام النووى فى " الأذكار " ١ / ١٢٢: فيه مجهولان لكن لم يضعفه أبو داود.
** تعقيب: قال عبد القادر الأرناؤوط ١ / ١٢٢: قال ابن علان فى شرح الأذكار قال الحافظ: وأما الحاكم فتساهل فى تصحيحه لكونه من فضائل الأعمال وعلى هذا يحمل سكوت أبو داود والعلم عند الله. قال الحافظ: ووجدت لحديث معقل شاهدا عن صفوان بن عمرو عن المشيخة أنهم حضروا غضيف بن الحارث حين اشتد سوقه فقال هل فيكم أحد يقرأ يس؟ قال: فقرأها
صالح بن شريح السكونى فلما بلغ أربعين آية منها قبض، فكان المشيخة يقولون: إذا قرئت عند الموت خفف عنه بها هذا موقوف حسن الإسناد وغضيف صحابى عند الجمهور، والمشيخة الذين نقل عنهم لم يسموا لكنهم ما بين صحابى وتابعى كبير ومثله لا يقال بالرأى فله حكم الرفع قال: وأخرج ابن أبى شيبة من طريق أبى الشعثاء جابر بن زيد وهو من ثقات التابعين أنه يقرأ عند الميت سورة الرعد وسنده صحيح. ٢','10','266',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4692,'<span data-type="title" id=toc-4702>٤٦٩٢ </span>- عن عبد الله بن مسعود رضى الله عنه عن النبى صلى الله عليه وسلم قال: " من عزى مصابا فله مثل أجره ".
** ت هق
(الأذكار ١٢٦/١)
** إسناده ضعيف','10','267',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4693,'<span data-type="title" id=toc-4703>٤٦٩٣ </span>- عن أبى برزة رضى الله عنه عن النبى صلى الله عليه وسلم قال: " من عزى ثكلى كسى بردا فى الجنة ".
** ت
(الأذكار ١٢٦/١)
** قال الإمام النووى فى " الأذكار " ١ / ١٢٦: قال الترمذى: ليس إسناده بالقوى.','10','268',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4694,'<span data-type="title" id=toc-4704>٤٦٩٤ </span>- عن عمرو بن حزم رضى الله عنه عن النبى صلى الله عليه وسلم قال: " ما من مؤمن يعزى أخاه بمصيبته إلا كساه الله عز وجل من حلل الكرامة يوم القيامة ".
** جه هق
(الأذكار ١٢٦/١)
** إسناده حسن','10','269',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4695,'<span data-type="title" id=toc-4705>٤٦٩٥ </span>- عن معاوية بن قرة بن إياس عن أبيه رضى الله عنه أن النبى صلى الله عليه وسلم فقد بعض أصحابه فسأل عنه فقالوا: يا رسول الله بنيه الذى رأيته هلك فلقيه النبى صلى الله عليه وسلم فسأله عن بنيه فأخبره أنه هلك فعزاه عليه ثم قال: " يافلان أيما كان أحب إليك أن تمتع به عمرك أو لا تأتى غدا بابا من
أبواب الجنة إلا وجدته قد سبقك إليه يفتحه لك؟ " قال: يا نبى الله بل يسبقنى
إلى الجنة فيفتحها لى أحب إلى قال: " فذلك لك ".
** ن
(الأذكار ١٢٨/١)
** إسناده حسن
** تعقيب: قال عبد القادر الأرناؤوط ١ / ١٢٨: هو حديث صحيح رواته متفق على التخريج لهم فى الصحيحين. قال الحافظ: وعجب من اقتصار الشيخ على تحسين سنده.','10','270',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4696,'<span data-type="title" id=toc-4706>٤٦٩٦ </span>- عن حذيفة رضى الله عنه قال: " إذا مت فلا تؤذنوا بى أحدا إنى أخاف أن يكون نعيا فإنى سمعت رسول الله صلى الله عليه وسلم ينهى عن النعى ".
** ت جه
(الأذكار ١٣٠/١)
** قال الإمام النووى فى " الأذكار " ١ / ١٣٠: قال الترمذى: حديث حسن.','10','271',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4697,'<span data-type="title" id=toc-4707>٤٦٩٧ </span>- عن عبد الله بن مسعود رضى الله عنه عن النبى صلى الله عليه وسلم قال: " إياكم والنعى، فإن النعى من عمل الجاهلية ".
** ت
(الأذكار ١٣٠/١)
** قال الإمام النووى فى " الأذكار " ١ / ١٣٠: وفى رواية عن عبد الله ولم يرفعه. قال الترمذى: هذا أصح من المرفوع، وضعف الترمذى الروايتين.','10','272',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4698,'<span data-type="title" id=toc-4708>٤٦٩٨ </span>- عن ابن عمر رضى الله عنهما قال: قال رسول الله صلى الله عليه وسلم:
" اذكروا محاسن موتاكم وكفوا عن مساويهم ".
** د ت
(الأذكار ١٣١/١)
** إسناده ضعيف
** قال الإمام النووى فى " الأذكار " ١ / ١٣١: ضعفه الترمذى.
** تعقيب: قال عبد القادر الأرناؤوط ١ / ١٣١: وهو حديث حسن بشواهده. قال عبد القادر الأرناؤوط ١ / ١٣١: قال ابن علان فى شرح الأذكار: قال الحافظ: لم أر فى شىء من نسخ الترمذى تصريح الترمذى بتضعيفه وإنما استغربه ونقل عن البخارى أن بعض رواته منكر الحديث وقد سكت عليه أبو داود وصححه ابن حبان وغيره فهو من شرط الحسن.','10','273',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4699,'<span data-type="title" id=toc-4709>٤٦٩٩ </span>- عن أبى رافع مولى رسول الله صلى الله عليه وسلم أن رسول الله صلى الله عليه وسلم قال: " من غسل ميتا فكتم عليه غفر الله له أربعين مرة ".
** هق ك
(الأذكار ١٣١/١)
** قال الإمام النووى فى " الأذكار " ١ / ١٣١: رواه الحاكم أبو عبد الله فى " المستدرك على الصحيحين " وقال: حديث صحيح على شرط مسلم.
** تعقيب: قال عبد القادر الأرناؤوط ١ / ١٣١: بل هو حديث حسن كما قال الحافظ فى تخريج الأذكار.','10','274',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4700,'<span data-type="title" id=toc-4710>٤٧٠٠ </span>- عن أبى هريرة رضى الله عنه عن النبى صلى الله عليه وسلم أنه صلى على جنازة فقال: " اللهم اغفر لحينا وميتنا وصغيرنا وكبيرنا وذكرنا وأنثانا وشاهدنا وغائبنا، اللهم من أحييته منا فأحيه على الإسلام ومن توفيته منا فتوفه على الإيمان اللهم لا تحرمنا أجره ولا تفتنا بعده ".
** د ت هق
(الأذكار ١٣٣/١)
** قال الإمام النووى فى " الأذكار " ١ / ١٣٣: قال الحاكم أبو عبد الله: هذا حديث صحيح على شرط البخارى ومسلم.
** تعقيب: قال عبد القادر الأرناؤوط ١ / ١٣٣: وهو حديث صحيح صححه الحاكم ووافقه الذهبى ولكن على شرط مسلم دون شرط البخارى كما قال الحافظ فى تخريج الأذكار.','10','275',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4701,'<span data-type="title" id=toc-4711>٤٧٠١ </span>- عن عبد الله بن أبى أوفى رضى الله عنهما " أنه كبر على جنازة ابنة له أربع تكبيرات فقام بعد الرابعة كقدر مابين التكبيرتين يستغفر لها ويدعو ثم قال: كان رسول الله صلى الله عليه وسلم يصنع هكذا وفى رواية كبر أربعة فمكث ساعة حتى ظننا أنه سيكبر الخامسة ثم سلم عن يمينه وعن شماله فلما انصرف قلنا له: ما هذا؟ فقال: إنى لا أزيدكم على مارأيت رسول الله صلى الله عليه وسلم يصنع أو هكذا صنع رسول الله صلى الله عليه وسلم ".
** هق
(الأذكار ١٣٥/١)
** قال الإمام النووى فى " الأذكار " ١ / ١٣٥: قال الحاكم: هذا حديث صحيح.
** تعقيب: قال عبد القادر الأرناؤوط ١ / ١٣٥: قال ابن علان فى شرح الأذكار: قال الحافظ بعد تخريجه: حديث غريب أخرجه ابن المنذر والطحاوى والحاكم والبيهقى وقال الحاكم: إنه حديث صحيح قال الحافظ: وليس مداره كما قال فإن مداره على إبراهيم بن مسلم الهجرى وهو ضعيف عند جميع الأئمة لم نجد فيه توثيقا لأحد إلا قول الأزدى صدوق والأزدى ضعيف واعتذر الحاكم بعد تخريجه بقوله لم ينقم عليه بحجة وهذا لا يكفى فى التصحيح.','10','276',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4702,'<span data-type="title" id=toc-4712>٤٧٠٢ </span>- عن ابن عمر رضى الله عنهما أن رسول الله صلى الله عليه وسلم كان إذا وضع الميت فى القبر قال: " بسم الله وعلى سنة رسول الله صلى الله عليه وسلم ".
** د ت هق
(الأذكار ١٣٦/١)
** قال الإمام النووى فى " الأذكار " ١ / ١٣٦: قال الترمذى: حديث حسن.','10','277',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4703,'<span data-type="title" id=toc-4713>٤٧٠٣ </span>- عن عثمان رضى الله عنه قال: كان النبى صلى الله عليه وسلم إذا فرغ من دفن الميت وقف عليه فقال: استغفروا لأخيكم واسألوا الله له التثبيت فإنه الآن يسأل ".
** د هق
(الأذكار ١٣٧/١)
** إسناده حسن','10','278',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4704,'<span data-type="title" id=toc-4714>٤٧٠٤ </span>- " أن ابن عمر استحب أن يقرأ على القبر بعد الدفن أول سورة البقرة وخاتمتها "
** هق
(الأذكار ١٣٧/١)
** إسناده حسن','10','279',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4705,'<span data-type="title" id=toc-4715>٤٧٠٥ </span>- عن أبى هريرة رضى الله عنه أن رسول الله صلى الله عليه وسلم خرج إلى المقبرة فقال: " السلام عليكم دار قوم مؤمنين وإنا إن شاء الله بكم لاحقون "
** د ن جه
(الأذكار ١٤٢/١)
** إسناده صحيح
** تعقيب: قال عبد القادر الأرناؤوط ١ / ١٤٢: هو حديث صحيح قال ابن علان فى شرح الأذكار: قال الحافظ وأخرجه مسلم أيضا من جملة حديث طويل.','10','280',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4706,'<span data-type="title" id=toc-4716>٤٧٠٦ </span>- عن ابن عباس رضى الله عنهما قال: مر رسول الله صلى الله عليه وسلم بقبور المدينة فأقبل بوجهه فقال: " السلام عليكم ياأهل القبور يغفر الله لنا ولكم أنتم سلفنا ونحن بالأثر ".
** ت
(الأذكار ١٤٢/١)
** قال الإمام النووى فى " الأذكار " ١ / ١٤٢: قال الترمذى: حديث حسن.','10','281',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4707,'<span data-type="title" id=toc-4717>٤٧٠٧ </span>- عن بشير بن معبد المعروف بابن الخصاصية رضى الله عنه قال: بينما أنا أماشى النبى صلى الله عليه وسلم، نظر فإذا رجل يمشى بين القبور عليه نعلان فقال: " يا صاحب السبتيتين ألق سبتيتيك...." وذكر تمام الحديث.
** د ن جه
(الأذكار ١٤٣/١)
** إسناده حسن','10','282',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4708,'<span data-type="title" id=toc-4718>٤٧٠٨ </span>- " من قام ليلتى العيدين لله محتسبا لم يمت قلبه يوم تموت القلوب ".
** شفع جه
(الأذكار ١٤٥/١)
** ضعيف','10','283',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4709,'<span data-type="title" id=toc-4719>٤٧٠٩ </span>- " ما العمل فى أيام أفضل من العمل فى عشر ذى الحجة، قيل: ولا الجهاد؟ ". وذكر تمامه، وفى رواية " عشر الأضحى ".
** دى
(الأذكار ١٤٧/١)
** إسناده إسناد الصحيحين','10','284',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4710,'<span data-type="title" id=toc-4720>٤٧١٠ </span>- عن عمرو بن شعيب عن أبيه عن جده عن النبى صلى الله عليه وسلم قال: " خير الدعاء دعاء يوم عرفة وخير ماقلت أنا والنبيون من قبلى: لا إله إلا الله وحده لا شريك له له الملك وله الحمد وهو على كل شىء قدير ".
** ت
(الأذكار ١٤٧/١)
** قال الإمام النووى فى " الأذكار " ١ / ١٤٧: ضعف الترمذى إسناده.
** تعقيب: قال عبد القادر الأرناؤوط ١ / ١٤٧: هو حديث حسن يشهد له الذى بعده.','10','285',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4711,'<span data-type="title" id=toc-4721>٤٧١١ </span>- " أفضل الدعاء دعاء يوم عرفة وأفضل ما قلت أنا والنبيون من قبلى: لا إله إلا الله وحده لا شريك له ".
** ما
(الأذكار ١٤٧/١)
** إسناده مرسل','10','286',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4712,'<span data-type="title" id=toc-4722>٤٧١٢ </span>- عن جابر بن عبد الله رضى الله عنهما قال: أتت النبى صلى الله عليه وسلم بواك فقال: " اللهم اسقنا غيثا مغيثا مريئا مريعا نافعا غير ضار عاجلا غير آجل فأطبقت عليهم السماء ".
** د
(الأذكار ١٥٠/١)
** إسناده صحيح على شرط مسلم','10','287',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4713,'<span data-type="title" id=toc-4723>٤٧١٣ </span>- عن عمرو بن شعيب عن أبيه عن جده رضى الله عنه قال: كان رسول الله صلى الله عليه وسلم إذا استسقى قال: " اللهم اسق عبادك وبهائمك وانشر رحمتك وأحى بلدك الميت ".
** د
(الأذكار ١٥٠/١)
** إسناده صحيح
** تعقيب: قال عبد القادر الأرناؤوط ١ / ١٥٠: حديث عمرو بن شعيب عن أبيه عن جده إسناده حسن وصححه بعضهم.','10','288',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4714,'<span data-type="title" id=toc-4724>٤٧١٤ </span>- عن عائشة رضى الله عنها قالت: شكا الناس إلى رسول الله صلى الله عليه وسلم قحوط المطر فأمر بمنبر فوضع له فى المصلى ووعد الناس يوما يخرجون فيه فخرج
رسول الله صلى الله عليه وسلم حين بدا حاجب الشمس فقعد على المنبر صلى الله عليه وسلم فكبر وحمد الله عز وجل ثم قال: " إنكم شكوتم جدب دياركم واستئخار المطر عن إبان زمانه عنكم وقد أمركم الله سبحانه أن تدعوه ووعدكم
أن يستجيب لكم ثم قال: الحمد لله رب العالمين الرحمن الرحيم مالك يوم الدين لا إله إلا الله يفعل ما يريد اللهم أنت الله لا إله إلا أنت الغنى ونحن الفقراء أنزل علينا الغيث واجعل ما أنزلت لنا قوة وبلاغا إلى حين " ثم رفع
يده فلم يزل فى الرفع حتى بدا بياض إبطيه ثم حول إلى الناس ظهره وقلب أو
حول ردائه وهو رافع يديه ثم أقبل على الناس ونزل فصلى ركعتين فأنشأ الله عز وجل سحابة فرعدت وبرقت ثم أمطرت بإذن الله تعالى فلم يأت مسجده حتى سالت
السيول فلما رأى سرعتهم إلى الكن ضحك صلى الله عليه وسلم حتى بدت نواجذه
فقال: " أشهد أن الله على كل شىء قدير وأنى عبد الله ورسوله ".
** د
(الأذكار ١٥٠/١)
** إسناده صحيح
** قال الإمام النووى فى " الأذكار " ١/ ١٥٠: قال أبو داود فى آخره: هذا إسناد جيد.
** تعقيب: قال عبد القادر الأرناؤوط ١ / ١٥٠: بل إسناده حسن.','10','289',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4715,'<span data-type="title" id=toc-4725>٤٧١٥ </span>- عن أبى هريرة رضى الله عنه قال: سمعت رسول الله صلى الله عليه وسلم يقول: " الريح من روح الله تعالى تأتى بالرحمة وتأتى بالعذاب فإذا رأيتموها فلا تسبوها، وسلوا الله خيرها واستعيذوا بالله من شرها ".
** د جه
(الأذكار ١٥٢/١)
** إسناده حسن','10','290',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4716,'<span data-type="title" id=toc-4726>٤٧١٦ </span>- عن أبى بن كعب رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " لا تسبوا الريح فإن رأيتم ما تكرهون فقولوا: اللهم إنا نسألك من خير هذه الريح وخير ما فيها وخير ما أمرت به ونعوذ بك من شر هذه الريح وشر ما فيها وشر ما أمرت به ".
** ت
(الأذكار ١٥٢/١)
** قال الإمام النووى فى " الأذكار " ١ / ١٥٢: قال الترمذى: حديث حسن صحيح. قال: وفى الباب عن عائشة وأبى هريرة وعثمان بن أبى العاص وأنس وابن عباس وجابر.','10','291',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4717,'<span data-type="title" id=toc-4727>٤٧١٧ </span>- عن سلمة بن الأكوع رضى الله عنه قال: " كان رسول الله صلى الله عليه وسلم إذا اشتدت الريح يقول: اللهم لقحا لا عقيما ".
** سنى
(الأذكار ١٥٢/١)
** إسناده صحيح
** تعقيب: قال عبد القادر الأرناؤوط ١ / ١٥٢: قال الحافظ فى تخريج الأذكار: هذا حديث صحيح.','10','292',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4718,'<span data-type="title" id=toc-4728>٤٧١٨ </span>- عن رجل أنه شكا إلى النبى صلى الله عليه وسلم الفقر فقال رسول الله صلى الله عليه وسلم: " لعلك تسب الريح ".
** شفع
(الأذكار ١٥٣/١)
** منقطع
** تعقيب: قال عبد القادر الأرناؤوط ١ / ١٥٣: قال ابن علان فى شرح الأذكار: قال الحافظ: سند هذا الحديث معضل لأنه سقط فيه
اثنان فصاعدا. وقول الشيخ عن رجل يوهم أن محمدا رواه عنه، وليس كذلك بل أرسل القصة ولم أجد شاهدا ولا متابعا.','10','293',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4719,'<span data-type="title" id=toc-4729>٤٧١٩ </span>- عن ابن عمر رضى الله عنهما أن رسول الله صلى الله عليه وسلم كان إذا سمع صوت الرعد والصواعق قال: " اللهم لا تقتلنا بغضبك ولا تهلكنا بعذابك وعافنا قبل ذلك ".
** ت
(الأذكار ١٥٤/١)
** إسناده ضعيف
** تعقيب: قال عبد القادر الأرناؤوط ١ / ١٥٤: لكن للحديث طرق قواه بها بعضهم.','10','294',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4720,'<span data-type="title" id=toc-4730>٤٧٢٠ </span>- عن عبد الله بن الزبير رضى الله عنهما أنه كان إذا سمع الرعد ترك الحديث وقال: " سبحان الذى يسبح الرعد بحمده والملائكة من خيفته ".
** ما
(الأذكار ١٥٤/١)
** إسناده صحيح','10','295',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4721,'<span data-type="title" id=toc-4731>٤٧٢١ </span>- عن عبد الله بن أبى أوفى قال: قال رسول الله صلى الله عليه وسلم: " من كانت له حاجة إلى الله تعالى أو إلى أحد من بنى آدم فليتوضأ وليحسن الوضوء ثم ليصل ركعتين ثم ليثن على الله عز وجل وليصل على النبى صلى الله عليه وسلم ثم ليقل: لا إله إلا الله الحليم الكريم سبحان الله رب العرش العظيم الحمد لله رب العالمين اسألك موجبات رحمتك وعزائم مغفرتك والغنيمة من كل بر والسلامة من كل إثم لا تدع لى ذنبا إلا غفرته ولا هما إلا فرجته ولا حاجة هى لك رضى إلا قضيتها يا أرحم الراحمين ".
** ت جه
(الأذكار ١٥٦/١)
** قال الإمام النووى فى " الأذكار " ١/ ١٥٦: قال الترمذى: فى إسناده مقال.
** تعقيب: قال عبد القادر الأرناؤوط ١ / ١٥٩: ولكن له شاهد من حديث أنس عند الطبرانى باسناد ضعيبف ولحديث أنس طرق أخرى فى مسند الفردوس وإسناده ضعيف أيضا كما قال الحافظ فى تخريج الأذكار.','10','296',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4722,'<span data-type="title" id=toc-4732>٤٧٢٢ </span>- عن عثمان بن حنيف رضى الله عنه أن رجلا ضرير البصر أتى النبى صلى الله عليه وسلم فقال: ادع الله تعالى أن يعافينى قال: " إن شئت دعوت وإن شئت صبرت فهو خير لك قال: فادعه فأمره أن يتوضأ فيحسن وضؤه ويدعو بهذا الدعاء: اللهم إنى أسألك وأتوجه إليك بنبيك محمد صلى الله عليه وسلم نبى الرحمة يا محمد إنى توجهت بك إلى ربى فى حاجتى هذه لتقضى لى اللهم فشفعه فى ".
** ت جه
(الأذكار ١٥٧/١)
** قال الإمام النووى فى " الأذكار " ١ / ١٥٧: قال الترمذى: حديث حسن صحيح.','10','297',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4723,'<span data-type="title" id=toc-4733>٤٧٢٣ </span>- عن أبى رافع رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم للعباس " يا عم ألا أصلك ألا أحبوك ألا أنفعك؟ قال: بلى يا رسول الله قال: ياعم صل أربع ركعات تقرأ فى كل ركعة بفاتحة القرآن وسورة فإذا انقضت القراءة فقل الله أكبر والحمد لله وسبحان الله خمس عشرة مرة قبل أن تركع ثم اركع فقلها عشرا ثم ارفع رأسك فقلها عشرا ثم اسجد فقلها عشرا ثم ارفع رأسك فقلها عشرا قبل
أن تقوم فتلك خمس وسبعون فى كل ركعة وهى ثلاثمائة فى أربع ركعات فلو كانت ذنوبك مثل رمل عالج غفرها الله تعالى لك قال: يا رسول الله من يستطيع أن يقولها فى يوم؟ قال: إن لم تستطع أن تقولها فى يوم فقلها فى جمعة فإن لم تستطع أن تقولها فى جمعة فقلها فى شهر فلم يزل يقوله حتى قال: قلها فى سنة ".
** ت جه
(الأذكار ١٥٨/١)
** قال الإمام النووى فى " الأذكار " ١ / ١٥٨: قال الترمذى: هذا حديث غريب اه. قلت: قال الإمام أبو بكر بن العربى فى كتابه الأحزذى فى شرح الترمذى: حديث أبى رافع هذا ضعيف ليس له أصل فى الصحة ولا فى الحسن قال: وإنما ذكره الترمذى لينبه عليه لئلا يغتر به قال: وقول ابن المبارك ليس بحجة. قال العقيلى: ليس فى صلاة التسابيح حديث يثبت. وذكر أبو الفرج ابن الجوزى أحاديث صلاة التسبيح وطرقها ثم ضعفها كلها وبين ضعفها، ذكره فى كتاب الموضوعات.
** تعقيب: قال عبد القادر الأرناؤوط ١ / ١٥٨: ولكن للحديث طرق وشواهد تدل على أن له أصلا وهو حديث حسن أو صحيح.','10','298',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4724,'<span data-type="title" id=toc-4734>٤٧٢٤ </span>- عن طلحة بن عبيد الله رضى الله عنه أن النبى صلى الله عليه وسلم كان إذا رأى الهلال قال: " اللهم أهله علينا باليمن والإيمان والسلامة والإسلام ربى وربك الله ".
** دى ت
(الأذكار ١٦٠/١)
** قال الإمام النووى فى " الأذكار " ١ / ١٦٠: قال الترمذى: حديث حسن.','10','299',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4725,'<span data-type="title" id=toc-4735>٤٧٢٥ </span>- عن قتادة أنه بلغه أن نبى الله صلى الله عليه وسلم كان إذا رأى الهلال قال: " هلال خير ورشد هلال خير ورشد هلال خير ورشد آمنت بالله الذى خلقك ثلاث مرات ثم يقول الحمد لله الذى ذهب بشهر كذا وجاء بشهر كذا ".
** د
(الأذكار ١٦٠/١)
** مرسل
** قال الإمام النووى فى " الأذكار " ١ / ١٦٠: قال أبو داود: ليس فى هذا الباب عن النبى صلى الله عليه وسلم حديث مسند صحيح.
** تعقيب: قال عبد القادر الأرناؤوط ١ / ١٦٠: وله شواهد مرسلة وموصولة يقوى بها، منها الذى بعده وفى الباب عن على وعبادة بن الصامت ورافع بن خديج وعائشة وغيرهم.','10','300',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4726,'<span data-type="title" id=toc-4736>٤٧٢٦ </span>- عن قتادة أن النبى صلى الله عليه وسلم: " كان إذا رأى الهلال صرف وجهه عنه "
** د
(الأذكار ١٦٠/١)
** مرسل
** قال الإمام النووى فى " الأذكار " ١ / ١٦٠: قال أبو داود: ليس فى هذا الباب عن النبى صلى الله عليه وسلم حديث مسند صحيح.
** تعقيب: قال عبد القادر الأرناؤوط ١ / ١٦٠: وله شواهد مرسلة وموصولة يقوى بها منها الذى بعده وفى الباب عن على وعبادة بن الصامت ورافع بن خديج وعائشة وغيرهم.','10','301',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4727,'<span data-type="title" id=toc-4737>٤٧٢٧ </span>- عن زياد النمرى عن أنس رضى الله عنه قال: كان رسول الله صلى الله عليه وسلم إذا دخل رجب قال: " اللهم بارك لنا فى رجب وشعبان وبلغنا رمضان ".
** حل سنى
(الأذكار ١٦١/١)
** إسناده فيه ضعف
** قال الإمام النووى فى " الأذكار " ١ / ١٦١: ورويناه أيضا فى كتاب ابن السنى بزيادة.
** تعقيب: قال عبد القادر الأرناؤوط ١ / ١٦١: وهى وكان يقول إن ليلة الجمعة ليلة غراء ويومها أزهر وإسناده ضعيف أيضا.','10','302',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4728,'<span data-type="title" id=toc-4738>٤٧٢٨ </span>- عن أبى هريرة رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " ثلاثة لا ترد٤ دعوتهم: الصائم حتى يفطر، والإمام العادل، ودعوة المظلوم ".
** ت جه
(الأذكار ١٦١/١)
** قال الإمام النووى فى " الأذكار " ١ / ١٦١: قال الترمذى: حديث حسن.','10','303',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4729,'<span data-type="title" id=toc-4739>٤٧٢٩ </span>- عن معاذ بن زهرة أنه بلغه أن النبى صلى الله عليه وسلم كان إذا أفطر قال: " اللهم لك صمت وعلى رزقك أفطرت ".
** د
(الأذكار ١٦٢/١)
** مرسل
** تعقيب: قال عبد القادر الأرناؤوط ١ / ١٦٢: ولكن له شواهد يقوى بها.','10','304',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4730,'<span data-type="title" id=toc-4740>٤٧٣٠ </span>- عن أنس رضى الله عنه: " أن النبى صلى الله عليه وسلم جاء إلى سعد بن عبادة فجاء بخبز وزيت فأكل ثم قال النبى صلى الله عليه وسلم: أفطر عندكم الصائمون وأكل طعامكم الأبرار وصلت عليكم الملائكة ".
** د
(الأذكار ١٦٢/١)
** إسناده صحيح','10','305',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4731,'<span data-type="title" id=toc-4741>٤٧٣١ </span>- عن عائشة رضى الله عنها قالت قلت يا رسول الله إن علمت ليلة القدر ما أقول فيها قال: " قولى اللهم إنك عفو تحب العفو فاعف عنى ".
** ت ن جه
(الأذكار ١٦٢/١)
** إسناده صحيح
** قال الإمام النووى فى " الأذكار " ١ / ١٦٢: قال الترمذى: حديث حسن صحيح.','10','306',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4732,'<span data-type="title" id=toc-4742>٤٧٣٢ </span>- عن معاذ رضى الله عنه أنه سمع رسول الله صلى الله عليه وسلم يقول: " من سأل الله القتل من نفسه صادقا ثم مات أو قتل فإن له أجر شهيد ".
** د ت ن جه
(الأذكار ١٧٧/١)
** قال الإمام النووى فى " الأذكار " ١ / ١٧٧: قال الترمذى: حديث صحيح.
** تعقيب: قال عبد القادر الأرناؤوط ١ / ١٧٧: أخرجه أيضا أحمد فى المسند وهو حديث صحيح، صححه الحافظ وغيره.','10','307',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4733,'<span data-type="title" id=toc-4743>٤٧٣٣ </span>- عن سهل بن سعد رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " ثنتان لا تردان أو قلما تردان الدعاء عند النداء وعند البأس حين يلجم بعضهم بعضا ".
** د
(الأذكار ١٧٩/١)
** إسناده صحيح','10','308',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4734,'<span data-type="title" id=toc-4744>٤٧٣٤ </span>- عن أنس بن مالك رضى الله عنه قال كان رسول الله صلى الله عليه وسلم إذا غزا قال: " اللهم أنت عضدى ونصيرى بك أحول وبك أصول وبك أقاتل ".
** د ت ن
(الأذكار ١٧٩/١)
** قال الإمام النووى فى " الأذكار " ١ / ١٧٩: قال الترمذى: حديث حسن.
** تعقيب: قال عبد القادر الأرناؤوط ١ / ١٧٩: وأخرجه ابن حبان فى صحيحه وهو حديث صحيح صححه الحافظ وغيره.','10','309',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4735,'<span data-type="title" id=toc-4745>٤٧٣٥ </span>- عن أبى موسى الأشعرى رضى الله عنه أن النبى صلى الله عليه وسلم كان إذا خاف قوما قال: " اللهم إنا نجعلك فى نحورهم ونعوذ بك من شرورهم ".
** د ن
(الأذكار ١٧٩/١)
** إسناده صحيح','10','310',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4736,'<span data-type="title" id=toc-4746>٤٧٣٦ </span>- عن عمارة بن زعركة رضى الله عنه قال: سمعت رسول الله صلى الله عليه وسلم يقول: " إن الله تعالى يقول إن عبدى كل عبدى الذى يذكرنى وهو ملاق قرنه ".
** ت
(الأذكار ١٧٩/١)
** قال الإمام النووى فى " الأذكار " ١ / ١٧٩: قال الترمذى: ليس إسناده بالقوى.
** تعقيب: قال عبد القادر الأرناؤوط ١ / ١٧٩: لكن له شاهد حسنه به الحافظ، قال ابن علان فى شرح الأذكار: قال الحافظ: ولكن وجدت له شاهدا قويا مع إرساله أخرجه البغوى من طريق جبير بن نفير فلذلك قلت: حسن.','10','311',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4737,'<span data-type="title" id=toc-4747>٤٧٣٧ </span>- عن سالم أن ابن عمر كان يقول للرجل إذا أراد سفرا: ادن منى أودعك كماكان رسول الله صلى الله عليه وسلم يودعنا فيقول: " استودع الله دينك وأمانتك وخواتيم عملك ".
** ت
(الأذكار ١٨٧/١)
** قال الإمام النووى فى " الأذكار " ١ / ١٨٧: قال الترمذى: هذا حديث حسن صحيح.','10','312',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4738,'<span data-type="title" id=toc-4748>٤٧٣٨ </span>- عن عبد الله بن يزيد الخطمى الصحابى رضى الله عنه قال: كان النبى صلى الله عليه وسلم إذا أراد أن يودع الجيش قال: " استودع الله دينكم وأمانتكم وخواتيم أعمالكم ".
** د
(الأذكار ١٨٧/١)
** إسناده صحيح','10','313',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4739,'<span data-type="title" id=toc-4749>٤٧٣٩ </span>- عن أنس رضى الله عنه قال: جاء رجل إلى النبى صلى الله عليه وسلم فقال: يارسول الله إنى أريد سفرا فزودنى فقال: " زودك الله التقوى " قال: زدنى قال: " وغفر ذنبك "، قال: زدنى قال: " ويسر لك الخير حيثما كنت ".
** ت
(الأذكار ١٨٧/١)
** قال الإمام النووى فى " الأذكار " ١ / ١٨٧: قال الترمذى: حديث حسن.','10','314',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4740,'<span data-type="title" id=toc-4750>٤٧٤٠ </span>- عن أبى هريرة رضى الله عنه أن رجلا قال: يا رسول الله إنى أريد أن أسافر فأوصنى قال: " عليك بتقوى الله تعالى والتكبير على كل شرف " فلما ولى الرجل قال: " اللهم اطو له البعيد وهون عليه السفر ".
** ت جه
(الأذكار ١٨٧/١)
** قال الإمام النووى فى " الأذكار " ١ / ١٨٧: قال الترمذى: حديث حسن.','10','315',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4741,'<span data-type="title" id=toc-4751>٤٧٤١ </span>- عن عمر بن الخطاب رضى الله عنه قال: استأذنت النبى صلى الله عليه وسلم فى العمرة فأذن لى وقال: " لا تنسنا ياأخى من دعائك " فقال كلمة ما يسرنى أن لى بها الدنيا وفى رواية قال: " أشركنا يا أخى فى دعائك ".
** د ت
(الأذكار ١٨٧/١)
** قال الإمام النووى فى " الأذكار " ١ / ١٨٧: قال الترمذى: حديث حسن صحيح.','10','316',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4742,'<span data-type="title" id=toc-4752>٤٧٤٢ </span>- عن على بن ربيعة قال: شهدت على بن أبى طالب رضى الله عنه أتى بدابة ليركبها فلما وضع رجله فى الركاب قال: بسم الله فلما استوى على ظهرها قال: الحمد لله قم قال: سبحان الذى سخر لنا هذا وما كنا له مقرنين وإنا إلى ربنا لمنقلبون، ثم قال: الحمد لله ثلاث مرات ثم قال: الله أكبر ثلاث مرات ثم قال: سبحانك إنى ظلمت نفسى فاغفر لى إنه لا يغفر الذنوب إلا أنت، ثم ضحك فقيل يا أمير المؤمنين من أى شىء ضحكت؟ قال: رأيت النبى صلى الله عليه وسلم فعل مثل ما فعلت ثم ضحك فقلت: يا رسول الله من أى شىء ضحكت؟ قال: " إن ربك سبحانه يعجب من عبده إذا قال: اغفر لى ذنوبى يعلم أنه لا يغفر الذنوب غيرى ".
** د ت ن
(الأذكار ١٨٨/١)
** إسناده صحيح
** قال الإمام النووى فى " الأذكار " ١ / ١٨٨: قال الترمذى: حديث حسن وفى بعض النسخ: حسن صحيح.
** تعقيب: قال عبد القادر الأرناؤوط ١ / ١٨٨: ورواه أيضا ابن حبان فى صحيحه وهو حديث صحيح.','10','317',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4743,'<span data-type="title" id=toc-4753>٤٧٤٣ </span>- عن أبى أيوب خالد بن زيد الأنصارى رضى الله عنه قال: كان رسول الله صلى الله عليه وسلم إذا أكل أو شرب قال: " الحمد لله الذى أطعم وسقى وسوغه وجعل له مخرجا ".
** د ن
(الأذكار ٢٠٢/١)
** إسناده صحيح','10','318',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4744,'<span data-type="title" id=toc-4754>٤٧٤٤ </span>- عن معاذ بن أنس رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " من أكل طعاما فقال:الحمد لله الذى أطعمنى هذا ورزقنيه من غير حول منى ولا قوة غفر له ما تقدم من ذنبه ".
** د ت جه
(الأذكار ٢٠٢/١)
** قال الإمام النووى فى " الأذكار " ١ / ٢٠٢: قال الترمذى: حديث حسن وفى الباب يعنى - باب الحمد على الطعام إذا فرغ منه -
عن عقبة بن عامر وأبى سعيد وعائشة وأبى أيوب وأبى هريرة.','10','319',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4745,'<span data-type="title" id=toc-4755>٤٧٤٥ </span>- عن عبد الله بن سرجس رضى الله عنه قال: كان النبى صلى الله عليه وسلم إذا سافر يقول: اللهم أنت الصاحب فى السفر والخليفة فى الأهل اللهم إنى أعوذ بك من وعثاء السفر وكآبة المنقلب ومن دعوة المظلوم ومن سوء المنظر فى الأهل والمال ".
** ت جه
(الأذكار ١٨٩/١)
** إسناده صحيح
** قال الإمام النووى فى " الأذكار " ١ / ١٨٩: قال الترمذى: حديث حسن صحيح.','10','320',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4746,'<span data-type="title" id=toc-4756>٤٧٤٦ </span>- عن أبى هريرة رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " ثلاث دعوات مستجابات لا شك فيهن: دعوة المظلوم ودعوة المسافر ودعوة الوالد على ولده ".
** د ت جه
(الأذكار ١٩٠/١)
** قال الإمام النووى فى " الأذكار " ١ / ١٩٠: قال الترمذى: حديث حسن. وليس فى رواية أبى داود " على ولده ".','10','321',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4747,'<span data-type="title" id=toc-4757>٤٧٤٧ </span>- عن ابن عمر رضى الله عنهما قال: " كان النبى صلى الله عليه وسلم وجيوشه إذا علوا الثنايا كبروا وإذا هبطوا سبحوا ".
** د
(الأذكار ١٩٠/١)
** صحيح
** تعقيب: قال عبد القادر الأرناؤوط ١ / ١٨٩: هذه الجملة من الحديث مدرجة وليست من حديث أبى داود بسنده وإنما رواها عبد الرازق عن ابن جريج قال: كان النبى صلى الله عليه وسلم.. الخ وهو معضل وقد سها عن هذا الإدراج الإمام النووى فجعله من الحديث وتعقبه الحافظ فى تخريج الأذكار كما فى شرح الأذكار لابن علان ٥ / ١٤٠، فقال وقع فى هذا الحديث خلل من بعض رواته وبيان ذلك أن مسلما وأبا داود وغيرهما أخرجوا هذا الحديث من رواية ابن جريج عن أبى الزبير عن على الأزدى عن ابن عمر قال: كان رسول الله صلى اله عليه وسلم إذا استوى على بعيره خارجا إلي سفر كبر ثلاثا ... الحديث إلى قوله: لربنا حامدون. فاتفق من أخرجه على سياقه إلى هنا، ووقع عند أبى داود بعد " حامدون ": وكان النبى صلى الله عليه وسلم وجيوشه ... إلخ وظاهره أن هذه الزيادة بسند التى قبلها فاعتمد النووى على ذلك وصرح بأنها عن ابن عمر وفيه نظر فإن أبا داود أخرج الحديث عن الحسن بن على عن عبد الرزاق عن ابن جريج بالسند المذكور إلى ابن عمر فوجدنا الحديث فى " مصنف عبد الرزاق " قال فيه: باب القول فى السفر، أخبرنا ابن جريج. . . فذكر الحديث إلى قوله " لربنا حامدون " ثم أورد ثلاثة عشر حديثا بين مرفوع وموقوف ثم قال بعدها: أخبرنا ابن جريج قال: كان النبى صلى الله عليه وسلم وجيوشه إذا صعدوا الثنايا كبروا
وإذا هبطوا سبحوا فوضعت الصلاة على ذلك. هكذا أخرجه معضلا ولم يذكر فيه لابن جريج سندا فظهر أن من عطفه على الأول أو مزجه، أدرجه وهذا أدق ما وجد فى المدرج. اهـ.','10','322',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4748,'<span data-type="title" id=toc-4758>٤٧٤٨ </span>- عن أبى هريرة رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " اللهم اغفر للحاج ولمن استغفر له الحاج ".
** هق
(الأذكار ١٩٥/١)
** قال الإمام النووى فى " الأذكار " ١ / ١٩٥: قال الحاكم: هو صحيح على شرط مسلم.
** تعقيب: قال عبد القادر الأرناؤوط ١ / ١٩٥: حسنه الحافظ فى تخريج الأذكار.','10','323',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4749,'<span data-type="title" id=toc-4759>٤٧٤٩ </span>- عن عائشة رضى الله عنها قالت: قال رسول الله صلى الله عليه وسلم: " إذا أكل أحدكم فليذكر اسم اله تعالى فى أوله فإن نسى أن يذكر اسم الله تعالى فى أوله فليقل بسم الله أوله وآخره ".
** د ت
(الأذكار ١٩٦/١)
** قال الإمام النووى فى " الأذكار " ١/ ١٩٦: قال الترمذى: حديث حسن صحيح.','10','324',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4750,'<span data-type="title" id=toc-4760>٤٧٥٠ </span>- عن عائشة رضى الله عنها قالت: كان رسول الله صلى الله عليه وسلم يأكل طعاما فى ستة من أصحابه فجاء أعرابى فأكله بلقمتين فقال رسول الله صلى الله عليه وسلم: " أما إنه لو سمى لكفاكم ".
** ت
(الأذكار ١٩٧/١)
** قال الإمام النووى فى " الأذكار " ١ / ١٩٧: قال الترمذى: حديث حسن صحيح.','10','325',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4751,'<span data-type="title" id=toc-4761>٤٧٥١ </span>- عن عبد الرحمن بن جبير التابعى أنه حدثه رجل خدم النبى صلى الله عليه وسلم ثمانى سنين أنه كان يسمع النبى صلى الله عليه وسلم إذا قرب إليه الطعام يقول: " بسم الله " فإذا فرغ من طعامه قال: " اللهم أطعمت وسقيت وأغنيت وأقنيت وهديت وأحييت فلك الحمد على ما أعطيت ".
** ن سنى
(الأذكار ٢٠٢/١)
** إسناده حسن
** تعقيب: قال عبد القادر الأرناؤوط ١ / ٢٠٢: قال ابن علان فى شرح الأذكار: قال الحافظ بعد تخريج الحديث: هذا حديث صحيح أخرجه النسائى فى الكبرى. وقال الأرناؤوط ١ / ٢٠٢: قال الحافظ: قوله - يعنى النووى - باسناد حسن، قال الحافظ: فى اقتصاره على
حسن نظر فإن رجال سنده من يونس إلى الصحابى أخرج لهم مسلم وقد صرح التابعى بأن الصحابى حدثه فى رواية المقرىء فلعل النووى خفى عليه حال ابن هبيرة.','10','326',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4752,'<span data-type="title" id=toc-4762>٤٧٥٢ </span>- عن ابن عباس رضى الله عنهما قال: قال رسول الله صلى الله عليه وسلم " إذا أكل أحدكم طعاما " وفى رواية ابن السنى " من أطعمه الله طعاما فليقل: اللهم بارك لنا فيه وأطعمنا خيرا منه ومن سقاه الله تعالى لبنا فليقل: اللهم بارك لنا فيه وزدنا منه فإنه ليس شىء يجزىء من الطعام والشراب غير اللبن ".
** د ت سنى
(الأذكار ٢٠٣/١)
** قال الإمام النووى فى " الأذكار " ١ / ٢٠٣: قال الترمذى: حديث حسن.','10','327',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4753,'<span data-type="title" id=toc-4763>٤٧٥٣ </span>- عن عبد الله بن مسعود رضى الله عنه قال: " كان رسول الله صلى الله عليه وسلم إذا شرب فى الإناء تنفس ثلاثة أنفاس يحمد الله تعالى فى كل نفس ويشكره فى آخره ".
** سنى
(الأذكار ٢٠٣/١)
** إسناده ضعيف
** تعقيب: قال عبد القادر الأرناؤوط ١ / ٢٠٣: وللمتن شاهد عن أبى هريرة. وقال الأرناؤوط ١ / ٢٠٣: قال الحافظ بعد تخريجه من طريق الطبرانى أيضا: هذا حديث حسن خرجه الخرائطى فى فضيلة الشكر.','10','328',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4754,'<span data-type="title" id=toc-4764>٤٧٥٤ </span>- عن عبد الله بن سلام رضى الله عنه قال: سمعت رسول الله صلى الله عليه وسلم يقول: " ياأيها الناس أفشوا السلام وأطعموا الطعام وصلوا الأرحام وصلوا والناس نيام تدخلوا الجنة بسلام ".
** دى ت جه
(الأذكار ٢٠٧/١)
** إسناده جيد
** قال الإمام النووى فى " الأذكار " ١ / ٢٠٧: قال الترمذى: حديث صحيح.
** تعقيب: قال عبد القادر الأرناؤوط ١ / ٢٠٧: قال الحافظ: حديث حسن.','10','329',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4755,'<span data-type="title" id=toc-4765>٤٧٥٥ </span>- عن عمران بن حصين رضى الله عنهما قال: جاء رجل إلى النبى صلى الله عليه وسلم فقال: السلام عليكم فرد عليه ثم جلس فقال النبى صلى الله عليه وسلم: " عشر " ثم جاء آخر فقال: السلام عليكم ورحمة الله فرد عليه ثم جلس، فقال: " عشرون " ثم جاء آخر فقال: السلام عليكم ورحمة الله وبركاته فرد عليه فجلس فقال " ثلاثون ".
** دى د ت
(الأذكار ٢٠٨/١)
** قال الإمام النووى فى " الأذكار " ١ / ٢٠٩: قال الترمذى: حديث حسن.','10','330',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4756,'<span data-type="title" id=toc-4766>٤٧٥٦ </span>- عن أنس رضى الله عنه قال: كان رجل يمر بالنبى صلى الله عليه وسلم يرعى دواب أصحابه فيقول: السلام عليك يا رسول الله فيقول له النبى صلى الله عليه وسلم وعليك السلام ورحمة الله وبركاته ومغفرته ورضوانه فقيل: يا رسول الله تسلم على هذا سلاما ما تسلمه على أحد من أصحابك؟ فقال: وما يمنعنى من ذلك وهو ينصرف بأجر بضعة عشر رجلا؟ ".
** سنى
(الأذكار ٢٠٩/١)
** إسناده ضعيف','10','331',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4757,'<span data-type="title" id=toc-4767>٤٧٥٧ </span>- عن عمرو بن شعيب عن أبيه عن جده عن النبى صلى الله عليه وسلم قال: " ليس منا من تشبه بغيرنا لا تشبهوا باليهود ولا بالنصارى فإن تسليم اليهود الإشارة بالأصابع وتسليم النصارى الإشارة بالكف ".
** ت
(الأذكار ٢١٠/١)
** قال الإمام النووى فى " الأذكار " ١ / ٢١٠: قال الترمذى: إسناده ضعيف.
** تعقيب: قال عبد القادر الأرناؤوط ١ / ٢١٠: ولكن له شواهد بمعناه يقوى بها.','10','332',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4758,'<span data-type="title" id=toc-4768>٤٧٥٨ </span>- عن أسماء بنت يزيد أن رسول الله صلى الله عليه وسلم: " مر فى المسجد يوما وعصبة من النساء قعود فألوى بيده بالتسليم ".
** ت د
(الأذكار ٢١٠/١)
** قال الإمام النووى فى " الأذكار " ١ / ٢١٠: قال الترمذى: حديث حسن. وقال الإمام النووى فى " الأذكار " ١ / ٢١٠: أن أبا داود روى هذا الحديث، وقال فى روايته: " فسلم علينا ".','10','333',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4759,'<span data-type="title" id=toc-4769>٤٧٥٩ </span>- عن زيد بن أسلم أن رسول الله صلى الله عليه وسلم قال: " إذا سلم واحد من القوم أجزأ عنهم ".
** لم
(الأذكار ٢١١/١)
** مرسل صحيح الإسناد','10','334',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4760,'<span data-type="title" id=toc-4770>٤٧٦٠ </span>- عن غالب القطان عن رجل قال: حدثنى أبى عن جدى قال: بعثنى أبى إلى رسول الله صلى الله عليه وسلم فقال: ائته فأقرئه السلام فأتيته فقلت: إن أبى يقرئك السلام، فقال: " وعليك وعلى أبيك السلام ".
** د
(الأذكار ٢١٢/١)
** قال الإمام النووى فى " الأذكار " ١ / ٢١٢: وهذا وإن كان رواية عن مجهول فقد قدمنا أن أحاديث الفضائل يتسامح فيها عند أهل العلم كلهم.','10','335',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4761,'<span data-type="title" id=toc-4771>٤٧٦١ </span>- عن أبى جرى الهجيمى رضى الله عنه واسمه جابر بن سليم وقيل سليم بن جابر قال: أتيت رسول الله صلى الله عليه وسلم فقلت: عليك السلام يا رسول الله قال: " لا تقل عليك السلام فإن عليك السلام تحية الموتى ".
** د ت
(الأذكار ٢١٤/١)
** إسناده صحيح
** قال الإمام النووى فى " الأذكار " ١ / ٢١٤: قال الترمذى: حديث حسن صحيح.
** تعقيب: قال عبد القادر الأرناؤوط ١ / ٢١٤: واسمه جابر بن سليم قال البخارى: إنه صحيح وكذا رجحه ابن عبد البر أيضا قال ابن علان: قال الحافظ فى فتح البارى فى أول كتاب الإستئذان: قال النووى بالأسانيد الصحيحة ... الخ يوهم أن له طرقا إلى الصحابى المذكور وليس كذلك فإنه لم يروه عن النبى صلى الله عليه وسلم غير أبى جرى ومع ذلك فمداره عند جميع من أخرجه عن أبى تميمة الهجيمى رواية عن جرى وقد أخرجه أيضا أحمد والنسائى وصححه الحاكم.','10','336',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4762,'<span data-type="title" id=toc-4772>٤٧٦٢ </span>- عن جابر رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " السلام قبل الكلام ".
** ت
(الأذكار ٢١٤/١)
** ضعيف
** قال الإمام النووى فى " الأذكار " ١ / ٢١٤: قال الترمذى: هذا حديث منكر.','10','337',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4763,'<span data-type="title" id=toc-4773>٤٧٦٣ </span>- عن أبى أمامة رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " إن أولى الناس بالله من بدأهم بالسلام ".
** د
(الأذكار ٢١٤/١)
** إسناده جيد','10','338',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4764,'<span data-type="title" id=toc-4774>٤٧٦٤ </span>- عن أبى أمامة رضى الله عنه قيل يا رسول الله الرجلان يلتقيان أيهما يبدأ بالسلام قال: " أولاهما بالله تعالى ".
** ت
(الأذكار ٢١٤/١)
** قال الإمام النووى فى " الأذكار " ١ / ٢١٤: قال الترمذى: حديث حسن.','10','339',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4765,'<span data-type="title" id=toc-4775>٤٧٦٥ </span>- عن أسماء بنت يزيد رضى الله عنها قالت: " مر علينا رسول الله صلى الله عليه وسلم فى نسوة فسلم علينا ".
** د ت جه
(الأذكار ٢١٦/١)
** قال الإمام النووى فى " الأذكار " ١ / ٢١٦: قال الترمذى: حديث حسن.','10','340',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4766,'<span data-type="title" id=toc-4776>٤٧٦٦ </span>- عن أنس " أن النبى صلى الله عليه وسلم مر على غلمان يلعبون فسلم عليهم ".
** د
(الأذكار ٢١٨/١)
** إسناده إسناد الصحيحين
** قال الإمام النووى فى " الأذكار " ١ / ٢١٨: فى كتاب ابن السنى وغيره قال فيه: " فقال السلام عليكم يا صبيان ".
** تعقيب: قال عبد القادر الأرناؤوط ١ / ٢١٨: قال ابن علان: قال الحافظ هو بعينه حديث الصحيحين إلا أن فيه زيادة يلعبون.','10','341',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4767,'<span data-type="title" id=toc-4777>٤٧٦٧ </span>- عن أبى هريرة رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " إذا انتهى أحدكم إلى المجلس فليسلم فإذا أراد أن يقوم فليسلم فليست الأولى بأحق من الآخرة ".
** د ت
(الأذكار ٢٢٠/١)
** إسناده جيد
** قال الإمام النووى فى " الأذكار " ١ / ٢٢٠: قال الترمذى: حديث حسن.','10','342',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4768,'<span data-type="title" id=toc-4778>٤٧٦٨ </span>- عن ربعى بن حراش التابعى الجليل قال: حدثنا رجل من بنى عامر استأذن على النبى صلى الله عليه وسلم وهو فى بيت فقال أألج؟ فقال رسول الله صلى الله عليه وسلم لخادمه: " اخرج إلى هذا فعلمه الإستئذان فقل له: قل: السلام عليكم، أأدخل؟ فسمعه الرجل فقال: السلام عليكم، أأدخل؟ فأذن له النبى صلى
الله عليه وسلم فدخل ".
** د
(الأذكار ٢٢٢/١)
** إسناده صحيح','10','343',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4769,'<span data-type="title" id=toc-4779>٤٧٦٩ </span>- عن كلدة بن الحنبل الصحابى رضى الله عنه قال: أتيت النبى صلى الله عليه وسلم فدخلت عليه ولم أسلم فقال النبى صلى الله عليه وسلم: " ارجع فقل: السلام عليكم أأدخل؟ ".
** د ت
(الأذكار ٢٢٢/١)
** قال الإمام النووى فى " الأذكار " ١ / ٢٢٢: قال الترمذى: حديث حسن.','10','344',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4770,'<span data-type="title" id=toc-4780>٤٧٧٠ </span>- عن صفوان بن عسال قال: " قال يهودى لصاحبه: اذهب بنا إلى هذا النبى فأتيا رسول الله صلى الله عليه وسلم فسألاه عن تسع آيات بينات ... فذكر الحديث ... إلى قوله: فقبلوا يده ورجله، وقالا نشهد إنك نبى ".
** ت ن جه
(الأذكار ٢٢٥/١)
** إسناده صحيح','10','345',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4771,'<span data-type="title" id=toc-4781>٤٧٧١ </span>- عن عائشة رضى الله عنها قالت: " قدم زيد بن حارثة المدينة ورسول الله صلى الله عليه وسلم فى بيتى فأتاه فقرع الباب فقام إليه النبى صلى الله عليه وسلم يجر ثوبه فاعتنقه وقبله ".
** ت
(الأذكار ٢٢٦/١)
** قال الإمام النووى فى " الأذكار " ١ / ٢٢٦: قال الترمذى: حديث حسن.','10','346',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4772,'<span data-type="title" id=toc-4782>٤٧٧٢ </span>- عن أنس بن مالك رضى الله عنه قال: قال رجل يا رسول الله الرجل منا يلقى أخاه أو صديقه أينحنى له؟ قال: " لا " قال: أفيلتزمه ويقبله؟ قال: " لا " قال: فيأخذ بيده ويصافحه؟ قال: " نعم ".
** ت جه
(الأذكار ٢٢٦/١)
** قال الإمام النووى فى " الأذكار " ١ / ٢٢٦: قال الترمذى: حديث حسن.','10','347',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4773,'<span data-type="title" id=toc-4783>٤٧٧٣ </span>- عن أنس رضى الله عنه قال: لما جاء أهل اليمن قال لهم رسول الله صلى الله عليه وسلم: " قد جاءكم أهل اليمن وهم أول من جاء بالمصافحة ".
** د
(الأذكار ٢٢٧/١)
** إسناده صحيح','10','348',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4774,'<span data-type="title" id=toc-4784>٤٧٧٤ </span>- عن عطاء بن عبد الله الخراسانى قال: قال لى رسول الله صلى الله عليه وسلم " تصافحوا يذهب الغل وتهادوا تحابوا وتذهب الشحناء ".
** ما
(الأذكار ٢٢٧/١)
** مرسل
** تعقيب: قال عبد القادر الأرناؤوط ١ / ٢٢٧: لكنه يعتضد بما جاء له من الشواهد الموصولة. قال الزرقانى فى شرح الموطأ: قال ابن عبد البر: هذا يتصل من وجوه شتى حسان كلها.','10','349',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4775,'<span data-type="title" id=toc-4785>٤٧٧٥ </span>- عن أبى هريرة رضى الله عنه عن النبى صلى الله عليه وسلم قال: " إذا عطس أحدكم فليقل الحمد لله على كل حال وليقل أخوه أو صاحبه: يرحمك الله ويقول هو: يهديكم الله ويصلح بالكم ".
** د
(الأذكار ٢٣١/١)
** إسناده صحيح','10','350',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4776,'<span data-type="title" id=toc-4786>٤٧٧٦ </span>- عن أبى هريرة رضى الله عنه قال: "كان رسول الله صلى الله عليه وسلم إذا عطس وضع يده أو ثوبه على فمه وخفض - أو غض - بها صوته ". شك الراوى أى اللفظين قال.
** د ت
(الأذكار ٢٣٢/١)
** قال الإمام النووى فى " الأذكار " ١ / ٢٣٢: قال الترمذى: حديث حسن صحيح.','10','351',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4777,'<span data-type="title" id=toc-4787>٤٧٧٧ </span>- عن عبيد الله بن رفاعة الصحابى رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " يشمت العاطس ثلاثا فإن زاد فإن شئت فشمته وإن شئت فلا ".
** د ت
(الأذكار ٢٣٤/١)
** ضعيف
** قال الإمام النووى فى " الأذكار " ١ / ٢٣٤: قال الترمذى: حديث غريب وإسناده مجهول.
** تعقيب: قال عبد القادر الأرناؤوط ١ / ٢٣٤: قال الحافظ فى الفتح ١٠ / ٤٩٩ فى الأدب باب تشميت العاطس: إطلاقه عليه الضعف ليس بجيد إذ لا يلزم من الغرابة الضعف. قال الحافظ: وأما وصف الترمذى إسناده يكونه مجهولا فلم يرد جميع رجال الإسناد فإن معظمهم موثقون وإنما وقع فى روايته تغير اسم بعض رواته وإبهام اثنين منهم وذلك أن أبا داود والترمذى أخرجاه معا من طريق عبد السلام بن حرب
عن يزيد بن عبد الرحمن ثم اختلفا فأما رواية أبى داود ففيها عن يحيى بن إسحاق بن طلحة عن أمه حميدة أو عبيدة بنت عبيد بن رفاعة عن أبيها وهذا إسناد حسن والحديث مع ذلك مرسل كما سأبينه، وعبد السلام بن حرب من رجال الصحيح ويزيد هو أبو خالد الدالانى وهو صدوق فى حفظه شىء ويحيى بن إسحاق وثقه يحيى بن معين وأمه حميدة روى عنها أيضا زوجها إسحاق بن أبى طلحة وذكرها ابن حبان فى ثقات التابعين وأبوها عبيد بن رفاعة ذكروه فى الصحابة لكونه ولد فى عهد النبى
صلى الله عليه وسلم وله رؤية قاله ابن السكن. قال: ولم يصح سماعه، وقال البغوى: روايته مرسلة، وحديثه عن أبيه عند الترمذى والنسائى وغيرهما وأما رواية الترمذى ففيها عن عمر بن إسحاق بن أبى
طلحة عن أمه عن أبيها كذا سماه عمر، ولم يسم أمه ولا أباها وكأنه يعنى الترمذى لم يمعن النظر فمن ثم قال: إن إسناده مجهول وقد تبين أنه ليس بالمجهول، وأن الصواب يحيى بن إسحاق لا عمر فقد أخرجه الحسن بن سفيان وابن السنى وأبو نعيم وغيرهم من طريق عبد السلام بن حرب فقالوا: يحيى بن إسحاق وقالوا: حميدة بغير شك وهو المعتمد. قال الأرناؤوط ١ / ٢٣٤: وقد ذكر الحافظ لهذا الحديث شواهد كثيرة مرسلة وموقوفة انظرها فى الفتح ١٠ / ٤٩٨.','10','352',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4778,'<span data-type="title" id=toc-4788>٤٧٧٨ </span>- عن أبى هريرة رضى الله عنه قال: سمعت رسول الله صلى الله عليه وسلم يقول " إذا عطس أحدكم فليشمته جليسه وإن زاد على ثلاثة فهو مزكوم ولا يشمت بعد ثلاث ".
** سنى
(الأذكار ٢٣٤/١)
** قال الإمام النووى فى " الأذكار " ١ / ٢٣٤: إسناده فيه رجل لم أتحقق حاله وباقى إسناده صحيح.
** تعقيب: قال عبد القادر الأرناؤوط ١ / ٢٣٤: قال الحافظ فى الفتح: الرجل المذكور هو سليمان بن أبى داود الحرانى والحديث عندهما من رواية محمد بن سليمان عن أبيه ومحمد موثق وأبوه يقال له الحرانى ضعيف، قال فيه النسائى: ليس بثقة ولا مأمون.','10','353',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4779,'<span data-type="title" id=toc-4789>٤٧٧٩ </span>- عن أبى موسى الأشعرى رضى الله عنه قال: كان اليهود يتعاطسون عند رسول الله صلى الله عليه وسلم يرجون أن يقول لهم: يرحمكم الله فيقول لهم: " يهديكم الله ويصلح بالكم ".
** د ت
(الأذكار ٢٣٥/١)
** إسناده صحيح
** قال الإمام النووى فى " الأذكار " ١ / ٢٣٥: قال الترمذى: حديث حسن صحيح.','10','354',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4780,'<span data-type="title" id=toc-4790>٤٧٨٠ </span>- عن أبى هريرة رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " من حدث حديثا فعطس عنده فهو حق ".
** يع
(الأذكار ٢٣٥/١)
** إسناده ثقات إلا بقية
** قال الإمام النووى فى " الأذكار "١ ١/ ٢٣٥: كل إسناده ثقات متقنون إلا بقية بن الوليد فمختلف فيه وأكثر الحفاظ والأئمة يحتجون بروايته عن الشاميين وقد روى هذا الحديث عن معاوية بن يحيى الشامى.
** تعقيب: قال عبد القادر الأرناؤوط ١ / ٢٣٥: قال الحافظ السخاوى فى المقاصد الحسنة: رواه أبو يعلى من حديث بقية عن معاوية بن يحيى عن أبى الزناد عن الأعرج عن أبى هريرة مرفوعا وكذا أخرجه الطبرانى والدارقطنى فى الأفراد والبيهقى وقال: إنه منكر. اهـ. أقول: بقية بن الوليد صدوق كثير التدليس عن الضعفاء وروايته هنا عن معاوية بن يحيى الصدفى الشامى، قال الهيثمى: ضعيف وقال الحافظ بن حجر: ضعيف وما حدث به بالشام أحسن مما حدث بالرى، وقال المناوى فى فيض القدير وبالجملة هو حديث ضعيف، قال السخاوى فى " المقاصد الحسنة ": وله شاهد عند الطبرانى من حديث خضر بن محمد بن شجاع عن غضيف بن سالم عن عمارة بن زاذان عن ثابت عن أنس مرفوعا.','10','355',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4781,'<span data-type="title" id=toc-4791>٤٧٨١ </span>- عن أبى هريرة رضى الله عنه عن النبى صلى الله عليه وسلم قال: " كل كلام " وفى بعض الروايات " كل أمر لا يبدأ فيه بالحمد لله فهو أجذم " وروى أقطع.
** د جه
(الأذكار ٢٤٠/١)
** حسن
** تعقيب: قال عبد القادر الأرناؤوط ١ / ٢٤٠: ورواه أيضا أحمد فى المسند وابن حبان فى صحيحه، وفى سنده قرة بن عبد الرحمن بن حيوئيل وهو صدوق له مناكير كما قال الحافظ فى التقريب ومع ذلك فقد حسنه المصنف ونقل ابن علان فى شرح الأذكار عن الحافظ تحسينه، وحسنه أيضا ابن الصلاح والعراقى وغيرهم.','10','356',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4782,'<span data-type="title" id=toc-4792>٤٧٨٢ </span>- عن أبى هريرة رضى الله عنه عن النبى صلى الله عليه وسلم قال: " كل خطبة ليس فيها تشهد فهى كاليد الجذماء ".
** د ت
(الأذكار ٢٤٠/١)
** قال الإمام النووى فى " الأذكار " ١ / ٢٤٠: قال الترمذى: حديث حسن.','10','357',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4783,'<span data-type="title" id=toc-4793>٤٧٨٣ </span>- عن عبد الله بن مسعود رضى الله عنه قال: علمنا رسول الله صلى الله عليه وسلم خطبة الحاجة: " الحمد لله نستعينه ونستغفره ونعوذ به من شرور أنفسنا من يهد الله فلا مضل له ومن يضلل فلا هادى له وأشهد أن لا إله إلاالله وحده لا شريك
له وأشهد أن محمدا عبده ورسوله " يأيها الناس اتقوا ربكم الذى خلقكم من نفس واحدة وخلق منها زوجها وبث منهما رجالا كثيرا ونساء واتقوا الله الذى تسائلون به والأرحام إن الله كان عليكم رقيبا " النساء: ا " ياأيها الذين آمنوا اتقوا الله حق تقاته ولا تموتن إلا وأنتم مسلمون " آل عمران ١٠٢، ياأيها الذين آمنوا اتقوا الله وقولوا قولا سديدا يصلح لكم أعمالكم ويغفر لكم ذنوبكم ومن يطع الله ورسوله فقد فاز فوزا عظيما " الأحزاب: ٧١ هذا لفظ إحدى روايات أبى داود. وفى رواية له أخرى: بعد قوله ورسوله " أرسله بالحق بشيرا ونذيرا بين يدى الساعة ومن يطع الله ورسوله فقد رشد ومن يعصهما فإنه لا يضر إلا نفسه ولا يضر الله شيئا ".
** د ت ن جه
(الأذكار ٢٤١/١)
** إسناده صحيح
** قال الإمام النووى فى " الأذكار " ١/ ٢٤١: قال الترمذى: حديث حسن.','10','358',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4784,'<span data-type="title" id=toc-4794>٤٧٨٤ </span>- عن أبى هريرة رضى الله عنه أن النبى صلى الله عليه وسلم كان إذا رفأ الإنسان إذا تزوج قال: " بارك الله لك وبارك عليك وجمع بينكما فى خير ".
** د ت جه
(الأذكار ٢٤٢/١)
** إسناده صحيح
** قال الإمام النووى فى " الأذكار " ١ / ٢٤٢: قال الترمذى: حديث حسن صحيح.','10','359',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4785,'<span data-type="title" id=toc-4795>٤٧٨٥ </span>- عن عمرو بن شعيب عن أبيه عن جده رضى الله عنه عن النبى صلى الله عليه وسلم قال: " إذا تزوج أحدكم امرأة أو اشترى خادما فليقل: اللهم إنى أسألك خيرها وخير ما جبلتها عليه وأعوذ بك من شرها وشر ما جبلتها عليه وإذا اشترى بعيرا فليأخذ بذروة سنامه وليقل مثل ذلك " وفى رواية " ثم ليأخذ بناصيتها وليدع بالبركة فى المرأة والخادم ".
** د جه سنى
(الأذكار ٢٤٢/١)
** إسناده صحيح
** تعقيب: قال عبد القادر الأرناؤوط ١ / ٢٤٢: إسناده حسن وصححه الحاكم ووافقه الذهبى.','10','360',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4786,'<span data-type="title" id=toc-4796>٤٧٨٦ </span>- عن أبى رافع رضى الله عنه مولى رسول الله صلى الله عليه وسلم قال: " رأيت رسول الله صلى الله عليه وسلم أذن فى أذن الحسن بن على حين ولدته فاطمة بالصلاة رضى الله عنهم ".
** د ت
(الأذكار ٢٤٤/١)
** قال الإمام النووى فى " الأذكار " ١ / ٢٤٤: قال الترمذى: حديث حسن صحيح.','10','361',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4787,'<span data-type="title" id=toc-4797>٤٧٨٧ </span>- عن عائشة رضى الله عنها قالت: " كان رسول الله صلى الله عليه وسلم يؤتى بالصبيان فيدعو لهم ويحنكهم ". وفى رواية " فيدعو لهم بالبركة ".
** د
(الأذكار ٢٤٤/١)
** إسناده صحيح','10','362',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4788,'<span data-type="title" id=toc-4798>٤٧٨٨ </span>- عن عمرو بن شعيب عن أبيه عن جده: " أن النبى صلى الله عليه وسلم أمر بتسمية المولود فى يوم سابعه ووضع الأذى عنه والعق ".
** ت
(الأذكار ٢٤٥/١)
** قال الإمام النووى فى " الأذكار " ١ / ٢٤٥: قال الترمذى: حديث حسن.','10','363',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4789,'<span data-type="title" id=toc-4799>٤٧٨٩ </span>- عن سمرة بن جندب رضى الله عنه أن رسول الله صلى الله عليه وسلم قال: " كل غلام رهينة بعقيقته تذبح عنه يوم سابعه ويحلق ويسمى ".
** د ت ن جه
(الأذكار ٢٤٥/١)
** إسناده صحيح
** قال الإمام النووى فى " الأذكار " ١ / ٢٤٥: قال الترمذى: حديث حسن صحيح.','10','364',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4790,'<span data-type="title" id=toc-4800>٤٧٩٠ </span>- عن أبى الدرداء رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " إنكم تدعون يوم القيامة بأسمائكم وأسماء آبائكم فأحسنوا أسمائكم ".
** د
(الأذكار ٢٤٦/١)
** إسناده جيد
** تعقيب: قال عبد القادر الأرناؤوط ١ / ٢٤٦: إلا أن فيه انقطاعا بين عبد الله بن أبى زكريا وأبى الدرداء فانه لم يدركه كما نص على ذلك المنذرى والحافظ وغيرهما.','10','365',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4791,'<span data-type="title" id=toc-4801>٤٧٩١ </span>- عن أسامة بن أخدرى الصحابى رضى الله عنه أن رجلا يقال له أصرم كان فى النفر الذين أتوا رسول الله صلى الله عليه وسلم فقال رسول الله صلى الله عليه وسلم " ما أسمك؟ قال: أصرم قال: بل أنت زرعة ".
** د
(الأذكار ٢٤٩/١)
** إسناده حسن','10','366',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4792,'<span data-type="title" id=toc-4802>٤٧٩٢ </span>- عن عائشة رضى الله عنه أنها قالت: يارسول الله كل صواحبى لهن كنى قال " فاكتنى بابنك عبد الله ". قال الراوى: يعنى عبد الله بن الزبير وهو ابن اختها أسماء بنت أبى بكر وكانت عائشة تكنى أم عبد الله.
** د
(الأذكار ٢٥١/١)
** إسناده صحيح
** قال الإمام النووى فى " الأذكار " ١ / ٢٥١: فهذا هو الصحيح المعروف.','10','367',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4793,'<span data-type="title" id=toc-4803>٤٧٩٣ </span>- عن عائشة رضى الله عنها قالت: " أسقطت من النبى صلى الله عليه وسلم سقطا فسماه عبد الله وكنانى بأم عبد الله ".
** سنى
(الأذكار ٢٥١/١)
** ضعيف
** تعقيب: قال عبد القادر الأرناؤوط ١ / ٢٤٦: وهو حديث ضعيف.','10','368',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4794,'<span data-type="title" id=toc-4804>٤٧٩٤ </span>- عن أبى هريرة رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " من جلس فى مجلس فكثر فيه لغطه فقال قبل أن يقوم من مجلسه ذلك: سبحانك اللهم وبحمدك أشهد أن لا إله إلا أنت أستغفرك وأتوب إليك إلا غفر له ماكان من مجلسه ذلك ".
** ت
(الأذكار ٢٥٤/١)
** قال الإمام النووى فى " الأذكار " ١ / ٢٥٤: قال الترمذى: حديث حسن صحيح.','10','369',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4795,'<span data-type="title" id=toc-4805>٤٧٩٥ </span>- عن ابن عمر رضى الله عنهما قال: قلما كان رسول الله صلى الله عليه وسلم يقوم من مجلس حتى يدعو بهؤلاء الدعوات لأصحابه: " اللهم اقسم لنا من خشيتك ما تحول به بيننا وبين معاصيك ومن طاعتك ما تبلغنا به جنتك ومن اليقين ما تهون به علينا مصائب الدنيااللهم متعنا بأسماعنا وأبصارنا وقوتنا ماأحييتنا واجعله الوارث منا واجعل ثأرنا على من ظلمنا وانصرنا على من عادنا ولاتجعل مصيبتنا فى ديننا ولا تجعل الدنيا أكبر همنا ولا مبلغ علمنا ولا تسلط علينا من لا يرحمنا ".
** ت
(الأذكار ٢٥٥/١)
** قال الإمام النووى فى " الأذكار " ١ / ٢٥٥: قال الترمذى: حديث حسن.','10','370',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4796,'<span data-type="title" id=toc-4806>٤٧٩٦ </span>- عن أبى هريرة رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " ما من قوم يقومون من مجلس لا يذكرون الله تعالى فيه إلا قاموا عن مثل جيفة حمار وكان لهم حسرة ".
** د
(الأذكار ٢٥٥/١)
** إسناده صحيح','10','371',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4797,'<span data-type="title" id=toc-4807>٤٧٩٧ </span>- عن أبى هريرة رضى الله عنه عن النبى صلى الله عليه وسلم قال: " ما جلس قوم مجلسا لم يذكروا الله تعالى فيه ولم يصلوا على نبيهم فيه إلا كان عليهم ترة فإن شاء عذبهم وإن شاء غفر لهم ".
** ت
(الأذكار ٢٥٥/١)
** قال الإمام النووى فى " الأذكار " ١ / ٢٥٦: قال الترمذى: حديث حسن.','10','372',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4798,'<span data-type="title" id=toc-4808>٤٧٩٨ </span>- عن معاذ بن أنس الجهنى الصحابى رضى الله عنه أن النبى صلى الله عليه وسلم قال: " من كظم غيظا وهو قادر على أن ينفذه دعاه الله سبحانه وتعالى على رؤوس الخلائق يوم القيامة حتى يخيره من الحور ما شاء ".
** د ت جه
(الأذكار ٢٥٦/١)
** قال الإمام النووى فى " الأذكار " ١ / ٢٥٧: قال الترمذى: حديث حسن.','10','373',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4799,'<span data-type="title" id=toc-4809>٤٧٩٩ </span>- عن المقدام بن معد يكرب رضى الله عنه عن النبى صلى الله عليه وسلم قال " إذا أحب الرجل أخاه فليخبره أنه يحبه ".
** د ت
(الأذكار ٢٥٧/١)
** قال الإمام النووى فى " الأذكار " ١ / ٢٥٨: قال الترمذى: حديث حسن صحيح.','10','374',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4800,'<span data-type="title" id=toc-4810>٤٨٠٠ </span>- عن يزيد بن نعامة الضبى قال: قال رسول الله صلى الله عليه وسلم: " إذا آخى الرجل الرجل فليسأله عن اسمه واسم أبيه وممن هو فإنه أوصل للمودة ".
** ت
(الأذكار ٢٥٨/١)
** قال الإمام النووى فى " الأذكار " ١ / ٢٥٨: قال الترمذى: حديث غريب لا نعرفه إلا من هذا الوجه قال: ولا نعلم ليزيد بن نعامة سماعا عن النبى صلى الله عليه وسلم، قال: ويروى عن ابن عمر عن النبى صلى الله عليه وسلم نحو هذا ولا يصح إسناده. قال الإمام النووى فى " الأذكار " ١ / ٢٥٨: وقد اختلف فى صحبة يزيد بن نعامة فقال أبى عبد الرحمن بن أبى حاتم: لا صحبة له. قال: وحكى البخارى أن له صحبة، قال: وغلط.','10','375',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4801,'<span data-type="title" id=toc-4811>٤٨٠١ </span>- عن أبى هريرة رضى الله عنه عن النبى صلى الله عليه وسلم قال: " من رأى مبتلى فقال: الحمد لله الذى عافانى مما ابتلاك به وفضلنى على كثير ممن خلق تفضيلا لم يصبه ذلك البلاء ".
** ت
(الأذكار ٢٥٨/١)
** قال الإمام النووى فى " الأذكار " ١ / ٢٥٨: قال الترمذى: حديث حسن.','10','376',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4802,'<span data-type="title" id=toc-4812>٤٨٠٢ </span>- عن عمربن الخطاب رضى الله عنه أن رسول الله صلى الله عليه وسلم قال: " من رأى صاحب بلاء فقال: الحمد لله الذى عافانى مما ابتلاك به وفضلنى على كثير ممن خلق تفضيلا إلا عوفى من ذلك البلاء كائنا ما كان ما عاش ".
** ت
(الأذكار ٢٥٨/١)
** قال الإمام النووى فى " الأذكار " ١ / ٢٥٨: ضعف الترمذى إسناده.
** تعقيب: قال عبد القادر الأرناؤوط ١ / ٢٥٨: ولكن يشهد له الذى قبله فهو به حسن. * الذى قبله حديث أبى هريرة عند الترمذى " من رأى مبتلى ".','10','377',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4803,'<span data-type="title" id=toc-4813>٤٨٠٣ </span>- عن أبى المليح التابعى المشهور عن رجل قال: كنت رديف النبى صلى الله عليه وسلم فعثرت دابته فقلت: تعس الشيطان فقال: " لا تقل: تعس الشيطان فإنك إذا قلت ذلك تعاظم حتى يكون مثل البيت ويقول: بقوتى ولكن قل: بسم الله فإنك إذا قلت ذلك تصاغر حتى يكون مثل الذباب ".
** د
(الأذكار ٢٦٣/١)
** إسناده صحيح متصل
** قال الإمام النووى فى " الأذكار " ١ / ٢٦٤: ورويناه فى كتاب ابن السنى عن أبى المليح عن أبيه وأبوه صحابى اسمه أسامة على الصحيح المشهور وقيل فيه أقوال أخرى وكلا الروايتين صحيحة متصلة فإن الرجل المجهول فى رواية أبى داود صحابى والصحابة رضى الله عنهم كلهم عدول لا تضر الجهالة بأعيانهم.','10','378',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4804,'<span data-type="title" id=toc-4814>٤٨٠٤ </span>- عن أسامة بن زيد رضى الله عنهما عن رسول الله صلى الله عليه وسلم قال: " من صنع إليه معروف فقال لفاعله: جزاك الله خيرا فقد أبلغ فى الثناء ".
** ت
(الأذكار ٢٦٥/١)
** قال الإمام النووى فى " الأذكار " ١ / ٢٦٥: قال الترمذى: حديث حسن صحيح.
** تعقيب: قال عبد القادر الأرناؤوط ١ / ٢٦٥: عبارة الترمذى فى النسخ المطبوعة: هذا حديث حسن جيد غريب لا نعرفه من حديث أسامة بن زيد إلا من هذا الوجه. وقد روى عن أبى هريرة عن النبى صلى الله عليه وسلم بمثله وسألت محمدا يعنى البخارى فلم يعرفه. ورواه النسائى فى " عمل اليوم والليلة " وابن حبان فى صحيحه وهو حديث حسن بشواهده.','10','379',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4805,'<span data-type="title" id=toc-4815>٤٨٠٥ </span>- ثبت عن عائشة رضى الله عنها قالت: " كان يؤمر العائن أن يتوضأ ثم يغتسل منه المعين ".
** د
(الأذكار ٢٧٢/١)
** إسناه صحيح على شرط البخارى ومسلم','10','380',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4806,'<span data-type="title" id=toc-4816>٤٨٠٦ </span>- عن أبى سعيد الخدرى رضى الله عنه قال: " كان رسول الله صلى الله عليه وسلم يتعوذ من الجان وعين الإنسان حتى نزلت المعوذتان فلما نزلتا أخذ بهما وترك ما سواهما ".
** ت ن جه
(الأذكار ٢٧٢/١)
** قال الإمام النووى فى " الأذكار " ١ / ٢٧٢: قال الترمذى: حديث حسن.','10','381',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4807,'<span data-type="title" id=toc-4817>٤٨٠٧ </span>- عن عائشة رضى الله عنها قالت: كان رسول الله صلى الله عليه وسلم إذا رأى ما يحب قال: " الحمد لله الذى بنعمته تتم الصالحات " وإذا رأى ما يكره قال: " الحمد لله على كل حال ".
** جه سنى
(الأذكار ٢٧٤/١)
** إسناده جيد
** قال الإمام النووى فى " الأذكار " ١ /٢٧٤: قال الحاكم أبو عبد الله: هذا حديث صحيح الإسناد.','10','382',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4808,'<span data-type="title" id=toc-4818>٤٨٠٨ </span>- عن أبى هريرة رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " نعم البيت الحمام يدخله المسلم إذا دخله سأل الله عز وجل الجنة واستعاذه من النار ".
** سنى
(الأذكار ٢٧٤/١)
** إسناده ضعيف
** تعقيب: قال عبد القادر الأرناؤوط ١ / ٢٧٥: وقد صح فى الحمام حديث مخالف لهذا وهو قوله صلى الله عليه وسلم: اتقوا بيتا يقال له: الحمام قالوا: إنه ينقى وينفع قال: فمن دخله فليستتر. صححه الحاكم ووافقه الذهبى وفيه رد على من قال: لا يصح فى الحمام حديث.','10','383',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4809,'<span data-type="title" id=toc-4819>٤٨٠٩ </span>- عن أنس أن النبى صلى الله عليه وسلم قال له: " يا ذا الأذنين ".
** د ت
(الأذكار ٢٧٩/١)
** قال الإمام النووى فى " الأذكار " ١ / ٢٧٩: قال الترمذى: حديث صحيح.','10','384',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4810,'<span data-type="title" id=toc-4820>٤٨١٠ </span>- عن أنس أن رجلا أتى النبى صلى الله عليه وسلم فقال: يا رسول الله احملنى فقال: " إنى حاملك على ولد الناقة " فقال: يا رسول الله وما أصنع بولد الناقة؟ فقال رسول الله صلى الله عليه وسلم: " وهل تلد الإبل إلا النوق ".
** د ت
(الأذكار ٢٧٩/١)
** قال الإمام النووى فى " الأذكار " ١ / ٢٧٩: قال الترمذى: حديث حسن صحيح.','10','385',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4811,'<span data-type="title" id=toc-4821>٤٨١١ </span>- عن أبى هريرة رضى الله عنه قال: قالوا يا رسول الله إنك تداعبنا قال: " إنى لا أقول إلا حقا ".
** ت
(الأذكار ٢٧٩/١)
** قال الإمام النووى فى " الأذكار " ١ / ٢٧٩: قال الترمذى: حديث حسن.','10','386',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4812,'<span data-type="title" id=toc-4822>٤٨١٢ </span>- عن حذيفة رضى الله عنه عن النبى صلى الله عليه وسلم قال: " والذى نفسى بيده لتأمرن بالمعروف ولتنهون عن المنكر أو ليوشكن الله تعالى يبعث عليكم عقابا منه ثم تدعونه فلا يستجاب لكم ".
** ت
(الأذكار ٢٨٣/١)
** قال الإمام النووى فى " الأذكار " ١ / ٢٨٣: قال الترمذى: حديث حسن.','10','387',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4813,'<span data-type="title" id=toc-4823>٤٨١٣ </span>- عن أبى بكر الصديق رضى الله عنه قال: ياأيها الناس إنكم تقرؤون هذه الآيه " ياأيها الذين آمنوا عليكم انفسكم لا يضركم من ضل إذا اهتديتم ". المائدة آيه: ١٠٥. وإنى سمعت رسول الله صلى الله عليه وسلم يقول: " إن الناس إذا رأوا الظالم فلم يأخذوا على يديه أوشك أن يعمهم الله بعقاب منه ".
** د ت ن جه
(الأذكار ٢٨٣/١)
** إسناده صحيح','10','388',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4814,'<span data-type="title" id=toc-4824>٤٨١٤ </span>- عن أبى سعيد عن النبى صلى الله عليه وسلم قال: " أفضل الجهاد كلمة عدل عند سلطان جائر ".
** د ت
(الأذكار ٢٨٣/١)
** قال الإمام النووى فى " الأذكار " ١ / ٢٨٤: قال الترمذى: حديث حسن.','10','389',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4815,'<span data-type="title" id=toc-4825>٤٨١٥ </span>- عن بلال بن الحارث المزنى رضى الله عنه أن رسول الله صلى الله عليه وسلم قال: " إن الرجل ليتكلم بالكلمة من رضوان الله تعالى ماكان يظن أن تبلغ ما بلغت يكتب الله تعالى له بها رضوانه إلى يوم يلقاه وإن الرجل ليتكلم بالكلمة من سخط الله تعالى ماكان يظن أن تبلغ ما بلغت يكتب الله تعالى بها سخطه إلى يوم يلقاه ".
** ما ت جه
(الأذكار ٢٨٥/١)
** قال الإمام النووى فى " الأذكار " ١ / ٢٨٥: قال الترمذى: حديث حسن صحيح.','10','390',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4816,'<span data-type="title" id=toc-4826>٤٨١٦ </span>- عن أبى هريرة رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " من وقاه الله تعالى شر مابين لحييه وشر ما بين رجليه دخل الجنة ".
** ت
(الأذكار ٢٨٥/١)
** قال الإمام النووى فى " الأذكار " ١ / ٢٨٥: قال الترمذى: حديث حسن.','10','391',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4817,'<span data-type="title" id=toc-4827>٤٨١٧ </span>- عن عقبة بن عامر رضى الله عنه قال: قلت يا رسول الله ما النجاة؟ قال " أمسك عليك لسانك وليسعك بيتك وابك على خطيئتك ".
** ت
(الأذكار ٢٨٥/١)
** قال الإمام النووى فى " الأذكار " ١ / ٢٨٦: قال الترمذى: حديث حسن.','10','392',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4818,'<span data-type="title" id=toc-4828>٤٨١٨ </span>- عن معاذ رضى الله عنه قال: قلت يا رسول الله أخبرنى بعمل يدخلنى الجنة ويباعدنى من النار قال: " لقد سألت عن عظيم وإنه ليسير على من يسره الله تعالى عليه: تعبد الله لا تشرك به شيئا، وتقيم الصلاة، وتؤتى الزكاة وتصوم رمضان، وتحج البيت، ثم قال: ألا أدلك على أبواب الخير؟ الصوم جنة والصدقة تطفىء الخطيئة كما يطفىء الماء النار وصلاة الرجل فى جوف الليل ثم تلا " تتجافى جنوبهم عن المضاجع " حتى بلغ: يعملون، " ألم السجدة: ١٦ " ثم قال: ألا أخبرك برأس الأمر وعموده وذروة سنامه؟ قلت بلى يا رسول الله
قال: رأس الأمر الإسلام وعموده الصلاة وذروة سنامه الجهاد، ثم قال: ألا أخبرك بملاك ذلك كله؟ قلت بلى يا رسول الله فأخذ بلسانه ثم قال: كف عليك هذا قلت يا رسول الله وإنا لمؤاخذون بما نتكلم به؟ فقال: ثكلتك أمك وهل يكب الناس فى النار على وجوههم إلا حصائد ألسنتهم ".
** ت
(الأذكار ٢٨٦/١)
** قال الإمام النووى فى " الأذكار " ١ / ٢٨٧: قال الترمذى: حديث حسن صحيح.','10','393',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4819,'<span data-type="title" id=toc-4829>٤٨١٩ </span>- عن أبى هريرة عن النبى صلى الله عليه وسلم قال: " من حسن إسلام المرء تركه ما لا يعنيه ".
** ت جه
(الأذكار ٢٨٧/١)
** حسن','10','394',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4820,'<span data-type="title" id=toc-4830>٤٨٢٠ </span>- عن عبد الله بن عمرو بن العاص أن النبى صلى الله عليه وسلم قال: " من صمت نجا ".
** ت
(الأذكار ٢٨٧/١)
** إسناده ضعيف
** قال الإمام النووى فى " الأذكار " ١ / ٢٨٧: إسناده ضعيف وإنما ذكرته لأبينه لكونه مشهورا.','10','395',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4821,'<span data-type="title" id=toc-4831>٤٨٢١ </span>- عن عائشة رضى الله عنها قالت: قلت للنبى صلى الله عليه وسلم حسبك من صفية كذا وكذا قال بعض الرواة تعنى قصيرة، فقال: " لقد قلت كلمة لو مزجت بماء البحر لمزجته " قالت وحكيت له إنسانا فقال: ما أحب أنى حكيت إنسانا وأن لى كذا وكذا ".
** د ت
(الأذكار ٢٨٩/١)
** قال الإمام النووى فى " الأذكار " ١ / ٢٨٩: قال الترمذى: حديث حسن صحيح.','10','396',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4822,'<span data-type="title" id=toc-4832>٤٨٢٢ </span>- عن أبى هريرة رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " المسلم
أخو المسلم لا يخونه ولا يكذبه ولا يخذله كل المسلم على المسلم حرام عرضه وماله ودمه التقوى ها هنا بحسب امرىء من الشر أن يحقر أخاه المسلم ".
** ت
(الأذكار ٢٩٠/١)
** قال الإمام النووى فى " الأذكار " ١ / ٢٩٠: قال الترمذى: حديث حسن.','10','397',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4823,'<span data-type="title" id=toc-4833>٤٨٢٣ </span>- عن أبى الدرداء رضى الله عنه عن النبى صلى الله عليه وسلم قال: " من رد عن عرض أخيه رد الله عن وجهه النار يوم القيامة ".
** ت
(الأذكار ٢٩٤/١)
** قال الإمام النووى فى " الأذكار " ١ / ٢٩٤: قال الترمذى: حديث حسن.
** تعقيب: قال عبد القادر الأرناؤوط ١ / ٢٩٤: وفى الباب عن أسماء بنت يزيد، وهو حديث حسن كما قال الترمذى.','10','398',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4824,'<span data-type="title" id=toc-4834>٤٨٢٤ </span>- عن واثلة بن الأسقع رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم " لا تظهر الشماتة لأخيك فيرحمه الله ويبتليك ".
** ت
(الأذكار ٣٠٠/١)
** قال الإمام النووى فى " الأذكار " ١ / ٣٠٠: قال الترمذى: حديث حسن.
** تعقيب: قال عبد القادر الأرناؤوط ١ / ٣٠٠: قال الترمذى: حسن غريب وهو حسن لغيره أخرجه من طريق مكحول عن واثلة بن الأسقع وقال: حديث حسن غريب وقد أخرج له شاهدا يؤدى معناه من طريق ثور بن يزيد عن خالد بن معدان عن معاذ بن جبل قال: رسول الله صلى الله عليه وسلم " من عير أخاه بذنب لم يمت حتى يعمله ". وقال أيضا: حديث حسن غريب قال الحافظ فى أجوبة عن أحاديث وقعت فى مصابيح السنة ووصفت بالوضع: هكذا وصف يعنى - الترمذى - كلا منها بالحسن والغرابة فأما الغرابة فلتفرد بعض رواة كل منهما عن شيخه فهى غرابة نسبية وأما الحسن فلاعتضاد كل منهما بالآخر.','10','399',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4825,'<span data-type="title" id=toc-4835>٤٨٢٥ </span>- عن سمرة بن جندب رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم " لا تلاعنوا بلعنة الله ولا بغضبه ولا بالنار ".
** د ت
(الأذكار ٣٠٢/١)
** قال الإمام النووى فى " الأذكار " ١ / ٣٠٢: قال الترمذى: حديث حسن صحيح.
** تعقيب: قال عبد القادر الأرناؤوط ١ / ٣٠٢: هو من حديث الحسن البصرى عن سمرة والحسن لم يسمع من سمرة ولكن الحديث حسن بشواهده منها الحديثان اللذان قبله والحديث الذى بعده. قال الترمذى: وفى الباب عن ابن عباس وابن عمر وعمران بن حصين.','10','400',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4826,'<span data-type="title" id=toc-4836>٤٨٢٦ </span>- عن ابن مسعود رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " ليس المؤمن بالطعان ولا اللعان ولا الفاحش ولا البذىء ".
** ت
(الأذكار ٣٠٢/١)
** قال الإمام النووى فى " الأذكار " ١ / ٣٠٢: قال الترمذى: حديث حسن.
** تعقيب: قال عبد القادر الأرناؤوط ١ / ٣٠٢: ورواه أيضا ابن حبان فى صحيحه والحاكم وسنده حسن.','10','401',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4827,'<span data-type="title" id=toc-4837>٤٨٢٧ </span>- عن حذيفة رضى الله عنه عن النبى صلى الله عليه وسلم قال: " لا تقولوا ما شاء الله وشاء فلان ولكن قولوا: ما شاء الله ثم ما شاء فلان ".
** د
(الأذكار ٣٠٨/١)
** إسناده صحيح','10','402',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4828,'<span data-type="title" id=toc-4838>٤٨٢٨ </span>- عن بريدة رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " لا تقولوا للمنافق سيد فإنه إن يك سيدا فقد أسخطتم ربكم عز وجل ".
** د
(الأذكار ٣١١/١)
** إسناده صحيح','10','403',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4829,'<span data-type="title" id=toc-4839>٤٨٢٩ </span>- عن زيد بن خالد الجهنى رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " لا تسبوا الديك فإنه يوقظ للصلاة ".
** د
(الأذكار ٣١٣/١)
** إسناده صحيح','10','404',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4830,'<span data-type="title" id=toc-4840>٤٨٣٠ </span>- عن عبد الرزاق عن معمر عن قتادة أو غيره عن عمران بن حصين رضى الله عنهما قال " كنا نقول فى الجاهلية: أنعم الله بك عينا وأنعم صباحا فلما كان الإسلام نهينا عن ذلك ".
** د
(الأذكار ٣١٤/١)
** قال الإمام النووى فى " الأذكار " ١ / ٣١٥: هكذا رواه أبو داود عن قتادة أو غيره ومثل هذا الحديث قال أهل العلم: لا يحكم له بالصحة لأن قتادة ثقة وغيره مجهول وهو محتمل أن يكون عن المجهول فلا يثبت به حكم شرعى ولكن الأحتياط للإنسان اجتناب هذا اللفظ لاحتمال صحته ولأن بعض العلماء يحتج بالمجهول والله أعلم.','10','405',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4831,'<span data-type="title" id=toc-4841>٤٨٣١ </span>- عن بريدة رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " من حلف بالأمانة فليس منا ".
** د
(الأذكار ٣١٦/١)
** إسناده صحيح','10','406',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4832,'<span data-type="title" id=toc-4842>٤٨٣٢ </span>- عن ابن عمر رضى الله عنهما قال: قال رسول الله صلى الله عليه وسلم " من اسستعاذ بالله فأعيذوه ومن سأل بالله تعالى فأعطوه ومن دعاكم فأجيبوه ومن صنع إليكم معروفا فكافئوه فإن لم تجدوا ما تكافئونه فادعوا له حتى تروا أنكم قد كافأتموه ".
** د ن
(الأذكار ٣١٨/١)
** إسناده صحيح','10','407',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4833,'<span data-type="title" id=toc-4843>٤٨٣٣ </span>- عن عبد الله بن عمرو بن العاص رضى الله عنهما أن رسول الله صلى الله عليه وسلم قال: " إن الله يبغض البليغ من الرجال الذى يتخلل بلسانه كما تتخلل البقرة ".
** د ت
(الأذكار ٣٢٠/١)
** قال الإمام النووى فى " الأذكار " ١ / ٣٢٠: قال الترمذى: حديث حسن.','10','408',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4834,'<span data-type="title" id=toc-4844>٤٨٣٤ </span>- عن جابر رضى الله عنه أن رسول الله صلى الله عليه وسلم قال: " إن من أحبكم إلى وأقربكم مجلسا منى يوم القيامة أحاسنكم أخلاقا وإن أبغضكم إلى وأبعدكم منى يوم القيامة الثرثارون والمتشدقون والمتفيقهون. قالوا: يا رسول الله قد علمنا الثرثارون والمتشدقون فما المتفيقهون؟ قال: المتكبرون ".
** ت
(الأذكار ٣٢٠/١)
** قال الإمام النووى فى " الأذكار " ١ / ٣٢٠: قال الترمذى: هذا حديث حسن.','10','409',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4835,'<span data-type="title" id=toc-4845>٤٨٣٥ </span>- عن جابر رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " إذا حدث الرجل بالحديث ثم التفت فهى أمانة ".
** د ت
(الأذكار ٣٢٢/١)
** قال الإمام النووى فى " الأذكار " ١ / ٣٢٢: قال الترمذى: حديث حسن.','10','410',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4836,'<span data-type="title" id=toc-4846>٤٨٣٦ </span>- عن عائشة رضى الله عنها قالت: سئل رسول الله صلى الله عليه وسلم عن الشعر فقال: " هو كلام حسنه كحسن الكلام وقبيحه كقبيح الكلام ".
** يع
(الأذكار ٣٢٢/١)
** إسناده حسن
** تعقيب: قال عبد القادر الأرناؤوط ١ / ٣٢٢: هو حديث حسن بشواهده انظر الفتح ١٠ / ٤٤٥.','10','411',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4837,'<span data-type="title" id=toc-4847>٤٨٣٧ </span>- عن أنس رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " ماكان الفحش فى شىء إلا شانه وماكان الحياء فى شىء إلا زانه ".
** ت جه
(الأذكار ٣٢٤/١)
** قال الإمام النووى فى " الأذكار " ١ / ٣٢٤: قال الترمذى: حديث حسن.','10','412',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4838,'<span data-type="title" id=toc-4848>٤٨٣٨ </span>- عن ابن عمر رضى الله عنهما قال: كان تحتى امرأة وكنت أحبها وكان عمر يكرهها
فقالى: طلقها فأبيت فأتى عمر رضى الله عنه النبى صلى الله عليه وسلم فذكر ذلك له فقال النبى صلى الله عليه وسلم: " طلقها ".
** د ت
(الأذكار ٣٢٤/١)
** قال الإمام النووى فى " الأذكار " ١ / ٣٢٤: قال الترمذى: حديث حسن صحيح.','10','413',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4839,'<span data-type="title" id=toc-4849>٤٨٣٩ </span>- عن ابن مسعود أو حذيفة بن اليمان رضى الله عنهما قال: سمعت رسول الله صلى الله عليه وسلم يقول: " بئس مطية الرجل زعموا ".
** د
(الأذكار ٣٢٧/١)
** إسناده صحيح
** تعقيب: قال عبد القادر الأرناؤوط ١ / ٣٢٧: كذا فى الأصل ابن مسعود وهو خطأ والصواب أبو مسعود. قال عبد القادر الأرناؤوط ١ / ٣٢٧: رواه أبو داود رقم ٤٩٧٢ فى الأدب باب " قول الرجل: زعموا " من حديث أبى قلابة
عن أبى مسعود أو حذيفة وأبو قلابة لم يسمع منهما فهو مرسل.','10','414',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4840,'<span data-type="title" id=toc-4850>٤٨٤٠ </span>- عن سفيان بن أسيد رضى الله عنه قال: سمعت رسول الله صلى الله عليه وسلم يقول: " كبرت خيانة أن تحدث أخاك حديثا هو لك به مصدق وأنت به كاذب ".
** د
(الأذكار ٣٢٧/١)
** إسناده ضعيف
** قال الإمام النووى فى " الأذكار " ١ / ٣٢٧: رويناه فى سنن أبى داود باسناد فيه ضعف، لكن لم يضعفه أبو داود فيقتضى أن يكون حسنا عنده.
** تعقيب: قال عبد القادر الأرناؤوط ١ / ٣٢٧: رواه أبو داود رقم " ٤٩٧١ " فى الأدب باب فى المعارض من حديث سفيان بن أسيد الخضرمى وإسناده ضعيف، فيه مجهولان وضعيف، ورواه أحمد فى المسند ٤ / ١٨٣ من حديث النواس بن سمعان وفى سنده عمر بن هارون وهو متروك، وشريح بن عبيد وهو يرسل كثيرا.','10','415',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4841,'<span data-type="title" id=toc-4851>٤٨٤١ </span>- عن أبى هريرة رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " لا تقولوا: رمضان فإن رمضان اسم من أسماء الله تعالى ولكن قولوا شهر رمضان ".
** هق
(الأذكار ٣٣١/١)
** ضعيف
** قال الإمام النووى فى " الأذكار " ١ / ٣٣١: وهذا الحديث ضعيف ضعفه البيهقى والضعف عليه ظاهر.','3','90',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4842,'<span data-type="title" id=toc-4852>٤٨٤٢ </span>- عن النعمان بن بشير رضى الله عنهما عن النبى صلى الله عليه وسلم قال: " الدعاء هو العبادة ".
** د ت ن جه
(الأذكار ٣٣٣/١)
** إسناده صحيح
** قال الإمام النووى فى " الأذكار " ١ / ٣٣٣: قال الترمذى: حديث حسن صحيح.
** تعقيب: قال عبد القادر الأرناؤوط ١ / ٣٣٣: هو حديث حسن.','3','91',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4843,'<span data-type="title" id=toc-4853>٤٨٤٣ </span>- عن عائشة رضى الله عنها قالت: " كان رسول الله صلى الله عليه وسلم يستحب الجوامع من الدعاء ويدع ما سوى ذلك ".
** د
(الأذكار ٣٣٣/١)
** إسناده جيد
** تعقيب: قال عبد القادر الأرناوط ١ / ٣٣٣: هو حديث حسن.','3','92',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4844,'<span data-type="title" id=toc-4854>٤٨٤٤ </span>- عن بريدة رضى الله عنه أن رسول الله صلى الله عليه وسلم سمع رجلا يقول: اللهم إنى أسألك بأنى أشهد أنك أنت الله لاإله إلا أنت الأحد الصمد الذى لم يلد ولم يولد ولم يكن له كفوا أحد. قال: " لقد سألت الله تعالى بالأسم الذى إذا سئل به أعطى وإذا دعى أجاب ". وفى رواية " لقد سألت الله باسمه الأعظم ".
** د ت ن جه
(الأذكار ٣٣٦/١)
** قال الإمام النووى فى " الأذكار " ١ / ٣٣٦: قال الترمذى: حديث حسن.','3','93',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4845,'<span data-type="title" id=toc-4855>٤٨٤٥ </span>- عن عائشة رضى الله عنها أن النبى صلى الله عليه وسلم كان يدعوا بهؤلاء الكلمات " اللهم إنى أعوذ بك من فتنة النار وعذاب النار ومن شر الغنى والفقر ".
** د ت ن جه
(الأذكار ٣٣٦/١)
** إسناده صحيح
** قال الإمام النووى فى " الأذكار " ١ / ٣٣٦: قال الترمذى: حديث حسن صحيح.','3','94',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4846,'<span data-type="title" id=toc-4856>٤٨٤٦ </span>- عن زياد بن علاقة عن عمه وهو قطبة بن مالك رضى الله عنه قال: كان النبى صلى الله عليه وسلم يقول: " اللهم إنى أعوذ بك من منكرات الأخلاق والأعمال والأهواء ".
** ت
(الأذكار ٣٣٦/١)
** قال الإمام النووى فى " الأذكار " ١ / ٣٣٦: قال الترمذى: حديث حسن.','3','95',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4847,'<span data-type="title" id=toc-4857>٤٨٤٧ </span>- عن شكل بن حميد رضى الله عنه قال: قلت يا رسول الله علمنى دعاء قال: " اللهم إنى أعوذ بك من شر سمعى ومن شر بصرى ومن شر لسانى ومن شر قلبى ومن شر منيى ".
** د ت ن
(الأذكار ٣٣٦/١)
** قال الإمام النووى فى " الأذكار " ١ / ٣٣٦: قالالترمذى: حديث حسن.','3','96',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4848,'<span data-type="title" id=toc-4858>٤٨٤٨ </span>- عن أنس رضى الله عنه أن النبى صلى الله عليه وسلم كان يقول: " اللهم إنى أعوذ بك من البرص والجنون والجذام وسيىء الأسقام ".
** د ن
(الأذكار ٣٣٦/١)
** إسناده حسن','3','97',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4849,'<span data-type="title" id=toc-4859>٤٨٤٩ </span>- عن أبى هريرة رضى الله عنه قال: كان رسول الله صلى الله عليه وسلم يقول: " اللهم إنى أعوذ بك من الجوع فإنه بئس الضجيع وأعوذ بك من الخيانة فإنها بئست البطانة ".
** د ن
(الأذكار ٣٣٧/١)
** إسناده صحيح','3','98',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4850,'<span data-type="title" id=toc-4860>٤٨٥٠ </span>- عن عمران بن حصين رضى الله عنهما أن النبى صلى الله عليه وسلم علم أباه حصينا كلمتين يدعو بهما " اللهم ألهمنى رشدى وأعذنى من شر نفسى ".
** ت
(الأذكار ٣٣٧/١)
** قال الإمام النووى فى " الأذكار " ١ / ٣٣٧: قال الترمذى: حديث حسن.','3','99',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4851,'<span data-type="title" id=toc-4861>٤٨٥١ </span>- عن أبى هريرة رضى الله عنه أن رسول الله صلى الله عليه وسلم كان يقول: " اللهم إنى أعوذ بك من الشقاق والنفاق وسوء الخلق ".
** د ن
(الأذكار ٣٣٧/١)
** إسناده ضعيف','3','100',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4852,'<span data-type="title" id=toc-4862>٤٨٥٢ </span>- عن شهر بن حوشب قال: قلت لأم سلمة رضى الله عنها: يا أم المؤمنين ما كان أكثر دعاء رسول الله صلى الله عليه وسلم إذا كان عندك قالت: كان أكثر دعائه " يا مقلب القلوب ثبت قلبى على دينك ".
** ت
(الأذكار ٣٣٧/١)
** قال الإمام النووى فى " الأذكار " ١ / ٣٣٧: قال الترمذى: حديث حسن.','3','101',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4853,'<span data-type="title" id=toc-4863>٤٨٥٣ </span>- عن أبى الدرداء رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " كان من دعاء داود صلى الله عليه وسلم: اللهم إنى أسألك حبك وحب من يحبك والعمل الذى يبلغنى حبك، اللهم اجعل حبك أحب إلى من نفسى وأهلى ومن الماء البارد ".
** ت
(الأذكار ٣٣٧/١)
** قال الإمام النووى فى " الأذكار " ١ / ٣٣٧: قال الترمذى: حديث حسن.','3','102',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4854,'<span data-type="title" id=toc-4864>٤٨٥٤ </span>- عن سعد بن أبى وقاص رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " دعوة ذى النون إذ دعا ربه وهو فى بطن الحوت: لا إله إلا أنت سبحانك إنى كنت من الظالمين فإنه لم يدع بها رجل مسلم فى شىء قط إلا استجاب له ".
** ت
(الأذكار ٣٣٧/١)
** قال الإمام النووى فى " الأذكار " ١ / ٣٣٨: قال الحاكم أبو عبد الله: هذا صحيح الإسناد.','3','103',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4855,'<span data-type="title" id=toc-4865>٤٨٥٥ </span>- عن أنس رضى الله عنه أن رجلا جاء إلى النبى صلى الله عليه وسلم فقال: يا رسول الله أى الدعاء أفضل؟ قال: " سل ربك العافية والمعافاة فى الدنيا والآخرة ". ثم أتاه فى اليوم الثانى فقال: يا رسول الله أى الدعاء أفضل؟
فقال له مثل ذلك ثم أتاه فى اليوم الثالث فقال له مثل ذلك قال: " فإذا أعطيت العافية فى الدنيا وأعطيتها فى الآخرة فقد أفلحت ".
** ت جه
(الأذكار ٣٣٨/١)
** قال الإمام النووى فى " الأذكار " ١ / ٣٣٨: قال الترمذى حديث حسن.
** تعقيب: قال عبد القادر الأرناؤوط ١ / ٣٣٨: وهو حديث حسن يشهد له الذى بعده. * الذى بعده حديث العباس بن عبد المطلب عند الترمذى " سلوا الله العافية ".','3','104',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4856,'<span data-type="title" id=toc-4866>٤٨٥٦ </span>- عن العباس بن عبد المطلب رضى الله عنه قال: قلت يا رسول الله علمنى شيئا اسأله الله تعالى قال: " سلوا الله العافية " فمكثت أياما ثم جئت فقلت: يا رسول الله علمنى شيئا اسأله الله تعالى فقال لى: " يا عباس يا عم رسول الله سلوا الله العافية فى الدنيا والآخرة ".
** ت
(الأذكار ٣٣٨/١)
** قال الإمام النووى فى " الأذكار " ١ / ٣٣٨: قال الترمذى: هذا حديث صحيح.
** تعقيب: قال عبد القادر الأرناؤوط ١ / ٣٣٨: هو حديث حسن يشهد له الذى قبله. * الذى قبله حديث أنس عند الترمذى وابن ماجه " سل ربك العافية ".','3','105',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4857,'<span data-type="title" id=toc-4867>٤٨٥٧ </span>- عن أبى أمامة رضى الله عنه قال: دعا رسول الله صلى الله عليه وسلم بدعاء كثير لم نحفظ منه شيئا فقال: " ألا أدلكم ما يجمع ذلك كله؟ تقول: اللهم إنى أسألك من خير ما سألك منه نبيك محمد صلى الله عليه وسلم ونعوذ بك من شر مااستعاذك منه نبيك محمد صلى الله عليه وسلم أنت المستعان وعليك البلاغ ولا حول ولا قوة إلا بالله ".
** ت
(الأذكار ٣٣٨/١)
** قال الإمام النووى فى " الأذكار " ١ / ٣٣٨: قال الترمذى: حديث حسن.
** تعقيب: قال عبد القادر الأرناؤوط ١ / ٣٣٨: وفى سنده ليث بن أبى سليم وهو صدوق اختلط أخيرا ولم يتميز حديثه فترك.','3','106',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4858,'<span data-type="title" id=toc-4868>٤٨٥٨ </span>- عن ابن عباس رضى الله عنهما قال: كان رسول الله صلى الله عليه وسلم يدعو ويقول: " رب أعنى ولا تعن على وانصرنى ولا نتصر على وامكر لى ولاتمكر على واهدنى ويسر الهدى لى وانصرنى على من بغى على رب اجعلنى لك شاكرا لك ذاكرا لك راهبا لك مطواعا إليك مخبتا أو منيبا تقبل توبتى واغسل حوبتى وأجب دعوتى وثبت حجتى واهد٦ قلبى وسدد لسانى واسلل سخيمة قلبى ".
** د ت جه
(الأذكار ٣٣٨/١)
** قال الإمام النووى فى " الأذكار " ١ / ٣٣٩: وفى رواية الترمذى: " أواها منيبا ". قال الترمذى: حديث حسن صحيح.','3','107',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4859,'<span data-type="title" id=toc-4869>٤٨٥٩ </span>- عن عائشة رضى الله عنها أن النبى صلى الله عليه وسلم قال لها: " قولى اللهم إنى أسألك من الخير كله عاجله وآجله ما علمت منه وما لم أعلم واعوذ بك من الشر كله عاجله وآجله ما علمت منه وما لم أعلم وأسألك الجنة وما قرب إليها من قول أو عمل، وأعوذ بك من النار وما قرب إليها من قول أو عمل وأسألك خير ماسألك به عبدك ورسولك محمد صلى الله عليه وسلم وأعوذ بك من شر مااستعاذك منه عبدك ورسولك محمد صلى الله عليه وسلم وأسألك ما قضيت لى من أمر أن تجعل عاقبته رشدا ".
** حم جه
(الأذكار ٣٣٩/١)
** قال الإمام النووى فى " الأذكار " ١ / ٣٣٩: قال الحاكم أبو عبد الله: هذا حديث صحيح الإسناد.
** تعقيب: قال عبد القادر الأرناؤوط ١ / ٣٣٩: رواه أيضا ابن حبان فى صحيحه " ٢٤١٣ " موارد، وابن ماجه رقم " ٣٨٤٦ " فى الدعاء باب " الجوامع من الدعاء " وهو حديث حسن.','3','108',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4860,'<span data-type="title" id=toc-4870>٤٨٦٠ </span>- ووجدت فى المستدرك للحاكم: عن ابن مسعود رضى الله عنه قال: كان من دعاء رسول الله صلى الله عليه وسلم " اللهم إنا نسألك موجبات رحمتك وعزائم مغفرتك والسلامة من كل إثم والغنيمة
من كل بر والفوز بالجنة والنجاة من النار ".
** ك
(الأذكار ٣٤٠/١)
** قال الإمام النووى فى " الأذكار " ١ / ٣٤٠: قال الحاكم: حديث صحيح على شرط مسلم.
** تعقيب: قال عبد القادر الأرناؤوط ١ / ٣٤٠: هو حديث حسن.','3','109',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4861,'<span data-type="title" id=toc-4871>٤٨٦١ </span>- عن أبى هريرة رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " ادعوا الله وأنتم موقنون بالإجابة واعلموا أن الله تعالى لا يستجيب دعاء من قلب غافل لاه ".
** ت
(الأذكار ٣٤٤/١)
** إسناده ضعيف
** تعقيب: قال عبد القادر الأرناؤوط ١ / ٣٤٤: ولكن له شاهد عند أحمد فى المسند من حديث عبد الله بن عمرو بن العاص رضى الله عنهما أن رسول الله صلى الله عليه وسلم قال: "القلوب أوعية وبعضها أوعى
من بعض فإذا سألتم الله عز وجل فاسألوه وأنتم موقنون بالإجابة فإن الله لا يستجيب لعبد دعاء عن ظهر قلب غافل " وهو حديث حسن.','3','110',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4862,'<span data-type="title" id=toc-4872>٤٨٦٢ </span>- عن ابن عمرو رضى الله عنهما أن رسول الله صلى الله عليه وسلم قال: " أسرع الدعاء إجابة دعوة غائب لغائب ".
** د ت
(الأذكار ٣٤٥/١)
** قال الإمام النووى فى " الأذكار " ١ / ٣٤٥: ضعفه الترمذى.
** تعقيب: قال عبد القادر الأرناؤوط ١ / ٣٤٥: ورواه البخارى فى الأدب المفرد باللفظ الذى أورده المصنف وفى سنده عبد الرحمن بن زياد بن أنعم الأفريقى وهو ضعيف.','3','111',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4863,'<span data-type="title" id=toc-4873>٤٨٦٣ </span>- عن أسامة بن زيد رضى الله عنهما قال: قال رسول الله صلى الله عليه وسلم " من صنع إليه معروف فقال لفاعله: جزاك الله خيرا فقد أبلغ فى الثناء ".
** ت
(الأذكار ٣٤٥/١)
** قال الإمام النووى فى " الأذكار " ١ / ٣٤٥: قال الترمذى: حديث حسن صحيح.','3','112',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4864,'<span data-type="title" id=toc-4874>٤٨٦٤ </span>- عن جابر رضى الله عنه قال: قال رسول الله صلى الله عليه وسلم: " لا تدعوا على أنفسكم ولا تدعوا على أولادكم ولا تدعوا على خدمكم ولا تدعوا على أموالكم، لا توافقوا من الله ساعة نيل فيها عطاء فيستجاب لكم ".
** د
(الأذكار ٣٤٦/١)
** إسناده صحيح','3','113',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4865,'<span data-type="title" id=toc-4875>٤٨٦٥ </span>- عن عبادة بن الصامت رضى الله تعالى عنه أن رسول الله صلى الله عليه وسلم قال: " ما على الأرض مسلم يدعوا بدعوة إلا آتاه الله إياها أو صرف عنه من السوء مثلها مالم يدع بإثم أو قطيعة رحم فقال رجل من القوم: إذا نكثر قال الله أكثر ".
** ت
(الأذكار ٣٤٦/١)
** قال الإمام النووى فى " الأذكار " ١ / ٣٤٦: قال الترمذى: حديث حسن صحيح. ورواه الحاكم أبو عبد الله فى " المستدرك على الصحيحين " من روايه أبى سعيد الخدرى وزاد فيه " أو يدخر له من الأجر مثلها ".','3','114',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4866,'<span data-type="title" id=toc-4876>٤٨٦٦ </span>- عن ابن عمر رضى الله تعالى عنهما قال: كنا نعد لرسول الله صلى الله عليه وسلم فى المجلس الواحد مائة مرة: " رب اغفر لى وتب على إنك أنت التواب الرحيم ".
** د ت جه
(الأذكار ٣٤٧/١)
** قال الإمام النووى فى " الأذكار " ١ / ٣٤٧: قال الترمذى: حديث حسن صحيح.','3','115',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4867,'<span data-type="title" id=toc-4877>٤٨٦٧ </span>- عن مولى لأبى بكر الصديق رضى الله تعالى عنه قال: قال رسول الله صلى الله عليه وسلم: " ما أصر من استغفر وإن عاد فى اليوم سبعين مرة ".
** د ت
(الأذكار ٣٤٨/١)
** قال الإمام النووى فى " الأذكار " ١ / ٣٤٨: قال الترمذى: ليس إسناده بالقوى.
** تعقيب: قال عبد القادر الأرناؤوط ١ / ٣٤٨: وفيه جهالة مولى أبى بكر، ولذلك قال الترمذى: حديث غريب إنما نعرفه فى حديث أبى نضيرة وليس إسناده بالقوى.','3','116',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4868,'<span data-type="title" id=toc-4878>٤٨٦٨ </span>- عن أنس رضى الله تعالى عنه قال: سمعت رسول الله صلى الله عليه وسلم يقول: " قال الله تعالى: ياابن آدم إنك ما دعوتنى ورجوتنى غفرت لك ماكان منك ولا أبالى ياابن آدم لو بلغت ذنوبك عنان السماء ثم استغفرتنى غفرت لك ياابن آدم لو أتيتنى بقراب الأرض خطايا ثم أتيتنى لا تشرك بى شيئا لأتيتك بقرابها مغفرة "
** ت
(الأذكار ٣٤٨/١)
** قال الإمام النووى فى " الأذكار " ١ / ٣٤٨: قال الترمذى: حديث حسن.','3','117',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4869,'<span data-type="title" id=toc-4879>٤٨٦٩ </span>- عن عبد الله بن بسر رضى الله تعالى عنه قال: قال رسول الله صلى الله عليه وسلم: " طوبى لمن وجد فى صحيفته استغفارا كثيرا ".
** جه
(الأذكار ٣٤٨/١)
** إسناده جيد','3','118',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4870,'<span data-type="title" id=toc-4880>٤٨٧٠ </span>- عنم ابن مسعود رضى الله تعالى عنه قال: قال رسول الله صلى الله عليه وسلم " من قال: أستغفر الله الذى لا إله إلا هو الحى القيوم وأتوب إليه غفرت ذنوبه وإن كان قد فر من الزحف ".
** د ت
(الأذكار ٣٤٩/١)
** قال الإمام النووى فى " الأذكار " ١ / ٣٤٩: قال الحاكم: هذا حديث صحيح على شرط البخارى ومسلم.
** تعقيب: قال عبد القادر الأرناؤوط ١ / ٣٤٩: رواية ابن مسعود هى عند الحاكم فى المستدرك " ١ / ٥١١ " وهو حديث صحيح صححه الحاكم ووافقه الذهبى. ورواية أبى داود والترمذى إنما هى من رواية بلال بن يسار بن زيد عن أبيه عن جده وهى عند أبى داود رقم " ١٥١٧ " فى الصلاة باب الإستغفار وعند الترمذى " ٣٥٧٢ " فى الدعوات باب فى دعاء الضيف وهو حديث
حسن.','3','119',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4871,'<span data-type="title" id=toc-4881>٤٨٧١ </span>- عن على رضى الله عنه قال: حفظت عن رسول الله صلى الله عليه وسلم " لا يتم بعد احتلام ولا صمات يوم إلى الليل ".
** د
(الأذكار ٣٤٩/١)
** إسناده حسن
** تعقيب: قال عبد القادر الأرناؤوط ١ / ٣٤٩: فى إسناده ضعف، قال الحافظ السخاوى فى المقاصد الحسنة: رواه أبو داود عن على
فى حديث، وقد أعله غير واحد، وحسنه النووى متمسكا بسكوت أبى داود عليه لاسيما وهو عند الطبرانى فى الصغير من وجه آخر عن على بل له شاهد عن جابر وأنس وغيرهما.','3','120',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4872,'<span data-type="title" id=toc-4882>٤٨٧٢ </span>- عن الحسن بن على رضى الله عنهما قال: حفظت من رسول الله صلى الله عليه وسلم " دع ما يريبك إلى مالا يريبك ".
** ت ن
(الأذكار ٣٥١/١)
** قال الإمام النووى فى " الأذكار: ١ / ٣٥١: قال الترمذى: حديث حسن صحيح.','3','121',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4873,'<span data-type="title" id=toc-4883>٤٨٧٣ </span>- عن أبى هريرة رضى الله عنه قال: قال: رسول الله صلى الله عليه وسلم: " من حسن إسلام المرء تركه ما لا يعنيه ".
** ت جه
(الأذكار ٣٥١/١)
** حسن','3','122',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4874,'<span data-type="title" id=toc-4884>٤٨٧٤ </span>- " لا ضرر ولا ضرار ".
** ما قط
(الأذكار ٣٥١/١)
** حسن
** قال الإمام النووى فى " الأذكار " ١ / ٣٥١: رويناه فى الموطأ مرسلا، وفى سنن الدارقطنى وغيره من طرق متصلا وهو حسن.','3','123',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4875,'<span data-type="title" id=toc-4885>٤٨٧٥ </span>- عن سهل بن سعد الساعدى رضى الله عنه قال: جاء رجل إلى النبى صلى الله عليه وسلم فقال: يا رسول الله دلنى على عمل إذا عملته أحبنى الله وأحبنى الناس فقال: " ازهد فى الدنيا يحبك الله وازهد فيما عند الناس يحبك الناس ".
** جه
(الأذكار ٣٥١/١)
** حسن
** تعقيب: قال عبد القادر الأرناؤوط ١ / ٣٥١: ورواه أيضا الطبرانى فى " الكبير " وأبو نعيم فى " الحلية "وابن حبان فى " روضة العقلاء " والحاكم فى " صحيحه " والبيهقى فى " شعب الإيمان " وآخرون
وهو حديث حسن.','3','124',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4876,'<span data-type="title" id=toc-4886>٤٨٧٦ </span>- عن ابن عباس رضى الله عنهما أن رسول الله صلى الله عليه وسلم قال: " لو يعطى الناس بدعواهم لادعى رجال اموال قوم ودماءهم لكن البينة على المدعى واليمين على من أنكر ".
** لم
(الأذكار ٣٥٢/١)
** حسن
** قال الإمام النووى فى " الأذكار " ١ / ٣٥٢: هو حسن بهذا اللفظ، وبعضه فى " الصحيحين ".
** تعقيب: قال عبد القادر الأرناؤوط ١ / ٣٥٢: رواه بهذا اللفظ البيهقى.','3','125',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4877,'<span data-type="title" id=toc-4887>٤٨٧٧ </span>- عن وابصة بن معبد رضى الله عنه أنه أتى رسول الله صلى الله عليه وسلم " فقال: جئت تسأل عن البر والإثم؟ " قال: نعم فقال: " استفت قلبك. البر ما اطمأنت إليه النفس واطمأن إليه القلب. والإثم: ما حاك فى النفس وتردد فى الصدر وإن أفتاك الناس وأفتوك ".
** حم دى
(الأذكار ٣٥٢/١)
** حسن','3','126',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4878,'<span data-type="title" id=toc-4888>٤٨٧٨ </span>- عن أبى ثعلبة الخشنى رضى الله عنه عن رسول الله صلى الله عليه وسلم قال: " إن الله عز وجل فرض فرائض فلا تضيعوها وحد حدودا فلا تعتدوها وحرم أشياء فلا تنتهكوها وسكت عن أشياء رحمة لكم غير نسيان فلا تبحثوا عنها ".
** قط
(الأذكار ٣٥٣/١)
** إسناده حسن
** تعقيب: قال عبد القادر الأرناؤوط ١ / ٣٥٣: وهو حديث حسن.','3','127',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4879,'<span data-type="title" id=toc-4889>٤٨٧٩ </span>- عن أبى ذر ومعاذ رضى الله عنهما عن رسول الله صلى الله عليه وسلم قال: " اتق الله حيثما كنت وأتبع السيئة الحسنة تمحها وخالق الناس بخلق حسن ".
** ت
(الأذكار ٣٥٣/١)
** قال الإمام النووى فى " الأذكار " ١ / ٣٥٣: قال الترمذى: حسن وفى بعض نسخه المعتمدة: حسن صحيح.','3','128',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4880,'<span data-type="title" id=toc-4890>٤٨٨٠ </span>- عن العرباض بن سارية رضى الله عنه قال: وعظنا رسول الله صلى الله عليه وسلم موعظة بليغة وجلت منها القلوب وذرفت منها العيون، فقلنا يا رسول الله كأنها
موعظة مودع فأوصنا قال: " أوصيكم بتقوى الله عز وجل والسمع والطاعة وإن تأمر عليكم عبد حبشى وإنه من يعش منكم فسيرى اختلافا كثيرا فعليكم بسنتى وسنة الخلفاء الراشدين المهديين عضوا عليها بالنواجذ وإياكم ومحدثات الأمور
فإن كل بدعة ضلالة ".
** د ت
(الأذكار ٣٥٤/١)
** قال الإمام النووى فى " الأذكار " ١ / ٣٥٤: قال الترمذى: حديث حسن صحيح.','3','129',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4881,'<span data-type="title" id=toc-4891>٤٨٨١ </span>- عن ابن عباس رضى الله عنهما قال: كنت خلف النبى صلى الله عليه وسلم يوما فقال: " يا غلام إنى أعلمك كلمات: احفظ الله يحفظك، احفظ الله تجده تجاهك أذا سألت فاسأل الله وإذا استعنت فاستعن بالله، واعلم أن الأمة لو اجتمعت على أن ينفعوك بشىء لم ينفعوك إلابشىء قد كتبه الله لك، وإن اجتمعوا على أن يضروك بشىء لم يضروك إلابشىء قد كتبه الله عليك رفعت الأقلام وجفت الصحف ".
** ت
(الأذكار ٣٥٥/١)
** قال الإمام النووى فى " الأذكار " ١ / ٣٥٥: قال الترمذى: حديث حسن صحيح.','3','130',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4882,'<span data-type="title" id=toc-4892>٤٨٨٢ </span>- عن سفيان بن عبد الله رضى الله عنه قال: قلت: يا رسول الله حدثنى بأمر أعتصم به. قال: قل: آمنت بالله ثم استقم. قال: قلت: يا رسول الله ما أخوف مما يخاف على؟ فأخذ بلسان نفسه ثم قال: هذا.
** ت ن جه
(الأذكار ٢٨٥/١)
** قال الإمام النووى فى " الأذكار " ١ / ٢٨٥: قال الترمذى: حديث حسن صحيح.','3','131',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4883,'<span data-type="title" id=toc-4893>٤٨٨٣ </span>- قال صاحب الشرع صلوات الله عليه وسلامه: " أشد الناس عذابا يوم القيامة عالم لم ينفعه الله سبحانه بعلمه ".
** طص هب
(إحياء علوم الدين ٢/١)
** إسناده ضعيف','3','132',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4884,'<span data-type="title" id=toc-4894>٤٨٨٤ </span>- قال رسول الله صلى الله عليه وسلم: " طلب العلم فريضة على كل مسلم ".
** جه
(إحياء علوم الدين ٣/١)
** قال الحافظ العراقى فى تخريج " الإحياء " ١ / ٣: ضعفه أحمد والبيهقى وغيرهما.','3','133',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4885,'<span data-type="title" id=toc-4895>٤٨٨٥ </span>- قال صلى الله عليه وسلم: " نعوذ بالله من علم لا ينفع ".
** جه
(إحياء علوم الدين ٣/١)
** إسناده حسن','3','134',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4886,'<span data-type="title" id=toc-4896>٤٨٨٦ </span>- قال صلى الله عليه وسلم: " إن الحكمة تزيد الشريف شرفا وترفع المملوك حتى يدرك مدارك الملوك ".
** حل ابن عبد البر فى بيان العلم عبد الغنى الأزدى فى آداب المحدث
(إحياء علوم الدين ٩/١)
** إسناده ضعيف','3','135',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4887,'<span data-type="title" id=toc-4897>٤٨٨٧ </span>- قال صلى الله عليه وسلم: " خصلتان لا تجتمعان فى منافق حسن سمت، وفقه فى الدين ".
** ت
(إحياء علوم الدين ٩/١)
** قال الحافظ العراقى فى تخريج " الإحياء " ١ / ٩: قال الترمذى: حديث غريب.','3','136',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4888,'<span data-type="title" id=toc-4898>٤٨٨٨ </span>- قال صلى الله عليه وسلم: " أفضل الناس المؤمن العالم الذى إن احتيج إليه نفع وإن استغنى عنه أغنى نفسه ".
** هب
(إحياء علوم الدين ١٠/١)
** إسناده ضعيف
** قال الحافظ العراقى فى تخريج " الإحياء " ١ / ١٠: لم أره مرفوعا.','3','137',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4889,'<span data-type="title" id=toc-4899>٤٨٨٩ </span>- قال صلى الله عليه وسلم: " الإيمان عريان ولباسه التقوى وزينته الحياء وثمرته العلم ".
** الحاكم فى تاريخ نيسابور
(إحياء علوم الدين ١٠/١)
** إسناده ضعيف','3','138',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4890,'<span data-type="title" id=toc-4900>٤٨٩٠ </span>- قال صلى الله عليه وسلم: " أقرب الناس من درجة النبوة أهل العلم والجهاد أماأهل العلم فدلوا الناس على ماجاءت به الرسل، وأماأهل الجهاد فجاهدوا بأسيافهم علم ماجاءت به الرسل ".
** أبو نعيم فى فضل العلم العفيف
(إحياء علوم الدين ١٠/١)
** إسناده ضعيف','3','139',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4891,'<span data-type="title" id=toc-4901>٤٨٩١ </span>- قال صلى الله عليه وسلم: " يوزن يوم القيامة مداد العلماء بدم الشهداء ".
** ابن عبد البر
(إحياء علوم الدين ١٠/١)
** إسناده ضيعف','3','140',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4892,'<span data-type="title" id=toc-4902>٤٨٩٢ </span>- قال صلى الله عليه وسلم: " من حفظ على أمتى أربعين حديثا من السنة حتى يؤديها إليهم كنت له شفيعا وشهيدا يوم القيامة ".
** ابن عبد البر فى العلم
(إحياء علوم الدين ١٠/١)
** قال الحافظ العراقى فى تخريج " الإحياء " ١ / ١٠: ابن عبد البر: فى العلم من حديث ابن عمر وضعفه.','3','141',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4893,'<span data-type="title" id=toc-4903>٤٨٩٣ </span>- قال صلى الله عليه وسلم: " من حمل من أمتى أربعين حديثا لقى الله عز وجل يوم القيامة فقيها عالما ".
** ابن عبد البر
(إحياء علوم الدين ١٠/١)
** قال الحافظ العراقى فى تخريج " الإحياء " ١ / ١٠: ابن عبد البر من حديث أنس وضعفه.','3','142',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4894,'<span data-type="title" id=toc-4904>٤٨٩٤ </span>- قال صلى الله عليه وسلم: " من تفقه فى دين الله عز وجل كفاه الله تعالى ماأهمه ورزقه من حيث لا يحتسب ".
** خط
(إحياء علوم الدين ١٠/١)
** إسناده ضعيف','3','143',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4895,'<span data-type="title" id=toc-4905>٤٨٩٥ </span>- قال صلى الله عليه وسلم: " أوحى الله عز وجل إلى إبراهيم عليه السلام ياإبراهيم إنى عليم أحب كل عليم ".
** ابن عبد البر تعليقا
(إحياء علوم الدين ١١/١)
** قال الحافظ العراقى فى تخريج " الإحياء " ١ / ١١: ذكره ابن عبد البر تعليقا ولم أظفر له على إسناد.','3','144',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4896,'<span data-type="title" id=toc-4906>٤٨٩٦ </span>- قال صلى الله عليه وسلم: " العالم أمين الله سبحانه فى الأرض ".
** ابن عبد البر
(إحياء علوم الدين ١١/١)
** إسناده ضعيف','3','145',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4897,'<span data-type="title" id=toc-4907>٤٨٩٧ </span>- قال صلى الله عليه وسلم: " صنفان من أمتى إذا صلحوا صلح الناس وإذا فسدوا فسد الناس: الأمراء والفقهاء ".
** ابن عبد البر جل
(إحياء علوم الدين ١١/١)
** إسناده ضعيف','3','146',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4898,'<span data-type="title" id=toc-4908>٤٨٩٨ </span>- قال عليه السلام: " إذا أتى على يوم لا أزداد فيه علما يقربنى إلى الله عز وجل فلا بورك لى فى طلوع شمس ذلك اليوم ".
** طس حل ابن عبد البر فى العلم
(إحياء علوم الدين ١١/١)
** إسناده ضعيف','3','147',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4899,'<span data-type="title" id=toc-4909>٤٨٩٩ </span>- قال صلى الله عليه وسلم: " فضل العلم على العابد كفضلى على أدنى رجل من أصحابى ".
** ت
(إحياء علوم الدين ١١/١)
** قال الحافظ العراقى فى تخريج " الإحياء " ١ / ١١: قال الترمذى: حسن صحيح.','3','148',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4900,'<span data-type="title" id=toc-4910>٤٩٠٠ </span>- قال صلى الله عليه وسلم: " يشفع يوم القيامة ثلاثة: الأنبياء ثم العلماء ثم الشهداء ".
** جه
(إحياء علوم الدين ١١/١)
** إسناده ضعيف','3','149',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4901,'<span data-type="title" id=toc-4911>٤٩٠١ </span>- قال صلى الله عليه وسلم: " ما عبد الله تعالى بشىء أفضل من فقه فى الدين ولفقيه واحد أشد على الشيطان من ألف عابد ولكل شىء عماد وعماد هذا الدين الفقه ".
** طس أبو بكر الآجرى فى كتاب فضل العلم وأبو نعيم فى رياضة المتعلمين
(إحياء علوم الدين ١١/١)
** إسناده ضعيف','3','150',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4902,'<span data-type="title" id=toc-4912>٤٩٠٢ </span>- حديث ابن عباس: " فقيه أشد على الشيطان من ألف عابد ".
** جه ت
(إحياء علوم الدين ١١/١)
** إسناده ضعيف
** قال الحافظ العراقى فى تخريج " الإحياء " ١ / ١١: عند الترمذى وابن ماجه من حديث ابن عباس بسند ضعيف.','3','151',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4903,'<span data-type="title" id=toc-4913>٤٩٠٣ </span>- قال صلى الله عليه وسلم: " خير دينكم أيسره، وأفضل العبادة الفقه ".
** حم طب
(إحياء علوم الدين ١٢/١)
** إسناده ضعيف
** قال الحافظ العراقى فى تخريج " الإحياء ١ / ١٢: الشطر الأول: عند أحمد من حديث محجن بن الأدرع بإسناد جيد. الشطر الثانى: عند الطبرانى من حديث ابن عمر بسند ضعيف.','3','152',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4904,'<span data-type="title" id=toc-4914>٤٩٠٤ </span>- قال صلى الله عليه وسلم: " فضل المؤمن العالم على المؤمن العابد سبعون درجة ".
** عد
(إحياء علوم الدين ١٢/١)
** إسناده ضعيف
** قال الحافظ العراقى فى تخريج " الإحياء " ١ / ١٢: ولأبى يعلى نحوه من حديث عبد الرحمن بن عوف.','3','153',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4905,'<span data-type="title" id=toc-4915>٤٩٠٥ </span>- قال صلى الله عليه وسلم: " إنكم أصبحتم فى زمن كثير فقهاؤه قليل قراؤه وخطباؤه فليس سائلوه كثير معطوه، العمل فيه خير من العلم، وسيأتى على الناس زمان قليل فقهاؤه كثير خطباؤه قليل معطوه كثير سائلوه، العلم فيه خير من العمل ".
** طب
(إحياء علوم الدين ١٢/١)
** إسناده ضعيف
** قال الحافظ العراقى فى تخريج " الإحياء " ١ / ١٢: الطبرانى من حديث حزام بن حكيم عن عمه، وقيل: عن أبيه.','3','154',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4906,'<span data-type="title" id=toc-4916>٤٩٠٦ </span>- قال صلى الله عليه وسلم: " بين العلم والعابد مائة درجة بين كل درجتين حضر الجواد المضمر سبعين سنة ".
** الأصفهانى فى الترغيب والترهيب
(إحياء علوم الدين ١٢/١)
** إسناده ضعيف
** قال الحافظ العراقى فى تخريج " الإحياء " ١ / ١٢: الأصفهانى فى الترغيب والترهيب من حديث ابن عمر عن أبيه وقال: سبعون درجة بسند ضعيف، وكذا رواه صاحب مسند الفردوس من حديث أبى هريرة.','3','155',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4907,'<span data-type="title" id=toc-4917>٤٩٠٧ </span>- " قيل يا رسول الله أى الأعمال أفضل؟ فقال: العلم بالله عز وجل، فقيل أى العلم تريد؟ قال صلى الله عليه وسلم: العلم بالله سبحانه، فقيل له نسأل عن العمل وتجيب عن العلم؟ فقال صلى الله عليه وسلم: العمل ينفع مع العلم بالله وإن كثير العمل لا ينفع مع الجهل بالله ".
** ابن عبد البر
(إحياء علوم الدين ١٢/١)
** إسناده ضعيف','3','156',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4908,'<span data-type="title" id=toc-4918>٤٩٠٨ </span>- قال صلى الله عليه وسلم: " يبعث الله سبحانه العباد يوم القيامة ثم يبعث العلماء ثم يقول: يا معشر العلماء إنى لم أضع علمى فيكم إلا لعلمى بكم ولم أضع علمى فيكم لأعذبكم، إذهبوا فقد غفرت لكم ".
** طب
(إحياء علوم الدين ١٢/١)
** إسناده ضعيف','3','157',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4909,'<span data-type="title" id=toc-4919>٤٩٠٩ </span>- قال صلى الله عليه وسلم: " إن الملائكة لتضع أجنحتها لطالب العلم رضاء بما يصنع ".
** حم حب ك
(إحياء علوم الدين ١٥/١)
** قال الحافظ العراقى فى تخريج " الإحياء " ١ / ١٥: صححه الحاكم.','3','158',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4910,'<span data-type="title" id=toc-4920>٤٩١٠ </span>- قال صلى الله عليه وسلم: " لأن تغدو فتتعلم بابا من العلم خير من أن تصلى مائة ركعة ".
** ابن عبد البر
(إحياء علوم الدين ١٥/١)
** إسناده ليس بذاك
** قال الحافظ العراقى فى تخريج " الإحياء " ١ / ١٥: ابن عبد البر من حديث أبى ذر وليس إسناده بذاك، والحديث عند ابن ماجه بلفظ آخر.','3','159',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4911,'<span data-type="title" id=toc-4921>٤٩١١ </span>- قال صلى الله عليه وسلم: " باب من العلم يتعلمه الرجل خير له من الدنيا وما فيها ".
** حب فى روضة العقلاء ابن عبد البر
(إحياء علوم الدين ١٥/١)
** قال الحافظ العراقى فى تخريج " الإحياء " ١ / ١٥: لم أره مرفوعا إلا بلفظ خير له من مائة ركعة. رواه الطبرانى فى الأوسط بسند ضعيف من حديث أبى ذر.','3','160',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4912,'<span data-type="title" id=toc-4922>٤٩١٢ </span>- قال صلى الله عليه وسلم: " اطلبوا العلم ولو فى الصين ".
** عد هب والبيهقى فى المدخل
(إحياء علوم الدين ١٥/١)
** قال الحافظ العراقى فى تخريج " الإحياء " ١ / ١٥: قال البيهقى: متنه مشهور وأسانيده ضعيفة.','3','161',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4913,'<span data-type="title" id=toc-4923>٤٩١٣ </span>- قال عليه الصلاة والسلام: " العلم خزائن مفاتيحهاالسؤال، ألا فاسألوا فإنه يؤجر فيه أربعة: السائل والعالم والمستمع والمحب لهم ".
** حل
(إحياء علوم الدين ١٥/١)
** إسناده ضعيف','3','162',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4914,'<span data-type="title" id=toc-4924>٤٩١٤ </span>- قال صلى الله عليه وسلم: " لا ينبغى للجاهل أن يسكت على جهله ولا للعالم أن يسكت على علمه ".
** طس ابن مردويه فى التفسير ابن السنى وأبو نعيم فى رياضة المتعلمين
(إحياء علوم الدين ١٥/١)
** إسناده ضعيف','3','163',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4915,'<span data-type="title" id=toc-4925>٤٩١٥ </span>- حديث أبى ذر رضى الله عنه: " حضور مجلس عالم أفضل من صلاة ألف ركعة وعيادة ألف مريض وشهود ألف جنازة فقيل يا رسول الله: ومن قراءة القرآن فقال صلى الله عليه وسلم: وهل ينفع القرآن إلا بالعلم؟ ".
** ابن الجوزى فى الموضوعات
(إحياء علوم الدين ١٦/١)
** قال الحافظ العراقى فى تخريج " الإحياء " ١ / ١٦: ذكره ابن الجوزى فى الموضوعات من حديث عمر ولم أجده من طريق أبى ذر.','3','164',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4916,'<span data-type="title" id=toc-4926>٤٩١٦ </span>- قال عليه الصلاة والسلام: " من جاءه الموت وهو يطلب العلم ليحيى به الإسلام فبينه وبين الجنة درجة واحدة ".
** دى ابن السنى فى رياضة المتعلمين
(إحياء علوم الدين ١٦/١)
** مرسل
** قال الحافظ العراقى فى تخريج " الإحياء " ١ / ١٦: الدارمى وابن السنى فى رياضة المتعلمين من حديث الحسن فقيل: هو ابن على وقيل: هو ابن يسار فيكون مرسلا.','10','416',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4917,'<span data-type="title" id=toc-4927>٤٩١٧ </span>- " من تعلم بابا من العلم ليعلم الناس أعطى ثواب سبعين صديقا ".
** فر
(إحياء علوم الدين ١٧/١)
** إسناده ضعيف','10','417',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4918,'<span data-type="title" id=toc-4928>٤٩١٨ </span>- " إذا كان يوم القيامة يقول الله سبحانه للعابد ين والمجاهدين: ادخلوا الجنة، فيقول العلماء: بفضل علمنا تعبدوا وجاهدوا، فيقول: الله عز وجل: أنتم عندى كبعض ملائكتى اشفعوا تشفعوا فيشفعون ثم يدخلون الجنة ".
** أبو العباس الذهبى فى العلم
(إحياء علوم الدين ١٧/١)
** إسناده ضعيف','10','418',NULL,NULL,'0');
INSERT INTO [b88] VALUES (4919,'<span data-type="title" id=toc-4929>٤٩١٩ </span>- " من علم علما فكتمه ألجمه الله يوم القيامة بلجام من نار ".
** د ت جه حب ك
(إحياء علوم الدين ١٨/١)
