INSERT INTO [b88] VALUES (4928,'<span data-type="title" id=toc-4938>٤٩٢٨ </span>- " على خلفائى رحمة الله، قيل: ومن خلفائك؟ قال: الذين يحيون سنتى ويعلمونها عباد الله ".
