INSERT INTO [b896] VALUES (246,'أبو بكر واسم أبيه سنبر وانما عرف بالدستوائى لانه كان يبيع الثياب التي تجلب من دستواء ودستواء موضع بالاهواز مات سنة أربع وخمسين ومائة وكان من المتقنين
