INSERT INTO [b953] VALUES (402,'نثار الازهار لابن منظور (الجوائب) نزهة الالبا في طبقات الادبا لابن الانباري (حجر ١٢٩٤ هـ) نظام الغريب للربعي (أمين هندية) النقائض بين جرير والفرزدق (ليدن ١٩٠٥ م) نقد الشعر لقدامة بن جعفر (الجوائب ١٣٠٢ هـ) النكت في إعجاز القرآن للرماني (دهلى ١٩٣٤ م) نهاية الايجاز في دراية الاعجاز للفخر الرازي (الآداب والمؤيد) نهج البلاغة جمع الشريف الرضى (الاستقامة) نوادر أبى زيد (بيروت ١٨٩٤ م) نوادر القالى (دار الكتب المصرية ١٣٤٤ هـ) (ى) يتيمة الدهر للثعالبي (حجازى) ٣٨١',NULL,'314',NULL,NULL,'0');
