INSERT INTO [b9617] VALUES (1,'بسم الله الرحمن الرحيم
INSERT INTO [b9617] VALUES (2,'وفق المنهج التالي:
INSERT INTO [b9617] VALUES (3,'الإشارة، أو القياس على قول صريح له في موضع آخر مماثل، ويكون له صفة الوضوح والسيرورة.
INSERT INTO [b9617] VALUES (4,'<span data-type="title" id=toc-2>سورة الفاتحة</span>','1','4',NULL,NULL,'0');
INSERT INTO [b9617] VALUES (5,'<span data-type="title" id=toc-3>١ </span>- {بِسْمِ اللَّهِ الرَّحْمَنِ الرَّحِيمِ}
INSERT INTO [b9617] VALUES (6,'<span data-type="title" id=toc-4>٢ </span>- {الْحَمْدُ لِلَّهِ رَبِّ الْعَالَمِينَ}
INSERT INTO [b9617] VALUES (7,'<span data-type="title" id=toc-5>٤ </span>- {مَالِكِ يَوْمِ الدِّينِ}
INSERT INTO [b9617] VALUES (8,'<span data-type="title" id=toc-6>٥ </span>- {إِيَّاكَ نَعْبُدُ وَإِيَّاكَ نَسْتَعِينُ}
INSERT INTO [b9617] VALUES (9,'<span data-type="title" id=toc-7>٦ </span>- {اهْدِنَا الصِّرَاطَ الْمُسْتَقِيمَ} ⦗٥⦘
INSERT INTO [b9617] VALUES (10,'<span data-type="title" id=toc-8>٧ </span>- {صِرَاطَ الَّذِينَ أَنْعَمْتَ عَلَيْهِمْ غَيْرِ الْمَغْضُوبِ عَلَيْهِمْ وَلا الضَّالِّينَ}
INSERT INTO [b9617] VALUES (11,'<span data-type="title" id=toc-9>سورة البقرة</span>','1','6',NULL,NULL,'0');
INSERT INTO [b9617] VALUES (12,'<span data-type="title" id=toc-10>١ </span>- {الم}
INSERT INTO [b9617] VALUES (13,'<span data-type="title" id=toc-11>٢ </span>- {ذَلِكَ الْكِتَابُ لا رَيْبَ فِيهِ هُدًى لِلْمُتَّقِينَ}
INSERT INTO [b9617] VALUES (14,'<span data-type="title" id=toc-12>٣ </span>- {الَّذِينَ يُؤْمِنُونَ بِالْغَيْبِ وَيُقِيمُونَ الصَّلاةَ وَمِمَّا رَزَقْنَاهُمْ يُنْفِقُونَ}
INSERT INTO [b9617] VALUES (15,'<span data-type="title" id=toc-13>٤ </span>- {وَالَّذِينَ يُؤْمِنُونَ بِمَا أُنْزِلَ إِلَيْكَ وَمَا أُنْزِلَ مِنْ قَبْلِكَ وَبِالآخِرَةِ هُمْ يُوقِنُونَ}
INSERT INTO [b9617] VALUES (16,'<span data-type="title" id=toc-14>٥ </span>- {أُولَئِكَ عَلَى هُدًى مِنْ رَبِّهِمْ وَأُولَئِكَ هُمُ الْمُفْلِحُونَ}
INSERT INTO [b9617] VALUES (17,'<span data-type="title" id=toc-15>٦ </span>- {إِنَّ الَّذِينَ كَفَرُوا سَوَاءٌ عَلَيْهِمْ أَأَنْذَرْتَهُمْ أَمْ لَمْ تُنْذِرْهُمْ لا يُؤْمِنُونَ}
INSERT INTO [b9617] VALUES (18,'<span data-type="title" id=toc-16>٧ </span>- {خَتَمَ اللَّهُ عَلَى قُلُوبِهِمْ وَعَلَى سَمْعِهِمْ وَعَلَى أَبْصَارِهِمْ غِشَاوَةٌ وَلَهُمْ عَذَابٌ عَظِيمٌ}
INSERT INTO [b9617] VALUES (19,'<span data-type="title" id=toc-17>٨ </span>- {وَمِنَ النَّاسِ مَنْ يَقُولُ آمَنَّا بِاللَّهِ وَبِالْيَوْمِ الآخِرِ وَمَا هُمْ بِمُؤْمِنِينَ}
INSERT INTO [b9617] VALUES (20,'<span data-type="title" id=toc-18>٩ </span>- {يُخَادِعُونَ اللَّهَ وَالَّذِينَ آمَنُوا وَمَا يَخْدَعُونَ إِلا أَنْفُسَهُمْ وَمَا يَشْعُرُونَ}
INSERT INTO [b9617] VALUES (21,'<span data-type="title" id=toc-19>١٠ </span>- {فِي قُلُوبِهِمْ مَرَضٌ فَزَادَهُمُ اللَّهُ مَرَضًا وَلَهُمْ عَذَابٌ أَلِيمٌ بِمَا كَانُوا يَكْذِبُونَ}
INSERT INTO [b9617] VALUES (22,'<span data-type="title" id=toc-20>١١ </span>- {وَإِذَا قِيلَ لَهُمْ لا تُفْسِدُوا فِي الأَرْضِ قَالُوا إِنَّمَا نَحْنُ مُصْلِحُونَ}
INSERT INTO [b9617] VALUES (23,'<span data-type="title" id=toc-21>١٢ </span>- {أَلا إِنَّهُمْ هُمُ الْمُفْسِدُونَ وَلَكِنْ لا يَشْعُرُونَ}
INSERT INTO [b9617] VALUES (24,'<span data-type="title" id=toc-22>١٣ </span>- {وَإِذَا قِيلَ لَهُمْ آمِنُوا كَمَا آمَنَ النَّاسُ قَالُوا أَنُؤْمِنُ كَمَا آمَنَ السُّفَهَاءُ أَلا إِنَّهُمْ هُمُ السُّفَهَاءُ وَلَكِنْ لا يَعْلَمُونَ} ⦗٩⦘
INSERT INTO [b9617] VALUES (25,'<span data-type="title" id=toc-23>١٤ </span>- {وَإِذَا لَقُوا الَّذِينَ آمَنُوا قَالُوا آمَنَّا وَإِذَا خَلَوْا إِلَى شَيَاطِينِهِمْ قَالُوا إِنَّا مَعَكُمْ إِنَّمَا نَحْنُ مُسْتَهْزِئُونَ}
INSERT INTO [b9617] VALUES (26,'<span data-type="title" id=toc-24>١٥ </span>- {اللَّهُ يَسْتَهْزِئُ بِهِمْ وَيَمُدُّهُمْ فِي طُغْيَانِهِمْ يَعْمَهُونَ}
INSERT INTO [b9617] VALUES (27,'<span data-type="title" id=toc-25>١٦ </span>- {أُولَئِكَ الَّذِينَ اشْتَرَوُا الضَّلالَةَ بِالْهُدَى فَمَا رَبِحَتْ تِجَارَتُهُمْ} ⦗١٠⦘
INSERT INTO [b9617] VALUES (28,'<span data-type="title" id=toc-26>١٧ </span>- {مَثَلُهُمْ كَمَثَلِ الَّذِي اسْتَوْقَدَ نَارًا فَلَمَّا أَضَاءَتْ مَا حَوْلَهُ ذَهَبَ اللَّهُ بِنُورِهِمْ وَتَرَكَهُمْ فِي ظُلُمَاتٍ لا يُبْصِرُونَ}
INSERT INTO [b9617] VALUES (29,'<span data-type="title" id=toc-27>١٨ </span>- {صُمٌّ بُكْمٌ عُمْيٌ فَهُمْ لا يَرْجِعُونَ}
INSERT INTO [b9617] VALUES (30,'<span data-type="title" id=toc-28>١٩ </span>- {أَوْ كَصَيِّبٍ مِنَ السَّمَاءِ فِيهِ ظُلُمَاتٌ وَرَعْدٌ وَبَرْقٌ يَجْعَلُونَ أَصَابِعَهُمْ فِي آذَانِهِمْ مِنَ الصَّوَاعِقِ حَذَرَ الْمَوْتِ وَاللَّهُ مُحِيطٌ بِالْكَافِرِينَ}
INSERT INTO [b9617] VALUES (31,'<span data-type="title" id=toc-29>٢٠ </span>- {كُلَّمَا أَضَاءَ لَهُمْ مَشَوْا فِيهِ وَإِذَا أَظْلَمَ عَلَيْهِمْ قَامُوا وَلَوْ شَاءَ اللَّهُ لَذَهَبَ بِسَمْعِهِمْ وَأَبْصَارِهِمْ}
INSERT INTO [b9617] VALUES (32,'<span data-type="title" id=toc-30>٢١ </span>- {يَا أَيُّهَا النَّاسُ اعْبُدُوا رَبَّكُمُ الَّذِي خَلَقَكُمْ وَالَّذِينَ مِنْ قَبْلِكُمْ لَعَلَّكُمْ تَتَّقُونَ}
INSERT INTO [b9617] VALUES (33,'<span data-type="title" id=toc-31>٢٢ </span>- {الَّذِي جَعَلَ لَكُمُ الأَرْضَ فِرَاشًا وَالسَّمَاءَ بِنَاءً وَأَنْزَلَ مِنَ السَّمَاءِ مَاءً فَأَخْرَجَ بِهِ مِنَ الثَّمَرَاتِ رِزْقًا لَكُمْ فَلا تَجْعَلُوا لِلَّهِ أَنْدَادًا وَأَنْتُمْ تَعْلَمُونَ}
INSERT INTO [b9617] VALUES (34,'<span data-type="title" id=toc-32>٢٣ </span>- {وَإِنْ كُنْتُمْ فِي رَيْبٍ مِمَّا نَزَّلْنَا عَلَى عَبْدِنَا فَأْتُوا بِسُورَةٍ مِنْ مِثْلِهِ وَادْعُوا شُهَدَاءَكُمْ مِنْ دُونِ اللَّهِ إِنْ كُنْتُمْ صَادِقِينَ}
INSERT INTO [b9617] VALUES (35,'<span data-type="title" id=toc-33>٢٤ </span>- {فَإِنْ لَمْ تَفْعَلُوا وَلَنْ تَفْعَلُوا فَاتَّقُوا النَّارَ الَّتِي وَقُودُهَا النَّاسُ وَالْحِجَارَةُ أُعِدَّتْ لِلْكَافِرِينَ}
INSERT INTO [b9617] VALUES (36,'<span data-type="title" id=toc-34>٢٥ </span>- {وَبَشِّرِ الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ أَنَّ لَهُمْ جَنَّاتٍ تَجْرِي مِنْ تَحْتِهَا الأَنْهَارُ كُلَّمَا رُزِقُوا مِنْهَا مِنْ ثَمَرَةٍ رِزْقًا قَالُوا هَذَا الَّذِي رُزِقْنَا مِنْ قَبْلُ وَأُتُوا بِهِ مُتَشَابِهًا وَلَهُمْ فِيهَا أَزْوَاجٌ مُطَهَّرَةٌ وَهُمْ فِيهَا خَالِدُونَ} ⦗١٣⦘
INSERT INTO [b9617] VALUES (37,'<span data-type="title" id=toc-35>٢٦ </span>- {إِنَّ اللَّهَ لا يَسْتَحْيِي أَنْ يَضْرِبَ مَثَلا مَا بَعُوضَةً فَمَا فَوْقَهَا فَأَمَّا الَّذِينَ آمَنُوا فَيَعْلَمُونَ أَنَّهُ الْحَقُّ مِنْ رَبِّهِمْ وَأَمَّا الَّذِينَ كَفَرُوا فَيَقُولُونَ مَاذَا أَرَادَ اللَّهُ بِهَذَا مَثَلا يُضِلُّ بِهِ كَثِيرًا وَيَهْدِي بِهِ كَثِيرًا وَمَا يُضِلُّ بِهِ إِلا الْفَاسِقِينَ}
INSERT INTO [b9617] VALUES (38,'<span data-type="title" id=toc-36>٢٧ </span>- {الَّذِينَ يَنْقُضُونَ عَهْدَ اللَّهِ مِنْ بَعْدِ مِيثَاقِهِ وَيَقْطَعُونَ مَا أَمَرَ اللَّهُ بِهِ أَنْ يُوصَلَ وَيُفْسِدُونَ فِي الأَرْضِ أُولَئِكَ هُمُ الْخَاسِرُونَ}
INSERT INTO [b9617] VALUES (39,'<span data-type="title" id=toc-37>٢٨ </span>- {كَيْفَ تَكْفُرُونَ بِاللَّهِ وَكُنْتُمْ أَمْوَاتًا فَأَحْيَاكُمْ ثُمَّ يُمِيتُكُمْ ثُمَّ يُحْيِيكُمْ ثُمَّ إِلَيْهِ تُرْجَعُونَ}
INSERT INTO [b9617] VALUES (40,'<span data-type="title" id=toc-38>٢٩ </span>- {هُوَ الَّذِي خَلَقَ لَكُمْ مَا فِي الأَرْضِ جَمِيعًا ثُمَّ اسْتَوَى إِلَى السَّمَاءِ فَسَوَّاهُنَّ سَبْعَ سَمَاوَاتٍ وَهُوَ بِكُلِّ شَيْءٍ عَلِيمٌ}
INSERT INTO [b9617] VALUES (41,'<span data-type="title" id=toc-39>٣٠ </span>- {وَإِذْ قَالَ رَبُّكَ لِلْمَلائِكَةِ إِنِّي جَاعِلٌ فِي الأَرْضِ خَلِيفَةً قَالُوا أَتَجْعَلُ فِيهَا مَنْ يُفْسِدُ فِيهَا وَيَسْفِكُ الدِّمَاءَ وَنَحْنُ نُسَبِّحُ بِحَمْدِكَ وَنُقَدِّسُ لَكَ قَالَ إِنِّي أَعْلَمُ مَا لا تَعْلَمُونَ}
INSERT INTO [b9617] VALUES (42,'<span data-type="title" id=toc-40>٣١ </span>- {وَعَلَّمَ آدَمَ الأَسْمَاءَ كُلَّهَا ثُمَّ عَرَضَهُمْ عَلَى الْمَلائِكَةِ فَقَالَ أَنْبِئُونِي بِأَسْمَاءِ هَؤُلاءِ إِنْ كُنْتُمْ صَادِقِينَ}
INSERT INTO [b9617] VALUES (43,'<span data-type="title" id=toc-41>٣٢ </span>- {قَالُوا سُبْحَانَكَ لا عِلْمَ لَنَا إِلا مَا عَلَّمْتَنَا إِنَّكَ أَنْتَ الْعَلِيمُ الْحَكِيمُ}
INSERT INTO [b9617] VALUES (44,'<span data-type="title" id=toc-42>٣٣ </span>- {قَالَ يَا آدَمُ أَنْبِئْهُمْ بِأَسْمَائِهِمْ فَلَمَّا أَنْبَأَهُمْ بِأَسْمَائِهِمْ قَالَ أَلَمْ أَقُلْ لَكُمْ إِنِّي أَعْلَمُ ⦗١٦⦘ غَيْبَ السَّمَاوَاتِ وَالأَرْضِ وَأَعْلَمُ مَا تُبْدُونَ وَمَا كُنْتُمْ تَكْتُمُونَ}
INSERT INTO [b9617] VALUES (45,'<span data-type="title" id=toc-43>٣٤ </span>- {وَإِذْ قُلْنَا لِلْمَلائِكَةِ اسْجُدُوا لآدَمَ فَسَجَدُوا إِلا إِبْلِيسَ أَبَى}
INSERT INTO [b9617] VALUES (46,'<span data-type="title" id=toc-44>٣٥ </span>- {اسْكُنْ أَنْتَ وَزَوْجُكَ الْجَنَّةَ وَكُلا مِنْهَا رَغَدًا حَيْثُ شِئْتُمَا وَلا تَقْرَبَا هَذِهِ الشَّجَرَةَ فَتَكُونَا مِنَ الظَّالِمِينَ}
INSERT INTO [b9617] VALUES (47,'<span data-type="title" id=toc-45>٣٦ </span>- {وَقُلْنَا اهْبِطُوا بَعْضُكُمْ لِبَعْضٍ عَدُوٌّ وَلَكُمْ فِي الأَرْضِ مُسْتَقَرٌّ وَمَتَاعٌ إِلَى حِينٍ}
INSERT INTO [b9617] VALUES (48,'<span data-type="title" id=toc-46>٣٧ </span>- {إِنَّهُ هُوَ التَّوَّابُ الرَّحِيمُ}
INSERT INTO [b9617] VALUES (49,'<span data-type="title" id=toc-47>٣٨ </span>- {فَإِمَّا يَأْتِيَنَّكُمْ مِنِّي هُدًى فَمَنْ تَبِعَ هُدَايَ فَلا خَوْفٌ عَلَيْهِمْ وَلا هُمْ يَحْزَنُونَ}
INSERT INTO [b9617] VALUES (50,'<span data-type="title" id=toc-48>٣٩ </span>- {وَالَّذِينَ كَفَرُوا وَكَذَّبُوا بِآيَاتِنَا أُولَئِكَ أَصْحَابُ النَّارِ هُمْ فِيهَا خَالِدُونَ}
INSERT INTO [b9617] VALUES (51,'<span data-type="title" id=toc-49>٤٠ </span>- {اذْكُرُوا نِعْمَتِيَ الَّتِي أَنْعَمْتُ عَلَيْكُمْ وَأَوْفُوا بِعَهْدِي أُوفِ بِعَهْدِكُمْ وَإِيَّايَ فَارْهَبُونِ}
INSERT INTO [b9617] VALUES (52,'<span data-type="title" id=toc-50>٤١ </span>- {وَآمِنُوا بِمَا أَنْزَلْتُ مُصَدِّقًا لِمَا مَعَكُمْ وَلا تَكُونُوا أَوَّلَ كَافِرٍ بِهِ وَلا تَشْتَرُوا بِآيَاتِي ثَمَنًا قَلِيلا وَإِيَّايَ فَاتَّقُونِ}
INSERT INTO [b9617] VALUES (53,'<span data-type="title" id=toc-51>٤٢ </span>- {وَلا تَلْبِسُوا الْحَقَّ بِالْبَاطِلِ وَتَكْتُمُوا الْحَقَّ وَأَنْتُمْ تَعْلَمُونَ}
INSERT INTO [b9617] VALUES (54,'<span data-type="title" id=toc-52>٤٤ </span>- {أَتَأْمُرُونَ النَّاسَ بِالْبِرِّ وَتَنْسَوْنَ أَنْفُسَكُمْ وَأَنْتُمْ تَتْلُونَ الْكِتَابَ أَفَلا تَعْقِلُونَ}
INSERT INTO [b9617] VALUES (55,'<span data-type="title" id=toc-53>٤٥ </span>- {وَاسْتَعِينُوا بِالصَّبْرِ وَالصَّلاةِ وَإِنَّهَا لَكَبِيرَةٌ إِلا عَلَى الْخَاشِعِينَ}
INSERT INTO [b9617] VALUES (56,'<span data-type="title" id=toc-54>٤٦ </span>- {الَّذِينَ يَظُنُّونَ أَنَّهُمْ مُلاقُو رَبِّهِمْ وَأَنَّهُمْ إِلَيْهِ رَاجِعُونَ}
INSERT INTO [b9617] VALUES (57,'<span data-type="title" id=toc-55>٤٧ </span>- {اذْكُرُوا نِعْمَتِيَ الَّتِي أَنْعَمْتُ عَلَيْكُمْ وَأَنِّي فَضَّلْتُكُمْ عَلَى الْعَالَمِينَ}
INSERT INTO [b9617] VALUES (58,'<span data-type="title" id=toc-56>٤٨ </span>- {وَاتَّقُوا يَوْمًا لا تَجْزِي نَفْسٌ عَنْ نَفْسٍ شَيْئًا وَلا يُقْبَلُ مِنْهَا شَفَاعَةٌ وَلا يُؤْخَذُ مِنْهَا ⦗٢٠⦘ عَدْلٌ وَلا هُمْ يُنْصَرُونَ}
INSERT INTO [b9617] VALUES (59,'<span data-type="title" id=toc-57>٤٩ </span>- {وَإِذْ نَجَّيْنَاكُمْ مِنْ آلِ فِرْعَوْنَ يَسُومُونَكُمْ سُوءَ الْعَذَابِ يُذَبِّحُونَ أَبْنَاءَكُمْ وَيَسْتَحْيُونَ نِسَاءَكُمْ وَفِي ذَلِكُمْ بَلاءٌ مِنْ رَبِّكُمْ عَظِيمٌ}
INSERT INTO [b9617] VALUES (60,'<span data-type="title" id=toc-58>٥١ </span>- {وَإِذْ وَاعَدْنَا مُوسَى أَرْبَعِينَ لَيْلَةً ثُمَّ اتَّخَذْتُمُ الْعِجْلَ مِنْ بَعْدِهِ وَأَنْتُمْ ظَالِمُونَ}
INSERT INTO [b9617] VALUES (61,'<span data-type="title" id=toc-59>٥٢ </span>- {ثُمَّ عَفَوْنَا عَنْكُمْ مِنْ بَعْدِ ذَلِكَ لَعَلَّكُمْ تَشْكُرُونَ}
INSERT INTO [b9617] VALUES (62,'<span data-type="title" id=toc-60>٥٤ </span>- {يَا قَوْمِ إِنَّكُمْ ظَلَمْتُمْ أَنْفُسَكُمْ بِاتِّخَاذِكُمُ الْعِجْلَ فَتُوبُوا إِلَى بَارِئِكُمْ فَاقْتُلُوا أَنْفُسَكُمْ ذَلِكُمْ خَيْرٌ لَكُمْ عِنْدَ بَارِئِكُمْ فَتَابَ عَلَيْكُمْ إِنَّهُ هُوَ التَّوَّابُ الرَّحِيمُ}
INSERT INTO [b9617] VALUES (63,'<span data-type="title" id=toc-61>٥٥ </span>- {وَإِذْ قُلْتُمْ يَا مُوسَى لَنْ نُؤْمِنَ لَكَ حَتَّى نَرَى اللَّهَ جَهْرَةً فَأَخَذَتْكُمُ الصَّاعِقَةُ وَأَنْتُمْ تَنْظُرُونَ}
INSERT INTO [b9617] VALUES (64,'<span data-type="title" id=toc-62>٥٧ </span>- {وَأَنْزَلْنَا عَلَيْكُمُ الْمَنَّ وَالسَّلْوَى كُلُوا مِنْ طَيِّبَاتِ مَا رَزَقْنَاكُمْ وَمَا ظَلَمُونَا وَلَكِنْ كَانُوا أَنْفُسَهُمْ يَظْلِمُونَ}
INSERT INTO [b9617] VALUES (65,'<span data-type="title" id=toc-63>٥٨ </span>- {وَإِذْ قُلْنَا ادْخُلُوا هَذِهِ الْقَرْيَةَ فَكُلُوا مِنْهَا حَيْثُ شِئْتُمْ رَغَدًا وَادْخُلُوا الْبَابَ سُجَّدًا وَقُولُوا حِطَّةٌ نَغْفِرْ لَكُمْ خَطَايَاكُمْ وَسَنَزِيدُ الْمُحْسِنِينَ}
INSERT INTO [b9617] VALUES (66,'<span data-type="title" id=toc-64>٥٩ </span>- {فَبَدَّلَ الَّذِينَ ظَلَمُوا قَوْلا غَيْرَ الَّذِي قِيلَ لَهُمْ فَأَنْزَلْنَا عَلَى الَّذِينَ ظَلَمُوا رِجْزًا مِنَ السَّمَاءِ بِمَا كَانُوا يَفْسُقُونَ}
INSERT INTO [b9617] VALUES (67,'<span data-type="title" id=toc-65>٦٠ </span>- {وَإِذِ اسْتَسْقَى مُوسَى لِقَوْمِهِ فَقُلْنَا اضْرِبْ بِعَصَاكَ الْحَجَرَ فَانْفَجَرَتْ مِنْهُ اثْنَتَا عَشْرَةَ عَيْنًا قَدْ عَلِمَ كُلُّ أُنَاسٍ مَشْرَبَهُمْ كُلُوا وَاشْرَبُوا مِنْ رِزْقِ اللَّهِ وَلا تَعْثَوْا فِي الأَرْضِ مُفْسِدِينَ}
INSERT INTO [b9617] VALUES (68,'<span data-type="title" id=toc-66>٦١ </span>- {لَنْ نَصْبِرَ عَلَى طَعَامٍ وَاحِدٍ فَادْعُ لَنَا رَبَّكَ يُخْرِجْ لَنَا مِمَّا تُنْبِتُ الأَرْضُ مِنْ بَقْلِهَا وَقِثَّائِهَا وَفُومِهَا وَعَدَسِهَا وَبَصَلِهَا قَالَ أَتَسْتَبْدِلُونَ الَّذِي هُوَ أَدْنَى بِالَّذِي هُوَ خَيْرٌ اهْبِطُوا مِصْرًا فَإِنَّ لَكُمْ مَا سَأَلْتُمْ وَضُرِبَتْ عَلَيْهِمُ الذِّلَّةُ وَالْمَسْكَنَةُ وَبَاءُوا بِغَضَبٍ مِنَ اللَّهِ ذَلِكَ بِأَنَّهُمْ كَانُوا يَكْفُرُونَ بِآيَاتِ اللَّهِ وَيَقْتُلُونَ النَّبِيِّينَ بِغَيْرِ الْحَقِّ ذَلِكَ بِمَا عَصَوْا وَكَانُوا يَعْتَدُونَ}
INSERT INTO [b9617] VALUES (69,'<span data-type="title" id=toc-67>٦٢ </span>- {إِنَّ الَّذِينَ آمَنُوا وَالَّذِينَ هَادُوا وَالنَّصَارَى وَالصَّابِئِينَ مَنْ آمَنَ بِاللَّهِ وَالْيَوْمِ الآخِرِ وَعَمِلَ صَالِحًا فَلَهُمْ أَجْرُهُمْ عِنْدَ رَبِّهِمْ وَلا خَوْفٌ عَلَيْهِمْ وَلا هُمْ يَحْزَنُونَ}
INSERT INTO [b9617] VALUES (70,'<span data-type="title" id=toc-68>٦٣ </span>- {وَإِذْ أَخَذْنَا مِيثَاقَكُمْ وَرَفَعْنَا فَوْقَكُمُ الطُّورَ خُذُوا مَا آتَيْنَاكُمْ بِقُوَّةٍ وَاذْكُرُوا مَا فِيهِ لَعَلَّكُمْ تَتَّقُونَ}
INSERT INTO [b9617] VALUES (71,'<span data-type="title" id=toc-69>٦٤ </span>- {فَلَوْلا فَضْلُ اللَّهِ عَلَيْكُمْ وَرَحْمَتُهُ لَكُنْتُمْ مِنَ الْخَاسِرِينَ}
INSERT INTO [b9617] VALUES (72,'<span data-type="title" id=toc-70>٦٥ </span>- {وَلَقَدْ عَلِمْتُمُ الَّذِينَ اعْتَدَوْا مِنْكُمْ فِي السَّبْتِ فَقُلْنَا لَهُمْ كُونُوا قِرَدَةً خَاسِئِينَ}
INSERT INTO [b9617] VALUES (73,'<span data-type="title" id=toc-71>٦٦ </span>- {فَجَعَلْنَاهَا نَكَالا لِمَا بَيْنَ يَدَيْهَا وَمَا خَلْفَهَا}
INSERT INTO [b9617] VALUES (74,'<span data-type="title" id=toc-72>٦٧ </span>- {وَإِذْ قَالَ مُوسَى لِقَوْمِهِ إِنَّ اللَّهَ يَأْمُرُكُمْ أَنْ تَذْبَحُوا بَقَرَةً قَالُوا أَتَتَّخِذُنَا هُزُوًا قَالَ أَعُوذُ بِاللَّهِ أَنْ أَكُونَ مِنَ الْجَاهِلِينَ}
INSERT INTO [b9617] VALUES (75,'<span data-type="title" id=toc-73>٦٨ </span>- {قَالُوا ادْعُ لَنَا رَبَّكَ يُبَيِّنْ لَنَا مَا هِيَ قَالَ إِنَّهُ يَقُولُ إِنَّهَا بَقَرَةٌ لا فَارِضٌ وَلا بِكْرٌ عَوَانٌ بَيْنَ ذَلِكَ فَافْعَلُوا مَا تُؤْمَرُونَ}
INSERT INTO [b9617] VALUES (76,'<span data-type="title" id=toc-74>٦٩ </span>- {يُبَيِّنْ لَنَا مَا لَوْنُهَا قَالَ إِنَّهُ يَقُولُ إِنَّهَا بَقَرَةٌ صَفْرَاءُ فَاقِعٌ لَوْنُهَا تَسُرُّ النَّاظِرِينَ}
INSERT INTO [b9617] VALUES (77,'<span data-type="title" id=toc-75>٧٠ </span>- {إِنَّ الْبَقَرَ تَشَابَهَ عَلَيْنَا وَإِنَّا إِنْ شَاءَ اللَّهُ لَمُهْتَدُونَ}
INSERT INTO [b9617] VALUES (78,'<span data-type="title" id=toc-76>٧١ </span>- {قَالَ إِنَّهُ يَقُولُ إِنَّهَا بَقَرَةٌ لا ذَلُولٌ تُثِيرُ الأَرْضَ وَلا تَسْقِي الْحَرْثَ مُسَلَّمَةٌ لا شِيَةَ فِيهَا قَالُوا الآنَ جِئْتَ بِالْحَقِّ فَذَبَحُوهَا وَمَا كَادُوا يَفْعَلُونَ}
INSERT INTO [b9617] VALUES (79,'<span data-type="title" id=toc-77>٧٢ </span>- {وَإِذْ قَتَلْتُمْ نَفْسًا فَادَّارَأْتُمْ فِيهَا وَاللَّهُ مُخْرِجٌ مَا كُنْتُمْ تَكْتُمُونَ}
INSERT INTO [b9617] VALUES (80,'<span data-type="title" id=toc-78>٧٣ </span>- {فَقُلْنَا اضْرِبُوهُ بِبَعْضِهَا كَذَلِكَ يُحْيِي اللَّهُ الْمَوْتَى وَيُرِيكُمْ آيَاتِهِ لَعَلَّكُمْ تَعْقِلُونَ}
INSERT INTO [b9617] VALUES (81,'<span data-type="title" id=toc-79>٧٤ </span>- {ثُمَّ قَسَتْ قُلُوبُكُمْ مِنْ بَعْدِ ذَلِكَ فَهِيَ كَالْحِجَارَةِ أَوْ أَشَدُّ قَسْوَةً وَإِنَّ مِنَ الْحِجَارَةِ لَمَا يَتَفَجَّرُ مِنْهُ الأَنْهَارُ وَإِنَّ مِنْهَا لَمَا يَشَّقَّقُ فَيَخْرُجُ مِنْهُ الْمَاءُ وَإِنَّ مِنْهَا لَمَا يَهْبِطُ مِنْ خَشْيَةِ اللَّهِ وَمَا اللَّهُ بِغَافِلٍ عَمَّا تَعْمَلُونَ}
INSERT INTO [b9617] VALUES (82,'<span data-type="title" id=toc-80>٧٥ </span>- {أَفَتَطْمَعُونَ أَنْ يُؤْمِنُوا لَكُمْ وَقَدْ كَانَ فَرِيقٌ مِنْهُمْ يَسْمَعُونَ كَلامَ اللَّهِ ثُمَّ يُحَرِّفُونَهُ مِنْ بَعْدِ مَا عَقَلُوهُ وَهُمْ يَعْلَمُونَ}
INSERT INTO [b9617] VALUES (83,'<span data-type="title" id=toc-81>٧٦ </span>- {قَالُوا أَتُحَدِّثُونَهُمْ بِمَا فَتَحَ اللَّهُ عَلَيْكُمْ لِيُحَاجُّوكُمْ بِهِ عِنْدَ رَبِّكُمْ أَفَلا تَعْقِلُونَ}
INSERT INTO [b9617] VALUES (84,'<span data-type="title" id=toc-82>٧٧ </span>- {أَوَلا يَعْلَمُونَ أَنَّ اللَّهَ يَعْلَمُ مَا يُسِرُّونَ وَمَا يُعْلِنُونَ}
INSERT INTO [b9617] VALUES (85,'<span data-type="title" id=toc-83>٧٨ </span>- {وَمِنْهُمْ أُمِّيُّونَ لا يَعْلَمُونَ الْكِتَابَ إِلا أَمَانِيَّ وَإِنْ هُمْ إِلا يَظُنُّونَ}
INSERT INTO [b9617] VALUES (86,'<span data-type="title" id=toc-84>٧٩ </span>- {فَوَيْلٌ لِلَّذِينَ يَكْتُبُونَ الْكِتَابَ بِأَيْدِيهِمْ ثُمَّ يَقُولُونَ هَذَا مِنْ عِنْدِ اللَّهِ لِيَشْتَرُوا بِهِ ثَمَنًا قَلِيلا فَوَيْلٌ لَهُمْ مِمَّا كَتَبَتْ أَيْدِيهِمْ}
INSERT INTO [b9617] VALUES (87,'<span data-type="title" id=toc-85>٨٠ </span>- {وَقَالُوا لَنْ تَمَسَّنَا النَّارُ إِلا أَيَّامًا مَعْدُودَةً قُلْ أَتَّخَذْتُمْ عِنْدَ اللَّهِ عَهْدًا فَلَنْ يُخْلِفَ اللَّهُ عَهْدَهُ أَمْ تَقُولُونَ عَلَى اللَّهِ مَا لا تَعْلَمُونَ}
INSERT INTO [b9617] VALUES (88,'<span data-type="title" id=toc-86>٨١ </span>- {بَلَى مَنْ كَسَبَ سَيِّئَةً وَأَحَاطَتْ بِهِ خَطِيئَتُهُ فَأُولَئِكَ أَصْحَابُ النَّارِ هُمْ فِيهَا خَالِدُونَ}
INSERT INTO [b9617] VALUES (89,'<span data-type="title" id=toc-87>٨٢ </span>- {وَالَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ أُولَئِكَ أَصْحَابُ الْجَنَّةِ هُمْ فِيهَا خَالِدُونَ}
INSERT INTO [b9617] VALUES (90,'<span data-type="title" id=toc-88>٨٣ </span>- {وَإِذْ أَخَذْنَا مِيثَاقَ بَنِي إِسْرَائِيلَ لا تَعْبُدُونَ إِلا اللَّهَ وَبِالْوَالِدَيْنِ إِحْسَانًا وَذِي الْقُرْبَى وَالْيَتَامَى وَالْمَسَاكِينِ وَقُولُوا لِلنَّاسِ حُسْنًا وَأَقِيمُوا الصَّلاةَ وَآتُوا الزَّكَاةَ ثُمَّ تَوَلَّيْتُمْ إِلا قَلِيلا مِنْكُمْ وَأَنْتُمْ مُعْرِضُونَ}
INSERT INTO [b9617] VALUES (91,'<span data-type="title" id=toc-89>٨٤ </span>- {وَإِذْ أَخَذْنَا مِيثَاقَكُمْ لا تَسْفِكُونَ دِمَاءَكُمْ وَلا تُخْرِجُونَ أَنْفُسَكُمْ مِنْ دِيَارِكُمْ ثُمَّ أَقْرَرْتُمْ وَأَنْتُمْ تَشْهَدُونَ}
INSERT INTO [b9617] VALUES (92,'<span data-type="title" id=toc-90>٨٥ </span>- {ثُمَّ أَنْتُمْ هَؤُلاءِ تَقْتُلُونَ أَنْفُسَكُمْ وَتُخْرِجُونَ فَرِيقًا مِنْكُمْ مِنْ دِيَارِهِمْ تَظَاهَرُونَ عَلَيْهِمْ بِالإِثْمِ وَالْعُدْوَانِ وَإِنْ يَأْتُوكُمْ أُسَارَى تُفَادُوهُمْ وَهُوَ مُحَرَّمٌ عَلَيْكُمْ إِخْرَاجُهُمْ أَفَتُؤْمِنُونَ بِبَعْضِ الْكِتَابِ وَتَكْفُرُونَ بِبَعْضٍ فَمَا جَزَاءُ مَنْ يَفْعَلُ ذَلِكَ مِنْكُمْ إِلا خِزْيٌ فِي الْحَيَاةِ الدُّنْيَا}
INSERT INTO [b9617] VALUES (93,'<span data-type="title" id=toc-91>٨٦ </span>- {أُولَئِكَ الَّذِينَ اشْتَرَوُا الْحَيَاةَ الدُّنْيَا بِالآخِرَةِ فَلا يُخَفَّفُ عَنْهُمُ الْعَذَابُ وَلا ⦗٣٢⦘ هُمْ يُنْصَرُونَ}
INSERT INTO [b9617] VALUES (94,'<span data-type="title" id=toc-92>٨٧ </span>- {وَلَقَدْ آتَيْنَا مُوسَى الْكِتَابَ وَقَفَّيْنَا مِنْ بَعْدِهِ بِالرُّسُلِ وَآتَيْنَا عِيسَى ابْنَ مَرْيَمَ الْبَيِّنَاتِ وَأَيَّدْنَاهُ بِرُوحِ الْقُدُسِ أَفَكُلَّمَا جَاءَكُمْ رَسُولٌ بِمَا لا تَهْوَى أَنْفُسُكُمُ اسْتَكْبَرْتُمْ فَفَرِيقًا كَذَّبْتُمْ وَفَرِيقًا تَقْتُلُونَ}
INSERT INTO [b9617] VALUES (95,'<span data-type="title" id=toc-93>٨٨ </span>- {بَلْ لَعَنَهُمُ اللَّهُ بِكُفْرِهِمْ فَقَلِيلا مَا يُؤْمِنُونَ}
INSERT INTO [b9617] VALUES (96,'<span data-type="title" id=toc-94>٨٩ </span>- {وَلَمَّا جَاءَهُمْ كِتَابٌ مِنْ عِنْدِ اللَّهِ مُصَدِّقٌ لِمَا مَعَهُمْ وَكَانُوا مِنْ قَبْلُ يَسْتَفْتِحُونَ عَلَى الَّذِينَ كَفَرُوا فَلَمَّا جَاءَهُمْ مَا عَرَفُوا كَفَرُوا بِهِ فَلَعْنَةُ اللَّهِ عَلَى الْكَافِرِينَ}
INSERT INTO [b9617] VALUES (97,'<span data-type="title" id=toc-95>٩٠ </span>- {بِئْسَمَا اشْتَرَوْا بِهِ أَنْفُسَهُمْ أَنْ يَكْفُرُوا بِمَا أَنْزَلَ اللَّهُ بَغْيًا أَنْ يُنَزِّلَ اللَّهُ مِنْ فَضْلِهِ عَلَى مَنْ يَشَاءُ مِنْ عِبَادِهِ فَبَاءُوا بِغَضَبٍ عَلَى غَضَبٍ وَلِلْكَافِرِينَ عَذَابٌ مُهِينٌ}
INSERT INTO [b9617] VALUES (98,'<span data-type="title" id=toc-96>٩١ </span>- {وَإِذَا قِيلَ لَهُمْ آمِنُوا بِمَا أَنْزَلَ اللَّهُ قَالُوا نُؤْمِنُ بِمَا أُنْزِلَ عَلَيْنَا وَيَكْفُرُونَ بِمَا وَرَاءَهُ وَهُوَ الْحَقُّ مُصَدِّقًا لِمَا مَعَهُمْ قُلْ فَلِمَ تَقْتُلُونَ أَنْبِيَاءَ اللَّهِ مِنْ قَبْلُ إِنْ كُنْتُمْ مُؤْمِنِينَ}
INSERT INTO [b9617] VALUES (99,'<span data-type="title" id=toc-97>٩٢ </span>- {وَلَقَدْ جَاءَكُمْ مُوسَى بِالْبَيِّنَاتِ ثُمَّ اتَّخَذْتُمُ الْعِجْلَ مِنْ بَعْدِهِ وَأَنْتُمْ ظَالِمُونَ} ⦗٣٥⦘
INSERT INTO [b9617] VALUES (100,'<span data-type="title" id=toc-98>٩٣ </span>- {وَإِذْ أَخَذْنَا مِيثَاقَكُمْ وَرَفَعْنَا فَوْقَكُمُ الطُّورَ خُذُوا مَا آتَيْنَاكُمْ بِقُوَّةٍ وَاسْمَعُوا قَالُوا سَمِعْنَا وَعَصَيْنَا وَأُشْرِبُوا فِي قُلُوبِهِمُ الْعِجْلَ بِكُفْرِهِمْ قُلْ بِئْسَمَا يَأْمُرُكُمْ بِهِ إِيمَانُكُمْ إِنْ كُنْتُمْ مُؤْمِنِينَ}
INSERT INTO [b9617] VALUES (101,'<span data-type="title" id=toc-99>٩٤ </span>- {قُلْ إِنْ كَانَتْ لَكُمُ الدَّارُ الآخِرَةُ عِنْدَ اللَّهِ خَالِصَةً مِنْ دُونِ النَّاسِ فَتَمَنَّوُا الْمَوْتَ إِنْ كُنْتُمْ صَادِقِينَ}
INSERT INTO [b9617] VALUES (102,'<span data-type="title" id=toc-100>٩٥ </span>- {وَاللَّهُ عَلِيمٌ بِالظَّالِمِينَ}
INSERT INTO [b9617] VALUES (103,'<span data-type="title" id=toc-101>٩٦ </span>- {وَلَتَجِدَنَّهُمْ أَحْرَصَ النَّاسِ عَلَى حَيَاةٍ وَمِنَ الَّذِينَ أَشْرَكُوا يَوَدُّ أَحَدُهُمْ لَوْ يُعَمَّرُ أَلْفَ سَنَةٍ وَمَا هُوَ بِمُزَحْزِحِهِ مِنَ الْعَذَابِ أَنْ يُعَمَّرَ وَاللَّهُ بَصِيرٌ بِمَا يَعْمَلُونَ}
INSERT INTO [b9617] VALUES (104,'<span data-type="title" id=toc-102>٩٧ </span>- {قُلْ مَنْ كَانَ عَدُوًّا لِجِبْرِيلَ فَإِنَّهُ نَزَّلَهُ عَلَى قَلْبِكَ بِإِذْنِ اللَّهِ مُصَدِّقًا لِمَا بَيْنَ يَدَيْهِ} ⦗٣٧⦘
INSERT INTO [b9617] VALUES (105,'<span data-type="title" id=toc-103>٩٨ </span>- {مَنْ كَانَ عَدُوًّا لِلَّهِ وَمَلائِكَتِهِ وَرُسُلِهِ وَجِبْرِيلَ وَمِيكَالَ فَإِنَّ اللَّهَ عَدُوٌّ لِلْكَافِرِينَ}
INSERT INTO [b9617] VALUES (106,'<span data-type="title" id=toc-104>٩٩ </span>- {وَلَقَدْ أَنْزَلْنَا إِلَيْكَ آيَاتٍ بَيِّنَاتٍ وَمَا يَكْفُرُ بِهَا إِلا الْفَاسِقُونَ}
INSERT INTO [b9617] VALUES (107,'<span data-type="title" id=toc-105>١٠٠ </span>- {أَوَكُلَّمَا عَاهَدُوا عَهْدًا نَبَذَهُ فَرِيقٌ مِنْهُمْ بَلْ أَكْثَرُهُمْ لا يُؤْمِنُونَ}
INSERT INTO [b9617] VALUES (108,'<span data-type="title" id=toc-106>١٠١ </span>- {وَلَمَّا جَاءَهُمْ رَسُولٌ مِنْ عِنْدِ اللَّهِ مُصَدِّقٌ لِمَا مَعَهُمْ نَبَذَ فَرِيقٌ مِنَ الَّذِينَ أُوتُوا الْكِتَابَ كِتَابَ اللَّهِ وَرَاءَ ظُهُورِهِمْ كَأَنَّهُمْ لا يَعْلَمُونَ}
INSERT INTO [b9617] VALUES (109,'<span data-type="title" id=toc-107>١٠٢ </span>- {وَمَا كَفَرَ سُلَيْمَانُ وَلَكِنَّ الشَّيَاطِينَ كَفَرُوا يُعَلِّمُونَ النَّاسَ السِّحْرَ وَمَا أُنْزِلَ عَلَى الْمَلَكَيْنِ بِبَابِلَ هَارُوتَ وَمَارُوتَ وَمَا يُعَلِّمَانِ مِنْ أَحَدٍ حَتَّى يَقُولا إِنَّمَا نَحْنُ فِتْنَةٌ فَلا تَكْفُرْ فَيَتَعَلَّمُونَ مِنْهُمَا مَا يُفَرِّقُونَ بِهِ بَيْنَ الْمَرْءِ وَزَوْجِهِ وَمَا هُمْ بِضَارِّينَ بِهِ مِنْ أَحَدٍ إِلا بِإِذْنِ اللَّهِ وَيَتَعَلَّمُونَ مَا يَضُرُّهُمْ وَلا يَنْفَعُهُمْ وَلَقَدْ عَلِمُوا لَمَنِ اشْتَرَاهُ مَا لَهُ فِي الآخِرَةِ مِنْ خَلاقٍ وَلَبِئْسَ مَا شَرَوْا بِهِ أَنْفُسَهُمْ لَوْ كَانُوا يَعْلَمُونَ}
INSERT INTO [b9617] VALUES (110,'<span data-type="title" id=toc-108>١٠٣ </span>- {وَلَوْ أَنَّهُمْ آمَنُوا وَاتَّقَوْا لَمَثُوبَةٌ مِنْ عِنْدِ اللَّهِ خَيْرٌ لَوْ كَانُوا يَعْلَمُونَ}
INSERT INTO [b9617] VALUES (111,'<span data-type="title" id=toc-109>١٠٤ </span>- {يَا أَيُّهَا الَّذِينَ آمَنُوا لا تَقُولُوا رَاعِنَا وَقُولُوا انْظُرْنَا وَاسْمَعُوا وَلِلْكَافِرِينَ ⦗٤٠⦘ عَذَابٌ أَلِيمٌ}
INSERT INTO [b9617] VALUES (112,'<span data-type="title" id=toc-110>١٠٥ </span>- {مَا يَوَدُّ الَّذِينَ كَفَرُوا مِنْ أَهْلِ الْكِتَابِ وَلا الْمُشْرِكِينَ أَنْ يُنَزَّلَ عَلَيْكُمْ مِنْ خَيْرٍ مِنْ رَبِّكُمْ وَاللَّهُ يَخْتَصُّ بِرَحْمَتِهِ مَنْ يَشَاءُ وَاللَّهُ ذُو الْفَضْلِ الْعَظِيمِ}
INSERT INTO [b9617] VALUES (113,'<span data-type="title" id=toc-111>١٠٦ </span>- {مَا نَنْسَخْ مِنْ آيَةٍ أَوْ نُنْسِهَا نَأْتِ بِخَيْرٍ مِنْهَا أَوْ مِثْلِهَا أَلَمْ تَعْلَمْ أَنَّ اللَّهَ عَلَى كُلِّ شَيْءٍ قَدِيرٌ}
INSERT INTO [b9617] VALUES (114,'<span data-type="title" id=toc-112>١٠٧ </span>- {أَلَمْ تَعْلَمْ أَنَّ اللَّهَ لَهُ مُلْكُ السَّمَاوَاتِ وَالأَرْضِ وَمَا لَكُمْ مِنْ دُونِ اللَّهِ مِنْ وَلِيٍّ وَلا نَصِيرٍ} ⦗٤١⦘
INSERT INTO [b9617] VALUES (115,'<span data-type="title" id=toc-113>١٠٨ </span>- {أَمْ تُرِيدُونَ أَنْ تَسْأَلُوا رَسُولَكُمْ كَمَا سُئِلَ مُوسَى مِنْ قَبْلُ وَمَنْ يَتَبَدَّلِ الْكُفْرَ بِالإِيمَانِ فَقَدْ ضَلَّ سَوَاءَ السَّبِيلِ}
INSERT INTO [b9617] VALUES (116,'<span data-type="title" id=toc-114>١٠٩ </span>- {وَدَّ كَثِيرٌ مِنْ أَهْلِ الْكِتَابِ لَوْ يَرُدُّونَكُمْ مِنْ بَعْدِ إِيمَانِكُمْ كُفَّارًا حَسَدًا مِنْ عِنْدِ أَنْفُسِهِمْ مِنْ بَعْدِ مَا تَبَيَّنَ لَهُمُ الْحَقُّ فَاعْفُوا وَاصْفَحُوا}
INSERT INTO [b9617] VALUES (117,'<span data-type="title" id=toc-115>١١٠ </span>- {وَمَا تُقَدِّمُوا لأَنْفُسِكُمْ مِنْ خَيْرٍ تَجِدُوهُ عِنْدَ اللَّهِ} ⦗٤٢⦘
INSERT INTO [b9617] VALUES (118,'<span data-type="title" id=toc-116>١١١ </span>- {وَقَالُوا لَنْ يَدْخُلَ الْجَنَّةَ إِلا مَنْ كَانَ هُودًا أَوْ نَصَارَى تِلْكَ أَمَانِيُّهُمْ قُلْ هَاتُوا بُرْهَانَكُمْ}
INSERT INTO [b9617] VALUES (119,'<span data-type="title" id=toc-117>١١٢ </span>- {بَلَى مَنْ أَسْلَمَ وَجْهَهُ لِلَّهِ وَهُوَ مُحْسِنٌ فَلَهُ أَجْرُهُ عِنْدَ رَبِّهِ وَلا خَوْفٌ عَلَيْهِمْ وَلا هُمْ يَحْزَنُونَ}
INSERT INTO [b9617] VALUES (120,'<span data-type="title" id=toc-118>١١٣ </span>- {وَقَالَتِ النَّصَارَى لَيْسَتِ الْيَهُودُ عَلَى شَيْءٍ وَهُمْ يَتْلُونَ الْكِتَابَ كَذَلِكَ قَالَ الَّذِينَ لا يَعْلَمُونَ مِثْلَ قَوْلِهِمْ فَاللَّهُ يَحْكُمُ بَيْنَهُمْ}
INSERT INTO [b9617] VALUES (121,'<span data-type="title" id=toc-119>١١٤ </span>- {وَمَنْ أَظْلَمُ مِمَّنْ مَنَعَ مَسَاجِدَ اللَّهِ أَنْ يُذْكَرَ فِيهَا اسْمُهُ وَسَعَى فِي خَرَابِهَا أُولَئِكَ مَا كَانَ لَهُمْ أَنْ يَدْخُلُوهَا إِلا خَائِفِينَ لَهُمْ فِي الدُّنْيَا خِزْيٌ وَلَهُمْ فِي الآخِرَةِ عَذَابٌ عَظِيمٌ}
INSERT INTO [b9617] VALUES (122,'<span data-type="title" id=toc-120>١١٥ </span>- {وَلِلَّهِ الْمَشْرِقُ وَالْمَغْرِبُ فَأَيْنَمَا تُوَلُّوا فَثَمَّ وَجْهُ اللَّهِ إِنَّ اللَّهَ وَاسِعٌ عَلِيمٌ}
INSERT INTO [b9617] VALUES (123,'<span data-type="title" id=toc-121>١١٦ </span>- {وَقَالُوا اتَّخَذَ اللَّهُ وَلَدًا سُبْحَانَهُ بَلْ لَهُ مَا فِي السَّمَاوَاتِ وَالأَرْضِ كُلٌّ لَهُ ⦗٤٤⦘ قَانِتُونَ}
INSERT INTO [b9617] VALUES (124,'<span data-type="title" id=toc-122>١١٧ </span>- {بَدِيعُ السَّمَاوَاتِ وَالأَرْضِ وَإِذَا قَضَى أَمْرًا فَإِنَّمَا يَقُولُ لَهُ كُنْ فَيَكُونُ}
INSERT INTO [b9617] VALUES (125,'<span data-type="title" id=toc-123>١١٨ </span>- {وَقَالَ الَّذِينَ لا يَعْلَمُونَ لَوْلا يُكَلِّمُنَا اللَّهُ أَوْ تَأْتِينَا آيَةٌ كَذَلِكَ قَالَ الَّذِينَ مِنْ قَبْلِهِمْ مِثْلَ قَوْلِهِمْ تَشَابَهَتْ قُلُوبُهُمْ قَدْ بَيَّنَّا الآيَاتِ لِقَوْمٍ يُوقِنُونَ}
INSERT INTO [b9617] VALUES (126,'<span data-type="title" id=toc-124>١١٩ </span>- {إِنَّا أَرْسَلْنَاكَ بِالْحَقِّ بَشِيرًا وَنَذِيرًا وَلا تُسْأَلُ عَنْ أَصْحَابِ الْجَحِيمِ} ⦗٤٥⦘
INSERT INTO [b9617] VALUES (127,'<span data-type="title" id=toc-125>١٢٠ </span>- {وَلَنْ تَرْضَى عَنْكَ الْيَهُودُ وَلا النَّصَارَى حَتَّى تَتَّبِعَ مِلَّتَهُمْ قُلْ إِنَّ هُدَى اللَّهِ هُوَ الْهُدَى وَلَئِنِ اتَّبَعْتَ أَهْوَاءَهُمْ بَعْدَ الَّذِي جَاءَكَ مِنَ الْعِلْمِ مَا لَكَ مِنَ اللَّهِ مِنْ وَلِيٍّ وَلا نَصِيرٍ}
INSERT INTO [b9617] VALUES (128,'<span data-type="title" id=toc-126>١٢١ </span>- {الَّذِينَ آتَيْنَاهُمُ الْكِتَابَ يَتْلُونَهُ حَقَّ تِلاوَتِهِ أُولَئِكَ يُؤْمِنُونَ بِهِ وَمَنْ يَكْفُرْ بِهِ فَأُولَئِكَ هُمُ الْخَاسِرُونَ}
INSERT INTO [b9617] VALUES (129,'<span data-type="title" id=toc-127>١٢٢ </span>- {اذْكُرُوا نِعْمَتِيَ الَّتِي أَنْعَمْتُ عَلَيْكُمْ وَأَنِّي فَضَّلْتُكُمْ عَلَى الْعَالَمِينَ}
INSERT INTO [b9617] VALUES (130,'<span data-type="title" id=toc-128>١٢٣ </span>- {وَاتَّقُوا يَوْمًا لا تَجْزِي نَفْسٌ عَنْ نَفْسٍ شَيْئًا وَلا يُقْبَلُ مِنْهَا عَدْلٌ وَلا تَنْفَعُهَا شَفَاعَةٌ ⦗٤٦⦘ وَلا هُمْ يُنْصَرُونَ}
INSERT INTO [b9617] VALUES (131,'<span data-type="title" id=toc-129>١٢٤ </span>- {وَإِذِ ابْتَلَى إِبْرَاهِيمَ رَبُّهُ بِكَلِمَاتٍ فَأَتَمَّهُنَّ قَالَ إِنِّي جَاعِلُكَ لِلنَّاسِ إِمَامًا قَالَ وَمِنْ ذُرِّيَّتِي}
INSERT INTO [b9617] VALUES (132,'<span data-type="title" id=toc-130>١٢٥ </span>- {وَإِذْ جَعَلْنَا الْبَيْتَ مَثَابَةً لِلنَّاسِ وَأَمْنًا وَاتَّخِذُوا مِنْ مَقَامِ إِبْرَاهِيمَ مُصَلًّى وَعَهِدْنَا إِلَى إِبْرَاهِيمَ وَإِسْمَاعِيلَ أَنْ طَهِّرَا بَيْتِيَ لِلطَّائِفِينَ}
INSERT INTO [b9617] VALUES (133,'<span data-type="title" id=toc-131>١٢٦ </span>- {وَارْزُقْ أَهْلَهُ مِنَ الثَّمَرَاتِ مَنْ آمَنَ مِنْهُمْ بِاللَّهِ وَالْيَوْمِ الآخِرِ قَالَ وَمَنْ كَفَرَ فَأُمَتِّعُهُ قَلِيلا ثُمَّ أَضْطَرُّهُ إِلَى عَذَابِ النَّارِ وَبِئْسَ الْمَصِيرُ}
INSERT INTO [b9617] VALUES (134,'<span data-type="title" id=toc-132>١٢٧ </span>- {وَإِذْ يَرْفَعُ إِبْرَاهِيمُ الْقَوَاعِدَ مِنَ الْبَيْتِ وَإِسْمَاعِيلُ رَبَّنَا تَقَبَّلْ مِنَّا إِنَّكَ أَنْتَ السَّمِيعُ الْعَلِيمُ}
INSERT INTO [b9617] VALUES (135,'<span data-type="title" id=toc-133>١٢٨ </span>- {رَبَّنَا وَاجْعَلْنَا مُسْلِمَيْنِ لَكَ وَمِنْ ذُرِّيَّتِنَا أُمَّةً مُسْلِمَةً لَكَ}
INSERT INTO [b9617] VALUES (136,'<span data-type="title" id=toc-134>١٢٩ </span>- {رَبَّنَا وَابْعَثْ فِيهِمْ رَسُولا مِنْهُمْ يَتْلُو عَلَيْهِمْ آيَاتِكَ}
INSERT INTO [b9617] VALUES (137,'<span data-type="title" id=toc-135>١٣٠ </span>- {وَمَنْ يَرْغَبُ عَنْ مِلَّةِ إِبْرَاهِيمَ إِلا مَنْ سَفِهَ نَفْسَهُ وَلَقَدِ اصْطَفَيْنَاهُ فِي الدُّنْيَا وَإِنَّهُ فِي الآخِرَةِ لَمِنَ الصَّالِحِينَ}
INSERT INTO [b9617] VALUES (138,'<span data-type="title" id=toc-136>١٣١ </span>- {إِذْ قَالَ لَهُ رَبُّهُ أَسْلِمْ قَالَ أَسْلَمْتُ لِرَبِّ الْعَالَمِينَ}
INSERT INTO [b9617] VALUES (139,'<span data-type="title" id=toc-137>١٣٢ </span>- {وَوَصَّى بِهَا إِبْرَاهِيمُ بَنِيهِ وَيَعْقُوبُ يَا بَنِيَّ إِنَّ اللَّهَ اصْطَفَى لَكُمُ الدِّينَ فَلا تَمُوتُنَّ إِلا وَأَنْتُمْ مُسْلِمُونَ} ⦗٤٩⦘
INSERT INTO [b9617] VALUES (140,'<span data-type="title" id=toc-138>١٣٣ </span>- {أَمْ كُنْتُمْ شُهَدَاءَ إِذْ حَضَرَ يَعْقُوبَ الْمَوْتُ إِذْ قَالَ لِبَنِيهِ مَا تَعْبُدُونَ مِنْ بَعْدِي قَالُوا نَعْبُدُ إِلَهَكَ وَإِلَهَ آبَائِكَ إِبْرَاهِيمَ وَإِسْمَاعِيلَ وَإِسْحَاقَ إِلَهًا وَاحِدًا وَنَحْنُ لَهُ مُسْلِمُونَ}
INSERT INTO [b9617] VALUES (141,'<span data-type="title" id=toc-139>١٣٤ </span>- {تِلْكَ أُمَّةٌ قَدْ خَلَتْ لَهَا مَا كَسَبَتْ وَلَكُمْ مَا كَسَبْتُمْ وَلا تُسْأَلُونَ عَمَّا كَانُوا يَعْمَلُونَ}
INSERT INTO [b9617] VALUES (142,'<span data-type="title" id=toc-140>١٣٥ </span>- {وَقَالُوا كُونُوا هُودًا أَوْ نَصَارَى تَهْتَدُوا قُلْ بَلْ مِلَّةَ إِبْرَاهِيمَ حَنِيفًا وَمَا كَانَ مِنَ الْمُشْرِكِينَ}
INSERT INTO [b9617] VALUES (143,'<span data-type="title" id=toc-141>١٣٦ </span>- {وَمَا أُوتِيَ النَّبِيُّونَ مِنْ رَبِّهِمْ لا نُفَرِّقُ بَيْنَ أَحَدٍ مِنْهُمْ وَنَحْنُ لَهُ مُسْلِمُونَ}
INSERT INTO [b9617] VALUES (144,'<span data-type="title" id=toc-142>١٣٧ </span>- {فَسَيَكْفِيكَهُمُ اللَّهُ وَهُوَ السَّمِيعُ الْعَلِيمُ}
INSERT INTO [b9617] VALUES (145,'<span data-type="title" id=toc-143>١٣٨ </span>- {صِبْغَةَ اللَّهِ وَمَنْ أَحْسَنُ مِنَ اللَّهِ صِبْغَةً وَنَحْنُ لَهُ عَابِدُونَ}
INSERT INTO [b9617] VALUES (146,'<span data-type="title" id=toc-144>١٣٩ </span>- {قُلْ أَتُحَاجُّونَنَا فِي اللَّهِ وَهُوَ رَبُّنَا وَرَبُّكُمْ وَلَنَا أَعْمَالُنَا وَلَكُمْ أَعْمَالُكُمْ وَنَحْنُ لَهُ مُخْلِصُونَ}
INSERT INTO [b9617] VALUES (147,'<span data-type="title" id=toc-145>١٤٠ </span>- {قُلْ أَأَنْتُمْ أَعْلَمُ أَمِ اللَّهُ وَمَنْ أَظْلَمُ مِمَّنْ كَتَمَ شَهَادَةً عِنْدَهُ مِنَ اللَّهِ وَمَا اللَّهُ بِغَافِلٍ عَمَّا تَعْمَلُونَ}
INSERT INTO [b9617] VALUES (148,'<span data-type="title" id=toc-146>١٤١ </span>- {تِلْكَ أُمَّةٌ قَدْ خَلَتْ لَهَا مَا كَسَبَتْ وَلَكُمْ مَا كَسَبْتُمْ وَلا تُسْأَلُونَ عَمَّا كَانُوا يَعْمَلُونَ}
INSERT INTO [b9617] VALUES (149,'<span data-type="title" id=toc-147>١٤٢ </span>- {سَيَقُولُ السُّفَهَاءُ مِنَ النَّاسِ مَا وَلاهُمْ عَنْ قِبْلَتِهِمُ الَّتِي كَانُوا عَلَيْهَا قُلْ لِلَّهِ الْمَشْرِقُ وَالْمَغْرِبُ يَهْدِي مَنْ يَشَاءُ}
INSERT INTO [b9617] VALUES (150,'<span data-type="title" id=toc-148>١٤٣ </span>- {وَكَذَلِكَ جَعَلْنَاكُمْ أُمَّةً وَسَطًا لِتَكُونُوا شُهَدَاءَ عَلَى النَّاسِ وَيَكُونَ الرَّسُولُ عَلَيْكُمْ شَهِيدًا وَمَا جَعَلْنَا الْقِبْلَةَ الَّتِي كُنْتَ عَلَيْهَا إِلا لِنَعْلَمَ مَنْ يَتَّبِعُ الرَّسُولَ مِمَّنْ يَنْقَلِبُ عَلَى عَقِبَيْهِ وَإِنْ كَانَتْ لَكَبِيرَةً إِلا عَلَى الَّذِينَ هَدَى اللَّهُ وَمَا كَانَ اللَّهُ لِيُضِيعَ إِيمَانَكُمْ إِنَّ اللَّهَ بِالنَّاسِ لَرَءُوفٌ رَحِيمٌ}
INSERT INTO [b9617] VALUES (151,'<span data-type="title" id=toc-149>١٤٤ </span>- {قَدْ نَرَى تَقَلُّبَ وَجْهِكَ فِي السَّمَاءِ فَلَنُوَلِّيَنَّكَ قِبْلَةً تَرْضَاهَا فَوَلِّ وَجْهَكَ شَطْرَ الْمَسْجِدِ الْحَرَامِ وَحَيْثُمَا كُنْتُمْ فَوَلُّوا وُجُوهَكُمْ شَطْرَهُ وَإِنَّ الَّذِينَ أُوتُوا الْكِتَابَ لَيَعْلَمُونَ أَنَّهُ الْحَقُّ مِنْ رَبِّهِمْ وَمَا اللَّهُ بِغَافِلٍ عَمَّا يَعْمَلُونَ}
INSERT INTO [b9617] VALUES (152,'<span data-type="title" id=toc-150>١٤٥ </span>- {وَلَئِنْ أَتَيْتَ الَّذِينَ أُوتُوا الْكِتَابَ بِكُلِّ آيَةٍ مَا تَبِعُوا قِبْلَتَكَ وَمَا أَنْتَ بِتَابِعٍ قِبْلَتَهُمْ وَمَا بَعْضُهُمْ بِتَابِعٍ قِبْلَةَ بَعْضٍ وَلَئِنِ اتَّبَعْتَ أَهْوَاءَهُمْ مِنْ بَعْدِ مَا جَاءَكَ مِنَ الْعِلْمِ إِنَّكَ إِذًا لَمِنَ الظَّالِمِينَ}
INSERT INTO [b9617] VALUES (153,'<span data-type="title" id=toc-151>١٤٦ </span>- {الَّذِينَ آتَيْنَاهُمُ الْكِتَابَ يَعْرِفُونَهُ كَمَا يَعْرِفُونَ أَبْنَاءَهُمْ وَإِنَّ فَرِيقًا مِنْهُمْ لَيَكْتُمُونَ الْحَقَّ وَهُمْ يَعْلَمُونَ}
INSERT INTO [b9617] VALUES (154,'<span data-type="title" id=toc-152>١٤٧ </span>- {الْحَقُّ مِنْ رَبِّكَ فَلا تَكُونَنَّ مِنَ الْمُمْتَرِينَ}
INSERT INTO [b9617] VALUES (155,'<span data-type="title" id=toc-153>١٤٨ </span>- {وَلِكُلٍّ وِجْهَةٌ هُوَ مُوَلِّيهَا فَاسْتَبِقُوا الْخَيْرَاتِ أَيْنَمَا تَكُونُوا يَأْتِ بِكُمُ اللَّهُ جَمِيعًا}
INSERT INTO [b9617] VALUES (156,'<span data-type="title" id=toc-154>١٤٩ </span>- {وَمِنْ حَيْثُ خَرَجْتَ فَوَلِّ وَجْهَكَ شَطْرَ الْمَسْجِدِ الْحَرَامِ وَإِنَّهُ لَلْحَقُّ مِنْ رَبِّكَ} ⦗٥٥⦘
INSERT INTO [b9617] VALUES (157,'<span data-type="title" id=toc-155>١٥٠ </span>- {وَمِنْ حَيْثُ خَرَجْتَ فَوَلِّ وَجْهَكَ شَطْرَ الْمَسْجِدِ الْحَرَامِ وَحَيْثُمَا كُنْتُمْ فَوَلُّوا وُجُوهَكُمْ شَطْرَهُ لِئَلا يَكُونَ لِلنَّاسِ عَلَيْكُمْ حُجَّةٌ إِلا الَّذِينَ ظَلَمُوا مِنْهُمْ فَلا تَخْشَوْهُمْ وَاخْشَوْنِي وَلأُتِمَّ نِعْمَتِي عَلَيْكُمْ وَلَعَلَّكُمْ تَهْتَدُونَ}
INSERT INTO [b9617] VALUES (158,'<span data-type="title" id=toc-156>١٥١ </span>- {كَمَا أَرْسَلْنَا فِيكُمْ رَسُولا مِنْكُمْ يَتْلُو عَلَيْكُمْ آيَاتِنَا} ⦗٥٦⦘
INSERT INTO [b9617] VALUES (159,'<span data-type="title" id=toc-157>١٥٢ </span>- {وَاشْكُرُوا لِي وَلا تَكْفُرُونِ}
INSERT INTO [b9617] VALUES (160,'<span data-type="title" id=toc-158>١٥٣ </span>- {يَا أَيُّهَا الَّذِينَ آمَنُوا اسْتَعِينُوا بِالصَّبْرِ وَالصَّلاةِ إِنَّ اللَّهَ مَعَ الصَّابِرِينَ}
INSERT INTO [b9617] VALUES (161,'<span data-type="title" id=toc-159>١٥٤ </span>- {وَلا تَقُولُوا لِمَنْ يُقْتَلُ فِي سَبِيلِ اللَّهِ أَمْوَاتٌ بَلْ أَحْيَاءٌ وَلَكِنْ لا تَشْعُرُونَ}
INSERT INTO [b9617] VALUES (162,'<span data-type="title" id=toc-160>١٥٦ </span>- {الَّذِينَ إِذَا أَصَابَتْهُمْ مُصِيبَةٌ قَالُوا إِنَّا لِلَّهِ وَإِنَّا إِلَيْهِ رَاجِعُونَ}
INSERT INTO [b9617] VALUES (163,'<span data-type="title" id=toc-161>١٥٧ </span>- {أُولَئِكَ عَلَيْهِمْ صَلَوَاتٌ مِنْ رَبِّهِمْ وَرَحْمَةٌ وَأُولَئِكَ هُمُ الْمُهْتَدُونَ} ⦗٥٧⦘
INSERT INTO [b9617] VALUES (164,'<span data-type="title" id=toc-162>١٥٨ </span>- {فَمَنْ حَجَّ الْبَيْتَ أَوِ اعْتَمَرَ فَلا جُنَاحَ عَلَيْهِ أَنْ يَطَّوَّفَ بِهِمَا وَمَنْ تَطَوَّعَ خَيْرًا فَإِنَّ اللَّهَ شَاكِرٌ عَلِيمٌ}
INSERT INTO [b9617] VALUES (165,'<span data-type="title" id=toc-163>١٥٩ </span>- {إِنَّ الَّذِينَ يَكْتُمُونَ مَا أَنْزَلْنَا مِنَ الْبَيِّنَاتِ وَالْهُدَى مِنْ بَعْدِ مَا بَيَّنَّاهُ لِلنَّاسِ فِي الْكِتَابِ أُولَئِكَ يَلْعَنُهُمُ اللَّهُ}
INSERT INTO [b9617] VALUES (166,'<span data-type="title" id=toc-164>١٦٠ </span>- {فَأُولَئِكَ أَتُوبُ عَلَيْهِمْ وَأَنَا التَّوَّابُ الرَّحِيمُ}
INSERT INTO [b9617] VALUES (167,'<span data-type="title" id=toc-165>١٦١ </span>- {إِنَّ الَّذِينَ كَفَرُوا وَمَاتُوا وَهُمْ كُفَّارٌ أُولَئِكَ عَلَيْهِمْ لَعْنَةُ اللَّهِ وَالْمَلائِكَةِ وَالنَّاسِ أَجْمَعِينَ} ⦗٥٨⦘
INSERT INTO [b9617] VALUES (168,'<span data-type="title" id=toc-166>١٦٢ </span>- {خَالِدِينَ فِيهَا لا يُخَفَّفُ عَنْهُمُ الْعَذَابُ وَلا هُمْ يُنْظَرُونَ}
INSERT INTO [b9617] VALUES (169,'<span data-type="title" id=toc-167>١٦٣ </span>- {وَإِلَهُكُمْ إِلَهٌ وَاحِدٌ لا إِلَهَ إِلا هُوَ الرَّحْمَنُ الرَّحِيمُ}
INSERT INTO [b9617] VALUES (170,'<span data-type="title" id=toc-168>١٦٤ </span>- {لآيَاتٍ لِقَوْمٍ يَعْقِلُونَ}
INSERT INTO [b9617] VALUES (171,'<span data-type="title" id=toc-169>١٦٥ </span>- {وَمِنَ النَّاسِ مَنْ يَتَّخِذُ مِنْ دُونِ اللَّهِ أَنْدَادًا يُحِبُّونَهُمْ كَحُبِّ اللَّهِ وَالَّذِينَ آمَنُوا أَشَدُّ حُبًّا لِلَّهِ وَلَوْ يَرَى الَّذِينَ ظَلَمُوا إِذْ يَرَوْنَ الْعَذَابَ أَنَّ الْقُوَّةَ لِلَّهِ جَمِيعًا وَأَنَّ اللَّهَ شَدِيدُ الْعَذَابِ}
INSERT INTO [b9617] VALUES (172,'<span data-type="title" id=toc-170>١٦٦ </span>- {إِذْ تَبَرَّأَ الَّذِينَ اتُّبِعُوا مِنَ الَّذِينَ اتَّبَعُوا وَرَأَوُا الْعَذَابَ وَتَقَطَّعَتْ بِهِمُ الأَسْبَابُ}
INSERT INTO [b9617] VALUES (173,'<span data-type="title" id=toc-171>١٦٧ </span>- {وَقَالَ الَّذِينَ اتَّبَعُوا لَوْ أَنَّ لَنَا كَرَّةً فَنَتَبَرَّأَ مِنْهُمْ كَمَا تَبَرَّءُوا مِنَّا كَذَلِكَ يُرِيهِمُ اللَّهُ أَعْمَالَهُمْ حَسَرَاتٍ عَلَيْهِمْ وَمَا هُمْ بِخَارِجِينَ مِنَ النَّارِ}
INSERT INTO [b9617] VALUES (174,'<span data-type="title" id=toc-172>١٦٨ </span>- {يَا أَيُّهَا النَّاسُ كُلُوا مِمَّا فِي الأَرْضِ حَلالا طَيِّبًا وَلا تَتَّبِعُوا خُطُوَاتِ الشَّيْطَانِ إِنَّهُ لَكُمْ عَدُوٌّ مُبِينٌ}
INSERT INTO [b9617] VALUES (175,'<span data-type="title" id=toc-173>١٦٩ </span>- {إِنَّمَا يَأْمُرُكُمْ بِالسُّوءِ وَالْفَحْشَاءِ وَأَنْ تَقُولُوا عَلَى اللَّهِ مَا لا تَعْلَمُونَ}
INSERT INTO [b9617] VALUES (176,'<span data-type="title" id=toc-174>١٧٠ </span>- {قَالُوا بَلْ نَتَّبِعُ مَا أَلْفَيْنَا عَلَيْهِ آبَاءَنَا ⦗٦١⦘ أَوَلَوْ كَانَ آبَاؤُهُمْ لا يَعْقِلُونَ شَيْئًا وَلا يَهْتَدُونَ}
INSERT INTO [b9617] VALUES (177,'<span data-type="title" id=toc-175>١٧١ </span>- {وَمَثَلُ الَّذِينَ كَفَرُوا كَمَثَلِ الَّذِي يَنْعِقُ بِمَا لا يَسْمَعُ إِلا دُعَاءً وَنِدَاءً صُمٌّ بُكْمٌ عُمْيٌ فَهُمْ لا يَعْقِلُونَ}
INSERT INTO [b9617] VALUES (178,'<span data-type="title" id=toc-176>١٧٢ </span>- {يَا أَيُّهَا الَّذِينَ آمَنُوا كُلُوا مِنْ طَيِّبَاتِ مَا رَزَقْنَاكُمْ وَاشْكُرُوا لِلَّهِ إِنْ كُنْتُمْ إِيَّاهُ تَعْبُدُونَ}
INSERT INTO [b9617] VALUES (179,'<span data-type="title" id=toc-177>١٧٣ </span>- {فَمَنِ اضْطُرَّ غَيْرَ بَاغٍ وَلا عَادٍ فَلا إِثْمَ عَلَيْهِ إِنَّ اللَّهَ غَفُورٌ رَحِيمٌ} ⦗٦٢⦘
INSERT INTO [b9617] VALUES (180,'<span data-type="title" id=toc-178>١٧٤ </span>- {إِنَّ الَّذِينَ يَكْتُمُونَ مَا أَنْزَلَ اللَّهُ مِنَ الْكِتَابِ وَيَشْتَرُونَ بِهِ ثَمَنًا قَلِيلا أُولَئِكَ مَا يَأْكُلُونَ فِي بُطُونِهِمْ إِلا النَّارَ وَلا يُكَلِّمُهُمُ اللَّهُ يَوْمَ الْقِيَامَةِ وَلا يُزَكِّيهِمْ وَلَهُمْ عَذَابٌ أَلِيمٌ}
INSERT INTO [b9617] VALUES (181,'<span data-type="title" id=toc-179>١٧٥ </span>- {فَمَا أَصْبَرَهُمْ عَلَى النَّارِ}
INSERT INTO [b9617] VALUES (182,'<span data-type="title" id=toc-180>١٧٦ </span>- {ذَلِكَ بِأَنَّ اللَّهَ نَزَّلَ الْكِتَابَ بِالْحَقِّ وَإِنَّ الَّذِينَ اخْتَلَفُوا فِي الْكِتَابِ لَفِي شِقَاقٍ بَعِيدٍ}
INSERT INTO [b9617] VALUES (183,'<span data-type="title" id=toc-181>١٧٧ </span>- {لَيْسَ الْبِرَّ أَنْ تُوَلُّوا وُجُوهَكُمْ قِبَلَ الْمَشْرِقِ وَالْمَغْرِبِ وَلَكِنَّ الْبِرَّ مَنْ ⦗٦٣⦘ آمَنَ بِاللَّهِ وَالْيَوْمِ الآخِرِ وَالْمَلائِكَةِ وَالْكِتَابِ وَالنَّبِيِّينَ وَآتَى الْمَالَ عَلَى حُبِّهِ ذَوِي الْقُرْبَى وَالْيَتَامَى وَالْمَسَاكِينَ وَابْنَ السَّبِيلِ وَالسَّائِلِينَ وَفِي الرِّقَابِ وَأَقَامَ الصَّلاةَ وَآتَى الزَّكَاةَ وَالْمُوفُونَ بِعَهْدِهِمْ إِذَا عَاهَدُوا وَالصَّابِرِينَ فِي الْبَأْسَاءِ وَالضَّرَّاءِ وَحِينَ الْبَأْسِ أُولَئِكَ الَّذِينَ صَدَقُوا وَأُولَئِكَ هُمُ الْمُتَّقُونَ}
INSERT INTO [b9617] VALUES (184,'<span data-type="title" id=toc-182>١٧٨ </span>- {يَا أَيُّهَا الَّذِينَ آمَنُوا كُتِبَ عَلَيْكُمُ الْقِصَاصُ فِي الْقَتْلَى الْحُرُّ بِالْحُرِّ وَالْعَبْدُ بِالْعَبْدِ وَالأُنْثَى بِالأُنْثَى فَمَنْ عُفِيَ لَهُ مِنْ أَخِيهِ شَيْءٌ فَاتِّبَاعٌ بِالْمَعْرُوفِ وَأَدَاءٌ إِلَيْهِ بِإِحْسَانٍ ذَلِكَ تَخْفِيفٌ مِنْ رَبِّكُمْ وَرَحْمَةٌ}
INSERT INTO [b9617] VALUES (185,'<span data-type="title" id=toc-183>١٧٩ </span>- {وَلَكُمْ فِي الْقِصَاصِ حَيَاةٌ يَا أُولِي الأَلْبَابِ لَعَلَّكُمْ تَتَّقُونَ}
INSERT INTO [b9617] VALUES (186,'<span data-type="title" id=toc-184>١٨٠ </span>- {كُتِبَ عَلَيْكُمْ إِذَا حَضَرَ أَحَدَكُمُ الْمَوْتُ إِنْ تَرَكَ خَيْرًا الْوَصِيَّةُ لِلْوَالِدَيْنِ وَالأَقْرَبِينَ بِالْمَعْرُوفِ حَقًّا عَلَى الْمُتَّقِينَ}
INSERT INTO [b9617] VALUES (187,'<span data-type="title" id=toc-185>١٨١ </span>- {فَمَنْ بَدَّلَهُ بَعْدَمَا سَمِعَهُ فَإِنَّمَا إِثْمُهُ عَلَى الَّذِينَ يُبَدِّلُونَهُ إِنَّ اللَّهَ سَمِيعٌ عَلِيمٌ}
INSERT INTO [b9617] VALUES (188,'<span data-type="title" id=toc-186>١٨٣ </span>- {يَا أَيُّهَا الَّذِينَ آمَنُوا كُتِبَ عَلَيْكُمُ الصِّيَامُ كَمَا كُتِبَ عَلَى الَّذِينَ مِنْ قَبْلِكُمْ لَعَلَّكُمْ تَتَّقُونَ}
INSERT INTO [b9617] VALUES (189,'<span data-type="title" id=toc-187>١٨٤ </span>- {أَيَّامًا مَعْدُودَاتٍ فَمَنْ كَانَ مِنْكُمْ مَرِيضًا أَوْ عَلَى سَفَرٍ فَعِدَّةٌ مِنْ أَيَّامٍ أُخَرَ وَعَلَى الَّذِينَ يُطِيقُونَهُ فِدْيَةٌ طَعَامُ مِسْكِينٍ فَمَنْ تَطَوَّعَ خَيْرًا فَهُوَ خَيْرٌ لَهُ وَأَنْ تَصُومُوا خَيْرٌ لَكُمْ إِنْ كُنْتُمْ تَعْلَمُونَ}
INSERT INTO [b9617] VALUES (190,'<span data-type="title" id=toc-188>١٨٥ </span>- {شَهْرُ رَمَضَانَ الَّذِي أُنْزِلَ فِيهِ الْقُرْآنُ هُدًى لِلنَّاسِ وَبَيِّنَاتٍ مِنَ الْهُدَى وَالْفُرْقَانِ فَمَنْ شَهِدَ مِنْكُمُ الشَّهْرَ فَلْيَصُمْهُ وَمَنْ كَانَ مَرِيضًا أَوْ عَلَى سَفَرٍ فَعِدَّةٌ مِنْ أَيَّامٍ أُخَرَ يُرِيدُ اللَّهُ بِكُمُ الْيُسْرَ وَلا يُرِيدُ بِكُمُ الْعُسْرَ وَلِتُكْمِلُوا الْعِدَّةَ وَلِتُكَبِّرُوا اللَّهَ عَلَى مَا هَدَاكُمْ وَلَعَلَّكُمْ تَشْكُرُونَ}
INSERT INTO [b9617] VALUES (191,'<span data-type="title" id=toc-189>١٨٦ </span>- {وَإِذَا سَأَلَكَ عِبَادِي عَنِّي فَإِنِّي قَرِيبٌ أُجِيبُ دَعْوَةَ الدَّاعِ إِذَا دَعَانِ فَلْيَسْتَجِيبُوا لِي وَلْيُؤْمِنُوا بِي لَعَلَّهُمْ يَرْشُدُونَ}
INSERT INTO [b9617] VALUES (192,'<span data-type="title" id=toc-190>١٨٧ </span>- {أُحِلَّ لَكُمْ لَيْلَةَ الصِّيَامِ الرَّفَثُ إِلَى نِسَائِكُمْ هُنَّ لِبَاسٌ لَكُمْ ⦗٦٧⦘ وَأَنْتُمْ لِبَاسٌ لَهُنَّ عَلِمَ اللَّهُ أَنَّكُمْ كُنْتُمْ تَخْتَانُونَ أَنْفُسَكُمْ فَتَابَ عَلَيْكُمْ وَعَفَا عَنْكُمْ فَالآنَ بَاشِرُوهُنَّ وَابْتَغُوا مَا كَتَبَ اللَّهُ لَكُمْ وَكُلُوا وَاشْرَبُوا حَتَّى يَتَبَيَّنَ لَكُمُ الْخَيْطُ الأَبْيَضُ مِنَ الْخَيْطِ الأَسْوَدِ مِنَ الْفَجْرِ ثُمَّ أَتِمُّوا الصِّيَامَ إِلَى اللَّيْلِ وَلا تُبَاشِرُوهُنَّ وَأَنْتُمْ عَاكِفُونَ فِي الْمَسَاجِدِ تِلْكَ حُدُودُ اللَّهِ فَلا تَقْرَبُوهَا كَذَلِكَ يُبَيِّنُ اللَّهُ آيَاتِهِ لِلنَّاسِ لَعَلَّهُمْ يَتَّقُونَ}
INSERT INTO [b9617] VALUES (193,'<span data-type="title" id=toc-191>١٨٨ </span>- {وَلا تَأْكُلُوا أَمْوَالَكُمْ بَيْنَكُمْ بِالْبَاطِلِ وَتُدْلُوا بِهَا إِلَى الْحُكَّامِ لِتَأْكُلُوا فَرِيقًا مِنْ أَمْوَالِ النَّاسِ بِالإِثْمِ وَأَنْتُمْ تَعْلَمُونَ}
INSERT INTO [b9617] VALUES (194,'<span data-type="title" id=toc-192>١٨٩ </span>- {وَلَيْسَ الْبِرُّ بِأَنْ تَأْتُوا الْبُيُوتَ مِنْ ظُهُورِهَا وَلَكِنَّ الْبِرَّ مَنِ اتَّقَى وَأْتُوا الْبُيُوتَ مِنْ أَبْوَابِهَا وَاتَّقُوا اللَّهَ لَعَلَّكُمْ تُفْلِحُونَ}
INSERT INTO [b9617] VALUES (195,'<span data-type="title" id=toc-193>١٩٠ </span>- {وَقَاتِلُوا فِي سَبِيلِ اللَّهِ الَّذِينَ يُقَاتِلُونَكُمْ وَلا تَعْتَدُوا}
INSERT INTO [b9617] VALUES (196,'<span data-type="title" id=toc-194>١٩١ </span>- {وَاقْتُلُوهُمْ حَيْثُ ثَقِفْتُمُوهُمْ وَأَخْرِجُوهُمْ مِنْ حَيْثُ أَخْرَجُوكُمْ وَالْفِتْنَةُ أَشَدُّ مِنَ الْقَتْلِ وَلا تُقَاتِلُوهُمْ عِنْدَ الْمَسْجِدِ الْحَرَامِ حَتَّى يُقَاتِلُوكُمْ فِيهِ فَإِنْ قَاتَلُوكُمْ فَاقْتُلُوهُمْ كَذَلِكَ جَزَاءُ الْكَافِرِينَ} ⦗٦٩⦘
INSERT INTO [b9617] VALUES (197,'<span data-type="title" id=toc-195>١٩٢ </span>- {فَإِنِ انْتَهَوْا فَإِنَّ اللَّهَ غَفُورٌ رَحِيمٌ}
INSERT INTO [b9617] VALUES (198,'<span data-type="title" id=toc-196>١٩٣ </span>- {وَقَاتِلُوهُمْ حَتَّى لا تَكُونَ فِتْنَةٌ وَيَكُونَ الدِّينُ لِلَّهِ فَإِنِ انْتَهَوْا فَلا عُدْوَانَ إِلا عَلَى الظَّالِمِينَ}
INSERT INTO [b9617] VALUES (199,'<span data-type="title" id=toc-197>١٩٦ </span>- {وَأَتِمُّوا الْحَجَّ وَالْعُمْرَةَ لِلَّهِ فَإِنْ أُحْصِرْتُمْ فَمَا اسْتَيْسَرَ مِنَ الْهَدْيِ وَلا تَحْلِقُوا رُءُوسَكُمْ حَتَّى يَبْلُغَ الْهَدْيُ مَحِلَّهُ فَمَنْ كَانَ مِنْكُمْ مَرِيضًا أَوْ بِهِ أَذًى مِنْ رَأْسِهِ فَفِدْيَةٌ مِنْ صِيَامٍ أَوْ صَدَقَةٍ أَوْ نُسُكٍ فَإِذَا أَمِنْتُمْ فَمَنْ تَمَتَّعَ بِالْعُمْرَةِ إِلَى الْحَجِّ فَمَا اسْتَيْسَرَ مِنَ الْهَدْيِ فَمَنْ لَمْ يَجِدْ فَصِيَامُ ثَلاثَةِ أَيَّامٍ فِي الْحَجِّ وَسَبْعَةٍ إِذَا رَجَعْتُمْ تِلْكَ عَشَرَةٌ كَامِلَةٌ ذَلِكَ لِمَنْ لَمْ يَكُنْ أَهْلُهُ حَاضِرِي الْمَسْجِدِ الْحَرَامِ وَاتَّقُوا اللَّهَ وَاعْلَمُوا أَنَّ اللَّهَ شَدِيدُ الْعِقَابِ} ⦗٧٠⦘
INSERT INTO [b9617] VALUES (200,'<span data-type="title" id=toc-198>١٩٧ </span>- {فَمَنْ فَرَضَ فِيهِنَّ الْحَجَّ فَلا رَفَثَ وَلا فُسُوقَ وَلا جِدَالَ فِي الْحَجِّ وَمَا تَفْعَلُوا مِنْ خَيْرٍ يَعْلَمْهُ اللَّهُ وَتَزَوَّدُوا فَإِنَّ خَيْرَ الزَّادِ التَّقْوَى وَاتَّقُونِ يَا أُولِي الأَلْبَابِ}
INSERT INTO [b9617] VALUES (201,'<span data-type="title" id=toc-199>١٩٨ </span>- {لَيْسَ عَلَيْكُمْ جُنَاحٌ أَنْ تَبْتَغُوا فَضْلا مِنْ رَبِّكُمْ فَإِذَا أَفَضْتُمْ مِنْ عَرَفَاتٍ فَاذْكُرُوا اللَّهَ عِنْدَ الْمَشْعَرِ الْحَرَامِ وَاذْكُرُوهُ كَمَا هَدَاكُمْ وَإِنْ كُنْتُمْ مِنْ قَبْلِهِ لَمِنَ الضَّالِّينَ}
INSERT INTO [b9617] VALUES (202,'<span data-type="title" id=toc-200>١٩٩ </span>- {ثُمَّ أَفِيضُوا مِنْ حَيْثُ أَفَاضَ النَّاسُ وَاسْتَغْفِرُوا اللَّهَ إِنَّ اللَّهَ غَفُورٌ رَحِيمٌ}
INSERT INTO [b9617] VALUES (203,'<span data-type="title" id=toc-201>٢٠٠ </span>- {فَإِذَا قَضَيْتُمْ مَنَاسِكَكُمْ فَاذْكُرُوا اللَّهَ كَذِكْرِكُمْ آبَاءَكُمْ أَوْ أَشَدَّ ذِكْرًا فَمِنَ النَّاسِ مَنْ يَقُولُ رَبَّنَا آتِنَا فِي الدُّنْيَا وَمَا لَهُ فِي الآخِرَةِ مِنْ خَلاقٍ}
INSERT INTO [b9617] VALUES (204,'<span data-type="title" id=toc-202>٢٠١ </span>- {آتِنَا فِي الدُّنْيَا حَسَنَةً وَفِي الآخِرَةِ حَسَنَةً وَقِنَا عَذَابَ النَّارِ}
INSERT INTO [b9617] VALUES (205,'<span data-type="title" id=toc-203>٢٠٢ </span>- {أُولَئِكَ لَهُمْ نَصِيبٌ مِمَّا كَسَبُوا وَاللَّهُ سَرِيعُ الْحِسَابِ}
INSERT INTO [b9617] VALUES (206,'<span data-type="title" id=toc-204>٢٠٣ </span>- {وَاذْكُرُوا اللَّهَ فِي أَيَّامٍ مَعْدُودَاتٍ فَمَنْ تَعَجَّلَ فِي يَوْمَيْنِ فَلا إِثْمَ عَلَيْهِ وَمَنْ تَأَخَّرَ فَلا إِثْمَ عَلَيْهِ لِمَنِ اتَّقَى وَاتَّقُوا اللَّهَ وَاعْلَمُوا أَنَّكُمْ إِلَيْهِ تُحْشَرُونَ}
INSERT INTO [b9617] VALUES (207,'<span data-type="title" id=toc-205>٢٠٤ </span>- {وَمِنَ النَّاسِ مَنْ يُعْجِبُكَ قَوْلُهُ فِي الْحَيَاةِ الدُّنْيَا وَيُشْهِدُ اللَّهَ عَلَى مَا فِي قَلْبِهِ وَهُوَ أَلَدُّ الْخِصَامِ}
INSERT INTO [b9617] VALUES (208,'<span data-type="title" id=toc-206>٢٠٥ </span>- {وَإِذَا تَوَلَّى سَعَى فِي الأَرْضِ لِيُفْسِدَ فِيهَا وَيُهْلِكَ الْحَرْثَ وَالنَّسْلَ وَاللَّهُ لا يُحِبُّ الْفَسَادَ}
INSERT INTO [b9617] VALUES (209,'<span data-type="title" id=toc-207>٢٠٦ </span>- {وَإِذَا قِيلَ لَهُ اتَّقِ اللَّهَ أَخَذَتْهُ الْعِزَّةُ بِالإِثْمِ فَحَسْبُهُ جَهَنَّمُ وَلَبِئْسَ ⦗٧٤⦘ الْمِهَادُ}
INSERT INTO [b9617] VALUES (210,'<span data-type="title" id=toc-208>٢٠٧ </span>- {يَشْرِي نَفْسَهُ ابْتِغَاءَ مَرْضَاتِ اللَّهِ}
INSERT INTO [b9617] VALUES (211,'<span data-type="title" id=toc-209>٢٠٨ </span>- {يَا أَيُّهَا الَّذِينَ آمَنُوا ادْخُلُوا فِي السِّلْمِ كَافَّةً وَلا تَتَّبِعُوا خُطُوَاتِ الشَّيْطَانِ إِنَّهُ لَكُمْ عَدُوٌّ مُبِينٌ}
INSERT INTO [b9617] VALUES (212,'<span data-type="title" id=toc-210>٢٠٩ </span>- {فَإِنْ زَلَلْتُمْ مِنْ بَعْدِ مَا جَاءَتْكُمُ الْبَيِّنَاتُ}
INSERT INTO [b9617] VALUES (213,'<span data-type="title" id=toc-211>٢١٠ </span>- {هَلْ يَنْظُرُونَ إِلا أَنْ يَأْتِيَهُمُ اللَّهُ فِي ظُلَلٍ مِنَ الْغَمَامِ وَالْمَلائِكَةُ وَقُضِيَ الأَمْرُ وَإِلَى اللَّهِ تُرْجَعُ الأُمُورُ}
INSERT INTO [b9617] VALUES (214,'<span data-type="title" id=toc-212>٢١١ </span>- {سَلْ بَنِي إِسْرَائِيلَ كَمْ آتَيْنَاهُمْ مِنْ آيَةٍ بَيِّنَةٍ وَمَنْ يُبَدِّلْ نِعْمَةَ اللَّهِ مِنْ بَعْدِ مَا جَاءَتْهُ فَإِنَّ اللَّهَ شَدِيدُ الْعِقَابِ}
INSERT INTO [b9617] VALUES (215,'<span data-type="title" id=toc-213>٢١٢ </span>- {زُيِّنَ لِلَّذِينَ كَفَرُوا الْحَيَاةُ الدُّنْيَا وَيَسْخَرُونَ مِنَ الَّذِينَ آمَنُوا وَالَّذِينَ اتَّقَوْا فَوْقَهُمْ يَوْمَ الْقِيَامَةِ}
INSERT INTO [b9617] VALUES (216,'<span data-type="title" id=toc-214>٢١٣ </span>- {وَأَنْزَلَ مَعَهُمُ الْكِتَابَ بِالْحَقِّ لِيَحْكُمَ بَيْنَ النَّاسِ فِيمَا اخْتَلَفُوا فِيهِ وَمَا اخْتَلَفَ فِيهِ إِلا الَّذِينَ أُوتُوهُ مِنْ بَعْدِ مَا جَاءَتْهُمُ الْبَيِّنَاتُ بَغْيًا بَيْنَهُمْ فَهَدَى اللَّهُ الَّذِينَ آمَنُوا لِمَا اخْتَلَفُوا فِيهِ مِنَ الْحَقِّ بِإِذْنِهِ}
INSERT INTO [b9617] VALUES (217,'<span data-type="title" id=toc-215>٢١٤ </span>- {أَمْ حَسِبْتُمْ أَنْ تَدْخُلُوا الْجَنَّةَ وَلَمَّا يَأْتِكُمْ مَثَلُ الَّذِينَ خَلَوْا مِنْ قَبْلِكُمْ مَسَّتْهُمُ الْبَأْسَاءُ وَالضَّرَّاءُ وَزُلْزِلُوا حَتَّى يَقُولَ الرَّسُولُ وَالَّذِينَ آمَنُوا مَعَهُ مَتَى نَصْرُ اللَّهِ}
INSERT INTO [b9617] VALUES (218,'<span data-type="title" id=toc-216>٢١٥ </span>- {يَسْأَلُونَكَ مَاذَا يُنْفِقُونَ قُلْ مَا أَنْفَقْتُمْ مِنْ خَيْرٍ فَلِلْوَالِدَيْنِ وَالأَقْرَبِينَ وَالْيَتَامَى وَالْمَسَاكِينِ وَابْنِ السَّبِيلِ وَمَا تَفْعَلُوا مِنْ خَيْرٍ فَإِنَّ اللَّهَ بِهِ عَلِيمٌ}
INSERT INTO [b9617] VALUES (219,'<span data-type="title" id=toc-217>٢١٦ </span>- {كُتِبَ عَلَيْكُمُ الْقِتَالُ وَهُوَ كُرْهٌ لَكُمْ وَعَسَى أَنْ تَكْرَهُوا شَيْئًا وَهُوَ خَيْرٌ ⦗٧٧⦘ لَكُمْ وَعَسَى أَنْ تُحِبُّوا شَيْئًا وَهُوَ شَرٌّ لَكُمْ وَاللَّهُ يَعْلَمُ وَأَنْتُمْ لا تَعْلَمُونَ}
INSERT INTO [b9617] VALUES (220,'<span data-type="title" id=toc-218>٢١٧ </span>- {يَسْأَلُونَكَ عَنِ الشَّهْرِ الْحَرَامِ قِتَالٍ فِيهِ قُلْ قِتَالٌ فِيهِ كَبِيرٌ وَصَدٌّ عَنْ سَبِيلِ اللَّهِ وَكُفْرٌ بِهِ وَالْمَسْجِدِ الْحَرَامِ وَإِخْرَاجُ أَهْلِهِ مِنْهُ أَكْبَرُ عِنْدَ اللَّهِ وَالْفِتْنَةُ أَكْبَرُ مِنَ الْقَتْلِ وَلا يَزَالُونَ يُقَاتِلُونَكُمْ حَتَّى يَرُدُّوكُمْ عَنْ دِينِكُمْ إِنِ اسْتَطَاعُوا وَمَنْ يَرْتَدِدْ مِنْكُمْ عَنْ دِينِهِ فَيَمُتْ وَهُوَ كَافِرٌ فَأُولَئِكَ حَبِطَتْ أَعْمَالُهُمْ فِي الدُّنْيَا وَالآخِرَةِ وَأُولَئِكَ أَصْحَابُ النَّارِ هُمْ فِيهَا خَالِدُونَ}
INSERT INTO [b9617] VALUES (221,'<span data-type="title" id=toc-219>٢١٨ </span>- {أُولَئِكَ يَرْجُونَ رَحْمَتَ اللَّهِ وَاللَّهُ غَفُورٌ رَحِيمٌ}
INSERT INTO [b9617] VALUES (222,'<span data-type="title" id=toc-220>٢١٩ </span>- {وَيَسْأَلُونَكَ مَاذَا يُنْفِقُونَ قُلِ الْعَفْوَ كَذَلِكَ يُبَيِّنُ اللَّهُ لَكُمُ الآيَاتِ لَعَلَّكُمْ تَتَفَكَّرُونَ}
INSERT INTO [b9617] VALUES (223,'<span data-type="title" id=toc-221>٢٢٠ </span>- {فِي الدُّنْيَا وَالآخِرَةِ وَيَسْأَلُونَكَ عَنِ الْيَتَامَى قُلْ إِصْلاحٌ لَهُمْ خَيْرٌ وَإِنْ تُخَالِطُوهُمْ فَإِخْوَانُكُمْ وَاللَّهُ يَعْلَمُ الْمُفْسِدَ مِنَ الْمُصْلِحِ}
INSERT INTO [b9617] VALUES (224,'<span data-type="title" id=toc-222>٢٢١ </span>- {وَلا تَنْكِحُوا الْمُشْرِكَاتِ حَتَّى يُؤْمِنَّ وَلأَمَةٌ مُؤْمِنَةٌ خَيْرٌ مِنْ مُشْرِكَةٍ وَلَوْ أَعْجَبَتْكُمْ وَلا تُنْكِحُوا الْمُشْرِكِينَ حَتَّى يُؤْمِنُوا وَلَعَبْدٌ مُؤْمِنٌ خَيْرٌ مِنْ مُشْرِكٍ وَلَوْ أَعْجَبَكُمْ أُولَئِكَ يَدْعُونَ إِلَى النَّارِ وَاللَّهُ يَدْعُو إِلَى الْجَنَّةِ وَالْمَغْفِرَةِ بِإِذْنِهِ وَيُبَيِّنُ آيَاتِهِ لِلنَّاسِ لَعَلَّهُمْ يَتَذَكَّرُونَ}
INSERT INTO [b9617] VALUES (225,'<span data-type="title" id=toc-223>٢٢٢ </span>- {وَيَسْأَلُونَكَ عَنِ الْمَحِيضِ قُلْ هُوَ أَذًى فَاعْتَزِلُوا النِّسَاءَ فِي الْمَحِيضِ وَلا تَقْرَبُوهُنَّ حَتَّى يَطْهُرْنَ فَإِذَا تَطَهَّرْنَ فَأْتُوهُنَّ مِنْ حَيْثُ أَمَرَكُمُ اللَّهُ إِنَّ اللَّهَ يُحِبُّ التَّوَّابِينَ وَيُحِبُّ الْمُتَطَهِّرِينَ}
INSERT INTO [b9617] VALUES (226,'<span data-type="title" id=toc-224>٢٢٣ </span>- {نِسَاؤُكُمْ حَرْثٌ لَكُمْ فَأْتُوا حَرْثَكُمْ أَنَّى شِئْتُمْ وَقَدِّمُوا لأَنْفُسِكُمْ وَاتَّقُوا ⦗٨٠⦘ اللَّهَ وَاعْلَمُوا أَنَّكُمْ مُلاقُوهُ وَبَشِّرِ الْمُؤْمِنِينَ}
INSERT INTO [b9617] VALUES (227,'<span data-type="title" id=toc-225>٢٢٤ </span>- {وَلا تَجْعَلُوا اللَّهَ عُرْضَةً لأَيْمَانِكُمْ أَنْ تَبَرُّوا وَتَتَّقُوا وَتُصْلِحُوا بَيْنَ النَّاسِ وَاللَّهُ سَمِيعٌ عَلِيمٌ}
INSERT INTO [b9617] VALUES (228,'<span data-type="title" id=toc-226>٢٢٥ </span>- {وَلَكِنْ يُؤَاخِذُكُمْ بِمَا كَسَبَتْ قُلُوبُكُمْ وَاللَّهُ غَفُورٌ حَلِيمٌ}
INSERT INTO [b9617] VALUES (229,'<span data-type="title" id=toc-227>٢٢٦ </span>- {لِلَّذِينَ يُؤْلُونَ مِنْ نِسَائِهِمْ تَرَبُّصُ أَرْبَعَةِ أَشْهُرٍ فَإِنْ فَاءُوا فَإِنَّ اللَّهَ غَفُورٌ رَحِيمٌ} ⦗٨١⦘
INSERT INTO [b9617] VALUES (230,'<span data-type="title" id=toc-228>٢٢٨ </span>- {وَالْمُطَلَّقَاتُ يَتَرَبَّصْنَ بِأَنْفُسِهِنَّ ثَلاثَةَ قُرُوءٍ وَلا يَحِلُّ لَهُنَّ أَنْ يَكْتُمْنَ مَا خَلَقَ اللَّهُ فِي أَرْحَامِهِنَّ إِنْ كُنَّ يُؤْمِنَّ بِاللَّهِ وَالْيَوْمِ الآخِرِ وَبُعُولَتُهُنَّ أَحَقُّ بِرَدِّهِنَّ فِي ذَلِكَ إِنْ أَرَادُوا إِصْلاحًا وَلَهُنَّ مِثْلُ الَّذِي عَلَيْهِنَّ بِالْمَعْرُوفِ وَلِلرِّجَالِ عَلَيْهِنَّ دَرَجَةٌ}
INSERT INTO [b9617] VALUES (231,'<span data-type="title" id=toc-229>٢٢٩ </span>- {الطَّلاقُ مَرَّتَانِ فَإِمْسَاكٌ بِمَعْرُوفٍ أَوْ تَسْرِيحٌ بِإِحْسَانٍ وَلا يَحِلُّ لَكُمْ أَنْ تَأْخُذُوا مِمَّا آتَيْتُمُوهُنَّ شَيْئًا إِلا أَنْ يَخَافَا أَلا يُقِيمَا حُدُودَ اللَّهِ فَإِنْ خِفْتُمْ أَلا يُقِيمَا حُدُودَ اللَّهِ فَلا جُنَاحَ عَلَيْهِمَا فِيمَا افْتَدَتْ بِهِ تِلْكَ حُدُودُ اللَّهِ فَلا تَعْتَدُوهَا وَمَنْ يَتَعَدَّ حُدُودَ اللَّهِ فَأُولَئِكَ هُمُ الظَّالِمُونَ}
INSERT INTO [b9617] VALUES (232,'<span data-type="title" id=toc-230>٢٣٠ </span>- {فَإِنْ طَلَّقَهَا فَلا تَحِلُّ لَهُ مِنْ بَعْدُ حَتَّى تَنْكِحَ زَوْجًا غَيْرَهُ فَإِنْ طَلَّقَهَا فَلا جُنَاحَ عَلَيْهِمَا أَنْ يَتَرَاجَعَا إِنْ ظَنَّا أَنْ يُقِيمَا حُدُودَ اللَّهِ وَتِلْكَ حُدُودُ اللَّهِ يُبَيِّنُهَا لِقَوْمٍ يَعْلَمُونَ}
INSERT INTO [b9617] VALUES (233,'<span data-type="title" id=toc-231>٢٣١ </span>- {وَإِذَا طَلَّقْتُمُ النِّسَاءَ فَبَلَغْنَ أَجَلَهُنَّ فَأَمْسِكُوهُنَّ بِمَعْرُوفٍ أَوْ سَرِّحُوهُنَّ بِمَعْرُوفٍ وَلا تُمْسِكُوهُنَّ ضِرَارًا لِتَعْتَدُوا وَمَنْ يَفْعَلْ ذَلِكَ فَقَدْ ظَلَمَ نَفْسَهُ وَلا تَتَّخِذُوا آيَاتِ اللَّهِ هُزُوًا وَاذْكُرُوا نِعْمَتَ اللَّهِ عَلَيْكُمْ وَمَا أَنْزَلَ عَلَيْكُمْ مِنَ الْكِتَابِ وَالْحِكْمَةِ يَعِظُكُمْ بِهِ}
INSERT INTO [b9617] VALUES (234,'<span data-type="title" id=toc-232>٢٣٢ </span>- {وَإِذَا طَلَّقْتُمُ النِّسَاءَ فَبَلَغْنَ أَجَلَهُنَّ فَلا تَعْضُلُوهُنَّ أَنْ يَنْكِحْنَ أَزْوَاجَهُنَّ إِذَا تَرَاضَوْا بَيْنَهُمْ بِالْمَعْرُوفِ ذَلِكَ يُوعَظُ بِهِ مَنْ كَانَ مِنْكُمْ يُؤْمِنُ بِاللَّهِ وَالْيَوْمِ الآخِرِ ذَلِكُمْ أَزْكَى لَكُمْ وَأَطْهَرُ}
INSERT INTO [b9617] VALUES (235,'<span data-type="title" id=toc-233>٢٣٣ </span>- {وَالْوَالِدَاتُ يُرْضِعْنَ أَوْلادَهُنَّ حَوْلَيْنِ كَامِلَيْنِ لِمَنْ أَرَادَ أَنْ يُتِمَّ الرَّضَاعَةَ وَعَلَى الْمَوْلُودِ لَهُ رِزْقُهُنَّ وَكِسْوَتُهُنَّ بِالْمَعْرُوفِ لا تُكَلَّفُ نَفْسٌ إِلا وُسْعَهَا لا تُضَارَّ وَالِدَةٌ بِوَلَدِهَا وَلا مَوْلُودٌ لَهُ بِوَلَدِهِ وَعَلَى الْوَارِثِ مِثْلُ ذَلِكَ فَإِنْ أَرَادَا فِصَالا عَنْ تَرَاضٍ مِنْهُمَا وَتَشَاوُرٍ فَلا جُنَاحَ عَلَيْهِمَا وَإِنْ أَرَدْتُمْ أَنْ تَسْتَرْضِعُوا أَوْلادَكُمْ فَلا جُنَاحَ عَلَيْكُمْ إِذَا سَلَّمْتُمْ مَا آتَيْتُمْ بِالْمَعْرُوفِ} ⦗٨٤⦘
INSERT INTO [b9617] VALUES (236,'<span data-type="title" id=toc-234>٢٣٤ </span>- {وَالَّذِينَ يُتَوَفَّوْنَ مِنْكُمْ وَيَذَرُونَ أَزْوَاجًا يَتَرَبَّصْنَ بِأَنْفُسِهِنَّ أَرْبَعَةَ أَشْهُرٍ وَعَشْرًا فَإِذَا بَلَغْنَ أَجَلَهُنَّ فَلا جُنَاحَ عَلَيْكُمْ فِيمَا فَعَلْنَ فِي أَنْفُسِهِنَّ بِالْمَعْرُوفِ}
INSERT INTO [b9617] VALUES (237,'<span data-type="title" id=toc-235>٢٣٥ </span>- {وَلَكِنْ لا تُوَاعِدُوهُنَّ سِرًّا إِلا أَنْ تَقُولُوا قَوْلا مَعْرُوفًا وَلا ⦗٨٥⦘ تَعْزِمُوا عُقْدَةَ النِّكَاحِ حَتَّى يَبْلُغَ الْكِتَابُ أَجَلَهُ وَاعْلَمُوا أَنَّ اللَّهَ يَعْلَمُ مَا فِي أَنْفُسِكُمْ فَاحْذَرُوهُ وَاعْلَمُوا أَنَّ اللَّهَ غَفُورٌ حَلِيمٌ}
INSERT INTO [b9617] VALUES (238,'<span data-type="title" id=toc-236>٢٣٦ </span>- {لا جُنَاحَ عَلَيْكُمْ إِنْ طَلَّقْتُمُ النِّسَاءَ مَا لَمْ تَمَسُّوهُنَّ أَوْ تَفْرِضُوا لَهُنَّ فَرِيضَةً وَمَتِّعُوهُنَّ عَلَى الْمُوسِعِ قَدَرُهُ وَعَلَى الْمُقْتِرِ قَدَرُهُ مَتَاعًا بِالْمَعْرُوفِ حَقًّا عَلَى الْمُحْسِنِينَ}
INSERT INTO [b9617] VALUES (239,'<span data-type="title" id=toc-237>٢٣٧ </span>- {وَإِنْ طَلَّقْتُمُوهُنَّ مِنْ قَبْلِ أَنْ تَمَسُّوهُنَّ وَقَدْ فَرَضْتُمْ لَهُنَّ فَرِيضَةً فَنِصْفُ مَا فَرَضْتُمْ إِلا أَنْ يَعْفُونَ أَوْ يَعْفُوَ الَّذِي بِيَدِهِ عُقْدَةُ النِّكَاحِ وَأَنْ تَعْفُوا أَقْرَبُ لِلتَّقْوَى وَلا تَنْسَوُا الْفَضْلَ بَيْنَكُمْ إِنَّ اللَّهَ بِمَا تَعْمَلُونَ بَصِيرٌ}
INSERT INTO [b9617] VALUES (240,'<span data-type="title" id=toc-238>٢٣٨ </span>- {وَقُومُوا لِلَّهِ قَانِتِينَ}
INSERT INTO [b9617] VALUES (241,'<span data-type="title" id=toc-239>٢٣٩ </span>- {فَإِنْ خِفْتُمْ فَرِجَالا أَوْ رُكْبَانًا فَإِذَا أَمِنْتُمْ فَاذْكُرُوا اللَّهَ كَمَا عَلَّمَكُمْ مَا لَمْ تَكُونُوا تَعْلَمُونَ}
INSERT INTO [b9617] VALUES (242,'<span data-type="title" id=toc-240>٢٤٠ </span>- {وَالَّذِينَ يُتَوَفَّوْنَ مِنْكُمْ وَيَذَرُونَ أَزْوَاجًا وَصِيَّةً لأَزْوَاجِهِمْ مَتَاعًا إِلَى الْحَوْلِ غَيْرَ إِخْرَاجٍ فَإِنْ خَرَجْنَ فَلا جُنَاحَ عَلَيْكُمْ فِي مَا فَعَلْنَ فِي أَنْفُسِهِنَّ مِنْ مَعْرُوفٍ وَاللَّهُ عَزِيزٌ حَكِيمٌ} ⦗٨٧⦘
INSERT INTO [b9617] VALUES (243,'<span data-type="title" id=toc-241>٢٤١ </span>- {وَلِلْمُطَلَّقَاتِ مَتَاعٌ بِالْمَعْرُوفِ حَقًّا عَلَى الْمُتَّقِينَ}
INSERT INTO [b9617] VALUES (244,'<span data-type="title" id=toc-242>٢٤٢ </span>- {كَذَلِكَ يُبَيِّنُ اللَّهُ لَكُمْ آيَاتِهِ لَعَلَّكُمْ تَعْقِلُونَ}
INSERT INTO [b9617] VALUES (245,'<span data-type="title" id=toc-243>٢٤٣ </span>- {أَلَمْ تَرَ إِلَى الَّذِينَ خَرَجُوا مِنْ دِيَارِهِمْ وَهُمْ أُلُوفٌ حَذَرَ الْمَوْتِ فَقَالَ لَهُمُ اللَّهُ مُوتُوا ثُمَّ أَحْيَاهُمْ إِنَّ اللَّهَ لَذُو فَضْلٍ عَلَى النَّاسِ وَلَكِنَّ أَكْثَرَ النَّاسِ لا يَشْكُرُونَ}
INSERT INTO [b9617] VALUES (246,'<span data-type="title" id=toc-244>٢٤٥ </span>- {مَنْ ذَا الَّذِي يُقْرِضُ اللَّهَ قَرْضًا حَسَنًا فَيُضَاعِفَهُ لَهُ أَضْعَافًا كَثِيرَةً وَاللَّهُ يَقْبِضُ وَيَبْسُطُ وَإِلَيْهِ تُرْجَعُونَ}
INSERT INTO [b9617] VALUES (247,'<span data-type="title" id=toc-245>٢٤٦ </span>- {أَلَمْ تَرَ إِلَى الْمَلإِ مِنْ بَنِي إِسْرَائِيلَ مِنْ بَعْدِ مُوسَى إِذْ قَالُوا لِنَبِيٍّ لَهُمُ ابْعَثْ لَنَا مَلِكًا نُقَاتِلْ فِي سَبِيلِ اللَّهِ قَالَ هَلْ عَسَيْتُمْ إِنْ كُتِبَ عَلَيْكُمُ الْقِتَالُ أَلا تُقَاتِلُوا قَالُوا وَمَا لَنَا أَلا نُقَاتِلَ فِي سَبِيلِ اللَّهِ وَقَدْ أُخْرِجْنَا مِنْ دِيَارِنَا وَأَبْنَائِنَا فَلَمَّا كُتِبَ عَلَيْهِمُ الْقِتَالُ تَوَلَّوْا إِلا قَلِيلا مِنْهُمْ}
INSERT INTO [b9617] VALUES (248,'<span data-type="title" id=toc-246>٢٤٧ </span>- {إِنَّ اللَّهَ قَدْ بَعَثَ لَكُمْ طَالُوتَ مَلِكًا قَالُوا أَنَّى يَكُونُ لَهُ الْمُلْكُ عَلَيْنَا وَنَحْنُ أَحَقُّ بِالْمُلْكِ مِنْهُ وَلَمْ يُؤْتَ سَعَةً مِنَ الْمَالِ قَالَ إِنَّ اللَّهَ اصْطَفَاهُ عَلَيْكُمْ وَزَادَهُ بَسْطَةً فِي الْعِلْمِ وَالْجِسْمِ وَاللَّهُ يُؤْتِي مُلْكَهُ مَنْ يَشَاءُ}
INSERT INTO [b9617] VALUES (249,'<span data-type="title" id=toc-247>٢٤٨ </span>- {إِنَّ آيَةَ مُلْكِهِ أَنْ يَأْتِيَكُمُ التَّابُوتُ فِيهِ سَكِينَةٌ مِنْ رَبِّكُمْ وَبَقِيَّةٌ مِمَّا تَرَكَ آلُ مُوسَى وَآلُ هَارُونَ تَحْمِلُهُ الْمَلائِكَةُ إِنَّ فِي ذَلِكَ لآيَةً لَكُمْ إِنْ كُنْتُمْ مُؤْمِنِينَ}
INSERT INTO [b9617] VALUES (250,'<span data-type="title" id=toc-248>٢٤٩ </span>- {فَلَمَّا فَصَلَ طَالُوتُ بِالْجُنُودِ قَالَ إِنَّ اللَّهَ مُبْتَلِيكُمْ بِنَهَرٍ فَمَنْ شَرِبَ مِنْهُ فَلَيْسَ مِنِّي وَمَنْ لَمْ يَطْعَمْهُ فَإِنَّهُ مِنِّي إِلا مَنِ اغْتَرَفَ غُرْفَةً بِيَدِهِ فَشَرِبُوا مِنْهُ إِلا قَلِيلا مِنْهُمْ فَلَمَّا جَاوَزَهُ هُوَ وَالَّذِينَ آمَنُوا مَعَهُ قَالُوا لا طَاقَةَ لَنَا الْيَوْمَ بِجَالُوتَ وَجُنُودِهِ قَالَ الَّذِينَ يَظُنُّونَ أَنَّهُمْ مُلاقُو اللَّهِ كَمْ مِنْ فِئَةٍ قَلِيلَةٍ غَلَبَتْ فِئَةً كَثِيرَةً بِإِذْنِ اللَّهِ}
INSERT INTO [b9617] VALUES (251,'<span data-type="title" id=toc-249>٢٥٠ </span>- {وَلَمَّا بَرَزُوا لِجَالُوتَ وَجُنُودِهِ قَالُوا رَبَّنَا أَفْرِغْ عَلَيْنَا صَبْرًا}
INSERT INTO [b9617] VALUES (252,'<span data-type="title" id=toc-250>٢٥١ </span>- {فَهَزَمُوهُمْ بِإِذْنِ اللَّهِ وَقَتَلَ دَاوُدُ جَالُوتَ وَآتَاهُ اللَّهُ الْمُلْكَ وَالْحِكْمَةَ وَعَلَّمَهُ مِمَّا يَشَاءُ وَلَوْلا دَفْعُ اللَّهِ النَّاسَ بَعْضَهُمْ بِبَعْضٍ لَفَسَدَتِ الأَرْضُ وَلَكِنَّ اللَّهَ ذُو فَضْلٍ عَلَى الْعَالَمِينَ}
INSERT INTO [b9617] VALUES (253,'<span data-type="title" id=toc-251>٢٥٢ </span>- {تِلْكَ آيَاتُ اللَّهِ نَتْلُوهَا عَلَيْكَ بِالْحَقِّ وَإِنَّكَ لَمِنَ الْمُرْسَلِينَ}
INSERT INTO [b9617] VALUES (254,'<span data-type="title" id=toc-252>٢٥٣ </span>- {تِلْكَ الرُّسُلُ فَضَّلْنَا بَعْضَهُمْ عَلَى بَعْضٍ مِنْهُمْ مَنْ كَلَّمَ اللَّهُ وَرَفَعَ بَعْضَهُمْ دَرَجَاتٍ وَآتَيْنَا عِيسَى ابْنَ مَرْيَمَ الْبَيِّنَاتِ وَأَيَّدْنَاهُ بِرُوحِ الْقُدُسِ وَلَوْ شَاءَ اللَّهُ مَا اقْتَتَلَ الَّذِينَ مِنْ بَعْدِهِمْ مِنْ بَعْدِ مَا جَاءَتْهُمُ الْبَيِّنَاتُ وَلَكِنِ اخْتَلَفُوا فَمِنْهُمْ مَنْ آمَنَ وَمِنْهُمْ مَنْ كَفَرَ}
INSERT INTO [b9617] VALUES (255,'<span data-type="title" id=toc-253>٢٥٤ </span>- {يَا أَيُّهَا الَّذِينَ آمَنُوا أَنْفِقُوا مِمَّا رَزَقْنَاكُمْ مِنْ قَبْلِ أَنْ يَأْتِيَ يَوْمٌ لا بَيْعٌ فِيهِ وَلا خُلَّةٌ وَلا شَفَاعَةٌ وَالْكَافِرُونَ هُمُ الظَّالِمُونَ}
INSERT INTO [b9617] VALUES (256,'<span data-type="title" id=toc-254>٢٥٥ </span>- {اللَّهُ لا إِلَهَ إِلا هُوَ الْحَيُّ الْقَيُّومُ لا تَأْخُذُهُ سِنَةٌ وَلا نَوْمٌ لَهُ مَا فِي السَّمَاوَاتِ وَمَا فِي الأَرْضِ مَنْ ذَا الَّذِي يَشْفَعُ عِنْدَهُ إِلا بِإِذْنِهِ يَعْلَمُ مَا بَيْنَ أَيْدِيهِمْ وَمَا خَلْفَهُمْ وَلا يُحِيطُونَ بِشَيْءٍ مِنْ عِلْمِهِ إِلا بِمَا شَاءَ وَسِعَ كُرْسِيُّهُ السَّمَاوَاتِ وَالأَرْضَ وَلا يَئُودُهُ حِفْظُهُمَا وَهُوَ الْعَلِيُّ الْعَظِيمُ}
INSERT INTO [b9617] VALUES (257,'<span data-type="title" id=toc-255>٢٥٦ </span>- {لا إِكْرَاهَ فِي الدِّينِ قَدْ تَبَيَّنَ الرُّشْدُ مِنَ الْغَيِّ فَمَنْ يَكْفُرْ بِالطَّاغُوتِ وَيُؤْمِنْ بِاللَّهِ فَقَدِ اسْتَمْسَكَ بِالْعُرْوَةِ الْوُثْقَى لا انْفِصَامَ لَهَا وَاللَّهُ سَمِيعٌ عَلِيمٌ}
INSERT INTO [b9617] VALUES (258,'<span data-type="title" id=toc-256>٢٥٧ </span>- {اللَّهُ وَلِيُّ الَّذِينَ آمَنُوا يُخْرِجُهُمْ مِنَ الظُّلُمَاتِ إِلَى النُّورِ وَالَّذِينَ كَفَرُوا أَوْلِيَاؤُهُمُ الطَّاغُوتُ يُخْرِجُونَهُمْ مِنَ النُّورِ إِلَى الظُّلُمَاتِ أُولَئِكَ أَصْحَابُ النَّارِ هُمْ فِيهَا خَالِدُونَ}
INSERT INTO [b9617] VALUES (259,'<span data-type="title" id=toc-257>٢٥٨ </span>- {أَلَمْ تَرَ إِلَى الَّذِي حَاجَّ إِبْرَاهِيمَ فِي رَبِّهِ أَنْ آتَاهُ اللَّهُ الْمُلْكَ إِذْ قَالَ إِبْرَاهِيمُ رَبِّيَ الَّذِي يُحْيِي وَيُمِيتُ قَالَ أَنَا أُحْيِي وَأُمِيتُ قَالَ إِبْرَاهِيمُ فَإِنَّ اللَّهَ يَأْتِي بِالشَّمْسِ مِنَ الْمَشْرِقِ فَأْتِ بِهَا مِنَ الْمَغْرِبِ}
INSERT INTO [b9617] VALUES (260,'<span data-type="title" id=toc-258>٢٥٩ </span>- {أَوْ كَالَّذِي مَرَّ عَلَى قَرْيَةٍ وَهِيَ خَاوِيَةٌ عَلَى عُرُوشِهَا قَالَ أَنَّى يُحْيِي هَذِهِ اللَّهُ بَعْدَ مَوْتِهَا فَأَمَاتَهُ اللَّهُ مِائَةَ عَامٍ ثُمَّ بَعَثَهُ قَالَ كَمْ لَبِثْتَ قَالَ لَبِثْتُ يَوْمًا أَوْ بَعْضَ يَوْمٍ قَالَ بَلْ لَبِثْتَ مِائَةَ عَامٍ فَانْظُرْ إِلَى طَعَامِكَ وَشَرَابِكَ لَمْ يَتَسَنَّهْ وَانْظُرْ إِلَى حِمَارِكَ وَلِنَجْعَلَكَ آيَةً لِلنَّاسِ وَانْظُرْ إِلَى الْعِظَامِ كَيْفَ نُنْشِزُهَا ثُمَّ نَكْسُوهَا لَحْمًا فَلَمَّا تَبَيَّنَ لَهُ قَالَ أَعْلَمُ أَنَّ اللَّهَ عَلَى كُلِّ شَيْءٍ قَدِيرٌ}
INSERT INTO [b9617] VALUES (261,'<span data-type="title" id=toc-259>٢٦٠ </span>- {وَإِذْ قَالَ إِبْرَاهِيمُ رَبِّ أَرِنِي كَيْفَ تُحْيِ الْمَوْتَى قَالَ أَوَلَمْ تُؤْمِنْ قَالَ بَلَى وَلَكِنْ لِيَطْمَئِنَّ قَلْبِي قَالَ فَخُذْ أَرْبَعَةً مِنَ الطَّيْرِ فَصُرْهُنَّ إِلَيْكَ ثُمَّ اجْعَلْ عَلَى كُلِّ جَبَلٍ مِنْهُنَّ جُزْءًا ثُمَّ ادْعُهُنَّ يَأْتِينَكَ سَعْيًا}
INSERT INTO [b9617] VALUES (262,'<span data-type="title" id=toc-260>٢٦١ </span>- {مَثَلُ الَّذِينَ يُنْفِقُونَ أَمْوَالَهُمْ فِي سَبِيلِ اللَّهِ كَمَثَلِ حَبَّةٍ أَنْبَتَتْ سَبْعَ سَنَابِلَ فِي كُلِّ سُنْبُلَةٍ مِائَةُ حَبَّةٍ وَاللَّهُ يُضَاعِفُ لِمَنْ يَشَاءُ وَاللَّهُ وَاسِعٌ عَلِيمٌ} ⦗٩٧⦘
INSERT INTO [b9617] VALUES (263,'<span data-type="title" id=toc-261>٢٦٢ </span>- {الَّذِينَ يُنْفِقُونَ أَمْوَالَهُمْ فِي سَبِيلِ اللَّهِ ثُمَّ لا يُتْبِعُونَ مَا أَنْفَقُوا مَنًّا وَلا أَذًى لَهُمْ أَجْرُهُمْ عِنْدَ رَبِّهِمْ وَلا خَوْفٌ عَلَيْهِمْ وَلا هُمْ يَحْزَنُونَ}
INSERT INTO [b9617] VALUES (264,'<span data-type="title" id=toc-262>٢٦٣ </span>- {قَوْلٌ مَعْرُوفٌ وَمَغْفِرَةٌ خَيْرٌ مِنْ صَدَقَةٍ يَتْبَعُهَا أَذًى}
INSERT INTO [b9617] VALUES (265,'<span data-type="title" id=toc-263>٢٦٤ </span>- {يَا أَيُّهَا الَّذِينَ آمَنُوا لا تُبْطِلُوا صَدَقَاتِكُمْ بِالْمَنِّ وَالأَذَى كَالَّذِي يُنْفِقُ مَالَهُ رِئَاءَ النَّاسِ وَلا يُؤْمِنُ بِاللَّهِ وَالْيَوْمِ الآخِرِ فَمَثَلُهُ كَمَثَلِ صَفْوَانٍ عَلَيْهِ تُرَابٌ فَأَصَابَهُ وَابِلٌ فَتَرَكَهُ صَلْدًا لا يَقْدِرُونَ عَلَى شَيْءٍ مِمَّا كَسَبُوا}
INSERT INTO [b9617] VALUES (266,'<span data-type="title" id=toc-264>٢٦٥ </span>- {فَآتَتْ أُكُلَهَا ضِعْفَيْنِ فَإِنْ لَمْ يُصِبْهَا وَابِلٌ فَطَلٌّ وَاللَّهُ بِمَا تَعْمَلُونَ بَصِيرٌ}
INSERT INTO [b9617] VALUES (267,'<span data-type="title" id=toc-265>٢٦٦ </span>- {أَيَوَدُّ أَحَدُكُمْ أَنْ تَكُونَ لَهُ جَنَّةٌ مِنْ نَخِيلٍ وَأَعْنَابٍ تَجْرِي مِنْ تَحْتِهَا الأَنْهَارُ لَهُ فِيهَا مِنْ كُلِّ الثَّمَرَاتِ وَأَصَابَهُ الْكِبَرُ وَلَهُ ذُرِّيَّةٌ ضُعَفَاءُ فَأَصَابَهَا إِعْصَارٌ فِيهِ نَارٌ فَاحْتَرَقَتْ كَذَلِكَ يُبَيِّنُ اللَّهُ لَكُمُ الآيَاتِ لَعَلَّكُمْ تَتَفَكَّرُونَ}
INSERT INTO [b9617] VALUES (268,'<span data-type="title" id=toc-266>٢٦٧ </span>- {وَلا تَيَمَّمُوا الْخَبِيثَ مِنْهُ تُنْفِقُونَ وَلَسْتُمْ بِآخِذِيهِ إِلا أَنْ تُغْمِضُوا فِيهِ وَاعْلَمُوا أَنَّ اللَّهَ غَنِيٌّ حَمِيدٌ}
INSERT INTO [b9617] VALUES (269,'<span data-type="title" id=toc-267>٢٦٨ </span>- {الشَّيْطَانُ يَعِدُكُمُ الْفَقْرَ وَيَأْمُرُكُمْ بِالْفَحْشَاءِ وَاللَّهُ يَعِدُكُمْ مَغْفِرَةً مِنْهُ}
INSERT INTO [b9617] VALUES (270,'<span data-type="title" id=toc-268>٢٦٩ </span>- {يُؤْتِي الْحِكْمَةَ مَنْ يَشَاءُ وَمَنْ يُؤْتَ الْحِكْمَةَ فَقَدْ أُوتِيَ خَيْرًا كَثِيرًا وَمَا يَذَّكَّرُ إِلا أُولُو الأَلْبَابِ}
INSERT INTO [b9617] VALUES (271,'<span data-type="title" id=toc-269>٢٧٠ </span>- {وَمَا أَنْفَقْتُمْ مِنْ نَفَقَةٍ أَوْ نَذَرْتُمْ مِنْ نَذْرٍ فَإِنَّ اللَّهَ يَعْلَمُهُ وَمَا لِلظَّالِمِينَ مِنْ أَنْصَارٍ}
INSERT INTO [b9617] VALUES (272,'<span data-type="title" id=toc-270>٢٧١ </span>- {إِنْ تُبْدُوا الصَّدَقَاتِ فَنِعِمَّا هِيَ وَإِنْ تُخْفُوهَا وَتُؤْتُوهَا الْفُقَرَاءَ فَهُوَ خَيْرٌ لَكُمْ وَيُكَفِّرُ عَنْكُمْ مِنْ سَيِّئَاتِكُمْ وَاللَّهُ بِمَا تَعْمَلُونَ خَبِيرٌ}
INSERT INTO [b9617] VALUES (273,'<span data-type="title" id=toc-271>٢٧٢ </span>- {لَيْسَ عَلَيْكَ هُدَاهُمْ وَلَكِنَّ اللَّهَ يَهْدِي مَنْ يَشَاءُ وَمَا تُنْفِقُوا مِنْ خَيْرٍ فَلأَنْفُسِكُمْ وَمَا تُنْفِقُونَ إِلا ابْتِغَاءَ وَجْهِ اللَّهِ وَمَا تُنْفِقُوا مِنْ خَيْرٍ يُوَفَّ ⦗١٠١⦘ إِلَيْكُمْ وَأَنْتُمْ لا تُظْلَمُونَ}
INSERT INTO [b9617] VALUES (274,'<span data-type="title" id=toc-272>٢٧٣ </span>- {لِلْفُقَرَاءِ الَّذِينَ أُحْصِرُوا فِي سَبِيلِ اللَّهِ لا يَسْتَطِيعُونَ ضَرْبًا فِي الأَرْضِ يَحْسَبُهُمُ الْجَاهِلُ أَغْنِيَاءَ مِنَ التَّعَفُّفِ تَعْرِفُهُمْ بِسِيمَاهُمْ لا يَسْأَلُونَ النَّاسَ إِلْحَافًا وَمَا تُنْفِقُوا مِنْ خَيْرٍ فَإِنَّ اللَّهَ بِهِ عَلِيمٌ}
INSERT INTO [b9617] VALUES (275,'<span data-type="title" id=toc-273>٢٧٤ </span>- {الَّذِينَ يُنْفِقُونَ أَمْوَالَهُمْ بِاللَّيْلِ وَالنَّهَارِ سِرًّا وَعَلانِيَةً فَلَهُمْ أَجْرُهُمْ عِنْدَ رَبِّهِمْ وَلا خَوْفٌ عَلَيْهِمْ وَلا هُمْ يَحْزَنُونَ}
INSERT INTO [b9617] VALUES (276,'<span data-type="title" id=toc-274>٢٧٥ </span>- {لا يَقُومُونَ إِلا كَمَا يَقُومُ الَّذِي يَتَخَبَّطُهُ الشَّيْطَانُ مِنَ الْمَسِّ ذَلِكَ بِأَنَّهُمْ قَالُوا إِنَّمَا الْبَيْعُ مِثْلُ الرِّبَا وَأَحَلَّ اللَّهُ الْبَيْعَ وَحَرَّمَ الرِّبَا فَمَنْ جَاءَهُ مَوْعِظَةٌ مِنْ رَبِّهِ فَانْتَهَى فَلَهُ مَا سَلَفَ وَأَمْرُهُ إِلَى اللَّهِ وَمَنْ عَادَ فَأُولَئِكَ أَصْحَابُ النَّارِ هُمْ فِيهَا خَالِدُونَ}
INSERT INTO [b9617] VALUES (277,'<span data-type="title" id=toc-275>٢٧٦ </span>- {وَاللَّهُ لا يُحِبُّ كُلَّ كَفَّارٍ أَثِيمٍ}
INSERT INTO [b9617] VALUES (278,'<span data-type="title" id=toc-276>٢٧٧ </span>- {إِنَّ الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ وَأَقَامُوا الصَّلاةَ وَآتَوُا الزَّكَاةَ لَهُمْ أَجْرُهُمْ عِنْدَ رَبِّهِمْ وَلا خَوْفٌ عَلَيْهِمْ}
INSERT INTO [b9617] VALUES (279,'<span data-type="title" id=toc-277>٢٧٨ </span>- {يَا أَيُّهَا الَّذِينَ آمَنُوا اتَّقُوا اللَّهَ وَذَرُوا مَا بَقِيَ مِنَ الرِّبَا إِنْ كُنْتُمْ مُؤْمِنِينَ} ⦗١٠٣⦘
INSERT INTO [b9617] VALUES (280,'<span data-type="title" id=toc-278>٢٧٩ </span>- {فَإِنْ لَمْ تَفْعَلُوا فَأْذَنُوا بِحَرْبٍ مِنَ اللَّهِ وَرَسُولِهِ وَإِنْ تُبْتُمْ فَلَكُمْ رُءُوسُ أَمْوَالِكُمْ لا تَظْلِمُونَ وَلا تُظْلَمُونَ}
INSERT INTO [b9617] VALUES (281,'<span data-type="title" id=toc-279>٢٨٠ </span>- {وَإِنْ كَانَ ذُو عُسْرَةٍ فَنَظِرَةٌ إِلَى مَيْسَرَةٍ وَأَنْ تَصَدَّقُوا خَيْرٌ لَكُمْ إِنْ كُنْتُمْ تَعْلَمُونَ}
INSERT INTO [b9617] VALUES (282,'<span data-type="title" id=toc-280>٢٨١ </span>- {وَاتَّقُوا يَوْمًا تُرْجَعُونَ فِيهِ إِلَى اللَّهِ ثُمَّ تُوَفَّى كُلُّ نَفْسٍ مَا كَسَبَتْ وَهُمْ لا يُظْلَمُونَ}
INSERT INTO [b9617] VALUES (283,'<span data-type="title" id=toc-281>٢٨٢ </span>- {يَا أَيُّهَا الَّذِينَ آمَنُوا إِذَا تَدَايَنْتُمْ بِدَيْنٍ إِلَى أَجَلٍ مُسَمًّى فَاكْتُبُوهُ وَلْيَكْتُبْ بَيْنَكُمْ كَاتِبٌ بِالْعَدْلِ وَلا يَأْبَ كَاتِبٌ أَنْ يَكْتُبَ كَمَا عَلَّمَهُ اللَّهُ فَلْيَكْتُبْ وَلْيُمْلِلِ الَّذِي عَلَيْهِ الْحَقُّ وَلْيَتَّقِ اللَّهَ رَبَّهُ وَلا يَبْخَسْ مِنْهُ شَيْئًا فَإِنْ كَانَ الَّذِي عَلَيْهِ الْحَقُّ سَفِيهًا أَوْ ضَعِيفًا أَوْ لا يَسْتَطِيعُ أَنْ يُمِلَّ هُوَ فَلْيُمْلِلْ وَلِيُّهُ بِالْعَدْلِ وَاسْتَشْهِدُوا شَهِيدَيْنِ مِنْ رِجَالِكُمْ فَإِنْ لَمْ يَكُونَا رَجُلَيْنِ فَرَجُلٌ وَامْرَأَتَانِ مِمَّنْ تَرْضَوْنَ مِنَ الشُّهَدَاءِ أَنْ تَضِلَّ إِحْدَاهُمَا فَتُذَكِّرَ إِحْدَاهُمَا الأُخْرَى وَلا يَأْبَ الشُّهَدَاءُ إِذَا مَا دُعُوا وَلا تَسْأَمُوا أَنْ تَكْتُبُوهُ صَغِيرًا أَوْ كَبِيرًا إِلَى أَجَلِهِ ذَلِكُمْ أَقْسَطُ عِنْدَ اللَّهِ وَأَقْوَمُ لِلشَّهَادَةِ وَأَدْنَى أَلا تَرْتَابُوا إِلا أَنْ تَكُونَ تِجَارَةً حَاضِرَةً تُدِيرُونَهَا بَيْنَكُمْ فَلَيْسَ عَلَيْكُمْ جُنَاحٌ أَلا تَكْتُبُوهَا وَأَشْهِدُوا إِذَا تَبَايَعْتُمْ وَلا يُضَارَّ كَاتِبٌ وَلا شَهِيدٌ وَإِنْ تَفْعَلُوا فَإِنَّهُ فُسُوقٌ بِكُمْ وَاتَّقُوا اللَّهَ وَيُعَلِّمُكُمُ اللَّهُ وَاللَّهُ بِكُلِّ شَيْءٍ عَلِيمٌ}
INSERT INTO [b9617] VALUES (284,'<span data-type="title" id=toc-282>٢٨٣ </span>- {وَإِنْ كُنْتُمْ عَلَى سَفَرٍ وَلَمْ تَجِدُوا كَاتِبًا فَرِهَانٌ مَقْبُوضَةٌ فَإِنْ أَمِنَ بَعْضُكُمْ بَعْضًا فَلْيُؤَدِّ الَّذِي اؤْتُمِنَ أَمَانَتَهُ وَلْيَتَّقِ اللَّهَ رَبَّهُ وَلا تَكْتُمُوا الشَّهَادَةَ وَمَنْ يَكْتُمْهَا فَإِنَّهُ آثِمٌ قَلْبُهُ وَاللَّهُ بِمَا تَعْمَلُونَ عَلِيمٌ}
INSERT INTO [b9617] VALUES (285,'<span data-type="title" id=toc-283>٢٨٤ </span>- {وَإِنْ تُبْدُوا مَا فِي أَنْفُسِكُمْ أَوْ تُخْفُوهُ يُحَاسِبْكُمْ بِهِ اللَّهُ فَيَغْفِرُ لِمَنْ يَشَاءُ وَيُعَذِّبُ مَنْ يَشَاءُ وَاللَّهُ عَلَى كُلِّ شَيْءٍ قَدِيرٌ}
INSERT INTO [b9617] VALUES (286,'<span data-type="title" id=toc-284>٢٨٥ </span>- {آمَنَ الرَّسُولُ بِمَا أُنْزِلَ إِلَيْهِ مِنْ رَبِّهِ وَالْمُؤْمِنُونَ كُلٌّ آمَنَ بِاللَّهِ وَمَلائِكَتِهِ وَكُتُبِهِ وَرُسُلِهِ لا نُفَرِّقُ بَيْنَ أَحَدٍ مِنْ رُسُلِهِ وَقَالُوا سَمِعْنَا وَأَطَعْنَا غُفْرَانَكَ رَبَّنَا وَإِلَيْكَ الْمَصِيرُ}
INSERT INTO [b9617] VALUES (287,'<span data-type="title" id=toc-285>٢٨٦ </span>- {لا يُكَلِّفُ اللَّهُ نَفْسًا إِلا وُسْعَهَا لَهَا مَا كَسَبَتْ وَعَلَيْهَا مَا اكْتَسَبَتْ رَبَّنَا لا تُؤَاخِذْنَا إِنْ نَسِينَا أَوْ أَخْطَأْنَا رَبَّنَا وَلا تَحْمِلْ عَلَيْنَا إِصْرًا كَمَا حَمَلْتَهُ عَلَى الَّذِينَ مِنْ قَبْلِنَا رَبَّنَا وَلا تُحَمِّلْنَا مَا لا طَاقَةَ لَنَا بِهِ وَاعْفُ عَنَّا وَاغْفِرْ لَنَا وَارْحَمْنَا أَنْتَ مَوْلانَا فَانْصُرْنَا عَلَى الْقَوْمِ الْكَافِرِينَ}
INSERT INTO [b9617] VALUES (288,'<span data-type="title" id=toc-286>سورة آل عمران</span>','1','108',NULL,NULL,'0');
INSERT INTO [b9617] VALUES (289,'<span data-type="title" id=toc-287>٢ </span>- {اللَّهُ لا إِلَهَ إِلا هُوَ الْحَيُّ الْقَيُّومُ}
INSERT INTO [b9617] VALUES (290,'<span data-type="title" id=toc-288>٣ </span>- {نَزَّلَ عَلَيْكَ الْكِتَابَ بِالْحَقِّ مُصَدِّقًا لِمَا بَيْنَ يَدَيْهِ}
INSERT INTO [b9617] VALUES (291,'<span data-type="title" id=toc-289>٤ </span>- {مِنْ قَبْلُ هُدًى لِلنَّاسِ وَأَنْزَلَ الْفُرْقَانَ إِنَّ الَّذِينَ كَفَرُوا بِآيَاتِ اللَّهِ لَهُمْ عَذَابٌ شَدِيدٌ وَاللَّهُ عَزِيزٌ ذُو انْتِقَامٍ}
INSERT INTO [b9617] VALUES (292,'<span data-type="title" id=toc-290>٥ </span>- {إِنَّ اللَّهَ لا يَخْفَى عَلَيْهِ شَيْءٌ فِي الأَرْضِ}
INSERT INTO [b9617] VALUES (293,'<span data-type="title" id=toc-291>٦ </span>- {هُوَ الَّذِي يُصَوِّرُكُمْ فِي الأَرْحَامِ كَيْفَ يَشَاءُ لا إِلَهَ إِلا هُوَ الْعَزِيزُ الْحَكِيمُ} ⦗١٠٩⦘
INSERT INTO [b9617] VALUES (294,'<span data-type="title" id=toc-292>٧ </span>- {هُوَ الَّذِي أَنْزَلَ عَلَيْكَ الْكِتَابَ مِنْهُ آيَاتٌ مُحْكَمَاتٌ هُنَّ أُمُّ الْكِتَابِ وَأُخَرُ مُتَشَابِهَاتٌ فَأَمَّا الَّذِينَ فِي قُلُوبِهِمْ زَيْغٌ فَيَتَّبِعُونَ مَا تَشَابَهَ مِنْهُ ابْتِغَاءَ الْفِتْنَةِ وَابْتِغَاءَ تَأْوِيلِهِ وَمَا يَعْلَمُ تَأْوِيلَهُ إِلا اللَّهُ وَالرَّاسِخُونَ فِي الْعِلْمِ يَقُولُونَ آمَنَّا بِهِ كُلٌّ مِنْ عِنْدِ رَبِّنَا وَمَا يَذَّكَّرُ إِلا أُولُو الأَلْبَابِ}
INSERT INTO [b9617] VALUES (295,'<span data-type="title" id=toc-293>٨ </span>- {إِنَّكَ أَنْتَ الْوَهَّابُ}
INSERT INTO [b9617] VALUES (296,'<span data-type="title" id=toc-294>٩ </span>- {رَبَّنَا إِنَّكَ جَامِعُ النَّاسِ لِيَوْمٍ لا رَيْبَ فِيهِ إِنَّ اللَّهَ لا يُخْلِفُ الْمِيعَادَ}
INSERT INTO [b9617] VALUES (297,'<span data-type="title" id=toc-295>١٠ </span>- {لَنْ تُغْنِيَ عَنْهُمْ أَمْوَالُهُمْ وَلا أَوْلادُهُمْ مِنَ اللَّهِ شَيْئًا وَأُولَئِكَ هُمْ وَقُودُ النَّارِ}
INSERT INTO [b9617] VALUES (298,'<span data-type="title" id=toc-296>١١ </span>- {كَدَأْبِ آلِ فِرْعَوْنَ وَالَّذِينَ مِنْ قَبْلِهِمْ كَذَّبُوا بِآيَاتِنَا فَأَخَذَهُمُ اللَّهُ بِذُنُوبِهِمْ}
INSERT INTO [b9617] VALUES (299,'<span data-type="title" id=toc-297>١٢ </span>- {وَبِئْسَ الْمِهَادُ}
INSERT INTO [b9617] VALUES (300,'<span data-type="title" id=toc-298>١٣ </span>- {قَدْ كَانَ لَكُمْ آيَةٌ فِي فِئَتَيْنِ الْتَقَتَا فِئَةٌ تُقَاتِلُ فِي سَبِيلِ اللَّهِ وَأُخْرَى كَافِرَةٌ يَرَوْنَهُمْ مِثْلَيْهِمْ رَأْيَ الْعَيْنِ}
INSERT INTO [b9617] VALUES (301,'<span data-type="title" id=toc-299>١٤ </span>- {زُيِّنَ لِلنَّاسِ حُبُّ الشَّهَوَاتِ مِنَ النِّسَاءِ وَالْبَنِينَ وَالْقَنَاطِيرِ الْمُقَنْطَرَةِ مِنَ الذَّهَبِ وَالْفِضَّةِ وَالْخَيْلِ الْمُسَوَّمَةِ وَالأَنْعَامِ وَالْحَرْثِ ذَلِكَ مَتَاعُ الْحَيَاةِ الدُّنْيَا وَاللَّهُ عِنْدَهُ حُسْنُ الْمَآبِ}
INSERT INTO [b9617] VALUES (302,'<span data-type="title" id=toc-300>١٥ </span>- {قُلْ أَؤُنَبِّئُكُمْ بِخَيْرٍ مِنْ ذَلِكُمْ لِلَّذِينَ اتَّقَوْا عِنْدَ رَبِّهِمْ جَنَّاتٌ تَجْرِي مِنْ تَحْتِهَا الأَنْهَارُ خَالِدِينَ فِيهَا وَأَزْوَاجٌ مُطَهَّرَةٌ وَرِضْوَانٌ مِنَ اللَّهِ وَاللَّهُ بَصِيرٌ بِالْعِبَادِ}
INSERT INTO [b9617] VALUES (303,'<span data-type="title" id=toc-301>١٦ </span>- {الَّذِينَ يَقُولُونَ رَبَّنَا إِنَّنَا آمَنَّا فَاغْفِرْ لَنَا ذُنُوبَنَا}
INSERT INTO [b9617] VALUES (304,'<span data-type="title" id=toc-302>١٧ </span>- {الصَّابِرِينَ وَالصَّادِقِينَ وَالْقَانِتِينَ وَالْمُنْفِقِينَ وَالْمُسْتَغْفِرِينَ بِالأَسْحَارِ}
INSERT INTO [b9617] VALUES (305,'<span data-type="title" id=toc-303>١٨ </span>- {شَهِدَ اللَّهُ أَنَّهُ لا إِلَهَ إِلا هُوَ وَالْمَلائِكَةُ وَأُولُو الْعِلْمِ قَائِمًا بِالْقِسْطِ لا إِلَهَ إِلا هُوَ الْعَزِيزُ الْحَكِيمُ}
INSERT INTO [b9617] VALUES (306,'<span data-type="title" id=toc-304>١٩ </span>- {وَمَا اخْتَلَفَ الَّذِينَ أُوتُوا الْكِتَابَ إِلا مِنْ بَعْدِ مَا جَاءَهُمُ الْعِلْمُ بَغْيًا بَيْنَهُمْ}
INSERT INTO [b9617] VALUES (307,'<span data-type="title" id=toc-305>٢٠ </span>- {فَإِنْ حَاجُّوكَ فَقُلْ أَسْلَمْتُ وَجْهِيَ لِلَّهِ وَمَنِ اتَّبَعَنِ وَقُلْ لِلَّذِينَ أُوتُوا الْكِتَابَ وَالأُمِّيِّينَ أَأَسْلَمْتُمْ فَإِنْ أَسْلَمُوا فَقَدِ اهْتَدَوْا وَإِنْ تَوَلَّوْا فَإِنَّمَا عَلَيْكَ الْبَلاغُ}
INSERT INTO [b9617] VALUES (308,'<span data-type="title" id=toc-306>٢١ </span>- {إِنَّ الَّذِينَ يَكْفُرُونَ بِآيَاتِ اللَّهِ وَيَقْتُلُونَ النَّبِيِّينَ بِغَيْرِ حَقٍّ وَيَقْتُلُونَ الَّذِينَ يَأْمُرُونَ بِالْقِسْطِ مِنَ النَّاسِ فَبَشِّرْهُمْ بِعَذَابٍ أَلِيمٍ}
INSERT INTO [b9617] VALUES (309,'<span data-type="title" id=toc-307>٢٢ </span>- {أُولَئِكَ الَّذِينَ حَبِطَتْ أَعْمَالُهُمْ فِي الدُّنْيَا وَالآخِرَةِ وَمَا لَهُمْ مِنْ نَاصِرِينَ}
INSERT INTO [b9617] VALUES (310,'<span data-type="title" id=toc-308>٢٣ </span>- {أَلَمْ تَرَ إِلَى الَّذِينَ أُوتُوا نَصِيبًا مِنَ الْكِتَابِ يُدْعَوْنَ إِلَى كِتَابِ اللَّهِ لِيَحْكُمَ بَيْنَهُمْ ثُمَّ يَتَوَلَّى فَرِيقٌ مِنْهُمْ وَهُمْ مُعْرِضُونَ}
INSERT INTO [b9617] VALUES (311,'<span data-type="title" id=toc-309>٢٤ </span>- {ذَلِكَ بِأَنَّهُمْ قَالُوا لَنْ تَمَسَّنَا النَّارُ إِلا أَيَّامًا مَعْدُودَاتٍ وَغَرَّهُمْ فِي دِينِهِمْ مَا كَانُوا يَفْتَرُونَ} ⦗١١٤⦘
INSERT INTO [b9617] VALUES (312,'<span data-type="title" id=toc-310>٢٥ </span>- {فَكَيْفَ إِذَا جَمَعْنَاهُمْ لِيَوْمٍ لا رَيْبَ فِيهِ وَوُفِّيَتْ كُلُّ نَفْسٍ مَا كَسَبَتْ وَهُمْ لا يُظْلَمُونَ}
INSERT INTO [b9617] VALUES (313,'<span data-type="title" id=toc-311>٢٦ </span>- {قُلِ اللَّهُمَّ مَالِكَ الْمُلْكِ تُؤْتِي الْمُلْكَ مَنْ تَشَاءُ وَتَنْزِعُ الْمُلْكَ مِمَّنْ تَشَاءُ وَتُعِزُّ مَنْ تَشَاءُ وَتُذِلُّ مَنْ تَشَاءُ بِيَدِكَ الْخَيْرُ}
INSERT INTO [b9617] VALUES (314,'<span data-type="title" id=toc-312>٢٨ </span>- {لا يَتَّخِذِ الْمُؤْمِنُونَ الْكَافِرِينَ أَوْلِيَاءَ مِنْ دُونِ الْمُؤْمِنِينَ وَمَنْ يَفْعَلْ ذَلِكَ فَلَيْسَ مِنَ اللَّهِ فِي شَيْءٍ إِلا أَنْ تَتَّقُوا مِنْهُمْ تُقَاةً وَيُحَذِّرُكُمُ اللَّهُ نَفْسَهُ وَإِلَى اللَّهِ الْمَصِيرُ}
INSERT INTO [b9617] VALUES (315,'<span data-type="title" id=toc-313>٢٩ </span>- {قُلْ إِنْ تُخْفُوا مَا فِي صُدُورِكُمْ أَوْ تُبْدُوهُ يَعْلَمْهُ اللَّهُ وَيَعْلَمُ مَا فِي السَّمَاوَاتِ وَمَا فِي الأَرْضِ وَاللَّهُ عَلَى كُلِّ شَيْءٍ قَدِيرٌ}
INSERT INTO [b9617] VALUES (316,'<span data-type="title" id=toc-314>٣٠ </span>- {يَوْمَ تَجِدُ كُلُّ نَفْسٍ مَا عَمِلَتْ مِنْ خَيْرٍ مُحْضَرًا وَمَا عَمِلَتْ مِنْ سُوءٍ تَوَدُّ لَوْ أَنَّ بَيْنَهَا وَبَيْنَهُ أَمَدًا بَعِيدًا وَيُحَذِّرُكُمُ اللَّهُ نَفْسَهُ وَاللَّهُ رَءُوفٌ بِالْعِبَادِ}
INSERT INTO [b9617] VALUES (317,'<span data-type="title" id=toc-315>٣٤ </span>- {ذُرِّيَّةً بَعْضُهَا مِنْ بَعْضٍ وَاللَّهُ سَمِيعٌ عَلِيمٌ}
INSERT INTO [b9617] VALUES (318,'<span data-type="title" id=toc-316>٣٥ </span>- {إِذْ قَالَتِ امْرَأَتُ عِمْرَانَ رَبِّ إِنِّي نَذَرْتُ لَكَ مَا فِي بَطْنِي مُحَرَّرًا فَتَقَبَّلْ مِنِّي}
INSERT INTO [b9617] VALUES (319,'<span data-type="title" id=toc-317>٣٦ </span>- {فَلَمَّا وَضَعَتْهَا قَالَتْ رَبِّ إِنِّي وَضَعْتُهَا أُنْثَى وَاللَّهُ أَعْلَمُ بِمَا وَضَعَتْ وَلَيْسَ الذَّكَرُ كَالأُنْثَى وَإِنِّي سَمَّيْتُهَا مَرْيَمَ}
INSERT INTO [b9617] VALUES (320,'<span data-type="title" id=toc-318>٣٧ </span>- {كُلَّمَا دَخَلَ عَلَيْهَا زَكَرِيَّا الْمِحْرَابَ وَجَدَ عِنْدَهَا رِزْقًا قَالَ يَا مَرْيَمُ أَنَّى لَكِ هَذَا قَالَتْ هُوَ مِنْ عِنْدِ اللَّهِ} ⦗١١٧⦘
INSERT INTO [b9617] VALUES (321,'<span data-type="title" id=toc-319>٣٨ </span>- {هُنَالِكَ دَعَا زَكَرِيَّا رَبَّهُ قَالَ رَبِّ هَبْ لِي مِنْ لَدُنْكَ ذُرِّيَّةً طَيِّبَةً}
INSERT INTO [b9617] VALUES (322,'<span data-type="title" id=toc-320>٣٩ </span>- {فَنَادَتْهُ الْمَلائِكَةُ وَهُوَ قَائِمٌ يُصَلِّي فِي الْمِحْرَابِ أَنَّ اللَّهَ يُبَشِّرُكَ بِيَحْيَى مُصَدِّقًا بِكَلِمَةٍ مِنَ اللَّهِ وَسَيِّدًا وَحَصُورًا وَنَبِيًّا مِنَ الصَّالِحِينَ}
INSERT INTO [b9617] VALUES (323,'<span data-type="title" id=toc-321>٤٠ </span>- {قَالَ رَبِّ أَنَّى يَكُونُ لِي غُلامٌ وَقَدْ بَلَغَنِيَ الْكِبَرُ وَامْرَأَتِي عَاقِرٌ قَالَ كَذَلِكَ اللَّهُ يَفْعَلُ مَا يَشَاءُ}
INSERT INTO [b9617] VALUES (324,'<span data-type="title" id=toc-322>٤١ </span>- {قَالَ آيَتُكَ أَلا تُكَلِّمَ النَّاسَ ثَلاثَةَ أَيَّامٍ إِلا رَمْزًا وَاذْكُرْ رَبَّكَ كَثِيرًا}
INSERT INTO [b9617] VALUES (325,'<span data-type="title" id=toc-323>٤٢ </span>- {وَإِذْ قَالَتِ الْمَلائِكَةُ يَا مَرْيَمُ إِنَّ اللَّهَ اصْطَفَاكِ}
INSERT INTO [b9617] VALUES (326,'<span data-type="title" id=toc-324>٤٤ </span>- {ذَلِكَ مِنْ أَنْبَاءِ الْغَيْبِ نُوحِيهِ إِلَيْكَ وَمَا كُنْتَ لَدَيْهِمْ إِذْ يُلْقُونَ أَقْلامَهُمْ أَيُّهُمْ يَكْفُلُ مَرْيَمَ وَمَا كُنْتَ لَدَيْهِمْ إِذْ يَخْتَصِمُونَ}
INSERT INTO [b9617] VALUES (327,'<span data-type="title" id=toc-325>٤٥ </span>- {وَجِيهًا فِي الدُّنْيَا وَالآخِرَةِ وَمِنَ الْمُقَرَّبِينَ}
INSERT INTO [b9617] VALUES (328,'<span data-type="title" id=toc-326>٤٦ </span>- {وَيُكَلِّمُ النَّاسَ فِي الْمَهْدِ وَكَهْلا وَمِنَ الصَّالِحِينَ}
INSERT INTO [b9617] VALUES (329,'<span data-type="title" id=toc-327>٤٧ </span>- {قَالَتْ رَبِّ أَنَّى يَكُونُ لِي وَلَدٌ وَلَمْ يَمْسَسْنِي بَشَرٌ قَالَ كَذَلِكِ اللَّهُ يَخْلُقُ مَا يَشَاءُ إِذَا قَضَى أَمْرًا فَإِنَّمَا يَقُولُ لَهُ كُنْ فَيَكُونُ} ⦗١٢٠⦘
INSERT INTO [b9617] VALUES (330,'<span data-type="title" id=toc-328>٤٩ </span>- {وَرَسُولا إِلَى بَنِي إِسْرَائِيلَ أَنِّي قَدْ جِئْتُكُمْ بِآيَةٍ مِنْ رَبِّكُمْ أَنِّي أَخْلُقُ لَكُمْ مِنَ الطِّينِ كَهَيْئَةِ الطَّيْرِ فَأَنْفُخُ فِيهِ فَيَكُونُ طَيْرًا بِإِذْنِ اللَّهِ وَأُبْرِئُ الأَكْمَهَ وَالأَبْرَصَ وَأُحْيِي الْمَوْتَى بِإِذْنِ اللَّهِ وَأُنَبِّئُكُمْ بِمَا تَأْكُلُونَ وَمَا تَدَّخِرُونَ فِي بُيُوتِكُمْ إِنَّ فِي ذَلِكَ لآيَةً لَكُمْ إِنْ كُنْتُمْ مُؤْمِنِينَ}
INSERT INTO [b9617] VALUES (331,'<span data-type="title" id=toc-329>٥٠ </span>- {وَمُصَدِّقًا لِمَا بَيْنَ يَدَيَّ مِنَ التَّوْرَاةِ وَلأُحِلَّ لَكُمْ بَعْضَ الَّذِي حُرِّمَ عَلَيْكُمْ وَجِئْتُكُمْ بِآيَةٍ مِنْ رَبِّكُمْ فَاتَّقُوا اللَّهَ وَأَطِيعُونِ}
INSERT INTO [b9617] VALUES (332,'<span data-type="title" id=toc-330>٥١ </span>- {إِنَّ اللَّهَ رَبِّي وَرَبُّكُمْ فَاعْبُدُوهُ هَذَا صِرَاطٌ مُسْتَقِيمٌ}
INSERT INTO [b9617] VALUES (333,'<span data-type="title" id=toc-331>٥٢ </span>- {قَالَ مَنْ أَنْصَارِي إِلَى اللَّهِ قَالَ الْحَوَارِيُّونَ نَحْنُ أَنْصَارُ اللَّهِ آمَنَّا بِاللَّهِ}
INSERT INTO [b9617] VALUES (334,'<span data-type="title" id=toc-332>٥٣ </span>- {وَاتَّبَعْنَا الرَّسُولَ فَاكْتُبْنَا مَعَ الشَّاهِدِينَ}
INSERT INTO [b9617] VALUES (335,'<span data-type="title" id=toc-333>٥٤ </span>- {وَمَكَرُوا وَمَكَرَ اللَّهُ وَاللَّهُ خَيْرُ الْمَاكِرِينَ}
INSERT INTO [b9617] VALUES (336,'<span data-type="title" id=toc-334>٥٥ </span>- {وَجَاعِلُ الَّذِينَ اتَّبَعُوكَ فَوْقَ الَّذِينَ كَفَرُوا إِلَى يَوْمِ الْقِيَامَةِ ثُمَّ إِلَيَّ مَرْجِعُكُمْ فَأَحْكُمُ بَيْنَكُمْ} ⦗١٢٢⦘
INSERT INTO [b9617] VALUES (337,'<span data-type="title" id=toc-335>٥٦ </span>- {وَمَا لَهُمْ مِنْ نَاصِرِينَ}
INSERT INTO [b9617] VALUES (338,'<span data-type="title" id=toc-336>٥٧ </span>- {وَأَمَّا الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ فَيُوَفِّيهِمْ أُجُورَهُمْ وَاللَّهُ لا يُحِبُّ الظَّالِمِينَ}
INSERT INTO [b9617] VALUES (339,'<span data-type="title" id=toc-337>٥٨ </span>- {ذَلِكَ نَتْلُوهُ عَلَيْكَ مِنَ الآيَاتِ}
INSERT INTO [b9617] VALUES (340,'<span data-type="title" id=toc-338>٥٩ </span>- {كَمَثَلِ آدَمَ خَلَقَهُ مِنْ تُرَابٍ ثُمَّ قَالَ لَهُ كُنْ فَيَكُونُ}
INSERT INTO [b9617] VALUES (341,'<span data-type="title" id=toc-339>٦٠ </span>- {الْحَقُّ مِنْ رَبِّكَ فَلا تَكُنْ مِنَ الْمُمْتَرِينَ}
INSERT INTO [b9617] VALUES (342,'<span data-type="title" id=toc-340>٦١ </span>- {فَمَنْ حَاجَّكَ فِيهِ مِنْ بَعْدِ مَا جَاءَكَ مِنَ الْعِلْمِ فَقُلْ تَعَالَوْا نَدْعُ أَبْنَاءَنَا وَأَبْنَاءَكُمْ وَنِسَاءَنَا وَنِسَاءَكُمْ وَأَنْفُسَنَا وَأَنْفُسَكُمْ ثُمَّ نَبْتَهِلْ فَنَجْعَلْ لَعْنَتَ اللَّهِ عَلَى الْكَاذِبِينَ}
INSERT INTO [b9617] VALUES (343,'<span data-type="title" id=toc-341>٦٢ </span>- {إِنَّ هَذَا لَهُوَ الْقَصَصُ الْحَقُّ وَمَا مِنْ إِلَهٍ إِلا اللَّهُ وَإِنَّ اللَّهَ لَهُوَ الْعَزِيزُ الْحَكِيمُ}
INSERT INTO [b9617] VALUES (344,'<span data-type="title" id=toc-342>٦٤ </span>- {قُلْ يَا أَهْلَ الْكِتَابِ تَعَالَوْا إِلَى كَلِمَةٍ سَوَاءٍ بَيْنَنَا وَبَيْنَكُمْ أَلا نَعْبُدَ إِلا اللَّهَ}
INSERT INTO [b9617] VALUES (345,'<span data-type="title" id=toc-343>٦٥ </span>- {يَا أَهْلَ الْكِتَابِ لِمَ تُحَاجُّونَ فِي إِبْرَاهِيمَ وَمَا أُنْزِلَتِ التَّوْرَاةُ وَالإِنْجِيلُ إِلا مِنْ بَعْدِهِ أَفَلا تَعْقِلُونَ}
INSERT INTO [b9617] VALUES (346,'<span data-type="title" id=toc-344>٦٦ </span>- {هَا أَنْتُمْ هَؤُلاءِ حَاجَجْتُمْ فِيمَا لَكُمْ بِهِ عِلْمٌ فَلِمَ تُحَاجُّونَ فِيمَا لَيْسَ لَكُمْ بِهِ عِلْمٌ وَاللَّهُ يَعْلَمُ وَأَنْتُمْ لا تَعْلَمُونَ}
INSERT INTO [b9617] VALUES (347,'<span data-type="title" id=toc-345>٦٧ </span>- {مَا كَانَ إِبْرَاهِيمُ يَهُودِيًّا وَلا نَصْرَانِيًّا وَلَكِنْ كَانَ حَنِيفًا مُسْلِمًا}
INSERT INTO [b9617] VALUES (348,'<span data-type="title" id=toc-346>٦٩ </span>- {وَدَّتْ طَائِفَةٌ مِنْ أَهْلِ الْكِتَابِ لَوْ يُضِلُّونَكُمْ وَمَا يُضِلُّونَ إِلا أَنْفُسَهُمْ وَمَا يَشْعُرُونَ} ⦗١٢٥⦘
INSERT INTO [b9617] VALUES (349,'<span data-type="title" id=toc-347>٧٠ </span>- {لِمَ تَكْفُرُونَ بِآيَاتِ اللَّهِ وَأَنْتُمْ تَشْهَدُونَ}
INSERT INTO [b9617] VALUES (350,'<span data-type="title" id=toc-348>٧١ </span>- {لِمَ تَلْبِسُونَ الْحَقَّ بِالْبَاطِلِ وَتَكْتُمُونَ الْحَقَّ وَأَنْتُمْ تَعْلَمُونَ}
INSERT INTO [b9617] VALUES (351,'<span data-type="title" id=toc-349>٧٢ </span>- {آمِنُوا بِالَّذِي أُنْزِلَ عَلَى الَّذِينَ آمَنُوا وَجْهَ النَّهَارِ وَاكْفُرُوا آخِرَهُ لَعَلَّهُمْ يَرْجِعُونَ}
INSERT INTO [b9617] VALUES (352,'<span data-type="title" id=toc-350>٧٣ </span>- {وَلا تُؤْمِنُوا إِلا لِمَنْ تَبِعَ دِينَكُمْ قُلْ إِنَّ الْهُدَى هُدَى اللَّهِ أَنْ يُؤْتَى أَحَدٌ مِثْلَ مَا أُوتِيتُمْ أَوْ يُحَاجُّوكُمْ عِنْدَ رَبِّكُمْ}
INSERT INTO [b9617] VALUES (353,'<span data-type="title" id=toc-351>٧٤ </span>- {يَخْتَصُّ بِرَحْمَتِهِ مَنْ يَشَاءُ وَاللَّهُ ذُو الْفَضْلِ الْعَظِيمِ} ⦗١٢٦⦘
INSERT INTO [b9617] VALUES (354,'<span data-type="title" id=toc-352>٧٥ </span>- {وَمِنْ أَهْلِ الْكِتَابِ مَنْ إِنْ تَأْمَنْهُ بِقِنْطَارٍ يُؤَدِّهِ إِلَيْكَ وَمِنْهُمْ مَنْ إِنْ تَأْمَنْهُ بِدِينَارٍ لا يُؤَدِّهِ إِلَيْكَ إِلا مَا دُمْتَ عَلَيْهِ قَائِمًا ذَلِكَ بِأَنَّهُمْ قَالُوا لَيْسَ عَلَيْنَا فِي الأُمِّيِّينَ سَبِيلٌ وَيَقُولُونَ عَلَى اللَّهِ الْكَذِبَ وَهُمْ يَعْلَمُونَ}
INSERT INTO [b9617] VALUES (355,'<span data-type="title" id=toc-353>٧٦ </span>- {بَلَى مَنْ أَوْفَى بِعَهْدِهِ وَاتَّقَى فَإِنَّ اللَّهَ يُحِبُّ الْمُتَّقِينَ}
INSERT INTO [b9617] VALUES (356,'<span data-type="title" id=toc-354>٧٧ </span>- {إِنَّ الَّذِينَ يَشْتَرُونَ بِعَهْدِ اللَّهِ وَأَيْمَانِهِمْ ثَمَنًا قَلِيلا أُولَئِكَ لا خَلاقَ لَهُمْ فِي الآخِرَةِ وَلا يُكَلِّمُهُمُ اللَّهُ وَلا يَنْظُرُ إِلَيْهِمْ يَوْمَ الْقِيَامَةِ وَلا يُزَكِّيهِمْ وَلَهُمْ عَذَابٌ أَلِيمٌ}
INSERT INTO [b9617] VALUES (357,'<span data-type="title" id=toc-355>٧٨ </span>- {وَإِنَّ مِنْهُمْ لَفَرِيقًا يَلْوُونَ أَلْسِنَتَهُمْ بِالْكِتَابِ لِتَحْسَبُوهُ مِنَ الْكِتَابِ وَمَا هُوَ ⦗١٢٧⦘ مِنَ الْكِتَابِ وَيَقُولُونَ هُوَ مِنْ عِنْدِ اللَّهِ وَمَا هُوَ مِنْ عِنْدِ اللَّهِ وَيَقُولُونَ عَلَى اللَّهِ الْكَذِبَ وَهُمْ يَعْلَمُونَ}
INSERT INTO [b9617] VALUES (358,'<span data-type="title" id=toc-356>٧٩ </span>- {مَا كَانَ لِبَشَرٍ أَنْ يُؤْتِيَهُ اللَّهُ الْكِتَابَ وَالْحُكْمَ وَالنُّبُوَّةَ ثُمَّ يَقُولَ لِلنَّاسِ كُونُوا عِبَادًا لِي مِنْ دُونِ اللَّهِ وَلَكِنْ كُونُوا رَبَّانِيِّينَ بِمَا كُنْتُمْ تُعَلِّمُونَ الْكِتَابَ}
INSERT INTO [b9617] VALUES (359,'<span data-type="title" id=toc-357>٨٠ </span>- {وَلا يَأْمُرَكُمْ أَنْ تَتَّخِذُوا الْمَلائِكَةَ وَالنَّبِيِّينَ أَرْبَابًا أَيَأْمُرُكُمْ بِالْكُفْرِ بَعْدَ إِذْ أَنْتُمْ مُسْلِمُونَ}
INSERT INTO [b9617] VALUES (360,'<span data-type="title" id=toc-358>٨١ </span>- {وَإِذْ أَخَذَ اللَّهُ مِيثَاقَ النَّبِيِّينَ لَمَا آتَيْتُكُمْ مِنْ كِتَابٍ وَحِكْمَةٍ ثُمَّ جَاءَكُمْ','1','127',NULL,NULL,'0');
INSERT INTO [b9617] VALUES (361,'رَسُولٌ مُصَدِّقٌ لِمَا مَعَكُمْ لَتُؤْمِنُنَّ بِهِ وَلَتَنْصُرُنَّهُ قَالَ أَأَقْرَرْتُمْ وَأَخَذْتُمْ عَلَى ذَلِكُمْ إِصْرِي قَالُوا أَقْرَرْنَا قَالَ فَاشْهَدُوا وَأَنَا مَعَكُمْ مِنَ الشَّاهِدِينَ}
INSERT INTO [b9617] VALUES (362,'<span data-type="title" id=toc-359>٨٣ </span>- {أَفَغَيْرَ دِينِ اللَّهِ يَبْغُونَ وَلَهُ أَسْلَمَ مَنْ فِي السَّمَاوَاتِ وَالأَرْضِ طَوْعًا وَكَرْهًا وَإِلَيْهِ يُرْجَعُونَ}
INSERT INTO [b9617] VALUES (363,'<span data-type="title" id=toc-360>٨٤ </span>- {لا نُفَرِّقُ بَيْنَ أَحَدٍ مِنْهُمْ وَنَحْنُ لَهُ مُسْلِمُونَ}
INSERT INTO [b9617] VALUES (364,'<span data-type="title" id=toc-361>٨٥ </span>- {وَمَنْ يَبْتَغِ غَيْرَ الإِسْلامِ دِينًا فَلَنْ يُقْبَلَ مِنْهُ وَهُوَ فِي الآخِرَةِ مِنَ الْخَاسِرِينَ}
INSERT INTO [b9617] VALUES (365,'<span data-type="title" id=toc-362>٨٦ </span>- {كَيْفَ يَهْدِي اللَّهُ قَوْمًا كَفَرُوا بَعْدَ إِيمَانِهِمْ وَشَهِدُوا أَنَّ الرَّسُولَ حَقٌّ وَجَاءَهُمُ الْبَيِّنَاتُ وَاللَّهُ لا يَهْدِي الْقَوْمَ الظَّالِمِينَ}
INSERT INTO [b9617] VALUES (366,'<span data-type="title" id=toc-363>٨٧ </span>- {أُولَئِكَ جَزَاؤُهُمْ أَنَّ عَلَيْهِمْ لَعْنَةَ اللَّهِ وَالْمَلائِكَةِ وَالنَّاسِ أَجْمَعِينَ}
INSERT INTO [b9617] VALUES (367,'<span data-type="title" id=toc-364>٨٨ </span>- {خَالِدِينَ فِيهَا لا يُخَفَّفُ عَنْهُمُ الْعَذَابُ وَلا هُمْ يُنْظَرُونَ}
INSERT INTO [b9617] VALUES (368,'<span data-type="title" id=toc-365>٩١ </span>- {إِنَّ الَّذِينَ كَفَرُوا وَمَاتُوا وَهُمْ كُفَّارٌ فَلَنْ يُقْبَلَ مِنْ أَحَدِهِمْ مِلْءُ الأَرْضِ ذَهَبًا وَلَوِ افْتَدَى بِهِ أُولَئِكَ لَهُمْ عَذَابٌ أَلِيمٌ وَمَا لَهُمْ مِنْ نَاصِرِينَ}
INSERT INTO [b9617] VALUES (369,'<span data-type="title" id=toc-366>٩٢ </span>- {لَنْ تَنَالُوا الْبِرَّ حَتَّى تُنْفِقُوا مِمَّا تُحِبُّونَ وَمَا تُنْفِقُوا مِنْ شَيْءٍ فَإِنَّ اللَّهَ بِهِ عَلِيمٌ}
INSERT INTO [b9617] VALUES (370,'<span data-type="title" id=toc-367>٩٣ </span>- {كُلُّ الطَّعَامِ كَانَ حِلا لِبَنِي إِسْرَائِيلَ إِلا مَا حَرَّمَ إِسْرَائِيلُ عَلَى نَفْسِهِ مِنْ قَبْلِ أَنْ تُنَزَّلَ التَّوْرَاةُ قُلْ فَأْتُوا بِالتَّوْرَاةِ فَاتْلُوهَا إِنْ كُنْتُمْ صَادِقِينَ}
INSERT INTO [b9617] VALUES (371,'<span data-type="title" id=toc-368>٩٤ </span>- {فَأُولَئِكَ هُمُ الظَّالِمُونَ}
INSERT INTO [b9617] VALUES (372,'<span data-type="title" id=toc-369>٩٥ </span>- {قُلْ صَدَقَ اللَّهُ فَاتَّبِعُوا مِلَّةَ إِبْرَاهِيمَ حَنِيفًا وَمَا كَانَ مِنَ الْمُشْرِكِينَ}
INSERT INTO [b9617] VALUES (373,'<span data-type="title" id=toc-370>٩٦ </span>- {إِنَّ أَوَّلَ بَيْتٍ وُضِعَ لِلنَّاسِ لَلَّذِي بِبَكَّةَ مُبَارَكًا وَهُدًى لِلْعَالَمِينَ}
INSERT INTO [b9617] VALUES (374,'<span data-type="title" id=toc-371>٩٧ </span>- {فِيهِ آيَاتٌ بَيِّنَاتٌ مَقَامُ إِبْرَاهِيمَ وَمَنْ دَخَلَهُ كَانَ آمِنًا وَلِلَّهِ عَلَى النَّاسِ حِجُّ الْبَيْتِ ⦗١٣٢⦘ مَنِ اسْتَطَاعَ إِلَيْهِ سَبِيلا}
INSERT INTO [b9617] VALUES (375,'<span data-type="title" id=toc-372>٩٨ </span>- {قُلْ يَا أَهْلَ الْكِتَابِ لِمَ تَكْفُرُونَ بِآيَاتِ اللَّهِ وَاللَّهُ شَهِيدٌ عَلَى مَا تَعْمَلُونَ}
INSERT INTO [b9617] VALUES (376,'<span data-type="title" id=toc-373>٩٩ </span>- {لِمَ تَصُدُّونَ عَنْ سَبِيلِ اللَّهِ مَنْ آمَنَ تَبْغُونَهَا عِوَجًا وَأَنْتُمْ شُهَدَاءُ وَمَا اللَّهُ بِغَافِلٍ عَمَّا تَعْمَلُونَ}
INSERT INTO [b9617] VALUES (377,'<span data-type="title" id=toc-374>١٠٠ </span>- {يَا أَيُّهَا الَّذِينَ آمَنُوا إِنْ تُطِيعُوا فَرِيقًا مِنَ الَّذِينَ أُوتُوا الْكِتَابَ يَرُدُّوكُمْ بَعْدَ إِيمَانِكُمْ كَافِرِينَ}
INSERT INTO [b9617] VALUES (378,'<span data-type="title" id=toc-375>١٠١ </span>- {وَكَيْفَ تَكْفُرُونَ وَأَنْتُمْ تُتْلَى عَلَيْكُمْ آيَاتُ اللَّهِ وَفِيكُمْ رَسُولُهُ وَمَنْ يَعْتَصِمْ بِاللَّهِ فَقَدْ هُدِيَ إِلَى صِرَاطٍ مُسْتَقِيمٍ}
INSERT INTO [b9617] VALUES (379,'<span data-type="title" id=toc-376>١٠٢ </span>- {يَا أَيُّهَا الَّذِينَ آمَنُوا اتَّقُوا اللَّهَ حَقَّ تُقَاتِهِ وَلا تَمُوتُنَّ إِلا وَأَنْتُمْ مُسْلِمُونَ}
INSERT INTO [b9617] VALUES (380,'<span data-type="title" id=toc-377>١٠٣ </span>- {وَاذْكُرُوا نِعْمَتَ اللَّهِ عَلَيْكُمْ إِذْ كُنْتُمْ أَعْدَاءً فَأَلَّفَ بَيْنَ قُلُوبِكُمْ فَأَصْبَحْتُمْ بِنِعْمَتِهِ إِخْوَانًا وَكُنْتُمْ عَلَى شَفَا حُفْرَةٍ مِنَ النَّارِ فَأَنْقَذَكُمْ مِنْهَا كَذَلِكَ يُبَيِّنُ اللَّهُ لَكُمْ آيَاتِهِ لَعَلَّكُمْ تَهْتَدُونَ}
INSERT INTO [b9617] VALUES (381,'<span data-type="title" id=toc-378>١٠٤ </span>- {وَأُولَئِكَ هُمُ الْمُفْلِحُونَ}
INSERT INTO [b9617] VALUES (382,'<span data-type="title" id=toc-379>١٠٥ </span>- {وَأُولَئِكَ لَهُمْ عَذَابٌ عَظِيمٌ}
INSERT INTO [b9617] VALUES (383,'<span data-type="title" id=toc-380>١٠٦ </span>- {يَوْمَ تَبْيَضُّ وُجُوهٌ وَتَسْوَدُّ وُجُوهٌ فَأَمَّا الَّذِينَ اسْوَدَّتْ وُجُوهُهُمْ أَكَفَرْتُمْ بَعْدَ إِيمَانِكُمْ فَذُوقُوا الْعَذَابَ بِمَا كُنْتُمْ تَكْفُرُونَ}
INSERT INTO [b9617] VALUES (384,'<span data-type="title" id=toc-381>١٠٧ </span>- {وَأَمَّا الَّذِينَ ابْيَضَّتْ وُجُوهُهُمْ فَفِي رَحْمَةِ اللَّهِ هُمْ فِيهَا خَالِدُونَ}
INSERT INTO [b9617] VALUES (385,'<span data-type="title" id=toc-382>١٠٨ </span>- {تِلْكَ آيَاتُ اللَّهِ نَتْلُوهَا عَلَيْكَ بِالْحَقِّ وَمَا اللَّهُ يُرِيدُ ظُلْمًا لِلْعَالَمِينَ}
INSERT INTO [b9617] VALUES (386,'<span data-type="title" id=toc-383>١١٠ </span>- {كُنْتُمْ خَيْرَ أُمَّةٍ أُخْرِجَتْ لِلنَّاسِ تَأْمُرُونَ بِالْمَعْرُوفِ وَتَنْهَوْنَ عَنِ الْمُنْكَرِ وَتُؤْمِنُونَ بِاللَّهِ وَلَوْ آمَنَ أَهْلُ الْكِتَابِ لَكَانَ خَيْرًا لَهُمْ مِنْهُمُ الْمُؤْمِنُونَ وَأَكْثَرُهُمُ الْفَاسِقُونَ}
INSERT INTO [b9617] VALUES (387,'<span data-type="title" id=toc-384>١١١ </span>- {لَنْ يَضُرُّوكُمْ إِلا أَذًى وَإِنْ يُقَاتِلُوكُمْ يُوَلُّوكُمُ الأَدْبَارَ ثُمَّ لا يُنْصَرُونَ}
INSERT INTO [b9617] VALUES (388,'<span data-type="title" id=toc-385>١١٢ </span>- {ضُرِبَتْ عَلَيْهِمُ الذِّلَّةُ أَيْنَ مَا ثُقِفُوا إِلا بِحَبْلٍ مِنَ اللَّهِ وَحَبْلٍ مِنَ النَّاسِ وَبَاءُوا بِغَضَبٍ مِنَ اللَّهِ وَضُرِبَتْ عَلَيْهِمُ الْمَسْكَنَةُ ذَلِكَ بِأَنَّهُمْ كَانُوا يَكْفُرُونَ بِآيَاتِ اللَّهِ وَيَقْتُلُونَ الأَنْبِيَاءَ بِغَيْرِ حَقٍّ ذَلِكَ بِمَا عَصَوْا وَكَانُوا يَعْتَدُونَ}
INSERT INTO [b9617] VALUES (389,'<span data-type="title" id=toc-386>١١٣ </span>- {لَيْسُوا سَوَاءً مِنْ أَهْلِ الْكِتَابِ أُمَّةٌ قَائِمَةٌ يَتْلُونَ آيَاتِ اللَّهِ آنَاءَ اللَّيْلِ وَهُمْ يَسْجُدُونَ}
INSERT INTO [b9617] VALUES (390,'<span data-type="title" id=toc-387>١١٤ </span>- {يُؤْمِنُونَ بِاللَّهِ وَالْيَوْمِ الآخِرِ وَيَأْمُرُونَ بِالْمَعْرُوفِ وَيَنْهَوْنَ عَنِ الْمُنْكَرِ وَيُسَارِعُونَ فِي الْخَيْرَاتِ وَأُولَئِكَ مِنَ الصَّالِحِينَ}
INSERT INTO [b9617] VALUES (391,'<span data-type="title" id=toc-388>١١٥ </span>- {وَمَا يَفْعَلُوا مِنْ خَيْرٍ فَلَنْ يُكْفَرُوهُ}
INSERT INTO [b9617] VALUES (392,'<span data-type="title" id=toc-389>١١٦ </span>- {إِنَّ الَّذِينَ كَفَرُوا لَنْ تُغْنِيَ عَنْهُمْ أَمْوَالُهُمْ وَلا أَوْلادُهُمْ مِنَ اللَّهِ شَيْئًا وَأُولَئِكَ أَصْحَابُ النَّارِ هُمْ فِيهَا خَالِدُونَ}
INSERT INTO [b9617] VALUES (393,'<span data-type="title" id=toc-390>١١٧ </span>- {مَثَلُ مَا يُنْفِقُونَ فِي هَذِهِ الْحَيَاةِ الدُّنْيَا كَمَثَلِ رِيحٍ فِيهَا صِرٌّ أَصَابَتْ حَرْثَ قَوْمٍ ظَلَمُوا أَنْفُسَهُمْ فَأَهْلَكَتْهُ وَمَا ظَلَمَهُمُ اللَّهُ وَلَكِنْ أَنْفُسَهُمْ يَظْلِمُونَ}
INSERT INTO [b9617] VALUES (394,'<span data-type="title" id=toc-391>١١٨ </span>- {لا تَتَّخِذُوا بِطَانَةً مِنْ دُونِكُمْ لا يَأْلُونَكُمْ خَبَالا وَدُّوا مَا عَنِتُّمْ قَدْ بَدَتِ الْبَغْضَاءُ مِنْ أَفْوَاهِهِمْ وَمَا تُخْفِي صُدُورُهُمْ أَكْبَرُ قَدْ بَيَّنَّا لَكُمُ الآيَاتِ إِنْ كُنْتُمْ تَعْقِلُونَ} ⦗١٣٨⦘
INSERT INTO [b9617] VALUES (395,'<span data-type="title" id=toc-392>١١٩ </span>- {هَا أَنْتُمْ أُولاءِ تُحِبُّونَهُمْ وَلا يُحِبُّونَكُمْ وَتُؤْمِنُونَ بِالْكِتَابِ كُلِّهِ وَإِذَا لَقُوكُمْ قَالُوا آمَنَّا وَإِذَا خَلَوْا عَضُّوا عَلَيْكُمُ الأَنَامِلَ مِنَ الْغَيْظِ قُلْ مُوتُوا بِغَيْظِكُمْ إِنَّ اللَّهَ عَلِيمٌ بِذَاتِ الصُّدُورِ}
INSERT INTO [b9617] VALUES (396,'<span data-type="title" id=toc-393>١٢٠ </span>- {وَإِنْ تَصْبِرُوا وَتَتَّقُوا لا يَضُرُّكُمْ كَيْدُهُمْ شَيْئًا}
INSERT INTO [b9617] VALUES (397,'<span data-type="title" id=toc-394>١٢١ </span>- {وَإِذْ غَدَوْتَ مِنْ أَهْلِكَ تُبَوِّئُ الْمُؤْمِنِينَ مَقَاعِدَ لِلْقِتَالِ}
INSERT INTO [b9617] VALUES (398,'<span data-type="title" id=toc-395>١٢٢ </span>- {إِذْ هَمَّتْ طَائِفَتَانِ مِنْكُمْ أَنْ تَفْشَلا وَاللَّهُ وَلِيُّهُمَا وَعَلَى اللَّهِ فَلْيَتَوَكَّلِ الْمُؤْمِنُونَ}
INSERT INTO [b9617] VALUES (399,'<span data-type="title" id=toc-396>١٢٣ </span>- {وَلَقَدْ نَصَرَكُمُ اللَّهُ بِبَدْرٍ وَأَنْتُمْ أَذِلَّةٌ فَاتَّقُوا اللَّهَ لَعَلَّكُمْ تَشْكُرُونَ}
INSERT INTO [b9617] VALUES (400,'<span data-type="title" id=toc-397>١٢٤ </span>- {إِذْ تَقُولُ لِلْمُؤْمِنِينَ أَلَنْ يَكْفِيَكُمْ أَنْ يُمِدَّكُمْ رَبُّكُمْ بِثَلاثَةِ آلافٍ مِنَ الْمَلائِكَةِ مُنْزَلِينَ}
INSERT INTO [b9617] VALUES (401,'<span data-type="title" id=toc-398>١٢٥ </span>- {بَلَى إِنْ تَصْبِرُوا وَتَتَّقُوا وَيَأْتُوكُمْ مِنْ فَوْرِهِمْ هَذَا يُمْدِدْكُمْ رَبُّكُمْ بِخَمْسَةِ آلافٍ مِنَ الْمَلائِكَةِ مُسَوِّمِينَ}
INSERT INTO [b9617] VALUES (402,'<span data-type="title" id=toc-399>١٢٦ </span>- {وَمَا جَعَلَهُ اللَّهُ إِلا بُشْرَى لَكُمْ وَلِتَطْمَئِنَّ قُلُوبُكُمْ بِهِ وَمَا النَّصْرُ إِلا مِنْ عِنْدِ اللَّهِ الْعَزِيزِ الْحَكِيمِ}
INSERT INTO [b9617] VALUES (403,'<span data-type="title" id=toc-400>١٢٧ </span>- {لِيَقْطَعَ طَرَفًا مِنَ الَّذِينَ كَفَرُوا أَوْ يَكْبِتَهُمْ فَيَنْقَلِبُوا خَائِبِينَ}
INSERT INTO [b9617] VALUES (404,'<span data-type="title" id=toc-401>١٢٨ </span>- {لَيْسَ لَكَ مِنَ الأَمْرِ شَيْءٌ أَوْ يَتُوبَ عَلَيْهِمْ أَوْ يُعَذِّبَهُمْ فَإِنَّهُمْ ظَالِمُونَ} ⦗١٤١⦘
INSERT INTO [b9617] VALUES (405,'<span data-type="title" id=toc-402>١٢٩ </span>- {يَغْفِرُ لِمَنْ يَشَاءُ وَيُعَذِّبُ مَنْ يَشَاءُ وَاللَّهُ غَفُورٌ رَحِيمٌ}
INSERT INTO [b9617] VALUES (406,'<span data-type="title" id=toc-403>١٣٠ </span>- {لا تَأْكُلُوا الرِّبَا أَضْعَافًا مُضَاعَفَةً وَاتَّقُوا اللَّهَ لَعَلَّكُمْ تُفْلِحُونَ}
INSERT INTO [b9617] VALUES (407,'<span data-type="title" id=toc-404>١٣١ </span>- {وَاتَّقُوا النَّارَ الَّتِي أُعِدَّتْ لِلْكَافِرِينَ}
INSERT INTO [b9617] VALUES (408,'<span data-type="title" id=toc-405>١٣٢ </span>- {وَأَطِيعُوا اللَّهَ وَالرَّسُولَ لَعَلَّكُمْ تُرْحَمُونَ}
INSERT INTO [b9617] VALUES (409,'<span data-type="title" id=toc-406>١٣٣ </span>- {وَسَارِعُوا إِلَى مَغْفِرَةٍ مِنْ رَبِّكُمْ وَجَنَّةٍ عَرْضُهَا السَّمَاوَاتُ وَالأَرْضُ أُعِدَّتْ لِلْمُتَّقِينَ}
INSERT INTO [b9617] VALUES (410,'<span data-type="title" id=toc-407>١٣٤ </span>- {الَّذِينَ يُنْفِقُونَ فِي السَّرَّاءِ وَالضَّرَّاءِ وَالْكَاظِمِينَ الْغَيْظَ وَالْعَافِينَ عَنِ النَّاسِ}
INSERT INTO [b9617] VALUES (411,'<span data-type="title" id=toc-408>١٣٥ </span>- {وَالَّذِينَ إِذَا فَعَلُوا فَاحِشَةً أَوْ ظَلَمُوا أَنْفُسَهُمْ ذَكَرُوا اللَّهَ فَاسْتَغْفَرُوا لِذُنُوبِهِمْ وَمَنْ يَغْفِرُ الذُّنُوبَ إِلا اللَّهُ وَلَمْ يُصِرُّوا عَلَى مَا فَعَلُوا وَهُمْ يَعْلَمُونَ}
INSERT INTO [b9617] VALUES (412,'<span data-type="title" id=toc-409>١٣٦ </span>- {أُولَئِكَ جَزَاؤُهُمْ مَغْفِرَةٌ مِنْ رَبِّهِمْ وَجَنَّاتٌ تَجْرِي مِنْ تَحْتِهَا الأَنْهَارُ خَالِدِينَ فِيهَا وَنِعْمَ أَجْرُ الْعَامِلِينَ}
INSERT INTO [b9617] VALUES (413,'<span data-type="title" id=toc-410>١٣٧ </span>- {فَسِيرُوا فِي الأَرْضِ فَانْظُرُوا كَيْفَ كَانَ عَاقِبَةُ الْمُكَذِّبِينَ}
INSERT INTO [b9617] VALUES (414,'<span data-type="title" id=toc-411>١٣٨ </span>- {هَذَا بَيَانٌ لِلنَّاسِ وَهُدًى وَمَوْعِظَةٌ لِلْمُتَّقِينَ}
INSERT INTO [b9617] VALUES (415,'<span data-type="title" id=toc-412>١٣٩ </span>- {وَأَنْتُمُ الأَعْلَوْنَ إِنْ كُنْتُمْ مُؤْمِنِينَ}
INSERT INTO [b9617] VALUES (416,'<span data-type="title" id=toc-413>١٤٠ </span>- {وَتِلْكَ الأَيَّامُ نُدَاوِلُهَا بَيْنَ النَّاسِ وَلِيَعْلَمَ اللَّهُ الَّذِينَ آمَنُوا وَيَتَّخِذَ مِنْكُمْ شُهَدَاءَ وَاللَّهُ لا يُحِبُّ الظَّالِمِينَ}
INSERT INTO [b9617] VALUES (417,'<span data-type="title" id=toc-414>١٤١ </span>- {وَلِيُمَحِّصَ اللَّهُ الَّذِينَ آمَنُوا}
INSERT INTO [b9617] VALUES (418,'<span data-type="title" id=toc-415>١٤٢ </span>- {أَمْ حَسِبْتُمْ أَنْ تَدْخُلُوا الْجَنَّةَ وَلَمَّا يَعْلَمِ اللَّهُ الَّذِينَ جَاهَدُوا مِنْكُمْ وَيَعْلَمَ ⦗١٤٤⦘ الصَّابِرِينَ}
INSERT INTO [b9617] VALUES (419,'<span data-type="title" id=toc-416>١٤٣ </span>- {وَلَقَدْ كُنْتُمْ تَمَنَّوْنَ الْمَوْتَ مِنْ قَبْلِ أَنْ تَلْقَوْهُ فَقَدْ رَأَيْتُمُوهُ وَأَنْتُمْ تَنْظُرُونَ}
INSERT INTO [b9617] VALUES (420,'<span data-type="title" id=toc-417>١٤٤ </span>- {وَمَا مُحَمَّدٌ إِلا رَسُولٌ قَدْ خَلَتْ مِنْ قَبْلِهِ الرُّسُلُ أَفَإِنْ مَاتَ أَوْ قُتِلَ انْقَلَبْتُمْ عَلَى أَعْقَابِكُمْ وَمَنْ يَنْقَلِبْ عَلَى عَقِبَيْهِ فَلَنْ يَضُرَّ اللَّهَ شَيْئًا وَسَيَجْزِي اللَّهُ الشَّاكِرِينَ}
INSERT INTO [b9617] VALUES (421,'<span data-type="title" id=toc-418>١٤٥ </span>- {وَمَا كَانَ لِنَفْسٍ أَنْ تَمُوتَ إِلا بِإِذْنِ اللَّهِ كِتَابًا مُؤَجَّلا وَمَنْ يُرِدْ ثَوَابَ الدُّنْيَا نُؤْتِهِ مِنْهَا وَمَنْ يُرِدْ ثَوَابَ الآخِرَةِ نُؤْتِهِ مِنْهَا وَسَنَجْزِي الشَّاكِرِينَ}
INSERT INTO [b9617] VALUES (422,'<span data-type="title" id=toc-419>١٤٦ </span>- {وَكَأَيِّنْ مِنْ نَبِيٍّ قَاتَلَ مَعَهُ رِبِّيُّونَ كَثِيرٌ فَمَا وَهَنُوا لِمَا أَصَابَهُمْ فِي سَبِيلِ اللَّهِ وَمَا ضَعُفُوا وَمَا اسْتَكَانُوا وَاللَّهُ يُحِبُّ الصَّابِرِينَ}
INSERT INTO [b9617] VALUES (423,'<span data-type="title" id=toc-420>١٤٧ </span>- {وَمَا كَانَ قَوْلَهُمْ إِلا أَنْ قَالُوا رَبَّنَا اغْفِرْ لَنَا ذُنُوبَنَا وَإِسْرَافَنَا فِي أَمْرِنَا}
INSERT INTO [b9617] VALUES (424,'<span data-type="title" id=toc-421>١٤٨ </span>- {فَآتَاهُمُ اللَّهُ ثَوَابَ الدُّنْيَا}
INSERT INTO [b9617] VALUES (425,'<span data-type="title" id=toc-422>١٤٩ </span>- {يَا أَيُّهَا الَّذِينَ آمَنُوا إِنْ تُطِيعُوا الَّذِينَ كَفَرُوا يَرُدُّوكُمْ عَلَى أَعْقَابِكُمْ فَتَنْقَلِبُوا خَاسِرِينَ}
INSERT INTO [b9617] VALUES (426,'<span data-type="title" id=toc-423>١٥٠ </span>- {بَلِ اللَّهُ مَوْلاكُمْ وَهُوَ خَيْرُ النَّاصِرِينَ}
INSERT INTO [b9617] VALUES (427,'<span data-type="title" id=toc-424>١٥١ </span>- {سَنُلْقِي فِي قُلُوبِ الَّذِينَ كَفَرُوا الرُّعْبَ بِمَا أَشْرَكُوا بِاللَّهِ مَا لَمْ يُنَزِّلْ بِهِ سُلْطَانًا وَمَأْوَاهُمُ النَّارُ وَبِئْسَ مَثْوَى الظَّالِمِينَ}
INSERT INTO [b9617] VALUES (428,'<span data-type="title" id=toc-425>١٥٢ </span>- {وَلَقَدْ صَدَقَكُمُ اللَّهُ وَعْدَهُ إِذْ تَحُسُّونَهُمْ بِإِذْنِهِ حَتَّى ⦗١٤٧⦘ إِذَا فَشِلْتُمْ وَتَنَازَعْتُمْ فِي الأَمْرِ وَعَصَيْتُمْ مِنْ بَعْدِ مَا أَرَاكُمْ مَا تُحِبُّونَ مِنْكُمْ مَنْ يُرِيدُ الدُّنْيَا وَمِنْكُمْ مَنْ يُرِيدُ الآخِرَةَ ثُمَّ صَرَفَكُمْ عَنْهُمْ لِيَبْتَلِيَكُمْ وَلَقَدْ عَفَا عَنْكُمْ وَاللَّهُ ذُو فَضْلٍ عَلَى الْمُؤْمِنِينَ}
INSERT INTO [b9617] VALUES (429,'<span data-type="title" id=toc-426>١٥٣ </span>- {إِذْ تُصْعِدُونَ وَلا تَلْوُونَ عَلَى أَحَدٍ وَالرَّسُولُ يَدْعُوكُمْ فِي أُخْرَاكُمْ فَأَثَابَكُمْ غَمًّا بِغَمٍّ لِكَيْلا تَحْزَنُوا عَلَى مَا فَاتَكُمْ وَلا مَا أَصَابَكُمْ وَاللَّهُ خَبِيرٌ بِمَا تَعْمَلُونَ}
INSERT INTO [b9617] VALUES (430,'<span data-type="title" id=toc-427>١٥٤ </span>- {ثُمَّ أَنْزَلَ عَلَيْكُمْ مِنْ بَعْدِ الْغَمِّ أَمَنَةً نُعَاسًا يَغْشَى طَائِفَةً مِنْكُمْ وَطَائِفَةٌ قَدْ أَهَمَّتْهُمْ أَنْفُسُهُمْ يَظُنُّونَ بِاللَّهِ غَيْرَ الْحَقِّ ظَنَّ الْجَاهِلِيَّةِ يَقُولُونَ هَلْ لَنَا مِنَ الأَمْرِ مِنْ شَيْءٍ قُلْ إِنَّ الأَمْرَ كُلَّهُ لِلَّهِ يُخْفُونَ فِي أَنْفُسِهِمْ مَا لا يُبْدُونَ لَكَ يَقُولُونَ لَوْ كَانَ لَنَا مِنَ الأَمْرِ شَيْءٌ مَا قُتِلْنَا','1','147',NULL,NULL,'0');
INSERT INTO [b9617] VALUES (431,'هَاهُنَا قُلْ لَوْ كُنْتُمْ فِي بُيُوتِكُمْ لَبَرَزَ الَّذِينَ كُتِبَ عَلَيْهِمُ الْقَتْلُ إِلَى مَضَاجِعِهِمْ وَلِيَبْتَلِيَ اللَّهُ مَا فِي صُدُورِكُمْ وَلِيُمَحِّصَ مَا فِي قُلُوبِكُمْ}
INSERT INTO [b9617] VALUES (432,'<span data-type="title" id=toc-428>١٥٥ </span>- {إِنَّ الَّذِينَ تَوَلَّوْا مِنْكُمْ يَوْمَ الْتَقَى الْجَمْعَانِ إِنَّمَا اسْتَزَلَّهُمُ الشَّيْطَانُ بِبَعْضِ مَا كَسَبُوا وَلَقَدْ عَفَا اللَّهُ عَنْهُمْ إِنَّ اللَّهَ غَفُورٌ حَلِيمٌ} ⦗١٤٩⦘
INSERT INTO [b9617] VALUES (433,'<span data-type="title" id=toc-429>١٥٦ </span>- {وَقَالُوا لإِخْوَانِهِمْ إِذَا ضَرَبُوا فِي الأَرْضِ أَوْ كَانُوا غُزًّى لَوْ كَانُوا عِنْدَنَا مَا مَاتُوا وَمَا قُتِلُوا لِيَجْعَلَ اللَّهُ ذَلِكَ حَسْرَةً فِي قُلُوبِهِمْ}
INSERT INTO [b9617] VALUES (434,'<span data-type="title" id=toc-430>١٥٧ </span>- {وَلَئِنْ قُتِلْتُمْ فِي سَبِيلِ اللَّهِ أَوْ مُتُّمْ لَمَغْفِرَةٌ مِنَ اللَّهِ وَرَحْمَةٌ خَيْرٌ مِمَّا يَجْمَعُونَ}
INSERT INTO [b9617] VALUES (435,'<span data-type="title" id=toc-431>١٥٨ </span>- {وَلَئِنْ مُتُّمْ أَوْ قُتِلْتُمْ لإِلَى اللَّهِ تُحْشَرُونَ}
INSERT INTO [b9617] VALUES (436,'<span data-type="title" id=toc-432>١٥٩ </span>- {فَبِمَا رَحْمَةٍ مِنَ اللَّهِ لِنْتَ لَهُمْ وَلَوْ كُنْتَ فَظًّا غَلِيظَ الْقَلْبِ لانْفَضُّوا مِنْ حَوْلِكَ فَاعْفُ عَنْهُمْ وَاسْتَغْفِرْ لَهُمْ وَشَاوِرْهُمْ فِي الأَمْرِ فَإِذَا عَزَمْتَ فَتَوَكَّلْ عَلَى اللَّهِ}
INSERT INTO [b9617] VALUES (437,'<span data-type="title" id=toc-433>١٦٠ </span>- {وَإِنْ يَخْذُلْكُمْ فَمَنْ ذَا الَّذِي يَنْصُرُكُمْ مِنْ بَعْدِهِ وَعَلَى اللَّهِ فَلْيَتَوَكَّلِ الْمُؤْمِنُونَ}
INSERT INTO [b9617] VALUES (438,'<span data-type="title" id=toc-434>١٦١ </span>- {وَمَا كَانَ لِنَبِيٍّ أَنْ يَغُلَّ وَمَنْ يَغْلُلْ يَأْتِ بِمَا غَلَّ يَوْمَ الْقِيَامَةِ ثُمَّ تُوَفَّى كُلُّ نَفْسٍ مَا كَسَبَتْ وَهُمْ لا يُظْلَمُونَ}
INSERT INTO [b9617] VALUES (439,'<span data-type="title" id=toc-435>١٦٢ </span>- {أَفَمَنِ اتَّبَعَ رِضْوَانَ اللَّهِ كَمَنْ بَاءَ بِسَخَطٍ مِنَ اللَّهِ وَمَأْوَاهُ جَهَنَّمُ وَبِئْسَ الْمَصِيرُ}
INSERT INTO [b9617] VALUES (440,'<span data-type="title" id=toc-436>١٦٣ </span>- {هُمْ دَرَجَاتٌ عِنْدَ اللَّهِ}
INSERT INTO [b9617] VALUES (441,'<span data-type="title" id=toc-437>١٦٤ </span>- {لَقَدْ مَنَّ اللَّهُ عَلَى الْمُؤْمِنِينَ إِذْ بَعَثَ فِيهِمْ رَسُولا مِنْ أَنْفُسِهِمْ يَتْلُو عَلَيْهِمْ آيَاتِهِ وَيُزَكِّيهِمْ وَيُعَلِّمُهُمُ الْكِتَابَ وَالْحِكْمَةَ وَإِنْ كَانُوا مِنْ قَبْلُ لَفِي ضَلالٍ مُبِينٍ}
INSERT INTO [b9617] VALUES (442,'<span data-type="title" id=toc-438>١٦٥ </span>- {أَوَلَمَّا أَصَابَتْكُمْ مُصِيبَةٌ قَدْ أَصَبْتُمْ مِثْلَيْهَا قُلْتُمْ أَنَّى هَذَا}
INSERT INTO [b9617] VALUES (443,'<span data-type="title" id=toc-439>١٦٦ </span>- {وَمَا أَصَابَكُمْ يَوْمَ الْتَقَى الْجَمْعَانِ فَبِإِذْنِ اللَّهِ وَلِيَعْلَمَ الْمُؤْمِنِينَ}
INSERT INTO [b9617] VALUES (444,'<span data-type="title" id=toc-440>١٦٧ </span>- {وَقِيلَ لَهُمْ تَعَالَوْا قَاتِلُوا فِي سَبِيلِ اللَّهِ أَوِ ادْفَعُوا قَالُوا لَوْ نَعْلَمُ قِتَالا لاتَّبَعْنَاكُمْ هُمْ لِلْكُفْرِ يَوْمَئِذٍ أَقْرَبُ مِنْهُمْ لِلإِيمَانِ يَقُولُونَ بِأَفْوَاهِهِمْ مَا لَيْسَ فِي قُلُوبِهِمْ}
INSERT INTO [b9617] VALUES (445,'<span data-type="title" id=toc-441>١٦٨ </span>- {قُلْ فَادْرَءُوا عَنْ أَنْفُسِكُمُ الْمَوْتَ إِنْ كُنْتُمْ صَادِقِينَ}
INSERT INTO [b9617] VALUES (446,'<span data-type="title" id=toc-442>١٦٩ </span>- {وَلا تَحْسَبَنَّ الَّذِينَ قُتِلُوا فِي سَبِيلِ اللَّهِ أَمْوَاتًا بَلْ أَحْيَاءٌ عِنْدَ رَبِّهِمْ يُرْزَقُونَ}
INSERT INTO [b9617] VALUES (447,'<span data-type="title" id=toc-443>١٧٠ </span>- {فَرِحِينَ بِمَا آتَاهُمُ اللَّهُ مِنْ فَضْلِهِ وَيَسْتَبْشِرُونَ بِالَّذِينَ لَمْ يَلْحَقُوا بِهِمْ مِنْ خَلْفِهِمْ أَلا خَوْفٌ عَلَيْهِمْ وَلا هُمْ يَحْزَنُونَ}
INSERT INTO [b9617] VALUES (448,'<span data-type="title" id=toc-444>١٧١ </span>- {يَسْتَبْشِرُونَ بِنِعْمَةٍ مِنَ اللَّهِ وَفَضْلٍ وَأَنَّ اللَّهَ لا يُضِيعُ أَجْرَ الْمُؤْمِنِينَ}
INSERT INTO [b9617] VALUES (449,'<span data-type="title" id=toc-445>١٧٢ </span>- {لِلَّذِينَ أَحْسَنُوا مِنْهُمْ وَاتَّقَوْا أَجْرٌ عَظِيمٌ}
INSERT INTO [b9617] VALUES (450,'<span data-type="title" id=toc-446>١٧٣ </span>- {قَالَ لَهُمُ النَّاسُ إِنَّ النَّاسَ قَدْ جَمَعُوا لَكُمْ فَاخْشَوْهُمْ فَزَادَهُمْ إِيمَانًا ⦗١٥٤⦘ وَقَالُوا حَسْبُنَا اللَّهُ وَنِعْمَ الْوَكِيلُ}
INSERT INTO [b9617] VALUES (451,'<span data-type="title" id=toc-447>١٧٤ </span>- {فَانْقَلَبُوا بِنِعْمَةٍ مِنَ اللَّهِ وَفَضْلٍ لَمْ يَمْسَسْهُمْ سُوءٌ}
INSERT INTO [b9617] VALUES (452,'<span data-type="title" id=toc-448>١٧٥ </span>- {إِنَّمَا ذَلِكُمُ الشَّيْطَانُ يُخَوِّفُ أَوْلِيَاءَهُ فَلا تَخَافُوهُمْ وَخَافُونِ إِنْ كُنْتُمْ مُؤْمِنِينَ}
INSERT INTO [b9617] VALUES (453,'<span data-type="title" id=toc-449>١٧٦ </span>- {وَلا يَحْزُنْكَ الَّذِينَ يُسَارِعُونَ فِي الْكُفْرِ إِنَّهُمْ لَنْ يَضُرُّوا اللَّهَ شَيْئًا يُرِيدُ اللَّهُ أَلا يَجْعَلَ لَهُمْ حَظًّا فِي الآخِرَةِ وَلَهُمْ عَذَابٌ عَظِيمٌ}
INSERT INTO [b9617] VALUES (454,'<span data-type="title" id=toc-450>١٧٧ </span>- {لَنْ يَضُرُّوا اللَّهَ شَيْئًا وَلَهُمْ عَذَابٌ أَلِيمٌ}
INSERT INTO [b9617] VALUES (455,'<span data-type="title" id=toc-451>١٧٨ </span>- {وَلا يَحْسَبَنَّ الَّذِينَ كَفَرُوا أَنَّمَا نُمْلِي لَهُمْ خَيْرٌ لأَنْفُسِهِمْ إِنَّمَا نُمْلِي لَهُمْ لِيَزْدَادُوا إِثْمًا وَلَهُمْ عَذَابٌ مُهِينٌ}
INSERT INTO [b9617] VALUES (456,'<span data-type="title" id=toc-452>١٧٩ </span>- {مَا كَانَ اللَّهُ لِيَذَرَ الْمُؤْمِنِينَ عَلَى مَا أَنْتُمْ عَلَيْهِ}
INSERT INTO [b9617] VALUES (457,'<span data-type="title" id=toc-453>١٨٠ </span>- {وَلا يَحْسَبَنَّ الَّذِينَ يَبْخَلُونَ بِمَا آتَاهُمُ اللَّهُ مِنْ فَضْلِهِ هُوَ خَيْرًا لَهُمْ بَلْ هُوَ شَرٌّ لَهُمْ سَيُطَوَّقُونَ مَا بَخِلُوا بِهِ يَوْمَ الْقِيَامَةِ}
INSERT INTO [b9617] VALUES (458,'<span data-type="title" id=toc-454>١٨١ </span>- {سَنَكْتُبُ مَا قَالُوا وَقَتْلَهُمُ الأَنْبِيَاءَ بِغَيْرِ حَقٍّ}
INSERT INTO [b9617] VALUES (459,'<span data-type="title" id=toc-455>١٨٢ </span>- {ذَلِكَ بِمَا قَدَّمَتْ أَيْدِيكُمْ وَأَنَّ اللَّهَ لَيْسَ بِظَلامٍ لِلْعَبِيدِ}
INSERT INTO [b9617] VALUES (460,'<span data-type="title" id=toc-456>١٨٣ </span>- {إِنَّ اللَّهَ عَهِدَ إِلَيْنَا أَلا نُؤْمِنَ لِرَسُولٍ حَتَّى يَأْتِيَنَا بِقُرْبَانٍ تَأْكُلُهُ النَّارُ قُلْ قَدْ جَاءَكُمْ رُسُلٌ مِنْ قَبْلِي بِالْبَيِّنَاتِ وَبِالَّذِي قُلْتُمْ فَلِمَ قَتَلْتُمُوهُمْ إِنْ كُنْتُمْ صَادِقِينَ}
INSERT INTO [b9617] VALUES (461,'<span data-type="title" id=toc-457>١٨٤ </span>- {فَقَدْ كُذِّبَ رُسُلٌ مِنْ قَبْلِكَ جَاءُوا بِالْبَيِّنَاتِ}
INSERT INTO [b9617] VALUES (462,'<span data-type="title" id=toc-458>١٨٥ </span>- {وَإِنَّمَا تُوَفَّوْنَ أُجُورَكُمْ يَوْمَ الْقِيَامَةِ فَمَنْ زُحْزِحَ عَنِ النَّارِ وَأُدْخِلَ الْجَنَّةَ فَقَدْ فَازَ}
INSERT INTO [b9617] VALUES (463,'<span data-type="title" id=toc-459>١٨٦ </span>- {لَتُبْلَوُنَّ فِي أَمْوَالِكُمْ} ⦗١٥٨⦘
INSERT INTO [b9617] VALUES (464,'<span data-type="title" id=toc-460>١٨٧ </span>- {وَإِذْ أَخَذَ اللَّهُ مِيثَاقَ الَّذِينَ أُوتُوا الْكِتَابَ لَتُبَيِّنُنَّهُ لِلنَّاسِ وَلا تَكْتُمُونَهُ فَنَبَذُوهُ وَرَاءَ ظُهُورِهِمْ وَاشْتَرَوْا بِهِ ثَمَنًا قَلِيلا فَبِئْسَ مَا يَشْتَرُونَ}
INSERT INTO [b9617] VALUES (465,'<span data-type="title" id=toc-461>١٨٨ </span>- {لا تَحْسَبَنَّ الَّذِينَ يَفْرَحُونَ بِمَا أَتَوْا وَيُحِبُّونَ أَنْ يُحْمَدُوا بِمَا لَمْ يَفْعَلُوا فَلا تَحْسَبَنَّهُمْ بِمَفَازَةٍ مِنَ الْعَذَابِ وَلَهُمْ عَذَابٌ أَلِيمٌ}
INSERT INTO [b9617] VALUES (466,'<span data-type="title" id=toc-462>١٨٩ </span>- {وَاللَّهُ عَلَى كُلِّ شَيْءٍ قَدِيرٌ}
INSERT INTO [b9617] VALUES (467,'<span data-type="title" id=toc-463>١٩٠ </span>- {لآيَاتٍ لأُولِي الأَلْبَابِ}
INSERT INTO [b9617] VALUES (468,'<span data-type="title" id=toc-464>١٩١ </span>- {الَّذِينَ يَذْكُرُونَ اللَّهَ قِيَامًا وَقُعُودًا وَعَلَى جُنُوبِهِمْ وَيَتَفَكَّرُونَ فِي خَلْقِ السَّمَاوَاتِ وَالأَرْضِ رَبَّنَا مَا خَلَقْتَ هَذَا بَاطِلا سُبْحَانَكَ فَقِنَا عَذَابَ النَّارِ}
INSERT INTO [b9617] VALUES (469,'<span data-type="title" id=toc-465>١٩٢ </span>- {رَبَّنَا إِنَّكَ مَنْ تُدْخِلِ النَّارَ فَقَدْ أَخْزَيْتَهُ وَمَا لِلظَّالِمِينَ مِنْ أَنْصَارٍ}
INSERT INTO [b9617] VALUES (470,'<span data-type="title" id=toc-466>١٩٣ </span>- {يُنَادِي لِلإِيمَانِ أَنْ آمِنُوا بِرَبِّكُمْ فَآمَنَّا رَبَّنَا فَاغْفِرْ لَنَا ذُنُوبَنَا}
INSERT INTO [b9617] VALUES (471,'<span data-type="title" id=toc-467>١٩٤ </span>- {رَبَّنَا وَآتِنَا مَا وَعَدْتَنَا عَلَى رُسُلِكَ}
INSERT INTO [b9617] VALUES (472,'<span data-type="title" id=toc-468>١٩٥ </span>- {فَاسْتَجَابَ لَهُمْ رَبُّهُمْ أَنِّي لا أُضِيعُ عَمَلَ عَامِلٍ مِنْكُمْ مِنْ ذَكَرٍ أَوْ أُنْثَى بَعْضُكُمْ مِنْ بَعْضٍ فَالَّذِينَ هَاجَرُوا وَأُخْرِجُوا مِنْ دِيَارِهِمْ وَأُوذُوا فِي سَبِيلِي وَقَاتَلُوا وَقُتِلُوا لأُكَفِّرَنَّ عَنْهُمْ سَيِّئَاتِهِمْ وَلأُدْخِلَنَّهُمْ جَنَّاتٍ تَجْرِي مِنْ تَحْتِهَا الأَنْهَارُ ثَوَابًا مِنْ عِنْدِ اللَّهِ وَاللَّهُ عِنْدَهُ حُسْنُ الثَّوَابِ}
INSERT INTO [b9617] VALUES (473,'<span data-type="title" id=toc-469>١٩٦ </span>- {لا يَغُرَّنَّكَ تَقَلُّبُ الَّذِينَ كَفَرُوا فِي الْبِلادِ} ⦗١٦١⦘
INSERT INTO [b9617] VALUES (474,'<span data-type="title" id=toc-470>١٩٧ </span>- {مَتَاعٌ قَلِيلٌ ثُمَّ مَأْوَاهُمْ جَهَنَّمُ وَبِئْسَ الْمِهَادُ}
INSERT INTO [b9617] VALUES (475,'<span data-type="title" id=toc-471>١٩٨ </span>- {لَكِنِ الَّذِينَ اتَّقَوْا رَبَّهُمْ لَهُمْ جَنَّاتٌ تَجْرِي مِنْ تَحْتِهَا الأَنْهَارُ خَالِدِينَ فِيهَا نُزُلا مِنْ عِنْدِ اللَّهِ وَمَا عِنْدَ اللَّهِ خَيْرٌ لِلأَبْرَارِ}
INSERT INTO [b9617] VALUES (476,'<span data-type="title" id=toc-472>١٩٩ </span>- {وَإِنَّ مِنْ أَهْلِ الْكِتَابِ لَمَنْ يُؤْمِنُ بِاللَّهِ وَمَا أُنْزِلَ إِلَيْكُمْ وَمَا أُنْزِلَ إِلَيْهِمْ خَاشِعِينَ لِلَّهِ لا يَشْتَرُونَ بِآيَاتِ اللَّهِ ثَمَنًا قَلِيلا أُولَئِكَ لَهُمْ أَجْرُهُمْ عِنْدَ رَبِّهِمْ}
INSERT INTO [b9617] VALUES (477,'<span data-type="title" id=toc-473>٢٠٠ </span>- {يَا أَيُّهَا الَّذِينَ آمَنُوا اصْبِرُوا وَصَابِرُوا وَرَابِطُوا وَاتَّقُوا اللَّهَ لَعَلَّكُمْ تُفْلِحُونَ}
INSERT INTO [b9617] VALUES (478,'<span data-type="title" id=toc-474>سورة النساء</span>','1','163',NULL,NULL,'0');
INSERT INTO [b9617] VALUES (479,'<span data-type="title" id=toc-475>١ </span>- {وَاتَّقُوا اللَّهَ الَّذِي تَسَاءَلُونَ بِهِ وَالأَرْحَامَ}
INSERT INTO [b9617] VALUES (480,'<span data-type="title" id=toc-476>٢ </span>- {وَلا تَأْكُلُوا أَمْوَالَهُمْ إِلَى أَمْوَالِكُمْ}
INSERT INTO [b9617] VALUES (481,'<span data-type="title" id=toc-477>٣ </span>- {وَإِنْ خِفْتُمْ أَلا تُقْسِطُوا فِي الْيَتَامَى فَانْكِحُوا مَا طَابَ لَكُمْ مِنَ النِّسَاءِ مَثْنَى وَثُلاثَ وَرُبَاعَ فَإِنْ خِفْتُمْ أَلا تَعْدِلُوا فَوَاحِدَةً أَوْ مَا مَلَكَتْ أَيْمَانُكُمْ ذَلِكَ أَدْنَى أَلا تَعُولُوا}
INSERT INTO [b9617] VALUES (482,'<span data-type="title" id=toc-478>٤ </span>- {وَآتُوا النِّسَاءَ صَدُقَاتِهِنَّ نِحْلَةً فَإِنْ طِبْنَ لَكُمْ عَنْ شَيْءٍ مِنْهُ نَفْسًا فَكُلُوهُ هَنِيئًا مَرِيئًا} ⦗١٦٤⦘
INSERT INTO [b9617] VALUES (483,'<span data-type="title" id=toc-479>٦ </span>- {حَتَّى إِذَا بَلَغُوا النِّكَاحَ فَإِنْ آنَسْتُمْ مِنْهُمْ رُشْدًا فَادْفَعُوا إِلَيْهِمْ أَمْوَالَهُمْ وَلا تَأْكُلُوهَا إِسْرَافًا وَبِدَارًا أَنْ يَكْبَرُوا وَمَنْ كَانَ غَنِيًّا فَلْيَسْتَعْفِفْ وَمَنْ كَانَ فَقِيرًا فَلْيَأْكُلْ بِالْمَعْرُوفِ فَإِذَا دَفَعْتُمْ إِلَيْهِمْ أَمْوَالَهُمْ فَأَشْهِدُوا عَلَيْهِمْ وَكَفَى بِاللَّهِ حَسِيبًا}
INSERT INTO [b9617] VALUES (484,'<span data-type="title" id=toc-480>٧ </span>- {لِلرِّجَالِ نَصِيبٌ مِمَّا تَرَكَ الْوَالِدَانِ وَالأَقْرَبُونَ وَلِلنِّسَاءِ نَصِيبٌ مِمَّا تَرَكَ الْوَالِدَانِ وَالأَقْرَبُونَ مِمَّا قَلَّ مِنْهُ أَوْ كَثُرَ نَصِيبًا مَفْرُوضًا}
INSERT INTO [b9617] VALUES (485,'<span data-type="title" id=toc-481>٩ </span>- {وَلْيَخْشَ الَّذِينَ لَوْ تَرَكُوا مِنْ خَلْفِهِمْ ذُرِّيَّةً ضِعَافًا خَافُوا عَلَيْهِمْ فَلْيَتَّقُوا اللَّهَ وَلْيَقُولُوا قَوْلا سَدِيدًا}
INSERT INTO [b9617] VALUES (486,'<span data-type="title" id=toc-482>١٠ </span>- {إِنَّ الَّذِينَ يَأْكُلُونَ أَمْوَالَ الْيَتَامَى ظُلْمًا إِنَّمَا يَأْكُلُونَ فِي بُطُونِهِمْ نَارًا}
INSERT INTO [b9617] VALUES (487,'<span data-type="title" id=toc-483>١١ </span>- {يُوصِيكُمُ اللَّهُ فِي أَوْلادِكُمْ لِلذَّكَرِ مِثْلُ حَظِّ الأُنْثَيَيْنِ فَإِنْ كُنَّ نِسَاءً فَوْقَ اثْنَتَيْنِ فَلَهُنَّ ثُلُثَا مَا تَرَكَ وَإِنْ كَانَتْ وَاحِدَةً فَلَهَا النِّصْفُ وَلأَبَوَيْهِ لِكُلِّ وَاحِدٍ مِنْهُمَا السُّدُسُ مِمَّا تَرَكَ إِنْ كَانَ لَهُ وَلَدٌ فَإِنْ لَمْ يَكُنْ لَهُ وَلَدٌ وَوَرِثَهُ أَبَوَاهُ فَلأُمِّهِ الثُّلُثُ فَإِنْ كَانَ لَهُ إِخْوَةٌ فَلأُمِّهِ السُّدُسُ مِنْ بَعْدِ وَصِيَّةٍ يُوصِي بِهَا أَوْ دَيْنٍ آبَاؤُكُمْ وَأَبْنَاؤُكُمْ لا تَدْرُونَ أَيُّهُمْ أَقْرَبُ لَكُمْ نَفْعًا فَرِيضَةً مِنَ اللَّهِ إِنَّ اللَّهَ كَانَ عَلِيمًا حَكِيمًا}
INSERT INTO [b9617] VALUES (488,'من «السدس» . والجار «من بعد وصية» متعلق بفعل مقدر أي: يستحقون ذلك، وجملة «يستحقون ذلك» المقدرة مستأنفة. وقوله «لا تدرون أيهم أقرب لكم نفعا» : «أيهم» : اسم استفهام مبتدأ، والضمير مضاف إليه، و «أقرب» خبره. والجار متعلق بأقرب، و «نفعا» : تمييز، وجملة «أيهم أقرب» مفعول به لـ «تدرون» . «فريضة» : مفعول مطلق لعامل مقدر أي: فرض الله ذلك فريضة. الجار «من الله» متعلق بنعت لـ «فريضة» ، جملة «إن الله كان عليما» مستأنفة، «حكيما» خبر ثان لـ «كان» .','1','166',NULL,NULL,'0');
INSERT INTO [b9617] VALUES (489,'<span data-type="title" id=toc-484>١٢ </span>- {وَلَكُمْ نِصْفُ مَا تَرَكَ أَزْوَاجُكُمْ إِنْ لَمْ يَكُنْ لَهُنَّ وَلَدٌ فَإِنْ كَانَ لَهُنَّ وَلَدٌ فَلَكُمُ الرُّبُعُ مِمَّا تَرَكْنَ مِنْ بَعْدِ وَصِيَّةٍ يُوصِينَ بِهَا أَوْ دَيْنٍ وَلَهُنَّ الرُّبُعُ مِمَّا تَرَكْتُمْ إِنْ لَمْ يَكُنْ لَكُمْ وَلَدٌ فَإِنْ كَانَ لَكُمْ وَلَدٌ فَلَهُنَّ الثُّمُنُ مِمَّا تَرَكْتُمْ مِنْ بَعْدِ وَصِيَّةٍ تُوصُونَ بِهَا أَوْ دَيْنٍ وَإِنْ كَانَ رَجُلٌ يُورَثُ كَلالَةً أَوِ امْرَأَةٌ وَلَهُ أَخٌ أَوْ أُخْتٌ فَلِكُلِّ وَاحِدٍ مِنْهُمَا السُّدُسُ فَإِنْ كَانُوا أَكْثَرَ مِنْ ذَلِكَ فَهُمْ شُرَكَاءُ فِي الثُّلُثِ مِنْ بَعْدِ وَصِيَّةٍ يُوصَى بِهَا أَوْ دَيْنٍ غَيْرَ مُضَارٍّ وَصِيَّةً مِنَ اللَّهِ وَاللَّهُ عَلِيمٌ حَلِيمٌ}
INSERT INTO [b9617] VALUES (490,'«يورث» . جملة «وله أخ» حال من ضمير «يورث» و «غير» حال من الفاعل في «يوصي» . وقوله «وصية من الله» : مفعول مطلق لعامل محذوف أي: يوصيكم وصية، والجار «من الله» متعلق بنعت لـ «وصية» . «حليم» خبر ثان.','1','167',NULL,NULL,'0');
INSERT INTO [b9617] VALUES (491,'<span data-type="title" id=toc-485>١٣ </span>- {وَذَلِكَ الْفَوْزُ الْعَظِيمُ}
INSERT INTO [b9617] VALUES (492,'<span data-type="title" id=toc-486>١٤ </span>- {وَمَنْ يَعْصِ اللَّهَ وَرَسُولَهُ وَيَتَعَدَّ حُدُودَهُ يُدْخِلْهُ نَارًا خَالِدًا فِيهَا وَلَهُ عَذَابٌ مُهِينٌ}
INSERT INTO [b9617] VALUES (493,'<span data-type="title" id=toc-487>١٥ </span>- {وَاللاتِي يَأْتِينَ الْفَاحِشَةَ مِنْ نِسَائِكُمْ فَاسْتَشْهِدُوا عَلَيْهِنَّ أَرْبَعَةً مِنْكُمْ فَإِنْ شَهِدُوا فَأَمْسِكُوهُنَّ فِي الْبُيُوتِ حَتَّى يَتَوَفَّاهُنَّ الْمَوْتُ أَوْ يَجْعَلَ اللَّهُ لَهُنَّ سَبِيلا}
INSERT INTO [b9617] VALUES (494,'<span data-type="title" id=toc-488>١٦ </span>- {وَاللَّذَانِ يَأْتِيَانِهَا مِنْكُمْ فَآذُوهُمَا فَإِنْ تَابَا وَأَصْلَحَا فَأَعْرِضُوا عَنْهُمَا إِنَّ اللَّهَ كَانَ تَوَّابًا رَحِيمًا}
INSERT INTO [b9617] VALUES (495,'<span data-type="title" id=toc-489>١٧ </span>- {إِنَّمَا التَّوْبَةُ عَلَى اللَّهِ لِلَّذِينَ يَعْمَلُونَ السُّوءَ بِجَهَالَةٍ ثُمَّ يَتُوبُونَ مِنْ قَرِيبٍ فَأُولَئِكَ يَتُوبُ اللَّهُ عَلَيْهِمْ وَكَانَ اللَّهُ عَلِيمًا حَكِيمًا}
INSERT INTO [b9617] VALUES (496,'<span data-type="title" id=toc-490>١٨ </span>- {حَتَّى إِذَا حَضَرَ أَحَدَهُمُ الْمَوْتُ قَالَ إِنِّي تُبْتُ الآنَ وَلا الَّذِينَ يَمُوتُونَ وَهُمْ كُفَّارٌ أُولَئِكَ أَعْتَدْنَا لَهُمْ عَذَابًا أَلِيمًا}
INSERT INTO [b9617] VALUES (497,'<span data-type="title" id=toc-491>١٩ </span>- {لا يَحِلُّ لَكُمْ أَنْ تَرِثُوا النِّسَاءَ كَرْهًا وَلا تَعْضُلُوهُنَّ لِتَذْهَبُوا بِبَعْضِ مَا آتَيْتُمُوهُنَّ إِلا أَنْ يَأْتِينَ بِفَاحِشَةٍ مُبَيِّنَةٍ وَعَاشِرُوهُنَّ بِالْمَعْرُوفِ فَإِنْ كَرِهْتُمُوهُنَّ فَعَسَى أَنْ تَكْرَهُوا شَيْئًا وَيَجْعَلَ اللَّهُ فِيهِ خَيْرًا كَثِيرًا}
INSERT INTO [b9617] VALUES (498,'<span data-type="title" id=toc-492>٢٠ </span>- {وَإِنْ أَرَدْتُمُ اسْتِبْدَالَ زَوْجٍ مَكَانَ زَوْجٍ وَآتَيْتُمْ إِحْدَاهُنَّ قِنْطَارًا فَلا تَأْخُذُوا مِنْهُ شَيْئًا أَتَأْخُذُونَهُ بُهْتَانًا وَإِثْمًا مُبِينًا}
INSERT INTO [b9617] VALUES (499,'<span data-type="title" id=toc-493>٢١ </span>- {وَكَيْفَ تَأْخُذُونَهُ وَقَدْ أَفْضَى بَعْضُكُمْ إِلَى بَعْضٍ}
INSERT INTO [b9617] VALUES (500,'<span data-type="title" id=toc-494>٢٢ </span>- {وَلا تَنْكِحُوا مَا نَكَحَ آبَاؤُكُمْ مِنَ النِّسَاءِ إِلا مَا قَدْ سَلَفَ إِنَّهُ كَانَ فَاحِشَةً وَمَقْتًا وَسَاءَ سَبِيلا}
INSERT INTO [b9617] VALUES (501,'<span data-type="title" id=toc-495>٢٣ </span>- {وَأَخَوَاتُكُمْ مِنَ الرَّضَاعَةِ وَأُمَّهَاتُ نِسَائِكُمْ وَرَبَائِبُكُمُ اللاتِي فِي حُجُورِكُمْ مِنْ نِسَائِكُمُ اللاتِي دَخَلْتُمْ بِهِنَّ فَإِنْ لَمْ تَكُونُوا دَخَلْتُمْ بِهِنَّ فَلا جُنَاحَ عَلَيْكُمْ وَحَلائِلُ أَبْنَائِكُمُ الَّذِينَ مِنْ أَصْلابِكُمْ وَأَنْ تَجْمَعُوا بَيْنَ الأُخْتَيْنِ إِلا مَا قَدْ سَلَفَ}
INSERT INTO [b9617] VALUES (502,'<span data-type="title" id=toc-496>٢٤ </span>- {وَالْمُحْصَنَاتُ مِنَ النِّسَاءِ إِلا مَا مَلَكَتْ أَيْمَانُكُمْ كِتَابَ اللَّهِ عَلَيْكُمْ وَأُحِلَّ لَكُمْ مَا وَرَاءَ ذَلِكُمْ أَنْ تَبْتَغُوا بِأَمْوَالِكُمْ مُحْصِنِينَ غَيْرَ مُسَافِحِينَ فَمَا اسْتَمْتَعْتُمْ بِهِ مِنْهُنَّ فَآتُوهُنَّ أُجُورَهُنَّ فَرِيضَةً وَلا جُنَاحَ عَلَيْكُمْ فِيمَا تَرَاضَيْتُمْ بِهِ مِنْ بَعْدِ الْفَرِيضَةِ}
INSERT INTO [b9617] VALUES (503,'<span data-type="title" id=toc-497>٢٥ </span>- {وَمَنْ لَمْ يَسْتَطِعْ مِنْكُمْ طَوْلا أَنْ يَنْكِحَ الْمُحْصَنَاتِ الْمُؤْمِنَاتِ فَمِنْ مَا مَلَكَتْ أَيْمَانُكُمْ مِنْ فَتَيَاتِكُمُ الْمُؤْمِنَاتِ وَاللَّهُ أَعْلَمُ بِإِيمَانِكُمْ بَعْضُكُمْ مِنْ بَعْضٍ فَانْكِحُوهُنَّ بِإِذْنِ أَهْلِهِنَّ وَآتُوهُنَّ أُجُورَهُنَّ بِالْمَعْرُوفِ مُحْصَنَاتٍ غَيْرَ مُسَافِحَاتٍ وَلا مُتَّخِذَاتِ أَخْدَانٍ فَإِذَا أُحْصِنَّ فَإِنْ أَتَيْنَ بِفَاحِشَةٍ فَعَلَيْهِنَّ نِصْفُ مَا عَلَى الْمُحْصَنَاتِ مِنَ الْعَذَابِ ذَلِكَ لِمَنْ خَشِيَ الْعَنَتَ مِنْكُمْ وَأَنْ تَصْبِرُوا خَيْرٌ لَكُمْ}','1','171',NULL,NULL,'0');
INSERT INTO [b9617] VALUES (504,'الجار «منكم» متعلق بحال من فاعل «يستطع» . «طولا» : مفعول به لـ «يستطع» ، والمصدر «أن ينكح» مفعول به للمصدر المنون «طولا» ، وقوله «فمن ما ملكت» : الفاء رابطة للجواب، والجار متعلق بنعت محذوف لمنعوت محذوف أي: فلينكح امرأة كائنة من «ما» . والجار «من فتياتكم» متعلق بحال من العائد المقدر أي: ملكته كائنا من فتياتكم. وجملة «والله أعلم» معترضة، وجملة «بعضكم من بعض» حال من ضمير المخاطب في «أيمانكم» ، وجملة «فانكحوهن» معطوفة على جملة الفعل المقدر فلينكح. الجار «بإذن» متعلق بحال من الضمير «هنَّ» ، «محصنات غير» حالان من الضمير في «آتوهن» . وجملة «فإذا أحصنَّ» مستأنفة، و «إذا» ظرفية شرطية متعلقة بمعنى الجواب أي: يُعَذَّبن بكذا إذا، وجملة «فإن أتين بفاحشة» جواب إذا، وجملة «فعليهن نصف» جواب الشرط الثاني. والمصدر «أن تصبروا» مبتدأ، و «خير» خبره. وجملة المصدر مستأنفة.','1','172',NULL,NULL,'0');
INSERT INTO [b9617] VALUES (505,'<span data-type="title" id=toc-498>٢٦ </span>- {يُرِيدُ اللَّهُ لِيُبَيِّنَ لَكُمْ وَيَهْدِيَكُمْ سُنَنَ الَّذِينَ مِنْ قَبْلِكُمْ}
INSERT INTO [b9617] VALUES (506,'<span data-type="title" id=toc-499>٢٧ </span>- {وَاللَّهُ يُرِيدُ أَنْ يَتُوبَ عَلَيْكُمْ وَيُرِيدُ الَّذِينَ يَتَّبِعُونَ الشَّهَوَاتِ أَنْ تَمِيلُوا ⦗١٧٣⦘ مَيْلا عَظِيمًا}
INSERT INTO [b9617] VALUES (507,'<span data-type="title" id=toc-500>٢٨ </span>- {وَخُلِقَ الإِنْسَانُ ضَعِيفًا}
INSERT INTO [b9617] VALUES (508,'<span data-type="title" id=toc-501>٢٩ </span>- {لا تَأْكُلُوا أَمْوَالَكُمْ بَيْنَكُمْ بِالْبَاطِلِ إِلا أَنْ تَكُونَ تِجَارَةً عَنْ تَرَاضٍ مِنْكُمْ وَلا تَقْتُلُوا أَنْفُسَكُمْ إِنَّ اللَّهَ كَانَ بِكُمْ رَحِيمًا}
INSERT INTO [b9617] VALUES (509,'<span data-type="title" id=toc-502>٣٠ </span>- {وَمَنْ يَفْعَلْ ذَلِكَ عُدْوَانًا وَظُلْمًا فَسَوْفَ نُصْلِيهِ نَارًا وَكَانَ ذَلِكَ عَلَى اللَّهِ يَسِيرًا}
INSERT INTO [b9617] VALUES (510,'<span data-type="title" id=toc-503>٣١ </span>- {إِنْ تَجْتَنِبُوا كَبَائِرَ مَا تُنْهَوْنَ عَنْهُ نُكَفِّرْ عَنْكُمْ سَيِّئَاتِكُمْ وَنُدْخِلْكُمْ مُدْخَلا كَرِيمًا}
INSERT INTO [b9617] VALUES (511,'<span data-type="title" id=toc-504>٣٢ </span>- {لِلرِّجَالِ نَصِيبٌ مِمَّا اكْتَسَبُوا ⦗١٧٤⦘ وَلِلنِّسَاءِ نَصِيبٌ مِمَّا اكْتَسَبْنَ وَاسْأَلُوا اللَّهَ مِنْ فَضْلِهِ إِنَّ اللَّهَ كَانَ بِكُلِّ شَيْءٍ عَلِيمًا}
INSERT INTO [b9617] VALUES (512,'<span data-type="title" id=toc-505>٣٣ </span>- {وَلِكُلٍّ جَعَلْنَا مَوَالِيَ مِمَّا تَرَكَ الْوَالِدَانِ وَالأَقْرَبُونَ وَالَّذِينَ عَقَدَتْ أَيْمَانُكُمْ فَآتُوهُمْ نَصِيبَهُمْ إِنَّ اللَّهَ كَانَ عَلَى كُلِّ شَيْءٍ شَهِيدًا}
INSERT INTO [b9617] VALUES (513,'<span data-type="title" id=toc-506>٣٤ </span>- {الرِّجَالُ قَوَّامُونَ عَلَى النِّسَاءِ بِمَا فَضَّلَ اللَّهُ بَعْضَهُمْ عَلَى بَعْضٍ وَبِمَا أَنْفَقُوا مِنْ أَمْوَالِهِمْ فَالصَّالِحَاتُ قَانِتَاتٌ حَافِظَاتٌ لِلْغَيْبِ بِمَا حَفِظَ اللَّهُ وَاللاتِي تَخَافُونَ نُشُوزَهُنَّ فَعِظُوهُنَّ وَاهْجُرُوهُنَّ فِي الْمَضَاجِعِ وَاضْرِبُوهُنَّ فَإِنْ أَطَعْنَكُمْ فَلا تَبْغُوا عَلَيْهِنَّ سَبِيلا}
INSERT INTO [b9617] VALUES (514,'<span data-type="title" id=toc-507>٣٥ </span>- {وَإِنْ خِفْتُمْ شِقَاقَ بَيْنِهِمَا فَابْعَثُوا حَكَمًا مِنْ أَهْلِهِ وَحَكَمًا مِنْ أَهْلِهَا إِنْ يُرِيدَا إِصْلاحًا يُوَفِّقِ اللَّهُ بَيْنَهُمَا}
INSERT INTO [b9617] VALUES (515,'<span data-type="title" id=toc-508>٣٦ </span>- {وَلا تُشْرِكُوا بِهِ شَيْئًا وَبِالْوَالِدَيْنِ إِحْسَانًا وَبِذِي الْقُرْبَى وَالْيَتَامَى وَالْمَسَاكِينِ وَالْجَارِ ذِي الْقُرْبَى وَالْجَارِ الْجُنُبِ وَالصَّاحِبِ بِالْجَنْبِ وَابْنِ السَّبِيلِ وَمَا مَلَكَتْ أَيْمَانُكُمْ إِنَّ اللَّهَ لا يُحِبُّ مَنْ كَانَ مُخْتَالا فَخُورًا}
INSERT INTO [b9617] VALUES (516,'<span data-type="title" id=toc-509>٣٧ </span>- {الَّذِينَ يَبْخَلُونَ وَيَأْمُرُونَ النَّاسَ بِالْبُخْلِ وَيَكْتُمُونَ مَا آتَاهُمُ اللَّهُ مِنْ فَضْلِهِ وَأَعْتَدْنَا لِلْكَافِرِينَ عَذَابًا مُهِينًا}
INSERT INTO [b9617] VALUES (517,'<span data-type="title" id=toc-510>٣٨ </span>- {وَالَّذِينَ يُنْفِقُونَ أَمْوَالَهُمْ رِئَاءَ النَّاسِ وَلا يُؤْمِنُونَ بِاللَّهِ وَلا بِالْيَوْمِ الآخِرِ وَمَنْ يَكُنِ الشَّيْطَانُ لَهُ قَرِينًا فَسَاءَ قَرِينًا}
INSERT INTO [b9617] VALUES (518,'<span data-type="title" id=toc-511>٣٩ </span>- {وَمَاذَا عَلَيْهِمْ لَوْ آمَنُوا بِاللَّهِ وَالْيَوْمِ الآخِرِ وَأَنْفَقُوا مِمَّا رَزَقَهُمُ اللَّهُ}
INSERT INTO [b9617] VALUES (519,'<span data-type="title" id=toc-512>٤٠ </span>- {إِنَّ اللَّهَ لا يَظْلِمُ مِثْقَالَ ذَرَّةٍ وَإِنْ تَكُ حَسَنَةً يُضَاعِفْهَا}
INSERT INTO [b9617] VALUES (520,'<span data-type="title" id=toc-513>٤١ </span>- {فَكَيْفَ إِذَا جِئْنَا مِنْ كُلِّ أُمَّةٍ بِشَهِيدٍ وَجِئْنَا بِكَ عَلَى هَؤُلاءِ شَهِيدًا}
INSERT INTO [b9617] VALUES (521,'<span data-type="title" id=toc-514>٤٢ </span>- {يَوْمَئِذٍ يَوَدُّ الَّذِينَ كَفَرُوا وَعَصَوُا الرَّسُولَ لَوْ تُسَوَّى بِهِمُ الأَرْضُ وَلا يَكْتُمُونَ اللَّهَ حَدِيثًا}
INSERT INTO [b9617] VALUES (522,'<span data-type="title" id=toc-515>٤٣ </span>- {لا تَقْرَبُوا الصَّلاةَ وَأَنْتُمْ سُكَارَى حَتَّى تَعْلَمُوا مَا تَقُولُونَ وَلا جُنُبًا إِلا عَابِرِي سَبِيلٍ حَتَّى تَغْتَسِلُوا وَإِنْ كُنْتُمْ مَرْضَى أَوْ عَلَى سَفَرٍ أَوْ جَاءَ أَحَدٌ مِنْكُمْ مِنَ الْغَائِطِ أَوْ لامَسْتُمُ النِّسَاءَ فَلَمْ تَجِدُوا مَاءً فَتَيَمَّمُوا صَعِيدًا طَيِّبًا}
INSERT INTO [b9617] VALUES (523,'<span data-type="title" id=toc-516>٤٤ </span>- {أُوتُوا نَصِيبًا مِنَ الْكِتَابِ يَشْتَرُونَ الضَّلالَةَ وَيُرِيدُونَ أَنْ تَضِلُّوا السَّبِيلَ}
INSERT INTO [b9617] VALUES (524,'<span data-type="title" id=toc-517>٤٥ </span>- {وَكَفَى بِاللَّهِ نَصِيرًا}
INSERT INTO [b9617] VALUES (525,'<span data-type="title" id=toc-518>٤٦ </span>- {مِنَ الَّذِينَ هَادُوا يُحَرِّفُونَ الْكَلِمَ عَنْ مَوَاضِعِهِ وَيَقُولُونَ سَمِعْنَا وَعَصَيْنَا وَاسْمَعْ غَيْرَ مُسْمَعٍ وَرَاعِنَا لَيًّا بِأَلْسِنَتِهِمْ وَطَعْنًا فِي الدِّينِ وَلَوْ أَنَّهُمْ قَالُوا سَمِعْنَا وَأَطَعْنَا وَاسْمَعْ وَانْظُرْنَا لَكَانَ خَيْرًا لَهُمْ وَأَقْوَمَ وَلَكِنْ لَعَنَهُمُ اللَّهُ بِكُفْرِهِمْ فَلا يُؤْمِنُونَ إِلا قَلِيلا}
INSERT INTO [b9617] VALUES (526,'<span data-type="title" id=toc-519>٤٧ </span>- {مُصَدِّقًا لِمَا مَعَكُمْ مِنْ قَبْلِ أَنْ نَطْمِسَ وُجُوهًا فَنَرُدَّهَا عَلَى أَدْبَارِهَا أَوْ نَلْعَنَهُمْ كَمَا لَعَنَّا أَصْحَابَ السَّبْتِ}
INSERT INTO [b9617] VALUES (527,'<span data-type="title" id=toc-520>٤٨ </span>- {وَيَغْفِرُ مَا دُونَ ذَلِكَ لِمَنْ يَشَاءُ}
INSERT INTO [b9617] VALUES (528,'<span data-type="title" id=toc-521>٤٩ </span>- {بَلِ اللَّهُ يُزَكِّي مَنْ يَشَاءُ وَلا يُظْلَمُونَ فَتِيلا}
INSERT INTO [b9617] VALUES (529,'<span data-type="title" id=toc-522>٥٠ </span>- {انْظُرْ كَيْفَ يَفْتَرُونَ عَلَى اللَّهِ الْكَذِبَ وَكَفَى بِهِ إِثْمًا مُبِينًا}
INSERT INTO [b9617] VALUES (530,'<span data-type="title" id=toc-523>٥١ </span>- {أَلَمْ تَرَ إِلَى الَّذِينَ أُوتُوا نَصِيبًا مِنَ الْكِتَابِ يُؤْمِنُونَ بِالْجِبْتِ وَالطَّاغُوتِ ⦗١٨٠⦘ وَيَقُولُونَ لِلَّذِينَ كَفَرُوا هَؤُلاءِ أَهْدَى مِنَ الَّذِينَ آمَنُوا سَبِيلا}
INSERT INTO [b9617] VALUES (531,'<span data-type="title" id=toc-524>٥٣ </span>- {أَمْ لَهُمْ نَصِيبٌ مِنَ الْمُلْكِ فَإِذًا لا يُؤْتُونَ النَّاسَ نَقِيرًا}
INSERT INTO [b9617] VALUES (532,'<span data-type="title" id=toc-525>٥٤ </span>- {أَمْ يَحْسُدُونَ النَّاسَ عَلَى مَا آتَاهُمُ اللَّهُ مِنْ فَضْلِهِ فَقَدْ آتَيْنَا آلَ إِبْرَاهِيمَ الْكِتَابَ وَالْحِكْمَةَ}
INSERT INTO [b9617] VALUES (533,'<span data-type="title" id=toc-526>٥٥ </span>- {فَمِنْهُمْ مَنْ آمَنَ بِهِ وَمِنْهُمْ مَنْ صَدَّ عَنْهُ}
INSERT INTO [b9617] VALUES (534,'<span data-type="title" id=toc-527>٥٦ </span>- {سَوْفَ نُصْلِيهِمْ نَارًا كُلَّمَا نَضِجَتْ جُلُودُهُمْ بَدَّلْنَاهُمْ جُلُودًا غَيْرَهَا لِيَذُوقُوا الْعَذَابَ}
INSERT INTO [b9617] VALUES (535,'<span data-type="title" id=toc-528>٥٧ </span>- {وَالَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ سَنُدْخِلُهُمْ جَنَّاتٍ تَجْرِي مِنْ تَحْتِهَا الأَنْهَارُ خَالِدِينَ فِيهَا أَبَدًا لَهُمْ فِيهَا أَزْوَاجٌ مُطَهَّرَةٌ وَنُدْخِلُهُمْ ظِلا ظَلِيلا}
INSERT INTO [b9617] VALUES (536,'<span data-type="title" id=toc-529>٥٨ </span>- {إِنَّ اللَّهَ يَأْمُرُكُمْ أَنْ تُؤَدُّوا الأَمَانَاتِ إِلَى أَهْلِهَا وَإِذَا حَكَمْتُمْ بَيْنَ النَّاسِ أَنْ تَحْكُمُوا بِالْعَدْلِ إِنَّ اللَّهَ نِعِمَّا يَعِظُكُمْ بِهِ}
INSERT INTO [b9617] VALUES (537,'<span data-type="title" id=toc-530>٥٩ </span>- {يَا أَيُّهَا الَّذِينَ آمَنُوا أَطِيعُوا اللَّهَ وَأَطِيعُوا الرَّسُولَ وَأُولِي الأَمْرِ مِنْكُمْ فَإِنْ تَنَازَعْتُمْ فِي شَيْءٍ فَرُدُّوهُ إِلَى اللَّهِ وَالرَّسُولِ إِنْ كُنْتُمْ تُؤْمِنُونَ بِاللَّهِ وَالْيَوْمِ الآخِرِ}
INSERT INTO [b9617] VALUES (538,'<span data-type="title" id=toc-531>٦٠ </span>- {أَلَمْ تَرَ إِلَى الَّذِينَ يَزْعُمُونَ أَنَّهُمْ آمَنُوا بِمَا أُنْزِلَ إِلَيْكَ وَمَا أُنْزِلَ مِنْ قَبْلِكَ يُرِيدُونَ أَنْ يَتَحَاكَمُوا إِلَى الطَّاغُوتِ وَقَدْ أُمِرُوا أَنْ يَكْفُرُوا بِهِ}
INSERT INTO [b9617] VALUES (539,'<span data-type="title" id=toc-532>٦١ </span>- {وَإِذَا قِيلَ لَهُمْ تَعَالَوْا إِلَى مَا أَنْزَلَ اللَّهُ وَإِلَى الرَّسُولِ رَأَيْتَ الْمُنَافِقِينَ يَصُدُّونَ عَنْكَ صُدُودًا}
INSERT INTO [b9617] VALUES (540,'<span data-type="title" id=toc-533>٦٢ </span>- {فَكَيْفَ إِذَا أَصَابَتْهُمْ مُصِيبَةٌ بِمَا قَدَّمَتْ أَيْدِيهِمْ ثُمَّ جَاءُوكَ يَحْلِفُونَ بِاللَّهِ إِنْ أَرَدْنَا إِلا إِحْسَانًا وَتَوْفِيقًا}
INSERT INTO [b9617] VALUES (541,'<span data-type="title" id=toc-534>٦٣ </span>- {فَأَعْرِضْ عَنْهُمْ}
INSERT INTO [b9617] VALUES (542,'<span data-type="title" id=toc-535>٦٤ </span>- {وَمَا أَرْسَلْنَا مِنْ رَسُولٍ إِلا لِيُطَاعَ بِإِذْنِ اللَّهِ وَلَوْ أَنَّهُمْ إِذْ ظَلَمُوا أَنْفُسَهُمْ جَاءُوكَ فَاسْتَغْفَرُوا اللَّهَ وَاسْتَغْفَرَ لَهُمُ الرَّسُولُ لَوَجَدُوا اللَّهَ تَوَّابًا رَحِيمًا}
INSERT INTO [b9617] VALUES (543,'<span data-type="title" id=toc-536>٦٥ </span>- {فَلا وَرَبِّكَ لا يُؤْمِنُونَ حَتَّى يُحَكِّمُوكَ فِيمَا شَجَرَ بَيْنَهُمْ ثُمَّ لا يَجِدُوا فِي ⦗١٨٤⦘ أَنْفُسِهِمْ حَرَجًا مِمَّا قَضَيْتَ}
INSERT INTO [b9617] VALUES (544,'<span data-type="title" id=toc-537>٦٦ </span>- {وَلَوْ أَنَّا كَتَبْنَا عَلَيْهِمْ أَنِ اقْتُلُوا أَنْفُسَكُمْ أَوِ اخْرُجُوا مِنْ دِيَارِكُمْ مَا فَعَلُوهُ إِلا قَلِيلٌ مِنْهُمْ وَلَوْ أَنَّهُمْ فَعَلُوا مَا يُوعَظُونَ بِهِ لَكَانَ خَيْرًا لَهُمْ وَأَشَدَّ تَثْبِيتًا}
INSERT INTO [b9617] VALUES (545,'<span data-type="title" id=toc-538>٦٧ </span>- {وَإِذًا لآتَيْنَاهُمْ مِنْ لَدُنَّا أَجْرًا عَظِيمًا}
INSERT INTO [b9617] VALUES (546,'<span data-type="title" id=toc-539>٦٨ </span>- {وَلَهَدَيْنَاهُمْ صِرَاطًا مُسْتَقِيمًا}
INSERT INTO [b9617] VALUES (547,'<span data-type="title" id=toc-540>٦٩ </span>- {فَأُولَئِكَ مَعَ الَّذِينَ أَنْعَمَ اللَّهُ عَلَيْهِمْ مِنَ النَّبِيِّينَ وَالصِّدِّيقِينَ وَالشُّهَدَاءِ وَالصَّالِحِينَ وَحَسُنَ أُولَئِكَ رَفِيقًا}
INSERT INTO [b9617] VALUES (548,'<span data-type="title" id=toc-541>٧٠ </span>- {ذَلِكَ الْفَضْلُ مِنَ اللَّهِ وَكَفَى بِاللَّهِ عَلِيمًا}
INSERT INTO [b9617] VALUES (549,'<span data-type="title" id=toc-542>٧١ </span>- {يَا أَيُّهَا الَّذِينَ آمَنُوا خُذُوا حِذْرَكُمْ فَانْفِرُوا ثُبَاتٍ أَوِ انْفِرُوا جَمِيعًا}
INSERT INTO [b9617] VALUES (550,'<span data-type="title" id=toc-543>٧٢ </span>- {وَإِنَّ مِنْكُمْ لَمَنْ لَيُبَطِّئَنَّ فَإِنْ أَصَابَتْكُمْ مُصِيبَةٌ قَالَ قَدْ أَنْعَمَ اللَّهُ عَلَيَّ إِذْ لَمْ أَكُنْ مَعَهُمْ شَهِيدًا}
INSERT INTO [b9617] VALUES (551,'<span data-type="title" id=toc-544>٧٣ </span>- {وَلَئِنْ أَصَابَكُمْ فَضْلٌ مِنَ اللَّهِ لَيَقُولَنَّ كَأَنْ لَمْ تَكُنْ بَيْنَكُمْ وَبَيْنَهُ مَوَدَّةٌ يَا لَيْتَنِي كُنْتُ مَعَهُمْ فَأَفُوزَ فَوْزًا عَظِيمًا}
INSERT INTO [b9617] VALUES (552,'<span data-type="title" id=toc-545>٧٤ </span>- {فَلْيُقَاتِلْ فِي سَبِيلِ اللَّهِ الَّذِينَ يَشْرُونَ الْحَيَاةَ الدُّنْيَا بِالآخِرَةِ وَمَنْ يُقَاتِلْ فِي سَبِيلِ اللَّهِ فَيُقْتَلْ أَوْ يَغْلِبْ فَسَوْفَ نُؤْتِيهِ أَجْرًا عَظِيمًا}
INSERT INTO [b9617] VALUES (553,'<span data-type="title" id=toc-546>٧٥ </span>- {وَمَا لَكُمْ لا تُقَاتِلُونَ فِي سَبِيلِ اللَّهِ وَالْمُسْتَضْعَفِينَ مِنَ الرِّجَالِ وَالنِّسَاءِ وَالْوِلْدَانِ الَّذِينَ يَقُولُونَ رَبَّنَا أَخْرِجْنَا مِنْ هَذِهِ الْقَرْيَةِ الظَّالِمِ أَهْلُهَا وَاجْعَلْ لَنَا مِنْ لَدُنْكَ وَلِيًّا}
INSERT INTO [b9617] VALUES (554,'<span data-type="title" id=toc-547>٧٧ </span>- {أَلَمْ تَرَ إِلَى الَّذِينَ قِيلَ لَهُمْ كُفُّوا أَيْدِيَكُمْ وَأَقِيمُوا الصَّلاةَ وَآتُوا الزَّكَاةَ فَلَمَّا كُتِبَ عَلَيْهِمُ الْقِتَالُ إِذَا فَرِيقٌ مِنْهُمْ يَخْشَوْنَ النَّاسَ كَخَشْيَةِ اللَّهِ أَوْ أَشَدَّ خَشْيَةً وَقَالُوا رَبَّنَا لِمَ كَتَبْتَ عَلَيْنَا الْقِتَالَ لَوْلا أَخَّرْتَنَا إِلَى أَجَلٍ قَرِيبٍ قُلْ مَتَاعُ الدُّنْيَا قَلِيلٌ وَالآخِرَةُ خَيْرٌ لِمَنِ اتَّقَى وَلا تُظْلَمُونَ فَتِيلا}
INSERT INTO [b9617] VALUES (555,'<span data-type="title" id=toc-548>٧٨ </span>- {أَيْنَمَا تَكُونُوا يُدْرِكْكُمُ الْمَوْتُ وَلَوْ كُنْتُمْ فِي بُرُوجٍ مُشَيَّدَةٍ وَإِنْ تُصِبْهُمْ حَسَنَةٌ يَقُولُوا هَذِهِ مِنْ عِنْدِ اللَّهِ وَإِنْ تُصِبْهُمْ سَيِّئَةٌ يَقُولُوا هَذِهِ مِنْ عِنْدِكَ قُلْ كُلٌّ مِنْ عِنْدِ اللَّهِ فَمَالِ هَؤُلاءِ الْقَوْمِ لا يَكَادُونَ يَفْقَهُونَ حَدِيثًا}
INSERT INTO [b9617] VALUES (556,'<span data-type="title" id=toc-549>٧٩ </span>- {مَا أَصَابَكَ مِنْ حَسَنَةٍ فَمِنَ اللَّهِ وَمَا أَصَابَكَ مِنْ سَيِّئَةٍ فَمِنْ نَفْسِكَ وَأَرْسَلْنَاكَ لِلنَّاسِ رَسُولا وَكَفَى بِاللَّهِ شَهِيدًا}
INSERT INTO [b9617] VALUES (557,'<span data-type="title" id=toc-550>٨٠ </span>- {وَمَنْ تَوَلَّى فَمَا أَرْسَلْنَاكَ عَلَيْهِمْ حَفِيظًا}
INSERT INTO [b9617] VALUES (558,'<span data-type="title" id=toc-551>٨١ </span>- {وَيَقُولُونَ طَاعَةٌ فَإِذَا بَرَزُوا مِنْ عِنْدِكَ بَيَّتَ طَائِفَةٌ مِنْهُمْ غَيْرَ الَّذِي تَقُولُ وَاللَّهُ ⦗١٨٩⦘ يَكْتُبُ مَا يُبَيِّتُونَ فَأَعْرِضْ عَنْهُمْ وَتَوَكَّلْ عَلَى اللَّهِ وَكَفَى بِاللَّهِ وَكِيلا}
INSERT INTO [b9617] VALUES (559,'<span data-type="title" id=toc-552>٨٢ </span>- {أَفَلا يَتَدَبَّرُونَ الْقُرْآنَ وَلَوْ كَانَ مِنْ عِنْدِ غَيْرِ اللَّهِ لَوَجَدُوا فِيهِ اخْتِلافًا كَثِيرًا}
INSERT INTO [b9617] VALUES (560,'<span data-type="title" id=toc-553>٨٣ </span>- {وَلَوْلا فَضْلُ اللَّهِ عَلَيْكُمْ وَرَحْمَتُهُ لاتَّبَعْتُمُ الشَّيْطَانَ إِلا قَلِيلا}
INSERT INTO [b9617] VALUES (561,'<span data-type="title" id=toc-554>٨٤ </span>- {فَقَاتِلْ فِي سَبِيلِ اللَّهِ لا تُكَلَّفُ إِلا نَفْسَكَ وَحَرِّضِ الْمُؤْمِنِينَ عَسَى اللَّهُ أَنْ يَكُفَّ بَأْسَ الَّذِينَ كَفَرُوا وَاللَّهُ أَشَدُّ بَأْسًا وَأَشَدُّ تَنْكِيلا}
INSERT INTO [b9617] VALUES (562,'<span data-type="title" id=toc-555>٨٥ </span>- {مَنْ يَشْفَعْ شَفَاعَةً حَسَنَةً يَكُنْ لَهُ نَصِيبٌ مِنْهَا}
INSERT INTO [b9617] VALUES (563,'<span data-type="title" id=toc-556>٨٦ </span>- {وَإِذَا حُيِّيتُمْ بِتَحِيَّةٍ فَحَيُّوا بِأَحْسَنَ مِنْهَا أَوْ رُدُّوهَا إِنَّ اللَّهَ كَانَ عَلَى كُلِّ شَيْءٍ حَسِيبًا}
INSERT INTO [b9617] VALUES (564,'<span data-type="title" id=toc-557>٨٧ </span>- {اللَّهُ لا إِلَهَ إِلا هُوَ لَيَجْمَعَنَّكُمْ إِلَى يَوْمِ الْقِيَامَةِ لا رَيْبَ فِيهِ وَمَنْ أَصْدَقُ مِنَ اللَّهِ حَدِيثًا}
INSERT INTO [b9617] VALUES (565,'<span data-type="title" id=toc-558>٨٨ </span>- {فَمَا لَكُمْ فِي الْمُنَافِقِينَ فِئَتَيْنِ وَاللَّهُ أَرْكَسَهُمْ بِمَا كَسَبُوا}
INSERT INTO [b9617] VALUES (566,'<span data-type="title" id=toc-559>٨٩ </span>- {وَدُّوا لَوْ تَكْفُرُونَ كَمَا كَفَرُوا فَتَكُونُونَ سَوَاءً فَلا تَتَّخِذُوا مِنْهُمْ أَوْلِيَاءَ حَتَّى يُهَاجِرُوا فِي سَبِيلِ اللَّهِ فَإِنْ تَوَلَّوْا فَخُذُوهُمْ وَاقْتُلُوهُمْ حَيْثُ وَجَدْتُمُوهُمْ}
INSERT INTO [b9617] VALUES (567,'<span data-type="title" id=toc-560>٩٠ </span>- {إِلا الَّذِينَ يَصِلُونَ إِلَى قَوْمٍ بَيْنَكُمْ وَبَيْنَهُمْ مِيثَاقٌ أَوْ جَاءُوكُمْ حَصِرَتْ صُدُورُهُمْ أَنْ يُقَاتِلُوكُمْ أَوْ يُقَاتِلُوا قَوْمَهُمْ وَلَوْ شَاءَ اللَّهُ لَسَلَّطَهُمْ عَلَيْكُمْ فَلَقَاتَلُوكُمْ فَإِنِ اعْتَزَلُوكُمْ فَلَمْ يُقَاتِلُوكُمْ وَأَلْقَوْا إِلَيْكُمُ السَّلَمَ فَمَا جَعَلَ اللَّهُ لَكُمْ عَلَيْهِمْ سَبِيلا} ⦗١٩٢⦘
INSERT INTO [b9617] VALUES (568,'<span data-type="title" id=toc-561>٩١ </span>- {سَتَجِدُونَ آخَرِينَ يُرِيدُونَ أَنْ يَأْمَنُوكُمْ وَيَأْمَنُوا قَوْمَهُمْ كُلَّمَا رُدُّوا إِلَى الْفِتْنَةِ أُرْكِسُوا فِيهَا فَإِنْ لَمْ يَعْتَزِلُوكُمْ وَيُلْقُوا إِلَيْكُمُ السَّلَمَ وَيَكُفُّوا أَيْدِيَهُمْ فَخُذُوهُمْ وَاقْتُلُوهُمْ حَيْثُ ثَقِفْتُمُوهُمْ وَأُولَئِكُمْ جَعَلْنَا لَكُمْ عَلَيْهِمْ سُلْطَانًا مُبِينًا}
INSERT INTO [b9617] VALUES (569,'<span data-type="title" id=toc-562>٩٢ </span>- {وَمَا كَانَ لِمُؤْمِنٍ أَنْ يَقْتُلَ مُؤْمِنًا إِلا خَطَأً وَمَنْ قَتَلَ مُؤْمِنًا خَطَأً فَتَحْرِيرُ رَقَبَةٍ مُؤْمِنَةٍ وَدِيَةٌ مُسَلَّمَةٌ إِلَى أَهْلِهِ إِلا أَنْ يَصَّدَّقُوا فَإِنْ كَانَ مِنْ قَوْمٍ عَدُوٍّ لَكُمْ وَهُوَ مُؤْمِنٌ فَتَحْرِيرُ رَقَبَةٍ مُؤْمِنَةٍ وَإِنْ كَانَ مِنْ قَوْمٍ بَيْنَكُمْ وَبَيْنَهُمْ مِيثَاقٌ فَدِيَةٌ مُسَلَّمَةٌ إِلَى أَهْلِهِ وَتَحْرِيرُ رَقَبَةٍ مُؤْمِنَةٍ فَمَنْ لَمْ يَجِدْ فَصِيَامُ شَهْرَيْنِ مُتَتَابِعَيْنِ تَوْبَةً مِنَ اللَّهِ}','1','192',NULL,NULL,'0');
INSERT INTO [b9617] VALUES (570,'اسم كان المصدر المؤول «أن يقتل» . وخبر «كان» متعلَّق الجار والمجرور «لمؤمن» . قوله «إلا خطأ» : نائب مفعول مطلق أي: إلا قَتْلا خطأ. وقوله «فتحرير» : الفاء رابطة، و «تحرير» مبتدأ، والخبر محذوف. أي: فعليه، والجارُّ «إلى أهله» متعلق بـ «مسلمة» . والمصدر «أن يصّدّقوا» مستثنى منقطع؛ لأن الدية ليست من جنس التصدق. وجملة «فإن كان» معطوفة على جملة «من قتل» لا محل لها. وقوله «عدو» : يجوز أن يكون صفة للمفرد والجمع. وجملة «هو مؤمن» حالية قوله «فتحرير» : مبتدأ، خبره مقدر أي: فعليه، والفاء رابطة لجواب الشرط. وجملة «بينكم وبينهم ميثاق» نعت لـ «قوم» . قوله «فدية» : مبتدأ، والخبر مقدر أي: عليه. قوله «فصيام» : مبتدأ، خبره مقدر أي: عليه، و «توبة» مفعول لأجله.','1','193',NULL,NULL,'0');
INSERT INTO [b9617] VALUES (571,'<span data-type="title" id=toc-563>٩٣ </span>- {وَمَنْ يَقْتُلْ مُؤْمِنًا مُتَعَمِّدًا فَجَزَاؤُهُ جَهَنَّمُ خَالِدًا فِيهَا وَغَضِبَ اللَّهُ عَلَيْهِ وَلَعَنَهُ وَأَعَدَّ لَهُ عَذَابًا عَظِيمًا}
INSERT INTO [b9617] VALUES (572,'<span data-type="title" id=toc-564>٩٤ </span>- {يَا أَيُّهَا الَّذِينَ آمَنُوا إِذَا ضَرَبْتُمْ فِي سَبِيلِ اللَّهِ فَتَبَيَّنُوا وَلا تَقُولُوا لِمَنْ أَلْقَى إِلَيْكُمُ السَّلامَ لَسْتَ مُؤْمِنًا تَبْتَغُونَ عَرَضَ الْحَيَاةِ الدُّنْيَا فَعِنْدَ اللَّهِ مَغَانِمُ كَثِيرَةٌ كَذَلِكَ كُنْتُمْ مِنْ قَبْلُ فَمَنَّ اللَّهُ عَلَيْكُمْ فَتَبَيَّنُوا} ⦗١٩٤⦘
INSERT INTO [b9617] VALUES (573,'<span data-type="title" id=toc-565>٩٥ </span>- {لا يَسْتَوِي الْقَاعِدُونَ مِنَ الْمُؤْمِنِينَ غَيْرُ أُولِي الضَّرَرِ وَالْمُجَاهِدُونَ فِي سَبِيلِ اللَّهِ بِأَمْوَالِهِمْ وَأَنْفُسِهِمْ فَضَّلَ اللَّهُ الْمُجَاهِدِينَ بِأَمْوَالِهِمْ وَأَنْفُسِهِمْ عَلَى الْقَاعِدِينَ دَرَجَةً وَكُلا وَعَدَ اللَّهُ الْحُسْنَى وَفَضَّلَ اللَّهُ الْمُجَاهِدِينَ عَلَى الْقَاعِدِينَ أَجْرًا عَظِيمًا}
INSERT INTO [b9617] VALUES (574,'<span data-type="title" id=toc-566>٩٦ </span>- {دَرَجَاتٍ مِنْهُ وَمَغْفِرَةً وَرَحْمَةً}
INSERT INTO [b9617] VALUES (575,'<span data-type="title" id=toc-567>٩٧ </span>- {إِنَّ الَّذِينَ تَوَفَّاهُمُ الْمَلائِكَةُ ظَالِمِي أَنْفُسِهِمْ قَالُوا فِيمَ كُنْتُمْ قَالُوا كُنَّا مُسْتَضْعَفِينَ فِي الأَرْضِ قَالُوا أَلَمْ تَكُنْ أَرْضُ اللَّهِ وَاسِعَةً فَتُهَاجِرُوا فِيهَا فَأُولَئِكَ مَأْوَاهُمْ جَهَنَّمُ وَسَاءَتْ مَصِيرًا}
INSERT INTO [b9617] VALUES (576,'<span data-type="title" id=toc-568>٩٨ </span>- {إِلا الْمُسْتَضْعَفِينَ مِنَ الرِّجَالِ وَالنِّسَاءِ وَالْوِلْدَانِ لا يَسْتَطِيعُونَ حِيلَةً}
INSERT INTO [b9617] VALUES (577,'<span data-type="title" id=toc-569>١٠١ </span>- {فَلَيْسَ عَلَيْكُمْ جُنَاحٌ أَنْ تَقْصُرُوا مِنَ الصَّلاةِ إِنْ خِفْتُمْ أَنْ يَفْتِنَكُمُ الَّذِينَ كَفَرُوا إِنَّ الْكَافِرِينَ كَانُوا لَكُمْ عَدُوًّا مُبِينًا}
INSERT INTO [b9617] VALUES (578,'<span data-type="title" id=toc-570>١٠٢ </span>- {وَإِذَا كُنْتَ فِيهِمْ فَأَقَمْتَ لَهُمُ الصَّلاةَ فَلْتَقُمْ طَائِفَةٌ مِنْهُمْ مَعَكَ وَلْيَأْخُذُوا أَسْلِحَتَهُمْ فَإِذَا سَجَدُوا فَلْيَكُونُوا مِنْ وَرَائِكُمْ وَلْتَأْتِ طَائِفَةٌ أُخْرَى لَمْ يُصَلُّوا فَلْيُصَلُّوا مَعَكَ وَلْيَأْخُذُوا حِذْرَهُمْ وَأَسْلِحَتَهُمْ وَدَّ الَّذِينَ كَفَرُوا لَوْ تَغْفُلُونَ عَنْ أَسْلِحَتِكُمْ وَأَمْتِعَتِكُمْ فَيَمِيلُونَ عَلَيْكُمْ مَيْلَةً وَاحِدَةً وَلا جُنَاحَ عَلَيْكُمْ إِنْ كَانَ بِكُمْ أَذًى مِنْ مَطَرٍ أَوْ كُنْتُمْ مَرْضَى ⦗١٩٦⦘ أَنْ تَضَعُوا أَسْلِحَتَكُمْ}
INSERT INTO [b9617] VALUES (579,'<span data-type="title" id=toc-571>١٠٣ </span>- {فَإِذَا قَضَيْتُمُ الصَّلاةَ فَاذْكُرُوا اللَّهَ قِيَامًا وَقُعُودًا وَعَلَى جُنُوبِكُمْ فَإِذَا اطْمَأْنَنْتُمْ فَأَقِيمُوا الصَّلاةَ إِنَّ الصَّلاةَ كَانَتْ عَلَى الْمُؤْمِنِينَ كِتَابًا مَوْقُوتًا}
INSERT INTO [b9617] VALUES (580,'<span data-type="title" id=toc-572>١٠٤ </span>- {وَلا تَهِنُوا فِي ابْتِغَاءِ الْقَوْمِ إِنْ تَكُونُوا تَأْلَمُونَ فَإِنَّهُمْ يَأْلَمُونَ كَمَا تَأْلَمُونَ وَتَرْجُونَ مِنَ اللَّهِ مَا لا يَرْجُونَ وَكَانَ اللَّهُ عَلِيمًا حَكِيمًا}
INSERT INTO [b9617] VALUES (581,'<span data-type="title" id=toc-573>١٠٥ </span>- {إِنَّا أَنْزَلْنَا إِلَيْكَ الْكِتَابَ بِالْحَقِّ لِتَحْكُمَ بَيْنَ النَّاسِ بِمَا أَرَاكَ اللَّهُ وَلا تَكُنْ ⦗١٩٧⦘ لِلْخَائِنِينَ خَصِيمًا}
INSERT INTO [b9617] VALUES (582,'<span data-type="title" id=toc-574>١٠٧ </span>- {وَلا تُجَادِلْ عَنِ الَّذِينَ يَخْتَانُونَ أَنْفُسَهُمْ إِنَّ اللَّهَ لا يُحِبُّ مَنْ كَانَ خَوَّانًا أَثِيمًا}
INSERT INTO [b9617] VALUES (583,'<span data-type="title" id=toc-575>١٠٨ </span>- {يَسْتَخْفُونَ مِنَ النَّاسِ وَلا يَسْتَخْفُونَ مِنَ اللَّهِ وَهُوَ مَعَهُمْ إِذْ يُبَيِّتُونَ مَا لا يَرْضَى مِنَ الْقَوْلِ وَكَانَ اللَّهُ بِمَا يَعْمَلُونَ مُحِيطًا}
INSERT INTO [b9617] VALUES (584,'<span data-type="title" id=toc-576>١٠٩ </span>- {هَا أَنْتُمْ هَؤُلاءِ جَادَلْتُمْ عَنْهُمْ فِي الْحَيَاةِ الدُّنْيَا فَمَنْ يُجَادِلُ اللَّهَ عَنْهُمْ يَوْمَ الْقِيَامَةِ أَمْ مَنْ يَكُونُ عَلَيْهِمْ وَكِيلا}
INSERT INTO [b9617] VALUES (585,'<span data-type="title" id=toc-577>١١٠ </span>- {وَمَنْ يَعْمَلْ سُوءًا أَوْ يَظْلِمْ نَفْسَهُ ثُمَّ يَسْتَغْفِرِ اللَّهَ يَجِدِ اللَّهَ غَفُورًا رَحِيمًا}
INSERT INTO [b9617] VALUES (586,'<span data-type="title" id=toc-578>١١١ </span>- {وَكَانَ اللَّهُ عَلِيمًا حَكِيمًا}
INSERT INTO [b9617] VALUES (587,'<span data-type="title" id=toc-579>١١٢ </span>- {وَمَنْ يَكْسِبْ خَطِيئَةً أَوْ إِثْمًا ثُمَّ يَرْمِ بِهِ بَرِيئًا}
INSERT INTO [b9617] VALUES (588,'<span data-type="title" id=toc-580>١١٣ </span>- {وَلَوْلا فَضْلُ اللَّهِ عَلَيْكَ وَرَحْمَتُهُ لَهَمَّتْ طَائِفَةٌ مِنْهُمْ أَنْ يُضِلُّوكَ وَمَا يُضِلُّونَ إِلا أَنْفُسَهُمْ وَمَا يَضُرُّونَكَ مِنْ شَيْءٍ وَأَنْزَلَ اللَّهُ عَلَيْكَ الْكِتَابَ وَالْحِكْمَةَ وَعَلَّمَكَ مَا لَمْ تَكُنْ تَعْلَمُ وَكَانَ فَضْلُ اللَّهِ عَلَيْكَ عَظِيمًا}
INSERT INTO [b9617] VALUES (589,'<span data-type="title" id=toc-581>١١٤ </span>- {لا خَيْرَ فِي كَثِيرٍ مِنْ نَجْوَاهُمْ إِلا مَنْ أَمَرَ بِصَدَقَةٍ أَوْ مَعْرُوفٍ أَوْ إِصْلاحٍ بَيْنَ النَّاسِ وَمَنْ يَفْعَلْ ذَلِكَ ابْتِغَاءَ مَرْضَاتِ اللَّهِ فَسَوْفَ نُؤْتِيهِ أَجْرًا عَظِيمًا}
INSERT INTO [b9617] VALUES (590,'<span data-type="title" id=toc-582>١١٥ </span>- {وَمَنْ يُشَاقِقِ الرَّسُولَ مِنْ بَعْدِ مَا تَبَيَّنَ لَهُ الْهُدَى وَيَتَّبِعْ غَيْرَ سَبِيلِ الْمُؤْمِنِينَ نُوَلِّهِ مَا تَوَلَّى وَنُصْلِهِ جَهَنَّمَ وَسَاءَتْ مَصِيرًا}
INSERT INTO [b9617] VALUES (591,'<span data-type="title" id=toc-583>١١٦ </span>- {وَيَغْفِرُ مَا دُونَ ذَلِكَ لِمَنْ يَشَاءُ}
INSERT INTO [b9617] VALUES (592,'<span data-type="title" id=toc-584>١١٧ </span>- {إِنْ يَدْعُونَ مِنْ دُونِهِ إِلا إِنَاثًا}
INSERT INTO [b9617] VALUES (593,'<span data-type="title" id=toc-585>١١٨ </span>- {لَعَنَهُ اللَّهُ وَقَالَ لأَتَّخِذَنَّ مِنْ عِبَادِكَ نَصِيبًا مَفْرُوضًا}
INSERT INTO [b9617] VALUES (594,'<span data-type="title" id=toc-586>١١٩ </span>- {وَلأُضِلَّنَّهُمْ وَلأُمَنِّيَنَّهُمْ وَلآمُرَنَّهُمْ فَلَيُبَتِّكُنَّ آذَانَ الأَنْعَامِ وَلآمُرَنَّهُمْ فَلَيُغَيِّرُنَّ خَلْقَ اللَّهِ وَمَنْ يَتَّخِذِ الشَّيْطَانَ وَلِيًّا مِنْ دُونِ اللَّهِ فَقَدْ خَسِرَ خُسْرَانًا مُبِينًا}
INSERT INTO [b9617] VALUES (595,'<span data-type="title" id=toc-587>١٢٠ </span>- {يَعِدُهُمْ وَيُمَنِّيهِمْ وَمَا يَعِدُهُمُ الشَّيْطَانُ إِلا غُرُورًا}
INSERT INTO [b9617] VALUES (596,'<span data-type="title" id=toc-588>١٢١ </span>- {أُولَئِكَ مَأْوَاهُمْ جَهَنَّمُ وَلا يَجِدُونَ عَنْهَا مَحِيصًا}
INSERT INTO [b9617] VALUES (597,'<span data-type="title" id=toc-589>١٢٢ </span>- {وَعْدَ اللَّهِ حَقًّا وَمَنْ أَصْدَقُ مِنَ اللَّهِ قِيلا}
INSERT INTO [b9617] VALUES (598,'<span data-type="title" id=toc-590>١٢٣ </span>- {لَيْسَ بِأَمَانِيِّكُمْ وَلا أَمَانِيِّ أَهْلِ الْكِتَابِ مَنْ يَعْمَلْ سُوءًا يُجْزَ بِهِ وَلا يَجِدْ لَهُ مِنْ دُونِ اللَّهِ وَلِيًّا وَلا نَصِيرًا} ⦗٢٠١⦘
INSERT INTO [b9617] VALUES (599,'<span data-type="title" id=toc-591>١٢٤ </span>- {وَمَنْ يَعْمَلْ مِنَ الصَّالِحَاتِ مِنْ ذَكَرٍ أَوْ أُنْثَى وَهُوَ مُؤْمِنٌ فَأُولَئِكَ يَدْخُلُونَ الْجَنَّةَ وَلا يُظْلَمُونَ نَقِيرًا}
INSERT INTO [b9617] VALUES (600,'<span data-type="title" id=toc-592>١٢٥ </span>- {وَمَنْ أَحْسَنُ دِينًا مِمَّنْ أَسْلَمَ وَجْهَهُ لِلَّهِ وَهُوَ مُحْسِنٌ وَاتَّبَعَ مِلَّةَ إِبْرَاهِيمَ حَنِيفًا وَاتَّخَذَ اللَّهُ إِبْرَاهِيمَ خَلِيلا}
INSERT INTO [b9617] VALUES (601,'<span data-type="title" id=toc-593>١٢٦ </span>- {وَلِلَّهِ مَا فِي السَّمَاوَاتِ وَمَا فِي الأَرْضِ وَكَانَ اللَّهُ بِكُلِّ شَيْءٍ مُحِيطًا}
INSERT INTO [b9617] VALUES (602,'<span data-type="title" id=toc-594>١٢٧ </span>- {قُلِ اللَّهُ يُفْتِيكُمْ فِيهِنَّ وَمَا يُتْلَى عَلَيْكُمْ فِي الْكِتَابِ فِي يَتَامَى النِّسَاءِ اللاتِي لا تُؤْتُونَهُنَّ مَا كُتِبَ لَهُنَّ وَتَرْغَبُونَ أَنْ تَنْكِحُوهُنَّ وَالْمُسْتَضْعَفِينَ مِنَ الْوِلْدَانِ وَأَنْ تَقُومُوا لِلْيَتَامَى بِالْقِسْطِ وَمَا تَفْعَلُوا مِنْ خَيْرٍ فَإِنَّ اللَّهَ كَانَ بِهِ عَلِيمًا} ⦗٢٠٢⦘
INSERT INTO [b9617] VALUES (603,'<span data-type="title" id=toc-595>١٢٨ </span>- {وَإِنِ امْرَأَةٌ خَافَتْ مِنْ بَعْلِهَا نُشُوزًا أَوْ إِعْرَاضًا فَلا جُنَاحَ عَلَيْهِمَا أَنْ يُصْلِحَا بَيْنَهُمَا صُلْحًا وَالصُّلْحُ خَيْرٌ وَأُحْضِرَتِ الأَنْفُسُ الشُّحَّ وَإِنْ تُحْسِنُوا وَتَتَّقُوا فَإِنَّ اللَّهَ كَانَ بِمَا تَعْمَلُونَ خَبِيرًا}
INSERT INTO [b9617] VALUES (604,'<span data-type="title" id=toc-596>١٢٩ </span>- {وَلَنْ تَسْتَطِيعُوا أَنْ تَعْدِلُوا بَيْنَ النِّسَاءِ وَلَوْ حَرَصْتُمْ فَلا تَمِيلُوا كُلَّ الْمَيْلِ فَتَذَرُوهَا كَالْمُعَلَّقَةِ}
INSERT INTO [b9617] VALUES (605,'<span data-type="title" id=toc-597>١٣٠ </span>- {يُغْنِ اللَّهُ كُلا مِنْ سَعَتِهِ}
INSERT INTO [b9617] VALUES (606,'<span data-type="title" id=toc-598>١٣١ </span>- {وَلَقَدْ وَصَّيْنَا الَّذِينَ أُوتُوا الْكِتَابَ مِنْ قَبْلِكُمْ وَإِيَّاكُمْ أَنِ اتَّقُوا اللَّهَ وَإِنْ تَكْفُرُوا فَإِنَّ لِلَّهِ مَا فِي السَّمَاوَاتِ وَمَا فِي الأَرْضِ وَكَانَ اللَّهُ غَنِيًّا حَمِيدًا}
INSERT INTO [b9617] VALUES (607,'<span data-type="title" id=toc-599>١٣٣ </span>- {إِنْ يَشَأْ يُذْهِبْكُمْ أَيُّهَا النَّاسُ وَيَأْتِ بِآخَرِينَ}
INSERT INTO [b9617] VALUES (608,'<span data-type="title" id=toc-600>١٣٤ </span>- {مَنْ كَانَ يُرِيدُ ثَوَابَ الدُّنْيَا فَعِنْدَ اللَّهِ ثَوَابُ الدُّنْيَا وَالآخِرَةِ}
INSERT INTO [b9617] VALUES (609,'<span data-type="title" id=toc-601>١٣٥ </span>- {يَا أَيُّهَا الَّذِينَ آمَنُوا كُونُوا قَوَّامِينَ بِالْقِسْطِ شُهَدَاءَ لِلَّهِ وَلَوْ عَلَى أَنْفُسِكُمْ أَوِ الْوَالِدَيْنِ وَالأَقْرَبِينَ إِنْ يَكُنْ غَنِيًّا أَوْ فَقِيرًا فَاللَّهُ أَوْلَى بِهِمَا فَلا تَتَّبِعُوا الْهَوَى أَنْ تَعْدِلُوا}
INSERT INTO [b9617] VALUES (610,'<span data-type="title" id=toc-602>١٣٧ </span>- {إِنَّ الَّذِينَ آمَنُوا ثُمَّ كَفَرُوا ثُمَّ آمَنُوا ثُمَّ كَفَرُوا ثُمَّ ازْدَادُوا كُفْرًا لَمْ يَكُنِ اللَّهُ لِيَغْفِرَ لَهُمْ}
INSERT INTO [b9617] VALUES (611,'<span data-type="title" id=toc-603>١٣٩ </span>- {الَّذِينَ يَتَّخِذُونَ الْكَافِرِينَ أَوْلِيَاءَ مِنْ دُونِ الْمُؤْمِنِينَ أَيَبْتَغُونَ عِنْدَهُمُ الْعِزَّةَ فَإِنَّ الْعِزَّةَ لِلَّهِ جَمِيعًا}
INSERT INTO [b9617] VALUES (612,'<span data-type="title" id=toc-604>١٤٠ </span>- {وَقَدْ نَزَّلَ عَلَيْكُمْ فِي الْكِتَابِ أَنْ إِذَا سَمِعْتُمْ آيَاتِ اللَّهِ يُكْفَرُ بِهَا وَيُسْتَهْزَأُ بِهَا فَلا تَقْعُدُوا مَعَهُمْ حَتَّى يَخُوضُوا فِي حَدِيثٍ غَيْرِهِ إِنَّكُمْ إِذًا مِثْلُهُمْ}
INSERT INTO [b9617] VALUES (613,'<span data-type="title" id=toc-605>١٤١ </span>- {الَّذِينَ يَتَرَبَّصُونَ بِكُمْ فَإِنْ كَانَ لَكُمْ فَتْحٌ مِنَ اللَّهِ قَالُوا أَلَمْ نَكُنْ مَعَكُمْ وَإِنْ كَانَ لِلْكَافِرِينَ نَصِيبٌ قَالُوا أَلَمْ نَسْتَحْوِذْ عَلَيْكُمْ وَنَمْنَعْكُمْ مِنَ الْمُؤْمِنِينَ فَاللَّهُ يَحْكُمُ بَيْنَكُمْ يَوْمَ الْقِيَامَةِ وَلَنْ يَجْعَلَ اللَّهُ لِلْكَافِرِينَ عَلَى الْمُؤْمِنِينَ سَبِيلا}
INSERT INTO [b9617] VALUES (614,'<span data-type="title" id=toc-606>١٤٢ </span>- {إِنَّ الْمُنَافِقِينَ يُخَادِعُونَ اللَّهَ وَهُوَ خَادِعُهُمْ وَإِذَا قَامُوا إِلَى الصَّلاةِ قَامُوا كُسَالَى ⦗٢٠٦⦘ يُرَاءُونَ النَّاسَ وَلا يَذْكُرُونَ اللَّهَ إِلا قَلِيلا}
INSERT INTO [b9617] VALUES (615,'<span data-type="title" id=toc-607>١٤٣ </span>- {مُذَبْذَبِينَ بَيْنَ ذَلِكَ لا إِلَى هَؤُلاءِ وَلا إِلَى هَؤُلاءِ}
INSERT INTO [b9617] VALUES (616,'<span data-type="title" id=toc-608>١٤٤ </span>- {يَا أَيُّهَا الَّذِينَ آمَنُوا لا تَتَّخِذُوا الْكَافِرِينَ أَوْلِيَاءَ مِنْ دُونِ الْمُؤْمِنِينَ أَتُرِيدُونَ أَنْ تَجْعَلُوا لِلَّهِ عَلَيْكُمْ سُلْطَانًا مُبِينًا}
INSERT INTO [b9617] VALUES (617,'<span data-type="title" id=toc-609>١٤٥ </span>- {إِنَّ الْمُنَافِقِينَ فِي الدَّرْكِ الأَسْفَلِ مِنَ النَّارِ وَلَنْ تَجِدَ لَهُمْ نَصِيرًا}
INSERT INTO [b9617] VALUES (618,'<span data-type="title" id=toc-610>١٤٦ </span>- {وَسَوْفَ يُؤْتِ اللَّهُ الْمُؤْمِنِينَ أَجْرًا عَظِيمًا}
INSERT INTO [b9617] VALUES (619,'<span data-type="title" id=toc-611>١٤٧ </span>- {مَا يَفْعَلُ اللَّهُ بِعَذَابِكُمْ إِنْ شَكَرْتُمْ وَآمَنْتُمْ}
INSERT INTO [b9617] VALUES (620,'<span data-type="title" id=toc-612>١٤٨ </span>- {لا يُحِبُّ اللَّهُ الْجَهْرَ بِالسُّوءِ مِنَ الْقَوْلِ إِلا مَنْ ظُلِمَ}
INSERT INTO [b9617] VALUES (621,'<span data-type="title" id=toc-613>١٤٩ </span>- {إِنْ تُبْدُوا خَيْرًا أَوْ تُخْفُوهُ أَوْ تَعْفُوا عَنْ سُوءٍ فَإِنَّ اللَّهَ كَانَ عَفُوًّا قَدِيرًا}
INSERT INTO [b9617] VALUES (622,'<span data-type="title" id=toc-614>١٥٠ </span>- {وَيُرِيدُونَ أَنْ يُفَرِّقُوا بَيْنَ اللَّهِ وَرُسُلِهِ}
INSERT INTO [b9617] VALUES (623,'<span data-type="title" id=toc-615>١٥١ </span>- {أُولَئِكَ هُمُ الْكَافِرُونَ حَقًّا وَأَعْتَدْنَا لِلْكَافِرِينَ عَذَابًا مُهِينًا} ⦗٢٠٨⦘
INSERT INTO [b9617] VALUES (624,'<span data-type="title" id=toc-616>١٥٢ </span>- {وَالَّذِينَ آمَنُوا بِاللَّهِ وَرُسُلِهِ وَلَمْ يُفَرِّقُوا بَيْنَ أَحَدٍ مِنْهُمْ أُولَئِكَ سَوْفَ يُؤْتِيهِمْ أُجُورَهُمْ}
INSERT INTO [b9617] VALUES (625,'<span data-type="title" id=toc-617>١٥٣ </span>- {يَسْأَلُكَ أَهْلُ الْكِتَابِ أَنْ تُنَزِّلَ عَلَيْهِمْ كِتَابًا مِنَ السَّمَاءِ فَقَدْ سَأَلُوا مُوسَى أَكْبَرَ مِنْ ذَلِكَ فَقَالُوا أَرِنَا اللَّهَ جَهْرَةً فَأَخَذَتْهُمُ الصَّاعِقَةُ بِظُلْمِهِمْ ثُمَّ اتَّخَذُوا الْعِجْلَ مِنْ بَعْدِ مَا جَاءَتْهُمُ الْبَيِّنَاتُ}
INSERT INTO [b9617] VALUES (626,'<span data-type="title" id=toc-618>١٥٤ </span>- {ادْخُلُوا الْبَابَ سُجَّدًا}
INSERT INTO [b9617] VALUES (627,'<span data-type="title" id=toc-619>١٥٥ </span>- {فَبِمَا نَقْضِهِمْ مِيثَاقَهُمْ وَكُفْرِهِمْ بِآيَاتِ اللَّهِ وَقَتْلِهِمُ الأَنْبِيَاءَ بِغَيْرِ حَقٍّ وَقَوْلِهِمْ قُلُوبُنَا غُلْفٌ بَلْ طَبَعَ اللَّهُ عَلَيْهَا بِكُفْرِهِمْ فَلا يُؤْمِنُونَ إِلا قَلِيلا} ⦗٢٠٩⦘
INSERT INTO [b9617] VALUES (628,'<span data-type="title" id=toc-620>١٥٦ </span>- {وَبِكُفْرِهِمْ وَقَوْلِهِمْ عَلَى مَرْيَمَ بُهْتَانًا عَظِيمًا}
INSERT INTO [b9617] VALUES (629,'<span data-type="title" id=toc-621>١٥٧ </span>- {وَمَا قَتَلُوهُ وَمَا صَلَبُوهُ وَلَكِنْ شُبِّهَ لَهُمْ وَإِنَّ الَّذِينَ اخْتَلَفُوا فِيهِ لَفِي شَكٍّ مِنْهُ مَا لَهُمْ بِهِ مِنْ عِلْمٍ إِلا اتِّبَاعَ الظَّنِّ وَمَا قَتَلُوهُ يَقِينًا}
INSERT INTO [b9617] VALUES (630,'<span data-type="title" id=toc-622>١٥٩ </span>- {وَإِنْ مِنْ أَهْلِ الْكِتَابِ إِلا لَيُؤْمِنَنَّ بِهِ قَبْلَ مَوْتِهِ وَيَوْمَ الْقِيَامَةِ يَكُونُ عَلَيْهِمْ شَهِيدًا}
INSERT INTO [b9617] VALUES (631,'<span data-type="title" id=toc-623>١٦٠ </span>- {فَبِظُلْمٍ مِنَ الَّذِينَ هَادُوا حَرَّمْنَا عَلَيْهِمْ طَيِّبَاتٍ أُحِلَّتْ لَهُمْ وَبِصَدِّهِمْ عَنْ سَبِيلِ اللَّهِ كَثِيرًا}
INSERT INTO [b9617] VALUES (632,'<span data-type="title" id=toc-624>١٦١ </span>- {وَأَخْذِهِمُ الرِّبَا وَقَدْ نُهُوا عَنْهُ}
INSERT INTO [b9617] VALUES (633,'<span data-type="title" id=toc-625>١٦٢ </span>- {لَكِنِ الرَّاسِخُونَ فِي الْعِلْمِ مِنْهُمْ وَالْمُؤْمِنُونَ يُؤْمِنُونَ بِمَا أُنْزِلَ إِلَيْكَ وَمَا أُنْزِلَ مِنْ قَبْلِكَ وَالْمُقِيمِينَ الصَّلاةَ وَالْمُؤْتُونَ الزَّكَاةَ وَالْمُؤْمِنُونَ بِاللَّهِ وَالْيَوْمِ الآخِرِ أُولَئِكَ سَنُؤْتِيهِمْ أَجْرًا عَظِيمًا}
INSERT INTO [b9617] VALUES (634,'<span data-type="title" id=toc-626>١٦٣ </span>- {إِنَّا أَوْحَيْنَا إِلَيْكَ كَمَا أَوْحَيْنَا إِلَى نُوحٍ}
INSERT INTO [b9617] VALUES (635,'<span data-type="title" id=toc-627>١٦٤ </span>- {وَرُسُلا قَدْ قَصَصْنَاهُمْ عَلَيْكَ مِنْ قَبْلُ وَرُسُلا لَمْ نَقْصُصْهُمْ عَلَيْكَ وَكَلَّمَ اللَّهُ مُوسَى تَكْلِيمًا}
INSERT INTO [b9617] VALUES (636,'<span data-type="title" id=toc-628>١٦٥ </span>- {رُسُلا مُبَشِّرِينَ وَمُنْذِرِينَ لِئَلا يَكُونَ لِلنَّاسِ عَلَى اللَّهِ حُجَّةٌ بَعْدَ الرُّسُلِ وَكَانَ اللَّهُ عَزِيزًا حَكِيمًا}
INSERT INTO [b9617] VALUES (637,'<span data-type="title" id=toc-629>١٦٦ </span>- {لَكِنِ اللَّهُ يَشْهَدُ بِمَا أَنْزَلَ إِلَيْكَ أَنْزَلَهُ بِعِلْمِهِ وَالْمَلائِكَةُ يَشْهَدُونَ وَكَفَى بِاللَّهِ شَهِيدًا} ⦗٢١٢⦘
INSERT INTO [b9617] VALUES (638,'<span data-type="title" id=toc-630>١٦٨ </span>- {وَلا لِيَهْدِيَهُمْ طَرِيقًا}
INSERT INTO [b9617] VALUES (639,'<span data-type="title" id=toc-631>١٦٩ </span>- {إِلا طَرِيقَ جَهَنَّمَ خَالِدِينَ فِيهَا أَبَدًا وَكَانَ ذَلِكَ عَلَى اللَّهِ يَسِيرًا}
INSERT INTO [b9617] VALUES (640,'<span data-type="title" id=toc-632>١٧٠ </span>- {يَا أَيُّهَا النَّاسُ قَدْ جَاءَكُمُ الرَّسُولُ بِالْحَقِّ مِنْ رَبِّكُمْ فَآمِنُوا خَيْرًا لَكُمْ وَإِنْ تَكْفُرُوا فَإِنَّ لِلَّهِ مَا فِي السَّمَاوَاتِ وَالأَرْضِ وَكَانَ اللَّهُ عَلِيمًا حَكِيمًا}
INSERT INTO [b9617] VALUES (641,'<span data-type="title" id=toc-633>١٧١ </span>- {وَلا تَقُولُوا عَلَى اللَّهِ إِلا الْحَقَّ إِنَّمَا الْمَسِيحُ عِيسَى ابْنُ مَرْيَمَ رَسُولُ اللَّهِ وَكَلِمَتُهُ أَلْقَاهَا إِلَى مَرْيَمَ وَرُوحٌ مِنْهُ فَآمِنُوا بِاللَّهِ وَرُسُلِهِ وَلا تَقُولُوا ثَلاثَةٌ انْتَهُوا خَيْرًا لَكُمْ إِنَّمَا اللَّهُ إِلَهٌ وَاحِدٌ سُبْحَانَهُ أَنْ يَكُونَ لَهُ وَلَدٌ لَهُ مَا فِي السَّمَاوَاتِ وَمَا فِي الأَرْضِ وَكَفَى بِاللَّهِ وَكِيلا}
INSERT INTO [b9617] VALUES (642,'<span data-type="title" id=toc-634>١٧٢ </span>- {لَنْ يَسْتَنْكِفَ الْمَسِيحُ أَنْ يَكُونَ عَبْدًا لِلَّهِ وَلا الْمَلائِكَةُ الْمُقَرَّبُونَ وَمَنْ يَسْتَنْكِفْ عَنْ عِبَادَتِهِ وَيَسْتَكْبِرْ فَسَيَحْشُرُهُمْ إِلَيْهِ جَمِيعًا}
INSERT INTO [b9617] VALUES (643,'<span data-type="title" id=toc-635>١٧٣ </span>- {فَأَمَّا الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ فَيُوَفِّيهِمْ أُجُورَهُمْ وَيَزِيدُهُمْ مِنْ فَضْلِهِ وَأَمَّا الَّذِينَ اسْتَنْكَفُوا وَاسْتَكْبَرُوا فَيُعَذِّبُهُمْ عَذَابًا أَلِيمًا وَلا يَجِدُونَ لَهُمْ مِنْ دُونِ اللَّهِ وَلِيًّا وَلا نَصِيرًا}
INSERT INTO [b9617] VALUES (644,'<span data-type="title" id=toc-636>١٧٤ </span>- {يَا أَيُّهَا النَّاسُ قَدْ جَاءَكُمْ بُرْهَانٌ مِنْ رَبِّكُمْ وَأَنْزَلْنَا إِلَيْكُمْ نُورًا مُبِينًا}
INSERT INTO [b9617] VALUES (645,'<span data-type="title" id=toc-637>١٧٥ </span>- {فَأَمَّا الَّذِينَ آمَنُوا بِاللَّهِ وَاعْتَصَمُوا بِهِ فَسَيُدْخِلُهُمْ فِي رَحْمَةٍ مِنْهُ وَفَضْلٍ ⦗٢١٤⦘ وَيَهْدِيهِمْ إِلَيْهِ صِرَاطًا مُسْتَقِيمًا}
INSERT INTO [b9617] VALUES (646,'<span data-type="title" id=toc-638>١٧٦ </span>- {قُلِ اللَّهُ يُفْتِيكُمْ فِي الْكَلالَةِ إِنِ امْرُؤٌ هَلَكَ لَيْسَ لَهُ وَلَدٌ وَلَهُ أُخْتٌ فَلَهَا نِصْفُ مَا تَرَكَ وَهُوَ يَرِثُهَا إِنْ لَمْ يَكُنْ لَهَا وَلَدٌ فَإِنْ كَانَتَا اثْنَتَيْنِ فَلَهُمَا الثُّلُثَانِ مِمَّا تَرَكَ وَإِنْ كَانُوا إِخْوَةً رِجَالا وَنِسَاءً فَلِلذَّكَرِ مِثْلُ حَظِّ الأُنْثَيَيْنِ يُبَيِّنُ اللَّهُ لَكُمْ أَنْ تَضِلُّوا وَاللَّهُ بِكُلِّ شَيْءٍ عَلِيمٌ}
INSERT INTO [b9617] VALUES (647,'<span data-type="title" id=toc-639>سورة المائدة</span>','1','215',NULL,NULL,'0');
INSERT INTO [b9617] VALUES (648,'<span data-type="title" id=toc-640>١ </span>- {أُحِلَّتْ لَكُمْ بَهِيمَةُ الأَنْعَامِ إِلا مَا يُتْلَى عَلَيْكُمْ غَيْرَ مُحِلِّي الصَّيْدِ وَأَنْتُمْ حُرُمٌ}
INSERT INTO [b9617] VALUES (649,'<span data-type="title" id=toc-641>٢ </span>- {وَلا الْقَلائِدَ وَلا آمِّينَ الْبَيْتَ الْحَرَامَ يَبْتَغُونَ فَضْلا مِنْ رَبِّهِمْ وَرِضْوَانًا وَإِذَا حَلَلْتُمْ فَاصْطَادُوا وَلا يَجْرِمَنَّكُمْ شَنَآنُ قَوْمٍ أَنْ صَدُّوكُمْ عَنِ الْمَسْجِدِ الْحَرَامِ أَنْ تَعْتَدُوا وَتَعَاوَنُوا عَلَى الْبِرِّ وَالتَّقْوَى وَلا تَعَاوَنُوا عَلَى الإثْمِ وَالْعُدْوَانِ وَاتَّقُوا اللَّهَ إِنَّ اللَّهَ شَدِيدُ الْعِقَابِ}
INSERT INTO [b9617] VALUES (650,'<span data-type="title" id=toc-642>٣ </span>- {إِلا مَا ذَكَّيْتُمْ وَمَا ذُبِحَ عَلَى النُّصُبِ وَأَنْ تَسْتَقْسِمُوا بِالأَزْلامِ ذَلِكُمْ فِسْقٌ الْيَوْمَ يَئِسَ الَّذِينَ كَفَرُوا مِنْ دِينِكُمْ فَلا تَخْشَوْهُمْ وَاخْشَوْنِ الْيَوْمَ أَكْمَلْتُ لَكُمْ دِينَكُمْ وَأَتْمَمْتُ عَلَيْكُمْ نِعْمَتِي وَرَضِيتُ لَكُمُ الإِسْلامَ دِينًا فَمَنِ اضْطُرَّ فِي مَخْمَصَةٍ غَيْرَ مُتَجَانِفٍ لإِثْمٍ فَإِنَّ اللَّهَ غَفُورٌ رَحِيمٌ} ⦗٢١٦⦘
INSERT INTO [b9617] VALUES (651,'<span data-type="title" id=toc-643>٤ </span>- {يَسْأَلُونَكَ مَاذَا أُحِلَّ لَهُمْ قُلْ أُحِلَّ لَكُمُ الطَّيِّبَاتُ وَمَا عَلَّمْتُمْ مِنَ الْجَوَارِحِ مُكَلِّبِينَ تُعَلِّمُونَهُنَّ مِمَّا عَلَّمَكُمُ اللَّهُ فَكُلُوا مِمَّا أَمْسَكْنَ عَلَيْكُمْ}
INSERT INTO [b9617] VALUES (652,'<span data-type="title" id=toc-644>٥ </span>- {الْيَوْمَ أُحِلَّ لَكُمُ الطَّيِّبَاتُ وَطَعَامُ الَّذِينَ أُوتُوا الْكِتَابَ حِلٌّ لَكُمْ وَطَعَامُكُمْ حِلٌّ لَهُمْ وَالْمُحْصَنَاتُ مِنَ الْمُؤْمِنَاتِ وَالْمُحْصَنَاتُ مِنَ الَّذِينَ أُوتُوا الْكِتَابَ مِنْ قَبْلِكُمْ إِذَا آتَيْتُمُوهُنَّ أُجُورَهُنَّ مُحْصِنِينَ غَيْرَ مُسَافِحِينَ وَلا مُتَّخِذِي أَخْدَانٍ وَمَنْ يَكْفُرْ بِالإِيمَانِ فَقَدْ حَبِطَ عَمَلُهُ وَهُوَ فِي الآخِرَةِ مِنَ الْخَاسِرِينَ}
INSERT INTO [b9617] VALUES (653,'<span data-type="title" id=toc-645>٦ </span>- {يَا أَيُّهَا الَّذِينَ آمَنُوا إِذَا قُمْتُمْ إِلَى الصَّلاةِ فَاغْسِلُوا وُجُوهَكُمْ وَأَيْدِيَكُمْ إِلَى الْمَرَافِقِ وَامْسَحُوا بِرُءُوسِكُمْ وَأَرْجُلَكُمْ إِلَى الْكَعْبَيْنِ وَإِنْ كُنْتُمْ جُنُبًا فَاطَّهَّرُوا وَإِنْ','1','217',NULL,NULL,'0');
INSERT INTO [b9617] VALUES (654,'كُنْتُمْ مَرْضَى أَوْ عَلَى سَفَرٍ أَوْ جَاءَ أَحَدٌ مِنْكُمْ مِنَ الْغَائِطِ أَوْ لامَسْتُمُ النِّسَاءَ فَلَمْ تَجِدُوا مَاءً فَتَيَمَّمُوا صَعِيدًا طَيِّبًا فَامْسَحُوا بِوُجُوهِكُمْ وَأَيْدِيكُمْ مِنْهُ مَا يُرِيدُ اللَّهُ لِيَجْعَلَ عَلَيْكُمْ مِنْ حَرَجٍ وَلَكِنْ يُرِيدُ لِيُطَهِّرَكُمْ وَلِيُتِمَّ نِعْمَتَهُ عَلَيْكُمْ لَعَلَّكُمْ تَشْكُرُونَ}
INSERT INTO [b9617] VALUES (655,'<span data-type="title" id=toc-646>٧ </span>- {وَاذْكُرُوا نِعْمَةَ اللَّهِ عَلَيْكُمْ وَمِيثَاقَهُ الَّذِي وَاثَقَكُمْ بِهِ إِذْ قُلْتُمْ سَمِعْنَا وَأَطَعْنَا وَاتَّقُوا اللَّهَ إِنَّ اللَّهَ عَلِيمٌ بِذَاتِ الصُّدُورِ}
INSERT INTO [b9617] VALUES (656,'<span data-type="title" id=toc-647>٨ </span>- {يَا أَيُّهَا الَّذِينَ آمَنُوا كُونُوا قَوَّامِينَ لِلَّهِ شُهَدَاءَ بِالْقِسْطِ وَلا يَجْرِمَنَّكُمْ شَنَآنُ قَوْمٍ عَلَى أَلا تَعْدِلُوا اعْدِلُوا هُوَ أَقْرَبُ لِلتَّقْوَى وَاتَّقُوا اللَّهَ إِنَّ اللَّهَ خَبِيرٌ بِمَا تَعْمَلُونَ}
INSERT INTO [b9617] VALUES (657,'<span data-type="title" id=toc-648>٩ </span>- {وَعَدَ اللَّهُ الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ لَهُمْ مَغْفِرَةٌ وَأَجْرٌ عَظِيمٌ}
INSERT INTO [b9617] VALUES (658,'<span data-type="title" id=toc-649>١٠ </span>- {وَالَّذِينَ كَفَرُوا وَكَذَّبُوا بِآيَاتِنَا أُولَئِكَ أَصْحَابُ الْجَحِيمِ}
INSERT INTO [b9617] VALUES (659,'<span data-type="title" id=toc-650>١١ </span>- {اذْكُرُوا نِعْمَتَ اللَّهِ عَلَيْكُمْ إِذْ هَمَّ قَوْمٌ أَنْ يَبْسُطُوا إِلَيْكُمْ أَيْدِيَهُمْ فَكَفَّ أَيْدِيَهُمْ عَنْكُمْ وَاتَّقُوا اللَّهَ وَعَلَى اللَّهِ فَلْيَتَوَكَّلِ الْمُؤْمِنُونَ}
INSERT INTO [b9617] VALUES (660,'<span data-type="title" id=toc-651>١٢ </span>- {وَلَقَدْ أَخَذَ اللَّهُ مِيثَاقَ بَنِي إِسْرَائِيلَ وَبَعَثْنَا مِنْهُمُ اثْنَيْ عَشَرَ نَقِيبًا وَقَالَ اللَّهُ إِنِّي مَعَكُمْ لَئِنْ أَقَمْتُمُ الصَّلاةَ وَآتَيْتُمُ الزَّكَاةَ وَآمَنْتُمْ بِرُسُلِي وَعَزَّرْتُمُوهُمْ وَأَقْرَضْتُمُ اللَّهَ قَرْضًا حَسَنًا لأُكَفِّرَنَّ عَنْكُمْ سَيِّئَاتِكُمْ وَلأُدْخِلَنَّكُمْ ⦗٢٢٠⦘ جَنَّاتٍ تَجْرِي مِنْ تَحْتِهَا الأَنْهَارُ فَمَنْ كَفَرَ بَعْدَ ذَلِكَ مِنْكُمْ فَقَدْ ضَلَّ سَوَاءَ السَّبِيلِ}
INSERT INTO [b9617] VALUES (661,'<span data-type="title" id=toc-652>١٣ </span>- {فَبِمَا نَقْضِهِمْ مِيثَاقَهُمْ لَعَنَّاهُمْ وَجَعَلْنَا قُلُوبَهُمْ قَاسِيَةً يُحَرِّفُونَ الْكَلِمَ عَنْ مَوَاضِعِهِ وَنَسُوا حَظًّا مِمَّا ذُكِّرُوا بِهِ وَلا تَزَالُ تَطَّلِعُ عَلَى خَائِنَةٍ مِنْهُمْ إِلا قَلِيلا مِنْهُمْ فَاعْفُ عَنْهُمْ وَاصْفَحْ}
INSERT INTO [b9617] VALUES (662,'<span data-type="title" id=toc-653>١٤ </span>- {وَمِنَ الَّذِينَ قَالُوا إِنَّا نَصَارَى أَخَذْنَا مِيثَاقَهُمْ فَنَسُوا حَظًّا مِمَّا ذُكِّرُوا بِهِ فَأَغْرَيْنَا بَيْنَهُمُ الْعَدَاوَةَ وَالْبَغْضَاءَ إِلَى يَوْمِ الْقِيَامَةِ وَسَوْفَ يُنَبِّئُهُمُ اللَّهُ بِمَا كَانُوا يَصْنَعُونَ}
INSERT INTO [b9617] VALUES (663,'<span data-type="title" id=toc-654>١٥ </span>- {قَدْ جَاءَكُمْ رَسُولُنَا يُبَيِّنُ لَكُمْ كَثِيرًا مِمَّا كُنْتُمْ تُخْفُونَ مِنَ الْكِتَابِ وَيَعْفُو عَنْ كَثِيرٍ قَدْ جَاءَكُمْ مِنَ اللَّهِ نُورٌ وَكِتَابٌ مُبِينٌ}
INSERT INTO [b9617] VALUES (664,'<span data-type="title" id=toc-655>١٦ </span>- {يَهْدِي بِهِ اللَّهُ مَنِ اتَّبَعَ رِضْوَانَهُ سُبُلَ السَّلامِ وَيُخْرِجُهُمْ مِنَ الظُّلُمَاتِ إِلَى النُّورِ بِإِذْنِهِ وَيَهْدِيهِمْ إِلَى صِرَاطٍ مُسْتَقِيمٍ}
INSERT INTO [b9617] VALUES (665,'<span data-type="title" id=toc-656>١٧ </span>- {لَقَدْ كَفَرَ الَّذِينَ قَالُوا إِنَّ اللَّهَ هُوَ الْمَسِيحُ ابْنُ مَرْيَمَ قُلْ فَمَنْ يَمْلِكُ مِنَ اللَّهِ شَيْئًا إِنْ أَرَادَ أَنْ يُهْلِكَ الْمَسِيحَ ابْنَ مَرْيَمَ وَأُمَّهُ وَمَنْ فِي الأَرْضِ جَمِيعًا وَلِلَّهِ مُلْكُ السَّمَاوَاتِ وَالأَرْضِ وَمَا بَيْنَهُمَا يَخْلُقُ مَا يَشَاءُ وَاللَّهُ عَلَى كُلِّ شَيْءٍ قَدِيرٌ}
INSERT INTO [b9617] VALUES (666,'<span data-type="title" id=toc-657>١٨ </span>- {قُلْ فَلِمَ يُعَذِّبُكُمْ بِذُنُوبِكُمْ بَلْ أَنْتُمْ بَشَرٌ مِمَّنْ خَلَقَ يَغْفِرُ لِمَنْ يَشَاءُ وَيُعَذِّبُ مَنْ يَشَاءُ وَلِلَّهِ مُلْكُ السَّمَاوَاتِ وَالأَرْضِ وَمَا بَيْنَهُمَا وَإِلَيْهِ الْمَصِيرُ}
INSERT INTO [b9617] VALUES (667,'<span data-type="title" id=toc-658>١٩ </span>- {قَدْ جَاءَكُمْ رَسُولُنَا يُبَيِّنُ لَكُمْ عَلَى فَتْرَةٍ مِنَ الرُّسُلِ أَنْ تَقُولُوا مَا جَاءَنَا مِنْ بَشِيرٍ وَلا نَذِيرٍ فَقَدْ جَاءَكُمْ بَشِيرٌ وَنَذِيرٌ وَاللَّهُ عَلَى كُلِّ شَيْءٍ قَدِيرٌ}
INSERT INTO [b9617] VALUES (668,'<span data-type="title" id=toc-659>٢٠ </span>- {وَإِذْ قَالَ مُوسَى لِقَوْمِهِ يَا قَوْمِ اذْكُرُوا نِعْمَةَ اللَّهِ عَلَيْكُمْ إِذْ جَعَلَ فِيكُمْ أَنْبِيَاءَ وَجَعَلَكُمْ مُلُوكًا وَآتَاكُمْ مَا لَمْ يُؤْتِ أَحَدًا مِنَ الْعَالَمِينَ}
INSERT INTO [b9617] VALUES (669,'<span data-type="title" id=toc-660>٢١ </span>- {يَا قَوْمِ ادْخُلُوا الأَرْضَ الْمُقَدَّسَةَ الَّتِي كَتَبَ اللَّهُ لَكُمْ وَلا تَرْتَدُّوا عَلَى أَدْبَارِكُمْ فَتَنْقَلِبُوا خَاسِرِينَ}
INSERT INTO [b9617] VALUES (670,'<span data-type="title" id=toc-661>٢٢ </span>- {وَإِنَّا لَنْ نَدْخُلَهَا حَتَّى يَخْرُجُوا مِنْهَا فَإِنْ يَخْرُجُوا مِنْهَا فَإِنَّا دَاخِلُونَ}
INSERT INTO [b9617] VALUES (671,'<span data-type="title" id=toc-662>٢٣ </span>- {قَالَ رَجُلانِ مِنَ الَّذِينَ يَخَافُونَ أَنْعَمَ اللَّهُ عَلَيْهِمَا ادْخُلُوا عَلَيْهِمُ الْبَابَ فَإِذَا دَخَلْتُمُوهُ فَإِنَّكُمْ غَالِبُونَ وَعَلَى اللَّهِ فَتَوَكَّلُوا إِنْ كُنْتُمْ مُؤْمِنِينَ}
INSERT INTO [b9617] VALUES (672,'<span data-type="title" id=toc-663>٢٤ </span>- {إِنَّا لَنْ نَدْخُلَهَا أَبَدًا مَا دَامُوا فِيهَا فَاذْهَبْ أَنْتَ وَرَبُّكَ فَقَاتِلا إِنَّا هَاهُنَا قَاعِدُونَ}
INSERT INTO [b9617] VALUES (673,'<span data-type="title" id=toc-664>٢٥ </span>- {إِنِّي لا أَمْلِكُ إِلا نَفْسِي وَأَخِي فَافْرُقْ بَيْنَنَا وَبَيْنَ الْقَوْمِ الْفَاسِقِينَ}
INSERT INTO [b9617] VALUES (674,'<span data-type="title" id=toc-665>٢٦ </span>- {قَالَ فَإِنَّهَا مُحَرَّمَةٌ عَلَيْهِمْ أَرْبَعِينَ سَنَةً يَتِيهُونَ فِي الأَرْضِ فَلا تَأْسَ عَلَى الْقَوْمِ الْفَاسِقِينَ}
INSERT INTO [b9617] VALUES (675,'<span data-type="title" id=toc-666>٢٧ </span>- {وَاتْلُ عَلَيْهِمْ نَبَأَ ابْنَيْ آدَمَ بِالْحَقِّ إِذْ قَرَّبَا قُرْبَانًا فَتُقُبِّلَ مِنْ أَحَدِهِمَا وَلَمْ يُتَقَبَّلْ مِنَ الآخَرِ قَالَ لأَقْتُلَنَّكَ قَالَ إِنَّمَا يَتَقَبَّلُ اللَّهُ مِنَ الْمُتَّقِينَ}
INSERT INTO [b9617] VALUES (676,'<span data-type="title" id=toc-667>٢٨ </span>- {لَئِنْ بَسَطْتَ إِلَيَّ يَدَكَ لِتَقْتُلَنِي مَا أَنَا بِبَاسِطٍ يَدِيَ إِلَيْكَ لأَقْتُلَكَ إِنِّي أَخَافُ اللَّهَ رَبَّ الْعَالَمِينَ}
INSERT INTO [b9617] VALUES (677,'<span data-type="title" id=toc-668>٣١ </span>- {لِيُرِيَهُ كَيْفَ يُوَارِي سَوْأَةَ أَخِيهِ قَالَ يَا وَيْلَتَى أَعَجَزْتُ أَنْ أَكُونَ مِثْلَ هَذَا الْغُرَابِ فَأُوَارِيَ سَوْأَةَ أَخِي فَأَصْبَحَ مِنَ النَّادِمِينَ}
INSERT INTO [b9617] VALUES (678,'<span data-type="title" id=toc-669>٣٢ </span>- {مِنْ أَجْلِ ذَلِكَ كَتَبْنَا عَلَى بَنِي إِسْرَائِيلَ أَنَّهُ مَنْ قَتَلَ نَفْسًا بِغَيْرِ نَفْسٍ أَوْ فَسَادٍ فِي الأَرْضِ فَكَأَنَّمَا قَتَلَ النَّاسَ جَمِيعًا وَمَنْ أَحْيَاهَا فَكَأَنَّمَا أَحْيَا النَّاسَ جَمِيعًا وَلَقَدْ جَاءَتْهُمْ رُسُلُنَا بِالْبَيِّنَاتِ ثُمَّ إِنَّ كَثِيرًا مِنْهُمْ بَعْدَ ذَلِكَ فِي الأَرْضِ لَمُسْرِفُونَ}
INSERT INTO [b9617] VALUES (679,'<span data-type="title" id=toc-670>٣٣ </span>- {وَيَسْعَوْنَ فِي الأَرْضِ فَسَادًا أَنْ يُقَتَّلُوا أَوْ يُصَلَّبُوا أَوْ تُقَطَّعَ أَيْدِيهِمْ وَأَرْجُلُهُمْ مِنْ خِلافٍ أَوْ يُنْفَوْا مِنَ الأَرْضِ ذَلِكَ لَهُمْ خِزْيٌ فِي الدُّنْيَا وَلَهُمْ فِي الآخِرَةِ عَذَابٌ عَظِيمٌ}
INSERT INTO [b9617] VALUES (680,'<span data-type="title" id=toc-671>٣٤ </span>- {مِنْ قَبْلِ أَنْ تَقْدِرُوا عَلَيْهِمْ فَاعْلَمُوا أَنَّ اللَّهَ غَفُورٌ رَحِيمٌ}
INSERT INTO [b9617] VALUES (681,'<span data-type="title" id=toc-672>٣٥ </span>- {لَعَلَّكُمْ تُفْلِحُونَ}
INSERT INTO [b9617] VALUES (682,'<span data-type="title" id=toc-673>٣٦ </span>- {إِنَّ الَّذِينَ كَفَرُوا لَوْ أَنَّ لَهُمْ مَا فِي الأَرْضِ جَمِيعًا وَمِثْلَهُ مَعَهُ لِيَفْتَدُوا بِهِ مِنْ عَذَابِ يَوْمِ الْقِيَامَةِ مَا تُقُبِّلَ مِنْهُمْ وَلَهُمْ عَذَابٌ أَلِيمٌ}
INSERT INTO [b9617] VALUES (683,'<span data-type="title" id=toc-674>٣٧ </span>- {يُرِيدُونَ أَنْ يَخْرُجُوا مِنَ النَّارِ وَمَا هُمْ بِخَارِجِينَ مِنْهَا وَلَهُمْ عَذَابٌ ⦗٢٢٨⦘ مُقِيمٌ}
INSERT INTO [b9617] VALUES (684,'<span data-type="title" id=toc-675>٣٨ </span>- {وَالسَّارِقُ وَالسَّارِقَةُ فَاقْطَعُوا أَيْدِيَهُمَا جَزَاءً بِمَا كَسَبَا نَكَالا مِنَ اللَّهِ وَاللَّهُ عَزِيزٌ حَكِيمٌ}
INSERT INTO [b9617] VALUES (685,'<span data-type="title" id=toc-676>٤٠ </span>- {أَلَمْ تَعْلَمْ أَنَّ اللَّهَ لَهُ مُلْكُ السَّمَاوَاتِ وَالأَرْضِ}
INSERT INTO [b9617] VALUES (686,'<span data-type="title" id=toc-677>٤١ </span>- {لا يَحْزُنْكَ الَّذِينَ يُسَارِعُونَ فِي الْكُفْرِ مِنَ الَّذِينَ قَالُوا آمَنَّا بِأَفْوَاهِهِمْ وَلَمْ تُؤْمِنْ قُلُوبُهُمْ وَمِنَ الَّذِينَ هَادُوا سَمَّاعُونَ لِلْكَذِبِ سَمَّاعُونَ لِقَوْمٍ آخَرِينَ لَمْ يَأْتُوكَ يُحَرِّفُونَ الْكَلِمَ مِنْ بَعْدِ مَوَاضِعِهِ يَقُولُونَ إِنْ أُوتِيتُمْ هَذَا فَخُذُوهُ وَإِنْ لَمْ تُؤْتَوْهُ فَاحْذَرُوا وَمَنْ يُرِدِ اللَّهُ فِتْنَتَهُ فَلَنْ تَمْلِكَ لَهُ مِنَ اللَّهِ شَيْئًا أُولَئِكَ الَّذِينَ لَمْ يُرِدِ اللَّهُ أَنْ يُطَهِّرَ قُلُوبَهُمْ لَهُمْ فِي الدُّنْيَا خِزْيٌ وَلَهُمْ فِي الآخِرَةِ عَذَابٌ عَظِيمٌ}
INSERT INTO [b9617] VALUES (687,'وجملة «ولم تؤمن قلوبهم» حالية من «الذين» . وقوله «ومن الذين هادوا» : معطوف على «من الذين قالوا» . وقوله «سمَّاعون» : خبر لمبتدأ محذوف أي: هم، و «للكذب» مفعول به. واللام زائدة للتقوية لأنَّ «سمَّاعون» فرع من الفعل. و «سمَّاعون لقوم» خبر ثان، و «لقوم» مفعول به، واللام زائدة، وجملة «لم يأتوك» نعت ثان لـ «قوم» في محل جر، وجملة «يحرفون» نعت ثالث لـ «قوم» ، وجملة «يقولون» حال من فاعل «يحرِّفون» . وجملة «لهم في الدنيا خزي» خبر ثان لأولئك، والجار «في الدنيا» متعلق بحال من «خزي» . وجملة «ولهم عذاب» معطوفة على جملة «لهم خزي» .','1','229',NULL,NULL,'0');
INSERT INTO [b9617] VALUES (688,'<span data-type="title" id=toc-678>٤٢ </span>- {سَمَّاعُونَ لِلْكَذِبِ أَكَّالُونَ لِلسُّحْتِ فَإِنْ جَاءُوكَ فَاحْكُمْ بَيْنَهُمْ أَوْ أَعْرِضْ عَنْهُمْ وَإِنْ تُعْرِضْ عَنْهُمْ فَلَنْ يَضُرُّوكَ شَيْئًا وَإِنْ حَكَمْتَ فَاحْكُمْ بَيْنَهُمْ بِالْقِسْطِ إِنَّ اللَّهَ يُحِبُّ الْمُقْسِطِينَ}
INSERT INTO [b9617] VALUES (689,'<span data-type="title" id=toc-679>٤٣ </span>- {وَكَيْفَ يُحَكِّمُونَكَ وَعِنْدَهُمُ التَّوْرَاةُ فِيهَا حُكْمُ اللَّهِ ثُمَّ يَتَوَلَّوْنَ مِنْ بَعْدِ ذَلِكَ وَمَا أُولَئِكَ بِالْمُؤْمِنِينَ}
INSERT INTO [b9617] VALUES (690,'<span data-type="title" id=toc-680>٤٤ </span>- {إِنَّا أَنْزَلْنَا التَّوْرَاةَ فِيهَا هُدًى وَنُورٌ يَحْكُمُ بِهَا النَّبِيُّونَ الَّذِينَ أَسْلَمُوا لِلَّذِينَ هَادُوا وَالرَّبَّانِيُّونَ وَالأَحْبَارُ بِمَا اسْتُحْفِظُوا مِنْ كِتَابِ اللَّهِ وَكَانُوا عَلَيْهِ شُهَدَاءَ فَلا تَخْشَوُا النَّاسَ وَاخْشَوْنِ وَلا تَشْتَرُوا بِآيَاتِي ثَمَنًا قَلِيلا وَمَنْ لَمْ يَحْكُمْ بِمَا أَنْزَلَ اللَّهُ فَأُولَئِكَ هُمُ الْكَافِرُونَ}
INSERT INTO [b9617] VALUES (691,'<span data-type="title" id=toc-681>٤٥ </span>- {وَكَتَبْنَا عَلَيْهِمْ فِيهَا أَنَّ النَّفْسَ بِالنَّفْسِ وَالْعَيْنَ بِالْعَيْنِ وَالأَنْفَ بِالأَنْفِ وَالأُذُنَ بِالأُذُنِ وَالسِّنَّ بِالسِّنِّ وَالْجُرُوحَ قِصَاصٌ فَمَنْ تَصَدَّقَ بِهِ فَهُوَ كَفَّارَةٌ لَهُ وَمَنْ لَمْ يَحْكُمْ بِمَا أَنْزَلَ اللَّهُ فَأُولَئِكَ هُمُ الظَّالِمُونَ}
INSERT INTO [b9617] VALUES (692,'<span data-type="title" id=toc-682>٤٦ </span>- {وَقَفَّيْنَا عَلَى آثَارِهِمْ بِعِيسَى ابْنِ مَرْيَمَ مُصَدِّقًا لِمَا بَيْنَ يَدَيْهِ مِنَ التَّوْرَاةِ وَآتَيْنَاهُ الإِنْجِيلَ فِيهِ هُدًى وَنُورٌ وَمُصَدِّقًا لِمَا بَيْنَ يَدَيْهِ مِنَ التَّوْرَاةِ وَهُدًى وَمَوْعِظَةً لِلْمُتَّقِينَ}
INSERT INTO [b9617] VALUES (693,'<span data-type="title" id=toc-683>٤٧ </span>- {وَلْيَحْكُمْ أَهْلُ الإِنْجِيلِ بِمَا أَنْزَلَ اللَّهُ فِيهِ وَمَنْ لَمْ يَحْكُمْ بِمَا أَنْزَلَ اللَّهُ فَأُولَئِكَ هُمُ الْفَاسِقُونَ}
INSERT INTO [b9617] VALUES (694,'<span data-type="title" id=toc-684>٤٨ </span>- {وَأَنْزَلْنَا إِلَيْكَ الْكِتَابَ بِالْحَقِّ مُصَدِّقًا لِمَا بَيْنَ يَدَيْهِ مِنَ الْكِتَابِ وَمُهَيْمِنًا عَلَيْهِ فَاحْكُمْ بَيْنَهُمْ بِمَا أَنْزَلَ اللَّهُ وَلا تَتَّبِعْ أَهْوَاءَهُمْ عَمَّا جَاءَكَ مِنَ الْحَقِّ لِكُلٍّ جَعَلْنَا مِنْكُمْ شِرْعَةً وَمِنْهَاجًا وَلَوْ شَاءَ اللَّهُ لَجَعَلَكُمْ أُمَّةً وَاحِدَةً وَلَكِنْ لِيَبْلُوَكُمْ فِي مَا آتَاكُمْ فَاسْتَبِقُوا الْخَيْرَاتِ إِلَى اللَّهِ مَرْجِعُكُمْ جَمِيعًا فَيُنَبِّئُكُمْ بِمَا كُنْتُمْ فِيهِ تَخْتَلِفُونَ}
INSERT INTO [b9617] VALUES (695,'الضمير في «جاءك» . وقوله «لكلٍ جَعلنا منكم شِرْعَةً» : الجار «لكل» متعلق بـ «جعلنا» ، والجار «منكم» متعلق بفعل مقدر بـ أعني، ولا يتعلق بنعت «لكل» ؛ لأنه يلزم منه الفصل بين الصفة والموصوف بقوله «جعلنا» ، وهو أجنبي، وجملة «ولو شاء الله» معطوفة على جملة «جَعلنا» لا محل لها. قوله «ولكن لِيبلُوَكم» : الواو عاطفة، «لكنْ» حرف استدراك، واللام للتعليل، والفعل منصوب بأن مضمرة بعد لام التعليل، والمصدر المؤول مجرور متعلق بمحذوف، أي: ولكن فرَّقكم ليبلوكم، وجملة «فرَّقكم» المقدرة معطوفة على جملة «ولو شاء» ، وجملة «فاستبِقوا» مستأنفة، وجملة «إلى الله مرجعُكم» مستأنفة، وجملة «فينبئُكم» معطوفة على المستأنفة.','1','232',NULL,NULL,'0');
INSERT INTO [b9617] VALUES (696,'<span data-type="title" id=toc-685>٤٩ </span>- {وَأَنِ احْكُمْ بَيْنَهُمْ بِمَا أَنْزَلَ اللَّهُ وَلا تَتَّبِعْ أَهْوَاءَهُمْ وَاحْذَرْهُمْ أَنْ يَفْتِنُوكَ عَنْ بَعْضِ مَا أَنْزَلَ اللَّهُ إِلَيْكَ فَإِنْ تَوَلَّوْا فَاعْلَمْ أَنَّمَا يُرِيدُ اللَّهُ أَنْ يُصِيبَهُمْ بِبَعْضِ ذُنُوبِهِمْ وَإِنَّ كَثِيرًا مِنَ النَّاسِ لَفَاسِقُونَ}
INSERT INTO [b9617] VALUES (697,'<span data-type="title" id=toc-686>٥٠ </span>- {أَفَحُكْمَ الْجَاهِلِيَّةِ يَبْغُونَ وَمَنْ أَحْسَنُ مِنَ اللَّهِ حُكْمًا لِقَوْمٍ يُوقِنُونَ}
INSERT INTO [b9617] VALUES (698,'<span data-type="title" id=toc-687>٥١ </span>- {لا تَتَّخِذُوا الْيَهُودَ وَالنَّصَارَى أَوْلِيَاءَ بَعْضُهُمْ أَوْلِيَاءُ بَعْضٍ وَمَنْ يَتَوَلَّهُمْ مِنْكُمْ فَإِنَّهُ مِنْهُمْ}
INSERT INTO [b9617] VALUES (699,'<span data-type="title" id=toc-688>٥٢ </span>- {فَتَرَى الَّذِينَ فِي قُلُوبِهِمْ مَرَضٌ يُسَارِعُونَ فِيهِمْ يَقُولُونَ نَخْشَى أَنْ تُصِيبَنَا دَائِرَةٌ فَعَسَى اللَّهُ أَنْ يَأْتِيَ بِالْفَتْحِ أَوْ أَمْرٍ مِنْ عِنْدِهِ فَيُصْبِحُوا عَلَى مَا أَسَرُّوا فِي أَنْفُسِهِمْ نَادِمِينَ}
INSERT INTO [b9617] VALUES (700,'<span data-type="title" id=toc-689>٥٣ </span>- {أَقْسَمُوا بِاللَّهِ جَهْدَ أَيْمَانِهِمْ إِنَّهُمْ لَمَعَكُمْ حَبِطَتْ أَعْمَالُهُمْ فَأَصْبَحُوا خَاسِرِينَ}
INSERT INTO [b9617] VALUES (701,'<span data-type="title" id=toc-690>٥٤ </span>- {يَا أَيُّهَا الَّذِينَ آمَنُوا مَنْ يَرْتَدَّ مِنْكُمْ عَنْ دِينِهِ فَسَوْفَ يَأْتِي اللَّهُ بِقَوْمٍ يُحِبُّهُمْ وَيُحِبُّونَهُ ⦗٢٣٤⦘ أَذِلَّةٍ عَلَى الْمُؤْمِنِينَ أَعِزَّةٍ عَلَى الْكَافِرِينَ يُجَاهِدُونَ فِي سَبِيلِ اللَّهِ وَلا يَخَافُونَ لَوْمَةَ لائِمٍ ذَلِكَ فَضْلُ اللَّهِ يُؤْتِيهِ مَنْ يَشَاءُ وَاللَّهُ وَاسِعٌ عَلِيمٌ}
INSERT INTO [b9617] VALUES (702,'<span data-type="title" id=toc-691>٥٥ </span>- {إِنَّمَا وَلِيُّكُمُ اللَّهُ وَرَسُولُهُ وَالَّذِينَ آمَنُوا الَّذِينَ يُقِيمُونَ الصَّلاةَ وَيُؤْتُونَ الزَّكَاةَ وَهُمْ رَاكِعُونَ}
INSERT INTO [b9617] VALUES (703,'<span data-type="title" id=toc-692>٥٦ </span>- {وَمَنْ يَتَوَلَّ اللَّهَ وَرَسُولَهُ وَالَّذِينَ آمَنُوا فَإِنَّ حِزْبَ اللَّهِ هُمُ الْغَالِبُونَ}
INSERT INTO [b9617] VALUES (704,'<span data-type="title" id=toc-693>٥٧ </span>- {لا تَتَّخِذُوا الَّذِينَ اتَّخَذُوا دِينَكُمْ هُزُوًا وَلَعِبًا مِنَ الَّذِينَ أُوتُوا الْكِتَابَ مِنْ قَبْلِكُمْ وَالْكُفَّارَ أَوْلِيَاءَ وَاتَّقُوا اللَّهَ إِنْ كُنْتُمْ مُؤْمِنِينَ}
INSERT INTO [b9617] VALUES (705,'<span data-type="title" id=toc-694>٥٨ </span>- {وَإِذَا نَادَيْتُمْ إِلَى الصَّلاةِ اتَّخَذُوهَا هُزُوًا وَلَعِبًا ذَلِكَ بِأَنَّهُمْ قَوْمٌ لا يَعْقِلُونَ}
INSERT INTO [b9617] VALUES (706,'<span data-type="title" id=toc-695>٥٩ </span>- {قُلْ يَا أَهْلَ الْكِتَابِ هَلْ تَنْقِمُونَ مِنَّا إِلا أَنْ آمَنَّا بِاللَّهِ وَمَا أُنْزِلَ إِلَيْنَا وَمَا أُنْزِلَ مِنْ قَبْلُ وَأَنَّ أَكْثَرَكُمْ فَاسِقُونَ}
INSERT INTO [b9617] VALUES (707,'<span data-type="title" id=toc-696>٦٠ </span>- {قُلْ هَلْ أُنَبِّئُكُمْ بِشَرٍّ مِنْ ذَلِكَ مَثُوبَةً عِنْدَ اللَّهِ مَنْ لَعَنَهُ اللَّهُ وَغَضِبَ عَلَيْهِ وَجَعَلَ مِنْهُمُ الْقِرَدَةَ وَالْخَنَازِيرَ وَعَبَدَ الطَّاغُوتَ أُولَئِكَ شَرٌّ مَكَانًا وَأَضَلُّ عَنْ سَوَاءِ السَّبِيلِ}
INSERT INTO [b9617] VALUES (708,'<span data-type="title" id=toc-697>٦١ </span>- {وَإِذَا جَاءُوكُمْ قَالُوا آمَنَّا وَقَدْ دَخَلُوا بِالْكُفْرِ وَهُمْ قَدْ خَرَجُوا بِهِ وَاللَّهُ أَعْلَمُ بِمَا كَانُوا يَكْتُمُونَ}
INSERT INTO [b9617] VALUES (709,'<span data-type="title" id=toc-698>٦٢ </span>- {وَتَرَى كَثِيرًا مِنْهُمْ يُسَارِعُونَ فِي الإِثْمِ وَالْعُدْوَانِ وَأَكْلِهِمُ السُّحْتَ لَبِئْسَ مَا كَانُوا يَعْمَلُونَ}
INSERT INTO [b9617] VALUES (710,'<span data-type="title" id=toc-699>٦٣ </span>- {لَوْلا يَنْهَاهُمُ الرَّبَّانِيُّونَ وَالأَحْبَارُ عَنْ قَوْلِهِمُ الإِثْمَ وَأَكْلِهِمُ السُّحْتَ لَبِئْسَ مَا كَانُوا يَصْنَعُونَ}
INSERT INTO [b9617] VALUES (711,'<span data-type="title" id=toc-700>٦٤ </span>- {غُلَّتْ أَيْدِيهِمْ وَلُعِنُوا بِمَا قَالُوا بَلْ يَدَاهُ مَبْسُوطَتَانِ يُنْفِقُ كَيْفَ يَشَاءُ وَلَيَزِيدَنَّ كَثِيرًا مِنْهُمْ مَا أُنْزِلَ إِلَيْكَ مِنْ رَبِّكَ طُغْيَانًا وَكُفْرًا وَأَلْقَيْنَا بَيْنَهُمُ الْعَدَاوَةَ وَالْبَغْضَاءَ إِلَى يَوْمِ الْقِيَامَةِ كُلَّمَا أَوْقَدُوا نَارًا لِلْحَرْبِ أَطْفَأَهَا اللَّهُ وَيَسْعَوْنَ فِي الأَرْضِ فَسَادًا وَاللَّهُ لا يُحِبُّ الْمُفْسِدِينَ}
INSERT INTO [b9617] VALUES (712,'<span data-type="title" id=toc-701>٦٥ </span>- {وَلَوْ أَنَّ أَهْلَ الْكِتَابِ آمَنُوا وَاتَّقَوْا لَكَفَّرْنَا عَنْهُمْ سَيِّئَاتِهِمْ وَلأَدْخَلْنَاهُمْ جَنَّاتِ النَّعِيمِ}
INSERT INTO [b9617] VALUES (713,'<span data-type="title" id=toc-702>٦٦ </span>- {وَلَوْ أَنَّهُمْ أَقَامُوا التَّوْرَاةَ وَالإِنْجِيلَ وَمَا أُنْزِلَ إِلَيْهِمْ مِنْ رَبِّهِمْ لأَكَلُوا مِنْ فَوْقِهِمْ وَمِنْ تَحْتِ أَرْجُلِهِمْ مِنْهُمْ أُمَّةٌ مُقْتَصِدَةٌ وَكَثِيرٌ مِنْهُمْ سَاءَ مَا يَعْمَلُونَ}
INSERT INTO [b9617] VALUES (714,'<span data-type="title" id=toc-703>٦٧ </span>- {يَا أَيُّهَا الرَّسُولُ بَلِّغْ مَا أُنْزِلَ إِلَيْكَ مِنْ رَبِّكَ وَإِنْ لَمْ تَفْعَلْ فَمَا بَلَّغْتَ رِسَالَتَهُ وَاللَّهُ يَعْصِمُكَ مِنَ النَّاسِ}
INSERT INTO [b9617] VALUES (715,'<span data-type="title" id=toc-704>٦٨ </span>- {لَسْتُمْ عَلَى شَيْءٍ حَتَّى تُقِيمُوا التَّوْرَاةَ وَالإِنْجِيلَ وَمَا أُنْزِلَ إِلَيْكُمْ مِنْ رَبِّكُمْوَلَيَزِيدَنَّ كَثِيرًا مِنْهُمْ مَا أُنْزِلَ إِلَيْكَ مِنْ رَبِّكَ طُغْيَانًا وَكُفْرًا فَلا تَأْسَ عَلَى الْقَوْمِ الْكَافِرِينَ}
INSERT INTO [b9617] VALUES (716,'<span data-type="title" id=toc-705>٦٩ </span>- {إِنَّ الَّذِينَ آمَنُوا وَالَّذِينَ هَادُوا وَالصَّابِئُونَ وَالنَّصَارَى مَنْ آمَنَ بِاللَّهِ وَالْيَوْمِ الآخِرِ وَعَمِلَ صَالِحًا فَلا خَوْفٌ عَلَيْهِمْ وَلا هُمْ يَحْزَنُونَ}
INSERT INTO [b9617] VALUES (717,'<span data-type="title" id=toc-706>٧٠ </span>- {لَقَدْ أَخَذْنَا مِيثَاقَ بَنِي إِسْرَائِيلَ وَأَرْسَلْنَا إِلَيْهِمْ رُسُلا كُلَّمَا جَاءَهُمْ رَسُولٌ بِمَا لا تَهْوَى أَنْفُسُهُمْ فَرِيقًا كَذَّبُوا وَفَرِيقًا يَقْتُلُونَ}
INSERT INTO [b9617] VALUES (718,'<span data-type="title" id=toc-707>٧١ </span>- {وَحَسِبُوا أَلا تَكُونَ فِتْنَةٌ فَعَمُوا وَصَمُّوا ثُمَّ تَابَ اللَّهُ عَلَيْهِمْ ثُمَّ عَمُوا وَصَمُّوا كَثِيرٌ مِنْهُمْ} ⦗٢٤٠⦘
INSERT INTO [b9617] VALUES (719,'<span data-type="title" id=toc-708>٧٢ </span>- {وَقَالَ الْمَسِيحُ يَا بَنِي إِسْرَائِيلَ اعْبُدُوا اللَّهَ رَبِّي وَرَبَّكُمْ إِنَّهُ مَنْ يُشْرِكْ بِاللَّهِ فَقَدْ حَرَّمَ اللَّهُ عَلَيْهِ الْجَنَّةَ وَمَأْوَاهُ النَّارُ وَمَا لِلظَّالِمِينَ مِنْ أَنْصَارٍ}
INSERT INTO [b9617] VALUES (720,'<span data-type="title" id=toc-709>٧٣ </span>- {وَمَا مِنْ إِلَهٍ إِلا إِلَهٌ وَاحِدٌ وَإِنْ لَمْ يَنْتَهُوا عَمَّا يَقُولُونَ لَيَمَسَّنَّ الَّذِينَ كَفَرُوا مِنْهُمْ عَذَابٌ أَلِيمٌ}
INSERT INTO [b9617] VALUES (721,'<span data-type="title" id=toc-710>٧٤ </span>- {أَفَلا يَتُوبُونَ إِلَى اللَّهِ}
INSERT INTO [b9617] VALUES (722,'<span data-type="title" id=toc-711>٧٥ </span>- {مَا الْمَسِيحُ ابْنُ مَرْيَمَ إِلا رَسُولٌ قَدْ خَلَتْ مِنْ قَبْلِهِ الرُّسُلُ وَأُمُّهُ صِدِّيقَةٌ كَانَا يَأْكُلانِ الطَّعَامَ انْظُرْ كَيْفَ نُبَيِّنُ لَهُمُ الآيَاتِ ثُمَّ انْظُرْ أَنَّى يُؤْفَكُونَ}
INSERT INTO [b9617] VALUES (723,'<span data-type="title" id=toc-712>٧٦ </span>- {قُلْ أَتَعْبُدُونَ مِنْ دُونِ اللَّهِ مَا لا يَمْلِكُ لَكُمْ ضَرًّا وَلا نَفْعًا}
INSERT INTO [b9617] VALUES (724,'<span data-type="title" id=toc-713>٧٧ </span>- {قُلْ يَا أَهْلَ الْكِتَابِ لا تَغْلُوا فِي دِينِكُمْ غَيْرَ الْحَقِّ وَلا تَتَّبِعُوا أَهْوَاءَ قَوْمٍ قَدْ ضَلُّوا مِنْ قَبْلُ}
INSERT INTO [b9617] VALUES (725,'<span data-type="title" id=toc-714>٧٨ </span>- {لُعِنَ الَّذِينَ كَفَرُوا مِنْ بَنِي إِسْرَائِيلَ عَلَى لِسَانِ دَاوُدَ وَعِيسَى ابْنِ مَرْيَمَ ذَلِكَ بِمَا عَصَوْا وَكَانُوا يَعْتَدُونَ}
INSERT INTO [b9617] VALUES (726,'<span data-type="title" id=toc-715>٧٩ </span>- {كَانُوا لا يَتَنَاهَوْنَ عَنْ مُنْكَرٍ فَعَلُوهُ لَبِئْسَ مَا كَانُوا يَفْعَلُونَ}
INSERT INTO [b9617] VALUES (727,'<span data-type="title" id=toc-716>٨٠ </span>- {لَبِئْسَ مَا قَدَّمَتْ لَهُمْ أَنْفُسُهُمْ أَنْ سَخِطَ اللَّهُ عَلَيْهِمْ وَفِي الْعَذَابِ هُمْ خَالِدُونَ}
INSERT INTO [b9617] VALUES (728,'<span data-type="title" id=toc-717>٨١ </span>- {وَلَوْ كَانُوا يُؤْمِنُونَ بِاللَّهِ وَالنَّبِيِّ وَمَا أُنْزِلَ إِلَيْهِ مَا اتَّخَذُوهُمْ أَوْلِيَاءَ ⦗٢٤٣⦘ وَلَكِنَّ كَثِيرًا مِنْهُمْ فَاسِقُونَ}
INSERT INTO [b9617] VALUES (729,'<span data-type="title" id=toc-718>٨٢ </span>- {لَتَجِدَنَّ أَشَدَّ النَّاسِ عَدَاوَةً لِلَّذِينَ آمَنُوا الْيَهُودَ وَالَّذِينَ أَشْرَكُوا وَلَتَجِدَنَّ أَقْرَبَهُمْ مَوَدَّةً لِلَّذِينَ آمَنُوا الَّذِينَ قَالُوا إِنَّا نَصَارَى ذَلِكَ بِأَنَّ مِنْهُمْ قِسِّيسِينَ وَرُهْبَانًا وَأَنَّهُمْ لا يَسْتَكْبِرُونَ}
INSERT INTO [b9617] VALUES (730,'<span data-type="title" id=toc-719>٨٣ </span>- {وَإِذَا سَمِعُوا مَا أُنْزِلَ إِلَى الرَّسُولِ تَرَى أَعْيُنَهُمْ تَفِيضُ مِنَ الدَّمْعِ مِمَّا عَرَفُوا مِنَ الْحَقِّ يَقُولُونَ رَبَّنَا آمَنَّا فَاكْتُبْنَا مَعَ الشَّاهِدِينَ}
INSERT INTO [b9617] VALUES (731,'<span data-type="title" id=toc-720>٨٤ </span>- {وَمَا لَنَا لا نُؤْمِنُ بِاللَّهِ وَمَا جَاءَنَا مِنَ الْحَقِّ وَنَطْمَعُ أَنْ يُدْخِلَنَا رَبُّنَا مَعَ الْقَوْمِ الصَّالِحِينَ}
INSERT INTO [b9617] VALUES (732,'<span data-type="title" id=toc-721>٨٦ </span>- {وَالَّذِينَ كَفَرُوا وَكَذَّبُوا بِآيَاتِنَا أُولَئِكَ أَصْحَابُ الْجَحِيمِ}
INSERT INTO [b9617] VALUES (733,'<span data-type="title" id=toc-722>٨٨ </span>- {وَكُلُوا مِمَّا رَزَقَكُمُ اللَّهُ حَلالا طَيِّبًا}
INSERT INTO [b9617] VALUES (734,'<span data-type="title" id=toc-723>٨٩ </span>- {لا يُؤَاخِذُكُمُ اللَّهُ بِاللَّغْوِ فِي أَيْمَانِكُمْ وَلَكِنْ يُؤَاخِذُكُمْ بِمَا عَقَّدْتُمُ الأَيْمَانَ فَكَفَّارَتُهُ إِطْعَامُ عَشَرَةِ مَسَاكِينَ مِنْ أَوْسَطِ مَا تُطْعِمُونَ أَهْلِيكُمْ أَوْ كِسْوَتُهُمْ أَوْ تَحْرِيرُ رَقَبَةٍ فَمَنْ لَمْ يَجِدْ فَصِيَامُ ثَلاثَةِ أَيَّامٍ ذَلِكَ كَفَّارَةُ أَيْمَانِكُمْ إِذَا حَلَفْتُمْ وَاحْفَظُوا أَيْمَانَكُمْ كَذَلِكَ يُبَيِّنُ اللَّهُ لَكُمْ آيَاتِهِ لَعَلَّكُمْ تَشْكُرُونَ}
INSERT INTO [b9617] VALUES (735,'<span data-type="title" id=toc-724>٩٠ </span>- {إِنَّمَا الْخَمْرُ وَالْمَيْسِرُ وَالأَنْصَابُ وَالأَزْلامُ رِجْسٌ مِنْ عَمَلِ الشَّيْطَانِ فَاجْتَنِبُوهُ لَعَلَّكُمْ تُفْلِحُونَ}
INSERT INTO [b9617] VALUES (736,'<span data-type="title" id=toc-725>٩١ </span>- {فَهَلْ أَنْتُمْ مُنْتَهُونَ}
INSERT INTO [b9617] VALUES (737,'<span data-type="title" id=toc-726>٩٢ </span>- {وَاحْذَرُوا فَإِنْ تَوَلَّيْتُمْ فَاعْلَمُوا أَنَّمَا عَلَى رَسُولِنَا الْبَلاغُ الْمُبِينُ}
INSERT INTO [b9617] VALUES (738,'<span data-type="title" id=toc-727>٩٣ </span>- {لَيْسَ عَلَى الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ جُنَاحٌ فِيمَا طَعِمُوا إِذَا مَا اتَّقَوْا وَآمَنُوا}
INSERT INTO [b9617] VALUES (739,'<span data-type="title" id=toc-728>٩٤ </span>- {لَيَبْلُوَنَّكُمُ اللَّهُ بِشَيْءٍ مِنَ الصَّيْدِ تَنَالُهُ أَيْدِيكُمْ وَرِمَاحُكُمْ لِيَعْلَمَ اللَّهُ مَنْ يَخَافُهُ بِالْغَيْبِ فَمَنِ اعْتَدَى بَعْدَ ذَلِكَ فَلَهُ عَذَابٌ أَلِيمٌ}
INSERT INTO [b9617] VALUES (740,'<span data-type="title" id=toc-729>٩٥ </span>- {لا تَقْتُلُوا الصَّيْدَ وَأَنْتُمْ حُرُمٌ وَمَنْ قَتَلَهُ مِنْكُمْ مُتَعَمِّدًا فَجَزَاءٌ مِثْلُ مَا قَتَلَ مِنَ النَّعَمِ يَحْكُمُ بِهِ ذَوَا عَدْلٍ مِنْكُمْ هَدْيًا بَالِغَ الْكَعْبَةِ أَوْ كَفَّارَةٌ طَعَامُ مَسَاكِينَ أَوْ عَدْلُ ذَلِكَ صِيَامًا لِيَذُوقَ وَبَالَ أَمْرِهِ عَفَا اللَّهُ عَمَّا سَلَفَ وَمَنْ عَادَ فَيَنْتَقِمُ اللَّهُ مِنْهُ}
INSERT INTO [b9617] VALUES (741,'وجملة «فينتقمُ» خبر لمبتدأ محذوف، تقديره هو، وجملة «فهو ينتقم» جواب الشرط.','1','247',NULL,NULL,'0');
INSERT INTO [b9617] VALUES (742,'<span data-type="title" id=toc-730>٩٦ </span>- {أُحِلَّ لَكُمْ صَيْدُ الْبَحْرِ وَطَعَامُهُ مَتَاعًا لَكُمْ وَلِلسَّيَّارَةِ وَحُرِّمَ عَلَيْكُمْ صَيْدُ الْبَرِّ مَا دُمْتُمْ حُرُمًا}
INSERT INTO [b9617] VALUES (743,'<span data-type="title" id=toc-731>٩٧ </span>- {جَعَلَ اللَّهُ الْكَعْبَةَ الْبَيْتَ الْحَرَامَ قِيَامًا لِلنَّاسِ وَالشَّهْرَ الْحَرَامَ وَالْهَدْيَ وَالْقَلائِدَ ذَلِكَ لِتَعْلَمُوا أَنَّ اللَّهَ يَعْلَمُ مَا فِي السَّمَاوَاتِ وَمَا فِي الأَرْضِ وَأَنَّ اللَّهَ بِكُلِّ شَيْءٍ عَلِيمٌ}
INSERT INTO [b9617] VALUES (744,'<span data-type="title" id=toc-732>٩٩ </span>- {مَا عَلَى الرَّسُولِ إِلا الْبَلاغُ وَاللَّهُ يَعْلَمُ مَا تُبْدُونَ وَمَا تَكْتُمُونَ}
INSERT INTO [b9617] VALUES (745,'<span data-type="title" id=toc-733>١٠٠ </span>- {قُلْ لا يَسْتَوِي الْخَبِيثُ وَالطَّيِّبُ وَلَوْ أَعْجَبَكَ كَثْرَةُ الْخَبِيثِ فَاتَّقُوا اللَّهَ يَا أُولِي ⦗٢٤٨⦘ الأَلْبَابِ لَعَلَّكُمْ تُفْلِحُونَ}
INSERT INTO [b9617] VALUES (746,'<span data-type="title" id=toc-734>١٠١ </span>- {لا تَسْأَلُوا عَنْ أَشْيَاءَ إِنْ تُبْدَ لَكُمْ تَسُؤْكُمْ وَإِنْ تَسْأَلُوا عَنْهَا حِينَ يُنَزَّلُ الْقُرْآنُ تُبْدَ لَكُمْ عَفَا اللَّهُ عَنْهَا}
INSERT INTO [b9617] VALUES (747,'<span data-type="title" id=toc-735>١٠٢ </span>- {قَدْ سَأَلَهَا قَوْمٌ مِنْ قَبْلِكُمْ}
INSERT INTO [b9617] VALUES (748,'<span data-type="title" id=toc-736>١٠٣ </span>- {مَا جَعَلَ اللَّهُ مِنْ بَحِيرَةٍ وَلا سَائِبَةٍ وَلا وَصِيلَةٍ وَلا حَامٍ وَلَكِنَّ الَّذِينَ كَفَرُوا يَفْتَرُونَ عَلَى اللَّهِ الْكَذِبَ وَأَكْثَرُهُمْ لا يَعْقِلُونَ}
INSERT INTO [b9617] VALUES (749,'<span data-type="title" id=toc-737>١٠٤ </span>- {وَإِذَا قِيلَ لَهُمْ تَعَالَوْا إِلَى مَا أَنْزَلَ اللَّهُ وَإِلَى الرَّسُولِ قَالُوا حَسْبُنَا مَا وَجَدْنَا عَلَيْهِ آبَاءَنَا أَوَلَوْ كَانَ آبَاؤُهُمْ لا يَعْلَمُونَ شَيْئًا وَلا يَهْتَدُونَ}
INSERT INTO [b9617] VALUES (750,'<span data-type="title" id=toc-738>١٠٥ </span>- {يَا أَيُّهَا الَّذِينَ آمَنُوا عَلَيْكُمْ أَنْفُسَكُمْ لا يَضُرُّكُمْ مَنْ ضَلَّ إِذَا اهْتَدَيْتُمْ إِلَى اللَّهِ مَرْجِعُكُمْ جَمِيعًا فَيُنَبِّئُكُمْ بِمَا كُنْتُمْ تَعْمَلُونَ}
INSERT INTO [b9617] VALUES (751,'<span data-type="title" id=toc-739>١٠٦ </span>- {يَا أَيُّهَا الَّذِينَ آمَنُوا شَهَادَةُ بَيْنِكُمْ إِذَا حَضَرَ أَحَدَكُمُ الْمَوْتُ حِينَ الْوَصِيَّةِ اثْنَانِ ذَوَا عَدْلٍ مِنْكُمْ أَوْ آخَرَانِ مِنْ غَيْرِكُمْ إِنْ أَنْتُمْ ضَرَبْتُمْ فِي الأَرْضِ فَأَصَابَتْكُمْ مُصِيبَةُ الْمَوْتِ تَحْبِسُونَهُمَا مِنْ بَعْدِ الصَّلاةِ فَيُقْسِمَانِ بِاللَّهِ إِنِ ارْتَبْتُمْ لا نَشْتَرِي بِهِ ثَمَنًا وَلَوْ كَانَ ذَا قُرْبَى وَلا نَكْتُمُ شَهَادَةَ اللَّهِ','1','249',NULL,NULL,'0');
INSERT INTO [b9617] VALUES (752,'إِنَّا إِذًا لَمِنَ الآثِمِينَ}
INSERT INTO [b9617] VALUES (753,'<span data-type="title" id=toc-740>١٠٧ </span>- {فَإِنْ عُثِرَ عَلَى أَنَّهُمَا اسْتَحَقَّا إِثْمًا فَآخَرَانِ يَقُومَانِ مَقَامَهُمَا مِنَ الَّذِينَ اسْتَحَقَّ عَلَيْهِمُ الأَوْلَيَانِ فَيُقْسِمَانِ بِاللَّهِ لَشَهَادَتُنَا أَحَقُّ مِنْ شَهَادَتِهِمَا وَمَا اعْتَدَيْنَا إِنَّا إِذًا لَمِنَ الظَّالِمِينَ}
INSERT INTO [b9617] VALUES (754,'<span data-type="title" id=toc-741>١٠٨ </span>- {ذَلِكَ أَدْنَى أَنْ يَأْتُوا بِالشَّهَادَةِ عَلَى وَجْهِهَا أَوْ يَخَافُوا أَنْ تُرَدَّ أَيْمَانٌ بَعْدَ أَيْمَانِهِمْ}
INSERT INTO [b9617] VALUES (755,'<span data-type="title" id=toc-742>١٠٩ </span>- {يَوْمَ يَجْمَعُ اللَّهُ الرُّسُلَ فَيَقُولُ مَاذَا أُجِبْتُمْ قَالُوا لا عِلْمَ لَنَا إِنَّكَ أَنْتَ عَلامُ الْغُيُوبِ}
INSERT INTO [b9617] VALUES (756,'<span data-type="title" id=toc-743>١١٠ </span>- {إِذْ قَالَ اللَّهُ يَا عِيسَى ابْنَ مَرْيَمَ اذْكُرْ نِعْمَتِي عَلَيْكَ وَعَلَى وَالِدَتِكَ إِذْ أَيَّدْتُكَ بِرُوحِ الْقُدُسِ تُكَلِّمُ النَّاسَ فِي الْمَهْدِ وَكَهْلا وَإِذْ عَلَّمْتُكَ الْكِتَابَ وَالْحِكْمَةَ وَالتَّوْرَاةَ وَالإِنْجِيلَ وَإِذْ تَخْلُقُ مِنَ الطِّينِ كَهَيْئَةِ الطَّيْرِ بِإِذْنِي فَتَنْفُخُ فِيهَا فَتَكُونُ طَيْرًا بِإِذْنِي وَتُبْرِئُ الأَكْمَهَ وَالأَبْرَصَ بِإِذْنِي وَإِذْ تُخْرِجُ الْمَوْتَى بِإِذْنِي وَإِذْ كَفَفْتُ بَنِي إِسْرَائِيلَ عَنْكَ إِذْ','1','251',NULL,NULL,'0');
INSERT INTO [b9617] VALUES (757,'جِئْتَهُمْ بِالْبَيِّنَاتِ فَقَالَ الَّذِينَ كَفَرُوا مِنْهُمْ إِنْ هَذَا إِلا سِحْرٌ مُبِينٌ}
INSERT INTO [b9617] VALUES (758,'<span data-type="title" id=toc-744>١١١ </span>- {وَإِذْ أَوْحَيْتُ إِلَى الْحَوَارِيِّينَ أَنْ آمِنُوا بِي وَبِرَسُولِي قَالُوا آمَنَّا وَاشْهَدْ بِأَنَّنَا مُسْلِمُونَ}
INSERT INTO [b9617] VALUES (759,'<span data-type="title" id=toc-745>١١٢ </span>- {إِذْ قَالَ الْحَوَارِيُّونَ يَا عِيسَى ابْنَ مَرْيَمَ هَلْ يَسْتَطِيعُ رَبُّكَ أَنْ يُنَزِّلَ عَلَيْنَا مَائِدَةً مِنَ السَّمَاءِ قَالَ اتَّقُوا اللَّهَ إِنْ كُنْتُمْ مُؤْمِنِينَ}
INSERT INTO [b9617] VALUES (760,'<span data-type="title" id=toc-746>١١٣ </span>- {قَالُوا نُرِيدُ أَنْ نَأْكُلَ مِنْهَا وَتَطْمَئِنَّ قُلُوبُنَا وَنَعْلَمَ أَنْ قَدْ صَدَقْتَنَا وَنَكُونَ عَلَيْهَا مِنَ الشَّاهِدِينَ}
INSERT INTO [b9617] VALUES (761,'<span data-type="title" id=toc-747>١١٤ </span>- {اللَّهُمَّ رَبَّنَا أَنْزِلْ عَلَيْنَا مَائِدَةً مِنَ السَّمَاءِ تَكُونُ لَنَا عِيدًا لأَوَّلِنَا وَآخِرِنَا وَآيَةً مِنْكَ وَارْزُقْنَا وَأَنْتَ خَيْرُ الرَّازِقِينَ}
INSERT INTO [b9617] VALUES (762,'<span data-type="title" id=toc-748>١١٥ </span>- {قَالَ اللَّهُ إِنِّي مُنَزِّلُهَا عَلَيْكُمْ فَمَنْ يَكْفُرْ بَعْدُ مِنْكُمْ فَإِنِّي أُعَذِّبُهُ عَذَابًا لا أُعَذِّبُهُ أَحَدًا مِنَ الْعَالَمِينَ}
INSERT INTO [b9617] VALUES (763,'<span data-type="title" id=toc-749>١١٦ </span>- {وَإِذْ قَالَ اللَّهُ يَا عِيسَى ابْنَ مَرْيَمَ أَأَنْتَ قُلْتَ لِلنَّاسِ اتَّخِذُونِي وَأُمِّيَ إِلَهَيْنِ مِنْ دُونِ اللَّهِ قَالَ سُبْحَانَكَ مَا يَكُونُ لِي أَنْ أَقُولَ مَا لَيْسَ لِي بِحَقٍّ إِنْ كُنْتُ قُلْتُهُ فَقَدْ عَلِمْتَهُ تَعْلَمُ مَا فِي نَفْسِي وَلا أَعْلَمُ مَا فِي نَفْسِكَ إِنَّكَ أَنْتَ عَلامُ الْغُيُوبِ}
INSERT INTO [b9617] VALUES (764,'<span data-type="title" id=toc-750>١١٧ </span>- {مَا قُلْتُ لَهُمْ إِلا مَا أَمَرْتَنِي بِهِ أَنِ اعْبُدُوا اللَّهَ رَبِّي وَرَبَّكُمْ وَكُنْتُ عَلَيْهِمْ شَهِيدًا مَا دُمْتُ فِيهِمْ فَلَمَّا تَوَفَّيْتَنِي كُنْتَ أَنْتَ الرَّقِيبَ عَلَيْهِمْ وَأَنْتَ عَلَى كُلِّ شَيْءٍ شَهِيدٌ}
INSERT INTO [b9617] VALUES (765,'<span data-type="title" id=toc-751>١١٩ </span>- {قَالَ اللَّهُ هَذَا يَوْمُ يَنْفَعُ الصَّادِقِينَ صِدْقُهُمْ لَهُمْ جَنَّاتٌ تَجْرِي مِنْ تَحْتِهَا الأَنْهَارُ خَالِدِينَ فِيهَا أَبَدًا رَضِيَ اللَّهُ عَنْهُمْ وَرَضُوا عَنْهُ} ⦗٢٥٥⦘
INSERT INTO [b9617] VALUES (766,'<span data-type="title" id=toc-752>١٢٠ </span>- {لِلَّهِ مُلْكُ السَّمَاوَاتِ وَالأَرْضِ وَمَا فِيهِنَّ وَهُوَ عَلَى كُلِّ شَيْءٍ قَدِيرٌ}
INSERT INTO [b9617] VALUES (767,'<span data-type="title" id=toc-753>سورة الأنعام</span>','1','256',NULL,NULL,'0');
INSERT INTO [b9617] VALUES (768,'<span data-type="title" id=toc-754>١ </span>- {الْحَمْدُ لِلَّهِ الَّذِي خَلَقَ السَّمَاوَاتِ وَالأَرْضَ وَجَعَلَ الظُّلُمَاتِ وَالنُّورَ ثُمَّ الَّذِينَ كَفَرُوا بِرَبِّهِمْ يَعْدِلُونَ}
INSERT INTO [b9617] VALUES (769,'<span data-type="title" id=toc-755>٢ </span>- {هُوَ الَّذِي خَلَقَكُمْ مِنْ طِينٍ ثُمَّ قَضَى أَجَلا وَأَجَلٌ مُسَمًّى عِنْدَهُ ثُمَّ أَنْتُمْ تَمْتَرُونَ}
INSERT INTO [b9617] VALUES (770,'<span data-type="title" id=toc-756>٣ </span>- {وَهُوَ اللَّهُ فِي السَّمَاوَاتِ وَفِي الأَرْضِ يَعْلَمُ سِرَّكُمْ وَجَهْرَكُمْ}
INSERT INTO [b9617] VALUES (771,'<span data-type="title" id=toc-757>٤ </span>- {وَمَا تَأْتِيهِمْ مِنْ آيَةٍ مِنْ آيَاتِ رَبِّهِمْ إِلا كَانُوا عَنْهَا مُعْرِضِينَ}
INSERT INTO [b9617] VALUES (772,'<span data-type="title" id=toc-758>٥ </span>- {فَقَدْ كَذَّبُوا بِالْحَقِّ لَمَّا جَاءَهُمْ فَسَوْفَ يَأْتِيهِمْ أَنْبَاءُ مَا كَانُوا بِهِ يَسْتَهْزِئُونَ}
INSERT INTO [b9617] VALUES (773,'<span data-type="title" id=toc-759>٦ </span>- {أَلَمْ يَرَوْا كَمْ أَهْلَكْنَا مِنْ قَبْلِهِمْ مِنْ قَرْنٍ مَكَّنَّاهُمْ فِي الأَرْضِ مَا لَمْ نُمَكِّنْ لَكُمْ وَأَرْسَلْنَا السَّمَاءَ عَلَيْهِمْ مِدْرَارًا وَجَعَلْنَا الأَنْهَارَ تَجْرِي مِنْ تَحْتِهِمْ فَأَهْلَكْنَاهُمْ بِذُنُوبِهِمْ وَأَنْشَأْنَا مِنْ بَعْدِهِمْ قَرْنًا آخَرِينَ}
INSERT INTO [b9617] VALUES (774,'<span data-type="title" id=toc-760>٧ </span>- {وَلَوْ نَزَّلْنَا عَلَيْكَ كِتَابًا فِي قِرْطَاسٍ فَلَمَسُوهُ بِأَيْدِيهِمْ لَقَالَ الَّذِينَ كَفَرُوا إِنْ هَذَا إِلا سِحْرٌ مُبِينٌ}
INSERT INTO [b9617] VALUES (775,'<span data-type="title" id=toc-761>٨ </span>- {وَلَوْ أَنْزَلْنَا مَلَكًا لَقُضِيَ الأَمْرُ ثُمَّ لا يُنْظَرُونَ}
INSERT INTO [b9617] VALUES (776,'<span data-type="title" id=toc-762>٩ </span>- {وَلَوْ جَعَلْنَاهُ مَلَكًا لَجَعَلْنَاهُ رَجُلا وَلَلَبَسْنَا عَلَيْهِمْ مَا يَلْبِسُونَ} ⦗٢٥٨⦘
INSERT INTO [b9617] VALUES (777,'<span data-type="title" id=toc-763>١٠ </span>- {وَلَقَدِ اسْتُهْزِئَ بِرُسُلٍ مِنْ قَبْلِكَ فَحَاقَ بِالَّذِينَ سَخِرُوا مِنْهُمْ مَا كَانُوا بِهِ يَسْتَهْزِئُونَ}
INSERT INTO [b9617] VALUES (778,'<span data-type="title" id=toc-764>١٢ </span>- {قُلْ لِمَنْ مَا فِي السَّمَاوَاتِ وَالأَرْضِ قُلْ لِلَّهِ كَتَبَ عَلَى نَفْسِهِ الرَّحْمَةَ لَيَجْمَعَنَّكُمْ إِلَى يَوْمِ الْقِيَامَةِ لا رَيْبَ فِيهِ الَّذِينَ خَسِرُوا أَنْفُسَهُمْ فَهُمْ لا يُؤْمِنُونَ}
INSERT INTO [b9617] VALUES (779,'<span data-type="title" id=toc-765>١٣ </span>- {وَلَهُ مَا سَكَنَ فِي اللَّيْلِ وَالنَّهَارِ وَهُوَ السَّمِيعُ الْعَلِيمُ}
INSERT INTO [b9617] VALUES (780,'<span data-type="title" id=toc-766>١٤ </span>- {قُلْ أَغَيْرَ اللَّهِ أَتَّخِذُ وَلِيًّا فَاطِرِ السَّمَاوَاتِ وَالأَرْضِ وَهُوَ يُطْعِمُ وَلا يُطْعَمُ قُلْ إِنِّي أُمِرْتُ أَنْ أَكُونَ أَوَّلَ مَنْ أَسْلَمَ وَلا تَكُونَنَّ مِنَ الْمُشْرِكِينَ}
INSERT INTO [b9617] VALUES (781,'<span data-type="title" id=toc-767>١٥ </span>- {قُلْ إِنِّي أَخَافُ إِنْ عَصَيْتُ رَبِّي عَذَابَ يَوْمٍ عَظِيمٍ}
INSERT INTO [b9617] VALUES (782,'<span data-type="title" id=toc-768>١٦ </span>- {مَنْ يُصْرَفْ عَنْهُ يَوْمَئِذٍ فَقَدْ رَحِمَهُ وَذَلِكَ الْفَوْزُ الْمُبِينُ}
INSERT INTO [b9617] VALUES (783,'<span data-type="title" id=toc-769>١٧ </span>- {وَإِنْ يَمْسَسْكَ اللَّهُ بِضُرٍّ فَلا كَاشِفَ لَهُ إِلا هُوَ}
INSERT INTO [b9617] VALUES (784,'<span data-type="title" id=toc-770>١٨ </span>- {وَهُوَ الْقَاهِرُ فَوْقَ عِبَادِهِ وَهُوَ الْحَكِيمُ الْخَبِيرُ}
INSERT INTO [b9617] VALUES (785,'<span data-type="title" id=toc-771>١٩ </span>- {قُلْ أَيُّ شَيْءٍ أَكْبَرُ شَهَادَةً قُلِ اللَّهُ شَهِيدٌ بَيْنِي وَبَيْنَكُمْ وَأُوحِيَ إِلَيَّ هَذَا الْقُرْآنُ لأُنْذِرَكُمْ بِهِ وَمَنْ بَلَغَ أَئِنَّكُمْ لَتَشْهَدُونَ أَنَّ مَعَ اللَّهِ آلِهَةً أُخْرَى قُلْ لا أَشْهَدُ قُلْ إِنَّمَا هُوَ إِلَهٌ وَاحِدٌ وَإِنَّنِي بَرِيءٌ مِمَّا تُشْرِكُونَ}
INSERT INTO [b9617] VALUES (786,'<span data-type="title" id=toc-772>٢٠ </span>- {الَّذِينَ آتَيْنَاهُمُ الْكِتَابَ يَعْرِفُونَهُ كَمَا يَعْرِفُونَ أَبْنَاءَهُمُ الَّذِينَ خَسِرُوا أَنْفُسَهُمْ فَهُمْ لا يُؤْمِنُونَ}
INSERT INTO [b9617] VALUES (787,'<span data-type="title" id=toc-773>٢١ </span>- {وَمَنْ أَظْلَمُ مِمَّنِ افْتَرَى عَلَى اللَّهِ كَذِبًا أَوْ كَذَّبَ بِآيَاتِهِ إِنَّهُ لا يُفْلِحُ الظَّالِمُونَ}
INSERT INTO [b9617] VALUES (788,'<span data-type="title" id=toc-774>٢٢ </span>- {وَيَوْمَ نَحْشُرُهُمْ جَمِيعًا ثُمَّ نَقُولُ لِلَّذِينَ أَشْرَكُوا أَيْنَ شُرَكَاؤُكُمُ الَّذِينَ كُنْتُمْ تَزْعُمُونَ}
INSERT INTO [b9617] VALUES (789,'<span data-type="title" id=toc-775>٢٣ </span>- {ثُمَّ لَمْ تَكُنْ فِتْنَتُهُمْ إِلا أَنْ قَالُوا وَاللَّهِ رَبِّنَا مَا كُنَّا مُشْرِكِينَ}
INSERT INTO [b9617] VALUES (790,'<span data-type="title" id=toc-776>٢٤ </span>- {انْظُرْ كَيْفَ كَذَبُوا عَلَى أَنْفُسِهِمْ وَضَلَّ عَنْهُمْ مَا كَانُوا يَفْتَرُونَ}
INSERT INTO [b9617] VALUES (791,'<span data-type="title" id=toc-777>٢٥ </span>- {وَمِنْهُمْ مَنْ يَسْتَمِعُ إِلَيْكَ وَجَعَلْنَا عَلَى قُلُوبِهِمْ أَكِنَّةً أَنْ يَفْقَهُوهُ وَفِي آذَانِهِمْ وَقْرًا وَإِنْ يَرَوْا كُلَّ آيَةٍ لا يُؤْمِنُوا بِهَا حَتَّى إِذَا جَاءُوكَ يُجَادِلُونَكَ يَقُولُ الَّذِينَ كَفَرُوا إِنْ هَذَا إِلا أَسَاطِيرُ الأَوَّلِينَ}
INSERT INTO [b9617] VALUES (792,'<span data-type="title" id=toc-778>٢٦ </span>- {وَهُمْ يَنْهَوْنَ عَنْهُ وَيَنْأَوْنَ عَنْهُ وَإِنْ يُهْلِكُونَ إِلا أَنْفُسَهُمْ وَمَا يَشْعُرُونَ}
INSERT INTO [b9617] VALUES (793,'<span data-type="title" id=toc-779>٢٧ </span>- {وَلَوْ تَرَى إِذْ وُقِفُوا عَلَى النَّارِ فَقَالُوا يَا لَيْتَنَا نُرَدُّ وَلا نُكَذِّبَ بِآيَاتِ رَبِّنَا}
INSERT INTO [b9617] VALUES (794,'<span data-type="title" id=toc-780>٢٨ </span>- {بَلْ بَدَا لَهُمْ مَا كَانُوا يُخْفُونَ مِنْ قَبْلُ وَلَوْ رُدُّوا لَعَادُوا لِمَا نُهُوا عَنْهُ وَإِنَّهُمْ لَكَاذِبُونَ}
INSERT INTO [b9617] VALUES (795,'<span data-type="title" id=toc-781>٢٩ </span>- {وَقَالُوا إِنْ هِيَ إِلا حَيَاتُنَا الدُّنْيَا وَمَا نَحْنُ بِمَبْعُوثِينَ} ⦗٢٦٣⦘
INSERT INTO [b9617] VALUES (796,'<span data-type="title" id=toc-782>٣٠ </span>- {وَلَوْ تَرَى إِذْ وُقِفُوا عَلَى رَبِّهِمْ قَالَ أَلَيْسَ هَذَا بِالْحَقِّ قَالُوا بَلَى وَرَبِّنَا قَالَ فَذُوقُوا الْعَذَابَ بِمَا كُنْتُمْ تَكْفُرُونَ}
INSERT INTO [b9617] VALUES (797,'<span data-type="title" id=toc-783>٣١ </span>- {حَتَّى إِذَا جَاءَتْهُمُ السَّاعَةُ بَغْتَةً قَالُوا يَا حَسْرَتَنَا عَلَى مَا فَرَّطْنَا فِيهَا وَهُمْ يَحْمِلُونَ أَوْزَارَهُمْ عَلَى ظُهُورِهِمْ أَلا سَاءَ مَا يَزِرُونَ}
INSERT INTO [b9617] VALUES (798,'<span data-type="title" id=toc-784>٣٢ </span>- {وَمَا الْحَيَاةُ الدُّنْيَا إِلا لَعِبٌ وَلَهْوٌ وَلَلدَّارُ الآخِرَةُ خَيْرٌ لِلَّذِينَ يَتَّقُونَ أَفَلا ⦗٢٦٤⦘ تَعْقِلُونَ}
INSERT INTO [b9617] VALUES (799,'<span data-type="title" id=toc-785>٣٣ </span>- {قَدْ نَعْلَمُ إِنَّهُ لَيَحْزُنُكَ الَّذِي يَقُولُونَ فَإِنَّهُمْ لا يُكَذِّبُونَكَ وَلَكِنَّ الظَّالِمِينَ بِآيَاتِ اللَّهِ يَجْحَدُونَ}
INSERT INTO [b9617] VALUES (800,'<span data-type="title" id=toc-786>٣٤ </span>- {وَلَقَدْ كُذِّبَتْ رُسُلٌ مِنْ قَبْلِكَ فَصَبَرُوا عَلَى مَا كُذِّبُوا وَأُوذُوا حَتَّى أَتَاهُمْ نَصْرُنَا وَلا مُبَدِّلَ لِكَلِمَاتِ اللَّهِ وَلَقَدْ جَاءَكَ مِنْ نَبَإِ الْمُرْسَلِينَ}
INSERT INTO [b9617] VALUES (801,'<span data-type="title" id=toc-787>٣٥ </span>- {وَإِنْ كَانَ كَبُرَ عَلَيْكَ إِعْرَاضُهُمْ فَإِنِ اسْتَطَعْتَ أَنْ تَبْتَغِيَ نَفَقًا فِي الأَرْضِ أَوْ سُلَّمًا فِي السَّمَاءِ فَتَأْتِيَهُمْ بِآيَةٍ وَلَوْ شَاءَ اللَّهُ لَجَمَعَهُمْ عَلَى الْهُدَى فَلا تَكُونَنَّ مِنَ الْجَاهِلِينَ} ⦗٢٦٥⦘
INSERT INTO [b9617] VALUES (802,'<span data-type="title" id=toc-788>٣٦ </span>- {إِنَّمَا يَسْتَجِيبُ الَّذِينَ يَسْمَعُونَ وَالْمَوْتَى يَبْعَثُهُمُ اللَّهُ ثُمَّ إِلَيْهِ يُرْجَعُونَ}
INSERT INTO [b9617] VALUES (803,'<span data-type="title" id=toc-789>٣٧ </span>- {وَقَالُوا لَوْلا نُزِّلَ عَلَيْهِ آيَةٌ مِنْ رَبِّهِ قُلْ إِنَّ اللَّهَ قَادِرٌ عَلَى أَنْ يُنَزِّلَ آيَةً وَلَكِنَّ أَكْثَرَهُمْ لا يَعْلَمُونَ}
INSERT INTO [b9617] VALUES (804,'<span data-type="title" id=toc-790>٣٨ </span>- {وَمَا مِنْ دَابَّةٍ فِي الأَرْضِ وَلا طَائِرٍ يَطِيرُ بِجَنَاحَيْهِ إِلا أُمَمٌ أَمْثَالُكُمْ مَا فَرَّطْنَا فِي الْكِتَابِ مِنْ شَيْءٍ ثُمَّ إِلَى رَبِّهِمْ يُحْشَرُونَ}
INSERT INTO [b9617] VALUES (805,'<span data-type="title" id=toc-791>٣٩ </span>- {وَالَّذِينَ كَذَّبُوا بِآيَاتِنَا صُمٌّ وَبُكْمٌ فِي الظُّلُمَاتِ مَنْ يَشَأِ اللَّهُ يُضْلِلْهُ}
INSERT INTO [b9617] VALUES (806,'<span data-type="title" id=toc-792>٤٠ </span>- {قُلْ أَرَأَيْتَكُمْ إِنْ أَتَاكُمْ عَذَابُ اللَّهِ أَوْ أَتَتْكُمُ السَّاعَةُ أَغَيْرَ اللَّهِ تَدْعُونَ إِنْ كُنْتُمْ صَادِقِينَ}
INSERT INTO [b9617] VALUES (807,'<span data-type="title" id=toc-793>٤١ </span>- {بَلْ إِيَّاهُ تَدْعُونَ فَيَكْشِفُ مَا تَدْعُونَ إِلَيْهِ إِنْ شَاءَ وَتَنْسَوْنَ مَا تُشْرِكُونَ}
INSERT INTO [b9617] VALUES (808,'<span data-type="title" id=toc-794>٤٢ </span>- {وَلَقَدْ أَرْسَلْنَا إِلَى أُمَمٍ مِنْ قَبْلِكَ فَأَخَذْنَاهُمْ بِالْبَأْسَاءِ وَالضَّرَّاءِ لَعَلَّهُمْ يَتَضَرَّعُونَ}
INSERT INTO [b9617] VALUES (809,'<span data-type="title" id=toc-795>٤٣ </span>- {فَلَوْلا إِذْ جَاءَهُمْ بَأْسُنَا تَضَرَّعُوا وَلَكِنْ قَسَتْ قُلُوبُهُمْ وَزَيَّنَ لَهُمُ الشَّيْطَانُ مَا كَانُوا يَعْمَلُونَ}
INSERT INTO [b9617] VALUES (810,'<span data-type="title" id=toc-796>٤٤ </span>- {فَلَمَّا نَسُوا مَا ذُكِّرُوا بِهِ فَتَحْنَا عَلَيْهِمْ أَبْوَابَ كُلِّ شَيْءٍ حَتَّى إِذَا فَرِحُوا بِمَا أُوتُوا أَخَذْنَاهُمْ بَغْتَةً فَإِذَا هُمْ مُبْلِسُونَ}
INSERT INTO [b9617] VALUES (811,'<span data-type="title" id=toc-797>٤٥ </span>- {فَقُطِعَ دَابِرُ الْقَوْمِ الَّذِينَ ظَلَمُوا وَالْحَمْدُ لِلَّهِ رَبِّ الْعَالَمِينَ}
INSERT INTO [b9617] VALUES (812,'<span data-type="title" id=toc-798>٤٦ </span>- {قُلْ أَرَأَيْتُمْ إِنْ أَخَذَ اللَّهُ سَمْعَكُمْ وَأَبْصَارَكُمْ وَخَتَمَ عَلَى قُلُوبِكُمْ مَنْ إِلَهٌ غَيْرُ اللَّهِ يَأْتِيكُمْ بِهِ انْظُرْ كَيْفَ نُصَرِّفُ الآيَاتِ ثُمَّ هُمْ يَصْدِفُونَ}
INSERT INTO [b9617] VALUES (813,'<span data-type="title" id=toc-799>٤٧ </span>- {قُلْ أَرَأَيْتَكُمْ إِنْ أَتَاكُمْ عَذَابُ اللَّهِ بَغْتَةً أَوْ جَهْرَةً هَلْ يُهْلَكُ إِلا الْقَوْمُ الظَّالِمُونَ}
INSERT INTO [b9617] VALUES (814,'<span data-type="title" id=toc-800>٤٨ </span>- {وَمَا نُرْسِلُ الْمُرْسَلِينَ إِلا مُبَشِّرِينَ وَمُنْذِرِينَ فَمَنْ آمَنَ وَأَصْلَحَ فَلا خَوْفٌ عَلَيْهِمْ وَلا هُمْ يَحْزَنُونَ}
INSERT INTO [b9617] VALUES (815,'<span data-type="title" id=toc-801>٤٩ </span>- {وَالَّذِينَ كَذَّبُوا بِآيَاتِنَا يَمَسُّهُمُ الْعَذَابُ بِمَا كَانُوا يَفْسُقُونَ}
INSERT INTO [b9617] VALUES (816,'<span data-type="title" id=toc-802>٥٠ </span>- {قُلْ لا أَقُولُ لَكُمْ عِنْدِي خَزَائِنُ اللَّهِ وَلا أَعْلَمُ الْغَيْبَ وَلا أَقُولُ لَكُمْ إِنِّي مَلَكٌ إِنْ أَتَّبِعُ إِلا مَا يُوحَى إِلَيَّ قُلْ هَلْ يَسْتَوِي الأَعْمَى وَالْبَصِيرُ أَفَلا تَتَفَكَّرُونَ}
INSERT INTO [b9617] VALUES (817,'<span data-type="title" id=toc-803>٥١ </span>- {وَأَنْذِرْ بِهِ الَّذِينَ يَخَافُونَ أَنْ يُحْشَرُوا إِلَى رَبِّهِمْ لَيْسَ لَهُمْ مِنْ دُونِهِ وَلِيٌّ وَلا شَفِيعٌ لَعَلَّهُمْ يَتَّقُونَ}
INSERT INTO [b9617] VALUES (818,'<span data-type="title" id=toc-804>٥٢ </span>- {وَلا تَطْرُدِ الَّذِينَ يَدْعُونَ رَبَّهُمْ بِالْغَدَاةِ وَالْعَشِيِّ يُرِيدُونَ وَجْهَهُ مَا عَلَيْكَ مِنْ حِسَابِهِمْ مِنْ شَيْءٍ وَمَا مِنْ حِسَابِكَ عَلَيْهِمْ مِنْ شَيْءٍ فَتَطْرُدَهُمْ فَتَكُونَ مِنَ الظَّالِمِينَ}
INSERT INTO [b9617] VALUES (819,'<span data-type="title" id=toc-805>٥٣ </span>- {وَكَذَلِكَ فَتَنَّا بَعْضَهُمْ بِبَعْضٍ لِيَقُولُوا أَهَؤُلاءِ مَنَّ اللَّهُ عَلَيْهِمْ مِنْ بَيْنِنَا أَلَيْسَ اللَّهُ بِأَعْلَمَ بِالشَّاكِرِينَ}
INSERT INTO [b9617] VALUES (820,'<span data-type="title" id=toc-806>٥٤ </span>- {وَإِذَا جَاءَكَ الَّذِينَ يُؤْمِنُونَ بِآيَاتِنَا فَقُلْ سَلامٌ عَلَيْكُمْ كَتَبَ رَبُّكُمْ عَلَى نَفْسِهِ الرَّحْمَةَ أَنَّهُ مَنْ عَمِلَ مِنْكُمْ سُوءًا بِجَهَالَةٍ ثُمَّ تَابَ مِنْ بَعْدِهِ وَأَصْلَحَ فَأَنَّهُ غَفُورٌ رَحِيمٌ}
INSERT INTO [b9617] VALUES (821,'<span data-type="title" id=toc-807>٥٥ </span>- {وَكَذَلِكَ نُفَصِّلُ الآيَاتِ وَلِتَسْتَبِينَ سَبِيلُ الْمُجْرِمِينَ}
INSERT INTO [b9617] VALUES (822,'<span data-type="title" id=toc-808>٥٦ </span>- {قُلْ إِنِّي نُهِيتُ أَنْ أَعْبُدَ الَّذِينَ تَدْعُونَ مِنْ دُونِ اللَّهِ قُلْ لا أَتَّبِعُ أَهْوَاءَكُمْ قَدْ ⦗٢٧٢⦘ ضَلَلْتُ إِذًا وَمَا أَنَا مِنَ الْمُهْتَدِينَ}
INSERT INTO [b9617] VALUES (823,'<span data-type="title" id=toc-809>٥٧ </span>- {قُلْ إِنِّي عَلَى بَيِّنَةٍ مِنْ رَبِّي وَكَذَّبْتُمْ بِهِ مَا عِنْدِي مَا تَسْتَعْجِلُونَ بِهِ إِنِ الْحُكْمُ إِلا لِلَّهِ يَقُصُّ الْحَقَّ وَهُوَ خَيْرُ الْفَاصِلِينَ}
INSERT INTO [b9617] VALUES (824,'<span data-type="title" id=toc-810>٥٨ </span>- {قُلْ لَوْ أَنَّ عِنْدِي مَا تَسْتَعْجِلُونَ بِهِ لَقُضِيَ الأَمْرُ بَيْنِي وَبَيْنَكُمْ وَاللَّهُ أَعْلَمُ بِالظَّالِمِينَ}
INSERT INTO [b9617] VALUES (825,'<span data-type="title" id=toc-811>٥٩ </span>- {وَعِنْدَهُ مَفَاتِحُ الْغَيْبِ لا يَعْلَمُهَا إِلا هُوَ وَيَعْلَمُ مَا فِي الْبَرِّ وَالْبَحْرِ وَمَا تَسْقُطُ مِنْ وَرَقَةٍ إِلا يَعْلَمُهَا وَلا حَبَّةٍ فِي ظُلُمَاتِ الأَرْضِ وَلا رَطْبٍ وَلا يَابِسٍ إِلا فِي كِتَابٍ ⦗٢٧٣⦘ مُبِينٍ}
INSERT INTO [b9617] VALUES (826,'<span data-type="title" id=toc-812>٦٠ </span>- {ثُمَّ يَبْعَثُكُمْ فِيهِ لِيُقْضَى أَجَلٌ مُسَمًّى ثُمَّ إِلَيْهِ مَرْجِعُكُمْ ثُمَّ يُنَبِّئُكُمْ بِمَا كُنْتُمْ تَعْمَلُونَ}
INSERT INTO [b9617] VALUES (827,'<span data-type="title" id=toc-813>٦١ </span>- {وَهُوَ الْقَاهِرُ فَوْقَ عِبَادِهِ وَيُرْسِلُ عَلَيْكُمْ حَفَظَةً حَتَّى إِذَا جَاءَ أَحَدَكُمُ الْمَوْتُ تَوَفَّتْهُ رُسُلُنَا وَهُمْ لا يُفَرِّطُونَ}
INSERT INTO [b9617] VALUES (828,'<span data-type="title" id=toc-814>٦٢ </span>- {ثُمَّ رُدُّوا إِلَى اللَّهِ مَوْلاهُمُ الْحَقِّ أَلا لَهُ الْحُكْمُ وَهُوَ أَسْرَعُ الْحَاسِبِينَ} ⦗٢٧٤⦘
INSERT INTO [b9617] VALUES (829,'<span data-type="title" id=toc-815>٦٣ </span>- {قُلْ مَنْ يُنَجِّيكُمْ مِنْ ظُلُمَاتِ الْبَرِّ وَالْبَحْرِ تَدْعُونَهُ تَضَرُّعًا وَخُفْيَةً لَئِنْ أَنْجَانَا مِنْ هَذِهِ لَنَكُونَنَّ مِنَ الشَّاكِرِينَ}
INSERT INTO [b9617] VALUES (830,'<span data-type="title" id=toc-816>٦٤ </span>- {قُلِ اللَّهُ يُنَجِّيكُمْ مِنْهَا وَمِنْ كُلِّ كَرْبٍ ثُمَّ أَنْتُمْ تُشْرِكُونَ}
INSERT INTO [b9617] VALUES (831,'<span data-type="title" id=toc-817>٦٥ </span>- {قُلْ هُوَ الْقَادِرُ عَلَى أَنْ يَبْعَثَ عَلَيْكُمْ عَذَابًا مِنْ فَوْقِكُمْ أَوْ مِنْ تَحْتِ أَرْجُلِكُمْ أَوْ يَلْبِسَكُمْ شِيَعًا وَيُذِيقَ بَعْضَكُمْ بَأْسَ بَعْضٍ انْظُرْ كَيْفَ نُصَرِّفُ الآيَاتِ لَعَلَّهُمْ يَفْقَهُونَ}
INSERT INTO [b9617] VALUES (832,'<span data-type="title" id=toc-818>٦٦ </span>- {وَكَذَّبَ بِهِ قَوْمُكَ وَهُوَ الْحَقُّ}
INSERT INTO [b9617] VALUES (833,'<span data-type="title" id=toc-819>٦٧ </span>- {وَسَوْفَ تَعْلَمُونَ}
INSERT INTO [b9617] VALUES (834,'<span data-type="title" id=toc-820>٦٨ </span>- {فَأَعْرِضْ عَنْهُمْ حَتَّى يَخُوضُوا فِي حَدِيثٍ غَيْرِهِ وَإِمَّا يُنْسِيَنَّكَ الشَّيْطَانُ فَلا تَقْعُدْ بَعْدَ الذِّكْرَى مَعَ الْقَوْمِ الظَّالِمِينَ}
INSERT INTO [b9617] VALUES (835,'<span data-type="title" id=toc-821>٦٩ </span>- {وَمَا عَلَى الَّذِينَ يَتَّقُونَ مِنْ حِسَابِهِمْ مِنْ شَيْءٍ وَلَكِنْ ذِكْرَى لَعَلَّهُمْ يَتَّقُونَ}
INSERT INTO [b9617] VALUES (836,'<span data-type="title" id=toc-822>٧٠ </span>- {وَذَرِ الَّذِينَ اتَّخَذُوا دِينَهُمْ لَعِبًا وَلَهْوًا وَغَرَّتْهُمُ الْحَيَاةُ الدُّنْيَا وَذَكِّرْ بِهِ أَنْ تُبْسَلَ نَفْسٌ بِمَا كَسَبَتْ لَيْسَ لَهَا مِنْ دُونِ اللَّهِ وَلِيٌّ وَلا شَفِيعٌ وَإِنْ تَعْدِلْ كُلَّ عَدْلٍ لا يُؤْخَذْ مِنْهَا أُولَئِكَ الَّذِينَ أُبْسِلُوا بِمَا كَسَبُوا لَهُمْ شَرَابٌ مِنْ حَمِيمٍ}
INSERT INTO [b9617] VALUES (837,'<span data-type="title" id=toc-823>٧١ </span>- {وَنُرَدُّ عَلَى أَعْقَابِنَا بَعْدَ إِذْ هَدَانَا اللَّهُ كَالَّذِي اسْتَهْوَتْهُ الشَّيَاطِينُ فِي الأَرْضِ حَيْرَانَ لَهُ أَصْحَابٌ يَدْعُونَهُ إِلَى الْهُدَى ائْتِنَا قُلْ إِنَّ هُدَى اللَّهِ هُوَ الْهُدَى وَأُمِرْنَا لِنُسْلِمَ لِرَبِّ الْعَالَمِينَ}
INSERT INTO [b9617] VALUES (838,'<span data-type="title" id=toc-824>٧٢ </span>- {وَأَنْ أَقِيمُوا الصَّلاةَ وَاتَّقُوهُ وَهُوَ الَّذِي إِلَيْهِ تُحْشَرُونَ}
INSERT INTO [b9617] VALUES (839,'<span data-type="title" id=toc-825>٧٣ </span>- {وَهُوَ الَّذِي خَلَقَ السَّمَاوَاتِ وَالأَرْضَ بِالْحَقِّ وَيَوْمَ يَقُولُ كُنْ فَيَكُونُ قَوْلُهُ الْحَقُّ وَلَهُ الْمُلْكُ يَوْمَ يُنْفَخُ فِي الصُّورِ عَالِمُ الْغَيْبِ وَالشَّهَادَةِ وَهُوَ الْحَكِيمُ الْخَبِيرُ}
INSERT INTO [b9617] VALUES (840,'<span data-type="title" id=toc-826>٧٤ </span>- {وَإِذْ قَالَ إِبْرَاهِيمُ لأَبِيهِ آزَرَ أَتَتَّخِذُ أَصْنَامًا آلِهَةً إِنِّي أَرَاكَ وَقَوْمَكَ فِي ضَلالٍ مُبِينٍ}
INSERT INTO [b9617] VALUES (841,'<span data-type="title" id=toc-827>٧٥ </span>- {وَكَذَلِكَ نُرِي إِبْرَاهِيمَ مَلَكُوتَ السَّمَاوَاتِ وَالأَرْضِ وَلِيَكُونَ مِنَ الْمُوقِنِينَ}
INSERT INTO [b9617] VALUES (842,'<span data-type="title" id=toc-828>٧٦ </span>- {فَلَمَّا جَنَّ عَلَيْهِ اللَّيْلُ رَأَى كَوْكَبًا قَالَ هَذَا رَبِّي فَلَمَّا أَفَلَ قَالَ لا أُحِبُّ الآفِلِينَ} ⦗٢٧٨⦘
INSERT INTO [b9617] VALUES (843,'<span data-type="title" id=toc-829>٧٧ </span>- {فَلَمَّا رَأَى الْقَمَرَ بَازِغًا قَالَ هَذَا رَبِّي فَلَمَّا أَفَلَ قَالَ لَئِنْ لَمْ يَهْدِنِي رَبِّي لأَكُونَنَّ مِنَ الْقَوْمِ الضَّالِّينَ}
INSERT INTO [b9617] VALUES (844,'<span data-type="title" id=toc-830>٧٨ </span>- {فَلَمَّا رَأَى الشَّمْسَ بَازِغَةً قَالَ هَذَا رَبِّي هَذَا أَكْبَرُ فَلَمَّا أَفَلَتْ قَالَ يَا قَوْمِ إِنِّي بَرِيءٌ مِمَّا تُشْرِكُونَ}
INSERT INTO [b9617] VALUES (845,'<span data-type="title" id=toc-831>٧٩ </span>- {وَجَّهْتُ وَجْهِيَ لِلَّذِي فَطَرَ السَّمَاوَاتِ وَالأَرْضَ حَنِيفًا وَمَا أَنَا مِنَ الْمُشْرِكِينَ}
INSERT INTO [b9617] VALUES (846,'<span data-type="title" id=toc-832>٨٠ </span>- {قَالَ أَتُحَاجُّونِّي فِي اللَّهِ وَقَدْ هَدَانِ وَلا أَخَافُ مَا تُشْرِكُونَ بِهِ إِلا أَنْ يَشَاءَ رَبِّي شَيْئًا وَسِعَ رَبِّي كُلَّ شَيْءٍ عِلْمًا أَفَلا تَتَذَكَّرُونَ}
INSERT INTO [b9617] VALUES (847,'<span data-type="title" id=toc-833>٨١ </span>- {وَكَيْفَ أَخَافُ مَا أَشْرَكْتُمْ وَلا تَخَافُونَ أَنَّكُمْ أَشْرَكْتُمْ بِاللَّهِ مَا لَمْ يُنَزِّلْ بِهِ عَلَيْكُمْ سُلْطَانًا فَأَيُّ الْفَرِيقَيْنِ أَحَقُّ بِالأَمْنِ إِنْ كُنْتُمْ تَعْلَمُونَ}
INSERT INTO [b9617] VALUES (848,'<span data-type="title" id=toc-834>٨٢ </span>- {الَّذِينَ آمَنُوا وَلَمْ يَلْبِسُوا إِيمَانَهُمْ بِظُلْمٍ أُولَئِكَ لَهُمُ الأَمْنُ وَهُمْ مُهْتَدُونَ}
INSERT INTO [b9617] VALUES (849,'<span data-type="title" id=toc-835>٨٣ </span>- {وَتِلْكَ حُجَّتُنَا آتَيْنَاهَا إِبْرَاهِيمَ عَلَى قَوْمِهِ نَرْفَعُ دَرَجَاتٍ مَنْ نَشَاءُ}
INSERT INTO [b9617] VALUES (850,'<span data-type="title" id=toc-836>٨٤ </span>- {وَوَهَبْنَا لَهُ إِسْحَاقَ وَيَعْقُوبَ كُلا هَدَيْنَا وَنُوحًا هَدَيْنَا مِنْ قَبْلُ وَمِنْ ذُرِّيَّتِهِ ⦗٢٨٠⦘ دَاوُدَ وَسُلَيْمَانَ وَأَيُّوبَ وَيُوسُفَ وَمُوسَى وَهَارُونَ وَكَذَلِكَ نَجْزِي الْمُحْسِنِينَ}
INSERT INTO [b9617] VALUES (851,'<span data-type="title" id=toc-837>٨٥ </span>- {وَزَكَرِيَّا وَيَحْيَى وَعِيسَى وَإِلْيَاسَ كُلٌّ مِنَ الصَّالِحِينَ}
INSERT INTO [b9617] VALUES (852,'<span data-type="title" id=toc-838>٨٦ </span>- {وَكُلا فَضَّلْنَا عَلَى الْعَالَمِينَ}
INSERT INTO [b9617] VALUES (853,'<span data-type="title" id=toc-839>٨٧ </span>- {وَمِنْ آبَائِهِمْ وَذُرِّيَّاتِهِمْ وَإِخْوَانِهِمْ وَاجْتَبَيْنَاهُمْ وَهَدَيْنَاهُمْ إِلَى صِرَاطٍ مُسْتَقِيمٍ}
INSERT INTO [b9617] VALUES (854,'<span data-type="title" id=toc-840>٨٨ </span>- {ذَلِكَ هُدَى اللَّهِ يَهْدِي بِهِ مَنْ يَشَاءُ مِنْ عِبَادِهِ وَلَوْ أَشْرَكُوا لَحَبِطَ عَنْهُمْ مَا كَانُوا يَعْمَلُونَ} ⦗٢٨١⦘
INSERT INTO [b9617] VALUES (855,'<span data-type="title" id=toc-841>٨٩ </span>- {أُولَئِكَ الَّذِينَ آتَيْنَاهُمُ الْكِتَابَ وَالْحُكْمَ وَالنُّبُوَّةَ فَإِنْ يَكْفُرْ بِهَا هَؤُلاءِ فَقَدْ وَكَّلْنَا بِهَا قَوْمًا لَيْسُوا بِهَا بِكَافِرِينَ}
INSERT INTO [b9617] VALUES (856,'<span data-type="title" id=toc-842>٩٠ </span>- {أُولَئِكَ الَّذِينَ هَدَى اللَّهُ فَبِهُدَاهُمُ اقْتَدِهِ قُلْ لا أَسْأَلُكُمْ عَلَيْهِ أَجْرًا إِنْ هُوَ إِلا ذِكْرَى لِلْعَالَمِينَ}
INSERT INTO [b9617] VALUES (857,'<span data-type="title" id=toc-843>٩١ </span>- {وَمَا قَدَرُوا اللَّهَ حَقَّ قَدْرِهِ إِذْ قَالُوا مَا أَنْزَلَ اللَّهُ عَلَى بَشَرٍ مِنْ شَيْءٍ قُلْ مَنْ أَنْزَلَ الْكِتَابَ الَّذِي جَاءَ بِهِ مُوسَى نُورًا وَهُدًى لِلنَّاسِ تَجْعَلُونَهُ قَرَاطِيسَ تُبْدُونَهَا وَتُخْفُونَ كَثِيرًا وَعُلِّمْتُمْ مَا لَمْ تَعْلَمُوا أَنْتُمْ وَلا آبَاؤُكُمْ قُلِ اللَّهُ ثُمَّ ذَرْهُمْ فِي خَوْضِهِمْ يَلْعَبُونَ} ⦗٢٨٢⦘
INSERT INTO [b9617] VALUES (858,'<span data-type="title" id=toc-844>٩٢ </span>- {وَهَذَا كِتَابٌ أَنْزَلْنَاهُ مُبَارَكٌ مُصَدِّقُ الَّذِي بَيْنَ يَدَيْهِ وَلِتُنْذِرَ أُمَّ الْقُرَى وَمَنْ حَوْلَهَا وَالَّذِينَ يُؤْمِنُونَ بِالآخِرَةِ يُؤْمِنُونَ بِهِ وَهُمْ عَلَى صَلاتِهِمْ يُحَافِظُونَ}
INSERT INTO [b9617] VALUES (859,'<span data-type="title" id=toc-845>٩٣ </span>- {وَمَنْ أَظْلَمُ مِمَّنِ افْتَرَى عَلَى اللَّهِ كَذِبًا أَوْ قَالَ أُوحِيَ إِلَيَّ وَلَمْ يُوحَ إِلَيْهِ شَيْءٌ وَمَنْ قَالَ سَأُنْزِلُ مِثْلَ مَا أَنْزَلَ اللَّهُ وَلَوْ تَرَى إِذِ الظَّالِمُونَ فِي غَمَرَاتِ الْمَوْتِ وَالْمَلائِكَةُ بَاسِطُو أَيْدِيهِمْ أَخْرِجُوا أَنْفُسَكُمُ الْيَوْمَ تُجْزَوْنَ عَذَابَ الْهُونِ بِمَا كُنْتُمْ تَقُولُونَ عَلَى اللَّهِ غَيْرَ الْحَقِّ} ⦗٢٨٣⦘
INSERT INTO [b9617] VALUES (860,'<span data-type="title" id=toc-846>٩٤ </span>- {وَلَقَدْ جِئْتُمُونَا فُرَادَى كَمَا خَلَقْنَاكُمْ أَوَّلَ مَرَّةٍ وَتَرَكْتُمْ مَا خَوَّلْنَاكُمْ وَرَاءَ ظُهُورِكُمْ وَمَا نَرَى مَعَكُمْ شُفَعَاءَكُمُ الَّذِينَ زَعَمْتُمْ أَنَّهُمْ فِيكُمْ شُرَكَاءُ لَقَدْ تَقَطَّعَ بَيْنَكُمْ وَضَلَّ عَنْكُمْ مَا كُنْتُمْ تَزْعُمُونَ}
INSERT INTO [b9617] VALUES (861,'<span data-type="title" id=toc-847>٩٥ </span>- {إِنَّ اللَّهَ فَالِقُ الْحَبِّ وَالنَّوَى يُخْرِجُ الْحَيَّ مِنَ الْمَيِّتِ وَمُخْرِجُ الْمَيِّتِ مِنَ الْحَيِّ ذَلِكُمُ اللَّهُ فَأَنَّى تُؤْفَكُونَ}
INSERT INTO [b9617] VALUES (862,'<span data-type="title" id=toc-848>٩٦ </span>- {فَالِقُ الإِصْبَاحِ وَجَعَلَ اللَّيْلَ سَكَنًا وَالشَّمْسَ وَالْقَمَرَ حُسْبَانًا ذَلِكَ تَقْدِيرُ الْعَزِيزِ الْعَلِيمِ}
INSERT INTO [b9617] VALUES (863,'<span data-type="title" id=toc-849>٩٧ </span>- {قَدْ فَصَّلْنَا الآيَاتِ لِقَوْمٍ يَعْلَمُونَ}
INSERT INTO [b9617] VALUES (864,'<span data-type="title" id=toc-850>٩٨ </span>- {وَهُوَ الَّذِي أَنْشَأَكُمْ مِنْ نَفْسٍ وَاحِدَةٍ فَمُسْتَقَرٌّ وَمُسْتَوْدَعٌ قَدْ فَصَّلْنَا الآيَاتِ لِقَوْمٍ يَفْقَهُونَ}
INSERT INTO [b9617] VALUES (865,'<span data-type="title" id=toc-851>٩٩ </span>- {وَهُوَ الَّذِي أَنْزَلَ مِنَ السَّمَاءِ مَاءً فَأَخْرَجْنَا بِهِ نَبَاتَ كُلِّ شَيْءٍ فَأَخْرَجْنَا مِنْهُ خَضِرًا نُخْرِجُ مِنْهُ حَبًّا مُتَرَاكِبًا وَمِنَ النَّخْلِ مِنْ طَلْعِهَا قِنْوَانٌ دَانِيَةٌ وَجَنَّاتٍ مِنْ أَعْنَابٍ وَالزَّيْتُونَ وَالرُّمَّانَ مُشْتَبِهًا وَغَيْرَ مُتَشَابِهٍ انْظُرُوا إِلَى ثَمَرِهِ إِذَا أَثْمَرَ وَيَنْعِهِ إِنَّ فِي ذَلِكُمْ لآيَاتٍ لِقَوْمٍ يُؤْمِنُونَ}
INSERT INTO [b9617] VALUES (866,'<span data-type="title" id=toc-852>١٠٠ </span>- {وَجَعَلُوا لِلَّهِ شُرَكَاءَ الْجِنَّ وَخَلَقَهُمْ وَخَرَقُوا لَهُ بَنِينَ وَبَنَاتٍ بِغَيْرِ عِلْمٍ سُبْحَانَهُ وَتَعَالَى عَمَّا يَصِفُونَ} ⦗٢٨٦⦘
INSERT INTO [b9617] VALUES (867,'<span data-type="title" id=toc-853>١٠١ </span>- {بَدِيعُ السَّمَاوَاتِ وَالأَرْضِ أَنَّى يَكُونُ لَهُ وَلَدٌ وَلَمْ تَكُنْ لَهُ صَاحِبَةٌ وَخَلَقَ كُلَّ شَيْءٍ وَهُوَ بِكُلِّ شَيْءٍ عَلِيمٌ}
INSERT INTO [b9617] VALUES (868,'<span data-type="title" id=toc-854>١٠٢ </span>- {ذَلِكُمُ اللَّهُ رَبُّكُمْ لا إِلَهَ إِلا هُوَ خَالِقُ كُلِّ شَيْءٍ فَاعْبُدُوهُ وَهُوَ عَلَى كُلِّ شَيْءٍ وَكِيلٌ}
INSERT INTO [b9617] VALUES (869,'<span data-type="title" id=toc-855>١٠٣ </span>- {لا تُدْرِكُهُ الأَبْصَارُ وَهُوَ يُدْرِكُ الأَبْصَارَ وَهُوَ اللَّطِيفُ الْخَبِيرُ} ⦗٢٨٧⦘
INSERT INTO [b9617] VALUES (870,'<span data-type="title" id=toc-856>١٠٤ </span>- {قَدْ جَاءَكُمْ بَصَائِرُ مِنْ رَبِّكُمْ فَمَنْ أَبْصَرَ فَلِنَفْسِهِ وَمَنْ عَمِيَ فَعَلَيْهَا وَمَا أَنَا عَلَيْكُمْ بِحَفِيظٍ}
INSERT INTO [b9617] VALUES (871,'<span data-type="title" id=toc-857>١٠٥ </span>- {وَكَذَلِكَ نُصَرِّفُ الآيَاتِ وَلِيَقُولُوا دَرَسْتَ وَلِنُبَيِّنَهُ لِقَوْمٍ يَعْلَمُونَ}
INSERT INTO [b9617] VALUES (872,'<span data-type="title" id=toc-858>١٠٦ </span>- {اتَّبِعْ مَا أُوحِيَ إِلَيْكَ مِنْ رَبِّكَ لا إِلَهَ إِلا هُوَ وَأَعْرِضْ عَنِ الْمُشْرِكِينَ}
INSERT INTO [b9617] VALUES (873,'<span data-type="title" id=toc-859>١٠٧ </span>- {وَمَا جَعَلْنَاكَ عَلَيْهِمْ حَفِيظًا وَمَا أَنْتَ عَلَيْهِمْ بِوَكِيلٍ}
INSERT INTO [b9617] VALUES (874,'<span data-type="title" id=toc-860>١٠٨ </span>- {وَلا تَسُبُّوا الَّذِينَ يَدْعُونَ مِنْ دُونِ اللَّهِ فَيَسُبُّوا اللَّهَ عَدْوًا بِغَيْرِ عِلْمٍ كَذَلِكَ زَيَّنَّا لِكُلِّ أُمَّةٍ عَمَلَهُمْ ثُمَّ إِلَى رَبِّهِمْ مَرْجِعُهُمْ فَيُنَبِّئُهُمْ بِمَا كَانُوا يَعْمَلُونَ}
INSERT INTO [b9617] VALUES (875,'<span data-type="title" id=toc-861>١٠٩ </span>- {وَأَقْسَمُوا بِاللَّهِ جَهْدَ أَيْمَانِهِمْ لَئِنْ جَاءَتْهُمْ آيَةٌ لَيُؤْمِنُنَّ بِهَا قُلْ إِنَّمَا الآيَاتُ عِنْدَ اللَّهِ وَمَا يُشْعِرُكُمْ أَنَّهَا إِذَا جَاءَتْ لا يُؤْمِنُونَ}
INSERT INTO [b9617] VALUES (876,'<span data-type="title" id=toc-862>١١٠ </span>- {وَنُقَلِّبُ أَفْئِدَتَهُمْ وَأَبْصَارَهُمْ كَمَا لَمْ يُؤْمِنُوا بِهِ أَوَّلَ مَرَّةٍ وَنَذَرُهُمْ فِي طُغْيَانِهِمْ يَعْمَهُونَ}
INSERT INTO [b9617] VALUES (877,'<span data-type="title" id=toc-863>١١١ </span>- {وَلَوْ أَنَّنَا نَزَّلْنَا إِلَيْهِمُ الْمَلائِكَةَ وَكَلَّمَهُمُ الْمَوْتَى وَحَشَرْنَا عَلَيْهِمْ كُلَّ شَيْءٍ قُبُلا مَا كَانُوا لِيُؤْمِنُوا إِلا أَنْ يَشَاءَ اللَّهُ وَلَكِنَّ أَكْثَرَهُمْ يَجْهَلُونَ}
INSERT INTO [b9617] VALUES (878,'<span data-type="title" id=toc-864>١١٢ </span>- {وَكَذَلِكَ جَعَلْنَا لِكُلِّ نَبِيٍّ عَدُوًّا شَيَاطِينَ الإِنْسِ وَالْجِنِّ يُوحِي بَعْضُهُمْ إِلَى بَعْضٍ زُخْرُفَ الْقَوْلِ غُرُورًا وَلَوْ شَاءَ رَبُّكَ مَا فَعَلُوهُ فَذَرْهُمْ وَمَا يَفْتَرُونَ}
INSERT INTO [b9617] VALUES (879,'<span data-type="title" id=toc-865>١١٣ </span>- {وَلِتَصْغَى إِلَيْهِ أَفْئِدَةُ الَّذِينَ لا يُؤْمِنُونَ بِالآخِرَةِ وَلِيَرْضَوْهُ وَلِيَقْتَرِفُوا مَا هُمْ مُقْتَرِفُونَ}
INSERT INTO [b9617] VALUES (880,'<span data-type="title" id=toc-866>١١٤ </span>- {أَفَغَيْرَ اللَّهِ أَبْتَغِي حَكَمًا وَهُوَ الَّذِي أَنْزَلَ إِلَيْكُمُ الْكِتَابَ مُفَصَّلا وَالَّذِينَ آتَيْنَاهُمُ الْكِتَابَ يَعْلَمُونَ أَنَّهُ مُنَزَّلٌ مِنْ رَبِّكَ بِالْحَقِّ فَلا تَكُونَنَّ مِنَ الْمُمْتَرِينَ}
INSERT INTO [b9617] VALUES (881,'<span data-type="title" id=toc-867>١١٥ </span>- {وَتَمَّتْ كَلِمَتُ رَبِّكَ صِدْقًا وَعَدْلا لا مُبَدِّلَ لِكَلِمَاتِهِ وَهُوَ السَّمِيعُ الْعَلِيمُ}
INSERT INTO [b9617] VALUES (882,'<span data-type="title" id=toc-868>١١٦ </span>- {إِنْ يَتَّبِعُونَ إِلا الظَّنَّ وَإِنْ هُمْ إِلا يَخْرُصُونَ}
INSERT INTO [b9617] VALUES (883,'<span data-type="title" id=toc-869>١١٧ </span>- {إِنَّ رَبَّكَ هُوَ أَعْلَمُ مَنْ يَضِلُّ عَنْ سَبِيلِهِ وَهُوَ أَعْلَمُ بِالْمُهْتَدِينَ}
INSERT INTO [b9617] VALUES (884,'<span data-type="title" id=toc-870>١١٨ </span>- {فَكُلُوا مِمَّا ذُكِرَ اسْمُ اللَّهِ عَلَيْهِ إِنْ كُنْتُمْ بِآيَاتِهِ مُؤْمِنِينَ} ⦗٢٩٢⦘
INSERT INTO [b9617] VALUES (885,'<span data-type="title" id=toc-871>١١٩ </span>- {وَمَا لَكُمْ أَلا تَأْكُلُوا مِمَّا ذُكِرَ اسْمُ اللَّهِ عَلَيْهِ وَقَدْ فَصَّلَ لَكُمْ مَا حَرَّمَ عَلَيْكُمْ إِلا مَا اضْطُرِرْتُمْ إِلَيْهِ وَإِنَّ كَثِيرًا لَيُضِلُّونَ بِأَهْوَائِهِمْ بِغَيْرِ عِلْمٍ إِنَّ رَبَّكَ هُوَ أَعْلَمُ بِالْمُعْتَدِينَ}
INSERT INTO [b9617] VALUES (886,'<span data-type="title" id=toc-872>١٢٠ </span>- {إِنَّ الَّذِينَ يَكْسِبُونَ الإِثْمَ سَيُجْزَوْنَ}
INSERT INTO [b9617] VALUES (887,'<span data-type="title" id=toc-873>١٢١ </span>- {وَلا تَأْكُلُوا مِمَّا لَمْ يُذْكَرِ اسْمُ اللَّهِ عَلَيْهِ وَإِنَّهُ لَفِسْقٌ وَإِنَّ الشَّيَاطِينَ لَيُوحُونَ إِلَى أَوْلِيَائِهِمْ لِيُجَادِلُوكُمْ وَإِنْ أَطَعْتُمُوهُمْ إِنَّكُمْ لَمُشْرِكُونَ}
INSERT INTO [b9617] VALUES (888,'<span data-type="title" id=toc-874>١٢٢ </span>- {أَوَمَنْ كَانَ مَيْتًا فَأَحْيَيْنَاهُ وَجَعَلْنَا لَهُ نُورًا يَمْشِي بِهِ فِي النَّاسِ كَمَنْ مَثَلُهُ فِي الظُّلُمَاتِ لَيْسَ بِخَارِجٍ مِنْهَا كَذَلِكَ زُيِّنَ لِلْكَافِرِينَ مَا كَانُوا يَعْمَلُونَ}
INSERT INTO [b9617] VALUES (889,'<span data-type="title" id=toc-875>١٢٣ </span>- {وَكَذَلِكَ جَعَلْنَا فِي كُلِّ قَرْيَةٍ أَكَابِرَ مُجْرِمِيهَا لِيَمْكُرُوا فِيهَا وَمَا يَمْكُرُونَ إِلا بِأَنْفُسِهِمْ وَمَا يَشْعُرُونَ}
INSERT INTO [b9617] VALUES (890,'<span data-type="title" id=toc-876>١٢٤ </span>- {اللَّهُ أَعْلَمُ حَيْثُ يَجْعَلُ رِسَالَتَهُ سَيُصِيبُ الَّذِينَ أَجْرَمُوا صَغَارٌ عِنْدَ اللَّهِ وَعَذَابٌ شَدِيدٌ بِمَا كَانُوا يَمْكُرُونَ}
INSERT INTO [b9617] VALUES (891,'<span data-type="title" id=toc-877>١٢٥ </span>- {فَمَنْ يُرِدِ اللَّهُ أَنْ يَهْدِيَهُ يَشْرَحْ صَدْرَهُ لِلإِسْلامِ وَمَنْ يُرِدْ أَنْ يُضِلَّهُ يَجْعَلْ صَدْرَهُ ضَيِّقًا حَرَجًا كَأَنَّمَا يَصَّعَّدُ فِي السَّمَاءِ كَذَلِكَ يَجْعَلُ اللَّهُ الرِّجْسَ عَلَى الَّذِينَ لا يُؤْمِنُونَ}
INSERT INTO [b9617] VALUES (892,'<span data-type="title" id=toc-878>١٢٦ </span>- {وَهَذَا صِرَاطُ رَبِّكَ مُسْتَقِيمًا قَدْ فَصَّلْنَا الآيَاتِ لِقَوْمٍ يَذَّكَّرُونَ}
INSERT INTO [b9617] VALUES (893,'<span data-type="title" id=toc-879>١٢٧ </span>- {لَهُمْ دَارُ السَّلامِ عِنْدَ رَبِّهِمْ وَهُوَ وَلِيُّهُمْ}
INSERT INTO [b9617] VALUES (894,'<span data-type="title" id=toc-880>١٢٨ </span>- {وَيَوْمَ يَحْشُرُهُمْ جَمِيعًا يَا مَعْشَرَ الْجِنِّ قَدِ اسْتَكْثَرْتُمْ مِنَ الإِنْسِ وَقَالَ أَوْلِيَاؤُهُمْ مِنَ الإِنْسِ رَبَّنَا اسْتَمْتَعَ بَعْضُنَا بِبَعْضٍ وَبَلَغْنَا أَجَلَنَا الَّذِي أَجَّلْتَ لَنَا قَالَ النَّارُ مَثْوَاكُمْ خَالِدِينَ فِيهَا إِلا مَا شَاءَ اللَّهُ} ⦗٢٩٥⦘
INSERT INTO [b9617] VALUES (895,'<span data-type="title" id=toc-881>١٢٩ </span>- {وَكَذَلِكَ نُوَلِّي بَعْضَ الظَّالِمِينَ بَعْضًا بِمَا كَانُوا يَكْسِبُونَ}
INSERT INTO [b9617] VALUES (896,'<span data-type="title" id=toc-882>١٣٠ </span>- {أَلَمْ يَأْتِكُمْ رُسُلٌ مِنْكُمْ يَقُصُّونَ عَلَيْكُمْ آيَاتِي وَيُنْذِرُونَكُمْ لِقَاءَ يَوْمِكُمْ هَذَا قَالُوا شَهِدْنَا عَلَى أَنْفُسِنَا وَغَرَّتْهُمُ الْحَيَاةُ الدُّنْيَا وَشَهِدُوا عَلَى أَنْفُسِهِمْ أَنَّهُمْ كَانُوا كَافِرِينَ}
INSERT INTO [b9617] VALUES (897,'<span data-type="title" id=toc-883>١٣١ </span>- {ذَلِكَ أَنْ لَمْ يَكُنْ رَبُّكَ مُهْلِكَ الْقُرَى بِظُلْمٍ وَأَهْلُهَا غَافِلُونَ}
INSERT INTO [b9617] VALUES (898,'<span data-type="title" id=toc-884>١٣٢ </span>- {وَلِكُلٍّ دَرَجَاتٌ مِمَّا عَمِلُوا وَمَا رَبُّكَ بِغَافِلٍ عَمَّا يَعْمَلُونَ}
INSERT INTO [b9617] VALUES (899,'<span data-type="title" id=toc-885>١٣٣ </span>- {وَرَبُّكَ الْغَنِيُّ ذُو الرَّحْمَةِ إِنْ يَشَأْ يُذْهِبْكُمْ وَيَسْتَخْلِفْ مِنْ بَعْدِكُمْ مَا يَشَاءُ كَمَا أَنْشَأَكُمْ مِنْ ذُرِّيَّةِ قَوْمٍ آخَرِينَ}
INSERT INTO [b9617] VALUES (900,'<span data-type="title" id=toc-886>١٣٤ </span>- {إِنَّ مَا تُوعَدُونَ لآتٍ وَمَا أَنْتُمْ بِمُعْجِزِينَ}
INSERT INTO [b9617] VALUES (901,'<span data-type="title" id=toc-887>١٣٥ </span>- {قُلْ يَا قَوْمِ اعْمَلُوا عَلَى مَكَانَتِكُمْ إِنِّي عَامِلٌ فَسَوْفَ تَعْلَمُونَ مَنْ تَكُونُ لَهُ عَاقِبَةُ الدَّارِ}
INSERT INTO [b9617] VALUES (902,'<span data-type="title" id=toc-888>١٣٦ </span>- {وَجَعَلُوا لِلَّهِ مِمَّا ذَرَأَ مِنَ الْحَرْثِ وَالأَنْعَامِ نَصِيبًا فَقَالُوا هَذَا لِلَّهِ بِزَعْمِهِمْ وَهَذَا لِشُرَكَائِنَا فَمَا كَانَ لِشُرَكَائِهِمْ فَلا يَصِلُ إِلَى اللَّهِ وَمَا كَانَ لِلَّهِ فَهُوَ يَصِلُ إِلَى شُرَكَائِهِمْ سَاءَ مَا يَحْكُمُونَ}
INSERT INTO [b9617] VALUES (903,'<span data-type="title" id=toc-889>١٣٧ </span>- {وَكَذَلِكَ زَيَّنَ لِكَثِيرٍ مِنَ الْمُشْرِكِينَ قَتْلَ أَوْلادِهِمْ شُرَكَاؤُهُمْ لِيُرْدُوهُمْ وَلِيَلْبِسُوا عَلَيْهِمْ دِينَهُمْ وَلَوْ شَاءَ اللَّهُ مَا فَعَلُوهُ فَذَرْهُمْ وَمَا يَفْتَرُونَ}
INSERT INTO [b9617] VALUES (904,'<span data-type="title" id=toc-890>١٣٨ </span>- {وَقَالُوا هَذِهِ أَنْعَامٌ وَحَرْثٌ حِجْرٌ لا يَطْعَمُهَا إِلا مَنْ نَشَاءُ بِزَعْمِهِمْ وَأَنْعَامٌ حُرِّمَتْ ظُهُورُهَا وَأَنْعَامٌ لا يَذْكُرُونَ اسْمَ اللَّهِ عَلَيْهَا افْتِرَاءً عَلَيْهِ سَيَجْزِيهِمْ بِمَا كَانُوا يَفْتَرُونَ} ⦗٢٩٨⦘
INSERT INTO [b9617] VALUES (905,'<span data-type="title" id=toc-891>١٣٩ </span>- {وَقَالُوا مَا فِي بُطُونِ هَذِهِ الأَنْعَامِ خَالِصَةٌ لِذُكُورِنَا وَمُحَرَّمٌ عَلَى أَزْوَاجِنَا وَإِنْ يَكُنْ مَيْتَةً فَهُمْ فِيهِ شُرَكَاءُ سَيَجْزِيهِمْ وَصْفَهُمْ إِنَّهُ حَكِيمٌ عَلِيمٌ}
INSERT INTO [b9617] VALUES (906,'<span data-type="title" id=toc-892>١٤٠ </span>- {قَدْ خَسِرَ الَّذِينَ قَتَلُوا أَوْلادَهُمْ سَفَهًا بِغَيْرِ عِلْمٍ وَحَرَّمُوا مَا رَزَقَهُمُ اللَّهُ افْتِرَاءً عَلَى اللَّهِ قَدْ ضَلُّوا وَمَا كَانُوا مُهْتَدِينَ}
INSERT INTO [b9617] VALUES (907,'<span data-type="title" id=toc-893>١٤١ </span>- {وَهُوَ الَّذِي أَنْشَأَ جَنَّاتٍ مَعْرُوشَاتٍ وَغَيْرَ مَعْرُوشَاتٍ وَالنَّخْلَ وَالزَّرْعَ مُخْتَلِفًا أُكُلُهُ وَالزَّيْتُونَ وَالرُّمَّانَ مُتَشَابِهًا وَغَيْرَ مُتَشَابِهٍ كُلُوا مِنْ ثَمَرِهِ إِذَا أَثْمَرَ وَآتُوا حَقَّهُ يَوْمَ حَصَادِهِ وَلا تُسْرِفُوا إِنَّهُ لا يُحِبُّ الْمُسْرِفِينَ} ⦗٢٩٩⦘
INSERT INTO [b9617] VALUES (908,'<span data-type="title" id=toc-894>١٤٢ </span>- {وَمِنَ الأَنْعَامِ حَمُولَةً وَفَرْشًا كُلُوا مِمَّا رَزَقَكُمُ اللَّهُ وَلا تَتَّبِعُوا خُطُوَاتِ الشَّيْطَانِ إِنَّهُ لَكُمْ عَدُوٌّ مُبِينٌ}
INSERT INTO [b9617] VALUES (909,'<span data-type="title" id=toc-895>١٤٣ </span>- {ثَمَانِيَةَ أَزْوَاجٍ مِنَ الضَّأْنِ اثْنَيْنِ وَمِنَ الْمَعْزِ اثْنَيْنِ قُلْ آلذَّكَرَيْنِ حَرَّمَ أَمِ الأُنْثَيَيْنِ أَمَّا اشْتَمَلَتْ عَلَيْهِ أَرْحَامُ الأُنْثَيَيْنِ نَبِّئُونِي بِعِلْمٍ إِنْ كُنْتُمْ صَادِقِينَ}
INSERT INTO [b9617] VALUES (910,'<span data-type="title" id=toc-896>١٤٤ </span>- {وَمِنَ الإِبِلِ اثْنَيْنِ وَمِنَ الْبَقَرِ اثْنَيْنِ قُلْ آلذَّكَرَيْنِ حَرَّمَ أَمِ الأُنْثَيَيْنِ أَمَّا اشْتَمَلَتْ عَلَيْهِ أَرْحَامُ الأُنْثَيَيْنِ أَمْ كُنْتُمْ شُهَدَاءَ إِذْ وَصَّاكُمُ اللَّهُ بِهَذَا فَمَنْ أَظْلَمُ مِمَّنِ افْتَرَى عَلَى اللَّهِ كَذِبًا لِيُضِلَّ النَّاسَ بِغَيْرِ عِلْمٍ}
INSERT INTO [b9617] VALUES (911,'<span data-type="title" id=toc-897>١٤٥ </span>- {قُلْ لا أَجِدُ فِي مَا أُوحِيَ إِلَيَّ مُحَرَّمًا عَلَى طَاعِمٍ يَطْعَمُهُ إِلا أَنْ يَكُونَ مَيْتَةً أَوْ دَمًا مَسْفُوحًا أَوْ لَحْمَ خِنْزِيرٍ فَإِنَّهُ رِجْسٌ أَوْ فِسْقًا أُهِلَّ لِغَيْرِ اللَّهِ بِهِ فَمَنِ اضْطُرَّ غَيْرَ بَاغٍ وَلا عَادٍ فَإِنَّ رَبَّكَ غَفُورٌ رَحِيمٌ}
INSERT INTO [b9617] VALUES (912,'<span data-type="title" id=toc-898>١٤٦ </span>- {وَعَلَى الَّذِينَ هَادُوا حَرَّمْنَا كُلَّ ذِي ظُفُرٍ وَمِنَ الْبَقَرِ وَالْغَنَمِ حَرَّمْنَا عَلَيْهِمْ شُحُومَهُمَا إِلا مَا حَمَلَتْ ظُهُورُهُمَا أَوِ الْحَوَايَا أَوْ مَا اخْتَلَطَ بِعَظْمٍ ذَلِكَ جَزَيْنَاهُمْ بِبَغْيِهِمْ ⦗٣٠١⦘ وَإِنَّا لَصَادِقُونَ}
INSERT INTO [b9617] VALUES (913,'<span data-type="title" id=toc-899>١٤٧ </span>- {فَإِنْ كَذَّبُوكَ فَقُلْ رَبُّكُمْ ذُو رَحْمَةٍ وَاسِعَةٍ وَلا يُرَدُّ بَأْسُهُ عَنِ الْقَوْمِ الْمُجْرِمِينَ}
INSERT INTO [b9617] VALUES (914,'<span data-type="title" id=toc-900>١٤٨ </span>- {لَوْ شَاءَ اللَّهُ مَا أَشْرَكْنَا وَلا آبَاؤُنَا وَلا حَرَّمْنَا مِنْ شَيْءٍ كَذَلِكَ كَذَّبَ الَّذِينَ مِنْ قَبْلِهِمْ حَتَّى ذَاقُوا بَأْسَنَا قُلْ هَلْ عِنْدَكُمْ مِنْ عِلْمٍ فَتُخْرِجُوهُ لَنَا إِنْ تَتَّبِعُونَ إِلا الظَّنَّ}
INSERT INTO [b9617] VALUES (915,'<span data-type="title" id=toc-901>١٤٩ </span>- {قُلْ فَلِلَّهِ الْحُجَّةُ الْبَالِغَةُ فَلَوْ شَاءَ لَهَدَاكُمْ أَجْمَعِينَ}
INSERT INTO [b9617] VALUES (916,'<span data-type="title" id=toc-902>١٥٠ </span>- {قُلْ هَلُمَّ شُهَدَاءَكُمُ الَّذِينَ يَشْهَدُونَ أَنَّ اللَّهَ حَرَّمَ هَذَا فَإِنْ شَهِدُوا فَلا تَشْهَدْ مَعَهُمْ وَلا تَتَّبِعْ أَهْوَاءَ الَّذِينَ كَذَّبُوا بِآيَاتِنَا وَالَّذِينَ لا يُؤْمِنُونَ بِالآخِرَةِ وَهُمْ بِرَبِّهِمْ يَعْدِلُونَ}
INSERT INTO [b9617] VALUES (917,'<span data-type="title" id=toc-903>١٥١ </span>- {قُلْ تَعَالَوْا أَتْلُ مَا حَرَّمَ رَبُّكُمْ عَلَيْكُمْ أَلا تُشْرِكُوا بِهِ شَيْئًا وَبِالْوَالِدَيْنِ إِحْسَانًا وَلا تَقْتُلُوا أَوْلادَكُمْ مِنْ إِمْلاقٍ نَحْنُ نَرْزُقُكُمْ وَإِيَّاهُمْ وَلا تَقْرَبُوا الْفَوَاحِشَ مَا ظَهَرَ مِنْهَا وَمَا بَطَنَ وَلا تَقْتُلُوا النَّفْسَ الَّتِي حَرَّمَ اللَّهُ إِلا بِالْحَقِّ ذَلِكُمْ وَصَّاكُمْ بِهِ لَعَلَّكُمْ تَعْقِلُونَ}
INSERT INTO [b9617] VALUES (918,'<span data-type="title" id=toc-904>١٥٢ </span>- {وَأَوْفُوا الْكَيْلَ وَالْمِيزَانَ بِالْقِسْطِ لا نُكَلِّفُ نَفْسًا إِلا وُسْعَهَا وَإِذَا قُلْتُمْ فَاعْدِلُوا وَلَوْ كَانَ ذَا قُرْبَى وَبِعَهْدِ اللَّهِ أَوْفُوا ذَلِكُمْ وَصَّاكُمْ بِهِ لَعَلَّكُمْ تَذَكَّرُونَ}
INSERT INTO [b9617] VALUES (919,'<span data-type="title" id=toc-905>١٥٣ </span>- {وَأَنَّ هَذَا صِرَاطِي مُسْتَقِيمًا فَاتَّبِعُوهُ وَلا تَتَّبِعُوا السُّبُلَ فَتَفَرَّقَ بِكُمْ عَنْ سَبِيلِهِ ذَلِكُمْ وَصَّاكُمْ بِهِ لَعَلَّكُمْ تَتَّقُونَ}
INSERT INTO [b9617] VALUES (920,'<span data-type="title" id=toc-906>١٥٤ </span>- {ثُمَّ آتَيْنَا مُوسَى الْكِتَابَ تَمَامًا عَلَى الَّذِي أَحْسَنَ وَتَفْصِيلا لِكُلِّ شَيْءٍ وَهُدًى وَرَحْمَةً لَعَلَّهُمْ بِلِقَاءِ رَبِّهِمْ يُؤْمِنُونَ}
INSERT INTO [b9617] VALUES (921,'<span data-type="title" id=toc-907>١٥٥ </span>- {وَهَذَا كِتَابٌ أَنْزَلْنَاهُ مُبَارَكٌ فَاتَّبِعُوهُ وَاتَّقُوا لَعَلَّكُمْ تُرْحَمُونَ}
INSERT INTO [b9617] VALUES (922,'<span data-type="title" id=toc-908>١٥٦ </span>- {أَنْ تَقُولُوا إِنَّمَا أُنْزِلَ الْكِتَابُ عَلَى طَائِفَتَيْنِ مِنْ قَبْلِنَا وَإِنْ كُنَّا عَنْ دِرَاسَتِهِمْ لَغَافِلِينَ}
INSERT INTO [b9617] VALUES (923,'<span data-type="title" id=toc-909>١٥٧ </span>- {أَوْ تَقُولُوا لَوْ أَنَّا أُنْزِلَ عَلَيْنَا الْكِتَابُ لَكُنَّا أَهْدَى مِنْهُمْ فَقَدْ جَاءَكُمْ بَيِّنَةٌ مِنْ رَبِّكُمْ وَهُدًى وَرَحْمَةٌ فَمَنْ أَظْلَمُ مِمَّنْ كَذَّبَ بِآيَاتِ اللَّهِ وَصَدَفَ عَنْهَا سَنَجْزِي الَّذِينَ يَصْدِفُونَ عَنْ آيَاتِنَا سُوءَ الْعَذَابِ بِمَا كَانُوا يَصْدِفُونَ}
INSERT INTO [b9617] VALUES (924,'<span data-type="title" id=toc-910>١٥٨ </span>- {هَلْ يَنْظُرُونَ إِلا أَنْ تَأْتِيَهُمُ الْمَلائِكَةُ أَوْ يَأْتِيَ رَبُّكَ أَوْ يَأْتِيَ بَعْضُ آيَاتِ رَبِّكَ يَوْمَ يَأْتِي بَعْضُ آيَاتِ رَبِّكَ لا يَنْفَعُ نَفْسًا إِيمَانُهَا لَمْ تَكُنْ آمَنَتْ مِنْ قَبْلُ أَوْ كَسَبَتْ فِي إِيمَانِهَا خَيْرًا قُلِ انْتَظِرُوا إِنَّا مُنْتَظِرُونَ}
INSERT INTO [b9617] VALUES (925,'<span data-type="title" id=toc-911>١٥٩ </span>- {لَسْتَ مِنْهُمْ فِي شَيْءٍ إِنَّمَا أَمْرُهُمْ إِلَى اللَّهِ ثُمَّ يُنَبِّئُهُمْ بِمَا كَانُوا يَفْعَلُونَ}
INSERT INTO [b9617] VALUES (926,'<span data-type="title" id=toc-912>١٦٠ </span>- {مَنْ جَاءَ بِالْحَسَنَةِ فَلَهُ عَشْرُ أَمْثَالِهَا وَمَنْ جَاءَ بِالسَّيِّئَةِ فَلا يُجْزَى إِلا مِثْلَهَا وَهُمْ لا يُظْلَمُونَ}
INSERT INTO [b9617] VALUES (927,'<span data-type="title" id=toc-913>١٦١ </span>- {هَدَانِي رَبِّي إِلَى صِرَاطٍ مُسْتَقِيمٍ دِينًا قِيَمًا مِلَّةَ إِبْرَاهِيمَ حَنِيفًا وَمَا كَانَ مِنَ الْمُشْرِكِينَ}
INSERT INTO [b9617] VALUES (928,'<span data-type="title" id=toc-914>١٦٢ </span>- {إِنَّ صَلاتِي وَنُسُكِي وَمَحْيَايَ وَمَمَاتِي لِلَّهِ رَبِّ الْعَالَمِينَ}
INSERT INTO [b9617] VALUES (929,'<span data-type="title" id=toc-915>١٦٣ </span>- {لا شَرِيكَ لَهُ وَبِذَلِكَ أُمِرْتُ وَأَنَا أَوَّلُ الْمُسْلِمِينَ}
INSERT INTO [b9617] VALUES (930,'<span data-type="title" id=toc-916>١٦٤ </span>- {أَغَيْرَ اللَّهِ أَبْغِي رَبًّا وَهُوَ رَبُّ كُلِّ شَيْءٍ وَلا تَكْسِبُ كُلُّ نَفْسٍ إِلا عَلَيْهَا وَلا تَزِرُ ⦗٣٠٧⦘ وَازِرَةٌ وِزْرَ أُخْرَى ثُمَّ إِلَى رَبِّكُمْ مَرْجِعُكُمْ فَيُنَبِّئُكُمْ بِمَا كُنْتُمْ فِيهِ تَخْتَلِفُونَ}
INSERT INTO [b9617] VALUES (931,'<span data-type="title" id=toc-917>١٦٥ </span>- {وَرَفَعَ بَعْضَكُمْ فَوْقَ بَعْضٍ دَرَجَاتٍ لِيَبْلُوَكُمْ فِي مَا آتَاكُمْ إِنَّ رَبَّكَ سَرِيعُ الْعِقَابِ}
INSERT INTO [b9617] VALUES (932,'<span data-type="title" id=toc-918>سورة الأعراف</span>','1','308',NULL,NULL,'0');
INSERT INTO [b9617] VALUES (933,'<span data-type="title" id=toc-919>٢ </span>- {كِتَابٌ أُنْزِلَ إِلَيْكَ فَلا يَكُنْ فِي صَدْرِكَ حَرَجٌ مِنْهُ لِتُنْذِرَ بِهِ وَذِكْرَى لِلْمُؤْمِنِينَ}
INSERT INTO [b9617] VALUES (934,'<span data-type="title" id=toc-920>٣ </span>- {وَلا تَتَّبِعُوا مِنْ دُونِهِ أَوْلِيَاءَ قَلِيلا مَا تَذَكَّرُونَ}
INSERT INTO [b9617] VALUES (935,'<span data-type="title" id=toc-921>٤ </span>- {وَكَمْ مِنْ قَرْيَةٍ أَهْلَكْنَاهَا فَجَاءَهَا بَأْسُنَا بَيَاتًا أَوْ هُمْ قَائِلُونَ}
INSERT INTO [b9617] VALUES (936,'<span data-type="title" id=toc-922>٥ </span>- {فَمَا كَانَ دَعْوَاهُمْ إِذْ جَاءَهُمْ بَأْسُنَا إِلا أَنْ قَالُوا إِنَّا كُنَّا ظَالِمِينَ}
INSERT INTO [b9617] VALUES (937,'<span data-type="title" id=toc-923>٦ </span>- {فَلَنَسْأَلَنَّ الَّذِينَ أُرْسِلَ إِلَيْهِمْ وَلَنَسْأَلَنَّ الْمُرْسَلِينَ}
INSERT INTO [b9617] VALUES (938,'<span data-type="title" id=toc-924>٧ </span>- {فَلَنَقُصَّنَّ عَلَيْهِمْ بِعِلْمٍ وَمَا كُنَّا غَائِبِينَ}
INSERT INTO [b9617] VALUES (939,'<span data-type="title" id=toc-925>٨ </span>- {وَالْوَزْنُ يَوْمَئِذٍ الْحَقُّ فَمَنْ ثَقُلَتْ مَوَازِينُهُ فَأُولَئِكَ هُمُ الْمُفْلِحُونَ}
INSERT INTO [b9617] VALUES (940,'<span data-type="title" id=toc-926>٩ </span>- {وَمَنْ خَفَّتْ مَوَازِينُهُ فَأُولَئِكَ الَّذِينَ خَسِرُوا أَنْفُسَهُمْ بِمَا كَانُوا بِآيَاتِنَا يَظْلِمُونَ}
INSERT INTO [b9617] VALUES (941,'<span data-type="title" id=toc-927>١٠ </span>- {قَلِيلا مَا تَشْكُرُونَ}
INSERT INTO [b9617] VALUES (942,'<span data-type="title" id=toc-928>١١ </span>- {وَلَقَدْ خَلَقْنَاكُمْ ثُمَّ صَوَّرْنَاكُمْ ثُمَّ قُلْنَا لِلْمَلائِكَةِ اسْجُدُوا لآدَمَ فَسَجَدُوا إِلا إِبْلِيسَ لَمْ يَكُنْ مِنَ السَّاجِدِينَ}
INSERT INTO [b9617] VALUES (943,'<span data-type="title" id=toc-929>١٢ </span>- {قَالَ مَا مَنَعَكَ أَلا تَسْجُدَ إِذْ أَمَرْتُكَ قَالَ أَنَا خَيْرٌ مِنْهُ خَلَقْتَنِي مِنْ نَارٍ وَخَلَقْتَهُ مِنْ طِينٍ}
INSERT INTO [b9617] VALUES (944,'<span data-type="title" id=toc-930>١٣ </span>- {قَالَ فَاهْبِطْ مِنْهَا فَمَا يَكُونُ لَكَ أَنْ تَتَكَبَّرَ فِيهَا فَاخْرُجْ إِنَّكَ مِنَ الصَّاغِرِينَ}
INSERT INTO [b9617] VALUES (945,'<span data-type="title" id=toc-931>١٤ </span>- {إِلَى يَوْمِ يُبْعَثُونَ} ⦗٣١١⦘
INSERT INTO [b9617] VALUES (946,'<span data-type="title" id=toc-932>١٦ </span>- {قَالَ فَبِمَا أَغْوَيْتَنِي لأَقْعُدَنَّ لَهُمْ صِرَاطَكَ الْمُسْتَقِيمَ}
INSERT INTO [b9617] VALUES (947,'<span data-type="title" id=toc-933>١٧ </span>- {ثُمَّ لآتِيَنَّهُمْ مِنْ بَيْنِ أَيْدِيهِمْ وَمِنْ خَلْفِهِمْ وَعَنْ أَيْمَانِهِمْ وَعَنْ شَمَائِلِهِمْ وَلا تَجِدُ أَكْثَرَهُمْ شَاكِرِينَ}
INSERT INTO [b9617] VALUES (948,'<span data-type="title" id=toc-934>١٨ </span>- {قَالَ اخْرُجْ مِنْهَا مَذْءُومًا مَدْحُورًا لَمَنْ تَبِعَكَ مِنْهُمْ لأَمْلأَنَّ جَهَنَّمَ مِنْكُمْ أَجْمَعِينَ}
INSERT INTO [b9617] VALUES (949,'<span data-type="title" id=toc-935>١٩ </span>- {وَيَا آدَمُ اسْكُنْ أَنْتَ وَزَوْجُكَ الْجَنَّةَ فَكُلا مِنْ حَيْثُ شِئْتُمَا وَلا تَقْرَبَا هَذِهِ الشَّجَرَةَ ⦗٣١٢⦘ فَتَكُونَا مِنَ الظَّالِمِينَ}
INSERT INTO [b9617] VALUES (950,'<span data-type="title" id=toc-936>٢٠ </span>- {فَوَسْوَسَ لَهُمَا الشَّيْطَانُ لِيُبْدِيَ لَهُمَا مَا وُورِيَ عَنْهُمَا مِنْ سَوْآتِهِمَا وَقَالَ مَا نَهَاكُمَا رَبُّكُمَا عَنْ هَذِهِ الشَّجَرَةِ إِلا أَنْ تَكُونَا مَلَكَيْنِ}
INSERT INTO [b9617] VALUES (951,'<span data-type="title" id=toc-937>٢١ </span>- {وَقَاسَمَهُمَا إِنِّي لَكُمَا لَمِنَ النَّاصِحِينَ}
INSERT INTO [b9617] VALUES (952,'<span data-type="title" id=toc-938>٢٢ </span>- {فَدَلاهُمَا بِغُرُورٍ فَلَمَّا ذَاقَا الشَّجَرَةَ بَدَتْ لَهُمَا سَوْآتُهُمَا وَطَفِقَا يَخْصِفَانِ عَلَيْهِمَا مِنْ وَرَقِ الْجَنَّةِ وَنَادَاهُمَا رَبُّهُمَا أَلَمْ أَنْهَكُمَا عَنْ تِلْكُمَا الشَّجَرَةِ وَأَقُلْ لَكُمَا إِنَّ الشَّيْطَانَ لَكُمَا عَدُوٌّ مُبِينٌ}
INSERT INTO [b9617] VALUES (953,'<span data-type="title" id=toc-939>٢٣ </span>- {وَإِنْ لَمْ تَغْفِرْ لَنَا وَتَرْحَمْنَا لَنَكُونَنَّ مِنَ الْخَاسِرِينَ}
INSERT INTO [b9617] VALUES (954,'<span data-type="title" id=toc-940>٢٤ </span>- {قَالَ اهْبِطُوا بَعْضُكُمْ لِبَعْضٍ عَدُوٌّ وَلَكُمْ فِي الأَرْضِ مُسْتَقَرٌّ وَمَتَاعٌ إِلَى حِينٍ}
INSERT INTO [b9617] VALUES (955,'<span data-type="title" id=toc-941>٢٦ </span>- {يَا بَنِي آدَمَ قَدْ أَنْزَلْنَا عَلَيْكُمْ لِبَاسًا يُوَارِي سَوْآتِكُمْ وَرِيشًا وَلِبَاسُ التَّقْوَى ذَلِكَ خَيْرٌ ذَلِكَ مِنْ آيَاتِ اللَّهِ لَعَلَّهُمْ يَذَّكَّرُونَ}
INSERT INTO [b9617] VALUES (956,'<span data-type="title" id=toc-942>٢٧ </span>- {يَا بَنِي آدَمَ لا يَفْتِنَنَّكُمُ الشَّيْطَانُ كَمَا أَخْرَجَ أَبَوَيْكُمْ مِنَ الْجَنَّةِ يَنْزِعُ عَنْهُمَا لِبَاسَهُمَا لِيُرِيَهُمَا سَوْآتِهِمَا إِنَّهُ يَرَاكُمْ هُوَ وَقَبِيلُهُ مِنْ حَيْثُ لا تَرَوْنَهُمْ}
INSERT INTO [b9617] VALUES (957,'<span data-type="title" id=toc-943>٢٨ </span>- {وَإِذَا فَعَلُوا فَاحِشَةً قَالُوا وَجَدْنَا عَلَيْهَا آبَاءَنَا وَاللَّهُ أَمَرَنَا بِهَا}
INSERT INTO [b9617] VALUES (958,'<span data-type="title" id=toc-944>٢٩ </span>- {قُلْ أَمَرَ رَبِّي بِالْقِسْطِ وَأَقِيمُوا وُجُوهَكُمْ عِنْدَ كُلِّ مَسْجِدٍ وَادْعُوهُ مُخْلِصِينَ لَهُ الدِّينَ كَمَا بَدَأَكُمْ تَعُودُونَ}
INSERT INTO [b9617] VALUES (959,'<span data-type="title" id=toc-945>٣٠ </span>- {فَرِيقًا هَدَى وَفَرِيقًا حَقَّ عَلَيْهِمُ الضَّلالَةُ إِنَّهُمُ اتَّخَذُوا الشَّيَاطِينَ أَوْلِيَاءَ مِنْ دُونِ اللَّهِ وَيَحْسَبُونَ أَنَّهُمْ مُهْتَدُونَ}
INSERT INTO [b9617] VALUES (960,'<span data-type="title" id=toc-946>٣٢ </span>- {قُلْ مَنْ حَرَّمَ زِينَةَ اللَّهِ الَّتِي أَخْرَجَ لِعِبَادِهِ وَالطَّيِّبَاتِ مِنَ الرِّزْقِ قُلْ هِيَ لِلَّذِينَ آمَنُوا فِي الْحَيَاةِ الدُّنْيَا خَالِصَةً يَوْمَ الْقِيَامَةِ كَذَلِكَ نُفَصِّلُ الآيَاتِ لِقَوْمٍ يَعْلَمُونَ}
INSERT INTO [b9617] VALUES (961,'<span data-type="title" id=toc-947>٣٣ </span>- {قُلْ إِنَّمَا حَرَّمَ رَبِّيَ الْفَوَاحِشَ مَا ظَهَرَ مِنْهَا وَمَا بَطَنَ وَالإِثْمَ وَالْبَغْيَ بِغَيْرِ الْحَقِّ وَأَنْ تُشْرِكُوا بِاللَّهِ مَا لَمْ يُنَزِّلْ بِهِ سُلْطَانًا}
INSERT INTO [b9617] VALUES (962,'<span data-type="title" id=toc-948>٣٤ </span>- {وَلِكُلِّ أُمَّةٍ أَجَلٌ فَإِذَا جَاءَ أَجَلُهُمْ لا يَسْتَأْخِرُونَ سَاعَةً وَلا يَسْتَقْدِمُونَ} ⦗٣١٦⦘
INSERT INTO [b9617] VALUES (963,'<span data-type="title" id=toc-949>٣٥ </span>- {يَا بَنِي آدَمَ إِمَّا يَأْتِيَنَّكُمْ رُسُلٌ مِنْكُمْ يَقُصُّونَ عَلَيْكُمْ آيَاتِي فَمَنِ اتَّقَى وَأَصْلَحَ فَلا خَوْفٌ عَلَيْهِمْ وَلا هُمْ يَحْزَنُونَ}
INSERT INTO [b9617] VALUES (964,'<span data-type="title" id=toc-950>٣٦ </span>- {وَالَّذِينَ كَذَّبُوا بِآيَاتِنَا وَاسْتَكْبَرُوا عَنْهَا أُولَئِكَ أَصْحَابُ النَّارِ هُمْ فِيهَا خَالِدُونَ}
INSERT INTO [b9617] VALUES (965,'<span data-type="title" id=toc-951>٣٧ </span>- {حَتَّى إِذَا جَاءَتْهُمْ رُسُلُنَا يَتَوَفَّوْنَهُمْ قَالُوا أَيْنَ مَا كُنْتُمْ تَدْعُونَ مِنْ دُونِ اللَّهِ قَالُوا ضَلُّوا عَنَّا وَشَهِدُوا عَلَى أَنْفُسِهِمْ أَنَّهُمْ كَانُوا كَافِرِينَ}
INSERT INTO [b9617] VALUES (966,'<span data-type="title" id=toc-952>٣٨ </span>- {ادْخُلُوا فِي أُمَمٍ قَدْ خَلَتْ مِنْ قَبْلِكُمْ مِنَ الْجِنِّ وَالإِنْسِ فِي النَّارِ كُلَّمَا دَخَلَتْ أُمَّةٌ لَعَنَتْ أُخْتَهَا حَتَّى إِذَا ادَّارَكُوا فِيهَا جَمِيعًا قَالَتْ أُخْرَاهُمْ لأُولاهُمْ رَبَّنَا هَؤُلاءِ أَضَلُّونَا فَآتِهِمْ عَذَابًا ضِعْفًا مِنَ النَّارِ قَالَ لِكُلٍّ ضِعْفٌ وَلَكِنْ لا تَعْلَمُونَ}
INSERT INTO [b9617] VALUES (967,'<span data-type="title" id=toc-953>٣٩ </span>- {وَقَالَتْ أُولاهُمْ لأُخْرَاهُمْ فَمَا كَانَ لَكُمْ عَلَيْنَا مِنْ فَضْلٍ فَذُوقُوا الْعَذَابَ بِمَا كُنْتُمْ تَكْسِبُونَ}
INSERT INTO [b9617] VALUES (968,'<span data-type="title" id=toc-954>٤٠ </span>- {وَكَذَلِكَ نَجْزِي الْمُجْرِمِينَ}
INSERT INTO [b9617] VALUES (969,'<span data-type="title" id=toc-955>٤١ </span>- {لَهُمْ مِنْ جَهَنَّمَ مِهَادٌ وَمِنْ فَوْقِهِمْ غَوَاشٍ وَكَذَلِكَ نَجْزِي الظَّالِمِينَ}
INSERT INTO [b9617] VALUES (970,'<span data-type="title" id=toc-956>٤٢ </span>- {وَالَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ لا نُكَلِّفُ نَفْسًا إِلا وُسْعَهَا أُولَئِكَ أَصْحَابُ الْجَنَّةِ هُمْ فِيهَا خَالِدُونَ}
INSERT INTO [b9617] VALUES (971,'<span data-type="title" id=toc-957>٤٣ </span>- {وَنَزَعْنَا مَا فِي صُدُورِهِمْ مِنْ غِلٍّ تَجْرِي مِنْ تَحْتِهِمُ الأَنْهَارُ وَقَالُوا الْحَمْدُ لِلَّهِ الَّذِي هَدَانَا لِهَذَا وَمَا كُنَّا لِنَهْتَدِيَ لَوْلا أَنْ هَدَانَا اللَّهُ لَقَدْ جَاءَتْ رُسُلُ رَبِّنَا بِالْحَقِّ وَنُودُوا أَنْ تِلْكُمُ الْجَنَّةُ أُورِثْتُمُوهَا بِمَا كُنْتُمْ تَعْمَلُونَ}
INSERT INTO [b9617] VALUES (972,'<span data-type="title" id=toc-958>٤٤ </span>- {وَنَادَى أَصْحَابُ الْجَنَّةِ أَصْحَابَ النَّارِ أَنْ قَدْ وَجَدْنَا مَا وَعَدَنَا رَبُّنَا حَقًّا فَهَلْ وَجَدْتُمْ مَا وَعَدَ رَبُّكُمْ حَقًّا قَالُوا نَعَمْ فَأَذَّنَ مُؤَذِّنٌ بَيْنَهُمْ أَنْ لَعْنَةُ اللَّهِ عَلَى الظَّالِمِينَ}
INSERT INTO [b9617] VALUES (973,'<span data-type="title" id=toc-959>٤٥ </span>- {الَّذِينَ يَصُدُّونَ عَنْ سَبِيلِ اللَّهِ وَيَبْغُونَهَا عِوَجًا وَهُمْ بِالآخِرَةِ كَافِرُونَ}
INSERT INTO [b9617] VALUES (974,'<span data-type="title" id=toc-960>٤٦ </span>- {وَبَيْنَهُمَا حِجَابٌ وَعَلَى الأَعْرَافِ رِجَالٌ يَعْرِفُونَ كُلا بِسِيمَاهُمْ وَنَادَوْا أَصْحَابَ الْجَنَّةِ أَنْ سَلامٌ عَلَيْكُمْ لَمْ يَدْخُلُوهَا وَهُمْ يَطْمَعُونَ}
INSERT INTO [b9617] VALUES (975,'<span data-type="title" id=toc-961>٤٧ </span>- {وَإِذَا صُرِفَتْ أَبْصَارُهُمْ تِلْقَاءَ أَصْحَابِ النَّارِ قَالُوا رَبَّنَا لا تَجْعَلْنَا مَعَ الْقَوْمِ الظَّالِمِينَ}
INSERT INTO [b9617] VALUES (976,'<span data-type="title" id=toc-962>٤٨ </span>- {وَنَادَى أَصْحَابُ الأَعْرَافِ رِجَالا يَعْرِفُونَهُمْ بِسِيمَاهُمْ قَالُوا مَا أَغْنَى عَنْكُمْ جَمْعُكُمْ وَمَا كُنْتُمْ تَسْتَكْبِرُونَ}
INSERT INTO [b9617] VALUES (977,'<span data-type="title" id=toc-963>٤٩ </span>- {أَهَؤُلاءِ الَّذِينَ أَقْسَمْتُمْ لا يَنَالُهُمُ اللَّهُ بِرَحْمَةٍ ادْخُلُوا الْجَنَّةَ لا خَوْفٌ عَلَيْكُمْ وَلا أَنْتُمْ تَحْزَنُونَ}
INSERT INTO [b9617] VALUES (978,'<span data-type="title" id=toc-964>٥٠ </span>- {وَنَادَى أَصْحَابُ النَّارِ أَصْحَابَ الْجَنَّةِ أَنْ أَفِيضُوا عَلَيْنَا مِنَ الْمَاءِ أَوْ مِمَّا رَزَقَكُمُ اللَّهُ قَالُوا إِنَّ اللَّهَ حَرَّمَهُمَا عَلَى الْكَافِرِينَ}
INSERT INTO [b9617] VALUES (979,'<span data-type="title" id=toc-965>٥١ </span>- {الَّذِينَ اتَّخَذُوا دِينَهُمْ لَهْوًا وَلَعِبًا وَغَرَّتْهُمُ الْحَيَاةُ الدُّنْيَا فَالْيَوْمَ نَنْسَاهُمْ كَمَا ⦗٣٢١⦘ نَسُوا لِقَاءَ يَوْمِهِمْ هَذَا وَمَا كَانُوا بِآيَاتِنَا يَجْحَدُونَ}
INSERT INTO [b9617] VALUES (980,'<span data-type="title" id=toc-966>٥٢ </span>- {وَلَقَدْ جِئْنَاهُمْ بِكِتَابٍ فَصَّلْنَاهُ عَلَى عِلْمٍ هُدًى وَرَحْمَةً لِقَوْمٍ يُؤْمِنُونَ}
INSERT INTO [b9617] VALUES (981,'<span data-type="title" id=toc-967>٥٣ </span>- {هَلْ يَنْظُرُونَ إِلا تَأْوِيلَهُ يَوْمَ يَأْتِي تَأْوِيلُهُ يَقُولُ الَّذِينَ نَسُوهُ مِنْ قَبْلُ قَدْ جَاءَتْ رُسُلُ رَبِّنَا بِالْحَقِّ فَهَلْ لَنَا مِنْ شُفَعَاءَ فَيَشْفَعُوا لَنَا أَوْ نُرَدُّ فَنَعْمَلَ غَيْرَ الَّذِي كُنَّا نَعْمَلُ قَدْ خَسِرُوا أَنْفُسَهُمْ وَضَلَّ عَنْهُمْ مَا كَانُوا يَفْتَرُونَ}
INSERT INTO [b9617] VALUES (982,'<span data-type="title" id=toc-968>٥٤ </span>- {إِنَّ رَبَّكُمُ اللَّهُ الَّذِي خَلَقَ السَّمَاوَاتِ وَالأَرْضَ فِي سِتَّةِ أَيَّامٍ ثُمَّ اسْتَوَى عَلَى الْعَرْشِ ⦗٣٢٢⦘ يُغْشِي اللَّيْلَ النَّهَارَ يَطْلُبُهُ حَثِيثًا وَالشَّمْسَ وَالْقَمَرَ وَالنُّجُومَ مُسَخَّرَاتٍ بِأَمْرِهِ أَلا لَهُ الْخَلْقُ وَالأَمْرُ تَبَارَكَ اللَّهُ رَبُّ الْعَالَمِينَ}
INSERT INTO [b9617] VALUES (983,'<span data-type="title" id=toc-969>٥٥ </span>- {ادْعُوا رَبَّكُمْ تَضَرُّعًا وَخُفْيَةً إِنَّهُ لا يُحِبُّ الْمُعْتَدِينَ}
INSERT INTO [b9617] VALUES (984,'<span data-type="title" id=toc-970>٥٦ </span>- {وَلا تُفْسِدُوا فِي الأَرْضِ بَعْدَ إِصْلاحِهَا وَادْعُوهُ خَوْفًا وَطَمَعًا إِنَّ رَحْمَتَ اللَّهِ قَرِيبٌ مِنَ الْمُحْسِنِينَ}
INSERT INTO [b9617] VALUES (985,'<span data-type="title" id=toc-971>٥٧ </span>- {وَهُوَ الَّذِي يُرْسِلُ الرِّيَاحَ بُشْرًا بَيْنَ يَدَيْ رَحْمَتِهِ حَتَّى إِذَا أَقَلَّتْ سَحَابًا ثِقَالا سُقْنَاهُ لِبَلَدٍ مَيِّتٍ فَأَنْزَلْنَا بِهِ الْمَاءَ فَأَخْرَجْنَا بِهِ مِنْ كُلِّ الثَّمَرَاتِ كَذَلِكَ نُخْرِجُ الْمَوْتَى لَعَلَّكُمْ تَذَكَّرُونَ}
INSERT INTO [b9617] VALUES (986,'<span data-type="title" id=toc-972>٥٨ </span>- {وَالْبَلَدُ الطَّيِّبُ يَخْرُجُ نَبَاتُهُ بِإِذْنِ رَبِّهِ وَالَّذِي خَبُثَ لا يَخْرُجُ إِلا نَكِدًا كَذَلِكَ نُصَرِّفُ الآيَاتِ لِقَوْمٍ يَشْكُرُونَ}
INSERT INTO [b9617] VALUES (987,'<span data-type="title" id=toc-973>٥٩ </span>- {اعْبُدُوا اللَّهَ مَا لَكُمْ مِنْ إِلَهٍ غَيْرُهُ}
INSERT INTO [b9617] VALUES (988,'<span data-type="title" id=toc-974>٦٠ </span>- {إِنَّا لَنَرَاكَ فِي ضَلالٍ مُبِينٍ}
INSERT INTO [b9617] VALUES (989,'<span data-type="title" id=toc-975>٦١ </span>- {قَالَ يَا قَوْمِ لَيْسَ بِي ضَلالَةٌ وَلَكِنِّي رَسُولٌ مِنْ رَبِّ الْعَالَمِينَ}
INSERT INTO [b9617] VALUES (990,'<span data-type="title" id=toc-976>٦٢ </span>- {أُبَلِّغُكُمْ رِسَالاتِ رَبِّي وَأَنْصَحُ لَكُمْ وَأَعْلَمُ مِنَ اللَّهِ مَا لا تَعْلَمُونَ} ⦗٣٢٤⦘
INSERT INTO [b9617] VALUES (991,'<span data-type="title" id=toc-977>٦٣ </span>- {أَوَعَجِبْتُمْ أَنْ جَاءَكُمْ ذِكْرٌ مِنْ رَبِّكُمْ عَلَى رَجُلٍ مِنْكُمْ لِيُنْذِرَكُمْ وَلِتَتَّقُوا وَلَعَلَّكُمْ تُرْحَمُونَ}
INSERT INTO [b9617] VALUES (992,'<span data-type="title" id=toc-978>٦٤ </span>- {فَكَذَّبُوهُ فَأَنْجَيْنَاهُ وَالَّذِينَ مَعَهُ فِي الْفُلْكِ وَأَغْرَقْنَا الَّذِينَ كَذَّبُوا بِآيَاتِنَا إِنَّهُمْ كَانُوا قَوْمًا عَمِينَ}
INSERT INTO [b9617] VALUES (993,'<span data-type="title" id=toc-979>٦٥ </span>- {وَإِلَى عَادٍ أَخَاهُمْ هُودًا قَالَ يَا قَوْمِ اعْبُدُوا اللَّهَ مَا لَكُمْ مِنْ إِلَهٍ غَيْرُهُ أَفَلا تَتَّقُونَ}
INSERT INTO [b9617] VALUES (994,'<span data-type="title" id=toc-980>٦٦ </span>- {قَالَ الْمَلأُ الَّذِينَ كَفَرُوا مِنْ قَوْمِهِ إِنَّا لَنَرَاكَ فِي سَفَاهَةٍ وَإِنَّا لَنَظُنُّكَ مِنَ ⦗٣٢٥⦘ الْكَاذِبِينَ}
INSERT INTO [b9617] VALUES (995,'<span data-type="title" id=toc-981>٦٨ </span>- {أُبَلِّغُكُمْ رِسَالاتِ رَبِّي وَأَنَا لَكُمْ نَاصِحٌ أَمِينٌ}
INSERT INTO [b9617] VALUES (996,'<span data-type="title" id=toc-982>٦٩ </span>- {أَوَعَجِبْتُمْ أَنْ جَاءَكُمْ ذِكْرٌ مِنْ رَبِّكُمْ عَلَى رَجُلٍ مِنْكُمْ لِيُنْذِرَكُمْ وَاذْكُرُوا إِذْ جَعَلَكُمْ خُلَفَاءَ مِنْ بَعْدِ قَوْمِ نُوحٍ وَزَادَكُمْ فِي الْخَلْقِ بَسْطَةً فَاذْكُرُوا آلاءَ اللَّهِ لَعَلَّكُمْ تُفْلِحُونَ}
INSERT INTO [b9617] VALUES (997,'<span data-type="title" id=toc-983>٧٠ </span>- {قَالُوا أَجِئْتَنَا لِنَعْبُدَ اللَّهَ وَحْدَهُ وَنَذَرَ مَا كَانَ يَعْبُدُ آبَاؤُنَا فَأْتِنَا بِمَا تَعِدُنَا إِنْ كُنْتَ مِنَ الصَّادِقِينَ}
INSERT INTO [b9617] VALUES (998,'<span data-type="title" id=toc-984>٧١ </span>- {قَالَ قَدْ وَقَعَ عَلَيْكُمْ مِنْ رَبِّكُمْ رِجْسٌ وَغَضَبٌ أَتُجَادِلُونَنِي فِي أَسْمَاءٍ سَمَّيْتُمُوهَا أَنْتُمْ وَآبَاؤُكُمْ مَا نَزَّلَ اللَّهُ بِهَا مِنْ سُلْطَانٍ فَانْتَظِرُوا إِنِّي مَعَكُمْ مِنَ الْمُنْتَظِرِينَ}
INSERT INTO [b9617] VALUES (999,'<span data-type="title" id=toc-985>٧٢ </span>- {فَأَنْجَيْنَاهُ وَالَّذِينَ مَعَهُ بِرَحْمَةٍ مِنَّا وَقَطَعْنَا دَابِرَ الَّذِينَ كَذَّبُوا بِآيَاتِنَا وَمَا كَانُوا مُؤْمِنِينَ}
INSERT INTO [b9617] VALUES (1000,'<span data-type="title" id=toc-986>٧٣ </span>- {وَإِلَى ثَمُودَ أَخَاهُمْ صَالِحًا قَالَ يَا قَوْمِ اعْبُدُوا اللَّهَ مَا لَكُمْ مِنْ إِلَهٍ غَيْرُهُ قَدْ جَاءَتْكُمْ بَيِّنَةٌ مِنْ رَبِّكُمْ هَذِهِ نَاقَةُ اللَّهِ لَكُمْ آيَةً فَذَرُوهَا تَأْكُلْ فِي أَرْضِ اللَّهِ وَلا تَمَسُّوهَا بِسُوءٍ فَيَأْخُذَكُمْ عَذَابٌ أَلِيمٌ}
INSERT INTO [b9617] VALUES (1001,'<span data-type="title" id=toc-987>٧٤ </span>- {وَاذْكُرُوا إِذْ جَعَلَكُمْ خُلَفَاءَ مِنْ بَعْدِ عَادٍ وَبَوَّأَكُمْ فِي الأَرْضِ تَتَّخِذُونَ مِنْ سُهُولِهَا قُصُورًا وَتَنْحِتُونَ الْجِبَالَ بُيُوتًا فَاذْكُرُوا آلاءَ اللَّهِ وَلا تَعْثَوْا فِي الأَرْضِ مُفْسِدِينَ}
INSERT INTO [b9617] VALUES (1002,'<span data-type="title" id=toc-988>٧٥ </span>- {قَالَ الْمَلأُ الَّذِينَ اسْتَكْبَرُوا مِنْ قَوْمِهِ لِلَّذِينَ اسْتُضْعِفُوا لِمَنْ آمَنَ مِنْهُمْ أَتَعْلَمُونَ أَنَّ صَالِحًا مُرْسَلٌ مِنْ رَبِّهِ}
INSERT INTO [b9617] VALUES (1003,'<span data-type="title" id=toc-989>٧٦ </span>- {إِنَّا بِالَّذِي آمَنْتُمْ بِهِ كَافِرُونَ}
INSERT INTO [b9617] VALUES (1004,'<span data-type="title" id=toc-990>٧٧ </span>- {فَعَقَرُوا النَّاقَةَ وَعَتَوْا عَنْ أَمْرِ رَبِّهِمْ وَقَالُوا يَا صَالِحُ ائْتِنَا بِمَا تَعِدُنَا إِنْ كُنْتَ مِنَ الْمُرْسَلِينَ}
INSERT INTO [b9617] VALUES (1005,'<span data-type="title" id=toc-991>٧٩ </span>- {فَتَوَلَّى عَنْهُمْ وَقَالَ يَا قَوْمِ لَقَدْ أَبْلَغْتُكُمْ رِسَالَةَ رَبِّي وَنَصَحْتُ لَكُمْ وَلَكِنْ لا تُحِبُّونَ النَّاصِحِينَ}
INSERT INTO [b9617] VALUES (1006,'<span data-type="title" id=toc-992>٨٠ </span>- {وَلُوطًا إِذْ قَالَ لِقَوْمِهِ أَتَأْتُونَ الْفَاحِشَةَ مَا سَبَقَكُمْ بِهَا مِنْ أَحَدٍ مِنَ الْعَالَمِينَ}
INSERT INTO [b9617] VALUES (1007,'<span data-type="title" id=toc-993>٨١ </span>- {إِنَّكُمْ لَتَأْتُونَ الرِّجَالَ شَهْوَةً مِنْ دُونِ النِّسَاءِ بَلْ أَنْتُمْ قَوْمٌ مُسْرِفُونَ}
INSERT INTO [b9617] VALUES (1008,'<span data-type="title" id=toc-994>٨٢ </span>- {وَمَا كَانَ جَوَابَ قَوْمِهِ إِلا أَنْ قَالُوا أَخْرِجُوهُمْ مِنْ قَرْيَتِكُمْ إِنَّهُمْ أُنَاسٌ يَتَطَهَّرُونَ}
INSERT INTO [b9617] VALUES (1009,'<span data-type="title" id=toc-995>٨٣ </span>- {فَأَنْجَيْنَاهُ وَأَهْلَهُ إِلا امْرَأَتَهُ كَانَتْ مِنَ الْغَابِرِينَ}
INSERT INTO [b9617] VALUES (1010,'<span data-type="title" id=toc-996>٨٤ </span>- {وَأَمْطَرْنَا عَلَيْهِمْ مَطَرًا فَانْظُرْ كَيْفَ كَانَ عَاقِبَةُ الْمُجْرِمِينَ}
INSERT INTO [b9617] VALUES (1011,'<span data-type="title" id=toc-997>٨٥ </span>- {وَإِلَى مَدْيَنَ أَخَاهُمْ شُعَيْبًا قَالَ يَا قَوْمِ اعْبُدُوا اللَّهَ مَا لَكُمْ مِنْ إِلَهٍ غَيْرُهُ قَدْ جَاءَتْكُمْ بَيِّنَةٌ مِنْ رَبِّكُمْ فَأَوْفُوا الْكَيْلَ وَالْمِيزَانَ وَلا تَبْخَسُوا النَّاسَ أَشْيَاءَهُمْ وَلا تُفْسِدُوا فِي الأَرْضِ بَعْدَ إِصْلاحِهَا ذَلِكُمْ خَيْرٌ لَكُمْ إِنْ كُنْتُمْ مُؤْمِنِينَ}
INSERT INTO [b9617] VALUES (1012,'<span data-type="title" id=toc-998>٨٦ </span>- {وَلا تَقْعُدُوا بِكُلِّ صِرَاطٍ تُوعِدُونَ وَتَصُدُّونَ عَنْ سَبِيلِ اللَّهِ مَنْ آمَنَ بِهِ وَتَبْغُونَهَا عِوَجًا وَاذْكُرُوا إِذْ كُنْتُمْ قَلِيلا فَكَثَّرَكُمْ وَانْظُرُوا كَيْفَ كَانَ عَاقِبَةُ الْمُفْسِدِينَ}
INSERT INTO [b9617] VALUES (1013,'<span data-type="title" id=toc-999>٨٧ </span>- {وَإِنْ كَانَ طَائِفَةٌ مِنْكُمْ آمَنُوا بِالَّذِي أُرْسِلْتُ بِهِ وَطَائِفَةٌ لَمْ يُؤْمِنُوا فَاصْبِرُوا حَتَّى يَحْكُمَ اللَّهُ بَيْنَنَا وَهُوَ خَيْرُ الْحَاكِمِينَ}
INSERT INTO [b9617] VALUES (1014,'<span data-type="title" id=toc-1000>٨٨ </span>- {قَالَ الْمَلأُ الَّذِينَ اسْتَكْبَرُوا مِنْ قَوْمِهِ لَنُخْرِجَنَّكَ يَا شُعَيْبُ وَالَّذِينَ آمَنُوا مَعَكَ مِنْ قَرْيَتِنَا أَوْ لَتَعُودُنَّ فِي مِلَّتِنَا قَالَ أَوَلَوْ كُنَّا كَارِهِينَ}
INSERT INTO [b9617] VALUES (1015,'<span data-type="title" id=toc-1001>٨٩ </span>- {قَدِ افْتَرَيْنَا عَلَى اللَّهِ كَذِبًا إِنْ عُدْنَا فِي مِلَّتِكُمْ بَعْدَ إِذْ نَجَّانَا اللَّهُ مِنْهَا وَمَا يَكُونُ لَنَا أَنْ نَعُودَ فِيهَا إِلا أَنْ يَشَاءَ اللَّهُ رَبُّنَا وَسِعَ رَبُّنَا كُلَّ شَيْءٍ عِلْمًا عَلَى اللَّهِ تَوَكَّلْنَا رَبَّنَا افْتَحْ بَيْنَنَا وَبَيْنَ قَوْمِنَا بِالْحَقِّ وَأَنْتَ خَيْرُ الْفَاتِحِينَ}
INSERT INTO [b9617] VALUES (1016,'<span data-type="title" id=toc-1002>٩٠ </span>- {لَئِنِ اتَّبَعْتُمْ شُعَيْبًا إِنَّكُمْ إِذًا لَخَاسِرُونَ}
INSERT INTO [b9617] VALUES (1017,'<span data-type="title" id=toc-1003>٩٢ </span>- {الَّذِينَ كَذَّبُوا شُعَيْبًا كَأَنْ لَمْ يَغْنَوْا فِيهَا الَّذِينَ كَذَّبُوا شُعَيْبًا كَانُوا هُمُ الْخَاسِرِينَ}
INSERT INTO [b9617] VALUES (1018,'<span data-type="title" id=toc-1004>٩٣ </span>- {فَتَوَلَّى عَنْهُمْ وَقَالَ يَا قَوْمِ لَقَدْ أَبْلَغْتُكُمْ رِسَالاتِ رَبِّي وَنَصَحْتُ لَكُمْ فَكَيْفَ آسَى عَلَى قَوْمٍ كَافِرِينَ}
INSERT INTO [b9617] VALUES (1019,'<span data-type="title" id=toc-1005>٩٤ </span>- {وَمَا أَرْسَلْنَا فِي قَرْيَةٍ مِنْ نَبِيٍّ إِلا أَخَذْنَا أَهْلَهَا بِالْبَأْسَاءِ وَالضَّرَّاءِ لَعَلَّهُمْ يَضَّرَّعُونَ}
INSERT INTO [b9617] VALUES (1020,'<span data-type="title" id=toc-1006>٩٥ </span>- {ثُمَّ بَدَّلْنَا مَكَانَ السَّيِّئَةِ الْحَسَنَةَ حَتَّى عَفَوْا وَقَالُوا قَدْ مَسَّ آبَاءَنَا الضَّرَّاءُ وَالسَّرَّاءُ فَأَخَذْنَاهُمْ بَغْتَةً وَهُمْ لا يَشْعُرُونَ}
INSERT INTO [b9617] VALUES (1021,'<span data-type="title" id=toc-1007>٩٦ </span>- {وَلَوْ أَنَّ أَهْلَ الْقُرَى آمَنُوا وَاتَّقَوْا لَفَتَحْنَا عَلَيْهِمْ بَرَكَاتٍ مِنَ السَّمَاءِ وَالأَرْضِ وَلَكِنْ كَذَّبُوا فَأَخَذْنَاهُمْ بِمَا كَانُوا يَكْسِبُونَ}
INSERT INTO [b9617] VALUES (1022,'<span data-type="title" id=toc-1008>٩٧ </span>- {أَفَأَمِنَ أَهْلُ الْقُرَى أَنْ يَأْتِيَهُمْ بَأْسُنَا بَيَاتًا وَهُمْ نَائِمُونَ}
INSERT INTO [b9617] VALUES (1023,'<span data-type="title" id=toc-1009>٩٨ </span>- {أَوَأَمِنَ أَهْلُ الْقُرَى أَنْ يَأْتِيَهُمْ بَأْسُنَا ضُحًى وَهُمْ يَلْعَبُونَ}
INSERT INTO [b9617] VALUES (1024,'<span data-type="title" id=toc-1010>٩٩ </span>- {فَلا يَأْمَنُ مَكْرَ اللَّهِ إِلا الْقَوْمُ الْخَاسِرُونَ}
INSERT INTO [b9617] VALUES (1025,'<span data-type="title" id=toc-1011>١٠٠ </span>- {أَوَلَمْ يَهْدِ لِلَّذِينَ يَرِثُونَ الأَرْضَ مِنْ بَعْدِ أَهْلِهَا أَنْ لَوْ نَشَاءُ أَصَبْنَاهُمْ بِذُنُوبِهِمْ وَنَطْبَعُ عَلَى قُلُوبِهِمْ فَهُمْ لا يَسْمَعُونَ}
INSERT INTO [b9617] VALUES (1026,'<span data-type="title" id=toc-1012>١٠١ </span>- {تِلْكَ الْقُرَى نَقُصُّ عَلَيْكَ مِنْ أَنْبَائِهَا وَلَقَدْ جَاءَتْهُمْ رُسُلُهُمْ بِالْبَيِّنَاتِ فَمَا ⦗٣٣٤⦘ كَانُوا لِيُؤْمِنُوا بِمَا كَذَّبُوا مِنْ قَبْلُ كَذَلِكَ يَطْبَعُ اللَّهُ عَلَى قُلُوبِ الْكَافِرِينَ}
INSERT INTO [b9617] VALUES (1027,'<span data-type="title" id=toc-1013>١٠٢ </span>- {وَمَا وَجَدْنَا لأَكْثَرِهِمْ مِنْ عَهْدٍ وَإِنْ وَجَدْنَا أَكْثَرَهُمْ لَفَاسِقِينَ}
INSERT INTO [b9617] VALUES (1028,'<span data-type="title" id=toc-1014>١٠٣ </span>- {فَانْظُرْ كَيْفَ كَانَ عَاقِبَةُ الْمُفْسِدِينَ}
INSERT INTO [b9617] VALUES (1029,'<span data-type="title" id=toc-1015>١٠٥ </span>- {حَقِيقٌ عَلَى أَنْ لا أَقُولَ عَلَى اللَّهِ إِلا الْحَقَّ قَدْ جِئْتُكُمْ بِبَيِّنَةٍ مِنْ رَبِّكُمْ فَأَرْسِلْ مَعِيَ بَنِي إِسْرَائِيلَ}
INSERT INTO [b9617] VALUES (1030,'<span data-type="title" id=toc-1016>١٠٦ </span>- {إِنْ كُنْتَ مِنَ الصَّادِقِينَ}
INSERT INTO [b9617] VALUES (1031,'<span data-type="title" id=toc-1017>١٠٧ </span>- {فَأَلْقَى عَصَاهُ فَإِذَا هِيَ ثُعْبَانٌ مُبِينٌ}
INSERT INTO [b9617] VALUES (1032,'<span data-type="title" id=toc-1018>١٠٨ </span>- {وَنَزَعَ يَدَهُ فَإِذَا هِيَ بَيْضَاءُ لِلنَّاظِرِينَ}
INSERT INTO [b9617] VALUES (1033,'<span data-type="title" id=toc-1019>١٠٩ </span>- {قَالَ الْمَلأُ مِنْ قَوْمِ فِرْعَوْنَ إِنَّ هَذَا لَسَاحِرٌ عَلِيمٌ}
INSERT INTO [b9617] VALUES (1034,'<span data-type="title" id=toc-1020>١١٠ </span>- {يُرِيدُ أَنْ يُخْرِجَكُمْ مِنْ أَرْضِكُمْ فَمَاذَا تَأْمُرُونَ}
INSERT INTO [b9617] VALUES (1035,'<span data-type="title" id=toc-1021>١١١ </span>- {قَالُوا أَرْجِهْ وَأَخَاهُ وَأَرْسِلْ فِي الْمَدَائِنِ حَاشِرِينَ} ⦗٣٣٦⦘
INSERT INTO [b9617] VALUES (1036,'<span data-type="title" id=toc-1022>١١٢ </span>- {يَأْتُوكَ بِكُلِّ سَاحِرٍ عَلِيمٍ}
INSERT INTO [b9617] VALUES (1037,'<span data-type="title" id=toc-1023>١١٣ </span>- {وَجَاءَ السَّحَرَةُ فِرْعَوْنَ قَالُوا إِنَّ لَنَا لأَجْرًا إِنْ كُنَّا نَحْنُ الْغَالِبِينَ}
INSERT INTO [b9617] VALUES (1038,'<span data-type="title" id=toc-1024>١١٤ </span>- {قَالَ نَعَمْ وَإِنَّكُمْ لَمِنَ الْمُقَرَّبِينَ}
INSERT INTO [b9617] VALUES (1039,'<span data-type="title" id=toc-1025>١١٥ </span>- {قَالُوا يَا مُوسَى إِمَّا أَنْ تُلْقِيَ وَإِمَّا أَنْ نَكُونَ نَحْنُ الْمُلْقِينَ}
INSERT INTO [b9617] VALUES (1040,'<span data-type="title" id=toc-1026>١١٦ </span>- {قَالَ أَلْقُوا فَلَمَّا أَلْقَوْا سَحَرُوا أَعْيُنَ النَّاسِ}
INSERT INTO [b9617] VALUES (1041,'<span data-type="title" id=toc-1027>١١٧ </span>- {وَأَوْحَيْنَا إِلَى مُوسَى أَنْ أَلْقِ عَصَاكَ فَإِذَا هِيَ تَلْقَفُ مَا يَأْفِكُونَ}
INSERT INTO [b9617] VALUES (1042,'<span data-type="title" id=toc-1028>١١٨ </span>- {فَوَقَعَ الْحَقُّ وَبَطَلَ مَا كَانُوا يَعْمَلُونَ}
INSERT INTO [b9617] VALUES (1043,'<span data-type="title" id=toc-1029>١١٩ </span>- {فَغُلِبُوا هُنَالِكَ وَانْقَلَبُوا صَاغِرِينَ}
INSERT INTO [b9617] VALUES (1044,'<span data-type="title" id=toc-1030>١٢٠ </span>- {وَأُلْقِيَ السَّحَرَةُ سَاجِدِينَ}
INSERT INTO [b9617] VALUES (1045,'<span data-type="title" id=toc-1031>١٢١ </span>- {قَالُوا آمَنَّا بِرَبِّ الْعَالَمِينَ}
INSERT INTO [b9617] VALUES (1046,'<span data-type="title" id=toc-1032>١٢٣ </span>- {آمَنْتُمْ بِهِ قَبْلَ أَنْ آذَنَ لَكُمْ إِنَّ هَذَا لَمَكْرٌ مَكَرْتُمُوهُ فِي ⦗٣٣٨⦘ الْمَدِينَةِ لِتُخْرِجُوا مِنْهَا أَهْلَهَا فَسَوْفَ تَعْلَمُونَ}
INSERT INTO [b9617] VALUES (1047,'<span data-type="title" id=toc-1033>١٢٤ </span>- {لأُقَطِّعَنَّ أَيْدِيَكُمْ وَأَرْجُلَكُمْ مِنْ خِلافٍ ثُمَّ لأُصَلِّبَنَّكُمْ أَجْمَعِينَ}
INSERT INTO [b9617] VALUES (1048,'<span data-type="title" id=toc-1034>١٢٦ </span>- {وَمَا تَنْقِمُ مِنَّا إِلا أَنْ آمَنَّا بِآيَاتِ رَبِّنَا لَمَّا جَاءَتْنَا}
INSERT INTO [b9617] VALUES (1049,'<span data-type="title" id=toc-1035>١٢٧ </span>- {وَقَالَ الْمَلأُ مِنْ قَوْمِ فِرْعَوْنَ أَتَذَرُ مُوسَى وَقَوْمَهُ لِيُفْسِدُوا فِي الأَرْضِ وَيَذَرَكَ وَآلِهَتَكَ قَالَ سَنُقَتِّلُ أَبْنَاءَهُمْ وَنَسْتَحْيِي نِسَاءَهُمْ}
INSERT INTO [b9617] VALUES (1050,'<span data-type="title" id=toc-1036>١٢٨ </span>- {قَالَ مُوسَى لِقَوْمِهِ اسْتَعِينُوا بِاللَّهِ وَاصْبِرُوا إِنَّ الأَرْضَ لِلَّهِ يُورِثُهَا مَنْ يَشَاءُ ⦗٣٣٩⦘ مِنْ عِبَادِهِ وَالْعَاقِبَةُ لِلْمُتَّقِينَ}
INSERT INTO [b9617] VALUES (1051,'<span data-type="title" id=toc-1037>١٢٩ </span>- {قَالُوا أُوذِينَا مِنْ قَبْلِ أَنْ تَأْتِيَنَا وَمِنْ بَعْدِ مَا جِئْتَنَا قَالَ عَسَى رَبُّكُمْ أَنْ يُهْلِكَ عَدُوَّكُمْ وَيَسْتَخْلِفَكُمْ فِي الأَرْضِ فَيَنْظُرَ كَيْفَ تَعْمَلُونَ}
INSERT INTO [b9617] VALUES (1052,'<span data-type="title" id=toc-1038>١٣١ </span>- {فَإِذَا جَاءَتْهُمُ الْحَسَنَةُ قَالُوا لَنَا هَذِهِ وَإِنْ تُصِبْهُمْ سَيِّئَةٌ يَطَّيَّرُوا بِمُوسَى وَمَنْ مَعَهُ أَلا إِنَّمَا طَائِرُهُمْ عِنْدَ اللَّهِ وَلَكِنَّ أَكْثَرَهُمْ لا يَعْلَمُونَ}
INSERT INTO [b9617] VALUES (1053,'<span data-type="title" id=toc-1039>١٣٢ </span>- {وَقَالُوا مَهْمَا تَأْتِنَا بِهِ مِنْ آيَةٍ لِتَسْحَرَنَا بِهَا فَمَا نَحْنُ لَكَ بِمُؤْمِنِينَ}
INSERT INTO [b9617] VALUES (1054,'<span data-type="title" id=toc-1040>١٣٣ </span>- {فَأَرْسَلْنَا عَلَيْهِمُ الطُّوفَانَ وَالْجَرَادَ وَالْقُمَّلَ وَالضَّفَادِعَ وَالدَّمَ آيَاتٍ ⦗٣٤٠⦘ مُفَصَّلاتٍ فَاسْتَكْبَرُوا وَكَانُوا قَوْمًا مُجْرِمِينَ}
INSERT INTO [b9617] VALUES (1055,'<span data-type="title" id=toc-1041>١٣٤ </span>- {لَئِنْ كَشَفْتَ عَنَّا الرِّجْزَ لَنُؤْمِنَنَّ لَكَ}
INSERT INTO [b9617] VALUES (1056,'<span data-type="title" id=toc-1042>١٣٥ </span>- {فَلَمَّا كَشَفْنَا عَنْهُمُ الرِّجْزَ إِلَى أَجَلٍ هُمْ بَالِغُوهُ إِذَا هُمْ يَنْكُثُونَ}
INSERT INTO [b9617] VALUES (1057,'<span data-type="title" id=toc-1043>١٣٦ </span>- {فَانْتَقَمْنَا مِنْهُمْ فَأَغْرَقْنَاهُمْ فِي الْيَمِّ بِأَنَّهُمْ كَذَّبُوا بِآيَاتِنَا}
INSERT INTO [b9617] VALUES (1058,'<span data-type="title" id=toc-1044>١٣٧ </span>- {وَأَوْرَثْنَا الْقَوْمَ الَّذِينَ كَانُوا يُسْتَضْعَفُونَ مَشَارِقَ الأَرْضِ وَمَغَارِبَهَا الَّتِي بَارَكْنَا فِيهَا وَتَمَّتْ كَلِمَتُ رَبِّكَ الْحُسْنَى عَلَى بَنِي إِسْرَائِيلَ بِمَا صَبَرُوا وَدَمَّرْنَا مَا كَانَ يَصْنَعُ فِرْعَوْنُ وَقَوْمُهُ وَمَا كَانُوا يَعْرِشُونَ}
INSERT INTO [b9617] VALUES (1059,'<span data-type="title" id=toc-1045>١٣٨ </span>- {فَأَتَوْا عَلَى قَوْمٍ يَعْكُفُونَ عَلَى أَصْنَامٍ لَهُمْ قَالُوا يَا مُوسَى اجْعَلْ لَنَا إِلَهًا كَمَا لَهُمْ آلِهَةٌ}
INSERT INTO [b9617] VALUES (1060,'<span data-type="title" id=toc-1046>١٣٩ </span>- {إِنَّ هَؤُلاءِ مُتَبَّرٌ مَا هُمْ فِيهِ وَبَاطِلٌ مَا كَانُوا يَعْمَلُونَ}
INSERT INTO [b9617] VALUES (1061,'<span data-type="title" id=toc-1047>١٤٠ </span>- {قَالَ أَغَيْرَ اللَّهِ أَبْغِيكُمْ إِلَهًا وَهُوَ فَضَّلَكُمْ عَلَى الْعَالَمِينَ}
INSERT INTO [b9617] VALUES (1062,'<span data-type="title" id=toc-1048>١٤١ </span>- {وَإِذْ أَنْجَيْنَاكُمْ مِنْ آلِ فِرْعَوْنَ يَسُومُونَكُمْ سُوءَ الْعَذَابِ يُقَتِّلُونَ أَبْنَاءَكُمْ وَيَسْتَحْيُونَ نِسَاءَكُمْ وَفِي ذَلِكُمْ بَلاءٌ مِنْ رَبِّكُمْ عَظِيمٌ}
INSERT INTO [b9617] VALUES (1063,'<span data-type="title" id=toc-1049>١٤٢ </span>- {وَوَاعَدْنَا مُوسَى ثَلاثِينَ لَيْلَةً وَأَتْمَمْنَاهَا بِعَشْرٍ فَتَمَّ مِيقَاتُ رَبِّهِ أَرْبَعِينَ لَيْلَةً وَقَالَ مُوسَى لأَخِيهِ هَارُونَ اخْلُفْنِي فِي قَوْمِي}
INSERT INTO [b9617] VALUES (1064,'<span data-type="title" id=toc-1050>١٤٣ </span>- {قَالَ رَبِّ أَرِنِي أَنْظُرْ إِلَيْكَ قَالَ لَنْ تَرَانِي وَلَكِنِ انْظُرْ إِلَى الْجَبَلِ فَإِنِ اسْتَقَرَّ مَكَانَهُ فَسَوْفَ تَرَانِي فَلَمَّا تَجَلَّى رَبُّهُ لِلْجَبَلِ جَعَلَهُ دَكًّا وَخَرَّ مُوسَى صَعِقًا فَلَمَّا أَفَاقَ قَالَ سُبْحَانَكَ تُبْتُ إِلَيْكَ وَأَنَا أَوَّلُ الْمُؤْمِنِينَ}
INSERT INTO [b9617] VALUES (1065,'<span data-type="title" id=toc-1051>١٤٥ </span>- {وَكَتَبْنَا لَهُ فِي الأَلْوَاحِ مِنْ كُلِّ شَيْءٍ مَوْعِظَةً وَتَفْصِيلا لِكُلِّ شَيْءٍ فَخُذْهَا بِقُوَّةٍ وَأْمُرْ قَوْمَكَ يَأْخُذُوا بِأَحْسَنِهَا سَأُرِيكُمْ دَارَ الْفَاسِقِينَ}
INSERT INTO [b9617] VALUES (1066,'<span data-type="title" id=toc-1052>١٤٦ </span>- {الَّذِينَ يَتَكَبَّرُونَ فِي الأَرْضِ بِغَيْرِ الْحَقِّ وَإِنْ يَرَوْا كُلَّ آيَةٍ لا يُؤْمِنُوا بِهَا وَإِنْ يَرَوْا سَبِيلَ الرُّشْدِ لا يَتَّخِذُوهُ سَبِيلا وَإِنْ يَرَوْا سَبِيلَ الْغَيِّ يَتَّخِذُوهُ سَبِيلا ذَلِكَ بِأَنَّهُمْ كَذَّبُوا بِآيَاتِنَا}
INSERT INTO [b9617] VALUES (1067,'<span data-type="title" id=toc-1053>١٤٧ </span>- {وَالَّذِينَ كَذَّبُوا بِآيَاتِنَا وَلِقَاءِ الآخِرَةِ حَبِطَتْ أَعْمَالُهُمْ هَلْ يُجْزَوْنَ إِلا مَا كَانُوا يَعْمَلُونَ}
INSERT INTO [b9617] VALUES (1068,'<span data-type="title" id=toc-1054>١٤٨ </span>- {وَاتَّخَذَ قَوْمُ مُوسَى مِنْ بَعْدِهِ مِنْ حُلِيِّهِمْ عِجْلا جَسَدًا لَهُ خُوَارٌ أَلَمْ يَرَوْا أَنَّهُ لا يُكَلِّمُهُمْ وَلا يَهْدِيهِمْ سَبِيلا اتَّخَذُوهُ وَكَانُوا ظَالِمِينَ}
INSERT INTO [b9617] VALUES (1069,'<span data-type="title" id=toc-1055>١٤٩ </span>- {وَلَمَّا سُقِطَ فِي أَيْدِيهِمْ وَرَأَوْا أَنَّهُمْ قَدْ ضَلُّوا قَالُوا لَئِنْ لَمْ يَرْحَمْنَا رَبُّنَا وَيَغْفِرْ لَنَا لَنَكُونَنَّ مِنَ الْخَاسِرِينَ}
INSERT INTO [b9617] VALUES (1070,'<span data-type="title" id=toc-1056>١٥٠ </span>- {وَلَمَّا رَجَعَ مُوسَى إِلَى قَوْمِهِ غَضْبَانَ أَسِفًا قَالَ بِئْسَمَا خَلَفْتُمُونِي مِنْ بَعْدِي أَعَجِلْتُمْ أَمْرَ رَبِّكُمْ وَأَلْقَى الأَلْوَاحَ وَأَخَذَ بِرَأْسِ أَخِيهِ يَجُرُّهُ إِلَيْهِ قَالَ ابْنَ أُمَّ إِنَّ الْقَوْمَ اسْتَضْعَفُونِي وَكَادُوا يَقْتُلُونَنِي فَلا تُشْمِتْ بِيَ الأَعْدَاءَ}
INSERT INTO [b9617] VALUES (1071,'<span data-type="title" id=toc-1057>١٥١ </span>- {وَأَدْخِلْنَا فِي رَحْمَتِكَ وَأَنْتَ أَرْحَمُ الرَّاحِمِينَ}
INSERT INTO [b9617] VALUES (1072,'<span data-type="title" id=toc-1058>١٥٢ </span>- {إِنَّ الَّذِينَ اتَّخَذُوا الْعِجْلَ سَيَنَالُهُمْ غَضَبٌ مِنْ رَبِّهِمْ وَذِلَّةٌ فِي الْحَيَاةِ الدُّنْيَا وَكَذَلِكَ نَجْزِي الْمُفْتَرِينَ}
INSERT INTO [b9617] VALUES (1073,'<span data-type="title" id=toc-1059>١٥٣ </span>- {وَالَّذِينَ عَمِلُوا السَّيِّئَاتِ ثُمَّ تَابُوا مِنْ بَعْدِهَا وَآمَنُوا إِنَّ رَبَّكَ مِنْ بَعْدِهَا لَغَفُورٌ رَحِيمٌ}
INSERT INTO [b9617] VALUES (1074,'<span data-type="title" id=toc-1060>١٥٤ </span>- {أَخَذَ الأَلْوَاحَ وَفِي نُسْخَتِهَا هُدًى وَرَحْمَةٌ لِلَّذِينَ هُمْ لِرَبِّهِمْ يَرْهَبُونَ}
INSERT INTO [b9617] VALUES (1075,'<span data-type="title" id=toc-1061>١٥٥ </span>- {وَاخْتَارَ مُوسَى قَوْمَهُ سَبْعِينَ رَجُلا لِمِيقَاتِنَا فَلَمَّا أَخَذَتْهُمُ الرَّجْفَةُ قَالَ رَبِّ لَوْ شِئْتَ أَهْلَكْتَهُمْ مِنْ قَبْلُ وَإِيَّايَ أَتُهْلِكُنَا بِمَا فَعَلَ السُّفَهَاءُ مِنَّا إِنْ هِيَ إِلا فِتْنَتُكَ تُضِلُّ بِهَا مَنْ ⦗٣٤٦⦘ تَشَاءُ وَتَهْدِي مَنْ تَشَاءُ أَنْتَ وَلِيُّنَا فَاغْفِرْ لَنَا}
INSERT INTO [b9617] VALUES (1076,'<span data-type="title" id=toc-1062>١٥٦ </span>- {وَاكْتُبْ لَنَا فِي هَذِهِ الدُّنْيَا حَسَنَةً وَفِي الآخِرَةِ إِنَّا هُدْنَا إِلَيْكَ قَالَ عَذَابِي أُصِيبُ بِهِ مَنْ أَشَاءُ وَرَحْمَتِي وَسِعَتْ كُلَّ شَيْءٍ فَسَأَكْتُبُهَا لِلَّذِينَ يَتَّقُونَ}
INSERT INTO [b9617] VALUES (1077,'<span data-type="title" id=toc-1063>١٥٧ </span>- {الَّذِينَ يَتَّبِعُونَ الرَّسُولَ النَّبِيَّ الأُمِّيَّ الَّذِي يَجِدُونَهُ مَكْتُوبًا عِنْدَهُمْ فِي التَّوْرَاةِ وَالإِنْجِيلِ يَأْمُرُهُمْ بِالْمَعْرُوفِ وَيَنْهَاهُمْ عَنِ الْمُنْكَرِ وَيُحِلُّ لَهُمُ الطَّيِّبَاتِ وَيُحَرِّمُ عَلَيْهِمُ الْخَبَائِثَ وَيَضَعُ عَنْهُمْ إِصْرَهُمْ وَالأَغْلالَ الَّتِي كَانَتْ عَلَيْهِمْ فَالَّذِينَ ⦗٣٤٧⦘ آمَنُوا بِهِ وَعَزَّرُوهُ وَنَصَرُوهُ وَاتَّبَعُوا النُّورَ الَّذِي أُنْزِلَ مَعَهُ أُولَئِكَ هُمُ الْمُفْلِحُونَ}
INSERT INTO [b9617] VALUES (1078,'<span data-type="title" id=toc-1064>١٥٨ </span>- {يَا أَيُّهَا النَّاسُ إِنِّي رَسُولُ اللَّهِ إِلَيْكُمْ جَمِيعًا الَّذِي لَهُ مُلْكُ السَّمَاوَاتِ وَالأَرْضِ لا إِلَهَ إِلا هُوَ يُحْيِي وَيُمِيتُ فَآمِنُوا بِاللَّهِ وَرَسُولِهِ النَّبِيِّ الأُمِّيِّ الَّذِي يُؤْمِنُ بِاللَّهِ وَكَلِمَاتِهِ وَاتَّبِعُوهُ لَعَلَّكُمْ تَهْتَدُونَ}
INSERT INTO [b9617] VALUES (1079,'<span data-type="title" id=toc-1065>١٥٩ </span>- {وَمِنْ قَوْمِ مُوسَى أُمَّةٌ يَهْدُونَ بِالْحَقِّ وَبِهِ يَعْدِلُونَ}
INSERT INTO [b9617] VALUES (1080,'<span data-type="title" id=toc-1066>١٦٠ </span>- {وَقَطَّعْنَاهُمُ اثْنَتَيْ عَشْرَةَ أَسْبَاطًا أُمَمًا وَأَوْحَيْنَا إِلَى مُوسَى إِذِ اسْتَسْقَاهُ قَوْمُهُ أَنِ اضْرِبْ بِعَصَاكَ الْحَجَرَ فَانْبَجَسَتْ مِنْهُ اثْنَتَا عَشْرَةَ عَيْنًا قَدْ عَلِمَ كُلُّ أُنَاسٍ مَشْرَبَهُمْ وَظَلَّلْنَا عَلَيْهِمُ الْغَمَامَ وَأَنْزَلْنَا عَلَيْهِمُ الْمَنَّ وَالسَّلْوَى كُلُوا مِنْ طَيِّبَاتِ مَا رَزَقْنَاكُمْ وَمَا ظَلَمُونَا وَلَكِنْ كَانُوا أَنْفُسَهُمْ يَظْلِمُونَ} ⦗٣٤٨⦘
INSERT INTO [b9617] VALUES (1081,'<span data-type="title" id=toc-1067>١٦١ </span>- {وَإِذْ قِيلَ لَهُمُ اسْكُنُوا هَذِهِ الْقَرْيَةَ وَكُلُوا مِنْهَا حَيْثُ شِئْتُمْ وَقُولُوا حِطَّةٌ وَادْخُلُوا الْبَابَ سُجَّدًا نَغْفِرْ لَكُمْ خَطِيئَاتِكُمْ سَنَزِيدُ الْمُحْسِنِينَ}
INSERT INTO [b9617] VALUES (1082,'<span data-type="title" id=toc-1068>١٦٢ </span>- {فَبَدَّلَ الَّذِينَ ظَلَمُوا مِنْهُمْ قَوْلا غَيْرَ الَّذِي قِيلَ لَهُمْ فَأَرْسَلْنَا عَلَيْهِمْ رِجْزًا مِنَ السَّمَاءِ بِمَا كَانُوا يَظْلِمُونَ}
INSERT INTO [b9617] VALUES (1083,'<span data-type="title" id=toc-1069>١٦٣ </span>- {وَاسْأَلْهُمْ عَنِ الْقَرْيَةِ الَّتِي كَانَتْ حَاضِرَةَ الْبَحْرِ إِذْ يَعْدُونَ فِي السَّبْتِ إِذْ تَأْتِيهِمْ حِيتَانُهُمْ يَوْمَ سَبْتِهِمْ شُرَّعًا وَيَوْمَ لا يَسْبِتُونَ لا تَأْتِيهِمْ كَذَلِكَ نَبْلُوهُمْ بِمَا كَانُوا يَفْسُقُونَ}
INSERT INTO [b9617] VALUES (1084,'<span data-type="title" id=toc-1070>١٦٤ </span>- {وَإِذْ قَالَتْ أُمَّةٌ مِنْهُمْ لِمَ تَعِظُونَ قَوْمًا اللَّهُ مُهْلِكُهُمْ أَوْ مُعَذِّبُهُمْ عَذَابًا شَدِيدًا قَالُوا مَعْذِرَةً إِلَى رَبِّكُمْ وَلَعَلَّهُمْ يَتَّقُونَ}
INSERT INTO [b9617] VALUES (1085,'<span data-type="title" id=toc-1071>١٦٥ </span>- {فَلَمَّا نَسُوا مَا ذُكِّرُوا بِهِ أَنْجَيْنَا الَّذِينَ يَنْهَوْنَ عَنِ السُّوءِ وَأَخَذْنَا الَّذِينَ ظَلَمُوا بِعَذَابٍ بَئِيسٍ بِمَا كَانُوا يَفْسُقُونَ}
INSERT INTO [b9617] VALUES (1086,'<span data-type="title" id=toc-1072>١٦٦ </span>- {فَلَمَّا عَتَوْا عَنْ مَا نُهُوا عَنْهُ قُلْنَا لَهُمْ كُونُوا قِرَدَةً خَاسِئِينَ}
INSERT INTO [b9617] VALUES (1087,'<span data-type="title" id=toc-1073>١٦٧ </span>- {وَإِذْ تَأَذَّنَ رَبُّكَ لَيَبْعَثَنَّ عَلَيْهِمْ إِلَى يَوْمِ الْقِيَامَةِ مَنْ يَسُومُهُمْ سُوءَ الْعَذَابِ}
INSERT INTO [b9617] VALUES (1088,'<span data-type="title" id=toc-1074>١٦٨ </span>- {وَقَطَّعْنَاهُمْ فِي الأَرْضِ أُمَمًا مِنْهُمُ الصَّالِحُونَ وَمِنْهُمْ دُونَ ذَلِكَ وَبَلَوْنَاهُمْ بِالْحَسَنَاتِ وَالسَّيِّئَاتِ لَعَلَّهُمْ يَرْجِعُونَ}
INSERT INTO [b9617] VALUES (1089,'<span data-type="title" id=toc-1075>١٦٩ </span>- {فَخَلَفَ مِنْ بَعْدِهِمْ خَلْفٌ وَرِثُوا الْكِتَابَ يَأْخُذُونَ عَرَضَ هَذَا الأَدْنَى وَيَقُولُونَ سَيُغْفَرُ لَنَا وَإِنْ يَأْتِهِمْ عَرَضٌ مِثْلُهُ يَأْخُذُوهُ أَلَمْ يُؤْخَذْ عَلَيْهِمْ مِيثَاقُ الْكِتَابِ أَنْ لا يَقُولُوا عَلَى اللَّهِ إِلا الْحَقَّ وَدَرَسُوا مَا فِيهِ وَالدَّارُ الآخِرَةُ خَيْرٌ لِلَّذِينَ يَتَّقُونَ}
INSERT INTO [b9617] VALUES (1090,'<span data-type="title" id=toc-1076>١٧٠ </span>- {وَالَّذِينَ يُمَسِّكُونَ بِالْكِتَابِ وَأَقَامُوا الصَّلاةَ إِنَّا لا نُضِيعُ أَجْرَ الْمُصْلِحِينَ}
INSERT INTO [b9617] VALUES (1091,'<span data-type="title" id=toc-1077>١٧١ </span>- {وَإِذْ نَتَقْنَا الْجَبَلَ فَوْقَهُمْ كَأَنَّهُ ظُلَّةٌ وَظَنُّوا أَنَّهُ وَاقِعٌ بِهِمْ خُذُوا مَا آتَيْنَاكُمْ بِقُوَّةٍ وَاذْكُرُوا مَا فِيهِ لَعَلَّكُمْ تَتَّقُونَ}
INSERT INTO [b9617] VALUES (1092,'<span data-type="title" id=toc-1078>١٧٢ </span>- {وَإِذْ أَخَذَ رَبُّكَ مِنْ بَنِي آدَمَ مِنْ ظُهُورِهِمْ ذُرِّيَّتَهُمْ وَأَشْهَدَهُمْ عَلَى أَنْفُسِهِمْ أَلَسْتُ ⦗٣٥٢⦘ بِرَبِّكُمْ قَالُوا بَلَى شَهِدْنَا أَنْ تَقُولُوا يَوْمَ الْقِيَامَةِ إِنَّا كُنَّا عَنْ هَذَا غَافِلِينَ}
INSERT INTO [b9617] VALUES (1093,'<span data-type="title" id=toc-1079>١٧٣ </span>- {أَوْ تَقُولُوا إِنَّمَا أَشْرَكَ آبَاؤُنَا مِنْ قَبْلُ وَكُنَّا ذُرِّيَّةً مِنْ بَعْدِهِمْ أَفَتُهْلِكُنَا بِمَا فَعَلَ الْمُبْطِلُونَ}
INSERT INTO [b9617] VALUES (1094,'<span data-type="title" id=toc-1080>١٧٤ </span>- {وَكَذَلِكَ نُفَصِّلُ الآيَاتِ وَلَعَلَّهُمْ يَرْجِعُونَ}
INSERT INTO [b9617] VALUES (1095,'<span data-type="title" id=toc-1081>١٧٥ </span>- {وَاتْلُ عَلَيْهِمْ نَبَأَ الَّذِي آتَيْنَاهُ آيَاتِنَا فَانْسَلَخَ مِنْهَا}
INSERT INTO [b9617] VALUES (1096,'<span data-type="title" id=toc-1082>١٧٦ </span>- {وَلَوْ شِئْنَا لَرَفَعْنَاهُ بِهَا وَلَكِنَّهُ أَخْلَدَ إِلَى الأَرْضِ وَاتَّبَعَ هَوَاهُ فَمَثَلُهُ كَمَثَلِ ⦗٣٥٣⦘ الْكَلْبِ إِنْ تَحْمِلْ عَلَيْهِ يَلْهَثْ أَوْ تَتْرُكْهُ يَلْهَثْ ذَلِكَ مَثَلُ الْقَوْمِ الَّذِينَ كَذَّبُوا بِآيَاتِنَا فَاقْصُصِ الْقَصَصَ لَعَلَّهُمْ يَتَفَكَّرُونَ}
INSERT INTO [b9617] VALUES (1097,'<span data-type="title" id=toc-1083>١٧٧ </span>- {سَاءَ مَثَلا الْقَوْمُ الَّذِينَ كَذَّبُوا بِآيَاتِنَا وَأَنْفُسَهُمْ كَانُوا يَظْلِمُونَ}
INSERT INTO [b9617] VALUES (1098,'<span data-type="title" id=toc-1084>١٧٨ </span>- {مَنْ يَهْدِ اللَّهُ فَهُوَ الْمُهْتَدِي وَمَنْ يُضْلِلْ فَأُولَئِكَ هُمُ الْخَاسِرُونَ}
INSERT INTO [b9617] VALUES (1099,'<span data-type="title" id=toc-1085>١٧٩ </span>- {وَلَقَدْ ذَرَأْنَا لِجَهَنَّمَ كَثِيرًا مِنَ الْجِنِّ وَالإِنْسِ لَهُمْ قُلُوبٌ لا يَفْقَهُونَ بِهَا وَلَهُمْ أَعْيُنٌ لا يُبْصِرُونَ بِهَا وَلَهُمْ آذَانٌ لا يَسْمَعُونَ بِهَا أُولَئِكَ كَالأَنْعَامِ بَلْ هُمْ أَضَلُّ} ⦗٣٥٤⦘
INSERT INTO [b9617] VALUES (1100,'<span data-type="title" id=toc-1086>١٨٠ </span>- {وَلِلَّهِ الأَسْمَاءُ الْحُسْنَى فَادْعُوهُ بِهَا وَذَرُوا الَّذِينَ يُلْحِدُونَ فِي أَسْمَائِهِ سَيُجْزَوْنَ مَا كَانُوا يَعْمَلُونَ}
INSERT INTO [b9617] VALUES (1101,'<span data-type="title" id=toc-1087>١٨١ </span>- {وَمِمَّنْ خَلَقْنَا أُمَّةٌ يَهْدُونَ بِالْحَقِّ وَبِهِ يَعْدِلُونَ}
INSERT INTO [b9617] VALUES (1102,'<span data-type="title" id=toc-1088>١٨٢ </span>- {سَنَسْتَدْرِجُهُمْ مِنْ حَيْثُ لا يَعْلَمُونَ}
INSERT INTO [b9617] VALUES (1103,'<span data-type="title" id=toc-1089>١٨٣ </span>- {إِنَّ كَيْدِي مَتِينٌ}
INSERT INTO [b9617] VALUES (1104,'<span data-type="title" id=toc-1090>١٨٤ </span>- {أَوَلَمْ يَتَفَكَّرُوا مَا بِصَاحِبِهِمْ مِنْ جِنَّةٍ إِنْ هُوَ إِلا نَذِيرٌ مُبِينٌ}
INSERT INTO [b9617] VALUES (1105,'<span data-type="title" id=toc-1091>١٨٥ </span>- {أَوَلَمْ يَنْظُرُوا فِي مَلَكُوتِ السَّمَاوَاتِ وَالأَرْضِ وَمَا خَلَقَ اللَّهُ مِنْ شَيْءٍ وَأَنْ عَسَى أَنْ يَكُونَ قَدِ اقْتَرَبَ أَجَلُهُمْ}
INSERT INTO [b9617] VALUES (1106,'<span data-type="title" id=toc-1092>١٨٦ </span>- {مَنْ يُضْلِلِ اللَّهُ فَلا هَادِيَ لَهُ وَيَذَرُهُمْ فِي طُغْيَانِهِمْ يَعْمَهُونَ}
INSERT INTO [b9617] VALUES (1107,'<span data-type="title" id=toc-1093>١٨٧ </span>- {يَسْأَلُونَكَ عَنِ السَّاعَةِ أَيَّانَ مُرْسَاهَا قُلْ إِنَّمَا عِلْمُهَا عِنْدَ رَبِّي لا يُجَلِّيهَا لِوَقْتِهَا إِلا هُوَ ثَقُلَتْ فِي السَّمَاوَاتِ وَالأَرْضِ لا تَأْتِيكُمْ إِلا بَغْتَةً يَسْأَلُونَكَ كَأَنَّكَ حَفِيٌّ عَنْهَا قُلْ إِنَّمَا عِلْمُهَا عِنْدَ اللَّهِ وَلَكِنَّ ⦗٣٥٦⦘ أَكْثَرَ النَّاسِ لا يَعْلَمُونَ}
INSERT INTO [b9617] VALUES (1108,'<span data-type="title" id=toc-1094>١٨٨ </span>- {قُلْ لا أَمْلِكُ لِنَفْسِي نَفْعًا وَلا ضَرًّا إِلا مَا شَاءَ اللَّهُ وَلَوْ كُنْتُ أَعْلَمُ الْغَيْبَ لاسْتَكْثَرْتُ مِنَ الْخَيْرِ وَمَا مَسَّنِيَ السُّوءُ إِنْ أَنَا إِلا نَذِيرٌ وَبَشِيرٌ}
INSERT INTO [b9617] VALUES (1109,'<span data-type="title" id=toc-1095>١٨٩ </span>- {وَجَعَلَ مِنْهَا زَوْجَهَا لِيَسْكُنَ إِلَيْهَا فَلَمَّا تَغَشَّاهَا حَمَلَتْ حَمْلا خَفِيفًا فَمَرَّتْ بِهِ فَلَمَّا أَثْقَلَتْ دَعَوَا اللَّهَ رَبَّهُمَا لَئِنْ آتَيْتَنَا صَالِحًا لَنَكُونَنَّ مِنَ الشَّاكِرِينَ}
INSERT INTO [b9617] VALUES (1110,'<span data-type="title" id=toc-1096>١٩٠ </span>- {جَعَلا لَهُ شُرَكَاءَ فِيمَا آتَاهُمَا فَتَعَالَى اللَّهُ عَمَّا يُشْرِكُونَ}
INSERT INTO [b9617] VALUES (1111,'<span data-type="title" id=toc-1097>١٩١ </span>- {أَيُشْرِكُونَ مَا لا يَخْلُقُ شَيْئًا وَهُمْ يُخْلَقُونَ}
INSERT INTO [b9617] VALUES (1112,'<span data-type="title" id=toc-1098>١٩٢ </span>- {وَلا يَسْتَطِيعُونَ لَهُمْ نَصْرًا وَلا أَنْفُسَهُمْ يَنْصُرُونَ}
INSERT INTO [b9617] VALUES (1113,'<span data-type="title" id=toc-1099>١٩٣ </span>- {سَوَاءٌ عَلَيْكُمْ أَدَعَوْتُمُوهُمْ أَمْ أَنْتُمْ صَامِتُونَ}
INSERT INTO [b9617] VALUES (1114,'<span data-type="title" id=toc-1100>١٩٤ </span>- {إِنَّ الَّذِينَ تَدْعُونَ مِنْ دُونِ اللَّهِ عِبَادٌ أَمْثَالُكُمْ فَادْعُوهُمْ فَلْيَسْتَجِيبُوا لَكُمْ}
INSERT INTO [b9617] VALUES (1115,'<span data-type="title" id=toc-1101>١٩٥ </span>- {أَلَهُمْ أَرْجُلٌ يَمْشُونَ بِهَا أَمْ لَهُمْ أَيْدٍ يَبْطِشُونَ بِهَا أَمْ لَهُمْ أَعْيُنٌ يُبْصِرُونَ بِهَا أَمْ لَهُمْ آذَانٌ يَسْمَعُونَ بِهَا قُلِ ادْعُوا شُرَكَاءَكُمْ ثُمَّ كِيدُونِ فَلا تُنْظِرُونِ}
INSERT INTO [b9617] VALUES (1116,'<span data-type="title" id=toc-1102>١٩٦ </span>- {إِنَّ وَلِيِّيَ اللَّهُ الَّذِي نَزَّلَ الْكِتَابَ وَهُوَ يَتَوَلَّى الصَّالِحِينَ}
INSERT INTO [b9617] VALUES (1117,'<span data-type="title" id=toc-1103>١٩٧ </span>- {وَالَّذِينَ تَدْعُونَ مِنْ دُونِهِ لا يَسْتَطِيعُونَ نَصْرَكُمْ}
INSERT INTO [b9617] VALUES (1118,'<span data-type="title" id=toc-1104>١٩٨ </span>- {وَإِنْ تَدْعُوهُمْ إِلَى الْهُدَى لا يَسْمَعُوا وَتَرَاهُمْ يَنْظُرُونَ إِلَيْكَ وَهُمْ لا يُبْصِرُونَ}
INSERT INTO [b9617] VALUES (1119,'<span data-type="title" id=toc-1105>٢٠٠ </span>- {وَإِمَّا يَنْزَغَنَّكَ مِنَ الشَّيْطَانِ نَزْغٌ فَاسْتَعِذْ بِاللَّهِ إِنَّهُ سَمِيعٌ عَلِيمٌ} ⦗٣٥٩⦘
INSERT INTO [b9617] VALUES (1120,'<span data-type="title" id=toc-1106>٢٠١ </span>- {إِنَّ الَّذِينَ اتَّقَوْا إِذَا مَسَّهُمْ طَائِفٌ مِنَ الشَّيْطَانِ تَذَكَّرُوا فَإِذَا هُمْ مُبْصِرُونَ}
INSERT INTO [b9617] VALUES (1121,'<span data-type="title" id=toc-1107>٢٠٢ </span>- {وَإِخْوَانُهُمْ يَمُدُّونَهُمْ فِي الْغَيِّ ثُمَّ لا يُقْصِرُونَ}
INSERT INTO [b9617] VALUES (1122,'<span data-type="title" id=toc-1108>٢٠٣ </span>- {لَوْلا اجْتَبَيْتَهَا قُلْ إِنَّمَا أَتَّبِعُ مَا يُوحَى إِلَيَّ مِنْ رَبِّي هَذَا بَصَائِرُ مِنْ رَبِّكُمْ وَهُدًى وَرَحْمَةٌ لِقَوْمٍ يُؤْمِنُونَ}
INSERT INTO [b9617] VALUES (1123,'<span data-type="title" id=toc-1109>٢٠٤ </span>- {لَعَلَّكُمْ تُرْحَمُونَ} ⦗٣٦٠⦘
INSERT INTO [b9617] VALUES (1124,'<span data-type="title" id=toc-1110>٢٠٥ </span>- {وَاذْكُرْ رَبَّكَ فِي نَفْسِكَ تَضَرُّعًا وَخِيفَةً وَدُونَ الْجَهْرِ مِنَ الْقَوْلِ بِالْغُدُوِّ وَالآصَالِ وَلا تَكُنْ مِنَ الْغَافِلِينَ}
INSERT INTO [b9617] VALUES (1125,'<span data-type="title" id=toc-1111>٢٠٦ </span>- {لا يَسْتَكْبِرُونَ عَنْ عِبَادَتِهِ وَيُسَبِّحُونَهُ وَلَهُ يَسْجُدُونَ}
INSERT INTO [b9617] VALUES (1126,'<span data-type="title" id=toc-1112>سورة الأنفال</span>','1','361',NULL,NULL,'0');
INSERT INTO [b9617] VALUES (1127,'<span data-type="title" id=toc-1113>١ </span>- {يَسْأَلُونَكَ عَنِ الأَنْفَالِ قُلِ الأَنْفَالُ لِلَّهِ وَالرَّسُولِ فَاتَّقُوا اللَّهَ وَأَصْلِحُوا ذَاتَ بَيْنِكُمْ وَأَطِيعُوا اللَّهَ وَرَسُولَهُ إِنْ كُنْتُمْ مُؤْمِنِينَ}
INSERT INTO [b9617] VALUES (1128,'<span data-type="title" id=toc-1114>٢ </span>- {إِنَّمَا الْمُؤْمِنُونَ الَّذِينَ إِذَا ذُكِرَ اللَّهُ وَجِلَتْ قُلُوبُهُمْ وَإِذَا تُلِيَتْ عَلَيْهِمْ آيَاتُهُ زَادَتْهُمْ إِيمَانًا وَعَلَى رَبِّهِمْ يَتَوَكَّلُونَ}
INSERT INTO [b9617] VALUES (1129,'<span data-type="title" id=toc-1115>٣ </span>- {الَّذِينَ يُقِيمُونَ الصَّلاةَ وَمِمَّا رَزَقْنَاهُمْ يُنْفِقُونَ}
INSERT INTO [b9617] VALUES (1130,'<span data-type="title" id=toc-1116>٤ </span>- {أُولَئِكَ هُمُ الْمُؤْمِنُونَ حَقًّا لَهُمْ دَرَجَاتٌ عِنْدَ رَبِّهِمْ}
INSERT INTO [b9617] VALUES (1131,'<span data-type="title" id=toc-1117>٥ </span>- {كَمَا أَخْرَجَكَ رَبُّكَ مِنْ بَيْتِكَ بِالْحَقِّ وَإِنَّ فَرِيقًا مِنَ الْمُؤْمِنِينَ لَكَارِهُونَ}
INSERT INTO [b9617] VALUES (1132,'<span data-type="title" id=toc-1118>٦ </span>- {يُجَادِلُونَكَ فِي الْحَقِّ بَعْدَمَا تَبَيَّنَ كَأَنَّمَا يُسَاقُونَ إِلَى الْمَوْتِ وَهُمْ يَنْظُرُونَ}
INSERT INTO [b9617] VALUES (1133,'<span data-type="title" id=toc-1119>٧ </span>- {وَإِذْ يَعِدُكُمُ اللَّهُ إِحْدَى الطَّائِفَتَيْنِ أَنَّهَا لَكُمْ وَتَوَدُّونَ أَنَّ غَيْرَ ذَاتِ الشَّوْكَةِ تَكُونُ لَكُمْ وَيُرِيدُ اللَّهُ أَنْ يُحِقَّ الْحَقَّ بِكَلِمَاتِهِ}
INSERT INTO [b9617] VALUES (1134,'<span data-type="title" id=toc-1120>٨ </span>- {لِيُحِقَّ الْحَقَّ وَيُبْطِلَ الْبَاطِلَ وَلَوْ كَرِهَ الْمُجْرِمُونَ}
INSERT INTO [b9617] VALUES (1135,'<span data-type="title" id=toc-1121>٩ </span>- {إِذْ تَسْتَغِيثُونَ رَبَّكُمْ فَاسْتَجَابَ لَكُمْ أَنِّي مُمِدُّكُمْ بِأَلْفٍ مِنَ الْمَلائِكَةِ مُرْدِفِينَ}
INSERT INTO [b9617] VALUES (1136,'<span data-type="title" id=toc-1122>١٠ </span>- {وَمَا جَعَلَهُ اللَّهُ إِلا بُشْرَى وَلِتَطْمَئِنَّ بِهِ قُلُوبُكُمْ وَمَا النَّصْرُ إِلا مِنْ عِنْدِ اللَّهِ}
INSERT INTO [b9617] VALUES (1137,'<span data-type="title" id=toc-1123>١١ </span>- {إِذْ يُغَشِّيكُمُ النُّعَاسَ أَمَنَةً مِنْهُ وَيُنَزِّلُ عَلَيْكُمْ مِنَ السَّمَاءِ مَاءً لِيُطَهِّرَكُمْ بِهِ وَيُذْهِبَ عَنْكُمْ رِجْزَ الشَّيْطَانِ وَلِيَرْبِطَ عَلَى قُلُوبِكُمْ}
INSERT INTO [b9617] VALUES (1138,'<span data-type="title" id=toc-1124>١٢ </span>- {إِذْ يُوحِي رَبُّكَ إِلَى الْمَلائِكَةِ أَنِّي مَعَكُمْ فَثَبِّتُوا الَّذِينَ آمَنُوا سَأُلْقِي فِي قُلُوبِ الَّذِينَ كَفَرُوا الرُّعْبَ فَاضْرِبُوا فَوْقَ الأَعْنَاقِ وَاضْرِبُوا مِنْهُمْ كُلَّ بَنَانٍ}
INSERT INTO [b9617] VALUES (1139,'<span data-type="title" id=toc-1125>١٣ </span>- {ذَلِكَ بِأَنَّهُمْ شَاقُّوا اللَّهَ وَرَسُولَهُ وَمَنْ يُشَاقِقِ اللَّهَ وَرَسُولَهُ فَإِنَّ اللَّهَ شَدِيدُ الْعِقَابِ}
INSERT INTO [b9617] VALUES (1140,'<span data-type="title" id=toc-1126>١٤ </span>- {ذَلِكُمْ فَذُوقُوهُ وَأَنَّ لِلْكَافِرِينَ عَذَابَ النَّارِ} ⦗٣٦٥⦘
INSERT INTO [b9617] VALUES (1141,'<span data-type="title" id=toc-1127>١٥ </span>- {يَا أَيُّهَا الَّذِينَ آمَنُوا إِذَا لَقِيتُمُ الَّذِينَ كَفَرُوا زَحْفًا فَلا تُوَلُّوهُمُ الأَدْبَارَ}
INSERT INTO [b9617] VALUES (1142,'<span data-type="title" id=toc-1128>١٦ </span>- {وَمَنْ يُوَلِّهِمْ يَوْمَئِذٍ دُبُرَهُ إِلا مُتَحَرِّفًا لِقِتَالٍ أَوْ مُتَحَيِّزًا إِلَى فِئَةٍ فَقَدْ بَاءَ بِغَضَبٍ مِنَ اللَّهِ وَمَأْوَاهُ جَهَنَّمُ وَبِئْسَ الْمَصِيرُ}
INSERT INTO [b9617] VALUES (1143,'<span data-type="title" id=toc-1129>١٧ </span>- {فَلَمْ تَقْتُلُوهُمْ وَلَكِنَّ اللَّهَ قَتَلَهُمْ وَمَا رَمَيْتَ إِذْ رَمَيْتَ وَلَكِنَّ اللَّهَ رَمَى وَلِيُبْلِيَ الْمُؤْمِنِينَ مِنْهُ بَلاءً حَسَنًا}
INSERT INTO [b9617] VALUES (1144,'<span data-type="title" id=toc-1130>١٨ </span>- {ذَلِكُمْ وَأَنَّ اللَّهَ مُوهِنُ كَيْدِ الْكَافِرِينَ}
INSERT INTO [b9617] VALUES (1145,'<span data-type="title" id=toc-1131>١٩ </span>- {وَلَنْ تُغْنِيَ عَنْكُمْ فِئَتُكُمْ شَيْئًا وَلَوْ كَثُرَتْ وَأَنَّ اللَّهَ مَعَ الْمُؤْمِنِينَ}
INSERT INTO [b9617] VALUES (1146,'<span data-type="title" id=toc-1132>٢٠ </span>- {يَا أَيُّهَا الَّذِينَ آمَنُوا أَطِيعُوا اللَّهَ وَرَسُولَهُ وَلا تَوَلَّوْا عَنْهُ وَأَنْتُمْ تَسْمَعُونَ}
INSERT INTO [b9617] VALUES (1147,'<span data-type="title" id=toc-1133>٢١ </span>- {قَالُوا سَمِعْنَا وَهُمْ لا يَسْمَعُونَ}
INSERT INTO [b9617] VALUES (1148,'<span data-type="title" id=toc-1134>٢٢ </span>- {إِنَّ شَرَّ الدَّوَابِّ عِنْدَ اللَّهِ الصُّمُّ الْبُكْمُ الَّذِينَ لا يَعْقِلُونَ}
INSERT INTO [b9617] VALUES (1149,'<span data-type="title" id=toc-1135>٢٣ </span>- {وَلَوْ عَلِمَ اللَّهُ فِيهِمْ خَيْرًا لأَسْمَعَهُمْ وَلَوْ أَسْمَعَهُمْ لَتَوَلَّوْا وَهُمْ مُعْرِضُونَ}
INSERT INTO [b9617] VALUES (1150,'<span data-type="title" id=toc-1136>٢٤ </span>- {يَا أَيُّهَا الَّذِينَ آمَنُوا اسْتَجِيبُوا لِلَّهِ وَلِلرَّسُولِ إِذَا دَعَاكُمْ لِمَا يُحْيِيكُمْ وَاعْلَمُوا أَنَّ اللَّهَ يَحُولُ بَيْنَ الْمَرْءِ وَقَلْبِهِ وَأَنَّهُ إِلَيْهِ تُحْشَرُونَ}
INSERT INTO [b9617] VALUES (1151,'<span data-type="title" id=toc-1137>٢٥ </span>- {وَاتَّقُوا فِتْنَةً لا تُصِيبَنَّ الَّذِينَ ظَلَمُوا مِنْكُمْ خَاصَّةً}
INSERT INTO [b9617] VALUES (1152,'<span data-type="title" id=toc-1138>٢٦ </span>- {وَاذْكُرُوا إِذْ أَنْتُمْ قَلِيلٌ مُسْتَضْعَفُونَ فِي الأَرْضِ تَخَافُونَ أَنْ يَتَخَطَّفَكُمُ النَّاسُ فَآوَاكُمْ وَأَيَّدَكُمْ بِنَصْرِهِ وَرَزَقَكُمْ مِنَ الطَّيِّبَاتِ لَعَلَّكُمْ تَشْكُرُونَ}
INSERT INTO [b9617] VALUES (1153,'<span data-type="title" id=toc-1139>٢٧ </span>- {لا تَخُونُوا اللَّهَ وَالرَّسُولَ وَتَخُونُوا أَمَانَاتِكُمْ وَأَنْتُمْ تَعْلَمُونَ}
INSERT INTO [b9617] VALUES (1154,'<span data-type="title" id=toc-1140>٢٨ </span>- {وَاعْلَمُوا أَنَّمَا أَمْوَالُكُمْ وَأَوْلادُكُمْ فِتْنَةٌ وَأَنَّ اللَّهَ عِنْدَهُ أَجْرٌ عَظِيمٌ}
INSERT INTO [b9617] VALUES (1155,'<span data-type="title" id=toc-1141>٢٩ </span>- {يَا أَيُّهَا الَّذِينَ آمَنُوا إِنْ تَتَّقُوا اللَّهَ يَجْعَلْ لَكُمْ فُرْقَانًا وَيُكَفِّرْ عَنْكُمْ سَيِّئَاتِكُمْ وَيَغْفِرْ لَكُمْ وَاللَّهُ ذُو الْفَضْلِ الْعَظِيمِ}
INSERT INTO [b9617] VALUES (1156,'<span data-type="title" id=toc-1142>٣٠ </span>- {وَإِذْ يَمْكُرُ بِكَ الَّذِينَ كَفَرُوا لِيُثْبِتُوكَ أَوْ يَقْتُلُوكَ أَوْ يُخْرِجُوكَ وَيَمْكُرُونَ وَيَمْكُرُ اللَّهُ وَاللَّهُ خَيْرُ الْمَاكِرِينَ}
INSERT INTO [b9617] VALUES (1157,'<span data-type="title" id=toc-1143>٣١ </span>- {وَإِذَا تُتْلَى عَلَيْهِمْ آيَاتُنَا قَالُوا قَدْ سَمِعْنَا لَوْ نَشَاءُ لَقُلْنَا مِثْلَ هَذَا إِنْ هَذَا إِلا أَسَاطِيرُ الأَوَّلِينَ}
INSERT INTO [b9617] VALUES (1158,'<span data-type="title" id=toc-1144>٣٢ </span>- {وَإِذْ قَالُوا اللَّهُمَّ إِنْ كَانَ هَذَا هُوَ الْحَقَّ مِنْ عِنْدِكَ فَأَمْطِرْ عَلَيْنَا حِجَارَةً}
INSERT INTO [b9617] VALUES (1159,'<span data-type="title" id=toc-1145>٣٣ </span>- {وَمَا كَانَ اللَّهُ لِيُعَذِّبَهُمْ وَأَنْتَ فِيهِمْ وَمَا كَانَ اللَّهُ مُعَذِّبَهُمْ وَهُمْ يَسْتَغْفِرُونَ}
INSERT INTO [b9617] VALUES (1160,'<span data-type="title" id=toc-1146>٣٤ </span>- {وَمَا لَهُمْ أَلا يُعَذِّبَهُمُ اللَّهُ وَهُمْ يَصُدُّونَ عَنِ الْمَسْجِدِ الْحَرَامِ وَمَا كَانُوا أَوْلِيَاءَهُ إِنْ أَوْلِيَاؤُهُ إِلا الْمُتَّقُونَ وَلَكِنَّ أَكْثَرَهُمْ لا يَعْلَمُونَ}
INSERT INTO [b9617] VALUES (1161,'<span data-type="title" id=toc-1147>٣٥ </span>- {وَمَا كَانَ صَلاتُهُمْ عِنْدَ الْبَيْتِ إِلا مُكَاءً وَتَصْدِيَةً فَذُوقُوا الْعَذَابَ بِمَا كُنْتُمْ تَكْفُرُونَ}
INSERT INTO [b9617] VALUES (1162,'<span data-type="title" id=toc-1148>٣٦ </span>- {إِنَّ الَّذِينَ كَفَرُوا يُنْفِقُونَ أَمْوَالَهُمْ لِيَصُدُّوا عَنْ سَبِيلِ اللَّهِ فَسَيُنْفِقُونَهَا ثُمَّ تَكُونُ عَلَيْهِمْ حَسْرَةً ثُمَّ يُغْلَبُونَ وَالَّذِينَ كَفَرُوا إِلَى جَهَنَّمَ يُحْشَرُونَ}
INSERT INTO [b9617] VALUES (1163,'<span data-type="title" id=toc-1149>٣٧ </span>- {لِيَمِيزَ اللَّهُ الْخَبِيثَ مِنَ الطَّيِّبِ وَيَجْعَلَ الْخَبِيثَ بَعْضَهُ عَلَى بَعْضٍ فَيَرْكُمَهُ جَمِيعًا فَيَجْعَلَهُ فِي جَهَنَّمَ}
INSERT INTO [b9617] VALUES (1164,'<span data-type="title" id=toc-1150>٣٨ </span>- {قُلْ لِلَّذِينَ كَفَرُوا إِنْ يَنْتَهُوا يُغْفَرْ لَهُمْ مَا قَدْ سَلَفَ}
INSERT INTO [b9617] VALUES (1165,'<span data-type="title" id=toc-1151>٣٩ </span>- {وَقَاتِلُوهُمْ حَتَّى لا تَكُونَ فِتْنَةٌ وَيَكُونَ الدِّينُ كُلُّهُ لِلَّهِ فَإِنِ انْتَهَوْا فَإِنَّ اللَّهَ بِمَا يَعْمَلُونَ بَصِيرٌ}
INSERT INTO [b9617] VALUES (1166,'<span data-type="title" id=toc-1152>٤٠ </span>- {وَإِنْ تَوَلَّوْا فَاعْلَمُوا أَنَّ اللَّهَ مَوْلاكُمْ نِعْمَ الْمَوْلَى وَنِعْمَ النَّصِيرُ}
INSERT INTO [b9617] VALUES (1167,'<span data-type="title" id=toc-1153>٤١ </span>- {وَاعْلَمُوا أَنَّمَا غَنِمْتُمْ مِنْ شَيْءٍ فَأَنَّ لِلَّهِ خُمُسَهُ وَلِلرَّسُولِ وَلِذِي الْقُرْبَى وَالْيَتَامَى وَالْمَسَاكِينِ وَابْنِ السَّبِيلِ إِنْ كُنْتُمْ آمَنْتُمْ بِاللَّهِ وَمَا أَنْزَلْنَا عَلَى عَبْدِنَا يَوْمَ الْفُرْقَانِ يَوْمَ الْتَقَى الْجَمْعَانِ}
INSERT INTO [b9617] VALUES (1168,'<span data-type="title" id=toc-1154>٤٢ </span>- {إِذْ أَنْتُمْ بِالْعُدْوَةِ الدُّنْيَا وَهُمْ بِالْعُدْوَةِ الْقُصْوَى وَالرَّكْبُ أَسْفَلَ مِنْكُمْ وَلَوْ تَوَاعَدْتُمْ لاخْتَلَفْتُمْ فِي الْمِيعَادِ وَلَكِنْ لِيَقْضِيَ اللَّهُ أَمْرًا كَانَ مَفْعُولا لِيَهْلِكَ مَنْ هَلَكَ عَنْ بَيِّنَةٍ وَيَحْيَا مَنْ حَيَّ عَنْ بَيِّنَةٍ}
INSERT INTO [b9617] VALUES (1169,'<span data-type="title" id=toc-1155>٤٣ </span>- {إِذْ يُرِيكَهُمُ اللَّهُ فِي مَنَامِكَ قَلِيلا وَلَوْ أَرَاكَهُمْ كَثِيرًا لَفَشِلْتُمْ وَلَتَنَازَعْتُمْ فِي الأَمْرِ وَلَكِنَّ اللَّهَ سَلَّمَ}
INSERT INTO [b9617] VALUES (1170,'<span data-type="title" id=toc-1156>٤٤ </span>- {وَإِذْ يُرِيكُمُوهُمْ إِذِ الْتَقَيْتُمْ فِي أَعْيُنِكُمْ قَلِيلا وَيُقَلِّلُكُمْ فِي أَعْيُنِهِمْ لِيَقْضِيَ اللَّهُ أَمْرًا كَانَ مَفْعُولا وَإِلَى اللَّهِ تُرْجَعُ الأُمُورُ} ⦗٣٧٤⦘
INSERT INTO [b9617] VALUES (1171,'<span data-type="title" id=toc-1157>٤٥ </span>- {يَا أَيُّهَا الَّذِينَ آمَنُوا إِذَا لَقِيتُمْ فِئَةً فَاثْبُتُوا وَاذْكُرُوا اللَّهَ كَثِيرًا لَعَلَّكُمْ تُفْلِحُونَ}
INSERT INTO [b9617] VALUES (1172,'<span data-type="title" id=toc-1158>٤٦ </span>- {وَأَطِيعُوا اللَّهَ وَرَسُولَهُ وَلا تَنَازَعُوا فَتَفْشَلُوا وَتَذْهَبَ رِيحُكُمْ}
INSERT INTO [b9617] VALUES (1173,'<span data-type="title" id=toc-1159>٤٧ </span>- {وَلا تَكُونُوا كَالَّذِينَ خَرَجُوا مِنْ دِيَارِهِمْ بَطَرًا وَرِئَاءَ النَّاسِ وَيَصُدُّونَ عَنْ سَبِيلِ اللَّهِ وَاللَّهُ بِمَا يَعْمَلُونَ مُحِيطٌ}
INSERT INTO [b9617] VALUES (1174,'<span data-type="title" id=toc-1160>٤٨ </span>- {وَإِذْ زَيَّنَ لَهُمُ الشَّيْطَانُ أَعْمَالَهُمْ وَقَالَ لا غَالِبَ لَكُمُ الْيَوْمَ مِنَ النَّاسِ وَإِنِّي جَارٌ لَكُمْ فَلَمَّا تَرَاءَتِ الْفِئَتَانِ نَكَصَ عَلَى عَقِبَيْهِ وَقَالَ إِنِّي بَرِيءٌ مِنْكُمْ إِنِّي أَرَى مَا لا تَرَوْنَ}
INSERT INTO [b9617] VALUES (1175,'<span data-type="title" id=toc-1161>٤٩ </span>- {إِذْ يَقُولُ الْمُنَافِقُونَ وَالَّذِينَ فِي قُلُوبِهِمْ مَرَضٌ غَرَّ هَؤُلاءِ دِينُهُمْ وَمَنْ يَتَوَكَّلْ عَلَى اللَّهِ فَإِنَّ اللَّهَ عَزِيزٌ حَكِيمٌ}
INSERT INTO [b9617] VALUES (1176,'<span data-type="title" id=toc-1162>٥٠ </span>- {وَلَوْ تَرَى إِذْ يَتَوَفَّى الَّذِينَ كَفَرُوا الْمَلائِكَةُ يَضْرِبُونَ وُجُوهَهُمْ وَأَدْبَارَهُمْ وَذُوقُوا عَذَابَ الْحَرِيقِ}
INSERT INTO [b9617] VALUES (1177,'<span data-type="title" id=toc-1163>٥١ </span>- {ذَلِكَ بِمَا قَدَّمَتْ أَيْدِيكُمْ وَأَنَّ اللَّهَ لَيْسَ بِظَلامٍ لِلْعَبِيدِ}
INSERT INTO [b9617] VALUES (1178,'<span data-type="title" id=toc-1164>٥٢ </span>- {كَدَأْبِ آلِ فِرْعَوْنَ وَالَّذِينَ مِنْ قَبْلِهِمْ كَفَرُوا بِآيَاتِ اللَّهِ}
INSERT INTO [b9617] VALUES (1179,'<span data-type="title" id=toc-1165>٥٣ </span>- {ذَلِكَ بِأَنَّ اللَّهَ لَمْ يَكُ مُغَيِّرًا نِعْمَةً أَنْعَمَهَا عَلَى قَوْمٍ حَتَّى يُغَيِّرُوا مَا بِأَنْفُسِهِمْ وَأَنَّ اللَّهَ سَمِيعٌ عَلِيمٌ}
INSERT INTO [b9617] VALUES (1180,'<span data-type="title" id=toc-1166>٥٤ </span>- {كَدَأْبِ آلِ فِرْعَوْنَ وَالَّذِينَ مِنْ قَبْلِهِمْ كَذَّبُوا بِآيَاتِ رَبِّهِمْ فَأَهْلَكْنَاهُمْ بِذُنُوبِهِمْ وَأَغْرَقْنَا آلَ فِرْعَوْنَ وَكُلٌّ كَانُوا ظَالِمِينَ} ⦗٣٧٧⦘
INSERT INTO [b9617] VALUES (1181,'<span data-type="title" id=toc-1167>٥٥ </span>- {إِنَّ شَرَّ الدَّوَابِّ عِنْدَ اللَّهِ الَّذِينَ كَفَرُوا فَهُمْ لا يُؤْمِنُونَ}
INSERT INTO [b9617] VALUES (1182,'<span data-type="title" id=toc-1168>٥٦ </span>- {الَّذِينَ عَاهَدْتَ مِنْهُمْ ثُمَّ يَنْقُضُونَ عَهْدَهُمْ فِي كُلِّ مَرَّةٍ وَهُمْ لا يَتَّقُونَ}
INSERT INTO [b9617] VALUES (1183,'<span data-type="title" id=toc-1169>٥٧ </span>- {فَإِمَّا تَثْقَفَنَّهُمْ فِي الْحَرْبِ فَشَرِّدْ بِهِمْ مَنْ خَلْفَهُمْ لَعَلَّهُمْ يَذَّكَّرُونَ}
INSERT INTO [b9617] VALUES (1184,'<span data-type="title" id=toc-1170>٥٨ </span>- {وَإِمَّا تَخَافَنَّ مِنْ قَوْمٍ خِيَانَةً فَانْبِذْ إِلَيْهِمْ عَلَى سَوَاءٍ إِنَّ اللَّهَ لا يُحِبُّ الْخَائِنِينَ}
INSERT INTO [b9617] VALUES (1185,'<span data-type="title" id=toc-1171>٥٩ </span>- {وَلا يَحْسَبَنَّ الَّذِينَ كَفَرُوا سَبَقُوا إِنَّهُمْ لا يُعْجِزُونَ}
INSERT INTO [b9617] VALUES (1186,'<span data-type="title" id=toc-1172>٦٠ </span>- {وَأَعِدُّوا لَهُمْ مَا اسْتَطَعْتُمْ مِنْ قُوَّةٍ وَمِنْ رِبَاطِ الْخَيْلِ تُرْهِبُونَ بِهِ عَدُوَّ اللَّهِ وَعَدُوَّكُمْ وَآخَرِينَ مِنْ دُونِهِمْ لا تَعْلَمُونَهُمُ اللَّهُ يَعْلَمُهُمْ وَمَا تُنْفِقُوا مِنْ شَيْءٍ فِي سَبِيلِ اللَّهِ يُوَفَّ إِلَيْكُمْ وَأَنْتُمْ لا تُظْلَمُونَ}
INSERT INTO [b9617] VALUES (1187,'<span data-type="title" id=toc-1173>٦١ </span>- {إِنَّهُ هُوَ السَّمِيعُ الْعَلِيمُ}
INSERT INTO [b9617] VALUES (1188,'<span data-type="title" id=toc-1174>٦٢ </span>- {وَإِنْ يُرِيدُوا أَنْ يَخْدَعُوكَ فَإِنَّ حَسْبَكَ اللَّهُ هُوَ الَّذِي أَيَّدَكَ بِنَصْرِهِ}
INSERT INTO [b9617] VALUES (1189,'<span data-type="title" id=toc-1175>٦٣ </span>- {وَأَلَّفَ بَيْنَ قُلُوبِهِمْ لَوْ أَنْفَقْتَ مَا فِي الأَرْضِ جَمِيعًا مَا أَلَّفْتَ بَيْنَ قُلُوبِهِمْ وَلَكِنَّ اللَّهَ أَلَّفَ بَيْنَهُمْ إِنَّهُ عَزِيزٌ حَكِيمٌ}
INSERT INTO [b9617] VALUES (1190,'<span data-type="title" id=toc-1176>٦٤ </span>- {يَا أَيُّهَا النَّبِيُّ حَسْبُكَ اللَّهُ وَمَنِ اتَّبَعَكَ مِنَ الْمُؤْمِنِينَ}
INSERT INTO [b9617] VALUES (1191,'<span data-type="title" id=toc-1177>٦٥ </span>- {يَا أَيُّهَا النَّبِيُّ حَرِّضِ الْمُؤْمِنِينَ عَلَى الْقِتَالِ إِنْ يَكُنْ مِنْكُمْ عِشْرُونَ صَابِرُونَ يَغْلِبُوا مِائَتَيْنِ وَإِنْ يَكُنْ مِنْكُمْ مِائَةٌ يَغْلِبُوا أَلْفًا مِنَ الَّذِينَ كَفَرُوا بِأَنَّهُمْ قَوْمٌ لا يَفْقَهُونَ}
INSERT INTO [b9617] VALUES (1192,'<span data-type="title" id=toc-1178>٦٦ </span>- {الآنَ خَفَّفَ اللَّهُ عَنْكُمْ وَعَلِمَ أَنَّ فِيكُمْ ضَعْفًا فَإِنْ يَكُنْ مِنْكُمْ مِائَةٌ صَابِرَةٌ ⦗٣٨٠⦘ يَغْلِبُوا مِائَتَيْنِ وَإِنْ يَكُنْ مِنْكُمْ أَلْفٌ يَغْلِبُوا أَلْفَيْنِ بِإِذْنِ اللَّهِ}
INSERT INTO [b9617] VALUES (1193,'<span data-type="title" id=toc-1179>٦٧ </span>- {مَا كَانَ لِنَبِيٍّ أَنْ يَكُونَ لَهُ أَسْرَى حَتَّى يُثْخِنَ فِي الأَرْضِ تُرِيدُونَ عَرَضَ الدُّنْيَا}
INSERT INTO [b9617] VALUES (1194,'<span data-type="title" id=toc-1180>٦٨ </span>- {لَوْلا كِتَابٌ مِنَ اللَّهِ سَبَقَ لَمَسَّكُمْ فِيمَا أَخَذْتُمْ عَذَابٌ عَظِيمٌ}
INSERT INTO [b9617] VALUES (1195,'<span data-type="title" id=toc-1181>٦٩ </span>- {فَكُلُوا مِمَّا غَنِمْتُمْ حَلالا طَيِّبًا}
INSERT INTO [b9617] VALUES (1196,'<span data-type="title" id=toc-1182>٧٠ </span>- {يَا أَيُّهَا النَّبِيُّ قُلْ لِمَنْ فِي أَيْدِيكُمْ مِنَ الأَسْرَى إِنْ يَعْلَمِ اللَّهُ فِي قُلُوبِكُمْ خَيْرًا يُؤْتِكُمْ ⦗٣٨١⦘ خَيْرًا مِمَّا أُخِذَ مِنْكُمْ}
INSERT INTO [b9617] VALUES (1197,'<span data-type="title" id=toc-1183>٧١ </span>- {وَإِنْ يُرِيدُوا خِيَانَتَكَ فَقَدْ خَانُوا اللَّهَ مِنْ قَبْلُ}
INSERT INTO [b9617] VALUES (1198,'<span data-type="title" id=toc-1184>٧٢ </span>- {إِنَّ الَّذِينَ آمَنُوا وَهَاجَرُوا وَجَاهَدُوا بِأَمْوَالِهِمْ وَأَنْفُسِهِمْ فِي سَبِيلِ اللَّهِ وَالَّذِينَ آوَوْا وَنَصَرُوا أُولَئِكَ بَعْضُهُمْ أَوْلِيَاءُ بَعْضٍ وَالَّذِينَ آمَنُوا وَلَمْ يُهَاجِرُوا مَا لَكُمْ مِنْ وَلايَتِهِمْ مِنْ شَيْءٍ حَتَّى يُهَاجِرُوا وَإِنِ اسْتَنْصَرُوكُمْ فِي الدِّينِ فَعَلَيْكُمُ النَّصْرُ إِلا عَلَى قَوْمٍ بَيْنَكُمْ وَبَيْنَهُمْ مِيثَاقٌ}
INSERT INTO [b9617] VALUES (1199,'<span data-type="title" id=toc-1185>٧٣ </span>- {وَالَّذِينَ كَفَرُوا بَعْضُهُمْ أَوْلِيَاءُ بَعْضٍ إِلا تَفْعَلُوهُ تَكُنْ فِتْنَةٌ فِي الأَرْضِ} ⦗٣٨٢⦘
INSERT INTO [b9617] VALUES (1200,'<span data-type="title" id=toc-1186>٧٤ </span>- {وَالَّذِينَ آمَنُوا وَهَاجَرُوا وَجَاهَدُوا فِي سَبِيلِ اللَّهِ وَالَّذِينَ آوَوْا وَنَصَرُوا أُولَئِكَ هُمُ الْمُؤْمِنُونَ حَقًّا لَهُمْ مَغْفِرَةٌ وَرِزْقٌ كَرِيمٌ}
INSERT INTO [b9617] VALUES (1201,'<span data-type="title" id=toc-1187>٧٥ </span>- {وَالَّذِينَ آمَنُوا مِنْ بَعْدُ وَهَاجَرُوا وَجَاهَدُوا مَعَكُمْ فَأُولَئِكَ مِنْكُمْ وَأُولُو الأَرْحَامِ بَعْضُهُمْ أَوْلَى بِبَعْضٍ فِي كِتَابِ اللَّهِ}
INSERT INTO [b9617] VALUES (1202,'<span data-type="title" id=toc-1188>سورة التوبة</span>','2','383',NULL,NULL,'0');
INSERT INTO [b9617] VALUES (1203,'<span data-type="title" id=toc-1189>١ </span>- {بَرَاءَةٌ مِنَ اللَّهِ وَرَسُولِهِ إِلَى الَّذِينَ عَاهَدْتُمْ مِنَ الْمُشْرِكِينَ}
INSERT INTO [b9617] VALUES (1204,'<span data-type="title" id=toc-1190>٢ </span>- {فَسِيحُوا فِي الأَرْضِ أَرْبَعَةَ أَشْهُرٍ وَاعْلَمُوا أَنَّكُمْ غَيْرُ مُعْجِزِي اللَّهِ وَأَنَّ اللَّهَ مُخْزِي الْكَافِرِينَ}
INSERT INTO [b9617] VALUES (1205,'<span data-type="title" id=toc-1191>٣ </span>- {وَأَذَانٌ مِنَ اللَّهِ وَرَسُولِهِ إِلَى النَّاسِ يَوْمَ الْحَجِّ الأَكْبَرِ أَنَّ اللَّهَ بَرِيءٌ مِنَ الْمُشْرِكِينَ وَرَسُولُهُ فَإِنْ تُبْتُمْ فَهُوَ خَيْرٌ لَكُمْ وَإِنْ تَوَلَّيْتُمْ فَاعْلَمُوا أَنَّكُمْ غَيْرُ مُعْجِزِي اللَّهِ وَبَشِّرِ الَّذِينَ كَفَرُوا بِعَذَابٍ أَلِيمٍ}
INSERT INTO [b9617] VALUES (1206,'<span data-type="title" id=toc-1192>٤ </span>- {إِلا الَّذِينَ عَاهَدْتُمْ مِنَ الْمُشْرِكِينَ ثُمَّ لَمْ يَنْقُصُوكُمْ شَيْئًا وَلَمْ يُظَاهِرُوا عَلَيْكُمْ أَحَدًا فَأَتِمُّوا إِلَيْهِمْ عَهْدَهُمْ إِلَى مُدَّتِهِمْ}
INSERT INTO [b9617] VALUES (1207,'<span data-type="title" id=toc-1193>٥ </span>- {فَإِذَا انْسَلَخَ الأَشْهُرُ الْحُرُمُ فَاقْتُلُوا الْمُشْرِكِينَ حَيْثُ وَجَدْتُمُوهُمْ وَخُذُوهُمْ وَاحْصُرُوهُمْ وَاقْعُدُوا لَهُمْ كُلَّ مَرْصَدٍ فَإِنْ تَابُوا وَأَقَامُوا الصَّلاةَ وَآتَوُا الزَّكَاةَ فَخَلُّوا سَبِيلَهُمْ}
INSERT INTO [b9617] VALUES (1208,'<span data-type="title" id=toc-1194>٦ </span>- {وَإِنْ أَحَدٌ مِنَ الْمُشْرِكِينَ اسْتَجَارَكَ فَأَجِرْهُ حَتَّى يَسْمَعَ كَلامَ اللَّهِ ثُمَّ أَبْلِغْهُ مَأْمَنَهُ ذَلِكَ بِأَنَّهُمْ قَوْمٌ لا يَعْلَمُونَ} ⦗٣٨٥⦘
INSERT INTO [b9617] VALUES (1209,'<span data-type="title" id=toc-1195>٧ </span>- {كَيْفَ يَكُونُ لِلْمُشْرِكِينَ عَهْدٌ عِنْدَ اللَّهِ وَعِنْدَ رَسُولِهِ إِلا الَّذِينَ عَاهَدْتُمْ عِنْدَ الْمَسْجِدِ الْحَرَامِ فَمَا اسْتَقَامُوا لَكُمْ فَاسْتَقِيمُوا لَهُمْ}
INSERT INTO [b9617] VALUES (1210,'<span data-type="title" id=toc-1196>٨ </span>- {كَيْفَ وَإِنْ يَظْهَرُوا عَلَيْكُمْ لا يَرْقُبُوا فِيكُمْ إِلا وَلا ذِمَّةً يُرْضُونَكُمْ بِأَفْوَاهِهِمْ وَتَأْبَى قُلُوبُهُمْ وَأَكْثَرُهُمْ فَاسِقُونَ}
INSERT INTO [b9617] VALUES (1211,'<span data-type="title" id=toc-1197>٩ </span>- {إِنَّهُمْ سَاءَ مَا كَانُوا يَعْمَلُونَ}
INSERT INTO [b9617] VALUES (1212,'<span data-type="title" id=toc-1198>١٠ </span>- {لا يَرْقُبُونَ فِي مُؤْمِنٍ إِلا وَلا ذِمَّةً وَأُولَئِكَ هُمُ الْمُعْتَدُونَ}
INSERT INTO [b9617] VALUES (1213,'<span data-type="title" id=toc-1199>١١ </span>- {فَإِنْ تَابُوا وَأَقَامُوا الصَّلاةَ وَآتَوُا الزَّكَاةَ فَإِخْوَانُكُمْ فِي الدِّينِ وَنُفَصِّلُ الآيَاتِ لِقَوْمٍ يَعْلَمُونَ}
INSERT INTO [b9617] VALUES (1214,'<span data-type="title" id=toc-1200>١٢ </span>- {وَإِنْ نَكَثُوا أَيْمَانَهُمْ مِنْ بَعْدِ عَهْدِهِمْ وَطَعَنُوا فِي دِينِكُمْ فَقَاتِلُوا أَئِمَّةَ الْكُفْرِ إِنَّهُمْ لا أَيْمَانَ لَهُمْ لَعَلَّهُمْ يَنْتَهُونَ}
INSERT INTO [b9617] VALUES (1215,'<span data-type="title" id=toc-1201>١٣ </span>- {أَلا تُقَاتِلُونَ قَوْمًا نَكَثُوا أَيْمَانَهُمْ وَهَمُّوا بِإِخْرَاجِ الرَّسُولِ وَهُمْ بَدَءُوكُمْ أَوَّلَ مَرَّةٍ أَتَخْشَوْنَهُمْ فَاللَّهُ أَحَقُّ أَنْ تَخْشَوْهُ إِنْ كُنْتُمْ مُؤْمِنِينَ}
INSERT INTO [b9617] VALUES (1216,'<span data-type="title" id=toc-1202>١٤ </span>- {قَاتِلُوهُمْ يُعَذِّبْهُمُ اللَّهُ بِأَيْدِيكُمْ}
INSERT INTO [b9617] VALUES (1217,'<span data-type="title" id=toc-1203>١٦ </span>- {أَمْ حَسِبْتُمْ أَنْ تُتْرَكُوا وَلَمَّا يَعْلَمِ اللَّهُ الَّذِينَ جَاهَدُوا مِنْكُمْ وَلَمْ يَتَّخِذُوا مِنْ دُونِ اللَّهِ وَلا رَسُولِهِ وَلا الْمُؤْمِنِينَ وَلِيجَةً وَاللَّهُ خَبِيرٌ بِمَا تَعْمَلُونَ}
INSERT INTO [b9617] VALUES (1218,'<span data-type="title" id=toc-1204>١٧ </span>- {مَا كَانَ لِلْمُشْرِكِينَ أَنْ يَعْمُرُوا مَسَاجِدَ اللَّهِ شَاهِدِينَ عَلَى أَنْفُسِهِمْ بِالْكُفْرِ أُولَئِكَ حَبِطَتْ أَعْمَالُهُمْ وَفِي النَّارِ هُمْ خَالِدُونَ} ⦗٣٨٨⦘
INSERT INTO [b9617] VALUES (1219,'<span data-type="title" id=toc-1205>١٨ </span>- {إِنَّمَا يَعْمُرُ مَسَاجِدَ اللَّهِ مَنْ آمَنَ بِاللَّهِ وَالْيَوْمِ الآخِرِ وَأَقَامَ الصَّلاةَ وَآتَى الزَّكَاةَ وَلَمْ يَخْشَ إِلا اللَّهَ فَعَسَى أُولَئِكَ أَنْ يَكُونُوا مِنَ الْمُهْتَدِينَ}
INSERT INTO [b9617] VALUES (1220,'<span data-type="title" id=toc-1206>١٩ </span>- {أَجَعَلْتُمْ سِقَايَةَ الْحَاجِّ وَعِمَارَةَ الْمَسْجِدِ الْحَرَامِ كَمَنْ آمَنَ بِاللَّهِ وَالْيَوْمِ الآخِرِ وَجَاهَدَ فِي سَبِيلِ اللَّهِ لا يَسْتَوُونَ عِنْدَ اللَّهِ}
INSERT INTO [b9617] VALUES (1221,'<span data-type="title" id=toc-1207>٢٠ </span>- {الَّذِينَ آمَنُوا وَهَاجَرُوا وَجَاهَدُوا فِي سَبِيلِ اللَّهِ بِأَمْوَالِهِمْ وَأَنْفُسِهِمْ أَعْظَمُ دَرَجَةً عِنْدَ اللَّهِ وَأُولَئِكَ هُمُ الْفَائِزُونَ}
INSERT INTO [b9617] VALUES (1222,'<span data-type="title" id=toc-1208>٢١ </span>- {يُبَشِّرُهُمْ رَبُّهُمْ بِرَحْمَةٍ مِنْهُ وَرِضْوَانٍ وَجَنَّاتٍ لَهُمْ فِيهَا نَعِيمٌ مُقِيمٌ}
INSERT INTO [b9617] VALUES (1223,'<span data-type="title" id=toc-1209>٢٢ </span>- {خَالِدِينَ فِيهَا أَبَدًا إِنَّ اللَّهَ عِنْدَهُ أَجْرٌ عَظِيمٌ}
INSERT INTO [b9617] VALUES (1224,'<span data-type="title" id=toc-1210>٢٣ </span>- {لا تَتَّخِذُوا آبَاءَكُمْ وَإِخْوَانَكُمْ أَوْلِيَاءَ إِنِ اسْتَحَبُّوا الْكُفْرَ عَلَى الإِيمَانِ وَمَنْ يَتَوَلَّهُمْ مِنْكُمْ فَأُولَئِكَ هُمُ الظَّالِمُونَ}
INSERT INTO [b9617] VALUES (1225,'<span data-type="title" id=toc-1211>٢٤ </span>- {قُلْ إِنْ كَانَ آبَاؤُكُمْ وَأَبْنَاؤُكُمْ وَإِخْوَانُكُمْ وَأَزْوَاجُكُمْ وَعَشِيرَتُكُمْ وَأَمْوَالٌ اقْتَرَفْتُمُوهَا وَتِجَارَةٌ تَخْشَوْنَ كَسَادَهَا وَمَسَاكِنُ تَرْضَوْنَهَا أَحَبَّ إِلَيْكُمْ مِنَ اللَّهِ وَرَسُولِهِ وَجِهَادٍ فِي سَبِيلِهِ فَتَرَبَّصُوا}
INSERT INTO [b9617] VALUES (1226,'<span data-type="title" id=toc-1212>٢٥ </span>- {لَقَدْ نَصَرَكُمُ اللَّهُ فِي مَوَاطِنَ كَثِيرَةٍ وَيَوْمَ حُنَيْنٍ إِذْ أَعْجَبَتْكُمْ كَثْرَتُكُمْ ⦗٣٩٠⦘ فَلَمْ تُغْنِ عَنْكُمْ شَيْئًا وَضَاقَتْ عَلَيْكُمُ الأَرْضُ بِمَا رَحُبَتْ ثُمَّ وَلَّيْتُمْ مُدْبِرِينَ}
INSERT INTO [b9617] VALUES (1227,'<span data-type="title" id=toc-1213>٢٦ </span>- {ثُمَّ أَنْزَلَ اللَّهُ سَكِينَتَهُ عَلَى رَسُولِهِ وَعَلَى الْمُؤْمِنِينَ وَأَنْزَلَ جُنُودًا لَمْ تَرَوْهَا وَعَذَّبَ الَّذِينَ كَفَرُوا وَذَلِكَ جَزَاءُ الْكَافِرِينَ}
INSERT INTO [b9617] VALUES (1228,'<span data-type="title" id=toc-1214>٢٧ </span>- {وَاللَّهُ غَفُورٌ رَحِيمٌ}
INSERT INTO [b9617] VALUES (1229,'<span data-type="title" id=toc-1215>٢٨ </span>- {يَا أَيُّهَا الَّذِينَ آمَنُوا إِنَّمَا الْمُشْرِكُونَ نَجَسٌ فَلا يَقْرَبُوا الْمَسْجِدَ الْحَرَامَ بَعْدَ عَامِهِمْ هَذَا وَإِنْ خِفْتُمْ عَيْلَةً فَسَوْفَ يُغْنِيكُمُ اللَّهُ مِنْ فَضْلِهِ إِنْ شَاءَ}
INSERT INTO [b9617] VALUES (1230,'<span data-type="title" id=toc-1216>٢٩ </span>- {وَلا يَدِينُونَ دِينَ الْحَقِّ مِنَ الَّذِينَ أُوتُوا الْكِتَابَ حَتَّى يُعْطُوا الْجِزْيَةَ عَنْ يَدٍ وَهُمْ صَاغِرُونَ}
INSERT INTO [b9617] VALUES (1231,'<span data-type="title" id=toc-1217>٣٠ </span>- {وَقَالَتِ الْيَهُودُ عُزَيْرٌ ابْنُ اللَّهِ وَقَالَتِ النَّصَارَى الْمَسِيحُ ابْنُ اللَّهِ ذَلِكَ قَوْلُهُمْ بِأَفْوَاهِهِمْ يُضَاهِئُونَ قَوْلَ الَّذِينَ كَفَرُوا مِنْ قَبْلُ قَاتَلَهُمُ اللَّهُ أَنَّى يُؤْفَكُونَ}
INSERT INTO [b9617] VALUES (1232,'<span data-type="title" id=toc-1218>٣١ </span>- {اتَّخَذُوا أَحْبَارَهُمْ وَرُهْبَانَهُمْ أَرْبَابًا مِنْ دُونِ اللَّهِ وَالْمَسِيحَ ابْنَ مَرْيَمَ وَمَا أُمِرُوا إِلا لِيَعْبُدُوا إِلَهًا وَاحِدًا لا إِلَهَ إِلا هُوَ سُبْحَانَهُ عَمَّا يُشْرِكُونَ}
INSERT INTO [b9617] VALUES (1233,'<span data-type="title" id=toc-1219>٣٢ </span>- {يُرِيدُونَ أَنْ يُطْفِئُوا نُورَ اللَّهِ بِأَفْوَاهِهِمْ وَيَأْبَى اللَّهُ إِلا أَنْ يُتِمَّ نُورَهُ وَلَوْ كَرِهَ الْكَافِرُونَ}
INSERT INTO [b9617] VALUES (1234,'<span data-type="title" id=toc-1220>٣٤ </span>- {وَالَّذِينَ يَكْنِزُونَ الذَّهَبَ وَالْفِضَّةَ وَلا يُنْفِقُونَهَا فِي سَبِيلِ اللَّهِ فَبَشِّرْهُمْ بِعَذَابٍ أَلِيمٍ}
INSERT INTO [b9617] VALUES (1235,'<span data-type="title" id=toc-1221>٣٥ </span>- {يَوْمَ يُحْمَى عَلَيْهَا فِي نَارِ جَهَنَّمَ فَتُكْوَى بِهَا جِبَاهُهُمْ وَجُنُوبُهُمْ وَظُهُورُهُمْ هَذَا مَا كَنَزْتُمْ لأَنْفُسِكُمْ فَذُوقُوا مَا كُنْتُمْ تَكْنِزُونَ} ⦗٣٩٣⦘
INSERT INTO [b9617] VALUES (1236,'<span data-type="title" id=toc-1222>٣٦ </span>- {إِنَّ عِدَّةَ الشُّهُورِ عِنْدَ اللَّهِ اثْنَا عَشَرَ شَهْرًا فِي كِتَابِ اللَّهِ يَوْمَ خَلَقَ السَّمَاوَاتِ وَالأَرْضَ مِنْهَا أَرْبَعَةٌ حُرُمٌ ذَلِكَ الدِّينُ الْقَيِّمُ فَلا تَظْلِمُوا فِيهِنَّ أَنْفُسَكُمْ وَقَاتِلُوا الْمُشْرِكِينَ كَافَّةً كَمَا يُقَاتِلُونَكُمْ كَافَّةً وَاعْلَمُوا أَنَّ اللَّهَ مَعَ الْمُتَّقِينَ}
INSERT INTO [b9617] VALUES (1237,'<span data-type="title" id=toc-1223>٣٧ </span>- {إِنَّمَا النَّسِيءُ زِيَادَةٌ فِي الْكُفْرِ يُضَلُّ بِهِ الَّذِينَ كَفَرُوا يُحِلُّونَهُ عَامًا وَيُحَرِّمُونَهُ عَامًا لِيُوَاطِئُوا عِدَّةَ مَا حَرَّمَ اللَّهُ فَيُحِلُّوا مَا حَرَّمَ اللَّهُ زُيِّنَ لَهُمْ سُوءُ أَعْمَالِهِمْ} ⦗٣٩٤⦘
INSERT INTO [b9617] VALUES (1238,'<span data-type="title" id=toc-1224>٣٨ </span>- {مَا لَكُمْ إِذَا قِيلَ لَكُمُ انْفِرُوا فِي سَبِيلِ اللَّهِ اثَّاقَلْتُمْ إِلَى الأَرْضِ أَرَضِيتُمْ بِالْحَيَاةِ الدُّنْيَا مِنَ الآخِرَةِ فَمَا مَتَاعُ الْحَيَاةِ الدُّنْيَا فِي الآخِرَةِ إِلا قَلِيلٌ}
INSERT INTO [b9617] VALUES (1239,'<span data-type="title" id=toc-1225>٣٩ </span>- {إِلا تَنْفِرُوا يُعَذِّبْكُمْ عَذَابًا أَلِيمًا وَيَسْتَبْدِلْ قَوْمًا غَيْرَكُمْ وَلا تَضُرُّوهُ شَيْئًا}
INSERT INTO [b9617] VALUES (1240,'<span data-type="title" id=toc-1226>٤٠ </span>- {إِلا تَنْصُرُوهُ فَقَدْ نَصَرَهُ اللَّهُ إِذْ أَخْرَجَهُ الَّذِينَ كَفَرُوا ثَانِيَ اثْنَيْنِ إِذْ هُمَا فِي الْغَارِ إِذْ يَقُولُ لِصَاحِبِهِ لا تَحْزَنْ إِنَّ اللَّهَ مَعَنَا فَأَنْزَلَ اللَّهُ سَكِينَتَهُ عَلَيْهِ وَأَيَّدَهُ بِجُنُودٍ لَمْ تَرَوْهَا وَجَعَلَ كَلِمَةَ الَّذِينَ كَفَرُوا السُّفْلَى وَكَلِمَةُ اللَّهِ هِيَ الْعُلْيَا}
INSERT INTO [b9617] VALUES (1241,'<span data-type="title" id=toc-1227>٤١ </span>- {انْفِرُوا خِفَافًا وَثِقَالا وَجَاهِدُوا بِأَمْوَالِكُمْ وَأَنْفُسِكُمْ فِي سَبِيلِ اللَّهِ ذَلِكُمْ خَيْرٌ لَكُمْ إِنْ كُنْتُمْ تَعْلَمُونَ}
INSERT INTO [b9617] VALUES (1242,'<span data-type="title" id=toc-1228>٤٢ </span>- {لَوْ كَانَ عَرَضًا قَرِيبًا وَسَفَرًا قَاصِدًا لاتَّبَعُوكَ وَلَكِنْ بَعُدَتْ عَلَيْهِمُ الشُّقَّةُ وَسَيَحْلِفُونَ بِاللَّهِ لَوِ اسْتَطَعْنَا لَخَرَجْنَا مَعَكُمْ يُهْلِكُونَ أَنْفُسَهُمْ وَاللَّهُ يَعْلَمُ إِنَّهُمْ لَكَاذِبُونَ}
INSERT INTO [b9617] VALUES (1243,'<span data-type="title" id=toc-1229>٤٣ </span>- {عَفَا اللَّهُ عَنْكَ لِمَ أَذِنْتَ لَهُمْ حَتَّى يَتَبَيَّنَ لَكَ الَّذِينَ صَدَقُوا وَتَعْلَمَ الْكَاذِبِينَ} ⦗٣٩٦⦘
INSERT INTO [b9617] VALUES (1244,'<span data-type="title" id=toc-1230>٤٤ </span>- {لا يَسْتَأْذِنُكَ الَّذِينَ يُؤْمِنُونَ بِاللَّهِ وَالْيَوْمِ الآخِرِ أَنْ يُجَاهِدُوا بِأَمْوَالِهِمْ وَأَنْفُسِهِمْ وَاللَّهُ عَلِيمٌ بِالْمُتَّقِينَ}
INSERT INTO [b9617] VALUES (1245,'<span data-type="title" id=toc-1231>٤٥ </span>- {وَارْتَابَتْ قُلُوبُهُمْ فَهُمْ فِي رَيْبِهِمْ يَتَرَدَّدُونَ}
INSERT INTO [b9617] VALUES (1246,'<span data-type="title" id=toc-1232>٤٦ </span>- {وَلَوْ أَرَادُوا الْخُرُوجَ لأَعَدُّوا لَهُ عُدَّةً وَلَكِنْ كَرِهَ اللَّهُ انْبِعَاثَهُمْ فَثَبَّطَهُمْ وَقِيلَ اقْعُدُوا مَعَ الْقَاعِدِينَ}
INSERT INTO [b9617] VALUES (1247,'<span data-type="title" id=toc-1233>٤٧ </span>- {لَوْ خَرَجُوا فِيكُمْ مَا زَادُوكُمْ إِلا خَبَالا وَلأَوْضَعُوا خِلالَكُمْ يَبْغُونَكُمُ الْفِتْنَةَ ⦗٣٩٧⦘ وَفِيكُمْ سَمَّاعُونَ لَهُمْ}
INSERT INTO [b9617] VALUES (1248,'<span data-type="title" id=toc-1234>٤٨ </span>- {لَقَدِ ابْتَغَوُا الْفِتْنَةَ مِنْ قَبْلُ وَقَلَّبُوا لَكَ الأُمُورَ حَتَّى جَاءَ الْحَقُّ وَظَهَرَ أَمْرُ اللَّهِ وَهُمْ كَارِهُونَ}
INSERT INTO [b9617] VALUES (1249,'<span data-type="title" id=toc-1235>٤٩ </span>- {وَمِنْهُمْ مَنْ يَقُولُ ائْذَنْ لِي وَلا تَفْتِنِّي أَلا فِي الْفِتْنَةِ سَقَطُوا وَإِنَّ جَهَنَّمَ لَمُحِيطَةٌ بِالْكَافِرِينَ}
INSERT INTO [b9617] VALUES (1250,'<span data-type="title" id=toc-1236>٥٠ </span>- {وَإِنْ تُصِبْكَ مُصِيبَةٌ يَقُولُوا قَدْ أَخَذْنَا أَمْرَنَا مِنْ قَبْلُ وَيَتَوَلَّوْا وَهُمْ فَرِحُونَ} ⦗٣٩٨⦘
INSERT INTO [b9617] VALUES (1251,'<span data-type="title" id=toc-1237>٥١ </span>- {قُلْ لَنْ يُصِيبَنَا إِلا مَا كَتَبَ اللَّهُ لَنَا هُوَ مَوْلانَا وَعَلَى اللَّهِ فَلْيَتَوَكَّلِ الْمُؤْمِنُونَ}
INSERT INTO [b9617] VALUES (1252,'<span data-type="title" id=toc-1238>٥٢ </span>- {قُلْ هَلْ تَرَبَّصُونَ بِنَا إِلا إِحْدَى الْحُسْنَيَيْنِ وَنَحْنُ نَتَرَبَّصُ بِكُمْ أَنْ يُصِيبَكُمُ اللَّهُ بِعَذَابٍ مِنْ عِنْدِهِ أَوْ بِأَيْدِينَا فَتَرَبَّصُوا إِنَّا مَعَكُمْ مُتَرَبِّصُونَ}
INSERT INTO [b9617] VALUES (1253,'<span data-type="title" id=toc-1239>٥٣ </span>- {قُلْ أَنْفِقُوا طَوْعًا أَوْ كَرْهًا لَنْ يُتَقَبَّلَ مِنْكُمْ إِنَّكُمْ كُنْتُمْ قَوْمًا فَاسِقِينَ}
INSERT INTO [b9617] VALUES (1254,'<span data-type="title" id=toc-1240>٥٤ </span>- {وَمَا مَنَعَهُمْ أَنْ تُقْبَلَ مِنْهُمْ نَفَقَاتُهُمْ إِلا أَنَّهُمْ كَفَرُوا بِاللَّهِ وَبِرَسُولِهِ وَلا يَأْتُونَ الصَّلاةَ إِلا وَهُمْ كُسَالَى وَلا يُنْفِقُونَ إِلا وَهُمْ كَارِهُونَ}
INSERT INTO [b9617] VALUES (1255,'<span data-type="title" id=toc-1241>٥٥ </span>- {إِنَّمَا يُرِيدُ اللَّهُ لِيُعَذِّبَهُمْ بِهَا فِي الْحَيَاةِ الدُّنْيَا وَتَزْهَقَ أَنْفُسُهُمْ وَهُمْ كَافِرُونَ}
INSERT INTO [b9617] VALUES (1256,'<span data-type="title" id=toc-1242>٥٦ </span>- {وَيَحْلِفُونَ بِاللَّهِ إِنَّهُمْ لَمِنْكُمْ وَمَا هُمْ مِنْكُمْ وَلَكِنَّهُمْ قَوْمٌ يَفْرَقُونَ}
INSERT INTO [b9617] VALUES (1257,'<span data-type="title" id=toc-1243>٥٧ </span>- {لَوْ يَجِدُونَ مَلْجَأً أَوْ مَغَارَاتٍ أَوْ مُدَّخَلا لَوَلَّوْا إِلَيْهِ وَهُمْ يَجْمَحُونَ}
INSERT INTO [b9617] VALUES (1258,'<span data-type="title" id=toc-1244>٥٨ </span>- {وَمِنْهُمْ مَنْ يَلْمِزُكَ فِي الصَّدَقَاتِ فَإِنْ أُعْطُوا مِنْهَا رَضُوا وَإِنْ لَمْ يُعْطَوْا مِنْهَا إِذَا هُمْ يَسْخَطُونَ}
INSERT INTO [b9617] VALUES (1259,'<span data-type="title" id=toc-1245>٥٩ </span>- {وَلَوْ أَنَّهُمْ رَضُوا مَا آتَاهُمُ اللَّهُ وَرَسُولُهُ وَقَالُوا حَسْبُنَا اللَّهُ سَيُؤْتِينَا اللَّهُ مِنْ فَضْلِهِ وَرَسُولُهُ إِنَّا إِلَى اللَّهِ رَاغِبُونَ}
INSERT INTO [b9617] VALUES (1260,'<span data-type="title" id=toc-1246>٦٠ </span>- {إِنَّمَا الصَّدَقَاتُ لِلْفُقَرَاءِ وَالْمَسَاكِينِ وَالْعَامِلِينَ عَلَيْهَا وَالْمُؤَلَّفَةِ قُلُوبُهُمْ وَفِي الرِّقَابِ وَالْغَارِمِينَ وَفِي سَبِيلِ اللَّهِ وَابْنِ السَّبِيلِ فَرِيضَةً مِنَ اللَّهِ}
INSERT INTO [b9617] VALUES (1261,'<span data-type="title" id=toc-1247>٦١ </span>- {وَمِنْهُمُ الَّذِينَ يُؤْذُونَ النَّبِيَّ وَيَقُولُونَ هُوَ أُذُنٌ قُلْ أُذُنُ خَيْرٍ لَكُمْ يُؤْمِنُ بِاللَّهِ وَيُؤْمِنُ لِلْمُؤْمِنِينَ وَرَحْمَةٌ لِلَّذِينَ آمَنُوا مِنْكُمْ وَالَّذِينَ يُؤْذُونَ رَسُولَ اللَّهِ لَهُمْ عَذَابٌ أَلِيمٌ}
INSERT INTO [b9617] VALUES (1262,'<span data-type="title" id=toc-1248>٦٢ </span>- {يَحْلِفُونَ بِاللَّهِ لَكُمْ لِيُرْضُوكُمْ وَاللَّهُ وَرَسُولُهُ أَحَقُّ أَنْ يُرْضُوهُ إِنْ كَانُوا مُؤْمِنِينَ}
INSERT INTO [b9617] VALUES (1263,'<span data-type="title" id=toc-1249>٦٣ </span>- {أَلَمْ يَعْلَمُوا أَنَّهُ مَنْ يُحَادِدِ اللَّهَ وَرَسُولَهُ فَأَنَّ لَهُ نَارَ جَهَنَّمَ خَالِدًا فِيهَا ذَلِكَ الْخِزْيُ الْعَظِيمُ}
INSERT INTO [b9617] VALUES (1264,'<span data-type="title" id=toc-1250>٦٤ </span>- {يَحْذَرُ الْمُنَافِقُونَ أَنْ تُنَزَّلَ عَلَيْهِمْ سُورَةٌ تُنَبِّئُهُمْ بِمَا فِي قُلُوبِهِمْ قُلِ اسْتَهْزِئُوا إِنَّ اللَّهَ مُخْرِجٌ مَا تَحْذَرُونَ}
INSERT INTO [b9617] VALUES (1265,'<span data-type="title" id=toc-1251>٦٥ </span>- {وَلَئِنْ سَأَلْتَهُمْ لَيَقُولُنَّ إِنَّمَا كُنَّا نَخُوضُ وَنَلْعَبُ قُلْ أَبِاللَّهِ وَآيَاتِهِ وَرَسُولِهِ كُنْتُمْ تَسْتَهْزِئُونَ}
INSERT INTO [b9617] VALUES (1266,'<span data-type="title" id=toc-1252>٦٦ </span>- {لا تَعْتَذِرُوا قَدْ كَفَرْتُمْ بَعْدَ إِيمَانِكُمْ إِنْ نَعْفُ عَنْ طَائِفَةٍ مِنْكُمْ نُعَذِّبْ طَائِفَةً بِأَنَّهُمْ كَانُوا مُجْرِمِينَ}
INSERT INTO [b9617] VALUES (1267,'<span data-type="title" id=toc-1253>٦٧ </span>- {الْمُنَافِقُونَ وَالْمُنَافِقَاتُ بَعْضُهُمْ مِنْ بَعْضٍ يَأْمُرُونَ بِالْمُنْكَرِ وَيَنْهَوْنَ عَنِ الْمَعْرُوفِ وَيَقْبِضُونَ أَيْدِيَهُمْ نَسُوا اللَّهَ فَنَسِيَهُمْ}
INSERT INTO [b9617] VALUES (1268,'<span data-type="title" id=toc-1254>٦٨ </span>- {وَعَدَ اللَّهُ الْمُنَافِقِينَ وَالْمُنَافِقَاتِ وَالْكُفَّارَ نَارَ جَهَنَّمَ خَالِدِينَ فِيهَا هِيَ حَسْبُهُمْ وَلَعَنَهُمُ اللَّهُ وَلَهُمْ عَذَابٌ مُقِيمٌ}
INSERT INTO [b9617] VALUES (1269,'<span data-type="title" id=toc-1255>٦٩ </span>- {كَالَّذِينَ مِنْ قَبْلِكُمْ كَانُوا أَشَدَّ مِنْكُمْ قُوَّةً وَأَكْثَرَ أَمْوَالا وَأَوْلادًا فَاسْتَمْتَعُوا بِخَلاقِهِمْ فَاسْتَمْتَعْتُمْ بِخَلاقِكُمْ كَمَا اسْتَمْتَعَ الَّذِينَ مِنْ قَبْلِكُمْ بِخَلاقِهِمْ وَخُضْتُمْ كَالَّذِي خَاضُوا أُولَئِكَ حَبِطَتْ أَعْمَالُهُمْ فِي الدُّنْيَا وَالآخِرَةِ وَأُولَئِكَ هُمُ الْخَاسِرُونَ}
INSERT INTO [b9617] VALUES (1270,'<span data-type="title" id=toc-1256>٧٠ </span>- {أَلَمْ يَأْتِهِمْ نَبَأُ الَّذِينَ مِنْ قَبْلِهِمْ قَوْمِ نُوحٍ وَعَادٍ وَثَمُودَ وَقَوْمِ إِبْرَاهِيمَ وَأَصْحَابِ مَدْيَنَ وَالْمُؤْتَفِكَاتِ أَتَتْهُمْ رُسُلُهُمْ بِالْبَيِّنَاتِ فَمَا كَانَ اللَّهُ لِيَظْلِمَهُمْ وَلَكِنْ كَانُوا أَنْفُسَهُمْ يَظْلِمُونَ}
INSERT INTO [b9617] VALUES (1271,'<span data-type="title" id=toc-1257>٧١ </span>- {وَالْمُؤْمِنُونَ وَالْمُؤْمِنَاتُ بَعْضُهُمْ أَوْلِيَاءُ بَعْضٍ يَأْمُرُونَ بِالْمَعْرُوفِ وَيَنْهَوْنَ عَنِ الْمُنْكَرِ وَيُقِيمُونَ الصَّلاةَ وَيُؤْتُونَ الزَّكَاةَ وَيُطِيعُونَ اللَّهَ وَرَسُولَهُ أُولَئِكَ سَيَرْحَمُهُمُ اللَّهُ}
INSERT INTO [b9617] VALUES (1272,'<span data-type="title" id=toc-1258>٧٢ </span>- {وَعَدَ اللَّهُ الْمُؤْمِنِينَ وَالْمُؤْمِنَاتِ جَنَّاتٍ تَجْرِي مِنْ تَحْتِهَا الأَنْهَارُ خَالِدِينَ فِيهَا وَمَسَاكِنَ طَيِّبَةً فِي جَنَّاتِ عَدْنٍ وَرِضْوَانٌ مِنَ اللَّهِ أَكْبَرُ ذَلِكَ هُوَ الْفَوْزُ الْعَظِيمُ}
INSERT INTO [b9617] VALUES (1273,'<span data-type="title" id=toc-1259>٧٣ </span>- {يَا أَيُّهَا النَّبِيُّ جَاهِدِ الْكُفَّارَ وَالْمُنَافِقِينَ وَاغْلُظْ عَلَيْهِمْ وَمَأْوَاهُمْ جَهَنَّمُ وَبِئْسَ الْمَصِيرُ}
INSERT INTO [b9617] VALUES (1274,'<span data-type="title" id=toc-1260>٧٤ </span>- {وَلَقَدْ قَالُوا كَلِمَةَ الْكُفْرِ وَكَفَرُوا بَعْدَ إِسْلامِهِمْ وَهَمُّوا ⦗٤٠٥⦘ بِمَا لَمْ يَنَالُوا وَمَا نَقَمُوا إِلا أَنْ أَغْنَاهُمُ اللَّهُ وَرَسُولُهُ مِنْ فَضْلِهِ فَإِنْ يَتُوبُوا يَكُ خَيْرًا لَهُمْ وَإِنْ يَتَوَلَّوْا يُعَذِّبْهُمُ اللَّهُ عَذَابًا أَلِيمًا فِي الدُّنْيَا وَالآخِرَةِ وَمَا لَهُمْ فِي الأَرْضِ مِنْ وَلِيٍّ وَلا نَصِيرٍ}
INSERT INTO [b9617] VALUES (1275,'<span data-type="title" id=toc-1261>٧٥ </span>- {وَمِنْهُمْ مَنْ عَاهَدَ اللَّهَ لَئِنْ آتَانَا مِنْ فَضْلِهِ لَنَصَّدَّقَنَّ وَلَنَكُونَنَّ مِنَ الصَّالِحِينَ}
INSERT INTO [b9617] VALUES (1276,'<span data-type="title" id=toc-1262>٧٦ </span>- {فَلَمَّا آتَاهُمْ مِنْ فَضْلِهِ بَخِلُوا بِهِ وَتَوَلَّوْا وَهُمْ مُعْرِضُونَ}
INSERT INTO [b9617] VALUES (1277,'<span data-type="title" id=toc-1263>٧٧ </span>- {فَأَعْقَبَهُمْ نِفَاقًا فِي قُلُوبِهِمْ إِلَى يَوْمِ يَلْقَوْنَهُ بِمَا أَخْلَفُوا اللَّهَ مَا وَعَدُوهُ وَبِمَا كَانُوا يَكْذِبُونَ} ⦗٤٠٦⦘
INSERT INTO [b9617] VALUES (1278,'<span data-type="title" id=toc-1264>٧٨ </span>- {أَلَمْ يَعْلَمُوا أَنَّ اللَّهَ يَعْلَمُ سِرَّهُمْ وَنَجْوَاهُمْ وَأَنَّ اللَّهَ عَلامُ الْغُيُوبِ}
INSERT INTO [b9617] VALUES (1279,'<span data-type="title" id=toc-1265>٧٩ </span>- {الَّذِينَ يَلْمِزُونَ الْمُطَّوِّعِينَ مِنَ الْمُؤْمِنِينَ فِي الصَّدَقَاتِ وَالَّذِينَ لا يَجِدُونَ إِلا جُهْدَهُمْ فَيَسْخَرُونَ مِنْهُمْ سَخِرَ اللَّهُ مِنْهُمْ وَلَهُمْ عَذَابٌ أَلِيمٌ}
INSERT INTO [b9617] VALUES (1280,'<span data-type="title" id=toc-1266>٨٠ </span>- {اسْتَغْفِرْ لَهُمْ أَوْ لا تَسْتَغْفِرْ لَهُمْ إِنْ تَسْتَغْفِرْ لَهُمْ سَبْعِينَ مَرَّةً فَلَنْ يَغْفِرَ اللَّهُ لَهُمْ ذَلِكَ بِأَنَّهُمْ كَفَرُوا بِاللَّهِ وَرَسُولِهِ}
INSERT INTO [b9617] VALUES (1281,'<span data-type="title" id=toc-1267>٨١ </span>- {فَرِحَ الْمُخَلَّفُونَ بِمَقْعَدِهِمْ خِلافَ رَسُولِ اللَّهِ وَكَرِهُوا أَنْ يُجَاهِدُوا بِأَمْوَالِهِمْ وَأَنْفُسِهِمْ ⦗٤٠٧⦘ فِي سَبِيلِ اللَّهِ وَقَالُوا لا تَنْفِرُوا فِي الْحَرِّ قُلْ نَارُ جَهَنَّمَ أَشَدُّ حَرًّا لَوْ كَانُوا يَفْقَهُونَ}
INSERT INTO [b9617] VALUES (1282,'<span data-type="title" id=toc-1268>٨٢ </span>- {فَلْيَضْحَكُوا قَلِيلا وَلْيَبْكُوا كَثِيرًا جَزَاءً بِمَا كَانُوا يَكْسِبُونَ}
INSERT INTO [b9617] VALUES (1283,'<span data-type="title" id=toc-1269>٨٣ </span>- {فَإِنْ رَجَعَكَ اللَّهُ إِلَى طَائِفَةٍ مِنْهُمْ فَاسْتَأْذَنُوكَ لِلْخُرُوجِ فَقُلْ لَنْ تَخْرُجُوا مَعِيَ أَبَدًا وَلَنْ تُقَاتِلُوا مَعِيَ عَدُوًّا إِنَّكُمْ رَضِيتُمْ بِالْقُعُودِ أَوَّلَ مَرَّةٍ فَاقْعُدُوا مَعَ الْخَالِفِينَ}
INSERT INTO [b9617] VALUES (1284,'<span data-type="title" id=toc-1270>٨٤ </span>- {وَلا تُصَلِّ عَلَى أَحَدٍ مِنْهُمْ مَاتَ أَبَدًا وَلا تَقُمْ عَلَى قَبْرِهِ إِنَّهُمْ كَفَرُوا بِاللَّهِ وَرَسُولِهِ وَمَاتُوا وَهُمْ فَاسِقُونَ}
INSERT INTO [b9617] VALUES (1285,'<span data-type="title" id=toc-1271>٨٥ </span>- {وَلا تُعْجِبْكَ أَمْوَالُهُمْ وَأَوْلادُهُمْ إِنَّمَا يُرِيدُ اللَّهُ أَنْ يُعَذِّبَهُمْ بِهَا فِي الدُّنْيَا وَتَزْهَقَ أَنْفُسُهُمْ وَهُمْ كَافِرُونَ}
INSERT INTO [b9617] VALUES (1286,'<span data-type="title" id=toc-1272>٨٦ </span>- {وَإِذَا أُنْزِلَتْ سُورَةٌ أَنْ آمِنُوا بِاللَّهِ وَجَاهِدُوا مَعَ رَسُولِهِ اسْتَأْذَنَكَ أُولُو الطَّوْلِ مِنْهُمْ وَقَالُوا ذَرْنَا نَكُنْ مَعَ الْقَاعِدِينَ}
INSERT INTO [b9617] VALUES (1287,'<span data-type="title" id=toc-1273>٨٧ </span>- {رَضُوا بِأَنْ يَكُونُوا مَعَ الْخَوَالِفِ وَطُبِعَ عَلَى قُلُوبِهِمْ فَهُمْ لا يَفْقَهُونَ}
INSERT INTO [b9617] VALUES (1288,'<span data-type="title" id=toc-1274>٨٨ </span>- {لَكِنِ الرَّسُولُ وَالَّذِينَ آمَنُوا مَعَهُ جَاهَدُوا بِأَمْوَالِهِمْ وَأَنْفُسِهِمْ وَأُولَئِكَ لَهُمُ الْخَيْرَاتُ}
INSERT INTO [b9617] VALUES (1289,'<span data-type="title" id=toc-1275>٨٩ </span>- {خَالِدِينَ فِيهَا ذَلِكَ الْفَوْزُ الْعَظِيمُ}
INSERT INTO [b9617] VALUES (1290,'<span data-type="title" id=toc-1276>٩٠ </span>- {سَيُصِيبُ الَّذِينَ كَفَرُوا مِنْهُمْ عَذَابٌ أَلِيمٌ}
INSERT INTO [b9617] VALUES (1291,'<span data-type="title" id=toc-1277>٩١ </span>- {لَيْسَ عَلَى الضُّعَفَاءِ وَلا عَلَى الْمَرْضَى وَلا عَلَى الَّذِينَ لا يَجِدُونَ مَا يُنْفِقُونَ حَرَجٌ إِذَا نَصَحُوا لِلَّهِ وَرَسُولِهِ مَا عَلَى الْمُحْسِنِينَ مِنْ سَبِيلٍ وَاللَّهُ غَفُورٌ رَحِيمٌ}
INSERT INTO [b9617] VALUES (1292,'<span data-type="title" id=toc-1278>٩٢ </span>- {وَلا عَلَى الَّذِينَ إِذَا مَا أَتَوْكَ لِتَحْمِلَهُمْ قُلْتَ لا أَجِدُ مَا أَحْمِلُكُمْ عَلَيْهِ تَوَلَّوْا وَأَعْيُنُهُمْ تَفِيضُ مِنَ الدَّمْعِ حَزَنًا أَلا يَجِدُوا مَا يُنْفِقُونَ}
INSERT INTO [b9617] VALUES (1293,'<span data-type="title" id=toc-1279>٩٣ </span>- {إِنَّمَا السَّبِيلُ عَلَى الَّذِينَ يَسْتَأْذِنُونَكَ وَهُمْ أَغْنِيَاءُ رَضُوا بِأَنْ يَكُونُوا مَعَ الْخَوَالِفِ وَطَبَعَ اللَّهُ عَلَى قُلُوبِهِمْ فَهُمْ لا يَعْلَمُونَ}
INSERT INTO [b9617] VALUES (1294,'<span data-type="title" id=toc-1280>٩٤ </span>- {يَعْتَذِرُونَ إِلَيْكُمْ إِذَا رَجَعْتُمْ إِلَيْهِمْ قُلْ لا تَعْتَذِرُوا لَنْ نُؤْمِنَ لَكُمْ قَدْ نَبَّأَنَا اللَّهُ مِنْ أَخْبَارِكُمْ وَسَيَرَى اللَّهُ عَمَلَكُمْ وَرَسُولُهُ}
INSERT INTO [b9617] VALUES (1295,'<span data-type="title" id=toc-1281>٩٥ </span>- {سَيَحْلِفُونَ بِاللَّهِ لَكُمْ إِذَا انْقَلَبْتُمْ إِلَيْهِمْ لِتُعْرِضُوا عَنْهُمْ فَأَعْرِضُوا عَنْهُمْ إِنَّهُمْ رِجْسٌ وَمَأْوَاهُمْ جَهَنَّمُ جَزَاءً بِمَا كَانُوا يَكْسِبُونَ}
INSERT INTO [b9617] VALUES (1296,'<span data-type="title" id=toc-1282>٩٦ </span>- {فَإِنْ تَرْضَوْا عَنْهُمْ فَإِنَّ اللَّهَ لا يَرْضَى عَنِ الْقَوْمِ الْفَاسِقِينَ}
INSERT INTO [b9617] VALUES (1297,'<span data-type="title" id=toc-1283>٩٧ </span>- {الأَعْرَابُ أَشَدُّ كُفْرًا وَنِفَاقًا وَأَجْدَرُ أَلا يَعْلَمُوا حُدُودَ مَا أَنْزَلَ اللَّهُ}
INSERT INTO [b9617] VALUES (1298,'<span data-type="title" id=toc-1284>٩٨ </span>- {وَمِنَ الأَعْرَابِ مَنْ يَتَّخِذُ مَا يُنْفِقُ مَغْرَمًا وَيَتَرَبَّصُ بِكُمُ الدَّوَائِرَ عَلَيْهِمْ دَائِرَةُ السَّوْءِ}
INSERT INTO [b9617] VALUES (1299,'<span data-type="title" id=toc-1285>٩٩ </span>- {وَمِنَ الأَعْرَابِ مَنْ يُؤْمِنُ بِاللَّهِ وَالْيَوْمِ الآخِرِ وَيَتَّخِذُ مَا يُنْفِقُ قُرُبَاتٍ عِنْدَ اللَّهِ وَصَلَوَاتِ الرَّسُولِ أَلا إِنَّهَا قُرْبَةٌ لَهُمْ سَيُدْخِلُهُمُ اللَّهُ فِي رَحْمَتِهِ}
INSERT INTO [b9617] VALUES (1300,'<span data-type="title" id=toc-1286>١٠٠ </span>- {وَالسَّابِقُونَ الأَوَّلُونَ مِنَ الْمُهَاجِرِينَ وَالأَنْصَارِ وَالَّذِينَ اتَّبَعُوهُمْ بِإِحْسَانٍ ⦗٤١٢⦘ رَضِيَ اللَّهُ عَنْهُمْ وَرَضُوا عَنْهُ وَأَعَدَّ لَهُمْ جَنَّاتٍ تَجْرِي تَحْتَهَا الأَنْهَارُ خَالِدِينَ فِيهَا أَبَدًا ذَلِكَ الْفَوْزُ الْعَظِيمُ}
INSERT INTO [b9617] VALUES (1301,'<span data-type="title" id=toc-1287>١٠١ </span>- {وَمِمَّنْ حَوْلَكُمْ مِنَ الأَعْرَابِ مُنَافِقُونَ وَمِنْ أَهْلِ الْمَدِينَةِ مَرَدُوا عَلَى النِّفَاقِ لا تَعْلَمُهُمْ نَحْنُ نَعْلَمُهُمْ سَنُعَذِّبُهُمْ مَرَّتَيْنِ}
INSERT INTO [b9617] VALUES (1302,'<span data-type="title" id=toc-1288>١٠٢ </span>- {وَآخَرُونَ اعْتَرَفُوا بِذُنُوبِهِمْ خَلَطُوا عَمَلا صَالِحًا وَآخَرَ سَيِّئًا عَسَى اللَّهُ أَنْ يَتُوبَ عَلَيْهِمْ إِنَّ اللَّهَ غَفُورٌ رَحِيمٌ}
INSERT INTO [b9617] VALUES (1303,'<span data-type="title" id=toc-1289>١٠٣ </span>- {خُذْ مِنْ أَمْوَالِهِمْ صَدَقَةً تُطَهِّرُهُمْ وَتُزَكِّيهِمْ بِهَا وَصَلِّ عَلَيْهِمْ إِنَّ صَلاتَكَ سَكَنٌ لَهُمْ}
INSERT INTO [b9617] VALUES (1304,'<span data-type="title" id=toc-1290>١٠٤ </span>- {أَلَمْ يَعْلَمُوا أَنَّ اللَّهَ هُوَ يَقْبَلُ التَّوْبَةَ عَنْ عِبَادِهِ وَيَأْخُذُ الصَّدَقَاتِ وَأَنَّ اللَّهَ هُوَ التَّوَّابُ الرَّحِيمُ}
INSERT INTO [b9617] VALUES (1305,'<span data-type="title" id=toc-1291>١٠٥ </span>- {وَقُلِ اعْمَلُوا فَسَيَرَى اللَّهُ عَمَلَكُمْ وَرَسُولُهُ وَالْمُؤْمِنُونَ وَسَتُرَدُّونَ إِلَى عَالِمِ الْغَيْبِ وَالشَّهَادَةِ}
INSERT INTO [b9617] VALUES (1306,'<span data-type="title" id=toc-1292>١٠٦ </span>- {وَآخَرُونَ مُرْجَوْنَ لأَمْرِ اللَّهِ إِمَّا يُعَذِّبُهُمْ وَإِمَّا يَتُوبُ عَلَيْهِمْ}
INSERT INTO [b9617] VALUES (1307,'<span data-type="title" id=toc-1293>١٠٧ </span>- {وَالَّذِينَ اتَّخَذُوا مَسْجِدًا ضِرَارًا وَكُفْرًا وَتَفْرِيقًا بَيْنَ الْمُؤْمِنِينَ وَإِرْصَادًا لِمَنْ حَارَبَ اللَّهَ وَرَسُولَهُ مِنْ قَبْلُ وَلَيَحْلِفُنَّ إِنْ أَرَدْنَا إِلا الْحُسْنَى وَاللَّهُ يَشْهَدُ إِنَّهُمْ لَكَاذِبُونَ}
INSERT INTO [b9617] VALUES (1308,'<span data-type="title" id=toc-1294>١٠٨ </span>- {لَمَسْجِدٌ أُسِّسَ عَلَى التَّقْوَى مِنْ أَوَّلِ يَوْمٍ أَحَقُّ أَنْ تَقُومَ فِيهِ فِيهِ رِجَالٌ يُحِبُّونَ أَنْ يَتَطَهَّرُوا}
INSERT INTO [b9617] VALUES (1309,'<span data-type="title" id=toc-1295>١٠٩ </span>- {أَفَمَنْ أَسَّسَ بُنْيَانَهُ عَلَى تَقْوَى مِنَ اللَّهِ وَرِضْوَانٍ خَيْرٌ أَمْ مَنْ أَسَّسَ بُنْيَانَهُ ⦗٤١٥⦘ عَلَى شَفَا جُرُفٍ هَارٍ فَانْهَارَ بِهِ فِي نَارِ جَهَنَّمَ وَاللَّهُ لا يَهْدِي الْقَوْمَ الظَّالِمِينَ}
INSERT INTO [b9617] VALUES (1310,'<span data-type="title" id=toc-1296>١١٠ </span>- {لا يَزَالُ بُنْيَانُهُمُ الَّذِي بَنَوْا رِيبَةً فِي قُلُوبِهِمْ إِلا أَنْ تَقَطَّعَ قُلُوبُهُمْ وَاللَّهُ عَلِيمٌ حَكِيمٌ}
INSERT INTO [b9617] VALUES (1311,'<span data-type="title" id=toc-1297>١١١ </span>- {إِنَّ اللَّهَ اشْتَرَى مِنَ الْمُؤْمِنِينَ أَنْفُسَهُمْ وَأَمْوَالَهُمْ بِأَنَّ لَهُمُ الْجَنَّةَ يُقَاتِلُونَ فِي سَبِيلِ اللَّهِ فَيَقْتُلُونَ وَيُقْتَلُونَ وَعْدًا عَلَيْهِ حَقًّا فِي التَّوْرَاةِ وَالإِنْجِيلِ وَالْقُرْآنِ وَمَنْ أَوْفَى بِعَهْدِهِ مِنَ اللَّهِ فَاسْتَبْشِرُوا بِبَيْعِكُمُ الَّذِي بَايَعْتُمْ بِهِ وَذَلِكَ هُوَ الْفَوْزُ الْعَظِيمُ}
INSERT INTO [b9617] VALUES (1312,'<span data-type="title" id=toc-1298>١١٢ </span>- {التَّائِبُونَ الْعَابِدُونَ الْحَامِدُونَ السَّائِحُونَ الرَّاكِعُونَ ⦗٤١٦⦘ السَّاجِدُونَ الآمِرُونَ بِالْمَعْرُوفِ وَالنَّاهُونَ عَنِ الْمُنْكَرِ وَالْحَافِظُونَ لِحُدُودِ اللَّهِ}
INSERT INTO [b9617] VALUES (1313,'<span data-type="title" id=toc-1299>١١٣ </span>- {مَا كَانَ لِلنَّبِيِّ وَالَّذِينَ آمَنُوا أَنْ يَسْتَغْفِرُوا لِلْمُشْرِكِينَ وَلَوْ كَانُوا أُولِي قُرْبَى مِنْ بَعْدِ مَا تَبَيَّنَ لَهُمْ أَنَّهُمْ أَصْحَابُ الْجَحِيمِ}
INSERT INTO [b9617] VALUES (1314,'<span data-type="title" id=toc-1300>١١٤ </span>- {وَمَا كَانَ اسْتِغْفَارُ إِبْرَاهِيمَ لأَبِيهِ إِلا عَنْ مَوْعِدَةٍ وَعَدَهَا إِيَّاهُ فَلَمَّا تَبَيَّنَ لَهُ أَنَّهُ عَدُوٌّ لِلَّهِ تَبَرَّأَ مِنْهُ إِنَّ إِبْرَاهِيمَ لأَوَّاهٌ حَلِيمٌ}
INSERT INTO [b9617] VALUES (1315,'<span data-type="title" id=toc-1301>١١٥ </span>- {وَمَا كَانَ اللَّهُ لِيُضِلَّ قَوْمًا بَعْدَ إِذْ هَدَاهُمْ}
INSERT INTO [b9617] VALUES (1316,'<span data-type="title" id=toc-1302>١١٦ </span>- {إِنَّ اللَّهَ لَهُ مُلْكُ السَّمَاوَاتِ وَالأَرْضِ يُحْيِي وَيُمِيتُ وَمَا لَكُمْ مِنْ دُونِ اللَّهِ مِنْ وَلِيٍّ وَلا نَصِيرٍ}
INSERT INTO [b9617] VALUES (1317,'<span data-type="title" id=toc-1303>١١٧ </span>- {لَقَدْ تَابَ اللَّهُ عَلَى النَّبِيِّ وَالْمُهَاجِرِينَ وَالأَنْصَارِ الَّذِينَ اتَّبَعُوهُ فِي سَاعَةِ الْعُسْرَةِ مِنْ بَعْدِ مَا كَادَ يَزِيغُ قُلُوبُ فَرِيقٍ مِنْهُمْ ثُمَّ تَابَ عَلَيْهِمْإِنَّهُ بِهِمْ رَءُوفٌ رَحِيمٌ}
INSERT INTO [b9617] VALUES (1318,'<span data-type="title" id=toc-1304>١١٨ </span>- {وَعَلَى الثَّلاثَةِ الَّذِينَ خُلِّفُوا حَتَّى إِذَا ضَاقَتْ عَلَيْهِمُ الأَرْضُ بِمَا رَحُبَتْ وَضَاقَتْ عَلَيْهِمْ أَنْفُسُهُمْ وَظَنُّوا أَنْ لا مَلْجَأَ مِنَ اللَّهِ إِلا إِلَيْهِ ثُمَّ تَابَ عَلَيْهِمْ لِيَتُوبُوا إِنَّ اللَّهَ هُوَ التَّوَّابُ الرَّحِيمُ}
INSERT INTO [b9617] VALUES (1319,'<span data-type="title" id=toc-1305>١٢٠ </span>- {مَا كَانَ لأَهْلِ الْمَدِينَةِ وَمَنْ حَوْلَهُمْ مِنَ الأَعْرَابِ أَنْ يَتَخَلَّفُوا عَنْ رَسُولِ اللَّهِ وَلا يَرْغَبُوا بِأَنْفُسِهِمْ عَنْ نَفْسِهِ ذَلِكَ بِأَنَّهُمْ لا يُصِيبُهُمْ ظَمَأٌ وَلا نَصَبٌ وَلا مَخْمَصَةٌ فِي سَبِيلِ اللَّهِ وَلا يَطَئُونَ مَوْطِئًا يَغِيظُ الْكُفَّارَ وَلا يَنَالُونَ مِنْ عَدُوٍّ نَيْلا إِلا كُتِبَ لَهُمْ بِهِ عَمَلٌ صَالِحٌ} ⦗٤١٩⦘
INSERT INTO [b9617] VALUES (1320,'<span data-type="title" id=toc-1306>١٢١ </span>- {وَلا يَقْطَعُونَ وَادِيًا إِلا كُتِبَ لَهُمْ لِيَجْزِيَهُمُ اللَّهُ أَحْسَنَ مَا كَانُوا يَعْمَلُونَ}
INSERT INTO [b9617] VALUES (1321,'<span data-type="title" id=toc-1307>١٢٢ </span>- {وَمَا كَانَ الْمُؤْمِنُونَ لِيَنْفِرُوا كَافَّةً فَلَوْلا نَفَرَ مِنْ كُلِّ فِرْقَةٍ مِنْهُمْ طَائِفَةٌ لِيَتَفَقَّهُوا فِي الدِّينِ وَلِيُنْذِرُوا قَوْمَهُمْ إِذَا رَجَعُوا إِلَيْهِمْ لَعَلَّهُمْ يَحْذَرُونَ}
INSERT INTO [b9617] VALUES (1322,'<span data-type="title" id=toc-1308>١٢٣ </span>- {قَاتِلُوا الَّذِينَ يَلُونَكُمْ مِنَ الْكُفَّارِ وَلْيَجِدُوا فِيكُمْ غِلْظَةً}
INSERT INTO [b9617] VALUES (1323,'<span data-type="title" id=toc-1309>١٢٤ </span>- {وَإِذَا مَا أُنْزِلَتْ سُورَةٌ فَمِنْهُمْ مَنْ يَقُولُ أَيُّكُمْ زَادَتْهُ هَذِهِ إِيمَانًا فَأَمَّا الَّذِينَ آمَنُوا فَزَادَتْهُمْ إِيمَانًا وَهُمْ يَسْتَبْشِرُونَ}
INSERT INTO [b9617] VALUES (1324,'<span data-type="title" id=toc-1310>١٢٥ </span>- {وَأَمَّا الَّذِينَ فِي قُلُوبِهِمْ مَرَضٌ فَزَادَتْهُمْ رِجْسًا إِلَى رِجْسِهِمْ وَمَاتُوا وَهُمْ كَافِرُونَ}
INSERT INTO [b9617] VALUES (1325,'<span data-type="title" id=toc-1311>١٢٦ </span>- {أَوَلا يَرَوْنَ أَنَّهُمْ يُفْتَنُونَ فِي كُلِّ عَامٍ مَرَّةً أَوْ مَرَّتَيْنِ ثُمَّ لا يَتُوبُونَ وَلا هُمْ يَذَّكَّرُونَ}
INSERT INTO [b9617] VALUES (1326,'<span data-type="title" id=toc-1312>١٢٧ </span>- {وَإِذَا مَا أُنْزِلَتْ سُورَةٌ نَظَرَ بَعْضُهُمْ إِلَى بَعْضٍ هَلْ يَرَاكُمْ مِنْ أَحَدٍ ثُمَّ انْصَرَفُوا صَرَفَ اللَّهُ قُلُوبَهُمْ بِأَنَّهُمْ قَوْمٌ لا يَفْقَهُونَ}
INSERT INTO [b9617] VALUES (1327,'<span data-type="title" id=toc-1313>١٢٨ </span>- {لَقَدْ جَاءَكُمْ رَسُولٌ مِنْ أَنْفُسِكُمْ عَزِيزٌ عَلَيْهِ مَا عَنِتُّمْ حَرِيصٌ عَلَيْكُمْ بِالْمُؤْمِنِينَ رَءُوفٌ رَحِيمٌ}
INSERT INTO [b9617] VALUES (1328,'<span data-type="title" id=toc-1314>١٢٩ </span>- {فَإِنْ تَوَلَّوْا فَقُلْ حَسْبِيَ اللَّهُ لا إِلَهَ إِلا هُوَ عَلَيْهِ تَوَكَّلْتُ وَهُوَ رَبُّ الْعَرْشِ الْعَظِيمِ}
INSERT INTO [b9617] VALUES (1329,'<span data-type="title" id=toc-1315>سورة يونس</span>','2','423',NULL,NULL,'0');
INSERT INTO [b9617] VALUES (1330,'<span data-type="title" id=toc-1316>٢ </span>- {أَكَانَ لِلنَّاسِ عَجَبًا أَنْ أَوْحَيْنَا إِلَى رَجُلٍ مِنْهُمْ أَنْ أَنْذِرِ النَّاسَ وَبَشِّرِ الَّذِينَ آمَنُوا أَنَّ لَهُمْ قَدَمَ صِدْقٍ عِنْدَ رَبِّهِمْ قَالَ الْكَافِرُونَ إِنَّ هَذَا لَسَاحِرٌ مُبِينٌ}
INSERT INTO [b9617] VALUES (1331,'<span data-type="title" id=toc-1317>٣ </span>- {إِنَّ رَبَّكُمُ اللَّهُ الَّذِي خَلَقَ السَّمَاوَاتِ وَالأَرْضَ فِي سِتَّةِ أَيَّامٍ ثُمَّ اسْتَوَى عَلَى الْعَرْشِ يُدَبِّرُ الأَمْرَ مَا مِنْ شَفِيعٍ إِلا مِنْ بَعْدِ إِذْنِهِ ذَلِكُمُ اللَّهُ رَبُّكُمْ فَاعْبُدُوهُ أَفَلا تَذَكَّرُونَ}
INSERT INTO [b9617] VALUES (1332,'<span data-type="title" id=toc-1318>٤ </span>- {إِلَيْهِ مَرْجِعُكُمْ جَمِيعًا وَعْدَ اللَّهِ حَقًّا إِنَّهُ يَبْدَأُ الْخَلْقَ ثُمَّ يُعِيدُهُ لِيَجْزِيَ الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ بِالْقِسْطِ وَالَّذِينَ كَفَرُوا لَهُمْ شَرَابٌ مِنْ حَمِيمٍ وَعَذَابٌ أَلِيمٌ بِمَا كَانُوا يَكْفُرُونَ}
INSERT INTO [b9617] VALUES (1333,'<span data-type="title" id=toc-1319>٥ </span>- {هُوَ الَّذِي جَعَلَ الشَّمْسَ ضِيَاءً وَالْقَمَرَ نُورًا وَقَدَّرَهُ مَنَازِلَ لِتَعْلَمُوا عَدَدَ السِّنِينَ وَالْحِسَابَ مَا خَلَقَ اللَّهُ ذَلِكَ إِلا بِالْحَقِّ يُفَصِّلُ الآيَاتِ لِقَوْمٍ يَعْلَمُونَ}
INSERT INTO [b9617] VALUES (1334,'<span data-type="title" id=toc-1320>٦ </span>- {إِنَّ فِي اخْتِلافِ اللَّيْلِ وَالنَّهَارِ وَمَا خَلَقَ اللَّهُ فِي السَّمَاوَاتِ وَالأَرْضِ لآيَاتٍ لِقَوْمٍ يَتَّقُونَ}
INSERT INTO [b9617] VALUES (1335,'<span data-type="title" id=toc-1321>٧ </span>- {إِنَّ الَّذِينَ لا يَرْجُونَ لِقَاءَنَا وَرَضُوا بِالْحَيَاةِ الدُّنْيَا وَاطْمَأَنُّوا بِهَا وَالَّذِينَ هُمْ عَنْ آيَاتِنَا غَافِلُونَ} ⦗٤٢٥⦘
INSERT INTO [b9617] VALUES (1336,'<span data-type="title" id=toc-1322>٨ </span>- {أُولَئِكَ مَأْوَاهُمُ النَّارُ بِمَا كَانُوا يَكْسِبُونَ}
INSERT INTO [b9617] VALUES (1337,'<span data-type="title" id=toc-1323>٩ </span>- {إِنَّ الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ يَهْدِيهِمْ رَبُّهُمْ بِإِيمَانِهِمْ تَجْرِي مِنْ تَحْتِهِمُ الأَنْهَارُ}
INSERT INTO [b9617] VALUES (1338,'<span data-type="title" id=toc-1324>١٠ </span>- {دَعْوَاهُمْ فِيهَا سُبْحَانَكَ اللَّهُمَّ وَتَحِيَّتُهُمْ فِيهَا سَلامٌ وَآخِرُ دَعْوَاهُمْ أَنِ الْحَمْدُ لِلَّهِ رَبِّ الْعَالَمِينَ}
INSERT INTO [b9617] VALUES (1339,'<span data-type="title" id=toc-1325>١١ </span>- {وَلَوْ يُعَجِّلُ اللَّهُ لِلنَّاسِ الشَّرَّ اسْتِعْجَالَهُمْ بِالْخَيْرِ لَقُضِيَ إِلَيْهِمْ أَجَلُهُمْ فَنَذَرُ الَّذِينَ لا يَرْجُونَ لِقَاءَنَا فِي طُغْيَانِهِمْ يَعْمَهُونَ}
INSERT INTO [b9617] VALUES (1340,'<span data-type="title" id=toc-1326>١٢ </span>- {وَإِذَا مَسَّ الإِنْسَانَ الضُّرُّ دَعَانَا لِجَنْبِهِ أَوْ قَاعِدًا أَوْ قَائِمًا فَلَمَّا كَشَفْنَا عَنْهُ ضُرَّهُ مَرَّ كَأَنْ لَمْ يَدْعُنَا إِلَى ضُرٍّ مَسَّهُ كَذَلِكَ زُيِّنَ لِلْمُسْرِفِينَ مَا كَانُوا يَعْمَلُونَ}
INSERT INTO [b9617] VALUES (1341,'<span data-type="title" id=toc-1327>١٣ </span>- {وَلَقَدْ أَهْلَكْنَا الْقُرُونَ مِنْ قَبْلِكُمْ لَمَّا ظَلَمُوا وَجَاءَتْهُمْ رُسُلُهُمْ بِالْبَيِّنَاتِ وَمَا كَانُوا لِيُؤْمِنُوا كَذَلِكَ نَجْزِي الْقَوْمَ الْمُجْرِمِينَ}
INSERT INTO [b9617] VALUES (1342,'<span data-type="title" id=toc-1328>١٤ </span>- {ثُمَّ جَعَلْنَاكُمْ خَلائِفَ فِي الأَرْضِ مِنْ بَعْدِهِمْ لِنَنْظُرَ كَيْفَ تَعْمَلُونَ}
INSERT INTO [b9617] VALUES (1343,'<span data-type="title" id=toc-1329>١٥ </span>- {ائْتِ بِقُرْآنٍ غَيْرِ هَذَا أَوْ بَدِّلْهُ قُلْ مَا يَكُونُ لِي أَنْ أُبَدِّلَهُ مِنْ تِلْقَاءِ نَفْسِي إِنْ أَتَّبِعُ إِلا مَا يُوحَى إِلَيَّ إِنِّي أَخَافُ إِنْ عَصَيْتُ رَبِّي عَذَابَ يَوْمٍ عَظِيمٍ}
INSERT INTO [b9617] VALUES (1344,'<span data-type="title" id=toc-1330>١٦ </span>- {قُلْ لَوْ شَاءَ اللَّهُ مَا تَلَوْتُهُ عَلَيْكُمْ وَلا أَدْرَاكُمْ بِهِ فَقَدْ لَبِثْتُ فِيكُمْ عُمُرًا مِنْ قَبْلِهِ أَفَلا تَعْقِلُونَ} ⦗٤٢٨⦘
INSERT INTO [b9617] VALUES (1345,'<span data-type="title" id=toc-1331>١٧ </span>- {فَمَنْ أَظْلَمُ مِمَّنِ افْتَرَى عَلَى اللَّهِ كَذِبًا}
INSERT INTO [b9617] VALUES (1346,'<span data-type="title" id=toc-1332>١٨ </span>- {وَيَقُولُونَ هَؤُلاءِ شُفَعَاؤُنَا عِنْدَ اللَّهِ قُلْ أَتُنَبِّئُونَ اللَّهَ بِمَا لا يَعْلَمُ فِي السَّمَاوَاتِ وَلا فِي الأَرْضِ سُبْحَانَهُ وَتَعَالَى عَمَّا يُشْرِكُونَ}
INSERT INTO [b9617] VALUES (1347,'<span data-type="title" id=toc-1333>١٩ </span>- {وَمَا كَانَ النَّاسُ إِلا أُمَّةً وَاحِدَةً فَاخْتَلَفُوا وَلَوْلا كَلِمَةٌ سَبَقَتْ مِنْ رَبِّكَ لَقُضِيَ بَيْنَهُمْ}
INSERT INTO [b9617] VALUES (1348,'<span data-type="title" id=toc-1334>٢٠ </span>- {وَيَقُولُونَ لَوْلا أُنْزِلَ عَلَيْهِ آيَةٌ مِنْ رَبِّهِ فَقُلْ إِنَّمَا الْغَيْبُ لِلَّهِ فَانْتَظِرُوا إِنِّي ⦗٤٢٩⦘ مَعَكُمْ مِنَ الْمُنْتَظِرِينَ}
INSERT INTO [b9617] VALUES (1349,'<span data-type="title" id=toc-1335>٢١ </span>- {وَإِذَا أَذَقْنَا النَّاسَ رَحْمَةً مِنْ بَعْدِ ضَرَّاءَ مَسَّتْهُمْ إِذَا لَهُمْ مَكْرٌ فِي آيَاتِنَا قُلِ اللَّهُ أَسْرَعُ مَكْرًا إِنَّ رُسُلَنَا يَكْتُبُونَ مَا تَمْكُرُونَ}
INSERT INTO [b9617] VALUES (1350,'<span data-type="title" id=toc-1336>٢٢ </span>- {حَتَّى إِذَا كُنْتُمْ فِي الْفُلْكِ وَجَرَيْنَ بِهِمْ بِرِيحٍ طَيِّبَةٍ وَفَرِحُوا بِهَا جَاءَتْهَا رِيحٌ عَاصِفٌ وَجَاءَهُمُ الْمَوْجُ مِنْ كُلِّ مَكَانٍ وَظَنُّوا أَنَّهُمْ أُحِيطَ بِهِمْ دَعَوُا اللَّهَ مُخْلِصِينَ لَهُ الدِّينَ لَئِنْ أَنْجَيْتَنَا مِنْ هَذِهِ لَنَكُونَنَّ مِنَ الشَّاكِرِينَ}
INSERT INTO [b9617] VALUES (1351,'<span data-type="title" id=toc-1337>٢٣ </span>- {فَلَمَّا أَنْجَاهُمْ إِذَا هُمْ يَبْغُونَ فِي الأَرْضِ بِغَيْرِ الْحَقِّ يَا أَيُّهَا النَّاسُ إِنَّمَا بَغْيُكُمْ عَلَى أَنْفُسِكُمْ مَتَاعَ الْحَيَاةِ الدُّنْيَا ثُمَّ إِلَيْنَا مَرْجِعُكُمْ فَنُنَبِّئُكُمْ بِمَا كُنْتُمْ تَعْمَلُونَ}
INSERT INTO [b9617] VALUES (1352,'<span data-type="title" id=toc-1338>٢٤ </span>- {حَتَّى إِذَا أَخَذَتِ الأَرْضُ زُخْرُفَهَا وَازَّيَّنَتْ وَظَنَّ أَهْلُهَا أَنَّهُمْ قَادِرُونَ عَلَيْهَا أَتَاهَا أَمْرُنَا لَيْلا أَوْ نَهَارًا فَجَعَلْنَاهَا حَصِيدًا كَأَنْ لَمْ تَغْنَ بِالأَمْسِ كَذَلِكَ نُفَصِّلُ الآيَاتِ لِقَوْمٍ يَتَفَكَّرُونَ}
INSERT INTO [b9617] VALUES (1353,'<span data-type="title" id=toc-1339>٢٦ </span>- {لِلَّذِينَ أَحْسَنُوا الْحُسْنَى وَزِيَادَةٌ وَلا يَرْهَقُ وُجُوهَهُمْ قَتَرٌ وَلا ذِلَّةٌ أُولَئِكَ أَصْحَابُ الْجَنَّةِ هُمْ فِيهَا خَالِدُونَ}
INSERT INTO [b9617] VALUES (1354,'<span data-type="title" id=toc-1340>٢٧ </span>- {وَالَّذِينَ كَسَبُوا السَّيِّئَاتِ جَزَاءُ سَيِّئَةٍ بِمِثْلِهَا وَتَرْهَقُهُمْ ذِلَّةٌ مَا لَهُمْ مِنَ اللَّهِ مِنْ عَاصِمٍ كَأَنَّمَا أُغْشِيَتْ وُجُوهُهُمْ قِطَعًا مِنَ اللَّيْلِ مُظْلِمًا أُولَئِكَ أَصْحَابُ النَّارِ هُمْ فِيهَا خَالِدُونَ}
INSERT INTO [b9617] VALUES (1355,'<span data-type="title" id=toc-1341>٢٨ </span>- {وَيَوْمَ نَحْشُرُهُمْ جَمِيعًا ثُمَّ نَقُولُ لِلَّذِينَ أَشْرَكُوا مَكَانَكُمْ أَنْتُمْ وَشُرَكَاؤُكُمْ فَزَيَّلْنَا بَيْنَهُمْ وَقَالَ شُرَكَاؤُهُمْ مَا كُنْتُمْ إِيَّانَا تَعْبُدُونَ}
INSERT INTO [b9617] VALUES (1356,'<span data-type="title" id=toc-1342>٢٩ </span>- {فَكَفَى بِاللَّهِ شَهِيدًا بَيْنَنَا وَبَيْنَكُمْ إِنْ كُنَّا عَنْ عِبَادَتِكُمْ لَغَافِلِينَ}
INSERT INTO [b9617] VALUES (1357,'<span data-type="title" id=toc-1343>٣٠ </span>- {هُنَالِكَ تَبْلُو كُلُّ نَفْسٍ مَا أَسْلَفَتْ وَرُدُّوا إِلَى اللَّهِ مَوْلاهُمُ الْحَقِّ وَضَلَّ عَنْهُمْ مَا كَانُوا يَفْتَرُونَ}
INSERT INTO [b9617] VALUES (1358,'<span data-type="title" id=toc-1344>٣١ </span>- {قُلْ مَنْ يَرْزُقُكُمْ مِنَ السَّمَاءِ وَالأَرْضِ أَمَّنْ يَمْلِكُ السَّمْعَ وَالأَبْصَارَ وَمَنْ يُخْرِجُ الْحَيَّ مِنَ الْمَيِّتِ وَيُخْرِجُ الْمَيِّتَ مِنَ الْحَيِّ وَمَنْ يُدَبِّرُ الأَمْرَ فَسَيَقُولُونَ اللَّهُ فَقُلْ أَفَلا تَتَّقُونَ}
INSERT INTO [b9617] VALUES (1359,'<span data-type="title" id=toc-1345>٣٢ </span>- {فَذَلِكُمُ اللَّهُ رَبُّكُمُ الْحَقُّ فَمَاذَا بَعْدَ الْحَقِّ إِلا الضَّلالُ فَأَنَّى تُصْرَفُونَ}
INSERT INTO [b9617] VALUES (1360,'<span data-type="title" id=toc-1346>٣٣ </span>- {كَذَلِكَ حَقَّتْ كَلِمَتُ رَبِّكَ عَلَى الَّذِينَ فَسَقُوا أَنَّهُمْ لا يُؤْمِنُونَ}
INSERT INTO [b9617] VALUES (1361,'<span data-type="title" id=toc-1347>٣٤ </span>- {قُلْ هَلْ مِنْ شُرَكَائِكُمْ مَنْ يَبْدَأُ الْخَلْقَ ثُمَّ يُعِيدُهُ قُلِ اللَّهُ يَبْدَأُ الْخَلْقَ ثُمَّ يُعِيدُهُ فَأَنَّى تُؤْفَكُونَ}
INSERT INTO [b9617] VALUES (1362,'<span data-type="title" id=toc-1348>٣٥ </span>- {قُلْ هَلْ مِنْ شُرَكَائِكُمْ مَنْ يَهْدِي إِلَى الْحَقِّ قُلِ اللَّهُ يَهْدِي لِلْحَقِّ أَفَمَنْ يَهْدِي إِلَى الْحَقِّ أَحَقُّ أَنْ يُتَّبَعَ أَمَّنْ لا يَهِدِّي إِلا أَنْ يُهْدَى فَمَا لَكُمْ كَيْفَ تَحْكُمُونَ}
INSERT INTO [b9617] VALUES (1363,'<span data-type="title" id=toc-1349>٣٦ </span>- {وَمَا يَتَّبِعُ أَكْثَرُهُمْ إِلا ظَنًّا إِنَّ الظَّنَّ لا يُغْنِي مِنَ الْحَقِّ شَيْئًا}
INSERT INTO [b9617] VALUES (1364,'<span data-type="title" id=toc-1350>٣٧ </span>- {وَمَا كَانَ هَذَا الْقُرْآنُ أَنْ يُفْتَرَى مِنْ دُونِ اللَّهِ وَلَكِنْ تَصْدِيقَ الَّذِي بَيْنَ يَدَيْهِ وَتَفْصِيلَ الْكِتَابِ لا رَيْبَ فِيهِ مِنْ رَبِّ الْعَالَمِينَ}
INSERT INTO [b9617] VALUES (1365,'<span data-type="title" id=toc-1351>٣٨ </span>- {أَمْ يَقُولُونَ افْتَرَاهُ قُلْ فَأْتُوا بِسُورَةٍ مِثْلِهِ وَادْعُوا مَنِ اسْتَطَعْتُمْ مِنْ دُونِ اللَّهِ إِنْ كُنْتُمْ ⦗٤٣٥⦘ صَادِقِينَ}
INSERT INTO [b9617] VALUES (1366,'<span data-type="title" id=toc-1352>٣٩ </span>- {بَلْ كَذَّبُوا بِمَا لَمْ يُحِيطُوا بِعِلْمِهِ وَلَمَّا يَأْتِهِمْ تَأْوِيلُهُ كَذَلِكَ كَذَّبَ الَّذِينَ مِنْ قَبْلِهِمْ فَانْظُرْ كَيْفَ كَانَ عَاقِبَةُ الظَّالِمِينَ}
INSERT INTO [b9617] VALUES (1367,'<span data-type="title" id=toc-1353>٤٠ </span>- {وَرَبُّكَ أَعْلَمُ بِالْمُفْسِدِينَ}
INSERT INTO [b9617] VALUES (1368,'<span data-type="title" id=toc-1354>٤١ </span>- {أَنْتُمْ بَرِيئُونَ مِمَّا أَعْمَلُ}
INSERT INTO [b9617] VALUES (1369,'<span data-type="title" id=toc-1355>٤٢ </span>- {وَمِنْهُمْ مَنْ يَسْتَمِعُونَ إِلَيْكَ أَفَأَنْتَ تُسْمِعُ الصُّمَّ وَلَوْ كَانُوا لا يَعْقِلُونَ}
INSERT INTO [b9617] VALUES (1370,'<span data-type="title" id=toc-1356>٤٣ </span>- {وَمِنْهُمْ مَنْ يَنْظُرُ إِلَيْكَ أَفَأَنْتَ تَهْدِي الْعُمْيَ وَلَوْ كَانُوا لا يُبْصِرُونَ}
INSERT INTO [b9617] VALUES (1371,'<span data-type="title" id=toc-1357>٤٤ </span>- {إِنَّ اللَّهَ لا يَظْلِمُ النَّاسَ شَيْئًا وَلَكِنَّ النَّاسَ أَنْفُسَهُمْ يَظْلِمُونَ}
INSERT INTO [b9617] VALUES (1372,'<span data-type="title" id=toc-1358>٤٥ </span>- {وَيَوْمَ يَحْشُرُهُمْ كَأَنْ لَمْ يَلْبَثُوا إِلا سَاعَةً مِنَ النَّهَارِ يَتَعَارَفُونَ بَيْنَهُمْ قَدْ خَسِرَ الَّذِينَ كَذَّبُوا بِلِقَاءِ اللَّهِ}
INSERT INTO [b9617] VALUES (1373,'<span data-type="title" id=toc-1359>٤٦ </span>- {وَإِمَّا نُرِيَنَّكَ بَعْضَ الَّذِي نَعِدُهُمْ أَوْ نَتَوَفَّيَنَّكَ فَإِلَيْنَا مَرْجِعُهُمْ ثُمَّ اللَّهُ شَهِيدٌ عَلَى مَا يَفْعَلُونَ} ⦗٤٣٧⦘
INSERT INTO [b9617] VALUES (1374,'<span data-type="title" id=toc-1360>٤٧ </span>- {وَلِكُلِّ أُمَّةٍ رَسُولٌ فَإِذَا جَاءَ رَسُولُهُمْ قُضِيَ بَيْنَهُمْ بِالْقِسْطِ وَهُمْ لا يُظْلَمُونَ}
INSERT INTO [b9617] VALUES (1375,'<span data-type="title" id=toc-1361>٤٨ </span>- {وَيَقُولُونَ مَتَى هَذَا الْوَعْدُ إِنْ كُنْتُمْ صَادِقِينَ}
INSERT INTO [b9617] VALUES (1376,'<span data-type="title" id=toc-1362>٤٩ </span>- {قُلْ لا أَمْلِكُ لِنَفْسِي ضَرًّا وَلا نَفْعًا إِلا مَا شَاءَ اللَّهُ لِكُلِّ أُمَّةٍ أَجَلٌ إِذَا جَاءَ أَجَلُهُمْ فَلا يَسْتَأْخِرُونَ سَاعَةً وَلا يَسْتَقْدِمُونَ}
INSERT INTO [b9617] VALUES (1377,'<span data-type="title" id=toc-1363>٥٠ </span>- {قُلْ أَرَأَيْتُمْ إِنْ أَتَاكُمْ عَذَابُهُ بَيَاتًا أَوْ نَهَارًا مَاذَا يَسْتَعْجِلُ مِنْهُ الْمُجْرِمُونَ}
INSERT INTO [b9617] VALUES (1378,'<span data-type="title" id=toc-1364>٥١ </span>- {أَثُمَّ إِذَا مَا وَقَعَ آمَنْتُمْ بِهِ آلآنَ وَقَدْ كُنْتُمْ بِهِ تَسْتَعْجِلُونَ}
INSERT INTO [b9617] VALUES (1379,'<span data-type="title" id=toc-1365>٥٢ </span>- {ثُمَّ قِيلَ لِلَّذِينَ ظَلَمُوا ذُوقُوا عَذَابَ الْخُلْدِ هَلْ تُجْزَوْنَ إِلا بِمَا كُنْتُمْ تَكْسِبُونَ}
INSERT INTO [b9617] VALUES (1380,'<span data-type="title" id=toc-1366>٥٣ </span>- {وَيَسْتَنْبِئُونَكَ أَحَقٌّ هُوَ قُلْ إِي وَرَبِّي إِنَّهُ لَحَقٌّ وَمَا أَنْتُمْ بِمُعْجِزِينَ}
INSERT INTO [b9617] VALUES (1381,'<span data-type="title" id=toc-1367>٥٤ </span>- {وَلَوْ أَنَّ لِكُلِّ نَفْسٍ ظَلَمَتْ مَا فِي الأَرْضِ لافْتَدَتْ بِهِ وَأَسَرُّوا النَّدَامَةَ لَمَّا رَأَوُا الْعَذَابَ وَقُضِيَ بَيْنَهُمْ بِالْقِسْطِ وَهُمْ لا يُظْلَمُونَ}
INSERT INTO [b9617] VALUES (1382,'<span data-type="title" id=toc-1368>٥٥ </span>- {أَلا إِنَّ لِلَّهِ مَا فِي السَّمَاوَاتِ وَالأَرْضِ أَلا إِنَّ وَعْدَ اللَّهِ حَقٌّ وَلَكِنَّ أَكْثَرَهُمْ لا يَعْلَمُونَ}
INSERT INTO [b9617] VALUES (1383,'<span data-type="title" id=toc-1369>٥٦ </span>- {هُوَ يُحْيِي وَيُمِيتُ وَإِلَيْهِ تُرْجَعُونَ}
INSERT INTO [b9617] VALUES (1384,'<span data-type="title" id=toc-1370>٥٧ </span>- {يَا أَيُّهَا النَّاسُ قَدْ جَاءَتْكُمْ مَوْعِظَةٌ مِنْ رَبِّكُمْ وَشِفَاءٌ لِمَا فِي الصُّدُورِ}
INSERT INTO [b9617] VALUES (1385,'<span data-type="title" id=toc-1371>٥٨ </span>- {قُلْ بِفَضْلِ اللَّهِ وَبِرَحْمَتِهِ فَبِذَلِكَ فَلْيَفْرَحُوا هُوَ خَيْرٌ مِمَّا يَجْمَعُونَ}
INSERT INTO [b9617] VALUES (1386,'<span data-type="title" id=toc-1372>٥٩ </span>- {قُلْ أَرَأَيْتُمْ مَا أَنْزَلَ اللَّهُ لَكُمْ مِنْ رِزْقٍ فَجَعَلْتُمْ مِنْهُ حَرَامًا وَحَلالا قُلْ آللَّهُ أَذِنَ لَكُمْ أَمْ عَلَى اللَّهِ تَفْتَرُونَ}
INSERT INTO [b9617] VALUES (1387,'<span data-type="title" id=toc-1373>٦٠ </span>- {وَمَا ظَنُّ الَّذِينَ يَفْتَرُونَ عَلَى اللَّهِ الْكَذِبَ يَوْمَ الْقِيَامَةِ إِنَّ اللَّهَ لَذُو فَضْلٍ عَلَى النَّاسِ وَلَكِنَّ أَكْثَرَهُمْ لا يَشْكُرُونَ}
INSERT INTO [b9617] VALUES (1388,'<span data-type="title" id=toc-1374>٦١ </span>- {وَمَا تَكُونُ فِي شَأْنٍ وَمَا تَتْلُو مِنْهُ مِنْ قُرْآنٍ وَلا تَعْمَلُونَ مِنْ عَمَلٍ إِلا كُنَّا عَلَيْكُمْ شُهُودًا إِذْ تُفِيضُونَ فِيهِ وَمَا يَعْزُبُ عَنْ رَبِّكَ مِنْ مِثْقَالِ ذَرَّةٍ فِي الأَرْضِ وَلا فِي السَّمَاءِ وَلا أَصْغَرَ مِنْ ذَلِكَ وَلا أَكْبَرَ إِلا فِي كِتَابٍ مُبِينٍ}
INSERT INTO [b9617] VALUES (1389,'<span data-type="title" id=toc-1375>٦٢ </span>- {أَلا إِنَّ أَوْلِيَاءَ اللَّهِ لا خَوْفٌ عَلَيْهِمْ وَلا هُمْ يَحْزَنُونَ}
INSERT INTO [b9617] VALUES (1390,'<span data-type="title" id=toc-1376>٦٣ </span>- {الَّذِينَ آمَنُوا وَكَانُوا يَتَّقُونَ}
INSERT INTO [b9617] VALUES (1391,'<span data-type="title" id=toc-1377>٦٤ </span>- {لَهُمُ الْبُشْرَى فِي الْحَيَاةِ الدُّنْيَا وَفِي الآخِرَةِ لا تَبْدِيلَ لِكَلِمَاتِ اللَّهِ ⦗٤٤٢⦘ ذَلِكَ هُوَ الْفَوْزُ الْعَظِيمُ}
INSERT INTO [b9617] VALUES (1392,'<span data-type="title" id=toc-1378>٦٥ </span>- {وَلا يَحْزُنْكَ قَوْلُهُمْ إِنَّ الْعِزَّةَ لِلَّهِ جَمِيعًا هُوَ السَّمِيعُ الْعَلِيمُ}
INSERT INTO [b9617] VALUES (1393,'<span data-type="title" id=toc-1379>٦٦ </span>- {أَلا إِنَّ لِلَّهِ مَنْ فِي السَّمَاوَاتِ وَمَنْ فِي الأَرْضِ وَمَا يَتَّبِعُ الَّذِينَ يَدْعُونَ مِنْ دُونِ اللَّهِ شُرَكَاءَ إِنْ يَتَّبِعُونَ إِلا الظَّنَّ وَإِنْ هُمْ إِلا يَخْرُصُونَ}
INSERT INTO [b9617] VALUES (1394,'<span data-type="title" id=toc-1380>٦٧ </span>- {هُوَ الَّذِي جَعَلَ لَكُمُ اللَّيْلَ لِتَسْكُنُوا فِيهِ وَالنَّهَارَ مُبْصِرًا إِنَّ فِي ذَلِكَ لآيَاتٍ لِقَوْمٍ يَسْمَعُونَ}
INSERT INTO [b9617] VALUES (1395,'<span data-type="title" id=toc-1381>٦٨ </span>- {قَالُوا اتَّخَذَ اللَّهُ وَلَدًا سُبْحَانَهُ هُوَ الْغَنِيُّ لَهُ مَا فِي السَّمَاوَاتِ وَمَا فِي الأَرْضِ إِنْ عِنْدَكُمْ مِنْ سُلْطَانٍ بِهَذَا أَتَقُولُونَ عَلَى اللَّهِ مَا لا تَعْلَمُونَ}
INSERT INTO [b9617] VALUES (1396,'<span data-type="title" id=toc-1382>٦٩ </span>- {إِنَّ الَّذِينَ يَفْتَرُونَ عَلَى اللَّهِ الْكَذِبَ لا يُفْلِحُونَ}
INSERT INTO [b9617] VALUES (1397,'<span data-type="title" id=toc-1383>٧٠ </span>- {مَتَاعٌ فِي الدُّنْيَا ثُمَّ إِلَيْنَا مَرْجِعُهُمْ ثُمَّ نُذِيقُهُمُ الْعَذَابَ الشَّدِيدَ بِمَا كَانُوا يَكْفُرُونَ}
INSERT INTO [b9617] VALUES (1398,'<span data-type="title" id=toc-1384>٧١ </span>- {وَاتْلُ عَلَيْهِمْ نَبَأَ نُوحٍ إِذْ قَالَ لِقَوْمِهِ يَا قَوْمِ إِنْ كَانَ كَبُرَ عَلَيْكُمْ مَقَامِي وَتَذْكِيرِي بِآيَاتِ اللَّهِ فَعَلَى اللَّهِ تَوَكَّلْتُ فَأَجْمِعُوا أَمْرَكُمْ وَشُرَكَاءَكُمْ}
INSERT INTO [b9617] VALUES (1399,'<span data-type="title" id=toc-1385>٧٢ </span>- {إِنْ أَجْرِيَ إِلا عَلَى اللَّهِ وَأُمِرْتُ أَنْ أَكُونَ مِنَ الْمُسْلِمِينَ}
INSERT INTO [b9617] VALUES (1400,'<span data-type="title" id=toc-1386>٧٣ </span>- {فَكَذَّبُوهُ فَنَجَّيْنَاهُ وَمَنْ مَعَهُ فِي الْفُلْكِ وَجَعَلْنَاهُمْ خَلائِفَ وَأَغْرَقْنَا الَّذِينَ كَذَّبُوا بِآيَاتِنَا فَانْظُرْ كَيْفَ كَانَ عَاقِبَةُ الْمُنْذَرِينَ} ⦗٤٤٥⦘
INSERT INTO [b9617] VALUES (1401,'<span data-type="title" id=toc-1387>٧٤ </span>- {ثُمَّ بَعَثْنَا مِنْ بَعْدِهِ رُسُلا إِلَى قَوْمِهِمْ فَجَاءُوهُمْ بِالْبَيِّنَاتِ فَمَا كَانُوا لِيُؤْمِنُوا بِمَا كَذَّبُوا بِهِ مِنْ قَبْلُ كَذَلِكَ نَطْبَعُ عَلَى قُلُوبِ الْمُعْتَدِينَ}
INSERT INTO [b9617] VALUES (1402,'<span data-type="title" id=toc-1388>٧٥ </span>- {ثُمَّ بَعَثْنَا مِنْ بَعْدِهِمْ مُوسَى وَهَارُونَ إِلَى فِرْعَوْنَ وَمَلَئِهِ بِآيَاتِنَا}
INSERT INTO [b9617] VALUES (1403,'<span data-type="title" id=toc-1389>٧٦ </span>- {فَلَمَّا جَاءَهُمُ الْحَقُّ مِنْ عِنْدِنَا قَالُوا إِنَّ هَذَا لَسِحْرٌ مُبِينٌ}
INSERT INTO [b9617] VALUES (1404,'<span data-type="title" id=toc-1390>٧٧ </span>- {قَالَ مُوسَى أَتَقُولُونَ لِلْحَقِّ لَمَّا جَاءَكُمْ أَسِحْرٌ هَذَا وَلا يُفْلِحُ السَّاحِرُونَ} ⦗٤٤٦⦘
INSERT INTO [b9617] VALUES (1405,'<span data-type="title" id=toc-1391>٧٨ </span>- {قَالُوا أَجِئْتَنَا لِتَلْفِتَنَا عَمَّا وَجَدْنَا عَلَيْهِ آبَاءَنَا وَتَكُونَ لَكُمَا الْكِبْرِيَاءُ فِي الأَرْضِ وَمَا نَحْنُ لَكُمَا بِمُؤْمِنِينَ}
INSERT INTO [b9617] VALUES (1406,'<span data-type="title" id=toc-1392>٨٠ </span>- {أَلْقُوا مَا أَنْتُمْ مُلْقُونَ}
INSERT INTO [b9617] VALUES (1407,'<span data-type="title" id=toc-1393>٨١ </span>- {فَلَمَّا أَلْقَوْا قَالَ مُوسَى مَا جِئْتُمْ بِهِ السِّحْرُ إِنَّ اللَّهَ سَيُبْطِلُهُ}
INSERT INTO [b9617] VALUES (1408,'<span data-type="title" id=toc-1394>٨٢ </span>- {وَيُحِقُّ اللَّهُ الْحَقَّ بِكَلِمَاتِهِ وَلَوْ كَرِهَ الْمُجْرِمُونَ}
INSERT INTO [b9617] VALUES (1409,'<span data-type="title" id=toc-1395>٨٣ </span>- {فَمَا آمَنَ لِمُوسَى إِلا ذُرِّيَّةٌ مِنْ قَوْمِهِ عَلَى خَوْفٍ مِنْ فِرْعَوْنَ وَمَلَئِهِمْ أَنْ يَفْتِنَهُمْ}
INSERT INTO [b9617] VALUES (1410,'<span data-type="title" id=toc-1396>٨٤ </span>- {إِنْ كُنْتُمْ مُسْلِمِينَ}
INSERT INTO [b9617] VALUES (1411,'<span data-type="title" id=toc-1397>٨٥ </span>- {فَقَالُوا عَلَى اللَّهِ تَوَكَّلْنَا رَبَّنَا لا تَجْعَلْنَا فِتْنَةً لِلْقَوْمِ الظَّالِمِينَ}
INSERT INTO [b9617] VALUES (1412,'<span data-type="title" id=toc-1398>٨٧ </span>- {وَأَوْحَيْنَا إِلَى مُوسَى وَأَخِيهِ أَنْ تَبَوَّآ لِقَوْمِكُمَا بِمِصْرَ بُيُوتًا وَاجْعَلُوا بُيُوتَكُمْ قِبْلَةً}
INSERT INTO [b9617] VALUES (1413,'<span data-type="title" id=toc-1399>٨٨ </span>- {وَقَالَ مُوسَى رَبَّنَا إِنَّكَ آتَيْتَ فِرْعَوْنَ وَمَلأَهُ زِينَةً وَأَمْوَالا فِي الْحَيَاةِ الدُّنْيَا رَبَّنَا لِيُضِلُّوا عَنْ سَبِيلِكَ رَبَّنَا اطْمِسْ عَلَى أَمْوَالِهِمْ وَاشْدُدْ عَلَى قُلُوبِهِمْ فَلا يُؤْمِنُوا حَتَّى يَرَوُا الْعَذَابَ الأَلِيمَ}
INSERT INTO [b9617] VALUES (1414,'<span data-type="title" id=toc-1400>٨٩ </span>- {قَالَ قَدْ أُجِيبَتْ دَعْوَتُكُمَا فَاسْتَقِيمَا وَلا تَتَّبِعَانِّ سَبِيلَ الَّذِينَ لا يَعْلَمُونَ}
INSERT INTO [b9617] VALUES (1415,'<span data-type="title" id=toc-1401>٩٠ </span>- {فَأَتْبَعَهُمْ فِرْعَوْنُ وَجُنُودُهُ بَغْيًا وَعَدْوًا حَتَّى إِذَا أَدْرَكَهُ الْغَرَقُ قَالَ آمَنْتُ أَنَّهُ لا إِلَهَ إِلا الَّذِي آمَنَتْ بِهِ بَنُو إِسْرَائِيلَ وَأَنَا مِنَ الْمُسْلِمِينَ}
INSERT INTO [b9617] VALUES (1416,'<span data-type="title" id=toc-1402>٩١ </span>- {آلآنَ وَقَدْ عَصَيْتَ قَبْلُ وَكُنْتَ مِنَ الْمُفْسِدِينَ}
INSERT INTO [b9617] VALUES (1417,'<span data-type="title" id=toc-1403>٩٢ </span>- {فَالْيَوْمَ نُنَجِّيكَ بِبَدَنِكَ لِتَكُونَ لِمَنْ خَلْفَكَ آيَةً وَإِنَّ كَثِيرًا مِنَ النَّاسِ عَنْ آيَاتِنَا لَغَافِلُونَ}
INSERT INTO [b9617] VALUES (1418,'<span data-type="title" id=toc-1404>٩٣ </span>- {وَلَقَدْ بَوَّأْنَا بَنِي إِسْرَائِيلَ مُبَوَّأَ صِدْقٍ وَرَزَقْنَاهُمْ مِنَ الطَّيِّبَاتِ فَمَا اخْتَلَفُوا حَتَّى جَاءَهُمُ الْعِلْمُ}
INSERT INTO [b9617] VALUES (1419,'<span data-type="title" id=toc-1405>٩٤ </span>- {فَإِنْ كُنْتَ فِي شَكٍّ مِمَّا أَنْزَلْنَا إِلَيْكَ فَاسْأَلِ الَّذِينَ يَقْرَءُونَ الْكِتَابَ مِنْ قَبْلِكَ لَقَدْ جَاءَكَ الْحَقُّ مِنْ رَبِّكَ فَلا تَكُونَنَّ مِنَ الْمُمْتَرِينَ}
INSERT INTO [b9617] VALUES (1420,'<span data-type="title" id=toc-1406>٩٥ </span>- {وَلا تَكُونَنَّ مِنَ الَّذِينَ كَذَّبُوا بِآيَاتِ اللَّهِ فَتَكُونَ مِنَ الْخَاسِرِينَ}
INSERT INTO [b9617] VALUES (1421,'<span data-type="title" id=toc-1407>٩٧ </span>- {وَلَوْ جَاءَتْهُمْ كُلُّ آيَةٍ}
INSERT INTO [b9617] VALUES (1422,'<span data-type="title" id=toc-1408>٩٨ </span>- {فَلَوْلا كَانَتْ قَرْيَةٌ آمَنَتْ فَنَفَعَهَا إِيمَانُهَا إِلا قَوْمَ يُونُسَ لَمَّا آمَنُوا كَشَفْنَا عَنْهُمْ عَذَابَ الْخِزْيِ فِي الْحَيَاةِ الدُّنْيَا}
INSERT INTO [b9617] VALUES (1423,'<span data-type="title" id=toc-1409>٩٩ </span>- {وَلَوْ شَاءَ رَبُّكَ لآمَنَ مَنْ فِي الأَرْضِ كُلُّهُمْ جَمِيعًا أَفَأَنْتَ تُكْرِهُ النَّاسَ حَتَّى يَكُونُوا مُؤْمِنِينَ}
INSERT INTO [b9617] VALUES (1424,'<span data-type="title" id=toc-1410>١٠٠ </span>- {وَمَا كَانَ لِنَفْسٍ أَنْ تُؤْمِنَ إِلا بِإِذْنِ اللَّهِ وَيَجْعَلُ الرِّجْسَ عَلَى الَّذِينَ لا يَعْقِلُونَ} ⦗٤٥١⦘
INSERT INTO [b9617] VALUES (1425,'<span data-type="title" id=toc-1411>١٠١ </span>- {قُلِ انْظُرُوا مَاذَا فِي السَّمَاوَاتِ وَالأَرْضِ وَمَا تُغْنِي الآيَاتُ وَالنُّذُرُ عَنْ قَوْمٍ لا يُؤْمِنُونَ}
INSERT INTO [b9617] VALUES (1426,'<span data-type="title" id=toc-1412>١٠٢ </span>- {فَهَلْ يَنْتَظِرُونَ إِلا مِثْلَ أَيَّامِ الَّذِينَ خَلَوْا مِنْ قَبْلِهِمْ قُلْ فَانْتَظِرُوا إِنِّي مَعَكُمْ مِنَ الْمُنْتَظِرِينَ}
INSERT INTO [b9617] VALUES (1427,'<span data-type="title" id=toc-1413>١٠٣ </span>- {ثُمَّ نُنَجِّي رُسُلَنَا وَالَّذِينَ آمَنُوا كَذَلِكَ حَقًّا عَلَيْنَا نُنْجِ الْمُؤْمِنِينَ}
INSERT INTO [b9617] VALUES (1428,'<span data-type="title" id=toc-1414>١٠٤ </span>- {قُلْ يَا أَيُّهَا النَّاسُ إِنْ كُنْتُمْ فِي شَكٍّ مِنْ دِينِي فَلا أَعْبُدُ الَّذِينَ تَعْبُدُونَ مِنْ دُونِ اللَّهِ وَلَكِنْ أَعْبُدُ اللَّهَ الَّذِي يَتَوَفَّاكُمْ وَأُمِرْتُ أَنْ أَكُونَ مِنَ الْمُؤْمِنِينَ}
INSERT INTO [b9617] VALUES (1429,'<span data-type="title" id=toc-1415>١٠٥ </span>- {وَأَنْ أَقِمْ وَجْهَكَ لِلدِّينِ حَنِيفًا}
INSERT INTO [b9617] VALUES (1430,'<span data-type="title" id=toc-1416>١٠٦ </span>- {وَلا تَدْعُ مِنْ دُونِ اللَّهِ مَا لا يَنْفَعُكَ وَلا يَضُرُّكَ فَإِنْ فَعَلْتَ فَإِنَّكَ إِذًا مِنَ الظَّالِمِينَ}
INSERT INTO [b9617] VALUES (1431,'<span data-type="title" id=toc-1417>١٠٧ </span>- {وَإِنْ يَمْسَسْكَ اللَّهُ بِضُرٍّ فَلا كَاشِفَ لَهُ إِلا هُوَ وَإِنْ يُرِدْكَ بِخَيْرٍ فَلا رَادَّ لِفَضْلِهِ يُصِيبُ بِهِ مَنْ يَشَاءُ مِنْ عِبَادِهِ وَهُوَ الْغَفُورُ الرَّحِيمُ}
INSERT INTO [b9617] VALUES (1432,'<span data-type="title" id=toc-1418>١٠٨ </span>- {قُلْ يَا أَيُّهَا النَّاسُ قَدْ جَاءَكُمُ الْحَقُّ مِنْ رَبِّكُمْ فَمَنِ اهْتَدَى فَإِنَّمَا يَهْتَدِي لِنَفْسِهِ وَمَنْ ضَلَّ فَإِنَّمَا يَضِلُّ عَلَيْهَا وَمَا أَنَا عَلَيْكُمْ بِوَكِيلٍ}
INSERT INTO [b9617] VALUES (1433,'<span data-type="title" id=toc-1419>١٠٩ </span>- {وَهُوَ خَيْرُ الْحَاكِمِينَ}
INSERT INTO [b9617] VALUES (1434,'<span data-type="title" id=toc-1420>سورة هود</span>','2','454',NULL,NULL,'0');
INSERT INTO [b9617] VALUES (1435,'<span data-type="title" id=toc-1421>١ </span>- {الر كِتَابٌ أُحْكِمَتْ آيَاتُهُ ثُمَّ فُصِّلَتْ مِنْ لَدُنْ حَكِيمٍ خَبِيرٍ}
INSERT INTO [b9617] VALUES (1436,'<span data-type="title" id=toc-1422>٢ </span>- {أَلا تَعْبُدُوا إِلا اللَّهَ إِنَّنِي لَكُمْ مِنْهُ نَذِيرٌ وَبَشِيرٌ}
INSERT INTO [b9617] VALUES (1437,'<span data-type="title" id=toc-1423>٣ </span>- {وَأَنِ اسْتَغْفِرُوا رَبَّكُمْ ثُمَّ تُوبُوا إِلَيْهِ يُمَتِّعْكُمْ مَتَاعًا حَسَنًا إِلَى أَجَلٍ مُسَمًّى وَيُؤْتِ كُلَّ ذِي فَضْلٍ فَضْلَهُ}
INSERT INTO [b9617] VALUES (1438,'<span data-type="title" id=toc-1424>٤ </span>- {إِلَى اللَّهِ مَرْجِعُكُمْ وَهُوَ عَلَى كُلِّ شَيْءٍ قَدِيرٌ}
INSERT INTO [b9617] VALUES (1439,'<span data-type="title" id=toc-1425>٥ </span>- {أَلا حِينَ يَسْتَغْشُونَ ثِيَابَهُمْ يَعْلَمُ مَا ⦗٤٥٥⦘ يُسِرُّونَ وَمَا يُعْلِنُونَ}
INSERT INTO [b9617] VALUES (1440,'<span data-type="title" id=toc-1426>٦ </span>- {وَمَا مِنْ دَابَّةٍ فِي الأَرْضِ إِلا عَلَى اللَّهِ رِزْقُهَا وَيَعْلَمُ مُسْتَقَرَّهَا وَمُسْتَوْدَعَهَا كُلٌّ فِي كِتَابٍ مُبِينٍ}
INSERT INTO [b9617] VALUES (1441,'<span data-type="title" id=toc-1427>٧ </span>- {وَهُوَ الَّذِي خَلَقَ السَّمَاوَاتِ وَالأَرْضَ فِي سِتَّةِ أَيَّامٍ وَكَانَ عَرْشُهُ عَلَى الْمَاءِ لِيَبْلُوَكُمْ أَيُّكُمْ أَحْسَنُ عَمَلا وَلَئِنْ قُلْتَ إِنَّكُمْ مَبْعُوثُونَ مِنْ بَعْدِ الْمَوْتِ لَيَقُولَنَّ الَّذِينَ كَفَرُوا إِنْ هَذَا إِلا سِحْرٌ مُبِينٌ}
INSERT INTO [b9617] VALUES (1442,'<span data-type="title" id=toc-1428>٨ </span>- {وَلَئِنْ أَخَّرْنَا عَنْهُمُ الْعَذَابَ إِلَى أُمَّةٍ مَعْدُودَةٍ لَيَقُولُنَّ مَا يَحْبِسُهُ أَلا يَوْمَ يَأْتِيهِمْ لَيْسَ مَصْرُوفًا عَنْهُمْ وَحَاقَ بِهِمْ مَا كَانُوا بِهِ يَسْتَهْزِئُونَ}
INSERT INTO [b9617] VALUES (1443,'<span data-type="title" id=toc-1429>٩ </span>- {وَلَئِنْ أَذَقْنَا الإِنْسَانَ مِنَّا رَحْمَةً ثُمَّ نَزَعْنَاهَا مِنْهُ إِنَّهُ لَيَئُوسٌ كَفُورٌ}
INSERT INTO [b9617] VALUES (1444,'<span data-type="title" id=toc-1430>١٠ </span>- {وَلَئِنْ أَذَقْنَاهُ نَعْمَاءَ بَعْدَ ضَرَّاءَ مَسَّتْهُ لَيَقُولَنَّ ذَهَبَ السَّيِّئَاتُ عَنِّي إِنَّهُ لَفَرِحٌ فَخُورٌ}
INSERT INTO [b9617] VALUES (1445,'<span data-type="title" id=toc-1431>١١ </span>- {إِلا الَّذِينَ صَبَرُوا وَعَمِلُوا الصَّالِحَاتِ أُولَئِكَ لَهُمْ مَغْفِرَةٌ}
INSERT INTO [b9617] VALUES (1446,'<span data-type="title" id=toc-1432>١٢ </span>- {فَلَعَلَّكَ تَارِكٌ بَعْضَ مَا يُوحَى إِلَيْكَ وَضَائِقٌ بِهِ صَدْرُكَ أَنْ يَقُولُوا لَوْلا أُنْزِلَ عَلَيْهِ كَنْزٌ أَوْ جَاءَ مَعَهُ مَلَكٌ إِنَّمَا أَنْتَ نَذِيرٌ وَاللَّهُ عَلَى كُلِّ شَيْءٍ وَكِيلٌ}
INSERT INTO [b9617] VALUES (1447,'<span data-type="title" id=toc-1433>١٣ </span>- {أَمْ يَقُولُونَ افْتَرَاهُ قُلْ فَأْتُوا بِعَشْرِ سُوَرٍ مِثْلِهِ مُفْتَرَيَاتٍ وَادْعُوا مَنِ اسْتَطَعْتُمْ مِنْ دُونِ اللَّهِ إِنْ كُنْتُمْ صَادِقِينَ}
INSERT INTO [b9617] VALUES (1448,'<span data-type="title" id=toc-1434>١٤ </span>- {فَإِلَّمْ يَسْتَجِيبُوا لَكُمْ فَاعْلَمُوا أَنَّمَا أُنْزِلَ بِعِلْمِ اللَّهِ وَأَنْ لا إِلَهَ إِلا هُوَ فَهَلْ أَنْتُمْ مُسْلِمُونَ}
INSERT INTO [b9617] VALUES (1449,'<span data-type="title" id=toc-1435>١٥ </span>- {مَنْ كَانَ يُرِيدُ الْحَيَاةَ الدُّنْيَا وَزِينَتَهَا نُوَفِّ إِلَيْهِمْ أَعْمَالَهُمْ فِيهَا وَهُمْ فِيهَا لا يُبْخَسُونَ}
INSERT INTO [b9617] VALUES (1450,'<span data-type="title" id=toc-1436>١٦ </span>- {أُولَئِكَ الَّذِينَ لَيْسَ لَهُمْ فِي الآخِرَةِ إِلا النَّارُ وَحَبِطَ مَا صَنَعُوا فِيهَا وَبَاطِلٌ مَا كَانُوا يَعْمَلُونَ}
INSERT INTO [b9617] VALUES (1451,'<span data-type="title" id=toc-1437>١٧ </span>- {أَفَمَنْ كَانَ عَلَى بَيِّنَةٍ مِنْ رَبِّهِ وَيَتْلُوهُ شَاهِدٌ مِنْهُ وَمِنْ قَبْلِهِ كِتَابُ مُوسَى إِمَامًا وَرَحْمَةً أُولَئِكَ يُؤْمِنُونَ بِهِ وَمَنْ يَكْفُرْ بِهِ مِنَ الأَحْزَابِ فَالنَّارُ مَوْعِدُهُ فَلا تَكُ فِي مِرْيَةٍ مِنْهُ إِنَّهُ الْحَقُّ مِنْ رَبِّكَ وَلَكِنَّ أَكْثَرَ النَّاسِ لا يُؤْمِنُونَ}
INSERT INTO [b9617] VALUES (1452,'<span data-type="title" id=toc-1438>١٨ </span>- {وَمَنْ أَظْلَمُ مِمَّنِ افْتَرَى عَلَى اللَّهِ كَذِبًا أُولَئِكَ يُعْرَضُونَ عَلَى رَبِّهِمْ وَيَقُولُ الأَشْهَادُ هَؤُلاءِ الَّذِينَ كَذَبُوا عَلَى رَبِّهِمْ أَلا لَعْنَةُ اللَّهِ عَلَى الظَّالِمِينَ}
INSERT INTO [b9617] VALUES (1453,'<span data-type="title" id=toc-1439>١٩ </span>- {الَّذِينَ يَصُدُّونَ عَنْ سَبِيلِ اللَّهِ وَيَبْغُونَهَا عِوَجًا وَهُمْ بِالآخِرَةِ هُمْ كَافِرُونَ}
INSERT INTO [b9617] VALUES (1454,'<span data-type="title" id=toc-1440>٢٠ </span>- {أُولَئِكَ لَمْ يَكُونُوا مُعْجِزِينَ فِي الأَرْضِ وَمَا كَانَ لَهُمْ مِنْ دُونِ اللَّهِ مِنْ أَوْلِيَاءَ يُضَاعَفُ لَهُمُ الْعَذَابُ مَا كَانُوا يَسْتَطِيعُونَ السَّمْعَ وَمَا كَانُوا يُبْصِرُونَ}
INSERT INTO [b9617] VALUES (1455,'<span data-type="title" id=toc-1441>٢١ </span>- {أُولَئِكَ الَّذِينَ خَسِرُوا أَنْفُسَهُمْ وَضَلَّ عَنْهُمْ مَا كَانُوا يَفْتَرُونَ}
INSERT INTO [b9617] VALUES (1456,'<span data-type="title" id=toc-1442>٢٢ </span>- {لا جَرَمَ أَنَّهُمْ فِي الآخِرَةِ هُمُ الأَخْسَرُونَ} ⦗٤٦١⦘
INSERT INTO [b9617] VALUES (1457,'<span data-type="title" id=toc-1443>٢٣ </span>- {إِنَّ الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ وَأَخْبَتُوا إِلَى رَبِّهِمْ أُولَئِكَ أَصْحَابُ الْجَنَّةِ هُمْ فِيهَا خَالِدُونَ}
INSERT INTO [b9617] VALUES (1458,'<span data-type="title" id=toc-1444>٢٤ </span>- {هَلْ يَسْتَوِيَانِ مَثَلا أَفَلا تَذَكَّرُونَ}
INSERT INTO [b9617] VALUES (1459,'<span data-type="title" id=toc-1445>٢٥ </span>- {وَلَقَدْ أَرْسَلْنَا نُوحًا إِلَى قَوْمِهِ إِنِّي لَكُمْ نَذِيرٌ مُبِينٌ}
INSERT INTO [b9617] VALUES (1460,'<span data-type="title" id=toc-1446>٢٦ </span>- {أَنْ لا تَعْبُدُوا إِلا اللَّهَ إِنِّي أَخَافُ عَلَيْكُمْ عَذَابَ يَوْمٍ أَلِيمٍ}
INSERT INTO [b9617] VALUES (1461,'<span data-type="title" id=toc-1447>٢٧ </span>- {فَقَالَ الْمَلأُ الَّذِينَ كَفَرُوا مِنْ قَوْمِهِ مَا نَرَاكَ إِلا بَشَرًا مِثْلَنَا وَمَا نَرَاكَ اتَّبَعَكَ إِلا الَّذِينَ هُمْ أَرَاذِلُنَا بَادِيَ الرَّأْيِ وَمَا نَرَى لَكُمْ عَلَيْنَا مِنْ فَضْلٍ بَلْ نَظُنُّكُمْ كَاذِبِينَ}
INSERT INTO [b9617] VALUES (1462,'<span data-type="title" id=toc-1448>٢٨ </span>- {قَالَ يَا قَوْمِ أَرَأَيْتُمْ إِنْ كُنْتُ عَلَى بَيِّنَةٍ مِنْ رَبِّي وَآتَانِي رَحْمَةً مِنْ عِنْدِهِ فَعُمِّيَتْ عَلَيْكُمْ أَنُلْزِمُكُمُوهَا وَأَنْتُمْ لَهَا كَارِهُونَ}
INSERT INTO [b9617] VALUES (1463,'<span data-type="title" id=toc-1449>٢٩ </span>- {وَيَا قَوْمِ لا أَسْأَلُكُمْ عَلَيْهِ مَالا إِنْ أَجْرِيَ إِلا عَلَى اللَّهِ وَمَا أَنَا بِطَارِدِ الَّذِينَ آمَنُوا إِنَّهُمْ مُلاقُو رَبِّهِمْ وَلَكِنِّي أَرَاكُمْ قَوْمًا تَجْهَلُونَ}
INSERT INTO [b9617] VALUES (1464,'<span data-type="title" id=toc-1450>٣٠ </span>- {وَيَا قَوْمِ مَنْ يَنْصُرُنِي مِنَ اللَّهِ إِنْ طَرَدْتُهُمْ أَفَلا تَذَكَّرُونَ}
INSERT INTO [b9617] VALUES (1465,'<span data-type="title" id=toc-1451>٣١ </span>- {وَلا أَقُولُ لَكُمْ عِنْدِي خَزَائِنُ اللَّهِ وَلا أَعْلَمُ الْغَيْبَ وَلا أَقُولُ إِنِّي مَلَكٌ وَلا أَقُولُ لِلَّذِينَ تَزْدَرِي أَعْيُنُكُمْ لَنْ يُؤْتِيَهُمُ اللَّهُ خَيْرًا اللَّهُ أَعْلَمُ بِمَا فِي أَنْفُسِهِمْ إِنِّي إِذًا لَمِنَ الظَّالِمِينَ}
INSERT INTO [b9617] VALUES (1466,'<span data-type="title" id=toc-1452>٣٢ </span>- {فَأْتِنَا بِمَا تَعِدُنَا إِنْ كُنْتَ مِنَ الصَّادِقِينَ}
INSERT INTO [b9617] VALUES (1467,'<span data-type="title" id=toc-1453>٣٣ </span>- {قَالَ إِنَّمَا يَأْتِيكُمْ بِهِ اللَّهُ إِنْ شَاءَ وَمَا أَنْتُمْ بِمُعْجِزِينَ} ⦗٤٦٤⦘
INSERT INTO [b9617] VALUES (1468,'<span data-type="title" id=toc-1454>٣٤ </span>- {وَلا يَنْفَعُكُمْ نُصْحِي إِنْ أَرَدْتُ أَنْ أَنْصَحَ لَكُمْ إِنْ كَانَ اللَّهُ يُرِيدُ أَنْ يُغْوِيَكُمْ هُوَ رَبُّكُمْ وَإِلَيْهِ تُرْجَعُونَ}
INSERT INTO [b9617] VALUES (1469,'<span data-type="title" id=toc-1455>٣٥ </span>- {أَمْ يَقُولُونَ افْتَرَاهُ قُلْ إِنِ افْتَرَيْتُهُ فَعَلَيَّ إِجْرَامِي وَأَنَا بَرِيءٌ مِمَّا تُجْرِمُونَ}
INSERT INTO [b9617] VALUES (1470,'<span data-type="title" id=toc-1456>٣٦ </span>- {وَأُوحِيَ إِلَى نُوحٍ أَنَّهُ لَنْ يُؤْمِنَ مِنْ قَوْمِكَ إِلا مَنْ قَدْ آمَنَ فَلا تَبْتَئِسْ بِمَا كَانُوا يَفْعَلُونَ}
INSERT INTO [b9617] VALUES (1471,'<span data-type="title" id=toc-1457>٣٧ </span>- {وَاصْنَعِ الْفُلْكَ بِأَعْيُنِنَا وَوَحْيِنَا وَلا تُخَاطِبْنِي فِي الَّذِينَ ظَلَمُوا إِنَّهُمْ مُغْرَقُونَ}
INSERT INTO [b9617] VALUES (1472,'<span data-type="title" id=toc-1458>٣٨ </span>- {وَيَصْنَعُ الْفُلْكَ وَكُلَّمَا مَرَّ عَلَيْهِ مَلأٌ مِنْ قَوْمِهِ سَخِرُوا مِنْهُ قَالَ إِنْ تَسْخَرُوا مِنَّا فَإِنَّا نَسْخَرُ مِنْكُمْ كَمَا تَسْخَرُونَ}
INSERT INTO [b9617] VALUES (1473,'<span data-type="title" id=toc-1459>٣٩ </span>- {فَسَوْفَ تَعْلَمُونَ مَنْ يَأْتِيهِ عَذَابٌ يُخْزِيهِ}
INSERT INTO [b9617] VALUES (1474,'<span data-type="title" id=toc-1460>٤٠ </span>- {حَتَّى إِذَا جَاءَ أَمْرُنَا وَفَارَ التَّنُّورُ قُلْنَا احْمِلْ فِيهَا مِنْ كُلٍّ زَوْجَيْنِ اثْنَيْنِ وَأَهْلَكَ إِلا مَنْ سَبَقَ عَلَيْهِ الْقَوْلُ وَمَنْ آمَنَ وَمَا آمَنَ مَعَهُ إِلا قَلِيلٌ} ⦗٤٦٦⦘
INSERT INTO [b9617] VALUES (1475,'<span data-type="title" id=toc-1461>٤١ </span>- {وَقَالَ ارْكَبُوا فِيهَا بِسْمِ اللَّهِ مَجْرَاهَا وَمُرْسَاهَا}
INSERT INTO [b9617] VALUES (1476,'<span data-type="title" id=toc-1462>٤٢ </span>- {وَهِيَ تَجْرِي بِهِمْ فِي مَوْجٍ كَالْجِبَالِ وَنَادَى نُوحٌ ابْنَهُ وَكَانَ فِي مَعْزِلٍ يَا بُنَيَّ ارْكَبْ مَعَنَا}
INSERT INTO [b9617] VALUES (1477,'<span data-type="title" id=toc-1463>٤٣ </span>- {قَالَ سَآوِي إِلَى جَبَلٍ يَعْصِمُنِي مِنَ الْمَاءِ قَالَ لا عَاصِمَ الْيَوْمَ مِنْ أَمْرِ اللَّهِ إِلا مَنْ ⦗٤٦٧⦘ رَحِمَ وَحَالَ بَيْنَهُمَا الْمَوْجُ فَكَانَ مِنَ الْمُغْرَقِينَ}
INSERT INTO [b9617] VALUES (1478,'<span data-type="title" id=toc-1464>٤٤ </span>- {وَقِيلَ يَا أَرْضُ ابْلَعِي مَاءَكِ وَيَا سَمَاءُ أَقْلِعِي وَغِيضَ الْمَاءُ وَقُضِيَ الأَمْرُ وَاسْتَوَتْ عَلَى الْجُودِيِّ وَقِيلَ بُعْدًا لِلْقَوْمِ الظَّالِمِينَ}
INSERT INTO [b9617] VALUES (1479,'<span data-type="title" id=toc-1465>٤٥ </span>- {وَنَادَى نُوحٌ رَبَّهُ فَقَالَ رَبِّ إِنَّ ابْنِي مِنْ أَهْلِي وَإِنَّ وَعْدَكَ الْحَقُّ وَأَنْتَ أَحْكَمُ الْحَاكِمِينَ}
INSERT INTO [b9617] VALUES (1480,'<span data-type="title" id=toc-1466>٤٦ </span>- {قَالَ يَا نُوحُ إِنَّهُ لَيْسَ مِنْ أَهْلِكَ إِنَّهُ عَمَلٌ غَيْرُ صَالِحٍ فَلا تَسْأَلْنِ مَا لَيْسَ لَكَ بِهِ عِلْمٌ إِنِّي أَعِظُكَ أَنْ تَكُونَ مِنَ الْجَاهِلِينَ} ⦗٤٦٨⦘
INSERT INTO [b9617] VALUES (1481,'<span data-type="title" id=toc-1467>٤٧ </span>- {إِنِّي أَعُوذُ بِكَ أَنْ أَسْأَلَكَ مَا لَيْسَ لِي بِهِ عِلْمٌ}
INSERT INTO [b9617] VALUES (1482,'<span data-type="title" id=toc-1468>٤٨ </span>- {قِيلَ يَا نُوحُ اهْبِطْ بِسَلامٍ مِنَّا وَبَرَكَاتٍ عَلَيْكَ وَعَلَى أُمَمٍ مِمَّنْ مَعَكَ وَأُمَمٌ سَنُمَتِّعُهُمْ ثُمَّ يَمَسُّهُمْ مِنَّا عَذَابٌ أَلِيمٌ}
INSERT INTO [b9617] VALUES (1483,'<span data-type="title" id=toc-1469>٤٩ </span>- {تِلْكَ مِنْ أَنْبَاءِ الْغَيْبِ نُوحِيهَا إِلَيْكَ مَا كُنْتَ تَعْلَمُهَا أَنْتَ وَلا قَوْمُكَ مِنْ ⦗٤٦٩⦘ قَبْلِ هَذَا فَاصْبِرْ إِنَّ الْعَاقِبَةَ لِلْمُتَّقِينَ}
INSERT INTO [b9617] VALUES (1484,'<span data-type="title" id=toc-1470>٥٠ </span>- {وَإِلَى عَادٍ أَخَاهُمْ هُودًا قَالَ يَا قَوْمِ اعْبُدُوا اللَّهَ مَا لَكُمْ مِنْ إِلَهٍ غَيْرُهُ إِنْ أَنْتُمْ إِلا مُفْتَرُونَ}
INSERT INTO [b9617] VALUES (1485,'<span data-type="title" id=toc-1471>٥١ </span>- {يَا قَوْمِ لا أَسْأَلُكُمْ عَلَيْهِ أَجْرًا إِنْ أَجْرِيَ إِلا عَلَى الَّذِي فَطَرَنِي أَفَلا تَعْقِلُونَ}
INSERT INTO [b9617] VALUES (1486,'<span data-type="title" id=toc-1472>٥٢ </span>- {وَيَا قَوْمِ اسْتَغْفِرُوا رَبَّكُمْ ثُمَّ تُوبُوا إِلَيْهِ يُرْسِلِ السَّمَاءَ عَلَيْكُمْ مِدْرَارًا ⦗٤٧٠⦘ وَيَزِدْكُمْ قُوَّةً إِلَى قُوَّتِكُمْ وَلا تَتَوَلَّوْا مُجْرِمِينَ}
INSERT INTO [b9617] VALUES (1487,'<span data-type="title" id=toc-1473>٥٣ </span>- {قَالُوا يَا هُودُ مَا جِئْتَنَا بِبَيِّنَةٍ وَمَا نَحْنُ بِتَارِكِي آلِهَتِنَا عَنْ قَوْلِكَ وَمَا نَحْنُ لَكَ بِمُؤْمِنِينَ}
INSERT INTO [b9617] VALUES (1488,'<span data-type="title" id=toc-1474>٥٤ </span>- {إِنْ نَقُولُ إِلا اعْتَرَاكَ بَعْضُ آلِهَتِنَا بِسُوءٍ قَالَ إِنِّي أُشْهِدُ اللَّهَ وَاشْهَدُوا أَنِّي بَرِيءٌ مِمَّا تُشْرِكُونَ}
INSERT INTO [b9617] VALUES (1489,'<span data-type="title" id=toc-1475>٥٥ </span>- {مِنْ دُونِهِ فَكِيدُونِي جَمِيعًا ثُمَّ لا تُنْظِرُونِ}
INSERT INTO [b9617] VALUES (1490,'<span data-type="title" id=toc-1476>٥٦ </span>- {إِنِّي تَوَكَّلْتُ عَلَى اللَّهِ رَبِّي وَرَبِّكُمْ مَا مِنْ دَابَّةٍ إِلا هُوَ آخِذٌ بِنَاصِيَتِهَا}
INSERT INTO [b9617] VALUES (1491,'<span data-type="title" id=toc-1477>٥٧ </span>- {فَإِنْ تَوَلَّوْا فَقَدْ أَبْلَغْتُكُمْ مَا أُرْسِلْتُ بِهِ إِلَيْكُمْ وَيَسْتَخْلِفُ رَبِّي قَوْمًا غَيْرَكُمْ وَلا تَضُرُّونَهُ شَيْئًا}
INSERT INTO [b9617] VALUES (1492,'<span data-type="title" id=toc-1478>٥٨ </span>- {وَلَمَّا جَاءَ أَمْرُنَا نَجَّيْنَا هُودًا وَالَّذِينَ آمَنُوا مَعَهُ بِرَحْمَةٍ مِنَّا وَنَجَّيْنَاهُمْ مِنْ عَذَابٍ غَلِيظٍ}
INSERT INTO [b9617] VALUES (1493,'<span data-type="title" id=toc-1479>٥٩ </span>- {وَتِلْكَ عَادٌ جَحَدُوا بِآيَاتِ رَبِّهِمْ وَعَصَوْا رُسُلَهُ وَاتَّبَعُوا أَمْرَ كُلِّ جَبَّارٍ عَنِيدٍ}
INSERT INTO [b9617] VALUES (1494,'<span data-type="title" id=toc-1480>٦٠ </span>- {وَأُتْبِعُوا فِي هَذِهِ الدُّنْيَا لَعْنَةً وَيَوْمَ الْقِيَامَةِ أَلا إِنَّ عَادًا كَفَرُوا رَبَّهُمْ أَلا بُعْدًا لِعَادٍ قَوْمِ هُودٍ}
INSERT INTO [b9617] VALUES (1495,'<span data-type="title" id=toc-1481>٦١ </span>- {وَإِلَى ثَمُودَ أَخَاهُمْ صَالِحًا قَالَ يَا قَوْمِ اعْبُدُوا اللَّهَ مَا لَكُمْ مِنْ إِلَهٍ غَيْرُهُ هُوَ أَنْشَأَكُمْ مِنَ الأَرْضِ وَاسْتَعْمَرَكُمْ فِيهَا فَاسْتَغْفِرُوهُ}
INSERT INTO [b9617] VALUES (1496,'<span data-type="title" id=toc-1482>٦٢ </span>- {قَالُوا يَا صَالِحُ قَدْ كُنْتَ فِينَا مَرْجُوًّا قَبْلَ هَذَا أَتَنْهَانَا أَنْ نَعْبُدَ مَا يَعْبُدُ آبَاؤُنَا وَإِنَّنَا لَفِي شَكٍّ مِمَّا تَدْعُونَا إِلَيْهِ مُرِيبٍ}
INSERT INTO [b9617] VALUES (1497,'<span data-type="title" id=toc-1483>٦٣ </span>- {أَرَأَيْتُمْ إِنْ كُنْتُ عَلَى بَيِّنَةٍ مِنْ رَبِّي وَآتَانِي مِنْهُ رَحْمَةً فَمَنْ يَنْصُرُنِي مِنَ اللَّهِ إِنْ عَصَيْتُهُ فَمَا تَزِيدُونَنِي غَيْرَ تَخْسِيرٍ}
INSERT INTO [b9617] VALUES (1498,'<span data-type="title" id=toc-1484>٦٤ </span>- {وَيَا قَوْمِ هَذِهِ نَاقَةُ اللَّهِ لَكُمْ آيَةً فَذَرُوهَا تَأْكُلْ فِي أَرْضِ اللَّهِ وَلا تَمَسُّوهَا بِسُوءٍ فَيَأْخُذَكُمْ عَذَابٌ قَرِيبٌ} ⦗٤٧٤⦘
INSERT INTO [b9617] VALUES (1499,'<span data-type="title" id=toc-1485>٦٥ </span>- {فَعَقَرُوهَا فَقَالَ تَمَتَّعُوا فِي دَارِكُمْ ثَلاثَةَ أَيَّامٍ ذَلِكَ وَعْدٌ غَيْرُ مَكْذُوبٍ}
INSERT INTO [b9617] VALUES (1500,'<span data-type="title" id=toc-1486>٦٦ </span>- {فَلَمَّا جَاءَ أَمْرُنَا نَجَّيْنَا صَالِحًا وَالَّذِينَ آمَنُوا مَعَهُ بِرَحْمَةٍ مِنَّا وَمِنْ خِزْيِ يَوْمِئِذٍ إِنَّ رَبَّكَ هُوَ الْقَوِيُّ الْعَزِيزُ}
INSERT INTO [b9617] VALUES (1501,'<span data-type="title" id=toc-1487>٦٧ </span>- {وَأَخَذَ الَّذِينَ ظَلَمُوا الصَّيْحَةُ فَأَصْبَحُوا فِي دِيَارِهِمْ جَاثِمِينَ} ⦗٤٧٥⦘
INSERT INTO [b9617] VALUES (1502,'<span data-type="title" id=toc-1488>٦٨ </span>- {كَأَنْ لَمْ يَغْنَوْا فِيهَا أَلا إِنَّ ثَمُودَ كَفَرُوا رَبَّهُمْ أَلا بُعْدًا لِثَمُودَ}
INSERT INTO [b9617] VALUES (1503,'<span data-type="title" id=toc-1489>٦٩ </span>- {وَلَقَدْ جَاءَتْ رُسُلُنَا إِبْرَاهِيمَ بِالْبُشْرَى قَالُوا سَلامًا قَالَ سَلامٌ فَمَا لَبِثَ أَنْ جَاءَ بِعِجْلٍ حَنِيذٍ}
INSERT INTO [b9617] VALUES (1504,'<span data-type="title" id=toc-1490>٧٠ </span>- {فَلَمَّا رَأَى أَيْدِيَهُمْ لا تَصِلُ إِلَيْهِ نَكِرَهُمْ وَأَوْجَسَ مِنْهُمْ خِيفَةً قَالُوا لا تَخَفْ إِنَّا أُرْسِلْنَا إِلَى قَوْمِ لُوطٍ}
INSERT INTO [b9617] VALUES (1505,'<span data-type="title" id=toc-1491>٧١ </span>- {وَامْرَأَتُهُ قَائِمَةٌ فَضَحِكَتْ فَبَشَّرْنَاهَا بِإِسْحَاقَ وَمِنْ وَرَاءِ إِسْحَاقَ يَعْقُوبَ}
INSERT INTO [b9617] VALUES (1506,'<span data-type="title" id=toc-1492>٧٢ </span>- {قَالَتْ يَا وَيْلَتَى أَأَلِدُ وَأَنَا عَجُوزٌ وَهَذَا بَعْلِي شَيْخًا}
INSERT INTO [b9617] VALUES (1507,'<span data-type="title" id=toc-1493>٧٣ </span>- {قَالُوا أَتَعْجَبِينَ مِنْ أَمْرِ اللَّهِ رَحْمَتُ اللَّهِ وَبَرَكَاتُهُ عَلَيْكُمْ أَهْلَ الْبَيْتِ إِنَّهُ حَمِيدٌ مَجِيدٌ}
INSERT INTO [b9617] VALUES (1508,'<span data-type="title" id=toc-1494>٧٤ </span>- {فَلَمَّا ذَهَبَ عَنْ إِبْرَاهِيمَ الرَّوْعُ وَجَاءَتْهُ الْبُشْرَى يُجَادِلُنَا فِي قَوْمِ لُوطٍ} ⦗٤٧٧⦘
INSERT INTO [b9617] VALUES (1509,'<span data-type="title" id=toc-1495>٧٥ </span>- {إِنَّ إِبْرَاهِيمَ لَحَلِيمٌ أَوَّاهٌ مُنِيبٌ}
INSERT INTO [b9617] VALUES (1510,'<span data-type="title" id=toc-1496>٧٦ </span>- {يَا إِبْرَاهِيمُ أَعْرِضْ عَنْ هَذَا إِنَّهُ قَدْ جَاءَ أَمْرُ رَبِّكَ وَإِنَّهُمْ آتِيهِمْ عَذَابٌ غَيْرُ مَرْدُودٍ}
INSERT INTO [b9617] VALUES (1511,'<span data-type="title" id=toc-1497>٧٧ </span>- {وَلَمَّا جَاءَتْ رُسُلُنَا لُوطًا سِيءَ بِهِمْ وَضَاقَ بِهِمْ ذَرْعًا}
INSERT INTO [b9617] VALUES (1512,'<span data-type="title" id=toc-1498>٧٨ </span>- {وَجَاءَهُ قَوْمُهُ يُهْرَعُونَ إِلَيْهِ وَمِنْ قَبْلُ كَانُوا يَعْمَلُونَ السَّيِّئَاتِ قَالَ يَا قَوْمِ هَؤُلاءِ بَنَاتِي هُنَّ أَطْهَرُ لَكُمْ فَاتَّقُوا اللَّهَ وَلا تُخْزُونِ فِي ضَيْفِي أَلَيْسَ مِنْكُمْ رَجُلٌ رَشِيدٌ}
INSERT INTO [b9617] VALUES (1513,'<span data-type="title" id=toc-1499>٧٩ </span>- {قَالُوا لَقَدْ عَلِمْتَ مَا لَنَا فِي بَنَاتِكَ مِنْ حَقٍّ}
INSERT INTO [b9617] VALUES (1514,'<span data-type="title" id=toc-1500>٨٠ </span>- {قَالَ لَوْ أَنَّ لِي بِكُمْ قُوَّةً أَوْ آوِي إِلَى رُكْنٍ شَدِيدٍ}
INSERT INTO [b9617] VALUES (1515,'<span data-type="title" id=toc-1501>٨١ </span>- {قَالُوا يَا لُوطُ إِنَّا رُسُلُ رَبِّكَ لَنْ يَصِلُوا إِلَيْكَ فَأَسْرِ بِأَهْلِكَ بِقِطْعٍ مِنَ اللَّيْلِ وَلا يَلْتَفِتْ مِنْكُمْ أَحَدٌ إِلا امْرَأَتَكَ إِنَّهُ مُصِيبُهَا مَا أَصَابَهُمْ إِنَّ مَوْعِدَهُمُ الصُّبْحُ أَلَيْسَ الصُّبْحُ بِقَرِيبٍ}
INSERT INTO [b9617] VALUES (1516,'<span data-type="title" id=toc-1502>٨٢ </span>- {جَعَلْنَا عَالِيَهَا سَافِلَهَا وَأَمْطَرْنَا عَلَيْهَا حِجَارَةً مِنْ سِجِّيلٍ مَنْضُودٍ}
INSERT INTO [b9617] VALUES (1517,'<span data-type="title" id=toc-1503>٨٣ </span>- {مُسَوَّمَةً عِنْدَ رَبِّكَ وَمَا هِيَ مِنَ الظَّالِمِينَ بِبَعِيدٍ}
INSERT INTO [b9617] VALUES (1518,'<span data-type="title" id=toc-1504>٨٤ </span>- {وَإِلَى مَدْيَنَ أَخَاهُمْ شُعَيْبًا قَالَ يَا قَوْمِ اعْبُدُوا اللَّهَ مَا لَكُمْ مِنْ إِلَهٍ غَيْرُهُ وَلا تَنْقُصُوا الْمِكْيَالَ وَالْمِيزَانَ إِنِّي أَرَاكُمْ بِخَيْرٍ}
INSERT INTO [b9617] VALUES (1519,'<span data-type="title" id=toc-1505>٨٥ </span>- {وَيَا قَوْمِ أَوْفُوا الْمِكْيَالَ وَالْمِيزَانَ بِالْقِسْطِ وَلا تَبْخَسُوا النَّاسَ أَشْيَاءَهُمْ وَلا تَعْثَوْا فِي الأَرْضِ مُفْسِدِينَ}
INSERT INTO [b9617] VALUES (1520,'<span data-type="title" id=toc-1506>٨٦ </span>- {بَقِيَّتُ اللَّهِ خَيْرٌ لَكُمْ إِنْ كُنْتُمْ مُؤْمِنِينَ وَمَا أَنَا عَلَيْكُمْ بِحَفِيظٍ}
INSERT INTO [b9617] VALUES (1521,'<span data-type="title" id=toc-1507>٨٧ </span>- {قَالُوا يَا شُعَيْبُ أَصَلاتُكَ تَأْمُرُكَ أَنْ نَتْرُكَ مَا يَعْبُدُ آبَاؤُنَا أَوْ أَنْ نَفْعَلَ فِي أَمْوَالِنَا مَا نَشَاءُ إِنَّكَ لأَنْتَ الْحَلِيمُ الرَّشِيدُ}
INSERT INTO [b9617] VALUES (1522,'<span data-type="title" id=toc-1508>٨٨ </span>- {قَالَ يَا قَوْمِ أَرَأَيْتُمْ إِنْ كُنْتُ عَلَى بَيِّنَةٍ مِنْ رَبِّي وَرَزَقَنِي مِنْهُ رِزْقًا حَسَنًا وَمَا أُرِيدُ أَنْ أُخَالِفَكُمْ إِلَى مَا أَنْهَاكُمْ عَنْهُ إِنْ أُرِيدُ إِلا الإِصْلاحَ مَا اسْتَطَعْتُ وَمَا تَوْفِيقِي إِلا بِاللَّهِ عَلَيْهِ تَوَكَّلْتُ وَإِلَيْهِ أُنِيبُ}
INSERT INTO [b9617] VALUES (1523,'<span data-type="title" id=toc-1509>٨٩ </span>- {وَيَا قَوْمِ لا يَجْرِمَنَّكُمْ شِقَاقِي أَنْ يُصِيبَكُمْ مِثْلُ مَا أَصَابَ قَوْمَ نُوحٍ أَوْ قَوْمَ هُودٍ أَوْ قَوْمَ صَالِحٍ وَمَا قَوْمُ لُوطٍ مِنْكُمْ بِبَعِيدٍ}
INSERT INTO [b9617] VALUES (1524,'<span data-type="title" id=toc-1510>٩٠ </span>- {إِنَّ رَبِّي رَحِيمٌ وَدُودٌ}
INSERT INTO [b9617] VALUES (1525,'<span data-type="title" id=toc-1511>٩١ </span>- {قَالُوا يَا شُعَيْبُ مَا نَفْقَهُ كَثِيرًا مِمَّا تَقُولُ وَإِنَّا لَنَرَاكَ فِينَا ضَعِيفًا وَلَوْلا رَهْطُكَ لَرَجَمْنَاكَ وَمَا أَنْتَ عَلَيْنَا بِعَزِيزٍ}
INSERT INTO [b9617] VALUES (1526,'<span data-type="title" id=toc-1512>٩٢ </span>- {قَالَ يَا قَوْمِ أَرَهْطِي أَعَزُّ عَلَيْكُمْ مِنَ اللَّهِ وَاتَّخَذْتُمُوهُ وَرَاءَكُمْ ظِهْرِيًّا إِنَّ رَبِّي بِمَا تَعْمَلُونَ مُحِيطٌ}
INSERT INTO [b9617] VALUES (1527,'<span data-type="title" id=toc-1513>٩٣ </span>- {وَيَا قَوْمِ اعْمَلُوا عَلَى مَكَانَتِكُمْ إِنِّي عَامِلٌ سَوْفَ تَعْلَمُونَ مَنْ يَأْتِيهِ عَذَابٌ يُخْزِيهِ وَمَنْ هُوَ كَاذِبٌ وَارْتَقِبُوا إِنِّي مَعَكُمْ رَقِيبٌ}
INSERT INTO [b9617] VALUES (1528,'<span data-type="title" id=toc-1514>٩٤ </span>- {وَلَمَّا جَاءَ أَمْرُنَا نَجَّيْنَا شُعَيْبًا وَالَّذِينَ آمَنُوا مَعَهُ بِرَحْمَةٍ مِنَّا وَأَخَذَتِ ⦗٤٨٣⦘ الَّذِينَ ظَلَمُوا الصَّيْحَةُ فَأَصْبَحُوا فِي دِيَارِهِمْ جَاثِمِينَ}
INSERT INTO [b9617] VALUES (1529,'<span data-type="title" id=toc-1515>٩٥ </span>- {كَأَنْ لَمْ يَغْنَوْا فِيهَا أَلا بُعْدًا لِمَدْيَنَ كَمَا بَعِدَتْ ثَمُودُ}
INSERT INTO [b9617] VALUES (1530,'<span data-type="title" id=toc-1516>٩٦ </span>- {وَلَقَدْ أَرْسَلْنَا مُوسَى بِآيَاتِنَا وَسُلْطَانٍ مُبِينٍ}
INSERT INTO [b9617] VALUES (1531,'<span data-type="title" id=toc-1517>٩٧ </span>- {إِلَى فِرْعَوْنَ وَمَلَئِهِ فَاتَّبَعُوا أَمْرَ فِرْعَوْنَ وَمَا أَمْرُ فِرْعَوْنَ بِرَشِيدٍ}
INSERT INTO [b9617] VALUES (1532,'<span data-type="title" id=toc-1518>٩٨ </span>- {يَقْدُمُ قَوْمَهُ يَوْمَ الْقِيَامَةِ فَأَوْرَدَهُمُ النَّارَ وَبِئْسَ الْوِرْدُ الْمَوْرُودُ}
INSERT INTO [b9617] VALUES (1533,'<span data-type="title" id=toc-1519>٩٩ </span>- {وَأُتْبِعُوا فِي هَذِهِ لَعْنَةً وَيَوْمَ الْقِيَامَةِ بِئْسَ الرِّفْدُ الْمَرْفُودُ}
INSERT INTO [b9617] VALUES (1534,'<span data-type="title" id=toc-1520>١٠٠ </span>- {ذَلِكَ مِنْ أَنْبَاءِ الْقُرَى نَقُصُّهُ عَلَيْكَ مِنْهَا قَائِمٌ وَحَصِيدٌ}
INSERT INTO [b9617] VALUES (1535,'<span data-type="title" id=toc-1521>١٠١ </span>- {وَمَا ظَلَمْنَاهُمْ وَلَكِنْ ظَلَمُوا أَنْفُسَهُمْ فَمَا أَغْنَتْ عَنْهُمْ آلِهَتُهُمُ الَّتِي يَدْعُونَ مِنْ دُونِ اللَّهِ مِنْ شَيْءٍ لَمَّا جَاءَ أَمْرُ رَبِّكَ وَمَا زَادُوهُمْ غَيْرَ تَتْبِيبٍ}
INSERT INTO [b9617] VALUES (1536,'<span data-type="title" id=toc-1522>١٠٢ </span>- {وَكَذَلِكَ أَخْذُ رَبِّكَ إِذَا أَخَذَ الْقُرَى وَهِيَ ظَالِمَةٌ إِنَّ أَخْذَهُ أَلِيمٌ شَدِيدٌ}
INSERT INTO [b9617] VALUES (1537,'<span data-type="title" id=toc-1523>١٠٣ </span>- {إِنَّ فِي ذَلِكَ لآيَةً لِمَنْ خَافَ عَذَابَ الآخِرَةِ ذَلِكَ يَوْمٌ مَجْمُوعٌ لَهُ النَّاسُ وَذَلِكَ يَوْمٌ مَشْهُودٌ}
INSERT INTO [b9617] VALUES (1538,'<span data-type="title" id=toc-1524>١٠٤ </span>- {وَمَا نُؤَخِّرُهُ إِلا لأَجَلٍ مَعْدُودٍ}
INSERT INTO [b9617] VALUES (1539,'<span data-type="title" id=toc-1525>١٠٥ </span>- {يَوْمَ يَأْتِ لا تَكَلَّمُ نَفْسٌ إِلا بِإِذْنِهِ فَمِنْهُمْ شَقِيٌّ وَسَعِيدٌ}
INSERT INTO [b9617] VALUES (1540,'<span data-type="title" id=toc-1526>١٠٦ </span>- {فَأَمَّا الَّذِينَ شَقُوا فَفِي النَّارِ لَهُمْ فِيهَا زَفِيرٌ وَشَهِيقٌ}
INSERT INTO [b9617] VALUES (1541,'<span data-type="title" id=toc-1527>١٠٧ </span>- {خَالِدِينَ فِيهَا مَا دَامَتِ السَّمَاوَاتُ وَالأَرْضُ إِلا مَا شَاءَ رَبُّكَ إِنَّ رَبَّكَ فَعَّالٌ لِمَا يُرِيدُ}
INSERT INTO [b9617] VALUES (1542,'<span data-type="title" id=toc-1528>١٠٨ </span>- {وَأَمَّا الَّذِينَ سُعِدُوا فَفِي الْجَنَّةِ خَالِدِينَ فِيهَا مَا دَامَتِ السَّمَاوَاتُ وَالأَرْضُ إِلا مَا شَاءَ رَبُّكَ عَطَاءً غَيْرَ مَجْذُوذٍ}
INSERT INTO [b9617] VALUES (1543,'<span data-type="title" id=toc-1529>١٠٩ </span>- {فَلا تَكُ فِي مِرْيَةٍ مِمَّا يَعْبُدُ هَؤُلاءِ مَا يَعْبُدُونَ إِلا كَمَا يَعْبُدُ آبَاؤُهُمْ مِنْ قَبْلُ وَإِنَّا لَمُوَفُّوهُمْ نَصِيبَهُمْ غَيْرَ مَنْقُوصٍ}
INSERT INTO [b9617] VALUES (1544,'<span data-type="title" id=toc-1530>١١٠ </span>- {وَلَقَدْ آتَيْنَا مُوسَى الْكِتَابَ فَاخْتُلِفَ فِيهِ وَلَوْلا كَلِمَةٌ سَبَقَتْ مِنْ رَبِّكَ لَقُضِيَ بَيْنَهُمْ وَإِنَّهُمْ لَفِي شَكٍّ مِنْهُ مُرِيبٍ}
INSERT INTO [b9617] VALUES (1545,'<span data-type="title" id=toc-1531>١١١ </span>- {وَإِنَّ كُلا لَمَّا لَيُوَفِّيَنَّهُمْ رَبُّكَ أَعْمَالَهُمْ إِنَّهُ بِمَا يَعْمَلُونَ خَبِيرٌ}
INSERT INTO [b9617] VALUES (1546,'<span data-type="title" id=toc-1532>١١٢ </span>- {فَاسْتَقِمْ كَمَا أُمِرْتَ وَمَنْ تَابَ مَعَكَ وَلا تَطْغَوْا}
INSERT INTO [b9617] VALUES (1547,'<span data-type="title" id=toc-1533>١١٣ </span>- {وَلا تَرْكَنُوا إِلَى الَّذِينَ ظَلَمُوا فَتَمَسَّكُمُ النَّارُ وَمَا لَكُمْ مِنْ دُونِ اللَّهِ مِنْ أَوْلِيَاءَ ثُمَّ لا تُنْصَرُونَ}
INSERT INTO [b9617] VALUES (1548,'<span data-type="title" id=toc-1534>١١٤ </span>- {وَأَقِمِ الصَّلاةَ طَرَفَيِ النَّهَارِ وَزُلَفًا مِنَ اللَّيْلِ إِنَّ الْحَسَنَاتِ يُذْهِبْنَ السَّيِّئَاتِ ذَلِكَ ذِكْرَى لِلذَّاكِرِينَ}
INSERT INTO [b9617] VALUES (1549,'<span data-type="title" id=toc-1535>١١٦ </span>- {فَلَوْلا كَانَ مِنَ الْقُرُونِ مِنْ قَبْلِكُمْ أُولُو بَقِيَّةٍ يَنْهَوْنَ عَنِ الْفَسَادِ فِي الأَرْضِ إِلا قَلِيلا مِمَّنْ أَنْجَيْنَا مِنْهُمْ وَاتَّبَعَ الَّذِينَ ظَلَمُوا مَا أُتْرِفُوا فِيهِ وَكَانُوا مُجْرِمِينَ}
INSERT INTO [b9617] VALUES (1550,'<span data-type="title" id=toc-1536>١١٧ </span>- {وَمَا كَانَ رَبُّكَ لِيُهْلِكَ الْقُرَى بِظُلْمٍ وَأَهْلُهَا مُصْلِحُونَ}
INSERT INTO [b9617] VALUES (1551,'<span data-type="title" id=toc-1537>١١٨ </span>- {وَلَوْ شَاءَ رَبُّكَ لَجَعَلَ النَّاسَ أُمَّةً وَاحِدَةً وَلا يَزَالُونَ مُخْتَلِفِينَ}
INSERT INTO [b9617] VALUES (1552,'<span data-type="title" id=toc-1538>١١٩ </span>- {إِلا مَنْ رَحِمَ رَبُّكَ وَلِذَلِكَ خَلَقَهُمْ وَتَمَّتْ كَلِمَةُ رَبِّكَ لأَمْلأَنَّ جَهَنَّمَ مِنَ الْجِنَّةِ وَالنَّاسِ أَجْمَعِينَ}
INSERT INTO [b9617] VALUES (1553,'<span data-type="title" id=toc-1539>١٢٠ </span>- {وَكُلا نَقُصُّ عَلَيْكَ مِنْ أَنْبَاءِ الرُّسُلِ مَا نُثَبِّتُ بِهِ فُؤَادَكَ وَجَاءَكَ فِي هَذِهِ الْحَقُّ وَمَوْعِظَةٌ وَذِكْرَى لِلْمُؤْمِنِينَ}
INSERT INTO [b9617] VALUES (1554,'<span data-type="title" id=toc-1540>١٢١ </span>- {اعْمَلُوا عَلَى مَكَانَتِكُمْ إِنَّا عَامِلُونَ}
INSERT INTO [b9617] VALUES (1555,'<span data-type="title" id=toc-1541>سورة يوسف</span>','2','491',NULL,NULL,'0');
INSERT INTO [b9617] VALUES (1556,'<span data-type="title" id=toc-1542>٢ </span>- {إِنَّا أَنْزَلْنَاهُ قُرْآنًا عَرَبِيًّا لَعَلَّكُمْ تَعْقِلُونَ}
INSERT INTO [b9617] VALUES (1557,'<span data-type="title" id=toc-1543>٣ </span>- {نَحْنُ نَقُصُّ عَلَيْكَ أَحْسَنَ الْقَصَصِ بِمَا أَوْحَيْنَا إِلَيْكَ هَذَا الْقُرْآنَ وَإِنْ كُنْتَ مِنْ قَبْلِهِ لَمِنَ الْغَافِلِينَ}
INSERT INTO [b9617] VALUES (1558,'<span data-type="title" id=toc-1544>٤ </span>- {إِذْ قَالَ يُوسُفُ لأَبِيهِ يَا أَبَتِ إِنِّي رَأَيْتُ أَحَدَ عَشَرَ كَوْكَبًا وَالشَّمْسَ وَالْقَمَرَ رَأَيْتُهُمْ لِي سَاجِدِينَ}
INSERT INTO [b9617] VALUES (1559,'<span data-type="title" id=toc-1545>٥ </span>- {قَالَ يَا بُنَيَّ لا تَقْصُصْ رُؤْيَاكَ عَلَى إِخْوَتِكَ فَيَكِيدُوا لَكَ كَيْدًا}
INSERT INTO [b9617] VALUES (1560,'<span data-type="title" id=toc-1546>٦ </span>- {وَكَذَلِكَ يَجْتَبِيكَ رَبُّكَ وَيُعَلِّمُكَ مِنْ تَأْوِيلِ الأَحَادِيثِ وَيُتِمُّ نِعْمَتَهُ عَلَيْكَ وَعَلَى آلِ يَعْقُوبَ كَمَا أَتَمَّهَا عَلَى أَبَوَيْكَ مِنْ قَبْلُ إِبْرَاهِيمَ وَإِسْحَاقَ}
INSERT INTO [b9617] VALUES (1561,'<span data-type="title" id=toc-1547>٧ </span>- {لَقَدْ كَانَ فِي يُوسُفَ وَإِخْوَتِهِ آيَاتٌ لِلسَّائِلِينَ}
INSERT INTO [b9617] VALUES (1562,'<span data-type="title" id=toc-1548>٨ </span>- {إِذْ قَالُوا لَيُوسُفُ وَأَخُوهُ أَحَبُّ إِلَى أَبِينَا مِنَّا وَنَحْنُ عُصْبَةٌ إِنَّ أَبَانَا لَفِي ضَلالٍ مُبِينٍ}
INSERT INTO [b9617] VALUES (1563,'<span data-type="title" id=toc-1549>٩ </span>- {اقْتُلُوا يُوسُفَ أَوِ اطْرَحُوهُ أَرْضًا يَخْلُ لَكُمْ وَجْهُ أَبِيكُمْ وَتَكُونُوا مِنْ بَعْدِهِ قَوْمًا صَالِحِينَ}
INSERT INTO [b9617] VALUES (1564,'<span data-type="title" id=toc-1550>١٠ </span>- {وَأَلْقُوهُ فِي غَيَابَتِ الْجُبِّ يَلْتَقِطْهُ بَعْضُ السَّيَّارَةِ إِنْ كُنْتُمْ فَاعِلِينَ}
INSERT INTO [b9617] VALUES (1565,'<span data-type="title" id=toc-1551>١١ </span>- {قَالُوا يَا أَبَانَا مَا لَكَ لا تَأْمَنَّا عَلَى يُوسُفَ وَإِنَّا لَهُ لَنَاصِحُونَ}
INSERT INTO [b9617] VALUES (1566,'<span data-type="title" id=toc-1552>١٢ </span>- {أَرْسِلْهُ مَعَنَا غَدًا يَرْتَعْ وَيَلْعَبْ وَإِنَّا لَهُ لَحَافِظُونَ}
INSERT INTO [b9617] VALUES (1567,'<span data-type="title" id=toc-1553>١٣ </span>- {قَالَ إِنِّي لَيَحْزُنُنِي أَنْ تَذْهَبُوا بِهِ وَأَخَافُ أَنْ يَأْكُلَهُ الذِّئْبُ وَأَنْتُمْ عَنْهُ غَافِلُونَ}
INSERT INTO [b9617] VALUES (1568,'<span data-type="title" id=toc-1554>١٤ </span>- {قَالُوا لَئِنْ أَكَلَهُ الذِّئْبُ وَنَحْنُ عُصْبَةٌ إِنَّا إِذًا لَخَاسِرُونَ}
INSERT INTO [b9617] VALUES (1569,'<span data-type="title" id=toc-1555>١٥ </span>- {فَلَمَّا ذَهَبُوا بِهِ وَأَجْمَعُوا أَنْ يَجْعَلُوهُ فِي غَيَابَتِ الْجُبِّ وَأَوْحَيْنَا إِلَيْهِ لَتُنَبِّئَنَّهُمْ بِأَمْرِهِمْ هَذَا وَهُمْ لا يَشْعُرُونَ}
INSERT INTO [b9617] VALUES (1570,'<span data-type="title" id=toc-1556>١٦ </span>- {وَجَاءُوا أَبَاهُمْ عِشَاءً يَبْكُونَ}
INSERT INTO [b9617] VALUES (1571,'<span data-type="title" id=toc-1557>١٧ </span>- {إِنَّا ذَهَبْنَا نَسْتَبِقُ وَتَرَكْنَا يُوسُفَ عِنْدَ مَتَاعِنَا فَأَكَلَهُ الذِّئْبُ وَمَا أَنْتَ بِمُؤْمِنٍ لَنَا وَلَوْ كُنَّا صَادِقِينَ}
INSERT INTO [b9617] VALUES (1572,'<span data-type="title" id=toc-1558>١٨ </span>- {وَجَاءُوا عَلَى قَمِيصِهِ بِدَمٍ كَذِبٍ قَالَ بَلْ سَوَّلَتْ لَكُمْ أَنْفُسُكُمْ أَمْرًا فَصَبْرٌ جَمِيلٌ وَاللَّهُ الْمُسْتَعَانُ عَلَى مَا تَصِفُونَ} ⦗٤٩٦⦘
INSERT INTO [b9617] VALUES (1573,'<span data-type="title" id=toc-1559>١٩ </span>- {قَالَ يَا بُشْرَى هَذَا غُلامٌ وَأَسَرُّوهُ بِضَاعَةً وَاللَّهُ عَلِيمٌ بِمَا يَعْمَلُونَ}
INSERT INTO [b9617] VALUES (1574,'<span data-type="title" id=toc-1560>٢٠ </span>- {وَشَرَوْهُ بِثَمَنٍ بَخْسٍ دَرَاهِمَ مَعْدُودَةٍ وَكَانُوا فِيهِ مِنَ الزَّاهِدِينَ}
INSERT INTO [b9617] VALUES (1575,'<span data-type="title" id=toc-1561>٢١ </span>- {وَقَالَ الَّذِي اشْتَرَاهُ مِنْ مِصْرَ لامْرَأَتِهِ أَكْرِمِي مَثْوَاهُ عَسَى أَنْ يَنْفَعَنَا أَوْ نَتَّخِذَهُ وَلَدًا وَكَذَلِكَ مَكَّنَّا لِيُوسُفَ فِي الأَرْضِ وَلِنُعَلِّمَهُ مِنْ تَأْوِيلِ الأَحَادِيثِ وَاللَّهُ غَالِبٌ عَلَى أَمْرِهِ وَلَكِنَّ أَكْثَرَ النَّاسِ لا يَعْلَمُونَ} ⦗٤٩٧⦘
INSERT INTO [b9617] VALUES (1576,'<span data-type="title" id=toc-1562>٢٢ </span>- {وَلَمَّا بَلَغَ أَشُدَّهُ آتَيْنَاهُ حُكْمًا وَعِلْمًا وَكَذَلِكَ نَجْزِي الْمُحْسِنِينَ}
INSERT INTO [b9617] VALUES (1577,'<span data-type="title" id=toc-1563>٢٣ </span>- {وَرَاوَدَتْهُ الَّتِي هُوَ فِي بَيْتِهَا عَنْ نَفْسِهِ وَغَلَّقَتِ الأَبْوَابَ وَقَالَتْ هَيْتَ لَكَ قَالَ مَعَاذَ اللَّهِ إِنَّهُ رَبِّي أَحْسَنَ مَثْوَايَ إِنَّهُ لا يُفْلِحُ الظَّالِمُونَ}
INSERT INTO [b9617] VALUES (1578,'<span data-type="title" id=toc-1564>٢٤ </span>- {وَلَقَدْ هَمَّتْ بِهِ وَهَمَّ بِهَا لَوْلا أَنْ رَأَى بُرْهَانَ رَبِّهِ كَذَلِكَ لِنَصْرِفَ عَنْهُ السُّوءَ وَالْفَحْشَاءَ إِنَّهُ مِنْ عِبَادِنَا الْمُخْلَصِينَ}
INSERT INTO [b9617] VALUES (1579,'<span data-type="title" id=toc-1565>٢٥ </span>- {وَاسْتَبَقَا الْبَابَ وَقَدَّتْ قَمِيصَهُ مِنْ دُبُرٍ وَأَلْفَيَا سَيِّدَهَا لَدَى الْبَابِ قَالَتْ مَا جَزَاءُ مَنْ أَرَادَ بِأَهْلِكَ سُوءًا إِلا أَنْ يُسْجَنَ أَوْ عَذَابٌ أَلِيمٌ}
INSERT INTO [b9617] VALUES (1580,'<span data-type="title" id=toc-1566>٢٦ </span>- {قَالَ هِيَ رَاوَدَتْنِي عَنْ نَفْسِي وَشَهِدَ شَاهِدٌ مِنْ أَهْلِهَا إِنْ كَانَ قَمِيصُهُ قُدَّ مِنْ قُبُلٍ فَصَدَقَتْ وَهُوَ مِنَ الْكَاذِبِينَ}
INSERT INTO [b9617] VALUES (1581,'<span data-type="title" id=toc-1567>٢٨ </span>- {فَلَمَّا رَأَى قَمِيصَهُ قُدَّ مِنْ دُبُرٍ قَالَ إِنَّهُ مِنْ كَيْدِكُنَّ إِنَّ كَيْدَكُنَّ عَظِيمٌ}
INSERT INTO [b9617] VALUES (1582,'<span data-type="title" id=toc-1568>٢٩ </span>- {يُوسُفُ أَعْرِضْ عَنْ هَذَا وَاسْتَغْفِرِي لِذَنْبِكِ إِنَّكِ كُنْتِ مِنَ الْخَاطِئِينَ}
INSERT INTO [b9617] VALUES (1583,'<span data-type="title" id=toc-1569>٣٠ </span>- {وَقَالَ نِسْوَةٌ فِي الْمَدِينَةِ امْرَأَتُ الْعَزِيزِ تُرَاوِدُ فَتَاهَا عَنْ نَفْسِهِ قَدْ شَغَفَهَا حُبًّا إِنَّا لَنَرَاهَا فِي ضَلالٍ مُبِينٍ}
INSERT INTO [b9617] VALUES (1584,'<span data-type="title" id=toc-1570>٣١ </span>- {فَلَمَّا سَمِعَتْ بِمَكْرِهِنَّ أَرْسَلَتْ إِلَيْهِنَّ وَأَعْتَدَتْ لَهُنَّ مُتَّكَأً وَآتَتْ كُلَّ وَاحِدَةٍ مِنْهُنَّ سِكِّينًا وَقَالَتِ اخْرُجْ عَلَيْهِنَّ فَلَمَّا رَأَيْنَهُ أَكْبَرْنَهُ وَقَطَّعْنَ أَيْدِيَهُنَّ وَقُلْنَ حَاشَ لِلَّهِ مَا هَذَا بَشَرًا إِنْ هَذَا إِلا مَلَكٌ كَرِيمٌ}
INSERT INTO [b9617] VALUES (1585,'<span data-type="title" id=toc-1571>٣٢ </span>- {قَالَتْ فَذَلِكُنَّ الَّذِي لُمْتُنَّنِي فِيهِ وَلَقَدْ رَاوَدْتُهُ عَنْ نَفْسِهِ فَاسْتَعْصَمَ وَلَئِنْ لَمْ يَفْعَلْ مَا آمُرُهُ لَيُسْجَنَنَّ وَلَيَكُونًا مِنَ الصَّاغِرِينَ}
INSERT INTO [b9617] VALUES (1586,'<span data-type="title" id=toc-1572>٣٣ </span>- {قَالَ رَبِّ السِّجْنُ أَحَبُّ إِلَيَّ مِمَّا يَدْعُونَنِي إِلَيْهِ وَإِلا تَصْرِفْ عَنِّي كَيْدَهُنَّ أَصْبُ إِلَيْهِنَّ}
INSERT INTO [b9617] VALUES (1587,'<span data-type="title" id=toc-1573>٣٤ </span>- {فَاسْتَجَابَ لَهُ رَبُّهُ فَصَرَفَ عَنْهُ كَيْدَهُنَّ إِنَّهُ هُوَ السَّمِيعُ الْعَلِيمُ}
INSERT INTO [b9617] VALUES (1588,'<span data-type="title" id=toc-1574>٣٥ </span>- {ثُمَّ بَدَا لَهُمْ مِنْ بَعْدِ مَا رَأَوُا الآيَاتِ لَيَسْجُنُنَّهُ حَتَّى حِينٍ}
INSERT INTO [b9617] VALUES (1589,'<span data-type="title" id=toc-1575>٣٦ </span>- {قَالَ أَحَدُهُمَا إِنِّي أَرَانِي أَعْصِرُ خَمْرًا وَقَالَ الآخَرُ إِنِّي أَرَانِي أَحْمِلُ فَوْقَ رَأْسِي خُبْزًا تَأْكُلُ الطَّيْرُ مِنْهُ نَبِّئْنَا بِتَأْوِيلِهِ إِنَّا نَرَاكَ مِنَ الْمُحْسِنِينَ}
INSERT INTO [b9617] VALUES (1590,'<span data-type="title" id=toc-1576>٣٧ </span>- {قَالَ لا يَأْتِيكُمَا طَعَامٌ تُرْزَقَانِهِ إِلا نَبَّأْتُكُمَا بِتَأْوِيلِهِ قَبْلَ أَنْ يَأْتِيَكُمَا ذَلِكُمَا مِمَّا عَلَّمَنِي رَبِّي إِنِّي تَرَكْتُ مِلَّةَ قَوْمٍ لا يُؤْمِنُونَ بِاللَّهِ وَهُمْ بِالآخِرَةِ هُمْ كَافِرُونَ}
INSERT INTO [b9617] VALUES (1591,'<span data-type="title" id=toc-1577>٣٨ </span>- {وَاتَّبَعْتُ مِلَّةَ آبَائِي إِبْرَاهِيمَ وَإِسْحَاقَ وَيَعْقُوبَ مَا كَانَ لَنَا أَنْ نُشْرِكَ بِاللَّهِ مِنْ شَيْءٍ ذَلِكَ مِنْ فَضْلِ اللَّهِ عَلَيْنَا وَعَلَى النَّاسِ وَلَكِنَّ أَكْثَرَ النَّاسِ لا يَشْكُرُونَ} ⦗٥٠٣⦘
INSERT INTO [b9617] VALUES (1592,'<span data-type="title" id=toc-1578>٣٩ </span>- {يَا صَاحِبَيِ السِّجْنِ أَأَرْبَابٌ مُتَفَرِّقُونَ خَيْرٌ أَمِ اللَّهُ الْوَاحِدُ الْقَهَّارُ}
INSERT INTO [b9617] VALUES (1593,'<span data-type="title" id=toc-1579>٤٠ </span>- {مَا تَعْبُدُونَ مِنْ دُونِهِ إِلا أَسْمَاءً سَمَّيْتُمُوهَا أَنْتُمْ وَآبَاؤُكُمْ مَا أَنْزَلَ اللَّهُ بِهَا مِنْ سُلْطَانٍ إِنِ الْحُكْمُ إِلا لِلَّهِ أَمَرَ أَلا تَعْبُدُوا إِلا إِيَّاهُ ذَلِكَ الدِّينُ الْقَيِّمُ وَلَكِنَّ أَكْثَرَ النَّاسِ لا يَعْلَمُونَ}
INSERT INTO [b9617] VALUES (1594,'<span data-type="title" id=toc-1580>٤١ </span>- {أَمَّا أَحَدُكُمَا فَيَسْقِي رَبَّهُ خَمْرًا وَأَمَّا الآخَرُ فَيُصْلَبُ فَتَأْكُلُ الطَّيْرُ مِنْ رَأْسِهِ قُضِيَ الأَمْرُ الَّذِي فِيهِ تَسْتَفْتِيَانِ}
INSERT INTO [b9617] VALUES (1595,'<span data-type="title" id=toc-1581>٤٢ </span>- {وَقَالَ لِلَّذِي ظَنَّ أَنَّهُ نَاجٍ مِنْهُمَا اذْكُرْنِي عِنْدَ رَبِّكَ فَأَنْسَاهُ الشَّيْطَانُ ذِكْرَ رَبِّهِ فَلَبِثَ فِي السِّجْنِ بِضْعَ سِنِينَ}
INSERT INTO [b9617] VALUES (1596,'<span data-type="title" id=toc-1582>٤٣ </span>- {وَقَالَ الْمَلِكُ إِنِّي أَرَى سَبْعَ بَقَرَاتٍ سِمَانٍ يَأْكُلُهُنَّ سَبْعٌ عِجَافٌ وَسَبْعَ سُنْبُلاتٍ خُضْرٍ وَأُخَرَ يَابِسَاتٍ يَا أَيُّهَا الْمَلأُ أَفْتُونِي فِي رُؤْيَايَ إِنْ كُنْتُمْ لِلرُّؤْيَا تَعْبُرُونَ}
INSERT INTO [b9617] VALUES (1597,'<span data-type="title" id=toc-1583>٤٤ </span>- {قَالُوا أَضْغَاثُ أَحْلامٍ وَمَا نَحْنُ بِتَأْوِيلِ الأَحْلامِ بِعَالِمِينَ}
INSERT INTO [b9617] VALUES (1598,'<span data-type="title" id=toc-1584>٤٥ </span>- {وَقَالَ الَّذِي نَجَا مِنْهُمَا وَادَّكَرَ بَعْدَ أُمَّةٍ أَنَا أُنَبِّئُكُمْ بِتَأْوِيلِهِ فَأَرْسِلُونِ}
INSERT INTO [b9617] VALUES (1599,'<span data-type="title" id=toc-1585>٤٦ </span>- {يُوسُفُ أَيُّهَا الصِّدِّيقُ أَفْتِنَا فِي سَبْعِ بَقَرَاتٍ سِمَانٍ يَأْكُلُهُنَّ سَبْعٌ عِجَافٌ وَسَبْعِ سُنْبُلاتٍ خُضْرٍ وَأُخَرَ يَابِسَاتٍ لَعَلِّي أَرْجِعُ إِلَى النَّاسِ لَعَلَّهُمْ يَعْلَمُونَ}
INSERT INTO [b9617] VALUES (1600,'<span data-type="title" id=toc-1586>٤٧ </span>- {قَالَ تَزْرَعُونَ سَبْعَ سِنِينَ دَأَبًا فَمَا حَصَدْتُمْ فَذَرُوهُ فِي سُنْبُلِهِ إِلا قَلِيلا مِمَّا تَأْكُلُونَ}
INSERT INTO [b9617] VALUES (1601,'<span data-type="title" id=toc-1587>٤٨ </span>- {ثُمَّ يَأْتِي مِنْ بَعْدِ ذَلِكَ سَبْعٌ شِدَادٌ يَأْكُلْنَ مَا قَدَّمْتُمْ لَهُنَّ إِلا قَلِيلا مِمَّا تُحْصِنُونَ}
INSERT INTO [b9617] VALUES (1602,'<span data-type="title" id=toc-1588>٤٩ </span>- {ثُمَّ يَأْتِي مِنْ بَعْدِ ذَلِكَ عَامٌ فِيهِ يُغَاثُ النَّاسُ وَفِيهِ يَعْصِرُونَ}
INSERT INTO [b9617] VALUES (1603,'<span data-type="title" id=toc-1589>٥٠ </span>- {وَقَالَ الْمَلِكُ ائْتُونِي بِهِ فَلَمَّا جَاءَهُ الرَّسُولُ قَالَ ارْجِعْ إِلَى رَبِّكَ فَاسْأَلْهُ مَا بَالُ النِّسْوَةِ اللاتِي قَطَّعْنَ أَيْدِيَهُنَّ}
INSERT INTO [b9617] VALUES (1604,'<span data-type="title" id=toc-1590>٥١ </span>- {قَالَ مَا خَطْبُكُنَّ إِذْ رَاوَدْتُنَّ يُوسُفَ عَنْ نَفْسِهِ قُلْنَ حَاشَ لِلَّهِ مَا عَلِمْنَا عَلَيْهِ مِنْ سُوءٍ قَالَتِ امْرَأَتُ الْعَزِيزِ الآنَ حَصْحَصَ الْحَقُّ أَنَا رَاوَدْتُهُ عَنْ نَفْسِهِ وَإِنَّهُ لَمِنَ الصَّادِقِينَ}
INSERT INTO [b9617] VALUES (1605,'<span data-type="title" id=toc-1591>٥٢ </span>- {ذَلِكَ لِيَعْلَمَ أَنِّي لَمْ أَخُنْهُ بِالْغَيْبِ وَأَنَّ اللَّهَ لا يَهْدِي كَيْدَ الْخَائِنِينَ}
INSERT INTO [b9617] VALUES (1606,'<span data-type="title" id=toc-1592>٥٣ </span>- {وَمَا أُبَرِّئُ نَفْسِي إِنَّ النَّفْسَ لأَمَّارَةٌ بِالسُّوءِ إِلا مَا رَحِمَ رَبِّي}
INSERT INTO [b9617] VALUES (1607,'<span data-type="title" id=toc-1593>٥٤ </span>- {وَقَالَ الْمَلِكُ ائْتُونِي بِهِ أَسْتَخْلِصْهُ لِنَفْسِي فَلَمَّا كَلَّمَهُ قَالَ إِنَّكَ الْيَوْمَ لَدَيْنَا مَكِينٌ أَمِينٌ}
INSERT INTO [b9617] VALUES (1608,'<span data-type="title" id=toc-1594>٥٦ </span>- {وَكَذَلِكَ مَكَّنَّا لِيُوسُفَ فِي الأَرْضِ يَتَبَوَّأُ مِنْهَا حَيْثُ يَشَاءُ نُصِيبُ بِرَحْمَتِنَا مَنْ نَشَاءُ}
INSERT INTO [b9617] VALUES (1609,'<span data-type="title" id=toc-1595>٥٧ </span>- {وَلأَجْرُ الآخِرَةِ خَيْرٌ لِلَّذِينَ آمَنُوا}
INSERT INTO [b9617] VALUES (1610,'<span data-type="title" id=toc-1596>٥٨ </span>- {فَعَرَفَهُمْ وَهُمْ لَهُ مُنْكِرُونَ}
INSERT INTO [b9617] VALUES (1611,'<span data-type="title" id=toc-1597>٥٩ </span>- {وَلَمَّا جَهَّزَهُمْ بِجَهَازِهِمْ قَالَ ائْتُونِي بِأَخٍ لَكُمْ مِنْ أَبِيكُمْ أَلا تَرَوْنَ أَنِّي أُوفِي الْكَيْلَ وَأَنَا خَيْرُ الْمُنْزِلِينَ}
INSERT INTO [b9617] VALUES (1612,'<span data-type="title" id=toc-1598>٦٠ </span>- {فَإِنْ لَمْ تَأْتُونِي بِهِ فَلا كَيْلَ لَكُمْ عِنْدِي وَلا تَقْرَبُونِ}
INSERT INTO [b9617] VALUES (1613,'<span data-type="title" id=toc-1599>٦١ </span>- {قَالُوا سَنُرَاوِدُ عَنْهُ أَبَاهُ وَإِنَّا لَفَاعِلُونَ}
INSERT INTO [b9617] VALUES (1614,'<span data-type="title" id=toc-1600>٦٢ </span>- {وَقَالَ لِفِتْيَانِهِ اجْعَلُوا بِضَاعَتَهُمْ فِي رِحَالِهِمْ لَعَلَّهُمْ يَعْرِفُونَهَا إِذَا انْقَلَبُوا إِلَى أَهْلِهِمْ لَعَلَّهُمْ يَرْجِعُونَ}
INSERT INTO [b9617] VALUES (1615,'<span data-type="title" id=toc-1601>٦٣ </span>- {فَلَمَّا رَجَعُوا إِلَى أَبِيهِمْ قَالُوا يَا أَبَانَا مُنِعَ مِنَّا الْكَيْلُ فَأَرْسِلْ مَعَنَا أَخَانَا نَكْتَلْ وَإِنَّا لَهُ لَحَافِظُونَ}
INSERT INTO [b9617] VALUES (1616,'<span data-type="title" id=toc-1602>٦٤ </span>- {قَالَ هَلْ آمَنُكُمْ عَلَيْهِ إِلا كَمَا أَمِنْتُكُمْ عَلَى أَخِيهِ مِنْ قَبْلُ فَاللَّهُ خَيْرٌ حَافِظًا}
INSERT INTO [b9617] VALUES (1617,'<span data-type="title" id=toc-1603>٦٥ </span>- {وَلَمَّا فَتَحُوا مَتَاعَهُمْ وَجَدُوا بِضَاعَتَهُمْ رُدَّتْ إِلَيْهِمْ قَالُوا يَا أَبَانَا مَا نَبْغِي هَذِهِ بِضَاعَتُنَا رُدَّتْ إِلَيْنَا وَنَمِيرُ أَهْلَنَا وَنَحْفَظُ أَخَانَا وَنَزْدَادُ كَيْلَ بَعِيرٍ ذَلِكَ كَيْلٌ يَسِيرٌ}
INSERT INTO [b9617] VALUES (1618,'<span data-type="title" id=toc-1604>٦٦ </span>- {قَالَ لَنْ أُرْسِلَهُ مَعَكُمْ حَتَّى تُؤْتُونِ مَوْثِقًا مِنَ اللَّهِ لَتَأْتُنَّنِي بِهِ إِلا أَنْ يُحَاطَ بِكُمْ فَلَمَّا آتَوْهُ مَوْثِقَهُمْ قَالَ اللَّهُ عَلَى مَا نَقُولُ وَكِيلٌ}
INSERT INTO [b9617] VALUES (1619,'<span data-type="title" id=toc-1605>٦٧ </span>- {وَقَالَ يَا بَنِيَّ لا تَدْخُلُوا مِنْ بَابٍ وَاحِدٍ وَادْخُلُوا مِنْ أَبْوَابٍ مُتَفَرِّقَةٍ وَمَا أُغْنِي عَنْكُمْ مِنَ اللَّهِ مِنْ شَيْءٍ إِنِ الْحُكْمُ إِلا لِلَّهِ عَلَيْهِ تَوَكَّلْتُ وَعَلَيْهِ فَلْيَتَوَكَّلِ الْمُتَوَكِّلُونَ}
INSERT INTO [b9617] VALUES (1620,'<span data-type="title" id=toc-1606>٦٨ </span>- {وَلَمَّا دَخَلُوا مِنْ حَيْثُ أَمَرَهُمْ أَبُوهُمْ مَا كَانَ يُغْنِي عَنْهُمْ مِنَ اللَّهِ مِنْ شَيْءٍ إِلا حَاجَةً فِي نَفْسِ يَعْقُوبَ قَضَاهَا وَإِنَّهُ لَذُو عِلْمٍ لِمَا عَلَّمْنَاهُ وَلَكِنَّ أَكْثَرَ النَّاسِ لا يَعْلَمُونَ}
INSERT INTO [b9617] VALUES (1621,'<span data-type="title" id=toc-1607>٦٩ </span>- {قَالَ إِنِّي أَنَا أَخُوكَ فَلا تَبْتَئِسْ بِمَا كَانُوا يَعْمَلُونَ}
INSERT INTO [b9617] VALUES (1622,'<span data-type="title" id=toc-1608>٧٠ </span>- {جَعَلَ السِّقَايَةَ فِي رَحْلِ أَخِيهِ ثُمَّ أَذَّنَ مُؤَذِّنٌ أَيَّتُهَا الْعِيرُ إِنَّكُمْ لَسَارِقُونَ}
INSERT INTO [b9617] VALUES (1623,'<span data-type="title" id=toc-1609>٧١ </span>- {قَالُوا وَأَقْبَلُوا عَلَيْهِمْ مَاذَا تَفْقِدُونَ}
INSERT INTO [b9617] VALUES (1624,'<span data-type="title" id=toc-1610>٧٢ </span>- {قَالُوا نَفْقِدُ صُوَاعَ الْمَلِكِ وَلِمَنْ جَاءَ بِهِ حِمْلُ بَعِيرٍ وَأَنَا بِهِ زَعِيمٌ}
INSERT INTO [b9617] VALUES (1625,'<span data-type="title" id=toc-1611>٧٣ </span>- {قَالُوا تَاللَّهِ لَقَدْ عَلِمْتُمْ مَا جِئْنَا لِنُفْسِدَ فِي الأَرْضِ وَمَا كُنَّا سَارِقِينَ}
INSERT INTO [b9617] VALUES (1626,'<span data-type="title" id=toc-1612>٧٤ </span>- {قَالُوا فَمَا جَزَاؤُهُ إِنْ كُنْتُمْ كَاذِبِينَ}
INSERT INTO [b9617] VALUES (1627,'<span data-type="title" id=toc-1613>٧٥ </span>- {قَالُوا جَزَاؤُهُ مَنْ وُجِدَ فِي رَحْلِهِ فَهُوَ جَزَاؤُهُ كَذَلِكَ نَجْزِي الظَّالِمِينَ}
INSERT INTO [b9617] VALUES (1628,'<span data-type="title" id=toc-1614>٧٦ </span>- {كَذَلِكَ كِدْنَا لِيُوسُفَ مَا كَانَ لِيَأْخُذَ أَخَاهُ فِي دِينِ الْمَلِكِ إِلا أَنْ يَشَاءَ اللَّهُ نَرْفَعُ دَرَجَاتٍ مَنْ نَشَاءُ وَفَوْقَ كُلِّ ذِي عِلْمٍ عَلِيمٌ}
INSERT INTO [b9617] VALUES (1629,'<span data-type="title" id=toc-1615>٧٧ </span>- {قَالُوا إِنْ يَسْرِقْ فَقَدْ سَرَقَ أَخٌ لَهُ مِنْ قَبْلُ فَأَسَرَّهَا يُوسُفُ فِي نَفْسِهِ وَلَمْ يُبْدِهَا لَهُمْ قَالَ أَنْتُمْ شَرٌّ مَكَانًا وَاللَّهُ أَعْلَمُ بِمَا تَصِفُونَ}
INSERT INTO [b9617] VALUES (1630,'<span data-type="title" id=toc-1616>٧٨ </span>- {قَالُوا يَا أَيُّهَا الْعَزِيزُ إِنَّ لَهُ أَبًا شَيْخًا كَبِيرًا فَخُذْ أَحَدَنَا مَكَانَهُ إِنَّا نَرَاكَ مِنَ الْمُحْسِنِينَ}
INSERT INTO [b9617] VALUES (1631,'<span data-type="title" id=toc-1617>٧٩ </span>- {قَالَ مَعَاذَ اللَّهِ أَنْ نَأْخُذَ إِلا مَنْ وَجَدْنَا مَتَاعَنَا عِنْدَهُ إِنَّا إِذًا لَظَالِمُونَ}
INSERT INTO [b9617] VALUES (1632,'<span data-type="title" id=toc-1618>٨٠ </span>- {فَلَمَّا اسْتَيْأَسُوا مِنْهُ خَلَصُوا نَجِيًّا قَالَ كَبِيرُهُمْ أَلَمْ تَعْلَمُوا أَنَّ أَبَاكُمْ قَدْ أَخَذَ عَلَيْكُمْ مَوْثِقًا مِنَ اللَّهِ وَمِنْ قَبْلُ مَا فَرَّطْتُمْ فِي يُوسُفَ فَلَنْ أَبْرَحَ الأَرْضَ حَتَّى يَأْذَنَ لِي أَبِي أَوْ يَحْكُمَ اللَّهُ لِي وَهُوَ خَيْرُ الْحَاكِمِينَ} ⦗٥١٥⦘
INSERT INTO [b9617] VALUES (1633,'<span data-type="title" id=toc-1619>٨١ </span>- {إِنَّ ابْنَكَ سَرَقَ وَمَا شَهِدْنَا إِلا بِمَا عَلِمْنَا وَمَا كُنَّا لِلْغَيْبِ حَافِظِينَ}
INSERT INTO [b9617] VALUES (1634,'<span data-type="title" id=toc-1620>٨٢ </span>- {وَاسْأَلِ الْقَرْيَةَ الَّتِي كُنَّا فِيهَا وَالْعِيرَ الَّتِي أَقْبَلْنَا فِيهَا وَإِنَّا لَصَادِقُونَ}
INSERT INTO [b9617] VALUES (1635,'<span data-type="title" id=toc-1621>٨٣ </span>- {قَالَ بَلْ سَوَّلَتْ لَكُمْ أَنْفُسُكُمْ أَمْرًا فَصَبْرٌ جَمِيلٌ عَسَى اللَّهُ أَنْ يَأْتِيَنِي بِهِمْ جَمِيعًا إِنَّهُ هُوَ الْعَلِيمُ الْحَكِيمُ}
INSERT INTO [b9617] VALUES (1636,'<span data-type="title" id=toc-1622>٨٤ </span>- {وَتَوَلَّى عَنْهُمْ وَقَالَ يَا أَسَفَى عَلَى يُوسُفَ وَابْيَضَّتْ عَيْنَاهُ مِنَ الْحُزْنِ فَهُوَ كَظِيمٌ}
INSERT INTO [b9617] VALUES (1637,'<span data-type="title" id=toc-1623>٨٥ </span>- {قَالُوا تَاللَّهِ تَفْتَأُ تَذْكُرُ يُوسُفَ}
INSERT INTO [b9617] VALUES (1638,'<span data-type="title" id=toc-1624>٨٦ </span>- {قَالَ إِنَّمَا أَشْكُو بَثِّي وَحُزْنِي إِلَى اللَّهِ وَأَعْلَمُ مِنَ اللَّهِ مَا لا تَعْلَمُونَ}
INSERT INTO [b9617] VALUES (1639,'<span data-type="title" id=toc-1625>٨٧ </span>- {يَا بَنِيَّ اذْهَبُوا فَتَحَسَّسُوا مِنْ يُوسُفَ وَأَخِيهِ وَلا تَيْأَسُوا مِنْ رَوْحِ اللَّهِ إِنَّهُ لا يَيْئَسُ مِنْ رَوْحِ اللَّهِ إِلا الْقَوْمُ الْكَافِرُونَ} ⦗٥١٧⦘
INSERT INTO [b9617] VALUES (1640,'<span data-type="title" id=toc-1626>٨٨ </span>- {فَلَمَّا دَخَلُوا عَلَيْهِ قَالُوا يَا أَيُّهَا الْعَزِيزُ مَسَّنَا وَأَهْلَنَا الضُّرُّ وَجِئْنَا بِبِضَاعَةٍ مُزْجَاةٍ فَأَوْفِ لَنَا الْكَيْلَ وَتَصَدَّقْ عَلَيْنَا إِنَّ اللَّهَ يَجْزِي الْمُتَصَدِّقِينَ}
INSERT INTO [b9617] VALUES (1641,'<span data-type="title" id=toc-1627>٨٩ </span>- {قَالَ هَلْ عَلِمْتُمْ مَا فَعَلْتُمْ بِيُوسُفَ وَأَخِيهِ إِذْ أَنْتُمْ جَاهِلُونَ}
INSERT INTO [b9617] VALUES (1642,'<span data-type="title" id=toc-1628>٩٠ </span>- {قَالُوا أَئِنَّكَ لأَنْتَ يُوسُفُ قَالَ أَنَا يُوسُفُ وَهَذَا أَخِي قَدْ مَنَّ اللَّهُ عَلَيْنَا إِنَّهُ مَنْ يَتَّقِ وَيَصْبِرْ فَإِنَّ اللَّهَ لا يُضِيعُ أَجْرَ الْمُحْسِنِينَ}
INSERT INTO [b9617] VALUES (1643,'<span data-type="title" id=toc-1629>٩١ </span>- {قَالُوا تَاللَّهِ لَقَدْ آثَرَكَ اللَّهُ عَلَيْنَا وَإِنْ كُنَّا لَخَاطِئِينَ}
INSERT INTO [b9617] VALUES (1644,'<span data-type="title" id=toc-1630>٩٢ </span>- {قَالَ لا تَثْرِيبَ عَلَيْكُمُ الْيَوْمَ يَغْفِرُ اللَّهُ لَكُمْ وَهُوَ أَرْحَمُ الرَّاحِمِينَ}
INSERT INTO [b9617] VALUES (1645,'<span data-type="title" id=toc-1631>٩٣ </span>- {اذْهَبُوا بِقَمِيصِي هَذَا فَأَلْقُوهُ عَلَى وَجْهِ أَبِي يَأْتِ بَصِيرًا وَأْتُونِي بِأَهْلِكُمْ أَجْمَعِينَ}
INSERT INTO [b9617] VALUES (1646,'<span data-type="title" id=toc-1632>٩٤ </span>- {وَلَمَّا فَصَلَتِ الْعِيرُ قَالَ أَبُوهُمْ إِنِّي لأَجِدُ رِيحَ يُوسُفَ لَوْلا أَنْ تُفَنِّدُونِ}
INSERT INTO [b9617] VALUES (1647,'<span data-type="title" id=toc-1633>٩٥ </span>- {قَالُوا تَاللَّهِ إِنَّكَ لَفِي ضَلالِكَ الْقَدِيمِ}
INSERT INTO [b9617] VALUES (1648,'<span data-type="title" id=toc-1634>٩٦ </span>- {فَلَمَّا أَنْ جَاءَ الْبَشِيرُ أَلْقَاهُ عَلَى وَجْهِهِ فَارْتَدَّ بَصِيرًا قَالَ أَلَمْ أَقُلْ لَكُمْ إِنِّي أَعْلَمُ مِنَ اللَّهِ مَا لا تَعْلَمُونَ}
INSERT INTO [b9617] VALUES (1649,'<span data-type="title" id=toc-1635>٩٧ </span>- {إِنَّا كُنَّا خَاطِئِينَ}
INSERT INTO [b9617] VALUES (1650,'<span data-type="title" id=toc-1636>٩٨ </span>- {إِنَّهُ هُوَ الْغَفُورُ الرَّحِيمُ}
INSERT INTO [b9617] VALUES (1651,'<span data-type="title" id=toc-1637>٩٩ </span>- {وَقَالَ ادْخُلُوا مِصْرَ إِنْ شَاءَ اللَّهُ آمِنِينَ}
INSERT INTO [b9617] VALUES (1652,'<span data-type="title" id=toc-1638>١٠٠ </span>- {وَرَفَعَ أَبَوَيْهِ عَلَى الْعَرْشِ وَخَرُّوا لَهُ سُجَّدًا وَقَالَ يَا أَبَتِ هَذَا تَأْوِيلُ رُؤْيَايَ مِنْ قَبْلُ قَدْ جَعَلَهَا رَبِّي حَقًّا وَقَدْ أَحْسَنَ بِي إِذْ أَخْرَجَنِي مِنَ السِّجْنِ وَجَاءَ بِكُمْ مِنَ الْبَدْوِ مِنْ بَعْدِ أَنْ نَزَغَ الشَّيْطَانُ بَيْنِي وَبَيْنَ إِخْوَتِي إِنَّ رَبِّي لَطِيفٌ لِمَا يَشَاءُ إِنَّهُ هُوَ الْعَلِيمُ الْحَكِيمُ} ⦗٥٢٠⦘
INSERT INTO [b9617] VALUES (1653,'<span data-type="title" id=toc-1639>١٠١ </span>- {رَبِّ قَدْ آتَيْتَنِي مِنَ الْمُلْكِ وَعَلَّمْتَنِي مِنْ تَأْوِيلِ الأَحَادِيثِ فَاطِرَ السَّمَاوَاتِ وَالأَرْضِ أَنْتَ وَلِيِّي فِي الدُّنْيَا وَالآخِرَةِ تَوَفَّنِي مُسْلِمًا وَأَلْحِقْنِي بِالصَّالِحِينَ}
INSERT INTO [b9617] VALUES (1654,'<span data-type="title" id=toc-1640>١٠٢ </span>- {ذَلِكَ مِنْ أَنْبَاءِ الْغَيْبِ نُوحِيهِ إِلَيْكَ وَمَا كُنْتَ لَدَيْهِمْ إِذْ أَجْمَعُوا أَمْرَهُمْ وَهُمْ يَمْكُرُونَ}
INSERT INTO [b9617] VALUES (1655,'<span data-type="title" id=toc-1641>١٠٣ </span>- {وَمَا أَكْثَرُ النَّاسِ وَلَوْ حَرَصْتَ بِمُؤْمِنِينَ}
INSERT INTO [b9617] VALUES (1656,'<span data-type="title" id=toc-1642>١٠٤ </span>- {وَمَا تَسْأَلُهُمْ عَلَيْهِ مِنْ أَجْرٍ إِنْ هُوَ إِلا ذِكْرٌ لِلْعَالَمِينَ}
INSERT INTO [b9617] VALUES (1657,'<span data-type="title" id=toc-1643>١٠٥ </span>- {وَكَأَيِّنْ مِنْ آيَةٍ فِي السَّمَاوَاتِ وَالأَرْضِ يَمُرُّونَ عَلَيْهَا وَهُمْ عَنْهَا مُعْرِضُونَ}
INSERT INTO [b9617] VALUES (1658,'<span data-type="title" id=toc-1644>١٠٦ </span>- {وَمَا يُؤْمِنُ أَكْثَرُهُمْ بِاللَّهِ إِلا وَهُمْ مُشْرِكُونَ}
INSERT INTO [b9617] VALUES (1659,'<span data-type="title" id=toc-1645>١٠٧ </span>- {أَفَأَمِنُوا أَنْ تَأْتِيَهُمْ غَاشِيَةٌ مِنْ عَذَابِ اللَّهِ أَوْ تَأْتِيَهُمُ السَّاعَةُ بَغْتَةً وَهُمْ لا يَشْعُرُونَ}
INSERT INTO [b9617] VALUES (1660,'<span data-type="title" id=toc-1646>١٠٨ </span>- {قُلْ هَذِهِ سَبِيلِي أَدْعُو إِلَى اللَّهِ عَلَى بَصِيرَةٍ أَنَا وَمَنِ اتَّبَعَنِي وَسُبْحَانَ اللَّهِ وَمَا أَنَا مِنَ الْمُشْرِكِينَ}
INSERT INTO [b9617] VALUES (1661,'<span data-type="title" id=toc-1647>١٠٩ </span>- {وَمَا أَرْسَلْنَا مِنْ قَبْلِكَ إِلا رِجَالا نُوحِي إِلَيْهِمْ مِنْ أَهْلِ الْقُرَى أَفَلَمْ يَسِيرُوا فِي الأَرْضِ فَيَنْظُرُوا كَيْفَ كَانَ عَاقِبَةُ الَّذِينَ مِنْ قَبْلِهِمْ وَلَدَارُ الآخِرَةِ خَيْرٌ لِلَّذِينَ اتَّقَوْا}
INSERT INTO [b9617] VALUES (1662,'<span data-type="title" id=toc-1648>١١٠ </span>- {حَتَّى إِذَا اسْتَيْئَسَ الرُّسُلُ وَظَنُّوا أَنَّهُمْ قَدْ كُذِبُوا جَاءَهُمْ نَصْرُنَا فَنُجِّيَ مَنْ نَشَاءُ وَلا يُرَدُّ بَأْسُنَا عَنِ الْقَوْمِ الْمُجْرِمِينَ}
INSERT INTO [b9617] VALUES (1663,'<span data-type="title" id=toc-1649>١١١ </span>- {لَقَدْ كَانَ فِي قَصَصِهِمْ عِبْرَةٌ لأُولِي الأَلْبَابِ مَا كَانَ حَدِيثًا يُفْتَرَى وَلَكِنْ تَصْدِيقَ الَّذِي بَيْنَ يَدَيْهِ وَتَفْصِيلَ كُلِّ شَيْءٍ وَهُدًى وَرَحْمَةً لِقَوْمٍ يُؤْمِنُونَ}
INSERT INTO [b9617] VALUES (1664,'<span data-type="title" id=toc-1650>سورة الرعد</span>','2','524',NULL,NULL,'0');
INSERT INTO [b9617] VALUES (1665,'<span data-type="title" id=toc-1651>١ </span>- {المر تِلْكَ آيَاتُ الْكِتَابِ وَالَّذِي أُنْزِلَ إِلَيْكَ مِنْ رَبِّكَ الْحَقُّ وَلَكِنَّ أَكْثَرَ النَّاسِ لا يُؤْمِنُونَ}
INSERT INTO [b9617] VALUES (1666,'<span data-type="title" id=toc-1652>٢ </span>- {اللَّهُ الَّذِي رَفَعَ السَّمَاوَاتِ بِغَيْرِ عَمَدٍ تَرَوْنَهَا ثُمَّ اسْتَوَى عَلَى الْعَرْشِ وَسَخَّرَ الشَّمْسَ وَالْقَمَرَ كُلٌّ يَجْرِي لأَجَلٍ مُسَمًّى يُدَبِّرُ الأَمْرَ يُفَصِّلُ الآيَاتِ لَعَلَّكُمْ بِلِقَاءِ رَبِّكُمْ تُوقِنُونَ}
INSERT INTO [b9617] VALUES (1667,'<span data-type="title" id=toc-1653>٣ </span>- {وَجَعَلَ فِيهَا رَوَاسِيَ وَأَنْهَارًا وَمِنْ كُلِّ الثَّمَرَاتِ جَعَلَ فِيهَا زَوْجَيْنِ اثْنَيْنِ يُغْشِي اللَّيْلَ النَّهَارَ إِنَّ فِي ذَلِكَ لآيَاتٍ لِقَوْمٍ يَتَفَكَّرُونَ}
INSERT INTO [b9617] VALUES (1668,'<span data-type="title" id=toc-1654>٤ </span>- {وَفِي الأَرْضِ قِطَعٌ مُتَجَاوِرَاتٌ وَجَنَّاتٌ مِنْ أَعْنَابٍ وَزَرْعٌ وَنَخِيلٌ صِنْوَانٌ وَغَيْرُ ⦗٥٢٥⦘ صِنْوَانٍ يُسْقَى بِمَاءٍ وَاحِدٍ وَنُفَضِّلُ بَعْضَهَا عَلَى بَعْضٍ فِي الأُكُلِ إِنَّ فِي ذَلِكَ لآيَاتٍ لِقَوْمٍ يَعْقِلُونَ}
INSERT INTO [b9617] VALUES (1669,'<span data-type="title" id=toc-1655>٥ </span>- {وَإِنْ تَعْجَبْ فَعَجَبٌ قَوْلُهُمْ أَئِذَا كُنَّا تُرَابًا أَئِنَّا لَفِي خَلْقٍ جَدِيدٍ أُولَئِكَ الَّذِينَ كَفَرُوا بِرَبِّهِمْ وَأُولَئِكَ الأَغْلالُ فِي أَعْنَاقِهِمْ وَأُولَئِكَ أَصْحَابُ النَّارِ هُمْ فِيهَا خَالِدُونَ}
INSERT INTO [b9617] VALUES (1670,'<span data-type="title" id=toc-1656>٦ </span>- {وَيَسْتَعْجِلُونَكَ بِالسَّيِّئَةِ قَبْلَ الْحَسَنَةِ وَقَدْ خَلَتْ مِنْ قَبْلِهِمُ الْمَثُلاتُ وَإِنَّ رَبَّكَ لَذُو مَغْفِرَةٍ لِلنَّاسِ عَلَى ظُلْمِهِمْ}
INSERT INTO [b9617] VALUES (1671,'<span data-type="title" id=toc-1657>٧ </span>- {وَيَقُولُ الَّذِينَ كَفَرُوا لَوْلا أُنْزِلَ عَلَيْهِ آيَةٌ مِنْ رَبِّهِ إِنَّمَا أَنْتَ مُنْذِرٌ وَلِكُلِّ قَوْمٍ هَادٍ}
INSERT INTO [b9617] VALUES (1672,'<span data-type="title" id=toc-1658>٨ </span>- {اللَّهُ يَعْلَمُ مَا تَحْمِلُ كُلُّ أُنْثَى وَمَا تَغِيضُ الأَرْحَامُ وَمَا تَزْدَادُ وَكُلُّ شَيْءٍ عِنْدَهُ بِمِقْدَارٍ}
INSERT INTO [b9617] VALUES (1673,'<span data-type="title" id=toc-1659>٩ </span>- {عَالِمُ الْغَيْبِ وَالشَّهَادَةِ الْكَبِيرُ الْمُتَعَالِ}
INSERT INTO [b9617] VALUES (1674,'<span data-type="title" id=toc-1660>١٠ </span>- {سَوَاءٌ مِنْكُمْ مَنْ أَسَرَّ الْقَوْلَ وَمَنْ جَهَرَ بِهِ وَمَنْ هُوَ مُسْتَخْفٍ بِاللَّيْلِ وَسَارِبٌ ⦗٥٢٧⦘ بِالنَّهَارِ}
INSERT INTO [b9617] VALUES (1675,'<span data-type="title" id=toc-1661>١١ </span>- {لَهُ مُعَقِّبَاتٌ مِنْ بَيْنِ يَدَيْهِ وَمِنْ خَلْفِهِ يَحْفَظُونَهُ مِنْ أَمْرِ اللَّهِ إِنَّ اللَّهَ لا يُغَيِّرُ مَا بِقَوْمٍ حَتَّى يُغَيِّرُوا مَا بِأَنْفُسِهِمْ وَإِذَا أَرَادَ اللَّهُ بِقَوْمٍ سُوءًا فَلا مَرَدَّ لَهُ وَمَا لَهُمْ مِنْ دُونِهِ مِنْ وَالٍ}
INSERT INTO [b9617] VALUES (1676,'<span data-type="title" id=toc-1662>١٢ </span>- {هُوَ الَّذِي يُرِيكُمُ الْبَرْقَ خَوْفًا وَطَمَعًا وَيُنْشِئُ السَّحَابَ الثِّقَالَ}
INSERT INTO [b9617] VALUES (1677,'<span data-type="title" id=toc-1663>١٣ </span>- {وَيُسَبِّحُ الرَّعْدُ بِحَمْدِهِ وَالْمَلائِكَةُ مِنْ خِيفَتِهِ وَيُرْسِلُ الصَّوَاعِقَ فَيُصِيبُ بِهَا مَنْ يَشَاءُ وَهُمْ يُجَادِلُونَ فِي اللَّهِ وَهُوَ شَدِيدُ الْمِحَالِ}
INSERT INTO [b9617] VALUES (1678,'<span data-type="title" id=toc-1664>١٤ </span>- {لَهُ دَعْوَةُ الْحَقِّ وَالَّذِينَ يَدْعُونَ مِنْ دُونِهِ لا يَسْتَجِيبُونَ لَهُمْ بِشَيْءٍ إِلا كَبَاسِطِ كَفَّيْهِ إِلَى الْمَاءِ لِيَبْلُغَ فَاهُ وَمَا هُوَ بِبَالِغِهِ وَمَا دُعَاءُ الْكَافِرِينَ إِلا فِي ضَلالٍ}
INSERT INTO [b9617] VALUES (1679,'<span data-type="title" id=toc-1665>١٥ </span>- {وَلِلَّهِ يَسْجُدُ مَنْ فِي السَّمَاوَاتِ وَالأَرْضِ طَوْعًا وَكَرْهًا}
INSERT INTO [b9617] VALUES (1680,'<span data-type="title" id=toc-1666>١٦ </span>- {قُلْ مَنْ رَبُّ السَّمَاوَاتِ وَالأَرْضِ قُلِ اللَّهُ قُلْ أَفَاتَّخَذْتُمْ مِنْ دُونِهِ أَوْلِيَاءَ لا يَمْلِكُونَ لأَنْفُسِهِمْ نَفْعًا وَلا ضَرًّا قُلْ هَلْ يَسْتَوِي الأَعْمَى وَالْبَصِيرُ أَمْ هَلْ تَسْتَوِي الظُّلُمَاتُ وَالنُّورُ أَمْ جَعَلُوا لِلَّهِ شُرَكَاءَ خَلَقُوا كَخَلْقِهِ فَتَشَابَهَ الْخَلْقُ عَلَيْهِمْ قُلِ اللَّهُ خَالِقُ كُلِّ شَيْءٍ وَهُوَ الْوَاحِدُ الْقَهَّارُ}
INSERT INTO [b9617] VALUES (1681,'<span data-type="title" id=toc-1667>١٧ </span>- {أَنْزَلَ مِنَ السَّمَاءِ مَاءً فَسَالَتْ أَوْدِيَةٌ بِقَدَرِهَا فَاحْتَمَلَ السَّيْلُ زَبَدًا رَابِيًا وَمِمَّا يُوقِدُونَ عَلَيْهِ فِي النَّارِ ابْتِغَاءَ حِلْيَةٍ أَوْ مَتَاعٍ زَبَدٌ مِثْلُهُ كَذَلِكَ يَضْرِبُ اللَّهُ الْحَقَّ وَالْبَاطِلَ فَأَمَّا الزَّبَدُ فَيَذْهَبُ جُفَاءً وَأَمَّا مَا يَنْفَعُ النَّاسَ فَيَمْكُثُ فِي الأَرْضِ كَذَلِكَ يَضْرِبُ اللَّهُ الأَمْثَالَ}
INSERT INTO [b9617] VALUES (1682,'<span data-type="title" id=toc-1668>١٨ </span>- {لِلَّذِينَ اسْتَجَابُوا لِرَبِّهِمُ الْحُسْنَى وَالَّذِينَ لَمْ يَسْتَجِيبُوا لَهُ لَوْ أَنَّ لَهُمْ مَا فِي الأَرْضِ جَمِيعًا وَمِثْلَهُ مَعَهُ لافْتَدَوْا بِهِ أُولَئِكَ لَهُمْ سُوءُ الْحِسَابِ وَمَأْوَاهُمْ جَهَنَّمُ وَبِئْسَ الْمِهَادُ}
INSERT INTO [b9617] VALUES (1683,'<span data-type="title" id=toc-1669>١٩ </span>- {أَفَمَنْ يَعْلَمُ أَنَّمَا أُنْزِلَ إِلَيْكَ مِنْ رَبِّكَ الْحَقُّ كَمَنْ هُوَ أَعْمَى}
INSERT INTO [b9617] VALUES (1684,'<span data-type="title" id=toc-1670>٢٠ </span>- {الَّذِينَ يُوفُونَ بِعَهْدِ اللَّهِ وَلا يَنْقُضُونَ الْمِيثَاقَ}
INSERT INTO [b9617] VALUES (1685,'<span data-type="title" id=toc-1671>٢١ </span>- {وَالَّذِينَ يَصِلُونَ مَا أَمَرَ اللَّهُ بِهِ أَنْ يُوصَلَ}
INSERT INTO [b9617] VALUES (1686,'<span data-type="title" id=toc-1672>٢٢ </span>- {وَالَّذِينَ صَبَرُوا ابْتِغَاءَ وَجْهِ رَبِّهِمْ وَأَقَامُوا الصَّلاةَ وَأَنْفَقُوا مِمَّا رَزَقْنَاهُمْ سِرًّا وَعَلانِيَةً وَيَدْرَءُونَ بِالْحَسَنَةِ السَّيِّئَةَ أُولَئِكَ لَهُمْ عُقْبَى الدَّارِ}
INSERT INTO [b9617] VALUES (1687,'<span data-type="title" id=toc-1673>٢٣ </span>- {جَنَّاتُ عَدْنٍ يَدْخُلُونَهَا وَمَنْ صَلَحَ مِنْ آبَائِهِمْ وَأَزْوَاجِهِمْ وَذُرِّيَّاتِهِمْ وَالْمَلائِكَةُ يَدْخُلُونَ عَلَيْهِمْ مِنْ كُلِّ بَابٍ}
INSERT INTO [b9617] VALUES (1688,'<span data-type="title" id=toc-1674>٢٤ </span>- {سَلامٌ عَلَيْكُمْ بِمَا صَبَرْتُمْ فَنِعْمَ عُقْبَى الدَّارِ}
INSERT INTO [b9617] VALUES (1689,'<span data-type="title" id=toc-1675>٢٥ </span>- {وَالَّذِينَ يَنْقُضُونَ عَهْدَ اللَّهِ مِنْ بَعْدِ مِيثَاقِهِ وَيَقْطَعُونَ مَا أَمَرَ اللَّهُ بِهِ أَنْ يُوصَلَ وَيُفْسِدُونَ فِي الأَرْضِ أُولَئِكَ لَهُمُ اللَّعْنَةُ وَلَهُمْ سُوءُ الدَّارِ}
INSERT INTO [b9617] VALUES (1690,'<span data-type="title" id=toc-1676>٢٦ </span>- {وَفَرِحُوا بِالْحَيَاةِ الدُّنْيَا وَمَا الْحَيَاةُ الدُّنْيَا فِي الآخِرَةِ إِلا مَتَاعٌ}
INSERT INTO [b9617] VALUES (1691,'<span data-type="title" id=toc-1677>٢٧ </span>- {وَيَقُولُ الَّذِينَ كَفَرُوا لَوْلا أُنْزِلَ عَلَيْهِ آيَةٌ مِنْ رَبِّهِ}
INSERT INTO [b9617] VALUES (1692,'<span data-type="title" id=toc-1678>٢٨ </span>- {الَّذِينَ آمَنُوا وَتَطْمَئِنُّ قُلُوبُهُمْ بِذِكْرِ اللَّهِ أَلا بِذِكْرِ اللَّهِ تَطْمَئِنُّ الْقُلُوبُ}
INSERT INTO [b9617] VALUES (1693,'<span data-type="title" id=toc-1679>٢٩ </span>- {الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ طُوبَى لَهُمْ وَحُسْنُ مَآبٍ} ⦗٥٣٣⦘
INSERT INTO [b9617] VALUES (1694,'<span data-type="title" id=toc-1680>٣٠ </span>- {كَذَلِكَ أَرْسَلْنَاكَ فِي أُمَّةٍ قَدْ خَلَتْ مِنْ قَبْلِهَا أُمَمٌ لِتَتْلُوَ عَلَيْهِمُ الَّذِي أَوْحَيْنَا إِلَيْكَ وَهُمْ يَكْفُرُونَ بِالرَّحْمَنِ قُلْ هُوَ رَبِّي لا إِلَهَ إِلا هُوَ عَلَيْهِ تَوَكَّلْتُ وَإِلَيْهِ مَتَابِ}
INSERT INTO [b9617] VALUES (1695,'<span data-type="title" id=toc-1681>٣١ </span>- {وَلَوْ أَنَّ قُرْآنًا سُيِّرَتْ بِهِ الْجِبَالُ أَوْ قُطِّعَتْ بِهِ الأَرْضُ أَوْ كُلِّمَ بِهِ الْمَوْتَى بَلْ لِلَّهِ الأَمْرُ جَمِيعًا أَفَلَمْ يَيْأَسِ الَّذِينَ آمَنُوا أَنْ لَوْ يَشَاءُ اللَّهُ لَهَدَى النَّاسَ جَمِيعًا وَلا يَزَالُ الَّذِينَ كَفَرُوا تُصِيبُهُمْ بِمَا صَنَعُوا قَارِعَةٌ أَوْ تَحُلُّ قَرِيبًا مِنْ دَارِهِمْ}
INSERT INTO [b9617] VALUES (1696,'<span data-type="title" id=toc-1682>٣٢ </span>- {وَلَقَدِ اسْتُهْزِئَ بِرُسُلٍ مِنْ قَبْلِكَ فَأَمْلَيْتُ لِلَّذِينَ كَفَرُوا ثُمَّ أَخَذْتُهُمْ فَكَيْفَ كَانَ عِقَابِ}
INSERT INTO [b9617] VALUES (1697,'<span data-type="title" id=toc-1683>٣٣ </span>- {أَفَمَنْ هُوَ قَائِمٌ عَلَى كُلِّ نَفْسٍ بِمَا كَسَبَتْ وَجَعَلُوا لِلَّهِ شُرَكَاءَ قُلْ سَمُّوهُمْ أَمْ تُنَبِّئُونَهُ بِمَا لا يَعْلَمُ فِي الأَرْضِ أَمْ بِظَاهِرٍ مِنَ الْقَوْلِ بَلْ زُيِّنَ لِلَّذِينَ كَفَرُوا مَكْرُهُمْ وَصُدُّوا عَنِ السَّبِيلِ وَمَنْ يُضْلِلِ اللَّهُ فَمَا لَهُ مِنْ هَادٍ}
INSERT INTO [b9617] VALUES (1698,'<span data-type="title" id=toc-1684>٣٤ </span>- {لَهُمْ عَذَابٌ فِي الْحَيَاةِ الدُّنْيَا وَلَعَذَابُ الآخِرَةِ أَشَقُّ وَمَا لَهُمْ مِنَ اللَّهِ مِنْ وَاقٍ}
INSERT INTO [b9617] VALUES (1699,'<span data-type="title" id=toc-1685>٣٥ </span>- {مَثَلُ الْجَنَّةِ الَّتِي وُعِدَ الْمُتَّقُونَ تَجْرِي مِنْ تَحْتِهَا الأَنْهَارُ أُكُلُهَا دَائِمٌ وَظِلُّهَا تِلْكَ عُقْبَى الَّذِينَ اتَّقَوْا وَعُقْبَى الْكَافِرِينَ النَّارُ}
INSERT INTO [b9617] VALUES (1700,'<span data-type="title" id=toc-1686>٣٦ </span>- {وَالَّذِينَ آتَيْنَاهُمُ الْكِتَابَ يَفْرَحُونَ بِمَا أُنْزِلَ إِلَيْكَ وَمِنَ الأَحْزَابِ مَنْ يُنْكِرُ بَعْضَهُ قُلْ إِنَّمَا أُمِرْتُ أَنْ أَعْبُدَ اللَّهَ وَلا أُشْرِكَ بِهِ إِلَيْهِ أَدْعُو وَإِلَيْهِ مَآبِ}
INSERT INTO [b9617] VALUES (1701,'<span data-type="title" id=toc-1687>٣٧ </span>- {وَكَذَلِكَ أَنْزَلْنَاهُ حُكْمًا عَرَبِيًّا وَلَئِنِ اتَّبَعْتَ أَهْوَاءَهُمْ بَعْدَمَا جَاءَكَ مِنَ الْعِلْمِ مَا لَكَ مِنَ اللَّهِ مِنْ وَلِيٍّ وَلا وَاقٍ}
INSERT INTO [b9617] VALUES (1702,'<span data-type="title" id=toc-1688>٣٨ </span>- {وَلَقَدْ أَرْسَلْنَا رُسُلا مِنْ قَبْلِكَ وَجَعَلْنَا لَهُمْ أَزْوَاجًا وَذُرِّيَّةً وَمَا كَانَ لِرَسُولٍ أَنْ يَأْتِيَ بِآيَةٍ إِلا بِإِذْنِ اللَّهِ لِكُلِّ أَجَلٍ كِتَابٌ}
INSERT INTO [b9617] VALUES (1703,'<span data-type="title" id=toc-1689>٣٩ </span>- {يَمْحُو اللَّهُ مَا يَشَاءُ وَيُثْبِتُ وَعِنْدَهُ أُمُّ الْكِتَابِ}
INSERT INTO [b9617] VALUES (1704,'<span data-type="title" id=toc-1690>٤٠ </span>- {وَإِنْ مَا نُرِيَنَّكَ بَعْضَ الَّذِي نَعِدُهُمْ أَوْ نَتَوَفَّيَنَّكَ فَإِنَّمَا عَلَيْكَ الْبَلاغُ} ⦗٥٣٧⦘
INSERT INTO [b9617] VALUES (1705,'<span data-type="title" id=toc-1691>٤١ </span>- {أَوَلَمْ يَرَوْا أَنَّا نَأْتِي الأَرْضَ نَنْقُصُهَا مِنْ أَطْرَافِهَا وَاللَّهُ يَحْكُمُ لا مُعَقِّبَ لِحُكْمِهِ وَهُوَ سَرِيعُ الْحِسَابِ}
INSERT INTO [b9617] VALUES (1706,'<span data-type="title" id=toc-1692>٤٢ </span>- {وَقَدْ مَكَرَ الَّذِينَ مِنْ قَبْلِهِمْ فَلِلَّهِ الْمَكْرُ جَمِيعًا يَعْلَمُ مَا تَكْسِبُ كُلُّ نَفْسٍ وَسَيَعْلَمُ الْكُفَّارُ لِمَنْ عُقْبَى الدَّارِ}
INSERT INTO [b9617] VALUES (1707,'<span data-type="title" id=toc-1693>٤٣ </span>- {قُلْ كَفَى بِاللَّهِ شَهِيدًا بَيْنِي وَبَيْنَكُمْ وَمَنْ عِنْدَهُ عِلْمُ الْكِتَابِ}
INSERT INTO [b9617] VALUES (1708,'<span data-type="title" id=toc-1694>سورة إبراهيم</span>','2','538',NULL,NULL,'0');
INSERT INTO [b9617] VALUES (1709,'<span data-type="title" id=toc-1695>١ </span>- {الر كِتَابٌ أَنْزَلْنَاهُ إِلَيْكَ لِتُخْرِجَ النَّاسَ مِنَ الظُّلُمَاتِ إِلَى النُّورِ بِإِذْنِ رَبِّهِمْ إِلَى صِرَاطِ الْعَزِيزِ الْحَمِيدِ}
INSERT INTO [b9617] VALUES (1710,'<span data-type="title" id=toc-1696>٢ </span>- {اللَّهِ الَّذِي لَهُ مَا فِي السَّمَاوَاتِ وَمَا فِي الأَرْضِ وَوَيْلٌ لِلْكَافِرِينَ مِنْ عَذَابٍ شَدِيدٍ}
INSERT INTO [b9617] VALUES (1711,'<span data-type="title" id=toc-1697>٣ </span>- {الَّذِينَ يَسْتَحِبُّونَ الْحَيَاةَ الدُّنْيَا عَلَى الآخِرَةِ وَيَصُدُّونَ عَنْ سَبِيلِ اللَّهِ وَيَبْغُونَهَا عِوَجًا أُولَئِكَ فِي ضَلالٍ بَعِيدٍ}
INSERT INTO [b9617] VALUES (1712,'<span data-type="title" id=toc-1698>٤ </span>- {وَمَا أَرْسَلْنَا مِنْ رَسُولٍ إِلا بِلِسَانِ قَوْمِهِ لِيُبَيِّنَ لَهُمْ فَيُضِلُّ اللَّهُ مَنْ يَشَاءُ وَيَهْدِي مَنْ يَشَاءُ وَهُوَ الْعَزِيزُ الْحَكِيمُ}
INSERT INTO [b9617] VALUES (1713,'<span data-type="title" id=toc-1699>٥ </span>- {وَلَقَدْ أَرْسَلْنَا مُوسَى بِآيَاتِنَا أَنْ أَخْرِجْ قَوْمَكَ مِنَ الظُّلُمَاتِ إِلَى النُّورِ وَذَكِّرْهُمْ بِأَيَّامِ اللَّهِ إِنَّ فِي ذَلِكَ لآيَاتٍ لِكُلِّ صَبَّارٍ شَكُورٍ}
INSERT INTO [b9617] VALUES (1714,'<span data-type="title" id=toc-1700>٦ </span>- {وَإِذْ قَالَ مُوسَى لِقَوْمِهِ اذْكُرُوا نِعْمَةَ اللَّهِ عَلَيْكُمْ إِذْ أَنْجَاكُمْ مِنْ آلِ فِرْعَوْنَ يَسُومُونَكُمْ سُوءَ الْعَذَابِ وَيُذَبِّحُونَ أَبْنَاءَكُمْ وَيَسْتَحْيُونَ نِسَاءَكُمْ وَفِي ذَلِكُمْ بَلاءٌ مِنْ رَبِّكُمْ عَظِيمٌ}
INSERT INTO [b9617] VALUES (1715,'<span data-type="title" id=toc-1701>٧ </span>- {وَإِذْ تَأَذَّنَ رَبُّكُمْ لَئِنْ شَكَرْتُمْ لأَزِيدَنَّكُمْ وَلَئِنْ كَفَرْتُمْ إِنَّ عَذَابِي لَشَدِيدٌ}
INSERT INTO [b9617] VALUES (1716,'<span data-type="title" id=toc-1702>٨ </span>- {إِنْ تَكْفُرُوا أَنْتُمْ وَمَنْ فِي الأَرْضِ جَمِيعًا فَإِنَّ اللَّهَ لَغَنِيٌّ حَمِيدٌ}
INSERT INTO [b9617] VALUES (1717,'<span data-type="title" id=toc-1703>٩ </span>- {أَلَمْ يَأْتِكُمْ نَبَأُ الَّذِينَ مِنْ قَبْلِكُمْ قَوْمِ نُوحٍ وَعَادٍ وَثَمُودَ وَالَّذِينَ مِنْ بَعْدِهِمْ لا يَعْلَمُهُمْ إِلا اللَّهُ جَاءَتْهُمْ رُسُلُهُمْ بِالْبَيِّنَاتِ فَرَدُّوا أَيْدِيَهُمْ فِي أَفْوَاهِهِمْ وَقَالُوا إِنَّا كَفَرْنَا بِمَا أُرْسِلْتُمْ بِهِ وَإِنَّا لَفِي شَكٍّ مِمَّا تَدْعُونَنَا إِلَيْهِ مُرِيبٍ}
INSERT INTO [b9617] VALUES (1718,'<span data-type="title" id=toc-1704>١٠ </span>- {قَالَتْ رُسُلُهُمْ أَفِي اللَّهِ شَكٌّ فَاطِرِ السَّمَاوَاتِ وَالأَرْضِ يَدْعُوكُمْ لِيَغْفِرَ لَكُمْ مِنْ ذُنُوبِكُمْ وَيُؤَخِّرَكُمْ إِلَى أَجَلٍ مُسَمًّى قَالُوا إِنْ أَنْتُمْ إِلا بَشَرٌ مِثْلُنَا تُرِيدُونَ أَنْ تَصُدُّونَا عَمَّا كَانَ يَعْبُدُ آبَاؤُنَا فَأْتُونَا بِسُلْطَانٍ مُبِينٍ} ⦗٥٤١⦘
INSERT INTO [b9617] VALUES (1719,'<span data-type="title" id=toc-1705>١١ </span>- {قَالَتْ لَهُمْ رُسُلُهُمْ إِنْ نَحْنُ إِلا بَشَرٌ مِثْلُكُمْ وَلَكِنَّ اللَّهَ يَمُنُّ عَلَى مَنْ يَشَاءُ مِنْ عِبَادِهِ وَمَا كَانَ لَنَا أَنْ نَأْتِيَكُمْ بِسُلْطَانٍ إِلا بِإِذْنِ اللَّهِ وَعَلَى اللَّهِ فَلْيَتَوَكَّلِ الْمُؤْمِنُونَ}
INSERT INTO [b9617] VALUES (1720,'<span data-type="title" id=toc-1706>١٢ </span>- {وَمَا لَنَا أَلا نَتَوَكَّلَ عَلَى اللَّهِ وَقَدْ هَدَانَا سُبُلَنَا وَلَنَصْبِرَنَّ عَلَى مَا آذَيْتُمُونَا وَعَلَى اللَّهِ فَلْيَتَوَكَّلِ الْمُتَوَكِّلُونَ}
INSERT INTO [b9617] VALUES (1721,'<span data-type="title" id=toc-1707>١٣ </span>- {وَقَالَ الَّذِينَ كَفَرُوا لِرُسُلِهِمْ لَنُخْرِجَنَّكُمْ مِنْ أَرْضِنَا أَوْ لَتَعُودُنَّ فِي مِلَّتِنَا فَأَوْحَى إِلَيْهِمْ رَبُّهُمْ لَنُهْلِكَنَّ الظَّالِمِينَ}
INSERT INTO [b9617] VALUES (1722,'<span data-type="title" id=toc-1708>١٤ </span>- {وَلَنُسْكِنَنَّكُمُ الأَرْضَ مِنْ بَعْدِهِمْ ذَلِكَ لِمَنْ خَافَ مَقَامِي وَخَافَ وَعِيدِ}
INSERT INTO [b9617] VALUES (1723,'<span data-type="title" id=toc-1709>١٥ </span>- {وَاسْتَفْتَحُوا وَخَابَ كُلُّ جَبَّارٍ عَنِيدٍ}
INSERT INTO [b9617] VALUES (1724,'<span data-type="title" id=toc-1710>١٦ </span>- {مِنْ وَرَائِهِ جَهَنَّمُ وَيُسْقَى مِنْ مَاءٍ صَدِيدٍ}
INSERT INTO [b9617] VALUES (1725,'<span data-type="title" id=toc-1711>١٧ </span>- {يَتَجَرَّعُهُ وَلا يَكَادُ يُسِيغُهُ وَيَأْتِيهِ الْمَوْتُ مِنْ كُلِّ مَكَانٍ وَمَا هُوَ بِمَيِّتٍ وَمِنْ وَرَائِهِ عَذَابٌ غَلِيظٌ} ⦗٥٤٣⦘
INSERT INTO [b9617] VALUES (1726,'<span data-type="title" id=toc-1712>١٨ </span>- {مَثَلُ الَّذِينَ كَفَرُوا بِرَبِّهِمْ أَعْمَالُهُمْ كَرَمَادٍ اشْتَدَّتْ بِهِ الرِّيحُ فِي يَوْمٍ عَاصِفٍ لا يَقْدِرُونَ مِمَّا كَسَبُوا عَلَى شَيْءٍ ذَلِكَ هُوَ الضَّلالُ الْبَعِيدُ}
INSERT INTO [b9617] VALUES (1727,'<span data-type="title" id=toc-1713>١٩ </span>- {أَلَمْ تَرَ أَنَّ اللَّهَ خَلَقَ السَّمَاوَاتِ وَالأَرْضَ بِالْحَقِّ إِنْ يَشَأْ يُذْهِبْكُمْ وَيَأْتِ بِخَلْقٍ جَدِيدٍ}
INSERT INTO [b9617] VALUES (1728,'<span data-type="title" id=toc-1714>٢٠ </span>- {وَمَا ذَلِكَ عَلَى اللَّهِ بِعَزِيزٍ}
INSERT INTO [b9617] VALUES (1729,'<span data-type="title" id=toc-1715>٢١ </span>- {وَبَرَزُوا لِلَّهِ جَمِيعًا فَقَالَ الضُّعَفَاءُ لِلَّذِينَ اسْتَكْبَرُوا إِنَّا كُنَّا لَكُمْ تَبَعًا فَهَلْ ⦗٥٤٤⦘ أَنْتُمْ مُغْنُونَ عَنَّا مِنْ عَذَابِ اللَّهِ مِنْ شَيْءٍ قَالُوا لَوْ هَدَانَا اللَّهُ لَهَدَيْنَاكُمْ سَوَاءٌ عَلَيْنَا أَجَزِعْنَا أَمْ صَبَرْنَا مَا لَنَا مِنْ مَحِيصٍ}
INSERT INTO [b9617] VALUES (1730,'<span data-type="title" id=toc-1716>٢٢ </span>- {وَقَالَ الشَّيْطَانُ لَمَّا قُضِيَ الأَمْرُ إِنَّ اللَّهَ وَعَدَكُمْ وَعْدَ الْحَقِّ وَوَعَدْتُكُمْ فَأَخْلَفْتُكُمْ وَمَا كَانَ لِي عَلَيْكُمْ مِنْ سُلْطَانٍ إِلا أَنْ دَعَوْتُكُمْ فَاسْتَجَبْتُمْ لِي فَلا تَلُومُونِي وَلُومُوا أَنْفُسَكُمْ مَا أَنَا بِمُصْرِخِكُمْ وَمَا أَنْتُمْ بِمُصْرِخِيَّ إِنِّي كَفَرْتُ بِمَا أَشْرَكْتُمُونِ مِنْ قَبْلُ إِنَّ الظَّالِمِينَ لَهُمْ عَذَابٌ أَلِيمٌ}
INSERT INTO [b9617] VALUES (1731,'«إلا» للاستثناء، «أن» مصدرية، والمصدر المؤول منصوب على الاستثناء المنقطع؛ لأن دعاءه ليس من جنس الحجة البينة، وجملة «فلا تلوموني» مستأنفة في حيز القول. وقوله «ما أنا بمصرخكم» : «ما» نافية تعمل عمل ليس، والباء في خبرها زائدة، والجملة مستأنفة في حيز القول، وكذا جملة «إني كفرت» . وقوله «أشركتمون» : فعل ماض مبني على السكون، والتاء فاعل، والميم للجمع، والواو للإشباع، والنون للوقاية، والياء المحذوفة مفعول به، وجملة «لهم عذاب» خبر إن.','2','545',NULL,NULL,'0');
INSERT INTO [b9617] VALUES (1732,'<span data-type="title" id=toc-1717>٢٣ </span>- {وَأُدْخِلَ الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ جَنَّاتٍ تَجْرِي مِنْ تَحْتِهَا الأَنْهَارُ خَالِدِينَ فِيهَا بِإِذْنِ رَبِّهِمْ تَحِيَّتُهُمْ فِيهَا سَلامٌ}
INSERT INTO [b9617] VALUES (1733,'<span data-type="title" id=toc-1718>٢٤ </span>- {أَلَمْ تَرَ كَيْفَ ضَرَبَ اللَّهُ مَثَلا كَلِمَةً طَيِّبَةً كَشَجَرَةٍ طَيِّبَةٍ أَصْلُهَا ثَابِتٌ وَفَرْعُهَا فِي السَّمَاءِ}
INSERT INTO [b9617] VALUES (1734,'<span data-type="title" id=toc-1719>٢٥ </span>- {تُؤْتِي أُكُلَهَا كُلَّ حِينٍ بِإِذْنِ رَبِّهَا وَيَضْرِبُ اللَّهُ الأَمْثَالَ لِلنَّاسِ لَعَلَّهُمْ يَتَذَكَّرُونَ} ⦗٥٤٦⦘
INSERT INTO [b9617] VALUES (1735,'<span data-type="title" id=toc-1720>٢٦ </span>- {وَمَثَلُ كَلِمَةٍ خَبِيثَةٍ كَشَجَرَةٍ خَبِيثَةٍ اجْتُثَّتْ مِنْ فَوْقِ الأَرْضِ مَا لَهَا مِنْ قَرَارٍ}
INSERT INTO [b9617] VALUES (1736,'<span data-type="title" id=toc-1721>٢٧ </span>- {يُثَبِّتُ اللَّهُ الَّذِينَ آمَنُوا بِالْقَوْلِ الثَّابِتِ فِي الْحَيَاةِ الدُّنْيَا وَفِي الآخِرَةِ وَيُضِلُّ اللَّهُ الظَّالِمِينَ}
INSERT INTO [b9617] VALUES (1737,'<span data-type="title" id=toc-1722>٢٨ </span>- {أَلَمْ تَرَ إِلَى الَّذِينَ بَدَّلُوا نِعْمَتَ اللَّهِ كُفْرًا وَأَحَلُّوا قَوْمَهُمْ دَارَ الْبَوَارِ}
INSERT INTO [b9617] VALUES (1738,'<span data-type="title" id=toc-1723>٢٩ </span>- {جَهَنَّمَ يَصْلَوْنَهَا وَبِئْسَ الْقَرَارُ}
INSERT INTO [b9617] VALUES (1739,'<span data-type="title" id=toc-1724>٣٠ </span>- {وَجَعَلُوا لِلَّهِ أَنْدَادًا لِيُضِلُّوا عَنْ سَبِيلِهِ قُلْ تَمَتَّعُوا فَإِنَّ مَصِيرَكُمْ إِلَى النَّارِ}
INSERT INTO [b9617] VALUES (1740,'<span data-type="title" id=toc-1725>٣١ </span>- {قُلْ لِعِبَادِيَ الَّذِينَ آمَنُوا يُقِيمُوا الصَّلاةَ وَيُنْفِقُوا مِمَّا رَزَقْنَاهُمْ سِرًّا وَعَلانِيَةً مِنْ قَبْلِ أَنْ يَأْتِيَ يَوْمٌ لا بَيْعٌ فِيهِ وَلا خِلالٌ}
INSERT INTO [b9617] VALUES (1741,'<span data-type="title" id=toc-1726>٣٢ </span>- {اللَّهُ الَّذِي خَلَقَ السَّمَاوَاتِ وَالأَرْضَ وَأَنْزَلَ مِنَ السَّمَاءِ مَاءً فَأَخْرَجَ بِهِ مِنَ الثَّمَرَاتِ رِزْقًا لَكُمْ}
INSERT INTO [b9617] VALUES (1742,'<span data-type="title" id=toc-1727>٣٣ </span>- {وَسَخَّرَ لَكُمُ الشَّمْسَ وَالْقَمَرَ دَائِبَيْنِ}
INSERT INTO [b9617] VALUES (1743,'<span data-type="title" id=toc-1728>٣٤ </span>- {وَآتَاكُمْ مِنْ كُلِّ مَا سَأَلْتُمُوهُ وَإِنْ تَعُدُّوا نِعْمَتَ اللَّهِ لا تُحْصُوهَا إِنَّ الإِنْسَانَ لَظَلُومٌ كَفَّارٌ}
INSERT INTO [b9617] VALUES (1744,'<span data-type="title" id=toc-1729>٣٥ </span>- {وَإِذْ قَالَ إِبْرَاهِيمُ رَبِّ اجْعَلْ هَذَا الْبَلَدَ آمِنًا وَاجْنُبْنِي وَبَنِيَّ أَنْ نَعْبُدَ الأَصْنَامَ}
INSERT INTO [b9617] VALUES (1745,'<span data-type="title" id=toc-1730>٣٦ </span>- {رَبِّ إِنَّهُنَّ أَضْلَلْنَ كَثِيرًا مِنَ النَّاسِ فَمَنْ تَبِعَنِي فَإِنَّهُ مِنِّي وَمَنْ عَصَانِي فَإِنَّكَ غَفُورٌ رَحِيمٌ}
INSERT INTO [b9617] VALUES (1746,'<span data-type="title" id=toc-1731>٣٧ </span>- {رَبَّنَا إِنِّي أَسْكَنْتُ مِنْ ذُرِّيَّتِي بِوَادٍ غَيْرِ ذِي زَرْعٍ عِنْدَ بَيْتِكَ الْمُحَرَّمِ رَبَّنَا لِيُقِيمُوا الصَّلاةَ فَاجْعَلْ أَفْئِدَةً مِنَ النَّاسِ تَهْوِي إِلَيْهِمْ وَارْزُقْهُمْ مِنَ الثَّمَرَاتِ لَعَلَّهُمْ يَشْكُرُونَ}
INSERT INTO [b9617] VALUES (1747,'<span data-type="title" id=toc-1732>٣٨ </span>- {رَبَّنَا إِنَّكَ تَعْلَمُ مَا نُخْفِي وَمَا نُعْلِنُ وَمَا يَخْفَى عَلَى اللَّهِ مِنْ شَيْءٍ فِي الأَرْضِ وَلا فِي السَّمَاءِ}
INSERT INTO [b9617] VALUES (1748,'<span data-type="title" id=toc-1733>٣٩ </span>- {وَهَبَ لِي عَلَى الْكِبَرِ إِسْمَاعِيلَ}
INSERT INTO [b9617] VALUES (1749,'<span data-type="title" id=toc-1734>٤٠ </span>- {رَبِّ اجْعَلْنِي مُقِيمَ الصَّلاةِ وَمِنْ ذُرِّيَّتِي رَبَّنَا وَتَقَبَّلْ دُعَاءِ}
INSERT INTO [b9617] VALUES (1750,'<span data-type="title" id=toc-1735>٤١ </span>- {يَوْمَ يَقُومُ الْحِسَابُ}
INSERT INTO [b9617] VALUES (1751,'<span data-type="title" id=toc-1736>٤٢ </span>- {وَلا تَحْسَبَنَّ اللَّهَ غَافِلا عَمَّا يَعْمَلُ الظَّالِمُونَ إِنَّمَا يُؤَخِّرُهُمْ لِيَوْمٍ تَشْخَصُ فِيهِ الأَبْصَارُ}
INSERT INTO [b9617] VALUES (1752,'<span data-type="title" id=toc-1737>٤٣ </span>- {مُهْطِعِينَ مُقْنِعِي رُءُوسِهِمْ لا يَرْتَدُّ إِلَيْهِمْ طَرْفُهُمْ وَأَفْئِدَتُهُمْ هَوَاءٌ}
INSERT INTO [b9617] VALUES (1753,'<span data-type="title" id=toc-1738>٤٤ </span>- {رَبَّنَا أَخِّرْنَا إِلَى أَجَلٍ قَرِيبٍ نُجِبْ دَعْوَتَكَ وَنَتَّبِعِ الرُّسُلَ أَوَلَمْ تَكُونُوا أَقْسَمْتُمْ مِنْ قَبْلُ مَا لَكُمْ مِنْ زَوَالٍ}
INSERT INTO [b9617] VALUES (1754,'<span data-type="title" id=toc-1739>٤٥ </span>- {وَسَكَنْتُمْ فِي مَسَاكِنِ الَّذِينَ ظَلَمُوا أَنْفُسَهُمْ وَتَبَيَّنَ لَكُمْ كَيْفَ فَعَلْنَا بِهِمْ وَضَرَبْنَا لَكُمُ الأَمْثَالَ}
INSERT INTO [b9617] VALUES (1755,'<span data-type="title" id=toc-1740>٤٦ </span>- {وَقَدْ مَكَرُوا مَكْرَهُمْ وَعِنْدَ اللَّهِ مَكْرُهُمْ وَإِنْ كَانَ مَكْرُهُمْ لِتَزُولَ مِنْهُ الْجِبَالُ}
INSERT INTO [b9617] VALUES (1756,'<span data-type="title" id=toc-1741>٤٧ </span>- {فَلا تَحْسَبَنَّ اللَّهَ مُخْلِفَ وَعْدِهِ رُسُلَهُ إِنَّ اللَّهَ عَزِيزٌ ذُو انْتِقَامٍ}
INSERT INTO [b9617] VALUES (1757,'<span data-type="title" id=toc-1742>٤٨ </span>- {يَوْمَ تُبَدَّلُ الأَرْضُ غَيْرَ الأَرْضِ وَالسَّمَاوَاتُ وَبَرَزُوا لِلَّهِ الْوَاحِدِ الْقَهَّارِ}
INSERT INTO [b9617] VALUES (1758,'<span data-type="title" id=toc-1743>٤٩ </span>- {وَتَرَى الْمُجْرِمِينَ يَوْمَئِذٍ مُقَرَّنِينَ فِي الأَصْفَادِ}
INSERT INTO [b9617] VALUES (1759,'<span data-type="title" id=toc-1744>٥٠ </span>- {سَرَابِيلُهُمْ مِنْ قَطِرَانٍ وَتَغْشَى وُجُوهَهُمُ النَّارُ}
INSERT INTO [b9617] VALUES (1760,'<span data-type="title" id=toc-1745>٥١ </span>- {لِيَجْزِيَ اللَّهُ كُلَّ نَفْسٍ مَا كَسَبَتْ}
INSERT INTO [b9617] VALUES (1761,'<span data-type="title" id=toc-1746>٥٢ </span>- {هَذَا بَلاغٌ لِلنَّاسِ وَلِيُنْذَرُوا بِهِ وَلِيَعْلَمُوا أَنَّمَا هُوَ إِلَهٌ وَاحِدٌ وَلِيَذَّكَّرَ أُولُو ⦗٥٥٢⦘ الأَلْبَابِ}
INSERT INTO [b9617] VALUES (1762,'<span data-type="title" id=toc-1747>سورة الحجر</span>','2','553',NULL,NULL,'0');
INSERT INTO [b9617] VALUES (1763,'<span data-type="title" id=toc-1748>١ </span>- {الر تِلْكَ آيَاتُ الْكِتَابِ وَقُرْآنٍ مُبِينٍ}
INSERT INTO [b9617] VALUES (1764,'<span data-type="title" id=toc-1749>٢ </span>- {رُبَمَا يَوَدُّ الَّذِينَ كَفَرُوا لَوْ كَانُوا مُسْلِمِينَ}
INSERT INTO [b9617] VALUES (1765,'<span data-type="title" id=toc-1750>٣ </span>- {ذَرْهُمْ يَأْكُلُوا وَيَتَمَتَّعُوا وَيُلْهِهِمُ الأَمَلُ فَسَوْفَ يَعْلَمُونَ}
INSERT INTO [b9617] VALUES (1766,'<span data-type="title" id=toc-1751>٤ </span>- {وَمَا أَهْلَكْنَا مِنْ قَرْيَةٍ إِلا وَلَهَا كِتَابٌ مَعْلُومٌ}
INSERT INTO [b9617] VALUES (1767,'<span data-type="title" id=toc-1752>٥ </span>- {مَا تَسْبِقُ مِنْ أُمَّةٍ أَجَلَهَا}
INSERT INTO [b9617] VALUES (1768,'<span data-type="title" id=toc-1753>٦ </span>- {وَقَالُوا يَا أَيُّهَا الَّذِي نُزِّلَ عَلَيْهِ الذِّكْرُ إِنَّكَ لَمَجْنُونٌ}
INSERT INTO [b9617] VALUES (1769,'<span data-type="title" id=toc-1754>٧ </span>- {لَوْ مَا تَأْتِينَا بِالْمَلائِكَةِ إِنْ كُنْتَ مِنَ الصَّادِقِينَ}
INSERT INTO [b9617] VALUES (1770,'<span data-type="title" id=toc-1755>٨ </span>- {مَا نُنَزِّلُ الْمَلائِكَةَ إِلا بِالْحَقِّ وَمَا كَانُوا إِذًا مُنْظَرِينَ}
INSERT INTO [b9617] VALUES (1771,'<span data-type="title" id=toc-1756>٩ </span>- {إِنَّا نَحْنُ نَزَّلْنَا الذِّكْرَ وَإِنَّا لَهُ لَحَافِظُونَ}
INSERT INTO [b9617] VALUES (1772,'<span data-type="title" id=toc-1757>١٠ </span>- {وَلَقَدْ أَرْسَلْنَا مِنْ قَبْلِكَ فِي شِيَعِ الأَوَّلِينَ}
INSERT INTO [b9617] VALUES (1773,'<span data-type="title" id=toc-1758>١١ </span>- {وَمَا يَأْتِيهِمْ مِنْ رَسُولٍ إِلا كَانُوا بِهِ يَسْتَهْزِئُونَ}
INSERT INTO [b9617] VALUES (1774,'<span data-type="title" id=toc-1759>١٢ </span>- {كَذَلِكَ نَسْلُكُهُ فِي قُلُوبِ الْمُجْرِمِينَ}
INSERT INTO [b9617] VALUES (1775,'<span data-type="title" id=toc-1760>١٣ </span>- {لا يُؤْمِنُونَ بِهِ وَقَدْ خَلَتْ سُنَّةُ الأَوَّلِينَ}
INSERT INTO [b9617] VALUES (1776,'<span data-type="title" id=toc-1761>١٤ </span>- {وَلَوْ فَتَحْنَا عَلَيْهِمْ بَابًا مِنَ السَّمَاءِ فَظَلُّوا فِيهِ يَعْرُجُونَ}
INSERT INTO [b9617] VALUES (1777,'<span data-type="title" id=toc-1762>١٥ </span>- {لَقَالُوا إِنَّمَا سُكِّرَتْ أَبْصَارُنَا بَلْ نَحْنُ قَوْمٌ مَسْحُورُونَ}
INSERT INTO [b9617] VALUES (1778,'<span data-type="title" id=toc-1763>١٦ </span>- {وَلَقَدْ جَعَلْنَا فِي السَّمَاءِ بُرُوجًا وَزَيَّنَّاهَا لِلنَّاظِرِينَ}
INSERT INTO [b9617] VALUES (1779,'<span data-type="title" id=toc-1764>١٨ </span>- {إِلا مَنِ اسْتَرَقَ السَّمْعَ فَأَتْبَعَهُ شِهَابٌ مُبِينٌ}
INSERT INTO [b9617] VALUES (1780,'<span data-type="title" id=toc-1765>١٩ </span>- {وَالأَرْضَ مَدَدْنَاهَا وَأَلْقَيْنَا فِيهَا رَوَاسِيَ وَأَنْبَتْنَا فِيهَا مِنْ كُلِّ شَيْءٍ مَوْزُونٍ}
INSERT INTO [b9617] VALUES (1781,'<span data-type="title" id=toc-1766>٢٠ </span>- {وَجَعَلْنَا لَكُمْ فِيهَا مَعَايِشَ وَمَنْ لَسْتُمْ لَهُ بِرَازِقِينَ}
INSERT INTO [b9617] VALUES (1782,'<span data-type="title" id=toc-1767>٢١ </span>- {وَإِنْ مِنْ شَيْءٍ إِلا عِنْدَنَا خَزَائِنُهُ وَمَا نُنَزِّلُهُ إِلا بِقَدَرٍ مَعْلُومٍ}
INSERT INTO [b9617] VALUES (1783,'<span data-type="title" id=toc-1768>٢٢ </span>- {وَأَرْسَلْنَا الرِّيَاحَ لَوَاقِحَ فَأَنْزَلْنَا مِنَ السَّمَاءِ مَاءً فَأَسْقَيْنَاكُمُوهُ وَمَا أَنْتُمْ لَهُ بِخَازِنِينَ}
INSERT INTO [b9617] VALUES (1784,'<span data-type="title" id=toc-1769>٢٣ </span>- {وَإِنَّا لَنَحْنُ نُحْيِي وَنُمِيتُ وَنَحْنُ الْوَارِثُونَ}
INSERT INTO [b9617] VALUES (1785,'<span data-type="title" id=toc-1770>٢٤ </span>- {وَلَقَدْ عَلِمْنَا الْمُسْتَقْدِمِينَ مِنْكُمْ}
INSERT INTO [b9617] VALUES (1786,'<span data-type="title" id=toc-1771>٢٥ </span>- {وَإِنَّ رَبَّكَ هُوَ يَحْشُرُهُمْ إِنَّهُ حَكِيمٌ عَلِيمٌ}
INSERT INTO [b9617] VALUES (1787,'<span data-type="title" id=toc-1772>٢٦ </span>- {وَلَقَدْ خَلَقْنَا الإِنْسَانَ مِنْ صَلْصَالٍ مِنْ حَمَإٍ مَسْنُونٍ}
INSERT INTO [b9617] VALUES (1788,'<span data-type="title" id=toc-1773>٢٧ </span>- {وَالْجَانَّ خَلَقْنَاهُ مِنْ قَبْلُ مِنْ نَارِ السَّمُومِ}
INSERT INTO [b9617] VALUES (1789,'<span data-type="title" id=toc-1774>٢٨ </span>- {وَإِذْ قَالَ رَبُّكَ لِلْمَلائِكَةِ إِنِّي خَالِقٌ بَشَرًا مِنْ صَلْصَالٍ مِنْ حَمَإٍ مَسْنُونٍ}
INSERT INTO [b9617] VALUES (1790,'<span data-type="title" id=toc-1775>٢٩ </span>- {فَإِذَا سَوَّيْتُهُ وَنَفَخْتُ فِيهِ مِنْ رُوحِي فَقَعُوا لَهُ سَاجِدِينَ} ⦗٥٥٨⦘
INSERT INTO [b9617] VALUES (1791,'<span data-type="title" id=toc-1776>٣٠ </span>- {فَسَجَدَ الْمَلائِكَةُ كُلُّهُمْ أَجْمَعُونَ}
INSERT INTO [b9617] VALUES (1792,'<span data-type="title" id=toc-1777>٣١ </span>- {إِلا إِبْلِيسَ أَبَى أَنْ يَكُونَ مَعَ السَّاجِدِينَ}
INSERT INTO [b9617] VALUES (1793,'<span data-type="title" id=toc-1778>٣٢ </span>- {قَالَ يَا إِبْلِيسُ مَا لَكَ أَلا تَكُونَ مَعَ السَّاجِدِينَ}
INSERT INTO [b9617] VALUES (1794,'<span data-type="title" id=toc-1779>٣٣ </span>- {قَالَ لَمْ أَكُنْ لأَسْجُدَ لِبَشَرٍ خَلَقْتَهُ مِنْ صَلْصَالٍ مِنْ حَمَإٍ مَسْنُونٍ}
INSERT INTO [b9617] VALUES (1795,'<span data-type="title" id=toc-1780>٣٤ </span>- {قَالَ فَاخْرُجْ مِنْهَا فَإِنَّكَ رَجِيمٌ}
INSERT INTO [b9617] VALUES (1796,'<span data-type="title" id=toc-1781>٣٥ </span>- {وَإِنَّ عَلَيْكَ اللَّعْنَةَ إِلَى يَوْمِ الدِّينِ}
INSERT INTO [b9617] VALUES (1797,'<span data-type="title" id=toc-1782>٣٦ </span>- {قَالَ رَبِّ فَأَنْظِرْنِي إِلَى يَوْمِ يُبْعَثُونَ}
INSERT INTO [b9617] VALUES (1798,'<span data-type="title" id=toc-1783>٣٧ </span>- {قَالَ فَإِنَّكَ مِنَ الْمُنْظَرِينَ}
INSERT INTO [b9617] VALUES (1799,'<span data-type="title" id=toc-1784>٣٨ </span>- {إِلَى يَوْمِ الْوَقْتِ الْمَعْلُومِ}
INSERT INTO [b9617] VALUES (1800,'<span data-type="title" id=toc-1785>٣٩ </span>- {قَالَ رَبِّ بِمَا أَغْوَيْتَنِي لأُزَيِّنَنَّ لَهُمْ فِي الأَرْضِ وَلأُغْوِيَنَّهُمْ أَجْمَعِينَ}
INSERT INTO [b9617] VALUES (1801,'<span data-type="title" id=toc-1786>٤٠ </span>- {إِلا عِبَادَكَ مِنْهُمُ الْمُخْلَصِينَ}
INSERT INTO [b9617] VALUES (1802,'<span data-type="title" id=toc-1787>٤١ </span>- {قَالَ هَذَا صِرَاطٌ عَلَيَّ مُسْتَقِيمٌ}
INSERT INTO [b9617] VALUES (1803,'<span data-type="title" id=toc-1788>٤٢ </span>- {إِنَّ عِبَادِي لَيْسَ لَكَ عَلَيْهِمْ سُلْطَانٌ إِلا مَنِ اتَّبَعَكَ مِنَ الْغَاوِينَ}
INSERT INTO [b9617] VALUES (1804,'<span data-type="title" id=toc-1789>٤٣ </span>- {وَإِنَّ جَهَنَّمَ لَمَوْعِدُهُمْ أَجْمَعِينَ}
INSERT INTO [b9617] VALUES (1805,'<span data-type="title" id=toc-1790>٤٤ </span>- {لَهَا سَبْعَةُ أَبْوَابٍ لِكُلِّ بَابٍ مِنْهُمْ جُزْءٌ مَقْسُومٌ}
INSERT INTO [b9617] VALUES (1806,'<span data-type="title" id=toc-1791>٤٦ </span>- {ادْخُلُوهَا بِسَلامٍ آمِنِينَ}
INSERT INTO [b9617] VALUES (1807,'<span data-type="title" id=toc-1792>٤٧ </span>- {وَنَزَعْنَا مَا فِي صُدُورِهِمْ مِنْ غِلٍّ إِخْوَانًا عَلَى سُرُرٍ مُتَقَابِلِينَ}
INSERT INTO [b9617] VALUES (1808,'<span data-type="title" id=toc-1793>٤٨ </span>- {لا يَمَسُّهُمْ فِيهَا نَصَبٌ وَمَا هُمْ مِنْهَا بِمُخْرَجِينَ}
INSERT INTO [b9617] VALUES (1809,'<span data-type="title" id=toc-1794>٤٩ </span>- {نَبِّئْ عِبَادِي أَنِّي أَنَا الْغَفُورُ الرَّحِيمُ}
INSERT INTO [b9617] VALUES (1810,'<span data-type="title" id=toc-1795>٥٠ </span>- {وَأَنَّ عَذَابِي هُوَ الْعَذَابُ الأَلِيمُ}
INSERT INTO [b9617] VALUES (1811,'<span data-type="title" id=toc-1796>٥٢ </span>- {إِذْ دَخَلُوا عَلَيْهِ فَقَالُوا سَلامًا قَالَ إِنَّا مِنْكُمْ وَجِلُونَ}
INSERT INTO [b9617] VALUES (1812,'<span data-type="title" id=toc-1797>٥٣ </span>- {قَالُوا لا تَوْجَلْ إِنَّا نُبَشِّرُكَ بِغُلامٍ عَلِيمٍ} ⦗٥٦٢⦘
INSERT INTO [b9617] VALUES (1813,'<span data-type="title" id=toc-1798>٥٤ </span>- {قَالَ أَبَشَّرْتُمُونِي عَلَى أَنْ مَسَّنِيَ الْكِبَرُ فَبِمَ تُبَشِّرُونَ}
INSERT INTO [b9617] VALUES (1814,'<span data-type="title" id=toc-1799>٥٥ </span>- {قَالُوا بَشَّرْنَاكَ بِالْحَقِّ فَلا تَكُنْ مِنَ الْقَانِطِينَ}
INSERT INTO [b9617] VALUES (1815,'<span data-type="title" id=toc-1800>٥٦ </span>- {قَالَ وَمَنْ يَقْنَطُ مِنْ رَحْمَةِ رَبِّهِ إِلا الضَّالُّونَ}
INSERT INTO [b9617] VALUES (1816,'<span data-type="title" id=toc-1801>٥٧ </span>- {قَالَ فَمَا خَطْبُكُمْ أَيُّهَا الْمُرْسَلُونَ}
INSERT INTO [b9617] VALUES (1817,'<span data-type="title" id=toc-1802>٥٩ </span>- {إِلا آلَ لُوطٍ إِنَّا لَمُنَجُّوهُمْ أَجْمَعِينَ} ⦗٥٦٣⦘
INSERT INTO [b9617] VALUES (1818,'<span data-type="title" id=toc-1803>٦٠ </span>- {إِلا امْرَأَتَهُ قَدَّرْنَا إِنَّهَا لَمِنَ الْغَابِرِينَ}
INSERT INTO [b9617] VALUES (1819,'<span data-type="title" id=toc-1804>٦١ </span>- {فَلَمَّا جَاءَ آلَ لُوطٍ الْمُرْسَلُونَ}
INSERT INTO [b9617] VALUES (1820,'<span data-type="title" id=toc-1805>٦٢ </span>- {قَالَ إِنَّكُمْ قَوْمٌ مُنْكَرُونَ}
INSERT INTO [b9617] VALUES (1821,'<span data-type="title" id=toc-1806>٦٣ </span>- {قَالُوا بَلْ جِئْنَاكَ بِمَا كَانُوا فِيهِ يَمْتَرُونَ}
INSERT INTO [b9617] VALUES (1822,'<span data-type="title" id=toc-1807>٦٤ </span>- {وَأَتَيْنَاكَ بِالْحَقِّ وَإِنَّا لَصَادِقُونَ}
INSERT INTO [b9617] VALUES (1823,'<span data-type="title" id=toc-1808>٦٥ </span>- {فَأَسْرِ بِأَهْلِكَ بِقِطْعٍ مِنَ اللَّيْلِ وَاتَّبِعْ أَدْبَارَهُمْ وَلا يَلْتَفِتْ مِنْكُمْ أَحَدٌ وَامْضُوا حَيْثُ تُؤْمَرُونَ}
INSERT INTO [b9617] VALUES (1824,'<span data-type="title" id=toc-1809>٦٦ </span>- {وَقَضَيْنَا إِلَيْهِ ذَلِكَ الأَمْرَ أَنَّ دَابِرَ هَؤُلاءِ مَقْطُوعٌ مُصْبِحِينَ}
INSERT INTO [b9617] VALUES (1825,'<span data-type="title" id=toc-1810>٦٧ </span>- {وَجَاءَ أَهْلُ الْمَدِينَةِ يَسْتَبْشِرُونَ}
INSERT INTO [b9617] VALUES (1826,'<span data-type="title" id=toc-1811>٦٨ </span>- {قَالَ إِنَّ هَؤُلاءِ ضَيْفِي فَلا تَفْضَحُونِ}
INSERT INTO [b9617] VALUES (1827,'<span data-type="title" id=toc-1812>٦٩ </span>- {وَاتَّقُوا اللَّهَ وَلا تُخْزُونِ}
INSERT INTO [b9617] VALUES (1828,'<span data-type="title" id=toc-1813>٧٠ </span>- {قَالُوا أَوَلَمْ نَنْهَكَ عَنِ الْعَالَمِينَ}
INSERT INTO [b9617] VALUES (1829,'<span data-type="title" id=toc-1814>٧١ </span>- {قَالَ هَؤُلاءِ بَنَاتِي إِنْ كُنْتُمْ فَاعِلِينَ}
INSERT INTO [b9617] VALUES (1830,'<span data-type="title" id=toc-1815>٧٢ </span>- {لَعَمْرُكَ إِنَّهُمْ لَفِي سَكْرَتِهِمْ يَعْمَهُونَ}
INSERT INTO [b9617] VALUES (1831,'<span data-type="title" id=toc-1816>٧٣ </span>- {فَأَخَذَتْهُمُ الصَّيْحَةُ مُشْرِقِينَ}
INSERT INTO [b9617] VALUES (1832,'<span data-type="title" id=toc-1817>٧٤ </span>- {فَجَعَلْنَا عَالِيَهَا سَافِلَهَا وَأَمْطَرْنَا عَلَيْهِمْ حِجَارَةً مِنْ سِجِّيلٍ}
INSERT INTO [b9617] VALUES (1833,'<span data-type="title" id=toc-1818>٧٥ </span>- {إِنَّ فِي ذَلِكَ لآيَاتٍ لِلْمُتَوَسِّمِينَ}
INSERT INTO [b9617] VALUES (1834,'<span data-type="title" id=toc-1819>٧٦ </span>- {وَإِنَّهَا لَبِسَبِيلٍ مُقِيمٍ}
INSERT INTO [b9617] VALUES (1835,'<span data-type="title" id=toc-1820>٧٧ </span>- {إِنَّ فِي ذَلِكَ لآيَةً لِلْمُؤْمِنِينَ}
INSERT INTO [b9617] VALUES (1836,'<span data-type="title" id=toc-1821>٧٨ </span>- {وَإِنْ كَانَ أَصْحَابُ الأَيْكَةِ لَظَالِمِينَ}
INSERT INTO [b9617] VALUES (1837,'<span data-type="title" id=toc-1822>٧٩ </span>- {فَانْتَقَمْنَا مِنْهُمْ وَإِنَّهُمَا لَبِإِمَامٍ مُبِينٍ}
INSERT INTO [b9617] VALUES (1838,'<span data-type="title" id=toc-1823>٨١ </span>- {وَآتَيْنَاهُمْ آيَاتِنَا}
INSERT INTO [b9617] VALUES (1839,'<span data-type="title" id=toc-1824>٨٢ </span>- {وَكَانُوا يَنْحِتُونَ مِنَ الْجِبَالِ بُيُوتًا آمِنِينَ}
INSERT INTO [b9617] VALUES (1840,'<span data-type="title" id=toc-1825>٨٣ </span>- {فَأَخَذَتْهُمُ الصَّيْحَةُ مُصْبِحِينَ}
INSERT INTO [b9617] VALUES (1841,'<span data-type="title" id=toc-1826>٨٤ </span>- {فَمَا أَغْنَى عَنْهُمْ مَا كَانُوا يَكْسِبُونَ}
INSERT INTO [b9617] VALUES (1842,'<span data-type="title" id=toc-1827>٨٥ </span>- {وَمَا خَلَقْنَا السَّمَاوَاتِ وَالأَرْضَ وَمَا بَيْنَهُمَا إِلا بِالْحَقِّ وَإِنَّ السَّاعَةَ لآتِيَةٌ فَاصْفَحِ الصَّفْحَ الْجَمِيلَ}
INSERT INTO [b9617] VALUES (1843,'<span data-type="title" id=toc-1828>٨٦ </span>- {إِنَّ رَبَّكَ هُوَ الْخَلاقُ الْعَلِيمُ} ⦗٥٦٧⦘
INSERT INTO [b9617] VALUES (1844,'<span data-type="title" id=toc-1829>٨٧ </span>- {وَلَقَدْ آتَيْنَاكَ سَبْعًا مِنَ الْمَثَانِي وَالْقُرْآنَ الْعَظِيمَ}
INSERT INTO [b9617] VALUES (1845,'<span data-type="title" id=toc-1830>٨٨ </span>- {لا تَمُدَّنَّ عَيْنَيْكَ إِلَى مَا مَتَّعْنَا بِهِ أَزْوَاجًا مِنْهُمْ وَلا تَحْزَنْ عَلَيْهِمْ وَاخْفِضْ جَنَاحَكَ لِلْمُؤْمِنِينَ}
INSERT INTO [b9617] VALUES (1846,'<span data-type="title" id=toc-1831>٨٩ </span>- {وَقُلْ إِنِّي أَنَا النَّذِيرُ الْمُبِينُ}
INSERT INTO [b9617] VALUES (1847,'<span data-type="title" id=toc-1832>٩٠ </span>- {كَمَا أَنْزَلْنَا عَلَى الْمُقْتَسِمِينَ}
INSERT INTO [b9617] VALUES (1848,'<span data-type="title" id=toc-1833>٩١ </span>- {الَّذِينَ جَعَلُوا الْقُرْآنَ عِضِينَ}
INSERT INTO [b9617] VALUES (1849,'<span data-type="title" id=toc-1834>٩٢ </span>- {فَوَرَبِّكَ لَنَسْأَلَنَّهُمْ أَجْمَعِينَ}
INSERT INTO [b9617] VALUES (1850,'<span data-type="title" id=toc-1835>٩٣ </span>- {عَمَّا كَانُوا يَعْمَلُونَ}
INSERT INTO [b9617] VALUES (1851,'<span data-type="title" id=toc-1836>٩٤ </span>- {فَاصْدَعْ بِمَا تُؤْمَرُ وَأَعْرِضْ عَنِ الْمُشْرِكِينَ}
INSERT INTO [b9617] VALUES (1852,'<span data-type="title" id=toc-1837>٩٥ </span>- {إِنَّا كَفَيْنَاكَ الْمُسْتَهْزِئِينَ}
INSERT INTO [b9617] VALUES (1853,'<span data-type="title" id=toc-1838>٩٦ </span>- {الَّذِينَ يَجْعَلُونَ مَعَ اللَّهِ إِلَهًا آخَرَ فَسَوْفَ يَعْلَمُونَ}
INSERT INTO [b9617] VALUES (1854,'<span data-type="title" id=toc-1839>٩٧ </span>- {وَلَقَدْ نَعْلَمُ أَنَّكَ يَضِيقُ صَدْرُكَ بِمَا يَقُولُونَ}
INSERT INTO [b9617] VALUES (1855,'<span data-type="title" id=toc-1840>٩٨ </span>- {فَسَبِّحْ بِحَمْدِ رَبِّكَ}
INSERT INTO [b9617] VALUES (1856,'<span data-type="title" id=toc-1841>٩٩ </span>- {وَاعْبُدْ رَبَّكَ}
INSERT INTO [b9617] VALUES (1857,'<span data-type="title" id=toc-1842>سورة النحل</span>','2','569',NULL,NULL,'0');
INSERT INTO [b9617] VALUES (1858,'<span data-type="title" id=toc-1843>١ </span>- {أَتَى أَمْرُ اللَّهِ فَلا تَسْتَعْجِلُوهُ سُبْحَانَهُ وَتَعَالَى عَمَّا يُشْرِكُونَ}
INSERT INTO [b9617] VALUES (1859,'<span data-type="title" id=toc-1844>٢ </span>- {يُنَزِّلُ الْمَلائِكَةَ بِالرُّوحِ مِنْ أَمْرِهِ عَلَى مَنْ يَشَاءُ مِنْ عِبَادِهِ أَنْ أَنْذِرُوا أَنَّهُ لا إِلَهَ إِلا أَنَا فَاتَّقُونِ}
INSERT INTO [b9617] VALUES (1860,'<span data-type="title" id=toc-1845>٣ </span>- {خَلَقَ السَّمَاوَاتِ وَالأَرْضَ بِالْحَقِّ تَعَالَى عَمَّا يُشْرِكُونَ}
INSERT INTO [b9617] VALUES (1861,'<span data-type="title" id=toc-1846>٤ </span>- {خَلَقَ الإِنْسَانَ مِنْ نُطْفَةٍ فَإِذَا هُوَ خَصِيمٌ مُبِينٌ}
INSERT INTO [b9617] VALUES (1862,'<span data-type="title" id=toc-1847>٥ </span>- {وَالأَنْعَامَ خَلَقَهَا لَكُمْ فِيهَا دِفْءٌ وَمَنَافِعُ وَمِنْهَا تَأْكُلُونَ}
INSERT INTO [b9617] VALUES (1863,'<span data-type="title" id=toc-1848>٦ </span>- {وَلَكُمْ فِيهَا جَمَالٌ حِينَ تُرِيحُونَ وَحِينَ تَسْرَحُونَ}
INSERT INTO [b9617] VALUES (1864,'<span data-type="title" id=toc-1849>٧ </span>- {وَتَحْمِلُ أَثْقَالَكُمْ إِلَى بَلَدٍ لَمْ تَكُونُوا بَالِغِيهِ إِلا بِشِقِّ الأَنْفُسِ}
INSERT INTO [b9617] VALUES (1865,'<span data-type="title" id=toc-1850>٨ </span>- {وَالْخَيْلَ وَالْبِغَالَ وَالْحَمِيرَ لِتَرْكَبُوهَا وَزِينَةً وَيَخْلُقُ مَا لا تَعْلَمُونَ} ⦗٥٧١⦘
INSERT INTO [b9617] VALUES (1866,'<span data-type="title" id=toc-1851>٩ </span>- {وَعَلَى اللَّهِ قَصْدُ السَّبِيلِ وَمِنْهَا جَائِرٌ وَلَوْ شَاءَ لَهَدَاكُمْ أَجْمَعِينَ}
INSERT INTO [b9617] VALUES (1867,'<span data-type="title" id=toc-1852>١٠ </span>- {هُوَ الَّذِي أَنْزَلَ مِنَ السَّمَاءِ مَاءً لَكُمْ مِنْهُ شَرَابٌ وَمِنْهُ شَجَرٌ فِيهِ تُسِيمُونَ}
INSERT INTO [b9617] VALUES (1868,'<span data-type="title" id=toc-1853>١١ </span>- {يُنْبِتُ لَكُمْ بِهِ الزَّرْعَ وَالزَّيْتُونَ وَالنَّخِيلَ وَالأَعْنَابَ وَمِنْ كُلِّ الثَّمَرَاتِ إِنَّ فِي ذَلِكَ لآيَةً لِقَوْمٍ يَتَفَكَّرُونَ}
INSERT INTO [b9617] VALUES (1869,'<span data-type="title" id=toc-1854>١٢ </span>- {وَسَخَّرَ لَكُمُ اللَّيْلَ وَالنَّهَارَ وَالشَّمْسَ وَالْقَمَرَ وَالنُّجُومُ مُسَخَّرَاتٌ بِأَمْرِهِ إِنَّ فِي ذَلِكَ لآيَاتٍ لِقَوْمٍ يَعْقِلُونَ}
INSERT INTO [b9617] VALUES (1870,'<span data-type="title" id=toc-1855>١٣ </span>- {وَمَا ذَرَأَ لَكُمْ فِي الأَرْضِ مُخْتَلِفًا أَلْوَانُهُ}
INSERT INTO [b9617] VALUES (1871,'<span data-type="title" id=toc-1856>١٤ </span>- {وَهُوَ الَّذِي سَخَّرَ الْبَحْرَ لِتَأْكُلُوا مِنْهُ لَحْمًا طَرِيًّا وَتَسْتَخْرِجُوا مِنْهُ حِلْيَةً تَلْبَسُونَهَا وَتَرَى الْفُلْكَ مَوَاخِرَ فِيهِ وَلِتَبْتَغُوا مِنْ فَضْلِهِ وَلَعَلَّكُمْ تَشْكُرُونَ}
INSERT INTO [b9617] VALUES (1872,'<span data-type="title" id=toc-1857>١٥ </span>- {وَأَلْقَى فِي الأَرْضِ رَوَاسِيَ أَنْ تَمِيدَ بِكُمْ وَأَنْهَارًا وَسُبُلا لَعَلَّكُمْ تَهْتَدُونَ}
INSERT INTO [b9617] VALUES (1873,'<span data-type="title" id=toc-1858>١٦ </span>- {وَعَلامَاتٍ وَبِالنَّجْمِ هُمْ يَهْتَدُونَ}
INSERT INTO [b9617] VALUES (1874,'<span data-type="title" id=toc-1859>١٧ </span>- {أَفَمَنْ يَخْلُقُ كَمَنْ لا يَخْلُقُ أَفَلا تَذَكَّرُونَ}
INSERT INTO [b9617] VALUES (1875,'<span data-type="title" id=toc-1860>١٨ </span>- {وَإِنْ تَعُدُّوا نِعْمَةَ اللَّهِ لا تُحْصُوهَا إِنَّ اللَّهَ لَغَفُورٌ رَحِيمٌ}
INSERT INTO [b9617] VALUES (1876,'<span data-type="title" id=toc-1861>١٩ </span>- {وَاللَّهُ يَعْلَمُ مَا تُسِرُّونَ وَمَا تُعْلِنُونَ}
INSERT INTO [b9617] VALUES (1877,'<span data-type="title" id=toc-1862>٢٠ </span>- {وَالَّذِينَ يَدْعُونَ مِنْ دُونِ اللَّهِ لا يَخْلُقُونَ شَيْئًا وَهُمْ يُخْلَقُونَ}
INSERT INTO [b9617] VALUES (1878,'<span data-type="title" id=toc-1863>٢١ </span>- {أَمْوَاتٌ غَيْرُ أَحْيَاءٍ وَمَا يَشْعُرُونَ أَيَّانَ يُبْعَثُونَ}
INSERT INTO [b9617] VALUES (1879,'<span data-type="title" id=toc-1864>٢٢ </span>- {إِلَهُكُمْ إِلَهٌ وَاحِدٌ فَالَّذِينَ لا يُؤْمِنُونَ بِالآخِرَةِ قُلُوبُهُمْ مُنْكِرَةٌ وَهُمْ مُسْتَكْبِرُونَ}
INSERT INTO [b9617] VALUES (1880,'<span data-type="title" id=toc-1865>٢٣ </span>- {لا جَرَمَ أَنَّ اللَّهَ يَعْلَمُ مَا يُسِرُّونَ وَمَا يُعْلِنُونَ إِنَّهُ لا يُحِبُّ الْمُسْتَكْبِرِينَ}
INSERT INTO [b9617] VALUES (1881,'<span data-type="title" id=toc-1866>٢٤ </span>- {وَإِذَا قِيلَ لَهُمْ مَاذَا أَنْزَلَ رَبُّكُمْ قَالُوا أَسَاطِيرُ الأَوَّلِينَ}
INSERT INTO [b9617] VALUES (1882,'<span data-type="title" id=toc-1867>٢٥ </span>- {لِيَحْمِلُوا أَوْزَارَهُمْ كَامِلَةً يَوْمَ الْقِيَامَةِ وَمِنْ أَوْزَارِ الَّذِينَ يُضِلُّونَهُمْ بِغَيْرِ عِلْمٍ أَلا سَاءَ مَا يَزِرُونَ}
INSERT INTO [b9617] VALUES (1883,'<span data-type="title" id=toc-1868>٢٦ </span>- {قَدْ مَكَرَ الَّذِينَ مِنْ قَبْلِهِمْ فَأَتَى اللَّهُ بُنْيَانَهُمْ مِنَ الْقَوَاعِدِ فَخَرَّ عَلَيْهِمُ السَّقْفُ مِنْ فَوْقِهِمْ وَأَتَاهُمُ الْعَذَابُ مِنْ حَيْثُ لا يَشْعُرُونَ}
INSERT INTO [b9617] VALUES (1884,'<span data-type="title" id=toc-1869>٢٧ </span>- {ثُمَّ يَوْمَ الْقِيَامَةِ يُخْزِيهِمْ وَيَقُولُ أَيْنَ شُرَكَائِيَ الَّذِينَ كُنْتُمْ تُشَاقُّونَ فِيهِمْ قَالَ الَّذِينَ أُوتُوا الْعِلْمَ إِنَّ الْخِزْيَ الْيَوْمَ وَالسُّوءَ عَلَى الْكَافِرِينَ}
INSERT INTO [b9617] VALUES (1885,'<span data-type="title" id=toc-1870>٢٨ </span>- {الَّذِينَ تَتَوَفَّاهُمُ الْمَلائِكَةُ ظَالِمِي أَنْفُسِهِمْ فَأَلْقَوُا السَّلَمَ مَا كُنَّا نَعْمَلُ مِنْ سُوءٍ بَلَى إِنَّ اللَّهَ عَلِيمٌ بِمَا كُنْتُمْ تَعْمَلُونَ}
INSERT INTO [b9617] VALUES (1886,'<span data-type="title" id=toc-1871>٢٩ </span>- {فَادْخُلُوا أَبْوَابَ جَهَنَّمَ خَالِدِينَ فِيهَا فَلَبِئْسَ مَثْوَى الْمُتَكَبِّرِينَ}
INSERT INTO [b9617] VALUES (1887,'<span data-type="title" id=toc-1872>٣٠ </span>- {وَقِيلَ لِلَّذِينَ اتَّقَوْا مَاذَا أَنْزَلَ رَبُّكُمْ قَالُوا خَيْرًا لِلَّذِينَ أَحْسَنُوا فِي هَذِهِ الدُّنْيَا حَسَنَةٌ وَلَدَارُ الآخِرَةِ خَيْرٌ وَلَنِعْمَ دَارُ الْمُتَّقِينَ}
INSERT INTO [b9617] VALUES (1888,'<span data-type="title" id=toc-1873>٣١ </span>- {جَنَّاتُ عَدْنٍ يَدْخُلُونَهَا تَجْرِي مِنْ تَحْتِهَا الأَنْهَارُ لَهُمْ فِيهَا مَا يَشَاءُونَ كَذَلِكَ يَجْزِي اللَّهُ الْمُتَّقِينَ}
INSERT INTO [b9617] VALUES (1889,'<span data-type="title" id=toc-1874>٣٢ </span>- {الَّذِينَ تَتَوَفَّاهُمُ الْمَلائِكَةُ طَيِّبِينَ يَقُولُونَ سَلامٌ عَلَيْكُمُ ادْخُلُوا الْجَنَّةَ}
INSERT INTO [b9617] VALUES (1890,'<span data-type="title" id=toc-1875>٣٣ </span>- {هَلْ يَنْظُرُونَ إِلا أَنْ تَأْتِيَهُمُ الْمَلائِكَةُ أَوْ يَأْتِيَ أَمْرُ رَبِّكَ كَذَلِكَ فَعَلَ الَّذِينَ مِنْ قَبْلِهِمْ وَمَا ظَلَمَهُمُ اللَّهُ وَلَكِنْ كَانُوا أَنْفُسَهُمْ يَظْلِمُونَ}
INSERT INTO [b9617] VALUES (1891,'<span data-type="title" id=toc-1876>٣٤ </span>- {فَأَصَابَهُمْ سَيِّئَاتُ مَا عَمِلُوا وَحَاقَ بِهِمْ مَا كَانُوا بِهِ يَسْتَهْزِئُونَ}
INSERT INTO [b9617] VALUES (1892,'<span data-type="title" id=toc-1877>٣٥ </span>- {وَقَالَ الَّذِينَ أَشْرَكُوا لَوْ شَاءَ اللَّهُ مَا عَبَدْنَا مِنْ دُونِهِ مِنْ شَيْءٍ نَحْنُ وَلا آبَاؤُنَا وَلا حَرَّمْنَا مِنْ دُونِهِ مِنْ شَيْءٍ كَذَلِكَ فَعَلَ الَّذِينَ مِنْ قَبْلِهِمْ فَهَلْ عَلَى الرُّسُلِ إِلا الْبَلاغُ الْمُبِينُ}
INSERT INTO [b9617] VALUES (1893,'<span data-type="title" id=toc-1878>٣٦ </span>- {وَلَقَدْ بَعَثْنَا فِي كُلِّ أُمَّةٍ رَسُولا أَنِ اعْبُدُوا اللَّهَ وَاجْتَنِبُوا الطَّاغُوتَ فَمِنْهُمْ مَنْ هَدَى اللَّهُ وَمِنْهُمْ مَنْ حَقَّتْ عَلَيْهِ الضَّلالَةُ فَسِيرُوا فِي الأَرْضِ فَانْظُرُوا كَيْفَ ⦗٥٧٩⦘ كَانَ عَاقِبَةُ الْمُكَذِّبِينَ}
INSERT INTO [b9617] VALUES (1894,'<span data-type="title" id=toc-1879>٣٧ </span>- {إِنْ تَحْرِصْ عَلَى هُدَاهُمْ فَإِنَّ اللَّهَ لا يَهْدِي مَنْ يُضِلُّ وَمَا لَهُمْ مِنْ نَاصِرِينَ}
INSERT INTO [b9617] VALUES (1895,'<span data-type="title" id=toc-1880>٣٨ </span>- {وَأَقْسَمُوا بِاللَّهِ جَهْدَ أَيْمَانِهِمْ لا يَبْعَثُ اللَّهُ مَنْ يَمُوتُ بَلَى وَعْدًا عَلَيْهِ حَقًّا وَلَكِنَّ أَكْثَرَ النَّاسِ لا يَعْلَمُونَ}
INSERT INTO [b9617] VALUES (1896,'<span data-type="title" id=toc-1881>٣٩ </span>- {لِيُبَيِّنَ لَهُمُ الَّذِي يَخْتَلِفُونَ فِيهِ وَلِيَعْلَمَ الَّذِينَ كَفَرُوا أَنَّهُمْ كَانُوا كَاذِبِينَ}
INSERT INTO [b9617] VALUES (1897,'<span data-type="title" id=toc-1882>٤٠ </span>- {إِنَّمَا قَوْلُنَا لِشَيْءٍ إِذَا أَرَدْنَاهُ أَنْ نَقُولَ لَهُ كُنْ فَيَكُونُ}
INSERT INTO [b9617] VALUES (1898,'<span data-type="title" id=toc-1883>٤١ </span>- {وَالَّذِينَ هَاجَرُوا فِي اللَّهِ مِنْ بَعْدِ مَا ظُلِمُوا لَنُبَوِّئَنَّهُمْ فِي الدُّنْيَا حَسَنَةً وَلأَجْرُ الآخِرَةِ أَكْبَرُ لَوْ كَانُوا يَعْلَمُونَ}
INSERT INTO [b9617] VALUES (1899,'<span data-type="title" id=toc-1884>٤٢ </span>- {الَّذِينَ صَبَرُوا وَعَلَى رَبِّهِمْ يَتَوَكَّلُونَ}
INSERT INTO [b9617] VALUES (1900,'<span data-type="title" id=toc-1885>٤٣ </span>- {وَمَا أَرْسَلْنَا مِنْ قَبْلِكَ إِلا رِجَالا نُوحِي إِلَيْهِمْ فَاسْأَلُوا أَهْلَ الذِّكْرِ إِنْ كُنْتُمْ لا تَعْلَمُونَ}
INSERT INTO [b9617] VALUES (1901,'<span data-type="title" id=toc-1886>٤٤ </span>- {بِالْبَيِّنَاتِ وَالزُّبُرِ وَأَنْزَلْنَا إِلَيْكَ الذِّكْرَ لِتُبَيِّنَ لِلنَّاسِ مَا نُزِّلَ إِلَيْهِمْ وَلَعَلَّهُمْ يَتَفَكَّرُونَ}
INSERT INTO [b9617] VALUES (1902,'<span data-type="title" id=toc-1887>٤٥ </span>- {أَفَأَمِنَ الَّذِينَ مَكَرُوا السَّيِّئَاتِ أَنْ يَخْسِفَ اللَّهُ بِهِمُ الأَرْضَ أَوْ يَأْتِيَهُمُ الْعَذَابُ مِنْ حَيْثُ لا يَشْعُرُونَ}
INSERT INTO [b9617] VALUES (1903,'<span data-type="title" id=toc-1888>٤٦ </span>- {أَوْ يَأْخُذَهُمْ فِي تَقَلُّبِهِمْ فَمَا هُمْ بِمُعْجِزِينَ}
INSERT INTO [b9617] VALUES (1904,'<span data-type="title" id=toc-1889>٤٧ </span>- {أَوْ يَأْخُذَهُمْ عَلَى تَخَوُّفٍ فَإِنَّ رَبَّكُمْ لَرَءُوفٌ رَحِيمٌ}
INSERT INTO [b9617] VALUES (1905,'<span data-type="title" id=toc-1890>٤٨ </span>- {أَوَلَمْ يَرَوْا إِلَى مَا خَلَقَ اللَّهُ مِنْ شَيْءٍ يَتَفَيَّأُ ظِلالُهُ عَنِ الْيَمِينِ وَالشَّمَائِلِ سُجَّدًا لِلَّهِ وَهُمْ دَاخِرُونَ}
INSERT INTO [b9617] VALUES (1906,'<span data-type="title" id=toc-1891>٤٩ </span>- {وَلِلَّهِ يَسْجُدُ مَا فِي السَّمَاوَاتِ وَمَا فِي الأَرْضِ مِنْ دَابَّةٍ وَالْمَلائِكَةُ وَهُمْ لا يَسْتَكْبِرُونَ}
INSERT INTO [b9617] VALUES (1907,'<span data-type="title" id=toc-1892>٥٠ </span>- {يَخَافُونَ رَبَّهُمْ مِنْ فَوْقِهِمْ وَيَفْعَلُونَ مَا يُؤْمَرُونَ}
INSERT INTO [b9617] VALUES (1908,'<span data-type="title" id=toc-1893>٥١ </span>- {وَقَالَ اللَّهُ لا تَتَّخِذُوا إِلَهَيْنِ اثْنَيْنِ إِنَّمَا هُوَ إِلَهٌ وَاحِدٌ فَإِيَّايَ فَارْهَبُونِ} ⦗٥٨٣⦘
INSERT INTO [b9617] VALUES (1909,'<span data-type="title" id=toc-1894>٥٢ </span>- {وَلَهُ مَا فِي السَّمَاوَاتِ وَالأَرْضِ وَلَهُ الدِّينُ وَاصِبًا أَفَغَيْرَ اللَّهِ تَتَّقُونَ}
INSERT INTO [b9617] VALUES (1910,'<span data-type="title" id=toc-1895>٥٣ </span>- {وَمَا بِكُمْ مِنْ نِعْمَةٍ فَمِنَ اللَّهِ ثُمَّ إِذَا مَسَّكُمُ الضُّرُّ فَإِلَيْهِ تَجْأَرُونَ}
INSERT INTO [b9617] VALUES (1911,'<span data-type="title" id=toc-1896>٥٤ </span>- {ثُمَّ إِذَا كَشَفَ الضُّرَّ عَنْكُمْ إِذَا فَرِيقٌ مِنْكُمْ بِرَبِّهِمْ يُشْرِكُونَ}
INSERT INTO [b9617] VALUES (1912,'<span data-type="title" id=toc-1897>٥٥ </span>- {لِيَكْفُرُوا بِمَا آتَيْنَاهُمْ فَتَمَتَّعُوا فَسَوْفَ تَعْلَمُونَ}
INSERT INTO [b9617] VALUES (1913,'<span data-type="title" id=toc-1898>٥٦ </span>- {وَيَجْعَلُونَ لِمَا لا يَعْلَمُونَ نَصِيبًا مِمَّا رَزَقْنَاهُمْ تَاللَّهِ لَتُسْأَلُنَّ عَمَّا كُنْتُمْ تَفْتَرُونَ}
INSERT INTO [b9617] VALUES (1914,'<span data-type="title" id=toc-1899>٥٧ </span>- {وَيَجْعَلُونَ لِلَّهِ الْبَنَاتِ سُبْحَانَهُ وَلَهُمْ مَا يَشْتَهُونَ}
INSERT INTO [b9617] VALUES (1915,'<span data-type="title" id=toc-1900>٥٨ </span>- {وَإِذَا بُشِّرَ أَحَدُهُمْ بِالأُنْثَى ظَلَّ وَجْهُهُ مُسْوَدًّا وَهُوَ كَظِيمٌ}
INSERT INTO [b9617] VALUES (1916,'<span data-type="title" id=toc-1901>٥٩ </span>- {يَتَوَارَى مِنَ الْقَوْمِ مِنْ سُوءِ مَا بُشِّرَ بِهِ أَيُمْسِكُهُ عَلَى هُونٍ أَمْ يَدُسُّهُ فِي التُّرَابِ أَلا سَاءَ مَا يَحْكُمُونَ}
INSERT INTO [b9617] VALUES (1917,'<span data-type="title" id=toc-1902>٦٠ </span>- {لِلَّذِينَ لا يُؤْمِنُونَ بِالآخِرَةِ مَثَلُ السَّوْءِ وَلِلَّهِ الْمَثَلُ الأَعْلَى وَهُوَ الْعَزِيزُ الْحَكِيمُ}
INSERT INTO [b9617] VALUES (1918,'<span data-type="title" id=toc-1903>٦١ </span>- {وَلَوْ يُؤَاخِذُ اللَّهُ النَّاسَ بِظُلْمِهِمْ مَا تَرَكَ عَلَيْهَا مِنْ دَابَّةٍ وَلَكِنْ يُؤَخِّرُهُمْ إِلَى أَجَلٍ مُسَمًّى فَإِذَا جَاءَ أَجَلُهُمْ لا يَسْتَأْخِرُونَ سَاعَةً وَلا يَسْتَقْدِمُونَ}
INSERT INTO [b9617] VALUES (1919,'<span data-type="title" id=toc-1904>٦٢ </span>- {وَيَجْعَلُونَ لِلَّهِ مَا يَكْرَهُونَ وَتَصِفُ أَلْسِنَتُهُمُ الْكَذِبَ أَنَّ لَهُمُ الْحُسْنَى لا جَرَمَ أَنَّ لَهُمُ النَّارَ}
INSERT INTO [b9617] VALUES (1920,'<span data-type="title" id=toc-1905>٦٣ </span>- {تَاللَّهِ لَقَدْ أَرْسَلْنَا إِلَى أُمَمٍ مِنْ قَبْلِكَ فَزَيَّنَ لَهُمُ الشَّيْطَانُ أَعْمَالَهُمْ فَهُوَ وَلِيُّهُمُ الْيَوْمَ وَلَهُمْ عَذَابٌ أَلِيمٌ}
INSERT INTO [b9617] VALUES (1921,'<span data-type="title" id=toc-1906>٦٤ </span>- {وَمَا أَنْزَلْنَا عَلَيْكَ الْكِتَابَ إِلا لِتُبَيِّنَ لَهُمُ الَّذِي اخْتَلَفُوا فِيهِ وَهُدًى وَرَحْمَةً لِقَوْمٍ}
INSERT INTO [b9617] VALUES (1922,'<span data-type="title" id=toc-1907>٦٥ </span>- {وَاللَّهُ أَنْزَلَ مِنَ السَّمَاءِ مَاءً فَأَحْيَا بِهِ الأَرْضَ بَعْدَ مَوْتِهَا إِنَّ فِي ذَلِكَ لآيَةً}
INSERT INTO [b9617] VALUES (1923,'<span data-type="title" id=toc-1908>٦٦ </span>- {وَإِنَّ لَكُمْ فِي الأَنْعَامِ لَعِبْرَةً نُسْقِيكُمْ مِمَّا فِي بُطُونِهِ مِنْ بَيْنِ فَرْثٍ وَدَمٍ لَبَنًا خَالِصًا سَائِغًا لِلشَّارِبِينَ}
INSERT INTO [b9617] VALUES (1924,'<span data-type="title" id=toc-1909>٦٧ </span>- {وَمِنْ ثَمَرَاتِ النَّخِيلِ وَالأَعْنَابِ تَتَّخِذُونَ مِنْهُ سَكَرًا وَرِزْقًا حَسَنًا إِنَّ فِي ذَلِكَ لآيَةً لِقَوْمٍ يَعْقِلُونَ}
INSERT INTO [b9617] VALUES (1925,'<span data-type="title" id=toc-1910>٦٨ </span>- {وَأَوْحَى رَبُّكَ إِلَى النَّحْلِ أَنِ اتَّخِذِي مِنَ الْجِبَالِ بُيُوتًا}
INSERT INTO [b9617] VALUES (1926,'<span data-type="title" id=toc-1911>٦٩ </span>- {ثُمَّ كُلِي مِنْ كُلِّ الثَّمَرَاتِ فَاسْلُكِي سُبُلَ رَبِّكِ ذُلُلا يَخْرُجُ مِنْ بُطُونِهَا شَرَابٌ مُخْتَلِفٌ أَلْوَانُهُ فِيهِ شِفَاءٌ لِلنَّاسِ إِنَّ فِي ذَلِكَ لآيَةً لِقَوْمٍ يَتَفَكَّرُونَ}
INSERT INTO [b9617] VALUES (1927,'<span data-type="title" id=toc-1912>٧٠ </span>- {وَمِنْكُمْ مَنْ يُرَدُّ إِلَى أَرْذَلِ الْعُمُرِ لِكَيْ لا يَعْلَمَ بَعْدَ عِلْمٍ شَيْئًا}
INSERT INTO [b9617] VALUES (1928,'<span data-type="title" id=toc-1913>٧١ </span>- {وَاللَّهُ فَضَّلَ بَعْضَكُمْ عَلَى بَعْضٍ فِي الرِّزْقِ فَمَا الَّذِينَ فُضِّلُوا بِرَادِّي رِزْقِهِمْ عَلَى مَا مَلَكَتْ أَيْمَانُهُمْ فَهُمْ فِيهِ سَوَاءٌ أَفَبِنِعْمَةِ اللَّهِ يَجْحَدُونَ}
INSERT INTO [b9617] VALUES (1929,'<span data-type="title" id=toc-1914>٧٢ </span>- {وَجَعَلَ لَكُمْ مِنْ أَزْوَاجِكُمْ بَنِينَ وَحَفَدَةً وَرَزَقَكُمْ مِنَ الطَّيِّبَاتِ أَفَبِالْبَاطِلِ يُؤْمِنُونَ وَبِنِعْمَتِ اللَّهِ هُمْ يَكْفُرُونَ}
INSERT INTO [b9617] VALUES (1930,'<span data-type="title" id=toc-1915>٧٣ </span>- {وَيَعْبُدُونَ مِنْ دُونِ اللَّهِ مَا لا يَمْلِكُ لَهُمْ رِزْقًا مِنَ السَّمَاوَاتِ وَالأَرْضِ شَيْئًا}
INSERT INTO [b9617] VALUES (1931,'<span data-type="title" id=toc-1916>٧٤ </span>- {فَلا تَضْرِبُوا لِلَّهِ الأَمْثَالَ إِنَّ اللَّهَ يَعْلَمُ وَأَنْتُمْ لا تَعْلَمُونَ}
INSERT INTO [b9617] VALUES (1932,'<span data-type="title" id=toc-1917>٧٥ </span>- {ضَرَبَ اللَّهُ مَثَلا عَبْدًا مَمْلُوكًا لا يَقْدِرُ عَلَى شَيْءٍ وَمَنْ رَزَقْنَاهُ مِنَّا رِزْقًا ⦗٥٩٠⦘ حَسَنًا فَهُوَ يُنْفِقُ مِنْهُ سِرًّا وَجَهْرًا هَلْ يَسْتَوُونَ الْحَمْدُ لِلَّهِ بَلْ أَكْثَرُهُمْ لا يَعْلَمُونَ}
INSERT INTO [b9617] VALUES (1933,'<span data-type="title" id=toc-1918>٧٦ </span>- {وَضَرَبَ اللَّهُ مَثَلا رَجُلَيْنِ أَحَدُهُمَا أَبْكَمُ لا يَقْدِرُ عَلَى شَيْءٍ وَهُوَ كَلٌّ عَلَى مَوْلاهُ أَيْنَمَا يُوَجِّهْهُ لا يَأْتِ بِخَيْرٍ هَلْ يَسْتَوِي هُوَ وَمَنْ يَأْمُرُ بِالْعَدْلِ وَهُوَ عَلَى صِرَاطٍ مُسْتَقِيمٍ}
INSERT INTO [b9617] VALUES (1934,'<span data-type="title" id=toc-1919>٧٧ </span>- {وَلِلَّهِ غَيْبُ السَّمَاوَاتِ وَالأَرْضِ وَمَا أَمْرُ السَّاعَةِ إِلا كَلَمْحِ الْبَصَرِ أَوْ هُوَ أَقْرَبُ}
INSERT INTO [b9617] VALUES (1935,'<span data-type="title" id=toc-1920>٧٨ </span>- {وَاللَّهُ أَخْرَجَكُمْ مِنْ بُطُونِ أُمَّهَاتِكُمْ لا تَعْلَمُونَ شَيْئًا وَجَعَلَ لَكُمُ السَّمْعَ وَالأَبْصَارَ وَالأَفْئِدَةَ لَعَلَّكُمْ تَشْكُرُونَ}
INSERT INTO [b9617] VALUES (1936,'<span data-type="title" id=toc-1921>٧٩ </span>- {أَلَمْ يَرَوْا إِلَى الطَّيْرِ مُسَخَّرَاتٍ فِي جَوِّ السَّمَاءِ مَا يُمْسِكُهُنَّ إِلا اللَّهُ إِنَّ فِي ذَلِكَ لآيَاتٍ لِقَوْمٍ يُؤْمِنُونَ}
INSERT INTO [b9617] VALUES (1937,'<span data-type="title" id=toc-1922>٨٠ </span>- {وَاللَّهُ جَعَلَ لَكُمْ مِنْ بُيُوتِكُمْ سَكَنًا وَجَعَلَ لَكُمْ مِنْ جُلُودِ الأَنْعَامِ بُيُوتًا تَسْتَخِفُّونَهَا يَوْمَ ظَعْنِكُمْ وَيَوْمَ إِقَامَتِكُمْ وَمِنْ أَصْوَافِهَا وَأَوْبَارِهَا وَأَشْعَارِهَا أَثَاثًا وَمَتَاعًا إِلَى حِينٍ}
INSERT INTO [b9617] VALUES (1938,'<span data-type="title" id=toc-1923>٨١ </span>- {وَاللَّهُ جَعَلَ لَكُمْ مِمَّا خَلَقَ ظِلالا وَجَعَلَ لَكُمْ مِنَ الْجِبَالِ أَكْنَانًا ⦗٥٩٢⦘ وَجَعَلَ لَكُمْ سَرَابِيلَ تَقِيكُمُ الْحَرَّ وَسَرَابِيلَ تَقِيكُمْ بَأْسَكُمْ كَذَلِكَ يُتِمُّ نِعْمَتَهُ عَلَيْكُمْ لَعَلَّكُمْ تُسْلِمُونَ}
INSERT INTO [b9617] VALUES (1939,'<span data-type="title" id=toc-1924>٨٢ </span>- {فَإِنْ تَوَلَّوْا فَإِنَّمَا عَلَيْكَ الْبَلاغُ الْمُبِينُ}
INSERT INTO [b9617] VALUES (1940,'<span data-type="title" id=toc-1925>٨٣ </span>- {يَعْرِفُونَ نِعْمَتَ اللَّهِ ثُمَّ يُنْكِرُونَهَا وَأَكْثَرُهُمُ الْكَافِرُونَ}
INSERT INTO [b9617] VALUES (1941,'<span data-type="title" id=toc-1926>٨٤ </span>- {وَيَوْمَ نَبْعَثُ مِنْ كُلِّ أُمَّةٍ شَهِيدًا ثُمَّ لا يُؤْذَنُ لِلَّذِينَ كَفَرُوا وَلا هُمْ يُسْتَعْتَبُونَ}
INSERT INTO [b9617] VALUES (1942,'<span data-type="title" id=toc-1927>٨٥ </span>- {وَإِذَا رَأَى الَّذِينَ ظَلَمُوا الْعَذَابَ فَلا يُخَفَّفُ عَنْهُمْ وَلا هُمْ يُنْظَرُونَ}
INSERT INTO [b9617] VALUES (1943,'<span data-type="title" id=toc-1928>٨٦ </span>- {وَإِذَا رَأَى الَّذِينَ أَشْرَكُوا شُرَكَاءَهُمْ قَالُوا رَبَّنَا هَؤُلاءِ شُرَكَاؤُنَا الَّذِينَ كُنَّا نَدْعُوا مِنْ دُونِكَ فَأَلْقَوْا إِلَيْهِمُ الْقَوْلَ إِنَّكُمْ لَكَاذِبُونَ}
INSERT INTO [b9617] VALUES (1944,'<span data-type="title" id=toc-1929>٨٧ </span>- {وَأَلْقَوْا إِلَى اللَّهِ يَوْمَئِذٍ السَّلَمَ وَضَلَّ عَنْهُمْ مَا كَانُوا يَفْتَرُونَ}
INSERT INTO [b9617] VALUES (1945,'<span data-type="title" id=toc-1930>٨٨ </span>- {الَّذِينَ كَفَرُوا وَصَدُّوا عَنْ سَبِيلِ اللَّهِ زِدْنَاهُمْ عَذَابًا فَوْقَ الْعَذَابِ بِمَا كَانُوا يُفْسِدُونَ} ⦗٥٩٤⦘
INSERT INTO [b9617] VALUES (1946,'<span data-type="title" id=toc-1931>٨٩ </span>- {وَيَوْمَ نَبْعَثُ فِي كُلِّ أُمَّةٍ شَهِيدًا عَلَيْهِمْ مِنْ أَنْفُسِهِمْ وَجِئْنَا بِكَ شَهِيدًا عَلَى هَؤُلاءِ وَنَزَّلْنَا عَلَيْكَ الْكِتَابَ تِبْيَانًا لِكُلِّ شَيْءٍ وَهُدًى وَرَحْمَةً وَبُشْرَى لِلْمُسْلِمِينَ}
INSERT INTO [b9617] VALUES (1947,'<span data-type="title" id=toc-1932>٩٠ </span>- {وَيَنْهَى عَنِ الْفَحْشَاءِ وَالْمُنْكَرِ وَالْبَغْيِ يَعِظُكُمْ لَعَلَّكُمْ تَذَكَّرُونَ}
INSERT INTO [b9617] VALUES (1948,'<span data-type="title" id=toc-1933>٩١ </span>- {وَأَوْفُوا بِعَهْدِ اللَّهِ إِذَا عَاهَدْتُمْ وَلا تَنْقُضُوا الأَيْمَانَ بَعْدَ تَوْكِيدِهَا وَقَدْ جَعَلْتُمُ اللَّهَ عَلَيْكُمْ كَفِيلا إِنَّ اللَّهَ يَعْلَمُ مَا تَفْعَلُونَ}
INSERT INTO [b9617] VALUES (1949,'<span data-type="title" id=toc-1934>٩٢ </span>- {وَلا تَكُونُوا كَالَّتِي نَقَضَتْ غَزْلَهَا مِنْ بَعْدِ قُوَّةٍ أَنْكَاثًا تَتَّخِذُونَ أَيْمَانَكُمْ دَخَلا بَيْنَكُمْ أَنْ تَكُونَ أُمَّةٌ هِيَ أَرْبَى مِنْ أُمَّةٍ إِنَّمَا يَبْلُوكُمُ اللَّهُ بِهِ وَلَيُبَيِّنَنَّ لَكُمْ يَوْمَ الْقِيَامَةِ مَا كُنْتُمْ فِيهِ تَخْتَلِفُونَ}
INSERT INTO [b9617] VALUES (1950,'<span data-type="title" id=toc-1935>٩٣ </span>- {وَلَوْ شَاءَ اللَّهُ لَجَعَلَكُمْ أُمَّةً وَاحِدَةً وَلَكِنْ يُضِلُّ مَنْ يَشَاءُ وَيَهْدِي مَنْ يَشَاءُ وَلَتُسْأَلُنَّ عَمَّا كُنْتُمْ تَعْمَلُونَ}
INSERT INTO [b9617] VALUES (1951,'<span data-type="title" id=toc-1936>٩٤ </span>- {وَلا تَتَّخِذُوا أَيْمَانَكُمْ دَخَلا بَيْنَكُمْ فَتَزِلَّ قَدَمٌ بَعْدَ ثُبُوتِهَا وَتَذُوقُوا السُّوءَ بِمَا صَدَدْتُمْ عَنْ سَبِيلِ اللَّهِ وَلَكُمْ عَذَابٌ عَظِيمٌ}
INSERT INTO [b9617] VALUES (1952,'<span data-type="title" id=toc-1937>٩٥ </span>- {وَلا تَشْتَرُوا بِعَهْدِ اللَّهِ ثَمَنًا قَلِيلا إِنَّمَا عِنْدَ اللَّهِ هُوَ خَيْرٌ لَكُمْ إِنْ كُنْتُمْ تَعْلَمُونَ}
INSERT INTO [b9617] VALUES (1953,'<span data-type="title" id=toc-1938>٩٦ </span>- {مَا عِنْدَكُمْ يَنْفَدُ وَمَا عِنْدَ اللَّهِ بَاقٍ وَلَنَجْزِيَنَّ الَّذِينَ صَبَرُوا أَجْرَهُمْ بِأَحْسَنِ مَا كَانُوا يَعْمَلُونَ}
INSERT INTO [b9617] VALUES (1954,'<span data-type="title" id=toc-1939>٩٧ </span>- {مَنْ عَمِلَ صَالِحًا مِنْ ذَكَرٍ أَوْ أُنْثَى وَهُوَ مُؤْمِنٌ فَلَنُحْيِيَنَّهُ حَيَاةً طَيِّبَةً} ⦗٥٩٧⦘
INSERT INTO [b9617] VALUES (1955,'<span data-type="title" id=toc-1940>٩٨ </span>- {فَإِذَا قَرَأْتَ الْقُرْآنَ فَاسْتَعِذْ بِاللَّهِ مِنَ الشَّيْطَانِ الرَّجِيمِ}
INSERT INTO [b9617] VALUES (1956,'<span data-type="title" id=toc-1941>٩٩ </span>- {إِنَّهُ لَيْسَ لَهُ سُلْطَانٌ عَلَى الَّذِينَ آمَنُوا وَعَلَى رَبِّهِمْ يَتَوَكَّلُونَ}
INSERT INTO [b9617] VALUES (1957,'<span data-type="title" id=toc-1942>١٠٠ </span>- {إِنَّمَا سُلْطَانُهُ عَلَى الَّذِينَ يَتَوَلَّوْنَهُ وَالَّذِينَ هُمْ بِهِ مُشْرِكُونَ}
INSERT INTO [b9617] VALUES (1958,'<span data-type="title" id=toc-1943>١٠١ </span>- {وَإِذَا بَدَّلْنَا آيَةً مَكَانَ آيَةٍ وَاللَّهُ أَعْلَمُ بِمَا يُنَزِّلُ قَالُوا إِنَّمَا أَنْتَ مُفْتَرٍ بَلْ أَكْثَرُهُمْ لا يَعْلَمُونَ}
INSERT INTO [b9617] VALUES (1959,'<span data-type="title" id=toc-1944>١٠٢ </span>- {قُلْ نَزَّلَهُ رُوحُ الْقُدُسِ مِنْ رَبِّكَ بِالْحَقِّ لِيُثَبِّتَ الَّذِينَ آمَنُوا وَهُدًى وَبُشْرَى لِلْمُسْلِمِينَ}
INSERT INTO [b9617] VALUES (1960,'<span data-type="title" id=toc-1945>١٠٣ </span>- {وَلَقَدْ نَعْلَمُ أَنَّهُمْ يَقُولُونَ إِنَّمَا يُعَلِّمُهُ بَشَرٌ لِسَانُ الَّذِي يُلْحِدُونَ إِلَيْهِ أَعْجَمِيٌّ}
INSERT INTO [b9617] VALUES (1961,'<span data-type="title" id=toc-1946>١٠٤ </span>- {إِنَّ الَّذِينَ لا يُؤْمِنُونَ بِآيَاتِ اللَّهِ لا يَهْدِيهِمُ اللَّهُ وَلَهُمْ عَذَابٌ أَلِيمٌ}
INSERT INTO [b9617] VALUES (1962,'<span data-type="title" id=toc-1947>١٠٥ </span>- {إِنَّمَا يَفْتَرِي الْكَذِبَ الَّذِينَ لا يُؤْمِنُونَ بِآيَاتِ اللَّهِ وَأُولَئِكَ هُمُ الْكَاذِبُونَ}
INSERT INTO [b9617] VALUES (1963,'<span data-type="title" id=toc-1948>١٠٦ </span>- {مَنْ كَفَرَ بِاللَّهِ مِنْ بَعْدِ إِيمَانِهِ إِلا مَنْ أُكْرِهَ وَقَلْبُهُ مُطْمَئِنٌّ بِالإِيمَانِ وَلَكِنْ مَنْ شَرَحَ بِالْكُفْرِ صَدْرًا فَعَلَيْهِمْ غَضَبٌ مِنَ اللَّهِ وَلَهُمْ عَذَابٌ عَظِيمٌ}
INSERT INTO [b9617] VALUES (1964,'<span data-type="title" id=toc-1949>١٠٧ </span>- {ذَلِكَ بِأَنَّهُمُ اسْتَحَبُّوا الْحَيَاةَ الدُّنْيَا عَلَى الآخِرَةِ وَأَنَّ اللَّهَ لا يَهْدِي الْقَوْمَ الْكَافِرِينَ}
INSERT INTO [b9617] VALUES (1965,'<span data-type="title" id=toc-1950>١٠٨ </span>- {أُولَئِكَ الَّذِينَ طَبَعَ اللَّهُ عَلَى قُلُوبِهِمْ وَسَمْعِهِمْ وَأَبْصَارِهِمْ وَأُولَئِكَ هُمُ الْغَافِلُونَ}
INSERT INTO [b9617] VALUES (1966,'<span data-type="title" id=toc-1951>١٠٩ </span>- {لا جَرَمَ أَنَّهُمْ فِي الآخِرَةِ هُمُ الْخَاسِرُونَ}
INSERT INTO [b9617] VALUES (1967,'<span data-type="title" id=toc-1952>١١٠ </span>- {ثُمَّ إِنَّ رَبَّكَ لِلَّذِينَ هَاجَرُوا مِنْ بَعْدِ مَا فُتِنُوا ثُمَّ جَاهَدُوا وَصَبَرُوا ⦗٦٠٠⦘ إِنَّ رَبَّكَ مِنْ بَعْدِهَا لَغَفُورٌ رَحِيمٌ}
INSERT INTO [b9617] VALUES (1968,'<span data-type="title" id=toc-1953>١١١ </span>- {يَوْمَ تَأْتِي كُلُّ نَفْسٍ تُجَادِلُ عَنْ نَفْسِهَا وَتُوَفَّى كُلُّ نَفْسٍ مَا عَمِلَتْ وَهُمْ لا يُظْلَمُونَ}
INSERT INTO [b9617] VALUES (1969,'<span data-type="title" id=toc-1954>١١٢ </span>- {وَضَرَبَ اللَّهُ مَثَلا قَرْيَةً كَانَتْ آمِنَةً مُطْمَئِنَّةً يَأْتِيهَا رِزْقُهَا رَغَدًا مِنْ كُلِّ مَكَانٍ فَكَفَرَتْ بِأَنْعُمِ اللَّهِ فَأَذَاقَهَا اللَّهُ لِبَاسَ الْجُوعِ وَالْخَوْفِ بِمَا كَانُوا يَصْنَعُونَ}
INSERT INTO [b9617] VALUES (1970,'<span data-type="title" id=toc-1955>١١٣ </span>- {وَلَقَدْ جَاءَهُمْ رَسُولٌ مِنْهُمْ فَكَذَّبُوهُ فَأَخَذَهُمُ الْعَذَابُ وَهُمْ ظَالِمُونَ}
INSERT INTO [b9617] VALUES (1971,'<span data-type="title" id=toc-1956>١١٤ </span>- {فَكُلُوا مِمَّا رَزَقَكُمُ اللَّهُ حَلالا طَيِّبًا وَاشْكُرُوا نِعْمَتَ اللَّهِ إِنْ كُنْتُمْ إِيَّاهُ تَعْبُدُونَ}
INSERT INTO [b9617] VALUES (1972,'<span data-type="title" id=toc-1957>١١٥ </span>- {إِنَّمَا حَرَّمَ عَلَيْكُمُ الْمَيْتَةَ وَالدَّمَ وَلَحْمَ الْخِنْزِيرِ وَمَا أُهِلَّ لِغَيْرِ اللَّهِ بِهِ فَمَنِ اضْطُرَّ غَيْرَ بَاغٍ وَلا عَادٍ فَإِنَّ اللَّهَ غَفُورٌ رَحِيمٌ}
INSERT INTO [b9617] VALUES (1973,'<span data-type="title" id=toc-1958>١١٦ </span>- {وَلا تَقُولُوا لِمَا تَصِفُ أَلْسِنَتُكُمُ الْكَذِبَ هَذَا حَلالٌ وَهَذَا حَرَامٌ لِتَفْتَرُوا عَلَى اللَّهِ الْكَذِبَ إِنَّ الَّذِينَ يَفْتَرُونَ عَلَى اللَّهِ الْكَذِبَ لا يُفْلِحُونَ}
INSERT INTO [b9617] VALUES (1974,'<span data-type="title" id=toc-1959>١١٧ </span>- {مَتَاعٌ قَلِيلٌ وَلَهُمْ عَذَابٌ أَلِيمٌ}
INSERT INTO [b9617] VALUES (1975,'<span data-type="title" id=toc-1960>١١٨ </span>- {وَعَلَى الَّذِينَ هَادُوا حَرَّمْنَا مَا قَصَصْنَا عَلَيْكَ مِنْ قَبْلُ وَمَا ظَلَمْنَاهُمْ وَلَكِنْ كَانُوا أَنْفُسَهُمْ يَظْلِمُونَ}
INSERT INTO [b9617] VALUES (1976,'<span data-type="title" id=toc-1961>١١٩ </span>- {ثُمَّ إِنَّ رَبَّكَ لِلَّذِينَ عَمِلُوا السُّوءَ بِجَهَالَةٍ ثُمَّ تَابُوا مِنْ بَعْدِ ذَلِكَ وَأَصْلَحُوا إِنَّ رَبَّكَ مِنْ بَعْدِهَا لَغَفُورٌ رَحِيمٌ}
INSERT INTO [b9617] VALUES (1977,'<span data-type="title" id=toc-1962>١٢٠ </span>- {إِنَّ إِبْرَاهِيمَ كَانَ أُمَّةً قَانِتًا لِلَّهِ حَنِيفًا وَلَمْ يَكُ مِنَ الْمُشْرِكِينَ}
INSERT INTO [b9617] VALUES (1978,'<span data-type="title" id=toc-1963>١٢١ </span>- {شَاكِرًا لأَنْعُمِهِ اجْتَبَاهُ وَهَدَاهُ إِلَى صِرَاطٍ مُسْتَقِيمٍ}
INSERT INTO [b9617] VALUES (1979,'<span data-type="title" id=toc-1964>١٢٢ </span>- {وَآتَيْنَاهُ فِي الدُّنْيَا حَسَنَةً وَإِنَّهُ فِي الآخِرَةِ لَمِنَ الصَّالِحِينَ}
INSERT INTO [b9617] VALUES (1980,'<span data-type="title" id=toc-1965>١٢٣ </span>- {ثُمَّ أَوْحَيْنَا إِلَيْكَ أَنِ اتَّبِعْ مِلَّةَ إِبْرَاهِيمَ حَنِيفًا وَمَا كَانَ مِنَ الْمُشْرِكِينَ}
INSERT INTO [b9617] VALUES (1981,'<span data-type="title" id=toc-1966>١٢٤ </span>- {وَإِنَّ رَبَّكَ لَيَحْكُمُ بَيْنَهُمْ يَوْمَ الْقِيَامَةِ فِيمَا كَانُوا فِيهِ يَخْتَلِفُونَ}
INSERT INTO [b9617] VALUES (1982,'<span data-type="title" id=toc-1967>١٢٥ </span>- {إِنَّ رَبَّكَ هُوَ أَعْلَمُ بِمَنْ ضَلَّ عَنْ سَبِيلِهِ وَهُوَ أَعْلَمُ بِالْمُهْتَدِينَ} ⦗٦٠٤⦘
INSERT INTO [b9617] VALUES (1983,'<span data-type="title" id=toc-1968>١٢٦ </span>- {وَإِنْ عَاقَبْتُمْ فَعَاقِبُوا بِمِثْلِ مَا عُوقِبْتُمْ بِهِ وَلَئِنْ صَبَرْتُمْ لَهُوَ خَيْرٌ لِلصَّابِرِينَ}
INSERT INTO [b9617] VALUES (1984,'<span data-type="title" id=toc-1969>١٢٧ </span>- {وَاصْبِرْ وَمَا صَبْرُكَ إِلا بِاللَّهِ وَلا تَحْزَنْ عَلَيْهِمْ وَلا تَكُ فِي ضَيْقٍ مِمَّا يَمْكُرُونَ}
INSERT INTO [b9617] VALUES (1985,'<span data-type="title" id=toc-1970>١٢٨ </span>- {إِنَّ اللَّهَ مَعَ الَّذِينَ اتَّقَوْا}
INSERT INTO [b9617] VALUES (1986,'<span data-type="title" id=toc-1971>سورة الإسراء</span>','2','605',NULL,NULL,'0');
INSERT INTO [b9617] VALUES (1987,'<span data-type="title" id=toc-1972>١ </span>- {سُبْحَانَ الَّذِي أَسْرَى بِعَبْدِهِ لَيْلا مِنَ الْمَسْجِدِ الْحَرَامِ إِلَى الْمَسْجِدِ الأَقْصَى الَّذِي بَارَكْنَا حَوْلَهُ لِنُرِيَهُ مِنْ آيَاتِنَا إِنَّهُ هُوَ السَّمِيعُ الْبَصِيرُ}
INSERT INTO [b9617] VALUES (1988,'<span data-type="title" id=toc-1973>٢ </span>- {وَآتَيْنَا مُوسَى الْكِتَابَ وَجَعَلْنَاهُ هُدًى لِبَنِي إِسْرَائِيلَ أَلا تَتَّخِذُوا مِنْ دُونِي وَكِيلا}
INSERT INTO [b9617] VALUES (1989,'<span data-type="title" id=toc-1974>٣ </span>- {ذُرِّيَّةَ مَنْ حَمَلْنَا مَعَ نُوحٍ إِنَّهُ كَانَ عَبْدًا شَكُورًا}
INSERT INTO [b9617] VALUES (1990,'<span data-type="title" id=toc-1975>٤ </span>- {وَقَضَيْنَا إِلَى بَنِي إِسْرَائِيلَ فِي الْكِتَابِ لَتُفْسِدُنَّ فِي الأَرْضِ مَرَّتَيْنِ وَلَتَعْلُنَّ عُلُوًّا كَبِيرًا} ⦗٦٠٦⦘
INSERT INTO [b9617] VALUES (1991,'<span data-type="title" id=toc-1976>٥ </span>- {فَإِذَا جَاءَ وَعْدُ أُولاهُمَا بَعَثْنَا عَلَيْكُمْ عِبَادًا لَنَا أُولِي بَأْسٍ شَدِيدٍ فَجَاسُوا خِلالَ الدِّيَارِ وَكَانَ وَعْدًا مَفْعُولا}
INSERT INTO [b9617] VALUES (1992,'<span data-type="title" id=toc-1977>٦ </span>- {ثُمَّ رَدَدْنَا لَكُمُ الْكَرَّةَ عَلَيْهِمْ وَأَمْدَدْنَاكُمْ بِأَمْوَالٍ وَبَنِينَ وَجَعَلْنَاكُمْ أَكْثَرَ نَفِيرًا}
INSERT INTO [b9617] VALUES (1993,'<span data-type="title" id=toc-1978>٧ </span>- {إِنْ أَحْسَنْتُمْ أَحْسَنْتُمْ لأَنْفُسِكُمْ وَإِنْ أَسَأْتُمْ فَلَهَا فَإِذَا جَاءَ وَعْدُ الآخِرَةِ لِيَسُوءُوا وُجُوهَكُمْ وَلِيَدْخُلُوا الْمَسْجِدَ كَمَا دَخَلُوهُ أَوَّلَ مَرَّةٍ وَلِيُتَبِّرُوا مَا عَلَوْا تَتْبِيرًا}
INSERT INTO [b9617] VALUES (1994,'<span data-type="title" id=toc-1979>٨ </span>- {عَسَى رَبُّكُمْ أَنْ يَرْحَمَكُمْ وَإِنْ عُدْتُمْ عُدْنَا وَجَعَلْنَا جَهَنَّمَ لِلْكَافِرِينَ حَصِيرًا}
INSERT INTO [b9617] VALUES (1995,'<span data-type="title" id=toc-1980>٩ </span>- {إِنَّ هَذَا الْقُرْآنَ يَهْدِي لِلَّتِي هِيَ أَقْوَمُ وَيُبَشِّرُ الْمُؤْمِنِينَ الَّذِينَ يَعْمَلُونَ الصَّالِحَاتِ أَنَّ لَهُمْ أَجْرًا كَبِيرًا}
INSERT INTO [b9617] VALUES (1996,'<span data-type="title" id=toc-1981>١٠ </span>- {وَأَنَّ الَّذِينَ لا يُؤْمِنُونَ بِالآخِرَةِ أَعْتَدْنَا لَهُمْ عَذَابًا أَلِيمًا}
INSERT INTO [b9617] VALUES (1997,'<span data-type="title" id=toc-1982>١١ </span>- {وَيَدْعُ الإِنْسَانُ بِالشَّرِّ دُعَاءَهُ بِالْخَيْرِ وَكَانَ الإِنْسَانُ عَجُولا}
INSERT INTO [b9617] VALUES (1998,'<span data-type="title" id=toc-1983>١٢ </span>- {وَجَعَلْنَا اللَّيْلَ وَالنَّهَارَ آيَتَيْنِ فَمَحَوْنَا آيَةَ اللَّيْلِ وَجَعَلْنَا آيَةَ النَّهَارِ مُبْصِرَةً لِتَبْتَغُوا فَضْلا مِنْ رَبِّكُمْ وَلِتَعْلَمُوا عَدَدَ السِّنِينَ وَالْحِسَابَ وَكُلَّ شَيْءٍ فَصَّلْنَاهُ تَفْصِيلا}
INSERT INTO [b9617] VALUES (1999,'<span data-type="title" id=toc-1984>١٣ </span>- {وَكُلَّ إِنْسَانٍ أَلْزَمْنَاهُ طَائِرَهُ فِي عُنُقِهِ وَنُخْرِجُ لَهُ يَوْمَ الْقِيَامَةِ كِتَابًا يَلْقَاهُ مَنْشُورًا}
INSERT INTO [b9617] VALUES (2000,'<span data-type="title" id=toc-1985>١٤ </span>- {اقْرَأْ كِتَابَكَ كَفَى بِنَفْسِكَ الْيَوْمَ عَلَيْكَ حَسِيبًا}
INSERT INTO [b9617] VALUES (2001,'<span data-type="title" id=toc-1986>١٥ </span>- {مَنِ اهْتَدَى فَإِنَّمَا يَهْتَدِي لِنَفْسِهِ وَمَنْ ضَلَّ فَإِنَّمَا يَضِلُّ عَلَيْهَا وَلا تَزِرُ وَازِرَةٌ وِزْرَ أُخْرَى وَمَا كُنَّا مُعَذِّبِينَ حَتَّى نَبْعَثَ رَسُولا}
INSERT INTO [b9617] VALUES (2002,'<span data-type="title" id=toc-1987>١٦ </span>- {وَإِذَا أَرَدْنَا أَنْ نُهْلِكَ قَرْيَةً أَمَرْنَا مُتْرَفِيهَا}
INSERT INTO [b9617] VALUES (2003,'<span data-type="title" id=toc-1988>١٧ </span>- {وَكَمْ أَهْلَكْنَا مِنَ الْقُرُونِ مِنْ بَعْدِ نُوحٍ وَكَفَى بِرَبِّكَ بِذُنُوبِ عِبَادِهِ خَبِيرًا بَصِيرًا}
INSERT INTO [b9617] VALUES (2004,'<span data-type="title" id=toc-1989>١٨ </span>- {مَنْ كَانَ يُرِيدُ الْعَاجِلَةَ عَجَّلْنَا لَهُ فِيهَا مَا نَشَاءُ لِمَنْ نُرِيدُ ثُمَّ جَعَلْنَا لَهُ جَهَنَّمَ يَصْلاهَا مَذْمُومًا مَدْحُورًا}
INSERT INTO [b9617] VALUES (2005,'<span data-type="title" id=toc-1990>١٩ </span>- {وَمَنْ أَرَادَ الآخِرَةَ وَسَعَى لَهَا سَعْيَهَا وَهُوَ مُؤْمِنٌ فَأُولَئِكَ كَانَ سَعْيُهُمْ مَشْكُورًا}
INSERT INTO [b9617] VALUES (2006,'<span data-type="title" id=toc-1991>٢٠ </span>- {كُلا نُمِدُّ هَؤُلاءِ وَهَؤُلاءِ مِنْ عَطَاءِ رَبِّكَ وَمَا كَانَ عَطَاءُ رَبِّكَ مَحْظُورًا}
INSERT INTO [b9617] VALUES (2007,'<span data-type="title" id=toc-1992>٢١ </span>- {انْظُرْ كَيْفَ فَضَّلْنَا بَعْضَهُمْ عَلَى بَعْضٍ وَلَلآخِرَةُ أَكْبَرُ دَرَجَاتٍ وَأَكْبَرُ تَفْضِيلا}
INSERT INTO [b9617] VALUES (2008,'<span data-type="title" id=toc-1993>٢٢ </span>- {لا تَجْعَلْ مَعَ اللَّهِ إِلَهًا آخَرَ فَتَقْعُدَ مَذْمُومًا مَخْذُولا}
INSERT INTO [b9617] VALUES (2009,'<span data-type="title" id=toc-1994>٢٣ </span>- {وَقَضَى رَبُّكَ أَلا تَعْبُدُوا إِلا إِيَّاهُ وَبِالْوَالِدَيْنِ إِحْسَانًا إِمَّا يَبْلُغَنَّ عِنْدَكَ الْكِبَرَ أَحَدُهُمَا أَوْ كِلاهُمَا فَلا تَقُلْ لَهُمَا أُفٍّ}
INSERT INTO [b9617] VALUES (2010,'<span data-type="title" id=toc-1995>٢٤ </span>- {وَاخْفِضْ لَهُمَا جَنَاحَ الذُّلِّ مِنَ الرَّحْمَةِ وَقُلْ رَبِّي ارْحَمْهُمَا كَمَا رَبَّيَانِي صَغِيرًا}
INSERT INTO [b9617] VALUES (2011,'<span data-type="title" id=toc-1996>٢٥ </span>- {رَبُّكُمْ أَعْلَمُ بِمَا فِي نُفُوسِكُمْ إِنْ تَكُونُوا صَالِحِينَ فَإِنَّهُ كَانَ لِلأَوَّابِينَ غَفُورًا}
INSERT INTO [b9617] VALUES (2012,'<span data-type="title" id=toc-1997>٢٦ </span>- {وَآتِ ذَا الْقُرْبَى حَقَّهُ}
INSERT INTO [b9617] VALUES (2013,'<span data-type="title" id=toc-1998>٢٧ </span>- {إِنَّ الْمُبَذِّرِينَ كَانُوا إِخْوَانَ الشَّيَاطِينِ وَكَانَ الشَّيْطَانُ لِرَبِّهِ كَفُورًا}
INSERT INTO [b9617] VALUES (2014,'<span data-type="title" id=toc-1999>٢٨ </span>- {وَإِمَّا تُعْرِضَنَّ عَنْهُمُ ابْتِغَاءَ رَحْمَةٍ مِنْ رَبِّكَ تَرْجُوهَا فَقُلْ لَهُمْ قَوْلا مَيْسُورًا}
INSERT INTO [b9617] VALUES (2015,'<span data-type="title" id=toc-2000>٢٩ </span>- {وَلا تَجْعَلْ يَدَكَ مَغْلُولَةً إِلَى عُنُقِكَ وَلا تَبْسُطْهَا كُلَّ الْبَسْطِ فَتَقْعُدَ مَلُومًا مَحْسُورًا}
INSERT INTO [b9617] VALUES (2016,'<span data-type="title" id=toc-2001>٣٠ </span>- {إِنَّ رَبَّكَ يَبْسُطُ الرِّزْقَ لِمَنْ يَشَاءُ وَيَقْدِرُ إِنَّهُ كَانَ بِعِبَادِهِ خَبِيرًا بَصِيرًا}
INSERT INTO [b9617] VALUES (2017,'<span data-type="title" id=toc-2002>٣١ </span>- {وَلا تَقْتُلُوا أَوْلادَكُمْ خَشْيَةَ إِمْلاقٍ نَحْنُ نَرْزُقُهُمْ وَإِيَّاكُمْ إِنَّ قَتْلَهُمْ كَانَ خِطْئًا كَبِيرًا} ⦗٦١٣⦘
INSERT INTO [b9617] VALUES (2018,'<span data-type="title" id=toc-2003>٣٢ </span>- {وَلا تَقْرَبُوا الزِّنَى إِنَّهُ كَانَ فَاحِشَةً وَسَاءَ سَبِيلا}
INSERT INTO [b9617] VALUES (2019,'<span data-type="title" id=toc-2004>٣٣ </span>- {وَلا تَقْتُلُوا النَّفْسَ الَّتِي حَرَّمَ اللَّهُ إِلا بِالْحَقِّ وَمَنْ قُتِلَ مَظْلُومًا فَقَدْ جَعَلْنَا لِوَلِيِّهِ سُلْطَانًا فَلا يُسْرِفْ فِي الْقَتْلِ إِنَّهُ كَانَ مَنْصُورًا}
INSERT INTO [b9617] VALUES (2020,'<span data-type="title" id=toc-2005>٣٤ </span>- {وَلا تَقْرَبُوا مَالَ الْيَتِيمِ إِلا بِالَّتِي هِيَ أَحْسَنُ حَتَّى يَبْلُغَ أَشُدَّهُ وَأَوْفُوا بِالْعَهْدِ إِنَّ الْعَهْدَ كَانَ مَسْئُولا}
INSERT INTO [b9617] VALUES (2021,'<span data-type="title" id=toc-2006>٣٥ </span>- {وَأَوْفُوا الْكَيْلَ إِذَا كِلْتُمْ وَزِنُوا بِالْقِسْطَاسِ الْمُسْتَقِيمِ ذَلِكَ خَيْرٌ وَأَحْسَنُ تَأْوِيلا}
INSERT INTO [b9617] VALUES (2022,'<span data-type="title" id=toc-2007>٣٦ </span>- {وَلا تَقْفُ مَا لَيْسَ لَكَ بِهِ عِلْمٌ إِنَّ السَّمْعَ وَالْبَصَرَ وَالْفُؤَادَ كُلُّ أُولَئِكَ كَانَ عَنْهُ مَسْئُولا}
INSERT INTO [b9617] VALUES (2023,'<span data-type="title" id=toc-2008>٣٧ </span>- {وَلا تَمْشِ فِي الأَرْضِ مَرَحًا إِنَّكَ لَنْ تَخْرِقَ الأَرْضَ وَلَنْ تَبْلُغَ الْجِبَالَ طُولا}
INSERT INTO [b9617] VALUES (2024,'<span data-type="title" id=toc-2009>٣٨ </span>- {كُلُّ ذَلِكَ كَانَ سَيِّئُهُ عِنْدَ رَبِّكَ مَكْرُوهًا}
INSERT INTO [b9617] VALUES (2025,'<span data-type="title" id=toc-2010>٣٩ </span>- {ذَلِكَ مِمَّا أَوْحَى إِلَيْكَ رَبُّكَ مِنَ الْحِكْمَةِ وَلا تَجْعَلْ مَعَ اللَّهِ إِلَهًا آخَرَ فَتُلْقَى فِي جَهَنَّمَ مَلُومًا مَدْحُورًا}
INSERT INTO [b9617] VALUES (2026,'<span data-type="title" id=toc-2011>٤٠ </span>- {أَفَأَصْفَاكُمْ رَبُّكُمْ بِالْبَنِينَ وَاتَّخَذَ مِنَ الْمَلائِكَةِ إِنَاثًا}
INSERT INTO [b9617] VALUES (2027,'<span data-type="title" id=toc-2012>٤١ </span>- {وَلَقَدْ صَرَّفْنَا فِي هَذَا الْقُرْآنِ لِيَذَّكَّرُوا وَمَا يَزِيدُهُمْ إِلا نُفُورًا}
INSERT INTO [b9617] VALUES (2028,'<span data-type="title" id=toc-2013>٤٢ </span>- {قُلْ لَوْ كَانَ مَعَهُ آلِهَةٌ كَمَا يَقُولُونَ إِذًا لابْتَغَوْا إِلَى ذِي الْعَرْشِ سَبِيلا}
INSERT INTO [b9617] VALUES (2029,'<span data-type="title" id=toc-2014>٤٣ </span>- {سُبْحَانَهُ وَتَعَالَى عَمَّا يَقُولُونَ عُلُوًّا كَبِيرًا}
INSERT INTO [b9617] VALUES (2030,'<span data-type="title" id=toc-2015>٤٤ </span>- {تُسَبِّحُ لَهُ السَّمَاوَاتُ السَّبْعُ وَالأَرْضُ وَمَنْ فِيهِنَّ وَإِنْ مِنْ شَيْءٍ إِلا يُسَبِّحُ بِحَمْدِهِ وَلَكِنْ لا تَفْقَهُونَ تَسْبِيحَهُمْ}
INSERT INTO [b9617] VALUES (2031,'<span data-type="title" id=toc-2016>٤٥ </span>- {وَإِذَا قَرَأْتَ الْقُرْآنَ جَعَلْنَا بَيْنَكَ وَبَيْنَ الَّذِينَ لا يُؤْمِنُونَ بِالآخِرَةِ حِجَابًا مَسْتُورًا}
INSERT INTO [b9617] VALUES (2032,'<span data-type="title" id=toc-2017>٤٦ </span>- {وَجَعَلْنَا عَلَى قُلُوبِهِمْ أَكِنَّةً أَنْ يَفْقَهُوهُ وَفِي آذَانِهِمْ وَقْرًا وَإِذَا ذَكَرْتَ رَبَّكَ فِي الْقُرْآنِ وَحْدَهُ وَلَّوْا عَلَى أَدْبَارِهِمْ نُفُورًا}
INSERT INTO [b9617] VALUES (2033,'<span data-type="title" id=toc-2018>٤٧ </span>- {نَحْنُ أَعْلَمُ بِمَا يَسْتَمِعُونَ بِهِ إِذْ يَسْتَمِعُونَ إِلَيْكَ وَإِذْ هُمْ نَجْوَى إِذْ يَقُولُ الظَّالِمُونَ إِنْ تَتَّبِعُونَ إِلا رَجُلا مَسْحُورًا}
INSERT INTO [b9617] VALUES (2034,'<span data-type="title" id=toc-2019>٤٨ </span>- {انْظُرْ كَيْفَ ضَرَبُوا لَكَ الأَمْثَالَ}
INSERT INTO [b9617] VALUES (2035,'<span data-type="title" id=toc-2020>٤٩ </span>- {وَقَالُوا أَئِذَا كُنَّا عِظَامًا وَرُفَاتًا أَئِنَّا لَمَبْعُوثُونَ خَلْقًا جَدِيدًا}
INSERT INTO [b9617] VALUES (2036,'<span data-type="title" id=toc-2021>٥١ </span>- {أَوْ خَلْقًا مِمَّا يَكْبُرُ فِي صُدُورِكُمْ فَسَيَقُولُونَ مَنْ يُعِيدُنَا قُلِ الَّذِي فَطَرَكُمْ أَوَّلَ مَرَّةٍ فَسَيُنْغِضُونَ إِلَيْكَ رُءُوسَهُمْ وَيَقُولُونَ مَتَى هُوَ قُلْ عَسَى أَنْ يَكُونَ قَرِيبًا}
INSERT INTO [b9617] VALUES (2037,'<span data-type="title" id=toc-2022>٥٢ </span>- {يَوْمَ يَدْعُوكُمْ فَتَسْتَجِيبُونَ بِحَمْدِهِ وَتَظُنُّونَ إِنْ لَبِثْتُمْ إِلا قَلِيلا}
INSERT INTO [b9617] VALUES (2038,'<span data-type="title" id=toc-2023>٥٣ </span>- {وَقُلْ لِعِبَادِي يَقُولُوا الَّتِي هِيَ أَحْسَنُ إِنَّ الشَّيْطَانَ يَنْزَغُ بَيْنَهُمْ إِنَّ الشَّيْطَانَ كَانَ لِلإِنْسَانِ عَدُوًّا مُبِينًا}
INSERT INTO [b9617] VALUES (2039,'<span data-type="title" id=toc-2024>٥٤ </span>- {رَبُّكُمْ أَعْلَمُ بِكُمْ إِنْ يَشَأْ يَرْحَمْكُمْ أَوْ إِنْ يَشَأْ يُعَذِّبْكُمْ وَمَا أَرْسَلْنَاكَ عَلَيْهِمْ وَكِيلا}
INSERT INTO [b9617] VALUES (2040,'<span data-type="title" id=toc-2025>٥٥ </span>- {وَرَبُّكَ أَعْلَمُ بِمَنْ فِي السَّمَاوَاتِ وَالأَرْضِ وَلَقَدْ فَضَّلْنَا بَعْضَ النَّبِيِّينَ عَلَى بَعْضٍ} ⦗٦١٩⦘
INSERT INTO [b9617] VALUES (2041,'<span data-type="title" id=toc-2026>٥٦ </span>- {قُلِ ادْعُوا الَّذِينَ زَعَمْتُمْ مِنْ دُونِهِ فَلا يَمْلِكُونَ كَشْفَ الضُّرِّ عَنْكُمْ وَلا تَحْوِيلا}
INSERT INTO [b9617] VALUES (2042,'<span data-type="title" id=toc-2027>٥٧ </span>- {أُولَئِكَ الَّذِينَ يَدْعُونَ يَبْتَغُونَ إِلَى رَبِّهِمُ الْوَسِيلَةَ أَيُّهُمْ أَقْرَبُ وَيَرْجُونَ رَحْمَتَهُ وَيَخَافُونَ عَذَابَهُ إِنَّ عَذَابَ رَبِّكَ كَانَ مَحْذُورًا}
INSERT INTO [b9617] VALUES (2043,'<span data-type="title" id=toc-2028>٥٨ </span>- {وَإِنْ مِنْ قَرْيَةٍ إِلا نَحْنُ مُهْلِكُوهَا قَبْلَ يَوْمِ الْقِيَامَةِ أَوْ مُعَذِّبُوهَا عَذَابًا شَدِيدًا كَانَ ذَلِكَ فِي الْكِتَابِ مَسْطُورًا}
INSERT INTO [b9617] VALUES (2044,'<span data-type="title" id=toc-2029>٥٩ </span>- {وَمَا مَنَعَنَا أَنْ نُرْسِلَ بِالآيَاتِ إِلا أَنْ كَذَّبَ بِهَا الأَوَّلُونَ وَآتَيْنَا ثَمُودَ النَّاقَةَ مُبْصِرَةً فَظَلَمُوا بِهَا وَمَا نُرْسِلُ بِالآيَاتِ إِلا تَخْوِيفًا}
INSERT INTO [b9617] VALUES (2045,'<span data-type="title" id=toc-2030>٦٠ </span>- {وَإِذْ قُلْنَا لَكَ إِنَّ رَبَّكَ أَحَاطَ بِالنَّاسِ وَمَا جَعَلْنَا الرُّؤْيَا الَّتِي أَرَيْنَاكَ إِلا فِتْنَةً لِلنَّاسِ وَالشَّجَرَةَ الْمَلْعُونَةَ فِي الْقُرْآنِ وَنُخَوِّفُهُمْ فَمَا يَزِيدُهُمْ إِلا طُغْيَانًا كَبِيرًا}
INSERT INTO [b9617] VALUES (2046,'<span data-type="title" id=toc-2031>٦١ </span>- {وَإِذْ قُلْنَا لِلْمَلائِكَةِ اسْجُدُوا لآدَمَ فَسَجَدُوا إِلا إِبْلِيسَ قَالَ أَأَسْجُدُ لِمَنْ خَلَقْتَ طِينًا}
INSERT INTO [b9617] VALUES (2047,'<span data-type="title" id=toc-2032>٦٢ </span>- {قَالَ أَرَأَيْتَكَ هَذَا الَّذِي كَرَّمْتَ عَلَيَّ لَئِنْ أَخَّرْتَنِ إِلَى يَوْمِ الْقِيَامَةِ لأَحْتَنِكَنَّ ذُرِّيَّتَهُ إِلا قَلِيلا}
INSERT INTO [b9617] VALUES (2048,'<span data-type="title" id=toc-2033>٦٣ </span>- {قَالَ اذْهَبْ فَمَنْ تَبِعَكَ مِنْهُمْ فَإِنَّ جَهَنَّمَ جَزَاؤُكُمْ جَزَاءً مَوْفُورًا}
INSERT INTO [b9617] VALUES (2049,'<span data-type="title" id=toc-2034>٦٤ </span>- {وَاسْتَفْزِزْ مَنِ اسْتَطَعْتَ مِنْهُمْ بِصَوْتِكَ وَأَجْلِبْ عَلَيْهِمْ بِخَيْلِكَ وَرَجِلِكَ ⦗٦٢٢⦘ وَشَارِكْهُمْ فِي الأَمْوَالِ وَالأَوْلادِ وَعِدْهُمْ وَمَا يَعِدُهُمُ الشَّيْطَانُ إِلا غُرُورًا}
INSERT INTO [b9617] VALUES (2050,'<span data-type="title" id=toc-2035>٦٥ </span>- {إِنَّ عِبَادِي لَيْسَ لَكَ عَلَيْهِمْ سُلْطَانٌ وَكَفَى بِرَبِّكَ وَكِيلا}
INSERT INTO [b9617] VALUES (2051,'<span data-type="title" id=toc-2036>٦٦ </span>- {رَبُّكُمُ الَّذِي يُزْجِي لَكُمُ الْفُلْكَ فِي الْبَحْرِ لِتَبْتَغُوا مِنْ فَضْلِهِ إِنَّهُ كَانَ بِكُمْ رَحِيمًا}
INSERT INTO [b9617] VALUES (2052,'<span data-type="title" id=toc-2037>٦٧ </span>- {وَإِذَا مَسَّكُمُ الضُّرُّ فِي الْبَحْرِ ضَلَّ مَنْ تَدْعُونَ إِلا إِيَّاهُ فَلَمَّا نَجَّاكُمْ إِلَى الْبَرِّ أَعْرَضْتُمْ وَكَانَ الإِنْسَانُ كَفُورًا}
INSERT INTO [b9617] VALUES (2053,'<span data-type="title" id=toc-2038>٦٨ </span>- {أَفَأَمِنْتُمْ أَنْ يَخْسِفَ بِكُمْ جَانِبَ الْبَرِّ أَوْ يُرْسِلَ عَلَيْكُمْ حَاصِبًا ثُمَّ لا تَجِدُوا لَكُمْ وَكِيلا}
INSERT INTO [b9617] VALUES (2054,'<span data-type="title" id=toc-2039>٦٩ </span>- {أَمْ أَمِنْتُمْ أَنْ يُعِيدَكُمْ فِيهِ تَارَةً أُخْرَى فَيُرْسِلَ عَلَيْكُمْ قَاصِفًا مِنَ الرِّيحِ فَيُغْرِقَكُمْ بِمَا كَفَرْتُمْ ثُمَّ لا تَجِدُوا لَكُمْ عَلَيْنَا بِهِ تَبِيعًا}
INSERT INTO [b9617] VALUES (2055,'<span data-type="title" id=toc-2040>٧٠ </span>- {وَلَقَدْ كَرَّمْنَا بَنِي آدَمَ وَحَمَلْنَاهُمْ فِي الْبَرِّ وَالْبَحْرِ وَرَزَقْنَاهُمْ مِنَ الطَّيِّبَاتِ وَفَضَّلْنَاهُمْ عَلَى كَثِيرٍ مِمَّنْ خَلَقْنَا تَفْضِيلا}
INSERT INTO [b9617] VALUES (2056,'<span data-type="title" id=toc-2041>٧١ </span>- {يَوْمَ نَدْعُوا كُلَّ أُنَاسٍ بِإِمَامِهِمْ فَمَنْ أُوتِيَ كِتَابَهُ بِيَمِينِهِ فَأُولَئِكَ يَقْرَءُونَ كِتَابَهُمْ وَلا يُظْلَمُونَ فَتِيلا}
INSERT INTO [b9617] VALUES (2057,'<span data-type="title" id=toc-2042>٧٢ </span>- {وَمَنْ كَانَ فِي هَذِهِ أَعْمَى فَهُوَ فِي الآخِرَةِ أَعْمَى وَأَضَلُّ سَبِيلا}
INSERT INTO [b9617] VALUES (2058,'<span data-type="title" id=toc-2043>٧٣ </span>- {وَإِنْ كَادُوا لَيَفْتِنُونَكَ عَنِ الَّذِي أَوْحَيْنَا إِلَيْكَ لِتَفْتَرِيَ عَلَيْنَا غَيْرَهُ وَإِذًا لاتَّخَذُوكَ خَلِيلا}
INSERT INTO [b9617] VALUES (2059,'<span data-type="title" id=toc-2044>٧٤ </span>- {وَلَوْلا أَنْ ثَبَّتْنَاكَ لَقَدْ كِدْتَ تَرْكَنُ إِلَيْهِمْ شَيْئًا قَلِيلا} ⦗٦٢٥⦘
INSERT INTO [b9617] VALUES (2060,'<span data-type="title" id=toc-2045>٧٥ </span>- {إِذًا لأَذَقْنَاكَ ضِعْفَ الْحَيَاةِ وَضِعْفَ الْمَمَاتِ ثُمَّ لا تَجِدُ لَكَ عَلَيْنَا نَصِيرًا}
INSERT INTO [b9617] VALUES (2061,'<span data-type="title" id=toc-2046>٧٦ </span>- {وَإِنْ كَادُوا لَيَسْتَفِزُّونَكَ مِنَ الأَرْضِ لِيُخْرِجُوكَ مِنْهَا وَإِذًا لا يَلْبَثُونَ خِلافَكَ إِلا قَلِيلا}
INSERT INTO [b9617] VALUES (2062,'<span data-type="title" id=toc-2047>٧٧ </span>- {سُنَّةَ مَنْ قَدْ أَرْسَلْنَا قَبْلَكَ مِنْ رُسُلِنَا وَلا تَجِدُ لِسُنَّتِنَا تَحْوِيلا}
INSERT INTO [b9617] VALUES (2063,'<span data-type="title" id=toc-2048>٧٨ </span>- {أَقِمِ الصَّلاةَ لِدُلُوكِ الشَّمْسِ إِلَى غَسَقِ اللَّيْلِ وَقُرْآنَ الْفَجْرِ}
INSERT INTO [b9617] VALUES (2064,'<span data-type="title" id=toc-2049>٧٩ </span>- {وَمِنَ اللَّيْلِ فَتَهَجَّدْ بِهِ نَافِلَةً لَكَ عَسَى أَنْ يَبْعَثَكَ رَبُّكَ مَقَامًا مَحْمُودًا}
INSERT INTO [b9617] VALUES (2065,'<span data-type="title" id=toc-2050>٨٠ </span>- {وَقُلْ رَبِّ أَدْخِلْنِي مُدْخَلَ صِدْقٍ وَأَخْرِجْنِي مُخْرَجَ صِدْقٍ وَاجْعَلْ لِي مِنْ لَدُنْكَ سُلْطَانًا نَصِيرًا}
INSERT INTO [b9617] VALUES (2066,'<span data-type="title" id=toc-2051>٨١ </span>- {إِنَّ الْبَاطِلَ كَانَ زَهُوقًا}
INSERT INTO [b9617] VALUES (2067,'<span data-type="title" id=toc-2052>٨٢ </span>- {وَنُنَزِّلُ مِنَ الْقُرْآنِ مَا هُوَ شِفَاءٌ وَرَحْمَةٌ لِلْمُؤْمِنِينَ وَلا يَزِيدُ الظَّالِمِينَ إِلا خَسَارًا}
INSERT INTO [b9617] VALUES (2068,'<span data-type="title" id=toc-2053>٨٣ </span>- {وَإِذَا أَنْعَمْنَا عَلَى الإِنْسَانِ أَعْرَضَ}
INSERT INTO [b9617] VALUES (2069,'<span data-type="title" id=toc-2054>٨٤ </span>- {قُلْ كُلٌّ يَعْمَلُ عَلَى شَاكِلَتِهِ فَرَبُّكُمْ أَعْلَمُ بِمَنْ هُوَ أَهْدَى سَبِيلا}
INSERT INTO [b9617] VALUES (2070,'<span data-type="title" id=toc-2055>٨٥ </span>- {وَيَسْأَلُونَكَ عَنِ الرُّوحِ قُلِ الرُّوحُ مِنْ أَمْرِ رَبِّي وَمَا أُوتِيتُمْ مِنَ الْعِلْمِ إِلا قَلِيلا}
INSERT INTO [b9617] VALUES (2071,'<span data-type="title" id=toc-2056>٨٦ </span>- {وَلَئِنْ شِئْنَا لَنَذْهَبَنَّ بِالَّذِي أَوْحَيْنَا إِلَيْكَ ثُمَّ لا تَجِدُ لَكَ بِهِ عَلَيْنَا وَكِيلا}
INSERT INTO [b9617] VALUES (2072,'<span data-type="title" id=toc-2057>٨٧ </span>- {إِلا رَحْمَةً مِنْ رَبِّكَ إِنَّ فَضْلَهُ كَانَ عَلَيْكَ كَبِيرًا}
INSERT INTO [b9617] VALUES (2073,'<span data-type="title" id=toc-2058>٨٨ </span>- {قُلْ لَئِنِ اجْتَمَعَتِ الإِنْسُ وَالْجِنُّ عَلَى أَنْ يَأْتُوا بِمِثْلِ هَذَا الْقُرْآنِ لا يَأْتُونَ بِمِثْلِهِ وَلَوْ كَانَ بَعْضُهُمْ لِبَعْضٍ ظَهِيرًا}
INSERT INTO [b9617] VALUES (2074,'<span data-type="title" id=toc-2059>٨٩ </span>- {وَلَقَدْ صَرَّفْنَا لِلنَّاسِ فِي هَذَا الْقُرْآنِ مِنْ كُلِّ مَثَلٍ فَأَبَى أَكْثَرُ النَّاسِ إِلا كُفُورًا} ⦗٦٢٩⦘
INSERT INTO [b9617] VALUES (2075,'<span data-type="title" id=toc-2060>٩٠ </span>- {حَتَّى تَفْجُرَ لَنَا مِنَ الأَرْضِ يَنْبُوعًا}
INSERT INTO [b9617] VALUES (2076,'<span data-type="title" id=toc-2061>٩١ </span>- {أَوْ تَكُونَ لَكَ جَنَّةٌ مِنْ نَخِيلٍ وَعِنَبٍ}
INSERT INTO [b9617] VALUES (2077,'<span data-type="title" id=toc-2062>٩٢ </span>- {أَوْ تُسْقِطَ السَّمَاءَ كَمَا زَعَمْتَ عَلَيْنَا كِسَفًا أَوْ تَأْتِيَ بِاللَّهِ وَالْمَلائِكَةِ قَبِيلا}
INSERT INTO [b9617] VALUES (2078,'<span data-type="title" id=toc-2063>٩٣ </span>- {أَوْ يَكُونَ لَكَ بَيْتٌ مِنْ زُخْرُفٍ أَوْ تَرْقَى فِي السَّمَاءِ وَلَنْ نُؤْمِنَ لِرُقِيِّكَ حَتَّى تُنَزِّلَ عَلَيْنَا كِتَابًا نَقْرَؤُهُ قُلْ سُبْحَانَ رَبِّي هَلْ كُنْتُ إِلا بَشَرًا رَسُولا}
INSERT INTO [b9617] VALUES (2079,'<span data-type="title" id=toc-2064>٩٤ </span>- {وَمَا مَنَعَ النَّاسَ أَنْ يُؤْمِنُوا إِذْ جَاءَهُمُ الْهُدَى إِلا أَنْ قَالُوا أَبَعَثَ اللَّهُ بَشَرًا رَسُولا}
INSERT INTO [b9617] VALUES (2080,'<span data-type="title" id=toc-2065>٩٥ </span>- {قُلْ لَوْ كَانَ فِي الأَرْضِ مَلائِكَةٌ يَمْشُونَ مُطْمَئِنِّينَ لَنَزَّلْنَا عَلَيْهِمْ مِنَ السَّمَاءِ مَلَكًا رَسُولا}
INSERT INTO [b9617] VALUES (2081,'<span data-type="title" id=toc-2066>٩٦ </span>- {قُلْ كَفَى بِاللَّهِ شَهِيدًا بَيْنِي وَبَيْنَكُمْ إِنَّهُ كَانَ بِعِبَادِهِ خَبِيرًا بَصِيرًا}
INSERT INTO [b9617] VALUES (2082,'<span data-type="title" id=toc-2067>٩٧ </span>- {وَمَنْ يَهْدِ اللَّهُ فَهُوَ الْمُهْتَدِ وَمَنْ يُضْلِلْ فَلَنْ تَجِدَ لَهُمْ أَوْلِيَاءَ مِنْ دُونِهِ وَنَحْشُرُهُمْ يَوْمَ الْقِيَامَةِ عَلَى وُجُوهِهِمْ عُمْيًا وَبُكْمًا وَصُمًّا مَأْوَاهُمْ جَهَنَّمُ كُلَّمَا خَبَتْ زِدْنَاهُمْ سَعِيرًا}
INSERT INTO [b9617] VALUES (2083,'<span data-type="title" id=toc-2068>٩٨ </span>- {ذَلِكَ جَزَاؤُهُمْ بِأَنَّهُمْ كَفَرُوا بِآيَاتِنَا وَقَالُوا أَئِذَا كُنَّا عِظَامًا وَرُفَاتًا أَئِنَّا لَمَبْعُوثُونَ خَلْقًا جَدِيدًا}
INSERT INTO [b9617] VALUES (2084,'<span data-type="title" id=toc-2069>٩٩ </span>- {أَوَلَمْ يَرَوْا أَنَّ اللَّهَ الَّذِي خَلَقَ السَّمَاوَاتِ وَالأَرْضَ قَادِرٌ عَلَى أَنْ يَخْلُقَ مِثْلَهُمْ وَجَعَلَ لَهُمْ أَجَلا لا رَيْبَ فِيهِ فَأَبَى الظَّالِمُونَ إِلا كُفُورًا}
INSERT INTO [b9617] VALUES (2085,'<span data-type="title" id=toc-2070>١٠٠ </span>- {قُلْ لَوْ أَنْتُمْ تَمْلِكُونَ خَزَائِنَ رَحْمَةِ رَبِّي إِذًا لأَمْسَكْتُمْ خَشْيَةَ الإِنْفَاقِ وَكَانَ الإِنْسَانُ قَتُورًا}
INSERT INTO [b9617] VALUES (2086,'<span data-type="title" id=toc-2071>١٠١ </span>- {وَلَقَدْ آتَيْنَا مُوسَى تِسْعَ آيَاتٍ بَيِّنَاتٍ فَاسْأَلْ بَنِي إِسْرَائِيلَ إِذْ جَاءَهُمْ فَقَالَ لَهُ فِرْعَوْنُ إِنِّي لأَظُنُّكَ يَا مُوسَى مَسْحُورًا}
INSERT INTO [b9617] VALUES (2087,'<span data-type="title" id=toc-2072>١٠٢ </span>- {قَالَ لَقَدْ عَلِمْتَ مَا أَنْزَلَ هَؤُلاءِ إِلا رَبُّ السَّمَاوَاتِ وَالأَرْضِ بَصَائِرَ وَإِنِّي لأَظُنُّكَ يَا فِرْعَوْنُ مَثْبُورًا}
INSERT INTO [b9617] VALUES (2088,'<span data-type="title" id=toc-2073>١٠٣ </span>- {فَأَرَادَ أَنْ يَسْتَفِزَّهُمْ مِنَ الأَرْضِ فَأَغْرَقْنَاهُ وَمَنْ مَعَهُ جَمِيعًا}
INSERT INTO [b9617] VALUES (2089,'<span data-type="title" id=toc-2074>١٠٤ </span>- {وَقُلْنَا مِنْ بَعْدِهِ لِبَنِي إِسْرَائِيلَ اسْكُنُوا الأَرْضَ فَإِذَا جَاءَ وَعْدُ الآخِرَةِ جِئْنَا بِكُمْ لَفِيفًا}
INSERT INTO [b9617] VALUES (2090,'<span data-type="title" id=toc-2075>١٠٥ </span>- {وَبِالْحَقِّ أَنْزَلْنَاهُ وَبِالْحَقِّ نَزَلَ وَمَا أَرْسَلْنَاكَ إِلا مُبَشِّرًا وَنَذِيرًا}
INSERT INTO [b9617] VALUES (2091,'<span data-type="title" id=toc-2076>١٠٦ </span>- {وَقُرْآنًا فَرَقْنَاهُ لِتَقْرَأَهُ عَلَى النَّاسِ عَلَى مُكْثٍ}
INSERT INTO [b9617] VALUES (2092,'<span data-type="title" id=toc-2077>١٠٧ </span>- {إِنَّ الَّذِينَ أُوتُوا الْعِلْمَ مِنْ قَبْلِهِ إِذَا يُتْلَى عَلَيْهِمْ يَخِرُّونَ لِلأَذْقَانِ سُجَّدًا}
INSERT INTO [b9617] VALUES (2093,'<span data-type="title" id=toc-2078>١٠٨ </span>- {وَيَقُولُونَ سُبْحَانَ رَبِّنَا إِنْ كَانَ وَعْدُ رَبِّنَا لَمَفْعُولا}
INSERT INTO [b9617] VALUES (2094,'<span data-type="title" id=toc-2079>١٠٩ </span>- {وَيَخِرُّونَ لِلأَذْقَانِ يَبْكُونَ وَيَزِيدُهُمْ خُشُوعًا}
INSERT INTO [b9617] VALUES (2095,'<span data-type="title" id=toc-2080>١١٠ </span>- {قُلِ ادْعُوا اللَّهَ أَوِ ادْعُوا الرَّحْمَنَ أَيًّا مَا تَدْعُوا فَلَهُ الأَسْمَاءُ الْحُسْنَى}
INSERT INTO [b9617] VALUES (2096,'<span data-type="title" id=toc-2081>١١١ </span>- {وَقُلِ الْحَمْدُ لِلَّهِ الَّذِي لَمْ يَتَّخِذْ وَلَدًا وَلَمْ يَكُنْ لَهُ شَرِيكٌ فِي الْمُلْكِ وَلَمْ يَكُنْ لَهُ وَلِيٌّ مِنَ الذُّلِّ}
INSERT INTO [b9617] VALUES (2097,'<span data-type="title" id=toc-2082>سورة الكهف</span>','2','635',NULL,NULL,'0');
INSERT INTO [b9617] VALUES (2098,'<span data-type="title" id=toc-2083>١ </span>- {الْحَمْدُ لِلَّهِ الَّذِي أَنْزَلَ عَلَى عَبْدِهِ الْكِتَابَ وَلَمْ يَجْعَلْ لَهُ عِوَجًا}
INSERT INTO [b9617] VALUES (2099,'<span data-type="title" id=toc-2084>٢ </span>- {قَيِّمًا لِيُنْذِرَ بَأْسًا شَدِيدًا مِنْ لَدُنْهُ وَيُبَشِّرَ الْمُؤْمِنِينَ الَّذِينَ يَعْمَلُونَ الصَّالِحَاتِ أَنَّ لَهُمْ أَجْرًا حَسَنًا}
INSERT INTO [b9617] VALUES (2100,'<span data-type="title" id=toc-2085>٣ </span>- {مَاكِثِينَ فِيهِ أَبَدًا}
INSERT INTO [b9617] VALUES (2101,'<span data-type="title" id=toc-2086>٤ </span>- {وَيُنْذِرَ الَّذِينَ قَالُوا اتَّخَذَ اللَّهُ وَلَدًا}
INSERT INTO [b9617] VALUES (2102,'<span data-type="title" id=toc-2087>٥ </span>- {مَا لَهُمْ بِهِ مِنْ عِلْمٍ وَلا لآبَائِهِمْ كَبُرَتْ كَلِمَةً تَخْرُجُ مِنْ أَفْوَاهِهِمْ إِنْ يَقُولُونَ إِلا كَذِبًا}
INSERT INTO [b9617] VALUES (2103,'<span data-type="title" id=toc-2088>٦ </span>- {فَلَعَلَّكَ بَاخِعٌ نَفْسَكَ عَلَى آثَارِهِمْ إِنْ لَمْ يُؤْمِنُوا بِهَذَا الْحَدِيثِ أَسَفًا}
INSERT INTO [b9617] VALUES (2104,'<span data-type="title" id=toc-2089>٧ </span>- {إِنَّا جَعَلْنَا مَا عَلَى الأَرْضِ زِينَةً لَهَا لِنَبْلُوَهُمْ أَيُّهُمْ أَحْسَنُ عَمَلا}
INSERT INTO [b9617] VALUES (2105,'<span data-type="title" id=toc-2090>٨ </span>- {وَإِنَّا لَجَاعِلُونَ مَا عَلَيْهَا صَعِيدًا جُرُزًا}
INSERT INTO [b9617] VALUES (2106,'<span data-type="title" id=toc-2091>٩ </span>- {أَمْ حَسِبْتَ أَنَّ أَصْحَابَ الْكَهْفِ وَالرَّقِيمِ كَانُوا مِنْ آيَاتِنَا عَجَبًا}
INSERT INTO [b9617] VALUES (2107,'<span data-type="title" id=toc-2092>١٠ </span>- {إِذْ أَوَى الْفِتْيَةُ إِلَى الْكَهْفِ فَقَالُوا رَبَّنَا آتِنَا مِنْ لَدُنْكَ رَحْمَةً وَهَيِّئْ لَنَا مِنْ أَمْرِنَا رَشَدًا}
INSERT INTO [b9617] VALUES (2108,'<span data-type="title" id=toc-2093>١١ </span>- {فَضَرَبْنَا عَلَى آذَانِهِمْ فِي الْكَهْفِ سِنِينَ عَدَدًا}
INSERT INTO [b9617] VALUES (2109,'<span data-type="title" id=toc-2094>١٢ </span>- {ثُمَّ بَعَثْنَاهُمْ لِنَعْلَمَ أَيُّ الْحِزْبَيْنِ أَحْصَى لِمَا لَبِثُوا أَمَدًا}
INSERT INTO [b9617] VALUES (2110,'<span data-type="title" id=toc-2095>١٣ </span>- {نَحْنُ نَقُصُّ عَلَيْكَ نَبَأَهُمْ بِالْحَقِّ إِنَّهُمْ فِتْيَةٌ آمَنُوا بِرَبِّهِمْ وَزِدْنَاهُمْ هُدًى}
INSERT INTO [b9617] VALUES (2111,'<span data-type="title" id=toc-2096>١٤ </span>- {وَرَبَطْنَا عَلَى قُلُوبِهِمْ إِذْ قَامُوا فَقَالُوا رَبُّنَا رَبُّ السَّمَاوَاتِ وَالأَرْضِ لَنْ نَدْعُوَ مِنْ دُونِهِ إِلَهًا لَقَدْ قُلْنَا إِذًا شَطَطًا}
INSERT INTO [b9617] VALUES (2112,'<span data-type="title" id=toc-2097>١٥ </span>- {هَؤُلاءِ قَوْمُنَا اتَّخَذُوا مِنْ دُونِهِ آلِهَةً لَوْلا يَأْتُونَ عَلَيْهِمْ بِسُلْطَانٍ بَيِّنٍ فَمَنْ أَظْلَمُ مِمَّنِ افْتَرَى عَلَى اللَّهِ كَذِبًا}
INSERT INTO [b9617] VALUES (2113,'<span data-type="title" id=toc-2098>١٦ </span>- {وَإِذِ اعْتَزَلْتُمُوهُمْ وَمَا يَعْبُدُونَ إِلا اللَّهَ فَأْوُوا إِلَى الْكَهْفِ يَنْشُرْ لَكُمْ رَبُّكُمْ مِنْ رَحْمَتِهِ وَيُهَيِّئْ لَكُمْ مِنْ أَمْرِكُمْ مِرْفَقًا}
INSERT INTO [b9617] VALUES (2114,'<span data-type="title" id=toc-2099>١٧ </span>- {وَتَرَى الشَّمْسَ إِذَا طَلَعَتْ تَزَاوَرُ عَنْ كَهْفِهِمْ ذَاتَ الْيَمِينِ وَإِذَا غَرَبَتْ تَقْرِضُهُمْ ذَاتَ الشِّمَالِ وَهُمْ فِي فَجْوَةٍ مِنْهُ ذَلِكَ مِنْ آيَاتِ اللَّهِ مَنْ يَهْدِ اللَّهُ فَهُوَ الْمُهْتَدِ وَمَنْ يُضْلِلْ فَلَنْ تَجِدَ لَهُ وَلِيًّا مُرْشِدًا}
INSERT INTO [b9617] VALUES (2115,'<span data-type="title" id=toc-2100>١٨ </span>- {وَتَحْسَبُهُمْ أَيْقَاظًا وَهُمْ رُقُودٌ وَنُقَلِّبُهُمْ ذَاتَ الْيَمِينِ وَذَاتَ الشِّمَالِ وَكَلْبُهُمْ بَاسِطٌ ذِرَاعَيْهِ بِالْوَصِيدِ لَوِ اطَّلَعْتَ عَلَيْهِمْ لَوَلَّيْتَ مِنْهُمْ فِرَارًا وَلَمُلِئْتَ مِنْهُمْ رُعْبًا}
INSERT INTO [b9617] VALUES (2116,'<span data-type="title" id=toc-2101>١٩ </span>- {وَكَذَلِكَ بَعَثْنَاهُمْ لِيَتَسَاءَلُوا بَيْنَهُمْ قَالَ قَائِلٌ مِنْهُمْ كَمْ لَبِثْتُمْ قَالُوا لَبِثْنَا يَوْمًا أَوْ بَعْضَ يَوْمٍ قَالُوا رَبُّكُمْ أَعْلَمُ بِمَا لَبِثْتُمْ فَابْعَثُوا أَحَدَكُمْ بِوَرِقِكُمْ هَذِهِ إِلَى الْمَدِينَةِ فَلْيَنْظُرْ أَيُّهَا أَزْكَى طَعَامًا فَلْيَأْتِكُمْ بِرِزْقٍ مِنْهُ}
INSERT INTO [b9617] VALUES (2117,'<span data-type="title" id=toc-2102>٢٠ </span>- {إِنَّهُمْ إِنْ يَظْهَرُوا عَلَيْكُمْ يَرْجُمُوكُمْ أَوْ يُعِيدُوكُمْ فِي مِلَّتِهِمْ وَلَنْ تُفْلِحُوا إِذًا أَبَدًا} ⦗٦٤١⦘
INSERT INTO [b9617] VALUES (2118,'<span data-type="title" id=toc-2103>٢١ </span>- {وَكَذَلِكَ أَعْثَرْنَا عَلَيْهِمْ لِيَعْلَمُوا أَنَّ وَعْدَ اللَّهِ حَقٌّ وَأَنَّ السَّاعَةَ لا رَيْبَ فِيهَا إِذْ يَتَنَازَعُونَ بَيْنَهُمْ أَمْرَهُمْ فَقَالُوا ابْنُوا عَلَيْهِمْ بُنْيَانًا رَبُّهُمْ أَعْلَمُ بِهِمْ قَالَ الَّذِينَ غَلَبُوا عَلَى أَمْرِهِمْ لَنَتَّخِذَنَّ عَلَيْهِمْ مَسْجِدًا}
INSERT INTO [b9617] VALUES (2119,'<span data-type="title" id=toc-2104>٢٢ </span>- {سَيَقُولُونَ ثَلاثَةٌ رَابِعُهُمْ كَلْبُهُمْ وَيَقُولُونَ خَمْسَةٌ سَادِسُهُمْ كَلْبُهُمْ رَجْمًا بِالْغَيْبِ وَيَقُولُونَ سَبْعَةٌ وَثَامِنُهُمْ كَلْبُهُمْ قُلْ رَبِّي أَعْلَمُ بِعِدَّتِهِمْ مَا يَعْلَمُهُمْ إِلا قَلِيلٌ فَلا تُمَارِ فِيهِمْ إِلا مِرَاءً ظَاهِرًا وَلا تَسْتَفْتِ فِيهِمْ مِنْهُمْ أَحَدًا}
INSERT INTO [b9617] VALUES (2120,'<span data-type="title" id=toc-2105>٢٣ </span>- {وَلا تَقُولَنَّ لِشَيْءٍ إِنِّي فَاعِلٌ ذَلِكَ غَدًا}
INSERT INTO [b9617] VALUES (2121,'<span data-type="title" id=toc-2106>٢٤ </span>- {إِلا أَنْ يَشَاءَ اللَّهُ وَاذْكُرْ رَبَّكَ إِذَا نَسِيتَ وَقُلْ عَسَى أَنْ يَهْدِيَنِ رَبِّي لأَقْرَبَ مِنْ هَذَا رَشَدًا}
INSERT INTO [b9617] VALUES (2122,'<span data-type="title" id=toc-2107>٢٥ </span>- {وَلَبِثُوا فِي كَهْفِهِمْ ثَلاثَ مِائَةٍ سِنِينَ وَازْدَادُوا تِسْعًا}
INSERT INTO [b9617] VALUES (2123,'<span data-type="title" id=toc-2108>٢٦ </span>- {قُلِ اللَّهُ أَعْلَمُ بِمَا لَبِثُوا لَهُ غَيْبُ السَّمَاوَاتِ وَالأَرْضِ أَبْصِرْ بِهِ وَأَسْمِعْ مَا لَهُمْ ⦗٦٤٣⦘ مِنْ دُونِهِ مِنْ وَلِيٍّ وَلا يُشْرِكُ فِي حُكْمِهِ أَحَدًا}
INSERT INTO [b9617] VALUES (2124,'<span data-type="title" id=toc-2109>٢٧ </span>- {وَاتْلُ مَا أُوحِيَ إِلَيْكَ مِنْ كِتَابِ رَبِّكَ لا مُبَدِّلَ لِكَلِمَاتِهِ وَلَنْ تَجِدَ مِنْ دُونِهِ مُلْتَحَدًا}
INSERT INTO [b9617] VALUES (2125,'<span data-type="title" id=toc-2110>٢٨ </span>- {وَاصْبِرْ نَفْسَكَ مَعَ الَّذِينَ يَدْعُونَ رَبَّهُمْ بِالْغَدَاةِ وَالْعَشِيِّ يُرِيدُونَ وَجْهَهُ وَلا تَعْدُ عَيْنَاكَ عَنْهُمْ تُرِيدُ زِينَةَ الْحَيَاةِ الدُّنْيَا وَلا تُطِعْ مَنْ أَغْفَلْنَا قَلْبَهُ عَنْ ذِكْرِنَا وَاتَّبَعَ هَوَاهُ وَكَانَ أَمْرُهُ فُرُطًا}
INSERT INTO [b9617] VALUES (2126,'<span data-type="title" id=toc-2111>٢٩ </span>- {وَقُلِ الْحَقُّ مِنْ رَبِّكُمْ فَمَنْ شَاءَ فَلْيُؤْمِنْ وَمَنْ شَاءَ فَلْيَكْفُرْ إِنَّا أَعْتَدْنَا لِلظَّالِمِينَ نَارًا أَحَاطَ بِهِمْ سُرَادِقُهَا وَإِنْ يَسْتَغِيثُوا يُغَاثُوا بِمَاءٍ كَالْمُهْلِ يَشْوِي الْوُجُوهَ بِئْسَ الشَّرَابُ وَسَاءَتْ مُرْتَفَقًا}
INSERT INTO [b9617] VALUES (2127,'<span data-type="title" id=toc-2112>٣٠ </span>- {إِنَّ الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ إِنَّا لا نُضِيعُ أَجْرَ مَنْ أَحْسَنَ عَمَلا}
INSERT INTO [b9617] VALUES (2128,'<span data-type="title" id=toc-2113>٣١ </span>- {أُولَئِكَ لَهُمْ جَنَّاتُ عَدْنٍ تَجْرِي مِنْ تَحْتِهِمُ الأَنْهَارُ يُحَلَّوْنَ فِيهَا مِنْ أَسَاوِرَ مِنْ ذَهَبٍ وَيَلْبَسُونَ ثِيَابًا خُضْرًا مِنْ سُنْدُسٍ وَإِسْتَبْرَقٍ مُتَّكِئِينَ فِيهَا عَلَى الأَرَائِكِ نِعْمَ الثَّوَابُ وَحَسُنَتْ مُرْتَفَقًا}
INSERT INTO [b9617] VALUES (2129,'<span data-type="title" id=toc-2114>٣٢ </span>- {وَاضْرِبْ لَهُمْ مَثَلا رَجُلَيْنِ جَعَلْنَا لأَحَدِهِمَا جَنَّتَيْنِ مِنْ أَعْنَابٍ وَحَفَفْنَاهُمَا بِنَخْلٍ وَجَعَلْنَا بَيْنَهُمَا زَرْعًا}
INSERT INTO [b9617] VALUES (2130,'<span data-type="title" id=toc-2115>٣٣ </span>- {كِلْتَا الْجَنَّتَيْنِ آتَتْ أُكُلَهَا}
INSERT INTO [b9617] VALUES (2131,'<span data-type="title" id=toc-2116>٣٤ </span>- {وَكَانَ لَهُ ثَمَرٌ فَقَالَ لِصَاحِبِهِ وَهُوَ يُحَاوِرُهُ أَنَا أَكْثَرُ مِنْكَ مَالا وَأَعَزُّ نَفَرًا}
INSERT INTO [b9617] VALUES (2132,'<span data-type="title" id=toc-2117>٣٥ </span>- {وَدَخَلَ جَنَّتَهُ وَهُوَ ظَالِمٌ لِنَفْسِهِ قَالَ مَا أَظُنُّ أَنْ تَبِيدَ هَذِهِ أَبَدًا}
INSERT INTO [b9617] VALUES (2133,'<span data-type="title" id=toc-2118>٣٦ </span>- {وَمَا أَظُنُّ السَّاعَةَ قَائِمَةً وَلَئِنْ رُدِدْتُ إِلَى رَبِّي لأَجِدَنَّ خَيْرًا مِنْهَا مُنْقَلَبًا} ⦗٦٤٦⦘
INSERT INTO [b9617] VALUES (2134,'<span data-type="title" id=toc-2119>٣٧ </span>- {قَالَ لَهُ صَاحِبُهُ وَهُوَ يُحَاوِرُهُ أَكَفَرْتَ بِالَّذِي خَلَقَكَ مِنْ تُرَابٍ ثُمَّ مِنْ نُطْفَةٍ ثُمَّ سَوَّاكَ رَجُلا}
INSERT INTO [b9617] VALUES (2135,'<span data-type="title" id=toc-2120>٣٨ </span>- {لَكِنَّا هُوَ اللَّهُ رَبِّي وَلا أُشْرِكُ بِرَبِّي أَحَدًا}
INSERT INTO [b9617] VALUES (2136,'<span data-type="title" id=toc-2121>٣٩ </span>- {وَلَوْلا إِذْ دَخَلْتَ جَنَّتَكَ قُلْتَ مَا شَاءَ اللَّهُ لا قُوَّةَ إِلا بِاللَّهِ إِنْ تَرَنِ أَنَا أَقَلَّ مِنْكَ مَالا وَوَلَدًا}
INSERT INTO [b9617] VALUES (2137,'<span data-type="title" id=toc-2122>٤٠ </span>- {فَعَسَى رَبِّي أَنْ يُؤْتِيَنِ خَيْرًا مِنْ جَنَّتِكَ}
INSERT INTO [b9617] VALUES (2138,'<span data-type="title" id=toc-2123>٤١ </span>- {فَلَنْ تَسْتَطِيعَ لَهُ طَلَبًا}
INSERT INTO [b9617] VALUES (2139,'<span data-type="title" id=toc-2124>٤٢ </span>- {عَلَى مَا أَنْفَقَ فِيهَا وَهِيَ خَاوِيَةٌ عَلَى عُرُوشِهَا وَيَقُولُ يَا لَيْتَنِي لَمْ أُشْرِكْ بِرَبِّي أَحَدًا}
INSERT INTO [b9617] VALUES (2140,'<span data-type="title" id=toc-2125>٤٣ </span>- {وَلَمْ تَكُنْ لَهُ فِئَةٌ يَنْصُرُونَهُ مِنْ دُونِ اللَّهِ وَمَا كَانَ مُنْتَصِرًا}
INSERT INTO [b9617] VALUES (2141,'<span data-type="title" id=toc-2126>٤٤ </span>- {هُنَالِكَ الْوَلايَةُ لِلَّهِ الْحَقِّ هُوَ خَيْرٌ ثَوَابًا}
INSERT INTO [b9617] VALUES (2142,'<span data-type="title" id=toc-2127>٤٥ </span>- {وَاضْرِبْ لَهُمْ مَثَلَ الْحَيَاةِ الدُّنْيَا كَمَاءٍ أَنْزَلْنَاهُ مِنَ السَّمَاءِ فَاخْتَلَطَ بِهِ نَبَاتُ الأَرْضِ فَأَصْبَحَ هَشِيمًا تَذْرُوهُ الرِّيَاحُ}
INSERT INTO [b9617] VALUES (2143,'<span data-type="title" id=toc-2128>٤٦ </span>- {خَيْرٌ عِنْدَ رَبِّكَ ثَوَابًا وَخَيْرٌ أَمَلا}
INSERT INTO [b9617] VALUES (2144,'<span data-type="title" id=toc-2129>٤٧ </span>- {وَيَوْمَ نُسَيِّرُ الْجِبَالَ وَتَرَى الأَرْضَ بَارِزَةً وَحَشَرْنَاهُمْ فَلَمْ نُغَادِرْ مِنْهُمْ أَحَدًا}
INSERT INTO [b9617] VALUES (2145,'<span data-type="title" id=toc-2130>٤٨ </span>- {وَعُرِضُوا عَلَى رَبِّكَ صَفًّا لَقَدْ جِئْتُمُونَا كَمَا خَلَقْنَاكُمْ أَوَّلَ مَرَّةٍ بَلْ زَعَمْتُمْ أَلَّنْ نَجْعَلَ لَكُمْ مَوْعِدًا}
INSERT INTO [b9617] VALUES (2146,'<span data-type="title" id=toc-2131>٤٩ </span>- {وَوُضِعَ الْكِتَابُ فَتَرَى الْمُجْرِمِينَ مُشْفِقِينَ مِمَّا فِيهِ وَيَقُولُونَ يَا وَيْلَتَنَا مَالِ هَذَا الْكِتَابِ لا يُغَادِرُ صَغِيرَةً وَلا كَبِيرَةً إِلا أَحْصَاهَا وَوَجَدُوا مَا عَمِلُوا حَاضِرًا وَلا يَظْلِمُ رَبُّكَ أَحَدًا}
INSERT INTO [b9617] VALUES (2147,'<span data-type="title" id=toc-2132>٥٠ </span>- {وَإِذْ قُلْنَا لِلْمَلائِكَةِ اسْجُدُوا لآدَمَ فَسَجَدُوا إِلا إِبْلِيسَ كَانَ مِنَ الْجِنِّ فَفَسَقَ عَنْ أَمْرِ رَبِّهِ أَفَتَتَّخِذُونَهُ وَذُرِّيَّتَهُ أَوْلِيَاءَ مِنْ دُونِي وَهُمْ لَكُمْ عَدُوٌّ بِئْسَ لِلظَّالِمِينَ بَدَلا}
INSERT INTO [b9617] VALUES (2148,'<span data-type="title" id=toc-2133>٥١ </span>- {وَمَا كُنْتُ مُتَّخِذَ الْمُضِلِّينَ عَضُدًا}
INSERT INTO [b9617] VALUES (2149,'<span data-type="title" id=toc-2134>٥٢ </span>- {وَيَوْمَ يَقُولُ نَادُوا شُرَكَائِيَ الَّذِينَ زَعَمْتُمْ فَدَعَوْهُمْ فَلَمْ يَسْتَجِيبُوا لَهُمْ وَجَعَلْنَا بَيْنَهُمْ مَوْبِقًا}
INSERT INTO [b9617] VALUES (2150,'<span data-type="title" id=toc-2135>٥٣ </span>- {وَرَأَى الْمُجْرِمُونَ النَّارَ فَظَنُّوا أَنَّهُمْ مُوَاقِعُوهَا وَلَمْ يَجِدُوا عَنْهَا مَصْرِفًا} ⦗٦٥١⦘
INSERT INTO [b9617] VALUES (2151,'<span data-type="title" id=toc-2136>٥٤ </span>- {وَلَقَدْ صَرَّفْنَا فِي هَذَا الْقُرْآنِ لِلنَّاسِ مِنْ كُلِّ مَثَلٍ وَكَانَ الإِنْسَانُ أَكْثَرَ شَيْءٍ جَدَلا}
INSERT INTO [b9617] VALUES (2152,'<span data-type="title" id=toc-2137>٥٥ </span>- {وَمَا مَنَعَ النَّاسَ أَنْ يُؤْمِنُوا إِذْ جَاءَهُمُ الْهُدَى وَيَسْتَغْفِرُوا رَبَّهُمْ إِلا أَنْ تَأْتِيَهُمْ سُنَّةُ الأَوَّلِينَ أَوْ يَأْتِيَهُمُ الْعَذَابُ قُبُلا}
INSERT INTO [b9617] VALUES (2153,'<span data-type="title" id=toc-2138>٥٦ </span>- {وَمَا نُرْسِلُ الْمُرْسَلِينَ إِلا مُبَشِّرِينَ وَمُنْذِرِينَ وَيُجَادِلُ الَّذِينَ كَفَرُوا بِالْبَاطِلِ لِيُدْحِضُوا بِهِ الْحَقَّ وَاتَّخَذُوا آيَاتِي وَمَا أُنْذِرُوا هُزُوًا}
INSERT INTO [b9617] VALUES (2154,'<span data-type="title" id=toc-2139>٥٧ </span>- {وَمَنْ أَظْلَمُ مِمَّنْ ذُكِّرَ بِآيَاتِ رَبِّهِ فَأَعْرَضَ عَنْهَا وَنَسِيَ مَا قَدَّمَتْ يَدَاهُ إِنَّا جَعَلْنَا ⦗٦٥٢⦘ عَلَى قُلُوبِهِمْ أَكِنَّةً أَنْ يَفْقَهُوهُ وَفِي آذَانِهِمْ وَقْرًا وَإِنْ تَدْعُهُمْ إِلَى الْهُدَى فَلَنْ يَهْتَدُوا إِذًا أَبَدًا}
INSERT INTO [b9617] VALUES (2155,'<span data-type="title" id=toc-2140>٥٨ </span>- {وَرَبُّكَ الْغَفُورُ ذُو الرَّحْمَةِ لَوْ يُؤَاخِذُهُمْ بِمَا كَسَبُوا لَعَجَّلَ لَهُمُ الْعَذَابَ بَلْ لَهُمْ مَوْعِدٌ لَنْ يَجِدُوا مِنْ دُونِهِ مَوْئِلا}
INSERT INTO [b9617] VALUES (2156,'<span data-type="title" id=toc-2141>٥٩ </span>- {وَتِلْكَ الْقُرَى أَهْلَكْنَاهُمْ لَمَّا ظَلَمُوا وَجَعَلْنَا لِمَهْلِكِهِمْ مَوْعِدًا}
INSERT INTO [b9617] VALUES (2157,'<span data-type="title" id=toc-2142>٦٠ </span>- {وَإِذْ قَالَ مُوسَى لِفَتَاهُ لا أَبْرَحُ حَتَّى أَبْلُغَ مَجْمَعَ الْبَحْرَيْنِ أَوْ أَمْضِيَ حُقُبًا}
INSERT INTO [b9617] VALUES (2158,'<span data-type="title" id=toc-2143>٦١ </span>- {فَلَمَّا بَلَغَا مَجْمَعَ بَيْنِهِمَا نَسِيَا حُوتَهُمَا فَاتَّخَذَ سَبِيلَهُ فِي الْبَحْرِ سَرَبًا}
INSERT INTO [b9617] VALUES (2159,'<span data-type="title" id=toc-2144>٦٢ </span>- {لَقَدْ لَقِينَا مِنْ سَفَرِنَا هَذَا نَصَبًا}
INSERT INTO [b9617] VALUES (2160,'<span data-type="title" id=toc-2145>٦٣ </span>- {قَالَ أَرَأَيْتَ إِذْ أَوَيْنَا إِلَى الصَّخْرَةِ فَإِنِّي نَسِيتُ الْحُوتَ وَمَا أَنْسَانِيهُ إِلا الشَّيْطَانُ أَنْ أَذْكُرَهُ وَاتَّخَذَ سَبِيلَهُ فِي الْبَحْرِ عَجَبًا}
INSERT INTO [b9617] VALUES (2161,'<span data-type="title" id=toc-2146>٦٤ </span>- {قَالَ ذَلِكَ مَا كُنَّا نَبْغِ فَارْتَدَّا عَلَى آثَارِهِمَا قَصَصًا}
INSERT INTO [b9617] VALUES (2162,'<span data-type="title" id=toc-2147>٦٥ </span>- {فَوَجَدَا عَبْدًا مِنْ عِبَادِنَا آتَيْنَاهُ رَحْمَةً مِنْ عِنْدِنَا}
INSERT INTO [b9617] VALUES (2163,'<span data-type="title" id=toc-2148>٦٦ </span>- {هَلْ أَتَّبِعُكَ عَلَى أَنْ تُعَلِّمَنِ مِمَّا عُلِّمْتَ رُشْدًا}
INSERT INTO [b9617] VALUES (2164,'<span data-type="title" id=toc-2149>٦٨ </span>- {وَكَيْفَ تَصْبِرُ عَلَى مَا لَمْ تُحِطْ بِهِ خُبْرًا}
INSERT INTO [b9617] VALUES (2165,'<span data-type="title" id=toc-2150>٦٩ </span>- {قَالَ سَتَجِدُنِي إِنْ شَاءَ اللَّهُ صَابِرًا وَلا أَعْصِي لَكَ أَمْرًا}
INSERT INTO [b9617] VALUES (2166,'<span data-type="title" id=toc-2151>٧٠ </span>- {قَالَ فَإِنِ اتَّبَعْتَنِي فَلا تَسْأَلْنِي عَنْ شَيْءٍ حَتَّى أُحْدِثَ لَكَ مِنْهُ ذِكْرًا}
INSERT INTO [b9617] VALUES (2167,'<span data-type="title" id=toc-2152>٧١ </span>- {فَانْطَلَقَا حَتَّى إِذَا رَكِبَا فِي السَّفِينَةِ خَرَقَهَا قَالَ أَخَرَقْتَهَا لِتُغْرِقَ أَهْلَهَا لَقَدْ جِئْتَ شَيْئًا إِمْرًا}
INSERT INTO [b9617] VALUES (2168,'<span data-type="title" id=toc-2153>٧٢ </span>- {لَنْ تَسْتَطِيعَ مَعِيَ صَبْرًا}
INSERT INTO [b9617] VALUES (2169,'<span data-type="title" id=toc-2154>٧٣ </span>- {قَالَ لا تُؤَاخِذْنِي بِمَا نَسِيتُ وَلا تُرْهِقْنِي مِنْ أَمْرِي عُسْرًا}
INSERT INTO [b9617] VALUES (2170,'<span data-type="title" id=toc-2155>٧٤ </span>- {فَانْطَلَقَا حَتَّى إِذَا لَقِيَا غُلامًا فَقَتَلَهُ قَالَ أَقَتَلْتَ نَفْسًا زَكِيَّةً بِغَيْرِ نَفْسٍ لَقَدْ جِئْتَ شَيْئًا نُكْرًا}
INSERT INTO [b9617] VALUES (2171,'<span data-type="title" id=toc-2156>٧٥ </span>- {لَنْ تَسْتَطِيعَ مَعِيَ صَبْرًا}
INSERT INTO [b9617] VALUES (2172,'<span data-type="title" id=toc-2157>٧٦ </span>- {قَالَ إِنْ سَأَلْتُكَ عَنْ شَيْءٍ بَعْدَهَا فَلا تُصَاحِبْنِي قَدْ بَلَغْتَ مِنْ لَدُنِّي عُذْرًا}
INSERT INTO [b9617] VALUES (2173,'<span data-type="title" id=toc-2158>٧٧ </span>- {فَانْطَلَقَا حَتَّى إِذَا أَتَيَا أَهْلَ قَرْيَةٍ اسْتَطْعَمَا أَهْلَهَا فَأَبَوْا أَنْ يُضَيِّفُوهُمَا فَوَجَدَا فِيهَا جِدَارًا يُرِيدُ أَنْ يَنْقَضَّ فَأَقَامَهُ قَالَ لَوْ شِئْتَ لاتَّخَذْتَ عَلَيْهِ أَجْرًا}
INSERT INTO [b9617] VALUES (2174,'<span data-type="title" id=toc-2159>٧٨ </span>- {قَالَ هَذَا فِرَاقُ بَيْنِي وَبَيْنِكَ سَأُنَبِّئُكَ بِتَأْوِيلِ مَا لَمْ تَسْتَطِعْ عَلَيْهِ صَبْرًا}
INSERT INTO [b9617] VALUES (2175,'<span data-type="title" id=toc-2160>٧٩ </span>- {أَمَّا السَّفِينَةُ فَكَانَتْ لِمَسَاكِينَ يَعْمَلُونَ فِي الْبَحْرِ فَأَرَدْتُ أَنْ أَعِيبَهَا وَكَانَ وَرَاءَهُمْ مَلِكٌ يَأْخُذُ كُلَّ سَفِينَةٍ غَصْبًا} ⦗٦٥٧⦘
INSERT INTO [b9617] VALUES (2176,'<span data-type="title" id=toc-2161>٨٠ </span>- {وَأَمَّا الْغُلامُ فَكَانَ أَبَوَاهُ مُؤْمِنَيْنِ فَخَشِينَا أَنْ يُرْهِقَهُمَا طُغْيَانًا وَكُفْرًا}
INSERT INTO [b9617] VALUES (2177,'<span data-type="title" id=toc-2162>٨١ </span>- {فَأَرَدْنَا أَنْ يُبْدِلَهُمَا رَبُّهُمَا خَيْرًا مِنْهُ زَكَاةً وَأَقْرَبَ رُحْمًا}
INSERT INTO [b9617] VALUES (2178,'<span data-type="title" id=toc-2163>٨٢ </span>- {وَأَمَّا الْجِدَارُ فَكَانَ لِغُلامَيْنِ يَتِيمَيْنِ فِي الْمَدِينَةِ وَكَانَ تَحْتَهُ كَنْزٌ لَهُمَا وَكَانَ أَبُوهُمَا صَالِحًا فَأَرَادَ رَبُّكَ أَنْ يَبْلُغَا أَشُدَّهُمَا وَيَسْتَخْرِجَا كَنْزَهُمَا رَحْمَةً مِنْ رَبِّكَ وَمَا فَعَلْتُهُ عَنْ أَمْرِي ذَلِكَ تَأْوِيلُ مَا لَمْ تَسْطِعْ عَلَيْهِ صَبْرًا}
INSERT INTO [b9617] VALUES (2179,'<span data-type="title" id=toc-2164>٨٣ </span>- {وَيَسْأَلُونَكَ عَنْ ذِي الْقَرْنَيْنِ قُلْ سَأَتْلُو عَلَيْكُمْ مِنْهُ ذِكْرًا} ⦗٦٥٨⦘
INSERT INTO [b9617] VALUES (2180,'<span data-type="title" id=toc-2165>٨٤ </span>- {إِنَّا مَكَّنَّا لَهُ فِي الأَرْضِ وَآتَيْنَاهُ مِنْ كُلِّ شَيْءٍ سَبَبًا}
INSERT INTO [b9617] VALUES (2181,'<span data-type="title" id=toc-2166>٨٥ </span>- {فَأَتْبَعَ سَبَبًا}
INSERT INTO [b9617] VALUES (2182,'<span data-type="title" id=toc-2167>٨٦ </span>- {حَتَّى إِذَا بَلَغَ مَغْرِبَ الشَّمْسِ وَجَدَهَا تَغْرُبُ فِي عَيْنٍ حَمِئَةٍ وَوَجَدَ عِنْدَهَا قَوْمًا قُلْنَا يَا ذَا الْقَرْنَيْنِ إِمَّا أَنْ تُعَذِّبَ وَإِمَّا أَنْ تَتَّخِذَ فِيهِمْ حُسْنًا}
INSERT INTO [b9617] VALUES (2183,'<span data-type="title" id=toc-2168>٨٧ </span>- {قَالَ أَمَّا مَنْ ظَلَمَ فَسَوْفَ نُعَذِّبُهُ ثُمَّ يُرَدُّ إِلَى رَبِّهِ فَيُعَذِّبُهُ عَذَابًا نُكْرًا}
INSERT INTO [b9617] VALUES (2184,'<span data-type="title" id=toc-2169>٨٨ </span>- {وَأَمَّا مَنْ آمَنَ وَعَمِلَ صَالِحًا فَلَهُ جَزَاءً الْحُسْنَى وَسَنَقُولُ لَهُ مِنْ أَمْرِنَا يُسْرًا}
INSERT INTO [b9617] VALUES (2185,'<span data-type="title" id=toc-2170>٨٩ </span>- {ثُمَّ أَتْبَعَ سَبَبًا}
INSERT INTO [b9617] VALUES (2186,'<span data-type="title" id=toc-2171>٩٠ </span>- {حَتَّى إِذَا بَلَغَ مَطْلِعَ الشَّمْسِ وَجَدَهَا تَطْلُعُ عَلَى قَوْمٍ لَمْ نَجْعَلْ لَهُمْ مِنْ دُونِهَا سِتْرًا}
INSERT INTO [b9617] VALUES (2187,'<span data-type="title" id=toc-2172>٩١ </span>- {كَذَلِكَ وَقَدْ أَحَطْنَا بِمَا لَدَيْهِ خُبْرًا}
INSERT INTO [b9617] VALUES (2188,'<span data-type="title" id=toc-2173>٩٣ </span>- {حَتَّى إِذَا بَلَغَ بَيْنَ السَّدَّيْنِ وَجَدَ مِنْ دُونِهِمَا قَوْمًا لا يَكَادُونَ يَفْقَهُونَ قَوْلا}
INSERT INTO [b9617] VALUES (2189,'<span data-type="title" id=toc-2174>٩٤ </span>- {يَا ذَا الْقَرْنَيْنِ إِنَّ يَأْجُوجَ وَمَأْجُوجَ مُفْسِدُونَ فِي الأَرْضِ فَهَلْ نَجْعَلُ لَكَ خَرْجًا ⦗٦٦٠⦘ عَلَى أَنْ تَجْعَلَ بَيْنَنَا وَبَيْنَهُمْ سَدًّا}
INSERT INTO [b9617] VALUES (2190,'<span data-type="title" id=toc-2175>٩٥ </span>- {قَالَ مَا مَكَّنِّي فِيهِ رَبِّي خَيْرٌ فَأَعِينُونِي بِقُوَّةٍ أَجْعَلْ بَيْنَكُمْ وَبَيْنَهُمْ رَدْمًا}
INSERT INTO [b9617] VALUES (2191,'<span data-type="title" id=toc-2176>٩٦ </span>- {آتُونِي زُبَرَ الْحَدِيدِ حَتَّى إِذَا سَاوَى بَيْنَ الصَّدَفَيْنِ قَالَ انْفُخُوا حَتَّى إِذَا جَعَلَهُ نَارًا قَالَ آتُونِي أُفْرِغْ عَلَيْهِ قِطْرًا}
INSERT INTO [b9617] VALUES (2192,'<span data-type="title" id=toc-2177>٩٧ </span>- {فَمَا اسْطَاعُوا أَنْ يَظْهَرُوهُ وَمَا اسْتَطَاعُوا لَهُ نَقْبًا}
INSERT INTO [b9617] VALUES (2193,'<span data-type="title" id=toc-2178>٩٨ </span>- {قَالَ هَذَا رَحْمَةٌ مِنْ رَبِّي فَإِذَا جَاءَ وَعْدُ رَبِّي جَعَلَهُ دَكَّاءَ وَكَانَ وَعْدُ رَبِّي حَقًّا}
INSERT INTO [b9617] VALUES (2194,'<span data-type="title" id=toc-2179>٩٩ </span>- {وَتَرَكْنَا بَعْضَهُمْ يَوْمَئِذٍ يَمُوجُ فِي بَعْضٍ وَنُفِخَ فِي الصُّورِ فَجَمَعْنَاهُمْ جَمْعًا}
INSERT INTO [b9617] VALUES (2195,'<span data-type="title" id=toc-2180>١٠٠ </span>- {وَعَرَضْنَا جَهَنَّمَ يَوْمَئِذٍ لِلْكَافِرِينَ عَرْضًا}
INSERT INTO [b9617] VALUES (2196,'<span data-type="title" id=toc-2181>١٠١ </span>- {الَّذِينَ كَانَتْ أَعْيُنُهُمْ فِي غِطَاءٍ عَنْ ذِكْرِي وَكَانُوا لا يَسْتَطِيعُونَ سَمْعًا}
INSERT INTO [b9617] VALUES (2197,'<span data-type="title" id=toc-2182>١٠٢ </span>- {أَفَحَسِبَ الَّذِينَ كَفَرُوا أَنْ يَتَّخِذُوا عِبَادِي مِنْ دُونِي أَوْلِيَاءَ إِنَّا أَعْتَدْنَا جَهَنَّمَ لِلْكَافِرِينَ نُزُلا}
INSERT INTO [b9617] VALUES (2198,'<span data-type="title" id=toc-2183>١٠٣ </span>- {قُلْ هَلْ نُنَبِّئُكُمْ بِالأَخْسَرِينَ أَعْمَالا}
INSERT INTO [b9617] VALUES (2199,'<span data-type="title" id=toc-2184>١٠٤ </span>- {الَّذِينَ ضَلَّ سَعْيُهُمْ فِي الْحَيَاةِ الدُّنْيَا وَهُمْ يَحْسَبُونَ أَنَّهُمْ يُحْسِنُونَ صُنْعًا}
INSERT INTO [b9617] VALUES (2200,'<span data-type="title" id=toc-2185>١٠٥ </span>- {فَحَبِطَتْ أَعْمَالُهُمْ فَلا نُقِيمُ لَهُمْ يَوْمَ الْقِيَامَةِ وَزْنًا}
INSERT INTO [b9617] VALUES (2201,'<span data-type="title" id=toc-2186>١٠٦ </span>- {ذَلِكَ جَزَاؤُهُمْ جَهَنَّمُ بِمَا كَفَرُوا وَاتَّخَذُوا آيَاتِي وَرُسُلِي هُزُوًا}
INSERT INTO [b9617] VALUES (2202,'<span data-type="title" id=toc-2187>١٠٧ </span>- {كَانَتْ لَهُمْ جَنَّاتُ الْفِرْدَوْسِ نُزُلا}
INSERT INTO [b9617] VALUES (2203,'<span data-type="title" id=toc-2188>١٠٨ </span>- {خَالِدِينَ فِيهَا لا يَبْغُونَ عَنْهَا حِوَلا}
INSERT INTO [b9617] VALUES (2204,'<span data-type="title" id=toc-2189>١٠٩ </span>- {قُلْ لَوْ كَانَ الْبَحْرُ مِدَادًا لِكَلِمَاتِ رَبِّي لَنَفِدَ الْبَحْرُ قَبْلَ أَنْ تَنْفَدَ كَلِمَاتُ رَبِّي وَلَوْ جِئْنَا بِمِثْلِهِ مَدَدًا} ⦗٦٦٣⦘
INSERT INTO [b9617] VALUES (2205,'<span data-type="title" id=toc-2190>١١٠ </span>- {قُلْ إِنَّمَا أَنَا بَشَرٌ مِثْلُكُمْ يُوحَى إِلَيَّ أَنَّمَا إِلَهُكُمْ إِلَهٌ وَاحِدٌ فَمَنْ كَانَ يَرْجُو لِقَاءَ رَبِّهِ فَلْيَعْمَلْ عَمَلا صَالِحًا}
INSERT INTO [b9617] VALUES (2206,'<span data-type="title" id=toc-2191>سورة مريم</span>','2','664',NULL,NULL,'0');
INSERT INTO [b9617] VALUES (2207,'<span data-type="title" id=toc-2192>٢ </span>- {ذِكْرُ رَحْمَةِ رَبِّكَ عَبْدَهُ زَكَرِيَّا}
INSERT INTO [b9617] VALUES (2208,'<span data-type="title" id=toc-2193>٣ </span>- {إِذْ نَادَى رَبَّهُ نِدَاءً خَفِيًّا}
INSERT INTO [b9617] VALUES (2209,'<span data-type="title" id=toc-2194>٤ </span>- {قَالَ رَبِّ إِنِّي وَهَنَ الْعَظْمُ مِنِّي وَاشْتَعَلَ الرَّأْسُ شَيْبًا وَلَمْ أَكُنْ بِدُعَائِكَ رَبِّ شَقِيًّا}
INSERT INTO [b9617] VALUES (2210,'<span data-type="title" id=toc-2195>٥ </span>- {وَإِنِّي خِفْتُ الْمَوَالِيَ مِنْ وَرَائِي وَكَانَتِ امْرَأَتِي عَاقِرًا فَهَبْ لِي مِنْ لَدُنْكَ وَلِيًّا}
INSERT INTO [b9617] VALUES (2211,'<span data-type="title" id=toc-2196>٦ </span>- {يَرِثُنِي وَيَرِثُ مِنْ آلِ يَعْقُوبَ وَاجْعَلْهُ رَبِّ رَضِيًّا}
INSERT INTO [b9617] VALUES (2212,'<span data-type="title" id=toc-2197>٧ </span>- {يَا زَكَرِيَّا إِنَّا نُبَشِّرُكَ بِغُلامٍ اسْمُهُ يَحْيَى لَمْ نَجْعَلْ لَهُ مِنْ قَبْلُ سَمِيًّا}
INSERT INTO [b9617] VALUES (2213,'<span data-type="title" id=toc-2198>٨ </span>- {قَالَ رَبِّ أَنَّى يَكُونُ لِي غُلامٌ وَكَانَتِ امْرَأَتِي عَاقِرًا وَقَدْ بَلَغْتُ مِنَ الْكِبَرِ عِتِيًّا}
INSERT INTO [b9617] VALUES (2214,'<span data-type="title" id=toc-2199>٩ </span>- {قَالَ كَذَلِكَ قَالَ رَبُّكَ هُوَ عَلَيَّ هَيِّنٌ وَقَدْ خَلَقْتُكَ مِنْ قَبْلُ وَلَمْ تَكُ شَيْئًا}
INSERT INTO [b9617] VALUES (2215,'<span data-type="title" id=toc-2200>١٠ </span>- {قَالَ رَبِّ اجْعَلْ لِي آيَةً قَالَ آيَتُكَ أَلا تُكَلِّمَ النَّاسَ ثَلاثَ لَيَالٍ سَوِيًّا} ⦗٦٦٦⦘
INSERT INTO [b9617] VALUES (2216,'<span data-type="title" id=toc-2201>١١ </span>- {فَخَرَجَ عَلَى قَوْمِهِ مِنَ الْمِحْرَابِ فَأَوْحَى إِلَيْهِمْ أَنْ سَبِّحُوا بُكْرَةً وَعَشِيًّا}
INSERT INTO [b9617] VALUES (2217,'<span data-type="title" id=toc-2202>١٢ </span>- {يَا يَحْيَى خُذِ الْكِتَابَ بِقُوَّةٍ وَآتَيْنَاهُ الْحُكْمَ صَبِيًّا}
INSERT INTO [b9617] VALUES (2218,'<span data-type="title" id=toc-2203>١٣ </span>- {وَحَنَانًا مِنْ لَدُنَّا وَزَكَاةً وَكَانَ تَقِيًّا}
INSERT INTO [b9617] VALUES (2219,'<span data-type="title" id=toc-2204>١٤ </span>- {وَبَرًّا بِوَالِدَيْهِ وَلَمْ يَكُنْ جَبَّارًا عَصِيًّا}
INSERT INTO [b9617] VALUES (2220,'<span data-type="title" id=toc-2205>١٥ </span>- {وَسَلامٌ عَلَيْهِ يَوْمَ وُلِدَ وَيَوْمَ يَمُوتُ وَيَوْمَ يُبْعَثُ حَيًّا}
INSERT INTO [b9617] VALUES (2221,'<span data-type="title" id=toc-2206>١٦ </span>- {وَاذْكُرْ فِي الْكِتَابِ مَرْيَمَ إِذِ انْتَبَذَتْ مِنْ أَهْلِهَا مَكَانًا شَرْقِيًّا}
INSERT INTO [b9617] VALUES (2222,'<span data-type="title" id=toc-2207>١٧ </span>- {فَاتَّخَذَتْ مِنْ دُونِهِمْ حِجَابًا فَأَرْسَلْنَا إِلَيْهَا رُوحَنَا فَتَمَثَّلَ لَهَا بَشَرًا سَوِيًّا}
INSERT INTO [b9617] VALUES (2223,'<span data-type="title" id=toc-2208>١٨ </span>- {قَالَتْ إِنِّي أَعُوذُ بِالرَّحْمَنِ مِنْكَ إِنْ كُنْتَ تَقِيًّا}
INSERT INTO [b9617] VALUES (2224,'<span data-type="title" id=toc-2209>١٩ </span>- {أَنَا رَسُولُ رَبِّكِ لأَهَبَ لَكِ غُلامًا زَكِيًّا}
INSERT INTO [b9617] VALUES (2225,'<span data-type="title" id=toc-2210>٢٠ </span>- {قَالَتْ أَنَّى يَكُونُ لِي غُلامٌ وَلَمْ يَمْسَسْنِي بَشَرٌ وَلَمْ أَكُ بَغِيًّا}
INSERT INTO [b9617] VALUES (2226,'<span data-type="title" id=toc-2211>٢١ </span>- {قَالَ كَذَلِكِ قَالَ رَبُّكِ هُوَ عَلَيَّ هَيِّنٌ وَلِنَجْعَلَهُ آيَةً لِلنَّاسِ وَرَحْمَةً مِنَّا ⦗٦٦٨⦘ وَكَانَ أَمْرًا مَقْضِيًّا}
INSERT INTO [b9617] VALUES (2227,'<span data-type="title" id=toc-2212>٢٢ </span>- {فَحَمَلَتْهُ فَانْتَبَذَتْ بِهِ مَكَانًا قَصِيًّا}
INSERT INTO [b9617] VALUES (2228,'<span data-type="title" id=toc-2213>٢٣ </span>- {فَأَجَاءَهَا الْمَخَاضُ إِلَى جِذْعِ النَّخْلَةِ قَالَتْ يَا لَيْتَنِي مِتُّ قَبْلَ هَذَا}
INSERT INTO [b9617] VALUES (2229,'<span data-type="title" id=toc-2214>٢٤ </span>- {فَنَادَاهَا مِنْ تَحْتِهَا أَلا تَحْزَنِي قَدْ جَعَلَ رَبُّكِ تَحْتَكِ سَرِيًّا}
INSERT INTO [b9617] VALUES (2230,'<span data-type="title" id=toc-2215>٢٥ </span>- {وَهُزِّي إِلَيْكِ بِجِذْعِ النَّخْلَةِ تُسَاقِطْ عَلَيْكِ رُطَبًا جَنِيًّا}
INSERT INTO [b9617] VALUES (2231,'<span data-type="title" id=toc-2216>٢٦ </span>- {فَكُلِي وَاشْرَبِي وَقَرِّي عَيْنًا فَإِمَّا تَرَيِنَّ مِنَ الْبَشَرِ أَحَدًا فَقُولِي إِنِّي نَذَرْتُ لِلرَّحْمَنِ صَوْمًا فَلَنْ أُكَلِّمَ الْيَوْمَ إِنْسِيًّا}
INSERT INTO [b9617] VALUES (2232,'<span data-type="title" id=toc-2217>٢٧ </span>- {فَأَتَتْ بِهِ قَوْمَهَا تَحْمِلُهُ قَالُوا يَا مَرْيَمُ لَقَدْ جِئْتِ شَيْئًا فَرِيًّا}
INSERT INTO [b9617] VALUES (2233,'<span data-type="title" id=toc-2218>٢٩ </span>- {فَأَشَارَتْ إِلَيْهِ قَالُوا كَيْفَ نُكَلِّمُ مَنْ كَانَ فِي الْمَهْدِ صَبِيًّا}
INSERT INTO [b9617] VALUES (2234,'<span data-type="title" id=toc-2219>٣٠ </span>- {قَالَ إِنِّي عَبْدُ اللَّهِ آتَانِيَ الْكِتَابَ وَجَعَلَنِي نَبِيًّا}
INSERT INTO [b9617] VALUES (2235,'<span data-type="title" id=toc-2220>٣١ </span>- {وَجَعَلَنِي مُبَارَكًا أَيْنَ مَا كُنْتُ وَأَوْصَانِي بِالصَّلاةِ وَالزَّكَاةِ مَا دُمْتُ حَيًّا}
INSERT INTO [b9617] VALUES (2236,'<span data-type="title" id=toc-2221>٣٢ </span>- {وَبَرًّا بِوَالِدَتِي وَلَمْ يَجْعَلْنِي جَبَّارًا شَقِيًّا}
INSERT INTO [b9617] VALUES (2237,'<span data-type="title" id=toc-2222>٣٣ </span>- {وَالسَّلامُ عَلَيَّ يَوْمَ وُلِدْتُ وَيَوْمَ أَمُوتُ وَيَوْمَ أُبْعَثُ حَيًّا}
INSERT INTO [b9617] VALUES (2238,'<span data-type="title" id=toc-2223>٣٤ </span>- {قَوْلَ الْحَقِّ الَّذِي فِيهِ يَمْتَرُونَ}
INSERT INTO [b9617] VALUES (2239,'<span data-type="title" id=toc-2224>٣٥ </span>- {مَا كَانَ لِلَّهِ أَنْ يَتَّخِذَ مِنْ وَلَدٍ سُبْحَانَهُ إِذَا قَضَى أَمْرًا فَإِنَّمَا يَقُولُ لَهُ كُنْ فَيَكُونُ} ⦗٦٧١⦘
INSERT INTO [b9617] VALUES (2240,'<span data-type="title" id=toc-2225>٣٦ </span>- {وَإِنَّ اللَّهَ رَبِّي وَرَبُّكُمْ فَاعْبُدُوهُ هَذَا صِرَاطٌ مُسْتَقِيمٌ}
INSERT INTO [b9617] VALUES (2241,'<span data-type="title" id=toc-2226>٣٧ </span>- {فَاخْتَلَفَ الأَحْزَابُ مِنْ بَيْنِهِمْ فَوَيْلٌ لِلَّذِينَ كَفَرُوا مِنْ مَشْهَدِ يَوْمٍ عَظِيمٍ}
INSERT INTO [b9617] VALUES (2242,'<span data-type="title" id=toc-2227>٣٨ </span>- {أَسْمِعْ بِهِمْ وَأَبْصِرْ يَوْمَ يَأْتُونَنَا لَكِنِ الظَّالِمُونَ الْيَوْمَ فِي ضَلالٍ مُبِينٍ}
INSERT INTO [b9617] VALUES (2243,'<span data-type="title" id=toc-2228>٣٩ </span>- {وَأَنْذِرْهُمْ يَوْمَ الْحَسْرَةِ إِذْ قُضِيَ الأَمْرُ وَهُمْ فِي غَفْلَةٍ وَهُمْ لا يُؤْمِنُونَ}
INSERT INTO [b9617] VALUES (2244,'<span data-type="title" id=toc-2229>٤٠ </span>- {إِنَّا نَحْنُ نَرِثُ الأَرْضَ وَمَنْ عَلَيْهَا وَإِلَيْنَا يُرْجَعُونَ}
INSERT INTO [b9617] VALUES (2245,'<span data-type="title" id=toc-2230>٤١ </span>- {وَاذْكُرْ فِي الْكِتَابِ إِبْرَاهِيمَ إِنَّهُ كَانَ صِدِّيقًا نَبِيًّا}
INSERT INTO [b9617] VALUES (2246,'<span data-type="title" id=toc-2231>٤٢ </span>- {إِذْ قَالَ لأَبِيهِ يَا أَبَتِ لِمَ تَعْبُدُ مَا لا يَسْمَعُ وَلا يُبْصِرُ وَلا يُغْنِي عَنْكَ شَيْئًا}
INSERT INTO [b9617] VALUES (2247,'<span data-type="title" id=toc-2232>٤٣ </span>- {يَا أَبَتِ إِنِّي قَدْ جَاءَنِي مِنَ الْعِلْمِ مَا لَمْ يَأْتِكَ فَاتَّبِعْنِي أَهْدِكَ صِرَاطًا سَوِيًّا}
INSERT INTO [b9617] VALUES (2248,'<span data-type="title" id=toc-2233>٤٤ </span>- {إِنَّ الشَّيْطَانَ كَانَ لِلرَّحْمَنِ عَصِيًّا}
INSERT INTO [b9617] VALUES (2249,'<span data-type="title" id=toc-2234>٤٥ </span>- {يَا أَبَتِ إِنِّي أَخَافُ أَنْ يَمَسَّكَ عَذَابٌ مِنَ الرَّحْمَنِ فَتَكُونَ لِلشَّيْطَانِ وَلِيًّا}
INSERT INTO [b9617] VALUES (2250,'<span data-type="title" id=toc-2235>٤٦ </span>- {قَالَ أَرَاغِبٌ أَنْتَ عَنْ آلِهَتِي يَا إِبْرَاهِيمُ لَئِنْ لَمْ تَنْتَهِ لأَرْجُمَنَّكَ وَاهْجُرْنِي مَلِيًّا}
INSERT INTO [b9617] VALUES (2251,'<span data-type="title" id=toc-2236>٤٧ </span>- {قَالَ سَلامٌ عَلَيْكَ سَأَسْتَغْفِرُ لَكَ رَبِّي إِنَّهُ كَانَ بِي حَفِيًّا}
INSERT INTO [b9617] VALUES (2252,'<span data-type="title" id=toc-2237>٤٨ </span>- {وَأَعْتَزِلُكُمْ وَمَا تَدْعُونَ مِنْ دُونِ اللَّهِ وَأَدْعُو رَبِّي عَسَى أَلا أَكُونَ بِدُعَاءِ رَبِّي شَقِيًّا}
INSERT INTO [b9617] VALUES (2253,'<span data-type="title" id=toc-2238>٤٩ </span>- {فَلَمَّا اعْتَزَلَهُمْ وَمَا يَعْبُدُونَ مِنْ دُونِ اللَّهِ وَهَبْنَا لَهُ إِسْحَاقَ وَيَعْقُوبَ وَكُلا جَعَلْنَا نَبِيًّا}
INSERT INTO [b9617] VALUES (2254,'<span data-type="title" id=toc-2239>٥٠ </span>- {وَوَهَبْنَا لَهُمْ مِنْ رَحْمَتِنَا وَجَعَلْنَا لَهُمْ لِسَانَ صِدْقٍ عَلِيًّا}
INSERT INTO [b9617] VALUES (2255,'<span data-type="title" id=toc-2240>٥١ </span>- {وَاذْكُرْ فِي الْكِتَابِ مُوسَى إِنَّهُ كَانَ مُخْلَصًا وَكَانَ رَسُولا نَبِيًّا}
INSERT INTO [b9617] VALUES (2256,'<span data-type="title" id=toc-2241>٥٢ </span>- {وَنَادَيْنَاهُ مِنْ جَانِبِ الطُّورِ الأَيْمَنِ وَقَرَّبْنَاهُ نَجِيًّا}
INSERT INTO [b9617] VALUES (2257,'<span data-type="title" id=toc-2242>٥٣ </span>- {وَوَهَبْنَا لَهُ مِنْ رَحْمَتِنَا أَخَاهُ هَارُونَ نَبِيًّا}
INSERT INTO [b9617] VALUES (2258,'<span data-type="title" id=toc-2243>٥٤ </span>- {وَاذْكُرْ فِي الْكِتَابِ إِسْمَاعِيلَ إِنَّهُ كَانَ صَادِقَ الْوَعْدِ}
INSERT INTO [b9617] VALUES (2259,'<span data-type="title" id=toc-2244>٥٥ </span>- {وَكَانَ عِنْدَ رَبِّهِ مَرْضِيًّا}
INSERT INTO [b9617] VALUES (2260,'<span data-type="title" id=toc-2245>٥٦ </span>- {وَاذْكُرْ فِي الْكِتَابِ إِدْرِيسَ إِنَّهُ كَانَ صِدِّيقًا نَبِيًّا}
INSERT INTO [b9617] VALUES (2261,'<span data-type="title" id=toc-2246>٥٧ </span>- {وَرَفَعْنَاهُ مَكَانًا عَلِيًّا}
INSERT INTO [b9617] VALUES (2262,'<span data-type="title" id=toc-2247>٥٨ </span>- {أُولَئِكَ الَّذِينَ أَنْعَمَ اللَّهُ عَلَيْهِمْ مِنَ النَّبِيِّينَ مِنْ ذُرِّيَّةِ آدَمَ وَمِمَّنْ حَمَلْنَا مَعَ نُوحٍ وَمِنْ ذُرِّيَّةِ إِبْرَاهِيمَ وَإِسْرَائِيلَ وَمِمَّنْ هَدَيْنَا وَاجْتَبَيْنَا إِذَا تُتْلَى عَلَيْهِمْ آيَاتُ الرَّحْمَنِ خَرُّوا سُجَّدًا وَبُكِيًّا}
INSERT INTO [b9617] VALUES (2263,'<span data-type="title" id=toc-2248>٥٩ </span>- {فَخَلَفَ مِنْ بَعْدِهِمْ خَلْفٌ أَضَاعُوا الصَّلاةَ وَاتَّبَعُوا الشَّهَوَاتِ فَسَوْفَ يَلْقَوْنَ غَيًّا} ⦗٦٧٦⦘
INSERT INTO [b9617] VALUES (2264,'<span data-type="title" id=toc-2249>٦٠ </span>- {إِلا مَنْ تَابَ وَآمَنَ وَعَمِلَ صَالِحًا فَأُولَئِكَ يَدْخُلُونَ الْجَنَّةَ وَلا يُظْلَمُونَ شَيْئًا}
INSERT INTO [b9617] VALUES (2265,'<span data-type="title" id=toc-2250>٦١ </span>- {جَنَّاتِ عَدْنٍ الَّتِي وَعَدَ الرَّحْمَنُ عِبَادَهُ بِالْغَيْبِ إِنَّهُ كَانَ وَعْدُهُ مَأْتِيًّا}
INSERT INTO [b9617] VALUES (2266,'<span data-type="title" id=toc-2251>٦٢ </span>- {لا يَسْمَعُونَ فِيهَا لَغْوًا إِلا سَلامًا وَلَهُمْ رِزْقُهُمْ فِيهَا بُكْرَةً وَعَشِيًّا}
INSERT INTO [b9617] VALUES (2267,'<span data-type="title" id=toc-2252>٦٣ </span>- {تِلْكَ الْجَنَّةُ الَّتِي نُورِثُ مِنْ عِبَادِنَا مَنْ كَانَ تَقِيًّا}
INSERT INTO [b9617] VALUES (2268,'<span data-type="title" id=toc-2253>٦٤ </span>- {وَمَا نَتَنَزَّلُ إِلا بِأَمْرِ رَبِّكَ لَهُ مَا بَيْنَ أَيْدِينَا وَمَا خَلْفَنَا وَمَا بَيْنَ ذَلِكَ وَمَا كَانَ رَبُّكَ ⦗٦٧٧⦘ نَسِيًّا}
INSERT INTO [b9617] VALUES (2269,'<span data-type="title" id=toc-2254>٦٥ </span>- {رَبُّ السَّمَاوَاتِ وَالأَرْضِ وَمَا بَيْنَهُمَا فَاعْبُدْهُ وَاصْطَبِرْ لِعِبَادَتِهِ هَلْ تَعْلَمُ لَهُ سَمِيًّا}
INSERT INTO [b9617] VALUES (2270,'<span data-type="title" id=toc-2255>٦٦ </span>- {وَيَقُولُ الإِنْسَانُ أَئِذَا مَا مِتُّ لَسَوْفَ أُخْرَجُ حَيًّا}
INSERT INTO [b9617] VALUES (2271,'<span data-type="title" id=toc-2256>٦٧ </span>- {أَوَلا يَذْكُرُ الإِنْسَانُ أَنَّا خَلَقْنَاهُ مِنْ قَبْلُ وَلَمْ يَكُ شَيْئًا}
INSERT INTO [b9617] VALUES (2272,'<span data-type="title" id=toc-2257>٦٨ </span>- {فَوَرَبِّكَ لَنَحْشُرَنَّهُمْ وَالشَّيَاطِينَ ثُمَّ لَنُحْضِرَنَّهُمْ حَوْلَ جَهَنَّمَ جِثِيًّا}
INSERT INTO [b9617] VALUES (2273,'<span data-type="title" id=toc-2258>٦٩ </span>- {ثُمَّ لَنَنْزِعَنَّ مِنْ كُلِّ شِيعَةٍ أَيُّهُمْ أَشَدُّ عَلَى الرَّحْمَنِ عِتِيًّا}
INSERT INTO [b9617] VALUES (2274,'<span data-type="title" id=toc-2259>٧٠ </span>- {ثُمَّ لَنَحْنُ أَعْلَمُ بِالَّذِينَ هُمْ أَوْلَى بِهَا صِلِيًّا}
INSERT INTO [b9617] VALUES (2275,'<span data-type="title" id=toc-2260>٧١ </span>- {وَإِنْ مِنْكُمْ إِلا وَارِدُهَا كَانَ عَلَى رَبِّكَ حَتْمًا مَقْضِيًّا}
INSERT INTO [b9617] VALUES (2276,'<span data-type="title" id=toc-2261>٧٢ </span>- {وَنَذَرُ الظَّالِمِينَ فِيهَا جِثِيًّا}
INSERT INTO [b9617] VALUES (2277,'<span data-type="title" id=toc-2262>٧٣ </span>- {وَإِذَا تُتْلَى عَلَيْهِمْ آيَاتُنَا بَيِّنَاتٍ قَالَ الَّذِينَ كَفَرُوا لِلَّذِينَ آمَنُوا أَيُّ الْفَرِيقَيْنِ خَيْرٌ مَقَامًا}
INSERT INTO [b9617] VALUES (2278,'<span data-type="title" id=toc-2263>٧٤ </span>- {وَكَمْ أَهْلَكْنَا قَبْلَهُمْ مِنْ قَرْنٍ هُمْ أَحْسَنُ أَثَاثًا وَرِئْيًا}
INSERT INTO [b9617] VALUES (2279,'<span data-type="title" id=toc-2264>٧٥ </span>- {قُلْ مَنْ كَانَ فِي الضَّلالَةِ فَلْيَمْدُدْ لَهُ الرَّحْمَنُ مَدًّا حَتَّى إِذَا رَأَوْا مَا يُوعَدُونَ إِمَّا الْعَذَابَ وَإِمَّا السَّاعَةَ فَسَيَعْلَمُونَ مَنْ هُوَ شَرٌّ مَكَانًا}
INSERT INTO [b9617] VALUES (2280,'<span data-type="title" id=toc-2265>٧٦ </span>- {وَيَزِيدُ اللَّهُ الَّذِينَ اهْتَدَوْا هُدًى وَالْبَاقِيَاتُ الصَّالِحَاتُ خَيْرٌ عِنْدَ رَبِّكَ ثَوَابًا}
INSERT INTO [b9617] VALUES (2281,'<span data-type="title" id=toc-2266>٧٧ </span>- {أَفَرَأَيْتَ الَّذِي كَفَرَ بِآيَاتِنَا وَقَالَ لأُوتَيَنَّ مَالا وَوَلَدًا}
INSERT INTO [b9617] VALUES (2282,'<span data-type="title" id=toc-2267>٧٨ </span>- {أَطَّلَعَ الْغَيْبَ أَمِ اتَّخَذَ عِنْدَ الرَّحْمَنِ عَهْدًا}
INSERT INTO [b9617] VALUES (2283,'<span data-type="title" id=toc-2268>٧٩ </span>- {كَلا سَنَكْتُبُ مَا يَقُولُ وَنَمُدُّ لَهُ مِنَ الْعَذَابِ مَدًّا}
INSERT INTO [b9617] VALUES (2284,'<span data-type="title" id=toc-2269>٨٠ </span>- {وَنَرِثُهُ مَا يَقُولُ وَيَأْتِينَا فَرْدًا}
INSERT INTO [b9617] VALUES (2285,'<span data-type="title" id=toc-2270>٨١ </span>- {وَاتَّخَذُوا مِنْ دُونِ اللَّهِ آلِهَةً لِيَكُونُوا لَهُمْ عِزًّا}
INSERT INTO [b9617] VALUES (2286,'<span data-type="title" id=toc-2271>٨٢ </span>- {كَلا سَيَكْفُرُونَ بِعِبَادَتِهِمْ وَيَكُونُونَ عَلَيْهِمْ ضِدًّا}
INSERT INTO [b9617] VALUES (2287,'<span data-type="title" id=toc-2272>٨٣ </span>- {أَلَمْ تَرَ أَنَّا أَرْسَلْنَا الشَّيَاطِينَ عَلَى الْكَافِرِينَ تَؤُزُّهُمْ أَزًّا}
INSERT INTO [b9617] VALUES (2288,'<span data-type="title" id=toc-2273>٨٤ </span>- {فَلا تَعْجَلْ عَلَيْهِمْ}
INSERT INTO [b9617] VALUES (2289,'<span data-type="title" id=toc-2274>٨٥ </span>- {يَوْمَ نَحْشُرُ الْمُتَّقِينَ إِلَى الرَّحْمَنِ وَفْدًا}
INSERT INTO [b9617] VALUES (2290,'<span data-type="title" id=toc-2275>٨٦ </span>- {وَنَسُوقُ الْمُجْرِمِينَ إِلَى جَهَنَّمَ وِرْدًا}
INSERT INTO [b9617] VALUES (2291,'<span data-type="title" id=toc-2276>٨٧ </span>- {لا يَمْلِكُونَ الشَّفَاعَةَ إِلا مَنِ اتَّخَذَ عِنْدَ الرَّحْمَنِ عَهْدًا}
INSERT INTO [b9617] VALUES (2292,'<span data-type="title" id=toc-2277>٨٨ </span>- {وَقَالُوا اتَّخَذَ الرَّحْمَنُ وَلَدًا}
INSERT INTO [b9617] VALUES (2293,'<span data-type="title" id=toc-2278>٨٩ </span>- {لَقَدْ جِئْتُمْ شَيْئًا إِدًّا}
INSERT INTO [b9617] VALUES (2294,'<span data-type="title" id=toc-2279>٩٠ </span>- {وَتَخِرُّ الْجِبَالُ هَدًّا}
INSERT INTO [b9617] VALUES (2295,'<span data-type="title" id=toc-2280>٩١ </span>- {أَنْ دَعَوْا لِلرَّحْمَنِ وَلَدًا}
INSERT INTO [b9617] VALUES (2296,'<span data-type="title" id=toc-2281>٩٢ </span>- {وَمَا يَنْبَغِي لِلرَّحْمَنِ أَنْ يَتَّخِذَ وَلَدًا}
INSERT INTO [b9617] VALUES (2297,'<span data-type="title" id=toc-2282>٩٣ </span>- {إِنْ كُلُّ مَنْ فِي السَّمَاوَاتِ وَالأَرْضِ إِلا آتِي الرَّحْمَنِ عَبْدًا}
INSERT INTO [b9617] VALUES (2298,'<span data-type="title" id=toc-2283>٩٤ </span>- {لَقَدْ أَحْصَاهُمْ وَعَدَّهُمْ عَدًّا}
INSERT INTO [b9617] VALUES (2299,'<span data-type="title" id=toc-2284>٩٥ </span>- {وَكُلُّهُمْ آتِيهِ يَوْمَ الْقِيَامَةِ فَرْدًا}
INSERT INTO [b9617] VALUES (2300,'<span data-type="title" id=toc-2285>٩٦ </span>- {سَيَجْعَلُ لَهُمُ الرَّحْمَنُ}
INSERT INTO [b9617] VALUES (2301,'<span data-type="title" id=toc-2286>٩٧ </span>- {فَإِنَّمَا يَسَّرْنَاهُ بِلِسَانِكَ لِتُبَشِّرَ بِهِ الْمُتَّقِينَ}
INSERT INTO [b9617] VALUES (2302,'<span data-type="title" id=toc-2287>٩٨ </span>- {وَكَمْ أَهْلَكْنَا قَبْلَهُمْ مِنْ قَرْنٍ هَلْ تُحِسُّ مِنْهُمْ مِنْ أَحَدٍ} ⦗٦٨٣⦘
INSERT INTO [b9617] VALUES (2303,'<span data-type="title" id=toc-2288>سورة طه</span>','2','684',NULL,NULL,'0');
INSERT INTO [b9617] VALUES (2304,'<span data-type="title" id=toc-2289>٢ </span>- {مَا أَنْزَلْنَا عَلَيْكَ الْقُرْآنَ لِتَشْقَى}
INSERT INTO [b9617] VALUES (2305,'<span data-type="title" id=toc-2290>٣ </span>- {إِلا تَذْكِرَةً لِمَنْ يَخْشَى}
INSERT INTO [b9617] VALUES (2306,'<span data-type="title" id=toc-2291>٤ </span>- {تَنْزِيلا مِمَّنْ خَلَقَ الأَرْضَ وَالسَّمَاوَاتِ الْعُلَى}
INSERT INTO [b9617] VALUES (2307,'<span data-type="title" id=toc-2292>٥ </span>- {الرَّحْمَنُ عَلَى الْعَرْشِ اسْتَوَى}
INSERT INTO [b9617] VALUES (2308,'<span data-type="title" id=toc-2293>٦ </span>- {لَهُ مَا فِي السَّمَاوَاتِ وَمَا فِي الأَرْضِ وَمَا بَيْنَهُمَا وَمَا تَحْتَ الثَّرَى}
INSERT INTO [b9617] VALUES (2309,'<span data-type="title" id=toc-2294>٧ </span>- {وَإِنْ تَجْهَرْ بِالْقَوْلِ فَإِنَّهُ يَعْلَمُ السِّرَّ وَأَخْفَى}
INSERT INTO [b9617] VALUES (2310,'<span data-type="title" id=toc-2295>٨ </span>- {اللَّهُ لا إِلَهَ إِلا هُوَ لَهُ الأَسْمَاءُ الْحُسْنَى}
INSERT INTO [b9617] VALUES (2311,'<span data-type="title" id=toc-2296>٩ </span>- {وَهَلْ أَتَاكَ حَدِيثُ مُوسَى}
INSERT INTO [b9617] VALUES (2312,'<span data-type="title" id=toc-2297>١٠ </span>- {إِذْ رَأَى نَارًا فَقَالَ لأَهْلِهِ امْكُثُوا إِنِّي آنَسْتُ نَارًا لَعَلِّي آتِيكُمْ مِنْهَا بِقَبَسٍ أَوْ أَجِدُ عَلَى النَّارِ هُدًى}
INSERT INTO [b9617] VALUES (2313,'<span data-type="title" id=toc-2298>١١ </span>- {فَلَمَّا أَتَاهَا نُودِيَ يَا مُوسَى}
INSERT INTO [b9617] VALUES (2314,'<span data-type="title" id=toc-2299>١٢ </span>- {إِنِّي أَنَا رَبُّكَ فَاخْلَعْ نَعْلَيْكَ إِنَّكَ بِالْوَادِ الْمُقَدَّسِ طُوًى}
INSERT INTO [b9617] VALUES (2315,'<span data-type="title" id=toc-2300>١٣ </span>- {وَأَنَا اخْتَرْتُكَ فَاسْتَمِعْ لِمَا يُوحَى} ⦗٦٨٦⦘
INSERT INTO [b9617] VALUES (2316,'<span data-type="title" id=toc-2301>١٤ </span>- {إِنَّنِي أَنَا اللَّهُ لا إِلَهَ إِلا أَنَا فَاعْبُدْنِي وَأَقِمِ الصَّلاةَ لِذِكْرِي}
INSERT INTO [b9617] VALUES (2317,'<span data-type="title" id=toc-2302>١٥ </span>- {إِنَّ السَّاعَةَ آتِيَةٌ أَكَادُ أُخْفِيهَا لِتُجْزَى كُلُّ نَفْسٍ بِمَا تَسْعَى}
INSERT INTO [b9617] VALUES (2318,'<span data-type="title" id=toc-2303>١٦ </span>- {فَلا يَصُدَّنَّكَ عَنْهَا مَنْ لا يُؤْمِنُ بِهَا وَاتَّبَعَ هَوَاهُ فَتَرْدَى}
INSERT INTO [b9617] VALUES (2319,'<span data-type="title" id=toc-2304>١٧ </span>- {وَمَا تِلْكَ بِيَمِينِكَ يَا مُوسَى}
INSERT INTO [b9617] VALUES (2320,'<span data-type="title" id=toc-2305>١٨ </span>- {قَالَ هِيَ عَصَايَ أَتَوَكَّأُ عَلَيْهَا وَأَهُشُّ بِهَا عَلَى غَنَمِي وَلِيَ فِيهَا مَآرِبُ أُخْرَى}
INSERT INTO [b9617] VALUES (2321,'<span data-type="title" id=toc-2306>١٩ </span>- {قَالَ أَلْقِهَا يَا مُوسَى}
INSERT INTO [b9617] VALUES (2322,'<span data-type="title" id=toc-2307>٢٠ </span>- {فَأَلْقَاهَا فَإِذَا هِيَ حَيَّةٌ تَسْعَى}
INSERT INTO [b9617] VALUES (2323,'<span data-type="title" id=toc-2308>٢١ </span>- {قَالَ خُذْهَا وَلا تَخَفْ سَنُعِيدُهَا سِيرَتَهَا الأُولَى}
INSERT INTO [b9617] VALUES (2324,'<span data-type="title" id=toc-2309>٢٢ </span>- {وَاضْمُمْ يَدَكَ إِلَى جَنَاحِكَ تَخْرُجْ بَيْضَاءَ مِنْ غَيْرِ سُوءٍ آيَةً أُخْرَى}
INSERT INTO [b9617] VALUES (2325,'<span data-type="title" id=toc-2310>٢٣ </span>- {لِنُرِيَكَ مِنْ آيَاتِنَا الْكُبْرَى}
INSERT INTO [b9617] VALUES (2326,'<span data-type="title" id=toc-2311>٢٤ </span>- {اذْهَبْ إِلَى فِرْعَوْنَ إِنَّهُ طَغَى}
INSERT INTO [b9617] VALUES (2327,'<span data-type="title" id=toc-2312>٢٥ </span>- {اشْرَحْ لِي صَدْرِي}
INSERT INTO [b9617] VALUES (2328,'<span data-type="title" id=toc-2313>٢٧ </span>- {وَاحْلُلْ عُقْدَةً مِنْ لِسَانِي}
INSERT INTO [b9617] VALUES (2329,'<span data-type="title" id=toc-2314>٢٨ </span>- {يَفْقَهُوا قَوْلِي}
INSERT INTO [b9617] VALUES (2330,'<span data-type="title" id=toc-2315>٢٩ </span>- {وَاجْعَلْ لِي وَزِيرًا مِنْ أَهْلِي}
INSERT INTO [b9617] VALUES (2331,'<span data-type="title" id=toc-2316>٣٠ </span>- {هَارُونَ أَخِي}
INSERT INTO [b9617] VALUES (2332,'<span data-type="title" id=toc-2317>٣١ </span>- {اشْدُدْ بِهِ أَزْرِي} ⦗٦٨٩⦘
INSERT INTO [b9617] VALUES (2333,'<span data-type="title" id=toc-2318>٣٣ </span>- {كَيْ نُسَبِّحَكَ كَثِيرًا}
INSERT INTO [b9617] VALUES (2334,'<span data-type="title" id=toc-2319>٣٥ </span>- {إِنَّكَ كُنْتَ بِنَا بَصِيرًا}
INSERT INTO [b9617] VALUES (2335,'<span data-type="title" id=toc-2320>٣٦ </span>- {قَالَ قَدْ أُوتِيتَ سُؤْلَكَ يَا مُوسَى}
INSERT INTO [b9617] VALUES (2336,'<span data-type="title" id=toc-2321>٣٧ </span>- {وَلَقَدْ مَنَنَّا عَلَيْكَ مَرَّةً أُخْرَى}
INSERT INTO [b9617] VALUES (2337,'<span data-type="title" id=toc-2322>٣٨ </span>- {إِذْ أَوْحَيْنَا إِلَى أُمِّكَ مَا يُوحَى}
INSERT INTO [b9617] VALUES (2338,'<span data-type="title" id=toc-2323>٣٩ </span>- {أَنِ اقْذِفِيهِ فِي التَّابُوتِ فَاقْذِفِيهِ فِي الْيَمِّ فَلْيُلْقِهِ الْيَمُّ بِالسَّاحِلِ يَأْخُذْهُ عَدُوٌّ لِي وَعَدُوٌّ لَهُ وَأَلْقَيْتُ عَلَيْكَ مَحَبَّةً مِنِّي وَلِتُصْنَعَ عَلَى عَيْنِي} ⦗٦٩٠⦘
INSERT INTO [b9617] VALUES (2339,'<span data-type="title" id=toc-2324>٤٠ </span>- {إِذْ تَمْشِي أُخْتُكَ فَتَقُولُ هَلْ أَدُلُّكُمْ عَلَى مَنْ يَكْفُلُهُ فَرَجَعْنَاكَ إِلَى أُمِّكَ كَيْ تَقَرَّ عَيْنُهَا وَلا تَحْزَنَ وَقَتَلْتَ نَفْسًا فَنَجَّيْنَاكَ مِنَ الْغَمِّ وَفَتَنَّاكَ فُتُونًا فَلَبِثْتَ سِنِينَ فِي أَهْلِ مَدْيَنَ ثُمَّ جِئْتَ عَلَى قَدَرٍ يَا مُوسَى}
INSERT INTO [b9617] VALUES (2340,'<span data-type="title" id=toc-2325>٤١ </span>- {وَاصْطَنَعْتُكَ لِنَفْسِي}
INSERT INTO [b9617] VALUES (2341,'<span data-type="title" id=toc-2326>٤٢ </span>- {اذْهَبْ أَنْتَ وَأَخُوكَ بِآيَاتِي}
INSERT INTO [b9617] VALUES (2342,'<span data-type="title" id=toc-2327>٤٣ </span>- {اذْهَبَا إِلَى فِرْعَوْنَ إِنَّهُ طَغَى}
INSERT INTO [b9617] VALUES (2343,'<span data-type="title" id=toc-2328>٤٤ </span>- {لَعَلَّهُ يَتَذَكَّرُ أَوْ يَخْشَى}
INSERT INTO [b9617] VALUES (2344,'<span data-type="title" id=toc-2329>٤٥ </span>- {قَالا رَبَّنَا إِنَّنَا نَخَافُ أَنْ يَفْرُطَ عَلَيْنَا أَوْ أَنْ يَطْغَى}
INSERT INTO [b9617] VALUES (2345,'<span data-type="title" id=toc-2330>٤٦ </span>- {قَالَ لا تَخَافَا إِنَّنِي مَعَكُمَا أَسْمَعُ وَأَرَى}
INSERT INTO [b9617] VALUES (2346,'<span data-type="title" id=toc-2331>٤٧ </span>- {فَأْتِيَاهُ فَقُولا إِنَّا رَسُولا رَبِّكَ فَأَرْسِلْ مَعَنَا بَنِي إِسْرَائِيلَ وَلا تُعَذِّبْهُمْ قَدْ جِئْنَاكَ بِآيَةٍ مِنْ رَبِّكَ وَالسَّلامُ عَلَى مَنِ اتَّبَعَ الْهُدَى}
INSERT INTO [b9617] VALUES (2347,'<span data-type="title" id=toc-2332>٤٨ </span>- {إِنَّا قَدْ أُوحِيَ إِلَيْنَا أَنَّ الْعَذَابَ عَلَى مَنْ كَذَّبَ وَتَوَلَّى}
INSERT INTO [b9617] VALUES (2348,'<span data-type="title" id=toc-2333>٤٩ </span>- {قَالَ فَمَنْ رَبُّكُمَا يَا مُوسَى}
INSERT INTO [b9617] VALUES (2349,'<span data-type="title" id=toc-2334>٥٠ </span>- {قَالَ رَبُّنَا الَّذِي أَعْطَى كُلَّ شَيْءٍ خَلْقَهُ ثُمَّ هَدَى}
INSERT INTO [b9617] VALUES (2350,'<span data-type="title" id=toc-2335>٥١ </span>- {قَالَ فَمَا بَالُ الْقُرُونِ الأُولَى}
INSERT INTO [b9617] VALUES (2351,'<span data-type="title" id=toc-2336>٥٢ </span>- {قَالَ عِلْمُهَا عِنْدَ رَبِّي فِي كِتَابٍ لا يَضِلُّ رَبِّي وَلا يَنْسَى}
INSERT INTO [b9617] VALUES (2352,'<span data-type="title" id=toc-2337>٥٣ </span>- {الَّذِي جَعَلَ لَكُمُ الأَرْضَ مَهْدًا وَسَلَكَ لَكُمْ فِيهَا سُبُلا وَأَنْزَلَ مِنَ السَّمَاءِ مَاءً فَأَخْرَجْنَا بِهِ أَزْوَاجًا مِنْ نَبَاتٍ شَتَّى}
INSERT INTO [b9617] VALUES (2353,'<span data-type="title" id=toc-2338>٥٤ </span>- {كُلُوا وَارْعَوْا أَنْعَامَكُمْ إِنَّ فِي ذَلِكَ لآيَاتٍ لأُولِي النُّهَى}
INSERT INTO [b9617] VALUES (2354,'<span data-type="title" id=toc-2339>٥٥ </span>- {مِنْهَا خَلَقْنَاكُمْ وَفِيهَا نُعِيدُكُمْ وَمِنْهَا نُخْرِجُكُمْ تَارَةً أُخْرَى}
INSERT INTO [b9617] VALUES (2355,'<span data-type="title" id=toc-2340>٥٦ </span>- {وَلَقَدْ أَرَيْنَاهُ آيَاتِنَا كُلَّهَا}
INSERT INTO [b9617] VALUES (2356,'<span data-type="title" id=toc-2341>٥٧ </span>- {قَالَ أَجِئْتَنَا لِتُخْرِجَنَا مِنْ أَرْضِنَا بِسِحْرِكَ يَا مُوسَى}
INSERT INTO [b9617] VALUES (2357,'<span data-type="title" id=toc-2342>٥٨ </span>- {فَلَنَأْتِيَنَّكَ بِسِحْرٍ مِثْلِهِ فَاجْعَلْ بَيْنَنَا وَبَيْنَكَ مَوْعِدًا لا نُخْلِفُهُ نَحْنُ وَلا أَنْتَ مَكَانًا سُوًى}
INSERT INTO [b9617] VALUES (2358,'<span data-type="title" id=toc-2343>٥٩ </span>- {قَالَ مَوْعِدُكُمْ يَوْمُ الزِّينَةِ وَأَنْ يُحْشَرَ النَّاسُ ضُحًى}
INSERT INTO [b9617] VALUES (2359,'<span data-type="title" id=toc-2344>٦٠ </span>- {فَتَوَلَّى فِرْعَوْنُ}
INSERT INTO [b9617] VALUES (2360,'<span data-type="title" id=toc-2345>٦١ </span>- {قَالَ لَهُمْ مُوسَى وَيْلَكُمْ لا تَفْتَرُوا عَلَى اللَّهِ كَذِبًا فَيُسْحِتَكُمْ بِعَذَابٍ وَقَدْ خَابَ مَنِ افْتَرَى}
INSERT INTO [b9617] VALUES (2361,'<span data-type="title" id=toc-2346>٦٢ </span>- {فَتَنَازَعُوا أَمْرَهُمْ بَيْنَهُمْ}
INSERT INTO [b9617] VALUES (2362,'<span data-type="title" id=toc-2347>٦٣ </span>- {قَالُوا إِنْ هَذَانِ لَسَاحِرَانِ يُرِيدَانِ أَنْ يُخْرِجَاكُمْ مِنْ أَرْضِكُمْ بِسِحْرِهِمَا وَيَذْهَبَا بِطَرِيقَتِكُمُ الْمُثْلَى}
INSERT INTO [b9617] VALUES (2363,'<span data-type="title" id=toc-2348>٦٤ </span>- {فَأَجْمِعُوا كَيْدَكُمْ ثُمَّ ائْتُوا صَفًّا وَقَدْ أَفْلَحَ الْيَوْمَ مَنِ اسْتَعْلَى}
INSERT INTO [b9617] VALUES (2364,'<span data-type="title" id=toc-2349>٦٥ </span>- {قَالُوا يَا مُوسَى إِمَّا أَنْ تُلْقِيَ وَإِمَّا أَنْ نَكُونَ أَوَّلَ مَنْ أَلْقَى}
INSERT INTO [b9617] VALUES (2365,'<span data-type="title" id=toc-2350>٦٦ </span>- {قَالَ بَلْ أَلْقُوا فَإِذَا حِبَالُهُمْ وَعِصِيُّهُمْ يُخَيَّلُ إِلَيْهِ مِنْ سِحْرِهِمْ أَنَّهَا تَسْعَى}
INSERT INTO [b9617] VALUES (2366,'<span data-type="title" id=toc-2351>٦٧ </span>- {فَأَوْجَسَ فِي نَفْسِهِ خِيفَةً مُوسَى}
INSERT INTO [b9617] VALUES (2367,'<span data-type="title" id=toc-2352>٦٨ </span>- {قُلْنَا لا تَخَفْ إِنَّكَ أَنْتَ الأَعْلَى}
INSERT INTO [b9617] VALUES (2368,'<span data-type="title" id=toc-2353>٦٩ </span>- {وَأَلْقِ مَا فِي يَمِينِكَ تَلْقَفْ مَا صَنَعُوا إِنَّمَا صَنَعُوا كَيْدُ سَاحِرٍ وَلا يُفْلِحُ السَّاحِرُ حَيْثُ ⦗٦٩٦⦘ أَتَى}
INSERT INTO [b9617] VALUES (2369,'<span data-type="title" id=toc-2354>٧٠ </span>- {فَأُلْقِيَ السَّحَرَةُ سُجَّدًا قَالُوا آمَنَّا بِرَبِّ هَارُونَ وَمُوسَى}
INSERT INTO [b9617] VALUES (2370,'<span data-type="title" id=toc-2355>٧١ </span>- {قَالَ آمَنْتُمْ لَهُ قَبْلَ أَنْ آذَنَ لَكُمْ إِنَّهُ لَكَبِيرُكُمُ الَّذِي عَلَّمَكُمُ السِّحْرَ فَلأُقَطِّعَنَّ أَيْدِيَكُمْ وَأَرْجُلَكُمْ مِنْ خِلافٍ وَلأُصَلِّبَنَّكُمْ فِي جُذُوعِ النَّخْلِ وَلَتَعْلَمُنَّ أَيُّنَا أَشَدُّ عَذَابًا وَأَبْقَى}
INSERT INTO [b9617] VALUES (2371,'<span data-type="title" id=toc-2356>٧٢ </span>- {قَالُوا لَنْ نُؤْثِرَكَ عَلَى مَا جَاءَنَا مِنَ الْبَيِّنَاتِ وَالَّذِي فَطَرَنَا فَاقْضِ مَا أَنْتَ قَاضٍ إِنَّمَا ⦗٦٩٧⦘ تَقْضِي هَذِهِ الْحَيَاةَ الدُّنْيَا}
INSERT INTO [b9617] VALUES (2372,'<span data-type="title" id=toc-2357>٧٣ </span>- {إِنَّا آمَنَّا بِرَبِّنَا لِيَغْفِرَ لَنَا خَطَايَانَا وَمَا أَكْرَهْتَنَا عَلَيْهِ مِنَ السِّحْرِ وَاللَّهُ خَيْرٌ وَأَبْقَى}
INSERT INTO [b9617] VALUES (2373,'<span data-type="title" id=toc-2358>٧٤ </span>- {إِنَّهُ مَنْ يَأْتِ رَبَّهُ مُجْرِمًا فَإِنَّ لَهُ جَهَنَّمَ لا يَمُوتُ فِيهَا وَلا يَحْيَا}
INSERT INTO [b9617] VALUES (2374,'<span data-type="title" id=toc-2359>٧٥ </span>- {وَمَنْ يَأْتِهِ مُؤْمِنًا قَدْ عَمِلَ الصَّالِحَاتِ فَأُولَئِكَ لَهُمُ الدَّرَجَاتُ}
INSERT INTO [b9617] VALUES (2375,'<span data-type="title" id=toc-2360>٧٦ </span>- {جَنَّاتُ عَدْنٍ تَجْرِي مِنْ تَحْتِهَا الأَنْهَارُ خَالِدِينَ فِيهَا وَذَلِكَ جَزَاءُ مَنْ تَزَكَّى} ⦗٦٩٨⦘
INSERT INTO [b9617] VALUES (2376,'<span data-type="title" id=toc-2361>٧٧ </span>- {وَلَقَدْ أَوْحَيْنَا إِلَى مُوسَى أَنْ أَسْرِ بِعِبَادِي فَاضْرِبْ لَهُمْ طَرِيقًا فِي الْبَحْرِ يَبَسًا لا تَخَافُ دَرَكًا وَلا تَخْشَى}
INSERT INTO [b9617] VALUES (2377,'<span data-type="title" id=toc-2362>٧٨ </span>- {فَأَتْبَعَهُمْ فِرْعَوْنُ بِجُنُودِهِ فَغَشِيَهُمْ مِنَ الْيَمِّ مَا غَشِيَهُمْ}
INSERT INTO [b9617] VALUES (2378,'<span data-type="title" id=toc-2363>٧٩ </span>- {وَأَضَلَّ فِرْعَوْنُ قَوْمَهُ وَمَا هَدَى}
INSERT INTO [b9617] VALUES (2379,'<span data-type="title" id=toc-2364>٨٠ </span>- {يَا بَنِي إِسْرَائِيلَ قَدْ أَنْجَيْنَاكُمْ مِنْ عَدُوِّكُمْ وَوَاعَدْنَاكُمْ جَانِبَ الطُّورِ الأَيْمَنَ}
INSERT INTO [b9617] VALUES (2380,'<span data-type="title" id=toc-2365>٨١ </span>- {كُلُوا مِنْ طَيِّبَاتِ مَا رَزَقْنَاكُمْ وَلا تَطْغَوْا فِيهِ فَيَحِلَّ عَلَيْكُمْ غَضَبِي وَمَنْ يَحْلِلْ عَلَيْهِ غَضَبِي فَقَدْ هَوَى} ⦗٦٩٩⦘
INSERT INTO [b9617] VALUES (2381,'<span data-type="title" id=toc-2366>٨٢ </span>- {وَإِنِّي لَغَفَّارٌ لِمَنْ تَابَ وَآمَنَ}
INSERT INTO [b9617] VALUES (2382,'<span data-type="title" id=toc-2367>٨٣ </span>- {وَمَا أَعْجَلَكَ عَنْ قَوْمِكَ يَا مُوسَى}
INSERT INTO [b9617] VALUES (2383,'<span data-type="title" id=toc-2368>٨٤ </span>- {قَالَ هُمْ أُولاءِ عَلَى أَثَرِي وَعَجِلْتُ إِلَيْكَ رَبِّ لِتَرْضَى}
INSERT INTO [b9617] VALUES (2384,'<span data-type="title" id=toc-2369>٨٥ </span>- {قَالَ فَإِنَّا قَدْ فَتَنَّا قَوْمَكَ مِنْ بَعْدِكَ وَأَضَلَّهُمُ السَّامِرِيُّ}
INSERT INTO [b9617] VALUES (2385,'<span data-type="title" id=toc-2370>٨٦ </span>- {فَرَجَعَ مُوسَى إِلَى قَوْمِهِ غَضْبَانَ أَسِفًا قَالَ يَا قَوْمِ أَلَمْ يَعِدْكُمْ رَبُّكُمْ وَعْدًا حَسَنًا ⦗٧٠٠⦘ أَفَطَالَ عَلَيْكُمُ الْعَهْدُ أَمْ أَرَدْتُمْ أَنْ يَحِلَّ عَلَيْكُمْ غَضَبٌ مِنْ رَبِّكُمْ فَأَخْلَفْتُمْ مَوْعِدِي}
INSERT INTO [b9617] VALUES (2386,'<span data-type="title" id=toc-2371>٨٧ </span>- {قَالُوا مَا أَخْلَفْنَا مَوْعِدَكَ بِمَلْكِنَا وَلَكِنَّا حُمِّلْنَا أَوْزَارًا مِنْ زِينَةِ الْقَوْمِ فَقَذَفْنَاهَا فَكَذَلِكَ أَلْقَى السَّامِرِيُّ}
INSERT INTO [b9617] VALUES (2387,'<span data-type="title" id=toc-2372>٨٨ </span>- {فَأَخْرَجَ لَهُمْ عِجْلا جَسَدًا لَهُ خُوَارٌ فَقَالُوا هَذَا إِلَهُكُمْ وَإِلَهُ مُوسَى فَنَسِيَ}
INSERT INTO [b9617] VALUES (2388,'<span data-type="title" id=toc-2373>٨٩ </span>- {أَفَلا يَرَوْنَ أَلا يَرْجِعُ إِلَيْهِمْ قَوْلا وَلا يَمْلِكُ لَهُمْ ضَرًّا وَلا نَفْعًا}
INSERT INTO [b9617] VALUES (2389,'<span data-type="title" id=toc-2374>٩٠ </span>- {وَلَقَدْ قَالَ لَهُمْ هَارُونُ مِنْ قَبْلُ يَا قَوْمِ إِنَّمَا فُتِنْتُمْ بِهِ وَإِنَّ رَبَّكُمُ الرَّحْمَنُ فَاتَّبِعُونِي وَأَطِيعُوا أَمْرِي}
INSERT INTO [b9617] VALUES (2390,'<span data-type="title" id=toc-2375>٩١ </span>- {قَالُوا لَنْ نَبْرَحَ عَلَيْهِ عَاكِفِينَ حَتَّى يَرْجِعَ إِلَيْنَا مُوسَى}
INSERT INTO [b9617] VALUES (2391,'<span data-type="title" id=toc-2376>٩٢ </span>- {قَالَ يَا هَارُونُ مَا مَنَعَكَ إِذْ رَأَيْتَهُمْ ضَلُّوا}
INSERT INTO [b9617] VALUES (2392,'<span data-type="title" id=toc-2377>٩٣ </span>- {أَلا تَتَّبِعَنِ أَفَعَصَيْتَ أَمْرِي}
INSERT INTO [b9617] VALUES (2393,'<span data-type="title" id=toc-2378>٩٤ </span>- {قَالَ يَا ابْنَ أُمَّ لا تَأْخُذْ بِلِحْيَتِي وَلا بِرَأْسِي إِنِّي خَشِيتُ أَنْ تَقُولَ فَرَّقْتَ بَيْنَ بَنِي إِسْرَائِيلَ}
INSERT INTO [b9617] VALUES (2394,'<span data-type="title" id=toc-2379>٩٥ </span>- {قَالَ فَمَا خَطْبُكَ يَا سَامِرِيُّ}
INSERT INTO [b9617] VALUES (2395,'<span data-type="title" id=toc-2380>٩٦ </span>- {قَالَ بَصُرْتُ بِمَا لَمْ يَبْصُرُوا بِهِ فَقَبَضْتُ قَبْضَةً مِنْ أَثَرِ الرَّسُولِ فَنَبَذْتُهَا وَكَذَلِكَ سَوَّلَتْ لِي نَفْسِي}
INSERT INTO [b9617] VALUES (2396,'<span data-type="title" id=toc-2381>٩٧ </span>- {قَالَ فَاذْهَبْ فَإِنَّ لَكَ فِي الْحَيَاةِ أَنْ تَقُولَ لا مِسَاسَ وَإِنَّ لَكَ مَوْعِدًا لَنْ تُخْلَفَهُ وَانْظُرْ إِلَى إِلَهِكَ الَّذِي ظَلْتَ عَلَيْهِ عَاكِفًا لَنُحَرِّقَنَّهُ}
INSERT INTO [b9617] VALUES (2397,'<span data-type="title" id=toc-2382>٩٨ </span>- {إِنَّمَا إِلَهُكُمُ اللَّهُ الَّذِي لا إِلَهَ إِلا هُوَ وَسِعَ كُلَّ شَيْءٍ عِلْمًا}
INSERT INTO [b9617] VALUES (2398,'<span data-type="title" id=toc-2383>٩٩ </span>- {كَذَلِكَ نَقُصُّ عَلَيْكَ مِنْ أَنْبَاءِ مَا قَدْ سَبَقَ وَقَدْ آتَيْنَاكَ مِنْ لَدُنَّا ذِكْرًا}
INSERT INTO [b9617] VALUES (2399,'<span data-type="title" id=toc-2384>١٠٠ </span>- {مَنْ أَعْرَضَ عَنْهُ فَإِنَّهُ يَحْمِلُ يَوْمَ الْقِيَامَةِ وِزْرًا}
INSERT INTO [b9617] VALUES (2400,'<span data-type="title" id=toc-2385>١٠١ </span>- {خَالِدِينَ فِيهِ وَسَاءَ لَهُمْ يَوْمَ الْقِيَامَةِ حِمْلا}
INSERT INTO [b9617] VALUES (2401,'<span data-type="title" id=toc-2386>١٠٢ </span>- {يَوْمَ يُنْفَخُ فِي الصُّورِ وَنَحْشُرُ الْمُجْرِمِينَ يَوْمَئِذٍ زُرْقًا}
INSERT INTO [b9617] VALUES (2402,'<span data-type="title" id=toc-2387>١٠٣ </span>- {يَتَخَافَتُونَ بَيْنَهُمْ إِنْ لَبِثْتُمْ إِلا عَشْرًا}
INSERT INTO [b9617] VALUES (2403,'<span data-type="title" id=toc-2388>١٠٤ </span>- {نَحْنُ أَعْلَمُ بِمَا يَقُولُونَ إِذْ يَقُولُ أَمْثَلُهُمْ طَرِيقَةً إِنْ لَبِثْتُمْ إِلا يَوْمًا}
INSERT INTO [b9617] VALUES (2404,'<span data-type="title" id=toc-2389>١٠٥ </span>- {وَيَسْأَلُونَكَ عَنِ الْجِبَالِ فَقُلْ يَنْسِفُهَا رَبِّي نَسْفًا}
INSERT INTO [b9617] VALUES (2405,'<span data-type="title" id=toc-2390>١٠٦ </span>- {فَيَذَرُهَا قَاعًا صَفْصَفًا} ⦗٧٠٥⦘
INSERT INTO [b9617] VALUES (2406,'<span data-type="title" id=toc-2391>١٠٧ </span>- {لا تَرَى فِيهَا عِوَجًا وَلا أَمْتًا}
INSERT INTO [b9617] VALUES (2407,'<span data-type="title" id=toc-2392>١٠٨ </span>- {يَوْمَئِذٍ يَتَّبِعُونَ الدَّاعِيَ لا عِوَجَ لَهُ وَخَشَعَتِ الأَصْوَاتُ لِلرَّحْمَنِ فَلا تَسْمَعُ إِلا هَمْسًا}
INSERT INTO [b9617] VALUES (2408,'<span data-type="title" id=toc-2393>١٠٩ </span>- {يَوْمَئِذٍ لا تَنْفَعُ الشَّفَاعَةُ إِلا مَنْ أَذِنَ لَهُ الرَّحْمَنُ}
INSERT INTO [b9617] VALUES (2409,'<span data-type="title" id=toc-2394>١١٠ </span>- {يَعْلَمُ مَا بَيْنَ أَيْدِيهِمْ وَمَا خَلْفَهُمْ وَلا يُحِيطُونَ بِهِ عِلْمًا}
INSERT INTO [b9617] VALUES (2410,'<span data-type="title" id=toc-2395>١١١ </span>- {وَعَنَتِ الْوُجُوهُ لِلْحَيِّ الْقَيُّومِ وَقَدْ خَابَ مَنْ حَمَلَ ظُلْمًا}
INSERT INTO [b9617] VALUES (2411,'<span data-type="title" id=toc-2396>١١٢ </span>- {وَمَنْ يَعْمَلْ مِنَ الصَّالِحَاتِ وَهُوَ مُؤْمِنٌ فَلا يَخَافُ ظُلْمًا وَلا هَضْمًا} ⦗٧٠٦⦘
INSERT INTO [b9617] VALUES (2412,'<span data-type="title" id=toc-2397>١١٣ </span>- {وَكَذَلِكَ أَنْزَلْنَاهُ قُرْآنًا عَرَبِيًّا وَصَرَّفْنَا فِيهِ مِنَ الْوَعِيدِ لَعَلَّهُمْ يَتَّقُونَ أَوْ يُحْدِثُ لَهُمْ ذِكْرًا}
INSERT INTO [b9617] VALUES (2413,'<span data-type="title" id=toc-2398>١١٤ </span>- {فَتَعَالَى اللَّهُ الْمَلِكُ الْحَقُّ وَلا تَعْجَلْ بِالْقُرْآنِ مِنْ قَبْلِ أَنْ يُقْضَى إِلَيْكَ وَحْيُهُ وَقُلْ رَبِّ زِدْنِي عِلْمًا}
INSERT INTO [b9617] VALUES (2414,'<span data-type="title" id=toc-2399>١١٥ </span>- {وَلَقَدْ عَهِدْنَا إِلَى آدَمَ مِنْ قَبْلُ فَنَسِيَ وَلَمْ نَجِدْ لَهُ عَزْمًا}
INSERT INTO [b9617] VALUES (2415,'<span data-type="title" id=toc-2400>١١٦ </span>- {وَإِذْ قُلْنَا لِلْمَلائِكَةِ اسْجُدُوا لآدَمَ فَسَجَدُوا إِلا إِبْلِيسَ أَبَى}
INSERT INTO [b9617] VALUES (2416,'<span data-type="title" id=toc-2401>١١٧ </span>- {فَقُلْنَا يَا آدَمُ إِنَّ هَذَا عَدُوٌّ لَكَ وَلِزَوْجِكَ فَلا يُخْرِجَنَّكُمَا مِنَ الْجَنَّةِ فَتَشْقَى}
INSERT INTO [b9617] VALUES (2417,'<span data-type="title" id=toc-2402>١١٨ </span>- {إِنَّ لَكَ أَلا تَجُوعَ فِيهَا وَلا تَعْرَى}
INSERT INTO [b9617] VALUES (2418,'<span data-type="title" id=toc-2403>١١٩ </span>- {وَأَنَّكَ لا تَظْمَأُ فِيهَا وَلا تَضْحَى}
INSERT INTO [b9617] VALUES (2419,'<span data-type="title" id=toc-2404>١٢٠ </span>- {فَوَسْوَسَ إِلَيْهِ الشَّيْطَانُ قَالَ يَا آدَمُ هَلْ أَدُلُّكَ عَلَى شَجَرَةِ الْخُلْدِ وَمُلْكٍ لا يَبْلَى}
INSERT INTO [b9617] VALUES (2420,'<span data-type="title" id=toc-2405>١٢١ </span>- {وَطَفِقَا يَخْصِفَانِ عَلَيْهِمَا مِنْ وَرَقِ الْجَنَّةِ ⦗٧٠٨⦘ وَعَصَى آدَمُ رَبَّهُ فَغَوَى}
INSERT INTO [b9617] VALUES (2421,'<span data-type="title" id=toc-2406>١٢٢ </span>- {ثُمَّ اجْتَبَاهُ رَبُّهُ فَتَابَ عَلَيْهِ}
INSERT INTO [b9617] VALUES (2422,'<span data-type="title" id=toc-2407>١٢٣ </span>- {قَالَ اهْبِطَا مِنْهَا جَمِيعًا بَعْضُكُمْ لِبَعْضٍ عَدُوٌّ فَإِمَّا يَأْتِيَنَّكُمْ مِنِّي هُدًى فَمَنِ اتَّبَعَ هُدَايَ فَلا يَضِلُّ وَلا يَشْقَى}
INSERT INTO [b9617] VALUES (2423,'<span data-type="title" id=toc-2408>١٢٤ </span>- {وَمَنْ أَعْرَضَ عَنْ ذِكْرِي فَإِنَّ لَهُ مَعِيشَةً ضَنْكًا وَنَحْشُرُهُ يَوْمَ الْقِيَامَةِ أَعْمَى}
INSERT INTO [b9617] VALUES (2424,'<span data-type="title" id=toc-2409>١٢٥ </span>- {قَالَ رَبِّ لِمَ حَشَرْتَنِي أَعْمَى وَقَدْ كُنْتُ بَصِيرًا}
INSERT INTO [b9617] VALUES (2425,'<span data-type="title" id=toc-2410>١٢٦ </span>- {قَالَ كَذَلِكَ أَتَتْكَ آيَاتُنَا فَنَسِيتَهَا وَكَذَلِكَ الْيَوْمَ تُنْسَى}
INSERT INTO [b9617] VALUES (2426,'<span data-type="title" id=toc-2411>١٢٧ </span>- {وَكَذَلِكَ نَجْزِي مَنْ أَسْرَفَ وَلَمْ يُؤْمِنْ بِآيَاتِ رَبِّهِ وَلَعَذَابُ الآخِرَةِ أَشَدُّ وَأَبْقَى}
INSERT INTO [b9617] VALUES (2427,'<span data-type="title" id=toc-2412>١٢٨ </span>- {أَفَلَمْ يَهْدِ لَهُمْ كَمْ أَهْلَكْنَا قَبْلَهُمْ مِنَ الْقُرُونِ يَمْشُونَ فِي مَسَاكِنِهِمْ إِنَّ فِي ذَلِكَ لآيَاتٍ لأُولِي النُّهَى}
INSERT INTO [b9617] VALUES (2428,'<span data-type="title" id=toc-2413>١٢٩ </span>- {وَلَوْلا كَلِمَةٌ سَبَقَتْ مِنْ رَبِّكَ لَكَانَ لِزَامًا وَأَجَلٌ مُسَمًّى}
INSERT INTO [b9617] VALUES (2429,'<span data-type="title" id=toc-2414>١٣٠ </span>- {فَاصْبِرْ عَلَى مَا يَقُولُونَ وَسَبِّحْ بِحَمْدِ رَبِّكَ قَبْلَ طُلُوعِ الشَّمْسِ وَقَبْلَ غُرُوبِهَا وَمِنْ آنَاءِ ⦗٧١٠⦘ اللَّيْلِ فَسَبِّحْ وَأَطْرَافَ النَّهَارِ لَعَلَّكَ تَرْضَى}
INSERT INTO [b9617] VALUES (2430,'<span data-type="title" id=toc-2415>١٣١ </span>- {وَلا تَمُدَّنَّ عَيْنَيْكَ إِلَى مَا مَتَّعْنَا بِهِ أَزْوَاجًا مِنْهُمْ زَهْرَةَ الْحَيَاةِ الدُّنْيَا لِنَفْتِنَهُمْ فِيهِ وَرِزْقُ رَبِّكَ خَيْرٌ وَأَبْقَى}
INSERT INTO [b9617] VALUES (2431,'<span data-type="title" id=toc-2416>١٣٢ </span>- {وَأْمُرْ أَهْلَكَ بِالصَّلاةِ وَاصْطَبِرْ عَلَيْهَا لا نَسْأَلُكَ رِزْقًا نَحْنُ نَرْزُقُكَ وَالْعَاقِبَةُ لِلتَّقْوَى}
INSERT INTO [b9617] VALUES (2432,'<span data-type="title" id=toc-2417>١٣٣ </span>- {وَقَالُوا لَوْلا يَأْتِينَا بِآيَةٍ مِنْ رَبِّهِ أَوَلَمْ تَأْتِهِمْ بَيِّنَةُ مَا فِي الصُّحُفِ الأُولَى}
INSERT INTO [b9617] VALUES (2433,'<span data-type="title" id=toc-2418>١٣٤ </span>- {وَلَوْ أَنَّا أَهْلَكْنَاهُمْ بِعَذَابٍ مِنْ قَبْلِهِ لَقَالُوا رَبَّنَا لَوْلا أَرْسَلْتَ إِلَيْنَا رَسُولا فَنَتَّبِعَ آيَاتِكَ مِنْ قَبْلِ أَنْ نَذِلَّ وَنَخْزَى}
INSERT INTO [b9617] VALUES (2434,'<span data-type="title" id=toc-2419>١٣٥ </span>- {قُلْ كُلٌّ مُتَرَبِّصٌ فَتَرَبَّصُوا فَسَتَعْلَمُونَ مَنْ أَصْحَابُ الصِّرَاطِ السَّوِيِّ وَمَنِ اهْتَدَى}
INSERT INTO [b9617] VALUES (2435,'<span data-type="title" id=toc-2420>سورة الأنبياء</span>','2','712',NULL,NULL,'0');
INSERT INTO [b9617] VALUES (2436,'<span data-type="title" id=toc-2421>١ </span>- {اقْتَرَبَ لِلنَّاسِ حِسَابُهُمْ وَهُمْ فِي غَفْلَةٍ مُعْرِضُونَ}
INSERT INTO [b9617] VALUES (2437,'<span data-type="title" id=toc-2422>٢ </span>- {مَا يَأْتِيهِمْ مِنْ ذِكْرٍ مِنْ رَبِّهِمْ مُحْدَثٍ إِلا اسْتَمَعُوهُ وَهُمْ يَلْعَبُونَ}
INSERT INTO [b9617] VALUES (2438,'<span data-type="title" id=toc-2423>٣ </span>- {لاهِيَةً قُلُوبُهُمْ وَأَسَرُّوا النَّجْوَى الَّذِينَ ظَلَمُوا هَلْ هَذَا إِلا بَشَرٌ مِثْلُكُمْ أَفَتَأْتُونَ السِّحْرَ وَأَنْتُمْ تُبْصِرُونَ}
INSERT INTO [b9617] VALUES (2439,'<span data-type="title" id=toc-2424>٤ </span>- {قَالَ رَبِّي يَعْلَمُ الْقَوْلَ فِي السَّمَاءِ وَالأَرْضِ وَهُوَ السَّمِيعُ الْعَلِيمُ} ⦗٧١٣⦘
INSERT INTO [b9617] VALUES (2440,'<span data-type="title" id=toc-2425>٥ </span>- {بَلْ قَالُوا أَضْغَاثُ أَحْلامٍ بَلِ افْتَرَاهُ بَلْ هُوَ شَاعِرٌ فَلْيَأْتِنَا بِآيَةٍ كَمَا أُرْسِلَ الأَوَّلُونَ}
INSERT INTO [b9617] VALUES (2441,'<span data-type="title" id=toc-2426>٦ </span>- {مَا آمَنَتْ قَبْلَهُمْ مِنْ قَرْيَةٍ أَهْلَكْنَاهَا أَفَهُمْ يُؤْمِنُونَ}
INSERT INTO [b9617] VALUES (2442,'<span data-type="title" id=toc-2427>٧ </span>- {وَمَا أَرْسَلْنَا قَبْلَكَ إِلا رِجَالا نُوحِي إِلَيْهِمْ فَاسْأَلُوا أَهْلَ الذِّكْرِ إِنْ كُنْتُمْ لا تَعْلَمُونَ}
INSERT INTO [b9617] VALUES (2443,'<span data-type="title" id=toc-2428>٨ </span>- {وَمَا جَعَلْنَاهُمْ جَسَدًا لا يَأْكُلُونَ الطَّعَامَ وَمَا كَانُوا خَالِدِينَ}
INSERT INTO [b9617] VALUES (2444,'<span data-type="title" id=toc-2429>٩ </span>- {ثُمَّ صَدَقْنَاهُمُ الْوَعْدَ فَأَنْجَيْنَاهُمْ وَمَنْ نَشَاءُ وَأَهْلَكْنَا الْمُسْرِفِينَ}
INSERT INTO [b9617] VALUES (2445,'<span data-type="title" id=toc-2430>١٠ </span>- {لَقَدْ أَنْزَلْنَا إِلَيْكُمْ كِتَابًا فِيهِ ذِكْرُكُمْ أَفَلا تَعْقِلُونَ}
INSERT INTO [b9617] VALUES (2446,'<span data-type="title" id=toc-2431>١١ </span>- {وَكَمْ قَصَمْنَا مِنْ قَرْيَةٍ كَانَتْ ظَالِمَةً وَأَنْشَأْنَا بَعْدَهَا قَوْمًا آخَرِينَ}
INSERT INTO [b9617] VALUES (2447,'<span data-type="title" id=toc-2432>١٢ </span>- {فَلَمَّا أَحَسُّوا بَأْسَنَا إِذَا هُمْ مِنْهَا يَرْكُضُونَ}
INSERT INTO [b9617] VALUES (2448,'<span data-type="title" id=toc-2433>١٣ </span>- {لا تَرْكُضُوا وَارْجِعُوا إِلَى مَا أُتْرِفْتُمْ فِيهِ وَمَسَاكِنِكُمْ لَعَلَّكُمْ تُسْأَلُونَ}
INSERT INTO [b9617] VALUES (2449,'<span data-type="title" id=toc-2434>١٤ </span>- {قَالُوا يَا وَيْلَنَا إِنَّا كُنَّا ظَالِمِينَ}
INSERT INTO [b9617] VALUES (2450,'<span data-type="title" id=toc-2435>١٥ </span>- {فَمَا زَالَتْ تِلْكَ دَعْوَاهُمْ حَتَّى جَعَلْنَاهُمْ حَصِيدًا خَامِدِينَ}
INSERT INTO [b9617] VALUES (2451,'<span data-type="title" id=toc-2436>١٦ </span>- {وَمَا خَلَقْنَا السَّمَاءَ وَالأَرْضَ وَمَا بَيْنَهُمَا لاعِبِينَ}
INSERT INTO [b9617] VALUES (2452,'<span data-type="title" id=toc-2437>١٧ </span>- {لَوْ أَرَدْنَا أَنْ نَتَّخِذَ لَهْوًا لاتَّخَذْنَاهُ مِنْ لَدُنَّا إِنْ كُنَّا فَاعِلِينَ}
INSERT INTO [b9617] VALUES (2453,'<span data-type="title" id=toc-2438>١٨ </span>- {بَلْ نَقْذِفُ بِالْحَقِّ عَلَى الْبَاطِلِ فَيَدْمَغُهُ فَإِذَا هُوَ زَاهِقٌ وَلَكُمُ الْوَيْلُ مِمَّا تَصِفُونَ}
INSERT INTO [b9617] VALUES (2454,'<span data-type="title" id=toc-2439>١٩ </span>- {وَلَهُ مَنْ فِي السَّمَاوَاتِ وَالأَرْضِ وَمَنْ عِنْدَهُ لا يَسْتَكْبِرُونَ عَنْ عِبَادَتِهِ}
INSERT INTO [b9617] VALUES (2455,'<span data-type="title" id=toc-2440>٢٠ </span>- {يُسَبِّحُونَ اللَّيْلَ وَالنَّهَارَ لا يَفْتُرُونَ}
INSERT INTO [b9617] VALUES (2456,'<span data-type="title" id=toc-2441>٢١ </span>- {أَمِ اتَّخَذُوا آلِهَةً مِنَ الأَرْضِ هُمْ يُنْشِرُونَ}
INSERT INTO [b9617] VALUES (2457,'<span data-type="title" id=toc-2442>٢٢ </span>- {لَوْ كَانَ فِيهِمَا آلِهَةٌ إِلا اللَّهُ لَفَسَدَتَا فَسُبْحَانَ اللَّهِ رَبِّ الْعَرْشِ عَمَّا يَصِفُونَ}
INSERT INTO [b9617] VALUES (2458,'<span data-type="title" id=toc-2443>٢٣ </span>- {لا يُسْأَلُ عَمَّا يَفْعَلُ وَهُمْ يُسْأَلُونَ}
INSERT INTO [b9617] VALUES (2459,'<span data-type="title" id=toc-2444>٢٤ </span>- {أَمِ اتَّخَذُوا مِنْ دُونِهِ آلِهَةً قُلْ هَاتُوا بُرْهَانَكُمْ هَذَا ذِكْرُ مَنْ مَعِيَ وَذِكْرُ مَنْ قَبْلِي بَلْ أَكْثَرُهُمْ لا يَعْلَمُونَ الْحَقَّ فَهُمْ مُعْرِضُونَ}
INSERT INTO [b9617] VALUES (2460,'<span data-type="title" id=toc-2445>٢٥ </span>- {وَمَا أَرْسَلْنَا مِنْ قَبْلِكَ مِنْ رَسُولٍ إِلا نُوحِي إِلَيْهِ أَنَّهُ لا إِلَهَ إِلا أَنَا فَاعْبُدُونِ}
INSERT INTO [b9617] VALUES (2461,'<span data-type="title" id=toc-2446>٢٦ </span>- {وَقَالُوا اتَّخَذَ الرَّحْمَنُ وَلَدًا سُبْحَانَهُ بَلْ عِبَادٌ مُكْرَمُونَ}
INSERT INTO [b9617] VALUES (2462,'<span data-type="title" id=toc-2447>٢٧ </span>- {لا يَسْبِقُونَهُ بِالْقَوْلِ وَهُمْ بِأَمْرِهِ يَعْمَلُونَ}
INSERT INTO [b9617] VALUES (2463,'<span data-type="title" id=toc-2448>٢٨ </span>- {يَعْلَمُ مَا بَيْنَ أَيْدِيهِمْ وَمَا خَلْفَهُمْ وَلا يَشْفَعُونَ إِلا لِمَنِ ارْتَضَى وَهُمْ مِنْ خَشْيَتِهِ مُشْفِقُونَ}
INSERT INTO [b9617] VALUES (2464,'<span data-type="title" id=toc-2449>٢٩ </span>- {وَمَنْ يَقُلْ مِنْهُمْ إِنِّي إِلَهٌ مِنْ دُونِهِ فَذَلِكَ نَجْزِيهِ جَهَنَّمَ كَذَلِكَ نَجْزِي الظَّالِمِينَ}
INSERT INTO [b9617] VALUES (2465,'<span data-type="title" id=toc-2450>٣٠ </span>- {أَوَلَمْ يَرَ الَّذِينَ كَفَرُوا أَنَّ السَّمَاوَاتِ وَالأَرْضَ كَانَتَا رَتْقًا فَفَتَقْنَاهُمَا وَجَعَلْنَا مِنَ الْمَاءِ كُلَّ شَيْءٍ حَيٍّ أَفَلا يُؤْمِنُونَ}
INSERT INTO [b9617] VALUES (2466,'<span data-type="title" id=toc-2451>٣١ </span>- {وَجَعَلْنَا فِي الأَرْضِ رَوَاسِيَ أَنْ تَمِيدَ بِهِمْ وَجَعَلْنَا فِيهَا فِجَاجًا سُبُلا لَعَلَّهُمْ يَهْتَدُونَ}
INSERT INTO [b9617] VALUES (2467,'<span data-type="title" id=toc-2452>٣٢ </span>- {وَهُمْ عَنْ آيَاتِهَا مُعْرِضُونَ}
INSERT INTO [b9617] VALUES (2468,'<span data-type="title" id=toc-2453>٣٣ </span>- {وَهُوَ الَّذِي خَلَقَ اللَّيْلَ وَالنَّهَارَ وَالشَّمْسَ وَالْقَمَرَ كُلٌّ فِي فَلَكٍ يَسْبَحُونَ}
INSERT INTO [b9617] VALUES (2469,'<span data-type="title" id=toc-2454>٣٤ </span>- {وَمَا جَعَلْنَا لِبَشَرٍ مِنْ قَبْلِكَ الْخُلْدَ أَفَإِنْ مِتَّ فَهُمُ الْخَالِدُونَ}
INSERT INTO [b9617] VALUES (2470,'<span data-type="title" id=toc-2455>٣٥ </span>- {كُلُّ نَفْسٍ ذَائِقَةُ الْمَوْتِ وَنَبْلُوكُمْ بِالشَّرِّ وَالْخَيْرِ فِتْنَةً وَإِلَيْنَا تُرْجَعُونَ}
INSERT INTO [b9617] VALUES (2471,'<span data-type="title" id=toc-2456>٣٦ </span>- {وَإِذَا رَآكَ الَّذِينَ كَفَرُوا إِنْ يَتَّخِذُونَكَ إِلا هُزُوًا أَهَذَا الَّذِي يَذْكُرُ آلِهَتَكُمْ وَهُمْ بِذِكْرِ الرَّحْمَنِ هُمْ كَافِرُونَ}
INSERT INTO [b9617] VALUES (2472,'<span data-type="title" id=toc-2457>٣٧ </span>- {خُلِقَ الإِنْسَانُ مِنْ عَجَلٍ سَأُرِيكُمْ آيَاتِي فَلا تَسْتَعْجِلُونِ}
INSERT INTO [b9617] VALUES (2473,'<span data-type="title" id=toc-2458>٣٨ </span>- {وَيَقُولُونَ مَتَى هَذَا الْوَعْدُ إِنْ كُنْتُمْ صَادِقِينَ}
INSERT INTO [b9617] VALUES (2474,'<span data-type="title" id=toc-2459>٣٩ </span>- {لَوْ يَعْلَمُ الَّذِينَ كَفَرُوا حِينَ لا يَكُفُّونَ عَنْ وُجُوهِهِمُ النَّارَ وَلا عَنْ ظُهُورِهِمْ وَلا هُمْ يُنْصَرُونَ}
INSERT INTO [b9617] VALUES (2475,'<span data-type="title" id=toc-2460>٤٠ </span>- {بَلْ تَأْتِيهِمْ بَغْتَةً فَتَبْهَتُهُمْ فَلا يَسْتَطِيعُونَ رَدَّهَا وَلا هُمْ يُنْظَرُونَ}
INSERT INTO [b9617] VALUES (2476,'<span data-type="title" id=toc-2461>٤١ </span>- {وَلَقَدِ اسْتُهْزِئَ بِرُسُلٍ مِنْ قَبْلِكَ فَحَاقَ بِالَّذِينَ سَخِرُوا مِنْهُمْ مَا كَانُوا بِهِ يَسْتَهْزِئُونَ}
INSERT INTO [b9617] VALUES (2477,'<span data-type="title" id=toc-2462>٤٢ </span>- {قُلْ مَنْ يَكْلَؤُكُمْ بِاللَّيْلِ وَالنَّهَارِ مِنَ الرَّحْمَنِ بَلْ هُمْ عَنْ ذِكْرِ رَبِّهِمْ مُعْرِضُونَ}
INSERT INTO [b9617] VALUES (2478,'<span data-type="title" id=toc-2463>٤٣ </span>- {أَمْ لَهُمْ آلِهَةٌ تَمْنَعُهُمْ مِنْ دُونِنَا لا يَسْتَطِيعُونَ نَصْرَ أَنْفُسِهِمْ وَلا هُمْ مِنَّا يُصْحَبُونَ}
INSERT INTO [b9617] VALUES (2479,'<span data-type="title" id=toc-2464>٤٤ </span>- {بَلْ مَتَّعْنَا هَؤُلاءِ وَآبَاءَهُمْ حَتَّى طَالَ عَلَيْهِمُ الْعُمُرُ أَفَلا يَرَوْنَ أَنَّا نَأْتِي الأَرْضَ نَنْقُصُهَا مِنْ أَطْرَافِهَا أَفَهُمُ الْغَالِبُونَ}
INSERT INTO [b9617] VALUES (2480,'<span data-type="title" id=toc-2465>٤٥ </span>- {وَلا يَسْمَعُ الصُّمُّ الدُّعَاءَ إِذَا مَا يُنْذَرُونَ}
INSERT INTO [b9617] VALUES (2481,'<span data-type="title" id=toc-2466>٤٦ </span>- {وَلَئِنْ مَسَّتْهُمْ نَفْحَةٌ مِنْ عَذَابِ رَبِّكَ لَيَقُولُنَّ يَا وَيْلَنَا إِنَّا كُنَّا ظَالِمِينَ}
INSERT INTO [b9617] VALUES (2482,'<span data-type="title" id=toc-2467>٤٧ </span>- {وَنَضَعُ الْمَوَازِينَ الْقِسْطَ لِيَوْمِ الْقِيَامَةِ فَلا تُظْلَمُ نَفْسٌ شَيْئًا وَإِنْ كَانَ مِثْقَالَ حَبَّةٍ مِنْ خَرْدَلٍ أَتَيْنَا بِهَا وَكَفَى بِنَا حَاسِبِينَ}
INSERT INTO [b9617] VALUES (2483,'<span data-type="title" id=toc-2468>٤٨ </span>- {وَلَقَدْ آتَيْنَا مُوسَى وَهَارُونَ الْفُرْقَانَ وَضِيَاءً وَذِكْرًا لِلْمُتَّقِينَ}
INSERT INTO [b9617] VALUES (2484,'<span data-type="title" id=toc-2469>٤٩ </span>- {الَّذِينَ يَخْشَوْنَ رَبَّهُمْ بِالْغَيْبِ وَهُمْ مِنَ السَّاعَةِ مُشْفِقُونَ}
INSERT INTO [b9617] VALUES (2485,'<span data-type="title" id=toc-2470>٥٠ </span>- {وَهَذَا ذِكْرٌ مُبَارَكٌ أَنْزَلْنَاهُ أَفَأَنْتُمْ لَهُ مُنْكِرُونَ}
INSERT INTO [b9617] VALUES (2486,'<span data-type="title" id=toc-2471>٥١ </span>- {وَلَقَدْ آتَيْنَا إِبْرَاهِيمَ رُشْدَهُ مِنْ قَبْلُ وَكُنَّا بِهِ عَالِمِينَ}
INSERT INTO [b9617] VALUES (2487,'<span data-type="title" id=toc-2472>٥٢ </span>- {إِذْ قَالَ لأَبِيهِ وَقَوْمِهِ مَا هَذِهِ التَّمَاثِيلُ الَّتِي أَنْتُمْ لَهَا عَاكِفُونَ}
INSERT INTO [b9617] VALUES (2488,'<span data-type="title" id=toc-2473>٥٣ </span>- {قَالُوا وَجَدْنَا آبَاءَنَا لَهَا عَابِدِينَ}
INSERT INTO [b9617] VALUES (2489,'<span data-type="title" id=toc-2474>٥٤ </span>- {قَالَ لَقَدْ كُنْتُمْ أَنْتُمْ وَآبَاؤُكُمْ فِي ضَلالٍ مُبِينٍ}
INSERT INTO [b9617] VALUES (2490,'<span data-type="title" id=toc-2475>٥٥ </span>- {قَالُوا أَجِئْتَنَا بِالْحَقِّ أَمْ أَنْتَ مِنَ اللاعِبِينَ}
INSERT INTO [b9617] VALUES (2491,'<span data-type="title" id=toc-2476>٥٦ </span>- {قَالَ بَلْ رَبُّكُمْ رَبُّ السَّمَاوَاتِ وَالأَرْضِ الَّذِي فَطَرَهُنَّ}
INSERT INTO [b9617] VALUES (2492,'<span data-type="title" id=toc-2477>٥٧ </span>- {وَتَاللَّهِ لأَكِيدَنَّ أَصْنَامَكُمْ بَعْدَ أَنْ تُوَلُّوا مُدْبِرِينَ}
INSERT INTO [b9617] VALUES (2493,'<span data-type="title" id=toc-2478>٥٨ </span>- {فَجَعَلَهُمْ جُذَاذًا إِلا كَبِيرًا لَهُمْ لَعَلَّهُمْ إِلَيْهِ يَرْجِعُونَ}
INSERT INTO [b9617] VALUES (2494,'<span data-type="title" id=toc-2479>٥٩ </span>- {قَالُوا مَنْ فَعَلَ هَذَا بِآلِهَتِنَا إِنَّهُ لَمِنَ الظَّالِمِينَ}
INSERT INTO [b9617] VALUES (2495,'<span data-type="title" id=toc-2480>٦٠ </span>- {قَالُوا سَمِعْنَا فَتًى يَذْكُرُهُمْ يُقَالُ لَهُ إِبْرَاهِيمُ}
INSERT INTO [b9617] VALUES (2496,'<span data-type="title" id=toc-2481>٦١ </span>- {قَالُوا فَأْتُوا بِهِ عَلَى أَعْيُنِ النَّاسِ لَعَلَّهُمْ يَشْهَدُونَ}
INSERT INTO [b9617] VALUES (2497,'<span data-type="title" id=toc-2482>٦٢ </span>- {قَالُوا أَأَنْتَ فَعَلْتَ هَذَا بِآلِهَتِنَا يَا إِبْرَاهِيمُ}
INSERT INTO [b9617] VALUES (2498,'<span data-type="title" id=toc-2483>٦٣ </span>- {قَالَ بَلْ فَعَلَهُ كَبِيرُهُمْ هَذَا فَاسْأَلُوهُمْ إِنْ كَانُوا يَنْطِقُونَ}
INSERT INTO [b9617] VALUES (2499,'<span data-type="title" id=toc-2484>٦٤ </span>- {فَرَجَعُوا إِلَى أَنْفُسِهِمْ فَقَالُوا إِنَّكُمْ أَنْتُمُ الظَّالِمُونَ}
INSERT INTO [b9617] VALUES (2500,'<span data-type="title" id=toc-2485>٦٥ </span>- {ثُمَّ نُكِسُوا عَلَى رُءُوسِهِمْ لَقَدْ عَلِمْتَ مَا هَؤُلاءِ يَنْطِقُونَ}
INSERT INTO [b9617] VALUES (2501,'<span data-type="title" id=toc-2486>٦٦ </span>- {قَالَ أَفَتَعْبُدُونَ مِنْ دُونِ اللَّهِ مَا لا يَنْفَعُكُمْ شَيْئًا وَلا يَضُرُّكُمْ}
INSERT INTO [b9617] VALUES (2502,'<span data-type="title" id=toc-2487>٦٧ </span>- {أُفٍّ لَكُمْ وَلِمَا تَعْبُدُونَ مِنْ دُونِ اللَّهِ أَفَلا تَعْقِلُونَ}
INSERT INTO [b9617] VALUES (2503,'<span data-type="title" id=toc-2488>٦٨ </span>- {وَانْصُرُوا آلِهَتَكُمْ إِنْ كُنْتُمْ فَاعِلِينَ}
INSERT INTO [b9617] VALUES (2504,'<span data-type="title" id=toc-2489>٦٩ </span>- {قُلْنَا يَا نَارُ كُونِي بَرْدًا وَسَلامًا عَلَى إِبْرَاهِيمَ}
INSERT INTO [b9617] VALUES (2505,'<span data-type="title" id=toc-2490>٧٠ </span>- {وَأَرَادُوا بِهِ كَيْدًا فَجَعَلْنَاهُمُ الأَخْسَرِينَ}
INSERT INTO [b9617] VALUES (2506,'<span data-type="title" id=toc-2491>٧١ </span>- {وَنَجَّيْنَاهُ وَلُوطًا إِلَى الأَرْضِ الَّتِي بَارَكْنَا فِيهَا لِلْعَالَمِينَ}
INSERT INTO [b9617] VALUES (2507,'<span data-type="title" id=toc-2492>٧٢ </span>- {وَوَهَبْنَا لَهُ إِسْحَاقَ وَيَعْقُوبَ نَافِلَةً وَكُلا جَعَلْنَا صَالِحِينَ}
INSERT INTO [b9617] VALUES (2508,'<span data-type="title" id=toc-2493>٧٣ </span>- {وَجَعَلْنَاهُمْ أَئِمَّةً يَهْدُونَ بِأَمْرِنَا وَأَوْحَيْنَا إِلَيْهِمْ فِعْلَ الْخَيْرَاتِ وَإِقَامَ الصَّلاةِ وَإِيتَاءَ الزَّكَاةِ وَكَانُوا لَنَا عَابِدِينَ}
INSERT INTO [b9617] VALUES (2509,'<span data-type="title" id=toc-2494>٧٤ </span>- {وَلُوطًا آتَيْنَاهُ حُكْمًا وَعِلْمًا وَنَجَّيْنَاهُ مِنَ الْقَرْيَةِ الَّتِي كَانَتْ تَعْمَلُ الْخَبَائِثَ إِنَّهُمْ كَانُوا قَوْمَ سَوْءٍ فَاسِقِينَ}
INSERT INTO [b9617] VALUES (2510,'<span data-type="title" id=toc-2495>٧٥ </span>- {وَأَدْخَلْنَاهُ فِي رَحْمَتِنَا إِنَّهُ مِنَ الصَّالِحِينَ}
INSERT INTO [b9617] VALUES (2511,'<span data-type="title" id=toc-2496>٧٦ </span>- {وَنُوحًا إِذْ نَادَى مِنْ قَبْلُ فَاسْتَجَبْنَا لَهُ فَنَجَّيْنَاهُ وَأَهْلَهُ مِنَ الْكَرْبِ الْعَظِيمِ}
INSERT INTO [b9617] VALUES (2512,'<span data-type="title" id=toc-2497>٧٧ </span>- {وَنَصَرْنَاهُ مِنَ الْقَوْمِ الَّذِينَ كَذَّبُوا بِآيَاتِنَا إِنَّهُمْ كَانُوا قَوْمَ سَوْءٍ فَأَغْرَقْنَاهُمْ أَجْمَعِينَ}
INSERT INTO [b9617] VALUES (2513,'<span data-type="title" id=toc-2498>٧٨ </span>- {وَدَاوُدَ وَسُلَيْمَانَ إِذْ يَحْكُمَانِ فِي الْحَرْثِ إِذْ نَفَشَتْ فِيهِ غَنَمُ الْقَوْمِ وَكُنَّا لِحُكْمِهِمْ شَاهِدِينَ}
INSERT INTO [b9617] VALUES (2514,'<span data-type="title" id=toc-2499>٧٩ </span>- {فَفَهَّمْنَاهَا سُلَيْمَانَ وَكُلا آتَيْنَا حُكْمًا وَعِلْمًا وَسَخَّرْنَا مَعَ دَاوُدَ الْجِبَالَ يُسَبِّحْنَ وَالطَّيْرَ وَكُنَّا فَاعِلِينَ}
INSERT INTO [b9617] VALUES (2515,'<span data-type="title" id=toc-2500>٨٠ </span>- {وَعَلَّمْنَاهُ صَنْعَةَ لَبُوسٍ لَكُمْ لِتُحْصِنَكُمْ مِنْ بَأْسِكُمْ فَهَلْ أَنْتُمْ شَاكِرُونَ}
INSERT INTO [b9617] VALUES (2516,'<span data-type="title" id=toc-2501>٨١ </span>- {وَلِسُلَيْمَانَ الرِّيحَ عَاصِفَةً تَجْرِي بِأَمْرِهِ إِلَى الأَرْضِ الَّتِي بَارَكْنَا فِيهَا وَكُنَّا بِكُلِّ شَيْءٍ عَالِمِينَ}
INSERT INTO [b9617] VALUES (2517,'<span data-type="title" id=toc-2502>٨٢ </span>- {وَمِنَ الشَّيَاطِينِ مَنْ يَغُوصُونَ لَهُ وَيَعْمَلُونَ عَمَلا دُونَ ذَلِكَ وَكُنَّا لَهُمْ حَافِظِينَ}
INSERT INTO [b9617] VALUES (2518,'<span data-type="title" id=toc-2503>٨٣ </span>- {وَأَيُّوبَ إِذْ نَادَى رَبَّهُ أَنِّي مَسَّنِيَ الضُّرُّ وَأَنْتَ أَرْحَمُ الرَّاحِمِينَ}
INSERT INTO [b9617] VALUES (2519,'<span data-type="title" id=toc-2504>٨٤ </span>- {فَكَشَفْنَا مَا بِهِ مِنْ ضُرٍّ وَآتَيْنَاهُ أَهْلَهُ وَمِثْلَهُمْ مَعَهُمْ رَحْمَةً مِنْ عِنْدِنَا وَذِكْرَى لِلْعَابِدِينَ}
INSERT INTO [b9617] VALUES (2520,'<span data-type="title" id=toc-2505>٨٥ </span>- {وَإِسْمَاعِيلَ وَإِدْرِيسَ وَذَا الْكِفْلِ كُلٌّ مِنَ الصَّابِرِينَ}
INSERT INTO [b9617] VALUES (2521,'<span data-type="title" id=toc-2506>٨٦ </span>- {وَأَدْخَلْنَاهُمْ فِي رَحْمَتِنَا إِنَّهُمْ مِنَ الصَّالِحِينَ}
INSERT INTO [b9617] VALUES (2522,'<span data-type="title" id=toc-2507>٨٧ </span>- {وَذَا النُّونِ إِذْ ذَهَبَ مُغَاضِبًا فَظَنَّ أَنْ لَنْ نَقْدِرَ عَلَيْهِ فَنَادَى فِي الظُّلُمَاتِ أَنْ لا إِلَهَ إِلا أَنْتَ سُبْحَانَكَ إِنِّي كُنْتُ مِنَ الظَّالِمِينَ}
INSERT INTO [b9617] VALUES (2523,'<span data-type="title" id=toc-2508>٨٨ </span>- {وَكَذَلِكَ نُنْجِي الْمُؤْمِنِينَ}
INSERT INTO [b9617] VALUES (2524,'<span data-type="title" id=toc-2509>٨٩ </span>- {وَزَكَرِيَّا إِذْ نَادَى رَبَّهُ رَبِّ لا تَذَرْنِي فَرْدًا وَأَنْتَ خَيْرُ الْوَارِثِينَ}
INSERT INTO [b9617] VALUES (2525,'<span data-type="title" id=toc-2510>٩٠ </span>- {إِنَّهُمْ كَانُوا يُسَارِعُونَ فِي الْخَيْرَاتِ وَيَدْعُونَنَا رَغَبًا وَرَهَبًا وَكَانُوا لَنَا خَاشِعِينَ}
INSERT INTO [b9617] VALUES (2526,'<span data-type="title" id=toc-2511>٩١ </span>- {وَالَّتِي أَحْصَنَتْ فَرْجَهَا فَنَفَخْنَا فِيهَا مِنْ رُوحِنَا وَجَعَلْنَاهَا وَابْنَهَا آيَةً لِلْعَالَمِينَ}
INSERT INTO [b9617] VALUES (2527,'<span data-type="title" id=toc-2512>٩٢ </span>- {إِنَّ هَذِهِ أُمَّتُكُمْ أُمَّةً وَاحِدَةً وَأَنَا رَبُّكُمْ فَاعْبُدُونِ}
INSERT INTO [b9617] VALUES (2528,'<span data-type="title" id=toc-2513>٩٣ </span>- {وَتَقَطَّعُوا أَمْرَهُمْ بَيْنَهُمْ كُلٌّ إِلَيْنَا رَاجِعُونَ}
INSERT INTO [b9617] VALUES (2529,'<span data-type="title" id=toc-2514>٩٤ </span>- {فَمَنْ يَعْمَلْ مِنَ الصَّالِحَاتِ وَهُوَ مُؤْمِنٌ فَلا كُفْرَانَ لِسَعْيِهِ وَإِنَّا لَهُ كَاتِبُونَ}
INSERT INTO [b9617] VALUES (2530,'<span data-type="title" id=toc-2515>٩٥ </span>- {وَحَرَامٌ عَلَى قَرْيَةٍ أَهْلَكْنَاهَا أَنَّهُمْ لا يَرْجِعُونَ} ⦗٧٣٥⦘
INSERT INTO [b9617] VALUES (2531,'<span data-type="title" id=toc-2516>٩٦ </span>- {حَتَّى إِذَا فُتِحَتْ يَأْجُوجُ وَمَأْجُوجُ وَهُمْ مِنْ كُلِّ حَدَبٍ يَنْسِلُونَ}
INSERT INTO [b9617] VALUES (2532,'<span data-type="title" id=toc-2517>٩٧ </span>- {وَاقْتَرَبَ الْوَعْدُ الْحَقُّ فَإِذَا هِيَ شَاخِصَةٌ أَبْصَارُ الَّذِينَ كَفَرُوا يَا وَيْلَنَا قَدْ كُنَّا فِي غَفْلَةٍ مِنْ هَذَا بَلْ كُنَّا ظَالِمِينَ}
INSERT INTO [b9617] VALUES (2533,'<span data-type="title" id=toc-2518>٩٨ </span>- {إِنَّكُمْ وَمَا تَعْبُدُونَ مِنْ دُونِ اللَّهِ حَصَبُ جَهَنَّمَ أَنْتُمْ لَهَا وَارِدُونَ}
INSERT INTO [b9617] VALUES (2534,'<span data-type="title" id=toc-2519>٩٩ </span>- {لَوْ كَانَ هَؤُلاءِ آلِهَةً مَا وَرَدُوهَا وَكُلٌّ فِيهَا خَالِدُونَ}
INSERT INTO [b9617] VALUES (2535,'<span data-type="title" id=toc-2520>١٠٠ </span>- {لَهُمْ فِيهَا زَفِيرٌ وَهُمْ فِيهَا لا يَسْمَعُونَ}
INSERT INTO [b9617] VALUES (2536,'<span data-type="title" id=toc-2521>١٠١ </span>- {إِنَّ الَّذِينَ سَبَقَتْ لَهُمْ مِنَّا الْحُسْنَى أُولَئِكَ عَنْهَا مُبْعَدُونَ}
INSERT INTO [b9617] VALUES (2537,'<span data-type="title" id=toc-2522>١٠٢ </span>- {لا يَسْمَعُونَ حَسِيسَهَا وَهُمْ فِي مَا اشْتَهَتْ أَنْفُسُهُمْ خَالِدُونَ}
INSERT INTO [b9617] VALUES (2538,'<span data-type="title" id=toc-2523>١٠٣ </span>- {لا يَحْزُنُهُمُ الْفَزَعُ الأَكْبَرُ وَتَتَلَقَّاهُمُ الْمَلائِكَةُ هَذَا يَوْمُكُمُ الَّذِي كُنْتُمْ تُوعَدُونَ}
INSERT INTO [b9617] VALUES (2539,'<span data-type="title" id=toc-2524>١٠٤ </span>- {يَوْمَ نَطْوِي السَّمَاءَ كَطَيِّ السِّجِلِّ لِلْكُتُبِ كَمَا بَدَأْنَا أَوَّلَ خَلْقٍ نُعِيدُهُ وَعْدًا عَلَيْنَا إِنَّا كُنَّا فَاعِلِينَ}
INSERT INTO [b9617] VALUES (2540,'<span data-type="title" id=toc-2525>١٠٥ </span>- {وَلَقَدْ كَتَبْنَا فِي الزَّبُورِ مِنْ بَعْدِ الذِّكْرِ أَنَّ الأَرْضَ يَرِثُهَا عِبَادِيَ الصَّالِحُونَ} ⦗٧٣٨⦘
INSERT INTO [b9617] VALUES (2541,'<span data-type="title" id=toc-2526>١٠٦ </span>- {إِنَّ فِي هَذَا لَبَلاغًا لِقَوْمٍ عَابِدِينَ}
INSERT INTO [b9617] VALUES (2542,'<span data-type="title" id=toc-2527>١٠٧ </span>- {وَمَا أَرْسَلْنَاكَ إِلا رَحْمَةً لِلْعَالَمِينَ}
INSERT INTO [b9617] VALUES (2543,'<span data-type="title" id=toc-2528>١٠٨ </span>- {قُلْ إِنَّمَا يُوحَى إِلَيَّ أَنَّمَا إِلَهُكُمْ إِلَهٌ وَاحِدٌ فَهَلْ أَنْتُمْ مُسْلِمُونَ}
INSERT INTO [b9617] VALUES (2544,'<span data-type="title" id=toc-2529>١٠٩ </span>- {فَإِنْ تَوَلَّوْا فَقُلْ آذَنْتُكُمْ عَلَى سَوَاءٍ وَإِنْ أَدْرِي أَقَرِيبٌ أَمْ بَعِيدٌ مَا تُوعَدُونَ}
INSERT INTO [b9617] VALUES (2545,'<span data-type="title" id=toc-2530>١١٠ </span>- {إِنَّهُ يَعْلَمُ الْجَهْرَ مِنَ الْقَوْلِ وَيَعْلَمُ مَا تَكْتُمُونَ}
INSERT INTO [b9617] VALUES (2546,'<span data-type="title" id=toc-2531>١١١ </span>- {وَإِنْ أَدْرِي لَعَلَّهُ فِتْنَةٌ لَكُمْ وَمَتَاعٌ إِلَى حِينٍ}
INSERT INTO [b9617] VALUES (2547,'<span data-type="title" id=toc-2532>١١٢ </span>- {قَالَ رَبِّ احْكُمْ بِالْحَقِّ وَرَبُّنَا الرَّحْمَنُ الْمُسْتَعَانُ عَلَى مَا تَصِفُونَ}
INSERT INTO [b9617] VALUES (2548,'<span data-type="title" id=toc-2533>سورة الحج</span>','2','740',NULL,NULL,'0');
INSERT INTO [b9617] VALUES (2549,'<span data-type="title" id=toc-2534>١ </span>- {يَا أَيُّهَا النَّاسُ اتَّقُوا رَبَّكُمْ إِنَّ زَلْزَلَةَ السَّاعَةِ شَيْءٌ عَظِيمٌ}
INSERT INTO [b9617] VALUES (2550,'<span data-type="title" id=toc-2535>٢ </span>- {يَوْمَ تَرَوْنَهَا تَذْهَلُ كُلُّ مُرْضِعَةٍ عَمَّا أَرْضَعَتْ وَتَضَعُ كُلُّ ذَاتِ حَمْلٍ حَمْلَهَا وَتَرَى النَّاسَ سُكَارَى وَمَا هُمْ بِسُكَارَى وَلَكِنَّ عَذَابَ اللَّهِ شَدِيدٌ}
INSERT INTO [b9617] VALUES (2551,'<span data-type="title" id=toc-2536>٣ </span>- {وَمِنَ النَّاسِ مَنْ يُجَادِلُ فِي اللَّهِ بِغَيْرِ عِلْمٍ وَيَتَّبِعُ كُلَّ شَيْطَانٍ مَرِيدٍ}
INSERT INTO [b9617] VALUES (2552,'<span data-type="title" id=toc-2537>٤ </span>- {كُتِبَ عَلَيْهِ أَنَّهُ مَنْ تَوَلاهُ فَأَنَّهُ يُضِلُّهُ وَيَهْدِيهِ إِلَى عَذَابِ السَّعِيرِ}
INSERT INTO [b9617] VALUES (2553,'<span data-type="title" id=toc-2538>٥ </span>- {يَا أَيُّهَا النَّاسُ إِنْ كُنْتُمْ فِي رَيْبٍ مِنَ الْبَعْثِ فَإِنَّا خَلَقْنَاكُمْ مِنْ تُرَابٍ ثُمَّ مِنْ نُطْفَةٍ ثُمَّ مِنْ عَلَقَةٍ ثُمَّ مِنْ مُضْغَةٍ مُخَلَّقَةٍ وَغَيْرِ مُخَلَّقَةٍ لِنُبَيِّنَ لَكُمْ وَنُقِرُّ فِي الأَرْحَامِ مَا نَشَاءُ إِلَى أَجَلٍ مُسَمًّى ثُمَّ نُخْرِجُكُمْ طِفْلا ثُمَّ لِتَبْلُغُوا أَشُدَّكُمْ وَمِنْكُمْ مَنْ يُتَوَفَّى وَمِنْكُمْ مَنْ يُرَدُّ إِلَى أَرْذَلِ الْعُمُرِ لِكَيْلا يَعْلَمَ مِنْ بَعْدِ عِلْمٍ شَيْئًا وَتَرَى الأَرْضَ هَامِدَةً فَإِذَا أَنْزَلْنَا عَلَيْهَا الْمَاءَ اهْتَزَّتْ وَرَبَتْ}
INSERT INTO [b9617] VALUES (2554,'<span data-type="title" id=toc-2539>٦ </span>- {ذَلِكَ بِأَنَّ اللَّهَ هُوَ الْحَقُّ وَأَنَّهُ يُحْيِي الْمَوْتَى وَأَنَّهُ عَلَى كُلِّ شَيْءٍ قَدِيرٌ}
INSERT INTO [b9617] VALUES (2555,'<span data-type="title" id=toc-2540>٧ </span>- {وَأَنَّ السَّاعَةَ آتِيَةٌ لا رَيْبَ فِيهَا وَأَنَّ اللَّهَ يَبْعَثُ مَنْ فِي الْقُبُورِ}
INSERT INTO [b9617] VALUES (2556,'<span data-type="title" id=toc-2541>٨ </span>- {وَمِنَ النَّاسِ مَنْ يُجَادِلُ فِي اللَّهِ بِغَيْرِ عِلْمٍ وَلا هُدًى وَلا كِتَابٍ مُنِيرٍ}
INSERT INTO [b9617] VALUES (2557,'<span data-type="title" id=toc-2542>٩ </span>- {ثَانِيَ عِطْفِهِ لِيُضِلَّ عَنْ سَبِيلِ اللَّهِ لَهُ فِي الدُّنْيَا خِزْيٌ وَنُذِيقُهُ يَوْمَ الْقِيَامَةِ عَذَابَ الْحَرِيقِ}
INSERT INTO [b9617] VALUES (2558,'<span data-type="title" id=toc-2543>١٠ </span>- {ذَلِكَ بِمَا قَدَّمَتْ يَدَاكَ وَأَنَّ اللَّهَ لَيْسَ بِظَلامٍ لِلْعَبِيدِ}
INSERT INTO [b9617] VALUES (2559,'<span data-type="title" id=toc-2544>١١ </span>- {وَمِنَ النَّاسِ مَنْ يَعْبُدُ اللَّهَ عَلَى حَرْفٍ فَإِنْ أَصَابَهُ خَيْرٌ اطْمَأَنَّ بِهِ وَإِنْ أَصَابَتْهُ فِتْنَةٌ انْقَلَبَ عَلَى وَجْهِهِ خَسِرَ الدُّنْيَا وَالآخِرَةَ ذَلِكَ هُوَ الْخُسْرَانُ الْمُبِينُ}
INSERT INTO [b9617] VALUES (2560,'<span data-type="title" id=toc-2545>١٢ </span>- {يَدْعُو مِنْ دُونِ اللَّهِ مَا لا يَضُرُّهُ وَمَا لا يَنْفَعُهُ ذَلِكَ هُوَ الضَّلالُ الْبَعِيدُ}
INSERT INTO [b9617] VALUES (2561,'<span data-type="title" id=toc-2546>١٣ </span>- {يَدْعُو لَمَنْ ضَرُّهُ أَقْرَبُ مِنْ نَفْعِهِ لَبِئْسَ الْمَوْلَى وَلَبِئْسَ الْعَشِيرُ}
INSERT INTO [b9617] VALUES (2562,'<span data-type="title" id=toc-2547>١٤ </span>- {إِنَّ اللَّهَ يُدْخِلُ الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ جَنَّاتٍ تَجْرِي مِنْ تَحْتِهَا ⦗٧٤٤⦘ الأَنْهَارُ}
INSERT INTO [b9617] VALUES (2563,'<span data-type="title" id=toc-2548>١٥ </span>- {مَنْ كَانَ يَظُنُّ أَنْ لَنْ يَنْصُرَهُ اللَّهُ فِي الدُّنْيَا وَالآخِرَةِ فَلْيَمْدُدْ بِسَبَبٍ إِلَى السَّمَاءِ ثُمَّ لِيَقْطَعْ فَلْيَنْظُرْ هَلْ يُذْهِبَنَّ كَيْدُهُ مَا يَغِيظُ}
INSERT INTO [b9617] VALUES (2564,'<span data-type="title" id=toc-2549>١٦ </span>- {وَكَذَلِكَ أَنْزَلْنَاهُ آيَاتٍ بَيِّنَاتٍ وَأَنَّ اللَّهَ يَهْدِي مَنْ يُرِيدُ}
INSERT INTO [b9617] VALUES (2565,'<span data-type="title" id=toc-2550>١٧ </span>- {إِنَّ الَّذِينَ آمَنُوا وَالَّذِينَ هَادُوا وَالصَّابِئِينَ وَالنَّصَارَى وَالْمَجُوسَ وَالَّذِينَ أَشْرَكُوا إِنَّ اللَّهَ يَفْصِلُ بَيْنَهُمْ يَوْمَ الْقِيَامَةِ}
INSERT INTO [b9617] VALUES (2566,'<span data-type="title" id=toc-2551>١٨ </span>- {أَلَمْ تَرَ أَنَّ اللَّهَ يَسْجُدُ لَهُ مَنْ فِي السَّمَاوَاتِ وَمَنْ فِي الأَرْضِ وَالشَّمْسُ وَالْقَمَرُ ⦗٧٤٥⦘ وَالنُّجُومُ وَالْجِبَالُ وَالشَّجَرُ وَالدَّوَابُّ وَكَثِيرٌ مِنَ النَّاسِ وَكَثِيرٌ حَقَّ عَلَيْهِ الْعَذَابُ وَمَنْ يُهِنِ اللَّهُ فَمَا لَهُ مِنْ مُكْرِمٍ إِنَّ اللَّهَ يَفْعَلُ مَا يَشَاءُ}
INSERT INTO [b9617] VALUES (2567,'<span data-type="title" id=toc-2552>١٩ </span>- {هَذَانِ خَصْمَانِ اخْتَصَمُوا فِي رَبِّهِمْ فَالَّذِينَ كَفَرُوا قُطِّعَتْ لَهُمْ ثِيَابٌ مِنْ نَارٍ يُصَبُّ مِنْ فَوْقِ رُءُوسِهِمُ الْحَمِيمُ}
INSERT INTO [b9617] VALUES (2568,'<span data-type="title" id=toc-2553>٢٠ </span>- {يُصْهَرُ بِهِ مَا فِي بُطُونِهِمْ وَالْجُلُودُ}
INSERT INTO [b9617] VALUES (2569,'<span data-type="title" id=toc-2554>٢١ </span>- {وَلَهُمْ مَقَامِعُ مِنْ حَدِيدٍ}
INSERT INTO [b9617] VALUES (2570,'<span data-type="title" id=toc-2555>٢٢ </span>- {كُلَّمَا أَرَادُوا أَنْ يَخْرُجُوا مِنْهَا مِنْ غَمٍّ أُعِيدُوا فِيهَا وَذُوقُوا عَذَابَ الْحَرِيقِ}
INSERT INTO [b9617] VALUES (2571,'<span data-type="title" id=toc-2556>٢٣ </span>- {إِنَّ اللَّهَ يُدْخِلُ الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ جَنَّاتٍ تَجْرِي مِنْ تَحْتِهَا الأَنْهَارُ يُحَلَّوْنَ فِيهَا مِنْ أَسَاوِرَ مِنْ ذَهَبٍ وَلُؤْلُؤًا وَلِبَاسُهُمْ فِيهَا حَرِيرٌ}
INSERT INTO [b9617] VALUES (2572,'<span data-type="title" id=toc-2557>٢٤ </span>- {وَهُدُوا إِلَى الطَّيِّبِ مِنَ الْقَوْلِ وَهُدُوا إِلَى صِرَاطِ الْحَمِيدِ}
INSERT INTO [b9617] VALUES (2573,'<span data-type="title" id=toc-2558>٢٥ </span>- {إِنَّ الَّذِينَ كَفَرُوا وَيَصُدُّونَ عَنْ سَبِيلِ اللَّهِ وَالْمَسْجِدِ الْحَرَامِ الَّذِي جَعَلْنَاهُ لِلنَّاسِ سَوَاءً الْعَاكِفُ فِيهِ وَالْبَادِ وَمَنْ يُرِدْ فِيهِ بِإِلْحَادٍ بِظُلْمٍ نُذِقْهُ مِنْ عَذَابٍ أَلِيمٍ} ⦗٧٤٧⦘
INSERT INTO [b9617] VALUES (2574,'<span data-type="title" id=toc-2559>٢٦ </span>- {وَإِذْ بَوَّأْنَا لإِبْرَاهِيمَ مَكَانَ الْبَيْتِ أَنْ لا تُشْرِكْ بِي شَيْئًا}
INSERT INTO [b9617] VALUES (2575,'<span data-type="title" id=toc-2560>٢٧ </span>- {وَأَذِّنْ فِي النَّاسِ بِالْحَجِّ يَأْتُوكَ رِجَالا وَعَلَى كُلِّ ضَامِرٍ يَأْتِينَ مِنْ كُلِّ فَجٍّ عَمِيقٍ}
INSERT INTO [b9617] VALUES (2576,'<span data-type="title" id=toc-2561>٢٨ </span>- {لِيَشْهَدُوا مَنَافِعَ لَهُمْ وَيَذْكُرُوا اسْمَ اللَّهِ فِي أَيَّامٍ مَعْلُومَاتٍ عَلَى مَا ⦗٧٤٨⦘ رَزَقَهُمْ مِنْ بَهِيمَةِ الأَنْعَامِ فَكُلُوا مِنْهَا وَأَطْعِمُوا الْبَائِسَ الْفَقِيرَ}
INSERT INTO [b9617] VALUES (2577,'<span data-type="title" id=toc-2562>٢٩ </span>- {ثُمَّ لْيَقْضُوا تَفَثَهُمْ}
INSERT INTO [b9617] VALUES (2578,'<span data-type="title" id=toc-2563>٣٠ </span>- {ذَلِكَ وَمَنْ يُعَظِّمْ حُرُمَاتِ اللَّهِ فَهُوَ خَيْرٌ لَهُ عِنْدَ رَبِّهِ وَأُحِلَّتْ لَكُمُ الأَنْعَامُ إِلا مَا يُتْلَى عَلَيْكُمْ فَاجْتَنِبُوا الرِّجْسَ مِنَ الأَوْثَانِ}
INSERT INTO [b9617] VALUES (2579,'<span data-type="title" id=toc-2564>٣١ </span>- {حُنَفَاءَ لِلَّهِ غَيْرَ مُشْرِكِينَ بِهِ وَمَنْ يُشْرِكْ بِاللَّهِ فَكَأَنَّمَا خَرَّ مِنَ السَّمَاءِ فَتَخْطَفُهُ الطَّيْرُ أَوْ تَهْوِي بِهِ الرِّيحُ}
INSERT INTO [b9617] VALUES (2580,'<span data-type="title" id=toc-2565>٣٢ </span>- {ذَلِكَ وَمَنْ يُعَظِّمْ شَعَائِرَ اللَّهِ فَإِنَّهَا مِنْ تَقْوَى الْقُلُوبِ}
INSERT INTO [b9617] VALUES (2581,'<span data-type="title" id=toc-2566>٣٣ </span>- {لَكُمْ فِيهَا مَنَافِعُ إِلَى أَجَلٍ مُسَمًّى ثُمَّ مَحِلُّهَا إِلَى الْبَيْتِ الْعَتِيقِ}
INSERT INTO [b9617] VALUES (2582,'<span data-type="title" id=toc-2567>٣٤ </span>- {وَلِكُلِّ أُمَّةٍ جَعَلْنَا مَنْسَكًا لِيَذْكُرُوا اسْمَ اللَّهِ عَلَى مَا رَزَقَهُمْ مِنْ بَهِيمَةِ الأَنْعَامِ فَإِلَهُكُمْ إِلَهٌ وَاحِدٌ فَلَهُ أَسْلِمُوا وَبَشِّرِ الْمُخْبِتِينَ}
INSERT INTO [b9617] VALUES (2583,'<span data-type="title" id=toc-2568>٣٥ </span>- {الَّذِينَ إِذَا ذُكِرَ اللَّهُ وَجِلَتْ قُلُوبُهُمْ وَالصَّابِرِينَ عَلَى مَا أَصَابَهُمْ وَالْمُقِيمِي ⦗٧٥٠⦘ الصَّلاةِ وَمِمَّا رَزَقْنَاهُمْ يُنْفِقُونَ}
INSERT INTO [b9617] VALUES (2584,'<span data-type="title" id=toc-2569>٣٦ </span>- {وَالْبُدْنَ جَعَلْنَاهَا لَكُمْ مِنْ شَعَائِرِ اللَّهِ لَكُمْ فِيهَا خَيْرٌ فَاذْكُرُوا اسْمَ اللَّهِ عَلَيْهَا صَوَافَّ فَإِذَا وَجَبَتْ جُنُوبُهَا فَكُلُوا مِنْهَا وَأَطْعِمُوا الْقَانِعَ وَالْمُعْتَرَّ كَذَلِكَ سَخَّرْنَاهَا لَكُمْ لَعَلَّكُمْ تَشْكُرُونَ}
INSERT INTO [b9617] VALUES (2585,'<span data-type="title" id=toc-2570>٣٧ </span>- {لَنْ يَنَالَ اللَّهَ لُحُومُهَا وَلا دِمَاؤُهَا وَلَكِنْ يَنَالُهُ التَّقْوَى مِنْكُمْ كَذَلِكَ سَخَّرَهَا لَكُمْ لِتُكَبِّرُوا اللَّهَ عَلَى مَا هَدَاكُمْ وَبَشِّرِ الْمُحْسِنِينَ}
INSERT INTO [b9617] VALUES (2586,'<span data-type="title" id=toc-2571>٣٨ </span>- {إِنَّ اللَّهَ لا يُحِبُّ كُلَّ خَوَّانٍ كَفُورٍ}
INSERT INTO [b9617] VALUES (2587,'<span data-type="title" id=toc-2572>٣٩ </span>- {أُذِنَ لِلَّذِينَ يُقَاتَلُونَ بِأَنَّهُمْ ظُلِمُوا وَإِنَّ اللَّهَ عَلَى نَصْرِهِمْ لَقَدِيرٌ}
INSERT INTO [b9617] VALUES (2588,'<span data-type="title" id=toc-2573>٤٠ </span>- {الَّذِينَ أُخْرِجُوا مِنْ دِيَارِهِمْ بِغَيْرِ حَقٍّ إِلا أَنْ يَقُولُوا رَبُّنَا اللَّهُ وَلَوْلا دَفْعُ اللَّهِ النَّاسَ بَعْضَهُمْ بِبَعْضٍ لَهُدِّمَتْ صَوَامِعُ وَبِيَعٌ وَصَلَوَاتٌ وَمَسَاجِدُ يُذْكَرُ فِيهَا اسْمُ اللَّهِ كَثِيرًا وَلَيَنْصُرَنَّ اللَّهُ مَنْ يَنْصُرُهُ}
INSERT INTO [b9617] VALUES (2589,'<span data-type="title" id=toc-2574>٤١ </span>- {الَّذِينَ إِنْ مَكَّنَّاهُمْ فِي الأَرْضِ أَقَامُوا الصَّلاةَ وَآتَوُا الزَّكَاةَ وَأَمَرُوا بِالْمَعْرُوفِ وَنَهَوْا عَنِ الْمُنْكَرِ وَلِلَّهِ عَاقِبَةُ الأُمُورِ}
INSERT INTO [b9617] VALUES (2590,'<span data-type="title" id=toc-2575>٤٢ </span>- {كَذَّبَتْ قَبْلَهُمْ قَوْمُ نُوحٍ وَعَادٌ وَثَمُودُ}
INSERT INTO [b9617] VALUES (2591,'<span data-type="title" id=toc-2576>٤٣ </span>- {وَقَوْمُ إِبْرَاهِيمَ وَقَوْمُ لُوطٍ}
INSERT INTO [b9617] VALUES (2592,'<span data-type="title" id=toc-2577>٤٤ </span>- {وَأَصْحَابُ مَدْيَنَ وَكُذِّبَ مُوسَى فَأَمْلَيْتُ لِلْكَافِرِينَ ثُمَّ أَخَذْتُهُمْ فَكَيْفَ كَانَ نَكِيرِ}
INSERT INTO [b9617] VALUES (2593,'<span data-type="title" id=toc-2578>٤٥ </span>- {فَكَأَيِّنْ مِنْ قَرْيَةٍ أَهْلَكْنَاهَا وَهِيَ ظَالِمَةٌ فَهِيَ خَاوِيَةٌ عَلَى عُرُوشِهَا وَبِئْرٍ مُعَطَّلَةٍ}
INSERT INTO [b9617] VALUES (2594,'<span data-type="title" id=toc-2579>٤٦ </span>- {أَفَلَمْ يَسِيرُوا فِي الأَرْضِ فَتَكُونَ لَهُمْ قُلُوبٌ يَعْقِلُونَ بِهَا أَوْ آذَانٌ يَسْمَعُونَ بِهَا فَإِنَّهَا لا تَعْمَى الأَبْصَارُ وَلَكِنْ تَعْمَى الْقُلُوبُ الَّتِي فِي الصُّدُورِ}
INSERT INTO [b9617] VALUES (2595,'<span data-type="title" id=toc-2580>٤٧ </span>- {وَيَسْتَعْجِلُونَكَ بِالْعَذَابِ وَلَنْ يُخْلِفَ اللَّهُ وَعْدَهُ وَإِنَّ يَوْمًا عِنْدَ رَبِّكَ كَأَلْفِ سَنَةٍ مِمَّا تَعُدُّونَ} ⦗٧٥٤⦘
INSERT INTO [b9617] VALUES (2596,'<span data-type="title" id=toc-2581>٤٨ </span>- {وَكَأَيِّنْ مِنْ قَرْيَةٍ أَمْلَيْتُ لَهَا وَهِيَ ظَالِمَةٌ ثُمَّ أَخَذْتُهَا وَإِلَيَّ الْمَصِيرُ}
INSERT INTO [b9617] VALUES (2597,'<span data-type="title" id=toc-2582>٤٩ </span>- {قُلْ يَا أَيُّهَا النَّاسُ إِنَّمَا أَنَا لَكُمْ نَذِيرٌ مُبِينٌ}
INSERT INTO [b9617] VALUES (2598,'<span data-type="title" id=toc-2583>٥٠ </span>- {فَالَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ لَهُمْ مَغْفِرَةٌ}
INSERT INTO [b9617] VALUES (2599,'<span data-type="title" id=toc-2584>٥١ </span>- {وَالَّذِينَ سَعَوْا فِي آيَاتِنَا مُعَاجِزِينَ أُولَئِكَ أَصْحَابُ الْجَحِيمِ}
INSERT INTO [b9617] VALUES (2600,'<span data-type="title" id=toc-2585>٥٢ </span>- {وَمَا أَرْسَلْنَا مِنْ قَبْلِكَ مِنْ رَسُولٍ وَلا نَبِيٍّ إِلا إِذَا تَمَنَّى أَلْقَى الشَّيْطَانُ فِي أُمْنِيَّتِهِ ⦗٧٥٥⦘ فَيَنْسَخُ اللَّهُ مَا يُلْقِي الشَّيْطَانُ ثُمَّ يُحْكِمُ اللَّهُ آيَاتِهِ وَاللَّهُ عَلِيمٌ حَكِيمٌ}
INSERT INTO [b9617] VALUES (2601,'<span data-type="title" id=toc-2586>٥٣ </span>- {لِيَجْعَلَ مَا يُلْقِي الشَّيْطَانُ فِتْنَةً لِلَّذِينَ فِي قُلُوبِهِمْ مَرَضٌ وَالْقَاسِيَةِ قُلُوبُهُمْ وَإِنَّ الظَّالِمِينَ لَفِي شِقَاقٍ بَعِيدٍ}
INSERT INTO [b9617] VALUES (2602,'<span data-type="title" id=toc-2587>٥٤ </span>- {وَلِيَعْلَمَ الَّذِينَ أُوتُوا الْعِلْمَ أَنَّهُ الْحَقُّ مِنْ رَبِّكَ فَيُؤْمِنُوا بِهِ فَتُخْبِتَ لَهُ قُلُوبُهُمْ وَإِنَّ اللَّهَ لَهَادِ الَّذِينَ آمَنُوا إِلَى صِرَاطٍ مُسْتَقِيمٍ}
INSERT INTO [b9617] VALUES (2603,'<span data-type="title" id=toc-2588>٥٥ </span>- {وَلا يَزَالُ الَّذِينَ كَفَرُوا فِي مِرْيَةٍ مِنْهُ حَتَّى تَأْتِيَهُمُ السَّاعَةُ بَغْتَةً} ⦗٧٥٦⦘
INSERT INTO [b9617] VALUES (2604,'<span data-type="title" id=toc-2589>٥٦ </span>- {الْمُلْكُ يَوْمَئِذٍ لِلَّهِ يَحْكُمُ بَيْنَهُمْ فَالَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ فِي جَنَّاتِ النَّعِيمِ}
INSERT INTO [b9617] VALUES (2605,'<span data-type="title" id=toc-2590>٥٧ </span>- {وَالَّذِينَ كَفَرُوا وَكَذَّبُوا بِآيَاتِنَا فَأُولَئِكَ لَهُمْ عَذَابٌ مُهِينٌ}
INSERT INTO [b9617] VALUES (2606,'<span data-type="title" id=toc-2591>٥٨ </span>- {وَالَّذِينَ هَاجَرُوا فِي سَبِيلِ اللَّهِ ثُمَّ قُتِلُوا أَوْ مَاتُوا لَيَرْزُقَنَّهُمُ اللَّهُ رِزْقًا حَسَنًا وَإِنَّ اللَّهَ لَهُوَ خَيْرُ الرَّازِقِينَ}
INSERT INTO [b9617] VALUES (2607,'<span data-type="title" id=toc-2592>٥٩ </span>- {لَيُدْخِلَنَّهُمْ مُدْخَلا يَرْضَوْنَهُ وَإِنَّ اللَّهَ لَعَلِيمٌ حَلِيمٌ}
INSERT INTO [b9617] VALUES (2608,'<span data-type="title" id=toc-2593>٦٠ </span>- {ذَلِكَ وَمَنْ عَاقَبَ بِمِثْلِ مَا عُوقِبَ بِهِ ثُمَّ بُغِيَ عَلَيْهِ لَيَنْصُرَنَّهُ اللَّهُ}
INSERT INTO [b9617] VALUES (2609,'<span data-type="title" id=toc-2594>٦١ </span>- {ذَلِكَ بِأَنَّ اللَّهَ يُولِجُ اللَّيْلَ فِي النَّهَارِ وَيُولِجُ النَّهَارَ فِي اللَّيْلِ وَأَنَّ اللَّهَ سَمِيعٌ بَصِيرٌ}
INSERT INTO [b9617] VALUES (2610,'<span data-type="title" id=toc-2595>٦٢ </span>- {ذَلِكَ بِأَنَّ اللَّهَ هُوَ الْحَقُّ وَأَنَّ مَا يَدْعُونَ مِنْ دُونِهِ هُوَ الْبَاطِلُ وَأَنَّ اللَّهَ هُوَ الْعَلِيُّ الْكَبِيرُ}
INSERT INTO [b9617] VALUES (2611,'<span data-type="title" id=toc-2596>٦٣ </span>- {أَلَمْ تَرَ أَنَّ اللَّهَ أَنْزَلَ مِنَ السَّمَاءِ مَاءً فَتُصْبِحُ الأَرْضُ مُخْضَرَّةً إِنَّ اللَّهَ لَطِيفٌ خَبِيرٌ}
INSERT INTO [b9617] VALUES (2612,'<span data-type="title" id=toc-2597>٦٤ </span>- {لَهُ مَا فِي السَّمَاوَاتِ وَمَا فِي الأَرْضِ وَإِنَّ اللَّهَ لَهُوَ الْغَنِيُّ الْحَمِيدُ}
INSERT INTO [b9617] VALUES (2613,'<span data-type="title" id=toc-2598>٦٥ </span>- {أَلَمْ تَرَ أَنَّ اللَّهَ سَخَّرَ لَكُمْ مَا فِي الأَرْضِ وَالْفُلْكَ تَجْرِي فِي الْبَحْرِ بِأَمْرِهِ وَيُمْسِكُ السَّمَاءَ أَنْ تَقَعَ عَلَى الأَرْضِ إِلا بِإِذْنِهِ}
INSERT INTO [b9617] VALUES (2614,'<span data-type="title" id=toc-2599>٦٦ </span>- {وَهُوَ الَّذِي أَحْيَاكُمْ ثُمَّ يُمِيتُكُمْ ثُمَّ يُحْيِيكُمْ إِنَّ الإِنْسَانَ لَكَفُورٌ}
INSERT INTO [b9617] VALUES (2615,'<span data-type="title" id=toc-2600>٦٧ </span>- {لِكُلِّ أُمَّةٍ جَعَلْنَا مَنْسَكًا هُمْ نَاسِكُوهُ فَلا يُنَازِعُنَّكَ فِي الأَمْرِ}
INSERT INTO [b9617] VALUES (2616,'<span data-type="title" id=toc-2601>٦٨ </span>- {وَإِنْ جَادَلُوكَ فَقُلِ اللَّهُ أَعْلَمُ بِمَا تَعْمَلُونَ}
INSERT INTO [b9617] VALUES (2617,'<span data-type="title" id=toc-2602>٦٩ </span>- {اللَّهُ يَحْكُمُ بَيْنَكُمْ يَوْمَ الْقِيَامَةِ}
INSERT INTO [b9617] VALUES (2618,'<span data-type="title" id=toc-2603>٧٠ </span>- {أَلَمْ تَعْلَمْ أَنَّ اللَّهَ يَعْلَمُ مَا فِي السَّمَاءِ وَالأَرْضِ إِنَّ ذَلِكَ فِي كِتَابٍ إِنَّ ذَلِكَ عَلَى اللَّهِ يَسِيرٌ}
INSERT INTO [b9617] VALUES (2619,'<span data-type="title" id=toc-2604>٧١ </span>- {وَيَعْبُدُونَ مِنْ دُونِ اللَّهِ مَا لَمْ يُنَزِّلْ بِهِ سُلْطَانًا وَمَا لَيْسَ لَهُمْ بِهِ عِلْمٌ وَمَا لِلظَّالِمِينَ مِنْ نَصِيرٍ} ⦗٧٦٠⦘
INSERT INTO [b9617] VALUES (2620,'<span data-type="title" id=toc-2605>٧٢ </span>- {وَإِذَا تُتْلَى عَلَيْهِمْ آيَاتُنَا بَيِّنَاتٍ تَعْرِفُ فِي وُجُوهِ الَّذِينَ كَفَرُوا الْمُنْكَرَ يَكَادُونَ يَسْطُونَ بِالَّذِينَ يَتْلُونَ عَلَيْهِمْ آيَاتِنَا قُلْ أَفَأُنَبِّئُكُمْ بِشَرٍّ مِنْ ذَلِكُمُ النَّارُ وَعَدَهَا اللَّهُ الَّذِينَ كَفَرُوا وَبِئْسَ الْمَصِيرُ}
INSERT INTO [b9617] VALUES (2621,'<span data-type="title" id=toc-2606>٧٣ </span>- {يَا أَيُّهَا النَّاسُ ضُرِبَ مَثَلٌ فَاسْتَمِعُوا لَهُ إِنَّ الَّذِينَ تَدْعُونَ مِنْ دُونِ اللَّهِ لَنْ يَخْلُقُوا ذُبَابًا وَلَوِ اجْتَمَعُوا لَهُ وَإِنْ يَسْلُبْهُمُ الذُّبَابُ شَيْئًا لا يَسْتَنْقِذُوهُ مِنْهُ ضَعُفَ الطَّالِبُ وَالْمَطْلُوبُ}
INSERT INTO [b9617] VALUES (2622,'<span data-type="title" id=toc-2607>٧٤ </span>- {مَا قَدَرُوا اللَّهَ حَقَّ قَدْرِهِ}
INSERT INTO [b9617] VALUES (2623,'<span data-type="title" id=toc-2608>٧٥ </span>- {اللَّهُ يَصْطَفِي مِنَ الْمَلائِكَةِ رُسُلا وَمِنَ النَّاسِ إِنَّ اللَّهَ سَمِيعٌ بَصِيرٌ}
INSERT INTO [b9617] VALUES (2624,'<span data-type="title" id=toc-2609>٧٦ </span>- {يَعْلَمُ مَا بَيْنَ أَيْدِيهِمْ وَمَا خَلْفَهُمْ وَإِلَى اللَّهِ تُرْجَعُ الأُمُورُ}
INSERT INTO [b9617] VALUES (2625,'<span data-type="title" id=toc-2610>٧٧ </span>- {يَا أَيُّهَا الَّذِينَ آمَنُوا ارْكَعُوا وَاسْجُدُوا وَاعْبُدُوا رَبَّكُمْ وَافْعَلُوا الْخَيْرَ لَعَلَّكُمْ تُفْلِحُونَ}
INSERT INTO [b9617] VALUES (2626,'<span data-type="title" id=toc-2611>٧٨ </span>- {وَجَاهِدُوا فِي اللَّهِ حَقَّ جِهَادِهِ هُوَ اجْتَبَاكُمْ وَمَا جَعَلَ عَلَيْكُمْ فِي الدِّينِ مِنْ حَرَجٍ مِلَّةَ أَبِيكُمْ إِبْرَاهِيمَ هُوَ سَمَّاكُمُ الْمُسْلِمِينَ مِنْ قَبْلُ وَفِي هَذَا لِيَكُونَ الرَّسُولُ شَهِيدًا عَلَيْكُمْ وَتَكُونُوا شُهَدَاءَ عَلَى النَّاسِ فَأَقِيمُوا الصَّلاةَ وَآتُوا الزَّكَاةَ وَاعْتَصِمُوا بِاللَّهِ هُوَ مَوْلاكُمْ فَنِعْمَ الْمَوْلَى وَنِعْمَ النَّصِيرُ}
INSERT INTO [b9617] VALUES (2627,'<span data-type="title" id=toc-2612>سورة المؤمنون</span>','2','763',NULL,NULL,'0');
INSERT INTO [b9617] VALUES (2628,'<span data-type="title" id=toc-2613>٢ </span>- {الَّذِينَ هُمْ فِي صَلاتِهِمْ خَاشِعُونَ}
INSERT INTO [b9617] VALUES (2629,'<span data-type="title" id=toc-2614>٣ </span>- {وَالَّذِينَ هُمْ عَنِ اللَّغْوِ مُعْرِضُونَ}
INSERT INTO [b9617] VALUES (2630,'<span data-type="title" id=toc-2615>٤ </span>- {وَالَّذِينَ هُمْ لِلزَّكَاةِ فَاعِلُونَ}
INSERT INTO [b9617] VALUES (2631,'<span data-type="title" id=toc-2616>٥ </span>- {وَالَّذِينَ هُمْ لِفُرُوجِهِمْ حَافِظُونَ}
INSERT INTO [b9617] VALUES (2632,'<span data-type="title" id=toc-2617>٦ </span>- {إِلا عَلَى أَزْوَاجِهِمْ أوْ مَا مَلَكَتْ أَيْمَانُهُمْ فَإِنَّهُمْ غَيْرُ مَلُومِينَ}
INSERT INTO [b9617] VALUES (2633,'<span data-type="title" id=toc-2618>٧ </span>- {فَمَنِ ابْتَغَى وَرَاءَ ذَلِكَ فَأُولَئِكَ هُمُ الْعَادُونَ}
INSERT INTO [b9617] VALUES (2634,'<span data-type="title" id=toc-2619>٨ </span>- {وَالَّذِينَ هُمْ لأَمَانَاتِهِمْ وَعَهْدِهِمْ رَاعُونَ}
INSERT INTO [b9617] VALUES (2635,'<span data-type="title" id=toc-2620>٩ </span>- {وَالَّذِينَ هُمْ عَلَى صَلَوَاتِهِمْ يُحَافِظُونَ}
INSERT INTO [b9617] VALUES (2636,'<span data-type="title" id=toc-2621>١٠ </span>- {أُولَئِكَ هُمُ الْوَارِثُونَ}
INSERT INTO [b9617] VALUES (2637,'<span data-type="title" id=toc-2622>١١ </span>- {الَّذِينَ يَرِثُونَ الْفِرْدَوْسَ هُمْ فِيهَا خَالِدُونَ}
INSERT INTO [b9617] VALUES (2638,'<span data-type="title" id=toc-2623>١٢ </span>- {وَلَقَدْ خَلَقْنَا الإِنْسَانَ مِنْ سُلالَةٍ مِنْ طِينٍ}
INSERT INTO [b9617] VALUES (2639,'<span data-type="title" id=toc-2624>١٣ </span>- {ثُمَّ جَعَلْنَاهُ نُطْفَةً فِي قَرَارٍ مَكِينٍ}
INSERT INTO [b9617] VALUES (2640,'<span data-type="title" id=toc-2625>١٤ </span>- {ثُمَّ خَلَقْنَا النُّطْفَةَ عَلَقَةً فَخَلَقْنَا الْعَلَقَةَ مُضْغَةً فَخَلَقْنَا الْمُضْغَةَ عِظَامًا فَكَسَوْنَا الْعِظَامَ لَحْمًا ثُمَّ أَنْشَأْنَاهُ خَلْقًا آخَرَ فَتَبَارَكَ اللَّهُ أَحْسَنُ الْخَالِقِينَ}
INSERT INTO [b9617] VALUES (2641,'<span data-type="title" id=toc-2626>١٥ </span>- {ثُمَّ إِنَّكُمْ بَعْدَ ذَلِكَ لَمَيِّتُونَ}
INSERT INTO [b9617] VALUES (2642,'<span data-type="title" id=toc-2627>١٦ </span>- {ثُمَّ إِنَّكُمْ يَوْمَ الْقِيَامَةِ تُبْعَثُونَ}
INSERT INTO [b9617] VALUES (2643,'<span data-type="title" id=toc-2628>١٧ </span>- {وَلَقَدْ خَلَقْنَا فَوْقَكُمْ سَبْعَ طَرَائِقَ وَمَا كُنَّا عَنِ الْخَلْقِ غَافِلِينَ}
INSERT INTO [b9617] VALUES (2644,'<span data-type="title" id=toc-2629>١٨ </span>- {وَأَنْزَلْنَا مِنَ السَّمَاءِ مَاءً بِقَدَرٍ فَأَسْكَنَّاهُ فِي الأَرْضِ وَإِنَّا عَلَى ذَهَابٍ بِهِ لَقَادِرُونَ}
INSERT INTO [b9617] VALUES (2645,'<span data-type="title" id=toc-2630>١٩ </span>- {فَأَنْشَأْنَا لَكُمْ بِهِ جَنَّاتٍ مِنْ نَخِيلٍ وَأَعْنَابٍ لَكُمْ فِيهَا فَوَاكِهُ كَثِيرَةٌ وَمِنْهَا تَأْكُلُونَ}
INSERT INTO [b9617] VALUES (2646,'<span data-type="title" id=toc-2631>٢٠ </span>- {وَشَجَرَةً تَخْرُجُ مِنْ طُورِ سَيْنَاءَ تَنْبُتُ بِالدُّهْنِ وَصِبْغٍ لِلآكِلِينَ}
INSERT INTO [b9617] VALUES (2647,'<span data-type="title" id=toc-2632>٢١ </span>- {وَإِنَّ لَكُمْ فِي الأَنْعَامِ لَعِبْرَةً نُسْقِيكُمْ مِمَّا فِي بُطُونِهَا وَلَكُمْ فِيهَا مَنَافِعُ كَثِيرَةٌ وَمِنْهَا تَأْكُلُونَ}
INSERT INTO [b9617] VALUES (2648,'<span data-type="title" id=toc-2633>٢٢ </span>- {وَعَلَيْهَا وَعَلَى الْفُلْكِ تُحْمَلُونَ}
INSERT INTO [b9617] VALUES (2649,'<span data-type="title" id=toc-2634>٢٣ </span>- {وَلَقَدْ أَرْسَلْنَا نُوحًا إِلَى قَوْمِهِ فَقَالَ يَا قَوْمِ اعْبُدُوا اللَّهَ مَا لَكُمْ مِنْ إِلَهٍ غَيْرُهُ أَفَلا تَتَّقُونَ}
INSERT INTO [b9617] VALUES (2650,'<span data-type="title" id=toc-2635>٢٤ </span>- {فَقَالَ الْمَلأُ الَّذِينَ كَفَرُوا مِنْ قَوْمِهِ مَا هَذَا إِلا بَشَرٌ مِثْلُكُمْ يُرِيدُ أَنْ يَتَفَضَّلَ عَلَيْكُمْ وَلَوْ شَاءَ اللَّهُ لأَنْزَلَ مَلائِكَةً مَا سَمِعْنَا بِهَذَا فِي آبَائِنَا الأَوَّلِينَ}
INSERT INTO [b9617] VALUES (2651,'<span data-type="title" id=toc-2636>٢٥ </span>- {إِنْ هُوَ إِلا رَجُلٌ بِهِ جِنَّةٌ فَتَرَبَّصُوا بِهِ حَتَّى حِينٍ}
INSERT INTO [b9617] VALUES (2652,'<span data-type="title" id=toc-2637>٢٦ </span>- {قَالَ رَبِّ انْصُرْنِي بِمَا كَذَّبُونِ}
INSERT INTO [b9617] VALUES (2653,'<span data-type="title" id=toc-2638>٢٧ </span>- {فَأَوْحَيْنَا إِلَيْهِ أَنِ اصْنَعِ الْفُلْكَ بِأَعْيُنِنَا وَوَحْيِنَا فَإِذَا جَاءَ أَمْرُنَا وَفَارَ التَّنُّورُ فَاسْلُكْ ⦗٧٦٨⦘ فِيهَا مِنْ كُلٍّ زَوْجَيْنِ اثْنَيْنِ وَأَهْلَكَ إِلا مَنْ سَبَقَ عَلَيْهِ الْقَوْلُ مِنْهُمْ وَلا تُخَاطِبْنِي فِي الَّذِينَ ظَلَمُوا إِنَّهُمْ مُغْرَقُونَ}
INSERT INTO [b9617] VALUES (2654,'<span data-type="title" id=toc-2639>٢٨ </span>- {فَإِذَا اسْتَوَيْتَ أَنْتَ وَمَنْ مَعَكَ عَلَى الْفُلْكِ فَقُلِ الْحَمْدُ لِلَّهِ الَّذِي نَجَّانَا مِنَ الْقَوْمِ الظَّالِمِينَ}
INSERT INTO [b9617] VALUES (2655,'<span data-type="title" id=toc-2640>٢٩ </span>- {وَقُلْ رَبِّ أَنْزِلْنِي مُنْزَلا مُبَارَكًا وَأَنْتَ خَيْرُ الْمُنْزِلِينَ}
INSERT INTO [b9617] VALUES (2656,'<span data-type="title" id=toc-2641>٣٠ </span>- {إِنَّ فِي ذَلِكَ لآيَاتٍ وَإِنْ كُنَّا لَمُبْتَلِينَ}
INSERT INTO [b9617] VALUES (2657,'<span data-type="title" id=toc-2642>٣١ </span>- {ثُمَّ أَنْشَأْنَا مِنْ بَعْدِهِمْ قَرْنًا آخَرِينَ}
INSERT INTO [b9617] VALUES (2658,'<span data-type="title" id=toc-2643>٣٢ </span>- {فَأَرْسَلْنَا فِيهِمْ رَسُولا مِنْهُمْ أَنِ اعْبُدُوا اللَّهَ مَا لَكُمْ مِنْ إِلَهٍ غَيْرُهُ أَفَلا تَتَّقُونَ}
INSERT INTO [b9617] VALUES (2659,'<span data-type="title" id=toc-2644>٣٣ </span>- {وَقَالَ الْمَلأُ مِنْ قَوْمِهِ الَّذِينَ كَفَرُوا وَكَذَّبُوا بِلِقَاءِ الآخِرَةِ وَأَتْرَفْنَاهُمْ فِي الْحَيَاةِ الدُّنْيَا مَا هَذَا إِلا بَشَرٌ مِثْلُكُمْ يَأْكُلُ مِمَّا تَأْكُلُونَ مِنْهُ وَيَشْرَبُ مِمَّا تَشْرَبُونَ}
INSERT INTO [b9617] VALUES (2660,'<span data-type="title" id=toc-2645>٣٤ </span>- {وَلَئِنْ أَطَعْتُمْ بَشَرًا مِثْلَكُمْ إِنَّكُمْ إِذًا لَخَاسِرُونَ}
INSERT INTO [b9617] VALUES (2661,'<span data-type="title" id=toc-2646>٣٥ </span>- {أَيَعِدُكُمْ أَنَّكُمْ إِذَا مِتُّمْ وَكُنْتُمْ تُرَابًا وَعِظَامًا أَنَّكُمْ مُخْرَجُونَ}
INSERT INTO [b9617] VALUES (2662,'<span data-type="title" id=toc-2647>٣٦ </span>- {هَيْهَاتَ هَيْهَاتَ لِمَا تُوعَدُونَ}
INSERT INTO [b9617] VALUES (2663,'<span data-type="title" id=toc-2648>٣٧ </span>- {إِنْ هِيَ إِلا حَيَاتُنَا الدُّنْيَا نَمُوتُ وَنَحْيَا وَمَا نَحْنُ بِمَبْعُوثِينَ}
INSERT INTO [b9617] VALUES (2664,'<span data-type="title" id=toc-2649>٣٨ </span>- {إِنْ هُوَ إِلا رَجُلٌ افْتَرَى عَلَى اللَّهِ كَذِبًا وَمَا نَحْنُ لَهُ بِمُؤْمِنِينَ}
INSERT INTO [b9617] VALUES (2665,'<span data-type="title" id=toc-2650>٣٩ </span>- {قَالَ رَبِّ انْصُرْنِي بِمَا كَذَّبُونِ}
INSERT INTO [b9617] VALUES (2666,'<span data-type="title" id=toc-2651>٤٠ </span>- {قَالَ عَمَّا قَلِيلٍ لَيُصْبِحُنَّ نَادِمِينَ} ⦗٧٧١⦘
INSERT INTO [b9617] VALUES (2667,'<span data-type="title" id=toc-2652>٤١ </span>- {فَأَخَذَتْهُمُ الصَّيْحَةُ بِالْحَقِّ فَجَعَلْنَاهُمْ غُثَاءً فَبُعْدًا لِلْقَوْمِ الظَّالِمِينَ}
INSERT INTO [b9617] VALUES (2668,'<span data-type="title" id=toc-2653>٤٢ </span>- {ثُمَّ أَنْشَأْنَا مِنْ بَعْدِهِمْ قُرُونًا آخَرِينَ}
INSERT INTO [b9617] VALUES (2669,'<span data-type="title" id=toc-2654>٤٣ </span>- {مَا تَسْبِقُ مِنْ أُمَّةٍ أَجَلَهَا وَمَا يَسْتَأْخِرُونَ}
INSERT INTO [b9617] VALUES (2670,'<span data-type="title" id=toc-2655>٤٤ </span>- {ثُمَّ أَرْسَلْنَا رُسُلَنَا تَتْرَى كُلَّ مَا جَاءَ أُمَّةً رَسُولُهَا كَذَّبُوهُ فَأَتْبَعْنَا بَعْضَهُمْ بَعْضًا ⦗٧٧٢⦘ وَجَعَلْنَاهُمْ أَحَادِيثَ فَبُعْدًا لِقَوْمٍ لا يُؤْمِنُونَ}
INSERT INTO [b9617] VALUES (2671,'<span data-type="title" id=toc-2656>٤٥ </span>- {ثُمَّ أَرْسَلْنَا مُوسَى وَأَخَاهُ هَارُونَ بِآيَاتِنَا وَسُلْطَانٍ مُبِينٍ}
INSERT INTO [b9617] VALUES (2672,'<span data-type="title" id=toc-2657>٤٦ </span>- {إِلَى فِرْعَوْنَ وَمَلَئِهِ فَاسْتَكْبَرُوا وَكَانُوا قَوْمًا عَالِينَ}
INSERT INTO [b9617] VALUES (2673,'<span data-type="title" id=toc-2658>٤٧ </span>- {فَقَالُوا أَنُؤْمِنُ لِبَشَرَيْنِ مِثْلِنَا وَقَوْمُهُمَا لَنَا عَابِدُونَ}
INSERT INTO [b9617] VALUES (2674,'<span data-type="title" id=toc-2659>٤٩ </span>- {وَلَقَدْ آتَيْنَا مُوسَى الْكِتَابَ لَعَلَّهُمْ يَهْتَدُونَ}
INSERT INTO [b9617] VALUES (2675,'<span data-type="title" id=toc-2660>٥٠ </span>- {وَجَعَلْنَا ابْنَ مَرْيَمَ وَأُمَّهُ آيَةً وَآوَيْنَاهُمَا إِلَى رَبْوَةٍ ذَاتِ قَرَارٍ وَمَعِينٍ}
INSERT INTO [b9617] VALUES (2676,'<span data-type="title" id=toc-2661>٥١ </span>- {يَا أَيُّهَا الرُّسُلُ كُلُوا مِنَ الطَّيِّبَاتِ وَاعْمَلُوا صَالِحًا إِنِّي بِمَا تَعْمَلُونَ عَلِيمٌ}
INSERT INTO [b9617] VALUES (2677,'<span data-type="title" id=toc-2662>٥٢ </span>- {وَإِنَّ هَذِهِ أُمَّتُكُمْ أُمَّةً وَاحِدَةً وَأَنَا رَبُّكُمْ فَاتَّقُونِ}
INSERT INTO [b9617] VALUES (2678,'<span data-type="title" id=toc-2663>٥٣ </span>- {فَتَقَطَّعُوا أَمْرَهُمْ بَيْنَهُمْ زُبُرًا كُلُّ حِزْبٍ بِمَا لَدَيْهِمْ فَرِحُونَ}
INSERT INTO [b9617] VALUES (2679,'<span data-type="title" id=toc-2664>٥٤ </span>- {فَذَرْهُمْ فِي غَمْرَتِهِمْ حَتَّى حِينٍ}
INSERT INTO [b9617] VALUES (2680,'<span data-type="title" id=toc-2665>٥٥ </span>- {أَيَحْسَبُونَ أَنَّمَا نُمِدُّهُمْ بِهِ مِنْ مَالٍ وَبَنِينَ}
INSERT INTO [b9617] VALUES (2681,'<span data-type="title" id=toc-2666>٥٦ </span>- {نُسَارِعُ لَهُمْ فِي الْخَيْرَاتِ بَلْ لا يَشْعُرُونَ}
INSERT INTO [b9617] VALUES (2682,'<span data-type="title" id=toc-2667>٥٧ </span>- {إِنَّ الَّذِينَ هُمْ مِنْ خَشْيَةِ رَبِّهِمْ مُشْفِقُونَ}
INSERT INTO [b9617] VALUES (2683,'<span data-type="title" id=toc-2668>٥٨ </span>- {وَالَّذِينَ هُمْ بِآيَاتِ رَبِّهِمْ يُؤْمِنُونَ}
INSERT INTO [b9617] VALUES (2684,'<span data-type="title" id=toc-2669>٥٩ </span>- {وَالَّذِينَ هُمْ بِرَبِّهِمْ لا يُشْرِكُونَ}
INSERT INTO [b9617] VALUES (2685,'<span data-type="title" id=toc-2670>٦٠ </span>- {وَالَّذِينَ يُؤْتُونَ مَا آتَوْا وَقُلُوبُهُمْ وَجِلَةٌ أَنَّهُمْ إِلَى رَبِّهِمْ رَاجِعُونَ}
INSERT INTO [b9617] VALUES (2686,'<span data-type="title" id=toc-2671>٦١ </span>- {أُولَئِكَ يُسَارِعُونَ فِي الْخَيْرَاتِ وَهُمْ لَهَا سَابِقُونَ}
INSERT INTO [b9617] VALUES (2687,'<span data-type="title" id=toc-2672>٦٢ </span>- {وَلا نُكَلِّفُ نَفْسًا إِلا وُسْعَهَا وَلَدَيْنَا كِتَابٌ يَنْطِقُ بِالْحَقِّ وَهُمْ لا يُظْلَمُونَ}
INSERT INTO [b9617] VALUES (2688,'<span data-type="title" id=toc-2673>٦٣ </span>- {بَلْ قُلُوبُهُمْ فِي غَمْرَةٍ مِنْ هَذَا وَلَهُمْ أَعْمَالٌ مِنْ دُونِ ذَلِكَ هُمْ لَهَا عَامِلُونَ}
INSERT INTO [b9617] VALUES (2689,'<span data-type="title" id=toc-2674>٦٤ </span>- {حَتَّى إِذَا أَخَذْنَا مُتْرَفِيهِمْ بِالْعَذَابِ إِذَا هُمْ يَجْأَرُونَ}
INSERT INTO [b9617] VALUES (2690,'<span data-type="title" id=toc-2675>٦٥ </span>- {لا تَجْأَرُوا الْيَوْمَ إِنَّكُمْ مِنَّا لا تُنْصَرُونَ}
INSERT INTO [b9617] VALUES (2691,'<span data-type="title" id=toc-2676>٦٦ </span>- {قَدْ كَانَتْ آيَاتِي تُتْلَى عَلَيْكُمْ فَكُنْتُمْ عَلَى أَعْقَابِكُمْ تَنْكِصُونَ}
INSERT INTO [b9617] VALUES (2692,'<span data-type="title" id=toc-2677>٦٧ </span>- {مُسْتَكْبِرِينَ بِهِ سَامِرًا تَهْجُرُونَ}
INSERT INTO [b9617] VALUES (2693,'<span data-type="title" id=toc-2678>٦٨ </span>- {أَفَلَمْ يَدَّبَّرُوا الْقَوْلَ أَمْ جَاءَهُمْ مَا لَمْ يَأْتِ آبَاءَهُمُ الأَوَّلِينَ}
INSERT INTO [b9617] VALUES (2694,'<span data-type="title" id=toc-2679>٦٩ </span>- {أَمْ لَمْ يَعْرِفُوا رَسُولَهُمْ فَهُمْ لَهُ مُنْكِرُونَ}
INSERT INTO [b9617] VALUES (2695,'<span data-type="title" id=toc-2680>٧٠ </span>- {أَمْ يَقُولُونَ بِهِ جِنَّةٌ بَلْ جَاءَهُمْ بِالْحَقِّ وَأَكْثَرُهُمْ لِلْحَقِّ كَارِهُونَ}
INSERT INTO [b9617] VALUES (2696,'<span data-type="title" id=toc-2681>٧١ </span>- {وَلَوِ اتَّبَعَ الْحَقُّ أَهْوَاءَهُمْ لَفَسَدَتِ السَّمَاوَاتُ وَالأَرْضُ وَمَنْ فِيهِنَّ بَلْ أَتَيْنَاهُمْ ⦗٧٧٧⦘ بِذِكْرِهِمْ فَهُمْ عَنْ ذِكْرِهِمْ مُعْرِضُونَ}
INSERT INTO [b9617] VALUES (2697,'<span data-type="title" id=toc-2682>٧٢ </span>- {أَمْ تَسْأَلُهُمْ خَرْجًا فَخَرَاجُ رَبِّكَ خَيْرٌ وَهُوَ خَيْرُ الرَّازِقِينَ}
INSERT INTO [b9617] VALUES (2698,'<span data-type="title" id=toc-2683>٧٣ </span>- {وَإِنَّكَ لَتَدْعُوهُمْ إِلَى صِرَاطٍ مُسْتَقِيمٍ}
INSERT INTO [b9617] VALUES (2699,'<span data-type="title" id=toc-2684>٧٤ </span>- {وَإِنَّ الَّذِينَ لا يُؤْمِنُونَ بِالآخِرَةِ عَنِ الصِّرَاطِ لَنَاكِبُونَ}
INSERT INTO [b9617] VALUES (2700,'<span data-type="title" id=toc-2685>٧٥ </span>- {وَلَوْ رَحِمْنَاهُمْ وَكَشَفْنَا مَا بِهِمْ مِنْ ضُرٍّ لَلَجُّوا فِي طُغْيَانِهِمْ يَعْمَهُونَ}
INSERT INTO [b9617] VALUES (2701,'<span data-type="title" id=toc-2686>٧٦ </span>- {وَلَقَدْ أَخَذْنَاهُمْ بِالْعَذَابِ فَمَا اسْتَكَانُوا لِرَبِّهِمْ وَمَا يَتَضَرَّعُونَ} ⦗٧٧٨⦘
INSERT INTO [b9617] VALUES (2702,'<span data-type="title" id=toc-2687>٧٧ </span>- {حَتَّى إِذَا فَتَحْنَا عَلَيْهِمْ بَابًا ذَا عَذَابٍ شَدِيدٍ إِذَا هُمْ فِيهِ مُبْلِسُونَ}
INSERT INTO [b9617] VALUES (2703,'<span data-type="title" id=toc-2688>٧٨ </span>- {وَهُوَ الَّذِي أَنْشَأَ لَكُمُ السَّمْعَ وَالأَبْصَارَ وَالأَفْئِدَةَ قَلِيلا مَا تَشْكُرُونَ}
INSERT INTO [b9617] VALUES (2704,'<span data-type="title" id=toc-2689>٧٩ </span>- {وَهُوَ الَّذِي ذَرَأَكُمْ فِي الأَرْضِ وَإِلَيْهِ تُحْشَرُونَ}
INSERT INTO [b9617] VALUES (2705,'<span data-type="title" id=toc-2690>٨٠ </span>- {وَهُوَ الَّذِي يُحْيِي وَيُمِيتُ وَلَهُ اخْتِلافُ اللَّيْلِ وَالنَّهَارِ أَفَلا تَعْقِلُونَ}
INSERT INTO [b9617] VALUES (2706,'<span data-type="title" id=toc-2691>٨١ </span>- {بَلْ قَالُوا مِثْلَ مَا قَالَ الأَوَّلُونَ}
INSERT INTO [b9617] VALUES (2707,'<span data-type="title" id=toc-2692>٨٢ </span>- {قَالُوا أَئِذَا مِتْنَا وَكُنَّا تُرَابًا وَعِظَامًا أَئِنَّا لَمَبْعُوثُونَ}
INSERT INTO [b9617] VALUES (2708,'<span data-type="title" id=toc-2693>٨٣ </span>- {لَقَدْ وُعِدْنَا نَحْنُ وَآبَاؤُنَا هَذَا مِنْ قَبْلُ إِنْ هَذَا إِلا أَسَاطِيرُ الأَوَّلِينَ}
INSERT INTO [b9617] VALUES (2709,'<span data-type="title" id=toc-2694>٨٤ </span>- {قُلْ لِمَنِ الأَرْضُ وَمَنْ فِيهَا إِنْ كُنْتُمْ تَعْلَمُونَ}
INSERT INTO [b9617] VALUES (2710,'<span data-type="title" id=toc-2695>٨٥ </span>- {سَيَقُولُونَ لِلَّهِ قُلْ أَفَلا تَذَكَّرُونَ}
INSERT INTO [b9617] VALUES (2711,'<span data-type="title" id=toc-2696>٨٦ </span>- {قُلْ مَنْ رَبُّ السَّمَاوَاتِ السَّبْعِ}
INSERT INTO [b9617] VALUES (2712,'<span data-type="title" id=toc-2697>٨٧ </span>- {سَيَقُولُونَ لِلَّهِ قُلْ أَفَلا تَتَّقُونَ} ⦗٧٨٠⦘
INSERT INTO [b9617] VALUES (2713,'<span data-type="title" id=toc-2698>٨٨ </span>- {قُلْ مَنْ بِيَدِهِ مَلَكُوتُ كُلِّ شَيْءٍ وَهُوَ يُجِيرُ وَلا يُجَارُ عَلَيْهِ إِنْ كُنْتُمْ تَعْلَمُونَ}
INSERT INTO [b9617] VALUES (2714,'<span data-type="title" id=toc-2699>٨٩ </span>- {سَيَقُولُونَ لِلَّهِ قُلْ فَأَنَّى تُسْحَرُونَ}
INSERT INTO [b9617] VALUES (2715,'<span data-type="title" id=toc-2700>٩٠ </span>- {بَلْ أَتَيْنَاهُمْ بِالْحَقِّ وَإِنَّهُمْ لَكَاذِبُونَ}
INSERT INTO [b9617] VALUES (2716,'<span data-type="title" id=toc-2701>٩١ </span>- {مَا اتَّخَذَ اللَّهُ مِنْ وَلَدٍ وَمَا كَانَ مَعَهُ مِنْ إِلَهٍ إِذًا لَذَهَبَ كُلُّ إِلَهٍ بِمَا خَلَقَ وَلَعَلا بَعْضُهُمْ عَلَى بَعْضٍ سُبْحَانَ اللَّهِ عَمَّا يَصِفُونَ}
INSERT INTO [b9617] VALUES (2717,'<span data-type="title" id=toc-2702>٩٢ </span>- {عَالِمِ الْغَيْبِ وَالشَّهَادَةِ فَتَعَالَى عَمَّا يُشْرِكُونَ}
INSERT INTO [b9617] VALUES (2718,'<span data-type="title" id=toc-2703>٩٣ </span>- {قُلْ رَبِّ إِمَّا تُرِيَنِّي مَا يُوعَدُونَ}
INSERT INTO [b9617] VALUES (2719,'<span data-type="title" id=toc-2704>٩٤ </span>- {رَبِّ فَلا تَجْعَلْنِي فِي الْقَوْمِ الظَّالِمِينَ}
INSERT INTO [b9617] VALUES (2720,'<span data-type="title" id=toc-2705>٩٥ </span>- {وَإِنَّا عَلَى أَنْ نُرِيَكَ مَا نَعِدُهُمْ لَقَادِرُونَ}
INSERT INTO [b9617] VALUES (2721,'<span data-type="title" id=toc-2706>٩٦ </span>- {ادْفَعْ بِالَّتِي هِيَ أَحْسَنُ السَّيِّئَةَ نَحْنُ أَعْلَمُ بِمَا يَصِفُونَ} ⦗٧٨٢⦘
INSERT INTO [b9617] VALUES (2722,'<span data-type="title" id=toc-2707>٩٧ </span>- {وَقُلْ رَبِّ أَعُوذُ بِكَ مِنْ هَمَزَاتِ الشَّيَاطِينِ}
INSERT INTO [b9617] VALUES (2723,'<span data-type="title" id=toc-2708>٩٨ </span>- {وَأَعُوذُ بِكَ رَبِّ أَنْ يَحْضُرُونِ}
INSERT INTO [b9617] VALUES (2724,'<span data-type="title" id=toc-2709>٩٩ </span>- {حَتَّى إِذَا جَاءَ أَحَدَهُمُ الْمَوْتُ قَالَ رَبِّ ارْجِعُونِ}
INSERT INTO [b9617] VALUES (2725,'<span data-type="title" id=toc-2710>١٠٠ </span>- {لَعَلِّي أَعْمَلُ صَالِحًا فِيمَا تَرَكْتُ كَلا إِنَّهَا كَلِمَةٌ هُوَ قَائِلُهَا وَمِنْ وَرَائِهِمْ بَرْزَخٌ إِلَى يَوْمِ يُبْعَثُونَ}
INSERT INTO [b9617] VALUES (2726,'<span data-type="title" id=toc-2711>١٠١ </span>- {فَإِذَا نُفِخَ فِي الصُّورِ فَلا أَنْسَابَ بَيْنَهُمْ يَوْمَئِذٍ وَلا يَتَسَاءَلُونَ}
INSERT INTO [b9617] VALUES (2727,'<span data-type="title" id=toc-2712>١٠٢ </span>- {فَمَنْ ثَقُلَتْ مَوَازِينُهُ فَأُولَئِكَ هُمُ الْمُفْلِحُونَ}
INSERT INTO [b9617] VALUES (2728,'<span data-type="title" id=toc-2713>١٠٣ </span>- {فَأُولَئِكَ الَّذِينَ خَسِرُوا أَنْفُسَهُمْ فِي جَهَنَّمَ خَالِدُونَ}
INSERT INTO [b9617] VALUES (2729,'<span data-type="title" id=toc-2714>١٠٤ </span>- {تَلْفَحُ وُجُوهَهُمُ النَّارُ وَهُمْ فِيهَا كَالِحُونَ}
INSERT INTO [b9617] VALUES (2730,'<span data-type="title" id=toc-2715>١٠٥ </span>- {أَلَمْ تَكُنْ آيَاتِي تُتْلَى عَلَيْكُمْ فَكُنْتُمْ بِهَا تُكَذِّبُونَ}
INSERT INTO [b9617] VALUES (2731,'<span data-type="title" id=toc-2716>١٠٦ </span>- {قَالُوا رَبَّنَا غَلَبَتْ عَلَيْنَا شِقْوَتُنَا}
INSERT INTO [b9617] VALUES (2732,'<span data-type="title" id=toc-2717>١٠٧ </span>- {رَبَّنَا أَخْرِجْنَا مِنْهَا فَإِنْ عُدْنَا فَإِنَّا ظَالِمُونَ}
INSERT INTO [b9617] VALUES (2733,'<span data-type="title" id=toc-2718>١٠٨ </span>- {قَالَ اخْسَئُوا فِيهَا وَلا تُكَلِّمُونِ}
INSERT INTO [b9617] VALUES (2734,'<span data-type="title" id=toc-2719>١٠٩ </span>- {إِنَّهُ كَانَ فَرِيقٌ مِنْ عِبَادِي يَقُولُونَ رَبَّنَا آمَنَّا فَاغْفِرْ لَنَا وَارْحَمْنَا وَأَنْتَ خَيْرُ الرَّاحِمِينَ}
INSERT INTO [b9617] VALUES (2735,'<span data-type="title" id=toc-2720>١١٠ </span>- {فَاتَّخَذْتُمُوهُمْ سِخْرِيًّا حَتَّى أَنْسَوْكُمْ ذِكْرِي وَكُنْتُمْ مِنْهُمْ تَضْحَكُونَ}
INSERT INTO [b9617] VALUES (2736,'<span data-type="title" id=toc-2721>١١١ </span>- {إِنِّي جَزَيْتُهُمُ الْيَوْمَ بِمَا صَبَرُوا أَنَّهُمْ هُمُ الْفَائِزُونَ}
INSERT INTO [b9617] VALUES (2737,'<span data-type="title" id=toc-2722>١١٢ </span>- {قَالَ كَمْ لَبِثْتُمْ فِي الأَرْضِ عَدَدَ سِنِينَ}
INSERT INTO [b9617] VALUES (2738,'<span data-type="title" id=toc-2723>١١٣ </span>- {قَالُوا لَبِثْنَا يَوْمًا أَوْ بَعْضَ يَوْمٍ فَاسْأَلِ الْعَادِّينَ}
INSERT INTO [b9617] VALUES (2739,'<span data-type="title" id=toc-2724>١١٤ </span>- {قَالَ إِنْ لَبِثْتُمْ إِلا قَلِيلا لَوْ أَنَّكُمْ كُنْتُمْ تَعْلَمُونَ}
INSERT INTO [b9617] VALUES (2740,'<span data-type="title" id=toc-2725>١١٥ </span>- {أَفَحَسِبْتُمْ أَنَّمَا خَلَقْنَاكُمْ عَبَثًا وَأَنَّكُمْ إِلَيْنَا لا تُرْجَعُونَ}
INSERT INTO [b9617] VALUES (2741,'<span data-type="title" id=toc-2726>١١٦ </span>- {فَتَعَالَى اللَّهُ الْمَلِكُ الْحَقُّ لا إِلَهَ إِلا هُوَ رَبُّ الْعَرْشِ الْكَرِيمِ}
INSERT INTO [b9617] VALUES (2742,'<span data-type="title" id=toc-2727>١١٧ </span>- {وَمَنْ يَدْعُ مَعَ اللَّهِ إِلَهًا آخَرَ لا بُرْهَانَ لَهُ بِهِ فَإِنَّمَا حِسَابُهُ عِنْدَ رَبِّهِ إِنَّهُ لا يُفْلِحُ الْكَافِرُونَ}
INSERT INTO [b9617] VALUES (2743,'<span data-type="title" id=toc-2728>١١٨ </span>- {وَقُلْ رَبِّ اغْفِرْ وَارْحَمْ وَأَنْتَ خَيْرُ الرَّاحِمِينَ}
INSERT INTO [b9617] VALUES (2744,'<span data-type="title" id=toc-2729>سورة النور</span>','2','787',NULL,NULL,'0');
INSERT INTO [b9617] VALUES (2745,'<span data-type="title" id=toc-2730>١ </span>- {سُورَةٌ أَنْزَلْنَاهَا وَفَرَضْنَاهَا وَأَنْزَلْنَا فِيهَا آيَاتٍ بَيِّنَاتٍ لَعَلَّكُمْ تَذَكَّرُونَ}
INSERT INTO [b9617] VALUES (2746,'<span data-type="title" id=toc-2731>٢ </span>- {الزَّانِيَةُ وَالزَّانِي فَاجْلِدُوا كُلَّ وَاحِدٍ مِنْهُمَا مِائَةَ جَلْدَةٍ وَلا تَأْخُذْكُمْ بِهِمَا رَأْفَةٌ فِي دِينِ اللَّهِ إِنْ كُنْتُمْ تُؤْمِنُونَ بِاللَّهِ وَالْيَوْمِ الآخِرِ وَلْيَشْهَدْ عَذَابَهُمَا طَائِفَةٌ مِنَ الْمُؤْمِنِينَ}
INSERT INTO [b9617] VALUES (2747,'<span data-type="title" id=toc-2732>٣ </span>- {الزَّانِي لا يَنْكِحُ إِلا زَانِيَةً أَوْ مُشْرِكَةً وَالزَّانِيَةُ لا يَنْكِحُهَا إِلا زَانٍ أَوْ مُشْرِكٌ وَحُرِّمَ ذَلِكَ عَلَى الْمُؤْمِنِينَ}
INSERT INTO [b9617] VALUES (2748,'<span data-type="title" id=toc-2733>٤ </span>- {وَالَّذِينَ يَرْمُونَ الْمُحْصَنَاتِ ثُمَّ لَمْ يَأْتُوا بِأَرْبَعَةِ شُهَدَاءَ فَاجْلِدُوهُمْ ثَمَانِينَ جَلْدَةً وَلا تَقْبَلُوا لَهُمْ شَهَادَةً أَبَدًا وَأُولَئِكَ هُمُ الْفَاسِقُونَ}
INSERT INTO [b9617] VALUES (2749,'<span data-type="title" id=toc-2734>٥ </span>- {إِلا الَّذِينَ تَابُوا مِنْ بَعْدِ ذَلِكَ وَأَصْلَحُوا فَإِنَّ اللَّهَ غَفُورٌ رَحِيمٌ}
INSERT INTO [b9617] VALUES (2750,'<span data-type="title" id=toc-2735>٦ </span>- {وَالَّذِينَ يَرْمُونَ أَزْوَاجَهُمْ وَلَمْ يَكُنْ لَهُمْ شُهَدَاءُ إِلا أَنْفُسُهُمْ فَشَهَادَةُ أَحَدِهِمْ أَرْبَعُ شَهَادَاتٍ بِاللَّهِ إِنَّهُ لَمِنَ الصَّادِقِينَ}
INSERT INTO [b9617] VALUES (2751,'<span data-type="title" id=toc-2736>٧ </span>- {وَالْخَامِسَةُ أَنَّ لَعْنَتَ اللَّهِ عَلَيْهِ إِنْ كَانَ مِنَ الْكَاذِبِينَ}
INSERT INTO [b9617] VALUES (2752,'<span data-type="title" id=toc-2737>٨ </span>- {وَيَدْرَأُ عَنْهَا الْعَذَابَ أَنْ تَشْهَدَ أَرْبَعَ شَهَادَاتٍ بِاللَّهِ إِنَّهُ لَمِنَ الْكَاذِبِينَ}
INSERT INTO [b9617] VALUES (2753,'<span data-type="title" id=toc-2738>٩ </span>- {وَالْخَامِسَةَ أَنَّ غَضَبَ اللَّهِ عَلَيْهَا إِنْ كَانَ مِنَ الصَّادِقِينَ}
INSERT INTO [b9617] VALUES (2754,'<span data-type="title" id=toc-2739>١٠ </span>- {وَلَوْلا فَضْلُ اللَّهِ عَلَيْكُمْ وَرَحْمَتُهُ وَأَنَّ اللَّهَ تَوَّابٌ حَكِيمٌ}
INSERT INTO [b9617] VALUES (2755,'<span data-type="title" id=toc-2740>١١ </span>- {إِنَّ الَّذِينَ جَاءُوا بِالإِفْكِ عُصْبَةٌ مِنْكُمْ لا تَحْسَبُوهُ شَرًّا لَكُمْ بَلْ هُوَ خَيْرٌ لَكُمْ لِكُلِّ امْرِئٍ مِنْهُمْ مَا اكْتَسَبَ مِنَ الإِثْمِ وَالَّذِي تَوَلَّى كِبْرَهُ مِنْهُمْ لَهُ عَذَابٌ عَظِيمٌ}
INSERT INTO [b9617] VALUES (2756,'<span data-type="title" id=toc-2741>١٢ </span>- {لَوْلا إِذْ سَمِعْتُمُوهُ ظَنَّ الْمُؤْمِنُونَ وَالْمُؤْمِنَاتُ بِأَنْفُسِهِمْ خَيْرًا}
INSERT INTO [b9617] VALUES (2757,'<span data-type="title" id=toc-2742>١٣ </span>- {لَوْلا جَاءُوا عَلَيْهِ بِأَرْبَعَةِ شُهَدَاءَ فَإِذْ لَمْ يَأْتُوا بِالشُّهَدَاءِ فَأُولَئِكَ عِنْدَ اللَّهِ هُمُ الْكَاذِبُونَ}
INSERT INTO [b9617] VALUES (2758,'<span data-type="title" id=toc-2743>١٤ </span>- {وَلَوْلا فَضْلُ اللَّهِ عَلَيْكُمْ وَرَحْمَتُهُ فِي الدُّنْيَا وَالآخِرَةِ لَمَسَّكُمْ فِي مَا أَفَضْتُمْ فِيهِ عَذَابٌ عَظِيمٌ}
INSERT INTO [b9617] VALUES (2759,'<span data-type="title" id=toc-2744>١٥ </span>- {إِذْ تَلَقَّوْنَهُ بِأَلْسِنَتِكُمْ وَتَقُولُونَ بِأَفْوَاهِكُمْ مَا لَيْسَ لَكُمْ بِهِ عِلْمٌ وَتَحْسَبُونَهُ هَيِّنًا وَهُوَ عِنْدَ اللَّهِ عَظِيمٌ}
INSERT INTO [b9617] VALUES (2760,'<span data-type="title" id=toc-2745>١٦ </span>- {وَلَوْلا إِذْ سَمِعْتُمُوهُ قُلْتُمْ مَا يَكُونُ لَنَا أَنْ نَتَكَلَّمَ بِهَذَا سُبْحَانَكَ هَذَا بُهْتَانٌ عَظِيمٌ}
INSERT INTO [b9617] VALUES (2761,'<span data-type="title" id=toc-2746>١٧ </span>- {يَعِظُكُمُ اللَّهُ أَنْ تَعُودُوا لِمِثْلِهِ أَبَدًا إِنْ كُنْتُمْ مُؤْمِنِينَ}
INSERT INTO [b9617] VALUES (2762,'<span data-type="title" id=toc-2747>١٨ </span>- {وَيُبَيِّنُ اللَّهُ لَكُمُ الآيَاتِ وَاللَّهُ عَلِيمٌ حَكِيمٌ}
INSERT INTO [b9617] VALUES (2763,'<span data-type="title" id=toc-2748>١٩ </span>- {إِنَّ الَّذِينَ يُحِبُّونَ أَنْ تَشِيعَ الْفَاحِشَةُ فِي الَّذِينَ آمَنُوا لَهُمْ عَذَابٌ أَلِيمٌ فِي الدُّنْيَا وَالآخِرَةِ وَاللَّهُ يَعْلَمُ وَأَنْتُمْ لا تَعْلَمُونَ}
INSERT INTO [b9617] VALUES (2764,'<span data-type="title" id=toc-2749>٢٠ </span>- {وَلَوْلا فَضْلُ اللَّهِ عَلَيْكُمْ وَرَحْمَتُهُ وَأَنَّ اللَّهَ رَءُوفٌ رَحِيمٌ}
INSERT INTO [b9617] VALUES (2765,'<span data-type="title" id=toc-2750>٢١ </span>- {يَا أَيُّهَا الَّذِينَ آمَنُوا لا تَتَّبِعُوا خُطُوَاتِ الشَّيْطَانِ وَمَنْ يَتَّبِعْ خُطُوَاتِ الشَّيْطَانِ فَإِنَّهُ يَأْمُرُ بِالْفَحْشَاءِ وَالْمُنْكَرِ وَلَوْلا فَضْلُ اللَّهِ عَلَيْكُمْ وَرَحْمَتُهُ مَا زَكَا مِنْكُمْ مِنْ أَحَدٍ أَبَدًا وَلَكِنَّ اللَّهَ يُزَكِّي مَنْ يَشَاءُ}
INSERT INTO [b9617] VALUES (2766,'<span data-type="title" id=toc-2751>٢٢ </span>- {وَلا يَأْتَلِ أُولُو الْفَضْلِ مِنْكُمْ وَالسَّعَةِ أَنْ يُؤْتُوا أُولِي الْقُرْبَى وَالْمَسَاكِينَ وَالْمُهَاجِرِينَ فِي سَبِيلِ اللَّهِ وَلْيَعْفُوا وَلْيَصْفَحُوا أَلا تُحِبُّونَ أَنْ يَغْفِرَ اللَّهُ لَكُمْ}
INSERT INTO [b9617] VALUES (2767,'<span data-type="title" id=toc-2752>٢٣ </span>- {لُعِنُوا فِي الدُّنْيَا وَالآخِرَةِ وَلَهُمْ عَذَابٌ عَظِيمٌ}
INSERT INTO [b9617] VALUES (2768,'<span data-type="title" id=toc-2753>٢٤ </span>- {يَوْمَ تَشْهَدُ عَلَيْهِمْ أَلْسِنَتُهُمْ وَأَيْدِيهِمْ وَأَرْجُلُهُمْ بِمَا كَانُوا يَعْمَلُونَ}
INSERT INTO [b9617] VALUES (2769,'<span data-type="title" id=toc-2754>٢٥ </span>- {يَوْمَئِذٍ يُوَفِّيهِمُ اللَّهُ دِينَهُمُ الْحَقَّ وَيَعْلَمُونَ أَنَّ اللَّهَ هُوَ الْحَقُّ الْمُبِينُ}
INSERT INTO [b9617] VALUES (2770,'<span data-type="title" id=toc-2755>٢٦ </span>- {أُولَئِكَ مُبَرَّءُونَ مِمَّا يَقُولُونَ لَهُمْ مَغْفِرَةٌ وَرِزْقٌ كَرِيمٌ}
INSERT INTO [b9617] VALUES (2771,'<span data-type="title" id=toc-2756>٢٧ </span>- {يَا أَيُّهَا الَّذِينَ آمَنُوا لا تَدْخُلُوا بُيُوتًا غَيْرَ بُيُوتِكُمْ حَتَّى تَسْتَأْنِسُوا وَتُسَلِّمُوا عَلَى أَهْلِهَا ذَلِكُمْ خَيْرٌ لَكُمْ لَعَلَّكُمْ تَذَكَّرُونَ}
INSERT INTO [b9617] VALUES (2772,'<span data-type="title" id=toc-2757>٢٨ </span>- {فَإِنْ لَمْ تَجِدُوا فِيهَا أَحَدًا فَلا تَدْخُلُوهَا حَتَّى يُؤْذَنَ لَكُمْ وَإِنْ قِيلَ لَكُمُ ارْجِعُوا فَارْجِعُوا هُوَ أَزْكَى لَكُمْ}
INSERT INTO [b9617] VALUES (2773,'<span data-type="title" id=toc-2758>٢٩ </span>- {لَيْسَ عَلَيْكُمْ جُنَاحٌ أَنْ تَدْخُلُوا بُيُوتًا غَيْرَ مَسْكُونَةٍ فِيهَا مَتَاعٌ لَكُمْ وَاللَّهُ يَعْلَمُ ⦗٧٩٥⦘ مَا تُبْدُونَ وَمَا تَكْتُمُونَ}
INSERT INTO [b9617] VALUES (2774,'<span data-type="title" id=toc-2759>٣٠ </span>- {قُلْ لِلْمُؤْمِنِينَ يَغُضُّوا مِنْ أَبْصَارِهِمْ وَيَحْفَظُوا فُرُوجَهُمْ ذَلِكَ أَزْكَى لَهُمْ إِنَّ اللَّهَ خَبِيرٌ بِمَا يَصْنَعُونَ}
INSERT INTO [b9617] VALUES (2775,'<span data-type="title" id=toc-2760>٣١ </span>- {وَقُلْ لِلْمُؤْمِنَاتِ يَغْضُضْنَ مِنْ أَبْصَارِهِنَّ وَيَحْفَظْنَ فُرُوجَهُنَّ وَلا يُبْدِينَ زِينَتَهُنَّ إِلا مَا ظَهَرَ مِنْهَا وَلْيَضْرِبْنَ بِخُمُرِهِنَّ عَلَى جُيُوبِهِنَّ وَلا يُبْدِينَ زِينَتَهُنَّ إِلا لِبُعُولَتِهِنَّ أَوْ آبَائِهِنَّ أَوْ آبَاءِ بُعُولَتِهِنَّ أَوْ أَبْنَائِهِنَّ أَوْ أَبْنَاءِ بُعُولَتِهِنَّ أَوْ إِخْوَانِهِنَّ أَوْ بَنِي إِخْوَانِهِنَّ أَوْ بَنِي أَخَوَاتِهِنَّ أَوْ نِسَائِهِنَّ أَوْ مَا مَلَكَتْ أَيْمَانُهُنَّ أَوِ التَّابِعِينَ غَيْرِ أُولِي الإِرْبَةِ مِنَ الرِّجَالِ أَوِ الطِّفْلِ الَّذِينَ لَمْ يَظْهَرُوا عَلَى عَوْرَاتِ النِّسَاءِ وَلا يَضْرِبْنَ بِأَرْجُلِهِنَّ لِيُعْلَمَ مَا يُخْفِينَ مِنْ زِينَتِهِنَّ وَتُوبُوا إِلَى اللَّهِ جَمِيعًا أَيُّهَا الْمُؤْمِنُونَ لَعَلَّكُمْ تُفْلِحُونَ}
INSERT INTO [b9617] VALUES (2776,'بالفعل، والجار «لبعولتهن» متعلق بـ «يبدين» . قوله «أو ما» : اسم موصول معطوف على «نسائهن» ، قوله «أو التابعين» : معطوف على «ما» مجرور بالياء، «غير» نعت مجرور، الجار «من الرجال» متعلق بحال من «التابعين» ، «الذين» نعت، والمصدر المجرور «ليُعلم» متعلق بـ «يضربن» ، «ما» موصول نائب فاعل، الجار «من زينتهن» متعلق بحال من «ما» ، و «جميعا» حال من الواو، وجملة «أيها المؤمنون» مستأنفة، وكذا جملة «لعلكم تفلحون» .','2','796',NULL,NULL,'0');
INSERT INTO [b9617] VALUES (2777,'<span data-type="title" id=toc-2761>٣٢ </span>- {وَأَنْكِحُوا الأَيَامَى مِنْكُمْ وَالصَّالِحِينَ مِنْ عِبَادِكُمْ وَإِمَائِكُمْ إِنْ يَكُونُوا فُقَرَاءَ يُغْنِهِمُ اللَّهُ مِنْ فَضْلِهِ وَاللَّهُ وَاسِعٌ عَلِيمٌ}
INSERT INTO [b9617] VALUES (2778,'<span data-type="title" id=toc-2762>٣٣ </span>- {وَلْيَسْتَعْفِفِ الَّذِينَ لا يَجِدُونَ نِكَاحًا حَتَّى يُغْنِيَهُمُ اللَّهُ مِنْ فَضْلِهِ وَالَّذِينَ يَبْتَغُونَ الْكِتَابَ مِمَّا مَلَكَتْ أَيْمَانُكُمْ فَكَاتِبُوهُمْ إِنْ عَلِمْتُمْ فِيهِمْ خَيْرًا وَآتُوهُمْ مِنْ مَالِ اللَّهِ الَّذِي آتَاكُمْ وَلا تُكْرِهُوا فَتَيَاتِكُمْ عَلَى الْبِغَاءِ إِنْ أَرَدْنَ تَحَصُّنًا لِتَبْتَغُوا عَرَضَ الْحَيَاةِ الدُّنْيَا وَمَنْ يُكْرِهُّنَّ فَإِنَّ اللَّهَ مِنْ بَعْدِ إِكْرَاهِهِنَّ غَفُورٌ رَحِيمٌ}
INSERT INTO [b9617] VALUES (2779,'<span data-type="title" id=toc-2763>٣٤ </span>- {وَلَقَدْ أَنْزَلْنَا إِلَيْكُمْ آيَاتٍ مُبَيِّنَاتٍ وَمَثَلا مِنَ الَّذِينَ خَلَوْا مِنْ قَبْلِكُمْ وَمَوْعِظَةً لِلْمُتَّقِينَ}
INSERT INTO [b9617] VALUES (2780,'<span data-type="title" id=toc-2764>٣٥ </span>- {مَثَلُ نُورِهِ كَمِشْكَاةٍ فِيهَا مِصْبَاحٌ الْمِصْبَاحُ فِي زُجَاجَةٍ الزُّجَاجَةُ كَأَنَّهَا كَوْكَبٌ دُرِّيٌّ يُوقَدُ مِنْ شَجَرَةٍ مُبَارَكَةٍ زَيْتُونَةٍ لا شَرْقِيَّةٍ وَلا غَرْبِيَّةٍ يَكَادُ زَيْتُهَا يُضِيءُ وَلَوْ لَمْ تَمْسَسْهُ نَارٌ نُورٌ عَلَى نُورٍ يَهْدِي اللَّهُ لِنُورِهِ مَنْ يَشَاءُ وَيَضْرِبُ اللَّهُ الأَمْثَالَ لِلنَّاسِ وَاللَّهُ بِكُلِّ شَيْءٍ عَلِيمٌ}
INSERT INTO [b9617] VALUES (2781,'<span data-type="title" id=toc-2765>٣٦ </span>- {فِي بُيُوتٍ أَذِنَ اللَّهُ أَنْ تُرْفَعَ وَيُذْكَرَ فِيهَا اسْمُهُ يُسَبِّحُ لَهُ فِيهَا بِالْغُدُوِّ وَالآصَالِ}
INSERT INTO [b9617] VALUES (2782,'<span data-type="title" id=toc-2766>٣٧ </span>- {رِجَالٌ لا تُلْهِيهِمْ تِجَارَةٌ وَلا بَيْعٌ عَنْ ذِكْرِ اللَّهِ وَإِقَامِ الصَّلاةِ وَإِيتَاءِ الزَّكَاةِ يَخَافُونَ يَوْمًا تَتَقَلَّبُ فِيهِ الْقُلُوبُ وَالأَبْصَارُ}
INSERT INTO [b9617] VALUES (2783,'<span data-type="title" id=toc-2767>٣٨ </span>- {لِيَجْزِيَهُمُ اللَّهُ أَحْسَنَ مَا عَمِلُوا وَيَزِيدَهُمْ مِنْ فَضْلِهِ وَاللَّهُ يَرْزُقُ مَنْ يَشَاءُ بِغَيْرِ حِسَابٍ}
INSERT INTO [b9617] VALUES (2784,'<span data-type="title" id=toc-2768>٣٩ </span>- {وَالَّذِينَ كَفَرُوا أَعْمَالُهُمْ كَسَرَابٍ بِقِيعَةٍ يَحْسَبُهُ الظَّمْآنُ مَاءً حَتَّى إِذَا جَاءَهُ لَمْ يَجِدْهُ شَيْئًا وَوَجَدَ اللَّهَ عِنْدَهُ فَوَفَّاهُ حِسَابَهُ وَاللَّهُ سَرِيعُ الْحِسَابِ}
INSERT INTO [b9617] VALUES (2785,'<span data-type="title" id=toc-2769>٤٠ </span>- {أَوْ كَظُلُمَاتٍ فِي بَحْرٍ لُجِّيٍّ يَغْشَاهُ مَوْجٌ مِنْ فَوْقِهِ مَوْجٌ مِنْ فَوْقِهِ سَحَابٌ ظُلُمَاتٌ بَعْضُهَا فَوْقَ بَعْضٍ إِذَا أَخْرَجَ يَدَهُ لَمْ يَكَدْ يَرَاهَا وَمَنْ لَمْ يَجْعَلِ اللَّهُ لَهُ نُورًا فَمَا لَهُ مِنْ نُورٍ}
INSERT INTO [b9617] VALUES (2786,'<span data-type="title" id=toc-2770>٤١ </span>- {أَلَمْ تَرَ أَنَّ اللَّهَ يُسَبِّحُ لَهُ مَنْ فِي السَّمَاوَاتِ وَالأَرْضِ وَالطَّيْرُ صَافَّاتٍ كُلٌّ قَدْ عَلِمَ صَلاتَهُ ⦗٨٠٠⦘ وَتَسْبِيحَهُ وَاللَّهُ عَلِيمٌ بِمَا يَفْعَلُونَ}
INSERT INTO [b9617] VALUES (2787,'<span data-type="title" id=toc-2771>٤٢ </span>- {وَلِلَّهِ مُلْكُ السَّمَاوَاتِ وَالأَرْضِ وَإِلَى اللَّهِ الْمَصِيرُ}
INSERT INTO [b9617] VALUES (2788,'<span data-type="title" id=toc-2772>٤٣ </span>- {أَلَمْ تَرَ أَنَّ اللَّهَ يُزْجِي سَحَابًا ثُمَّ يُؤَلِّفُ بَيْنَهُ ثُمَّ يَجْعَلُهُ رُكَامًا فَتَرَى الْوَدْقَ يَخْرُجُ مِنْ خِلالِهِ وَيُنَزِّلُ مِنَ السَّمَاءِ مِنْ جِبَالٍ فِيهَا مِنْ بَرَدٍ فَيُصِيبُ بِهِ مَنْ يَشَاءُ وَيَصْرِفُهُ عَنْ مَنْ يَشَاءُ يَكَادُ سَنَا بَرْقِهِ يَذْهَبُ بِالأَبْصَارِ}
INSERT INTO [b9617] VALUES (2789,'<span data-type="title" id=toc-2773>٤٤ </span>- {يُقَلِّبُ اللَّهُ اللَّيْلَ وَالنَّهَارَ إِنَّ فِي ذَلِكَ لَعِبْرَةً لأُولِي الأَبْصَارِ}
INSERT INTO [b9617] VALUES (2790,'<span data-type="title" id=toc-2774>٤٥ </span>- {وَاللَّهُ خَلَقَ كُلَّ دَابَّةٍ مِنْ مَاءٍ فَمِنْهُمْ مَنْ يَمْشِي عَلَى بَطْنِهِ وَمِنْهُمْ مَنْ يَمْشِي عَلَى رِجْلَيْنِ وَمِنْهُمْ مَنْ يَمْشِي عَلَى أَرْبَعٍ يَخْلُقُ اللَّهُ مَا يَشَاءُ}
INSERT INTO [b9617] VALUES (2791,'<span data-type="title" id=toc-2775>٤٦ </span>- {لَقَدْ أَنْزَلْنَا آيَاتٍ مُبَيِّنَاتٍ وَاللَّهُ يَهْدِي مَنْ يَشَاءُ إِلَى صِرَاطٍ مُسْتَقِيمٍ}
INSERT INTO [b9617] VALUES (2792,'<span data-type="title" id=toc-2776>٤٧ </span>- {وَيَقُولُونَ آمَنَّا بِاللَّهِ وَبِالرَّسُولِ وَأَطَعْنَا ثُمَّ يَتَوَلَّى فَرِيقٌ مِنْهُمْ مِنْ بَعْدِ ذَلِكَ وَمَا أُولَئِكَ بِالْمُؤْمِنِينَ}
INSERT INTO [b9617] VALUES (2793,'<span data-type="title" id=toc-2777>٤٨ </span>- {وَإِذَا دُعُوا إِلَى اللَّهِ وَرَسُولِهِ لِيَحْكُمَ بَيْنَهُمْ إِذَا فَرِيقٌ مِنْهُمْ مُعْرِضُونَ}
INSERT INTO [b9617] VALUES (2794,'<span data-type="title" id=toc-2778>٤٩ </span>- {وَإِنْ يَكُنْ لَهُمُ الْحَقُّ يَأْتُوا إِلَيْهِ مُذْعِنِينَ}
INSERT INTO [b9617] VALUES (2795,'<span data-type="title" id=toc-2779>٥٠ </span>- {أَفِي قُلُوبِهِمْ مَرَضٌ أَمِ ارْتَابُوا أَمْ يَخَافُونَ أَنْ يَحِيفَ اللَّهُ عَلَيْهِمْ وَرَسُولُهُ بَلْ أُولَئِكَ هُمُ الظَّالِمُونَ}
INSERT INTO [b9617] VALUES (2796,'<span data-type="title" id=toc-2780>٥١ </span>- {إِنَّمَا كَانَ قَوْلَ الْمُؤْمِنِينَ إِذَا دُعُوا إِلَى اللَّهِ وَرَسُولِهِ لِيَحْكُمَ بَيْنَهُمْ أَنْ يَقُولُوا سَمِعْنَا وَأَطَعْنَا وَأُولَئِكَ هُمُ الْمُفْلِحُونَ}
INSERT INTO [b9617] VALUES (2797,'<span data-type="title" id=toc-2781>٥٢ </span>- {وَمَنْ يُطِعِ اللَّهَ وَرَسُولَهُ وَيَخْشَ اللَّهَ وَيَتَّقْهِ فَأُولَئِكَ هُمُ الْفَائِزُونَ}
INSERT INTO [b9617] VALUES (2798,'<span data-type="title" id=toc-2782>٥٣ </span>- {وَأَقْسَمُوا بِاللَّهِ جَهْدَ أَيْمَانِهِمْ لَئِنْ أَمَرْتَهُمْ لَيَخْرُجُنَّ قُلْ لا تُقْسِمُوا طَاعَةٌ مَعْرُوفَةٌ}
INSERT INTO [b9617] VALUES (2799,'<span data-type="title" id=toc-2783>٥٤ </span>- {قُلْ أَطِيعُوا اللَّهَ وَأَطِيعُوا الرَّسُولَ فَإِنْ تَوَلَّوْا فَإِنَّمَا عَلَيْهِ مَا حُمِّلَ وَعَلَيْكُمْ مَا حُمِّلْتُمْ وَإِنْ تُطِيعُوهُ تَهْتَدُوا وَمَا عَلَى الرَّسُولِ إِلا الْبَلاغُ الْمُبِينُ}
INSERT INTO [b9617] VALUES (2800,'<span data-type="title" id=toc-2784>٥٥ </span>- {وَعَدَ اللَّهُ الَّذِينَ آمَنُوا مِنْكُمْ وَعَمِلُوا الصَّالِحَاتِ لَيَسْتَخْلِفَنَّهُمْ فِي الأَرْضِ ⦗٨٠٤⦘ كَمَا اسْتَخْلَفَ الَّذِينَ مِنْ قَبْلِهِمْ وَلَيُمَكِّنَنَّ لَهُمْ دِينَهُمُ الَّذِي ارْتَضَى لَهُمْ وَلَيُبَدِّلَنَّهُمْ مِنْ بَعْدِ خَوْفِهِمْ أَمْنًا يَعْبُدُونَنِي لا يُشْرِكُونَ بِي شَيْئًا وَمَنْ كَفَرَ بَعْدَ ذَلِكَ فَأُولَئِكَ هُمُ الْفَاسِقُونَ}
INSERT INTO [b9617] VALUES (2801,'<span data-type="title" id=toc-2785>٥٦ </span>- {وَأَقِيمُوا الصَّلاةَ وَآتُوا الزَّكَاةَ وَأَطِيعُوا الرَّسُولَ لَعَلَّكُمْ تُرْحَمُونَ}
INSERT INTO [b9617] VALUES (2802,'<span data-type="title" id=toc-2786>٥٧ </span>- {لا تَحْسَبَنَّ الَّذِينَ كَفَرُوا مُعْجِزِينَ فِي الأَرْضِ وَمَأْوَاهُمُ النَّارُ وَلَبِئْسَ الْمَصِيرُ}
INSERT INTO [b9617] VALUES (2803,'<span data-type="title" id=toc-2787>٥٨ </span>- {يَا أَيُّهَا الَّذِينَ آمَنُوا لِيَسْتَأْذِنْكُمُ الَّذِينَ مَلَكَتْ أَيْمَانُكُمْ وَالَّذِينَ لَمْ يَبْلُغُوا الْحُلُمَ مِنْكُمْ','2','804',NULL,NULL,'0');
INSERT INTO [b9617] VALUES (2804,'ثَلاثَ مَرَّاتٍ مِنْ قَبْلِ صَلاةِ الْفَجْرِ وَحِينَ تَضَعُونَ ثِيَابَكُمْ مِنَ الظَّهِيرَةِ وَمِنْ بَعْدِ صَلاةِ الْعِشَاءِ ثَلاثُ عَوْرَاتٍ لَكُمْ لَيْسَ عَلَيْكُمْ وَلا عَلَيْهِمْ جُنَاحٌ بَعْدَهُنَّ طَوَّافُونَ عَلَيْكُمْ بَعْضُكُمْ عَلَى بَعْضٍ كَذَلِكَ يُبَيِّنُ اللَّهُ لَكُمُ الآيَاتِ وَاللَّهُ عَلِيمٌ حَكِيمٌ}
INSERT INTO [b9617] VALUES (2805,'<span data-type="title" id=toc-2788>٥٩ </span>- {وَإِذَا بَلَغَ الأَطْفَالُ مِنْكُمُ الْحُلُمَ فَلْيَسْتَأْذِنُوا كَمَا اسْتَأْذَنَ الَّذِينَ مِنْ قَبْلِهِمْ كَذَلِكَ يُبَيِّنُ اللَّهُ لَكُمْ آيَاتِهِ}
INSERT INTO [b9617] VALUES (2806,'<span data-type="title" id=toc-2789>٦٠ </span>- {وَالْقَوَاعِدُ مِنَ النِّسَاءِ اللاتِي لا يَرْجُونَ نِكَاحًا فَلَيْسَ عَلَيْهِنَّ جُنَاحٌ أَنْ يَضَعْنَ ثِيَابَهُنَّ غَيْرَ مُتَبَرِّجَاتٍ بِزِينَةٍ وَأَنْ يَسْتَعْفِفْنَ خَيْرٌ لَهُنَّ وَاللَّهُ سَمِيعٌ عَلِيمٌ}
INSERT INTO [b9617] VALUES (2807,'<span data-type="title" id=toc-2790>٦١ </span>- {لَيْسَ عَلَى الأَعْمَى حَرَجٌ وَلا عَلَى الأَعْرَجِ حَرَجٌ وَلا عَلَى الْمَرِيضِ حَرَجٌ وَلا عَلَى أَنْفُسِكُمْ أَنْ تَأْكُلُوا مِنْ بُيُوتِكُمْ أَوْ بُيُوتِ آبَائِكُمْ أَوْ بُيُوتِ أُمَّهَاتِكُمْ أَوْ بُيُوتِ إِخْوَانِكُمْ أَوْ بُيُوتِ أَخَوَاتِكُمْ أَوْ بُيُوتِ أَعْمَامِكُمْ أَوْ بُيُوتِ عَمَّاتِكُمْ أَوْ بُيُوتِ أَخْوَالِكُمْ أَوْ بُيُوتِ خَالاتِكُمْ أَوْ مَا مَلَكْتُمْ مَفَاتِحَهُ أَوْ صَدِيقِكُمْ لَيْسَ عَلَيْكُمْ جُنَاحٌ أَنْ تَأْكُلُوا جَمِيعًا أَوْ أَشْتَاتًا فَإِذَا دَخَلْتُمْ بُيُوتًا فَسَلِّمُوا عَلَى أَنْفُسِكُمْ تَحِيَّةً مِنْ عِنْدِ اللَّهِ مُبَارَكَةً طَيِّبَةً كَذَلِكَ يُبَيِّنُ اللَّهُ لَكُمُ الآيَاتِ لَعَلَّكُمْ تَعْقِلُونَ}
INSERT INTO [b9617] VALUES (2808,'الواو عاطفة، لا نافية، والجار معطوف على «على المريض» ويتعلق بما تعلق به، «أن» ناصبة، والمصدر منصوب على نزع الخافض أي: ولا على أنفسكم حرج في أن تأكلوا، وقوله «أو ما ملكتم» : اسم موصول مجرور معطوف على «خالاتكم» . وقوله «أو صديقكم» : اسم معطوف على «ما» مجرور، وجملة «ليس عليكم جناح» مستأنفة، والمصدر «أن تأكلوا» منصوب على نزع الخافض (في) ، و «جميعا» حال من الواو، وجملة الشرط مستأنفة، و «إذا» ظرفية شرطية متعلقة بمعنى الجواب، «تحية» نائب مفعول مطلق مرادف لعامله، والجار متعلق بنعت لـ «تحية» ، وجملة «يبيِّن» مستأنفة، والكاف نائب مفعول مطلق، وجملة «لعلكم تعقلون» مستأنفة.','2','807',NULL,NULL,'0');
INSERT INTO [b9617] VALUES (2809,'<span data-type="title" id=toc-2791>٦٢ </span>- {إِنَّمَا الْمُؤْمِنُونَ الَّذِينَ آمَنُوا بِاللَّهِ وَرَسُولِهِ وَإِذَا كَانُوا مَعَهُ عَلَى أَمْرٍ جَامِعٍ لَمْ يَذْهَبُوا حَتَّى يَسْتَأْذِنُوهُ إِنَّ الَّذِينَ يَسْتَأْذِنُونَكَ أُولَئِكَ الَّذِينَ يُؤْمِنُونَ بِاللَّهِ وَرَسُولِهِ فَإِذَا اسْتَأْذَنُوكَ لِبَعْضِ شَأْنِهِمْ فَأْذَنْ لِمَنْ شِئْتَ مِنْهُمْ}
INSERT INTO [b9617] VALUES (2810,'<span data-type="title" id=toc-2792>٦٣ </span>- {لا تَجْعَلُوا دُعَاءَ الرَّسُولِ بَيْنَكُمْ كَدُعَاءِ بَعْضِكُمْ بَعْضًا قَدْ يَعْلَمُ اللَّهُ الَّذِينَ يَتَسَلَّلُونَ مِنْكُمْ لِوَاذًا فَلْيَحْذَرِ الَّذِينَ يُخَالِفُونَ عَنْ أَمْرِهِ أَنْ تُصِيبَهُمْ فِتْنَةٌ أَوْ يُصِيبَهُمْ عَذَابٌ أَلِيمٌ} ⦗٨٠٨⦘
INSERT INTO [b9617] VALUES (2811,'<span data-type="title" id=toc-2793>٦٤ </span>- {أَلا إِنَّ لِلَّهِ مَا فِي السَّمَاوَاتِ وَالأَرْضِ قَدْ يَعْلَمُ مَا أَنْتُمْ عَلَيْهِ وَيَوْمَ يُرْجَعُونَ إِلَيْهِ فَيُنَبِّئُهُمْ بِمَا عَمِلُوا وَاللَّهُ بِكُلِّ شَيْءٍ عَلِيمٌ}
INSERT INTO [b9617] VALUES (2812,'<span data-type="title" id=toc-2794>سورة الفرقان</span>','3','809',NULL,NULL,'0');
INSERT INTO [b9617] VALUES (2813,'<span data-type="title" id=toc-2795>١ </span>- {تَبَارَكَ الَّذِي نَزَّلَ الْفُرْقَانَ عَلَى عَبْدِهِ لِيَكُونَ لِلْعَالَمِينَ نَذِيرًا}
INSERT INTO [b9617] VALUES (2814,'<span data-type="title" id=toc-2796>٢ </span>- {الَّذِي لَهُ مُلْكُ السَّمَاوَاتِ وَالأَرْضِ وَلَمْ يَتَّخِذْ وَلَدًا وَلَمْ يَكُنْ لَهُ شَرِيكٌ فِي الْمُلْكِ}
INSERT INTO [b9617] VALUES (2815,'<span data-type="title" id=toc-2797>٣ </span>- {وَاتَّخَذُوا مِنْ دُونِهِ آلِهَةً لا يَخْلُقُونَ شَيْئًا وَهُمْ يُخْلَقُونَ}
INSERT INTO [b9617] VALUES (2816,'<span data-type="title" id=toc-2798>٤ </span>- {إِنْ هَذَا إِلا إِفْكٌ افْتَرَاهُ وَأَعَانَهُ عَلَيْهِ قَوْمٌ آخَرُونَ فَقَدْ جَاءُوا ظُلْمًا وَزُورًا}
INSERT INTO [b9617] VALUES (2817,'<span data-type="title" id=toc-2799>٥ </span>- {وَقَالُوا أَسَاطِيرُ الأَوَّلِينَ اكْتَتَبَهَا فَهِيَ تُمْلَى عَلَيْهِ بُكْرَةً وَأَصِيلا}
INSERT INTO [b9617] VALUES (2818,'<span data-type="title" id=toc-2800>٦ </span>- {يَعْلَمُ السِّرَّ فِي السَّمَاوَاتِ وَالأَرْضِ}
INSERT INTO [b9617] VALUES (2819,'<span data-type="title" id=toc-2801>٧ </span>- {وَقَالُوا مَالِ هَذَا الرَّسُولِ يَأْكُلُ الطَّعَامَ وَيَمْشِي فِي الأَسْوَاقِ لَوْلا أُنْزِلَ إِلَيْهِ مَلَكٌ فَيَكُونَ مَعَهُ نَذِيرًا}
INSERT INTO [b9617] VALUES (2820,'<span data-type="title" id=toc-2802>٨ </span>- {وَقَالَ الظَّالِمُونَ إِنْ تَتَّبِعُونَ إِلا رَجُلا مَسْحُورًا}
INSERT INTO [b9617] VALUES (2821,'<span data-type="title" id=toc-2803>٩ </span>- {انْظُرْ كَيْفَ ضَرَبُوا لَكَ الأَمْثَالَ فَضَلُّوا فَلا يَسْتَطِيعُونَ سَبِيلا}
INSERT INTO [b9617] VALUES (2822,'<span data-type="title" id=toc-2804>١٠ </span>- {تَبَارَكَ الَّذِي إِنْ شَاءَ جَعَلَ لَكَ خَيْرًا مِنْ ذَلِكَ جَنَّاتٍ تَجْرِي مِنْ تَحْتِهَا الأَنْهَارُ وَيَجْعَلْ لَكَ قُصُورًا}
INSERT INTO [b9617] VALUES (2823,'<span data-type="title" id=toc-2805>١١ </span>- {بَلْ كَذَّبُوا بِالسَّاعَةِ وَأَعْتَدْنَا لِمَنْ كَذَّبَ بِالسَّاعَةِ سَعِيرًا}
INSERT INTO [b9617] VALUES (2824,'<span data-type="title" id=toc-2806>١٢ </span>- {إِذَا رَأَتْهُمْ مِنْ مَكَانٍ بَعِيدٍ سَمِعُوا لَهَا تَغَيُّظًا}
INSERT INTO [b9617] VALUES (2825,'<span data-type="title" id=toc-2807>١٣ </span>- {وَإِذَا أُلْقُوا مِنْهَا مَكَانًا ضَيِّقًا مُقَرَّنِينَ دَعَوْا هُنَالِكَ ثُبُورًا}
INSERT INTO [b9617] VALUES (2826,'<span data-type="title" id=toc-2808>١٤ </span>- {لا تَدْعُوا الْيَوْمَ ثُبُورًا وَاحِدًا}
INSERT INTO [b9617] VALUES (2827,'<span data-type="title" id=toc-2809>١٥ </span>- {قُلْ أَذَلِكَ خَيْرٌ أَمْ جَنَّةُ الْخُلْدِ الَّتِي وُعِدَ الْمُتَّقُونَ كَانَتْ لَهُمْ جَزَاءً وَمَصِيرًا}
INSERT INTO [b9617] VALUES (2828,'<span data-type="title" id=toc-2810>١٦ </span>- {لَهُمْ فِيهَا مَا يَشَاءُونَ خَالِدِينَ كَانَ عَلَى رَبِّكَ وَعْدًا مَسْئُولا}
INSERT INTO [b9617] VALUES (2829,'<span data-type="title" id=toc-2811>١٧ </span>- {وَيَوْمَ يَحْشُرُهُمْ وَمَا يَعْبُدُونَ مِنْ دُونِ اللَّهِ فَيَقُولُ أَأَنْتُمْ أَضْلَلْتُمْ عِبَادِي هَؤُلاءِ أَمْ هُمْ ضَلُّوا السَّبِيلَ}
INSERT INTO [b9617] VALUES (2830,'<span data-type="title" id=toc-2812>١٨ </span>- {قَالُوا سُبْحَانَكَ مَا كَانَ يَنْبَغِي لَنَا أَنْ نَتَّخِذَ مِنْ دُونِكَ مِنْ أَوْلِيَاءَ وَلَكِنْ مَتَّعْتَهُمْ ⦗٨١٣⦘ وَآبَاءَهُمْ حَتَّى نَسُوا الذِّكْرَ}
INSERT INTO [b9617] VALUES (2831,'<span data-type="title" id=toc-2813>١٩ </span>- {فَقَدْ كَذَّبُوكُمْ بِمَا تَقُولُونَ فَمَا تَسْتَطِيعُونَ صَرْفًا وَلا نَصْرًا وَمَنْ يَظْلِمْ مِنْكُمْ نُذِقْهُ عَذَابًا كَبِيرًا}
INSERT INTO [b9617] VALUES (2832,'<span data-type="title" id=toc-2814>٢٠ </span>- {وَمَا أَرْسَلْنَا قَبْلَكَ مِنَ الْمُرْسَلِينَ إِلا إِنَّهُمْ لَيَأْكُلُونَ الطَّعَامَ وَيَمْشُونَ فِي الأَسْوَاقِ وَجَعَلْنَا بَعْضَكُمْ لِبَعْضٍ فِتْنَةً أَتَصْبِرُونَ وَكَانَ رَبُّكَ بَصِيرًا}
INSERT INTO [b9617] VALUES (2833,'<span data-type="title" id=toc-2815>٢١ </span>- {وَقَالَ الَّذِينَ لا يَرْجُونَ لِقَاءَنَا لَوْلا أُنْزِلَ عَلَيْنَا الْمَلائِكَةُ أَوْ نَرَى رَبَّنَا لَقَدِ ⦗٨١٤⦘ اسْتَكْبَرُوا فِي أَنْفُسِهِمْ وَعَتَوْا عُتُوًّا كَبِيرًا}
INSERT INTO [b9617] VALUES (2834,'<span data-type="title" id=toc-2816>٢٢ </span>- {يَوْمَ يَرَوْنَ الْمَلائِكَةَ لا بُشْرَى يَوْمَئِذٍ لِلْمُجْرِمِينَ وَيَقُولُونَ حِجْرًا مَحْجُورًا}
INSERT INTO [b9617] VALUES (2835,'<span data-type="title" id=toc-2817>٢٣ </span>- {وَقَدِمْنَا إِلَى مَا عَمِلُوا مِنْ عَمَلٍ فَجَعَلْنَاهُ هَبَاءً مَنْثُورًا}
INSERT INTO [b9617] VALUES (2836,'<span data-type="title" id=toc-2818>٢٤ </span>- {أَصْحَابُ الْجَنَّةِ يَوْمَئِذٍ خَيْرٌ مُسْتَقَرًّا}
INSERT INTO [b9617] VALUES (2837,'<span data-type="title" id=toc-2819>٢٥ </span>- {وَيَوْمَ تَشَقَّقُ السَّمَاءُ بِالْغَمَامِ وَنُزِّلَ الْمَلائِكَةُ تَنْزِيلا}
INSERT INTO [b9617] VALUES (2838,'<span data-type="title" id=toc-2820>٢٦ </span>- {الْمُلْكُ يَوْمَئِذٍ الْحَقُّ لِلرَّحْمَنِ وَكَانَ يَوْمًا عَلَى الْكَافِرِينَ عَسِيرًا}
INSERT INTO [b9617] VALUES (2839,'<span data-type="title" id=toc-2821>٢٧ </span>- {وَيَوْمَ يَعَضُّ الظَّالِمُ عَلَى يَدَيْهِ يَقُولُ يَا لَيْتَنِي اتَّخَذْتُ مَعَ الرَّسُولِ سَبِيلا}
INSERT INTO [b9617] VALUES (2840,'<span data-type="title" id=toc-2822>٢٨ </span>- {يَا وَيْلَتَى لَيْتَنِي لَمْ أَتَّخِذْ فُلانًا خَلِيلا}
INSERT INTO [b9617] VALUES (2841,'<span data-type="title" id=toc-2823>٢٩ </span>- {لَقَدْ أَضَلَّنِي عَنِ الذِّكْرِ بَعْدَ إِذْ جَاءَنِي وَكَانَ الشَّيْطَانُ لِلإِنْسَانِ خَذُولا}
INSERT INTO [b9617] VALUES (2842,'<span data-type="title" id=toc-2824>٣٠ </span>- {وَقَالَ الرَّسُولُ يَا رَبِّ إِنَّ قَوْمِي اتَّخَذُوا هَذَا الْقُرْآنَ مَهْجُورًا}
INSERT INTO [b9617] VALUES (2843,'<span data-type="title" id=toc-2825>٣١ </span>- {وَكَذَلِكَ جَعَلْنَا لِكُلِّ نَبِيٍّ عَدُوًّا مِنَ الْمُجْرِمِينَ وَكَفَى بِرَبِّكَ هَادِيًا وَنَصِيرًا}
INSERT INTO [b9617] VALUES (2844,'<span data-type="title" id=toc-2826>٣٢ </span>- {وَقَالَ الَّذِينَ كَفَرُوا لَوْلا نُزِّلَ عَلَيْهِ الْقُرْآنُ جُمْلَةً وَاحِدَةً كَذَلِكَ لِنُثَبِّتَ بِهِ فُؤَادَكَ وَرَتَّلْنَاهُ تَرْتِيلا}
INSERT INTO [b9617] VALUES (2845,'<span data-type="title" id=toc-2827>٣٣ </span>- {وَلا يَأْتُونَكَ بِمَثَلٍ إِلا جِئْنَاكَ بِالْحَقِّ وَأَحْسَنَ تَفْسِيرًا}
INSERT INTO [b9617] VALUES (2846,'<span data-type="title" id=toc-2828>٣٤ </span>- {الَّذِينَ يُحْشَرُونَ عَلَى وُجُوهِهِمْ إِلَى جَهَنَّمَ أُولَئِكَ شَرٌّ مَكَانًا}
INSERT INTO [b9617] VALUES (2847,'<span data-type="title" id=toc-2829>٣٥ </span>- {وَلَقَدْ آتَيْنَا مُوسَى الْكِتَابَ وَجَعَلْنَا مَعَهُ أَخَاهُ هَارُونَ وَزِيرًا}
INSERT INTO [b9617] VALUES (2848,'<span data-type="title" id=toc-2830>٣٦ </span>- {فَقُلْنَا اذْهَبَا إِلَى الْقَوْمِ الَّذِينَ كَذَّبُوا بِآيَاتِنَا فَدَمَّرْنَاهُمْ تَدْمِيرًا}
INSERT INTO [b9617] VALUES (2849,'<span data-type="title" id=toc-2831>٣٧ </span>- {وَقَوْمَ نُوحٍ لَمَّا كَذَّبُوا الرُّسُلَ أَغْرَقْنَاهُمْ وَجَعَلْنَاهُمْ لِلنَّاسِ آيَةً وَأَعْتَدْنَا لِلظَّالِمِينَ عَذَابًا أَلِيمًا}
INSERT INTO [b9617] VALUES (2850,'<span data-type="title" id=toc-2832>٣٨ </span>- {وَعَادًا وَثَمُودَ وَأَصْحَابَ الرَّسِّ وَقُرُونًا بَيْنَ ذَلِكَ كَثِيرًا}
INSERT INTO [b9617] VALUES (2851,'<span data-type="title" id=toc-2833>٣٩ </span>- {وَكُلا ضَرَبْنَا لَهُ الأَمْثَالَ وَكُلا تَبَّرْنَا تَتْبِيرًا}
INSERT INTO [b9617] VALUES (2852,'<span data-type="title" id=toc-2834>٤٠ </span>- {وَلَقَدْ أَتَوْا عَلَى الْقَرْيَةِ الَّتِي أُمْطِرَتْ مَطَرَ السَّوْءِ أَفَلَمْ يَكُونُوا يَرَوْنَهَا بَلْ كَانُوا لا يَرْجُونَ نُشُورًا}
INSERT INTO [b9617] VALUES (2853,'<span data-type="title" id=toc-2835>٤١ </span>- {وَإِذَا رَأَوْكَ إِنْ يَتَّخِذُونَكَ إِلا هُزُوًا أَهَذَا الَّذِي بَعَثَ اللَّهُ رَسُولا}
INSERT INTO [b9617] VALUES (2854,'<span data-type="title" id=toc-2836>٤٢ </span>- {إِنْ كَادَ لَيُضِلُّنَا عَنْ آلِهَتِنَا لَوْلا أَنْ صَبَرْنَا عَلَيْهَا وَسَوْفَ يَعْلَمُونَ حِينَ يَرَوْنَ الْعَذَابَ مَنْ أَضَلُّ سَبِيلا}
INSERT INTO [b9617] VALUES (2855,'<span data-type="title" id=toc-2837>٤٣ </span>- {أَرَأَيْتَ مَنِ اتَّخَذَ إِلَهَهُ هَوَاهُ أَفَأَنْتَ تَكُونُ عَلَيْهِ وَكِيلا}
INSERT INTO [b9617] VALUES (2856,'<span data-type="title" id=toc-2838>٤٤ </span>- {أَمْ تَحْسَبُ أَنَّ أَكْثَرَهُمْ يَسْمَعُونَ أَوْ يَعْقِلُونَ إِنْ هُمْ إِلا كَالأَنْعَامِ بَلْ هُمْ أَضَلُّ سَبِيلا}
INSERT INTO [b9617] VALUES (2857,'<span data-type="title" id=toc-2839>٤٥ </span>- {أَلَمْ تَرَ إِلَى رَبِّكَ كَيْفَ مَدَّ الظِّلَّ وَلَوْ شَاءَ لَجَعَلَهُ سَاكِنًا ثُمَّ جَعَلْنَا الشَّمْسَ عَلَيْهِ دَلِيلا}
INSERT INTO [b9617] VALUES (2858,'<span data-type="title" id=toc-2840>٤٦ </span>- {ثُمَّ قَبَضْنَاهُ إِلَيْنَا قَبْضًا يَسِيرًا}
INSERT INTO [b9617] VALUES (2859,'<span data-type="title" id=toc-2841>٤٧ </span>- {وَهُوَ الَّذِي جَعَلَ لَكُمُ اللَّيْلَ لِبَاسًا وَالنَّوْمَ سُبَاتًا وَجَعَلَ النَّهَارَ نُشُورًا}
INSERT INTO [b9617] VALUES (2860,'<span data-type="title" id=toc-2842>٤٨ </span>- {وَهُوَ الَّذِي أَرْسَلَ الرِّيَاحَ بُشْرًا بَيْنَ يَدَيْ رَحْمَتِهِ}
INSERT INTO [b9617] VALUES (2861,'<span data-type="title" id=toc-2843>٤٩ </span>- {لِنُحْيِيَ بِهِ بَلْدَةً مَيْتًا وَنُسْقِيَهُ مِمَّا خَلَقْنَا أَنْعَامًا وَأَنَاسِيَّ كَثِيرًا}
INSERT INTO [b9617] VALUES (2862,'<span data-type="title" id=toc-2844>٥٠ </span>- {وَلَقَدْ صَرَّفْنَاهُ بَيْنَهُمْ لِيَذَّكَّرُوا فَأَبَى أَكْثَرُ النَّاسِ إِلا كُفُورًا}
INSERT INTO [b9617] VALUES (2863,'<span data-type="title" id=toc-2845>٥١ </span>- {وَلَوْ شِئْنَا لَبَعَثْنَا فِي كُلِّ قَرْيَةٍ نَذِيرًا}
INSERT INTO [b9617] VALUES (2864,'<span data-type="title" id=toc-2846>٥٢ </span>- {فَلا تُطِعِ الْكَافِرِينَ}
INSERT INTO [b9617] VALUES (2865,'<span data-type="title" id=toc-2847>٥٣ </span>- {وَهُوَ الَّذِي مَرَجَ الْبَحْرَيْنِ هَذَا عَذْبٌ فُرَاتٌ وَهَذَا مِلْحٌ أُجَاجٌ وَجَعَلَ بَيْنَهُمَا بَرْزَخًا}
INSERT INTO [b9617] VALUES (2866,'<span data-type="title" id=toc-2848>٥٤ </span>- {وَهُوَ الَّذِي خَلَقَ مِنَ الْمَاءِ بَشَرًا فَجَعَلَهُ نَسَبًا وَصِهْرًا وَكَانَ رَبُّكَ قَدِيرًا}
INSERT INTO [b9617] VALUES (2867,'<span data-type="title" id=toc-2849>٥٥ </span>- {وَيَعْبُدُونَ مِنْ دُونِ اللَّهِ مَا لا يَنْفَعُهُمْ وَلا يَضُرُّهُمْ وَكَانَ الْكَافِرُ عَلَى رَبِّهِ ظَهِيرًا}
INSERT INTO [b9617] VALUES (2868,'<span data-type="title" id=toc-2850>٥٦ </span>- {وَمَا أَرْسَلْنَاكَ إِلا مُبَشِّرًا وَنَذِيرًا}
INSERT INTO [b9617] VALUES (2869,'<span data-type="title" id=toc-2851>٥٧ </span>- {قُلْ مَا أَسْأَلُكُمْ عَلَيْهِ مِنْ أَجْرٍ إِلا مَنْ شَاءَ أَنْ يَتَّخِذَ إِلَى رَبِّهِ سَبِيلا}
INSERT INTO [b9617] VALUES (2870,'<span data-type="title" id=toc-2852>٥٨ </span>- {وَتَوَكَّلْ عَلَى الْحَيِّ الَّذِي لا يَمُوتُ وَسَبِّحْ بِحَمْدِهِ وَكَفَى بِهِ بِذُنُوبِ عِبَادِهِ خَبِيرًا}
INSERT INTO [b9617] VALUES (2871,'<span data-type="title" id=toc-2853>٥٩ </span>- {الَّذِي خَلَقَ السَّمَاوَاتِ وَالأَرْضَ وَمَا بَيْنَهُمَا فِي سِتَّةِ أَيَّامٍ ثُمَّ اسْتَوَى عَلَى الْعَرْشِ الرَّحْمَنُ فَاسْأَلْ بِهِ خَبِيرًا}
INSERT INTO [b9617] VALUES (2872,'<span data-type="title" id=toc-2854>٦٠ </span>- {وَإِذَا قِيلَ لَهُمُ اسْجُدُوا لِلرَّحْمَنِ قَالُوا وَمَا الرَّحْمَنُ أَنَسْجُدُ لِمَا تَأْمُرُنَا وَزَادَهُمْ نُفُورًا}
INSERT INTO [b9617] VALUES (2873,'<span data-type="title" id=toc-2855>٦١ </span>- {جَعَلَ فِي السَّمَاءِ بُرُوجًا}
INSERT INTO [b9617] VALUES (2874,'<span data-type="title" id=toc-2856>٦٢ </span>- {وَهُوَ الَّذِي جَعَلَ اللَّيْلَ وَالنَّهَارَ خِلْفَةً لِمَنْ أَرَادَ أَنْ يَذَّكَّرَ أَوْ أَرَادَ شُكُورًا}
INSERT INTO [b9617] VALUES (2875,'<span data-type="title" id=toc-2857>٦٣ </span>- {وَعِبَادُ الرَّحْمَنِ الَّذِينَ يَمْشُونَ عَلَى الأَرْضِ هَوْنًا وَإِذَا خَاطَبَهُمُ الْجَاهِلُونَ قَالُوا سَلامًا}
INSERT INTO [b9617] VALUES (2876,'<span data-type="title" id=toc-2858>٦٤ </span>- {وَالَّذِينَ يَبِيتُونَ لِرَبِّهِمْ سُجَّدًا وَقِيَامًا}
INSERT INTO [b9617] VALUES (2877,'<span data-type="title" id=toc-2859>٦٥ </span>- {إِنَّ عَذَابَهَا كَانَ غَرَامًا}
INSERT INTO [b9617] VALUES (2878,'<span data-type="title" id=toc-2860>٦٦ </span>- {سَاءَتْ مُسْتَقَرًّا وَمُقَامًا}
INSERT INTO [b9617] VALUES (2879,'<span data-type="title" id=toc-2861>٦٧ </span>- {وَالَّذِينَ إِذَا أَنْفَقُوا لَمْ يُسْرِفُوا وَلَمْ يَقْتُرُوا وَكَانَ بَيْنَ ذَلِكَ قَوَامًا}
INSERT INTO [b9617] VALUES (2880,'<span data-type="title" id=toc-2862>٦٨ </span>- {وَالَّذِينَ لا يَدْعُونَ مَعَ اللَّهِ إِلَهًا آخَرَ وَلا يَقْتُلُونَ النَّفْسَ الَّتِي حَرَّمَ اللَّهُ إِلا بِالْحَقِّ وَلا يَزْنُونَ وَمَنْ يَفْعَلْ ذَلِكَ يَلْقَ أَثَامًا}
INSERT INTO [b9617] VALUES (2881,'<span data-type="title" id=toc-2863>٦٩ </span>- {يُضَاعَفْ لَهُ الْعَذَابُ يَوْمَ الْقِيَامَةِ وَيَخْلُدْ فِيهِ مُهَانًا} ⦗٨٢٥⦘
INSERT INTO [b9617] VALUES (2882,'<span data-type="title" id=toc-2864>٧٠ </span>- {إِلا مَنْ تَابَ وَآمَنَ وَعَمِلَ عَمَلا صَالِحًا فَأُولَئِكَ يُبَدِّلُ اللَّهُ سَيِّئَاتِهِمْ حَسَنَاتٍ}
INSERT INTO [b9617] VALUES (2883,'<span data-type="title" id=toc-2865>٧١ </span>- {وَمَنْ تَابَ وَعَمِلَ صَالِحًا فَإِنَّهُ يَتُوبُ إِلَى اللَّهِ مَتَابًا}
INSERT INTO [b9617] VALUES (2884,'<span data-type="title" id=toc-2866>٧٢ </span>- {وَالَّذِينَ لا يَشْهَدُونَ الزُّورَ وَإِذَا مَرُّوا بِاللَّغْوِ مَرُّوا كِرَامًا}
INSERT INTO [b9617] VALUES (2885,'<span data-type="title" id=toc-2867>٧٣ </span>- {وَالَّذِينَ إِذَا ذُكِّرُوا بِآيَاتِ رَبِّهِمْ لَمْ يَخِرُّوا عَلَيْهَا صُمًّا وَعُمْيَانًا}
INSERT INTO [b9617] VALUES (2886,'<span data-type="title" id=toc-2868>٧٤ </span>- {رَبَّنَا هَبْ لَنَا مِنْ أَزْوَاجِنَا وَذُرِّيَّاتِنَا قُرَّةَ أَعْيُنٍ وَاجْعَلْنَا لِلْمُتَّقِينَ إِمَامًا}
INSERT INTO [b9617] VALUES (2887,'<span data-type="title" id=toc-2869>٧٥ </span>- {أُولَئِكَ يُجْزَوْنَ الْغُرْفَةَ بِمَا صَبَرُوا وَيُلَقَّوْنَ فِيهَا تَحِيَّةً وَسَلامًا} ⦗٨٢٦⦘
INSERT INTO [b9617] VALUES (2888,'<span data-type="title" id=toc-2870>٧٦ </span>- {خَالِدِينَ فِيهَا حَسُنَتْ مُسْتَقَرًّا وَمُقَامًا}
INSERT INTO [b9617] VALUES (2889,'<span data-type="title" id=toc-2871>٧٧ </span>- {قُلْ مَا يَعْبَأُ بِكُمْ رَبِّي لَوْلا دُعَاؤُكُمْ فَقَدْ كَذَّبْتُمْ فَسَوْفَ يَكُونُ لِزَامًا}
INSERT INTO [b9617] VALUES (2890,'<span data-type="title" id=toc-2872>سورة الشعراء</span>','3','827',NULL,NULL,'0');
INSERT INTO [b9617] VALUES (2891,'<span data-type="title" id=toc-2873>٢ </span>- {تِلْكَ آيَاتُ الْكِتَابِ الْمُبِينِ}
INSERT INTO [b9617] VALUES (2892,'<span data-type="title" id=toc-2874>٣ </span>- {لَعَلَّكَ بَاخِعٌ نَفْسَكَ أَلا يَكُونُوا مُؤْمِنِينَ}
INSERT INTO [b9617] VALUES (2893,'<span data-type="title" id=toc-2875>٤ </span>- {إِنْ نَشَأْ نُنَزِّلْ عَلَيْهِمْ مِنَ السَّمَاءِ آيَةً فَظَلَّتْ أَعْنَاقُهُمْ لَهَا خَاضِعِينَ}
INSERT INTO [b9617] VALUES (2894,'<span data-type="title" id=toc-2876>٥ </span>- {وَمَا يَأْتِيهِمْ مِنْ ذِكْرٍ مِنَ الرَّحْمَنِ مُحْدَثٍ إِلا كَانُوا عَنْهُ مُعْرِضِينَ}
INSERT INTO [b9617] VALUES (2895,'<span data-type="title" id=toc-2877>٦ </span>- {فَقَدْ كَذَّبُوا فَسَيَأْتِيهِمْ أَنْبَاءُ مَا كَانُوا بِهِ يَسْتَهْزِئُونَ}
INSERT INTO [b9617] VALUES (2896,'<span data-type="title" id=toc-2878>٧ </span>- {أَوَلَمْ يَرَوْا إِلَى الأَرْضِ كَمْ أَنْبَتْنَا فِيهَا مِنْ كُلِّ زَوْجٍ كَرِيمٍ} ⦗٨٢٨⦘
INSERT INTO [b9617] VALUES (2897,'<span data-type="title" id=toc-2879>٨ </span>- {إِنَّ فِي ذَلِكَ لآيَةً وَمَا كَانَ أَكْثَرُهُمْ مُؤْمِنِينَ}
INSERT INTO [b9617] VALUES (2898,'<span data-type="title" id=toc-2880>٩ </span>- {وَإِنَّ رَبَّكَ لَهُوَ الْعَزِيزُ الرَّحِيمُ}
INSERT INTO [b9617] VALUES (2899,'<span data-type="title" id=toc-2881>١٠ </span>- {وَإِذْ نَادَى رَبُّكَ مُوسَى أَنِ ائْتِ الْقَوْمَ الظَّالِمِينَ}
INSERT INTO [b9617] VALUES (2900,'<span data-type="title" id=toc-2882>١١ </span>- {قَوْمَ فِرْعَوْنَ أَلا يَتَّقُونَ}
INSERT INTO [b9617] VALUES (2901,'<span data-type="title" id=toc-2883>١٢ </span>- {قَالَ رَبِّ إِنِّي أَخَافُ أَنْ يُكَذِّبُونِ}
INSERT INTO [b9617] VALUES (2902,'<span data-type="title" id=toc-2884>١٣ </span>- {وَيَضِيقُ صَدْرِي وَلا يَنْطَلِقُ لِسَانِي فَأَرْسِلْ إِلَى هَارُونَ}
INSERT INTO [b9617] VALUES (2903,'<span data-type="title" id=toc-2885>١٤ </span>- {وَلَهُمْ عَلَيَّ ذَنْبٌ فَأَخَافُ أَنْ يَقْتُلُونِ}
INSERT INTO [b9617] VALUES (2904,'<span data-type="title" id=toc-2886>١٥ </span>- {قَالَ كَلا فَاذْهَبَا بِآيَاتِنَا إِنَّا مَعَكُمْ مُسْتَمِعُونَ}
INSERT INTO [b9617] VALUES (2905,'<span data-type="title" id=toc-2887>١٦ </span>- {فَأْتِيَا فِرْعَوْنَ فَقُولا إِنَّا رَسُولُ رَبِّ الْعَالَمِينَ}
INSERT INTO [b9617] VALUES (2906,'<span data-type="title" id=toc-2888>١٧ </span>- {أَنْ أَرْسِلْ مَعَنَا بَنِي إِسْرَائِيلَ}
INSERT INTO [b9617] VALUES (2907,'<span data-type="title" id=toc-2889>١٨ </span>- {قَالَ أَلَمْ نُرَبِّكَ فِينَا وَلِيدًا وَلَبِثْتَ فِينَا مِنْ عُمُرِكَ سِنِينَ}
INSERT INTO [b9617] VALUES (2908,'<span data-type="title" id=toc-2890>١٩ </span>- {وَفَعَلْتَ فَعْلَتَكَ الَّتِي فَعَلْتَ وَأَنْتَ مِنَ الْكَافِرِينَ}
INSERT INTO [b9617] VALUES (2909,'<span data-type="title" id=toc-2891>٢٠ </span>- {قَالَ فَعَلْتُهَا إِذًا وَأَنَا مِنَ الضَّالِّينَ}
INSERT INTO [b9617] VALUES (2910,'<span data-type="title" id=toc-2892>٢١ </span>- {فَفَرَرْتُ مِنْكُمْ لَمَّا خِفْتُكُمْ فَوَهَبَ لِي رَبِّي حُكْمًا وَجَعَلَنِي مِنَ الْمُرْسَلِينَ}
INSERT INTO [b9617] VALUES (2911,'<span data-type="title" id=toc-2893>٢٢ </span>- {وَتِلْكَ نِعْمَةٌ تَمُنُّهَا عَلَيَّ أَنْ عَبَّدْتَ بَنِي إِسْرَائِيلَ}
INSERT INTO [b9617] VALUES (2912,'<span data-type="title" id=toc-2894>٢٣ </span>- {قَالَ فِرْعَوْنُ وَمَا رَبُّ الْعَالَمِينَ} ⦗٨٣١⦘
INSERT INTO [b9617] VALUES (2913,'<span data-type="title" id=toc-2895>٢٤ </span>- {قَالَ رَبُّ السَّمَاوَاتِ وَالأَرْضِ وَمَا بَيْنَهُمَا إِنْ كُنْتُمْ مُوقِنِينَ}
INSERT INTO [b9617] VALUES (2914,'<span data-type="title" id=toc-2896>٢٥ </span>- {قَالَ لِمَنْ حَوْلَهُ أَلا تَسْتَمِعُونَ}
INSERT INTO [b9617] VALUES (2915,'<span data-type="title" id=toc-2897>٢٦ </span>- {قَالَ رَبُّكُمْ وَرَبُّ آبَائِكُمُ الأَوَّلِينَ}
INSERT INTO [b9617] VALUES (2916,'<span data-type="title" id=toc-2898>٢٧ </span>- {قَالَ إِنَّ رَسُولَكُمُ الَّذِي أُرْسِلَ إِلَيْكُمْ لَمَجْنُونٌ}
INSERT INTO [b9617] VALUES (2917,'<span data-type="title" id=toc-2899>٢٨ </span>- {قَالَ رَبُّ الْمَشْرِقِ وَالْمَغْرِبِ وَمَا بَيْنَهُمَا إِنْ كُنْتُمْ تَعْقِلُونَ}
INSERT INTO [b9617] VALUES (2918,'<span data-type="title" id=toc-2900>٢٩ </span>- {قَالَ لَئِنِ اتَّخَذْتَ إِلَهًا غَيْرِي لأَجْعَلَنَّكَ مِنَ الْمَسْجُونِينَ}
INSERT INTO [b9617] VALUES (2919,'<span data-type="title" id=toc-2901>٣٠ </span>- {قَالَ أَوَلَوْ جِئْتُكَ بِشَيْءٍ مُبِينٍ}
INSERT INTO [b9617] VALUES (2920,'<span data-type="title" id=toc-2902>٣١ </span>- {قَالَ فَأْتِ بِهِ إِنْ كُنْتَ مِنَ الصَّادِقِينَ}
INSERT INTO [b9617] VALUES (2921,'<span data-type="title" id=toc-2903>٣٢ </span>- {فَأَلْقَى عَصَاهُ فَإِذَا هِيَ ثُعْبَانٌ مُبِينٌ}
INSERT INTO [b9617] VALUES (2922,'<span data-type="title" id=toc-2904>٣٣ </span>- {وَنَزَعَ يَدَهُ فَإِذَا هِيَ بَيْضَاءُ لِلنَّاظِرِينَ}
INSERT INTO [b9617] VALUES (2923,'<span data-type="title" id=toc-2905>٣٤ </span>- {قَالَ لِلْمَلإِ حَوْلَهُ إِنَّ هَذَا لَسَاحِرٌ عَلِيمٌ}
INSERT INTO [b9617] VALUES (2924,'<span data-type="title" id=toc-2906>٣٥ </span>- {يُرِيدُ أَنْ يُخْرِجَكُمْ مِنْ أَرْضِكُمْ بِسِحْرِهِ فَمَاذَا تَأْمُرُونَ}
INSERT INTO [b9617] VALUES (2925,'<span data-type="title" id=toc-2907>٣٦ </span>- {قَالُوا أَرْجِهْ وَأَخَاهُ وَابْعَثْ فِي الْمَدَائِنِ حَاشِرِينَ}
INSERT INTO [b9617] VALUES (2926,'<span data-type="title" id=toc-2908>٣٧ </span>- {يَأْتُوكَ بِكُلِّ سَحَّارٍ عَلِيمٍ}
INSERT INTO [b9617] VALUES (2927,'<span data-type="title" id=toc-2909>٣٨ </span>- {فَجُمِعَ السَّحَرَةُ لِمِيقَاتِ يَوْمٍ مَعْلُومٍ}
INSERT INTO [b9617] VALUES (2928,'<span data-type="title" id=toc-2910>٣٩ </span>- {وَقِيلَ لِلنَّاسِ هَلْ أَنْتُمْ مُجْتَمِعُونَ}
INSERT INTO [b9617] VALUES (2929,'<span data-type="title" id=toc-2911>٤٠ </span>- {لَعَلَّنَا نَتَّبِعُ السَّحَرَةَ إِنْ كَانُوا هُمُ الْغَالِبِينَ}
INSERT INTO [b9617] VALUES (2930,'<span data-type="title" id=toc-2912>٤١ </span>- {فَلَمَّا جَاءَ السَّحَرَةُ قَالُوا لِفِرْعَوْنَ أَئِنَّ لَنَا لأَجْرًا إِنْ كُنَّا نَحْنُ الْغَالِبِينَ} ⦗٨٣٤⦘
INSERT INTO [b9617] VALUES (2931,'<span data-type="title" id=toc-2913>٤٢ </span>- {قَالَ نَعَمْ وَإِنَّكُمْ إِذًا لَمِنَ الْمُقَرَّبِينَ}
INSERT INTO [b9617] VALUES (2932,'<span data-type="title" id=toc-2914>٤٣ </span>- {قَالَ لَهُمْ مُوسَى أَلْقُوا مَا أَنْتُمْ مُلْقُونَ}
INSERT INTO [b9617] VALUES (2933,'<span data-type="title" id=toc-2915>٤٤ </span>- {فَأَلْقَوْا حِبَالَهُمْ وَعِصِيَّهُمْ وَقَالُوا بِعِزَّةِ فِرْعَوْنَ إِنَّا لَنَحْنُ الْغَالِبُونَ}
INSERT INTO [b9617] VALUES (2934,'<span data-type="title" id=toc-2916>٤٥ </span>- {فَأَلْقَى مُوسَى عَصَاهُ فَإِذَا هِيَ تَلْقَفُ مَا يَأْفِكُونَ}
INSERT INTO [b9617] VALUES (2935,'<span data-type="title" id=toc-2917>٤٦ </span>- {فَأُلْقِيَ السَّحَرَةُ سَاجِدِينَ}
INSERT INTO [b9617] VALUES (2936,'<span data-type="title" id=toc-2918>٤٧ </span>- {قَالُوا آمَنَّا بِرَبِّ الْعَالَمِينَ}
INSERT INTO [b9617] VALUES (2937,'<span data-type="title" id=toc-2919>٤٨ </span>- {رَبِّ مُوسَى وَهَارُونَ}
INSERT INTO [b9617] VALUES (2938,'<span data-type="title" id=toc-2920>٤٩ </span>- {قَالَ آمَنْتُمْ لَهُ قَبْلَ أَنْ آذَنَ لَكُمْ إِنَّهُ لَكَبِيرُكُمُ الَّذِي عَلَّمَكُمُ السِّحْرَ فَلَسَوْفَ تَعْلَمُونَ لأُقَطِّعَنَّ أَيْدِيَكُمْ وَأَرْجُلَكُمْ مِنْ خِلافٍ وَلأُصَلِّبَنَّكُمْ أَجْمَعِينَ}
INSERT INTO [b9617] VALUES (2939,'<span data-type="title" id=toc-2921>٥٠ </span>- {قَالُوا لا ضَيْرَ إِنَّا إِلَى رَبِّنَا مُنْقَلِبُونَ}
INSERT INTO [b9617] VALUES (2940,'<span data-type="title" id=toc-2922>٥١ </span>- {إِنَّا نَطْمَعُ أَنْ يَغْفِرَ لَنَا رَبُّنَا خَطَايَانَا أَنْ كُنَّا أَوَّلَ الْمُؤْمِنِينَ}
INSERT INTO [b9617] VALUES (2941,'<span data-type="title" id=toc-2923>٥٢ </span>- {وَأَوْحَيْنَا إِلَى مُوسَى أَنْ أَسْرِ بِعِبَادِي إِنَّكُمْ مُتَّبَعُونَ}
INSERT INTO [b9617] VALUES (2942,'<span data-type="title" id=toc-2924>٥٣ </span>- {فَأَرْسَلَ فِرْعَوْنُ فِي الْمَدَائِنِ حَاشِرِينَ}
INSERT INTO [b9617] VALUES (2943,'<span data-type="title" id=toc-2925>٥٤ </span>- {إِنَّ هَؤُلاءِ لَشِرْذِمَةٌ قَلِيلُونَ}
INSERT INTO [b9617] VALUES (2944,'<span data-type="title" id=toc-2926>٥٥ </span>- {وَإِنَّهُمْ لَنَا لَغَائِظُونَ}
INSERT INTO [b9617] VALUES (2945,'<span data-type="title" id=toc-2927>٥٦ </span>- {وَإِنَّا لَجَمِيعٌ حَاذِرُونَ}
INSERT INTO [b9617] VALUES (2946,'<span data-type="title" id=toc-2928>٥٧ </span>- {فَأَخْرَجْنَاهُمْ مِنْ جَنَّاتٍ وَعُيُونٍ}
INSERT INTO [b9617] VALUES (2947,'<span data-type="title" id=toc-2929>٥٩ </span>- {كَذَلِكَ وَأَوْرَثْنَاهَا بَنِي إِسْرَائِيلَ}
INSERT INTO [b9617] VALUES (2948,'<span data-type="title" id=toc-2930>٦٠ </span>- {فَأَتْبَعُوهُمْ مُشْرِقِينَ}
INSERT INTO [b9617] VALUES (2949,'<span data-type="title" id=toc-2931>٦١ </span>- {فَلَمَّا تَرَاءَى الْجَمْعَانِ قَالَ أَصْحَابُ مُوسَى إِنَّا لَمُدْرَكُونَ}
INSERT INTO [b9617] VALUES (2950,'<span data-type="title" id=toc-2932>٦٢ </span>- {قَالَ كَلا إِنَّ مَعِيَ رَبِّي سَيَهْدِينِ}
INSERT INTO [b9617] VALUES (2951,'<span data-type="title" id=toc-2933>٦٣ </span>- {فَأَوْحَيْنَا إِلَى مُوسَى أَنِ اضْرِبْ بِعَصَاكَ الْبَحْرَ فَانْفَلَقَ فَكَانَ كُلُّ فِرْقٍ كَالطَّوْدِ الْعَظِيمِ}
INSERT INTO [b9617] VALUES (2952,'<span data-type="title" id=toc-2934>٦٤ </span>- {وَأَزْلَفْنَا ثَمَّ الآخَرِينَ}
INSERT INTO [b9617] VALUES (2953,'<span data-type="title" id=toc-2935>٦٥ </span>- {وَأَنْجَيْنَا مُوسَى وَمَنْ مَعَهُ أَجْمَعِينَ}
INSERT INTO [b9617] VALUES (2954,'<span data-type="title" id=toc-2936>٦٧ </span>- {إِنَّ فِي ذَلِكَ لآيَةً وَمَا كَانَ أَكْثَرُهُمْ مُؤْمِنِينَ}
INSERT INTO [b9617] VALUES (2955,'<span data-type="title" id=toc-2937>٦٨ </span>- {وَإِنَّ رَبَّكَ لَهُوَ الْعَزِيزُ الرَّحِيمُ}
INSERT INTO [b9617] VALUES (2956,'<span data-type="title" id=toc-2938>٦٩ </span>- {وَاتْلُ عَلَيْهِمْ نَبَأَ إِبْرَاهِيمَ}
INSERT INTO [b9617] VALUES (2957,'<span data-type="title" id=toc-2939>٧٠ </span>- {إِذْ قَالَ لأَبِيهِ وَقَوْمِهِ مَا تَعْبُدُونَ}
INSERT INTO [b9617] VALUES (2958,'<span data-type="title" id=toc-2940>٧١ </span>- {قَالُوا نَعْبُدُ أَصْنَامًا فَنَظَلُّ لَهَا عَاكِفِينَ}
INSERT INTO [b9617] VALUES (2959,'<span data-type="title" id=toc-2941>٧٢ </span>- {هَلْ يَسْمَعُونَكُمْ إِذْ تَدْعُونَ}
INSERT INTO [b9617] VALUES (2960,'<span data-type="title" id=toc-2942>٧٤ </span>- {قَالُوا بَلْ وَجَدْنَا آبَاءَنَا كَذَلِكَ يَفْعَلُونَ}
INSERT INTO [b9617] VALUES (2961,'<span data-type="title" id=toc-2943>٧٥ </span>- {قَالَ أَفَرَأَيْتُمْ مَا كُنْتُمْ تَعْبُدُونَ}
INSERT INTO [b9617] VALUES (2962,'<span data-type="title" id=toc-2944>٧٦ </span>- {أَنْتُمْ وَآبَاؤُكُمُ الأَقْدَمُونَ}
INSERT INTO [b9617] VALUES (2963,'<span data-type="title" id=toc-2945>٧٧ </span>- {فَإِنَّهُمْ عَدُوٌّ لِي إِلا رَبَّ الْعَالَمِينَ}
INSERT INTO [b9617] VALUES (2964,'<span data-type="title" id=toc-2946>٧٨ </span>- {الَّذِي خَلَقَنِي فَهُوَ يَهْدِينِ}
INSERT INTO [b9617] VALUES (2965,'<span data-type="title" id=toc-2947>٧٩ </span>- {وَالَّذِي هُوَ يُطْعِمُنِي وَيَسْقِينِ}
INSERT INTO [b9617] VALUES (2966,'<span data-type="title" id=toc-2948>٨٠ </span>- {وَإِذَا مَرِضْتُ فَهُوَ يَشْفِينِ}
INSERT INTO [b9617] VALUES (2967,'<span data-type="title" id=toc-2949>٨١ </span>- {وَالَّذِي يُمِيتُنِي ثُمَّ يُحْيِينِ}
INSERT INTO [b9617] VALUES (2968,'<span data-type="title" id=toc-2950>٨٢ </span>- {وَالَّذِي أَطْمَعُ أَنْ يَغْفِرَ لِي خَطِيئَتِي يَوْمَ الدِّينِ}
INSERT INTO [b9617] VALUES (2969,'<span data-type="title" id=toc-2951>٨٣ </span>- {رَبِّ هَبْ لِي حُكْمًا وَأَلْحِقْنِي بِالصَّالِحِينَ}
INSERT INTO [b9617] VALUES (2970,'<span data-type="title" id=toc-2952>٨٤ </span>- {وَاجْعَلْ لِي لِسَانَ صِدْقٍ فِي الآخِرِينَ}
INSERT INTO [b9617] VALUES (2971,'<span data-type="title" id=toc-2953>٨٥ </span>- {وَاجْعَلْنِي مِنْ وَرَثَةِ جَنَّةِ النَّعِيمِ}
INSERT INTO [b9617] VALUES (2972,'<span data-type="title" id=toc-2954>٨٦ </span>- {وَاغْفِرْ لأَبِي إِنَّهُ كَانَ مِنَ الضَّالِّينَ}
INSERT INTO [b9617] VALUES (2973,'<span data-type="title" id=toc-2955>٨٧ </span>- {وَلا تُخْزِنِي يَوْمَ يُبْعَثُونَ} ⦗٨٤١⦘
INSERT INTO [b9617] VALUES (2974,'<span data-type="title" id=toc-2956>٨٨ </span>- {يَوْمَ لا يَنْفَعُ مَالٌ وَلا بَنُونَ}
INSERT INTO [b9617] VALUES (2975,'<span data-type="title" id=toc-2957>٨٩ </span>- {إِلا مَنْ أَتَى اللَّهَ بِقَلْبٍ سَلِيمٍ}
INSERT INTO [b9617] VALUES (2976,'<span data-type="title" id=toc-2958>٩٠ </span>- {وَأُزْلِفَتِ الْجَنَّةُ لِلْمُتَّقِينَ}
INSERT INTO [b9617] VALUES (2977,'<span data-type="title" id=toc-2959>٩١ </span>- {وَبُرِّزَتِ الْجَحِيمُ لِلْغَاوِينَ}
INSERT INTO [b9617] VALUES (2978,'<span data-type="title" id=toc-2960>٩٢ </span>- {أَيْنَ مَا كُنْتُمْ تَعْبُدُونَ}
INSERT INTO [b9617] VALUES (2979,'<span data-type="title" id=toc-2961>٩٣ </span>- {مِنْ دُونِ اللَّهِ هَلْ يَنْصُرُونَكُمْ}
