INSERT INTO [b9617] VALUES (3079,'<span data-type="title" id=toc-3061>١٩٩ </span>- {فَقَرَأَهُ عَلَيْهِمْ مَا كَانُوا بِهِ مُؤْمِنِينَ} ⦗٨٥٦⦘
INSERT INTO [b9617] VALUES (3080,'<span data-type="title" id=toc-3062>٢٠٠ </span>- {كَذَلِكَ سَلَكْنَاهُ فِي قُلُوبِ الْمُجْرِمِينَ}
INSERT INTO [b9617] VALUES (3081,'<span data-type="title" id=toc-3063>٢٠١ </span>- {لا يُؤْمِنُونَ بِهِ حَتَّى يَرَوُا الْعَذَابَ الأَلِيمَ}
