INSERT INTO [b9617] VALUES (3083,'<span data-type="title" id=toc-3065>٢٠٣ </span>- {فَيَقُولُوا هَلْ نَحْنُ مُنْظَرُونَ}
INSERT INTO [b9617] VALUES (3084,'<span data-type="title" id=toc-3066>٢٠٤ </span>- {أَفَبِعَذَابِنَا يَسْتَعْجِلُونَ}
INSERT INTO [b9617] VALUES (3085,'<span data-type="title" id=toc-3067>٢٠٥ </span>- {أَفَرَأَيْتَ إِنْ مَتَّعْنَاهُمْ سِنِينَ}
INSERT INTO [b9617] VALUES (3086,'<span data-type="title" id=toc-3068>٢٠٦ </span>- {ثُمَّ جَاءَهُمْ مَا كَانُوا يُوعَدُونَ}
INSERT INTO [b9617] VALUES (3087,'<span data-type="title" id=toc-3069>٢٠٧ </span>- {مَا أَغْنَى عَنْهُمْ مَا كَانُوا يُمَتَّعُونَ}
INSERT INTO [b9617] VALUES (3088,'<span data-type="title" id=toc-3070>٢٠٨ </span>- {وَمَا أَهْلَكْنَا مِنْ قَرْيَةٍ إِلا لَهَا مُنْذِرُونَ}
INSERT INTO [b9617] VALUES (3089,'<span data-type="title" id=toc-3071>٢٠٩ </span>- {ذِكْرَى وَمَا كُنَّا ظَالِمِينَ}
INSERT INTO [b9617] VALUES (3090,'<span data-type="title" id=toc-3072>٢١٠ </span>- {وَمَا تَنَزَّلَتْ بِهِ الشَّيَاطِينُ}
INSERT INTO [b9617] VALUES (3091,'<span data-type="title" id=toc-3073>٢١١ </span>- {وَمَا يَنْبَغِي لَهُمْ وَمَا يَسْتَطِيعُونَ} ⦗٨٥٨⦘
INSERT INTO [b9617] VALUES (3092,'<span data-type="title" id=toc-3074>٢١٢ </span>- {إِنَّهُمْ عَنِ السَّمْعِ لَمَعْزُولُونَ}
INSERT INTO [b9617] VALUES (3093,'<span data-type="title" id=toc-3075>٢١٣ </span>- {فَلا تَدْعُ مَعَ اللَّهِ إِلَهًا آخَرَ فَتَكُونَ مِنَ الْمُعَذَّبِينَ}
INSERT INTO [b9617] VALUES (3094,'<span data-type="title" id=toc-3076>٢١٤ </span>- {وَأَنْذِرْ عَشِيرَتَكَ الأَقْرَبِينَ}
INSERT INTO [b9617] VALUES (3095,'<span data-type="title" id=toc-3077>٢١٥ </span>- {وَاخْفِضْ جَنَاحَكَ لِمَنِ اتَّبَعَكَ مِنَ الْمُؤْمِنِينَ}
INSERT INTO [b9617] VALUES (3096,'<span data-type="title" id=toc-3078>٢١٦ </span>- {فَإِنْ عَصَوْكَ فَقُلْ إِنِّي بَرِيءٌ مِمَّا تَعْمَلُونَ}
INSERT INTO [b9617] VALUES (3097,'<span data-type="title" id=toc-3079>٢١٨ </span>- {الَّذِي يَرَاكَ حِينَ تَقُومُ}
INSERT INTO [b9617] VALUES (3098,'<span data-type="title" id=toc-3080>٢١٩ </span>- {وَتَقَلُّبَكَ فِي السَّاجِدِينَ} ⦗٨٥٩⦘
INSERT INTO [b9617] VALUES (3099,'<span data-type="title" id=toc-3081>٢٢٠ </span>- {إِنَّهُ هُوَ السَّمِيعُ الْعَلِيمُ}
INSERT INTO [b9617] VALUES (3100,'<span data-type="title" id=toc-3082>٢٢١ </span>- {هَلْ أُنَبِّئُكُمْ عَلَى مَنْ تَنَزَّلُ الشَّيَاطِينُ}
INSERT INTO [b9617] VALUES (3101,'<span data-type="title" id=toc-3083>٢٢٢ </span>- {تَنَزَّلُ عَلَى كُلِّ أَفَّاكٍ أَثِيمٍ}
INSERT INTO [b9617] VALUES (3102,'<span data-type="title" id=toc-3084>٢٢٣ </span>- {يُلْقُونَ السَّمْعَ وَأَكْثَرُهُمْ كَاذِبُونَ}
INSERT INTO [b9617] VALUES (3103,'<span data-type="title" id=toc-3085>٢٢٤ </span>- {وَالشُّعَرَاءُ يَتَّبِعُهُمُ الْغَاوُونَ}
INSERT INTO [b9617] VALUES (3104,'<span data-type="title" id=toc-3086>٢٢٥ </span>- {أَلَمْ تَرَ أَنَّهُمْ فِي كُلِّ وَادٍ يَهِيمُونَ}
INSERT INTO [b9617] VALUES (3105,'<span data-type="title" id=toc-3087>٢٢٦ </span>- {وَأَنَّهُمْ يَقُولُونَ مَا لا يَفْعَلُونَ}
INSERT INTO [b9617] VALUES (3106,'<span data-type="title" id=toc-3088>٢٢٧ </span>- {إِلا الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ وَذَكَرُوا اللَّهَ كَثِيرًا وَانْتَصَرُوا مِنْ بَعْدِ مَا ظُلِمُوا وَسَيَعْلَمُ الَّذِينَ ظَلَمُوا أَيَّ مُنْقَلَبٍ يَنْقَلِبُونَ}
INSERT INTO [b9617] VALUES (3107,'<span data-type="title" id=toc-3089>سورة النمل</span>','3','861',NULL,NULL,'0');
INSERT INTO [b9617] VALUES (3108,'<span data-type="title" id=toc-3090>١ </span>- {طس تِلْكَ آيَاتُ الْقُرْآنِ وَكِتَابٍ مُبِينٍ}
INSERT INTO [b9617] VALUES (3109,'<span data-type="title" id=toc-3091>٢ </span>- {هُدًى وَبُشْرَى لِلْمُؤْمِنِينَ}
INSERT INTO [b9617] VALUES (3110,'<span data-type="title" id=toc-3092>٣ </span>- {الَّذِينَ يُقِيمُونَ الصَّلاةَ وَيُؤْتُونَ الزَّكَاةَ وَهُمْ بِالآخِرَةِ هُمْ يُوقِنُونَ}
INSERT INTO [b9617] VALUES (3111,'<span data-type="title" id=toc-3093>٤ </span>- {إِنَّ الَّذِينَ لا يُؤْمِنُونَ بِالآخِرَةِ زَيَّنَّا لَهُمْ أَعْمَالَهُمْ فَهُمْ يَعْمَهُونَ}
INSERT INTO [b9617] VALUES (3112,'<span data-type="title" id=toc-3094>٥ </span>- {أُولَئِكَ الَّذِينَ لَهُمْ سُوءُ الْعَذَابِ وَهُمْ فِي الآخِرَةِ هُمُ الأَخْسَرُونَ}
INSERT INTO [b9617] VALUES (3113,'<span data-type="title" id=toc-3095>٦ </span>- {وَإِنَّكَ لَتُلَقَّى الْقُرْآنَ مِنْ لَدُنْ حَكِيمٍ عَلِيمٍ}
INSERT INTO [b9617] VALUES (3114,'<span data-type="title" id=toc-3096>٧ </span>- {إِذْ قَالَ مُوسَى لأَهْلِهِ إِنِّي آنَسْتُ نَارًا سَآتِيكُمْ مِنْهَا بِخَبَرٍ أَوْ آتِيكُمْ بِشِهَابٍ قَبَسٍ لَعَلَّكُمْ ⦗٨٦٢⦘ تَصْطَلُونَ}
INSERT INTO [b9617] VALUES (3115,'<span data-type="title" id=toc-3097>٨ </span>- {فَلَمَّا جَاءَهَا نُودِيَ أَنْ بُورِكَ مَنْ فِي النَّارِ وَمَنْ حَوْلَهَا وَسُبْحَانَ اللَّهِ رَبِّ الْعَالَمِينَ}
INSERT INTO [b9617] VALUES (3116,'<span data-type="title" id=toc-3098>٩ </span>- {يَا مُوسَى إِنَّهُ أَنَا اللَّهُ الْعَزِيزُ الْحَكِيمُ}
INSERT INTO [b9617] VALUES (3117,'<span data-type="title" id=toc-3099>١٠ </span>- {وَأَلْقِ عَصَاكَ فَلَمَّا رَآهَا تَهْتَزُّ كَأَنَّهَا جَانٌّ وَلَّى مُدْبِرًا وَلَمْ يُعَقِّبْ يَا مُوسَى لا تَخَفْ إِنِّي لا يَخَافُ لَدَيَّ الْمُرْسَلُونَ}
INSERT INTO [b9617] VALUES (3118,'<span data-type="title" id=toc-3100>١١ </span>- {إِلا مَنْ ظَلَمَ ثُمَّ بَدَّلَ حُسْنًا بَعْدَ سُوءٍ فَإِنِّي غَفُورٌ رَحِيمٌ}
INSERT INTO [b9617] VALUES (3119,'<span data-type="title" id=toc-3101>١٢ </span>- {وَأَدْخِلْ يَدَكَ فِي جَيْبِكَ تَخْرُجْ بَيْضَاءَ مِنْ غَيْرِ سُوءٍ فِي تِسْعِ آيَاتٍ إِلَى فِرْعَوْنَ وَقَوْمِهِ إِنَّهُمْ كَانُوا قَوْمًا فَاسِقِينَ}
INSERT INTO [b9617] VALUES (3120,'<span data-type="title" id=toc-3102>١٣ </span>- {فَلَمَّا جَاءَتْهُمْ آيَاتُنَا مُبْصِرَةً قَالُوا هَذَا سِحْرٌ مُبِينٌ}
INSERT INTO [b9617] VALUES (3121,'<span data-type="title" id=toc-3103>١٤ </span>- {وَجَحَدُوا بِهَا وَاسْتَيْقَنَتْهَا أَنْفُسُهُمْ ظُلْمًا وَعُلُوًّا فَانْظُرْ كَيْفَ كَانَ عَاقِبَةُ الْمُفْسِدِينَ}
INSERT INTO [b9617] VALUES (3122,'<span data-type="title" id=toc-3104>١٥ </span>- {وَلَقَدْ آتَيْنَا دَاوُدَ وَسُلَيْمَانَ عِلْمًا وَقَالا الْحَمْدُ لِلَّهِ الَّذِي فَضَّلَنَا عَلَى كَثِيرٍ مِنْ عِبَادِهِ الْمُؤْمِنِينَ}
INSERT INTO [b9617] VALUES (3123,'<span data-type="title" id=toc-3105>١٦ </span>- {وَوَرِثَ سُلَيْمَانُ دَاوُدَ وَقَالَ يَا أَيُّهَا النَّاسُ عُلِّمْنَا مَنْطِقَ الطَّيْرِ وَأُوتِينَا مِنْ كُلِّ شَيْءٍ إِنَّ هَذَا لَهُوَ الْفَضْلُ الْمُبِينُ}
INSERT INTO [b9617] VALUES (3124,'<span data-type="title" id=toc-3106>١٧ </span>- {وَحُشِرَ لِسُلَيْمَانَ جُنُودُهُ مِنَ الْجِنِّ وَالإِنْسِ وَالطَّيْرِ فَهُمْ يُوزَعُونَ}
INSERT INTO [b9617] VALUES (3125,'<span data-type="title" id=toc-3107>١٨ </span>- {حَتَّى إِذَا أَتَوْا عَلَى وَادِ النَّمْلِ قَالَتْ نَمْلَةٌ يَا أَيُّهَا النَّمْلُ ادْخُلُوا مَسَاكِنَكُمْ لا يَحْطِمَنَّكُمْ سُلَيْمَانُ وَجُنُودُهُ وَهُمْ لا يَشْعُرُونَ}
INSERT INTO [b9617] VALUES (3126,'<span data-type="title" id=toc-3108>١٩ </span>- {فَتَبَسَّمَ ضَاحِكًا مِنْ قَوْلِهَا وَقَالَ رَبِّ أَوْزِعْنِي أَنْ أَشْكُرَ نِعْمَتَكَ الَّتِي أَنْعَمْتَ عَلَيَّ وَعَلَى وَالِدَيَّ وَأَنْ أَعْمَلَ صَالِحًا تَرْضَاهُ وَأَدْخِلْنِي بِرَحْمَتِكَ فِي عِبَادِكَ الصَّالِحِينَ}
INSERT INTO [b9617] VALUES (3127,'<span data-type="title" id=toc-3109>٢٠ </span>- {وَتَفَقَّدَ الطَّيْرَ فَقَالَ مَا لِيَ لا أَرَى الْهُدْهُدَ أَمْ كَانَ مِنَ الْغَائِبِينَ}
INSERT INTO [b9617] VALUES (3128,'<span data-type="title" id=toc-3110>٢١ </span>- {لأُعَذِّبَنَّهُ عَذَابًا شَدِيدًا}
INSERT INTO [b9617] VALUES (3129,'<span data-type="title" id=toc-3111>٢٢ </span>- {فَمَكَثَ غَيْرَ بَعِيدٍ فَقَالَ أَحَطْتُ بِمَا لَمْ تُحِطْ بِهِ وَجِئْتُكَ مِنْ سَبَإٍ بِنَبَإٍ يَقِينٍ}
INSERT INTO [b9617] VALUES (3130,'<span data-type="title" id=toc-3112>٢٣ </span>- {إِنِّي وَجَدْتُ امْرَأَةً تَمْلِكُهُمْ وَأُوتِيَتْ مِنْ كُلِّ شَيْءٍ وَلَهَا عَرْشٌ عَظِيمٌ}
INSERT INTO [b9617] VALUES (3131,'<span data-type="title" id=toc-3113>٢٤ </span>- {وَجَدْتُهَا وَقَوْمَهَا يَسْجُدُونَ لِلشَّمْسِ مِنْ دُونِ اللَّهِ وَزَيَّنَ لَهُمُ الشَّيْطَانُ أَعْمَالَهُمْ فَصَدَّهُمْ عَنِ السَّبِيلِ فَهُمْ لا يَهْتَدُونَ}
INSERT INTO [b9617] VALUES (3132,'<span data-type="title" id=toc-3114>٢٥ </span>- {أَلا يَسْجُدُوا لِلَّهِ الَّذِي يُخْرِجُ الْخَبْءَ فِي السَّمَاوَاتِ وَالأَرْضِ} ⦗٨٦٧⦘
INSERT INTO [b9617] VALUES (3133,'<span data-type="title" id=toc-3115>٢٦ </span>- {اللَّهُ لا إِلَهَ إِلا هُوَ رَبُّ الْعَرْشِ الْعَظِيمِ}
INSERT INTO [b9617] VALUES (3134,'<span data-type="title" id=toc-3116>٢٧ </span>- {قَالَ سَنَنْظُرُ أَصَدَقْتَ أَمْ كُنْتَ مِنَ الْكَاذِبِينَ}
INSERT INTO [b9617] VALUES (3135,'<span data-type="title" id=toc-3117>٢٨ </span>- {اذْهَبْ بِكِتَابِي هَذَا فَأَلْقِهْ إِلَيْهِمْ ثُمَّ تَوَلَّ عَنْهُمْ فَانْظُرْ مَاذَا يَرْجِعُونَ}
INSERT INTO [b9617] VALUES (3136,'<span data-type="title" id=toc-3118>٢٩ </span>- {قَالَتْ يَا أَيُّهَا الْمَلأُ إِنِّي أُلْقِيَ إِلَيَّ كِتَابٌ كَرِيمٌ}
INSERT INTO [b9617] VALUES (3137,'<span data-type="title" id=toc-3119>٣٠ </span>- {إِنَّهُ مِنْ سُلَيْمَانَ وَإِنَّهُ بِسْمِ اللَّهِ الرَّحْمَنِ الرَّحِيمِ}
INSERT INTO [b9617] VALUES (3138,'<span data-type="title" id=toc-3120>٣١ </span>- {أَلا تَعْلُوا عَلَيَّ وَأْتُونِي مُسْلِمِينَ}
INSERT INTO [b9617] VALUES (3139,'<span data-type="title" id=toc-3121>٣٢ </span>- {مَا كُنْتُ قَاطِعَةً أَمْرًا حَتَّى تَشْهَدُونِ}
INSERT INTO [b9617] VALUES (3140,'<span data-type="title" id=toc-3122>٣٣ </span>- {قَالُوا نَحْنُ أُولُو قُوَّةٍ وَأُولُو بَأْسٍ شَدِيدٍ وَالأَمْرُ إِلَيْكِ فَانْظُرِي مَاذَا تَأْمُرِينَ}
INSERT INTO [b9617] VALUES (3141,'<span data-type="title" id=toc-3123>٣٤ </span>- {قَالَتْ إِنَّ الْمُلُوكَ إِذَا دَخَلُوا قَرْيَةً أَفْسَدُوهَا وَجَعَلُوا أَعِزَّةَ أَهْلِهَا أَذِلَّةً وَكَذَلِكَ يَفْعَلُونَ}
INSERT INTO [b9617] VALUES (3142,'<span data-type="title" id=toc-3124>٣٥ </span>- {وَإِنِّي مُرْسِلَةٌ إِلَيْهِمْ بِهَدِيَّةٍ فَنَاظِرَةٌ بِمَ يَرْجِعُ الْمُرْسَلُونَ}
INSERT INTO [b9617] VALUES (3143,'<span data-type="title" id=toc-3125>٣٦ </span>- {فَلَمَّا جَاءَ سُلَيْمَانَ قَالَ أَتُمِدُّونَنِ بِمَالٍ فَمَا آتَانِ اللَّهُ خَيْرٌ مِمَّا آتَاكُمْ بَلْ أَنْتُمْ بِهَدِيَّتِكُمْ تَفْرَحُونَ}
INSERT INTO [b9617] VALUES (3144,'<span data-type="title" id=toc-3126>٣٧ </span>- {ارْجِعْ إِلَيْهِمْ فَلَنَأْتِيَنَّهُمْ بِجُنُودٍ لا قِبَلَ لَهُمْ بِهَا وَلَنُخْرِجَنَّهُمْ مِنْهَا أَذِلَّةً وَهُمْ صَاغِرُونَ}
INSERT INTO [b9617] VALUES (3145,'<span data-type="title" id=toc-3127>٣٨ </span>- {قَالَ يَا أَيُّهَا الْمَلأُ أَيُّكُمْ يَأْتِينِي بِعَرْشِهَا قَبْلَ أَنْ يَأْتُونِي مُسْلِمِينَ}
INSERT INTO [b9617] VALUES (3146,'<span data-type="title" id=toc-3128>٣٩ </span>- {قَالَ عِفْرِيتٌ مِنَ الْجِنِّ أَنَا آتِيكَ بِهِ قَبْلَ أَنْ تَقُومَ مِنْ مَقَامِكَ وَإِنِّي عَلَيْهِ لَقَوِيٌّ أَمِينٌ}
INSERT INTO [b9617] VALUES (3147,'<span data-type="title" id=toc-3129>٤٠ </span>- {قَالَ الَّذِي عِنْدَهُ عِلْمٌ مِنَ الْكِتَابِ أَنَا آتِيكَ بِهِ قَبْلَ أَنْ يَرْتَدَّ إِلَيْكَ طَرْفُكَ فَلَمَّا رَآهُ مُسْتَقِرًّا عِنْدَهُ قَالَ هَذَا مِنْ فَضْلِ رَبِّي لِيَبْلُوَنِي أَأَشْكُرُ أَمْ أَكْفُرُ وَمَنْ شَكَرَ فَإِنَّمَا يَشْكُرُ لِنَفْسِهِ}
INSERT INTO [b9617] VALUES (3148,'<span data-type="title" id=toc-3130>٤١ </span>- {قَالَ نَكِّرُوا لَهَا عَرْشَهَا نَنْظُرْ أَتَهْتَدِي أَمْ تَكُونُ مِنَ الَّذِينَ لا يَهْتَدُونَ}
INSERT INTO [b9617] VALUES (3149,'<span data-type="title" id=toc-3131>٤٢ </span>- {فَلَمَّا جَاءَتْ قِيلَ أَهَكَذَا عَرْشُكِ قَالَتْ كَأَنَّهُ هُوَ وَأُوتِينَا الْعِلْمَ مِنْ قَبْلِهَا وَكُنَّا مُسْلِمِينَ}
INSERT INTO [b9617] VALUES (3150,'<span data-type="title" id=toc-3132>٤٣ </span>- {وَصَدَّهَا مَا كَانَتْ تَعْبُدُ مِنْ دُونِ اللَّهِ إِنَّهَا كَانَتْ مِنْ قَوْمٍ كَافِرِينَ}
INSERT INTO [b9617] VALUES (3151,'<span data-type="title" id=toc-3133>٤٤ </span>- {قِيلَ لَهَا ادْخُلِي الصَّرْحَ فَلَمَّا رَأَتْهُ حَسِبَتْهُ لُجَّةً وَكَشَفَتْ عَنْ سَاقَيْهَا قَالَ إِنَّهُ صَرْحٌ مُمَرَّدٌ مِنْ قَوَارِيرَ قَالَتْ رَبِّ إِنِّي ظَلَمْتُ نَفْسِي وَأَسْلَمْتُ مَعَ سُلَيْمَانَ لِلَّهِ رَبِّ الْعَالَمِينَ}
INSERT INTO [b9617] VALUES (3152,'<span data-type="title" id=toc-3134>٤٥ </span>- {وَلَقَدْ أَرْسَلْنَا إِلَى ثَمُودَ أَخَاهُمْ صَالِحًا أَنِ اعْبُدُوا اللَّهَ فَإِذَا هُمْ فَرِيقَانِ يَخْتَصِمُونَ}
INSERT INTO [b9617] VALUES (3153,'<span data-type="title" id=toc-3135>٤٦ </span>- {قَالَ يَا قَوْمِ لِمَ تَسْتَعْجِلُونَ بِالسَّيِّئَةِ قَبْلَ الْحَسَنَةِ لَوْلا تَسْتَغْفِرُونَ اللَّهَ لَعَلَّكُمْ تُرْحَمُونَ}
INSERT INTO [b9617] VALUES (3154,'<span data-type="title" id=toc-3136>٤٧ </span>- {قَالُوا اطَّيَّرْنَا بِكَ وَبِمَنْ مَعَكَ قَالَ طَائِرُكُمْ عِنْدَ اللَّهِ بَلْ أَنْتُمْ قَوْمٌ تُفْتَنُونَ}
INSERT INTO [b9617] VALUES (3155,'<span data-type="title" id=toc-3137>٤٨ </span>- {وَكَانَ فِي الْمَدِينَةِ تِسْعَةُ رَهْطٍ يُفْسِدُونَ فِي الأَرْضِ} ⦗٨٧٣⦘
INSERT INTO [b9617] VALUES (3156,'<span data-type="title" id=toc-3138>٤٩ </span>- {لَنُبَيِّتَنَّهُ وَأَهْلَهُ ثُمَّ لَنَقُولَنَّ لِوَلِيِّهِ مَا شَهِدْنَا مَهْلِكَ أَهْلِهِ وَإِنَّا لَصَادِقُونَ}
INSERT INTO [b9617] VALUES (3157,'<span data-type="title" id=toc-3139>٥٠ </span>- {وَمَكَرْنَا مَكْرًا وَهُمْ لا يَشْعُرُونَ}
INSERT INTO [b9617] VALUES (3158,'<span data-type="title" id=toc-3140>٥١ </span>- {فَانْظُرْ كَيْفَ كَانَ عَاقِبَةُ مَكْرِهِمْ أَنَّا دَمَّرْنَاهُمْ وَقَوْمَهُمْ أَجْمَعِينَ}
INSERT INTO [b9617] VALUES (3159,'<span data-type="title" id=toc-3141>٥٢ </span>- {فَتِلْكَ بُيُوتُهُمْ خَاوِيَةً بِمَا ظَلَمُوا إِنَّ فِي ذَلِكَ لآيَةً لِقَوْمٍ يَعْلَمُونَ}
INSERT INTO [b9617] VALUES (3160,'<span data-type="title" id=toc-3142>٥٣ </span>- {وَأَنْجَيْنَا الَّذِينَ آمَنُوا وَكَانُوا يَتَّقُونَ} ⦗٨٧٤⦘
INSERT INTO [b9617] VALUES (3161,'<span data-type="title" id=toc-3143>٥٤ </span>- {وَلُوطًا إِذْ قَالَ لِقَوْمِهِ أَتَأْتُونَ الْفَاحِشَةَ وَأَنْتُمْ تُبْصِرُونَ}
INSERT INTO [b9617] VALUES (3162,'<span data-type="title" id=toc-3144>٥٥ </span>- {أَئِنَّكُمْ لَتَأْتُونَ الرِّجَالَ شَهْوَةً مِنْ دُونِ النِّسَاءِ بَلْ أَنْتُمْ قَوْمٌ تَجْهَلُونَ}
INSERT INTO [b9617] VALUES (3163,'<span data-type="title" id=toc-3145>٥٦ </span>- {فَمَا كَانَ جَوَابَ قَوْمِهِ إِلا أَنْ قَالُوا أَخْرِجُوا آلَ لُوطٍ مِنْ قَرْيَتِكُمْ إِنَّهُمْ أُنَاسٌ يَتَطَهَّرُونَ}
INSERT INTO [b9617] VALUES (3164,'<span data-type="title" id=toc-3146>٥٧ </span>- {فَأَنْجَيْنَاهُ وَأَهْلَهُ إِلا امْرَأَتَهُ قَدَّرْنَاهَا مِنَ الْغَابِرِينَ}
INSERT INTO [b9617] VALUES (3165,'<span data-type="title" id=toc-3147>٥٨ </span>- {وَأَمْطَرْنَا عَلَيْهِمْ مَطَرًا فَسَاءَ مَطَرُ الْمُنْذَرِينَ}
INSERT INTO [b9617] VALUES (3166,'<span data-type="title" id=toc-3148>٥٩ </span>- {قُلِ الْحَمْدُ لِلَّهِ وَسَلامٌ عَلَى عِبَادِهِ الَّذِينَ اصْطَفَى آللَّهُ خَيْرٌ أَمْ مَا يُشْرِكُونَ}
INSERT INTO [b9617] VALUES (3167,'<span data-type="title" id=toc-3149>٦٠ </span>- {أَمَّنْ خَلَقَ السَّمَاوَاتِ وَالأَرْضَ وَأَنْزَلَ لَكُمْ مِنَ السَّمَاءِ مَاءً فَأَنْبَتْنَا بِهِ حَدَائِقَ ذَاتَ بَهْجَةٍ مَا كَانَ لَكُمْ أَنْ تُنْبِتُوا شَجَرَهَا أَإِلَهٌ مَعَ اللَّهِ بَلْ هُمْ قَوْمٌ يَعْدِلُونَ}
INSERT INTO [b9617] VALUES (3168,'<span data-type="title" id=toc-3150>٦١ </span>- {أَمَّنْ جَعَلَ الأَرْضَ قَرَارًا وَجَعَلَ خِلالَهَا أَنْهَارًا وَجَعَلَ لَهَا رَوَاسِيَ}
INSERT INTO [b9617] VALUES (3169,'<span data-type="title" id=toc-3151>٦٢ </span>- {أَمَّنْ يُجِيبُ الْمُضْطَرَّ إِذَا دَعَاهُ وَيَكْشِفُ السُّوءَ وَيَجْعَلُكُمْ خُلَفَاءَ الأَرْضِ أَإِلَهٌ مَعَ اللَّهِ قَلِيلا مَا تَذَكَّرُونَ}
INSERT INTO [b9617] VALUES (3170,'<span data-type="title" id=toc-3152>٦٣ </span>- {أَمَّنْ يَهْدِيكُمْ فِي ظُلُمَاتِ الْبَرِّ وَالْبَحْرِ وَمَنْ يُرْسِلُ الرِّيَاحَ بُشْرًا بَيْنَ يَدَيْ رَحْمَتِهِ أَإِلَهٌ مَعَ اللَّهِ تَعَالَى اللَّهُ عَمَّا يُشْرِكُونَ}
INSERT INTO [b9617] VALUES (3171,'<span data-type="title" id=toc-3153>٦٤ </span>- {أَمَّنْ يَبْدَأُ الْخَلْقَ ثُمَّ يُعِيدُهُ وَمَنْ يَرْزُقُكُمْ مِنَ السَّمَاءِ وَالأَرْضِ أَإِلَهٌ مَعَ اللَّهِ قُلْ هَاتُوا بُرْهَانَكُمْ إِنْ كُنْتُمْ صَادِقِينَ}
INSERT INTO [b9617] VALUES (3172,'<span data-type="title" id=toc-3154>٦٥ </span>- {قُلْ لا يَعْلَمُ مَنْ فِي السَّمَاوَاتِ وَالأَرْضِ الْغَيْبَ إِلا اللَّهُ وَمَا يَشْعُرُونَ أَيَّانَ يُبْعَثُونَ}
INSERT INTO [b9617] VALUES (3173,'<span data-type="title" id=toc-3155>٦٦ </span>- {بَلِ ادَّارَكَ عِلْمُهُمْ فِي الآخِرَةِ بَلْ هُمْ فِي شَكٍّ مِنْهَا بَلْ هُمْ مِنْهَا عَمُونَ}
INSERT INTO [b9617] VALUES (3174,'<span data-type="title" id=toc-3156>٦٧ </span>- {وَقَالَ الَّذِينَ كَفَرُوا أَئِذَا كُنَّا تُرَابًا وَآبَاؤُنَا أَئِنَّا لَمُخْرَجُونَ}
INSERT INTO [b9617] VALUES (3175,'<span data-type="title" id=toc-3157>٦٨ </span>- {لَقَدْ وُعِدْنَا هَذَا نَحْنُ وَآبَاؤُنَا مِنْ قَبْلُ إِنْ هَذَا إِلا أَسَاطِيرُ الأَوَّلِينَ}
INSERT INTO [b9617] VALUES (3176,'<span data-type="title" id=toc-3158>٦٩ </span>- {قُلْ سِيرُوا فِي الأَرْضِ فَانْظُرُوا كَيْفَ كَانَ عَاقِبَةُ الْمُجْرِمِينَ}
INSERT INTO [b9617] VALUES (3177,'<span data-type="title" id=toc-3159>٧٠ </span>- {وَلا تَكُنْ فِي ضَيْقٍ مِمَّا يَمْكُرُونَ}
INSERT INTO [b9617] VALUES (3178,'<span data-type="title" id=toc-3160>٧١ </span>- {وَيَقُولُونَ مَتَى هَذَا الْوَعْدُ إِنْ كُنْتُمْ صَادِقِينَ}
INSERT INTO [b9617] VALUES (3179,'<span data-type="title" id=toc-3161>٧٢ </span>- {قُلْ عَسَى أَنْ يَكُونَ رَدِفَ لَكُمْ بَعْضُ الَّذِي تَسْتَعْجِلُونَ}
INSERT INTO [b9617] VALUES (3180,'<span data-type="title" id=toc-3162>٧٣ </span>- {وَإِنَّ رَبَّكَ لَذُو فَضْلٍ عَلَى النَّاسِ وَلَكِنَّ أَكْثَرَهُمْ لا يَشْكُرُونَ}
INSERT INTO [b9617] VALUES (3181,'<span data-type="title" id=toc-3163>٧٤ </span>- {وَإِنَّ رَبَّكَ لَيَعْلَمُ مَا تُكِنُّ صُدُورُهُمْ}
INSERT INTO [b9617] VALUES (3182,'<span data-type="title" id=toc-3164>٧٥ </span>- {وَمَا مِنْ غَائِبَةٍ فِي السَّمَاءِ وَالأَرْضِ إِلا فِي كِتَابٍ مُبِينٍ}
INSERT INTO [b9617] VALUES (3183,'<span data-type="title" id=toc-3165>٧٦ </span>- {يَقُصُّ عَلَى بَنِي إِسْرَائِيلَ أَكْثَرَ الَّذِي هُمْ فِيهِ يَخْتَلِفُونَ}
INSERT INTO [b9617] VALUES (3184,'<span data-type="title" id=toc-3166>٧٧ </span>- {وَإِنَّهُ لَهُدًى وَرَحْمَةٌ لِلْمُؤْمِنِينَ}
INSERT INTO [b9617] VALUES (3185,'<span data-type="title" id=toc-3167>٧٨ </span>- {يَقْضِي بَيْنَهُمْ بِحُكْمِهِ وَهُوَ الْعَزِيزُ الْعَلِيمُ}
INSERT INTO [b9617] VALUES (3186,'<span data-type="title" id=toc-3168>٧٩ </span>- {فَتَوَكَّلْ عَلَى اللَّهِ}
INSERT INTO [b9617] VALUES (3187,'<span data-type="title" id=toc-3169>٨٠ </span>- {إِنَّكَ لا تُسْمِعُ الْمَوْتَى وَلا تُسْمِعُ الصُّمَّ الدُّعَاءَ إِذَا وَلَّوْا مُدْبِرِينَ}
INSERT INTO [b9617] VALUES (3188,'<span data-type="title" id=toc-3170>٨١ </span>- {وَمَا أَنْتَ بِهَادِ الْعُمْيِ عَنْ ضَلالَتِهِمْ إِنْ تُسْمِعُ إِلا مَنْ يُؤْمِنُ بِآيَاتِنَا فَهُمْ مُسْلِمُونَ}
INSERT INTO [b9617] VALUES (3189,'<span data-type="title" id=toc-3171>٨٢ </span>- {وَإِذَا وَقَعَ الْقَوْلُ عَلَيْهِمْ أَخْرَجْنَا لَهُمْ دَابَّةً مِنَ الأَرْضِ تُكَلِّمُهُمْ أَنَّ النَّاسَ كَانُوا بِآيَاتِنَا لا يُوقِنُونَ}
INSERT INTO [b9617] VALUES (3190,'<span data-type="title" id=toc-3172>٨٣ </span>- {وَيَوْمَ نَحْشُرُ مِنْ كُلِّ أُمَّةٍ فَوْجًا مِمَّنْ يُكَذِّبُ بِآيَاتِنَا فَهُمْ يُوزَعُونَ}
INSERT INTO [b9617] VALUES (3191,'<span data-type="title" id=toc-3173>٨٤ </span>- {حَتَّى إِذَا جَاءُوا قَالَ أَكَذَّبْتُمْ بِآيَاتِي وَلَمْ تُحِيطُوا بِهَا عِلْمًا أَمْ مَاذَا كُنْتُمْ تَعْمَلُونَ}
INSERT INTO [b9617] VALUES (3192,'<span data-type="title" id=toc-3174>٨٥ </span>- {وَوَقَعَ الْقَوْلُ عَلَيْهِمْ بِمَا ظَلَمُوا فَهُمْ لا يَنْطِقُونَ}
INSERT INTO [b9617] VALUES (3193,'<span data-type="title" id=toc-3175>٨٦ </span>- {أَلَمْ يَرَوْا أَنَّا جَعَلْنَا اللَّيْلَ لِيَسْكُنُوا فِيهِ وَالنَّهَارَ مُبْصِرًا إِنَّ فِي ذَلِكَ لآيَاتٍ لِقَوْمٍ يُؤْمِنُونَ}
INSERT INTO [b9617] VALUES (3194,'<span data-type="title" id=toc-3176>٨٧ </span>- {وَيَوْمَ يُنْفَخُ فِي الصُّورِ فَفَزِعَ مَنْ فِي السَّمَاوَاتِ وَمَنْ فِي الأَرْضِ إِلا مَنْ شَاءَ اللَّهُ وَكُلٌّ أَتَوْهُ دَاخِرِينَ}
INSERT INTO [b9617] VALUES (3195,'<span data-type="title" id=toc-3177>٨٨ </span>- {وَتَرَى الْجِبَالَ تَحْسَبُهَا جَامِدَةً وَهِيَ تَمُرُّ مَرَّ السَّحَابِ صُنْعَ اللَّهِ الَّذِي أَتْقَنَ كُلَّ شَيْءٍ إِنَّهُ خَبِيرٌ بِمَا تَفْعَلُونَ}
INSERT INTO [b9617] VALUES (3196,'<span data-type="title" id=toc-3178>٨٩ </span>- {مَنْ جَاءَ بِالْحَسَنَةِ فَلَهُ خَيْرٌ مِنْهَا وَهُمْ مِنْ فَزَعٍ يَوْمَئِذٍ آمِنُونَ}
INSERT INTO [b9617] VALUES (3197,'<span data-type="title" id=toc-3179>٩٠ </span>- {وَمَنْ جَاءَ بِالسَّيِّئَةِ فَكُبَّتْ وُجُوهُهُمْ فِي النَّارِ هَلْ تُجْزَوْنَ إِلا مَا كُنْتُمْ تَعْمَلُونَ}
INSERT INTO [b9617] VALUES (3198,'<span data-type="title" id=toc-3180>٩١ </span>- {إِنَّمَا أُمِرْتُ أَنْ أَعْبُدَ رَبَّ هَذِهِ الْبَلْدَةِ الَّذِي حَرَّمَهَا وَلَهُ كُلُّ شَيْءٍ وَأُمِرْتُ أَنْ أَكُونَ مِنَ الْمُسْلِمِينَ}
INSERT INTO [b9617] VALUES (3199,'<span data-type="title" id=toc-3181>٩٢ </span>- {فَمَنِ اهْتَدَى فَإِنَّمَا يَهْتَدِي لِنَفْسِهِ}
INSERT INTO [b9617] VALUES (3200,'<span data-type="title" id=toc-3182>٩٣ </span>- {وَقُلِ الْحَمْدُ لِلَّهِ سَيُرِيكُمْ آيَاتِهِ فَتَعْرِفُونَهَا وَمَا رَبُّكَ بِغَافِلٍ عَمَّا تَعْمَلُونَ}
INSERT INTO [b9617] VALUES (3201,'<span data-type="title" id=toc-3183>سورة القصص</span>','3','883',NULL,NULL,'0');
INSERT INTO [b9617] VALUES (3202,'<span data-type="title" id=toc-3184>٣ </span>- {نَتْلُو عَلَيْكَ مِنْ نَبَإِ مُوسَى وَفِرْعَوْنَ بِالْحَقِّ لِقَوْمٍ يُؤْمِنُونَ}
INSERT INTO [b9617] VALUES (3203,'<span data-type="title" id=toc-3185>٤ </span>- {إِنَّ فِرْعَوْنَ عَلا فِي الأَرْضِ وَجَعَلَ أَهْلَهَا شِيَعًا يَسْتَضْعِفُ طَائِفَةً مِنْهُمْ يُذَبِّحُ أَبْنَاءَهُمْ وَيَسْتَحْيِي نِسَاءَهُمْ إِنَّهُ كَانَ مِنَ الْمُفْسِدِينَ}
INSERT INTO [b9617] VALUES (3204,'<span data-type="title" id=toc-3186>٥ </span>- {وَنُرِيدُ أَنْ نَمُنَّ عَلَى الَّذِينَ اسْتُضْعِفُوا فِي الأَرْضِ وَنَجْعَلَهُمْ أَئِمَّةً وَنَجْعَلَهُمُ الْوَارِثِينَ}
INSERT INTO [b9617] VALUES (3205,'<span data-type="title" id=toc-3187>٦ </span>- {وَنُمَكِّنَ لَهُمْ فِي الأَرْضِ وَنُرِيَ فِرْعَوْنَ وَهَامَانَ وَجُنُودَهُمَا مِنْهُمْ مَا كَانُوا يَحْذَرُونَ}
INSERT INTO [b9617] VALUES (3206,'<span data-type="title" id=toc-3188>٧ </span>- {وَأَوْحَيْنَا إِلَى أُمِّ مُوسَى أَنْ أَرْضِعِيهِ فَإِذَا خِفْتِ عَلَيْهِ فَأَلْقِيهِ فِي الْيَمِّ وَلا تَخَافِي وَلا تَحْزَنِي إِنَّا رَادُّوهُ إِلَيْكِ وَجَاعِلُوهُ مِنَ الْمُرْسَلِينَ}
INSERT INTO [b9617] VALUES (3207,'<span data-type="title" id=toc-3189>٨ </span>- {فَالْتَقَطَهُ آلُ فِرْعَوْنَ لِيَكُونَ لَهُمْ عَدُوًّا وَحَزَنًا إِنَّ فِرْعَوْنَ وَهَامَانَ وَجُنُودَهُمَا كَانُوا خَاطِئِينَ}
INSERT INTO [b9617] VALUES (3208,'<span data-type="title" id=toc-3190>٩ </span>- {وَقَالَتِ امْرَأَتُ فِرْعَوْنَ قُرَّتُ عَيْنٍ لِي وَلَكَ لا تَقْتُلُوهُ عَسَى أَنْ يَنْفَعَنَا أَوْ نَتَّخِذَهُ وَلَدًا وَهُمْ لا يَشْعُرُونَ}
INSERT INTO [b9617] VALUES (3209,'<span data-type="title" id=toc-3191>١٠ </span>- {وَأَصْبَحَ فُؤَادُ أُمِّ مُوسَى فَارِغًا إِنْ كَادَتْ لَتُبْدِي بِهِ لَوْلا أَنْ رَبَطْنَا عَلَى قَلْبِهَا} ⦗٨٨٥⦘
INSERT INTO [b9617] VALUES (3210,'<span data-type="title" id=toc-3192>١١ </span>- {وَقَالَتْ لأُخْتِهِ قُصِّيهِ فَبَصُرَتْ بِهِ عَنْ جُنُبٍ وَهُمْ لا يَشْعُرُونَ}
INSERT INTO [b9617] VALUES (3211,'<span data-type="title" id=toc-3193>١٢ </span>- {وَحَرَّمْنَا عَلَيْهِ الْمَرَاضِعَ مِنْ قَبْلُ فَقَالَتْ هَلْ أَدُلُّكُمْ عَلَى أَهْلِ بَيْتٍ يَكْفُلُونَهُ لَكُمْ وَهُمْ لَهُ نَاصِحُونَ}
INSERT INTO [b9617] VALUES (3212,'<span data-type="title" id=toc-3194>١٣ </span>- {فَرَدَدْنَاهُ إِلَى أُمِّهِ كَيْ تَقَرَّ عَيْنُهَا وَلا تَحْزَنَ وَلِتَعْلَمَ أَنَّ وَعْدَ اللَّهِ حَقٌّ وَلَكِنَّ أَكْثَرَهُمْ لا يَعْلَمُونَ}
INSERT INTO [b9617] VALUES (3213,'<span data-type="title" id=toc-3195>١٤ </span>- {وَلَمَّا بَلَغَ أَشُدَّهُ وَاسْتَوَى آتَيْنَاهُ حُكْمًا وَعِلْمًا وَكَذَلِكَ نَجْزِي الْمُحْسِنِينَ}
INSERT INTO [b9617] VALUES (3214,'<span data-type="title" id=toc-3196>١٥ </span>- {وَدَخَلَ الْمَدِينَةَ عَلَى حِينِ غَفْلَةٍ مِنْ أَهْلِهَا فَوَجَدَ فِيهَا رَجُلَيْنِ يَقْتَتِلانِ هَذَا مِنْ شِيعَتِهِ وَهَذَا مِنْ عَدُوِّهِ فَاسْتَغَاثَهُ الَّذِي مِنْ شِيعَتِهِ عَلَى الَّذِي مِنْ عَدُوِّهِ فَوَكَزَهُ مُوسَى فَقَضَى عَلَيْهِ قَالَ هَذَا مِنْ عَمَلِ الشَّيْطَانِ إِنَّهُ عَدُوٌّ مُضِلٌّ مُبِينٌ}
INSERT INTO [b9617] VALUES (3215,'<span data-type="title" id=toc-3197>١٦ </span>- {قَالَ رَبِّ إِنِّي ظَلَمْتُ نَفْسِي فَاغْفِرْ لِي فَغَفَرَ لَهُ إِنَّهُ هُوَ الْغَفُورُ الرَّحِيمُ}
INSERT INTO [b9617] VALUES (3216,'<span data-type="title" id=toc-3198>١٧ </span>- {قَالَ رَبِّ بِمَا أَنْعَمْتَ عَلَيَّ فَلَنْ أَكُونَ ظَهِيرًا لِلْمُجْرِمِينَ} ⦗٨٨٧⦘
INSERT INTO [b9617] VALUES (3217,'<span data-type="title" id=toc-3199>١٨ </span>- {فَأَصْبَحَ فِي الْمَدِينَةِ خَائِفًا يَتَرَقَّبُ فَإِذَا الَّذِي اسْتَنْصَرَهُ بِالأَمْسِ يَسْتَصْرِخُهُ قَالَ لَهُ مُوسَى إِنَّكَ لَغَوِيٌّ مُبِينٌ}
INSERT INTO [b9617] VALUES (3218,'<span data-type="title" id=toc-3200>١٩ </span>- {فَلَمَّا أَنْ أَرَادَ أَنْ يَبْطِشَ بِالَّذِي هُوَ عَدُوٌّ لَهُمَا قَالَ يَا مُوسَى أَتُرِيدُ أَنْ تَقْتُلَنِي كَمَا قَتَلْتَ نَفْسًا بِالأَمْسِ إِنْ تُرِيدُ إِلا أَنْ تَكُونَ جَبَّارًا فِي الأَرْضِ}
INSERT INTO [b9617] VALUES (3219,'<span data-type="title" id=toc-3201>٢٠ </span>- {وَجَاءَ رَجُلٌ مِنْ أَقْصَى الْمَدِينَةِ يَسْعَى قَالَ يَا مُوسَى إِنَّ الْمَلأَ يَأْتَمِرُونَ بِكَ لِيَقْتُلُوكَ فَاخْرُجْ إِنِّي لَكَ مِنَ النَّاصِحِينَ} ⦗٨٨٨⦘
INSERT INTO [b9617] VALUES (3220,'<span data-type="title" id=toc-3202>٢١ </span>- {فَخَرَجَ مِنْهَا خَائِفًا يَتَرَقَّبُ قَالَ رَبِّ نَجِّنِي مِنَ الْقَوْمِ الظَّالِمِينَ}
INSERT INTO [b9617] VALUES (3221,'<span data-type="title" id=toc-3203>٢٢ </span>- {وَلَمَّا تَوَجَّهَ تِلْقَاءَ مَدْيَنَ قَالَ عَسَى رَبِّي أَنْ يَهْدِيَنِي سَوَاءَ السَّبِيلِ}
INSERT INTO [b9617] VALUES (3222,'<span data-type="title" id=toc-3204>٢٣ </span>- {وَلَمَّا وَرَدَ مَاءَ مَدْيَنَ وَجَدَ عَلَيْهِ أُمَّةً مِنَ النَّاسِ يَسْقُونَ وَوَجَدَ مِنْ دُونِهِمُ امْرَأتَيْنِ تَذُودَانِ قَالَ مَا خَطْبُكُمَا قَالَتَا لا نَسْقِي حَتَّى يُصْدِرَ الرِّعَاءُ وَأَبُونَا شَيْخٌ كَبِيرٌ}
INSERT INTO [b9617] VALUES (3223,'<span data-type="title" id=toc-3205>٢٤ </span>- {فَسَقَى لَهُمَا ثُمَّ تَوَلَّى إِلَى الظِّلِّ فَقَالَ رَبِّ إِنِّي لِمَا أَنْزَلْتَ إِلَيَّ مِنْ خَيْرٍ فَقِيرٌ}
INSERT INTO [b9617] VALUES (3224,'<span data-type="title" id=toc-3206>٢٥ </span>- {فَجَاءَتْهُ إِحْدَاهُمَا تَمْشِي عَلَى اسْتِحْيَاءٍ قَالَتْ إِنَّ أَبِي يَدْعُوكَ لِيَجْزِيَكَ أَجْرَ مَا سَقَيْتَ لَنَا فَلَمَّا جَاءَهُ وَقَصَّ عَلَيْهِ الْقَصَصَ قَالَ لا تَخَفْ نَجَوْتَ مِنَ الْقَوْمِ الظَّالِمِينَ}
INSERT INTO [b9617] VALUES (3225,'<span data-type="title" id=toc-3207>٢٦ </span>- {قَالَتْ إِحْدَاهُمَا يَا أَبَتِ اسْتَأْجِرْهُ إِنَّ خَيْرَ مَنِ اسْتَأْجَرْتَ الْقَوِيُّ الأَمِينُ}
INSERT INTO [b9617] VALUES (3226,'<span data-type="title" id=toc-3208>٢٧ </span>- {قَالَ إِنِّي أُرِيدُ أَنْ أُنْكِحَكَ إِحْدَى ابْنَتَيَّ هَاتَيْنِ عَلَى أَنْ تَأْجُرَنِي ثَمَانِيَ حِجَجٍ فَإِنْ أَتْمَمْتَ عَشْرًا فَمِنْ عِنْدِكَ وَمَا أُرِيدُ أَنْ أَشُقَّ عَلَيْكَ سَتَجِدُنِي إِنْ شَاءَ اللَّهُ مِنَ ⦗٨٩٠⦘ الصَّالِحِينَ}
INSERT INTO [b9617] VALUES (3227,'<span data-type="title" id=toc-3209>٢٨ </span>- {قَالَ ذَلِكَ بَيْنِي وَبَيْنَكَ أَيَّمَا الأَجَلَيْنِ قَضَيْتُ فَلا عُدْوَانَ عَلَيَّ وَاللَّهُ عَلَى مَا نَقُولُ وَكِيلٌ}
INSERT INTO [b9617] VALUES (3228,'<span data-type="title" id=toc-3210>٢٩ </span>- {فَلَمَّا قَضَى مُوسَى الأَجَلَ وَسَارَ بِأَهْلِهِ آنَسَ مِنْ جَانِبِ الطُّورِ نَارًا قَالَ لأَهْلِهِ امْكُثُوا إِنِّي آنَسْتُ نَارًا لَعَلِّي آتِيكُمْ مِنْهَا بِخَبَرٍ أَوْ جَذْوَةٍ مِنَ النَّارِ لَعَلَّكُمْ تَصْطَلُونَ} ⦗٨٩١⦘
INSERT INTO [b9617] VALUES (3229,'<span data-type="title" id=toc-3211>٣٠ </span>- {فَلَمَّا أَتَاهَا نُودِيَ مِنْ شَاطِئِ الْوَادِ الأَيْمَنِ فِي الْبُقْعَةِ الْمُبَارَكَةِ مِنَ الشَّجَرَةِ أَنْ يَا مُوسَى إِنِّي أَنَا اللَّهُ رَبُّ الْعَالَمِينَ}
INSERT INTO [b9617] VALUES (3230,'<span data-type="title" id=toc-3212>٣١ </span>- {وَأَنْ أَلْقِ عَصَاكَ فَلَمَّا رَآهَا تَهْتَزُّ كَأَنَّهَا جَانٌّ وَلَّى مُدْبِرًا وَلَمْ يُعَقِّبْ يَا مُوسَى أَقْبِلْ وَلا تَخَفْ إِنَّكَ مِنَ الآمِنِينَ}
INSERT INTO [b9617] VALUES (3231,'<span data-type="title" id=toc-3213>٣٢ </span>- {اسْلُكْ يَدَكَ فِي جَيْبِكَ تَخْرُجْ بَيْضَاءَ مِنْ غَيْرِ سُوءٍ وَاضْمُمْ إِلَيْكَ جَنَاحَكَ مِنَ الرَّهْبِ فَذَانِكَ بُرْهَانَانِ مِنْ رَبِّكَ إِلَى فِرْعَوْنَ وَمَلَئِهِ إِنَّهُمْ كَانُوا قَوْمًا فَاسِقِينَ} ⦗٨٩٢⦘
INSERT INTO [b9617] VALUES (3232,'<span data-type="title" id=toc-3214>٣٣ </span>- {قَالَ رَبِّ إِنِّي قَتَلْتُ مِنْهُمْ نَفْسًا فَأَخَافُ أَنْ يَقْتُلُونِ}
INSERT INTO [b9617] VALUES (3233,'<span data-type="title" id=toc-3215>٣٤ </span>- {وَأَخِي هَارُونُ هُوَ أَفْصَحُ مِنِّي لِسَانًا فَأَرْسِلْهُ مَعِيَ رِدْءًا يُصَدِّقُنِي إِنِّي أَخَافُ أَنْ يُكَذِّبُونِ}
INSERT INTO [b9617] VALUES (3234,'<span data-type="title" id=toc-3216>٣٥ </span>- {وَنَجْعَلُ لَكُمَا سُلْطَانًا فَلا يَصِلُونَ إِلَيْكُمَا بِآيَاتِنَا أَنْتُمَا وَمَنِ اتَّبَعَكُمَا الْغَالِبُونَ}
INSERT INTO [b9617] VALUES (3235,'<span data-type="title" id=toc-3217>٣٦ </span>- {فَلَمَّا جَاءَهُمْ مُوسَى بِآيَاتِنَا بَيِّنَاتٍ قَالُوا مَا هَذَا إِلا سِحْرٌ مُفْتَرًى وَمَا سَمِعْنَا بِهَذَا فِي آبَائِنَا الأَوَّلِينَ}
INSERT INTO [b9617] VALUES (3236,'<span data-type="title" id=toc-3218>٣٧ </span>- {وَقَالَ مُوسَى رَبِّي أَعْلَمُ بِمَنْ جَاءَ بِالْهُدَى مِنْ عِنْدِهِ وَمَنْ تَكُونُ لَهُ عَاقِبَةُ الدَّارِ إِنَّهُ لا يُفْلِحُ الظَّالِمُونَ}
INSERT INTO [b9617] VALUES (3237,'<span data-type="title" id=toc-3219>٣٨ </span>- {وَقَالَ فِرْعَوْنُ يَا أَيُّهَا الْمَلأُ مَا عَلِمْتُ لَكُمْ مِنْ إِلَهٍ غَيْرِي فَأَوْقِدْ لِي يَاهَامَانُ ⦗٨٩٤⦘ عَلَى الطِّينِ فَاجْعَلْ لِي صَرْحًا لَعَلِّي أَطَّلِعُ إِلَى إِلَهِ مُوسَى وَإِنِّي لأَظُنُّهُ مِنَ الْكَاذِبِينَ}
INSERT INTO [b9617] VALUES (3238,'<span data-type="title" id=toc-3220>٣٩ </span>- {وَاسْتَكْبَرَ هُوَ وَجُنُودُهُ فِي الأَرْضِ بِغَيْرِ الْحَقِّ وَظَنُّوا أَنَّهُمْ إِلَيْنَا لا يُرْجَعُونَ}
INSERT INTO [b9617] VALUES (3239,'<span data-type="title" id=toc-3221>٤٠ </span>- {فَأَخَذْنَاهُ وَجُنُودَهُ فَنَبَذْنَاهُمْ فِي الْيَمِّ فَانْظُرْ كَيْفَ كَانَ عَاقِبَةُ الظَّالِمِينَ}
INSERT INTO [b9617] VALUES (3240,'<span data-type="title" id=toc-3222>٤١ </span>- {وَجَعَلْنَاهُمْ أَئِمَّةً يَدْعُونَ إِلَى النَّارِ وَيَوْمَ الْقِيَامَةِ لا يُنْصَرُونَ}
INSERT INTO [b9617] VALUES (3241,'<span data-type="title" id=toc-3223>٤٢ </span>- {وَأَتْبَعْنَاهُمْ فِي هَذِهِ الدُّنْيَا لَعْنَةً وَيَوْمَ الْقِيَامَةِ هُمْ مِنَ الْمَقْبُوحِينَ}
INSERT INTO [b9617] VALUES (3242,'<span data-type="title" id=toc-3224>٤٣ </span>- {وَلَقَدْ آتَيْنَا مُوسَى الْكِتَابَ مِنْ بَعْدِ مَا أَهْلَكْنَا الْقُرُونَ الأُولَى بَصَائِرَ لِلنَّاسِ وَهُدًى وَرَحْمَةً لَعَلَّهُمْ يَتَذَكَّرُونَ}
INSERT INTO [b9617] VALUES (3243,'<span data-type="title" id=toc-3225>٤٤ </span>- {وَمَا كُنْتَ بِجَانِبِ الْغَرْبِيِّ إِذْ قَضَيْنَا إِلَى مُوسَى الأَمْرَ وَمَا كُنْتَ مِنَ الشَّاهِدِينَ}
INSERT INTO [b9617] VALUES (3244,'<span data-type="title" id=toc-3226>٤٥ </span>- {وَلَكِنَّا أَنْشَأْنَا قُرُونًا فَتَطَاوَلَ عَلَيْهِمُ الْعُمُرُ وَمَا كُنْتَ ثَاوِيًا فِي أَهْلِ مَدْيَنَ تَتْلُو عَلَيْهِمْ آيَاتِنَا وَلَكِنَّا كُنَّا مُرْسِلِينَ}
INSERT INTO [b9617] VALUES (3245,'<span data-type="title" id=toc-3227>٤٦ </span>- {وَمَا كُنْتَ بِجَانِبِ الطُّورِ إِذْ نَادَيْنَا وَلَكِنْ رَحْمَةً مِنْ رَبِّكَ لِتُنْذِرَ قَوْمًا مَا أَتَاهُمْ مِنْ نَذِيرٍ مِنْ قَبْلِكَ}
INSERT INTO [b9617] VALUES (3246,'<span data-type="title" id=toc-3228>٤٧ </span>- {وَلَوْلا أَنْ تُصِيبَهُمْ مُصِيبَةٌ بِمَا قَدَّمَتْ أَيْدِيهِمْ فَيَقُولُوا رَبَّنَا لَوْلا أَرْسَلْتَ إِلَيْنَا رَسُولا فَنَتَّبِعَ آيَاتِكَ وَنَكُونَ مِنَ الْمُؤْمِنِينَ}
INSERT INTO [b9617] VALUES (3247,'<span data-type="title" id=toc-3229>٤٨ </span>- {فَلَمَّا جَاءَهُمُ الْحَقُّ مِنْ عِنْدِنَا قَالُوا لَوْلا أُوتِيَ مِثْلَ مَا أُوتِيَ مُوسَى أَوَلَمْ يَكْفُرُوا بِمَا أُوتِيَ مُوسَى مِنْ قَبْلُ قَالُوا سِحْرَانِ تَظَاهَرَا وَقَالُوا إِنَّا بِكُلٍّ كَافِرُونَ}
INSERT INTO [b9617] VALUES (3248,'<span data-type="title" id=toc-3230>٤٩ </span>- {قُلْ فَأْتُوا بِكِتَابٍ مِنْ عِنْدِ اللَّهِ هُوَ أَهْدَى مِنْهُمَا أَتَّبِعْهُ إِنْ كُنْتُمْ صَادِقِينَ}
INSERT INTO [b9617] VALUES (3249,'<span data-type="title" id=toc-3231>٥٠ </span>- {فَإِنْ لَمْ يَسْتَجِيبُوا لَكَ فَاعْلَمْ أَنَّمَا يَتَّبِعُونَ أَهْوَاءَهُمْ وَمَنْ أَضَلُّ مِمَّنَ اتَّبَعَ هَوَاهُ بِغَيْرِ هُدًى مِنَ اللَّهِ}
INSERT INTO [b9617] VALUES (3250,'<span data-type="title" id=toc-3232>٥١ </span>- {وَلَقَدْ وَصَّلْنَا لَهُمُ الْقَوْلَ لَعَلَّهُمْ يَتَذَكَّرُونَ} ⦗٨٩٨⦘
INSERT INTO [b9617] VALUES (3251,'<span data-type="title" id=toc-3233>٥٢ </span>- {الَّذِينَ آتَيْنَاهُمُ الْكِتَابَ مِنْ قَبْلِهِ هُمْ بِهِ يُؤْمِنُونَ}
INSERT INTO [b9617] VALUES (3252,'<span data-type="title" id=toc-3234>٥٣ </span>- {وَإِذَا يُتْلَى عَلَيْهِمْ قَالُوا آمَنَّا بِهِ إِنَّهُ الْحَقُّ مِنْ رَبِّنَا إِنَّا كُنَّا مِنْ قَبْلِهِ مُسْلِمِينَ}
INSERT INTO [b9617] VALUES (3253,'<span data-type="title" id=toc-3235>٥٤ </span>- {أُولَئِكَ يُؤْتَوْنَ أَجْرَهُمْ مَرَّتَيْنِ بِمَا صَبَرُوا وَيَدْرَءُونَ بِالْحَسَنَةِ السَّيِّئَةَ وَمِمَّا رَزَقْنَاهُمْ يُنْفِقُونَ}
INSERT INTO [b9617] VALUES (3254,'<span data-type="title" id=toc-3236>٥٥ </span>- {وَإِذَا سَمِعُوا اللَّغْوَ أَعْرَضُوا عَنْهُ وَقَالُوا لَنَا أَعْمَالُنَا وَلَكُمْ أَعْمَالُكُمْ سَلامٌ عَلَيْكُمْ لا نَبْتَغِي الْجَاهِلِينَ}
INSERT INTO [b9617] VALUES (3255,'<span data-type="title" id=toc-3237>٥٦ </span>- {إِنَّكَ لا تَهْدِي مَنْ أَحْبَبْتَ وَلَكِنَّ اللَّهَ يَهْدِي مَنْ يَشَاءُ وَهُوَ أَعْلَمُ بِالْمُهْتَدِينَ}
INSERT INTO [b9617] VALUES (3256,'<span data-type="title" id=toc-3238>٥٧ </span>- {وَقَالُوا إِنْ نَتَّبِعِ الْهُدَى مَعَكَ نُتَخَطَّفْ مِنْ أَرْضِنَا أَوَلَمْ نُمَكِّنْ لَهُمْ حَرَمًا آمِنًا يُجْبَى إِلَيْهِ ثَمَرَاتُ كُلِّ شَيْءٍ رِزْقًا مِنْ لَدُنَّا وَلَكِنَّ أَكْثَرَهُمْ لا يَعْلَمُونَ}
INSERT INTO [b9617] VALUES (3257,'<span data-type="title" id=toc-3239>٥٨ </span>- {وَكَمْ أَهْلَكْنَا مِنْ قَرْيَةٍ بَطِرَتْ مَعِيشَتَهَا فَتِلْكَ مَسَاكِنُهُمْ لَمْ تُسْكَنْ مِنْ بَعْدِهِمْ إِلا قَلِيلا وَكُنَّا نَحْنُ الْوَارِثِينَ}
INSERT INTO [b9617] VALUES (3258,'<span data-type="title" id=toc-3240>٥٩ </span>- {وَمَا كَانَ رَبُّكَ مُهْلِكَ الْقُرَى حَتَّى يَبْعَثَ فِي أُمِّهَا رَسُولا يَتْلُو عَلَيْهِمْ آيَاتِنَا وَمَا كُنَّا مُهْلِكِي الْقُرَى إِلا وَأَهْلُهَا ظَالِمُونَ}
INSERT INTO [b9617] VALUES (3259,'<span data-type="title" id=toc-3241>٦٠ </span>- {وَمَا أُوتِيتُمْ مِنْ شَيْءٍ فَمَتَاعُ الْحَيَاةِ الدُّنْيَا وَزِينَتُهَا وَمَا عِنْدَ اللَّهِ خَيْرٌ وَأَبْقَى أَفَلا تَعْقِلُونَ}
INSERT INTO [b9617] VALUES (3260,'<span data-type="title" id=toc-3242>٦١ </span>- {أَفَمَنْ وَعَدْنَاهُ وَعْدًا حَسَنًا فَهُوَ لاقِيهِ كَمَنْ مَتَّعْنَاهُ مَتَاعَ الْحَيَاةِ الدُّنْيَا ثُمَّ هُوَ يَوْمَ الْقِيَامَةِ مِنَ الْمُحْضَرِينَ}
INSERT INTO [b9617] VALUES (3261,'<span data-type="title" id=toc-3243>٦٢ </span>- {وَيَوْمَ يُنَادِيهِمْ فَيَقُولُ أَيْنَ شُرَكَائِيَ الَّذِينَ كُنْتُمْ تَزْعُمُونَ}
INSERT INTO [b9617] VALUES (3262,'<span data-type="title" id=toc-3244>٦٣ </span>- {قَالَ الَّذِينَ حَقَّ عَلَيْهِمُ الْقَوْلُ رَبَّنَا هَؤُلاءِ الَّذِينَ أَغْوَيْنَا أَغْوَيْنَاهُمْ كَمَا غَوَيْنَا تَبَرَّأْنَا إِلَيْكَ مَا كَانُوا إِيَّانَا يَعْبُدُونَ}
INSERT INTO [b9617] VALUES (3263,'<span data-type="title" id=toc-3245>٦٤ </span>- {وَقِيلَ ادْعُوا شُرَكَاءَكُمْ فَدَعَوْهُمْ فَلَمْ يَسْتَجِيبُوا لَهُمْ وَرَأَوُا الْعَذَابَ لَوْ أَنَّهُمْ كَانُوا يَهْتَدُونَ} ⦗٩٠٢⦘
INSERT INTO [b9617] VALUES (3264,'<span data-type="title" id=toc-3246>٦٥ </span>- {وَيَوْمَ يُنَادِيهِمْ فَيَقُولُ مَاذَا أَجَبْتُمُ الْمُرْسَلِينَ}
INSERT INTO [b9617] VALUES (3265,'<span data-type="title" id=toc-3247>٦٦ </span>- {فَعَمِيَتْ عَلَيْهِمُ الأَنْبَاءُ يَوْمَئِذٍ فَهُمْ لا يَتَسَاءَلُونَ}
INSERT INTO [b9617] VALUES (3266,'<span data-type="title" id=toc-3248>٦٧ </span>- {فَأَمَّا مَنْ تَابَ وَآمَنَ وَعَمِلَ صَالِحًا فَعَسَى أَنْ يَكُونَ مِنَ الْمُفْلِحِينَ}
INSERT INTO [b9617] VALUES (3267,'<span data-type="title" id=toc-3249>٦٨ </span>- {وَرَبُّكَ يَخْلُقُ مَا يَشَاءُ وَيَخْتَارُ مَا كَانَ لَهُمُ الْخِيَرَةُ سُبْحَانَ اللَّهِ وَتَعَالَى عَمَّا يُشْرِكُونَ} ⦗٩٠٣⦘
INSERT INTO [b9617] VALUES (3268,'<span data-type="title" id=toc-3250>٧٠ </span>- {وَهُوَ اللَّهُ لا إِلَهَ إِلا هُوَ لَهُ الْحَمْدُ فِي الأُولَى وَالآخِرَةِ وَلَهُ الْحُكْمُ وَإِلَيْهِ تُرْجَعُونَ}
INSERT INTO [b9617] VALUES (3269,'<span data-type="title" id=toc-3251>٧١ </span>- {قُلْ أَرَأَيْتُمْ إِنْ جَعَلَ اللَّهُ عَلَيْكُمُ اللَّيْلَ سَرْمَدًا إِلَى يَوْمِ الْقِيَامَةِ مَنْ إِلَهٌ غَيْرُ اللَّهِ يَأْتِيكُمْ بِضِيَاءٍ أَفَلا تَسْمَعُونَ}
INSERT INTO [b9617] VALUES (3270,'<span data-type="title" id=toc-3252>٧٢ </span>- {قُلْ أَرَأَيْتُمْ إِنْ جَعَلَ اللَّهُ عَلَيْكُمُ النَّهَارَ سَرْمَدًا إِلَى يَوْمِ الْقِيَامَةِ مَنْ إِلَهٌ غَيْرُ اللَّهِ يَأْتِيكُمْ بِلَيْلٍ تَسْكُنُونَ فِيهِ أَفَلا تُبْصِرُونَ}
INSERT INTO [b9617] VALUES (3271,'<span data-type="title" id=toc-3253>٧٣ </span>- {وَمِنْ رَحْمَتِهِ جَعَلَ لَكُمُ اللَّيْلَ وَالنَّهَارَ لِتَسْكُنُوا فِيهِ وَلِتَبْتَغُوا مِنْ فَضْلِهِ وَلَعَلَّكُمْ تَشْكُرُونَ}
INSERT INTO [b9617] VALUES (3272,'<span data-type="title" id=toc-3254>٧٤ </span>- {وَيَوْمَ يُنَادِيهِمْ فَيَقُولُ أَيْنَ شُرَكَائِيَ الَّذِينَ كُنْتُمْ تَزْعُمُونَ}
INSERT INTO [b9617] VALUES (3273,'<span data-type="title" id=toc-3255>٧٥ </span>- {وَنَزَعْنَا مِنْ كُلِّ أُمَّةٍ شَهِيدًا فَقُلْنَا هَاتُوا بُرْهَانَكُمْ فَعَلِمُوا أَنَّ الْحَقَّ لِلَّهِ وَضَلَّ عَنْهُمْ مَا كَانُوا يَفْتَرُونَ}
INSERT INTO [b9617] VALUES (3274,'<span data-type="title" id=toc-3256>٧٦ </span>- {إِنَّ قَارُونَ كَانَ مِنْ قَوْمِ مُوسَى فَبَغَى عَلَيْهِمْ وَآتَيْنَاهُ مِنَ الْكُنُوزِ مَا إِنَّ مَفَاتِحَهُ لَتَنُوءُ بِالْعُصْبَةِ أُولِي الْقُوَّةِ إِذْ قَالَ لَهُ قَوْمُهُ لا تَفْرَحْ إِنَّ اللَّهَ لا يُحِبُّ الْفَرِحِينَ}
INSERT INTO [b9617] VALUES (3275,'<span data-type="title" id=toc-3257>٧٧ </span>- {وَابْتَغِ فِيمَا آتَاكَ اللَّهُ الدَّارَ الآخِرَةَ وَلا تَنْسَ نَصِيبَكَ مِنَ الدُّنْيَا وَأَحْسِنْ كَمَا أَحْسَنَ اللَّهُ إِلَيْكَ وَلا تَبْغِ الْفَسَادَ فِي الأَرْضِ إِنَّ اللَّهَ لا يُحِبُّ الْمُفْسِدِينَ}
INSERT INTO [b9617] VALUES (3276,'<span data-type="title" id=toc-3258>٧٨ </span>- {قَالَ إِنَّمَا أُوتِيتُهُ عَلَى عِلْمٍ عِنْدِي أَوَلَمْ يَعْلَمْ أَنَّ اللَّهَ قَدْ أَهْلَكَ مِنْ قَبْلِهِ مِنَ الْقُرُونِ مَنْ هُوَ أَشَدُّ مِنْهُ قُوَّةً وَأَكْثَرُ جَمْعًا وَلا يُسْأَلُ عَنْ ذُنُوبِهِمُ الْمُجْرِمُونَ}
INSERT INTO [b9617] VALUES (3277,'<span data-type="title" id=toc-3259>٧٩ </span>- {فَخَرَجَ عَلَى قَوْمِهِ فِي زِينَتِهِ قَالَ الَّذِينَ يُرِيدُونَ الْحَيَاةَ الدُّنْيَا يَا لَيْتَ لَنَا مِثْلَ مَا أُوتِيَ قَارُونُ إِنَّهُ لَذُو حَظٍّ عَظِيمٍ}
INSERT INTO [b9617] VALUES (3278,'<span data-type="title" id=toc-3260>٨٠ </span>- {وَقَالَ الَّذِينَ أُوتُوا الْعِلْمَ وَيْلَكُمْ ثَوَابُ اللَّهِ خَيْرٌ لِمَنْ آمَنَ وَعَمِلَ صَالِحًا وَلا يُلَقَّاهَا إِلا الصَّابِرُونَ}
INSERT INTO [b9617] VALUES (3279,'<span data-type="title" id=toc-3261>٨١ </span>- {فَخَسَفْنَا بِهِ وَبِدَارِهِ الأَرْضَ فَمَا كَانَ لَهُ مِنْ فِئَةٍ يَنْصُرُونَهُ مِنْ دُونِ اللَّهِ وَمَا كَانَ مِنَ الْمُنْتَصِرِينَ}
INSERT INTO [b9617] VALUES (3280,'<span data-type="title" id=toc-3262>٨٢ </span>- {وَأَصْبَحَ الَّذِينَ تَمَنَّوْا مَكَانَهُ بِالأَمْسِ يَقُولُونَ وَيْكَأَنَّ اللَّهَ يَبْسُطُ الرِّزْقَ لِمَنْ يَشَاءُ مِنْ عِبَادِهِ وَيَقْدِرُ لَوْلا أَنْ مَنَّ اللَّهُ عَلَيْنَا لَخَسَفَ بِنَا وَيْكَأَنَّهُ لا يُفْلِحُ الْكَافِرُونَ}
INSERT INTO [b9617] VALUES (3281,'<span data-type="title" id=toc-3263>٨٣ </span>- {تِلْكَ الدَّارُ الآخِرَةُ نَجْعَلُهَا لِلَّذِينَ لا يُرِيدُونَ عُلُوًّا فِي الأَرْضِ وَلا فَسَادًا وَالْعَاقِبَةُ لِلْمُتَّقِينَ}
INSERT INTO [b9617] VALUES (3282,'<span data-type="title" id=toc-3264>٨٤ </span>- {وَمَنْ جَاءَ بِالسَّيِّئَةِ فَلا يُجْزَى الَّذِينَ عَمِلُوا السَّيِّئَاتِ إِلا مَا كَانُوا يَعْمَلُونَ}
INSERT INTO [b9617] VALUES (3283,'<span data-type="title" id=toc-3265>٨٥ </span>- {إِنَّ الَّذِي فَرَضَ عَلَيْكَ الْقُرْآنَ لَرَادُّكَ إِلَى مَعَادٍ قُلْ رَبِّي أَعْلَمُ مَنْ جَاءَ بِالْهُدَى وَمَنْ هُوَ فِي ضَلالٍ مُبِينٍ}
INSERT INTO [b9617] VALUES (3284,'<span data-type="title" id=toc-3266>٨٦ </span>- {وَمَا كُنْتَ تَرْجُو أَنْ يُلْقَى إِلَيْكَ الْكِتَابُ إِلا رَحْمَةً مِنْ رَبِّكَ فَلا تَكُونَنَّ ظَهِيرًا لِلْكَافِرِينَ}
INSERT INTO [b9617] VALUES (3285,'<span data-type="title" id=toc-3267>٨٧ </span>- {وَلا يَصُدُّنَّكَ عَنْ آيَاتِ اللَّهِ بَعْدَ إِذْ أُنْزِلَتْ إِلَيْكَ}
INSERT INTO [b9617] VALUES (3286,'<span data-type="title" id=toc-3268>٨٨ </span>- {وَلا تَدْعُ مَعَ اللَّهِ إِلَهًا آخَرَ لا إِلَهَ إِلا هُوَ كُلُّ شَيْءٍ هَالِكٌ إِلا وَجْهَهُ لَهُ الْحُكْمُ وَإِلَيْهِ تُرْجَعُونَ}
INSERT INTO [b9617] VALUES (3287,'<span data-type="title" id=toc-3269>سورة العنكبوت</span>','3','910',NULL,NULL,'0');
INSERT INTO [b9617] VALUES (3288,'<span data-type="title" id=toc-3270>٢ </span>- {أَحَسِبَ النَّاسُ أَنْ يُتْرَكُوا أَنْ يَقُولُوا آمَنَّا وَهُمْ لا يُفْتَنُونَ}
INSERT INTO [b9617] VALUES (3289,'<span data-type="title" id=toc-3271>٣ </span>- {وَلَقَدْ فَتَنَّا الَّذِينَ مِنْ قَبْلِهِمْ فَلَيَعْلَمَنَّ اللَّهُ الَّذِينَ صَدَقُوا}
INSERT INTO [b9617] VALUES (3290,'<span data-type="title" id=toc-3272>٤ </span>- {أَمْ حَسِبَ الَّذِينَ يَعْمَلُونَ السَّيِّئَاتِ أَنْ يَسْبِقُونَا سَاءَ مَا يَحْكُمُونَ}
INSERT INTO [b9617] VALUES (3291,'<span data-type="title" id=toc-3273>٥ </span>- {مَنْ كَانَ يَرْجُو لِقَاءَ اللَّهِ فَإِنَّ أَجَلَ اللَّهِ لآتٍ وَهُوَ السَّمِيعُ الْعَلِيمُ}
INSERT INTO [b9617] VALUES (3292,'<span data-type="title" id=toc-3274>٦ </span>- {إِنَّ اللَّهَ لَغَنِيٌّ عَنِ الْعَالَمِينَ}
INSERT INTO [b9617] VALUES (3293,'<span data-type="title" id=toc-3275>٧ </span>- {وَالَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ لَنُكَفِّرَنَّ عَنْهُمْ سَيِّئَاتِهِمْ وَلَنَجْزِيَنَّهُمْ أَحْسَنَ الَّذِي ⦗٩١١⦘ كَانُوا يَعْمَلُونَ}
INSERT INTO [b9617] VALUES (3294,'<span data-type="title" id=toc-3276>٨ </span>- {وَوَصَّيْنَا الإِنْسَانَ بِوَالِدَيْهِ حُسْنًا وَإِنْ جَاهَدَاكَ لِتُشْرِكَ بِي مَا لَيْسَ لَكَ بِهِ عِلْمٌ فَلا تُطِعْهُمَا إِلَيَّ مَرْجِعُكُمْ فَأُنَبِّئُكُمْ بِمَا كُنْتُمْ تَعْمَلُونَ}
INSERT INTO [b9617] VALUES (3295,'<span data-type="title" id=toc-3277>٩ </span>- {وَالَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ لَنُدْخِلَنَّهُمْ فِي الصَّالِحِينَ}
INSERT INTO [b9617] VALUES (3296,'<span data-type="title" id=toc-3278>١٠ </span>- {وَمِنَ النَّاسِ مَنْ يَقُولُ آمَنَّا بِاللَّهِ فَإِذَا أُوذِيَ فِي اللَّهِ جَعَلَ فِتْنَةَ النَّاسِ كَعَذَابِ اللَّهِ وَلَئِنْ جَاءَ نَصْرٌ مِنْ رَبِّكَ لَيَقُولُنَّ إِنَّا كُنَّا مَعَكُمْ أَوَلَيْسَ اللَّهُ بِأَعْلَمَ بِمَا فِي صُدُورِ الْعَالَمِينَ}
INSERT INTO [b9617] VALUES (3297,'<span data-type="title" id=toc-3279>١١ </span>- {وَلَيَعْلَمَنَّ اللَّهُ الَّذِينَ آمَنُوا}
INSERT INTO [b9617] VALUES (3298,'<span data-type="title" id=toc-3280>١٢ </span>- {وَقَالَ الَّذِينَ كَفَرُوا لِلَّذِينَ آمَنُوا اتَّبِعُوا سَبِيلَنَا وَلْنَحْمِلْ خَطَايَاكُمْ وَمَا هُمْ بِحَامِلِينَ مِنْ خَطَايَاهُمْ مِنْ شَيْءٍ إِنَّهُمْ لَكَاذِبُونَ}
INSERT INTO [b9617] VALUES (3299,'<span data-type="title" id=toc-3281>١٣ </span>- {وَلَيَحْمِلُنَّ أَثْقَالَهُمْ وَأَثْقَالا مَعَ أَثْقَالِهِمْ وَلَيُسْأَلُنَّ يَوْمَ الْقِيَامَةِ عَمَّا كَانُوا يَفْتَرُونَ}
INSERT INTO [b9617] VALUES (3300,'<span data-type="title" id=toc-3282>١٤ </span>- {وَلَقَدْ أَرْسَلْنَا نُوحًا إِلَى قَوْمِهِ فَلَبِثَ فِيهِمْ أَلْفَ سَنَةٍ إِلا خَمْسِينَ عَامًا}
INSERT INTO [b9617] VALUES (3301,'<span data-type="title" id=toc-3283>١٥ </span>- {فَأَنْجَيْنَاهُ وَأَصْحَابَ السَّفِينَةِ وَجَعَلْنَاهَا آيَةً لِلْعَالَمِينَ}
INSERT INTO [b9617] VALUES (3302,'<span data-type="title" id=toc-3284>١٦ </span>- {وَإِبْرَاهِيمَ إِذْ قَالَ لِقَوْمِهِ اعْبُدُوا اللَّهَ وَاتَّقُوهُ ذَلِكُمْ خَيْرٌ لَكُمْ إِنْ كُنْتُمْ تَعْلَمُونَ}
INSERT INTO [b9617] VALUES (3303,'<span data-type="title" id=toc-3285>١٧ </span>- {إِنَّمَا تَعْبُدُونَ مِنْ دُونِ اللَّهِ أَوْثَانًا وَتَخْلُقُونَ إِفْكًا إِنَّ الَّذِينَ تَعْبُدُونَ مِنْ دُونِ اللَّهِ لا يَمْلِكُونَ لَكُمْ رِزْقًا فَابْتَغُوا عِنْدَ اللَّهِ الرِّزْقَ وَاعْبُدُوهُ وَاشْكُرُوا لَهُ إِلَيْهِ تُرْجَعُونَ}
INSERT INTO [b9617] VALUES (3304,'<span data-type="title" id=toc-3286>١٨ </span>- {وَإِنْ تُكَذِّبُوا فَقَدْ كَذَّبَ أُمَمٌ مِنْ قَبْلِكُمْ وَمَا عَلَى الرَّسُولِ إِلا الْبَلاغُ الْمُبِينُ}
INSERT INTO [b9617] VALUES (3305,'<span data-type="title" id=toc-3287>١٩ </span>- {أَوَلَمْ يَرَوْا كَيْفَ يُبْدِئُ اللَّهُ الْخَلْقَ ثُمَّ يُعِيدُهُ إِنَّ ذَلِكَ عَلَى اللَّهِ يَسِيرٌ}
INSERT INTO [b9617] VALUES (3306,'<span data-type="title" id=toc-3288>٢٠ </span>- {فَانْظُرُوا كَيْفَ بَدَأَ الْخَلْقَ ثُمَّ اللَّهُ يُنْشِئُ النَّشْأَةَ الآخِرَةَ}
INSERT INTO [b9617] VALUES (3307,'<span data-type="title" id=toc-3289>٢١ </span>- {يُعَذِّبُ مَنْ يَشَاءُ وَيَرْحَمُ مَنْ يَشَاءُ وَإِلَيْهِ تُقْلَبُونَ}
INSERT INTO [b9617] VALUES (3308,'<span data-type="title" id=toc-3290>٢٢ </span>- {وَمَا أَنْتُمْ بِمُعْجِزِينَ فِي الأَرْضِ وَلا فِي السَّمَاءِ وَمَا لَكُمْ مِنْ دُونِ اللَّهِ مِنْ وَلِيٍّ وَلا نَصِيرٍ}
INSERT INTO [b9617] VALUES (3309,'<span data-type="title" id=toc-3291>٢٣ </span>- {وَالَّذِينَ كَفَرُوا بِآيَاتِ اللَّهِ وَلِقَائِهِ أُولَئِكَ يَئِسُوا مِنْ رَحْمَتِي وَأُولَئِكَ لَهُمْ عَذَابٌ أَلِيمٌ}
INSERT INTO [b9617] VALUES (3310,'<span data-type="title" id=toc-3292>٢٤ </span>- {فَمَا كَانَ جَوَابَ قَوْمِهِ إِلا أَنْ قَالُوا اقْتُلُوهُ أَوْ حَرِّقُوهُ فَأَنْجَاهُ اللَّهُ مِنَ النَّارِ إِنَّ فِي ذَلِكَ لآيَاتٍ لِقَوْمٍ يُؤْمِنُونَ}
INSERT INTO [b9617] VALUES (3311,'<span data-type="title" id=toc-3293>٢٥ </span>- {وَقَالَ إِنَّمَا اتَّخَذْتُمْ مِنْ دُونِ اللَّهِ أَوْثَانًا مَوَدَّةَ بَيْنِكُمْ فِي الْحَيَاةِ الدُّنْيَا ثُمَّ يَوْمَ الْقِيَامَةِ يَكْفُرُ بَعْضُكُمْ بِبَعْضٍ وَيَلْعَنُ بَعْضُكُمْ بَعْضًا وَمَأْوَاكُمُ النَّارُ وَمَا لَكُمْ مِنْ نَاصِرِينَ}
INSERT INTO [b9617] VALUES (3312,'<span data-type="title" id=toc-3294>٢٦ </span>- {فَآمَنَ لَهُ لُوطٌ وَقَالَ إِنِّي مُهَاجِرٌ إِلَى رَبِّي إِنَّهُ هُوَ الْعَزِيزُ الْحَكِيمُ}
INSERT INTO [b9617] VALUES (3313,'<span data-type="title" id=toc-3295>٢٧ </span>- {وَوَهَبْنَا لَهُ إِسْحَاقَ وَيَعْقُوبَ وَجَعَلْنَا فِي ذُرِّيَّتِهِ النُّبُوَّةَ وَالْكِتَابَ وَآتَيْنَاهُ أَجْرَهُ فِي الدُّنْيَا وَإِنَّهُ فِي الآخِرَةِ لَمِنَ الصَّالِحِينَ}
INSERT INTO [b9617] VALUES (3314,'<span data-type="title" id=toc-3296>٢٨ </span>- {وَلُوطًا إِذْ قَالَ لِقَوْمِهِ إِنَّكُمْ لَتَأْتُونَ الْفَاحِشَةَ مَا سَبَقَكُمْ بِهَا مِنْ أَحَدٍ مِنَ الْعَالَمِينَ}
INSERT INTO [b9617] VALUES (3315,'<span data-type="title" id=toc-3297>٢٩ </span>- {أَئِنَّكُمْ لَتَأْتُونَ الرِّجَالَ وَتَقْطَعُونَ السَّبِيلَ وَتَأْتُونَ فِي نَادِيكُمُ الْمُنْكَرَ ⦗٩١٧⦘ فَمَا كَانَ جَوَابَ قَوْمِهِ إِلا أَنْ قَالُوا ائْتِنَا بِعَذَابِ اللَّهِ إِنْ كُنْتَ مِنَ الصَّادِقِينَ}
INSERT INTO [b9617] VALUES (3316,'<span data-type="title" id=toc-3298>٣١ </span>- {وَلَمَّا جَاءَتْ رُسُلُنَا إِبْرَاهِيمَ بِالْبُشْرَى قَالُوا إِنَّا مُهْلِكُو أَهْلِ هَذِهِ الْقَرْيَةِ إِنَّ أَهْلَهَا كَانُوا ظَالِمِينَ}
INSERT INTO [b9617] VALUES (3317,'<span data-type="title" id=toc-3299>٣٢ </span>- {قَالَ إِنَّ فِيهَا لُوطًا قَالُوا نَحْنُ أَعْلَمُ بِمَنْ فِيهَا لَنُنَجِّيَنَّهُ وَأَهْلَهُ إِلا امْرَأَتَهُ كَانَتْ مِنَ الْغَابِرِينَ}
INSERT INTO [b9617] VALUES (3318,'<span data-type="title" id=toc-3300>٣٣ </span>- {وَلَمَّا أَنْ جَاءَتْ رُسُلُنَا لُوطًا سِيءَ بِهِمْ وَضَاقَ بِهِمْ ذَرْعًا وَقَالُوا لا تَخَفْ وَلا تَحْزَنْ إِنَّا مُنَجُّوكَ وَأَهْلَكَ إِلا امْرَأَتَكَ كَانَتْ مِنَ الْغَابِرِينَ}
INSERT INTO [b9617] VALUES (3319,'<span data-type="title" id=toc-3301>٣٤ </span>- {إِنَّا مُنْزِلُونَ عَلَى أَهْلِ هَذِهِ الْقَرْيَةِ رِجْزًا مِنَ السَّمَاءِ بِمَا كَانُوا يَفْسُقُونَ}
INSERT INTO [b9617] VALUES (3320,'<span data-type="title" id=toc-3302>٣٥ </span>- {وَلَقَدْ تَرَكْنَا مِنْهَا آيَةً بَيِّنَةً لِقَوْمٍ يَعْقِلُونَ}
INSERT INTO [b9617] VALUES (3321,'<span data-type="title" id=toc-3303>٣٦ </span>- {وَإِلَى مَدْيَنَ أَخَاهُمْ شُعَيْبًا فَقَالَ يَا قَوْمِ اعْبُدُوا اللَّهَ وَارْجُوا الْيَوْمَ الآخِرَ وَلا تَعْثَوْا فِي الأَرْضِ مُفْسِدِينَ}
INSERT INTO [b9617] VALUES (3322,'<span data-type="title" id=toc-3304>٣٧ </span>- {فَكَذَّبُوهُ فَأَخَذَتْهُمُ الرَّجْفَةُ فَأَصْبَحُوا فِي دَارِهِمْ جَاثِمِينَ}
INSERT INTO [b9617] VALUES (3323,'<span data-type="title" id=toc-3305>٣٨ </span>- {وَعَادًا وَثَمُودَ وَقَدْ تَبَيَّنَ لَكُمْ مِنْ مَسَاكِنِهِمْ وَزَيَّنَ لَهُمُ الشَّيْطَانُ أَعْمَالَهُمْ فَصَدَّهُمْ عَنِ السَّبِيلِ وَكَانُوا مُسْتَبْصِرِينَ}
INSERT INTO [b9617] VALUES (3324,'<span data-type="title" id=toc-3306>٣٩ </span>- {وَقَارُونَ وَفِرْعَوْنَ وَهَامَانَ وَلَقَدْ جَاءَهُمْ مُوسَى بِالْبَيِّنَاتِ فَاسْتَكْبَرُوا فِي الأَرْضِ وَمَا كَانُوا سَابِقِينَ}
INSERT INTO [b9617] VALUES (3325,'<span data-type="title" id=toc-3307>٤٠ </span>- {فَكُلا أَخَذْنَا بِذَنْبِهِ فَمِنْهُمْ مَنْ أَرْسَلْنَا عَلَيْهِ حَاصِبًا وَمِنْهُمْ مَنْ أَخَذَتْهُ الصَّيْحَةُ وَمِنْهُمْ مَنْ خَسَفْنَا بِهِ الأَرْضَ وَمِنْهُمْ مَنْ أَغْرَقْنَا وَمَا كَانَ اللَّهُ لِيَظْلِمَهُمْ وَلَكِنْ كَانُوا أَنْفُسَهُمْ يَظْلِمُونَ}
INSERT INTO [b9617] VALUES (3326,'<span data-type="title" id=toc-3308>٤١ </span>- {مَثَلُ الَّذِينَ اتَّخَذُوا مِنْ دُونِ اللَّهِ أَوْلِيَاءَ كَمَثَلِ الْعَنْكَبُوتِ اتَّخَذَتْ بَيْتًا وَإِنَّ أَوْهَنَ الْبُيُوتِ لَبَيْتُ الْعَنْكَبُوتِ لَوْ كَانُوا يَعْلَمُونَ}
INSERT INTO [b9617] VALUES (3327,'<span data-type="title" id=toc-3309>٤٢ </span>- {إِنَّ اللَّهَ يَعْلَمُ مَا يَدْعُونَ مِنْ دُونِهِ مِنْ شَيْءٍ وَهُوَ الْعَزِيزُ الْحَكِيمُ}
INSERT INTO [b9617] VALUES (3328,'<span data-type="title" id=toc-3310>٤٣ </span>- {وَتِلْكَ الأَمْثَالُ نَضْرِبُهَا لِلنَّاسِ وَمَا يَعْقِلُهَا إِلا الْعَالِمُونَ}
INSERT INTO [b9617] VALUES (3329,'<span data-type="title" id=toc-3311>٤٤ </span>- {خَلَقَ اللَّهُ السَّمَاوَاتِ وَالأَرْضَ بِالْحَقِّ إِنَّ فِي ذَلِكَ لآيَةً لِلْمُؤْمِنِينَ}
INSERT INTO [b9617] VALUES (3330,'<span data-type="title" id=toc-3312>٤٥ </span>- {اتْلُ مَا أُوحِيَ إِلَيْكَ مِنَ الْكِتَابِ وَأَقِمِ الصَّلاةَ إِنَّ الصَّلاةَ تَنْهَى عَنِ الْفَحْشَاءِ وَالْمُنْكَرِ وَلَذِكْرُ اللَّهِ أَكْبَرُ وَاللَّهُ يَعْلَمُ مَا تَصْنَعُونَ}
INSERT INTO [b9617] VALUES (3331,'<span data-type="title" id=toc-3313>٤٦ </span>- {وَلا تُجَادِلُوا أَهْلَ الْكِتَابِ إِلا بِالَّتِي هِيَ أَحْسَنُ إِلا الَّذِينَ ظَلَمُوا مِنْهُمْ وَقُولُوا آمَنَّا بِالَّذِي أُنْزِلَ إِلَيْنَا وَأُنْزِلَ إِلَيْكُمْ وَإِلَهُنَا وَإِلَهُكُمْ وَاحِدٌ وَنَحْنُ لَهُ مُسْلِمُونَ}
INSERT INTO [b9617] VALUES (3332,'<span data-type="title" id=toc-3314>٤٧ </span>- {وَكَذَلِكَ أَنْزَلْنَا إِلَيْكَ الْكِتَابَ فَالَّذِينَ آتَيْنَاهُمُ الْكِتَابَ يُؤْمِنُونَ بِهِ وَمِنْ هَؤُلاءِ مَنْ يُؤْمِنُ بِهِ وَمَا يَجْحَدُ بِآيَاتِنَا إِلا الْكَافِرُونَ}
INSERT INTO [b9617] VALUES (3333,'<span data-type="title" id=toc-3315>٤٨ </span>- {وَمَا كُنْتَ تَتْلُو مِنْ قَبْلِهِ مِنْ كِتَابٍ وَلا تَخُطُّهُ بِيَمِينِكَ إِذًا لارْتَابَ ⦗٩٢٢⦘ الْمُبْطِلُونَ}
INSERT INTO [b9617] VALUES (3334,'<span data-type="title" id=toc-3316>٤٩ </span>- {بَلْ هُوَ آيَاتٌ بَيِّنَاتٌ فِي صُدُورِ الَّذِينَ أُوتُوا الْعِلْمَ وَمَا يَجْحَدُ بِآيَاتِنَا إِلا الظَّالِمُونَ}
INSERT INTO [b9617] VALUES (3335,'<span data-type="title" id=toc-3317>٥٠ </span>- {وَقَالُوا لَوْلا أُنْزِلَ عَلَيْهِ آيَاتٌ مِنْ رَبِّهِ}
INSERT INTO [b9617] VALUES (3336,'<span data-type="title" id=toc-3318>٥١ </span>- {أَوَلَمْ يَكْفِهِمْ أَنَّا أَنْزَلْنَا عَلَيْكَ الْكِتَابَ يُتْلَى عَلَيْهِمْ إِنَّ فِي ذَلِكَ لَرَحْمَةً وَذِكْرَى لِقَوْمٍ يُؤْمِنُونَ}
INSERT INTO [b9617] VALUES (3337,'<span data-type="title" id=toc-3319>٥٢ </span>- {قُلْ كَفَى بِاللَّهِ بَيْنِي وَبَيْنَكُمْ شَهِيدًا يَعْلَمُ مَا فِي السَّمَاوَاتِ وَالأَرْضِ وَالَّذِينَ آمَنُوا بِالْبَاطِلِ وَكَفَرُوا بِاللَّهِ أُولَئِكَ هُمُ الْخَاسِرُونَ}
INSERT INTO [b9617] VALUES (3338,'<span data-type="title" id=toc-3320>٥٣ </span>- {وَيَسْتَعْجِلُونَكَ بِالْعَذَابِ وَلَوْلا أَجَلٌ مُسَمًّى لَجَاءَهُمُ الْعَذَابُ وَلَيَأْتِيَنَّهُمْ بَغْتَةً وَهُمْ لا يَشْعُرُونَ}
INSERT INTO [b9617] VALUES (3339,'<span data-type="title" id=toc-3321>٥٤ </span>- {يَسْتَعْجِلُونَكَ بِالْعَذَابِ وَإِنَّ جَهَنَّمَ لَمُحِيطَةٌ بِالْكَافِرِينَ}
INSERT INTO [b9617] VALUES (3340,'<span data-type="title" id=toc-3322>٥٥ </span>- {يَوْمَ يَغْشَاهُمُ الْعَذَابُ مِنْ فَوْقِهِمْ وَمِنْ تَحْتِ أَرْجُلِهِمْ وَيَقُولُ ذُوقُوا مَا كُنْتُمْ تَعْمَلُونَ}
INSERT INTO [b9617] VALUES (3341,'<span data-type="title" id=toc-3323>٥٦ </span>- {يَا عِبَادِيَ الَّذِينَ آمَنُوا إِنَّ أَرْضِي وَاسِعَةٌ فَإِيَّايَ فَاعْبُدُونِ}
INSERT INTO [b9617] VALUES (3342,'<span data-type="title" id=toc-3324>٥٧ </span>- {كُلُّ نَفْسٍ ذَائِقَةُ الْمَوْتِ ثُمَّ إِلَيْنَا تُرْجَعُونَ}
INSERT INTO [b9617] VALUES (3343,'<span data-type="title" id=toc-3325>٥٨ </span>- {وَالَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ لَنُبَوِّئَنَّهُمْ مِنَ الْجَنَّةِ غُرَفًا تَجْرِي مِنْ تَحْتِهَا الأَنْهَارُ خَالِدِينَ فِيهَا نِعْمَ أَجْرُ الْعَامِلِينَ}
INSERT INTO [b9617] VALUES (3344,'<span data-type="title" id=toc-3326>٥٩ </span>- {الَّذِينَ صَبَرُوا وَعَلَى رَبِّهِمْ يَتَوَكَّلُونَ}
INSERT INTO [b9617] VALUES (3345,'<span data-type="title" id=toc-3327>٦٠ </span>- {وَكَأَيِّنْ مِنْ دَابَّةٍ لا تَحْمِلُ رِزْقَهَا اللَّهُ يَرْزُقُهَا وَإِيَّاكُمْ وَهُوَ السَّمِيعُ الْعَلِيمُ}
INSERT INTO [b9617] VALUES (3346,'<span data-type="title" id=toc-3328>٦١ </span>- {وَلَئِنْ سَأَلْتَهُمْ مَنْ خَلَقَ السَّمَاوَاتِ وَالأَرْضَ وَسَخَّرَ الشَّمْسَ وَالْقَمَرَ لَيَقُولُنَّ اللَّهُ فَأَنَّى يُؤْفَكُونَ}
INSERT INTO [b9617] VALUES (3347,'<span data-type="title" id=toc-3329>٦٢ </span>- {اللَّهُ يَبْسُطُ الرِّزْقَ لِمَنْ يَشَاءُ مِنْ عِبَادِهِ}
INSERT INTO [b9617] VALUES (3348,'<span data-type="title" id=toc-3330>٦٣ </span>- {وَلَئِنْ سَأَلْتَهُمْ مَنْ نَزَّلَ مِنَ السَّمَاءِ مَاءً فَأَحْيَا بِهِ الأَرْضَ مِنْ بَعْدِ مَوْتِهَا لَيَقُولُنَّ اللَّهُ قُلِ الْحَمْدُ لِلَّهِ بَلْ أَكْثَرُهُمْ لا يَعْقِلُونَ}
INSERT INTO [b9617] VALUES (3349,'<span data-type="title" id=toc-3331>٦٤ </span>- {وَمَا هَذِهِ الْحَيَاةُ الدُّنْيَا إِلا لَهْوٌ وَلَعِبٌ وَإِنَّ الدَّارَ الآخِرَةَ لَهِيَ الْحَيَوَانُ لَوْ كَانُوا يَعْلَمُونَ}
INSERT INTO [b9617] VALUES (3350,'<span data-type="title" id=toc-3332>٦٥ </span>- {فَإِذَا رَكِبُوا فِي الْفُلْكِ دَعَوُا اللَّهَ مُخْلِصِينَ لَهُ الدِّينَ فَلَمَّا نَجَّاهُمْ إِلَى الْبَرِّ إِذَا هُمْ يُشْرِكُونَ}
INSERT INTO [b9617] VALUES (3351,'<span data-type="title" id=toc-3333>٦٦ </span>- {لِيَكْفُرُوا بِمَا آتَيْنَاهُمْ وَلِيَتَمَتَّعُوا فَسَوْفَ يَعْلَمُونَ}
INSERT INTO [b9617] VALUES (3352,'<span data-type="title" id=toc-3334>٦٧ </span>- {أَوَلَمْ يَرَوْا أَنَّا جَعَلْنَا حَرَمًا آمِنًا وَيُتَخَطَّفُ النَّاسُ مِنْ حَوْلِهِمْ أَفَبِالْبَاطِلِ يُؤْمِنُونَ وَبِنِعْمَةِ اللَّهِ يَكْفُرُونَ}
INSERT INTO [b9617] VALUES (3353,'<span data-type="title" id=toc-3335>٦٨ </span>- {وَمَنْ أَظْلَمُ مِمَّنِ افْتَرَى عَلَى اللَّهِ كَذِبًا أَوْ كَذَّبَ بِالْحَقِّ لَمَّا جَاءَهُ أَلَيْسَ فِي جَهَنَّمَ مَثْوًى لِلْكَافِرِينَ} ⦗٩٢٧⦘
INSERT INTO [b9617] VALUES (3354,'<span data-type="title" id=toc-3336>٦٩ </span>- {وَالَّذِينَ جَاهَدُوا فِينَا لَنَهْدِيَنَّهُمْ سُبُلَنَا وَإِنَّ اللَّهَ لَمَعَ الْمُحْسِنِينَ}
INSERT INTO [b9617] VALUES (3355,'<span data-type="title" id=toc-3337>سورة الروم</span>','3','928',NULL,NULL,'0');
INSERT INTO [b9617] VALUES (3356,'<span data-type="title" id=toc-3338>٣ </span>- {فِي أَدْنَى الأَرْضِ وَهُمْ مِنْ بَعْدِ غَلَبِهِمْ سَيَغْلِبُونَ}
INSERT INTO [b9617] VALUES (3357,'<span data-type="title" id=toc-3339>٤ </span>- {فِي بِضْعِ سِنِينَ لِلَّهِ الأَمْرُ مِنْ قَبْلُ وَمِنْ بَعْدُ وَيَوْمَئِذٍ يَفْرَحُ الْمُؤْمِنُونَ}
INSERT INTO [b9617] VALUES (3358,'<span data-type="title" id=toc-3340>٥ </span>- {بِنَصْرِ اللَّهِ يَنْصُرُ مَنْ يَشَاءُ وَهُوَ الْعَزِيزُ الرَّحِيمُ}
INSERT INTO [b9617] VALUES (3359,'<span data-type="title" id=toc-3341>٦ </span>- {وَعْدَ اللَّهِ لا يُخْلِفُ اللَّهُ وَعْدَهُ وَلَكِنَّ أَكْثَرَ النَّاسِ لا يَعْلَمُونَ}
INSERT INTO [b9617] VALUES (3360,'<span data-type="title" id=toc-3342>٧ </span>- {يَعْلَمُونَ ظَاهِرًا مِنَ الْحَيَاةِ الدُّنْيَا وَهُمْ عَنِ الآخِرَةِ هُمْ غَافِلُونَ}
INSERT INTO [b9617] VALUES (3361,'<span data-type="title" id=toc-3343>٨ </span>- {أَوَلَمْ يَتَفَكَّرُوا فِي أَنْفُسِهِمْ مَا خَلَقَ اللَّهُ السَّمَاوَاتِ وَالأَرْضَ وَمَا بَيْنَهُمَا إِلا بِالْحَقِّ وَأَجَلٍ مُسَمًّى وَإِنَّ كَثِيرًا مِنَ النَّاسِ بِلِقَاءِ رَبِّهِمْ لَكَافِرُونَ}
INSERT INTO [b9617] VALUES (3362,'<span data-type="title" id=toc-3344>٩ </span>- {أَوَلَمْ يَسِيرُوا فِي الأَرْضِ فَيَنْظُرُوا كَيْفَ كَانَ عَاقِبَةُ الَّذِينَ مِنْ قَبْلِهِمْ كَانُوا أَشَدَّ مِنْهُمْ قُوَّةً وَأَثَارُوا الأَرْضَ وَعَمَرُوهَا أَكْثَرَ مِمَّا عَمَرُوهَا وَجَاءَتْهُمْ رُسُلُهُمْ بِالْبَيِّنَاتِ فَمَا كَانَ اللَّهُ لِيَظْلِمَهُمْ وَلَكِنْ كَانُوا أَنْفُسَهُمْ يَظْلِمُونَ}
INSERT INTO [b9617] VALUES (3363,'<span data-type="title" id=toc-3345>١٠ </span>- {ثُمَّ كَانَ عَاقِبَةَ الَّذِينَ أَسَاءُوا السُّوءَى أَنْ كَذَّبُوا بِآيَاتِ اللَّهِ وَكَانُوا بِهَا يَسْتَهْزِئُونَ}
INSERT INTO [b9617] VALUES (3364,'<span data-type="title" id=toc-3346>١١ </span>- {ثُمَّ يُعِيدُهُ ثُمَّ إِلَيْهِ تُرْجَعُونَ}
INSERT INTO [b9617] VALUES (3365,'<span data-type="title" id=toc-3347>١٢ </span>- {وَيَوْمَ تَقُومُ السَّاعَةُ يُبْلِسُ الْمُجْرِمُونَ}
INSERT INTO [b9617] VALUES (3366,'<span data-type="title" id=toc-3348>١٣ </span>- {وَلَمْ يَكُنْ لَهُمْ مِنْ شُرَكَائِهِمْ شُفَعَاءُ وَكَانُوا بِشُرَكَائِهِمْ كَافِرِينَ}
INSERT INTO [b9617] VALUES (3367,'<span data-type="title" id=toc-3349>١٤ </span>- {وَيَوْمَ تَقُومُ السَّاعَةُ يَوْمَئِذٍ يَتَفَرَّقُونَ}
INSERT INTO [b9617] VALUES (3368,'<span data-type="title" id=toc-3350>١٥ </span>- {فَأَمَّا الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ فَهُمْ فِي رَوْضَةٍ يُحْبَرُونَ}
INSERT INTO [b9617] VALUES (3369,'<span data-type="title" id=toc-3351>١٦ </span>- {وَأَمَّا الَّذِينَ كَفَرُوا وَكَذَّبُوا بِآيَاتِنَا وَلِقَاءِ الآخِرَةِ فَأُولَئِكَ فِي الْعَذَابِ مُحْضَرُونَ}
INSERT INTO [b9617] VALUES (3370,'<span data-type="title" id=toc-3352>١٧ </span>- {فَسُبْحَانَ اللَّهِ حِينَ تُمْسُونَ وَحِينَ تُصْبِحُونَ}
INSERT INTO [b9617] VALUES (3371,'<span data-type="title" id=toc-3353>١٨ </span>- {وَلَهُ الْحَمْدُ فِي السَّمَاوَاتِ وَالأَرْضِ وَعَشِيًّا وَحِينَ تُظْهِرُونَ}
INSERT INTO [b9617] VALUES (3372,'<span data-type="title" id=toc-3354>١٩ </span>- {يُخْرِجُ الْحَيَّ مِنَ الْمَيِّتِ وَيُخْرِجُ الْمَيِّتَ مِنَ الْحَيِّ وَيُحْيِي الأَرْضَ بَعْدَ مَوْتِهَا وَكَذَلِكَ تُخْرَجُونَ}
INSERT INTO [b9617] VALUES (3373,'<span data-type="title" id=toc-3355>٢٠ </span>- {وَمِنْ آيَاتِهِ أَنْ خَلَقَكُمْ مِنْ تُرَابٍ ثُمَّ إِذَا أَنْتُمْ بَشَرٌ تَنْتَشِرُونَ}
INSERT INTO [b9617] VALUES (3374,'<span data-type="title" id=toc-3356>٢١ </span>- {وَمِنْ آيَاتِهِ أَنْ خَلَقَ لَكُمْ مِنْ أَنْفُسِكُمْ أَزْوَاجًا لِتَسْكُنُوا إِلَيْهَا وَجَعَلَ بَيْنَكُمْ مَوَدَّةً وَرَحْمَةً إِنَّ فِي ذَلِكَ لآيَاتٍ لِقَوْمٍ يَتَفَكَّرُونَ}
INSERT INTO [b9617] VALUES (3375,'<span data-type="title" id=toc-3357>٢٢ </span>- {وَمِنْ آيَاتِهِ خَلْقُ السَّمَاوَاتِ وَالأَرْضِ وَاخْتِلافُ أَلْسِنَتِكُمْ وَأَلْوَانِكُمْ إِنَّ فِي ذَلِكَ لآيَاتٍ لِلْعَالِمِينَ}
INSERT INTO [b9617] VALUES (3376,'<span data-type="title" id=toc-3358>٢٣ </span>- {وَمِنْ آيَاتِهِ مَنَامُكُمْ بِاللَّيْلِ وَالنَّهَارِ وَابْتِغَاؤُكُمْ مِنْ فَضْلِهِ إِنَّ فِي ذَلِكَ لآيَاتٍ لِقَوْمٍ يَسْمَعُونَ}
INSERT INTO [b9617] VALUES (3377,'<span data-type="title" id=toc-3359>٢٤ </span>- {وَمِنْ آيَاتِهِ يُرِيكُمُ الْبَرْقَ خَوْفًا وَطَمَعًا وَيُنَزِّلُ مِنَ السَّمَاءِ مَاءً فَيُحْيِي بِهِ الأَرْضَ بَعْدَ مَوْتِهَا إِنَّ فِي ذَلِكَ لآيَاتٍ لِقَوْمٍ يَعْقِلُونَ}
INSERT INTO [b9617] VALUES (3378,'<span data-type="title" id=toc-3360>٢٥ </span>- {وَمِنْ آيَاتِهِ أَنْ تَقُومَ السَّمَاءُ وَالأَرْضُ بِأَمْرِهِ ثُمَّ إِذَا دَعَاكُمْ دَعْوَةً مِنَ الأَرْضِ إِذَا أَنْتُمْ تَخْرُجُونَ}
INSERT INTO [b9617] VALUES (3379,'<span data-type="title" id=toc-3361>٢٦ </span>- {وَلَهُ مَنْ فِي السَّمَاوَاتِ وَالأَرْضِ كُلٌّ لَهُ قَانِتُونَ}
INSERT INTO [b9617] VALUES (3380,'<span data-type="title" id=toc-3362>٢٧ </span>- {وَهُوَ الَّذِي يَبْدَأُ الْخَلْقَ ثُمَّ يُعِيدُهُ وَهُوَ أَهْوَنُ عَلَيْهِ وَلَهُ الْمَثَلُ الأَعْلَى فِي السَّمَاوَاتِ وَالأَرْضِ}
INSERT INTO [b9617] VALUES (3381,'<span data-type="title" id=toc-3363>٢٨ </span>- {ضَرَبَ لَكُمْ مَثَلا مِنْ أَنْفُسِكُمْ هَلْ لَكُمْ مِنْ مَا مَلَكَتْ أَيْمَانُكُمْ مِنْ شُرَكَاءَ فِي مَا رَزَقْنَاكُمْ فَأَنْتُمْ فِيهِ سَوَاءٌ تَخَافُونَهُمْ كَخِيفَتِكُمْ أَنْفُسَكُمْ كَذَلِكَ نُفَصِّلُ الآيَاتِ}
INSERT INTO [b9617] VALUES (3382,'<span data-type="title" id=toc-3364>٢٩ </span>- {بَلِ اتَّبَعَ الَّذِينَ ظَلَمُوا أَهْوَاءَهُمْ بِغَيْرِ عِلْمٍ فَمَنْ يَهْدِي مَنْ أَضَلَّ اللَّهُ وَمَا لَهُمْ ⦗٩٣٥⦘ مِنْ نَاصِرِينَ}
INSERT INTO [b9617] VALUES (3383,'<span data-type="title" id=toc-3365>٣٠ </span>- {فَأَقِمْ وَجْهَكَ لِلدِّينِ حَنِيفًا فِطْرَتَ اللَّهِ الَّتِي فَطَرَ النَّاسَ عَلَيْهَا لا تَبْدِيلَ لِخَلْقِ اللَّهِ ذَلِكَ الدِّينُ الْقَيِّمُ وَلَكِنَّ أَكْثَرَ النَّاسِ لا يَعْلَمُونَ}
INSERT INTO [b9617] VALUES (3384,'<span data-type="title" id=toc-3366>٣١ </span>- {مُنِيبِينَ إِلَيْهِ وَاتَّقُوهُ وَأَقِيمُوا الصَّلاةَ}
INSERT INTO [b9617] VALUES (3385,'<span data-type="title" id=toc-3367>٣٢ </span>- {مِنَ الَّذِينَ فَرَّقُوا دِينَهُمْ وَكَانُوا شِيَعًا كُلُّ حِزْبٍ بِمَا لَدَيْهِمْ فَرِحُونَ}
INSERT INTO [b9617] VALUES (3386,'<span data-type="title" id=toc-3368>٣٣ </span>- {وَإِذَا مَسَّ النَّاسَ ضُرٌّ دَعَوْا رَبَّهُمْ مُنِيبِينَ إِلَيْهِ ثُمَّ إِذَا أَذَاقَهُمْ مِنْهُ رَحْمَةً إِذَا فَرِيقٌ مِنْهُمْ بِرَبِّهِمْ يُشْرِكُونَ}
INSERT INTO [b9617] VALUES (3387,'<span data-type="title" id=toc-3369>٣٤ </span>- {لِيَكْفُرُوا بِمَا آتَيْنَاهُمْ فَتَمَتَّعُوا فَسَوْفَ تَعْلَمُونَ}
INSERT INTO [b9617] VALUES (3388,'<span data-type="title" id=toc-3370>٣٥ </span>- {أَمْ أَنْزَلْنَا عَلَيْهِمْ سُلْطَانًا فَهُوَ يَتَكَلَّمُ بِمَا كَانُوا بِهِ يُشْرِكُونَ}
INSERT INTO [b9617] VALUES (3389,'<span data-type="title" id=toc-3371>٣٦ </span>- {وَإِذَا أَذَقْنَا النَّاسَ رَحْمَةً فَرِحُوا بِهَا وَإِنْ تُصِبْهُمْ سَيِّئَةٌ بِمَا قَدَّمَتْ أَيْدِيهِمْ إِذَا هُمْ يَقْنَطُونَ} ⦗٩٣٧⦘
INSERT INTO [b9617] VALUES (3390,'<span data-type="title" id=toc-3372>٣٧ </span>- {أَوَلَمْ يَرَوْا أَنَّ اللَّهَ يَبْسُطُ الرِّزْقَ لِمَنْ يَشَاءُ وَيَقْدِرُ إِنَّ فِي ذَلِكَ لآيَاتٍ لِقَوْمٍ يُؤْمِنُونَ}
INSERT INTO [b9617] VALUES (3391,'<span data-type="title" id=toc-3373>٣٨ </span>- {فَآتِ ذَا الْقُرْبَى حَقَّهُ وَالْمِسْكِينَ وَابْنَ السَّبِيلِ ذَلِكَ خَيْرٌ لِلَّذِينَ يُرِيدُونَ وَجْهَ اللَّهِ وَأُولَئِكَ هُمُ الْمُفْلِحُونَ}
INSERT INTO [b9617] VALUES (3392,'<span data-type="title" id=toc-3374>٣٩ </span>- {وَمَا آتَيْتُمْ مِنْ رِبًا لِيَرْبُوَ فِي أَمْوَالِ النَّاسِ فَلا يَرْبُو عِنْدَ اللَّهِ وَمَا آتَيْتُمْ مِنْ زَكَاةٍ تُرِيدُونَ وَجْهَ اللَّهِ فَأُولَئِكَ هُمُ الْمُضْعِفُونَ}
INSERT INTO [b9617] VALUES (3393,'<span data-type="title" id=toc-3375>٤٠ </span>- {هَلْ مِنْ شُرَكَائِكُمْ مَنْ يَفْعَلُ مِنْ ذَلِكُمْ مِنْ شَيْءٍ سُبْحَانَهُ وَتَعَالَى عَمَّا يُشْرِكُونَ}
INSERT INTO [b9617] VALUES (3394,'<span data-type="title" id=toc-3376>٤١ </span>- {ظَهَرَ الْفَسَادُ فِي الْبَرِّ وَالْبَحْرِ بِمَا كَسَبَتْ أَيْدِي النَّاسِ لِيُذِيقَهُمْ بَعْضَ الَّذِي عَمِلُوا لَعَلَّهُمْ يَرْجِعُونَ}
INSERT INTO [b9617] VALUES (3395,'<span data-type="title" id=toc-3377>٤٢ </span>- {فَانْظُرُوا كَيْفَ كَانَ عَاقِبَةُ الَّذِينَ مِنْ قَبْلُ كَانَ أَكْثَرُهُمْ مُشْرِكِينَ}
INSERT INTO [b9617] VALUES (3396,'<span data-type="title" id=toc-3378>٤٣ </span>- {فَأَقِمْ وَجْهَكَ لِلدِّينِ الْقَيِّمِ مِنْ قَبْلِ أَنْ يَأْتِيَ يَوْمٌ لا مَرَدَّ لَهُ مِنَ اللَّهِ يَوْمَئِذٍ يَصَّدَّعُونَ} ⦗٩٣٩⦘
INSERT INTO [b9617] VALUES (3397,'<span data-type="title" id=toc-3379>٤٤ </span>- {مَنْ كَفَرَ فَعَلَيْهِ كُفْرُهُ وَمَنْ عَمِلَ صَالِحًا فَلأَنْفُسِهِمْ يَمْهَدُونَ}
INSERT INTO [b9617] VALUES (3398,'<span data-type="title" id=toc-3380>٤٥ </span>- {لِيَجْزِيَ الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ مِنْ فَضْلِهِ إِنَّهُ لا يُحِبُّ الْكَافِرِينَ}
INSERT INTO [b9617] VALUES (3399,'<span data-type="title" id=toc-3381>٤٦ </span>- {وَمِنْ آيَاتِهِ أَنْ يُرْسِلَ الرِّيَاحَ مُبَشِّرَاتٍ وَلِيُذِيقَكُمْ مِنْ رَحْمَتِهِ وَلِتَجْرِيَ الْفُلْكُ بِأَمْرِهِ وَلِتَبْتَغُوا مِنْ فَضْلِهِ وَلَعَلَّكُمْ تَشْكُرُونَ}
INSERT INTO [b9617] VALUES (3400,'<span data-type="title" id=toc-3382>٤٧ </span>- {وَلَقَدْ أَرْسَلْنَا مِنْ قَبْلِكَ رُسُلا إِلَى قَوْمِهِمْ فَجَاءُوهُمْ بِالْبَيِّنَاتِ فَانْتَقَمْنَا مِنَ الَّذِينَ أَجْرَمُوا وَكَانَ حَقًّا عَلَيْنَا نَصْرُ الْمُؤْمِنِينَ}
INSERT INTO [b9617] VALUES (3401,'<span data-type="title" id=toc-3383>٤٨ </span>- {اللَّهُ الَّذِي يُرْسِلُ الرِّيَاحَ فَتُثِيرُ سَحَابًا فَيَبْسُطُهُ فِي السَّمَاءِ كَيْفَ يَشَاءُ وَيَجْعَلُهُ كِسَفًا فَتَرَى الْوَدْقَ يَخْرُجُ مِنْ خِلالِهِ فَإِذَا أَصَابَ بِهِ مَنْ يَشَاءُ مِنْ عِبَادِهِ إِذَا هُمْ يَسْتَبْشِرُونَ}
INSERT INTO [b9617] VALUES (3402,'<span data-type="title" id=toc-3384>٤٩ </span>- {وَإِنْ كَانُوا مِنْ قَبْلِ أَنْ يُنَزَّلَ عَلَيْهِمْ مِنْ قَبْلِهِ لَمُبْلِسِينَ}
INSERT INTO [b9617] VALUES (3403,'<span data-type="title" id=toc-3385>٥٠ </span>- {فَانْظُرْ إِلَى آثَارِ رَحْمَتِ اللَّهِ كَيْفَ يُحْيِي الأَرْضَ بَعْدَ مَوْتِهَا إِنَّ ذَلِكَ لَمُحْيِي الْمَوْتَى وَهُوَ عَلَى كُلِّ شَيْءٍ قَدِيرٌ}
INSERT INTO [b9617] VALUES (3404,'<span data-type="title" id=toc-3386>٥١ </span>- {وَلَئِنْ أَرْسَلْنَا رِيحًا فَرَأَوْهُ مُصْفَرًّا لَظَلُّوا مِنْ بَعْدِهِ يَكْفُرُونَ}
INSERT INTO [b9617] VALUES (3405,'<span data-type="title" id=toc-3387>٥٢ </span>- {فَإِنَّكَ لا تُسْمِعُ الْمَوْتَى وَلا تُسْمِعُ الصُّمَّ الدُّعَاءَ إِذَا وَلَّوْا مُدْبِرِينَ}
INSERT INTO [b9617] VALUES (3406,'<span data-type="title" id=toc-3388>٥٣ </span>- {وَمَا أَنْتَ بِهَادِ الْعُمْيِ عَنْ ضَلالَتِهِمْ إِنْ تُسْمِعُ إِلا مَنْ يُؤْمِنُ بِآيَاتِنَا فَهُمْ مُسْلِمُونَ}
INSERT INTO [b9617] VALUES (3407,'<span data-type="title" id=toc-3389>٥٤ </span>- {ثُمَّ جَعَلَ مِنْ بَعْدِ ضَعْفٍ قُوَّةً ثُمَّ جَعَلَ مِنْ بَعْدِ قُوَّةٍ ضَعْفًا وَشَيْبَةً يَخْلُقُ مَا يَشَاءُ وَهُوَ الْعَلِيمُ الْقَدِيرُ}
INSERT INTO [b9617] VALUES (3408,'<span data-type="title" id=toc-3390>٥٥ </span>- {وَيَوْمَ تَقُومُ السَّاعَةُ يُقْسِمُ الْمُجْرِمُونَ مَا لَبِثُوا غَيْرَ سَاعَةٍ كَذَلِكَ كَانُوا يُؤْفَكُونَ}
INSERT INTO [b9617] VALUES (3409,'<span data-type="title" id=toc-3391>٥٦ </span>- {وَقَالَ الَّذِينَ أُوتُوا الْعِلْمَ وَالإِيمَانَ لَقَدْ لَبِثْتُمْ فِي كِتَابِ اللَّهِ إِلَى يَوْمِ الْبَعْثِ فَهَذَا يَوْمُ الْبَعْثِ وَلَكِنَّكُمْ كُنْتُمْ لا تَعْلَمُونَ}
INSERT INTO [b9617] VALUES (3410,'<span data-type="title" id=toc-3392>٥٧ </span>- {فَيَوْمَئِذٍ لا يَنْفَعُ الَّذِينَ ظَلَمُوا مَعْذِرَتُهُمْ وَلا هُمْ يُسْتَعْتَبُونَ}
INSERT INTO [b9617] VALUES (3411,'<span data-type="title" id=toc-3393>٥٨ </span>- {وَلَقَدْ ضَرَبْنَا لِلنَّاسِ فِي هَذَا الْقُرْآنِ مِنْ كُلِّ مَثَلٍ وَلَئِنْ جِئْتَهُمْ بِآيَةٍ لَيَقُولَنَّ ⦗٩٤٣⦘ الَّذِينَ كَفَرُوا إِنْ أَنْتُمْ إِلا مُبْطِلُونَ}
INSERT INTO [b9617] VALUES (3412,'<span data-type="title" id=toc-3394>٥٩ </span>- {كَذَلِكَ يَطْبَعُ اللَّهُ عَلَى قُلُوبِ الَّذِينَ لا يَعْلَمُونَ}
INSERT INTO [b9617] VALUES (3413,'<span data-type="title" id=toc-3395>٦٠ </span>- {فَاصْبِرْ إِنَّ وَعْدَ اللَّهِ حَقٌّ وَلا يَسْتَخِفَّنَّكَ الَّذِينَ لا يُوقِنُونَ}
INSERT INTO [b9617] VALUES (3414,'<span data-type="title" id=toc-3396>سورة لقمان</span>','3','944',NULL,NULL,'0');
INSERT INTO [b9617] VALUES (3415,'<span data-type="title" id=toc-3397>٣ </span>- {هُدًى وَرَحْمَةً لِلْمُحْسِنِينَ}
INSERT INTO [b9617] VALUES (3416,'<span data-type="title" id=toc-3398>٤ </span>- {الَّذِينَ يُقِيمُونَ الصَّلاةَ وَيُؤْتُونَ الزَّكَاةَ وَهُمْ بِالآخِرَةِ هُمْ يُوقِنُونَ}
INSERT INTO [b9617] VALUES (3417,'<span data-type="title" id=toc-3399>٥ </span>- {أُولَئِكَ عَلَى هُدًى مِنْ رَبِّهِمْ وَأُولَئِكَ هُمُ الْمُفْلِحُونَ}
INSERT INTO [b9617] VALUES (3418,'<span data-type="title" id=toc-3400>٦ </span>- {وَمِنَ النَّاسِ مَنْ يَشْتَرِي لَهْوَ الْحَدِيثِ لِيُضِلَّ عَنْ سَبِيلِ اللَّهِ بِغَيْرِ عِلْمٍ وَيَتَّخِذَهَا هُزُوًا أُولَئِكَ لَهُمْ عَذَابٌ مُهِينٌ}
INSERT INTO [b9617] VALUES (3419,'<span data-type="title" id=toc-3401>٧ </span>- {وَإِذَا تُتْلَى عَلَيْهِ آيَاتُنَا وَلَّى مُسْتَكْبِرًا كَأَنْ لَمْ يَسْمَعْهَا كَأَنَّ فِي أُذُنَيْهِ وَقْرًا فَبَشِّرْهُ بِعَذَابٍ أَلِيمٍ} ⦗٩٤٥⦘
INSERT INTO [b9617] VALUES (3420,'<span data-type="title" id=toc-3402>٨ </span>- {إِنَّ الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ لَهُمْ جَنَّاتُ النَّعِيمِ}
INSERT INTO [b9617] VALUES (3421,'<span data-type="title" id=toc-3403>٩ </span>- {خَالِدِينَ فِيهَا وَعْدَ اللَّهِ حَقًّا وَهُوَ الْعَزِيزُ الْحَكِيمُ}
INSERT INTO [b9617] VALUES (3422,'<span data-type="title" id=toc-3404>١٠ </span>- {خَلَقَ السَّمَاوَاتِ بِغَيْرِ عَمَدٍ تَرَوْنَهَا وَأَلْقَى فِي الأَرْضِ رَوَاسِيَ أَنْ تَمِيدَ بِكُمْ وَبَثَّ فِيهَا مِنْ كُلِّ دَابَّةٍ وَأَنْزَلْنَا مِنَ السَّمَاءِ مَاءً فَأَنْبَتْنَا فِيهَا مِنْ كُلِّ زَوْجٍ كَرِيمٍ}
INSERT INTO [b9617] VALUES (3423,'<span data-type="title" id=toc-3405>١١ </span>- {هَذَا خَلْقُ اللَّهِ فَأَرُونِي مَاذَا خَلَقَ الَّذِينَ مِنْ دُونِهِ بَلِ الظَّالِمُونَ فِي ضَلالٍ مُبِينٍ}
INSERT INTO [b9617] VALUES (3424,'<span data-type="title" id=toc-3406>١٢ </span>- {وَلَقَدْ آتَيْنَا لُقْمَانَ الْحِكْمَةَ أَنِ اشْكُرْ لِلَّهِ وَمَنْ يَشْكُرْ فَإِنَّمَا يَشْكُرُ لِنَفْسِهِ وَمَنْ كَفَرَ فَإِنَّ اللَّهَ غَنِيٌّ حَمِيدٌ}
INSERT INTO [b9617] VALUES (3425,'<span data-type="title" id=toc-3407>١٣ </span>- {وَإِذْ قَالَ لُقْمَانُ لابْنِهِ وَهُوَ يَعِظُهُ يَا بُنَيَّ لا تُشْرِكْ بِاللَّهِ}
INSERT INTO [b9617] VALUES (3426,'<span data-type="title" id=toc-3408>١٤ </span>- {وَوَصَّيْنَا الإِنْسَانَ بِوَالِدَيْهِ حَمَلَتْهُ أُمُّهُ وَهْنًا عَلَى وَهْنٍ وَفِصَالُهُ فِي عَامَيْنِ أَنِ اشْكُرْ لِي وَلِوَالِدَيْكَ إِلَيَّ الْمَصِيرُ}
INSERT INTO [b9617] VALUES (3427,'<span data-type="title" id=toc-3409>١٥ </span>- {وَإِنْ جَاهَدَاكَ عَلَى أَنْ تُشْرِكَ بِي مَا لَيْسَ لَكَ بِهِ عِلْمٌ فَلا تُطِعْهُمَا وَصَاحِبْهُمَا فِي الدُّنْيَا مَعْرُوفًا وَاتَّبِعْ سَبِيلَ مَنْ أَنَابَ إِلَيَّ ثُمَّ إِلَيَّ مَرْجِعُكُمْ فَأُنَبِّئُكُمْ بِمَا كُنْتُمْ تَعْمَلُونَ}
INSERT INTO [b9617] VALUES (3428,'<span data-type="title" id=toc-3410>١٦ </span>- {يَا بُنَيَّ إِنَّهَا إِنْ تَكُ مِثْقَالَ حَبَّةٍ مِنْ خَرْدَلٍ فَتَكُنْ فِي صَخْرَةٍ أَوْ فِي السَّمَاوَاتِ أَوْ فِي الأَرْضِ يَأْتِ بِهَا اللَّهُ}
INSERT INTO [b9617] VALUES (3429,'<span data-type="title" id=toc-3411>١٧ </span>- {يَا بُنَيَّ أَقِمِ الصَّلاةَ وَأْمُرْ بِالْمَعْرُوفِ}
INSERT INTO [b9617] VALUES (3430,'<span data-type="title" id=toc-3412>١٨ </span>- {وَلا تُصَعِّرْ خَدَّكَ لِلنَّاسِ وَلا تَمْشِ فِي الأَرْضِ مَرَحًا}
INSERT INTO [b9617] VALUES (3431,'<span data-type="title" id=toc-3413>١٩ </span>- {وَاقْصِدْ فِي مَشْيِكَ وَاغْضُضْ مِنْ صَوْتِكَ إِنَّ أَنْكَرَ الأَصْوَاتِ لَصَوْتُ الْحَمِيرِ}
INSERT INTO [b9617] VALUES (3432,'<span data-type="title" id=toc-3414>٢٠ </span>- {أَلَمْ تَرَوْا أَنَّ اللَّهَ سَخَّرَ لَكُمْ مَا فِي السَّمَاوَاتِ وَمَا فِي الأَرْضِ وَأَسْبَغَ عَلَيْكُمْ نِعَمَهُ ظَاهِرَةً وَبَاطِنَةً وَمِنَ النَّاسِ مَنْ يُجَادِلُ فِي اللَّهِ بِغَيْرِ عِلْمٍ}
INSERT INTO [b9617] VALUES (3433,'<span data-type="title" id=toc-3415>٢١ </span>- {وَإِذَا قِيلَ لَهُمُ اتَّبِعُوا مَا أَنْزَلَ اللَّهُ قَالُوا بَلْ نَتَّبِعُ مَا وَجَدْنَا عَلَيْهِ آبَاءَنَا أَوَلَوْ كَانَ الشَّيْطَانُ يَدْعُوهُمْ إِلَى عَذَابِ السَّعِيرِ}
INSERT INTO [b9617] VALUES (3434,'<span data-type="title" id=toc-3416>٢٢ </span>- {وَمَنْ يُسْلِمْ وَجْهَهُ إِلَى اللَّهِ وَهُوَ مُحْسِنٌ فَقَدِ اسْتَمْسَكَ بِالْعُرْوَةِ الْوُثْقَى وَإِلَى اللَّهِ عَاقِبَةُ الأُمُورِ}
INSERT INTO [b9617] VALUES (3435,'<span data-type="title" id=toc-3417>٢٣ </span>- {إِلَيْنَا مَرْجِعُهُمْ فَنُنَبِّئُهُمْ بِمَا عَمِلُوا}
INSERT INTO [b9617] VALUES (3436,'<span data-type="title" id=toc-3418>٢٤ </span>- {نُمَتِّعُهُمْ قَلِيلا}
INSERT INTO [b9617] VALUES (3437,'<span data-type="title" id=toc-3419>٢٥ </span>- {وَلَئِنْ سَأَلْتَهُمْ مَنْ خَلَقَ السَّمَاوَاتِ وَالأَرْضَ لَيَقُولُنَّ اللَّهُ} ⦗٩٥٠⦘
INSERT INTO [b9617] VALUES (3438,'<span data-type="title" id=toc-3420>٢٦ </span>- {لِلَّهِ مَا فِي السَّمَاوَاتِ وَالأَرْضِ}
INSERT INTO [b9617] VALUES (3439,'<span data-type="title" id=toc-3421>٢٧ </span>- {وَلَوْ أَنَّمَا فِي الأَرْضِ مِنْ شَجَرَةٍ أَقْلامٌ وَالْبَحْرُ يَمُدُّهُ مِنْ بَعْدِهِ سَبْعَةُ أَبْحُرٍ مَا نَفِدَتْ كَلِمَاتُ اللَّهِ}
INSERT INTO [b9617] VALUES (3440,'<span data-type="title" id=toc-3422>٢٨ </span>- {مَا خَلْقُكُمْ وَلا بَعْثُكُمْ إِلا كَنَفْسٍ وَاحِدَةٍ إِنَّ اللَّهَ سَمِيعٌ بَصِيرٌ}
INSERT INTO [b9617] VALUES (3441,'<span data-type="title" id=toc-3423>٢٩ </span>- {أَلَمْ تَرَ أَنَّ اللَّهَ يُولِجُ اللَّيْلَ فِي النَّهَارِ وَيُولِجُ النَّهَارَ فِي اللَّيْلِ وَسَخَّرَ الشَّمْسَ وَالْقَمَرَ ⦗٩٥١⦘ كُلٌّ يَجْرِي إِلَى أَجَلٍ مُسَمًّى وَأَنَّ اللَّهَ بِمَا تَعْمَلُونَ خَبِيرٌ}
INSERT INTO [b9617] VALUES (3442,'<span data-type="title" id=toc-3424>٣٠ </span>- {ذَلِكَ بِأَنَّ اللَّهَ هُوَ الْحَقُّ وَأَنَّ مَا يَدْعُونَ مِنْ دُونِهِ الْبَاطِلُ وَأَنَّ اللَّهَ هُوَ الْعَلِيُّ الْكَبِيرُ}
INSERT INTO [b9617] VALUES (3443,'<span data-type="title" id=toc-3425>٣١ </span>- {أَلَمْ تَرَ أَنَّ الْفُلْكَ تَجْرِي فِي الْبَحْرِ بِنِعْمَتِ اللَّهِ لِيُرِيَكُمْ مِنْ آيَاتِهِ}
INSERT INTO [b9617] VALUES (3444,'<span data-type="title" id=toc-3426>٣٢ </span>- {وَإِذَا غَشِيَهُمْ مَوْجٌ كَالظُّلَلِ دَعَوُا اللَّهَ مُخْلِصِينَ لَهُ الدِّينَ فَلَمَّا نَجَّاهُمْ إِلَى الْبَرِّ فَمِنْهُمْ مُقْتَصِدٌ وَمَا يَجْحَدُ بِآيَاتِنَا إِلا كُلُّ خَتَّارٍ كَفُورٍ}
INSERT INTO [b9617] VALUES (3445,'<span data-type="title" id=toc-3427>٣٣ </span>- {وَاخْشَوْا يَوْمًا لا يَجْزِي وَالِدٌ عَنْ وَلَدِهِ وَلا مَوْلُودٌ هُوَ جَازٍ عَنْ وَالِدِهِ شَيْئًا إِنَّ وَعْدَ اللَّهِ حَقٌّ فَلا تَغُرَّنَّكُمُ الْحَيَاةُ الدُّنْيَا}
INSERT INTO [b9617] VALUES (3446,'<span data-type="title" id=toc-3428>٣٤ </span>- {إِنَّ اللَّهَ عِنْدَهُ عِلْمُ السَّاعَةِ وَيُنَزِّلُ الْغَيْثَ وَيَعْلَمُ مَا فِي الأَرْحَامِ وَمَا تَدْرِي نَفْسٌ مَاذَا تَكْسِبُ غَدًا وَمَا تَدْرِي نَفْسٌ بِأَيِّ أَرْضٍ تَمُوتُ إِنَّ اللَّهَ عَلِيمٌ خَبِيرٌ}
INSERT INTO [b9617] VALUES (3447,'<span data-type="title" id=toc-3429>سورة السجدة</span>','3','953',NULL,NULL,'0');
INSERT INTO [b9617] VALUES (3448,'<span data-type="title" id=toc-3430>٢ </span>- {تَنْزِيلُ الْكِتَابِ لا رَيْبَ فِيهِ مِنْ رَبِّ الْعَالَمِينَ}
INSERT INTO [b9617] VALUES (3449,'<span data-type="title" id=toc-3431>٣ </span>- {أَمْ يَقُولُونَ افْتَرَاهُ بَلْ هُوَ الْحَقُّ مِنْ رَبِّكَ لِتُنْذِرَ قَوْمًا مَا أَتَاهُمْ مِنْ نَذِيرٍ مِنْ قَبْلِكَ لَعَلَّهُمْ يَهْتَدُونَ}
INSERT INTO [b9617] VALUES (3450,'<span data-type="title" id=toc-3432>٤ </span>- {اللَّهُ الَّذِي خَلَقَ السَّمَاوَاتِ وَالأَرْضَ وَمَا بَيْنَهُمَا فِي سِتَّةِ أَيَّامٍ ثُمَّ اسْتَوَى عَلَى الْعَرْشِ مَا لَكُمْ مِنْ دُونِهِ مِنْ وَلِيٍّ وَلا شَفِيعٍ}
INSERT INTO [b9617] VALUES (3451,'<span data-type="title" id=toc-3433>٥ </span>- {يُدَبِّرُ الأَمْرَ مِنَ السَّمَاءِ إِلَى الأَرْضِ ثُمَّ يَعْرُجُ إِلَيْهِ فِي يَوْمٍ كَانَ مِقْدَارُهُ أَلْفَ سَنَةٍ مِمَّا ⦗٩٥٤⦘ تَعُدُّونَ}
INSERT INTO [b9617] VALUES (3452,'<span data-type="title" id=toc-3434>٦ </span>- {ذَلِكَ عَالِمُ الْغَيْبِ وَالشَّهَادَةِ الْعَزِيزُ الرَّحِيمُ}
INSERT INTO [b9617] VALUES (3453,'<span data-type="title" id=toc-3435>٧ </span>- {الَّذِي أَحْسَنَ كُلَّ شَيْءٍ خَلَقَهُ وَبَدَأَ خَلْقَ الإِنْسَانِ مِنْ طِينٍ}
INSERT INTO [b9617] VALUES (3454,'<span data-type="title" id=toc-3436>٨ </span>- {ثُمَّ جَعَلَ نَسْلَهُ مِنْ سُلالَةٍ مِنْ مَاءٍ مَهِينٍ}
INSERT INTO [b9617] VALUES (3455,'<span data-type="title" id=toc-3437>٩ </span>- {ثُمَّ سَوَّاهُ وَنَفَخَ فِيهِ مِنْ رُوحِهِ وَجَعَلَ لَكُمُ السَّمْعَ وَالأَبْصَارَ وَالأَفْئِدَةَ قَلِيلا مَا تَشْكُرُونَ}
INSERT INTO [b9617] VALUES (3456,'<span data-type="title" id=toc-3438>١٠ </span>- {وَقَالُوا أَئِذَا ضَلَلْنَا فِي الأَرْضِ أَئِنَّا لَفِي خَلْقٍ جَدِيدٍ بَلْ هُمْ بِلِقَاءِ رَبِّهِمْ كَافِرُونَ}
INSERT INTO [b9617] VALUES (3457,'<span data-type="title" id=toc-3439>١١ </span>- {قُلْ يَتَوَفَّاكُمْ مَلَكُ الْمَوْتِ الَّذِي وُكِّلَ بِكُمْ ثُمَّ إِلَى رَبِّكُمْ تُرْجَعُونَ}
INSERT INTO [b9617] VALUES (3458,'<span data-type="title" id=toc-3440>١٢ </span>- {وَلَوْ تَرَى إِذِ الْمُجْرِمُونَ نَاكِسُو رُءُوسِهِمْ عِنْدَ رَبِّهِمْ رَبَّنَا أَبْصَرْنَا وَسَمِعْنَا فَارْجِعْنَا نَعْمَلْ صَالِحًا إِنَّا مُوقِنُونَ}
INSERT INTO [b9617] VALUES (3459,'<span data-type="title" id=toc-3441>١٣ </span>- {وَلَوْ شِئْنَا لآتَيْنَا كُلَّ نَفْسٍ هُدَاهَا وَلَكِنْ حَقَّ الْقَوْلُ مِنِّي لأَمْلأَنَّ جَهَنَّمَ مِنَ الْجِنَّةِ وَالنَّاسِ أَجْمَعِينَ}
INSERT INTO [b9617] VALUES (3460,'<span data-type="title" id=toc-3442>١٤ </span>- {فَذُوقُوا بِمَا نَسِيتُمْ لِقَاءَ يَوْمِكُمْ هَذَا إِنَّا نَسِينَاكُمْ وَذُوقُوا عَذَابَ الْخُلْدِ ⦗٩٥٦⦘}
INSERT INTO [b9617] VALUES (3461,'<span data-type="title" id=toc-3443>١٥ </span>- {إِنَّمَا يُؤْمِنُ بِآيَاتِنَا الَّذِينَ إِذَا ذُكِّرُوا بِهَا خَرُّوا سُجَّدًا وَسَبَّحُوا بِحَمْدِ رَبِّهِمْ وَهُمْ لا يَسْتَكْبِرُونَ}
INSERT INTO [b9617] VALUES (3462,'<span data-type="title" id=toc-3444>١٦ </span>- {تَتَجَافَى جُنُوبُهُمْ عَنِ الْمَضَاجِعِ يَدْعُونَ رَبَّهُمْ خَوْفًا وَطَمَعًا وَمِمَّا رَزَقْنَاهُمْ يُنْفِقُونَ}
INSERT INTO [b9617] VALUES (3463,'<span data-type="title" id=toc-3445>١٧ </span>- {فَلا تَعْلَمُ نَفْسٌ مَا أُخْفِيَ لَهُمْ مِنْ قُرَّةِ أَعْيُنٍ جَزَاءً بِمَا كَانُوا يَعْمَلُونَ}
INSERT INTO [b9617] VALUES (3464,'<span data-type="title" id=toc-3446>١٨ </span>- {أَفَمَنْ كَانَ مُؤْمِنًا كَمَنْ كَانَ فَاسِقًا لا يَسْتَوُونَ}
INSERT INTO [b9617] VALUES (3465,'<span data-type="title" id=toc-3447>١٩ </span>- {أَمَّا الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ فَلَهُمْ جَنَّاتُ الْمَأْوَى نُزُلا بِمَا كَانُوا يَعْمَلُونَ}
INSERT INTO [b9617] VALUES (3466,'<span data-type="title" id=toc-3448>٢٠ </span>- {وَأَمَّا الَّذِينَ فَسَقُوا فَمَأْوَاهُمُ النَّارُ كُلَّمَا أَرَادُوا أَنْ يَخْرُجُوا مِنْهَا أُعِيدُوا فِيهَا وَقِيلَ لَهُمْ ذُوقُوا عَذَابَ النَّارِ الَّذِي كُنْتُمْ بِهِ تُكَذِّبُونَ}
INSERT INTO [b9617] VALUES (3467,'<span data-type="title" id=toc-3449>٢١ </span>- {وَلَنُذِيقَنَّهُمْ مِنَ الْعَذَابِ الأَدْنَى دُونَ الْعَذَابِ الأَكْبَرِ لَعَلَّهُمْ يَرْجِعُونَ}
INSERT INTO [b9617] VALUES (3468,'<span data-type="title" id=toc-3450>٢٢ </span>- {وَمَنْ أَظْلَمُ مِمَّنْ ذُكِّرَ بِآيَاتِ رَبِّهِ ثُمَّ أَعْرَضَ عَنْهَا إِنَّا مِنَ الْمُجْرِمِينَ مُنْتَقِمُونَ} ⦗٩٥٨⦘
INSERT INTO [b9617] VALUES (3469,'<span data-type="title" id=toc-3451>٢٣ </span>- {وَلَقَدْ آتَيْنَا مُوسَى الْكِتَابَ فَلا تَكُنْ فِي مِرْيَةٍ مِنْ لِقَائِهِ وَجَعَلْنَاهُ هُدًى لِبَنِي إِسْرَائِيلَ}
INSERT INTO [b9617] VALUES (3470,'<span data-type="title" id=toc-3452>٢٤ </span>- {وَجَعَلْنَا مِنْهُمْ أَئِمَّةً يَهْدُونَ بِأَمْرِنَا لَمَّا صَبَرُوا وَكَانُوا بِآيَاتِنَا يُوقِنُونَ}
INSERT INTO [b9617] VALUES (3471,'<span data-type="title" id=toc-3453>٢٥ </span>- {إِنَّ رَبَّكَ هُوَ يَفْصِلُ بَيْنَهُمْ يَوْمَ الْقِيَامَةِ فِيمَا كَانُوا فِيهِ يَخْتَلِفُونَ}
INSERT INTO [b9617] VALUES (3472,'<span data-type="title" id=toc-3454>٢٦ </span>- {أَوَلَمْ يَهْدِ لَهُمْ كَمْ أَهْلَكْنَا مِنْ قَبْلِهِمْ مِنَ الْقُرُونِ يَمْشُونَ فِي مَسَاكِنِهِمْ إِنَّ فِي ذَلِكَ لآيَاتٍ أَفَلا يَسْمَعُونَ}
INSERT INTO [b9617] VALUES (3473,'<span data-type="title" id=toc-3455>٢٧ </span>- {أَوَلَمْ يَرَوْا أَنَّا نَسُوقُ الْمَاءَ إِلَى الأَرْضِ الْجُرُزِ فَنُخْرِجُ بِهِ زَرْعًا تَأْكُلُ مِنْهُ أَنْعَامُهُمْ وَأَنْفُسُهُمْ أَفَلا يُبْصِرُونَ}
INSERT INTO [b9617] VALUES (3474,'<span data-type="title" id=toc-3456>٢٨ </span>- {وَيَقُولُونَ مَتَى هَذَا الْفَتْحُ إِنْ كُنْتُمْ صَادِقِينَ}
INSERT INTO [b9617] VALUES (3475,'<span data-type="title" id=toc-3457>٢٩ </span>- {قُلْ يَوْمَ الْفَتْحِ لا يَنْفَعُ الَّذِينَ كَفَرُوا إِيمَانُهُمْ وَلا هُمْ يُنْظَرُونَ}
INSERT INTO [b9617] VALUES (3476,'<span data-type="title" id=toc-3458>٣٠ </span>- {فَأَعْرِضْ عَنْهُمْ وَانْتَظِرْ إِنَّهُمْ مُنْتَظِرُونَ}
INSERT INTO [b9617] VALUES (3477,'<span data-type="title" id=toc-3459>سورة الأحزاب</span>','3','960',NULL,NULL,'0');
INSERT INTO [b9617] VALUES (3478,'<span data-type="title" id=toc-3460>١ </span>- {يَا أَيُّهَا النَّبِيُّ اتَّقِ اللَّهَ وَلا تُطِعِ الْكَافِرِينَ وَالْمُنَافِقِينَ إِنَّ اللَّهَ كَانَ عَلِيمًا حَكِيمًا}
INSERT INTO [b9617] VALUES (3479,'<span data-type="title" id=toc-3461>٢ </span>- {وَاتَّبِعْ مَا يُوحَى إِلَيْكَ مِنْ رَبِّكَ إِنَّ اللَّهَ كَانَ بِمَا تَعْمَلُونَ خَبِيرًا}
INSERT INTO [b9617] VALUES (3480,'<span data-type="title" id=toc-3462>٣ </span>- {وَكَفَى بِاللَّهِ وَكِيلا}
INSERT INTO [b9617] VALUES (3481,'<span data-type="title" id=toc-3463>٤ </span>- {مَا جَعَلَ اللَّهُ لِرَجُلٍ مِنْ قَلْبَيْنِ فِي جَوْفِهِ وَمَا جَعَلَ أَزْوَاجَكُمُ اللائِي تُظَاهِرُونَ مِنْهُنَّ أُمَّهَاتِكُمْ وَمَا جَعَلَ أَدْعِيَاءَكُمْ أَبْنَاءَكُمْ ذَلِكُمْ قَوْلُكُمْ بِأَفْوَاهِكُمْ وَاللَّهُ يَقُولُ الْحَقَّ وَهُوَ يَهْدِي السَّبِيلَ}
INSERT INTO [b9617] VALUES (3482,'<span data-type="title" id=toc-3464>٥ </span>- {هُوَ أَقْسَطُ عِنْدَ اللَّهِ فَإِنْ لَمْ تَعْلَمُوا آبَاءَهُمْ فَإِخْوَانُكُمْ فِي الدِّينِ وَمَوَالِيكُمْ وَلَيْسَ عَلَيْكُمْ جُنَاحٌ فِيمَا أَخْطَأْتُمْ بِهِ وَلَكِنْ مَا تَعَمَّدَتْ قُلُوبُكُمْ وَكَانَ اللَّهُ غَفُورًا رَحِيمًا}
INSERT INTO [b9617] VALUES (3483,'<span data-type="title" id=toc-3465>٦ </span>- {النَّبِيُّ أَوْلَى بِالْمُؤْمِنِينَ مِنْ أَنْفُسِهِمْ وَأَزْوَاجُهُ أُمَّهَاتُهُمْ وَأُولُو الأَرْحَامِ بَعْضُهُمْ أَوْلَى بِبَعْضٍ فِي كِتَابِ اللَّهِ مِنَ الْمُؤْمِنِينَ وَالْمُهَاجِرِينَ إِلا أَنْ تَفْعَلُوا إِلَى أَوْلِيَائِكُمْ مَعْرُوفًا كَانَ ذَلِكَ فِي الْكِتَابِ مَسْطُورًا}
INSERT INTO [b9617] VALUES (3484,'<span data-type="title" id=toc-3466>٧ </span>- {وَإِذْ أَخَذْنَا مِنَ النَّبِيِّينَ مِيثَاقَهُمْ وَمِنْكَ وَمِنْ نُوحٍ وَإِبْرَاهِيمَ وَمُوسَى وَعِيسَى ابْنِ مَرْيَمَ وَأَخَذْنَا مِنْهُمْ مِيثَاقًا غَلِيظًا}
INSERT INTO [b9617] VALUES (3485,'<span data-type="title" id=toc-3467>٨ </span>- {لِيَسْأَلَ الصَّادِقِينَ عَنْ صِدْقِهِمْ وَأَعَدَّ لِلْكَافِرِينَ عَذَابًا أَلِيمًا}
INSERT INTO [b9617] VALUES (3486,'<span data-type="title" id=toc-3468>٩ </span>- {يَا أَيُّهَا الَّذِينَ آمَنُوا اذْكُرُوا نِعْمَةَ اللَّهِ عَلَيْكُمْ إِذْ جَاءَتْكُمْ جُنُودٌ فَأَرْسَلْنَا عَلَيْهِمْ رِيحًا وَجُنُودًا لَمْ تَرَوْهَا وَكَانَ اللَّهُ بِمَا تَعْمَلُونَ بَصِيرًا}
INSERT INTO [b9617] VALUES (3487,'<span data-type="title" id=toc-3469>١٠ </span>- {إِذْ جَاءُوكُمْ مِنْ فَوْقِكُمْ وَمِنْ أَسْفَلَ مِنْكُمْ وَإِذْ زَاغَتِ الأَبْصَارُ وَبَلَغَتِ الْقُلُوبُ الْحَنَاجِرَ وَتَظُنُّونَ بِاللَّهِ الظُّنُونَا}
INSERT INTO [b9617] VALUES (3488,'<span data-type="title" id=toc-3470>١١ </span>- {هُنَالِكَ ابْتُلِيَ الْمُؤْمِنُونَ} ⦗٩٦٣⦘
INSERT INTO [b9617] VALUES (3489,'<span data-type="title" id=toc-3471>١٢ </span>- {وَإِذْ يَقُولُ الْمُنَافِقُونَ وَالَّذِينَ فِي قُلُوبِهِمْ مَرَضٌ مَا وَعَدَنَا اللَّهُ وَرَسُولُهُ إِلا غُرُورًا}
INSERT INTO [b9617] VALUES (3490,'<span data-type="title" id=toc-3472>١٣ </span>- {وَإِذْ قَالَتْ طَائِفَةٌ مِنْهُمْ يَا أَهْلَ يَثْرِبَ لا مُقَامَ لَكُمْ فَارْجِعُوا وَيَسْتَأْذِنُ فَرِيقٌ مِنْهُمُ النَّبِيَّ يَقُولُونَ إِنَّ بُيُوتَنَا عَوْرَةٌ وَمَا هِيَ بِعَوْرَةٍ إِنْ يُرِيدُونَ إِلا فِرَارًا}
INSERT INTO [b9617] VALUES (3491,'<span data-type="title" id=toc-3473>١٤ </span>- {وَلَوْ دُخِلَتْ عَلَيْهِمْ مِنْ أَقْطَارِهَا ثُمَّ سُئِلُوا الْفِتْنَةَ لآتَوْهَا وَمَا تَلَبَّثُوا بِهَا إِلا يَسِيرًا}
INSERT INTO [b9617] VALUES (3492,'<span data-type="title" id=toc-3474>١٥ </span>- {وَلَقَدْ كَانُوا عَاهَدُوا اللَّهَ مِنْ قَبْلُ لا يُوَلُّونَ الأَدْبَارَ وَكَانَ عَهْدُ اللَّهِ مَسْئُولا} ⦗٩٦٤⦘
INSERT INTO [b9617] VALUES (3493,'<span data-type="title" id=toc-3475>١٦ </span>- {قُلْ لَنْ يَنْفَعَكُمُ الْفِرَارُ إِنْ فَرَرْتُمْ مِنَ الْمَوْتِ أَوِ الْقَتْلِ وَإِذًا لا تُمَتَّعُونَ إِلا قَلِيلا}
INSERT INTO [b9617] VALUES (3494,'<span data-type="title" id=toc-3476>١٧ </span>- {قُلْ مَنْ ذَا الَّذِي يَعْصِمُكُمْ مِنَ اللَّهِ إِنْ أَرَادَ بِكُمْ سُوءًا أَوْ أَرَادَ بِكُمْ رَحْمَةً وَلا يَجِدُونَ لَهُمْ مِنْ دُونِ اللَّهِ وَلِيًّا وَلا نَصِيرًا}
INSERT INTO [b9617] VALUES (3495,'<span data-type="title" id=toc-3477>١٨ </span>- {قَدْ يَعْلَمُ اللَّهُ الْمُعَوِّقِينَ مِنْكُمْ وَالْقَائِلِينَ لإِخْوَانِهِمْ هَلُمَّ إِلَيْنَا وَلا يَأْتُونَ الْبَأْسَ إِلا قَلِيلا}
INSERT INTO [b9617] VALUES (3496,'<span data-type="title" id=toc-3478>١٩ </span>- {أَشِحَّةً عَلَيْكُمْ فَإِذَا جَاءَ الْخَوْفُ رَأَيْتَهُمْ يَنْظُرُونَ إِلَيْكَ تَدُورُ أَعْيُنُهُمْ كَالَّذِي يُغْشَى عَلَيْهِ مِنَ الْمَوْتِ فَإِذَا ذَهَبَ الْخَوْفُ سَلَقُوكُمْ بِأَلْسِنَةٍ حِدَادٍ أَشِحَّةً عَلَى الْخَيْرِ أُولَئِكَ لَمْ يُؤْمِنُوا فَأَحْبَطَ اللَّهُ أَعْمَالَهُمْ وَكَانَ ذَلِكَ عَلَى اللَّهِ يَسِيرًا}
INSERT INTO [b9617] VALUES (3497,'<span data-type="title" id=toc-3479>٢٠ </span>- {يَحْسَبُونَ الأَحْزَابَ لَمْ يَذْهَبُوا وَإِنْ يَأْتِ الأَحْزَابُ يَوَدُّوا لَوْ أَنَّهُمْ بَادُونَ فِي الأَعْرَابِ يَسْأَلُونَ عَنْ أَنْبَائِكُمْ وَلَوْ كَانُوا فِيكُمْ مَا قَاتَلُوا إِلا قَلِيلا}
INSERT INTO [b9617] VALUES (3498,'<span data-type="title" id=toc-3480>٢١ </span>- {لَقَدْ كَانَ لَكُمْ فِي رَسُولِ اللَّهِ أُسْوَةٌ حَسَنَةٌ لِمَنْ كَانَ يَرْجُو اللَّهَ وَالْيَوْمَ الآخِرَ وَذَكَرَ اللَّهَ كَثِيرًا}
INSERT INTO [b9617] VALUES (3499,'<span data-type="title" id=toc-3481>٢٢ </span>- {وَلَمَّا رَأَى الْمُؤْمِنُونَ الأَحْزَابَ قَالُوا هَذَا مَا وَعَدَنَا اللَّهُ وَرَسُولُهُ وَصَدَقَ اللَّهُ وَرَسُولُهُ وَمَا زَادَهُمْ إِلا إِيمَانًا وَتَسْلِيمًا}
INSERT INTO [b9617] VALUES (3500,'<span data-type="title" id=toc-3482>٢٣ </span>- {مِنَ الْمُؤْمِنِينَ رِجَالٌ صَدَقُوا مَا عَاهَدُوا اللَّهَ عَلَيْهِ فَمِنْهُمْ مَنْ قَضَى نَحْبَهُ وَمِنْهُمْ مَنْ يَنْتَظِرُ وَمَا بَدَّلُوا تَبْدِيلا}
INSERT INTO [b9617] VALUES (3501,'<span data-type="title" id=toc-3483>٢٤ </span>- {لِيَجْزِيَ اللَّهُ الصَّادِقِينَ بِصِدْقِهِمْ وَيُعَذِّبَ الْمُنَافِقِينَ إِنْ شَاءَ أَوْ يَتُوبَ عَلَيْهِمْ ⦗٩٦٧⦘}
INSERT INTO [b9617] VALUES (3502,'<span data-type="title" id=toc-3484>٢٥ </span>- {وَرَدَّ اللَّهُ الَّذِينَ كَفَرُوا بِغَيْظِهِمْ لَمْ يَنَالُوا خَيْرًا وَكَفَى اللَّهُ الْمُؤْمِنِينَ الْقِتَالَ وَكَانَ اللَّهُ قَوِيًّا عَزِيزًا}
INSERT INTO [b9617] VALUES (3503,'<span data-type="title" id=toc-3485>٢٦ </span>- {وَأَنْزَلَ الَّذِينَ ظَاهَرُوهُمْ مِنْ أَهْلِ الْكِتَابِ مِنْ صَيَاصِيهِمْ وَقَذَفَ فِي قُلُوبِهِمُ الرُّعْبَ فَرِيقًا تَقْتُلُونَ وَتَأْسِرُونَ فَرِيقًا}
INSERT INTO [b9617] VALUES (3504,'<span data-type="title" id=toc-3486>٢٧ </span>- {وَأَوْرَثَكُمْ أَرْضَهُمْ وَدِيَارَهُمْ وَأَمْوَالَهُمْ وَأَرْضًا لَمْ تَطَئُوهَا}
INSERT INTO [b9617] VALUES (3505,'<span data-type="title" id=toc-3487>٢٨ </span>- {يَا أَيُّهَا النَّبِيُّ قُلْ لأَزْوَاجِكَ إِنْ كُنْتُنَّ تُرِدْنَ الْحَيَاةَ الدُّنْيَا وَزِينَتَهَا فَتَعَالَيْنَ أُمَتِّعْكُنَّ وَأُسَرِّحْكُنَّ سَرَاحًا جَمِيلا}
INSERT INTO [b9617] VALUES (3506,'<span data-type="title" id=toc-3488>٢٩ </span>- {أَعَدَّ لِلْمُحْسِنَاتِ مِنْكُنَّ أَجْرًا عَظِيمًا}
INSERT INTO [b9617] VALUES (3507,'<span data-type="title" id=toc-3489>٣٠ </span>- {يَا نِسَاءَ النَّبِيِّ مَنْ يَأْتِ مِنْكُنَّ بِفَاحِشَةٍ مُبَيِّنَةٍ يُضَاعَفْ لَهَا الْعَذَابُ ضِعْفَيْنِ وَكَانَ ذَلِكَ عَلَى اللَّهِ يَسِيرًا}
INSERT INTO [b9617] VALUES (3508,'<span data-type="title" id=toc-3490>٣١ </span>- {وَمَنْ يَقْنُتْ مِنْكُنَّ لِلَّهِ وَرَسُولِهِ وَتَعْمَلْ صَالِحًا نُؤْتِهَا أَجْرَهَا مَرَّتَيْنِ}
INSERT INTO [b9617] VALUES (3509,'<span data-type="title" id=toc-3491>٣٢ </span>- {يَا نِسَاءَ النَّبِيِّ لَسْتُنَّ كَأَحَدٍ مِنَ النِّسَاءِ إِنِ اتَّقَيْتُنَّ فَلا تَخْضَعْنَ بِالْقَوْلِ فَيَطْمَعَ الَّذِي فِي قَلْبِهِ مَرَضٌ}
INSERT INTO [b9617] VALUES (3510,'<span data-type="title" id=toc-3492>٣٣ </span>- {إِنَّمَا يُرِيدُ اللَّهُ لِيُذْهِبَ عَنْكُمُ الرِّجْسَ أَهْلَ الْبَيْتِ وَيُطَهِّرَكُمْ تَطْهِيرًا}
INSERT INTO [b9617] VALUES (3511,'<span data-type="title" id=toc-3493>٣٤ </span>- {وَاذْكُرْنَ مَا يُتْلَى فِي بُيُوتِكُنَّ مِنْ آيَاتِ اللَّهِ وَالْحِكْمَةِ إِنَّ اللَّهَ كَانَ لَطِيفًا خَبِيرًا}
INSERT INTO [b9617] VALUES (3512,'<span data-type="title" id=toc-3494>٣٥ </span>- {وَالْحَافِظِينَ فُرُوجَهُمْ وَالْحَافِظَاتِ وَالذَّاكِرِينَ اللَّهَ كَثِيرًا وَالذَّاكِرَاتِ أَعَدَّ اللَّهُ لَهُمْ مَغْفِرَةً وَأَجْرًا عَظِيمًا} ⦗٩٧٠⦘
INSERT INTO [b9617] VALUES (3513,'<span data-type="title" id=toc-3495>٣٦ </span>- {وَمَا كَانَ لِمُؤْمِنٍ وَلا مُؤْمِنَةٍ إِذَا قَضَى اللَّهُ وَرَسُولُهُ أَمْرًا أَنْ يَكُونَ لَهُمُ الْخِيَرَةُ مِنْ أَمْرِهِمْ وَمَنْ يَعْصِ اللَّهَ وَرَسُولَهُ فَقَدْ ضَلَّ ضَلالا مُبِينًا}
INSERT INTO [b9617] VALUES (3514,'<span data-type="title" id=toc-3496>٣٧ </span>- {وَإِذْ تَقُولُ لِلَّذِي أَنْعَمَ اللَّهُ عَلَيْهِ وَأَنْعَمْتَ عَلَيْهِ أَمْسِكْ عَلَيْكَ زَوْجَكَ وَاتَّقِ اللَّهَ وَتُخْفِي فِي نَفْسِكَ مَا اللَّهُ مُبْدِيهِ وَتَخْشَى النَّاسَ وَاللَّهُ أَحَقُّ أَنْ تَخْشَاهُ فَلَمَّا قَضَى زَيْدٌ مِنْهَا وَطَرًا زَوَّجْنَاكَهَا لِكَيْ لا يَكُونَ عَلَى الْمُؤْمِنِينَ حَرَجٌ فِي أَزْوَاجِ أَدْعِيَائِهِمْ إِذَا قَضَوْا مِنْهُنَّ وَطَرًا}
INSERT INTO [b9617] VALUES (3515,'منصوب على نزع الخافض الباء، وجملة «فلمَّا قضى» معطوفة على جملة «تقول» ، والضميران في «زوَّجناكها» مفعولا الفعل، واللام في «لكي» جارّة للتعليل، و «كي» حرف مصدري ونصب، والمصدر المؤول مجرور باللام متعلق بالفعل «زوَّجْناكها» ، والجار «في أزواج» متعلق بنعت لـ «حرج» . «إذا» ظرفية شرطية متعلقة بالجواب المقدر دلَّ عليه ما قبله، أي: إذا قضوا منهن وطرًا، فليس عليهم حرج، والعامل في «إذا» مقدر أي: انتفى الحرج إذا. وجملة الشرط مستأنفة، وجملة «قضوا» مضاف إليه.','3','971',NULL,NULL,'0');
INSERT INTO [b9617] VALUES (3516,'<span data-type="title" id=toc-3497>٣٨ </span>- {مَا كَانَ عَلَى النَّبِيِّ مِنْ حَرَجٍ فِيمَا فَرَضَ اللَّهُ لَهُ سُنَّةَ اللَّهِ فِي الَّذِينَ خَلَوْا مِنْ قَبْلُ وَكَانَ أَمْرُ اللَّهِ قَدَرًا مَقْدُورًا}
INSERT INTO [b9617] VALUES (3517,'<span data-type="title" id=toc-3498>٣٩ </span>- {الَّذِينَ يُبَلِّغُونَ رِسَالاتِ اللَّهِ وَيَخْشَوْنَهُ وَلا يَخْشَوْنَ أَحَدًا إِلا اللَّهَ وَكَفَى بِاللَّهِ حَسِيبًا}
INSERT INTO [b9617] VALUES (3518,'<span data-type="title" id=toc-3499>٤٠ </span>- {مَا كَانَ مُحَمَّدٌ أَبَا أَحَدٍ مِنْ رِجَالِكُمْ وَلَكِنْ رَسُولَ اللَّهِ وَخَاتَمَ النَّبِيِّينَ وَكَانَ اللَّهُ بِكُلِّ شَيْءٍ عَلِيمًا} ⦗٩٧٢⦘
INSERT INTO [b9617] VALUES (3519,'<span data-type="title" id=toc-3500>٤١ </span>- {اذْكُرُوا اللَّهَ ذِكْرًا كَثِيرًا}
INSERT INTO [b9617] VALUES (3520,'<span data-type="title" id=toc-3501>٤٢ </span>- {وَسَبِّحُوهُ بُكْرَةً وَأَصِيلا}
INSERT INTO [b9617] VALUES (3521,'<span data-type="title" id=toc-3502>٤٣ </span>- {هُوَ الَّذِي يُصَلِّي عَلَيْكُمْ وَمَلائِكَتُهُ لِيُخْرِجَكُمْ مِنَ الظُّلُمَاتِ إِلَى النُّورِ وَكَانَ بِالْمُؤْمِنِينَ رَحِيمًا}
INSERT INTO [b9617] VALUES (3522,'<span data-type="title" id=toc-3503>٤٤ </span>- {تَحِيَّتُهُمْ يَوْمَ يَلْقَوْنَهُ سَلامٌ وَأَعَدَّ لَهُمْ أَجْرًا كَرِيمًا}
INSERT INTO [b9617] VALUES (3523,'<span data-type="title" id=toc-3504>٤٥ </span>- {إِنَّا أَرْسَلْنَاكَ شَاهِدًا}
INSERT INTO [b9617] VALUES (3524,'<span data-type="title" id=toc-3505>٤٦ </span>- {وَدَاعِيًا إِلَى اللَّهِ بِإِذْنِهِ وَسِرَاجًا مُنِيرًا}
INSERT INTO [b9617] VALUES (3525,'<span data-type="title" id=toc-3506>٤٧ </span>- {وَبَشِّرِ الْمُؤْمِنِينَ بِأَنَّ لَهُمْ مِنَ اللَّهِ فَضْلا كَبِيرًا}
INSERT INTO [b9617] VALUES (3526,'<span data-type="title" id=toc-3507>٤٨ </span>- {وَكَفَى بِاللَّهِ وَكِيلا}
INSERT INTO [b9617] VALUES (3527,'<span data-type="title" id=toc-3508>٤٩ </span>- {يَا أَيُّهَا الَّذِينَ آمَنُوا إِذَا نَكَحْتُمُ الْمُؤْمِنَاتِ ثُمَّ طَلَّقْتُمُوهُنَّ مِنْ قَبْلِ أَنْ تَمَسُّوهُنَّ فَمَا لَكُمْ عَلَيْهِنَّ مِنْ عِدَّةٍ تَعْتَدُّونَهَا فَمَتِّعُوهُنَّ وَسَرِّحُوهُنَّ سَرَاحًا جَمِيلا}
INSERT INTO [b9617] VALUES (3528,'<span data-type="title" id=toc-3509>٥٠ </span>- {إِنَّا أَحْلَلْنَا لَكَ أَزْوَاجَكَ اللاتِي آتَيْتَ أُجُورَهُنَّ وَمَا مَلَكَتْ يَمِينُكَ مِمَّا أَفَاءَ اللَّهُ عَلَيْكَ وَبَنَاتِ عَمِّكَ وَبَنَاتِ عَمَّاتِكَ وَبَنَاتِ خَالِكَ وَبَنَاتِ خَالاتِكَ اللاتِي هَاجَرْنَ مَعَكَ وَامْرَأَةً مُؤْمِنَةً إِنْ وَهَبَتْ نَفْسَهَا لِلنَّبِيِّ إِنْ أَرَادَ النَّبِيُّ أَنْ يَسْتَنْكِحَهَا خَالِصَةً لَكَ مِنْ دُونِ الْمُؤْمِنِينَ ⦗٩٧٤⦘ قَدْ عَلِمْنَا مَا فَرَضْنَا عَلَيْهِمْ فِي أَزْوَاجِهِمْ وَمَا مَلَكَتْ أَيْمَانُهُمْ لِكَيْلا يَكُونَ عَلَيْكَ حَرَجٌ وَكَانَ اللَّهُ غَفُورًا رَحِيمًا}
INSERT INTO [b9617] VALUES (3529,'<span data-type="title" id=toc-3510>٥١ </span>- {تُرْجِي مَنْ تَشَاءُ مِنْهُنَّ وَتُؤْوِي إِلَيْكَ مَنْ تَشَاءُ وَمَنِ ابْتَغَيْتَ مِمَّنْ عَزَلْتَ فَلا جُنَاحَ عَلَيْكَ ذَلِكَ أَدْنَى أَنْ تَقَرَّ أَعْيُنُهُنَّ وَلا يَحْزَنَّ وَيَرْضَيْنَ بِمَا آتَيْتَهُنَّ كُلُّهُنَّ وَاللَّهُ يَعْلَمُ مَا فِي قُلُوبِكُمْ وَكَانَ اللَّهُ عَلِيمًا حَلِيمًا}
INSERT INTO [b9617] VALUES (3530,'<span data-type="title" id=toc-3511>٥٢ </span>- {لا يَحِلُّ لَكَ النِّسَاءُ مِنْ بَعْدُ وَلا أَنْ تَبَدَّلَ بِهِنَّ مِنْ أَزْوَاجٍ وَلَوْ أَعْجَبَكَ حُسْنُهُنَّ إِلا مَا مَلَكَتْ يَمِينُكَ وَكَانَ اللَّهُ عَلَى كُلِّ شَيْءٍ رَقِيبًا}
INSERT INTO [b9617] VALUES (3531,'<span data-type="title" id=toc-3512>٥٣ </span>- {يَا أَيُّهَا الَّذِينَ آمَنُوا لا تَدْخُلُوا بُيُوتَ النَّبِيِّ إِلا أَنْ يُؤْذَنَ لَكُمْ إِلَى طَعَامٍ غَيْرَ نَاظِرِينَ إِنَاهُ وَلَكِنْ إِذَا دُعِيتُمْ فَادْخُلُوا فَإِذَا طَعِمْتُمْ فَانْتَشِرُوا وَلا مُسْتَأْنِسِينَ لِحَدِيثٍ إِنَّ ذَلِكُمْ كَانَ يُؤْذِي النَّبِيَّ فَيَسْتَحْيِي مِنْكُمْ وَاللَّهُ لا يَسْتَحْيِي مِنَ الْحَقِّ وَإِذَا سَأَلْتُمُوهُنَّ مَتَاعًا فَاسْأَلُوهُنَّ مِنْ وَرَاءِ حِجَابٍ ذَلِكُمْ أَطْهَرُ لِقُلُوبِكُمْ وَقُلُوبِهِنَّ وَمَا كَانَ لَكُمْ أَنْ تُؤْذُوا رَسُولَ اللَّهِ وَلا أَنْ تَنْكِحُوا أَزْوَاجَهُ مِنْ بَعْدِهِ أَبَدًا إِنَّ ذَلِكُمْ كَانَ عِنْدَ اللَّهِ عَظِيمًا}
INSERT INTO [b9617] VALUES (3532,'«فإذا طعمتم فانتشروا» معطوفة على جملة الشرط المتقدمة، وجملة «طعمتم» مضاف إليه. قوله «مستأنسين» : اسم معطوف على «غير» ، والجار متعلق بـ «مستأنسين» أي: لا تدخلوها غير ناظرين، ولا مستأنسين، وجملة «إن ذلكم ... » معترضة، جملة «والله لا يستحيي» معطوفة على جملة «إن ذلكم كان» ، جملة «وإذا سألتموهن فاسألوهنَّ» معطوفة على جملة الشرط المتقدمة، و «متاعا» مفعول ثان، وجملة «ذلكم أطهر» مستأنفة، والجار «لقلوبكم» متعلق بأطهر. جملة «وما كان لكم أن تؤذوا» معطوفة على جملة «لا تدخلوا» جواب النداء، الجار «لكم» متعلق بخبر كان، والمصدر «أن تؤذوا» اسم كان، والمصدر الثاني المؤول «أن تنكحوا» معطوف على المصدر المتقدم، وجملة «إن ذلكم كان» مستأنفة.','3','976',NULL,NULL,'0');
INSERT INTO [b9617] VALUES (3533,'<span data-type="title" id=toc-3513>٥٤ </span>- {كَانَ بِكُلِّ شَيْءٍ عَلِيمًا}
INSERT INTO [b9617] VALUES (3534,'<span data-type="title" id=toc-3514>٥٥ </span>- {لا جُنَاحَ عَلَيْهِنَّ فِي آبَائِهِنَّ وَلا أَبْنَائِهِنَّ وَلا إِخْوَانِهِنَّ وَلا أَبْنَاءِ إِخْوَانِهِنَّ وَلا أَبْنَاءِ أَخَوَاتِهِنَّ وَلا نِسَائِهِنَّ وَلا مَا مَلَكَتْ أَيْمَانُهُنَّ وَاتَّقِينَ اللَّهَ إِنَّ اللَّهَ كَانَ عَلَى كُلِّ شَيْءٍ شَهِيدًا}
INSERT INTO [b9617] VALUES (3535,'<span data-type="title" id=toc-3515>٥٦ </span>- {يَا أَيُّهَا الَّذِينَ آمَنُوا صَلُّوا عَلَيْهِ وَسَلِّمُوا تَسْلِيمًا}
INSERT INTO [b9617] VALUES (3536,'<span data-type="title" id=toc-3516>٥٧ </span>- {إِنَّ الَّذِينَ يُؤْذُونَ اللَّهَ وَرَسُولَهُ لَعَنَهُمُ اللَّهُ فِي الدُّنْيَا وَالآخِرَةِ}
INSERT INTO [b9617] VALUES (3537,'<span data-type="title" id=toc-3517>٥٨ </span>- {وَالَّذِينَ يُؤْذُونَ الْمُؤْمِنِينَ وَالْمُؤْمِنَاتِ بِغَيْرِ مَا اكْتَسَبُوا فَقَدِ احْتَمَلُوا بُهْتَانًا وَإِثْمًا مُبِينًا}
INSERT INTO [b9617] VALUES (3538,'<span data-type="title" id=toc-3518>٥٩ </span>- {يَا أَيُّهَا النَّبِيُّ قُلْ لأَزْوَاجِكَ وَبَنَاتِكَ وَنِسَاءِ الْمُؤْمِنِينَ يُدْنِينَ عَلَيْهِنَّ مِنْ جَلابِيبِهِنَّ ذَلِكَ أَدْنَى أَنْ يُعْرَفْنَ فَلا يُؤْذَيْنَ}
INSERT INTO [b9617] VALUES (3539,'<span data-type="title" id=toc-3519>٦٠ </span>- {لَئِنْ لَمْ يَنْتَهِ الْمُنَافِقُونَ وَالَّذِينَ فِي قُلُوبِهِمْ مَرَضٌ وَالْمُرْجِفُونَ فِي الْمَدِينَةِ لَنُغْرِيَنَّكَ بِهِمْ ثُمَّ لا يُجَاوِرُونَكَ فِيهَا إِلا قَلِيلا}
INSERT INTO [b9617] VALUES (3540,'<span data-type="title" id=toc-3520>٦١ </span>- {مَلْعُونِينَ أَيْنَمَا ثُقِفُوا أُخِذُوا وَقُتِّلُوا تَقْتِيلا} ⦗٩٧٨⦘
INSERT INTO [b9617] VALUES (3541,'<span data-type="title" id=toc-3521>٦٢ </span>- {سُنَّةَ اللَّهِ فِي الَّذِينَ خَلَوْا مِنْ قَبْلُ وَلَنْ تَجِدَ لِسُنَّةِ اللَّهِ تَبْدِيلا}
INSERT INTO [b9617] VALUES (3542,'<span data-type="title" id=toc-3522>٦٣ </span>- {يَسْأَلُكَ النَّاسُ عَنِ السَّاعَةِ قُلْ إِنَّمَا عِلْمُهَا عِنْدَ اللَّهِ وَمَا يُدْرِيكَ لَعَلَّ السَّاعَةَ تَكُونُ قَرِيبًا}
INSERT INTO [b9617] VALUES (3543,'<span data-type="title" id=toc-3523>٦٥ </span>- {خَالِدِينَ فِيهَا أَبَدًا لا يَجِدُونَ وَلِيًّا وَلا نَصِيرًا}
INSERT INTO [b9617] VALUES (3544,'<span data-type="title" id=toc-3524>٦٦ </span>- {يَوْمَ تُقَلَّبُ وُجُوهُهُمْ فِي النَّارِ يَقُولُونَ يَا لَيْتَنَا أَطَعْنَا اللَّهَ وَأَطَعْنَا الرَّسُولا}
INSERT INTO [b9617] VALUES (3545,'<span data-type="title" id=toc-3525>٦٧ </span>- {وَقَالُوا رَبَّنَا إِنَّا أَطَعْنَا سَادَتَنَا} ⦗٩٧٩⦘
INSERT INTO [b9617] VALUES (3546,'<span data-type="title" id=toc-3526>٦٨ </span>- {رَبَّنَا آتِهِمْ ضِعْفَيْنِ مِنَ الْعَذَابِ}
INSERT INTO [b9617] VALUES (3547,'<span data-type="title" id=toc-3527>٦٩ </span>- {وَكَانَ عِنْدَ اللَّهِ وَجِيهًا}
INSERT INTO [b9617] VALUES (3548,'<span data-type="title" id=toc-3528>٧٠ </span>- {وَقُولُوا قَوْلا سَدِيدًا}
INSERT INTO [b9617] VALUES (3549,'<span data-type="title" id=toc-3529>٧١ </span>- {يُصْلِحْ لَكُمْ أَعْمَالَكُمْ وَيَغْفِرْ لَكُمْ ذُنُوبَكُمْ وَمَنْ يُطِعِ اللَّهَ وَرَسُولَهُ فَقَدْ فَازَ فَوْزًا عَظِيمًا}
INSERT INTO [b9617] VALUES (3550,'<span data-type="title" id=toc-3530>٧٢ </span>- {فَأَبَيْنَ أَنْ يَحْمِلْنَهَا}
INSERT INTO [b9617] VALUES (3551,'<span data-type="title" id=toc-3531>٧٣ </span>- {لِيُعَذِّبَ اللَّهُ الْمُنَافِقِينَ وَالْمُنَافِقَاتِ وَالْمُشْرِكِينَ وَالْمُشْرِكَاتِ وَيَتُوبَ اللَّهُ عَلَى الْمُؤْمِنِينَ وَالْمُؤْمِنَاتِ وَكَانَ اللَّهُ غَفُورًا رَحِيمًا} ⦗٩٨٠⦘
INSERT INTO [b9617] VALUES (3552,'<span data-type="title" id=toc-3532>سورة سبأ</span>','3','981',NULL,NULL,'0');
INSERT INTO [b9617] VALUES (3553,'<span data-type="title" id=toc-3533>١ </span>- {الْحَمْدُ لِلَّهِ الَّذِي لَهُ مَا فِي السَّمَاوَاتِ وَمَا فِي الأَرْضِ وَلَهُ الْحَمْدُ فِي الآخِرَةِ وَهُوَ الْحَكِيمُ الْخَبِيرُ}
INSERT INTO [b9617] VALUES (3554,'<span data-type="title" id=toc-3534>٢ </span>- {يَعْلَمُ مَا يَلِجُ فِي الأَرْضِ وَمَا يَخْرُجُ مِنْهَا وَمَا يَنْزِلُ مِنَ السَّمَاءِ وَمَا يَعْرُجُ فِيهَا وَهُوَ الرَّحِيمُ الْغَفُورُ}
INSERT INTO [b9617] VALUES (3555,'<span data-type="title" id=toc-3535>٣ </span>- {قُلْ بَلَى وَرَبِّي لَتَأْتِيَنَّكُمْ عَالِمِ الْغَيْبِ لا يَعْزُبُ عَنْهُ مِثْقَالُ ذَرَّةٍ فِي السَّمَاوَاتِ وَلا فِي الأَرْضِ وَلا أَصْغَرُ مِنْ ذَلِكَ وَلا أَكْبَرُ إِلا فِي كِتَابٍ مُبِينٍ}
INSERT INTO [b9617] VALUES (3556,'<span data-type="title" id=toc-3536>٤ </span>- {لِيَجْزِيَ الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ أُولَئِكَ لَهُمْ مَغْفِرَةٌ وَرِزْقٌ كَرِيمٌ}
INSERT INTO [b9617] VALUES (3557,'<span data-type="title" id=toc-3537>٥ </span>- {وَالَّذِينَ سَعَوْا فِي آيَاتِنَا مُعَاجِزِينَ أُولَئِكَ لَهُمْ عَذَابٌ مِنْ رِجْزٍ أَلِيمٌ}
INSERT INTO [b9617] VALUES (3558,'<span data-type="title" id=toc-3538>٦ </span>- {وَيَرَى الَّذِينَ أُوتُوا الْعِلْمَ الَّذِي أُنْزِلَ إِلَيْكَ مِنْ رَبِّكَ هُوَ الْحَقَّ وَيَهْدِي إِلَى صِرَاطِ الْعَزِيزِ الْحَمِيدِ}
INSERT INTO [b9617] VALUES (3559,'<span data-type="title" id=toc-3539>٧ </span>- {هَلْ نَدُلُّكُمْ عَلَى رَجُلٍ يُنَبِّئُكُمْ إِذَا مُزِّقْتُمْ كُلَّ مُمَزَّقٍ إِنَّكُمْ لَفِي خَلْقٍ جَدِيدٍ}
INSERT INTO [b9617] VALUES (3560,'<span data-type="title" id=toc-3540>٨ </span>- {أَفْتَرَى عَلَى اللَّهِ كَذِبًا أَمْ بِهِ جِنَّةٌ بَلِ الَّذِينَ لا يُؤْمِنُونَ بِالآخِرَةِ فِي الْعَذَابِ وَالضَّلالِ الْبَعِيدِ}
INSERT INTO [b9617] VALUES (3561,'<span data-type="title" id=toc-3541>٩ </span>- {أَفَلَمْ يَرَوْا إِلَى مَا بَيْنَ أَيْدِيهِمْ وَمَا خَلْفَهُمْ مِنَ السَّمَاءِ وَالأَرْضِ إِنْ نَشَأْ نَخْسِفْ بِهِمُ الأَرْضَ أَوْ نُسْقِطْ عَلَيْهِمْ كِسَفًا مِنَ السَّمَاءِ إِنَّ فِي ذَلِكَ لآيَةً لِكُلِّ عَبْدٍ مُنِيبٍ}
INSERT INTO [b9617] VALUES (3562,'<span data-type="title" id=toc-3542>١٠ </span>- {وَلَقَدْ آتَيْنَا دَاوُدَ مِنَّا فَضْلا يَا جِبَالُ أَوِّبِي مَعَهُ وَالطَّيْرَ وَأَلَنَّا لَهُ الْحَدِيدَ}
INSERT INTO [b9617] VALUES (3563,'<span data-type="title" id=toc-3543>١١ </span>- {أَنِ اعْمَلْ سَابِغَاتٍ وَقَدِّرْ فِي السَّرْدِ وَاعْمَلُوا صَالِحًا إِنِّي بِمَا تَعْمَلُونَ بَصِيرٌ}
INSERT INTO [b9617] VALUES (3564,'<span data-type="title" id=toc-3544>١٢ </span>- {وَلِسُلَيْمَانَ الرِّيحَ غُدُوُّهَا شَهْرٌ وَرَوَاحُهَا شَهْرٌ وَأَسَلْنَا لَهُ عَيْنَ الْقِطْرِ وَمِنَ الْجِنِّ مَنْ يَعْمَلُ بَيْنَ يَدَيْهِ بِإِذْنِ رَبِّهِ وَمَنْ يَزِغْ مِنْهُمْ عَنْ أَمْرِنَا نُذِقْهُ مِنْ عَذَابِ السَّعِيرِ}
INSERT INTO [b9617] VALUES (3565,'<span data-type="title" id=toc-3545>١٣ </span>- {يَعْمَلُونَ لَهُ مَا يَشَاءُ مِنْ مَحَارِيبَ وَتَمَاثِيلَ وَجِفَانٍ كَالْجَوَابِ وَقُدُورٍ رَاسِيَاتٍ اعْمَلُوا آلَ دَاوُدَ شُكْرًا وَقَلِيلٌ مِنْ عِبَادِيَ الشَّكُورُ} ⦗٩٨٥⦘
INSERT INTO [b9617] VALUES (3566,'<span data-type="title" id=toc-3546>١٤ </span>- {فَلَمَّا قَضَيْنَا عَلَيْهِ الْمَوْتَ مَا دَلَّهُمْ عَلَى مَوْتِهِ إِلا دَابَّةُ الأَرْضِ تَأْكُلُ مِنْسَأَتَهُ فَلَمَّا خَرَّ تَبَيَّنَتِ الْجِنُّ أَنْ لَوْ كَانُوا يَعْلَمُونَ الْغَيْبَ مَا لَبِثُوا فِي الْعَذَابِ الْمُهِينِ}
INSERT INTO [b9617] VALUES (3567,'<span data-type="title" id=toc-3547>١٥ </span>- {لَقَدْ كَانَ لِسَبَإٍ فِي مَسْكَنِهِمْ آيَةٌ جَنَّتَانِ عَنْ يَمِينٍ وَشِمَالٍ كُلُوا مِنْ رِزْقِ رَبِّكُمْ وَاشْكُرُوا لَهُ بَلْدَةٌ طَيِّبَةٌ وَرَبٌّ غَفُورٌ}
INSERT INTO [b9617] VALUES (3568,'<span data-type="title" id=toc-3548>١٦ </span>- {فَأَعْرَضُوا فَأَرْسَلْنَا عَلَيْهِمْ سَيْلَ الْعَرِمِ وَبَدَّلْنَاهُمْ بِجَنَّتَيْهِمْ جَنَّتَيْنِ ذَوَاتَيْ أُكُلٍ خَمْطٍ وَأَثْلٍ وَشَيْءٍ مِنْ سِدْرٍ قَلِيلٍ}
INSERT INTO [b9617] VALUES (3569,'<span data-type="title" id=toc-3549>١٧ </span>- {ذَلِكَ جَزَيْنَاهُمْ بِمَا كَفَرُوا وَهَلْ نُجَازِي إِلا الْكَفُورَ}
INSERT INTO [b9617] VALUES (3570,'<span data-type="title" id=toc-3550>١٨ </span>- {وَجَعَلْنَا بَيْنَهُمْ وَبَيْنَ الْقُرَى الَّتِي بَارَكْنَا فِيهَا قُرًى ظَاهِرَةً وَقَدَّرْنَا فِيهَا السَّيْرَ سِيرُوا فِيهَا لَيَالِيَ وَأَيَّامًا آمِنِينَ}
INSERT INTO [b9617] VALUES (3571,'<span data-type="title" id=toc-3551>١٩ </span>- {فَقَالُوا رَبَّنَا بَاعِدْ بَيْنَ أَسْفَارِنَا وَظَلَمُوا أَنْفُسَهُمْ فَجَعَلْنَاهُمْ أَحَادِيثَ وَمَزَّقْنَاهُمْ كُلَّ ⦗٩٨٧⦘ مُمَزَّقٍ إِنَّ فِي ذَلِكَ لآيَاتٍ لِكُلِّ صَبَّارٍ شَكُورٍ}
INSERT INTO [b9617] VALUES (3572,'<span data-type="title" id=toc-3552>٢٠ </span>- {وَلَقَدْ صَدَّقَ عَلَيْهِمْ إِبْلِيسُ ظَنَّهُ فَاتَّبَعُوهُ إِلا فَرِيقًا مِنَ الْمُؤْمِنِينَ}
INSERT INTO [b9617] VALUES (3573,'<span data-type="title" id=toc-3553>٢١ </span>- {وَمَا كَانَ لَهُ عَلَيْهِمْ مِنْ سُلْطَانٍ إِلا لِنَعْلَمَ مَنْ يُؤْمِنُ بِالآخِرَةِ مِمَّنْ هُوَ مِنْهَا فِي شَكٍّ وَرَبُّكَ عَلَى كُلِّ شَيْءٍ حَفِيظٌ}
INSERT INTO [b9617] VALUES (3574,'<span data-type="title" id=toc-3554>٢٢ </span>- {قُلِ ادْعُوا الَّذِينَ زَعَمْتُمْ مِنْ دُونِ اللَّهِ لا يَمْلِكُونَ مِثْقَالَ ذَرَّةٍ فِي السَّمَاوَاتِ وَلا فِي الأَرْضِ وَمَا لَهُمْ فِيهِمَا مِنْ شِرْكٍ وَمَا لَهُ مِنْهُمْ مِنْ ظَهِيرٍ}
INSERT INTO [b9617] VALUES (3575,'<span data-type="title" id=toc-3555>٢٣ </span>- {وَلا تَنْفَعُ الشَّفَاعَةُ عِنْدَهُ إِلا لِمَنْ أَذِنَ لَهُ حَتَّى إِذَا فُزِّعَ عَنْ قُلُوبِهِمْ قَالُوا مَاذَا قَالَ رَبُّكُمْ قَالُوا الْحَقَّ وَهُوَ الْعَلِيُّ الْكَبِيرُ}
INSERT INTO [b9617] VALUES (3576,'<span data-type="title" id=toc-3556>٢٤ </span>- {قُلْ مَنْ يَرْزُقُكُمْ مِنَ السَّمَاوَاتِ وَالأَرْضِ قُلِ اللَّهُ وَإِنَّا أَوْ إِيَّاكُمْ لَعَلَى هُدًى أَوْ فِي ضَلالٍ مُبِينٍ}
INSERT INTO [b9617] VALUES (3577,'<span data-type="title" id=toc-3557>٢٥ </span>- {قُلْ لا تُسْأَلُونَ عَمَّا أَجْرَمْنَا} ⦗٩٨٩⦘
INSERT INTO [b9617] VALUES (3578,'<span data-type="title" id=toc-3558>٢٦ </span>- {وَهُوَ الْفَتَّاحُ الْعَلِيمُ}
INSERT INTO [b9617] VALUES (3579,'<span data-type="title" id=toc-3559>٢٧ </span>- {قُلْ أَرُونِيَ الَّذِينَ أَلْحَقْتُمْ بِهِ شُرَكَاءَ كَلا بَلْ هُوَ اللَّهُ الْعَزِيزُ الْحَكِيمُ}
INSERT INTO [b9617] VALUES (3580,'<span data-type="title" id=toc-3560>٢٨ </span>- {وَمَا أَرْسَلْنَاكَ إِلا كَافَّةً لِلنَّاسِ بَشِيرًا وَنَذِيرًا وَلَكِنَّ أَكْثَرَ النَّاسِ لا يَعْلَمُونَ}
INSERT INTO [b9617] VALUES (3581,'<span data-type="title" id=toc-3561>٢٩ </span>- {وَيَقُولُونَ مَتَى هَذَا الْوَعْدُ إِنْ كُنْتُمْ صَادِقِينَ}
INSERT INTO [b9617] VALUES (3582,'<span data-type="title" id=toc-3562>٣٠ </span>- {قُلْ لَكُمْ مِيعَادُ يَوْمٍ لا تَسْتَأْخِرُونَ عَنْهُ سَاعَةً وَلا تَسْتَقْدِمُونَ}
INSERT INTO [b9617] VALUES (3583,'<span data-type="title" id=toc-3563>٣١ </span>- {لَنْ نُؤْمِنَ بِهَذَا الْقُرْآنِ وَلا بِالَّذِي بَيْنَ يَدَيْهِ وَلَوْ تَرَى إِذِ الظَّالِمُونَ مَوْقُوفُونَ عِنْدَ رَبِّهِمْ يَرْجِعُ بَعْضُهُمْ إِلَى بَعْضٍ الْقَوْلَ يَقُولُ الَّذِينَ اسْتُضْعِفُوا لِلَّذِينَ اسْتَكْبَرُوا لَوْلا أَنْتُمْ لَكُنَّا مُؤْمِنِينَ}
INSERT INTO [b9617] VALUES (3584,'<span data-type="title" id=toc-3564>٣٢ </span>- {قَالَ الَّذِينَ اسْتَكْبَرُوا لِلَّذِينَ اسْتُضْعِفُوا أَنَحْنُ صَدَدْنَاكُمْ عَنِ الْهُدَى بَعْدَ إِذْ جَاءَكُمْ بَلْ كُنْتُمْ مُجْرِمِينَ}
INSERT INTO [b9617] VALUES (3585,'<span data-type="title" id=toc-3565>٣٣ </span>- {وَقَالَ الَّذِينَ اسْتُضْعِفُوا لِلَّذِينَ اسْتَكْبَرُوا بَلْ مَكْرُ اللَّيْلِ وَالنَّهَارِ إِذْ تَأْمُرُونَنَا أَنْ نَكْفُرَ بِاللَّهِ وَنَجْعَلَ لَهُ أَنْدَادًا وَأَسَرُّوا النَّدَامَةَ لَمَّا رَأَوُا الْعَذَابَ وَجَعَلْنَا الأَغْلالَ فِي أَعْنَاقِ الَّذِينَ كَفَرُوا هَلْ يُجْزَوْنَ إِلا مَا كَانُوا يَعْمَلُونَ} ⦗٩٩١⦘
INSERT INTO [b9617] VALUES (3586,'<span data-type="title" id=toc-3566>٣٤ </span>- {وَمَا أَرْسَلْنَا فِي قَرْيَةٍ مِنْ نَذِيرٍ إِلا قَالَ مُتْرَفُوهَا إِنَّا بِمَا أُرْسِلْتُمْ بِهِ كَافِرُونَ}
INSERT INTO [b9617] VALUES (3587,'<span data-type="title" id=toc-3567>٣٥ </span>- {وَقَالُوا نَحْنُ أَكْثَرُ أَمْوَالا وَأَوْلادًا وَمَا نَحْنُ بِمُعَذَّبِينَ}
INSERT INTO [b9617] VALUES (3588,'<span data-type="title" id=toc-3568>٣٦ </span>- {قُلْ إِنَّ رَبِّي يَبْسُطُ الرِّزْقَ لِمَنْ يَشَاءُ وَيَقْدِرُ وَلَكِنَّ أَكْثَرَ النَّاسِ لا يَعْلَمُونَ}
INSERT INTO [b9617] VALUES (3589,'<span data-type="title" id=toc-3569>٣٧ </span>- {وَمَا أَمْوَالُكُمْ وَلا أَوْلادُكُمْ بِالَّتِي تُقَرِّبُكُمْ عِنْدَنَا زُلْفَى إِلا مَنْ آمَنَ وَعَمِلَ صَالِحًا ⦗٩٩٢⦘ فَأُولَئِكَ لَهُمْ جَزَاءُ الضِّعْفِ بِمَا عَمِلُوا وَهُمْ فِي الْغُرُفَاتِ آمِنُونَ}
INSERT INTO [b9617] VALUES (3590,'<span data-type="title" id=toc-3570>٣٨ </span>- {وَالَّذِينَ يَسْعَوْنَ فِي آيَاتِنَا مُعَاجِزِينَ أُولَئِكَ فِي الْعَذَابِ مُحْضَرُونَ}
INSERT INTO [b9617] VALUES (3591,'<span data-type="title" id=toc-3571>٣٩ </span>- {قُلْ إِنَّ رَبِّي يَبْسُطُ الرِّزْقَ لِمَنْ يَشَاءُ مِنْ عِبَادِهِ وَيَقْدِرُ لَهُ وَمَا أَنْفَقْتُمْ مِنْ شَيْءٍ فَهُوَ يُخْلِفُهُ وَهُوَ خَيْرُ الرَّازِقِينَ}
INSERT INTO [b9617] VALUES (3592,'<span data-type="title" id=toc-3572>٤٠ </span>- {وَيَوْمَ يَحْشُرُهُمْ جَمِيعًا ثُمَّ يَقُولُ لِلْمَلائِكَةِ أَهَؤُلاءِ إِيَّاكُمْ كَانُوا يَعْبُدُونَ} ⦗٩٩٣⦘
INSERT INTO [b9617] VALUES (3593,'<span data-type="title" id=toc-3573>٤١ </span>- {قَالُوا سُبْحَانَكَ أَنْتَ وَلِيُّنَا مِنْ دُونِهِمْ بَلْ كَانُوا يَعْبُدُونَ الْجِنَّ أَكْثَرُهُمْ بِهِمْ مُؤْمِنُونَ}
INSERT INTO [b9617] VALUES (3594,'<span data-type="title" id=toc-3574>٤٢ </span>- {فَالْيَوْمَ لا يَمْلِكُ بَعْضُكُمْ لِبَعْضٍ نَفْعًا وَلا ضَرًّا وَنَقُولُ لِلَّذِينَ ظَلَمُوا ذُوقُوا عَذَابَ النَّارِ الَّتِي كُنْتُمْ بِهَا تُكَذِّبُونَ}
INSERT INTO [b9617] VALUES (3595,'<span data-type="title" id=toc-3575>٤٣ </span>- {وَإِذَا تُتْلَى عَلَيْهِمْ آيَاتُنَا بَيِّنَاتٍ قَالُوا مَا هَذَا إِلا رَجُلٌ يُرِيدُ أَنْ يَصُدَّكُمْ عَمَّا كَانَ يَعْبُدُ آبَاؤُكُمْ وَقَالُوا مَا هَذَا إِلا إِفْكٌ مُفْتَرًى وَقَالَ الَّذِينَ كَفَرُوا لِلْحَقِّ لَمَّا جَاءَهُمْ إِنْ هَذَا إِلا سِحْرٌ مُبِينٌ}
INSERT INTO [b9617] VALUES (3596,'<span data-type="title" id=toc-3576>٤٤ </span>- {وَمَا آتَيْنَاهُمْ مِنْ كُتُبٍ يَدْرُسُونَهَا وَمَا أَرْسَلْنَا إِلَيْهِمْ قَبْلَكَ مِنْ نَذِيرٍ}
INSERT INTO [b9617] VALUES (3597,'<span data-type="title" id=toc-3577>٤٥ </span>- {وَكَذَّبَ الَّذِينَ مِنْ قَبْلِهِمْ وَمَا بَلَغُوا مِعْشَارَ مَا آتَيْنَاهُمْ فَكَذَّبُوا رُسُلِي فَكَيْفَ كَانَ نَكِيرِ}
INSERT INTO [b9617] VALUES (3598,'<span data-type="title" id=toc-3578>٤٦ </span>- {قُلْ إِنَّمَا أَعِظُكُمْ بِوَاحِدَةٍ أَنْ تَقُومُوا لِلَّهِ مَثْنَى وَفُرَادَى ثُمَّ تَتَفَكَّرُوا مَا بِصَاحِبِكُمْ مِنْ جِنَّةٍ إِنْ هُوَ إِلا نَذِيرٌ لَكُمْ بَيْنَ يَدَيْ عَذَابٍ شَدِيدٍ}
INSERT INTO [b9617] VALUES (3599,'<span data-type="title" id=toc-3579>٤٧ </span>- {قُلْ مَا سَأَلْتُكُمْ مِنْ أَجْرٍ فَهُوَ لَكُمْ إِنْ أَجْرِيَ إِلا عَلَى اللَّهِ وَهُوَ عَلَى كُلِّ شَيْءٍ شَهِيدٌ}
INSERT INTO [b9617] VALUES (3600,'<span data-type="title" id=toc-3580>٤٨ </span>- {قُلْ إِنَّ رَبِّي يَقْذِفُ بِالْحَقِّ عَلامُ الْغُيُوبِ}
INSERT INTO [b9617] VALUES (3601,'<span data-type="title" id=toc-3581>٤٩ </span>- {قُلْ جَاءَ الْحَقُّ وَمَا يُبْدِئُ الْبَاطِلُ وَمَا يُعِيدُ}
INSERT INTO [b9617] VALUES (3602,'<span data-type="title" id=toc-3582>٥٠ </span>- {قُلْ إِنْ ضَلَلْتُ فَإِنَّمَا أَضِلُّ عَلَى نَفْسِي وَإِنِ اهْتَدَيْتُ فَبِمَا يُوحِي إِلَيَّ رَبِّي إِنَّهُ سَمِيعٌ قَرِيبٌ}
INSERT INTO [b9617] VALUES (3603,'<span data-type="title" id=toc-3583>٥١ </span>- {وَلَوْ تَرَى إِذْ فَزِعُوا فَلا فَوْتَ وَأُخِذُوا مِنْ مَكَانٍ قَرِيبٍ}
INSERT INTO [b9617] VALUES (3604,'<span data-type="title" id=toc-3584>٥٢ </span>- {وَقَالُوا آمَنَّا بِهِ وَأَنَّى لَهُمُ التَّنَاوُشُ مِنْ مَكَانٍ بَعِيدٍ}
INSERT INTO [b9617] VALUES (3605,'<span data-type="title" id=toc-3585>٥٣ </span>- {وَقَدْ كَفَرُوا بِهِ مِنْ قَبْلُ وَيَقْذِفُونَ بِالْغَيْبِ}
INSERT INTO [b9617] VALUES (3606,'<span data-type="title" id=toc-3586>٥٤ </span>- {وَحِيلَ بَيْنَهُمْ وَبَيْنَ مَا يَشْتَهُونَ كَمَا فُعِلَ بِأَشْيَاعِهِمْ مِنْ قَبْلُ}
INSERT INTO [b9617] VALUES (3607,'<span data-type="title" id=toc-3587>سورة فاطر</span>','3','997',NULL,NULL,'0');
INSERT INTO [b9617] VALUES (3608,'<span data-type="title" id=toc-3588>١ </span>- {الْحَمْدُ لِلَّهِ فَاطِرِ السَّمَاوَاتِ وَالأَرْضِ جَاعِلِ الْمَلائِكَةِ رُسُلا أُولِي أَجْنِحَةٍ مَثْنَى وَثُلاثَ وَرُبَاعَ يَزِيدُ فِي الْخَلْقِ مَا يَشَاءُ إِنَّ اللَّهَ عَلَى كُلِّ شَيْءٍ قَدِيرٌ}
INSERT INTO [b9617] VALUES (3609,'<span data-type="title" id=toc-3589>٢ </span>- {مَا يَفْتَحِ اللَّهُ لِلنَّاسِ مِنْ رَحْمَةٍ فَلا مُمْسِكَ لَهَا وَمَا يُمْسِكْ فَلا مُرْسِلَ لَهُ مِنْ بَعْدِهِ وَهُوَ الْعَزِيزُ الْحَكِيمُ}
INSERT INTO [b9617] VALUES (3610,'<span data-type="title" id=toc-3590>٣ </span>- {يَا أَيُّهَا النَّاسُ اذْكُرُوا نِعْمَتَ اللَّهِ عَلَيْكُمْ هَلْ مِنْ خَالِقٍ غَيْرُ اللَّهِ يَرْزُقُكُمْ مِنَ السَّمَاءِ وَالأَرْضِ لا إِلَهَ إِلا هُوَ فَأَنَّى تُؤْفَكُونَ}
INSERT INTO [b9617] VALUES (3611,'<span data-type="title" id=toc-3591>٤ </span>- {وَإِنْ يُكَذِّبُوكَ فَقَدْ كُذِّبَتْ رُسُلٌ مِنْ قَبْلِكَ وَإِلَى اللَّهِ تُرْجَعُ الأُمُورُ}
INSERT INTO [b9617] VALUES (3612,'<span data-type="title" id=toc-3592>٥ </span>- {يَا أَيُّهَا النَّاسُ إِنَّ وَعْدَ اللَّهِ حَقٌّ فَلا تَغُرَّنَّكُمُ الْحَيَاةُ الدُّنْيَا}
INSERT INTO [b9617] VALUES (3613,'<span data-type="title" id=toc-3593>٦ </span>- {إِنَّ الشَّيْطَانَ لَكُمْ عَدُوٌّ فَاتَّخِذُوهُ عَدُوًّا}
INSERT INTO [b9617] VALUES (3614,'<span data-type="title" id=toc-3594>٧ </span>- {الَّذِينَ كَفَرُوا لَهُمْ عَذَابٌ شَدِيدٌ وَالَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ لَهُمْ مَغْفِرَةٌ}
INSERT INTO [b9617] VALUES (3615,'<span data-type="title" id=toc-3595>٨ </span>- {أَفَمَنْ زُيِّنَ لَهُ سُوءُ عَمَلِهِ فَرَآهُ حَسَنًا فَإِنَّ اللَّهَ يُضِلُّ مَنْ يَشَاءُ وَيَهْدِي مَنْ يَشَاءُ فَلا تَذْهَبْ نَفْسُكَ عَلَيْهِمْ حَسَرَاتٍ إِنَّ اللَّهَ عَلِيمٌ بِمَا يَصْنَعُونَ}
INSERT INTO [b9617] VALUES (3616,'<span data-type="title" id=toc-3596>٩ </span>- {وَاللَّهُ الَّذِي أَرْسَلَ الرِّيَاحَ فَتُثِيرُ سَحَابًا فَسُقْنَاهُ إِلَى بَلَدٍ مَيِّتٍ فَأَحْيَيْنَا بِهِ الأَرْضَ بَعْدَ مَوْتِهَا كَذَلِكَ النُّشُورُ}
INSERT INTO [b9617] VALUES (3617,'<span data-type="title" id=toc-3597>١٠ </span>- {مَنْ كَانَ يُرِيدُ الْعِزَّةَ فَلِلَّهِ الْعِزَّةُ جَمِيعًا إِلَيْهِ يَصْعَدُ الْكَلِمُ الطَّيِّبُ وَالْعَمَلُ الصَّالِحُ يَرْفَعُهُ وَالَّذِينَ يَمْكُرُونَ السَّيِّئَاتِ لَهُمْ عَذَابٌ شَدِيدٌ وَمَكْرُ أُولَئِكَ هُوَ يَبُورُ}
INSERT INTO [b9617] VALUES (3618,'<span data-type="title" id=toc-3598>١١ </span>- {وَاللَّهُ خَلَقَكُمْ مِنْ تُرَابٍ ثُمَّ مِنْ نُطْفَةٍ ثُمَّ جَعَلَكُمْ أَزْوَاجًا وَمَا تَحْمِلُ مِنْ أُنْثَى وَلا تَضَعُ إِلا بِعِلْمِهِ وَمَا يُعَمَّرُ مِنْ مُعَمَّرٍ وَلا يُنْقَصُ مِنْ عُمُرِهِ إِلا فِي كِتَابٍ إِنَّ ذَلِكَ عَلَى اللَّهِ يَسِيرٌ}
INSERT INTO [b9617] VALUES (3619,'<span data-type="title" id=toc-3599>١٢ </span>- {وَمَا يَسْتَوِي الْبَحْرَانِ هَذَا عَذْبٌ فُرَاتٌ سَائِغٌ شَرَابُهُ وَهَذَا مِلْحٌ أُجَاجٌ وَمِنْ كُلٍّ تَأْكُلُونَ لَحْمًا طَرِيًّا وَتَسْتَخْرِجُونَ حِلْيَةً تَلْبَسُونَهَا وَتَرَى الْفُلْكَ فِيهِ مَوَاخِرَ لِتَبْتَغُوا مِنْ فَضْلِهِ وَلَعَلَّكُمْ تَشْكُرُونَ}
INSERT INTO [b9617] VALUES (3620,'<span data-type="title" id=toc-3600>١٣ </span>- {يُولِجُ اللَّيْلَ فِي النَّهَارِ وَيُولِجُ النَّهَارَ فِي اللَّيْلِ وَسَخَّرَ الشَّمْسَ وَالْقَمَرَ كُلٌّ يَجْرِي لأَجَلٍ مُسَمًّى ذَلِكُمُ اللَّهُ رَبُّكُمْ لَهُ الْمُلْكُ وَالَّذِينَ تَدْعُونَ مِنْ دُونِهِ مَا يَمْلِكُونَ مِنْ قِطْمِيرٍ}
INSERT INTO [b9617] VALUES (3621,'<span data-type="title" id=toc-3601>١٤ </span>- {إِنْ تَدْعُوهُمْ لا يَسْمَعُوا دُعَاءَكُمْ وَلَوْ سَمِعُوا مَا اسْتَجَابُوا لَكُمْ وَيَوْمَ الْقِيَامَةِ يَكْفُرُونَ بِشِرْكِكُمْ وَلا يُنَبِّئُكَ مِثْلُ خَبِيرٍ}
INSERT INTO [b9617] VALUES (3622,'<span data-type="title" id=toc-3602>١٥ </span>- {يَا أَيُّهَا النَّاسُ أَنْتُمُ الْفُقَرَاءُ إِلَى اللَّهِ وَاللَّهُ هُوَ الْغَنِيُّ الْحَمِيدُ}
INSERT INTO [b9617] VALUES (3623,'<span data-type="title" id=toc-3603>١٦ </span>- {إِنْ يَشَأْ يُذْهِبْكُمْ}
INSERT INTO [b9617] VALUES (3624,'<span data-type="title" id=toc-3604>١٧ </span>- {وَمَا ذَلِكَ عَلَى اللَّهِ بِعَزِيزٍ}
INSERT INTO [b9617] VALUES (3625,'<span data-type="title" id=toc-3605>١٨ </span>- {وَلا تَزِرُ وَازِرَةٌ وِزْرَ أُخْرَى وَإِنْ تَدْعُ مُثْقَلَةٌ إِلَى حِمْلِهَا لا يُحْمَلْ مِنْهُ شَيْءٌ وَلَوْ كَانَ ذَا قُرْبَى إِنَّمَا تُنْذِرُ الَّذِينَ يَخْشَوْنَ رَبَّهُمْ بِالْغَيْبِ وَأَقَامُوا الصَّلاةَ وَمَنْ تَزَكَّى فَإِنَّمَا يَتَزَكَّى لِنَفْسِهِ وَإِلَى اللَّهِ الْمَصِيرُ}
INSERT INTO [b9617] VALUES (3626,'<span data-type="title" id=toc-3606>١٩ </span>- {وَمَا يَسْتَوِي الأَعْمَى وَالْبَصِيرُ}
INSERT INTO [b9617] VALUES (3627,'<span data-type="title" id=toc-3607>٢٠ </span>- {وَلا الظُّلُمَاتُ وَلا النُّورُ}
INSERT INTO [b9617] VALUES (3628,'<span data-type="title" id=toc-3608>٢٢ </span>- {إِنَّ اللَّهَ يُسْمِعُ مَنْ يَشَاءُ وَمَا أَنْتَ بِمُسْمِعٍ مَنْ فِي الْقُبُورِ}
INSERT INTO [b9617] VALUES (3629,'<span data-type="title" id=toc-3609>٢٣ </span>- {إِنْ أَنْتَ إِلا نَذِيرٌ}
INSERT INTO [b9617] VALUES (3630,'<span data-type="title" id=toc-3610>٢٤ </span>- {إِنَّا أَرْسَلْنَاكَ بِالْحَقِّ بَشِيرًا وَنَذِيرًا وَإِنْ مِنْ أُمَّةٍ إِلا خَلا فِيهَا نَذِيرٌ}
INSERT INTO [b9617] VALUES (3631,'<span data-type="title" id=toc-3611>٢٥ </span>- {وَإِنْ يُكَذِّبُوكَ فَقَدْ كَذَّبَ الَّذِينَ مِنْ قَبْلِهِمْ جَاءَتْهُمْ رُسُلُهُمْ بِالْبَيِّنَاتِ وَبِالزُّبُرِ وَبِالْكِتَابِ الْمُنِيرِ}
INSERT INTO [b9617] VALUES (3632,'<span data-type="title" id=toc-3612>٢٦ </span>- {ثُمَّ أَخَذْتُ الَّذِينَ كَفَرُوا فَكَيْفَ كَانَ نَكِيرِ}
INSERT INTO [b9617] VALUES (3633,'<span data-type="title" id=toc-3613>٢٧ </span>- {أَلَمْ تَرَ أَنَّ اللَّهَ أَنْزَلَ مِنَ السَّمَاءِ مَاءً فَأَخْرَجْنَا بِهِ ثَمَرَاتٍ مُخْتَلِفًا أَلْوَانُهَا وَمِنَ الْجِبَالِ جُدَدٌ بِيضٌ وَحُمْرٌ مُخْتَلِفٌ أَلْوَانُهَا وَغَرَابِيبُ سُودٌ}
INSERT INTO [b9617] VALUES (3634,'<span data-type="title" id=toc-3614>٢٨ </span>- {وَمِنَ النَّاسِ وَالدَّوَابِّ وَالأَنْعَامِ مُخْتَلِفٌ أَلْوَانُهُ كَذَلِكَ إِنَّمَا يَخْشَى اللَّهَ مِنْ عِبَادِهِ الْعُلَمَاءُ} ⦗١٠٠٤⦘
INSERT INTO [b9617] VALUES (3635,'<span data-type="title" id=toc-3615>٢٩ </span>- {إِنَّ الَّذِينَ يَتْلُونَ كِتَابَ اللَّهِ وَأَقَامُوا الصَّلاةَ وَأَنْفَقُوا مِمَّا رَزَقْنَاهُمْ سِرًّا وَعَلانِيَةً يَرْجُونَ تِجَارَةً لَنْ تَبُورَ}
INSERT INTO [b9617] VALUES (3636,'<span data-type="title" id=toc-3616>٣٠ </span>- {لِيُوَفِّيَهُمْ أُجُورَهُمْ وَيَزِيدَهُمْ مِنْ فَضْلِهِ إِنَّهُ غَفُورٌ شَكُورٌ}
INSERT INTO [b9617] VALUES (3637,'<span data-type="title" id=toc-3617>٣١ </span>- {وَالَّذِي أَوْحَيْنَا إِلَيْكَ مِنَ الْكِتَابِ هُوَ الْحَقُّ مُصَدِّقًا لِمَا بَيْنَ يَدَيْهِ إِنَّ اللَّهَ بِعِبَادِهِ لَخَبِيرٌ بَصِيرٌ}
INSERT INTO [b9617] VALUES (3638,'<span data-type="title" id=toc-3618>٣٢ </span>- {ثُمَّ أَوْرَثْنَا الْكِتَابَ الَّذِينَ اصْطَفَيْنَا مِنْ عِبَادِنَا فَمِنْهُمْ ظَالِمٌ لِنَفْسِهِ وَمِنْهُمْ مُقْتَصِدٌ وَمِنْهُمْ سَابِقٌ بِالْخَيْرَاتِ بِإِذْنِ اللَّهِ ذَلِكَ هُوَ الْفَضْلُ الْكَبِيرُ}
INSERT INTO [b9617] VALUES (3639,'<span data-type="title" id=toc-3619>٣٣ </span>- {جَنَّاتُ عَدْنٍ يَدْخُلُونَهَا يُحَلَّوْنَ فِيهَا مِنْ أَسَاوِرَ مِنْ ذَهَبٍ وَلُؤْلُؤًا وَلِبَاسُهُمْ فِيهَا حَرِيرٌ}
INSERT INTO [b9617] VALUES (3640,'<span data-type="title" id=toc-3620>٣٤ </span>- {وَقَالُوا الْحَمْدُ لِلَّهِ الَّذِي أَذْهَبَ عَنَّا الْحَزَنَ إِنَّ رَبَّنَا لَغَفُورٌ شَكُورٌ}
INSERT INTO [b9617] VALUES (3641,'<span data-type="title" id=toc-3621>٣٥ </span>- {الَّذِي أَحَلَّنَا دَارَ الْمُقَامَةِ مِنْ فَضْلِهِ لا يَمَسُّنَا فِيهَا نَصَبٌ}
INSERT INTO [b9617] VALUES (3642,'<span data-type="title" id=toc-3622>٣٦ </span>- {وَالَّذِينَ كَفَرُوا لَهُمْ نَارُ جَهَنَّمَ لا يُقْضَى عَلَيْهِمْ فَيَمُوتُوا وَلا يُخَفَّفُ عَنْهُمْ مِنْ عَذَابِهَا كَذَلِكَ نَجْزِي كُلَّ كَفُورٍ}
INSERT INTO [b9617] VALUES (3643,'<span data-type="title" id=toc-3623>٣٧ </span>- {وَهُمْ يَصْطَرِخُونَ فِيهَا رَبَّنَا أَخْرِجْنَا نَعْمَلْ صَالِحًا غَيْرَ الَّذِي كُنَّا نَعْمَلُ أَوَلَمْ نُعَمِّرْكُمْ مَا يَتَذَكَّرُ فِيهِ مَنْ تَذَكَّرَ وَجَاءَكُمُ النَّذِيرُ فَذُوقُوا فَمَا لِلظَّالِمِينَ مِنْ نَصِيرٍ}
INSERT INTO [b9617] VALUES (3644,'<span data-type="title" id=toc-3624>٣٨ </span>- {إِنَّهُ عَلِيمٌ بِذَاتِ الصُّدُورِ}
INSERT INTO [b9617] VALUES (3645,'<span data-type="title" id=toc-3625>٣٩ </span>- {هُوَ الَّذِي جَعَلَكُمْ خَلائِفَ فِي الأَرْضِ فَمَنْ كَفَرَ فَعَلَيْهِ كُفْرُهُ وَلا يَزِيدُ الْكَافِرِينَ كُفْرُهُمْ عِنْدَ رَبِّهِمْ إِلا مَقْتًا وَلا يَزِيدُ الْكَافِرِينَ كُفْرُهُمْ إِلا خَسَارًا}
INSERT INTO [b9617] VALUES (3646,'<span data-type="title" id=toc-3626>٤٠ </span>- {قُلْ أَرَأَيْتُمْ شُرَكَاءَكُمُ الَّذِينَ تَدْعُونَ مِنْ دُونِ اللَّهِ أَرُونِي مَاذَا خَلَقُوا مِنَ الأَرْضِ أَمْ لَهُمْ شِرْكٌ فِي السَّمَاوَاتِ أَمْ آتَيْنَاهُمْ كِتَابًا فَهُمْ عَلَى بَيِّنَتٍ مِنْهُ بَلْ إِنْ يَعِدُ الظَّالِمُونَ بَعْضُهُمْ بَعْضًا إِلا غُرُورًا}
INSERT INTO [b9617] VALUES (3647,'<span data-type="title" id=toc-3627>٤١ </span>- {إِنَّ اللَّهَ يُمْسِكُ السَّمَاوَاتِ وَالأَرْضَ أَنْ تَزُولا وَلَئِنْ زَالَتَا إِنْ أَمْسَكَهُمَا مِنْ أَحَدٍ مِنْ بَعْدِهِ إِنَّهُ كَانَ حَلِيمًا غَفُورًا}
INSERT INTO [b9617] VALUES (3648,'<span data-type="title" id=toc-3628>٤٢ </span>- {وَأَقْسَمُوا بِاللَّهِ جَهْدَ أَيْمَانِهِمْ لَئِنْ جَاءَهُمْ نَذِيرٌ لَيَكُونُنَّ أَهْدَى مِنْ إِحْدَى الأُمَمِ فَلَمَّا جَاءَهُمْ نَذِيرٌ مَا زَادَهُمْ إِلا نُفُورًا}
INSERT INTO [b9617] VALUES (3649,'<span data-type="title" id=toc-3629>٤٣ </span>- {اسْتِكْبَارًا فِي الأَرْضِ وَمَكْرَ السَّيِّئِ وَلا يَحِيقُ الْمَكْرُ السَّيِّئُ إِلا بِأَهْلِهِ فَهَلْ يَنْظُرُونَ إِلا سُنَّتَ الأَوَّلِينَ فَلَنْ تَجِدَ لِسُنَّتِ اللَّهِ تَبْدِيلا وَلَنْ تَجِدَ لِسُنَّتِ اللَّهِ تَحْوِيلا}
INSERT INTO [b9617] VALUES (3650,'<span data-type="title" id=toc-3630>٤٤ </span>- {أَوَلَمْ يَسِيرُوا فِي الأَرْضِ فَيَنْظُرُوا كَيْفَ كَانَ عَاقِبَةُ الَّذِينَ مِنْ قَبْلِهِمْ وَكَانُوا أَشَدَّ مِنْهُمْ قُوَّةً وَمَا كَانَ اللَّهُ لِيُعْجِزَهُ مِنْ شَيْءٍ فِي السَّمَاوَاتِ وَلا فِي الأَرْضِ}
INSERT INTO [b9617] VALUES (3651,'<span data-type="title" id=toc-3631>٤٥ </span>- {وَلَوْ يُؤَاخِذُ اللَّهُ النَّاسَ بِمَا كَسَبُوا مَا تَرَكَ عَلَى ظَهْرِهَا مِنْ دَابَّةٍ وَلَكِنْ يُؤَخِّرُهُمْ إِلَى أَجَلٍ مُسَمًّى فَإِذَا جَاءَ أَجَلُهُمْ فَإِنَّ اللَّهَ كَانَ بِعِبَادِهِ بَصِيرًا}
INSERT INTO [b9617] VALUES (3652,'<span data-type="title" id=toc-3632>سورة يس</span>','3','1011',NULL,NULL,'0');
INSERT INTO [b9617] VALUES (3653,'<span data-type="title" id=toc-3633>١ </span>- {يس}
INSERT INTO [b9617] VALUES (3654,'<span data-type="title" id=toc-3634>٢ </span>- {وَالْقُرْآنِ الْحَكِيمِ}
INSERT INTO [b9617] VALUES (3655,'<span data-type="title" id=toc-3635>٣ </span>- {إِنَّكَ لَمِنَ الْمُرْسَلِينَ}
INSERT INTO [b9617] VALUES (3656,'<span data-type="title" id=toc-3636>٤ </span>- {عَلَى صِرَاطٍ مُسْتَقِيمٍ}
INSERT INTO [b9617] VALUES (3657,'<span data-type="title" id=toc-3637>٥ </span>- {تَنْزِيلَ الْعَزِيزِ الرَّحِيمِ}
INSERT INTO [b9617] VALUES (3658,'<span data-type="title" id=toc-3638>٦ </span>- {لِتُنْذِرَ قَوْمًا مَا أُنْذِرَ آبَاؤُهُمْ فَهُمْ غَافِلُونَ}
INSERT INTO [b9617] VALUES (3659,'<span data-type="title" id=toc-3639>٧ </span>- {لَقَدْ حَقَّ الْقَوْلُ عَلَى أَكْثَرِهِمْ فَهُمْ لا يُؤْمِنُونَ}
INSERT INTO [b9617] VALUES (3660,'<span data-type="title" id=toc-3640>٨ </span>- {إِنَّا جَعَلْنَا فِي أَعْنَاقِهِمْ أَغْلالا فَهِيَ إِلَى الأَذْقَانِ فَهُمْ مُقْمَحُونَ}
INSERT INTO [b9617] VALUES (3661,'<span data-type="title" id=toc-3641>٩ </span>- {وَجَعَلْنَا مِنْ بَيْنِ أَيْدِيهِمْ سَدًّا وَمِنْ خَلْفِهِمْ سَدًّا فَأَغْشَيْنَاهُمْ فَهُمْ لا يُبْصِرُونَ}
INSERT INTO [b9617] VALUES (3662,'<span data-type="title" id=toc-3642>١٠ </span>- {وَسَوَاءٌ عَلَيْهِمْ أَأَنْذَرْتَهُمْ أَمْ لَمْ تُنْذِرْهُمْ لا يُؤْمِنُونَ}
INSERT INTO [b9617] VALUES (3663,'<span data-type="title" id=toc-3643>١١ </span>- {إِنَّمَا تُنْذِرُ مَنِ اتَّبَعَ الذِّكْرَ وَخَشِيَ الرَّحْمَنَ بِالْغَيْبِ فَبَشِّرْهُ بِمَغْفِرَةٍ وَأَجْرٍ كَرِيمٍ}
INSERT INTO [b9617] VALUES (3664,'<span data-type="title" id=toc-3644>١٢ </span>- {إِنَّا نَحْنُ نُحْيِي الْمَوْتَى وَنَكْتُبُ مَا قَدَّمُوا وَآثَارَهُمْ وَكُلَّ شَيْءٍ أحْصَيْنَاهُ فِي إِمَامٍ مُبِينٍ}
INSERT INTO [b9617] VALUES (3665,'<span data-type="title" id=toc-3645>١٣ </span>- {وَاضْرِبْ لَهُمْ مَثَلا أَصْحَابَ الْقَرْيَةِ إِذْ جَاءَهَا الْمُرْسَلُونَ}
INSERT INTO [b9617] VALUES (3666,'<span data-type="title" id=toc-3646>١٤ </span>- {إِذْ أَرْسَلْنَا إِلَيْهِمُ اثْنَيْنِ فَكَذَّبُوهُمَا فَعَزَّزْنَا بِثَالِثٍ فَقَالُوا إِنَّا إِلَيْكُمْ مُرْسَلُونَ}
INSERT INTO [b9617] VALUES (3667,'<span data-type="title" id=toc-3647>١٥ </span>- {قَالُوا مَا أَنْتُمْ إِلا بَشَرٌ مِثْلُنَا وَمَا أَنْزَلَ الرَّحْمَنُ مِنْ شَيْءٍ إِنْ أَنْتُمْ إِلا تَكْذِبُونَ}
INSERT INTO [b9617] VALUES (3668,'<span data-type="title" id=toc-3648>١٦ </span>- {قَالُوا رَبُّنَا يَعْلَمُ إِنَّا إِلَيْكُمْ لَمُرْسَلُونَ}
INSERT INTO [b9617] VALUES (3669,'<span data-type="title" id=toc-3649>١٧ </span>- {وَمَا عَلَيْنَا إِلا الْبَلاغُ الْمُبِينُ}
INSERT INTO [b9617] VALUES (3670,'<span data-type="title" id=toc-3650>١٨ </span>- {قَالُوا إِنَّا تَطَيَّرْنَا بِكُمْ لَئِنْ لَمْ تَنْتَهُوا لَنَرْجُمَنَّكُمْ وَلَيَمَسَّنَّكُمْ مِنَّا عَذَابٌ أَلِيمٌ}
INSERT INTO [b9617] VALUES (3671,'<span data-type="title" id=toc-3651>١٩ </span>- {قَالُوا طَائِرُكُمْ مَعَكُمْ أَئِنْ ذُكِّرْتُمْ بَلْ أَنْتُمْ قَوْمٌ مُسْرِفُونَ}
INSERT INTO [b9617] VALUES (3672,'<span data-type="title" id=toc-3652>٢٠ </span>- {وَجَاءَ مِنْ أَقْصَى الْمَدِينَةِ رَجُلٌ يَسْعَى قَالَ يَا قَوْمِ اتَّبِعُوا الْمُرْسَلِينَ}
INSERT INTO [b9617] VALUES (3673,'<span data-type="title" id=toc-3653>٢١ </span>- {اتَّبِعُوا مَنْ لا يَسْأَلُكُمْ أَجْرًا وَهُمْ مُهْتَدُونَ}
INSERT INTO [b9617] VALUES (3674,'<span data-type="title" id=toc-3654>٢٢ </span>- {وَمَا لِيَ لا أَعْبُدُ الَّذِي فَطَرَنِي وَإِلَيْهِ تُرْجَعُونَ} ⦗١٠١٥⦘
INSERT INTO [b9617] VALUES (3675,'<span data-type="title" id=toc-3655>٢٣ </span>- {أَأَتَّخِذُ مِنْ دُونِهِ آلِهَةً إِنْ يُرِدْنِ الرَّحْمَنُ بِضُرٍّ لا تُغْنِ عَنِّي شَفَاعَتُهُمْ شَيْئًا وَلا يُنْقِذُونِ}
INSERT INTO [b9617] VALUES (3676,'<span data-type="title" id=toc-3656>٢٤ </span>- {إِنِّي إِذًا لَفِي ضَلالٍ مُبِينٍ}
INSERT INTO [b9617] VALUES (3677,'<span data-type="title" id=toc-3657>٢٥ </span>- {إِنِّي آمَنْتُ بِرَبِّكُمْ فَاسْمَعُونِ}
INSERT INTO [b9617] VALUES (3678,'<span data-type="title" id=toc-3658>٢٦ </span>- {قِيلَ ادْخُلِ الْجَنَّةَ قَالَ يَا لَيْتَ قَوْمِي يَعْلَمُونَ}
INSERT INTO [b9617] VALUES (3679,'<span data-type="title" id=toc-3659>٢٧ </span>- {بِمَا غَفَرَ لِي رَبِّي وَجَعَلَنِي مِنَ الْمُكْرَمِينَ}
INSERT INTO [b9617] VALUES (3680,'<span data-type="title" id=toc-3660>٢٨ </span>- {وَمَا أَنْزَلْنَا عَلَى قَوْمِهِ مِنْ بَعْدِهِ مِنْ جُنْدٍ مِنَ السَّمَاءِ وَمَا كُنَّا مُنْزِلِينَ}
INSERT INTO [b9617] VALUES (3681,'<span data-type="title" id=toc-3661>٢٩ </span>- {إِنْ كَانَتْ إِلا صَيْحَةً وَاحِدَةً فَإِذَا هُمْ خَامِدُونَ}
INSERT INTO [b9617] VALUES (3682,'<span data-type="title" id=toc-3662>٣٠ </span>- {يَا حَسْرَةً عَلَى الْعِبَادِ مَا يَأْتِيهِمْ مِنْ رَسُولٍ إِلا كَانُوا بِهِ يَسْتَهْزِئُونَ}
INSERT INTO [b9617] VALUES (3683,'<span data-type="title" id=toc-3663>٣١ </span>- {أَلَمْ يَرَوْا كَمْ أَهْلَكْنَا قَبْلَهُمْ مِنَ الْقُرُونِ أَنَّهُمْ إِلَيْهِمْ لا يَرْجِعُونَ}
INSERT INTO [b9617] VALUES (3684,'<span data-type="title" id=toc-3664>٣٢ </span>- {وَإِنْ كُلٌّ لَمَّا جَمِيعٌ لَدَيْنَا مُحْضَرُونَ} ⦗١٠١٧⦘
INSERT INTO [b9617] VALUES (3685,'<span data-type="title" id=toc-3665>٣٣ </span>- {وَآيَةٌ لَهُمُ الأَرْضُ الْمَيْتَةُ أَحْيَيْنَاهَا وَأَخْرَجْنَا مِنْهَا حَبًّا فَمِنْهُ يَأْكُلُونَ}
INSERT INTO [b9617] VALUES (3686,'<span data-type="title" id=toc-3666>٣٤ </span>- {وَجَعَلْنَا فِيهَا جَنَّاتٍ مِنْ نَخِيلٍ وَأَعْنَابٍ وَفَجَّرْنَا فِيهَا مِنَ الْعُيُونِ}
INSERT INTO [b9617] VALUES (3687,'<span data-type="title" id=toc-3667>٣٥ </span>- {لِيَأْكُلُوا مِنْ ثَمَرِهِ وَمَا عَمِلَتْهُ أَيْدِيهِمْ أَفَلا يَشْكُرُونَ}
INSERT INTO [b9617] VALUES (3688,'<span data-type="title" id=toc-3668>٣٦ </span>- {سُبْحَانَ الَّذِي خَلَقَ الأَزْوَاجَ كُلَّهَا مِمَّا تُنْبِتُ الأَرْضُ وَمِنْ أَنْفُسِهِمْ وَمِمَّا لا يَعْلَمُونَ}
INSERT INTO [b9617] VALUES (3689,'<span data-type="title" id=toc-3669>٣٧ </span>- {وَآيَةٌ لَهُمُ اللَّيْلُ نَسْلَخُ مِنْهُ النَّهَارَ فَإِذَا هُمْ مُظْلِمُونَ}
INSERT INTO [b9617] VALUES (3690,'<span data-type="title" id=toc-3670>٣٨ </span>- {وَالشَّمْسُ تَجْرِي لِمُسْتَقَرٍّ لَهَا ذَلِكَ تَقْدِيرُ الْعَزِيزِ الْعَلِيمِ}
INSERT INTO [b9617] VALUES (3691,'<span data-type="title" id=toc-3671>٣٩ </span>- {وَالْقَمَرَ قَدَّرْنَاهُ مَنَازِلَ حَتَّى عَادَ كَالْعُرْجُونِ الْقَدِيمِ}
INSERT INTO [b9617] VALUES (3692,'<span data-type="title" id=toc-3672>٤٠ </span>- {لا الشَّمْسُ يَنْبَغِي لَهَا أَنْ تُدْرِكَ الْقَمَرَ وَلا اللَّيْلُ سَابِقُ النَّهَارِ وَكُلٌّ فِي فَلَكٍ يَسْبَحُونَ}
INSERT INTO [b9617] VALUES (3693,'<span data-type="title" id=toc-3673>٤١ </span>- {وَآيَةٌ لَهُمْ أَنَّا حَمَلْنَا ذُرِّيَّتَهُمْ فِي الْفُلْكِ الْمَشْحُونِ} ⦗١٠١٩⦘
INSERT INTO [b9617] VALUES (3694,'<span data-type="title" id=toc-3674>٤٢ </span>- {وَخَلَقْنَا لَهُمْ مِنْ مِثْلِهِ مَا يَرْكَبُونَ}
INSERT INTO [b9617] VALUES (3695,'<span data-type="title" id=toc-3675>٤٣ </span>- {وَإِنْ نَشَأْ نُغْرِقْهُمْ فَلا صَرِيخَ لَهُمْ وَلا هُمْ يُنْقَذُونَ}
INSERT INTO [b9617] VALUES (3696,'<span data-type="title" id=toc-3676>٤٤ </span>- {إِلا رَحْمَةً مِنَّا وَمَتَاعًا إِلَى حِينٍ}
INSERT INTO [b9617] VALUES (3697,'<span data-type="title" id=toc-3677>٤٥ </span>- {وَإِذَا قِيلَ لَهُمُ اتَّقُوا مَا بَيْنَ أَيْدِيكُمْ وَمَا خَلْفَكُمْ لَعَلَّكُمْ تُرْحَمُونَ}
INSERT INTO [b9617] VALUES (3698,'<span data-type="title" id=toc-3678>٤٦ </span>- {وَمَا تَأْتِيهِمْ مِنْ آيَةٍ مِنْ آيَاتِ رَبِّهِمْ إِلا كَانُوا عَنْهَا مُعْرِضِينَ} ⦗١٠٢٠⦘
INSERT INTO [b9617] VALUES (3699,'<span data-type="title" id=toc-3679>٤٧ </span>- {وَإِذَا قِيلَ لَهُمْ أَنْفِقُوا مِمَّا رَزَقَكُمُ اللَّهُ قَالَ الَّذِينَ كَفَرُوا لِلَّذِينَ آمَنُوا أَنُطْعِمُ مَنْ لَوْ يَشَاءُ اللَّهُ أَطْعَمَهُ إِنْ أَنْتُمْ إِلا فِي ضَلالٍ مُبِينٍ}
INSERT INTO [b9617] VALUES (3700,'<span data-type="title" id=toc-3680>٤٨ </span>- {وَيَقُولُونَ مَتَى هَذَا الْوَعْدُ إِنْ كُنْتُمْ صَادِقِينَ}
INSERT INTO [b9617] VALUES (3701,'<span data-type="title" id=toc-3681>٤٩ </span>- {مَا يَنْظُرُونَ إِلا صَيْحَةً وَاحِدَةً تَأْخُذُهُمْ وَهُمْ يَخِصِّمُونَ}
INSERT INTO [b9617] VALUES (3702,'<span data-type="title" id=toc-3682>٥٠ </span>- {فَلا يَسْتَطِيعُونَ تَوْصِيَةً وَلا إِلَى أَهْلِهِمْ يَرْجِعُونَ} ⦗١٠٢١⦘
INSERT INTO [b9617] VALUES (3703,'<span data-type="title" id=toc-3683>٥١ </span>- {وَنُفِخَ فِي الصُّورِ فَإِذَا هُمْ مِنَ الأَجْدَاثِ إِلَى رَبِّهِمْ يَنْسِلُونَ}
INSERT INTO [b9617] VALUES (3704,'<span data-type="title" id=toc-3684>٥٢ </span>- {قَالُوا يَا وَيْلَنَا مَنْ بَعَثَنَا مِنْ مَرْقَدِنَا هَذَا مَا وَعَدَ الرَّحْمَنُ وَصَدَقَ الْمُرْسَلُونَ}
INSERT INTO [b9617] VALUES (3705,'<span data-type="title" id=toc-3685>٥٣ </span>- {إِنْ كَانَتْ إِلا صَيْحَةً وَاحِدَةً فَإِذَا هُمْ جَمِيعٌ لَدَيْنَا مُحْضَرُونَ}
INSERT INTO [b9617] VALUES (3706,'<span data-type="title" id=toc-3686>٥٤ </span>- {فَالْيَوْمَ لا تُظْلَمُ نَفْسٌ شَيْئًا وَلا تُجْزَوْنَ إِلا مَا كُنْتُمْ تَعْمَلُونَ}
INSERT INTO [b9617] VALUES (3707,'<span data-type="title" id=toc-3687>٥٥ </span>- {إِنَّ أَصْحَابَ الْجَنَّةِ الْيَوْمَ فِي شُغُلٍ فَاكِهُونَ}
INSERT INTO [b9617] VALUES (3708,'<span data-type="title" id=toc-3688>٥٦ </span>- {هُمْ وَأَزْوَاجُهُمْ فِي ظِلالٍ عَلَى الأَرَائِكِ مُتَّكِئُونَ}
INSERT INTO [b9617] VALUES (3709,'<span data-type="title" id=toc-3689>٥٧ </span>- {لَهُمْ فِيهَا فَاكِهَةٌ وَلَهُمْ مَا يَدَّعُونَ}
INSERT INTO [b9617] VALUES (3710,'<span data-type="title" id=toc-3690>٥٨ </span>- {سَلامٌ قَوْلا مِنْ رَبٍّ رَحِيمٍ}
INSERT INTO [b9617] VALUES (3711,'<span data-type="title" id=toc-3691>٥٩ </span>- {وَامْتَازُوا الْيَوْمَ أَيُّهَا الْمُجْرِمُونَ}
INSERT INTO [b9617] VALUES (3712,'<span data-type="title" id=toc-3692>٦٠ </span>- {أَلَمْ أَعْهَدْ إِلَيْكُمْ يَا بَنِي آدَمَ أَنْ لا تَعْبُدُوا الشَّيْطَانَ إِنَّهُ لَكُمْ عَدُوٌّ مُبِينٌ}
INSERT INTO [b9617] VALUES (3713,'<span data-type="title" id=toc-3693>٦١ </span>- {وَأَنِ اعْبُدُونِي هَذَا صِرَاطٌ مُسْتَقِيمٌ}
INSERT INTO [b9617] VALUES (3714,'<span data-type="title" id=toc-3694>٦٢ </span>- {وَلَقَدْ أَضَلَّ مِنْكُمْ جِبِلا كَثِيرًا أَفَلَمْ تَكُونُوا تَعْقِلُونَ}
INSERT INTO [b9617] VALUES (3715,'<span data-type="title" id=toc-3695>٦٣ </span>- {هَذِهِ جَهَنَّمُ الَّتِي كُنْتُمْ تُوعَدُونَ}
INSERT INTO [b9617] VALUES (3716,'<span data-type="title" id=toc-3696>٦٤ </span>- {اصْلَوْهَا الْيَوْمَ بِمَا كُنْتُمْ تَكْفُرُونَ}
INSERT INTO [b9617] VALUES (3717,'<span data-type="title" id=toc-3697>٦٥ </span>- {وَتَشْهَدُ أَرْجُلُهُمْ بِمَا كَانُوا يَكْسِبُونَ}
INSERT INTO [b9617] VALUES (3718,'<span data-type="title" id=toc-3698>٦٦ </span>- {وَلَوْ نَشَاءُ لَطَمَسْنَا عَلَى أَعْيُنِهِمْ فَاسْتَبَقُوا الصِّرَاطَ فَأَنَّى يُبْصِرُونَ}
INSERT INTO [b9617] VALUES (3719,'<span data-type="title" id=toc-3699>٦٧ </span>- {وَلَوْ نَشَاءُ لَمَسَخْنَاهُمْ عَلَى مَكَانَتِهِمْ فَمَا اسْتَطَاعُوا مُضِيًّا وَلا يَرْجِعُونَ}
INSERT INTO [b9617] VALUES (3720,'<span data-type="title" id=toc-3700>٦٨ </span>- {وَمَنْ نُعَمِّرْهُ نُنَكِّسْهُ فِي الْخَلْقِ أَفَلا يَعْقِلُونَ}
INSERT INTO [b9617] VALUES (3721,'<span data-type="title" id=toc-3701>٦٩ </span>- {وَمَا عَلَّمْنَاهُ الشِّعْرَ وَمَا يَنْبَغِي لَهُ إِنْ هُوَ إِلا ذِكْرٌ وَقُرْآنٌ مُبِينٌ}
INSERT INTO [b9617] VALUES (3722,'<span data-type="title" id=toc-3702>٧٠ </span>- {لِيُنْذِرَ مَنْ كَانَ حَيًّا}
INSERT INTO [b9617] VALUES (3723,'<span data-type="title" id=toc-3703>٧١ </span>- {أَوَلَمْ يَرَوْا أَنَّا خَلَقْنَا لَهُمْ مِمَّا عَمِلَتْ أَيْدِينَا أَنْعَامًا فَهُمْ لَهَا مَالِكُونَ}
INSERT INTO [b9617] VALUES (3724,'<span data-type="title" id=toc-3704>٧٢ </span>- {وَذَلَّلْنَاهَا لَهُمْ فَمِنْهَا رَكُوبُهُمْ وَمِنْهَا يَأْكُلُونَ}
INSERT INTO [b9617] VALUES (3725,'<span data-type="title" id=toc-3705>٧٣ </span>- {وَلَهُمْ فِيهَا مَنَافِعُ وَمَشَارِبُ أَفَلا يَشْكُرُونَ}
INSERT INTO [b9617] VALUES (3726,'<span data-type="title" id=toc-3706>٧٤ </span>- {وَاتَّخَذُوا مِنْ دُونِ اللَّهِ آلِهَةً لَعَلَّهُمْ يُنْصَرُونَ}
INSERT INTO [b9617] VALUES (3727,'<span data-type="title" id=toc-3707>٧٥ </span>- {لا يَسْتَطِيعُونَ نَصْرَهُمْ وَهُمْ لَهُمْ جُنْدٌ مُحْضَرُونَ}
INSERT INTO [b9617] VALUES (3728,'<span data-type="title" id=toc-3708>٧٦ </span>- {فَلا يَحْزُنْكَ قَوْلُهُمْ إِنَّا نَعْلَمُ مَا يُسِرُّونَ وَمَا يُعْلِنُونَ}
INSERT INTO [b9617] VALUES (3729,'<span data-type="title" id=toc-3709>٧٧ </span>- {أَوَلَمْ يَرَ الإِنْسَانُ أَنَّا خَلَقْنَاهُ مِنْ نُطْفَةٍ فَإِذَا هُوَ خَصِيمٌ مُبِينٌ} ⦗١٠٢٦⦘
INSERT INTO [b9617] VALUES (3730,'<span data-type="title" id=toc-3710>٧٨ </span>- {وَضَرَبَ لَنَا مَثَلا وَنَسِيَ خَلْقَهُ قَالَ مَنْ يُحْيِي الْعِظَامَ وَهِيَ رَمِيمٌ}
INSERT INTO [b9617] VALUES (3731,'<span data-type="title" id=toc-3711>٧٩ </span>- {قُلْ يُحْيِيهَا الَّذِي أَنْشَأَهَا أَوَّلَ مَرَّةٍ وَهُوَ بِكُلِّ خَلْقٍ عَلِيمٌ}
INSERT INTO [b9617] VALUES (3732,'<span data-type="title" id=toc-3712>٨٠ </span>- {الَّذِي جَعَلَ لَكُمْ مِنَ الشَّجَرِ الأَخْضَرِ نَارًا فَإِذَا أَنْتُمْ مِنْهُ تُوقِدُونَ}
INSERT INTO [b9617] VALUES (3733,'<span data-type="title" id=toc-3713>٨١ </span>- {أَوَلَيْسَ الَّذِي خَلَقَ السَّمَاوَاتِ وَالأَرْضَ بِقَادِرٍ عَلَى أَنْ يَخْلُقَ مِثْلَهُمْ بَلَى وَهُوَ الْخَلاقُ الْعَلِيمُ}
INSERT INTO [b9617] VALUES (3734,'<span data-type="title" id=toc-3714>٨٢ </span>- {إِنَّمَا أَمْرُهُ إِذَا أَرَادَ شَيْئًا أَنْ يَقُولَ لَهُ كُنْ فَيَكُونُ}
INSERT INTO [b9617] VALUES (3735,'<span data-type="title" id=toc-3715>٨٣ </span>- {فَسُبْحَانَ الَّذِي بِيَدِهِ مَلَكُوتُ كُلِّ شَيْءٍ وَإِلَيْهِ تُرْجَعُونَ}
INSERT INTO [b9617] VALUES (3736,'<span data-type="title" id=toc-3716>سورة الصافات</span>','3','1028',NULL,NULL,'0');
INSERT INTO [b9617] VALUES (3737,'<span data-type="title" id=toc-3717>١ </span>- {وَالصَّافَّاتِ صَفًّا}
INSERT INTO [b9617] VALUES (3738,'<span data-type="title" id=toc-3718>٢ </span>- {فَالزَّاجِرَاتِ زَجْرًا}
INSERT INTO [b9617] VALUES (3739,'<span data-type="title" id=toc-3719>٣ </span>- {فَالتَّالِيَاتِ ذِكْرًا}
INSERT INTO [b9617] VALUES (3740,'<span data-type="title" id=toc-3720>٤ </span>- {إِنَّ إِلَهَكُمْ لَوَاحِدٌ}
INSERT INTO [b9617] VALUES (3741,'<span data-type="title" id=toc-3721>٥ </span>- {رَبُّ السَّمَاوَاتِ وَالأَرْضِ وَمَا بَيْنَهُمَا}
INSERT INTO [b9617] VALUES (3742,'<span data-type="title" id=toc-3722>٦ </span>- {إِنَّا زَيَّنَّا السَّمَاءَ الدُّنْيَا بِزِينَةٍ الْكَوَاكِبِ}
INSERT INTO [b9617] VALUES (3743,'<span data-type="title" id=toc-3723>٧ </span>- {وَحِفْظًا مِنْ كُلِّ شَيْطَانٍ مَارِدٍ}
INSERT INTO [b9617] VALUES (3744,'<span data-type="title" id=toc-3724>٨ </span>- {لا يَسَّمَّعُونَ إِلَى الْمَلإِ الأَعْلَى وَيُقْذَفُونَ مِنْ كُلِّ جَانِبٍ}
INSERT INTO [b9617] VALUES (3745,'<span data-type="title" id=toc-3725>٩ </span>- {دُحُورًا وَلَهُمْ عَذَابٌ وَاصِبٌ}
INSERT INTO [b9617] VALUES (3746,'<span data-type="title" id=toc-3726>١٠ </span>- {إِلا مَنْ خَطِفَ الْخَطْفَةَ فَأَتْبَعَهُ شِهَابٌ ثَاقِبٌ}
INSERT INTO [b9617] VALUES (3747,'<span data-type="title" id=toc-3727>١١ </span>- {فَاسْتَفْتِهِمْ أَهُمْ أَشَدُّ خَلْقًا أَمْ مَنْ خَلَقْنَا إِنَّا خَلَقْنَاهُمْ مِنْ طِينٍ لازِبٍ}
INSERT INTO [b9617] VALUES (3748,'<span data-type="title" id=toc-3728>١٢ </span>- {بَلْ عَجِبْتَ وَيَسْخَرُونَ}
INSERT INTO [b9617] VALUES (3749,'<span data-type="title" id=toc-3729>١٣ </span>- {وَإِذَا ذُكِّرُوا لا يَذْكُرُونَ} ⦗١٠٣٠⦘
INSERT INTO [b9617] VALUES (3750,'<span data-type="title" id=toc-3730>١٤ </span>- {وَإِذَا رَأَوْا آيَةً يَسْتَسْخِرُونَ}
INSERT INTO [b9617] VALUES (3751,'<span data-type="title" id=toc-3731>١٥ </span>- {وَقَالُوا إِنْ هَذَا إِلا سِحْرٌ مُبِينٌ}
INSERT INTO [b9617] VALUES (3752,'<span data-type="title" id=toc-3732>١٦ </span>- {أَئِذَا مِتْنَا وَكُنَّا تُرَابًا وَعِظَامًا أَئِنَّا لَمَبْعُوثُونَ}
INSERT INTO [b9617] VALUES (3753,'<span data-type="title" id=toc-3733>١٧ </span>- {أَوَآبَاؤُنَا الأَوَّلُونَ}
INSERT INTO [b9617] VALUES (3754,'<span data-type="title" id=toc-3734>١٨ </span>- {قُلْ نَعَمْ وَأَنْتُمْ دَاخِرُونَ}
INSERT INTO [b9617] VALUES (3755,'<span data-type="title" id=toc-3735>١٩ </span>- {فَإِنَّمَا هِيَ زَجْرَةٌ وَاحِدَةٌ فَإِذَا هُمْ يَنْظُرُونَ}
INSERT INTO [b9617] VALUES (3756,'<span data-type="title" id=toc-3736>٢٠ </span>- {وَقَالُوا يَا وَيْلَنَا هَذَا يَوْمُ الدِّينِ}
INSERT INTO [b9617] VALUES (3757,'<span data-type="title" id=toc-3737>٢١ </span>- {هَذَا يَوْمُ الْفَصْلِ الَّذِي كُنْتُمْ بِهِ تُكَذِّبُونَ}
INSERT INTO [b9617] VALUES (3758,'<span data-type="title" id=toc-3738>٢٢ </span>- {احْشُرُوا الَّذِينَ ظَلَمُوا وَأَزْوَاجَهُمْ وَمَا كَانُوا يَعْبُدُونَ}
INSERT INTO [b9617] VALUES (3759,'<span data-type="title" id=toc-3739>٢٣ </span>- {مِنْ دُونِ اللَّهِ فَاهْدُوهُمْ إِلَى صِرَاطِ الْجَحِيمِ}
