INSERT INTO [b9617] VALUES (5337,'<span data-type="title" id=toc-5315>٢٥ </span>- {مِمَّا خَطِيئَاتِهِمْ أُغْرِقُوا فَأُدْخِلُوا نَارًا فَلَمْ يَجِدُوا لَهُمْ مِنْ دُونِ اللَّهِ أَنْصَارًا}
