INSERT INTO [b9617] VALUES (5340,'<span data-type="title" id=toc-5318>٢٨ </span>- {وَلِمَنْ دَخَلَ بَيْتِيَ مُؤْمِنًا وَلِلْمُؤْمِنِينَ وَالْمُؤْمِنَاتِ وَلا تَزِدِ الظَّالِمِينَ إِلا تَبَارًا}
INSERT INTO [b9617] VALUES (5341,'<span data-type="title" id=toc-5319>سورة الجن</span>','4','1374',NULL,NULL,'0');
INSERT INTO [b9617] VALUES (5342,'<span data-type="title" id=toc-5320>١ </span>- {قُلْ أُوحِيَ إِلَيَّ أَنَّهُ اسْتَمَعَ نَفَرٌ مِنَ الْجِنِّ فَقَالُوا إِنَّا سَمِعْنَا قُرْآنًا عَجَبًا}
INSERT INTO [b9617] VALUES (5343,'<span data-type="title" id=toc-5321>٢ </span>- {يَهْدِي إِلَى الرُّشْدِ فَآمَنَّا بِهِ وَلَنْ نُشْرِكَ بِرَبِّنَا أَحَدًا}
INSERT INTO [b9617] VALUES (5344,'<span data-type="title" id=toc-5322>٣ </span>- {وَأَنَّهُ تَعَالَى جَدُّ رَبِّنَا مَا اتَّخَذَ صَاحِبَةً وَلا وَلَدًا}
INSERT INTO [b9617] VALUES (5345,'<span data-type="title" id=toc-5323>٤ </span>- {وَأَنَّهُ كَانَ يَقُولُ سَفِيهُنَا عَلَى اللَّهِ شَطَطًا}
INSERT INTO [b9617] VALUES (5346,'<span data-type="title" id=toc-5324>٥ </span>- {وَأَنَّا ظَنَنَّا أَنْ لَنْ تَقُولَ الإِنْسُ وَالْجِنُّ عَلَى اللَّهِ كَذِبًا} ⦗١٣٧٥⦘
INSERT INTO [b9617] VALUES (5347,'<span data-type="title" id=toc-5325>٦ </span>- {وَأَنَّهُ كَانَ رِجَالٌ مِنَ الإِنْسِ يَعُوذُونَ بِرِجَالٍ مِنَ الْجِنِّ فَزَادُوهُمْ رَهَقًا}
INSERT INTO [b9617] VALUES (5348,'<span data-type="title" id=toc-5326>٧ </span>- {وَأَنَّهُمْ ظَنُّوا كَمَا ظَنَنْتُمْ أَنْ لَنْ يَبْعَثَ اللَّهُ أَحَدًا}
INSERT INTO [b9617] VALUES (5349,'<span data-type="title" id=toc-5327>٨ </span>- {وَأَنَّا لَمَسْنَا السَّمَاءَ فَوَجَدْنَاهَا مُلِئَتْ حَرَسًا شَدِيدًا وَشُهُبًا}
INSERT INTO [b9617] VALUES (5350,'<span data-type="title" id=toc-5328>٩ </span>- {وَأَنَّا كُنَّا نَقْعُدُ مِنْهَا مَقَاعِدَ لِلسَّمْعِ فَمَنْ يَسْتَمِعِ الآنَ يَجِدْ لَهُ شِهَابًا رَصَدًا}
INSERT INTO [b9617] VALUES (5351,'<span data-type="title" id=toc-5329>١٠ </span>- {وَأَنَّا لا نَدْرِي أَشَرٌّ أُرِيدَ بِمَنْ فِي الأَرْضِ أَمْ أَرَادَ بِهِمْ رَبُّهُمْ رَشَدًا}
INSERT INTO [b9617] VALUES (5352,'<span data-type="title" id=toc-5330>١١ </span>- {وَأَنَّا مِنَّا الصَّالِحُونَ وَمِنَّا دُونَ ذَلِكَ كُنَّا طَرَائِقَ قِدَدًا}
INSERT INTO [b9617] VALUES (5353,'<span data-type="title" id=toc-5331>١٢ </span>- {وَأَنَّا ظَنَنَّا أَنْ لَنْ نُعْجِزَ اللَّهَ فِي الأَرْضِ وَلَنْ نُعْجِزَهُ هَرَبًا}
INSERT INTO [b9617] VALUES (5354,'<span data-type="title" id=toc-5332>١٣ </span>- {وَأَنَّا لَمَّا سَمِعْنَا الْهُدَى آمَنَّا بِهِ فَمَنْ يُؤْمِنْ بِرَبِّهِ فَلا يَخَافُ بَخْسًا وَلا رَهَقًا}
INSERT INTO [b9617] VALUES (5355,'<span data-type="title" id=toc-5333>١٤ </span>- {وَأَنَّا مِنَّا الْمُسْلِمُونَ وَمِنَّا الْقَاسِطُونَ فَمَنْ أَسْلَمَ فَأُولَئِكَ تَحَرَّوْا رَشَدًا}
INSERT INTO [b9617] VALUES (5356,'<span data-type="title" id=toc-5334>١٥ </span>- {وَأَمَّا الْقَاسِطُونَ فَكَانُوا لِجَهَنَّمَ حَطَبًا}
INSERT INTO [b9617] VALUES (5357,'<span data-type="title" id=toc-5335>١٦ </span>- {وَأَنْ لَوِ اسْتَقَامُوا عَلَى الطَّرِيقَةِ لأَسْقَيْنَاهُمْ مَاءً غَدَقًا}
INSERT INTO [b9617] VALUES (5358,'<span data-type="title" id=toc-5336>١٧ </span>- {لِنَفْتِنَهُمْ فِيهِ وَمَنْ يُعْرِضْ عَنْ ذِكْرِ رَبِّهِ يَسْلُكْهُ عَذَابًا صَعَدًا}
INSERT INTO [b9617] VALUES (5359,'<span data-type="title" id=toc-5337>١٨ </span>- {وَأَنَّ الْمَسَاجِدَ لِلَّهِ فَلا تَدْعُوا مَعَ اللَّهِ أَحَدًا}
