-- Insert data for b96602
INSERT INTO [b96602] VALUES (1,'مجالس ابن القاسم التي سأل عنها مالكا رحمه الله

تحقيق: مصطفى باحو',NULL,NULL,NULL,NULL,'0');
INSERT INTO [b96602] VALUES (2,'بسم الله الرحمن الرحيم، وصلى الله على سيدنا مولانا محمد وآله وصحبه وسلم تسليما.
وهذا كتاب فيه مجالس ابن القاسم رحمه الله تعالى ورضي عنه التي سأل عنها مالكا رحمه الله تعالى ورضي عنه.
وفيه كل ما يلزم المصلي في صلاته من الزيادة والنقصان، وما تجبر صلاته وتصح إن شاء الله، وكل ما فيها واجب على الإنسان في أمر صلاته وغيره.
وهاهنا أذكره من أوله إلى أخره:
١ - قال ابن القاسم: قلت لمالك رحمه الله: رأيت رجلا صلى المغرب، فلما جلس في التشهد الأخير ذكر أنه نسي ثلاث سجدات من ثلاث ركعات، ماذا يفعل؟',NULL,'22',NULL,NULL,'0');
INSERT INTO [b96602] VALUES (3,'قال سحنون عن ابن القاسم: يسجد سجدة فتصح له ركعة، ويقوم إلى الثانية بأم القرآن (وسورة ويجلس ويتشهد، ثم يقوم إلى الثالثة بأم القرآن خاصة ويسلم ويسجد لسهوه بعد السلام، فتصح صلاته، إن شاء الله).',NULL,'23',NULL,NULL,'0');
INSERT INTO [b96602] VALUES (4,'٢ - قال ابن القاسم: قلت لمالك (رحمه الله): رأيت رجلا توضأ في واد فنسي غسل رجليه، حتى جاز الماء وصلى، فلما فرغ من صلاته أيقن أنه نسي غسل رجليه، ماذا يفعل؟
قال: قال (مالك): صلاته باطلة، يرجع إلى الماء ويعتقد النية ويغسل رجليه (فقط) ويعيد الصلاة، فتصح إن شاء الله.
٣ - قال ابن القاسم: قلت لمالك (رحمه الله): (رأيت) رجلا توضأ يريد الصلاة فأحدث وأراد أن يتوضأ، هل يغسل',NULL,'24',NULL,NULL,'0');
INSERT INTO [b96602] VALUES (5,'يديه، وهما طاهرة، وهل يغسلهما قبل إدخالهما في الإناء إن كان قريب العهد من الماء؟
قال مالك: يغسلهما.
٤ - قال ابن القاسم: قلت لمالك (رحمه الله): رأيت رجلا جاء للصلاة، فلما صلى أيقن بالماء الذي توضأ منه (قد) ولغ فيه كلب، هل يعيد الوضوء (والصلاة) أم لا؟',NULL,'25',NULL,NULL,'0');
INSERT INTO [b96602] VALUES (6,'قال مالك: يعيد الوضوء والصلاة في الوقت، فإذا خرج الوقت فلا إعادة عليه في الصلاة، ويعيد الوضوء لما يستقبل من الصلوات.
٥ - قال ابن القاسم: قلت لمالك (رحمه الله): رأيت رجلا توضأ ونسي (المضمضة و) الاستنشاق وصلى؟',NULL,'26',NULL,NULL,'0');
INSERT INTO [b96602] VALUES (7,'قال مالك: (يفعل ذلك) لما يستقبل من الصلوات، وصلاته تامة (إن شاء الله).
٦ - قال ابن القاسم: قلت لمالك (رحمه الله): رأيت رجلا توضأ ونسي أذنيه ودخل في الصلاة، ثم أيقن أنه نسي مسح أذنيه، (ماذا يفعل)؟
قال مالك: يتمادى (على صلاته)، فإذا فرغ من صلاته فعل ما نسي، وصلاته تامة إن شاء الله.
٧ - قال ابن القاسم: قلت لمالك (رحمه الله): رأيت رجلا قام لصلاة الصبح فنسب مسح رأسه وتمادى (بذلك) الوضوء إلى',NULL,'27',NULL,NULL,'0');
INSERT INTO [b96602] VALUES (8,'الظهر، فصلى به الظهر مجددا الوضوء من غير انتقاض، يريد (به) استحباب، فصلى به العصر، فلما فرغ من صلاة العصر أيقن أنه نسي مسح رأسه من الوضوء الذي توضأ (به) لصلاة الصبح، ماذا يفعل؟
قال مالك: يأخذ الماء بيده ويمسح رأسه فقط، (ويعيد النية) ويعيد كل ما صلى بذلك الوضوء: (صلاة) الصبح، ثم',NULL,'28',NULL,NULL,'0');
INSERT INTO [b96602] VALUES (9,'(صلاة) الظهر، ثم (صلاة) العصر، فتصح صلاته إن شاء الله.
٨ - قال ابن القاسم: قلت لمالك (رحمه الله): رأيت رجلا في رجله قروح ولَفَّهم في خرقة، هـ يتيمم أم لا؟
قال مالك: الوضوء أحب غلي، ويمسح من فوق الأذى.
٩ - قال ابن القاسم: قلت لمالك رحمه الله: رأيت رجلا في رأسه قرحة، فأصابته جنابة فاغتسل ولم يغسل رأسه، (إلا أنه مسح عليه)، هل صلاته تامة أم لا؟.',NULL,'29',NULL,NULL,'0');
INSERT INTO [b96602] VALUES (10,'قال مالك: صلاته تجزيه لقوله تعالى: {لَا يُكَلِّفُ اللَّهُ نَفْسًا} [البقرة: ٢٨٦]، الآية.
١٠ - قال ابن القاسم: قلت لمالك (رحمه الله): رأيت رجلا تيمم لصلاة الفرض، هل يصلي بذلك (التيمم) صلاة النافلة أم لا؟
قال مالك: إن كان قبل صلاة الفرض يعيد التيمم لصلاة الفرض، وإن كان بعد صلاة الفرض فلا بأس عليه أن يتنفل بعده ويتيمم لصلاة الفرض.',NULL,'30',NULL,NULL,'0');
INSERT INTO [b96602] VALUES (11,'١١ - (قال ابن القاسم: قلت لمالك رحمه الله: رأيت رجلا نسي مسح أذنيه، ماذا يجب عليه؟
قال: يمسح (أذنيه) لما يستقبل من الصلاة، ولا شيء عليه فيما مضى).
١٢ - قال ابن القاسم: قلت لمالك (رحمه الله): رأيت رجلا توضأ، فلما فرغ من وضوئه نظر إلى الماء الذي توضأ منه قد ماتت فيه خنفسة أو عقرب أو جرادة، هل يعد ما صلى بذلك الوضوء أم لا؟',NULL,'31',NULL,NULL,'0');
INSERT INTO [b96602] VALUES (12,'قال مالك: لا إعادة عليه، لأنهم من الحيوان الذي لا لحم فيه ولا دم.
١٣ - قال ابن القاسم: قلت لمالك (رحمه الله): رأيت رجلا فيه دماميل فانفجرت عليه وهو يصلي، هل يتم صلاته أم لا؟
قال مالك: إن كان (الدم) كثيرا يقطع ويخرج يغسل الدم ويصلي، وإن كان (الدم) قليلا تمادى على الصلاة، وصلاته تامة إن شاء الله.',NULL,'32',NULL,NULL,'0');
INSERT INTO [b96602] VALUES (13,'١٤ - قال ابن القاسم: قلت لمالك (رحمه الله): رأيت رجلا توضأ ومسح على عمامته (وصلى بذلك الوضوء)، هل يعيد الصلاة أم لا؟
قال مالك: يأخذ الماء ويمسح على رأسه ويعيد صلاته، وقد نهى النبي - صلى الله عليه وسلم - عن المسح على العمامة.',NULL,'33',NULL,NULL,'0');
INSERT INTO [b96602] VALUES (14,'١٥ - قال ابن القاسم: قلت لمالك (رحمه الله): رأيت رجلا (اغتسل) في واد ولم يتوضأ؟
قال مالك: إن كان في الغسل (فالغسل أجزأه، لقول عائشة رضي الله عنها: فإن اقتصر المغتسل على الغسل (دون الوضوء) أجزأه).',NULL,'34',NULL,NULL,'0');
INSERT INTO [b96602] VALUES (15,'١٦ - قال ابن القاسم: قلت لمالك (رحمه الله): رأيت رجلا قام من نومه وعلى رجليه بلل وتوضأ ولم يغسله، ماذا يفعل؟
قال مالك: صلاته تامة، إلا إذا أيقن أنه نجس.
١٧ - قال ابن القاسم: قلت لمالك (رحمه الله): رأيت رجلا أصابته جنابة ولم يجد الماء فأدركه الوقت، ماذا يفعل؟
قال مالك: يتيمم ويصلي.
(قلت له): وإن وجد الماء (في الوقت).
(قال): يغتسل ويعيد الصلاة.',NULL,'35',NULL,NULL,'0');
INSERT INTO [b96602] VALUES (16,'قلت له: وإن لم يجد الماء حتى خرج الوقت؟
قال: فلا إعادة عليه، وصلاته تامة (إن شاء الله)، لأنه كان في عهد النبي - صلى الله عليه وسلم - أتى إليه رجل فقال: يا رسول الله صلى الله عليه، قد أدركني الوقت واصابتني جنابة، (ولم نجد الماء)، وتيممت وصليت، فبعد ذلك وجدت الماء فاغتسلت وأعدت صلاتي. فقال له: يا أخي إذا خرج الوقت فلا إعادة عليك.',NULL,'36',NULL,NULL,'0');
INSERT INTO [b96602] VALUES (17,'١٨ - قال ابن القاسم: قلت لمالك (رحمه الله): رأيت رجلا مس في شعر جارية فالتذ بذلك وهو على وضوء، هل يعيد الوضوء أم لا؟
قال مالك: إن (حبسه استحسانا) فلا شيء عليه.',NULL,'37',NULL,NULL,'0');
INSERT INTO [b96602] VALUES (18,'١٩ - (قال ابن القاسم: قلت لمالك رحمه الله: رأيت رجلا دخل الحمام فتوضأ فيه لصلواته، هل يجوز له ذلك الوضوء أم لا؟
قال: ذلك جائز ولا دخول فيه).
٢٠ - قال ابن القاسم: قلت لمالك (رحمه الله): رأيت رجلا جاء لصلاة الجمعة فركع وجلس ينتظر الوقت فنام وهو جالس، ماذا يفعل؟
قال مالك: إن كان نوما طويلا فعليه الوضوء، وإن لم يطل ولم يحل له عقلا فلا وضوء عليه.',NULL,'38',NULL,NULL,'0');
INSERT INTO [b96602] VALUES (19,'٢١ - قال ابن القاسم: قلت لمالك (رحمه الله): رأيت رجلا في سفر (أدركه وقت الصلاة)، ومعه ماء قليل يخاف أن يتوضأ بذلك الماء أن يدركه العطش، ماذا يفعل؟
قال مالك: يتيمم ويصلي.
قلت (له): وإن وجد الماء في الوقت.
(قال): يتوضأ ويعيد صلاته.
قلت: وإن وجد الماء بعد خروج الوقت؟
قال: فلا إعادة عليه وصلاته تامة (إن شاء الله).',NULL,'39',NULL,NULL,'0');
INSERT INTO [b96602] VALUES (20,'(قلت: أترى أن يشتريه أو يطلبه من الرفقة؟ قال: ليس عليه شراؤه).
٢٢ - قال ابن القاسم: قلت لمالك (رحمه الله): رأيت رجلا صلى فلما فرغ من صلاته نظر في ثوبه ماء أبيض، ماذا يفعل؟
قال مالك: إن كان فيه رائحة يعيد صلاته بعد غسل ذلك الماء من ثوبه، وإلا فلا شيء عليه، وصلاته تامة (إن شاء الله).',NULL,'40',NULL,NULL,'0');
INSERT INTO [b96602] VALUES (21,'٢٣ - قال ابن القاسم: قلت لمالك (رحمه الله): رأيت رجلا صار إلى المسجد (حافيا)، فوطئ برجله مكانا نجسا، ماذا يفعل؟
قال مالك: فلا شيء عليه إلا أن يكون رطبا (فإنه يغسل رجله)، وصلاته تامة (إن شاء الله).
٢٤ - قال ابن القاسم: قلت لمالك (رحمه الله): رأيت رجلا توضأ فلما فرغ من وضوئه خرجت من دبره دودة، ماذا يفعل؟
قال مالك: إن كانت الدودة مبلولة يعيد الوضوء، وإن لم يكن فيها بلل فلا شيء عليه.',NULL,'41',NULL,NULL,'0');
INSERT INTO [b96602] VALUES (22,'٢٥ - (قال ابن القاسم: قلت لمالك رحمه الله: رأيت رجلا توضأ، فلما فرغ من وضوئه قبلته امرأته، ماذا يجب عليه؟
قال: يعيد الوضوء على كل حال).
٢٦ - قال ابن القاسم: قلت لمالك رحمه الله: (رأيت) رجلا (خرج) مسافرا فلما رجع من سفره قام في منزله عشرة أيام ثم',NULL,'42',NULL,NULL,'0');
INSERT INTO [b96602] VALUES (23,'أيقن أنه سقطت له صلاة يوم واحد، ولا يدري هل هي (من) صلاة السفر أو من صلاة الحضر، ماذا يفعل؟
قال مالك (رحمه الله): يصلي فيها عشر صلوات: خمس منها حضرية، وخمس سفرية. فإن كان (اليوم) من صلاة السفر وجدها. وإن كان من صلاة الحضر وجدها، فتصح صلاته إن شاء الله.
٢٧ - قال ابن القاسم: قلت لمالك (رحمه الله): رأيت رجلا توضأ، فلما فرغ من وضوئه وجد في ثوبه بول فرس أو حمر أو بغال، ماذا يفعل؟',NULL,'43',NULL,NULL,'0');
INSERT INTO [b96602] VALUES (24,'قال مالك: يغسل (ذلك) البول، وإن كان بعدما صلى يعيد صلاته في الوقت، فإذا خرج الوقت فلا إعادة عليه، وصلاته تامة (إن شاء الله).
٢٨ - قال ابن القاسم: قلت لمالك (رحمه الله): (رأيت) رجلا (توضأ و) صلى، فلما فرغ من صلاته نظر في جسده لمعة من نجاسة، ماذا يفعل؟',NULL,'44',NULL,NULL,'0');
INSERT INTO [b96602] VALUES (25,'قال مالك: يغسل اللمعة ويعيد الصلاة في الوقت، فإذا خرج الوقت فلا إعادة عليه، وصلاته تامة (إن شاء الله).
٢٩ - قال ابن القاسم: قلت لمالك (رحمه الله): رأيت رجلا توضأ (وصلى)، فلما فرغ من صلاته نظر البقعة التي صلى فيها ناجسة، ماذا يفعل؟
قال مالك: يعيد صلاته (في الوقت)، فإذا خرج الوقت فلا إعادة عليه، وصلاته تامة (إن شاء الله).
٣٠ - قال ابن القاسم: قلت لمالك (رحمه الله): رأيت رجلا (إماما) صلى بالقوم مجنبا ساهيا، فلما فرغ من صلاته أيقن أنه مجنب، ماذا يفعل؟',NULL,'45',NULL,NULL,'0');
INSERT INTO [b96602] VALUES (26,'قال مالك: يعيد الإمام الصلاة في الوقت وبعده، ولو كانت له سنة خارجا، وأما القوم فلا إعادة عليهم.
٣١ - قال ابن القاسم: قلت لمالك (رحمه الله): رأيت رجلا أيقن في ثوبه نجاسة ولم يعرف موضعها، وخاف أن تكون النجاسة أصابت الثوب كله، ماذا يفعل؟',NULL,'46',NULL,NULL,'0');
INSERT INTO [b96602] VALUES (27,'قال مالك: يغسل الثوب كله، (ويعيد كل ما صلى به).
٣٢ - قال ابن القاسم: قلت لمالك (رحمه الله): رأيت رجلا فاتته ركعة فأدرك الإمام في الثانية، فلما كان في الجلوس الأخير سمع تنحنحا، فظن الإمام سلم فقام وقرأ أم القرآن والسورة، ثم إن الإمام لم يسلم، (ماذا يجب عليه؟).',NULL,'47',NULL,NULL,'0');
INSERT INTO [b96602] VALUES (28,'قال: يرجع ويجلس ينتظر الإمام حتى يسلم، ثم يقوم ويقرأ أم القرآن والسورة، وليس عليه سجود، وصلاته تامة (إن شاء الله).
٣٣ - قال ابن القاسم: (قلت لمالك رحمه الله): رأيت رجلا نسي الظهر والعصر والمغرب من أيام كثيرة، ولم يدر من أي يوم نساهن، (ماذا يفعل؟).
قال مالك: (يصلي فيها سبع صلوات يبتدئ بالظهر، ثم العصر، ثم المغرب، ثم العشاء، ثم الصبح، ثم الظهر، ثم العصر، فتتم صلاته إن شاء الله).',NULL,'48',NULL,NULL,'0');
INSERT INTO [b96602] VALUES (29,'٣٤ - قال ابن القاسم: (قلت لمالك رحمه الله): رأيت رجلا مسافرا ودخل مع المقيمين فصلى معهم أربعا، ماذا يفعل؟
قال مالك: إن كان عامدا يعيد في الوقت، وإن كان ساهيا فلا شيء عليه.
٣٥ - قال ابن القاسم: قلت لمالك (رحمه الله): رأيت رجلا مسافرا فدخل مع المقيمين وصلى معهم وسلم، ماذا يفعل؟',NULL,'49',NULL,NULL,'0');
INSERT INTO [b96602] VALUES (30,'قال مالك: يرجع بنية (ويكبر ويجلس ينتظر الإمام حتى يتم صلاته ويسلم ويوافقه في السلام ولا يسجد).
٣٦ - قال (ابن القاسم): قلت لمالك (رحمه الله): رأيت رجلا مسافرا دخل (على قوم) وصلى معهم ولا يدري هل مقيمين أو مسافرين؟ ماذا يفعل؟
قال مالك: يصلي صلاة الحضر، ويصلي صلاة السفر.',NULL,'50',NULL,NULL,'0');
INSERT INTO [b96602] VALUES (31,'٣٧ - قال ابن القاسم: (قلت لمالك رحمه الله): رأيت رجلا مسافرا صلى بالمقيمين ركعتين (وسلم، وسلم القوم معه، ماذا يفعل؟
قال مالك رحمه الله: لا إعادة على الإمام ويعيد القوم، لأن الإمام مسافر صلى بهم ركعتين)، ليس له أكثر منها.
٣٨ - قال ابن القاسم: (قلت لمالك رحمه الله): رأيت رجلا (مسافرا) صلى بالمقيمين ساهيا، ماذا يجب عليه؟
قال مالك: (يعيد المسافر في الوقت ويعيد المقيمون في الوقت. وهذا الذي يجب على المقيمين الإعادة.',NULL,'51',NULL,NULL,'0');
INSERT INTO [b96602] VALUES (32,'قال: لأن الإمام مسافر صلى بهم ركعتين ليست له).
٣٩ - قال ابن القاسم: (قلت لمالك رحمه الله): رأيت رجلا صلى في بيته المغرب وخرج إلى المسجد فوجد الإمام يصلي فنسي صلاته التي صلى في بيته وأحرم خلف الإمام، ماذا يفعل؟
قال مالك: إن أيقن بصلاته التي صلى في بيته قبل أن يركع (مع الإمام) يقطع ويخرج، وإن لم يتفكر حتى أتى بركعة يتمادى إلى الثانية ويسلم ويخرج وينوي بها النافلة.',NULL,'52',NULL,NULL,'0');
INSERT INTO [b96602] VALUES (33,'وإن (لم) يتذكر حتى جلس الإمام في التشهد الأخير إذا سلم الإمام (لم) يسلم معه، (فإنه يقوم) ويأتي بالركعة تصح له نافلة.',NULL,'53',NULL,NULL,'0');
INSERT INTO [b96602] VALUES (34,'٤٠ - قال ابن القاسم: قلت لمالك (رحمه الله): رأيت رجلا (توضأ و) صلى الظهر فنسي السلام وجلس ينتظر العصر، فلما صلى العصر ذكر أنه نسي السلام، ماذا يفعل؟
قال مالك: يعيد الظهر والعصر.
٤١ - قال (ابن القاسم): قلت لمالك (رحمه الله): رأيت رجلا صلى شهر رمضان كله مجنبا، (فلما فرغ من صيامه أيقن أنه صلى شهر رمضان كله مجنبا)، ماذا يفعل؟',NULL,'54',NULL,NULL,'0');
INSERT INTO [b96602] VALUES (35,'قال مالك: يغتسل ويعيد صلاته كلها وليس عليه إعادة الصوم. (وإن كان جاهلا فعليه القضاء والكفارة، إلا أن يكون لا علم له).
٤٢ - قال ابن القاسم: قلت لمالك (رحمه الله): رأيت رجلا له إناءان من الماء ولغ كلب في واحد منهما، ولم يدر أيهما الذي ولغ فيه الكلب، ماذا يفعل؟
قال مالك: يتوضأ بكل واحد منهما ويصلي، وصلاته تامة (إن شاء الله). وهذا الفعل يجزئ لعدم الماء.',NULL,'55',NULL,NULL,'0');
INSERT INTO [b96602] VALUES (36,'٤٣ - قال ابن القاسم: (قلت) لمالك (رحمه الله): رأيت رجلا كان له (ماء) مشكوك، شك فيه نجاسة؟
قال مالك: يتيمم ويتوضأ (منه) ويصلي صلاة واحدة.
قال (سحنون): يتيمم ويتوضأ منه.
قال محمد بن سحنون: يتيمم ويصلي ويتوضأ ويصلي.
٤٤ - قال ابن القاسم: قلت لمالك (رحمه الله): رأيت رجلا توضأ من ماء وصلى، فلما فرغ من صلاته وجد في الماء الذي توضأ منه فأرة مسلوخة، ماذا يفعل؟
قال مالك: يعيد في الوقت، فإن خرج الوقت فلا إعادة عليه.',NULL,'56',NULL,NULL,'0');
INSERT INTO [b96602] VALUES (37,'٤٥ - قال ابن القاسم: قلت لمالك (رحمه الله): رأيت رجلا له ثوبان في أحدهما نجاسة، ولم يدر أيهما، ماذا يفعل؟
قال مالك: يصلي بكل ثوب صلاة.
٦٤ - قلت له: ورأيت رجلا واحدا في ليل مظلم ولم يعرف القبلة، ماذا يفعل؟
قال مالك: يصلي إلى أربعة أركان، وصلاته تامة (إن شاء الله).',NULL,'57',NULL,NULL,'0');
INSERT INTO [b96602] VALUES (38,'٤٧ - قال ابن القاسم: قلت لمالك (رحمه الله): رأيت رجلا سجد بأنفه ولم يسجد بجبهته، ماذا يفعل؟
قال مالك: يعيد في الوقت وبعده إلا أن يكون به عذر.
٤٨ - قال ابن القاسم: قلت لمالك (رحمه الله): (رأيت) رجلا فاتته ركعة من صلاة الجمعة (فقام يقضيها)، فلما جلس في التشهد الأخير ذكر أنه نسي (قراءة) أم القرآن، ماذا يفعل؟
قال مالك: يلغ تلك الركعة ويأتي بمثلها بأم القرآن وحدها ويسجد قبل السلام، وصلاته تامة، (إن شاء الله).
٤٩ - قال ابن القاسم: قلت لمالك (رحمه الله): رأيت رجلا دخل في صلاة العصر فصلى ركعة، ثم ذكر أنه نسي صلاة الظهر، ماذا يجب عليه؟',NULL,'58',NULL,NULL,'0');
INSERT INTO [b96602] VALUES (39,'(قال مالك): إن كان مأموما سجنه الإمام ولم يخرج حتى يسلم الإمام ويعيد الظهر والعصر.
وإن كان وحده يضف إليها (ركعة) ثانية ويسلم، فتصح له نافلة، ثم يعيد الظهر والعصر.
٥٠ - (قال ابن القاسم: قلت لمالك رحمه الله: رأيت أقواما أدركهم وقت الصلاة ولم يجدوا ماء إلا قدر ما يتوضأ به واحد منهم.',NULL,'59',NULL,NULL,'0');
INSERT INTO [b96602] VALUES (40,'قال: يتوضأ به الإمام، ويتيمم الباقون).
٥١ - قال ابن القاسم: قلت لمالك (رحمه الله): رأيت رجلا (ما) صلى حتى تحركت الشمس عن وسط السماء، ماذا يفعل؟
قال مالك: آخر وقت الظهر: أول وقت العصر. وآخر وقت العصر: مغيب الشمس. وآخر وقت المغرب التي تكون بعد مغيب الشمس، وهو مغيب الشفق، وآخر وقت العشاء: طلوع الفجر.',NULL,'60',NULL,NULL,'0');
INSERT INTO [b96602] VALUES (41,'وهذا هو الوقت المختار، وأما وقت الضرورة فهو الظهر والعصر مشتركان في الوقت، (ما لم تغب الشمس، والمغرب والعشاء مشتركان في الوقت) ما لم يطلع عليهما الفجر، لكن على الخلاف.
٥٢ - قال ابن القاسم: قلت لمالك (رحمه الله): رأيت رجلا إماما خطب يوم الجمعة، فأقيمت عليه الصلاة وكبر (وقرأ)، فلما وضع يديه على ركبتيه ذكر أن القوم لم يكبروا؟
قال مالك: فإنه يرجع (ويكبر ثم يقرأ ويتم صلاته ويسجد بعد السلام).',NULL,'61',NULL,NULL,'0');
INSERT INTO [b96602] VALUES (42,'(قال أشهب: يقطع ويسلم).
٥٣ - قال (ابن القاسم): قلت لمالك (رحمه الله): رأيت رجلا إماما أحدث وهو راكع، واستخلف رجلا وقد بقيت عليه ركعة غير تلك الركعة التي استخلف فيها، ماذا يفعل؟
قال مالك: يتم بهم صلاتهم ويشير إلى القوم أن يمكثوا، يعني يجلسون، ثم يأتي بما بقي له ويسلم بهم.',NULL,'62',NULL,NULL,'0');
INSERT INTO [b96602] VALUES (43,'٥٤ - قال ابن القاسم: قلت لمالك (رحمه الله): رأيت رجلا إمام القوم فنظرهم، فلم يأت أحد منهم (إليه) فأقام الصلاة على نفسه فصلى ركعة، فدخلوا عليه فأحدث الإمام، فاستخلف واحدا منهم، وخرج عنهم، فلما رجع إليهم قال لهم: إني صليت قبلكم ركعة، وإني شاك في الإمامة، ماذا يجب عليهم؟
قال مالك: صلاتهم باطلة.
٥٥ - قال ابن القاسم: قلت لمالك: رأيت رجلا يهوديا صلى بالناس شهرا أو سنة، ماذا يجب عليهم؟
قال مالك: يعيدون كل ما صلى بهم.',NULL,'63',NULL,NULL,'0');
INSERT INTO [b96602] VALUES (44,'٥٦ - قال ابن القاسم: قلت لمالك: رأيت رجلا صلى في بيته فدخل مع الجماعة (للفضل) ناسيا، فلما ركع (ركعة) ذكر أنه نسي سجدة من الصلاة التي صلى في بيته، ماذا يفعل؟
قال مالك: يقطع ويكون كالداخل، فإذا سلم الإمام يقوم ويأتي بما بقي له، وصلاته تامة، (إن شاء الله).
٥٧ - قال ابن القاسم: قلت لمالك (رحمه الله): رأيت رجلا إماما يخطب يوم الجمعة، وصلى الركعة الأولى، وقام إلى الثانية، ثُم ذكر أنه لم يصل الصبح، ماذا يفعل؟',NULL,'64',NULL,NULL,'0');
INSERT INTO [b96602] VALUES (45,'قال مالك: يقطع الإمام ومن خلفه ويصلي الصبح ويبتدئ الخطبة.
وقال سحنون: يستخلف من يتم بهم صلاتهم.
٥٨ - قال ابن القاسم: قلت لمالك (رحمه الله): رأيت رجلا دخل في الصف فوجد الإمام في التشهد الأخير فأحرم وجلس مع القوم، فلما سلم الإمام قام (المأموم) فقضى ما فاته. فلما سلم قال (له) رجل من الصف: إن الجلسة التي أدركت معنا فهي سجدة السهو؟',NULL,'65',NULL,NULL,'0');
INSERT INTO [b96602] VALUES (46,'قال مالك: صلاته تامة، وإن كان من غير سهو صلاته باطلة.
٥٩ - قال ابن القاسم: قلت لمالك (رحمه الله): رأيت رجلا صلى (صلاة) الصبح بثلاثة أثواب، والظهر بثوبين، والعصر بثوب واحد، (وواحد) من الأثواب نجس، ولا يدري ما هو؟
قال مالك: يأخذ الثوب الذي صلى به (الظهر ويصلي به العصر، ويأخذ الثوب الذي صلى به) العصر ويصلي به الظهر، (وصلاته تامة).',NULL,'66',NULL,NULL,'0');
INSERT INTO [b96602] VALUES (47,'وأما الصبح خرج وقته فلا إعادة عليه، لأن خروج وقته: طلوع الشمس.
٦٠ - قال ابن القاسم: قلت لمالك (رحمه الله): رأيت رجلا دخل في السوق ولطم الصابون ثوبه، هل ثوبه نجس أم لا؟
قال رحمه الله: (ثوبه) نجس إن كان الصابون يستعمل من الزيت التي ماتت فيه دابة، لها نفس سائلة.',NULL,'67',NULL,NULL,'0');
INSERT INTO [b96602] VALUES (48,'٦١ - قال ابن القاسم: قلت لمالك (رحمه الله): رأيت رجلا قام على سريره وحصره البول وقام يبول، (هل هو نجس أم لا؟)؟
قال مالك: (جسده كله نجس، إذا استنثره بيده ويرجع في مرقده، ولم يستنج، لأنه إذا نام يجري الدم في جسده ويعرق ويحكه فإنه ينجس).
٦٢ - قال ابن القاسم: قلت لمالك (رحمه الله): رأيت رجلا توضأ ولم يربط نعلا على رجليه، فوطئ على دم المجزرة أو دم مثله؟',NULL,'68',NULL,NULL,'0');
INSERT INTO [b96602] VALUES (49,'قال مالك: إن كان الدم يابسا ورجله ناشفة فلا شيء عليه.
٦٣ - قال ابن القاسم: قلت لمالك (رحمه الله): رأيت رجلا رعف وهو في الصلاة، ماذا يفعل؟
قال مالك: إن كان الدم قليلا فليفتله (بأصابعه واحدا بعد واحد)، وإن تمادى به خرج من الصلاة حتى ينقطع الدم ويرجع إلى الصلاة وبنى على ما تقدم، وإن تكلم عند خروجه من الصلاة',NULL,'69',NULL,NULL,'0');
INSERT INTO [b96602] VALUES (50,'(يعيد صلاته)، (وإن كان إماما) فليستخلف واحدا منهم يتم بهم صلاتهم.
٦٤ - (مسألة: رجل غاط أو قطع (الواد) ولم يستنج، هل ثوبه (أو جسده) نجس أم لا؟.
قال ابن القاسم: نجس ثوبه وجسده).
٦٥ - قال ابن القاسم: قلت لمالك (رحمه الله): رأيت رجلا توضأ وفي ثوبه نجاسة، هل يصلي به أم لا؟
قال مالك: لا خير فيه إلا أن يغسل ويصلي.',NULL,'70',NULL,NULL,'0');
INSERT INTO [b96602] VALUES (51,'وقال أصبغ بن الفرج: يصلي به للضرورة.
٦٦ - قال ابن القاسم: قلت لمالك: رأيت رجلا صلى بثوب ولم يعلم (أنه نجس)، هل صلاته جائزة أم لا؟
قال مالك: إن كان الثوب لتارك الصلاة يعيد الغسل إن كان اغتسل، والوضوء إن كان توضأ، والصلاة إن كان صلى، لأن ثوب تارك الصلاة نجس مبين للنجاسة، وأما الصلاة فيعيدها في الوقت، وإلا فلا إعادة عليه.
وقال أشهب: إن (كان) الثوب للمرأة يعيد الغسل والوضوء، ولا يعيد الصلاة إلا في وقتها.',NULL,'71',NULL,NULL,'0');
INSERT INTO [b96602] VALUES (52,'٦٧ - قال ابن القاسم: قلت لمالك: رأيت أربعة رجال مسافرين كيف يصلي بعضهم ببعض، واحد منهم (رجل) صالح، والثاني: رجل تقي، والثالث: رجل جليل بهي، والرابع: رجل شريف (كبير السن)، بين لنا يا سيدي من يصلي بهؤلاء؟
قال ابن حبيب: يصلي بهم العالم، وهو الواجب.
وقال أشهب: يصلي بهم التقي لقوله تعالى: {إِنَّ أَكْرَمَكُمْ عِنْدَ اللَّهِ أَتْقَاكُمْ} [الحجرات: ١٣]، إن كان يحسن قراءة أم القرآن فقط.',NULL,'72',NULL,NULL,'0');
INSERT INTO [b96602] VALUES (53,'٦٨ - قال ابن القاسم: قلت لمالك (رحمه الله): مسألة: رجل دخل في صلاة الجهر وأسر فيها بالقراءة، وهو إمام، وسبحوا له فلم يجب حتى تم صلاته، ماذا يفعل (القوم)؟
قال مالك: فأما القوم فلا إعادة عليهم، وأما الإمام يعيد.
٦٩ - (مسألة: رجل مسافر صلى مع إمام مقيم، هل يعيد صلاته أم لا؟.',NULL,'73',NULL,NULL,'0');
INSERT INTO [b96602] VALUES (54,'قال ابن القاسم: وإن كان عامدا أعاد في الوقت فقط، وكذلك الجاهل مثل العامد سواء).
٧٠ - قال ابن القاسم: قلت لمالك: رأيت امرأة تطهرت من حيضها مقدار ما تصلي ركعة (من النهار)، فغربت الشمس عليها، هل تتم صلاتها أم لا؟
(قال ابن القاسم: تتم صلاتها، ولا شيء عليها.
٧١ - مسألة: امرأة خرجت من (صلاة الفجر) وهي عريانة، هل تصلي قائمة أم لا؟).
قال مالك: إن أمنت ولم يرها أحد فصلاتها تامة، وإن لم تأمن فلا تجزيها صلاتها.',NULL,'74',NULL,NULL,'0');
INSERT INTO [b96602] VALUES (55,'٧٢ - قال ابن القاسم: قلت لمالك (رحمه الله): رأيت رجلا مسافرا صلى على دابته خائفا أو مريضا، ماذا يفعل؟
قال (مالك): صلاته تامة.
٧٣ - قال ابن القاسم: قلت لمالك (رحمه الله): رأيت رجلا إماما قهقه والناس خلفه، ماذا يفعل؟ (هل يستخلف من يتم بالناس أم لا؟).
قال (مالك): يستخلف من يتم بالناس ويكون هو من المأمومين، فإذا سلم الإمام قام وأتى بما بقي له، وصلاته تامة معهم.',NULL,'75',NULL,NULL,'0');
INSERT INTO [b96602] VALUES (56,'٧٤ - قال ابن القاسم: قلت لمالك: رأيت رجلا فاتته ركعة من صلاة الصبح وأدرك (الثانية مع) الإمام فاستخلفه الإمام، فلما جلس (في التشهد) رجع الإمام الأول، وقال له: أصلح بالقوم صلاتهم فإني نسيت تكبيرتين وتحميدة.
قال ابن القاسم: يقوم بغير تكبير ويشير على القوم أن يمكثوا، ويأتي (بركعة) بأم القرآن وسورة ويتشهد ويسجد قبل السلام، وصلاته تامة.',NULL,'76',NULL,NULL,'0');
INSERT INTO [b96602] VALUES (57,'وقال أصبغ بن الفرج: يسجد لسهوه ويتشهد ويسلم ويعيد الصلاة استحبابا.
قال علي بن زياد: قول ابن حبيب أحسن، لأن من نسي ثلاث تكبيرات وثلاث تحميدات يجزئ فيهم السجود.
٧٥ - قال ابن القاسم: قلت لمالك (رحمه الله): رأيت (رجلا) صلى المغرب في بيته، ثم أتى إلى المسجد فقدموه إلى الصلاة فلما جلس في التشهد الأخير ذكر أنه صلى في بيته، ماذا يفعل؟
قال مالك: يشير على القوم أن يمكثوا ويأتي بركعة ويتشهد ويسلم، تصح له نافلة ويعيد القوم صلاتهم، لأنها فريضة بخلاف النافلة.',NULL,'77',NULL,NULL,'0');
INSERT INTO [b96602] VALUES (58,'٧٦ - قال ابن القاسم: قلت لمالك (رحمه الله): رأيت رجلا خرج مسافرا ومعه رجال عليهم جنابة واغتسلوا من الجنابة في آخر الطريق، فقاموا شهرا، ثم رجعوا إلى الموضع الذي',NULL,'78',NULL,NULL,'0');
INSERT INTO [b96602] VALUES (59,'اغتسلوا فيه، فذكر واحد منهم أنه نسي لمعة في أذنيه، والآخر ترك لمعة في ذراعيه ماذا يفعل؟
قال مالك: الذي ترك لمعة في أذنيه لا إعادة عليه، والذي ترك لمعة في ذراعيه يعيد كل ما صلى في سفره.
٧٧ - قال ابن القاسم: قلت لمالك (رحمه الله): رأيت رجلا صلى المغرب في بيته فدخل مع الإمام ساهيا، فلما صلى الإمام ركعتين',NULL,'79',NULL,NULL,'0');
INSERT INTO [b96602] VALUES (60,'ركعتين أحدث، فاستخلفه، فلما تم صلاته ذكر أنه صلى في بيته بثوب نجس، ماذا يفعل؟
قال مالك: يعيد الإمام صلاته، وصلاة القوم تامة.
٧٨ - قال ابن القاسم: قلت لمالك (رحمه الله): رأيت رجلا فاتته ركعة من صلاة المغرب، فلما صلى مع الإمام الثانية أحدث الإمام فاستخلفه الإمام وقال له: أصلح بالقوم صلاتهم فإني نسيت سجدة سقطت لي من الأولى، ماذا يفعل؟
قال مالك: إذا أتى بالركعة التي هو فيها يجلس ويقوم إلى الركعة يقرأ فيها بأم القرآن فقط ويقوم القوم معه ويركع ويسجد قبل السلام، وإن نسي قبل السلام فبعده بقرب.',NULL,'80',NULL,NULL,'0');
INSERT INTO [b96602] VALUES (61,'٧٩ - قال ابن القاسم: قلت لمالك (رحمه الله): رأيت رجلا مأموما مع القوم وقد كبر القوم، ولم يكبر الإمام، كيف صلاتهم؟ هل يستخلف أم لا؟
قال مالك: صلاتهم باطلة.
٨٠ - قال ابن القاسم: قلت لمالك (رحمه الله): رأيت رجلا وقف في الركعة الرابعة ثم ذكر أنه نسي أم القرآن من الأولى، وسجدة من الثانية، وركوعا من الثالثة، ماذا يفعل؟
قال (مالك): يقرأ في الركعة التي هو فيها بأم القرآن وسورة، ثم يركع ويسجد، وتصح عنده لأنها أول صلاته، ثم يأتي',NULL,'81',NULL,NULL,'0');
INSERT INTO [b96602] VALUES (62,'يأتي بالثانية بأم القرآن وسورة، ثم يجلس، ثم يأتي بركعتين بأم القرآن، ثم يسجد لسهوه قبل السلام، لأنه زاد ونقص، فأما الزيادة فهي الركعة التي بنى بها، وأما النقص فهي السورة التي ترك من الركعة الأولى.
٨١ - قال ابن القاسم: قلت لمالك (رحمه الله): رأيت رجلا جلس في التشهد الأخير، وكان خلف الإمام، ثم شرب الماء ناسيا، ماذا يفعل؟
قال مالك: يسلم الإمام ويسلم معه، وكذلك شيء قليل مثله ناسيا.
٨٢ - قال ابن القاسم: قلت لمالك (رحمه الله): رأيت رجلا دخل الحمام ليغتسل من الجنابة ويتوضأ هل يصلي فيه أم لا؟',NULL,'82',NULL,NULL,'0');
INSERT INTO [b96602] VALUES (63,'قال مالك: يجزيه ذلك ولا يعيد الوضوء ولا الغسل إن اعتقد النية.
قلت له: وإن كان في الشتاء أو في البرد يفعل ذلك؟
قال: يجزيه.
٨٣ - قال ابن القاسم: قلت لمالك (رحمه الله): رأيت رجلا سها عن غسل يديه: اليسرى قبل اليمنى، ورجليه: اليسرى قبل اليمنى، ماذا يفعل؟
قال مالك: ينتقض وضوءه من حين سها. وإن كان قد صلى ساهيا فلا شيء عليه في صلاته.
٨٤ - قال ابن القاسم: قلت لمالك: رأيت رجلا توضأ ودخلت عليه جارية، وهي تحدثه لم يذكر منها شيء، ماذا يفعل؟',NULL,'83',NULL,NULL,'0');
INSERT INTO [b96602] VALUES (64,'قال مالك: لا يعيد الوضوء (على كل حال).
٨٥ - قال ابن القاسم: قلت لمالك (رحمه الله): رأيت (رجلا) توضأ وصلى، فلما فرغ من صلاته رأى في ثوبه ماء أبيض، ماذا يفعل؟
قال مالك: إذا كانت له رائحة يغسل ثوبه ويعيد الصلاة، وإن لم يكن له رائحة فلا شيء عليه، لأن الول له رائحة زفرة، والماء ليس له رائحة.
٨٦ - قال ابن القاسم: قلت لمالك (رحمه الله): رأيت رجلا كان له قرح تسيل بالدم والصديد، هل ثوبه نجس أم لا؟
قال مالك: إن كانت (تسيل) بالدم تنجس الثوب والبدن، وإن كانت الصديد تنجس، وإن كانت قرح كثيرة يصلي لأجل الضرورة، فلا شيء عليه.',NULL,'84',NULL,NULL,'0');
INSERT INTO [b96602] VALUES (65,'٨٧ - قال ابن القاسم: قلت لمالك (رحمه الله): رأيت رجلا شرع في صلاة النافلة وهو في أرض الخلاء، وعنده دابة وفرت عنه، هل يتركها أو يمشي إليها؟
قال مالك: إن كانت قريبة يقوم إليها حتى يقبضها ويتم صلاته، وإن كانت بعيدة جدا صلاته باطلة.
٨٨ - قال ابن القاسم: قلت لمالك (رحمه الله): رأيت رجلا صلى الظهر في بيته وأتى إلى المسجد وجدد وضوءه يريد به استحبابا وصلى مع الإمام، فلما صلى (ذكر أنه صلى في بيته في الوقت، وذكر',NULL,'85',NULL,NULL,'0');
INSERT INTO [b96602] VALUES (66,'أنه) شك في وضوئه الأول الذي صلى (به) في بيته، ماذا يفعل؟
قال مالك: يعيد الوضوء والصلاة.
وقال ابن القاسم: صلاة المسجد تامة؟
وقال أشهب: يعيد.
وقال سحنون: يعيد، (لأن دخوله في الصلاة فضيلة).
وقال أصبغ (بن الفرج): لا إعادة عليه.
٨٩ - وقال ابن القاسم: قلت لمالك (رحمه الله): رأيت رجلا صلى ركعتين من الظهر، ثم إنه شك في الوضوء، (ثم ذكر أنه على وضوء من غير شك)، ماذا يفعل؟',NULL,'86',NULL,NULL,'0');
INSERT INTO [b96602] VALUES (67,'قال مالك: إن أيقن على وضوء من غير شك صلاته تامة، وإن بقي الشك على حاله صلاته باطلة.
٩٠ - قال ابن القاسم: قلت لمالك (رحمه الله): رأيت رجلا جلس في التشهد الأخير وذكر أنه نسي (قراءة) أم القرآن من الركعة الأولى في الصلاة الرباعية، ماذا يفعل؟
قال مالك: فيها ثلاثة أقوال:
الأول: يسجد سجدتين قبل السلام، ويعيد الصلاة احتياطا.
وبهذا القول كان يأخذ مالك في خاصة نفسه.',NULL,'87',NULL,NULL,'0');
INSERT INTO [b96602] VALUES (68,'والقول الثاني: يسجد سجدتين قبل السلام، وصلاته تامة، وما هو عندنا البيان، والإعادة أفضل وأحسن.
والقول الثالث: يلغيها ويأتي بركعة مثلها، ويسجد بعد السلام.
٩١ - (مسألة: رجل صلى في بيته بصلاة الإمام في المسجد، هل تجوز أم لا؟).
قال مالك: وأما رجل يصلي على صلاة الإمام التي يصلي في المسجد فلا تجزيه صلاة الإمام، إلا أن يكون مضرورا من مرض',NULL,'88',NULL,NULL,'0');
INSERT INTO [b96602] VALUES (69,'أو خوف أو طين (أو ظلمة) أو مثل ذلك فتجزيه، ويصلي صلاته، (إلا) خطبة الجمعة، فإنها لا تجزيه إلا في المسجد مع الإمام والجماعة.
٩٢ - سأل ابن القاسم مالكا عن المسافرين ومعهم نساء، ماذا يفعلون؟ وكيف تكون صلاة الرجال مع النساء؟ وأين يكون الإمام؟',NULL,'89',NULL,NULL,'0');
INSERT INTO [b96602] VALUES (70,'قال مالك (رحمه الله): إذا كان النهار يتباعدون بعضهم عن بعض ويكون الإمام في الوسط.
وإذا كان الليل مظلما يتقدم الإمام، وصلاتهم تامة (إن شاء الله).
٩٣ - قال ابن القاسم: (قلت لمالك رحمه الله: مسألة) (مسافرين خرجوا) ومعهم امرأة وماتت في الطريق، وليس لها رجل (من محارمها)، وليس معهم نساء، ماذا يفعلون؟ هل يغسلونها أم لا؟
قال مالك: يغسلونها من فوق ثوبها ويصلون عليها، وإن لم يجدوا الماء (يتيممون) وجهها ويديها ويصلون (عليها).',NULL,'90',NULL,NULL,'0');
INSERT INTO [b96602] VALUES (71,'٩٤ - قال ابن القاسم: قلت لمالك (رحمه الله): رأيت رجلا توضأ من حفرة، فلما توضأ وجد الماء الذي توضأ منه (فيه) فأرة مسلوخة، هل يعيد الصلاة والوضوء أم لا؟
قال (مالك): يعيد في الوقت. وإن خرج الوقت فلا إعادة عليه.
٩٥ - قال ابن القاسم: قلت لمالك (رحمه الله): رأيت رجلا توضأ وعلى صدغيه قرطاس (من وجع أخذه)، ومسح على القرطاس، ولم يمسح على رأسه، هل صلاته تجزيه أم لا؟
قال مالك: تجزيه إذا بالغ في الغسل.',NULL,'91',NULL,NULL,'0');
INSERT INTO [b96602] VALUES (72,'٩٦ - قال ابن القاسم: قلت لمالك (رحمه الله): رأيت رجلا وقف في الصلاة وشك أنه خرج منه مذي، ماذا يجب عليه؟
قال مالك: قد بلغني في الحديث عن سعيد بن المسيب رضي الله (عنه قال): لو سال على فخذي ماء لا انصرفت، لأنه من الشيطان.
وقال عمر بن الخطاب رضي الله عنه: خرج مني مذي وأنا في الصلاة، فلما عرفت أنه من الشيطان، وقد جاء في الحديث أن الشيطان إذا أيس أن يطاع إليه أتى الإنسان على هذا الوجه.',NULL,'92',NULL,NULL,'0');
INSERT INTO [b96602] VALUES (73,'٩٧ - قال ابن القاسم: قلت لمالك (رحمه الله): رأيت رجلا صلى الصبح بوضوء ثم بقي إلى الظهر وجدد الوضوء من غير نقص، ثم صلى (به) الظهر، ثم العصر، ثم المغرب، ثم العشاء، ثم ذكر أنه نسي شيئا من فرائض وضوئه، ولم يدر من أي وضوء؟
قال مالك: يعيد الصبح وحده، لأن الصلاة تكون بوضوء واحد.
٩٨ - قال ابن القاسم: قلت لمالك (رحمه الله): إمام صلى بالقوم وهو مرتفع والقوم تحته، (ماذا يجب عليهم)؟
قال مالك: الإعادة.',NULL,'93',NULL,NULL,'0');
INSERT INTO [b96602] VALUES (74,'قلت له: وإن صلى معهم؟
قال: صلاتهم تامة.
٩٩ - قال ابن القاسم: قلت لمالك (رحمه الله): رجل دخل في المسجد فقام إلى الصلاة، وأتاه رجل آخر فقام للصلاة وظنه الأول أنه إمام، وظن أهل المسجد أنه إمام، ماذا يفعلان؟
قال مالك: ويسلم قبل صاحبه، يعني الذي ظن أنه إمام صلاته باطلة.
١٠٠ - قال ابن القاسم: قلت لمالك (رحمه الله): رجل فاتته الركعة الأولى مع الإمام وصلى معه الثانية ورعف في الثانية، ثم رجع فوجد الإمام قد سلم.',NULL,'94',NULL,NULL,'0');
INSERT INTO [b96602] VALUES (75,'قال: إنه يبدأ بالبناء قبل القضاء، ويأتي بالركعة فيقرأ فيها بأمر القرآن خاصة ويتشهد، ثم يقوم ويأتي بالركعة الأخرى بأم القرآن خاصة ويجلس عليها متتابعة للإمام.
وقال عبد الملك: يجلس عليها ويتشهد على مذهب ابن القاسم، ثم يقوم ويأتي بالركعة بأم القرآن والسورة ويتشهد ويسلم، فتصير صلاته كاملة كلها جلوسا على مذهب ابن القاسم، تسمى عنده أم الجناحين، وكذلك المقيم يدرك الركعة مع المسافر فإنه يقوم بعد أن سلم الإمام أيضا، ويصلي ما فاته يبدأ بالبناء ثم القضاء، ويفعل في بقية صلاته كلها.
١٠١ - وقال ابن القاسم: قلت لمالك (رحمه الله):رأيت رجلا أدرك الإمام في الثانية ورعف في الثالثة، ماذا يفعل؟
قال (مالك): الواجب فيها واحد، وتسمى أيضا أم الجناحين، وهاتان المسألتان لا نظير لهما في المذهب.',NULL,'95',NULL,NULL,'0');
INSERT INTO [b96602] VALUES (76,'١٠٢ - قال ابن القاسم: قلت لمالك (رحمه الله): رأيت رجلا إماما صلى بالقوم ونسي تكبيرة الإحرام (حتى صلى وسلم، ثم ذكر أنه نسي تكبيرة الإحرام)؟
قال: يعيدون كلهم.
١٠٣ - قال ابن القاسم: قلت لمالك (رحمه الله): رجل أحرم قبل الإمام وسلم قبله، ماذا يفعل؟
قال مالك: فإن ذكره في الوقت يرجع ويكبر ويتشهد ويسلم، وإن لم يذكر حتى طال أعاد صلاته.
١٠٤ - قال ابن القاسم: قلت لمالك (رحمه الله): رأيت رجلا نسي أم القرآن في نصف صلاته.
قال مالك: يعيد صلاته.',NULL,'96',NULL,NULL,'0');
INSERT INTO [b96602] VALUES (77,'١٠٥ - قال ابن القاسم: قلت لمالك (رحمه الله: مسألة عن رجل نسي سجدتين؟
قال مالك: يسجدهما أين ما ذكرهما ولو بعد شهر.
١٠٦ - قال ابن القاسم: قلت لمالك رحمه الله مسألة عن) رجل جهر فيما يسر فيه؟
قال مالك: يعيد صلاته.
قال ابن القاسم: يسجد بعد السلام، وإن سر فيما يجهر يسجد قبل السلام.
١٠٧ - قال ابن القاسم: قلت لمالك (رحمه الله مسألة عن) إمام صلى بالقوم صلاة الصبح فترك القنوت (ساهيا) فسجد بالقوم جاهلا.
قال (مالك): عليه وعليهم الإعادة، لأن القنوت لا سجود له.',NULL,'97',NULL,NULL,'0');
INSERT INTO [b96602] VALUES (78,'١٠٨ - قال ابن القاسم: قلت لمالك (رحمه الله مسألة عن) رجل اغتسل للجمعة ولم يغتسل للجنابة، هل يجزيه غسل الجمعة أم لا؟
قال مالك: يجزيه.
١٠٩ - قال ابن القاسم: قلت (لمالك رحمه الله مسألة عن رجل) فرغ من صلاته، وذكر أنه نسي لمعة من جسده؟
قال (مالك): يعيد الغسل ويعيد الصلاة.
انتهى وكمل المجالس التي سأل عنها ابن القاسم مالكا رحمه الله ورضي عنهما، آمين.',NULL,'98',NULL,NULL,'0');
INSERT INTO [b96602] VALUES (79,'زيادات النسخة (١٣٥٨٣):
١١٠ - مسألة: من صلى الوتر بالفاتحة وحدها عامدا، فلا سجود عليه. وإن كان ساهيا يسجد بعد السلام، وصلاته تامة.
١١١ - مسألة: رجل كان له ماء في الثياب من ماء، وأحدها نجس، لا يدري أيهما بعينه.
قال مالك: يتوضأ بواحد منهما ويصلي ويتوضأ بالآخر ويصلي.',NULL,'99',NULL,NULL,'0');
INSERT INTO [b96602] VALUES (80,'زيادات النسخة (١٢٣٨٩):
١١٢ - قال: سألت مالكا رضي الله عنه عن قول الله تعالى: {يَا أَيُّهَا الَّذِينَ آمَنُوا إِذَا قُمْتُمْ إِلَى الصَّلَاةِ فَاغْسِلُوا وُجُوهَكُمْ وَأَيْدِيَكُمْ إِلَى الْمَرَافِقِ وَامْسَحُوا بِرُءُوسِكُمْ وَأَرْجُلَكُمْ إِلَى الْكَعْبَيْنِ} [المائدة: ٦]، ما يريد الله بالمسح؟
قال: الغسل، ما تمسح على الرجلين، إنما تغسلهما.
١١٣ - مسألة: قلت: ما يريد بقوله تعالى: {إِذَا قُمْتُمْ} [المائدة: ٦]؟ قال: يريد من المضاجع.
١١٤ - قال ابن القاسم: رأيت رجلا صلى ومسح برأسه ورجليه أولَ الآية، ولا يدري ما يفسدها لصلاته تجزئ عنه؟ ويجزيه المسح؟.
قال: لا تجزيه صلاته، وعليه الإعادة، إنما هو الغسل كما أخبرتك.
١١٥ - قال ابن القاسم: قلت: من قام من نومه وأدخل يديه في الإناء، وتوضأ وصلى؟
قال: صلاته تجزيه إن لم تصب يداه نجاسة قبل دخولها في الإناء.',NULL,'100',NULL,NULL,'0');
INSERT INTO [b96602] VALUES (81,'١١٦ - قال ابن القاسم: قلت لمالك رحمه الله: أرأيت رجلا يصيب بطن قدميه الثلج، ويتقرح ويتعصب، وعلى تلك القروح باللفائف، هل يتوضأ ويغسل تلك القروح والخروق أو يتيمم؟
قال: يتوضأ ويمسح على الخروق أحب إلي من التيمم.
١١٧ - قال ابن القاسم: قلت لمالك: أرأيت رجلا دخل في الحمام فاغتسل ولم يتوضأ فصلى، أصلاته تامة أم لا؟
قال: إن بالغ في الغسل فصلاته تامة.
١١٨ - قال ابن القاسم: قلت لمالك: أرأيت رجلا توضأ ومسح على خفيه، ثم مشى إلى المسجد ودخل خفيه حصاة، فنزعها وأخرجها، ثم رد الخف في رجليه، وصلى؟
قال: يسير فيغسل قدميه مكانه، ويعيد الصلاة.
١١٩ - قل ابن القاسم: قلت لمالك: أرأيت رجلا دخل الحمام، فلما أراد الخروج منه توضأ بماء سخون، هل يجزيه ذلك أم لا؟
قال: لا أرى ذلك جائزا، (له) أن يتوضأ من البئر، ثم قال بعد ذلك: والله ما أرى دخول الحمام بصواب، فكيف الوضوء منه؟
١٢٠ - قال: قلت: الوضوء قبل الغسل، أو الغسل قبل الوضوء؟
قال: الوضوء قبل الغسل أحب إلي.',NULL,'101',NULL,NULL,'0');
INSERT INTO [b96602] VALUES (82,'١٢١ - قال: قلت لمالك: الإمام يصلي بالقوم وهو غير متوضأ ناسيا؟
قال: صلاة القوم تامة، ويعيد هو.
١٢٢ - قال: قلت: رأيت رجلا رعف في الصلاة فخرج ليغسل الدم فوطئ برجله نجاسة رطبة؟
قال: انهدمت صلاته كلها.
١٢٣ - قال: قلت لمالك: الإمام يصلي بالقوم فجره حظ شديد في أن ستخلف من يصلي بالقوم وإن صلى وحده انهدمت صلاته فلا يقطع بالسلام، وقد جاء عن النبي - صلى الله عليه وسلم - أنه قال: لا يدافع أحدكم الأخبثين، يعني: البول والغائط.
١٢٤ - قال: قلت: أيقدم الناس أعمى؟
قال: لا بأس بذلك، وقد استخلف رسول الله - صلى الله عليه وسلم - بن حازم منشدد على المدينة، وكان رجلا أعمى، وإمامة الأعمى جائزة.',NULL,'102',NULL,NULL,'0');
INSERT INTO [b96602] VALUES (83,'١٢٥ - قال: قلت لمالك: رأيت رجلا نسي الصبح فذكرها فقام يقضيها فنام فلما كان وقت الظهر ذكر أن الثوب الذي صلى به صلاة الصبح ناجس. لا شيء عليه.
١٢٦ - مسألة: قلت لمالك: رأيت رجلا نسي التشهد حتى سلم.
قال: يتشهد ويسلم.
١٢٧ - قال ابن القاسم: قلت لمالك: رأيت رجلا جاء ثم توضأ وصلى الظهر، فلما كان في صلاة العصر وجد بللا، ولا يدري إن كان هو ماء أو بول.
قال: لا شيء عليه.
١٢٨ - قال: قلت لمالك: رأيت رجلا توضأ ثم لبس خفه الواحد. فلما أراد أن يلبس الثاني.
قال: ينقض الخف من رجله ويعيد الوضوء كما كان أول مرة.
١٢٩ - قال: قلت: الرجل المريض لا يستطيع الوضوء.
قال: يتيمم.
١٣٠ - قال: قلت: أيمسح الرجل الخف المثقوب؟
قال: الثقب اليسير لا بأس به، وإذا تفاحش فلا يمسح عليه.',NULL,'103',NULL,NULL,'0');
INSERT INTO [b96602] VALUES (84,'١٣١ - قال: قلت: هل يؤذن المسافر؟
قال: لا آذان عليه.
١٣٢ - قال: قلت لمالك: رأيت رجلا أراد أن يؤذن فأقام ساهيا وأراد أن يقيم فأذن.
قال: يعيد حتى يكون على نية.
١٣٣ - قال: قلت لمالك: يصلي متيمم بمتوضئين؟
قال: لا بأس عليه، والمتوضئ أحب إلي.
١٣٤ - قال: قلت لمالك: أيؤم الرجل بالنساء؟
قال: لا بأس بذلك.
١٣٥ - قال: أيؤم (المجنون والسكران)؟
قال: لا يؤم المجنون ولا السكران ولا من لا عقل له.
١٣٦ - قال: قلت: أيصلي خلف المحدود وولد الزنا؟
قال: إذا ظهرت توبة المحدود يصلى خلفه، وأما ولد الزنا فلا يكون إماما.
١٣٧ - قال: قلت لمالك: كيف يرفع الرجل يديه في الصلاة؟
قال: حذو منكبيه.',NULL,'104',NULL,NULL,'0');
INSERT INTO [b96602] VALUES (85,'١٣٨ - قال: قلت لمالك: ايقسم الرجل ماذا يفعل؟
الجواب: قال ابن القاسم: يعيد الصلاة هو ومن خلفه.
١٣٩ - قال ابن القاسم: رأيت رجلا نسي من صلاتين أحدهما من صلاة الليل، والأخرى من صلاة النهار، ماذا يفعل؟
قال مالك: يصلي سبع صلوات.
١٤٠ - قال ابن القاسم: رجل كان واقفا في الرباعية، وذكر أربع سجدة، لا يدري من أي صلاة هي؟
قال ابن القاسم: إن كان في صلاة الظهر بطلت عليه ويسلم ويصلي العصر ثم المغرب ثم العشاء الآخر، أعاد صلاة الصبح على وجوب الترتيب.',NULL,'105',NULL,NULL,'0');
INSERT INTO [b96602] VALUES (86,'زيادات النسخة (١٣٥٩٤)
١٤١ - مسألة: ما تقول في رجل احتلم في نومه ثم قام فلم يجد أثرا فتوضأ ثم صلى، ثم خرج منه الماء الدافق.
جوابه: قال: إن خرج منه الماء الدافق وهو في الصلاة فليقطع ويخرج يغسل ويعيد الصلاة إن شاء الله.
١٤٢ - مسألة: قلت: الرجل الجنب يغتسل في البركة فيها ماء ثم يصلي، أصلاته مجزية عنه؟
جوابه: قال: إن كانت من البرك العظام مثل التي بين مكة والمدينة فصلاته تامة، وإن كانت مثل التي يجف ماؤها فيغتسل من غيرها ويعيد صلاته.
١٤٣ - مسألة: قلت: الرجل يخضب رأسه ولحيته بالحناء، ثم يتوضأ للصلاة ويمسح على رأسه ولم يباشر أصول الشعر، يجزيه ذلك أم لا؟
قال: والله لا أحب ذلك.',NULL,'106',NULL,NULL,'0');
INSERT INTO [b96602] VALUES (87,'قلت: قد فعله، وعلمت أنك تكرهه، ولكن تجزئ عنه صلاته؟
قال: حسابه على الله.
١٤٤ - مسألة: قلت: أرأيت إن لبس ثوبا فيه جنابة وتوضأ للصلاة وعرق فيه ثم صلى، أصلاته مجزية عنه؟
جوابه: قال: أحب أن يغسل جسده، إلا أن يعرف الموضع الذي فيه الجنابة فيغسله، فإن صلى بذلك أعاد في الوقت.
١٤٥ - مسألة: قلت: رجل جنب دخل النهر أو البحر فاغتسل وتدلك ولم يتوضأ، ثم صلى أترى صلاته مجزية عنه.
جوابه: قال: نعم صلاته مجزية عنه إذا بالغ في الغسل.
١٤٦ - مسألة: قلت: رجل قام من نومه فوجد بللا على ذكره أو على سرته ثم توضأ وصلى ولم يجد بللا في ملحفته التي قام فيها، صلاته مجزية عنه؟',NULL,'107',NULL,NULL,'0');
INSERT INTO [b96602] VALUES (88,'جوابه: قال: صلاته مجزية عنه، إلا إن تيقن أنه منيٌّ، وربما كان عرقا.
١٤٧ - مسألة: قلت لمالك: ما معنى التيمم؟
جوابه: قال: يضرب ضربة لوجهه وضربة ليديه، فيمسحهما إلى المرفقين.
١٤٨ - مسألة: قلت: إن أجنب رجل ولم يجد الماء ما يصنع؟
قال: يتيمم ويصلي.
قلت: فإن وجد الماء بعد صلاته؟
قال: أحب إلي ما كان في الوقت.
١٤٩ - مسألة: قلت: رجل توضأ للصلاة ثم مس ذكره بظاهر الكف يعيد الوضوء؟
جوابه: قال: لا شيء عليه إنما يكره بباطن الكف.
١٥٠ - مسألة: قلت: رجل صلى في ثوب حاضت فيه امرأة، ثم رأى بعد صلاته موضعا فيه دم.',NULL,'108',NULL,NULL,'0');
INSERT INTO [b96602] VALUES (89,'جوابه: قال: يغسل الدم ويعيد الصلاة.
١٥١ - مسألة: قلت: لمالك: الرجل يدخل الحمام فيغتسل ويتوضأ من الماء السخن ترى أن يصلي بذلك الوضوء؟ ويجزيه ذلك الغسل؟
جوابه: قال: ما يعجبني ذلك، والغسل بالماء البارد أحب إلي.
قلت: يكون في الشتاء والبرد فيغتسل من ماء الحمام؟
قال: والله ما دخول الحمام بصواب.
١٥٢ - مسألة: رجل توضأ في ثوب نجس وعرق فيه، هل يصلي فيه أم لا؟
قال مالك: لا خير في صلاته، إلا أن يغتسل ويصلي.
١٥٣ - مسألة: رجل صلى المغرب وكان إماما، ثم أتى المسجد فقدموه إلى الصلاة فلما جلس في التشهد ذكر سجدة لا يدرى من أي صلاة هي؟ من التي صلى في بيته أو من التي صلى بالقوم، ماذا يفعل؟
قال مالك: يقوم بغير تكبير ويأتي بركعة بأم القرآن خاصة ويتشهد ويسلم ويسجد بعد السلام، وصلاته تامة.',NULL,'109',NULL,NULL,'0');
INSERT INTO [b96602] VALUES (90,'١٥٤ - مسألة: رجل ركع ركعتين في الظهر ثم شك في الحدث، فلما سلم ذكر أنه على غير وضوء.
قال ابن القاسم: يصلي صلوات كثيرة.
وقال مالك: يصلي فيها سبع صلوات.',NULL,'110',NULL,NULL,'0');
