INSERT INTO [b96616] VALUES (268,'١٠٦ - شرح المواهب اللدنية - المطبعة الأزهرية الم<span data-type="title" id=toc-143>ص</span>رية - الطبعة الأولى ١٣٢٥ هـ - محمد بن عبد الباقي الزرقاني.
