INSERT INTO [b96616] VALUES (269,'١١٧ - صرا<span data-type="title" id=toc-145>ع </span>بين الحق والبا<span data-type="title" id=toc-144>ط</span>ل - مكتبة النهضة المصرية - ط. ثانية ١٣٨٨ هـ - ١٩٦٨ م - سعد صادق محمد.
