معاذ بن جبل الأنصار/ سلمة ١٩ هـ وال عامل الجند ١٠ هـ ابن هشام (ج ٢، ص ٦٠٠) .
البلاذري، أنساب (ج ١، ص ٩٧، ٩٨) .
أبو موسى الأشعري الأشعريون ٤٢ هـ وال عامل مارب ١٠ هـ البلاذري (ج ١، ص ٩٣، ٩٤) .
علي بن أبي طالب بنو هاشم ٤٠ هـ عامل نجران/ اليعقوبي، تاريخ (ج ٢، ص ٧٦) .
عمرو بن حزم النجار ٥١ هـ وال عامل نجران ٩ هـ م. ن (ج ١، ص ٩٣، ٩٤) .
أبو سفيان بن حرب قريش/ عبد شمس ٣٢ هـ وال نجران ١٠ هـ م. ن',NULL,'251',NULL,NULL,'0');
INSERT INTO [b9762] VALUES (242,'عامر بن شمر الهمداني همدان/ وال همدان/ ابن خالدون، تاريخ (ج ٢، ص ٨٤٣، ٨٤٤) .
طاهر بن أبي هالة تميم/ وال عك والأشعريين/ م. ن (ص ٨٤٤) .
زياد بن لبيد الأنصار/ بياضة ٤١ هـ وال عامل بعض حضرموت ٩ هـ البلاذري (ج ١، ص ٥٢٩) .
صرد بن عبد الله الأزد/ عامل الصدف ٩ هـ ابن هشام (ج ٢، ص ٦٠٠) .
وائل بن حجر ربيعة ٥٠ هـ وال بعض حضرموت ٩ هـ ابن عبد البر، الاستيعاب (ج ٤، ص ١٥٦٢)
العلاء بن الحضرمي مولى عبد شمس ١٤ هـ وال عامل البحرين ٩ هـ ابن سعد، الطبقات (ج ١، ص ٢٣٦) .
المنذر بن ساوي عبد قيس ١١ هـ وال البحرين ٩ هـ م. ن
إبان بن سعيد ابن العاص بنو أميّة ١٥ هـ وال البحرين ١٠ هـ م. ن (ص ٣٦٠، ٣٦١) .
عمرو بن العاص قريش ٤٣ هـ وال عامل عمان ٩ هـ ابن هشام (ج ٢، ص ٦٠٠) .
عمرو بن سعيد ابن العاص بنو أميّة ١٢ هـ وال (تبوك، فدك، خيبر) ٩ هـ خليفة بن خياط، تاريخ (ج ١، ص ٦٢) .
صرد بن عبد الله الأزد/ وال جرش ٩ هـ ابن سعد (ج ١، ص ٣٣٧) .
حذيفة بن اليمان عبس ٣٦ هـ وال دبا ٩ هـ م. ن (ج ٥، ص ٥٢٧) .
أبان بن سعيد ابن العاص بنو أميّة ١٥ هـ وال الخط ٩ هـ ابن حزم، (ص ٢٤) .',NULL,'252',NULL,NULL,'0');
INSERT INTO [b9762] VALUES (243,'بلال بن الحارث مزينة ٦٠ هـ وال وادي العقيق ٩ هـ ياقوت، معجم البلدان (ج ٤، ص ١٣٩) .
علي بن الحارث بن كعب كعب/ شيخ بنو الحارث ٩ هـ ابن هشام (ج ٢، ص ٥٩٤) .
قضاعي بن عمرو بنو عذرة/ شيخ بنو عذرة ٩ هـ ابن سعد (ج ١، ص ٢٧٠) .
صرد بن عبد الله الأزد/ شيخ الأزد ٩ هـ م. ن (ج ١، ص ٣٣٨) .
قيس بن مالك همدان/ شيخ همدان ٩ هـ م. ن (ص ٢٤٠) .
امرؤ القيس بن الأصبع بنو كلب/ شيخ كلب ٩ هـ الطبري، التاريخ (ج ٣، ص ٢٤٣) .
قيس بن مالك الأرجبي أرحب/ شيخ بنو أرحب ٩ هـ ابن سعد (ج ١، ص ٣٤١) .
عبادة بن الأشيب عنز/ شيخ عنز ٩ هـ ابن الأثير، أسد الغابة (ج ٣، ص ١٠٤) .
المهاجر بن أبى أمية مخزوم ١٢ هـ عامل صنعاء/ ابن هشام (ج ٢، ص ٦٠٠) .
عدي بن حاتم الطائي طيء ٦٧ هـ عامل طيّء أسد ١٠ هـ م. ن (ج ٢، ص ٦٠٠) .
خالد بن سعيد بنو أميّة ١٤ هـ عامل مراد ومذحج ١٠ هـ م. ن
مالك بن نويرة تميم ١٢ هـ عامل بنو حنظلة ١٠ هـ م. ن
الزبرقان بن بدر تميم ٤٥ هـ عامل ناحية من بني سعد ١٠ هـ م. ن
