INSERT INTO [b9790] VALUES (809,'الاكتفاء في شرح الموطأ للداودي: [١٨٧] ٦١٥، ٦١٦ [٦٢١، ٦٢٢] الاكمال للقاضي عياض: ٤٤، ١٠٧، ١٢٢، ١٥٥، ١٥٦، ٢٧١، ٥٠٩، ٥٢٨، ٥٤٤، ٥٦٢، ٦٤١، ٧٥٨، ٧٥٩، ٧٨٥، ٧٩١ الأموال لأبي عبيد القاسم بن سلام: ٢١١، ٢٤٣، ٢٥٤، ٢٩٤، ٥٢٩- ٥٣٢، ٥٣٥، ٥٧٥، ٥٧٦، ٥٨٢، ٥٩٦، ٦٠٧، ٦٢١، ٦٤٢، ٦٤٤، ٧٨٨، ٧٩٢ الأموال لأحمد بن نصر الداودي: ١٨٧، ٥٨٤، ٥٩٩، ٦٢٠، ٧٩٢ أنباء الأنبياء لمحمد بن سلامة القضاعي: ٦٣، ٦٦، ٦٧، ١٧١، ١٧٢، ٢٩٠، ٣٦٥، ٤٨٢، ٥٠١، ٥٥٠، ٥٦٩، ٥٧٢، ٧٩٤ البارع لأبي علي القالي: ٥٤٠ البسيط في شرح الجمل لابن أبي الربيع: ٥٨، ٧٩٦ بغية الأديب لعلي بن محمد الحراني: ٧٦٧ بلغة الظرفاء لابن أبي السرور الروحي: ٦٦، ٧٩٤ بهجة المجالس لابن عبد البر: ٣٢٣، ٦٨٥، ٧٩٦ البهجة لابن هشام: ٦٦، ٦٧، ٢١٨، ٤٥٢، ٧٨٦، ٧٩٤ البيان والتبيين للجاحظ: ٤٩١، ٧٩٦ البيان والتحصيل لابن رشد: ١١٢، ١١٣، ١٣٦، ٢٢٠، ٢٢١، ٢٩٧، ٣٠٥، ٥٢٦، ٧٠٣، ٧٩٢ تاريخ ابن أبي خيثمة: ٦٥ تاريخ ابن الأثير: ٩٧، ١٨٩، ٢٤٣، ٢٧٦، ٣١٣، ٣١٤، ٣٨٦، ٤٧٨، ٤٩١، ٥٠٢، ٥٥٣، ٥٧٣، ٥٩١، ٦٣٤، ٧٨٦، ٧٩٤ تاريخ أحمد بن محمد بن عيسى: ٦٦١ تاريخ بغداد للخطيب: ٦١، ١٠١، ١٠٢، ٢٤١، ٧٩٤ تاريخ خليفة بن خياط: ١٦٦، ٢١٤، ٤٠٨ التاريخ الكبير للبخاري: ٩٧، ١٨٢، ١٩٣، ٢١٩، ٣٠٠، ٣٦٥، ٤٣٩، ٥٩٣، ٧٩٣ التبصرة في شرح كتاب ابن الحاجب للتلمساني: ٦١٠، ٧٩٢ التبصرة للصيمري: ٥٧٧ التبصرة للخمي: ٢٨٨، ٦٠٨، ٧٩٢ التحبير في تفسير أسماء الله تعالى للقشيري:
