INSERT INTO [t10269] VALUES (1,'الله عز وجل يدخل بالسهم الواحد ثلاثة الجنة: صانعه محتسبا، والمعين به، والرامي به في سبيل','1','0','0');
INSERT INTO [t10269] VALUES (2,'إن الله عز وجل يدخل بالسهم الواحد ثلاثة الجنة: الرامي به، والممد به، والمحتسب','2','0','0');
INSERT INTO [t10269] VALUES (3,'إن الله عز وجل يدخل بالسهم الواحد الجنة من عمله في سبيله، ومن قوى به في سبيل الله عز وجل،','3','0','0');
INSERT INTO [t10269] VALUES (4,'كل شيء ليس من ذكر الله عز وجل فهو لغو وسهو إلا أربع خصال: مشي الرجل بين الغرضين، وتأديب','4','0','0');
INSERT INTO [t10269] VALUES (5,'كل شيء ليس من ذكر الله، فهو لهو أو سهو غير أربع خصال: تأديب الرجل فرسه، وملاعبته أهله،','5','0','0');
INSERT INTO [t10269] VALUES (6,'نعم لهو المؤمن الرمي، ومن ترك الرمي بعدما علمه، فهو نعمة تركها','6','0','0');
INSERT INTO [t10269] VALUES (7,'لن أترك الرمي أبدا، وإن كانت يدي مقطوعة بعد شيء سمعته من رسول الله صلى الله عليه','7','0','0');
INSERT INTO [t10269] VALUES (8,'من تعلم الرمي ثم تركه فقد عصاني','8','0','0');
INSERT INTO [t10269] VALUES (9,'وأعدوا لهم ما استطعتم من قوة} [الأنفال: ٦٠] ألا إن القوة الرمي، ألا إن القوة','9','0','0');
INSERT INTO [t10269] VALUES (10,'وأعدوا لهم ما استطعتم من قوة} [الأنفال: ٦٠] ألا إن القوة الرمي، ألا إن القوة الرمي، ألا','10','0','0');
INSERT INTO [t10269] VALUES (11,'وأعدوا لهم ما استطعتم من قوة} [الأنفال: ٦٠] أنه قال: القوة الرمي','11','0','0');
INSERT INTO [t10269] VALUES (12,'إن كل لهو لهى به المؤمن باطل، إلا في ثلاث: رميه الصيد بقوسه، وتأديبه فرسه، وملاعبته','12','0','0');
INSERT INTO [t10269] VALUES (13,'اللهو في ثلاث: تأديبك فرسك، ورميك بقوسك - أو قال نصلك - وملاعبتك أهلك','13','0','0');
INSERT INTO [t10269] VALUES (14,'كل لهو باطل، إلا ركوب الخيل والرمي، ولهو الرجل مع أهله، فعليكم بركوب الخيل والرمي، والرمي','14','0','0');
INSERT INTO [t10269] VALUES (15,'أن علموا أولادكم السباحة والرمي والفروسية','15','0','0');
INSERT INTO [t10269] VALUES (16,'كان رسول الله صلى الله عليه وسلم يعجبه أن يكون الرجل سابحا راميا','16','0','0');
INSERT INTO [t10269] VALUES (17,'من رمى بسهم في سبيل الله، فهو له درجة في الجنة، ومن بلغ بسهم في سبيل الله فهو عدل محرر','17','0','0');
INSERT INTO [t10269] VALUES (18,'من رمى بسهم في سبيل الله فأصاب أو أخطأ أو قصر، فكأنما أعتق رقبة، ومن أعتق رقبة كانت فكاكه','18','0','0');
INSERT INTO [t10269] VALUES (19,'من بلغه برميه فله درجة في الجنة. فقال رجل: يا رسول الله، إن بلغته برمية فلي درجة في','19','0','0');
INSERT INTO [t10269] VALUES (20,'من رمى بسهم في سبيل الله، قصر أو بلغ، فله درجة في الجنة قال: فرميت يومئذ ستة عشر','20','0','0');
INSERT INTO [t10269] VALUES (21,'قاتلوا أهل البغي فمن بلغ منهم فله درجة. قالوا: يا رسول الله ما الدرجة؟ قال: ما بين','21','0','0');
INSERT INTO [t10269] VALUES (22,'من رمى العدو بسهم ن فبلغ سهمه، أخطأ أو أصاب، فعدل رقبة','22','0','0');
INSERT INTO [t10269] VALUES (23,'من رمى بسهم في سبيل الله، بلغه العدو، أو قصر، أخطأ أو أصاب، كان كعدل محرر من بني','23','0','0');
INSERT INTO [t10269] VALUES (24,'أيما مسلم رمى بسهم في سبيل الله، فبلغ، مخطئا أو مصيبا، فله من الأجر كرقبة أعتقها من ولد','24','0','0');
INSERT INTO [t10269] VALUES (25,'من رمى بسهم في سبيل الله، فبلغ أو قصر، كان ذلك السهم نورا يوم القيامة','25','0','0');
INSERT INTO [t10269] VALUES (26,'من رمى بسهم في سبيل الله أصاب أو أخطأ كان له مثل أجر عتق رقبة العضو بالعضو حتى الفرج','26','0','0');
INSERT INTO [t10269] VALUES (27,'من رمى بسهم في سبيل الله عز وجل كان له نورا تاما','27','0','0');
INSERT INTO [t10269] VALUES (28,'ما بقي من رميك يا فلان؟ قال: لقد جفوته. فقال رسول الله صلى الله عليه وسلم: أما إنها نعمة','28','0','0');
INSERT INTO [t10269] VALUES (29,'من أحسن الرمي، ثم تركه، فقد ترك نعمة من النعم','29','0','0');
INSERT INTO [t10269] VALUES (30,'من ترك الرمي، بعد أن يحسنه فقد ترك سنة','30','0','0');
INSERT INTO [t10269] VALUES (31,'من تعلم الرمي فنسيه، كان نعمة أنعمها الله عليه فتركها','31','0','0');
INSERT INTO [t10269] VALUES (32,'لصوت أبي طلحة في الجيش خير من مائة','32','0','0');
INSERT INTO [t10269] VALUES (33,'لصوت أبي طلحة في الجيش خير من مئة','33','0','0');
INSERT INTO [t10269] VALUES (34,'صوت أبي طلحة في الجيش خير من مئة وكان يجثو بين يدي النبي صلى الله عليه وسلم في الحرب','34','0','0');
INSERT INTO [t10269] VALUES (35,'وكان إذا رمى يشرف النبي صلى الله عليه وسلم، فينظر إلى موضع نبله','35','0','0');
INSERT INTO [t10269] VALUES (36,'كان يرمي بين يدي نبي الله صلى الله عليه وسلم، فجعل رسول الله صلى الله عليه وسلم يتطاول','36','0','0');
INSERT INTO [t10269] VALUES (37,'نحري دون نحرك','37','0','0');
INSERT INTO [t10269] VALUES (38,'كل ما رد عليك قوسك','38','0','0');
