INSERT INTO [t10461] VALUES (1,'«رأيت أنسا رضي الله عنه» ، وقال يعقوب بن شيبة السدوسي: أبو حنيفة مولى لبني تيم الله بن ثعلبة','2','0','0');
INSERT INTO [t10461] VALUES (2,'«سبي ثابت أبو أبي حنيفة من كابل، فاشترته امرأة من بني تيم الله بن ثعلبة','3','0','0');
INSERT INTO [t10461] VALUES (3,'أبو حنيفة حسن الوجه واللحية حسن الثياب»','3','0','0');
INSERT INTO [t10461] VALUES (4,'" رأيت شيخا في مسجد الكوفة يفتي الناس وعليه قلنسوة طويلة، فقلت من هذا؟ قالوا: أبو','4','0','0');
INSERT INTO [t10461] VALUES (5,'أبو حنيفة جميل الوجه، سري الثوب عطرا، ولقد أتيته في حاجة، فصليت معه الصبح وعلي كساء','4','0','0');
INSERT INTO [t10461] VALUES (6,'شديد الذب عن حرام الله، مجانبا لأهل الدنيا، وطويل الصمت دائم الفكر، لم يكن مهذارا ولا','5','0','0');
INSERT INTO [t10461] VALUES (7,'أبو حنيفة أكثر الناس صلاة، وأعظمهم أمانة، وأحسنهم مروءة»','5','0','0');
INSERT INTO [t10461] VALUES (8,'أبو حنيفة طويل الصمت دائم الفكر، كبير العقل، قليل المحادثة للناس»','5','0','0');
INSERT INTO [t10461] VALUES (9,'أبو حنيفة شديد الخوف لله، هائبا للحرام أن يستحل»','6','0','0');
INSERT INTO [t10461] VALUES (10,'«ما رأيت رجلا أوقر في مجلسه، ولا أحسن سمتا وحلما من أبي حنيفة، ولقد كنا عنده في المسجد','6','0','0');
INSERT INTO [t10461] VALUES (11,'«لما حذق أبي حماد قراءة الفاتحة، أعطى أبو حنيفة المعلم خمس مائة درهم» ، وقد ورد في كرم أبي','6','0','0');
INSERT INTO [t10461] VALUES (12,'«جعل أبو حنيفة على نفسه إن حلف بالله صادقا في عرض حديثه أن يتصدق بدينار، فكان إذا حلف تصدق','6','0','0');
INSERT INTO [t10461] VALUES (13,'«لقي أبو حنيفة من الناس عتبا لقلة مخالطته الناس، فكانوا يرونه من زهو فيه، وإنما كان ذلك','6','0','0');
INSERT INTO [t10461] VALUES (14,'أبو حنيفة ورعا تقيا، مفضلا على إخوانه»','6','0','0');
INSERT INTO [t10461] VALUES (15,'«إن كنتم تنتفعون بهذا فافعلوا» . رواها الطحاوي، عن أبي خازم القاضي عنه شيوخ أبي حنيفة','7','0','0');
INSERT INTO [t10461] VALUES (16,'أبو حنيفة يسمى الوتد لكثرة صلاته»','9','0','0');
INSERT INTO [t10461] VALUES (17,'أبو حنيفة يختم القرآن في كل ليلة في ركعة»','9','0','0');
INSERT INTO [t10461] VALUES (18,'أمشي مع أبي حنيفة إذ سمعت رجلا، يقول لآخر: هذا أبو حنيفة لا ينام الليل، فقال أبو حنيفة:','9','0','0');
INSERT INTO [t10461] VALUES (19,'أبو حنيفة يصلي العشاء والفجر بطهر واحد»','9','0','0');
INSERT INTO [t10461] VALUES (20,'«صحبت أبا حنيفة ستة أشهر، فما رأيته صلى الغداة إلا بوضوء العشاء الآخرة، وكان يختم القرآن','9','0','0');
INSERT INTO [t10461] VALUES (21,'«ما توسط أبو حنيفة فراشا بليل منذ عرفته، وإنما كان نومه بين الظهر والعصر في الصيف وبالليل','9','0','0');
INSERT INTO [t10461] VALUES (22,'«لو رأيت أبا حنيفة يصلي، علمت أن الصلاة من همه»','9','0','0');
INSERT INTO [t10461] VALUES (23,'" يردد هذه الآية {فمن الله علينا ووقانا عذاب السموم} [الطور: ٢٧] وهو يبكي، ويقول: اللهم من','10','0','0');
INSERT INTO [t10461] VALUES (24,'«صحبت أبا حنيفة ستة أشهر، فما رأيته ليلة واحدة وضع جنبه»','10','0','0');
INSERT INTO [t10461] VALUES (25,'«ربما قرأت في ركعتي الفجر حزبين من القرآن»','10','0','0');
INSERT INTO [t10461] VALUES (26,'" دخلت المسجد، فرأيت رجلا يصلي فاستحليت قراءته، فوقفت حتى قرأ سبعا، فقلت: يركع، ثم بلغ','10','0','0');
INSERT INTO [t10461] VALUES (27,'" ختم القرآن في ركعة أربعة من الأئمة: عثمان بن عفان، وتميم الداري، وسعيد بن جبير، وأبو','10','0','0');
INSERT INTO [t10461] VALUES (28,'«ربما ختم أبو حنيفة القرآن في رمضان ستين مرة»','11','0','0');
INSERT INTO [t10461] VALUES (29,'أبا حنيفة قام ليلة يردد قوله تعالى: {بل الساعة موعدهم والساعة أدهى وأمر} [القمر: ٤٦] ،','11','0','0');
INSERT INTO [t10461] VALUES (30,'«جزاك الله خيرا، ما أحوج الناس كل وقت إلى من يقول لهم مثل هذا!»','11','0','0');
INSERT INTO [t10461] VALUES (31,'ما أجرأنا يقول أحدنا: نسأل الله الجنة! إنما يسأل الله الجنة من راض نفسه - يعني لها -،','11','0','0');
INSERT INTO [t10461] VALUES (32,'«صلى أبو حنيفة فيما حفظ عليه صلاة الفجر بوضوء العشاء أربعين سنة، فكان عامة الليل يقرأ جميع','11','0','0');
INSERT INTO [t10461] VALUES (33,'«رأيت أبا حنيفة قرأ القرآن في ركعة»','12','0','0');
INSERT INTO [t10461] VALUES (34,'«مكث أبو حنيفة مدة يصلي الخمس بوضوء واحد»','12','0','0');
INSERT INTO [t10461] VALUES (35,'«غفر الله لك لم تفطر منذ ثلاثين سنة، ولم تتوسد يمينك بالليل منذ أربعين سنة، ولقد أتعبت من','12','0','0');
INSERT INTO [t10461] VALUES (36,'«ما رأيت أورع من أبي حنيفة، وقد جرب بالسياط والأموال»','12','0','0');
INSERT INTO [t10461] VALUES (37,'" مررت مع أبي بالكناسة، فبكى، فقلت له: يا أبت ما يبكيك؟ قال: يا بني، في هذا الموضع ضرب ابن','12','0','0');
INSERT INTO [t10461] VALUES (38,'«أقول أخطأ الحسن وأصاب ابن مسعود»','13','0','0');
INSERT INTO [t10461] VALUES (39,'«غفر الله لك، هو يعلم مني خلاف ما تقول»','13','0','0');
INSERT INTO [t10461] VALUES (40,'«ما رأيت أحدا أحلم من أبي حنيفة»','13','0','0');
INSERT INTO [t10461] VALUES (41,'«لو سألني أن أعد له أبواب المسجد لم أفعل»','13','0','0');
INSERT INTO [t10461] VALUES (42,'" ضرب ابن هبيرة أبا حنيفة على أن يلي القضاء، فأبى، فقال الناس: استتابه ".','13','0','0');
INSERT INTO [t10461] VALUES (43,'«ما أصابني في ضربي شيء أشد علي من غم والدتي، وكان بها برا»','14','0','0');
INSERT INTO [t10461] VALUES (44,'" أشخص المنصور أبا حنيفة، فأراده على أن يوليه، فأبى فحلف عليه ليفعلن وحلف أبو حنيفة أن لا','14','0','0');
INSERT INTO [t10461] VALUES (45,'المنصور ينازل أبا حنيفة في أمر القضاء، فقال: والله ما أنا بمأمون الرضا، فكيف أكون بمأمون','15','0','0');
INSERT INTO [t10461] VALUES (46,'" أنتم مسار قلبي، وجلاء حزني، وأسرجت لكم الفقه وألجمته، وقد تركت الناس يطئون أعقابكم،','16','0','0');
INSERT INTO [t10461] VALUES (47,'«إذا ارتشى القاضي فهو معزول، وإن لم يعزل»','16','0','0');
INSERT INTO [t10461] VALUES (48,'الأعمش إذا سئل عن الدقائق أرسلهم إلى أبي حنيفة، وقد قال مغيرة له ألا تأتي أبا','17','0','0');
INSERT INTO [t10461] VALUES (49,'«جالس أبا حنيفة تفقه، فإن إبراهيم لو كان حيا لجالسه»','17','0','0');
INSERT INTO [t10461] VALUES (50,'شعبة حسن الرأي في أبي حنيفة كثير الترحم عليه»','17','0','0');
INSERT INTO [t10461] VALUES (51,'«رحم الله أبا حنيفة إن كان لفقيها عالما»','17','0','0');
INSERT INTO [t10461] VALUES (52,'«النعمان بن ثابت فقيه البدن لم يعد ما أدرك عليه أهل الكوفة» .','17','0','0');
INSERT INTO [t10461] VALUES (53,'«لما مات أبو حنيفة، ذهب مفتي العراق وفقيهها»','17','0','0');
INSERT INTO [t10461] VALUES (54,'«إذا أردت الآثار، فسفيان الثوري، وإذا أردت تلك الدقائق فأبو حنيفة»','17','0','0');
INSERT INTO [t10461] VALUES (55,'ذهب معه علم كثير»','17','0','0');
INSERT INTO [t10461] VALUES (56,'أبو حنيفة عالم العراق» ، قال زيد بن هارون: أفقه من رأيت أبو حنيفة، وعن شداد بن حكيم: " ما','18','0','0');
INSERT INTO [t10461] VALUES (57,'أبو حنيفة فهما بعلمه متثبتا فيه، إذا صح عنده الخبر عن رسول الله صلى الله عليه وسلم لم','18','0','0');
INSERT INTO [t10461] VALUES (58,'«الناس عيال على أبي حنيفة في الفقه»','18','0','0');
INSERT INTO [t10461] VALUES (59,'«ما مقلت عيني مثل أبي حنيفة»','18','0','0');
INSERT INTO [t10461] VALUES (60,'أبو حنيفة آية»','18','0','0');
INSERT INTO [t10461] VALUES (61,'رأيت رجلا لو كلمك في هذه السارية أن يجعلها ذهبا لقام بحجته»','19','0','0');
INSERT INTO [t10461] VALUES (62,'«إن كان الأثر قد عرف واحتيج إلى الرأي، فرأي مالك وسفيان، وأبي حنيفة، وأبو حنيفة أحسنهم','19','0','0');
INSERT INTO [t10461] VALUES (63,'«إن كان أحد ينبغي له أن يقول برأيه، فأبو حنيفة»','19','0','0');
INSERT INTO [t10461] VALUES (64,'" أمالك أفقه أم أبو حنيفة؟ ، فقال: أبو حنيفة "','20','0','0');
INSERT INTO [t10461] VALUES (65,'«ما يقع في أبي حنيفة إلا جاهل أو حاسد»','20','0','0');
INSERT INTO [t10461] VALUES (66,'«يجب على أهل الإسلام أن يدعوا الله لأبي حنيفة في صلاتهم»','20','0','0');
INSERT INTO [t10461] VALUES (67,'أبو حنيفة أعلم أهل زمانه»','20','0','0');
INSERT INTO [t10461] VALUES (68,'«لا نكذب الله ما سمعنا أحسن من رأي أبي حنيفة، وقد أخذنا بأكثر أقواله»','20','0','0');
INSERT INTO [t10461] VALUES (69,'«لو وزن علم أبي حنيفة بعلم أهل زمانه لرجح»','20','0','0');
INSERT INTO [t10461] VALUES (70,'«كلام أبي حنيفة أدق من الشعر لا يعيبه إلا جاهل»','20','0','0');
INSERT INTO [t10461] VALUES (71,'" شيئان ما ظننتهما أن يتجاوزا قنطرة الكوفة: قراءة حمزة، ورأي أبي حنيفة وقد بلغا','20','0','0');
INSERT INTO [t10461] VALUES (72,'«آخذ بكتاب الله، فما لم أجد فبسنة رسول الله والآثار الصحاح عنه التي فشت في أيدي الثقات عن','22','0','0');
INSERT INTO [t10461] VALUES (73,'«البول في المسجد أحسن من بعض القياس»','22','0','0');
INSERT INTO [t10461] VALUES (74,'«هذا الذي نحن فيه رأي لا نجبر عليه أحدا، ولا نقول يجب على أحد قبوله، فمن كان عنده أحسن منه','22','0','0');
INSERT INTO [t10461] VALUES (75,'«علمنا هذا رأي وهو أحسن ما قدرنا عليه، ومن جاءنا بأحسن منه قبلناه منه» .','22','0','0');
INSERT INTO [t10461] VALUES (76,'" كنا عند الأعمش وعنده أبو حنيفة، فسئل الأعمش عن مسألة، فقال: أفته يا نعمان، فأفتاه أبو','22','0','0');
INSERT INTO [t10461] VALUES (77,'«دعهم فإنهم لا يتفقهون إلا بهذا»','23','0','0');
INSERT INTO [t10461] VALUES (78,'أبو حنيفة يجهر في أمر إبراهيم بن عبد الله بن حسن جهرا شديدا، فقلت: والله ما أنت بمنته حتى','23','0','0');
INSERT INTO [t10461] VALUES (79,'" يقدم علينا من هذا الوجه صنفان يعني من خراسان: الجهمية، والمشبهة "','23','0','0');
INSERT INTO [t10461] VALUES (80,'«جهم ومقاتل كانا فاسقين، أفرط هذا في التشبيه وهذا في النفي»','23','0','0');
INSERT INTO [t10461] VALUES (81,'«لا ينبغي للرجل أن يحدث من الحديث، إلا ما يحفظه من وقت ما سمعه»','23','0','0');
INSERT INTO [t10461] VALUES (82,'" رأيت رؤيا أفزعتني، رأيت كأني أنبش قبر النبي صلى الله عليه وسلم، فأتيت البصرة، فأمرت رجلا','23','0','0');
INSERT INTO [t10461] VALUES (83,'«إذا سمعتهم يذكرون أبا حنيفة بسوء ساءني ذلك، وأخاف عليهم المقت من الله تعالى علي بن','24','0','0');
INSERT INTO [t10461] VALUES (84,'«أدخله سوق الرقيق، فإذا وقعت عينه على جارية فاشترها لنفسك، ثم زوجها إياه، فإن طلقها رجعت','24','0','0');
INSERT INTO [t10461] VALUES (85,'«لو وزن عقل أبي حنيفة بعقل نصف أهل الأرض، لرجح بهم»','25','0','0');
INSERT INTO [t10461] VALUES (86,'" شككت في طلاق امرأتي، فسألت شريكا، فقال: طلقها وأشهد على رجعتها، ثم سألت سفيان الثوري،','25','0','0');
INSERT INTO [t10461] VALUES (87,'«أعلمنا الله أنه كلامه، فمن أخذ بما علمه الله فقد استمسك بالعروة الوثقى، فهل بعد التمسك','26','0','0');
INSERT INTO [t10461] VALUES (88,'«ليس هو بفقيه هو مفت متكلف»','26','0','0');
INSERT INTO [t10461] VALUES (89,'" أتت امرأة أبا حنيفة بثوب، فقال: بكم هو؟ قالت: بمائة درهم، قال لها: هو خير من ذلك، قالت:','26','0','0');
INSERT INTO [t10461] VALUES (90,'سفيان إذا قيل له: أمؤمن أنت؟ قال: نعم، وإذا قيل له: عند الله، قال: أرجو، وكان أبو حنيفة،','26','0','0');
INSERT INTO [t10461] VALUES (91,'أبو أسيد يجالس أبا حنيفة، وكان شيخا عفيفا مغفلا، فقال مرة في مجلس أبي حنيفة لرجل: ارفع','27','0','0');
INSERT INTO [t10461] VALUES (92,'«قبر النبي صلى الله عليه وسلم مسنما عليه أفلاق من حجارة بيض»','28','0','0');
INSERT INTO [t10461] VALUES (93,'«جالست أبا حنيفة سنتين ونصف سنة، فما سمعته لحن في شيء إلا في حرف واحد، زعم أهل اللغة أن له','28','0','0');
INSERT INTO [t10461] VALUES (94,'«ما لزمت سفيان حتى جعلت علم أبي حنيفة هكذا، وأشار بقبض يده»','28','0','0');
INSERT INTO [t10461] VALUES (95,'رأي أبي حنيفة خلد في الكتب، قلت: فقد خلد رأي مالك في الكتب، قال: أبو حنيفة أكثر رأيا منه،','29','0','0');
INSERT INTO [t10461] VALUES (96,'حفص بن عبد الرحمن شريك أبي حنيفة، وكان أبو حنيفة يجهز عليه، فبعث إليه أن في ثوب كذا عيبا،','29','0','0');
INSERT INTO [t10461] VALUES (97,'أبو حنيفة حسن الدين، عظيم الأمانة»','29','0','0');
INSERT INTO [t10461] VALUES (98,'ابتعت ثوبين، فبعت أحدهما برأس المال إلا أربعة دراهم»','30','0','0');
INSERT INTO [t10461] VALUES (99,'" طلب العلم، قيل: ثم أي؟ قال: ما اشتد عليك "','30','0','0');
INSERT INTO [t10461] VALUES (100,'" أجاز المنصور أبا حنيفة بعشرة آلاف درهم، ودعي ليقبضها فشاورني، ثم قال: هذا رجل إن رددتها','30','0','0');
INSERT INTO [t10461] VALUES (101,'ما قبل أبو حنيفة لأحد منهم جائزة، ولا هدية، يعني الأمراء»','30','0','0');
INSERT INTO [t10461] VALUES (102,'«لم أر أعقل، ولا أفضل، ولا أورع من أبي حنيفة»','30','0','0');
INSERT INTO [t10461] VALUES (103,'أبو حنيفة تبين عقله في منطقه، وفعله، ومشيته، ومدخله، ومخرجه»','30','0','0');
INSERT INTO [t10461] VALUES (104,'«ما رأيت أفضل من أبي حنيفة دينا، وورعا»','30','0','0');
INSERT INTO [t10461] VALUES (105,'«غفر الله لنا ولسفيان، لو أن سفيان فقد في زمن إبراهيم النخعي، لدخل على المسلمين','31','0','0');
INSERT INTO [t10461] VALUES (106,'«استحل مني ابن أبي ليلى مالا أستحله أنا من بهيمة»','31','0','0');
INSERT INTO [t10461] VALUES (107,'«طلبت مع أبي حنيفة الحديث، فغلبنا وأخذنا في الزهد، فبرع علينا وطلبنا معه الفقه، فجاء منه','31','0','0');
INSERT INTO [t10461] VALUES (108,'" لم يصح عندنا أن أبا حنيفة رحمه الله، قال: القرآن مخلوق، فقلت: الحمد لله يا أبا عبد الله،','31','0','0');
INSERT INTO [t10461] VALUES (109,'«جهم بن صفوان الخراساني كافر»','32','0','0');
INSERT INTO [t10461] VALUES (110,'«رحم الله مالكا كان إماما، رحم الله أبا حنيفة كان إماما»','34','0','0');
INSERT INTO [t10461] VALUES (111,'«لا تحمدوا إلا الله، فإني ما أعطيتكم من مالي شيئا، ولكن من فضل الله علي','34','0','0');
INSERT INTO [t10461] VALUES (112,'" دعا المنصور أبا حنيفة، فقال الربيع الحاجب وكان يعادي أبا حنيفة: يا أمير المؤمنين، هذا','35','0','0');
INSERT INTO [t10461] VALUES (113,'ما أدركنا أحدا تكلم في الفقه أبلغ، ولا أصبر، ولا أحضر جوابا منك، وإنك لسيد من تكلم في','35','0','0');
INSERT INTO [t10461] VALUES (114,'إن يحسدوني فإني غير لائمهم ... قبلي من الناس أهل الفضل قد حسدوا فدام لي ولهم ما بي وما','35','0','0');
INSERT INTO [t10461] VALUES (115,'«مات أبو حنيفة في نصف شوال سنة خمسين ومائة» ، وقال الواقدي وغيره: «مات أبو حنيفة في رجب سنة','36','0','0');
INSERT INTO [t10461] VALUES (116,'فأمر به فرجم في موضع قليل الحجارة، فأبطأ عليه الموت، فانطلق يسعى إلى موضع كثير الحجارة','37','0','0');
INSERT INTO [t10461] VALUES (117,'متعة النساء»','38','0','0');
INSERT INTO [t10461] VALUES (118,'" دخلت على الحسن بن صالح يوم موت أخيه، فرأيته يستطعم شيئا من رجل ويضحك، فقلت: تدفن أخاك','38','0','0');
INSERT INTO [t10461] VALUES (119,'" رأيت محمد بن الحسن في المنام، فقلت: إلام صرت؟ قال: غفر لي، قلت: بم؟ قال: قيل لي: لم نجعل','40','0','0');
INSERT INTO [t10461] VALUES (120,'" رأيت أبا حنيفة في النوم، فقلت له: ما فعل الله بك يا أبا حنيفة؟ قال: غفر لي، قلت له:','40','0','0');
INSERT INTO [t10461] VALUES (121,'" رأيت أبا حنيفة في النوم، فقلت: إلام صرت؟ قال: إلى سعة رحمته، قلت: بالعلم؟ قال: هيهات!','40','0','0');
INSERT INTO [t10461] VALUES (122,'أطلب الحديث والفقه وأنا مقل، فجاء أبي يوما وأنا عند أبي حنيفة، فقال: يا بني، لا تمدن رجلك','44','0','0');
INSERT INTO [t10461] VALUES (123,'" مرض أبو يوسف، فعاده أبو حنيفة، فلما خرج، قال: إن يمت هذا الفتى فهو أعلم من عليها، وأومأ','45','0','0');
INSERT INTO [t10461] VALUES (124,'" أول ما كتبت الحديث اختلفت إلى أبي يوسف القاضي، فكتبت عنه، ثم اختلفت بعد إلى الناس، قال:','45','0','0');
INSERT INTO [t10461] VALUES (125,'«ما رأيت في أصحاب الرأي أثبت في الحديث ولا أحفظ، ولا أصح رواية من أبي يوسف، وأبو حنيفة','45','0','0');
INSERT INTO [t10461] VALUES (126,'«أبو يوسف صاحب حديث، صاحب سنة»','46','0','0');
INSERT INTO [t10461] VALUES (127,'«قدم علينا أبو يوسف وأقل ما فيه الفقه، وقد ملأ بفقهه ما بين الخافقين»','46','0','0');
INSERT INTO [t10461] VALUES (128,'" سألني الأعمش عن مسألة فأجبته عنها، فقال لي: من أين قلت هذا؟ قلت: لحديث حدثتناه أنت، فقال:','46','0','0');
INSERT INTO [t10461] VALUES (129,'أبو يوسف قد اطلع على الفقه، أو العلم اطلاعا يتناوله كيف يشاء»','46','0','0');
INSERT INTO [t10461] VALUES (130,'«ما أحب أن أروي عن أحد من أصحاب الرأي، إلا عن أبي يوسف، فإنه كان صاحب','46','0','0');
INSERT INTO [t10461] VALUES (131,'«كل ما أفتيت به فقد رجعت، إلا ما وافق الكتاب والسنة»','46','0','0');
INSERT INTO [t10461] VALUES (132,'«أبو يوسف كان منصفا في الحديث»','47','0','0');
INSERT INTO [t10461] VALUES (133,'«أبو يوسف صدوق كثير الغلط»','47','0','0');
INSERT INTO [t10461] VALUES (134,'«صحبت أبا حنيفة سبع عشرة سنة، ثم رتعت في الدنيا سبع عشرة سنة، وإني أظن أن أجلي قد قرب، فما','47','0','0');
INSERT INTO [t10461] VALUES (135,'«لم يكن في أصحاب أبي حنيفة مثل أبي يوسف علما، وفقها، ومعرفة، ولولاه لم يذكر أبو حنيفة، ولا','47','0','0');
INSERT INTO [t10461] VALUES (136,'أبو يوسف يحفظ التفسير والمغازي وأيام العرب، وكان أحد علومه الفقه»','47','0','0');
INSERT INTO [t10461] VALUES (137,'أبو يوسف أتبعهم للحديث»','47','0','0');
INSERT INTO [t10461] VALUES (138,'أبو يوسف يصلي بعد ما ولي القضاء كل يوم مائتي ركعة»','47','0','0');
INSERT INTO [t10461] VALUES (139,'أبو يوسف يحب أصحاب الحديث ويميل إليهم»','48','0','0');
INSERT INTO [t10461] VALUES (140,'نأتي أبا يوسف لما قدم البصرة سنة ثمانين ومائة، فكان يحدث بعشرة أحاديث وعشرة آراء وأراه،','48','0','0');
INSERT INTO [t10461] VALUES (141,'" لما قدم أبو يوسف البصرة مع الرشيد، اجتمع أصحاب الرأي وأصحاب الحديث على بابه، فطلب كل فريق','48','0','0');
INSERT INTO [t10461] VALUES (142,'«لو استطعت أن أشاطركم ما في قلبي من العلم لفعلت»','49','0','0');
INSERT INTO [t10461] VALUES (143,'«مرضت مرضا نسيت فيه كل ما كنت أحفظه حتى القرآن، ولم أنس الفقه لأن علمي بما سوى الفقه علم','49','0','0');
INSERT INTO [t10461] VALUES (144,'«مخاشنة الولاة ذل، ومخاشنة القضاة فقر»','49','0','0');
INSERT INTO [t10461] VALUES (145,'«لا أقل من عشرة شهود، اثنان يموتان، واثنان يغيبان، واثنان لا يؤديان، واثنان يثبتان، واثنان','50','0','0');
INSERT INTO [t10461] VALUES (146,'" القرآن كلام الله، ومن قال: كيف ولم، وتعاطى مراء ومجادلة، استوجب الحبس والضرب','50','0','0');
INSERT INTO [t10461] VALUES (147,'«لا يفلح من استحلى شيئا من الكلام»','50','0','0');
INSERT INTO [t10461] VALUES (148,'«لا يصلى خلف من قال القرآن مخلوق»','50','0','0');
INSERT INTO [t10461] VALUES (149,'أبو العباس يعني السفاح، قد أشخص العلماء، فكنا نسمع تلك الأيام»','50','0','0');
INSERT INTO [t10461] VALUES (150,'«من قال إيماني كإيمان جبريل فهو صاحب بدعة»','50','0','0');
INSERT INTO [t10461] VALUES (151,'«إذا دخل في القنوت للوتر، رفع يديه في الدعاء» ، إن كان فرج ثقة','50','0','0');
INSERT INTO [t10461] VALUES (152,'" قدم علينا ربيعة بن أبي عبد الرحمن فأتيته، فقلت: ما تقول في عبد بين رجلين أعتقه أحدهما؟','51','0','0');
INSERT INTO [t10461] VALUES (153,'" كنا يوما بباب أبي يوسف إذ أقبل من دار الرشيد يبتسم، فقال: حدثت مسألة في دار أمير','51','0','0');
INSERT INTO [t10461] VALUES (154,'«من طلب المال بالكمياء أفلس، ومن طلب العلم بالكلام تزندق، ومن طلب غريب الحديث','52','0','0');
INSERT INTO [t10461] VALUES (155,'" دخلت على الرشيد وفي يديه درتان يقلبهما، فقال: يا يعقوب، هل رأيت أحسن من هاتين؟ قلت: نعم،','52','0','0');
INSERT INTO [t10461] VALUES (156,'ما زنيت قط، ولا جرت في حكم، ولا أخاف من شيء إلا من شيء كان مني كنت آخذ القصص، فأقرؤها على','53','0','0');
INSERT INTO [t10461] VALUES (157,'هذا إن تاب وإلا قتلته»','53','0','0');
INSERT INTO [t10461] VALUES (158,'«اللهم إنك تعلم أني لم أطأ فرجا حراما، وأني لم آكل درهما حراما، وأنا','53','0','0');
INSERT INTO [t10461] VALUES (159,'«ليتني مت على ما كنت عليه من الفقه، وإني والله دخلت في القضاء ولم أتعمد جورا، ولا رفعت','53','0','0');
INSERT INTO [t10461] VALUES (160,'رأيت في ليلتي هذه كأني أدخلت الجنة، فرأيت قصرا، فقلت: لمن هذا؟ قال: ليعقوب القاضي، قلت:','54','0','0');
INSERT INTO [t10461] VALUES (161,'في أبي يوسف رحمه الله لثغة، فكان يحدثنا، فيقول: ثنا مطيف بن طييف الحايثي أي مطرف بن طريف','54','0','0');
INSERT INTO [t10461] VALUES (162,'أبو يوسف قاضي الرشيد، فاستخلف ولده يوسف وكان يقضي معه، فلما مات أبو يوسف أقر الرشيد ابنه','54','0','0');
INSERT INTO [t10461] VALUES (163,'«وليت القضاء وولي أبي من قبلي، فكانت ولايتنا القضاء ثلاثين سنة، ما بلينا أن نقضي بين جد','54','0','0');
INSERT INTO [t10461] VALUES (164,'«اشهدوا أني قد رجعت عن كل ما أفتيت به الناس، إلا ما في القرآن، واجتمع عليه','56','0','0');
INSERT INTO [t10461] VALUES (165,'«توفي أبو يوسف رحمه الله يوم الخميس لخمس خلون من ربيع الأول سنة اثنتين وثمانين ومائة» ، وقال','57','0','0');
INSERT INTO [t10461] VALUES (166,'«حضرنا جنازة أبي يوسف»','57','0','0');
INSERT INTO [t10461] VALUES (167,'«ينبغي لأهل الإسلام أن يعزي بعضهم بعضا بأبي يوسف»','57','0','0');
INSERT INTO [t10461] VALUES (168,'«واستأذنوه في دفنه والصلاة عليه، فأذن لهم»','58','0','0');
INSERT INTO [t10461] VALUES (169,'«لو وجدت إلا مدا لاغتسلت»','58','0','0');
INSERT INTO [t10461] VALUES (170,'«لا وضوء في القبلة»','58','0','0');
INSERT INTO [t10461] VALUES (171,'«من أتى الجمعة فليغتسل»','58','0','0');
INSERT INTO [t10461] VALUES (172,'" لا حسد إلا في اثنتين: رجل آتاه الله مالا فسلطه على هلكته في الحق، ورجل آتاه الله علما','59','0','0');
INSERT INTO [t10461] VALUES (173,'«ما رأيت أعلم بكتاب الله من محمد بن الحسن»','62','0','0');
INSERT INTO [t10461] VALUES (174,'«لو أشاء أن أقول نزل القرآن بلغة محمد بن الحسن لقلته لفصاحته»','62','0','0');
INSERT INTO [t10461] VALUES (175,'«ما رأيت سمينا أخف روحا من محمد بن الحسن، وما رأيت أفصح منه، كنت إذا رأيته يقرأ كأن القرآن','62','0','0');
INSERT INTO [t10461] VALUES (176,'" بلغني أن داود الطائي كان يسأل عني وعن حالي، فإذا أخبر قال: إن عاش فسيكون له','62','0','0');
INSERT INTO [t10461] VALUES (177,'«ما رأيت أعلم بكتاب الله من محمد، كأنه عليه نزل»','63','0','0');
INSERT INTO [t10461] VALUES (178,'«ما سمعت أحدا قط، كان إذا تكلم رأيت أن القرآن نزل بلغته غير محمد بن الحسن، وقد كتبت عنه','63','0','0');
INSERT INTO [t10461] VALUES (179,'«حملت عن محمد بن الحسن حمل بختي كتبا، وما ناظرت أحدا إلا تغير وجهه، ما خلا محمد بن الحسن','63','0','0');
INSERT INTO [t10461] VALUES (180,'«حملت عن محمد بن الحسن حمل بختي، ليس عليه إلا سماعي»','63','0','0');
INSERT INTO [t10461] VALUES (181,'«أنفقت على كتب محمد بن الحسن ستين دينارا، ثم تدبرتها، فوضعت إلى جنب كل مسألة','63','0','0');
INSERT INTO [t10461] VALUES (182,'«ما ناظرت سمينا أذكى من محمد بن الحسن، وقد ناظرته مرة، فجعلت أوداجه تنتفخ، وأزراره','63','0','0');
INSERT INTO [t10461] VALUES (183,'«كتبت عن محمد بن الحسن الجامع الصغير»','63','0','0');
INSERT INTO [t10461] VALUES (184,'محمد بن الحسن قد انقطع قلبه من فكره في الفقه، حتى كان الرجل يسلم عليه فيدعو له محمد،','64','0','0');
INSERT INTO [t10461] VALUES (185,'«ما وضع شيء لشيء قط يوافق ذلك، إلا كتاب محمد بن الحسن في الإيمان، فإنه وافق كلام','64','0','0');
INSERT INTO [t10461] VALUES (186,'«محسدون وشر الناس منزلة من عاش في الناس يوما غير محسود»','64','0','0');
INSERT INTO [t10461] VALUES (187,'" قلت لمحمد بن الحسن، تقول: ما كان لصاحبك أن يتكلم، ولا لصاحبي أن يسكت أنشدك بالله هل تعلم','64','0','0');
INSERT INTO [t10461] VALUES (188,'" حججت مع محمد بن الحسن، فقلت له: حدثني بكتابك في كذا من الفقه، فقال: ما أنشط له، فقلت: أنا','66','0','0');
INSERT INTO [t10461] VALUES (189,'«إذا اختلف في مسألة، فحرم فقيه وأحل آخر، وكلاهما يسعه أن يجتهد، فالصواب عند الله واحد حلال','66','0','0');
INSERT INTO [t10461] VALUES (190,'«ما وضع في الإسلام كتاب في الفقه مثل جامع محمد بن الحسن الكبير»','66','0','0');
INSERT INTO [t10461] VALUES (191,'«ما أعلم أحدا أسوأ ثناء على أصحابه منكم، إذا حدثتكم عن مالك ملأتم علي الموضع، وإذا حدثتكم','67','0','0');
INSERT INTO [t10461] VALUES (192,'«هذا الكتاب يعني كتاب الحيل ليس من كتبنا، إنما ألقي فيها» ، قال ابن أبي عمران: إنما وضعه','67','0','0');
INSERT INTO [t10461] VALUES (193,'محمد بن الحسن إذا قعد للمناظرة للفقه، أقعد معه رجلا حكما بينه وبين من يناظره، فيقول لهذا','68','0','0');
INSERT INTO [t10461] VALUES (194,'«خرجنا مع محمد بن الحسن من المدينة، فلما أتى ذا الحليفة نزلنا معه وذلك قبيل الظهر، فتنحى','68','0','0');
INSERT INTO [t10461] VALUES (195,'«من كتب محمد بن الحسن»','68','0','0');
INSERT INTO [t10461] VALUES (196,'«خلف أبي ثلاثين ألف درهم، فأنفقت خمسة عشر ألفا على النحو والشعر، وخمسة عشر ألفا على الحديث','68','0','0');
INSERT INTO [t10461] VALUES (197,'«لا تسألوني حاجة من حوائج الدنيا فتشغلوا قلبي، وخذوا ما تحتاجون إليه من وكيلي، فإنه أفرغ','68','0','0');
INSERT INTO [t10461] VALUES (198,'«ما رأيت أعقل، ولا أفقه، ولا أزهد، ولا أورع، ولا أحسن نطقا وإيرادا من محمد بن الحسن» ، قلت:','69','0','0');
INSERT INTO [t10461] VALUES (199,'الحلقة في المسجد يوم الجمعة ببغداد لبشر بن الوليد، فلم يزل كذلك ونحن نجالسه فيها حتى قدم','71','0','0');
INSERT INTO [t10461] VALUES (200,'" رأيت بشر بن الوليد عند أبي، فنال من محمد بن الحسن، فقال له أبي: لا تفعل يا أبا الوليد! ،','71','0','0');
INSERT INTO [t10461] VALUES (201,'«لم يكن أبو يوسف يدقق هذا التدقيق الشديد»','71','0','0');
INSERT INTO [t10461] VALUES (202,'" مثل محمد بن الحسن في الجامع الكبير، كرجل بنى دارا، فكان كلما على بنى مرقاة يرقى منها إلى','72','0','0');
INSERT INTO [t10461] VALUES (203,'" قدمت الرقة ومحمد بن الحسن قاض عليها، فأتيت بابه فاستأذنت عليه، فحجبت عنه فانصرفت، وأقمت','72','0','0');
INSERT INTO [t10461] VALUES (204,'" لم يزل محمد بن الحسن عندي عظيما، أنفقت على كتبه ستين دينارا حتى جمعني وإياه مجلس هارون،','73','0','0');
INSERT INTO [t10461] VALUES (205,'«من كان له ذمتنا فدمه كدمنا، وديته كديتنا» ، ويحكى عن محمد بن الحسن ذكاء مفرط، وعقل تام،','76','0','0');
INSERT INTO [t10461] VALUES (206,'محمدا كان حزبه في كل يوم وليلة ثلث القرآن»','76','0','0');
INSERT INTO [t10461] VALUES (207,'أخذ ابن سماعة وعيسى بن أبان الصلاة من محمد بن الحسن»','76','0','0');
INSERT INTO [t10461] VALUES (208,'" حضرت محمدا وهو يموت فبكى، فقلت له: أتبكي مع العمل؟ فقال لي: أرأيت إن أوقفني الله تعالى،','76','0','0');
INSERT INTO [t10461] VALUES (209,'أسفت على قاضي القضاة محمد ... فأذريت دمعي والفؤاد عميد وأقلقني موت الكسائي بعده ... وكادت','77','0','0');
