INSERT INTO [t10463] VALUES (4,'(بسم الله الرحمن الرحيم (١) الحمد لله رب العالمين (٢) الرحمن الرحيم (٣) مالك يوم الدين (٤) إياك نعبد وإياك نستعين (٥) اهدنا الصراط المستقيم (٦)','44','3','0');
INSERT INTO [t10463] VALUES (5,'سورة البقرة','70','0','0');
INSERT INTO [t10463] VALUES (6,'قوله تعالى: (أو كصيب من السماء)','70','5','0');
INSERT INTO [t10463] VALUES (7,'قوله تعالى: (فإن لم تفعلوا ولن تفعلوا فاتقوا النار التي وقودها الناس والحجارة أعدت للكافرين (٢٤)','74','5','0');
INSERT INTO [t10463] VALUES (8,'قوله تعالى: (ولهم فيها أزواج مطهرة)','80','5','0');
INSERT INTO [t10463] VALUES (9,'قوله تعالى: (بلى من كسب سيئة وأحاطت به خطيئته فأولئك أصحاب النار هم فيها خالدون (٨١)','81','5','0');
INSERT INTO [t10463] VALUES (10,'قوله تعالى: (قل إن كانت لكم الدار الآخرة عند الله خالصة من دون الناس فتمنوا الموت إن كنتم صادقين (٩٤)','82','5','0');
INSERT INTO [t10463] VALUES (11,'قوله تعالى: (ويتعلمون ما يضرهم ولا ينفعهم ولقد علموا لمن اشتراه ما له في الآخرة من خلاق ولبئس ما شروا به أنفسهم لو كانوا يعلمون (١٠٢)','83','5','0');
INSERT INTO [t10463] VALUES (12,'قوله تعالى: (واتخذوا من مقام إبراهيم مصلى)','86','5','0');
INSERT INTO [t10463] VALUES (13,'قوله تعالى: (وما كان الله ليضيع إيمانكم)','92','5','0');
INSERT INTO [t10463] VALUES (14,'قوله تعالى: (فاذكروني أذكركم واشكروا لي ولا تكفرون (١٥٢)','105','5','0');
INSERT INTO [t10463] VALUES (15,'قوله تعالى: (وبشر الصابرين (١٥٥) الذين إذا أصابتهم مصيبة قالوا إنا لله وإنا إليه راجعون (١٥٦) أولئك عليهم صلوات من ربهم ورحمة وأولئك هم المهتدون (١٥٧)','108','5','0');
INSERT INTO [t10463] VALUES (16,'قوله تعالى: (ليس البر أن تولوا وجوهكم قبل المشرق والمغرب ولكن البر من آمن بالله واليوم الآخر والملائكة والكتاب والنبيين وآتى المال على حبه ذوي القربى','110','5','0');
INSERT INTO [t10463] VALUES (17,'قوله تعالى: (ولتكملوا العدة ولتكبروا الله على ما هداكم ولعلكم تشكرون (١٨٥)','112','5','0');
INSERT INTO [t10463] VALUES (18,'قوله تعالى: (وإذا سألك عبادي عني فإني قريب أجيب دعوة الداع إذا دعان فليستجيبوا لي وليؤمنوا بي لعلهم يرشدون (١٨٦)','114','5','0');
INSERT INTO [t10463] VALUES (19,'قوله تعالى: (فالآن باشروهن وابتغوا ما كتب الله لكم)','119','5','0');
INSERT INTO [t10463] VALUES (20,'قوله تعالى: (تلك حدود الله فلا تقربوها كذلك يبين الله آياته للناس لعلهم يتقون (١٨٧)','120','5','0');
INSERT INTO [t10463] VALUES (21,'قوله تعالى: (وأنفقوا في سبيل الله ولا تلقوا بأيديكم إلى التهلكة وأحسنوا إن الله يحب المحسنين (١٩٥) وأتموا الحج والعمرة لله) .','122','5','0');
INSERT INTO [t10463] VALUES (22,'وقال الله تعالى: (الحج أشهر معلومات فمن فرض فيهن الحج فلا رفث ولا فسوق ولا جدال في الحج)','123','5','0');
INSERT INTO [t10463] VALUES (23,'قوله تعالى: (وتزودوا فإن خير الزاد التقوى)','125','5','0');
INSERT INTO [t10463] VALUES (24,'قوله تعالى: (واستغفروا الله إن الله غفور رحيم (١٩٩)','126','5','0');
INSERT INTO [t10463] VALUES (25,'قوله تعالى: (واذكروا الله في أيام معدودات)','130','5','0');
INSERT INTO [t10463] VALUES (26,'قوله تعالى: (ويسألونك عن المحيض قل هو أذى فاعتزلوا النساء في المحيض ولا تقربوهن حتى يطهرن فإذا تطهرن فأتوهن من حيث أمركم الله إن الله يحب التوابين ويحب','143','5','0');
INSERT INTO [t10463] VALUES (27,'قوله تعالى: (لا يؤاخذكم الله باللغو في أيمانكم ولكن يؤاخذكم بما كسبت قلوبكم)','149','5','0');
INSERT INTO [t10463] VALUES (28,'قوله تعالى: (ولا يحل لهن أن يكتمن ما خلق الله في أرحامهن إن كن يؤمن بالله واليوم الآخر)','154','5','0');
INSERT INTO [t10463] VALUES (29,'قوله تعالى: (فأمسكوهن بمعروف أو سرحوهن بمعروف ولا تمسكوهن ضرارا لتعتدوا ومن يفعل ذلك فقد ظلم نفسه)','156','5','0');
INSERT INTO [t10463] VALUES (30,'قال تعالى: (لا تضار والدة بولدها ولا مولود له بولده)','157','5','0');
INSERT INTO [t10463] VALUES (31,'قوله تعالى: (حافظوا على الصلوات والصلاة الوسطى وقوموا لله قانتين)','158','5','0');
INSERT INTO [t10463] VALUES (32,'قوله تعالى: (فإن خفتم فرجالا أو ركبانا فإذا أمنتم فاذكروا الله كما علمكم ما لم تكونوا تعلمون (٢٣٩)','162','5','0');
INSERT INTO [t10463] VALUES (33,'قوله تعالى: (ولولا دفع الله الناس بعضهم ببعض لفسدت الأرض ولكن الله ذو فضل على العالمين (٢٥١)','168','5','0');
INSERT INTO [t10463] VALUES (34,'قوله تعالى: (وإذ قال إبراهيم رب أرني كيف تحي الموتى قال أولم تؤمن قال بلى ولكن ليطمئن قلبي قال فخذ أربعة من الطير فصرهن إليك ثم اجعل على كل جبل منهن ج','168','5','0');
INSERT INTO [t10463] VALUES (35,'قوله تعالى: (إن تبدوا الصدقات فنعما هي وإن تخفوها وتؤتوها الفقراء فهو خير لكم ويكفر عنكم من سيئاتكم والله بما تعملون خبير (٢٧١)','169','5','0');
INSERT INTO [t10463] VALUES (36,'قوله تعالى: (الذين يأكلون الربا لا يقومون إلا كما يقوم الذي يتخبطه الشيطان من المس ذلك بأنهم قالوا إنما البيع مثل الربا','172','5','0');
INSERT INTO [t10463] VALUES (37,'قوله تعالى: (لله ما في السماوات وما في الأرض وإن تبدوا ما في أنفسكم أو تخفوه يحاسبكم به الله فيغفر لمن يشاء ويعذب من يشاء','176','5','0');
INSERT INTO [t10463] VALUES (38,'سورة آل عمران','177','0','0');
INSERT INTO [t10463] VALUES (39,'قوله تعالى: (إن الدين عند الله الإسلام)','177','38','0');
INSERT INTO [t10463] VALUES (40,'قوله تعالى: (قل إن كنتم تحبون الله فاتبعوني يحببكم الله ويغفر لكم ذنوبكم والله غفور رحيم (٣١)','177','38','0');
INSERT INTO [t10463] VALUES (41,'قوله تعالى: (إذ قالت امرأت عمران رب إني نذرت لك ما في بطني محررا فتقبل مني إنك أنت السميع العليم (٣٥)','181','38','0');
INSERT INTO [t10463] VALUES (42,'قوله تعالى: (كنتم خير أمة أخرجت للناس تأمرون بالمعروف وتنهون عن المنكر وتؤمنون بالله)','183','38','0');
INSERT INTO [t10463] VALUES (43,'قوله تعالى: (وسارعوا إلى مغفرة من ربكم وجنة عرضها السماوات والأرض أعدت للمتقين (١٣٣)','184','38','0');
INSERT INTO [t10463] VALUES (44,'قوله تعالى: (فبما رحمة من الله لنت لهم ولو كنت فظا غليظ القلب لانفضوا من حولك فاعف عنهم واستغفر لهم وشاورهم في الأمر','197','38','0');
INSERT INTO [t10463] VALUES (45,'قوله تعالى: (لقد من الله على المؤمنين إذ بعث فيهم رسولا من أنفسهم يتلو عليهم آياته ويزكيهم ويعلمهم الكتاب والحكمة وإن كانوا من قبل لفي ضلال مبين (١٦٤)','199','38','0');
INSERT INTO [t10463] VALUES (46,'قوله تعالى: (ولا تحسبن الذين قتلوا في سبيل الله أمواتا بل أحياء عند ربهم يرزقون (١٦٩) فرحين بما آتاهم الله من فضله','202','38','0');
INSERT INTO [t10463] VALUES (47,'قوله تعالى: (الذين قال لهم الناس إن الناس قد جمعوا لكم فاخشوهم فزادهم إيمانا وقالوا حسبنا الله ونعم الوكيل (١٧٣)','248','38','0');
INSERT INTO [t10463] VALUES (48,'قوله تعالى: (لا تحسبن الذين يفرحون بما أتوا ويحبون أن يحمدوا بما لم يفعلوا فلا تحسبنهم بمفازة من العذاب ولهم عذاب أليم (١٨٨)','249','38','0');
INSERT INTO [t10463] VALUES (49,'سورة النساء','256','0','0');
INSERT INTO [t10463] VALUES (50,'قوله تعالى: (فإن خفتم ألا تعدلوا فواحدة أو ما ملكت أيمانكم ذلك أدنى ألا تعولوا (٣)','256','49','0');
INSERT INTO [t10463] VALUES (51,'قوله تعالى: (وليخش الذين لو تركوا من خلفهم ذرية ضعافا خافوا عليهم فليتقوا الله وليقولوا قولا سديدا (٩)','257','49','0');
INSERT INTO [t10463] VALUES (52,'قوله تعالى: (يوصيكم الله في أولادكم للذكر مثل حظ الأنثيين فإن كن نساء فوق اثنتين فلهن ثلثا ما ترك وإن كانت واحدة فلها النصف','257','49','0');
INSERT INTO [t10463] VALUES (53,'قوله تعالى: (إنما التوبة على الله للذين يعملون السوء بجهالة ثم يتوبون من قريب فأولئك يتوب الله عليهم وكان الله عليما حكيما (١٧)','274','49','0');
INSERT INTO [t10463] VALUES (54,'قوله تعالى: (يا أيها الذين آمنوا لا تأكلوا أموالكم بينكم بالباطل إلا أن تكون تجارة عن تراض منكم ولا تقتلوا أنفسكم إن الله كان بكم رحيما (٢٩)','297','49','0');
INSERT INTO [t10463] VALUES (55,'قوله تعالى: (إن تجتنبوا كبائر ما تنهون عنه نكفر عنكم سيئاتكم وندخلكم مدخلا كريما (٣١)','304','49','0');
INSERT INTO [t10463] VALUES (56,'قوله تعالى: (ولا تتمنوا ما فضل الله به بعضكم على بعض للرجال نصيب مما اكتسبوا وللنساء نصيب مما اكتسبن واسألوا الله من فضله','310','49','0');
INSERT INTO [t10463] VALUES (57,'قوله تعالى: (واعبدوا الله ولا تشركوا به شيئا وبالوالدين إحسانا وبذي القربى واليتامى والمساكين والجار ذي القربى','310','49','0');
INSERT INTO [t10463] VALUES (58,'قوله تعالى: (يا أيها الذين آمنوا لا تقربوا الصلاة وأنتم سكارى حتى تعلموا ما تقولون ولا جنبا إلا عابري سبيل حتى تغتسلوا','314','49','0');
INSERT INTO [t10463] VALUES (59,'قوله تعالى: (إن الله لا يغفر أن يشرك به ويغفر ما دون ذلك لمن يشاء ومن يشرك بالله فقد افترى إثما عظيما (٤٨)','316','49','0');
INSERT INTO [t10463] VALUES (60,'قوله تعالى: (إن الذين كفروا بآياتنا سوف نصليهم نارا كلما نضجت جلودهم بدلناهم جلودا غيرها ليذوقوا العذاب إن الله كان عزيزا حكيما (٥٦)','318','49','0');
INSERT INTO [t10463] VALUES (61,'قوله تعالى: (يا أيها الذين آمنوا أطيعوا الله وأطيعوا الرسول وأولي الأمر منكم فإن تنازعتم في شيء فردوه إلى الله والرسول إن كنتم تؤمنون بالله واليوم الآخر ذلك خ','319','49','0');
INSERT INTO [t10463] VALUES (62,'قوله تعالى: (لا يستوي القاعدون من المؤمنين غير أولي الضرر والمجاهدون في سبيل الله بأموالهم وأنفسهم فضل الله المجاهدين بأموالهم وأنفسهم على القاعدين درجة','320','49','0');
INSERT INTO [t10463] VALUES (63,'قوله تعالى: (وإذا ضربتم في الأرض فليس عليكم جناح أن تقصروا من الصلاة إن خفتم أن يفتنكم الذين كفروا إن الكافرين كانوا لكم عدوا مبينا (١٠١)','321','49','0');
INSERT INTO [t10463] VALUES (64,'قوله تعالى: (فإذا قضيتم الصلاة فاذكروا الله قياما وقعودا وعلى جنوبكم فإذا اطمأننتم فأقيموا الصلاة إن الصلاة كانت على المؤمنين كتابا موقوتا (١٠٣)','333','49','0');
INSERT INTO [t10463] VALUES (65,'قوله تعالى: (لا خير في كثير من نجواهم إلا من أمر بصدقة أو معروف أو إصلاح بين الناس ومن يفعل ذلك ابتغاء مرضات الله فسوف نؤتيه أجرا عظيما (١١٤)','335','49','0');
INSERT INTO [t10463] VALUES (66,'قوله تعالى: (ليس بأمانيكم ولا أماني أهل الكتاب من يعمل سوءا يجز به ولا يجد له من دون الله وليا ولا نصيرا (١٢٣)','336','49','0');
INSERT INTO [t10463] VALUES (67,'قوله تعالى: (وما في الأرض ولقد وصينا الذين أوتوا الكتاب من قبلكم وإياكم أن اتقوا الله وإن تكفروا فإن لله ما في السماوات وما في الأرض وكان الله غنيا حميدا (١٣١)','337','49','0');
INSERT INTO [t10463] VALUES (68,'قوله تعالى: (إن المنافقين في الدرك الأسفل من النار ولن تجد لهم نصيرا (١٤٥)','345','49','0');
INSERT INTO [t10463] VALUES (69,'قوله تعالى: (لا يحب الله الجهر بالسوء من القول إلا من ظلم وكان الله سميعا عليما (١٤٨)','352','49','0');
INSERT INTO [t10463] VALUES (70,'قوله تعالى: (يستفتونك قل الله يفتيكم في الكلالة إن امرؤ هلك ليس له ولد وله أخت فلها نصف ما ترك وهو يرثها إن لم يكن لها ولد فإن كانتا اثنتين فلهما الثلثان م','353','49','0');
INSERT INTO [t10463] VALUES (71,'سورة المائدة','358','0','0');
INSERT INTO [t10463] VALUES (72,'قوله تعالى: (وتعاونوا على البر والتقوى ولا تعاونوا على الإثم والعدوان واتقوا الله إن الله شديد العقاب (٢)','358','71','0');
INSERT INTO [t10463] VALUES (73,'قوله تعالى: (اليوم أكملت لكم دينكم وأتممت عليكم نعمتي ورضيت لكم الإسلام دينا)','360','71','0');
INSERT INTO [t10463] VALUES (74,'قوله تعالى: (يا أيها الذين آمنوا إذا قمتم إلى الصلاة فاغسلوا وجوهكم وأيديكم إلى المرافق وامسحوا برءوسكم وأرجلكم إلى الكعبين','372','71','0');
INSERT INTO [t10463] VALUES (75,'قوله تعالى: (فبما نقضهم ميثاقهم لعناهم وجعلنا قلوبهم قاسية يحرفون الكلم عن مواضعه ونسوا حظا مما ذكروا به ولا تزال تطلع على خائنة منهم إلا قليلا منهم','395','71','0');
INSERT INTO [t10463] VALUES (76,'قوله تعالى: (يا أهل الكتاب قد جاءكم رسولنا يبين لكم كثيرا مما كنتم تخفون من الكتاب ويعفو عن كثير قد جاءكم من الله نور وكتاب مبين (١٥)','398','71','0');
INSERT INTO [t10463] VALUES (77,'قوله تعالى: (إنما يتقبل الله من المتقين)','400','71','0');
INSERT INTO [t10463] VALUES (78,'قوله تعالى: (من قتل نفسا بغير نفس أو فساد في الأرض فكأنما قتل الناس جميعا ومن أحياها فكأنما أحيا الناس جميعا)','402','71','0');
INSERT INTO [t10463] VALUES (79,'قوله تعالى: (ومن لم يحكم بما أنزل الله فأولئك هم الكافرون)','402','71','0');
INSERT INTO [t10463] VALUES (80,'قوله تعالى: (وكتبنا عليهم فيها أن النفس بالنفس والعين بالعين والأنف بالأنف والأذن بالأذن والسن بالسن والجروح قصاص فمن تصدق به فهو كفارة له','408','71','0');
INSERT INTO [t10463] VALUES (81,'قوله تعالى: (لكل جعلنا منكم شرعة ومنهاجا)','411','71','0');
INSERT INTO [t10463] VALUES (82,'قوله تعالى: (يا أيها الذين آمنوا من يرتد منكم عن دينه فسوف يأتي الله بقوم يحبهم ويحبونه','412','71','0');
INSERT INTO [t10463] VALUES (83,'قوله تعالى: (وإذا ناديتم إلى الصلاة اتخذوها هزوا ولعبا ذلك بأنهم قوم لا يعقلون (٥٨)','418','71','0');
INSERT INTO [t10463] VALUES (84,'قوله تعالى: (إنما الخمر والميسر والأنصاب والأزلام رجس من عمل الشيطان فاجتنبوه لعلكم تفلحون (٩٠)','422','71','0');
INSERT INTO [t10463] VALUES (85,'قوله تعالى: (يا أيها الذين آمنوا لا تسألوا عن أشياء إن تبد لكم تسؤكم وإن تسألوا عنها حين ينزل القرآن تبد لكم عفا الله عنها والله غفور حليم (١٠١)','424','71','0');
INSERT INTO [t10463] VALUES (86,'قوله تعالى: (يا أيها الذين آمنوا عليكم أنفسكم لا يضركم من ضل إذا اهتديتم إلى الله مرجعكم جميعا فينبئكم بما كنتم تعملون (١٠٥)','438','71','0');
INSERT INTO [t10463] VALUES (87,'قوله تعالى: (يا أيها الذين آمنوا شهادة بينكم إذا حضر أحدكم الموت حين الوصية اثنان ذوا عدل منكم أو آخران من غيركم إن أنتم ضربتم في الأرض فأصابتكم مصيبة الم','440','71','0');
INSERT INTO [t10463] VALUES (88,'سورة الأنعام','443','0','0');
INSERT INTO [t10463] VALUES (89,'قوله تعالى: (وعنده مفاتح الغيب لا يعلمها إلا هو ويعلم ما في البر والبحر وما تسقط من ورقة إلا يعلمها ولا حبة في ظلمات الأرض ولا رطب ولا يابس إلا في كتاب مبي','443','88','0');
INSERT INTO [t10463] VALUES (90,'قوله تعالى: (الذين آمنوا ولم يلبسوا إيمانهم بظلم أولئك لهم الأمن وهم مهتدون (٨٢)','447','88','0');
INSERT INTO [t10463] VALUES (91,'قوله تعالى: (قل تعالوا أتل ما حرم ربكم عليكم ألا تشركوا به شيئا وبالوالدين إحسانا ولا تقتلوا أولادكم من إملاق نحن نرزقكم وإياهم','450','88','0');
INSERT INTO [t10463] VALUES (92,'قوله تعالى: (من جاء بالحسنة فله عشر أمثالها ومن جاء بالسيئة فلا يجزى إلا مثلها وهم لا يظلمون (١٦٠)','451','88','0');
INSERT INTO [t10463] VALUES (93,'سورة الأعراف','454','0','0');
INSERT INTO [t10463] VALUES (94,'قوله تعالى: (يا بني آدم خذوا زينتكم عند كل مسجد وكلوا واشربوا ولا تسرفوا إنه لا يحب المسرفين (٣١)','454','93','0');
INSERT INTO [t10463] VALUES (95,'قوله تعالى: (لهم من جهنم مهاد ومن فوقهم غواش وكذلك نجزي الظالمين (٤١)','458','93','0');
INSERT INTO [t10463] VALUES (96,'قوله تعالى: (ونادى أصحاب الجنة أصحاب النار أن قد وجدنا ما وعدنا ربنا حقا فهل وجدتم ما وعد ربكم حقا قالوا نعم فأذن مؤذن بينهم أن لعنة الله على الظالمين (٤٤','460','93','0');
INSERT INTO [t10463] VALUES (97,'قوله تعالى: (قد افترينا على الله كذبا إن عدنا في ملتكم بعد إذ نجانا الله منها)','463','93','0');
INSERT INTO [t10463] VALUES (98,'قوله تعالى: (وواعدنا موسى ثلاثين ليلة وأتممناها بعشر فتم ميقات ربه أربعين ليلة)','463','93','0');
INSERT INTO [t10463] VALUES (99,'سورة الأنفال','464','0','0');
INSERT INTO [t10463] VALUES (100,'قوله تعالى: (واعلموا أن الله يحول بين المرء وقلبه وأنه إليه تحشرون (٢٤)','464','99','0');
INSERT INTO [t10463] VALUES (101,'قوله تعالى: (وما كان صلاتهم عند البيت إلا مكاء وتصدية فذوقوا العذاب بما كنتم تكفرون (٣٥)','464','99','0');
INSERT INTO [t10463] VALUES (102,'سورة التوبة','467','0','0');
INSERT INTO [t10463] VALUES (103,'قوله تعالى: (ما كان للمشركين أن يعمروا مساجد الله شاهدين على أنفسهم بالكفر أولئك حبطت أعمالهم وفي النار هم خالدون (١٧)','467','102','0');
INSERT INTO [t10463] VALUES (104,'قال الله تعالى: (أجعلتم سقاية الحاج وعمارة المسجد الحرام كمن آمن بالله واليوم الآخر وجاهد في سبيل الله لا يستوون عند الله والله لا يهدي القوم الظالمين (١٩)','471','102','0');
INSERT INTO [t10463] VALUES (105,'قوله تعالى: (قل إن كان آباؤكم وأبناؤكم وإخوانكم وأزواجكم وعشيرتكم وأموال اقترفتموها وتجارة تخشون كسادها ومساكن ترضونها','472','102','0');
INSERT INTO [t10463] VALUES (106,'قوله تعالى: (يا أيها الذين آمنوا إنما المشركون نجس فلا يقربوا المسجد الحرام بعد عامهم هذا وإن خفتم عيلة فسوف يغنيكم الله من فضله إن شاء إن الله عليم حكيم (٢','485','102','0');
INSERT INTO [t10463] VALUES (107,'قوله تعالى: (يا أيها الذين آمنوا إن كثيرا من الأحبار والرهبان ليأكلون أموال الناس بالباطل ويصدون عن سبيل الله','490','102','0');
INSERT INTO [t10463] VALUES (108,'قال تعالى: (إن عدة الشهور عند الله اثنا عشر شهرا في كتاب الله يوم خلق السماوات والأرض منها أربعة حرم ذلك الدين القيم فلا تظلموا فيهن أنفسكم)','492','102','0');
INSERT INTO [t10463] VALUES (109,'قوله تعالى: (قل لن يصيبنا إلا ما كتب الله لنا هو مولانا وعلى الله فليتوكل المؤمنون (٥١)','502','102','0');
INSERT INTO [t10463] VALUES (110,'قل لن يمميبنا إلا ما كتب الله لمما','502','102','0');
INSERT INTO [t10463] VALUES (111,'قوله تعالى: (وقالوا لا تنفروا في الحر قل نار جهنم أشد حرا لو كانوا يفقهون (٨١)','503','102','0');
INSERT INTO [t10463] VALUES (112,'قوله تعالى: (ليس على الضعفاء ولا على المرضى ولا على الذين لا يجدون ما ينفقون حرج إذا نصحوا لله ورسوله ما على المحسنين من سبيل والله غفور رحيم (٩١)','505','102','0');
INSERT INTO [t10463] VALUES (113,'قوله تعالى (والذين اتخذوا مسجدا ضرارا وكفرا وتفريقا بين المؤمنين وإرصادا لمن حارب الله ورسوله من قبل وليحلفن إن أردنا إلا الحسنى','506','102','0');
INSERT INTO [t10463] VALUES (114,'سورة يونس','507','0','0');
INSERT INTO [t10463] VALUES (115,'قوله تعالى: (هو الذي جعل الشمس ضياء والقمر نورا وقدره منازل لتعلموا عدد السنين والحساب ما خلق الله ذلك إلا بالحق يفصل الآيات لقوم يعلمون (٥)','507','114','0');
INSERT INTO [t10463] VALUES (116,'قوله تعالى: (إن الذين لا يرجون لقاءنا ورضوا بالحياة الدنيا واطمأنوا بها والذين هم عن آياتنا غافلون (٧)','513','114','0');
INSERT INTO [t10463] VALUES (117,'قوله تعالى: (للذين أحسنوا الحسنى وزيادة)','517','114','0');
INSERT INTO [t10463] VALUES (118,'سورة هود','524','0','0');
INSERT INTO [t10463] VALUES (119,'قوله تعالى: (ألا إنهم يثنون صدورهم ليستخفوا منه ألا حين يستغشون ثيابهم يعلم ما يسرون وما يعلنون إنه عليم بذات الصدور (٥)','524','118','0');
INSERT INTO [t10463] VALUES (120,'قوله تعالى: (وهو الذي خلق السماوات والأرض في ستة أيام وكان عرشه على الماء ليبلوكم أيكم أحسن عملا)','525','118','0');
INSERT INTO [t10463] VALUES (121,'قوله تعالى: (ألا يوم يأتيهم ليس مصروفا عنهم وحاق بهم ما كانوا به يستهزئون (٨)','529','118','0');
INSERT INTO [t10463] VALUES (122,'قوله تعالى: (من كان يريد الحياة الدنيا وزينتها نوف إليهم أعمالهم فيها وهم فيها لا يبخسون (١٥)','529','118','0');
INSERT INTO [t10463] VALUES (123,'قال تعالى: (وأقم الصلاة طرفي النهار وزلفا من الليل إن الحسنات يذهبن السيئات ذلك ذكرى للذاكرين (١١٤)','535','118','0');
INSERT INTO [t10463] VALUES (124,'قوله تعالى: (وكلا نقص عليك من أنباء الرسل ما نثبت به فؤادك وجاءك في هذه الحق وموعظة وذكرى للمؤمنين (١٢٠)','549','118','0');
INSERT INTO [t10463] VALUES (125,'سورة يوسف','550','0','0');
INSERT INTO [t10463] VALUES (126,'قوله تعالى: (توفني مسلما وألحقني بالصالحين (١٠١)','550','125','0');
INSERT INTO [t10463] VALUES (127,'سورة الرعد','552','0','0');
INSERT INTO [t10463] VALUES (128,'قوله تعالى: (له معقبات من بين يديه ومن خلفه يحفظونه من أمر الله)','552','127','0');
INSERT INTO [t10463] VALUES (129,'قوله تعالى: (أنزل من السماء ماء فسالت أودية بقدرها فاحتمل السيل زبدا رابيا ومما يوقدون عليه في النار ابتغاء حلية أو متاع زبد مثله كذلك يضرب الله الحق والب','557','127','0');
INSERT INTO [t10463] VALUES (130,'قوله تعالى: (يمحو الله ما يشاء ويثبت وعنده أم الكتاب (٣٩)','562','127','0');
INSERT INTO [t10463] VALUES (131,'سورة إبراهيم','564','0','0');
INSERT INTO [t10463] VALUES (132,'قال الله تعالى: (ويأتيه الموت من كل مكان وما هو بميت ومن ورائه عذاب غليظ (١٧)','564','131','0');
INSERT INTO [t10463] VALUES (133,'قوله تعالى: (ألم تر كيف ضرب الله مثلا كلمة طيبة كشجرة طيبة أصلها ثابت وفرعها في السماء (٢٤) تؤتي أكلها كل حين بإذن ربها','565','131','0');
INSERT INTO [t10463] VALUES (134,'قال الله عز وجل: (يثبت الله الذين آمنوا بالقول الثابت في الحياة الدنيا وفي الآخرة ويضل الله الظالمين ويفعل الله ما يشاء (٢٧)','567','131','0');
INSERT INTO [t10463] VALUES (135,'قال الله عز وجل: (وترى المجرمين يومئذ مقرنين في الأصفاد (٤٩) سرابيلهم من قطران وتغشى وجوههم النار (٥٠)','577','131','0');
INSERT INTO [t10463] VALUES (136,'سورة الحجر','579','0','0');
INSERT INTO [t10463] VALUES (137,'قوله تعالى: (إنا نحن نزلنا الذكر وإنا له لحافظون (٩)','579','136','0');
INSERT INTO [t10463] VALUES (138,'قال الله عز وجل: (وإن جهنم لموعدهم أجمعين (٤٣) لها سبعة أبواب لكل باب منهم جزء مقسوم (٤٤)','583','136','0');
INSERT INTO [t10463] VALUES (139,'قوله تعالى: (فوربك لنسألنهم أجمعين (٩٢) عما كانوا يعملون (٩٣)','586','136','0');
INSERT INTO [t10463] VALUES (140,'قوله تعالى: (واعبد ربك حتى يأتيك اليقين (٩٩)','587','136','0');
INSERT INTO [t10463] VALUES (141,'سورة النحل','589','0','0');
INSERT INTO [t10463] VALUES (142,'قوله تعالى: (وبالنجم هم يهتدون (١٦)','589','141','0');
INSERT INTO [t10463] VALUES (143,'قال تعالى: (وما بكم من نعمة فمن الله)','590','141','0');
INSERT INTO [t10463] VALUES (144,'قوله تعالى: (الذين كفروا وصدوا عن سبيل الله زدناهم عذابا فوق العذاب بما كانوا يفسدون (٨٨)','590','141','0');
INSERT INTO [t10463] VALUES (145,'قوله تعالى: (ونزلنا عليك الكتاب تبيانا لكل شيء وهدى ورحمة وبشرى للمسلمين (٨٩)','592','141','0');
INSERT INTO [t10463] VALUES (146,'قوله تعالى: (إن الله يأمر بالعدل والإحسان وإيتاء ذي القربى وينهى عن الفحشاء والمنكر والبغي يعظكم لعلكم تذكرون (٩٠)','593','141','0');
INSERT INTO [t10463] VALUES (147,'قوله تعالى: (من عمل صالحا من ذكر أو أنثى وهو مؤمن فلنحيينه حياة طيبة ولنجزينهم أجرهم بأحسن ما كانوا يعملون (٩٧)','598','141','0');
INSERT INTO [t10463] VALUES (148,'قوله تعالى: (فإذا قرأت القرآن فاستعذ بالله من الشيطان الرجيم (٩٨)','598','141','0');
INSERT INTO [t10463] VALUES (149,'سورة الإسراء','603','0','0');
INSERT INTO [t10463] VALUES (150,'قوله تعالى: (سبحان الذي أسرى بعبده ليلا من المسجد الحرام إلى المسجد الأقصا الذي باركنا حوله لنريه من آياتنا إنه هو السميع البصير (١)','603','149','0');
INSERT INTO [t10463] VALUES (151,'قوله تعالى: (ولا تجعل يدك مغلولة إلى عنقك ولا تبسطها كل البسط فتقعد ملوما محسورا (٢٩)','604','149','0');
INSERT INTO [t10463] VALUES (152,'قوله تعالى: (وإن من شيء إلا يسبح بحمده ولكن لا تفقهون تسبيحهم إنه كان حليما غفورا (٤٤)','606','149','0');
INSERT INTO [t10463] VALUES (153,'قوله تعالى: (وإذا قرأت القرآن جعلنا بينك وبين الذين لا يؤمنون بالآخرة حجابا مستورا (٤٥)','606','149','0');
INSERT INTO [t10463] VALUES (154,'قوله تعالى: (يوم ندعو كل أناس بإمامهم فمن أوتي كتابه بيمينه فأولئك يقرءون كتابهم ولا يظلمون فتيلا (٧١)','607','149','0');
INSERT INTO [t10463] VALUES (155,'قوله تعالى: (أقم الصلاة لدلوك الشمس إلى غسق الليل وقرآن الفجر إن قرآن الفجر كان مشهودا (٧٨)','608','149','0');
INSERT INTO [t10463] VALUES (156,'قوله تعالى: (وننزل من القرآن ما هو شفاء ورحمة للمؤمنين ولا يزيد الظالمين إلا خسارا (٨٢)','616','149','0');
INSERT INTO [t10463] VALUES (157,'قال الله عز وجل: (ومن يهد الله فهو المهتد ومن يضلل فلن تجد لهم أولياء من دونه ونحشرهم يوم القيامة على وجوههم عميا وبكما وصما مأواهم جهنم','616','149','0');
INSERT INTO [t10463] VALUES (158,'قوله تعالى: (ولا تجهر بصلاتك ولا تخافت بها وابتغ بين ذلك سبيلا (١١٠)','617','149','0');
INSERT INTO [t10463] VALUES (159,'سورة الكهف','619','0','0');
INSERT INTO [t10463] VALUES (160,'قوله تعالى: (وكذلك أعثرنا عليهم ليعلموا أن وعد الله حق وأن الساعة لا ريب فيها إذ يتنازعون بينهم أمرهم فقالوا ابنوا عليهم بنيانا ربهم أعلم','619','159','0');
INSERT INTO [t10463] VALUES (161,'قوله عز وجل: (ولا تقولن لشيء إني فاعل ذلك غدا (٢٣) إلا أن يشاء الله واذكر ربك إذا نسيت وقل عسى أن يهدين ربي لأقرب من هذا رشدا (٢٤)','628','159','0');
INSERT INTO [t10463] VALUES (162,'قال الله تعالى: (إنا أعتدنا للظالمين نارا أحاط بهم سرادقها وإن يستغيثوا يغاثوا بماء كالمهل يشوي الوجوه بئس الشراب وساءت مرتفقا (٢٩)','632','159','0');
INSERT INTO [t10463] VALUES (163,'قوله تعالى: (ولولا إذ دخلت جنتك قلت ما شاء الله لا قوة إلا بالله إن ترن أنا أقل منك مالا وولدا (٣٩)','635','159','0');
INSERT INTO [t10463] VALUES (164,'قوله تعالى: (ووضع الكتاب فترى المجرمين مشفقين مما فيه ويقولون يا ويلتنا مال هذا الكتاب لا يغادر صغيرة ولا كبيرة إلا أحصاها)','636','159','0');
INSERT INTO [t10463] VALUES (165,'قوله تعالى: (فما اسطاعوا أن يظهروه وما استطاعوا له نقبا (٩٧)','638','159','0');
INSERT INTO [t10463] VALUES (166,'سورة مريم','639','0','0');
INSERT INTO [t10463] VALUES (167,'قوله تعالى: (وأنذرهم يوم الحسرة إذ قضي الأمر وهم في غفلة وهم لا يؤمنون (٣٩)','639','166','0');
INSERT INTO [t10463] VALUES (168,'قوله تعالى: (وإن منكم إلا واردها كان على ربك حتما مقضيا (٧١) ثم ننجي الذين اتقوا ونذر الظالمين فيها جثيا (٧٢)','643','166','0');
INSERT INTO [t10463] VALUES (169,'قوله تعالى: (إن الذين آمنوا وعملوا الصالحات سيجعل لهم الرحمن ودا (٩٦)','653','166','0');
INSERT INTO [t10463] VALUES (170,'سورة طه','655','0','0');
INSERT INTO [t10463] VALUES (171,'قوله تعالى: (وأقم الصلاة لذكري)','655','170','0');
INSERT INTO [t10463] VALUES (172,'قوله تعالى: (إن الساعة آتية أكاد أخفيها لتجزى كل نفس بما تسعى (١٥)','659','170','0');
INSERT INTO [t10463] VALUES (173,'قوله تعالى: (وما تلك بيمينك يا موسى (١٧) قال هي عصاي أتوكأ عليها وأهش بها على غنمي ولي فيها مآرب أخرى (١٨)','659','170','0');
INSERT INTO [t10463] VALUES (174,'قوله تعالى: (قال هم أولاء على أثري وعجلت إليك رب لترضى (٨٤)','659','170','0');
INSERT INTO [t10463] VALUES (175,'قوله تعالى: (ومن أعرض عن ذكري فإن له معيشة ضنكا)','660','170','0');
INSERT INTO [t10463] VALUES (176,'قوله تعالى: (ورزق ربك خير وأبقى)','671','170','0');
INSERT INTO [t10463] VALUES (177,'سورة الأنبياء','675','0','0');
INSERT INTO [t10463] VALUES (178,'قوله تعالى: (ونبلوكم بالشر والخير فتنة)','675','177','0');
INSERT INTO [t10463] VALUES (179,'قوله تعالى: (الذين يخشون ربهم بالغيب وهم من الساعة مشفقون (٤٩)','678','177','0');
INSERT INTO [t10463] VALUES (180,'قوله تعالى: (لا يحزنهم الفزع الأكبر وتتلقاهم الملائكة هذا يومكم الذي كنتم توعدون (١٠٣)','682','177','0');
INSERT INTO [t10463] VALUES (181,'قوله تعالى: (إنه يعلم الجهر من القول ويعلم ما تكتمون (١١٠)','683','177','0');
INSERT INTO [t10463] VALUES (182,'قوله تعالى: (قال رب احكم بالحق وربنا الرحمن المستعان على ما تصفون (١١٢)','683','177','0');
INSERT INTO [t10463] VALUES (183,'سورة الحج','684','0','0');
INSERT INTO [t10463] VALUES (184,'قوله تعالى: (يا أيها الناس إن كنتم في ريب من البعث فإنا خلقناكم من تراب ثم من نطفة ثم من علقة ثم من مضغة مخلقة وغير مخلقة لنبين لكم','684','183','0');
INSERT INTO [t10463] VALUES (185,'قوله تعالى: (فالذين كفروا قطعت لهم ثياب من نار يصب من فوق رءوسهم الحميم (١٩) يصهر به ما في بطونهم والجلود (٢٠) ولهم مقامع من حديد (٢١)','690','183','0');
INSERT INTO [t10463] VALUES (186,'قوله تعالى: (لن ينال الله لحومها ولا دماؤها ولكن يناله التقوى منكم)','694','183','0');
INSERT INTO [t10463] VALUES (187,'سورة المؤمنون','699','0','0');
INSERT INTO [t10463] VALUES (188,'قوله تعالى: (قد أفلح المؤمنون (١) الذين هم في صلاتهم خاشعون (٢)','699','187','0');
INSERT INTO [t10463] VALUES (189,'قوله تعالى: (يؤتون ما آتوا وقلوبهم وجلة)','722','187','0');
INSERT INTO [t10463] VALUES (190,'قوله تعالى: (أم تسألهم خرجا فخراج ربك خير وهو خير الرازقين (٧٢)','724','187','0');
INSERT INTO [t10463] VALUES (191,'قوله تعالى: (ومن ورائهم برزخ إلى يوم يبعثون (١٠٠)','725','187','0');
INSERT INTO [t10463] VALUES (192,'قوله تعالى: (تلفح وجوههم النار وهم فيها كالحون (١٠٤)','726','187','0');
INSERT INTO [t10463] VALUES (193,'سورة النور','728','0','0');
