INSERT INTO [t10470] VALUES (1,'بسم الله الرحمن الرحيم','2','0','0');
INSERT INTO [t10470] VALUES (2,'- قال في جـ ٤ ص ٩٣ , ٩٤:','6','0','0');
INSERT INTO [t10470] VALUES (3,'- قال في جـ ٤ ص ١٠٥ على قوله في الحديث أطيب عند الله من ريح المسك:','7','0','0');
INSERT INTO [t10470] VALUES (4,'- قال في جـ ٤ ص ٣٤٥ قوله " خالد بن معدان عن المقدام بن معد يكرب هكذا رواه الوليد وتابعه يحيى بن حمزة عن ثور وهكذا رواه عبد الرحمن بن مهدي عن ابن المبارك عن ثور أخرجه أحمد عنه وتابعه يحيى بن سعد عن خالد بن معدان "','8','0','0');
INSERT INTO [t10470] VALUES (5,'- قال" ووقع في رواية إسماعيل بن عياش عند الطبراني ونفيه عنده وعند ابن ماجه كلاهما عن يحيى بن سعيد"','8','0','0');
INSERT INTO [t10470] VALUES (6,'- قال في الجزء الخامس ص ١٠٥ "وفيه أن الأرضين السبع متراكمة لم يفتق بعضها من بعض لأنها لو فتقت لاكتفى في حق هذا الغاصب بتطويق التي غصبها لانفصالها عما تحتها أشار إلى ذلك الداودي "','9','0','0');
INSERT INTO [t10470] VALUES (7,'- قال في الجزء الخامس ص ١٨٣ "وزعم بعضهم أن الضمير يعود على آدم أي على صفته أي خلقه موصوفا بالعلم إلخ "','10','0','0');
INSERT INTO [t10470] VALUES (8,'- قال في جـ ٥ ص ١٨٣" وقد قال المازري غلط ابن قتيبة فأجرى هذا الحديث على ظاهره وقال صورة لا كالصور انتهى "','10','0','0');
INSERT INTO [t10470] VALUES (9,'- قال في جـ ٥ ص ١٨٣ "لما ذكر حديث لا تقولن قبح الله وجهك ووجه من أشبه وجهك فإن الله خلق آدم على صورته وهو ظاهر في عود الضمير على المقول له ذلك "','11','0','0');
INSERT INTO [t10470] VALUES (10,'- قال في الجزء الخامس ص ٣٣٦ " ووقع للمهلب','11','0','0');
INSERT INTO [t10470] VALUES (11,'- قال في المجلد الخامس ص ٢٩ " على قول ابن عباس وكتابكم أي القرآن أحدث الأخبار بالله قال أي أقربها نزولا إليكم من عند الله عزوجل فالحديث بالنسبة إلى','12','0','0');
INSERT INTO [t10470] VALUES (12,'- قال في الجزء الخامس ص ٣٥١ " وفي رواية موسى بن عقبة عن الزهري فكتب رسول الله - صلى الله عليه وسلم - إلى أبي بصير فقدم كتابه وأبو بصير يموت فمات وكتاب رسول الله - صلى الله عليه وسلم - في يده فدفنه أبو جندل مكانه وجعل عند قبره مسجدا "','13','0','0');
INSERT INTO [t10470] VALUES (13,'- قال في الجزء السادس ص ٤٠ " قال الخطابي وقد تأول البخاري الضحك في موضع آخر على معنى الرحمة وهو قريب "','14','0','0');
INSERT INTO [t10470] VALUES (14,'- قال في الجزء السادس ص ١٣٦ " ولا يلزم من كون جهتي العلو والسفل محال على الله أن لا يوصف بالعلو لأن وصفه بالعلو من جهة المعنى والمستحل كون ذلك من جهة الحس ولذلك ورد في صفته العالي والعلي والمتعالي ولم يرد ضد ذلك وإن كان الله قد أحاط بكل شيء علما "','15','0','0');
INSERT INTO [t10470] VALUES (15,'- قال في الجزء السادس ص ١٤٢ "هذا كله في تعليق التمائم وغيرها ليس فيه قرآن ونحوه فأما ما فيه ذكر الله فلا نهي فيه فإنه إنما يجعل للتبرك به والتعوذ بأسمائه وذكره "','16','0','0');
INSERT INTO [t10470] VALUES (16,'- قال في الجزء الساس ص ١٤٥ " وقد تقدم توجيه العجب في حق الله في أوائل الجهاد وأن معناه الرضا ونحو ذلك "','16','0','0');
INSERT INTO [t10470] VALUES (17,'- قال في الجزء السادس ص ٢٢١ , ٢٢٢ " وروى الطحاوي والطبراني في الكبير والحاكم والبيهقي في الدرئل عن أسماء بنت عميس أنه - صلى الله عليه وسلم - دعا لما نام على ركبه علي ففاتته صلاة العصر فردت الشمس حتى صلى علي ثم غربت وهذا أبلغ في المعجزة وقد أخطأ ابن الجوزي بإيراده له في الموضوعات وكذا ابن تيمية في كتاب الرد على الروافض في زعم وضعه والله أعلم "','17','0','0');
INSERT INTO [t10470] VALUES (18,'- قال في الجزء الساس ص ٢٩١ على قوله في الحديث','17','0','0');
INSERT INTO [t10470] VALUES (19,'- قال في ص ٢٩٢ على قوله إن رحمتي غلبت غضبي " والمراد من الغضب لا زمه وهو إرادة إيصال العذاب إلى من يقع عليه الغضب "','18','0','0');
INSERT INTO [t10470] VALUES (20,'- قال في الجزء السادس ص ٣٦٦ على قوله - صلى الله عليه وسلم - خلق الله آدم على صورته وطوله ستون ذراعا " وهذه الرواية تؤيد قول من قال إن الضمير لآدم والمعنى أن الله تعالى أوجده على الهيئة التي خلقه عليها','18','0','0');
INSERT INTO [t10470] VALUES (21,'- قال في الجزء السادس ص ٥٤٠ لما ذكر حديث أبي ذر ليس من رجل ادعى إلى غير أبيه وهو يعلمه إلا كفر قال " فقوله في الإسناد عن الحسين هو ابن واقد المعلم "','19','0','0');
INSERT INTO [t10470] VALUES (22,'- قال في الجزء السادس ص ٣٨٩ على قوله تعالى {واتخذ الله إبراهيم خليلا} [النساء: ١٢٥] "والخليل بمعنى فعيل بمعنى فاعل وهو','19','0','0');
INSERT INTO [t10470] VALUES (23,'- قال في الجزء السادس ص ٤٥٤ لما تكلم على قول البخاري باب قول الله تعالى ... {وآتينا داوود زبورا} [النساء: ١٦٣, الإسراء: ٥٥] إلى أن قال " أفرغ أنزل" قال " لم أعرف المراد من هذه الكلمة هنا واستقريت قصة داود في المواضع التي ذكرت فيها فلم أجدها "','20','0','0');
INSERT INTO [t10470] VALUES (24,'- قال في الجزء السادس ص ٦٠٠ " وفيه التبرك بطعام الأولياء والصلحاء وفيه عرض الطعام الذي تظهر فيه البركة على الكبار وقبولهم ذلك "','21','0','0');
INSERT INTO [t10470] VALUES (25,'- قال في الجزء السابع ص ٢٩ لما تكلم على قول أبي بكر لا يذيقك الله الموتتين ... " تقدم شرحه في أوائل الجنائز وقد تمسك به من أنكر الحياة في القبر وأجيب عن أهل السنة المثبتين لذلك بأن المراد نفي الموت اللازم من الذي اثبته عمر بقوله وليبعثه الله في الدنيا ليقطه أيدي','21','0','0');
INSERT INTO [t10470] VALUES (26,'- قال في الجزء السابع ص ١٢٠ لما تكلم على قوله - صلى الله عليه وسلم - ضحك الله الليلة أو عجب من فعالكما " قال ونسبة الضحك والتعجب إلى الله تعالى مجازية والمراد بهما الرضا بصنيعهما "','22','0','0');
INSERT INTO [t10470] VALUES (27,'- قال في المجلد السابع ص ١٢٤ " فمعتقد سلف الأمة وعلماء السنة من الخلف أن الله منزه عن الحركة والتحول إلخ "','23','0','0');
INSERT INTO [t10470] VALUES (28,'- قال في الجزء السابع ص ١٤٥ "والمراد بغضب الله إرادة إيصال العقاب كما أن المراد بلعنة الله الإبعاد عن رحمته "','23','0','0');
INSERT INTO [t10470] VALUES (29,'- قال في الجزء السابع ص ٤١٢ , ٤١٣ " قال السهيلي قوله من فوق سبع سماوات معناه أن الحكم نزل من فوق قال ومثله قول زينب بنت جحش وزوجني الله من نبيه من فوق سبع سماوات أي نزل تزويجها من فوق قال ولا يستحيل وصفه تعالى بالفوق على المعنى الذي يليق بجلاله لا على المعنى الذي يسبق إلى الوهم من التحديد الذي يفضي إلى التشبيه "','24','0','0');
INSERT INTO [t10470] VALUES (30,'- قال في الجزء السابع ص ١٩٢ قوله " باب تقاسم المشركين على النبي - صلى الله عليه وسلم - كان ذلك أول يوم من المحرم سنة سبع من البعثة وكان النجاشي قد جهز جعفرا ومن معه فقدموا والنبي - صلى الله عليه وسلم - بخيبر وذلك في صفر منهما "','24','0','0');
INSERT INTO [t10470] VALUES (31,'- قال في الجزء الثامن ص ١٥٥ " والرحمة لغة الرقة والإنعطاف وعلى هذا فوصفه به تعالى مجاز عن إنعامه على عباده "','25','0','0');
INSERT INTO [t10470] VALUES (32,'- قال في الجزء الثامن ص ١٨٥ " جاء مفسرا في حديث أبي أيوب الذي أخرجه مسلم والنسائي وأبو داود والترمذي وابن حبان والحاكم من طريق أسلم بن عمران قال " كنا بالقسطنطينية، فخرج صف عظيم من الروم، فحمل رجل من المسلمين على صف الروم حتى دخل فيهم، ثم رجع مقبلا. فصاح الناس: سبحان الله، ألقى بيده إلى التهلكة. فقال أبو أيوب: أيها الناس، إنكم','25','0','0');
INSERT INTO [t10470] VALUES (33,'- قال في الجزء الثامن ص ٣٣٦ لما تكلم على قوله تعالى {استغفر لهم} [التوبة: ٨٠] الآية فنقل كلاما لابن بطال ثم قال "وتعقبه ابن المنير بأن الإيمان لا يتبعض قال وهو كما قال "','26','0','0');
INSERT INTO [t10470] VALUES (34,'- قال في الجزء الثامن ص ٣٤٠ لما تكلم على قوله {ولا تصل على أحد منهم} [التوبة: ٨٤] وحديث طلب الصلاة على عبد اله بن أبي بن سلول قال" وفيه جواز سؤال الموسر من المال من ترجى بركته شيئا من ماله لضرورة دينية "','27','0','0');
INSERT INTO [t10470] VALUES (35,'- قال في الجزء الثامن ص ٣٨٣ لما ذكر الكلام على لا أقسم ... " قيل إنها زائدة وتعقب بأنها لا تزاد إلا في أثناء الكلام وأجيب بأن القرآن كله كالكلام الواحد "','27','0','0');
INSERT INTO [t10470] VALUES (36,'- قال في الجزء الثامن ص ٥٥١ على حديث أن الله يجعل السماوات على إصبع "وقال ابن فورك يحتمل أن يكون المراد بالإصبع إصبع بعض المخلوقات وما ورد في بعض طرقه أصابع الرحمن يدل على القدرة والملك "','27','0','0');
INSERT INTO [t10470] VALUES (37,'- قال في الجزء الثامن ص ٥٩٦ على قوله في الحديث حتى يضع قدمه فيها "واختلف في المراد بالقدم فطريق السلف في هذا وغيره مشهورة وهو أن تمر كما جاءت ولا يتعرض لتأويله بل نعتقد استحالة ما يوهم النقص على اللع وخاض كثير من أهل العلم في تأويل ذلك إلخ"','28','0','0');
INSERT INTO [t10470] VALUES (38,'- قال في الجزء الثامن ص ٦٦٤ على قوله في الحديث يكشف ربنا عن ساقه " قال الإسماعيلي هو من رواية سعيد بن أبي هلال عن زيد بن أسلم ثم أخرجه من طريق حفص بن ميسرة عن زيد بن أسلم بلفظ يكشف عن ساق قال الإسماعيلي هذه اصح لموافقتها لفظ القرآن في الجملة لا يظن أن الله ذو أعظاء وجوارح لما في ذلك من مشابهة المخلوقين "','28','0','0');
INSERT INTO [t10470] VALUES (39,'- قال في الجزء التاسع ص ٦٩ على قوله في الحديث ما أذن الله لشيء كأذنه إلخ "وقال القرطبي: أصل الأذن بفتحتين أن المستمتع يميل بأذنه إلى جهة من يسمعه، وهذا المعنى في حق الله لا يراد به ظاهره، وإنما هو على سبيل التوسع على ما جرى به عرف المخاطب، والمراد به في حق الله تعالى إكرام القارئ وإجزال ثوابه؛ لأن ذلك ثمرة الإصغاء "','29','0','0');
INSERT INTO [t10470] VALUES (40,'- قال في الجزء التاسع ص ٢٤٧ لما ذكر إجابة الدعوة" وفي الحضور فوائد أخرى كالتبرك بالمدعو "','30','0','0');
INSERT INTO [t10470] VALUES (41,'- قال في الجزء التاسع ص ٣٢٠ لما تكلم على قوله ما أحد أغير من الله " قال عياض: ويحتمل أن تكون الغيرة في حق الله الإشارة إلى تغير حال فاعل ذلك، وقيل: الغيرة في الأصل الحمية والأنفة، وهو تفسير بلازم التغير فيرجع إلى الغضب، وقد نسب سبحانه وتعالى إلى نفسه في كتابه الغضب والرضا. وقال ابن العربي: التغير محال على الله بالدلالة القطعية، فيجب تأويله بلازمه، كالوعيد أو إيقاع العقوبة بالفاعل، ونحو ذلك "','30','0','0');
