INSERT INTO [t10490] VALUES (355,'بلال بن رباح أبو عبد الله، وقيل: أبو عمرو، وقيل: أبو عبد الكريم واسم أمه حمامة، من السابقين الأولين شهد بدرا والمشاهد كلها مع رسول الله صلى الله عليه وسلم، كان من المعذبين في الله فاشتراه الصديق فأعتقه، وكان ترب أبي بكر من مولدي السراة، كان يؤذن','1400','354','0');
INSERT INTO [t10490] VALUES (356,'بلال بن الحارث المزني وهو ابن عكيم بن سعيد بن مرة بن حلاوة بن ثعلبة بن ثور أبو عبد الرحمن، أحد من وفد على رسول الله صلى الله عليه وسلم في وفد مزينة في رجب من سنة خمس، فنزل الأشعر وراء المدينة، توفي آخر أيام معاوية سنة ستين، وهو ابن ثمانين سنة. روى','1414','354','0');
INSERT INTO [t10490] VALUES (357,'بلال بن يحيى ذكره الحسن بن سفيان في الوحدان، وأراه عندي العبسي الكوفي، وهو صاحب حذيفة، لا صحبة له','1420','354','0');
INSERT INTO [t10490] VALUES (358,'من اسمه البراء','1422','353','0');
INSERT INTO [t10490] VALUES (359,'البراء بن مالك بن النضر بن ضمضم أخو أنس بن مالك النجاري كان خادم النبي صلى الله عليه وسلم، ويرتجز بين يديه في أسفاره ومغازيه، لحسن صوته، كان شجاعا مقداما، قتل مائة من المشركين مبارزة، سوى من شارك فيه. بارز مرزبان الزأرة يوم تستر فقتله، وأخذ سلبه.','1423','358','0');
INSERT INTO [t10490] VALUES (360,'البراء بن معرور الأنصاري ثم السلمي وهو ابن صخر بن خنساء بن سنان بن عبيد بن عدي بن غنم بن كعب بن سلمة، أحد النقباء، وأول من بايع ليلة العقبة، وأول من استقبل القبلة، وأوصى بثلث ماله، توفي أول الإسلام على عهد النبي صلى الله عليه وسلم، فلما قدم النبي','1432','358','0');
INSERT INTO [t10490] VALUES (361,'البراء بن عازب الأنصاري ثم الحارثي يكنى أبا عمارة، رده رسول الله صلى الله عليه وسلم عن بدر، وأحد لصغر سنه، وأول مشاهده الخندق، وقيل أحد، وغزا مع رسول الله صلى الله عليه وسلم أربع عشرة غزوة، بنى دارا بالكوفة أيام مصعب، فنزلها، ثم رجع إلى المدينة.','1436','358','0');
INSERT INTO [t10490] VALUES (362,'البراء بن أوس بن خالد شهد مع النبي صلى الله عليه وسلم أحد غزواته','1447','358','0');
INSERT INTO [t10490] VALUES (363,'باب بشر','1449','353','0');
