INSERT INTO [t10490] VALUES (384,'بشير ابن الخصاصية. والخصاصية أمه، ونسب إلى أمه وهو بشير بن معبد بن شراحيل بن سبع بن ضباب بن سدوس السدوسي، وقيل: بشير بن يزيد بن معبد بن ضباب بن سبيع، كان اسمه في الجاهلية زحم بن معبد، فسماه النبي صلى الله عليه وسلم بشيرا. عداده في البصريين، روى','1498','380','0');
