INSERT INTO [t10490] VALUES (399,'وبشير بن جابر بن غراب بن عوف بن دؤالة العبسي وفد على النبي صلى الله عليه وسلم، وشهد فتح مصر، ذكره أبو سعيد بن عبد الأعلى','1535','380','0');
