INSERT INTO [t10490] VALUES (422,'بجير بن أبي بجير شهد مع النبي صلى الله عليه وسلم بدرا، ولا يعرف له رواية','1585','407','0');
INSERT INTO [t10490] VALUES (423,'بريدة بن الحصيب بن عبد الله ابن الحارث بن الأعرج بن سعد بن رزاح بن عدي بن سهم بن مازن بن الحارث بن سلامان بن أسلم بن أفصى، يكنى أبا عبد الله، وقيل أبو سهل، وقيل أبو ساسان، ويقال كان اسمه عامر قبل أن يسلم، فمر به النبي صلى الله عليه وسلم بالغميم','1587','407','0');
INSERT INTO [t10490] VALUES (424,'ومن مسانيده','1590','407','0');
INSERT INTO [t10490] VALUES (425,'برير بن عبد الله، ويقال بر بن عبد الله بن رزين ابن عميت بن ربيعة بن ذراع بن عدي بن الدار أبو هند الداري أخو تميم والطيب، سكن فلسطين كور بيت المقدس، روى عنه مكحول. وقال علي بن عبد الله المديني أبو هند: سماه النبي صلى الله عليه وسلم عبد الله، وهو أخو','1604','407','0');
INSERT INTO [t10490] VALUES (426,'برير أبو هريرة سماه مروان بن محمد، عن سعيد بن عبد العزيز قال: اسم أبي هريرة برير. ولم يتابع عليه. إنما هو وهم، أراد أن يقول: اسم أبي هند برير، واختلف في اسم أبي هريرة','1606','407','0');
INSERT INTO [t10490] VALUES (427,'بيرح بن أسد الطاحي هاجر إلى النبي عليه السلام، فأدرك وفاته، ولم يره، روى عنه لمازة بن زبار أبو لبيد','1607','407','0');
INSERT INTO [t10490] VALUES (428,'بسيس الأنصاري الجهني وقيل: بسبسة، ويقال: بسيسة بن عمرو، شهد بدرا، وبعثه النبي صلى الله عليه وسلم عينا إلى عير أبي سفيان','1609','407','0');
INSERT INTO [t10490] VALUES (429,'بدر بن عبد الله المزني روى عنه بكر المزني','1612','407','0');
INSERT INTO [t10490] VALUES (430,'بدر أبو عبد الله الخطمي','1614','407','0');
INSERT INTO [t10490] VALUES (431,'بكر بن عبد الله بن ربيع الأنصاري','1617','407','0');
INSERT INTO [t10490] VALUES (432,'بكر بن حبيب الحنفي له ذكر في حديث بكر بن حارثة الجهني، سماه رسول الله صلى الله عليه وسلم بريرا','1619','407','0');
INSERT INTO [t10490] VALUES (433,'بهز غير منسوب','1620','407','0');
INSERT INTO [t10490] VALUES (434,'بيحرة بن عامر، ويقال بحرة عداده في أعراب البصرة','1622','407','0');
INSERT INTO [t10490] VALUES (435,'بحر بن ضبع بن أته الرعيني وفد على النبي صلى الله عليه وسلم، وشهد فتح مصر. ذكره بعض الرواة عن أبي سعيد يونس بن عبد الأعلى المصري، وزعم أن له ذكرا في كتبهم','1624','407','0');
INSERT INTO [t10490] VALUES (436,'بلز، وقيل برز، وقيل رزن، وقيل مالك بن قحطم أبو أبي العشراء الدارمي، نأتي على حديثه في الكنى','1625','407','0');
INSERT INTO [t10490] VALUES (437,'بردع بن زيد الجذامي أخو رفاعة، وسويد، وبعجة. يكنى أبا زيد، وفد هو وإخوته على النبي صلى الله عليه وسلم، وهم ممن نزلوا بيت جبرين من فلسطين','1626','407','0');
INSERT INTO [t10490] VALUES (438,'وبعجة بن زيد الجذامي أخو بردع','1628','407','0');
INSERT INTO [t10490] VALUES (439,'بريح بن عرفجة، أو عرفجة بن بريح هكذا قاله البخاري، وهو وهم، وإنما هو عرفجة بن ضريح. وقيل ضريح بن عرفجة','1630','407','0');
INSERT INTO [t10490] VALUES (440,'بنة الجهني','1631','407','0');
INSERT INTO [t10490] VALUES (441,'بهير بن الهيثم الأنصاري عقبي','1634','407','0');
INSERT INTO [t10490] VALUES (442,'بحير بن أبي ربيعة المخزومي سماه النبي صلى الله عليه وسلم عبد الله، روى قتيبة، عن مفضل، عن ابن جريج بهذا','1636','407','0');
INSERT INTO [t10490] VALUES (443,'بحيرا الراهب رأى النبي صلى الله عليه وسلم قبل مبعثه، وآمن به','1637','407','0');
INSERT INTO [t10490] VALUES (444,'بريل الشهالي ذكره بعض الناس في الصحابة، وهو وهم. وكذلك','1639','407','0');
INSERT INTO [t10490] VALUES (445,'بذيمة أبو علي بن بذيمة ذكره بعض الناس فيهم، وهو وهم','1640','407','0');
INSERT INTO [t10490] VALUES (446,'وبرح بن عسكر شهد فتح مصر، وفد على النبي صلى الله عليه وسلم، ذكره أبو سعيد بن عبد الأعلى','1641','407','0');
INSERT INTO [t10490] VALUES (447,'وباقوم الرومي صانع منبر رسول الله صلى الله عليه وسلم، روى عنه صالح مولى التوأمة، وقال: باقوم مولى سعيد بن العاص','1642','407','0');
INSERT INTO [t10490] VALUES (448,'باب التاء','1644','18','0');
INSERT INTO [t10490] VALUES (449,'تميم بن أوس الداري ويقال ابن قيس، وهو تميم بن أوس بن خارجة بن سويد بن جذيمة، وقيل ابن سواد بن جذيمة بن ذراع بن عدي بن الدار من لخم، وقيل هانئ بن حبيب بن أنمار بن عدي بن عمرو بن سبأ أبو رقية، حدث عنه سيد الأولين والآخرين محمد رسول الله صلى الله عليه','1645','448','0');
INSERT INTO [t10490] VALUES (450,'تميم بن أسيد ويقال ابن أسد الخزاعي، ولاه النبي صلى الله عليه وسلم تجديد أنصاب الحرم وإعادتها، سكن مكة. قاله الواقدي روى عنه ابن عباس','1656','448','0');
INSERT INTO [t10490] VALUES (451,'تميم بن زيد أبو عباد أخو عبد الله بن زيد الأنصاري المازني، روى عنه ابنه عباد، عداده في المدنيين','1658','448','0');
INSERT INTO [t10490] VALUES (452,'تميم مولى بني غنم ابن السلم بن مالك بن الأوس بن حارثة، بدري','1660','448','0');
INSERT INTO [t10490] VALUES (453,'تميم مولى خراش بن الصمة الأنصاري، شهد بدرا','1663','448','0');
INSERT INTO [t10490] VALUES (454,'تميم بن يعار الأنصاري ثم الخدري، شهد بدرا','1666','448','0');
INSERT INTO [t10490] VALUES (455,'تميم بن الحارث بن قيس السهمي القرشي قتل يوم أجنادين','1669','448','0');
INSERT INTO [t10490] VALUES (456,'تميم بن أسيد وقيل تميم بن إياس أبو رفاعة العدوي، يعد في البصريين، وتوفي بسجستان مع عبد الرحمن بن سمرة، روى عنه حميد بن عبد الرحمن، وصلة بن أشيم','1672','448','0');
INSERT INTO [t10490] VALUES (457,'تميم بن حجر أبو أوس الأسلمي جد بريدة بن سفيان، كان ينزل بلاد أسلم من ناحية العرج. قاله محمد بن سعد الواقدي، ووهم فيه، وصحيحه إياس بن مالك بن أوس بن عبد الله بن حجر، عن أبيه، عن جده أوس قصة اجتياز النبي صلى الله عليه وسلم به مهاجرا، وبعث معه مولاه','1674','448','0');
INSERT INTO [t10490] VALUES (458,'تميم بن الحمام الأنصاري قتل ببدر. ذكره بعض الواهمين، وصحف فيه، وإنما هو عمير بن الحمام، واتفقت الروايات عن الرواة وأصحاب المغازي والسير أنه عمير بن الحمام الأنصاري، من بني حرام بن كعب بن غنم بن كعب بن سلمة','1675','448','0');
INSERT INTO [t10490] VALUES (459,'تميم بن عبد عمرو المازني أبو الحسن، كان عاملا لعلي بن أبي طالب على المدينة حين خرج إليه سهل بن حنيف','1680','448','0');
INSERT INTO [t10490] VALUES (460,'تميم بن يزيد، وقيل ابن زيد، مجهول','1682','448','0');
INSERT INTO [t10490] VALUES (461,'تميم بن غيلان بن سلمة الثقفي يقال إنه ولد على عهد رسول الله صلى الله عليه وسلم. ذكره المنيعي إن صح','1684','448','0');
INSERT INTO [t10490] VALUES (462,'تميم غير منسوب روى عنه، يزيد بن حصين في قصة سبأ، وقيل إنه تميم الداري، ولا يصح حديثه. عند عبد الوهاب الحوطي، عن أبي عمرو، عن الليث بن سعد، عن موسى بن علي، عن يزيد بن حصين، عن تميم قال: سئل النبي صلى الله عليه وسلم عن سبأ، أرجل كان أو امرأة؟ .','1686','448','0');
INSERT INTO [t10490] VALUES (463,'تمام بن العباس بن عبد المطلب وقيل تمام بن قثم. تفرد بالرواية عنه ابنه جعفر، مختلف في صحبته','1687','448','0');
INSERT INTO [t10490] VALUES (464,'تمام بن عبيدة أخو الزبير بن عبيدة من بني غنم بن دودان، ممن هاجر إلى المدينة مع النبي صلى الله عليه وسلم ذكره العطاردي، عن يونس بن بكير، عن محمد بن إسحاق، قال: ثم قدم المهاجرون أرسالا، وكانت بنو غنم بن ذودان أهل الإسلام، قد أوعبوا إلى المدينة مع','1690','448','0');
INSERT INTO [t10490] VALUES (465,'التلب بن ثعلبة بن ربيعة بن عطية بن الأحنف بن مجفر بن كعب بن العنبر بن عمرو بن تميم أبو هلقام، وقيل أبو ملقام، سكن البصرة، وكان شعبة يقول: ثلب، بالثاء , مشدد. والصواب بالتاء','1691','448','0');
INSERT INTO [t10490] VALUES (466,'التيهان أبو أبي الهيثم ابن التيهان الأنصاري','1695','448','0');
INSERT INTO [t10490] VALUES (467,'التوأم أبو دخان','1697','448','0');
INSERT INTO [t10490] VALUES (468,'باب الثاء','1699','18','0');
INSERT INTO [t10490] VALUES (469,'ثابت بن قيس بن الشماس بن ثعلبة بن زهير بن امرئ القيس بن مالك بن الحارث بن الخزرج، يكنى أبا محمد، كان خطيب الأنصار، جهير الصوت، شهد له النبي صلى الله عليه وسلم بالجنة، استشهد باليمامة سنة اثنتي عشرة، روى عنه أنس بن مالك، وبنوه محمد، وإسماعيل،','1700','468','0');
INSERT INTO [t10490] VALUES (470,'ثابت بن وقش بن زعوراء الأنصاري استشهد بأحد','1706','468','0');
INSERT INTO [t10490] VALUES (471,'ثابت بن الضحاك بن خليفة الأنصاري ابن ثعلبة بن عدي بن كعب بن عبد الأشهل، يكنى أبا زيد، يقال إنه أخو أبي جبيرة بن الضحاك، ودليل النبي صلى الله عليه وسلم إلى حمراء الأسد، شهد الشجرة بالحديبية، كان رديف النبي صلى الله عليه وسلم يوم الخندق، ودليله إلى','1708','468','0');
INSERT INTO [t10490] VALUES (472,'ثابت بن الصامت الأنصاري يقال إنه أخو عبادة بن الصامت، روى عنه ابنه عبد الرحمن، سكن المدينة','1713','468','0');
INSERT INTO [t10490] VALUES (473,'ثابت بن خالد بن النعمان ابن خنساء بن عسيرة من بني تيم الله، شهد بدرا، واستشهد يوم اليمامة','1715','468','0');
INSERT INTO [t10490] VALUES (474,'ثابت بن يزيد بن وديعة الأنصاري وقيل ثابت بن وديعة بن خذام بن خالد بن ثعلبة بن زيد بن عبيد بن أمية بن زيد، وقيل ثابت بن زيد بن وديعة، يكنى أبا سعد، نزل الكوفة، روى عنه البراء بن عازب، وزيد بن وهب، وعامر بن سعد البجلي','1721','468','0');
INSERT INTO [t10490] VALUES (475,'ثابت بن الدحداح وقيل ابن الدحداحة الأنصاري سماه ابن عباس أنه سأل النبي صلى الله عليه وسلم عن المحيض، فأنزل الله عز وجل: ويسألونك عن المحيض، ذكر ذلك ابن إسحاق، عن محمد بن أبي محمد، عن عكرمة، أو سعيد بن جبير، عن ابن عباس أن ثابت بن دحداحة سأل النبي','1725','468','0');
INSERT INTO [t10490] VALUES (476,'ثابت بن هزال بن عمرو الأنصاري من بني عوف بن الخزرج، شهد بدرا، واستشهد باليمامة','1727','468','0');
INSERT INTO [t10490] VALUES (477,'ثابت بن زيد الأنصاري أحد بني الحارث بن الخزرج، يكنى أبا زيد، أحد الذين جمعوا القرآن على عهد النبي صلى الله عليه وسلم، وشهد أحدا، ذكره أنس بن مالك، واختلف في اسمه، فقيل قيس بن زعوراء، وقيل قيس بن السكن من بني عدي بن النجار، وهو الصحيح. روى عنه','1730','468','0');
INSERT INTO [t10490] VALUES (478,'ثابت بن أقرم ابن ثعلبة بن عدي بن العجلان الأنصاري، شهد بدرا، وهو حامل الراية يوم مؤتة بعد ابن رواحة','1733','468','0');
INSERT INTO [t10490] VALUES (479,'ثابت بن يزيد روى عنه عبد الرحمن بن عائذ الأزدي، وأراه من الأنصار','1739','468','0');
INSERT INTO [t10490] VALUES (480,'ثابت بن رفيع الأنصاري يعد في المصريين، تفرد بالرواية عنه الحسن','1742','468','0');
INSERT INTO [t10490] VALUES (481,'ثابت بن رفاعة الأنصاري له ذكر في حديث أرسله عن قتادة','1745','468','0');
INSERT INTO [t10490] VALUES (482,'ثابت بن الحارث الأنصاري شهد بدرا، روى عنه الحارث بن يزيد، عداده في المصريين','1747','468','0');
INSERT INTO [t10490] VALUES (483,'ثابت بن يزيد الأنصاري وأراه الأول الذي تقدم. روى عنه الشعبي، وعامر بن سعد، حديثه عند الكوفيين','1750','468','0');
INSERT INTO [t10490] VALUES (484,'ثابت بن ثعلبة الأنصاري بدري، استشهد بالطائف ويقال له ثابت بن الجدع شهد العقبة، واسم الجدع ثعلبة بن زيد بن الحارث بن حرام','1752','468','0');
INSERT INTO [t10490] VALUES (485,'ثابت بن ربيعة الأنصاري من بني عوف بن الخزرج، ثم من بني الحبلى، شهد بدرا','1756','468','0');
INSERT INTO [t10490] VALUES (486,'ثابت بن حسان بن عمرو الأنصاري من بني عدي بن النجار لا عقب له، شهد بدرا','1758','468','0');
INSERT INTO [t10490] VALUES (487,'ثابت بن عمرو الأنصاري شهد بدرا','1760','468','0');
INSERT INTO [t10490] VALUES (488,'ثابت بن عتيك الأنصاري من بني عمرو بن مبذول، قتل يوم الجسر مع أبي عبد الله الثقفي سنة خمس عشرة','1762','468','0');
INSERT INTO [t10490] VALUES (489,'ثابت بن المنذر بن حرام بن عمرو بن زيد مناة بن عدي بن عمرو من بني مالك بن النجار','1766','468','0');
INSERT INTO [t10490] VALUES (490,'ثابت بن مخلد ابن زيد بن مخلد بن حارثة بن عمرو، وهو آخر ولد عامر بن لوذان بن خطمة، قتل يوم الحرة، لا عقب له. قاله بعض الرواة عن ابن أبي داود، وذكر حديث محمد بن بكر، عن ابن جريج، عن ابن المنكدر، عن أبي أيوب، فقال: عن ثابت بن مخلد أن رسول الله صلى','1769','468','0');
INSERT INTO [t10490] VALUES (491,'ثابت بن النعمان ابن أمية بن امرئ القيس، ذكره بعض الرواة أنه المكنى بأبي حبة البدري، وحكى عن أبي سعيد يونس بن عبد الأعلى أنه شهد فتح مصر','1771','468','0');
INSERT INTO [t10490] VALUES (492,'ثابت بن معبد روي أن رجلا سأل النبي صلى الله عليه وسلم عن امرأة من قومه أعجبه حسنها. من حديث عبيد الله بن عمرو، عن عبد الملك بن عمير، عن ثابت بن معبد، عن رجل من كلب، وثابت هذا من تابعي أهل الكوفة، ليست له صحبة، وذكره في جملة الصحابة وهم','1773','468','0');
INSERT INTO [t10490] VALUES (493,'وثابت بن طريف المرادي ذكره أيضا، عن أبي سعيد بن عبد الأعلى أنه صحابي، وأنه أردك الجاهلية','1774','468','0');
INSERT INTO [t10490] VALUES (494,'ثابت بن عمرو الأشجعي حليف الأنصار، شهد بدرا','1775','468','0');
INSERT INTO [t10490] VALUES (495,'ثابت بن أبي عاصم ذكره ابن أبي عاصم في الصحابة، وأراه تابعيا','1777','468','0');
INSERT INTO [t10490] VALUES (496,'ثعلبة بن الحكم الليثي عداده في الكوفيين، شهد خيبر مع النبي صلى الله عليه وسلم. روى عنه سماك، ويزيد بن أبي زياد','1779','468','0');
INSERT INTO [t10490] VALUES (497,'ثعلبة أبو عبد الله الأنصاري','1785','468','0');
INSERT INTO [t10490] VALUES (498,'ثعلبة بن زهدم الحنظلي يعد في الكوفيين، روى عنه الأسود بن هلال','1788','468','0');
INSERT INTO [t10490] VALUES (499,'ثعلبة أبو عبد الرحمن الأنصاري روى عنه ابنه عبد الرحمن، عداده في المصريين','1790','468','0');
INSERT INTO [t10490] VALUES (500,'ثعلبة بن أبي مالك القرظي إمام بني قريظة، يكنى أبا يحيى، أدرك النبي صلى الله عليه وسلم، كان هو وعطية القرظي في سن واحد يوم بني قريظة، فتركا جميعا في الذرية، ولم يقتلا. قاله مصعب الزبيري. وقال محمد بن سعد الواقدي: قدم أبو مالك من اليمن وهو على دين','1792','468','0');
INSERT INTO [t10490] VALUES (501,'ثعلبة بن سعد الساعدي أخو سهل بن سعد، شهد بدرا، وقتل يوم أحد، لم يعقب','1795','468','0');
INSERT INTO [t10490] VALUES (502,'ثعلبة بن صعير العذري مختلف فيه، فقيل ابن أبي صعير، وقيل ثعلبة بن عبد الله، وقيل عبد الله بن ثعلبة','1797','468','0');
INSERT INTO [t10490] VALUES (503,'ثعلبة بن وديعة الأنصاري أحد من تخلف عن تبوك، فربط نفسه على السارية حتى تاب الله عز وجل عليه','1799','468','0');
INSERT INTO [t10490] VALUES (504,'ثعلبة بن سعية وقيل ابن يامين','1801','468','0');
INSERT INTO [t10490] VALUES (505,'ثعلبة بن عنمة ابن عدي بن نابي، شهد بدرا، واستشهد يوم الخندق','1803','468','0');
INSERT INTO [t10490] VALUES (506,'ثعلبة بن قيظي الأنصاري','1806','468','0');
INSERT INTO [t10490] VALUES (507,'ثعلبة بن زيد الأنصاري له ذكر في المغازي، ولا يعرف له حديث، ذكره بعض المتأخرين، ولم يخرج له شيئا، ولا نسبه إلى غيره من المتقدمين','1808','468','0');
INSERT INTO [t10490] VALUES (508,'ثعلبة بن حاطب الأنصاري شهد بدرا، وتوفي في خلافة عثمان رضي الله عنه','1809','468','0');
INSERT INTO [t10490] VALUES (509,'ثعلبة بن عمرو بن محصن الأنصاري شهد بدرا، وقتل يوم الجسر بالمدائن سنة خمس عشرة','1813','468','0');
INSERT INTO [t10490] VALUES (510,'ثعلبة بن الجذع الأنصاري بدري، وقتل بالطائف','1815','468','0');
INSERT INTO [t10490] VALUES (511,'ثعلبة بن ساعدة بن مالك بن خالد بن ثعلبة بن حارثة بن عمرو بن الخزرج، استشهد بأحد، وأراه أخا سهل بن سعد الذي تقدم ذكره','1818','468','0');
INSERT INTO [t10490] VALUES (512,'ثعلبة بن عبد الرحمن الأنصاري أحد خدم رسول الله صلى الله عليه وسلم والقائمين بحوائجه، روى عنه جابر بن عبد الله','1821','468','0');
INSERT INTO [t10490] VALUES (513,'ثعلبة بن زبيب قال: كان علي رقبة من ولد إسماعيل، في إسناد حديثه إرسال وضعف، ولم يخرج إسناد حديثه','1823','468','0');
INSERT INTO [t10490] VALUES (514,'وثعلبة بن أبي رقية اللخمي شهد فتح مصر، له ذكر في كتبهم , ذكره عن أبي سعيد بن عبد الأعلى','1824','468','0');
INSERT INTO [t10490] VALUES (515,'ثوبان بن بجدد أبو عبد الله مولى رسول الله صلى الله عليه وسلم، وقيل ابن جحدر، من أهل اليمن من حمير، أصابه سباء، فاشتراه رسول الله صلى الله عليه وسلم، فأعتقه، سكن حمص، وله بها دار الضيافة، توفي سنة أربع وخمسين، وله أيضا دار بالرملة وبمصر أخرى،','1825','468','0');
INSERT INTO [t10490] VALUES (516,'ثوبان أبو عبد الرحمن الأنصاري روى حديثه محمد بن عبد الرحمن بن ثوبان، عن أبيه، عن جده','1832','468','0');
INSERT INTO [t10490] VALUES (517,'ثوبان بن سعد أبو الحكم يذكر في التابعين وأخرجه أبو بكر بن أبي عاصم في الصحابة','1834','468','0');
INSERT INTO [t10490] VALUES (518,'ثمامة بن أثال الحنفي أتي به النبي صلى الله عليه وسلم أسيرا، فحبسه، فأسلم. روى قصته أبو هريرة، وابن عباس رضي الله عنهما','1836','468','0');
INSERT INTO [t10490] VALUES (519,'ثمامة بن عدي القرشي له صحبة، كان على صنعاء الشام واليا حين قتل عثمان بن عفان. روى عنه أبو الأشعث الصنعاني','1840','468','0');
INSERT INTO [t10490] VALUES (520,'ثمامة بن بجاد العبدي يذكر أن له صحبة، يعد في الكوفيين','1843','468','0');
INSERT INTO [t10490] VALUES (521,'ثمامة بن أبي ثمامة أبو سوادة الجذامي، من الصحابة','1846','468','0');
INSERT INTO [t10490] VALUES (522,'ثمامة بن حزن القشيري أدرك النبي صلى الله عليه وسلم، ولم يره، رأى عمر بن الخطاب، وعثمان، وعائشة رضي الله عنهم، قدم على عمر في خلافته وهو ابن خمس وثلاثين سنة. كذا حدثه','1848','468','0');
INSERT INTO [t10490] VALUES (523,'ثقف بن عمرو بن شميط من بني غنم بن دودان بن أسد، حليف الأنصار، استشهد بخيبر مع رسول الله صلى الله عليه وسلم','1849','468','0');
