INSERT INTO [t10490] VALUES (527,'جعفر بن أبي طالب أبو عبد الله الهاشمي، الطيار، في الجنة، ذو الجناحين، صاحب الهجرتين، استشهد بمؤتة في حياة رسول الله صلى الله عليه وسلم أميرا، سنة ثمان في جمادى الأولى، شبيه رسول الله صلى الله عليه وسلم، خلقا وخلقا، وكان النبي صلى الله عليه وسلم','1857','526','0');
INSERT INTO [t10490] VALUES (528,'جعفر بن الزبير بن العوام أخو عبد الله','1873','526','0');
INSERT INTO [t10490] VALUES (529,'جعفر بن أبي سفيان بن الحارث بن عبد المطلب واسم أبي سفيان، المغيرة، ذكر محمد بن سعد الواقدي أنه أدرك النبي صلى الله عليه وسلم وشهد معه حنينا، وأدرك زمن معاوية رضي الله عنه وتوفي في وسط أيامه، وهو وهم؛ لأن الذي شهد حنينا أبو سفيان، وجعفر لم يشهد حنينا','1875','526','0');
INSERT INTO [t10490] VALUES (530,'جعفر بن أبي الحكم ذكره الحماني، ومحمد بن عثمان بن أبي شيبة في الوحدان','1876','526','0');
INSERT INTO [t10490] VALUES (531,'جبير بن مطعم بن عدي بن نوفل بن عبد مناف بن قصي أسلم ما بين الحديبية والفتح، أمه أم جميل. وقيل: أم حبيب بنت شعبة بن عبد الله بن أبي قيس بن عبد ود بن نصر بن مالك بن حسل، وأمها أم حبيبة بنت العاص بن أمية بن عبد شمس، يكنى أبا محمد، وقيل: أبو عدي،','1878','526','0');
INSERT INTO [t10490] VALUES (532,'جبير بن إياس بن خالد بن مخلد بن زريق الأنصاري شهد بدرا مع رسول الله صلى الله عليه وسلم، لم يسند عنه شيئا','1891','526','0');
INSERT INTO [t10490] VALUES (533,'جبير مولى كبيرة بنت سفيان له ذكر فيمن أدرك النبي صلى الله عليه وسلم.','1895','526','0');
INSERT INTO [t10490] VALUES (534,'جبير بن نوفل غير منسوب، ذكره الحضرمي في الصحابة','1897','526','0');
INSERT INTO [t10490] VALUES (535,'جبير بن نفير أبو عبد الرحمن الحضرمي أسلم في حياة النبي صلى الله عليه وسلم وهو باليمن ولم يره، ثم قدم بالمدينة فأدرك أبا بكر، وعمر رضي الله عنهما، ثم انتقل إلى الشام فسكن حمصا،','1899','526','0');
