INSERT INTO [t10490] VALUES (548,'جابر بن عمير الأنصاري، يعد في المدنيين له صحبة','1964','541','0');
INSERT INTO [t10490] VALUES (549,'جابر بن أسامة الجهني يعد في الحجازيين وقيل: إنه قدم مصر، وتوفي بها، ويكنى: أبا سعاد، قاله أبو سعيد بن يونس بن عبد الأعلى','1966','541','0');
INSERT INTO [t10490] VALUES (550,'جابر بن طارق وقيل: ابن أبي طارق. وقيل: ابن عوف، أبو حكيم الأحمسي، سكن الكوفة، وقال ابن نمير، جابر بن أبي طارق','1968','541','0');
INSERT INTO [t10490] VALUES (551,'جابر بن سمرة السوائي سكن الكوفة، مختلف في كنيته، قيل: أبو خالد. وقيل: أبو عبد الله، هو جابر بن سمرة بن جنادة بن جندب بن حجير بن رياب بن حبيب بن سواءة بن عامر، وأمه: خالدة بنت أبي وقاص، أخت سعد بن أبي وقاص، توفي بالكوفة في ولاية بشر بن مروان','1971','541','0');
INSERT INTO [t10490] VALUES (552,'جابر بن سليم أبو جري الهجيمي وقيل: سليم بن جابر، تميمي، نزل البصرة في بني نمير، في حديثه اختلاف','1980','541','0');
