INSERT INTO [t10490] VALUES (556,'جابر بن الأزرق الغاضري عداده في أهل حمص حديثه عند نصر بن خزيمة','1988','541','0');
INSERT INTO [t10490] VALUES (557,'وجابر بن عبد الله ذكر صالح بن محمد البغدادي جزرة أنه الراسبي ونزل البصرة، قال الشيخ: ولا أراه إلا جابر بن عبد الله الأنصاري السلمي','1990','541','0');
