INSERT INTO [t10490] VALUES (559,'وجابر بن ماجد الصدفي وفد على النبي صلى الله عليه وسلم، وشهد فتح مصر، قاله أبو سعيد بن عبد الأعلى في حديثه اضطراب','1994','541','0');
INSERT INTO [t10490] VALUES (560,'وجابر بن حابس العبدي','1997','541','0');
INSERT INTO [t10490] VALUES (561,'وجابر بن ياسر بن عويص القتباني شهد فتح مصر، له ذكر في الصحابة، جد عياش، لا يعرف له ذكر ولا رواية. ذكر المحيل على أبي سعيد بن عبد الأعلى','1999','541','0');
INSERT INTO [t10490] VALUES (562,'وجابر بن عياش بن جابر لا يعرف له حديث','2000','541','0');
