INSERT INTO [t10490] VALUES (576,'جندب بن عمرو بن حممة الدوسي استشهد بأجنادين','2069','566','0');
INSERT INTO [t10490] VALUES (577,'جندب بن ضمرة الليثي نزلت فيه: ومن يخرج من بيته مهاجرا إلى الله ورسوله فيه اختلاف، فقيل: جندب بن ضمرة. وقيل: جندع بن ضمرة وهو المشهور، وقيل: ضمرة بن جندب، وقيل: ضمرة بن أبي العيص. وقيل: ضمرة بن العمص، وقيل: ضمضم بن عمرو الخزاعي','2072','566','0');
INSERT INTO [t10490] VALUES (578,'*','2081','525','0');
INSERT INTO [t10490] VALUES (579,'جبلة بن حارثة الكلبي أخو زيد بن حارثة، قدم على النبي صلى الله عليه وسلم مع أبيه حارثة، فقام حارثة بمكة مع ابنه زيد مؤانسا له مسلما، وخرج جبلة ثم رجع جبلة فأسلم وآمن وكان أكبر سنا من زيد، حديثه عن أبي عمرو الشيباني، وأبي إسحاق السبيعي','2081','578','0');
INSERT INTO [t10490] VALUES (580,'جبلة بن الأزرق له صحبة عداده في الشاميين، روى عنه راشد بن سعد المقرائي','2084','578','0');
INSERT INTO [t10490] VALUES (581,'جبلة بن عمرو الأنصاري أخو أبي مسعود الأنصاري روى عنه، ثابت بن عبيد، وسليمان بن يسار، شهد فتح إفريقية مع معاوية بن خديج سنة خمسين','2086','578','0');
