INSERT INTO [t10490] VALUES (583,'جبلة بن ثعلبة الأنصاري بدري، من بني بياضة حدثنا سليمان بن أحمد، ثنا محمد بن عبد الله الحضرمي، قال: وفي حديث عبيد الله بن أبي رافع في تسمية من شهد مع علي بن أبي طالب رضي الله عنه: جبلة من بني بياضة، بدري "','2091','578','0');
INSERT INTO [t10490] VALUES (584,'جرير بن عبد الله بن الشليل البجلي أبو عمرو وقيل: أبو عبد الله، من خير ذي يمن، فاق الناس في الجمال والقامة، طوله ستة أذرع وطول نعله ذراع، وكان عمر بن الخطاب رضي الله عنه يسميه يوسف هذه الأمة؛ لجماله، بارز مهران يوم القادسية فقتله، كان يخضب بالصفرة','2092','578','0');
INSERT INTO [t10490] VALUES (585,'جرير بن الأرقط حديثه عند يعلى بن الأشدق','2113','578','0');
INSERT INTO [t10490] VALUES (586,'جرير أو أبو جرير، وقيل: حريز','2115','578','0');
