INSERT INTO [t10490] VALUES (588,'الجارود بن عمرو بن المعلى العبدي يكنى: أبا المنذر وقيل: الجارود بن العلاء وقيل: الجارود لقب، واسمه بشر بن عمرو بن حنش بن معلى، قتل بأرض فارس في خلافة عمر، كان وفد على النبي صلى الله عليه وسلم مع وفد عبد القيس، وكان نصرانيا، فأسلم، ففرح رسول الله','2119','578','0');
INSERT INTO [t10490] VALUES (589,'جارية بن ظفر الحنفي أبو نمران يعد في الكوفيين، حديثه عند ابنه نمران، ومولاه عقيل بن دينار','2131','578','0');
INSERT INTO [t10490] VALUES (590,'جارية بن قدامة السعدي التميمي عم الأحنف بن قيس، وقيل: ابن عم الأحنف، وقيل: ليس بعمه أخي أبيه. بل سماه عمه توقيرا له، سكن البصرة','2135','578','0');
INSERT INTO [t10490] VALUES (591,'جارية بن عبد المنذر وهو وهم، وصوابه رفاعة بن عبد المنذر، وحكى بعض الرواة عن ابن أبي داود أنه قال: خارجة بن عبد المنذر','2138','578','0');
INSERT INTO [t10490] VALUES (592,'جارية بن أصرم الأجداري حي من كلب يعد في أعراب البصرة، لا يعرف له صحبة ولا رؤية، ذكره بعض الرواة في جملة الصحابة','2140','578','0');
INSERT INTO [t10490] VALUES (593,'جارية بن حميل بن نشبة بن قرط صحب النبي صلى الله عليه وسلم فيما حكاه الدارقطني، عن ابن جرير الطبري وقال بعضهم: هو من أهل الصفة','2142','578','0');
INSERT INTO [t10490] VALUES (594,'جويرية العصري أتى النبي صلى الله عليه وسلم في وفد عبد القيس','2143','578','0');
INSERT INTO [t10490] VALUES (595,'جنيد بن سبع الجهني يعد في الشاميين، روى عنه عبد الله بن عوف الكناني. وقيل: هو أبو جمعة، واسمه حبيب بن سباع','2145','578','0');
INSERT INTO [t10490] VALUES (596,'جنادح بن ميمون يعد في الصحابة شهد فتح مصر. لا يعرف له حديث، ذكره بعض المتأخرين وأحاله بذكره على أبي سعيد بن عبد الأعلى','2147','578','0');
INSERT INTO [t10490] VALUES (597,'جنادة بن أبي أمية الأزدي أبو عبيد الله له صحبة نزل مصر، واسم أبي أمية كبير قاله البخاري، وتوفي سنة سبع وستين ومن عقبه بالكوفة مصعب بن عبيد الله بن جنادة','2148','578','0');
INSERT INTO [t10490] VALUES (598,'جنادة بن مالك الأزدي يكنى أبا عبيد الله، عقبه بالكوفة','2151','578','0');
INSERT INTO [t10490] VALUES (599,'جنادة غير منسوب ذكره بعض المتأخرين ولم ينسبه، واقتصر على أن له ذكرا في كتاب عمرو بن حزم، إن كان محفوظا','2153','578','0');
INSERT INTO [t10490] VALUES (600,'جنادة بن جرادة الغيلاني له صحبة يعد في البصريين، روى عنه زياد بن قريع، وقيل: قربع','2155','578','0');
INSERT INTO [t10490] VALUES (601,'جنادة بن زيد الحارثي يعد في البصريين من أعرابها، لا تصح صحبته، وفي سند حديثه نظر.','2157','578','0');
INSERT INTO [t10490] VALUES (602,'جنادة بن أبي أمية واسم أبي أمية، كثير، وهو عندي جنادة بن أبي أمية الأزدي الذي تقدم حديثه، وفرق بينهما بعض الرواة من المتأخرين وهما عندي واحد','2159','578','0');
INSERT INTO [t10490] VALUES (603,'جنادة بن أبي أمية الأزدي الزهراني من بني زهران، شهد فتح مصر من أصحاب النبي صلى الله عليه وسلم، وولي البحر في زمان معاوية رضي الله عنه. روى عنه أبو الخير، وأبو قبيل الحارث بن يزيد، توفي بالشام سنة ثمانين وهو عندي المتقدم، وفرق بينه بعض المتأخرين.','2161','578','0');
INSERT INTO [t10490] VALUES (604,'جعدة الجشمي قال يزيد بن زريع هو جعدة بن خالد بن الصمة الجشمي، تفرد بالرواية عنه أبو إسرائيل الجشمي، يعد في الكوفيين','2163','578','0');
INSERT INTO [t10490] VALUES (605,'جعدة بن هبيرة بن أبي وهب المخزومي ابن بنت أم هانئ، اختلف في صحبته. روى عنه مجاهد، ويزيد بن عبد الرحمن الأودي، وسعيد بن علاقة، سكن الكوفة','2166','578','0');
INSERT INTO [t10490] VALUES (606,'جعدة بن هانئ الحضرمي جاهلي من أهل حمص، حديثه عند نصر بن علقمة، عن أخيه محفوظ، عن ابن عائذ، وفيه نظر','2169','578','0');
INSERT INTO [t10490] VALUES (607,'جرثوم بن ناشب، وقيل: ابن ناشم، وقيل: ابن ناشر وقيل: ابن لاشر بن وبرة، وقيل: جرهم واختلف فيه، وقيل غير ما ذكرنا، كنيته أبو ثعلبة الخشني، وخشنة بطن من قضاعة، سكن الشام، وتوفي سنة خمس وسبعين. روى عنه عبد الله بن عمرو بن العاص، وأبو إدريس','2171','578','0');
INSERT INTO [t10490] VALUES (608,'جناب أبو خابط الكناني','2174','578','0');
INSERT INTO [t10490] VALUES (609,'جرهد الأسلمي يقال: ابن خويلد، وقيل: ابن رزاح بن عدي بن سهم بن مازن بن الحارث بن سلامان بن أفصى، نسبه محمد بن سعد الواقدي، وقال الزهري هو ابن خويلد، شهد الحديبية وكان من أهل الصفة، يكنى أبا عبد الرحمن، سكن المدينة، وله بها دار، توفي آخر ولاية','2176','578','0');
INSERT INTO [t10490] VALUES (610,'جعيل الأشجعي غير منسوب','2183','578','0');
INSERT INTO [t10490] VALUES (611,'جعيل بن سراقة الضمري أخو عوف، وقيل: جعال أصيب عينه يوم قريظة، فلم يجعل له النبي صلى الله عليه وسلم دية، يعد في أهل الصفة. رواه عبد الله بن عمرو بن زهير الكعبي، عن يعقوب بن عتبة، عن عبد الواحد بن عوف، عن أبيه قال: أصاب أخي جعيلا عينه في بني قريظة','2185','578','0');
INSERT INTO [t10490] VALUES (612,'جميل بن بصرة الغفاري وقيل حميل ويقال: حميل، والصواب جميل وقيل: بصرة بن أبي بصرة الغفاري يكنى أبا بصرة قال علي بن المديني: سألت رجلا من غفار عن اسم أبي بصرة، فقال: اسمه حميل، سكن مصر، ويقال: جميل بن وقاص.','2188','578','0');
INSERT INTO [t10490] VALUES (613,'جميل بن ردام العذري أقطعه النبي صلى الله عليه وسلم الرمداء، له ذكر في حديث عمرو بن حزم.','2190','578','0');
INSERT INTO [t10490] VALUES (614,'جزء غير منسوب عداده في الشاميين','2192','578','0');
INSERT INTO [t10490] VALUES (615,'جزء بن الحدرجان بن مالك له ولأبيه، ولأخيه صحبة، قدم على النبي صلى الله عليه وسلم طالبا لدية أخيه قذاذ، وثأره','2194','578','0');
INSERT INTO [t10490] VALUES (616,'جزي السلمي روى حديثه حيان، وعبد الله ابناه.','2196','578','0');
INSERT INTO [t10490] VALUES (617,'جري الحنفي روى حديثه القاسم بن الحكم العرني','2198','578','0');
INSERT INTO [t10490] VALUES (618,'جرموز الهجيمي، وقيل: القريعي من بني تميم له صحبة، روى عنه أبو تميمة الهجيمي، سكن البصرة','2200','578','0');
INSERT INTO [t10490] VALUES (619,'جفينة الجهني روى عنه عرينة','2203','578','0');
INSERT INTO [t10490] VALUES (620,'جرو السدوسي','2205','578','0');
INSERT INTO [t10490] VALUES (621,'جرو بن مالك بن عامر الأنصاري استشهد باليمامة. حدثنا سليمان بن أحمد، ثنا محمد بن عمرو بن خالد، حدثني أبي، ثنا ابن لهيعة، عن أبي الأسود، عن عروة، في تسمية من قتل يوم اليمامة من الأنصار، من بني جحجبي: جرو بن مالك بن عامر بن حدير "','2207','578','0');
INSERT INTO [t10490] VALUES (622,'جري بن عمرو العذري، وقيل: جرير','2209','578','0');
INSERT INTO [t10490] VALUES (623,'جودان سكن الكوفة وقيل: ابن جودان، غير منسوب، روى عنه الأشعث بن عمير، والعباس بن عبد الرحمن، والسائب بن مالك','2211','578','0');
INSERT INTO [t10490] VALUES (624,'جمد الكندي','2213','578','0');
INSERT INTO [t10490] VALUES (625,'جهم الأسلمي وهو وهم إنما هو جاهمة السلمي','2215','578','0');
INSERT INTO [t10490] VALUES (626,'جهم البلوي روى عنه ابنه علي','2218','578','0');
INSERT INTO [t10490] VALUES (627,'جهم، غير منسوب روى عنه ذو الكلاع، وهو عندي البلوي الذي تقدم','2220','578','0');
INSERT INTO [t10490] VALUES (628,'وجهم بن قيس له ذكر في قصة أبي هند الداري وكتابه.','2222','578','0');
INSERT INTO [t10490] VALUES (629,'جهم بن قثم وفد مع الذارع على النبي صلى الله عليه وسلم إن صح','2223','578','0');
INSERT INTO [t10490] VALUES (630,'جليبيب الأنصاري له ذكر في حديث أبي برزة الأسلمي','2225','578','0');
INSERT INTO [t10490] VALUES (631,'جبيب بن الحارث له ذكر في حديث نوح بن ذكوان عن هشام','2227','578','0');
INSERT INTO [t10490] VALUES (632,'جون بن قتادة التميمي يعد في البصريين، لا يثبت له صحبة ولا رؤية. ذكره بعض الواهمين في الصحابة','2229','578','0');
INSERT INTO [t10490] VALUES (633,'الجفشيش بن النعمان الكندي يكنى أبا الخير، ويقال: إن الجفشيش لقب، واسمه معدان، له ذكر في حديث الأشعث بن قيس','2231','578','0');
INSERT INTO [t10490] VALUES (634,'جشيب مجهول روى عنه ابنه، إن كان صاحب أبي الدرداء فهو حمصي وليست له صحبة، وروى عنه سعيد بن سويد','2234','578','0');
INSERT INTO [t10490] VALUES (635,'جاهمة: أبو معاوية السلمي','2236','578','0');
INSERT INTO [t10490] VALUES (636,'جاحل أبو مسلم روى عنه ابنه مسلم','2238','578','0');
INSERT INTO [t10490] VALUES (637,'جعونة بن زياد الشني سمع النبي صلى الله عليه وسلم في العريف','2240','578','0');
INSERT INTO [t10490] VALUES (638,'جلاس بن صليت اليربوعي سأل النبي صلى الله عليه وسلم عن الوضوء','2242','578','0');
INSERT INTO [t10490] VALUES (639,'جلاس بن سويد بن الصامت أخو بني عمرو بن عوف، له ذكر في المغازي','2244','578','0');
INSERT INTO [t10490] VALUES (640,'جد بن قيس السلمي روى عنه جابر بن عبد الله، وأبو هريرة وفيه نزلت: ومنهم من يقول ائذن لي ولا تفتني','2246','578','0');
INSERT INTO [t10490] VALUES (641,'جندرة بن خيشنة بن نفير بن مرة بن عرنة بن وايلة بن الفاكه بن عمرو بن مالك بن خزيمة بن مدركة نسبه عبد الله بن أبي داود، عن أيوب بن علي بن الهيضم بن أيوب بن مسلم بن خيشنة مولى بني ليث بن بكر بن عبد مناة من كنانة. وقيل: من بني مالك بن النضر بن كنانة يعد','2249','578','0');
INSERT INTO [t10490] VALUES (642,'جهيش بن أويس النخعي قدم على النبي صلى الله عليه وسلم، في إسناد حديثه نظر','2253','578','0');
INSERT INTO [t10490] VALUES (643,'الجراد العقيلي أبو عبد الله روى عنه ابنه إن كان محفوظا','2255','578','0');
INSERT INTO [t10490] VALUES (644,'الجراد بن عبس وقيل: عيسى يعد في أعراب البصرة','2257','578','0');
