INSERT INTO [t10490] VALUES (646,'جهر أبو عبد الله','2261','578','0');
INSERT INTO [t10490] VALUES (647,'جثامة بن مساحق بن الربيع بن قيس الكناني','2263','578','0');
INSERT INTO [t10490] VALUES (648,'جندع الأنصاري مختلف في اسمه','2265','578','0');
INSERT INTO [t10490] VALUES (649,'جمرة بن عوف يكنى أبا يزيد، عداده في أهل فلسطين، حديثه عند ابنه يزيد بن جمرة','2268','578','0');
INSERT INTO [t10490] VALUES (650,'جمرة بن النعمان العذري','2270','578','0');
INSERT INTO [t10490] VALUES (651,'جحدم والد حكيم روى عنه ابنه','2272','578','0');
INSERT INTO [t10490] VALUES (652,'جحدم بن فضالة أتى النبي صلى الله عليه وسلم فمسح رأسه، وكتب له كتابا روى عنه أولاده','2274','578','0');
INSERT INTO [t10490] VALUES (653,'جليحة بن عبد الله الليثي استشهد يوم الطائف','2276','578','0');
INSERT INTO [t10490] VALUES (654,'جهجاه بن قيس وقيل: ابن سعيد الغفاري، عداده في المدنيين، مات بعد قتل عثمان بسنة، حديثه عند عطاء بن يسار','2278','578','0');
INSERT INTO [t10490] VALUES (655,'جدار روى عنه يزيد بن شجرة','2280','578','0');
INSERT INTO [t10490] VALUES (656,'جبارة بن زرارة البلوي وجذرة بن سبرة العتقي، وجديع بن ندير المرادي صحب النبي صلى الله عليه وسلم، وخدمه، كل هؤلاء ذكرهم الحاكي عن أبي سعيد بن عبد الأعلى، مقتصرا على الأسامي من دون الروايات والشواهد','2282','578','0');
INSERT INTO [t10490] VALUES (657,'باب الحاء','2283','18','0');
INSERT INTO [t10490] VALUES (658,'من اسمه الحسن','2284','657','0');
INSERT INTO [t10490] VALUES (659,'الحسن بن علي بن أبي طالب الحسن بن علي بن أبي طالب بن عبد المطلب بن هاشم، أبو محمد سيد شباب أهل الجنة، وريحانة رسول الله صلى الله عليه وسلم، والسيد المصلح به بين الأمة، وسبط من الأسباط سماه النبي صلى الله عليه وسلم حسنا، شبيه رسول الله صلى الله عليه','2285','658','0');
INSERT INTO [t10490] VALUES (660,'ومما أسند','2308','658','0');
INSERT INTO [t10490] VALUES (661,'أبو عبد الله الحسين بن علي بن أبي طالب ريحانة رسول الله صلى الله عليه وسلم، وشبيهه، أذن رسول الله صلى الله عليه وسلم في أذنه حين ولد، سيد شباب أهل الجنة، خامس أهل الكساء، وابن سيدة النساء، أبوه الذائد عن الحوض، وعمه ذو الجناحين، غذته أكف النبوة،','2313','658','0');
INSERT INTO [t10490] VALUES (662,'ومما أسند','2353','658','0');
INSERT INTO [t10490] VALUES (663,'حسين بن السائب الأنصاري','2358','658','0');
INSERT INTO [t10490] VALUES (664,'حمزة بن عبد المطلب بن هاشم بن عبد مناف أبو عمارة وقيل: أبو يعلى، كان عم النبي صلى الله عليه وسلم، وأخاه من الرضاعة، أسد الله وأسد رسوله، تزوج عبد المطلب هالة بنت أهيب بن عبد مناف بن زهرة، فولدت له حمزة وصفية، وكانت ثويبة مولاة أبي لهب، أرضعت حمزة','2360','658','0');
INSERT INTO [t10490] VALUES (665,'حمزة بن عمرو الأسلمي وهو ابن عويمر بن الحارث بن الأعرج بن سعد بن رزاح بن عدي بن سهم بن مازن بن الحارث بن سلامان بن أسلم بن أقصى بن حارثة يكنى أبا صالح، وقيل: أبو محمد، روت عنه عائشة، وابنه محمد، وعروة بن الزبير، وسليمان بن يسار، وأبو سلمة بن عبد','2385','658','0');
INSERT INTO [t10490] VALUES (666,'حمزة بن عمر لا يصح وهو وهم','2397','658','0');
INSERT INTO [t10490] VALUES (667,'حذيفة بن اليمان وهو ابن حسل بن جابر بن ربيعة بن عمرو بن اليمان. وقيل: حذيفة بن اليمان؛ لأنه من ولد اليمان بن جروة بن الحارث بن قطيعة بن عبس، يكنى أبا عبد الله، مهاجري، هاجر هو وأبوه إلى النبي صلى الله عليه وسلم فخيره بين الهجرة والنصرة، فاختار','2399','658','0');
INSERT INTO [t10490] VALUES (668,'ومما أسند حذيفة','2410','658','0');
INSERT INTO [t10490] VALUES (669,'حذيفة بن أسيد أبو سريحة الغفاري وهو حذيفة بن أسيد بن الأعوس، وقيل: ابن عمار بن واقعة بن حرام بن غفار بن مليل بن ضمرة بن بكر بن عبد مناة بن كنانة بن خزيمة بن مدركة بن إلياس بن مضر بن نزار بن معد بن عدنان وكان من أصحاب الشجرة، ومن أهل الصفة توفي، وصلى','2416','658','0');
INSERT INTO [t10490] VALUES (670,'ومما أسند','2419','658','0');
INSERT INTO [t10490] VALUES (671,'حذيفة البارقي ذكر فيمن أدرك النبي صلى الله عليه وسلم، يحدث عنه أبو الخير اليزني، روى عن جنادة الأزدي، ذكرنا حديثه، في حديث جنادة','2423','658','0');
INSERT INTO [t10490] VALUES (672,'وحذيفة بن عبيد المرادي له ذكر في قضاء لعمر، شهد فتح مصر، أدرك الجاهلية، لا يعرف له رواية، ذكرهما المحيل عن ابن سعيد بن عبد الأعلى','2424','658','0');
INSERT INTO [t10490] VALUES (673,'من اسمه حاطب','2425','657','0');
INSERT INTO [t10490] VALUES (674,'حاطب بن أبي بلتعة وهو ابن عمرو بن عمير بن سلمة، يكنى أبا محمد، حليف بني أسد بن عبد العزى، شهد بدرا. وقيل: حاطب بن أبي بلتعة بن أدرب بن حرملة بن لخم بن عدي بن الحارث بن مرة بن آدد بن زيد بن يشجب بن يعرب بن قحطان، بعثه رسول الله صلى الله عليه وسلم','2426','673','0');
INSERT INTO [t10490] VALUES (675,'حاطب بن الحارث بن معمر بن حبيب بن وهب بن حذافة بن جمح هاجر هو وامرأته فاطمة بنت المجلل وابناه الحارث ومحمد إلى أرض الحبشة','2429','673','0');
INSERT INTO [t10490] VALUES (676,'وحاطب بن عمرو بن عبد شمس أول مهاجري إلى أرض الحبشة، لا يعرف له رواية، قاله الزهري، ومحمد بن إسحاق','2430','673','0');
INSERT INTO [t10490] VALUES (677,'من اسمه حويطب','2431','657','0');
INSERT INTO [t10490] VALUES (678,'حويطب، وقيل خوط: وقيل حوط بن عبد العزى بن أبي قيس بن عبد ود بن نصر بن مالك بن حسل بن عامر بن لؤي بن غالب بن فهر بن مالك يكنى أبا محمد وقيل: أبو الأصبع، توفي سنة أربع وخمسين في إمارة معاوية، سكن مكة، من مسلمة الفتح','2432','677','0');
INSERT INTO [t10490] VALUES (679,'حوط بن قرواش بن حصين أتى النبي صلى الله عليه وسلم، مجهول، روى حديثه سهل بن شاذان','2437','677','0');
INSERT INTO [t10490] VALUES (680,'حوط بن يزيد الأنصاري ابن عم الحارث بن زياد، روى عنه الحارث','2439','677','0');
INSERT INTO [t10490] VALUES (681,'من اسمه حكيم','2441','657','0');
INSERT INTO [t10490] VALUES (682,'حكيم بن حزام بن خويلد بن أسد بن عبد العزى بن قصي بن كلاب أبو خالد، أمه صفية، وقيل: فاختة بنت زهير بن الحارث بن أسد، وأمها سلمى بنت عبد مناف بن عبد الدار، من مسلمة الفتح، من المؤلفة، أعطاه رسول الله صلى الله عليه وسلم يوم حنين مائة بعير ثم حسن','2442','681','0');
INSERT INTO [t10490] VALUES (683,'حكيم بن معاوية النميري حديثه عند الحمصيين، في سند حديثه اختلاف','2458','681','0');
INSERT INTO [t10490] VALUES (684,'حكيم بن حزن بن أبي وهب بن عمرو بن عائذ عم سعيد بن المسيب، استشهد باليمامة، لم يسند شيئا. حدثنا سليمان بن أحمد، ثنا محمد بن عمرو بن خالد، حدثني أبي، ثنا ابن لهيعة، عن أبي الأسود، عن عروة بن الزبير، في تسمية من قتل يوم اليمامة من المسلمين، ثم من','2461','681','0');
INSERT INTO [t10490] VALUES (685,'حكيم بن قيس بن عاصم المنقري قيل: إن ولادته: كانت في أيام النبي صلى الله عليه وسلم، روى عن أبيه عن النبي صلى الله عليه وسلم، روى عنه مطرف بن الشخير','2462','681','0');
INSERT INTO [t10490] VALUES (686,'من اسمه الحكم','2463','657','0');
INSERT INTO [t10490] VALUES (687,'الحكم بن عمرو الغفاري يعرف بالأقرع صحب النبي صلى الله عليه وسلم حتى قبض، ثم نزل البصرة فولاه زياد بن أبي سفيان خراسان، فخرج إليها، وسكن مرو، وتوفي بها واليا عليها سنة خمسين، وقيل خمس وأربعين، ودفن مع بريدة الأسلمي بجنبه، وهو الحكم بن عمرو بن مجدع','2464','686','0');
INSERT INTO [t10490] VALUES (688,'الحكم بن حزن الكلفي وفد على النبي صلى الله عليه وسلم','2472','686','0');
INSERT INTO [t10490] VALUES (689,'الحكم بن أبي العاص بن أمية بن عبد شمس بن عبد مناف أبو مروان، يعد في الحجازيين، نفاه رسول الله صلى الله عليه وسلم عن المدينة حياته','2474','686','0');
INSERT INTO [t10490] VALUES (690,'الحكم بن أبي العاص الثقفي أخو عثمان بن أبي العاص بن بشر بن عبيد بن دهان، عداده في البصريين، روى عنه معاوية بن قرة','2478','686','0');
INSERT INTO [t10490] VALUES (691,'الحكم بن سعيد بن العاص بن أمية بن عبد شمس سماه رسول الله صلى الله عليه وسلم عبد الله، قيل: إنه استشهد يوم بدر، ولا يثبت','2481','686','0');
INSERT INTO [t10490] VALUES (692,'الحكم بن الحارث السلمي حديثه عند عطية الدعاء، وابن أخيه حبيب بن هرم بن الحارث، سكن البصرة، غزا مع النبي صلى الله عليه وسلم غير غزوة','2485','686','0');
INSERT INTO [t10490] VALUES (693,'الحكم بن كيسان المخزومي استشهد يوم بئر معونة كان مع عبد الله بن جحش في سريته وهي أول سرية كانت في الإسلام','2490','686','0');
INSERT INTO [t10490] VALUES (694,'الحكم بن سفيان الثقفي مختلف فيه، فقيل: الحكم بن سفيان، وقيل: سفيان بن الحكم، وقيل: أبو الحكم الثقفي، وقيل: ابن أبي سفيان','2492','686','0');
INSERT INTO [t10490] VALUES (695,'الحكم أبو شبث بن الحكم له ذكر','2498','686','0');
INSERT INTO [t10490] VALUES (696,'الحكم بن أبي الحكم له ذكر في حديث كعب بن الخزرج','2500','686','0');
