INSERT INTO [t10490] VALUES (698,'الحكم بن عمير الثمالي يعد في الشاميين سكن حمص، تفرد بالرواية عنه موسى بن أبي حبيب','2505','686','0');
INSERT INTO [t10490] VALUES (699,'الحكم بن عبد الله الثقفي في إسناد حديثه مقال','2510','686','0');
INSERT INTO [t10490] VALUES (700,'الحكم بن عمرو بن الشريد مختلف في اسمه','2512','686','0');
INSERT INTO [t10490] VALUES (701,'الحكم أبو عبد الله الأنصاري جد مطيع','2514','686','0');
INSERT INTO [t10490] VALUES (702,'الحكم أبو مسعود الزرقي في حديثه اختلاف، أخرجه بعض المتأخرين في الصحابة','2517','686','0');
INSERT INTO [t10490] VALUES (703,'الحكم بن مرة صحب النبي صلى الله عليه وسلم، في سند حديثه نظر','2519','686','0');
INSERT INTO [t10490] VALUES (704,'*','2521','657','0');
INSERT INTO [t10490] VALUES (705,'الحجاج بن عمرو بن غزية المازني الأنصاري من أهل المدينة، حديثه عند كثير بن العباس، وعبد الله بن رافع، وعكرمة، شهد مع علي بن أبي طالب رضي الله عنه. حدثنا سليمان بن أحمد، ثنا محمد بن عثمان بن أبي شيبة، ثنا ضرار بن صرد، ثنا علي بن هاشم، عن محمد بن','2521','704','0');
INSERT INTO [t10490] VALUES (706,'الحجاج بن علاط السلمي ثم البهزي شهد خيبر ع النبي صلى الله عليه وسلم، وهو أول من بعث بصدقته إلى النبي صلى الله عليه وسلم من عدن بني سليم وأهدى سيفه المسمى ذا الفقار، حجازي','2524','704','0');
INSERT INTO [t10490] VALUES (707,'حجاج بن مالك الأسلمي مختلف في حديثه، يعد في المدنيين','2527','704','0');
INSERT INTO [t10490] VALUES (708,'حجاج بن عامر الثمالي عداده في الحمصيين، روى عنه خالد بن معدان، وشرحبيل بن مسلم، وعاصم بن حجاج','2534','704','0');
INSERT INTO [t10490] VALUES (709,'الحجاج بن عبد الله الثمالي وأراه المتقدم','2537','704','0');
INSERT INTO [t10490] VALUES (710,'الحجاج بن عبد الله النصري','2539','704','0');
INSERT INTO [t10490] VALUES (711,'حجاج الباهلي','2542','704','0');
INSERT INTO [t10490] VALUES (712,'وحجاج بن الحارث بن قيس القرشي ثم السهمي، قتل يوم أجنادين حدثنا سليمان بن أحمد، ثنا محمد بن عمرو بن خالد الحراني، حدثني أبي، ثنا ابن لهيعة، عن أبي الأسود، عن عروة، في تسمية من قتل يوم أجنادين من المسلمين ثم من قريش، ثم من بني سهم: حجاج بن الحارث','2544','704','0');
INSERT INTO [t10490] VALUES (713,'الحجاج بن الحارث بن قيس بن عدي بن سعيد بن سهم. " من مهاجرة الحبشة، عم عبد الله بن حذافة بن قيس بن عدي السهمي، وأراه المتقدم. حدثنا حبيب بن الحسن، ثنا محمد بن يحيى بن سليمان، ثنا أحمد بن محمد بن أيوب، ثنا إبراهيم بن سعد، عن محمد بن إسحاق، في','2547','704','0');
INSERT INTO [t10490] VALUES (714,'حارثة بن النعمان الأنصاري بدري كان أحد الثمانين الذين ثبتوا وصبروا مع رسول الله صلى الله عليه وسلم يوم حنين ولم يفروا، فأخبر جبريل النبي صلى الله عليه وسلم بأن رزقهم ورزق عيالهم على الله عز وجل في الجنة، وكان قد مر بالمقاعد على رسول الله صلى الله عليه','2548','704','0');
INSERT INTO [t10490] VALUES (715,'حارثة ابن الربيع " والربيع اسم أمه، وهو حارثة بن سراقة بن الحارث، من بني عدي بن النجار، أصيب ببدر، وهو ابن عمة أنس بن مالك، وهو الذي بلغ به بره أمه أن قال النبي صلى الله عليه وسلم فيه: " دخلت الجنة فرأيت حارثة، كذلكم البر "، وشهد له أنه أسكن','2556','704','0');
INSERT INTO [t10490] VALUES (716,'حارثة بن زيد الأنصاري بدري حدثنا محمد بن أحمد بن عبد الوهاب، وأبو محمد بن حيان، وسليمان بن أحمد قالوا: ثنا الحسن بن هارون بن سليمان، ثنا محمد بن إسحاق المسيبي، حدثنا محمد بن فليح، عن موسى بن عقبة، عن ابن شهاب، في تسمية من شهد بدرا من الأنصار، ثم','2560','704','0');
INSERT INTO [t10490] VALUES (717,'وحارثة بن مالك الأنصاري من بني حبيب بن عبد، شهد بدرا، ذكره بعض الواهمين، ونسب وهمه إلى محمد بن إسحاق، ووهم هو، وصوابه حبيب بن عبد حارثة بن مالك، ففصل بين عبد وحارثة، فقدر أن حارثة اسم، والذي قاله محمد بن إسحاق بخلاف ما حكاه عنه','2561','704','0');
INSERT INTO [t10490] VALUES (718,'حارثة بن الحمير الأشجعي بدري , حليف بني دهمان حدثنا سليمان بن أحمد، ثنا محمد بن عمرو بن خالد الحراني، حدثني أبي، ثنا ابن لهيعة، عن أبي الأسود، عن عروة، في تسمية من شهد بدرا من الأنصار من الأوس، ثم من بني عبيد بن عدي بن غنم بن كعب بن سلمة: حارثة','2565','704','0');
INSERT INTO [t10490] VALUES (719,'حارثة بن الأضبط الذكواني عداده في الجزريين','2566','704','0');
INSERT INTO [t10490] VALUES (720,'حارثة بن وهب الخزاعي أخو عمر بن الخطاب لأمه، روى عنه أبو إسحاق، ومعبد بن خالد، والصلت بن بهرام','2568','704','0');
INSERT INTO [t10490] VALUES (721,'حارثة بن عدي بن أمية بن الضبيب عداده في الشاميين','2574','704','0');
INSERT INTO [t10490] VALUES (722,'حارثة بن شراحيل بن كعب بن عبد العزى بن امرئ القيس بن عامر بن النعمان الكلبي، أبو زيد بن حارثة، قدم على النبي صلى الله عليه وسلم طالبا لابنه زيد، فأسلم','2576','704','0');
INSERT INTO [t10490] VALUES (723,'من اسمه الحارث','2578','657','0');
INSERT INTO [t10490] VALUES (724,'الحارث بن نوفل بن الحارث بن عبد المطلب بن هاشم الهاشمي أبو الحارث، مات في أيام عمر رضي الله عنه، صحب النبي صلى الله عليه وسلم، وولد له ابنه عبد الله، الملقب بببه في عهد النبي صلى الله عليه وسلم، نزل البصرة، واختط بها دارا، توفي وله سبعون سنة،','2579','723','0');
INSERT INTO [t10490] VALUES (725,'الحارث بن ربعي أبو قتادة الأنصاري وهو ابن بلدمة بن خناس بن سنان بن عبيد بن عدي بن غنم بن كعب بن سلمة، من خير فرسان رسول الله صلى الله عليه وسلم، توفي بالمدينة - وكان يخضب بالصفرة - سنة أربع وخمسين، وله سبعون سنة. روى عنه أبو سعيد الخدري، وأنس، وجابر','2582','723','0');
INSERT INTO [t10490] VALUES (726,'الحارث بن أنس بن مالك الأنصاري بدري','2595','723','0');
INSERT INTO [t10490] VALUES (727,'الحارث بن أوس الأنصاري شهد بدرا، لا يعرف له رواية','2599','723','0');
INSERT INTO [t10490] VALUES (728,'الحارث بن أوس بن معاذ الأشهلي ابن أخي سعد بن معاذ، شهد بدرا، له ذكر في حديث عائشة','2601','723','0');
INSERT INTO [t10490] VALUES (729,'الحارث بن أوس بن رافع الأنصاري وقيل: ابن أنس بن رافع، استشهد يوم أحد','2603','723','0');
INSERT INTO [t10490] VALUES (730,'والحارث بن أوس بن النعمان الأنصاري النجاري حضر قتل كعب بن الأشرف مع محمد بن مسلمة، حين بعثهما رسول الله صلى الله عليه وسلم','2606','723','0');
INSERT INTO [t10490] VALUES (731,'الحارث بن أشيم بن رافع بن امرئ القيس حدثنا سليمان بن أحمد، ثنا محمد بن عمرو بن خالد، حدثني أبي، ثنا ابن لهيعة، عن أبي الأسود، عن عروة، في تسمية من شهد بدرا من الأنصار، ثم من الأوس، ثم من بني عبد الأشهل: الحارث بن أشيم بن رافع بن امرئ القيس بن','2608','723','0');
INSERT INTO [t10490] VALUES (732,'والحارث بن النعمان الأنصاري " بدري: وقيل: حارثة بن النعمان، وهو ابن رافع بن ثعلبة بن جشم بن مالك حدثنا فاروق الخطابي، ثنا زياد بن الخليل، ثنا إبراهيم بن المنذر، ثنا محمد بن فليح، عن موسى بن عقبة، عن ابن شهاب، في تسمية من شهد بدرا من الأنصار، ثم','2609','723','0');
INSERT INTO [t10490] VALUES (733,'الحارث بن عدي بن مالك بن معاوية استشهد مع أبي عبيد يوم الجسر سنة خمس عشرة، لا يعرف له رواية','2610','723','0');
INSERT INTO [t10490] VALUES (734,'الحارث بن سلمة العجلاني شهد أحدا، لا يعرف له رواية، قاله ابن إسحاق، فيما حكاه عنه بعض المتأخرين','2612','723','0');
INSERT INTO [t10490] VALUES (735,'والحارث بن مسعود بن عبد بن مظاهر المعاوي الأنصاري، استشهد بالجسر','2613','723','0');
INSERT INTO [t10490] VALUES (736,'والحارث بن الطفيل بن صخر بن خزيمة أخو عوف بن الطفيل، ذكره محمد بن إسماعيل البخاري في الصحابة، لا يعرف له رواية','2616','723','0');
INSERT INTO [t10490] VALUES (737,'والحارث بن زيد بن العطاف من بني عمرو بن عوف، ثم من بني ضبيعة بن زيد، قاله محمد بن إسحاق','2617','723','0');
INSERT INTO [t10490] VALUES (738,'والحارث بن سهل بن أبي صعصعة من بني مازن بن النجار استشهد يوم الطائف لا يعرف له رواية، قاله ابن إسحاق، ذكره عنه بعض المتأخرين، ووهم فيه، إنما هو الحباب بن سهل','2618','723','0');
INSERT INTO [t10490] VALUES (739,'الحارث بن عوف أبو واقد الليثي مختلف في اسمه، واسم أبيه فقيل: الحارث بن مالك، وقيل: عوف بن مالك، له صحبة، وهو الحارث بن عوف بن أسيد بن جابر بن عتوارة بن عبد مناة بن شجع بن عامر بن ليث بن بكر بن عبد مناة بن كنانة، مات سنة خمس وقيل: ثمان وستين،','2619','723','0');
INSERT INTO [t10490] VALUES (740,'الحارث بن هشام بن المغيرة بن عبد الله بن عمر بن مخزوم بن يقظة بن مرة بن كعب بن لؤي بن غالب يكنى أبا عبد الرحمن، لا يختلف في كنيته ووهم بعض المتأخرين، فقال: هو أبو المغيرة ابن عبد الله بن عمر بن مخزوم، وصحف، وذلك أنه حكي عن مصعب الزبيري أنه قال:','2626','723','0');
INSERT INTO [t10490] VALUES (741,'الحارث بن حاطب الجمحي من مهاجرة الحبشة، أخو محمد بن حاطب، أمهما فاطمة بنت المجلل، كانت إليه الإمرة بمكة حدثنا محمد بن أحمد بن الحسن، ثنا محمد بن عثمان بن أبي شيبة، ثنا منجاب، ثنا إبراهيم بن يوسف، ثنا زياد، عن محمد بن إسحاق، في تسمية من هاجر إلى','2634','723','0');
INSERT INTO [t10490] VALUES (742,'الحارث بن حاطب الأنصاري بدري بسهمه، أخو ثعلبة بن حاطب حدثنا فاروق الخطابي، ثنا زياد بن الخليل، ثنا إبراهيم بن المنذر، ثنا محمد بن فليح، عن موسى بن عقبة، عن ابن شهاب، في تسمية من شهد بدرا من الأنصار، من الأوس، ثم من بني عمرو بن عوف، ثم من بني','2640','723','0');
INSERT INTO [t10490] VALUES (743,'الحارث بن عمرو الأنصاري عم البراء بن عازب','2642','723','0');
INSERT INTO [t10490] VALUES (744,'الحارث بن الصمة بن عمرو الأنصاري شهد بدرا، كسر بالروحاء، فضرب له رسول الله صلى الله عليه وسلم بسهمه، واستشهد يوم بئر معونة حدثنا فاروق الخطابي، ثنا زياد بن الخليل، ثنا إبراهيم بن المنذر، ثنا محمد بن فليح، ثنا موسى بن عقبة، عن ابن شهاب، في تسمية','2646','723','0');
INSERT INTO [t10490] VALUES (745,'الحارث بن خزمة بن أبي غنم الأنصاري بدري، وقيل: ابن خزيمة، يكنى أبا بشير، توفي في خلافة علي رضي الله عنه، ذكره البخاري في الكنى حدثنا فاروق بن عبد الكبير الخطابي، ثنا زياد بن الخليل، ثنا إبراهيم بن المنذر، ثنا محمد بن فليح، ثنا موسى بن عقبة، عن','2649','723','0');
INSERT INTO [t10490] VALUES (746,'الحارث بن زياد الأنصاري الساعدي بدري يعد في المدنيين','2652','723','0');
INSERT INTO [t10490] VALUES (747,'الحارث بن المعلى الأنصاري أبو سعيد، سماه فليح، عن سعيد بن الحارث بن المعلى، عن أبيه، أخرجه بعض المتأخرين، وأراه وهما','2655','723','0');
INSERT INTO [t10490] VALUES (748,'الحارث بن سواد الأنصاري شهد بدرا حدثنا سليمان بن أحمد، ثنا محمد بن عمرو بن خالد، حدثني أبي، ثنا ابن لهيعة، عن أبي الأسود، عن عروة، في تسمية من شهد بدرا: الحارث بن سواد','2657','723','0');
INSERT INTO [t10490] VALUES (749,'والحارث بن سراقة وقيل: حارثة بن سراقة أنصاري، استشهد ببدر من بني عدي بن النجار','2658','723','0');
INSERT INTO [t10490] VALUES (750,'والحارث بن معاذ بن النعمان الأوسي الأشهلي أخو سعد بن معاذ، شهد بدرا، وهم ثلاثة إخوة: سعد، والحارث، وأوس بنو معاذ حدثنا سليمان بن أحمد، ثنا محمد بن عمرو بن خالد، ثنا أبي، ثنا ابن لهيعة، عن أبي الأسود، عن عروة، في تسمية من شهد بدرا من الأنصار،','2661','723','0');
INSERT INTO [t10490] VALUES (751,'الحارث بن قيس بن مخلد الأنصاري ثم الزرقي عقبي، بدري، يكنى أبا خالد','2662','723','0');
INSERT INTO [t10490] VALUES (752,'الحارث بن سويد بن الصامت أخو الجلاس - أحد بني عمرو بن عوف - لحق بمكة بالمشركين، مرتدا ثم ندم، فنزلت فيه إلا الذين تابوا من بعد ذلك','2664','723','0');
INSERT INTO [t10490] VALUES (753,'الحارث بن مالك الأنصاري وقيل: حارثة، روى عنه زيد بن أسلم، وجماعة','2666','723','0');
INSERT INTO [t10490] VALUES (754,'الحارث بن كلدة الثقفي طبيب العرب، مولى أبي بكرة مسروح، وقيل: نفيع، من نوق، مختلف فيه','2668','723','0');
INSERT INTO [t10490] VALUES (755,'الحارث بن مالك بن البرصاء الليثي يعد في الحجازيين وهو الحارث بن مالك بن قيس بن عويد بن عبد الله بن جابر بن عبد مناف بن شجع بن عامر بن ليث بن بكر بن عبد مناة بن كنانة، حديثه عند الشعبي، وعبيد بن جريج','2674','723','0');
INSERT INTO [t10490] VALUES (756,'الحارث بن عمرو السهمي: سهم باهلة عداده في البصريين، مسح النبي صلى الله عليه وسلم وجهه، فما زالت مسحة النبي صلى الله عليه وسلم نضرة وغرة في وجهه إلى أن مات','2678','723','0');
INSERT INTO [t10490] VALUES (757,'الحارث بن ضرار وقيل: ابن أبي ضرار الخزاعي يكنى أبا مالك، عداده في الحجازيين','2682','723','0');
INSERT INTO [t10490] VALUES (758,'الحارث بن أبي ربيعة المخزومي استسلف منه النبي صلى الله عليه وسلم، في سنده اختلاف، ذكره بعض المتأخرين ونسب الوهم فيه إلى عبد الله بن أبي خداش الموصلي، عن القاسم الجرمي، عن سفيان، عن إسماعيل بن إبراهيم، عن أبيه، عن الحارث بن أبي ربيعة، والصواب ما','2684','723','0');
INSERT INTO [t10490] VALUES (759,'الحارث بن أوس الثقفي، وقيل: الحارث بن عبد الله بن أوس الثقفي','2687','723','0');
INSERT INTO [t10490] VALUES (760,'الحارث بن أقيش وقيل: وقيش العكلي يعد في البصريين','2690','723','0');
INSERT INTO [t10490] VALUES (761,'الحارث بن حسان البكري الذهلي وقيل: حويرث، سكن الكوفة، وعداده فيها، روى عنه: شقيق أبو وائل، وسماك بن حرب','2693','723','0');
INSERT INTO [t10490] VALUES (762,'الحارث بن قيس وقيل: ابن عبد قيس بن عامر بن أمية، من بني الحارث بن فهر من مهاجري الحبشة، قالا: محمد بن إسحاق حدثنا حبيب بن الحسن، ثنا محمد بن يحيى المروزي، ثنا أحمد بن محمود بن أيوب، ثنا إبراهيم بن سعد، عن محمد بن إسحاق، في تسمية من هاجر مع جعفر','2699','723','0');
INSERT INTO [t10490] VALUES (763,'الحارث بن خالد بن صخر بن عامر بن كعب بن سعد بن تيم بن مرة جد محمد بن إبراهيم بن الحارث التيمي، مهاجري أول مهاجر إلى أرض الحبشة مع امرأته ريطة، فولدت له بأرض الحبشة موسى وعائشة، وزينبا، وفاطمة','2700','723','0');
INSERT INTO [t10490] VALUES (764,'الحارث بن خالد القرشي سافر مع الرسول صلى الله عليه وسلم فأخبر عن وضوئه','2702','723','0');
INSERT INTO [t10490] VALUES (765,'الحارث بن ظالم بن عبس أبو الأعور السلمي مختلف في اسمه، روى عنه قيس بن أبي حازم حدثنا أبو بكر بن مالك، ثنا عبد الله بن أحمد بن حنبل، قال: حدثني أبي قال: قرئ على يعقوب بن إبراهيم بن سعد، عن أبيه، عن ابن إسحاق، في تسمية من شهد بدرا من الأنصار من','2704','723','0');
INSERT INTO [t10490] VALUES (766,'الحارث بن عبد الله البجلي ويقال: الجهني يعد في أهل الكوفة إن كان محفوظا','2705','723','0');
INSERT INTO [t10490] VALUES (767,'الحارث بن مسلم التميمي اختلف في حديثه، يكنى: أبا مسلم','2707','723','0');
INSERT INTO [t10490] VALUES (768,'الحارث بن غزية وقيل: غزية بن الحارث يعد في المدنيين، روى عنه يزيد بن خصيفة','2710','723','0');
INSERT INTO [t10490] VALUES (769,'الحارث بن سهل بن صعصعة من بني النجار من بني مازن بن النجار استشهد يوم الطائف، لا يعرف له رواية، قاله محمد بن إسحاق ذكره بعض المتأخرين فوهم فيه وصحف، وإنما هو الحباب بن سهل بن صعصعة. حدثناه محمد بن أحمد بن الحسن، ثنا أبو شعيب الحراني، ثنا أبو جعفر','2713','723','0');
INSERT INTO [t10490] VALUES (770,'الحارث بن الحارث القرشي السهمي قتل بأجنادين، لا يعرف له رواية. حدثنا سليمان بن أحمد، ثنا محمد بن عمرو بن خالد الحراني، حدثني أبي، ثنا ابن لهيعة، عن أبي الأسود، عن عروة، في تسمية من قتل يوم أجنادين من المسلمين ثم من قريش ثم من بني سهم: الحارث بن','2714','723','0');
INSERT INTO [t10490] VALUES (771,'الحارث بن معاوية له ذكر في الصحابة في حديث لعبادة بن الصامت','2715','723','0');
INSERT INTO [t10490] VALUES (772,'الحارث بن الحارث الغامدي له ولأبيه صحبة، روى عنه الوليد بن عبد الرحمن، وعدي بن هلال السلمي، وشريح بن عبيد، وسليم بن عامر','2717','723','0');
INSERT INTO [t10490] VALUES (773,'الحارث بن الحارث الأشعري أبو مالك وقيل: كعب بن عاصم، مختلف فيه، يعد في الشاميين، حديثه عند ربيعة الجرشي، وعبد الرحمن بن غنم الأشعري، وأبي سلام ممطور الأسود، وشريح بن عبيد الحضرمي، وشهر بن حوشب، وخالد بن سعيد بن أبي مريم، وعطاء بن يسار، وابن','2720','723','0');
