INSERT INTO [t10490] VALUES (775,'الحارث بن زياد وليس بالأنصاري يعد في الشاميين مختلف في صحبته','2724','723','0');
INSERT INTO [t10490] VALUES (776,'الحارث بن غطيف السكوني وقيل: غضيف بن الحارث، يعد في الشاميين بالضاد والظاء جميعا كذا في الأصل بالضاد والفاء بنقطتين','2727','723','0');
