INSERT INTO [t10490] VALUES (833,'حنظلة بن أبي عامر بن صيفي بن النعمان الراهب الأنصاري ثم الأوسي من بني ضيعة بن زيد بن مالك بن عمرو بن عوف غسيل الملائكة، استشهد بأحد قتله شداد بن الأسود حدثنا سليمان بن أحمد، ثنا محمد بن عمرو بن خالد، ثنا أبي، ثنا ابن لهيعة، عن أبي الأسود، عن عروة،','2864','829','0');
INSERT INTO [t10490] VALUES (834,'حنظلة بن الربيع بن المرقع بن صيفي الأسيدي التميمي كاتب النبي صلى الله عليه وسلم وهو ابن أخي أكثم بن صيفي روى عنه أبو عثمان النهدي ويزيد بن الشخير والمرقع بن صيفي والهيثم بن حنش','2869','829','0');
INSERT INTO [t10490] VALUES (835,'حنظلة بن حذيم بن حنيفة المالكي','2875','829','0');
INSERT INTO [t10490] VALUES (836,'حنظلة بن أبي حنظلة الأنصاري من أهل قباء، أخرجه البخاري في الصحابة، حديثه عند زيد بن أبي أنيسة، عن جبلة بن سحيم قال: صليت خلف حنظلة الأنصاري إمام مسجد قباء فقرأ في الركعة الأولى سورة مريم فلما بلغ السجدة سجد','2879','829','0');
