INSERT INTO [t10490] VALUES (997,'خرباق السلمي وقيل: إنه ذو اليدين روى عنه عمران بن حصين، وغيره','3284','972','0');
INSERT INTO [t10490] VALUES (998,'خوط بن عبد العزى ويقال: حوط، بالحاء المهملة','3287','972','0');
INSERT INTO [t10490] VALUES (999,'خوط الأنصاري ذكره بعض المتأخرين، وزعم أنه وهم','3289','972','0');
INSERT INTO [t10490] VALUES (1000,'خليفة بن عدي بن المعلى الأنصاري بدري حدثنا سليمان بن أحمد، ثنا محمد بن عثمان بن أبي شيبة، ثنا ضرار بن صرد، ثنا علي بن هاشم، ثنا محمد بن عبيد الله بن أبي رافع، عن أبيه، في " تسمية من شهد مع علي رضي الله عنه من الأنصار: خليفة بن عدي، من بني بياضة،','3291','972','0');
INSERT INTO [t10490] VALUES (1001,'خليفة أبو سهيل، وهو أبو أبي سوية تقدم ذكره فيمن اسمه محمد، ولا يصح له صحبة','3292','972','0');
INSERT INTO [t10490] VALUES (1002,'خصفة، أو ابن خصفة مجهول','3293','972','0');
INSERT INTO [t10490] VALUES (1003,'خير أسلم في عهد النبي صلى الله عليه وسلم وذهب إليه، وقيل: اسمه عبد خير','3295','972','0');
INSERT INTO [t10490] VALUES (1004,'باب الدال','3297','18','0');
INSERT INTO [t10490] VALUES (1005,'داود بن بلال بن بليل وقيل: ابن أحيحة أبو ليلي الأنصاري روى عنه ابنه عبد الرحمن وداود، وهو وهم قاله الصائغ، عن الحلواني، وهو المشهور، مختلف في اسمه: قيل: سفيان وقيل: يسار قتل بصفين هو وابنه عبد الله','3298','1004','0');
INSERT INTO [t10490] VALUES (1006,'ديلم بن فيروز الحميري وقيل: هو فيروز، وديلم لقب، وهو فيروز بن يسع بن سعد بن ذي حباب بن مسعود بن عز بن شحر بن هوشع بن موهب بن سعد بن جبل بن نمران بن الحارث بن حبران، وحبران هو جيشان بن وائل بن رعين الرعيني وفد مع معاذ بن جبل على النبي صلى الله عليه','3300','1004','0');
INSERT INTO [t10490] VALUES (1007,'دحية بن خليفة الكلبي كان جبريل عليه السلام، يأتي في الأحايين النبي صلى الله عليه وسلم متصورا في صورته روى عنه: الشعبي، وعبد الله بن شداد بن الهاد، ومنصور الكلبي، وخالد بن يزيد بن معاوية','3306','1004','0');
INSERT INTO [t10490] VALUES (1008,'دغفل بن حنظلة الشيباني نسابة العرب، من بني عمرو بن شيبان، سدوسي، ذهلي، حديثه عند الحسن، وابن سيرين، مختلف في صحبته، قال الإمام أحمد بن حنبل رحمه الله: ما أرى له صحبة، وقال البخاري: لا يعرف لدغفل أنه أدرك النبي صلى الله عليه وسلم','3312','1004','0');
INSERT INTO [t10490] VALUES (1009,'دكين بن سعيد المزني وقيل: إنه خثعمي، سكن الكوفة، روى عنه قيس بن أبي حازم','3315','1004','0');
INSERT INTO [t10490] VALUES (1010,'دينار الأنصاري جد عدي بن ثابت الأنصاري قال يحيى بن معين: هو دينار وقال غير يحيى: اسمه قيس الخطمي، حديثه عند أبي اليقظان عثمان بن عمير','3317','1004','0');
INSERT INTO [t10490] VALUES (1011,'دخان أبو شعبة ذكره بعض المتأخرين، وذكر أنه لا يصح له رؤية ولا صحبة، وفي إسناد حديثه وهم','3319','1004','0');
INSERT INTO [t10490] VALUES (1012,'درهم أبو معاوية','3321','1004','0');
INSERT INTO [t10490] VALUES (1013,'درهم أبو زياد','3323','1004','0');
INSERT INTO [t10490] VALUES (1014,'دارم بن أبي دارم الجرشي في إسناد حديثه نظر','3325','1004','0');
INSERT INTO [t10490] VALUES (1015,'دعامة بن عزيز بن عمرو بن ربيعة ابن عمران بن الحارث السدوسي، والد قتادة، نسبه عمرو بن علي، ولا يصح له صحبة','3327','1004','0');
INSERT INTO [t10490] VALUES (1016,'دوس مولى رسول الله صلى الله عليه وسلم له ذكر في حديث','3330','1004','0');
INSERT INTO [t10490] VALUES (1017,'دليم سأل النبي صلى الله عليه وسلم عن بعض الأشربة حديثه عند يزيد بن أبي حبيب، عن أبي الخير ذكره الحسن بن سفيان في الوحدان من الصحابة','3332','1004','0');
INSERT INTO [t10490] VALUES (1018,'دلجة بن قيس ذكره بعض المتأخرين وقال: لا يصح له رؤية ولا صحبة وقال: روى حديثه المسيب بن واضح، عن ابن المبارك، عن سليمان التيمي','3334','1004','0');
INSERT INTO [t10490] VALUES (1019,'دهر بن أخرم بن مالك ابن أمية بن يقظة بن خزيمة بن مالك بن سلامان بن أسلم ذكره البخاري في الصحابة، ولا أعرف له رواية','3337','1004','0');
INSERT INTO [t10490] VALUES (1020,'باب الذال','3338','18','0');
INSERT INTO [t10490] VALUES (1021,'من اسمه ذؤيب','3339','1020','0');
INSERT INTO [t10490] VALUES (1022,'ذؤيب بن قبيصة الخزاعي أبو قبيصة بن ذؤيب الفقيه سكن " المدينة "، وقيل: ذؤيب بن حبيب الأسلمي، من بني مالك بن أفصى، أخو أسلم، وقيل: هو ذؤيب بن حلحة بن عمرو بن كعب روى عنه عبد الله بن عباس، ولذؤيب بالمدينة دار، وبقي إلى خلافة معاوية رضي الله عنه،','3340','1021','0');
INSERT INTO [t10490] VALUES (1023,'ذؤيب بن شعثم بن قرط بن مناف ابن حبان بن الحارث بن جهمة، وقيل: ابن خزيمة بن عدي بن خبيب بن عنبر بن عمرو بن تميم العنبري حدثنا بنسبته علي بن أحمد المصيصي، قال: ثنا الحسن بن علي بن عمر البغدادي، ثنا عطاء بن خالد بن الزبير بن عبد الله بن رديح بن ذؤيب بن','3343','1021','0');
INSERT INTO [t10490] VALUES (1024,'*','3347','1020','0');
INSERT INTO [t10490] VALUES (1025,'ذكوان بن عبد قيس بن خالد بن عامر بن زريق الأنصاري، بدري عقبي، استشهد يوم أحد وكان أحد من خرج من المدينة إلى مكة مهاجرا إلى الله يكنى: أبا السبع، وقال له النبي صلى الله عليه وسلم: " من أحب أن ينظر إلى رجل يطأ بقدمه غدا خضرة الجنة فلينظر إليه "، وكان','3347','1024','0');
INSERT INTO [t10490] VALUES (1026,'ذكوان: مولى رسول الله صلى الله عليه وسلم مختلف في اسمه، فقيل: طهمان وقيل: مهران','3349','1024','0');
INSERT INTO [t10490] VALUES (1027,'ذو اليدين ويقال اسمه الخرباق، ويكنى أبا العريان، كان ينزل بذي خشب، من ناحية البصرة','3352','1024','0');
INSERT INTO [t10490] VALUES (1028,'ذو الشمالين ابن عبد عمرو بن نضلة من " خزاعة "، حليف بني زهرة، قاله الزهري وقال محمد بن إسحاق: ذو الشمالين ابن عبد بن عمرو بن نضلة بن غيشان، قتل ببدر، وذو الشمالين غير ذي اليدين؛ لأن ذا اليدين سليمي سكن " وادي القرى " يقال له: الخرباق حدثنا فاروق','3355','1024','0');
INSERT INTO [t10490] VALUES (1029,'ذو الأصابع','3356','1024','0');
INSERT INTO [t10490] VALUES (1030,'ذو الزوائد له صحبة، عداده في المدنيين، وقيل: إنه هو ذو الأصابع الذي تقدم ذكره','3359','1024','0');
INSERT INTO [t10490] VALUES (1031,'ذو اللحية الكلابي ابن عمرو بن قرط بن أبي بكر بن عبد الله بن كلاب','3361','1024','0');
INSERT INTO [t10490] VALUES (1032,'ذو الغرة الجهني وقيل: إن اسمه يعيش','3364','1024','0');
INSERT INTO [t10490] VALUES (1033,'ذو الجوشن الضبابي يكنى: أبا شمر، من بني الضباب بن كنانة بن ربيعة بن عامر بن صعصعة، وذكر المنيعي، عن الواقدي أن اسمه عثمان بن نوفل، وقال عبد الله بن المبارك: عن يونس بن أبي إسحاق، عن أبي إسحاق، قال: ذو الجوشن اسمه: شرحبيل، وسمي ذا الجوشن من أجل','3366','1024','0');
INSERT INTO [t10490] VALUES (1034,'ذو مخمر: وقيل: ذو مخبر ابن أخي النجاشي الحبشي خادم النبي صلى الله عليه وسلم حدث عنه: أبو حي المؤذن، وجبير بن نفير، والعباس بن عبد الرحمن، وأبو الزاهرية، وعمرو بن عبد الله الحضرمي، ويحيى بن أبي عمرو السيباني حدثنا أبو بكر بن مالك، ثنا عبد الله بن','3368','1024','0');
INSERT INTO [t10490] VALUES (1035,'ذو مهدم، وذو جدن، وذو مناح','3375','1024','0');
INSERT INTO [t10490] VALUES (1036,'ذو الكلاع، وذو حوشب كانا في عهد النبي صلى الله عليه وسلم ولم يرياه، أعتق ذو الكلاع اثني عشر ألف بيت','3377','1024','0');
INSERT INTO [t10490] VALUES (1037,'ذابل بن طفيل بن عمرو الدوسي أتى النبي صلى الله عليه وسلم','3379','1024','0');
INSERT INTO [t10490] VALUES (1038,'باب الراء','3381','18','0');
INSERT INTO [t10490] VALUES (1039,'*','3382','1038','0');
INSERT INTO [t10490] VALUES (1040,'رافع بن مالك بن عجلان بن عمرو بن عامر بن زريق بن عبد حارثة بن مالك بن غضب بن جشم بن الخزرج عقبي، نقيب، أحد الستة، وأحد الاثني عشر، وأحد السبعين، شهدا المشاهد الثلاثة من " العقبة "، وبايع بها، كان هو ومعاذ ابن عفراء أول أنصاريين أسلما من الخزرج','3382','1039','0');
INSERT INTO [t10490] VALUES (1041,'رافع أبو البهي مولى رسول الله صلى الله عليه وسلم، له ذكر في حديث عبد الله بن عمرو بن العاص، قيل: إنه كان مملوكا لسعيد بن العاص وغيره من شركائه فأعتق كل واحد منهم نصيبه إلا رجلا، فأتى النبي صلى الله عليه وسلم يستشفع به على الرجل فوهب الرجل نصيبه للنبي','3386','1039','0');
INSERT INTO [t10490] VALUES (1042,'رافع مولى عائشة رضي الله عنهما كان يخدم النبي صلى الله عليه وسلم','3388','1039','0');
INSERT INTO [t10490] VALUES (1043,'رافع، وأسلم، حاديا النبي صلى الله عليه وسلم تقدم ذكره مع أسلم روى إسحاق بن سليمان، عن سعيد بن عبد الرحمن المدني قال: كان رافع وأسلم حاديين للنبي صلى الله عليه وسلم','3390','1039','0');
INSERT INTO [t10490] VALUES (1044,'رافع بن خديج أبو عبد الله وهو رافع بن خديج بن رافع بن عدي بن زيد بن جشم بن حارثة بن حارث بن الخزرج، كان يخضب بالصفرة ويحفي شاربه، وكان عريف قومه، استصغر عن بدر وأجيز يوم أحد وكان يعد في الرماة، أصيب بسهم يوم أحد في ثندوته فبقيت الحديدة في ثندوته','3391','1039','0');
INSERT INTO [t10490] VALUES (1045,'رافع بن عمرو الغفاري أخو الحكم بن عمرو سكن " البصرة "، حديثه عند عبد الله بن الصامت','3407','1039','0');
INSERT INTO [t10490] VALUES (1046,'رافع بن سنان الأوسي الأنصاري أبو الحكم جد عبد الحميد بن جعفر','3410','1039','0');
INSERT INTO [t10490] VALUES (1047,'رافع بن عمرو بن عويم بن زيد ابن رواحة بن زيد بن عدي المزني، حديثه عند عمرو بن سليم المزني، وهلال بن عامر، يعد في البصريين','3412','1039','0');
INSERT INTO [t10490] VALUES (1048,'رافع بن يزيد الثقفي عداده في البصريين، روى عنه الحسن بن أبي الحسن مختلف فيه','3415','1039','0');
INSERT INTO [t10490] VALUES (1049,'رافع بن مكيث الجهني: شهد الحديبية','3417','1039','0');
INSERT INTO [t10490] VALUES (1050,'رافع بن المعلى أبو سعيد الأنصاري وقيل: اسمه الحارث، حديثه عند حفص بن عاصم، وعبيد بن حنين، وابنه سعيد حدثنا سليمان بن أحمد، ثنا محمد بن عبد الله الحضرمي، قال: سمعت محمد بن عبد الله بن نمير، يقول: أبو سعيد بن المعلى، حدثني رجل من ولده أن اسمه،','3419','1039','0');
INSERT INTO [t10490] VALUES (1051,'رافع بن المعلى الأنصاري من بني حبيب بن عبد حارثة بن مالك، استشهد يوم بدر، مع رسول الله صلى الله عليه وسلم','3421','1039','0');
INSERT INTO [t10490] VALUES (1052,'رافع بن سهل بن زيد الأنصاري الأوسي من بني عبد الأشهل، وقيل: رافع بن زيد، وقيل: ابن يزيد حدثنا فاروق الخطابي، قال: ثنا زياد بن الخليل، ثنا إبراهيم بن المنذر، ثنا محمد بن فليح، عن موسى بن عقبة، عن ابن شهاب في تسمية من شهد بدرا مع رسول الله صلى','3423','1039','0');
INSERT INTO [t10490] VALUES (1053,'رافع بن الحارث بن سواد بن زيد بن ثعلبة الأنصاري، بدري حدثنا فاروق، ثنا زياد بن الخليل، ثنا إبراهيم بن المنذر، ثنا محمد بن فليح، ثنا موسى بن عقبة، عن ابن شهاب، في تسمية من شهد بدرا، من الأنصار، من بني النجار: رافع بن الحارث بن سواد حدثنا سليمان','3424','1039','0');
INSERT INTO [t10490] VALUES (1054,'رافع بن عنجدة الأنصاري: بدري من بني أمية بن زيد، وقيل: ابن عنترة، وقيل: عنجرة حدثنا فاروق، ثنا زياد بن الخليل، ثنا إبراهيم بن المنذر، ثنا محمد بن فليح، ثنا موسى بن عقبة، عن ابن شهاب، في تسمية من شهد بدرا، من الأنصار من الأوس، ثم من بني عمرو','3425','1039','0');
INSERT INTO [t10490] VALUES (1055,'رافع بن جعدبة الأنصاري: بدري','3426','1039','0');
INSERT INTO [t10490] VALUES (1056,'رافع بن ثابت أكل مع النبي صلى الله عليه وسلم رطبا، يعد في المصريين ذكره بعض المتأخرين من حديث ابن لهيعة، عن عراك بن يزيد، عن بكر بن سوادة، عن شيخ سمع رافع بن ثابت، ووهم فيه، وصوابه: رويفع بن ثابت','3428','1039','0');
INSERT INTO [t10490] VALUES (1057,'رافع بن بديل بن ورقاء الخزاعي قتل ببئر معونة شهيدا، له ولإخوته عبد الله، وعبد الرحمن، وسلمة صحبة، وهم فيه بعض المتأخرين وصحف، وإنما هو نافع بن بديل، لا يختلف فيه قال فيه ابن رواحة يبكيه: رحم الله نافع بن بديل رحمة المبتغي ثواب الجهاد قال الشيخ:','3429','1039','0');
INSERT INTO [t10490] VALUES (1058,'رافع بن أبي رافع الطائي واسم أبي رافع عميرة وقيل: عمرو، ورافع يكنى: أبا الحسن، سنبسي من طيئ، زعمت طيئ أن الذئب كلمه في ضأن له يرعاها، فدعاه الذئب إلى اللحوق برسول الله صلى الله عليه وسلم','3430','1039','0');
INSERT INTO [t10490] VALUES (1059,'رافع مولى سعد سكن المدينة، ذكره البخاري في الصحابة','3433','1039','0');
INSERT INTO [t10490] VALUES (1060,'رافع بن عمير','3435','1039','0');
INSERT INTO [t10490] VALUES (1061,'رويفع بن ثابت الأنصاري يعد في المصريين، حديثه عند حنش الصنعاني، ووفاء بن شريح، وأبي الخير، وسحيم، وغيرهم حدثنا أبو محمد بن حيان، ثنا الفضل بن العباس، ثنا يحيى بن بكير، عن الليث بن سعد، قال: وفي سنة ست وأربعين أمر رويفع بن ثابت على أطرابلس مدينة','3437','1039','0');
INSERT INTO [t10490] VALUES (1062,'رفيع أبو العالية أدرك أيام النبي صلى الله عليه وسلم، وقيل: اسمه زياد بن فيروز، مولى بني رياح','3451','1039','0');
INSERT INTO [t10490] VALUES (1063,'باب من اسمه رفاعة','3454','1038','0');
INSERT INTO [t10490] VALUES (1064,'رفاعة بن رافع بن مالك بن العجلان بن عمرو بن عامر بن زريق بن عبد حارثة بن مالك بن عصب بن جشم بن الخزرج عقبي بدري، يكنى: أبا معاذ، توفي في ولاية معاوية','3455','1063','0');
INSERT INTO [t10490] VALUES (1065,'ومما أسند','3457','1063','0');
INSERT INTO [t10490] VALUES (1066,'رفاعة بن عبد المنذر بن الزبير وقيل: زبر بن زيد بن أمية بن زيد بن مالك بن عمرو بن عوف بن مالك بن الأوس أبو لبابة، بدري بسهمه، مختلف في اسمه: فقيل: رفاعة، وقيل: بشير، وقيل: يسير، خرج إلى بدر فرده النبي صلى الله عليه وسلم إلى المدينة أميرا عليها،','3463','1063','0');
INSERT INTO [t10490] VALUES (1067,'رفاعة بن عبد المنذر بن رفاعة بن دينار الأنصاري، عقبي، بدري حدثنا سليمان بن أحمد، ثنا محمد بن عمرو بن خالد الحراني، حدثني أبي، ثنا ابن لهيعة، عن أبي الأسود، عن عروة، في تسمية من شهد العقبة، من الأنصار، ثم من بني ظفر واسم ظفر كعب بن الخزرج:','3471','1063','0');
INSERT INTO [t10490] VALUES (1068,'رفاعة بن عرابة الجهني عداده في أهل الحجاز، حديثه عند عطاء بن يسار','3472','1063','0');
INSERT INTO [t10490] VALUES (1069,'رفاعة بن أوس الأنصاري استشهد يوم أحد','3476','1063','0');
INSERT INTO [t10490] VALUES (1070,'رفاعة بن عمرو بن نوفل بن عبد الله بن سنان، استشهد يوم أحد عقبي، بدري، وخرج مهاجرا من المدينة إلى رسول الله صلى الله عليه وسلم.','3478','1063','0');
INSERT INTO [t10490] VALUES (1071,'رفاعة بن سموأل القرظي','3480','1063','0');
INSERT INTO [t10490] VALUES (1072,'رفاعة بن قرظة القرظي','3482','1063','0');
INSERT INTO [t10490] VALUES (1073,'رفاعة بن زيد الجذامي، ثم الضبيبي قدم على النبي صلى الله عليه وسلم زمن الحديبية، فأهدى للنبي صلى الله عليه وسلم غلاما اسمه: مدعم، وكتب لرفاعة كتابا له ذكر في حديث أبي هريرة','3484','1063','0');
INSERT INTO [t10490] VALUES (1074,'رفاعة بن رافع بن عفراء ابن أخي معاذ بن عفراء حديثه عند ابنه معاذ، رواه زيد بن الحباب، عن هشام بن هارون، عنه','3487','1063','0');
INSERT INTO [t10490] VALUES (1075,'رفاعة بن قيس الأشهلي، وقيل: ابن وقش استشهد يوم أحد حدثنا حبيب بن الحسن، ثنا محمد بن يحيى، ثنا أحمد بن محمد بن أيوب، ثنا إبراهيم بن سعد، عن محمد بن إسحاق، في تسمية من قتل يوم أحد، من المسلمين، من الأنصار، من بني عبد الأشهل: رفاعة بن قيس','3489','1063','0');
INSERT INTO [t10490] VALUES (1076,'رفاعة بن مسموح، وقيل: مسروح من بني غنم بن ذودان بن أسد، استشهد يوم خيبر حدثنا حبيب بن الحسن، ثنا محمد بن يحيى، ثنا أحمد بن محمد بن أيوب، ثنا إبراهيم بن سعد، عن محمد بن إسحاق، في تسمية من استشهد من المسلمين يوم خيبر: رفاعة بن مسموح','3490','1063','0');
INSERT INTO [t10490] VALUES (1077,'رفاعة بن يثربي أبو رمثة التيمي: تيم الرباب مختلف في اسمه: فقال أحمد بن حنبل: اسمه رفاعة، وقال يحيى بن معين: اسمه يثربي بن عوف وقيل: حبيب، وقيل: خشخاش','3491','1063','0');
INSERT INTO [t10490] VALUES (1078,'رفاعة غير منسوب روى عنه أبو سلمة بن عبد الرحمن، ذكره بعض المتأخرين من حديث علي بن ثابت','3493','1063','0');
INSERT INTO [t10490] VALUES (1079,'باب من اسمه ربيعة','3495','1038','0');
INSERT INTO [t10490] VALUES (1080,'ربيعة بن الحارث بن عبد المطلب بن هاشم يكنى أبا أروى، وأم ربيعة اسمها عزة بنت قيس بن طريف من ولد الحارث بن فهر، ولدت ربيعة، ونوفلا، وأبا سفيان بني الحارث بن عبد المطلب، ونوفل يكنى: أبا الحارث، ثلاثتهم إخوة لأب وأم أبوهم الحارث، وأمهم عزة، توفي','3496','1079','0');
INSERT INTO [t10490] VALUES (1081,'ربيعة بن كعب أبو فراس الأسلمي يعد في الحجازيين، حديثه عند أبي سلمة بن عبد الرحمن، وحنظلة بن عمرو الأسلمي، وأبي عمران الجوني','3499','1079','0');
INSERT INTO [t10490] VALUES (1082,'ربيعة بن عباد وقيل: ابن عياد، وقيل: ابن عباد الديلي من بني الديل بن بكر بن عبد مناة بن كنانة، حجازي، حديثه عند محمد بن المنكدر، وزيد بن أسلم، وأبي الزناد، وسعيد بن خالد القارظي، وحسين بن عبد الله بن عبيد الله بن عباس','3505','1079','0');
INSERT INTO [t10490] VALUES (1083,'ربيعة بن عامر بن بجاد يعد في أهل فلسطين، حديثه عند يحيى بن حسان','3511','1079','0');
INSERT INTO [t10490] VALUES (1084,'ربيعة بن عمرو بن عمير بن عوف الثقفي أخو مسعود وعبد ياليل وخبيب، فيهم نزلت: وإن تبتم فلكم رءوس أموالكم','3514','1079','0');
INSERT INTO [t10490] VALUES (1085,'ربيعة بن شرحبيل ابن حسنة أبو جعفر، ذكره المحيل، ذكره على أبي سعيد بن يونس بن عبد الأعلى أنه رأى النبي صلى الله عليه وسلم، وشهد فتح مصر، روى عنه ابنه جعفر حدثناه عن أحمد بن الحسن بن عتبة، ثنا يحيى بن عثمان بن صالح، ثنا أبي، عن ابن لهيعة، عن جعفر','3516','1079','0');
INSERT INTO [t10490] VALUES (1086,'ربيعة بن أمية بن خلف الجمحي','3517','1079','0');
INSERT INTO [t10490] VALUES (1087,'ربيعة بن السكن: أبو رويحة الفزعي يعد في أهل فلسطين، قاله موسى بن سهل الرملي، وذكر أنه من الصحابة','3519','1079','0');
INSERT INTO [t10490] VALUES (1088,'ربيعة بن الغاز الجرشي يعد في الشاميين، ويقال: ابن عمرو جد هشام بن الغاز، حديثه عند عطية بن قيس، والحارث بن يزيد، وبشير بن كعب، وابنه الغاز، كان يفتي الناس في زمن معاوية رضي الله عنه','3521','1079','0');
INSERT INTO [t10490] VALUES (1089,'ربيعة القرشي غير منسوب، ذكره بعض المتأخرين وأراه ربيعة بن عباد الديلي الذي تقدم ذكره','3524','1079','0');
INSERT INTO [t10490] VALUES (1090,'ربيعة بن لهيعة الحضرمي وفد على النبي صلى الله عليه وسلم وكتب له كتابا','3526','1079','0');
INSERT INTO [t10490] VALUES (1091,'ربيعة بن الفضل بن حبيب الأنصاري استشهد يوم أحد','3528','1079','0');
INSERT INTO [t10490] VALUES (1092,'ربيعة بن أكثم بن سخبرة بن عمرو بن بكير بن عامر بن غنم بن ذودان بن أسد بن خزيمة الأسدي، أسد خزيمة حليف بني أمية بن عبد شمس استشهد بخيبر، وشهد بدرا حدثنا فاروق الخطابي، ثنا زياد بن الخليل، ثنا إبراهيم بن المنذر، ثنا محمد بن فليح، عن موسى بن عقبة، عن','3530','1079','0');
INSERT INTO [t10490] VALUES (1093,'ربيعة بن عبدان الكندي ويقال: الحضرمي خاصم امرأ القيس في أرضه، له ذكر في حديث وائل بن علقمة بن وائل بن حجر الحضرمي','3532','1079','0');
INSERT INTO [t10490] VALUES (1094,'ربيعة بن فراس روى عنه زياد بن نعيم، يعد في المصريين، ذكره بعض المتأخرين، وزعم أنه من الصحابة','3534','1079','0');
