INSERT INTO [t10490] VALUES (1100,'ربيع بن زيد، غير منسوب وقيل: ربيعة بن يزيد السلمي، وقيل: ابن زياد','3546','1079','0');
INSERT INTO [t10490] VALUES (1101,'ربيع الجرمي أبو سوادة','3548','1079','0');
INSERT INTO [t10490] VALUES (1102,'ربيع بن إياس بن غنم بن لوذان بن غنم بن عوف بن الخزرج الأنصاري شهد بدرا حدثنا فاروق الخطابي، ثنا زياد بن الخليل، ثنا إبراهيم بن المنذر، ثنا محمد بن فليح، ثنا موسى بن عقبة، عن ابن شهاب، في تسمية من شهد بدرا، من الأنصار، من بني عوف بن الخزرج، ثم من','3550','1079','0');
