INSERT INTO [t10490] VALUES (1104,'ربعي بن عمرو الأنصاري','3552','1079','0');
INSERT INTO [t10490] VALUES (1105,'وربعي بن رافع الأنصاري، البدريان من بني عمرو بن عوف حدثنا سليمان بن أحمد، ثنا محمد بن عثمان بن أبي شيبة، ثنا ضرار بن صرد، ثنا علي بن هاشم، عن محمد بن عبيد الله بن أبي رافع، عن أبيه، في تسمية من شهد مع علي رضي الله عنه، من أصحاب رسول الله صلى الله','3553','1079','0');
INSERT INTO [t10490] VALUES (1106,'ربعي بن أبي ربعي الأنصاري، بدري وهو: ربعي بن رافع بن الحارث بن زيد بن حارثة بن الجد بن العجلان حدثنا فاروق الخطابي، ثنا زياد بن الخليل، ثنا إبراهيم بن المنذر، ثنا محمد بن فليح، ثنا موسى بن عقبة، عن ابن شهاب، في تسمية من شهد بدرا من الأنصار، ثم','3554','1079','0');
INSERT INTO [t10490] VALUES (1107,'*','3555','1038','0');
INSERT INTO [t10490] VALUES (1108,'رباح بن الربيع بن مرقع بن صيفي الأسيدي أخو حنظلة الكاتب الأسيدي التميمي، حديثه عند المرقع بن صيفي، وقيس بن زهير، وقيل: رياح، وهو وهم','3555','1107','0');
INSERT INTO [t10490] VALUES (1109,'رباح بن قصير اللخمي من بني القشيب من شرقية مصر، أدرك النبي صلى الله عليه وسلم وأسلم زمن أبي بكر حين قدم حاطب بن أبي بلتعة رسولا من أبي بكر رضي الله عنه إلى المقوقس، فنزل عليهم بركوب قرية من قرى مصر، وهو جد موسى بن علي','3560','1107','0');
INSERT INTO [t10490] VALUES (1110,'رباح الأسود مولى رسول الله صلى الله عليه وسلم كان بوابه حين آلى عن نسائه واعتزل عنهن وهو في العلية يستأذن لعمر وللناس عليه','3563','1107','0');
INSERT INTO [t10490] VALUES (1111,'رباح مولى أم سلمة','3567','1107','0');
INSERT INTO [t10490] VALUES (1112,'رباح بن المغترف بن حجوان بن عمرو بن شيبان بن محارب بن فهر، أبو حسان القرشي، كان يحسن غناء النصب، غنى عبد الرحمن بن عوف في طريق " مكة " فأنكر عليه عمر، فقال: إن كنت قائلا فعليك بشعر ضرار، ذكره بعض المتأخرين في الصحابة، ولا أعلم له صحبة، ولم يذكره','3569','1107','0');
INSERT INTO [t10490] VALUES (1113,'رباح أبو عبدة يروي عنه ابنه عبدة، غير منسوب، عداده في الشاميين، ذكره بعض المتأخرين، ولم يخرج له شيئا','3571','1107','0');
INSERT INTO [t10490] VALUES (1114,'رباح مولى بني جحجبي حدثنا فاروق الخطابي، ثنا زياد بن الخليل، ثنا إبراهيم بن المنذر، ثنا محمد بن فليح، ثنا موسى بن عقبة، عن ابن شهاب، في تسمية من استشهد من المسلمين من الأنصار يوم اليمامة: رباح مولى بني جحجبي حدثنا حبيب بن الحسن، ثنا محمد بن يحيى','3572','1107','0');
INSERT INTO [t10490] VALUES (1115,'روح بن زنباع بن سلامة الجذامي له من النبي صلى الله عليه وسلم إدراك، ولا يصح له صحبة، ولأبيه زنباع رؤية، يكنى أبا زرعة، يعد في المصريين، حديثه عند عبيدة بن عبد الرحمن، وابنه سلمة بن روح','3573','1107','0');
INSERT INTO [t10490] VALUES (1116,'روح بن يسار، أو يسار بن روح','3575','1107','0');
INSERT INTO [t10490] VALUES (1117,'ركانة بن عبد يزيد بن هاشم بن المطلب بن عبد مناف بن قصي بن كلاب بن مرة بن لؤي بن غالب بن فهر وأمه العجلة بنت العجلان بن البياع، من بني سعد بن ليث بن بكر بن كنانة، صارعه النبي صلى الله عليه وسلم فأسلم، وسكن المدينة، فبقي إلى زمن عثمان، روى عنه ابن','3577','1107','0');
INSERT INTO [t10490] VALUES (1118,'ركانة أبو محمد غير منسوب، فرق بعض المتأخرين بينه وبين الأول، وما أراه إلا المتقدم','3582','1107','0');
INSERT INTO [t10490] VALUES (1119,'رقاد بن ربيعة العقيلي أدرك النبي صلى الله عليه وسلم','3584','1107','0');
INSERT INTO [t10490] VALUES (1120,'رشيد بن مالك أبو عميرة السعدي عداده في الكوفيين، حديثه عند حفصة بنت طلق','3588','1107','0');
INSERT INTO [t10490] VALUES (1121,'رشيد الهجري وقيل: الفارسي، مولى بني معاوية، لا يثبت له صحبة، ذكره بعض المتأخرين','3590','1107','0');
INSERT INTO [t10490] VALUES (1122,'راشد بن حفص وقيل: ابن عبد ربه السلمي، ذكره مسلم بن الحجاج في الصحابة، أبو أثيلة، كان اسمه ظالما فسماه النبي صلى الله عليه وسلم راشدا وأقطعه أرضا برهاط وهو كاسر الصنم الذي يدعى سواعا','3592','1107','0');
INSERT INTO [t10490] VALUES (1123,'راشد بن حبيش عداده في الشاميين، ذكره الإمام أحمد بن حنبل رحمه الله في الصحابة، مختلف في صحبته','3596','1107','0');
INSERT INTO [t10490] VALUES (1124,'رشدان الجهني سأله النبي صلى الله عليه وسلم عن اسمه، فقال: غيبان، فقال: " أنت رشدان " ذكره بعض المتأخرين','3598','1107','0');
INSERT INTO [t10490] VALUES (1125,'رزين بن أنس السلمي عداده في البصريين، حديثه عند مطرف، سكن البادية','3600','1107','0');
