INSERT INTO [t10490] VALUES (1127,'رومان الرومي وهو سفينة واسم سفينة عمير مولى أم سلمة من سبي بلخ وولاه النبي صلى الله عليه وسلم فيما ذكره بعض المتأخرين، وقال: من سبي بلخ، ومن سبي من بلخ لا ينسب إلى الروم، وبلخ والروم لم يفتحا في عهد النبي صلى الله عليه وسلم فكيف يسبى منه؟','3604','1107','0');
INSERT INTO [t10490] VALUES (1128,'رقيم بن ثابت بن ثعلبة بن زيد بن لوذان بن معاوية الأوسي استشهد بالطائف حدثنا سليمان بن أحمد، ثنا محمد بن عمرو بن خالد، حدثني أبي، ثنا ابن لهيعة، عن أبي الأسود، عن عروة بن الزبير، في تسمية من استشهد يوم الطائف، من الأنصار ثم من بني عمرو بن عوف، ثم','3607','1107','0');
INSERT INTO [t10490] VALUES (1129,'رسيم العبدي من أهل هجر وكان فقيها','3608','1107','0');
INSERT INTO [t10490] VALUES (1130,'رديح بن ذؤيب العنبري عتيق عائشة أم المؤمنين رضي الله عنها، برك عليه النبي صلى الله عليه وسلم ومسح رأسه','3610','1107','0');
INSERT INTO [t10490] VALUES (1131,'رقيبة بن عقيبة، أو: عقيبة بن رقيبة هكذا روي على الشك، وهو مجهول','3612','1107','0');
INSERT INTO [t10490] VALUES (1132,'رحيلة بن ثعلبة بن خالد الأنصاري، بدري حدثنا فاروق الخطابي، ثنا زياد بن الخليل، ثنا إبراهيم بن المنذر، ثنا محمد بن فليح، عن موسى بن عقبة، عن ابن شهاب، في تسمية من شهد بدرا من الأنصار، من الخزرج، ثم من بني بياضة: رخيلة بن ثعلبة بن خالد حدثنا حبيب','3614','1107','0');
