INSERT INTO [t10490] VALUES (1141,'الزبير بن عبيدة هاجر مع أهله إلى رسول الله صلى الله عليه وسلم، من المهاجرين الأولين، وقيل: إنه من بني غنم بن ذودان بن أسد بن خزيمة','3631','1139','0');
