INSERT INTO [t10490] VALUES (1143,'من اسمه زيد','3635','1138','0');
INSERT INTO [t10490] VALUES (1144,'زيد بن عمرو بن نفيل أبو سعيد أدرك النبي صلى الله عليه وسلم، وسئل عنه فقال: " يبعث يوم القيامة أمة وحده "، وكان يتأله في الجاهلية، ويوحد الله تعالى، ويقول: إلهي إله إبراهيم، وديني دين إبراهيم روى عنه: زيد بن حارثة، وعبد الله بن عمر بن الخطاب،','3636','1143','0');
INSERT INTO [t10490] VALUES (1145,'زيد بن حارثة بن شراحيل بن كعب بن عبد العزى بن زيد بن امرئ القيس بن عامر بن النعمان بن عامر بن عبد ود بن عوف بن كنانة بن بكر بن عوف بن عذرة بن زيد اللات بن رفيدة بن كلب بن وبرة بن الحارث بن قضاعة. ويقال: إن أمه سعادة بنت زيد، من طيئ، يكنى: أبا أسامة','3642','1143','0');
INSERT INTO [t10490] VALUES (1146,'زيد بن الخطاب بن نفيل بن عبد العزى بن رياح بن عبد الله بن قرط بن رزاح بن عدي بن كعب، بدري، أخو عمر بن الخطاب رضي الله عنهما كان أسن من عمر، يكنى أبا عبد الرحمن، وقيل: أبا ثور، أمه أسماء بنت حبيب بن وهب بن عمرو بن عمير بن نصير بن أسد بن خزيمة، شهد','3659','1143','0');
INSERT INTO [t10490] VALUES (1147,'زيد بن بولي مولى رسول الله صلى الله عليه وسلم','3667','1143','0');
INSERT INTO [t10490] VALUES (1148,'زيد بن سهل بن الأسود بن حرام بن عمرو بن زيد مناة بن عدي بن عمرو بن مالك بن النجار أبو طلحة، عقبي، بدري، نقيب، آخى رسول الله صلى الله عليه وسلم بينه وبين أبي عبيدة بن الجراح وولاه النبي صلى الله عليه وسلم قسمة شعره بين أصحابه، وكان يسرد الصوم بعد','3669','1143','0');
INSERT INTO [t10490] VALUES (1149,'زيد بن ثابت بن الضحاك بن حارثة بن زيد بن ثعلبة من بني سلمة، ثم من بني غنم بن مالك بن النجار، وقيل: زيد بن ثابت بن الضحاك بن زيد بن لوذان بن عمرو بن عبد عوف بن غنم بن مالك بن النجار، ثم أحد بني الخزرج، يكنى أبا سعيد، وقيل: أبو خارجة، وقتل ثابت','3689','1143','0');
INSERT INTO [t10490] VALUES (1150,'زيد بن أسلم بن ثعلبة بن عدي بن العجلان من بني عبيد بن زيد بن مالك، شهد بدرا حدثنا سليمان بن أحمد، ثنا محمد بن عمرو بن خالد، ثنا أبي، ثنا ابن لهيعة، عن أبي الأسود، عن عروة، في تسمية من شهد بدرا من الأنصار: زيد بن أسلم بن ثعلبة بن عدي بن العجلان','3737','1143','0');
INSERT INTO [t10490] VALUES (1151,'زيد بن المزين الأنصاري من بني الحارث بن الخزرج، شهد بدرا، أخو عبد الله، وقيل: زيد بن المزين بن قيس بن عدي بن أمية بن جدارة بن عوف بن الحارث بن الخزرج','3741','1143','0');
INSERT INTO [t10490] VALUES (1152,'زيد بن وديعة بن عمرو بن قيس الخزرجي شهد بدرا','3744','1143','0');
INSERT INTO [t10490] VALUES (1153,'زيد بن الحارث الأنصاري بدري، كذا ذكره عروة بن الزبير','3748','1143','0');
INSERT INTO [t10490] VALUES (1154,'زيد بن المرس الأنصاري بدري، قاله عروة بن الزبير، ووهم فيه بعض الرواة عنه، وهو زيد بن المزين الذي تقدم ذكره','3750','1143','0');
INSERT INTO [t10490] VALUES (1155,'زيد بن أرقم بن قيس بن النعمان بن مالك بن ثعلبة بن الخزرج، وقيل: زيد بن أرقم بن زيد بن قيس، كان في حجر عبد الله بن رواحة، وخرج معه إلى مؤتة، شهد مع النبي صلى الله عليه وسلم سبع عشرة غزوة، رمدت عيناه فعاده النبي صلى الله عليه وسلم فقال: " يا زيد','3752','1143','0');
INSERT INTO [t10490] VALUES (1156,'زيد بن الصامت وقيل: زيد بن النعمان الزرقي أبو عياش، مختلف في اسمه، فقيل: زيد، وقيل: عبيد بن معاوية بن الصامت بن زيد بن خلدة بن عامر بن زريق، وقيل: عبيد بن معاذ بن الصامت، صدقه رسول الله صلى الله عليه وسلم بعد موته فيما حدث عنه، سكن المدينة،','3778','1143','0');
INSERT INTO [t10490] VALUES (1157,'زيد بن خارجة بن أبي زهير الخزرجي شهد بدرا مع رسول الله صلى الله عليه وسلم، توفي في خلافة عثمان، يقال: إنه الذي تكلم على لسانه بعد الموت، به ورد أكثر الروايات، وهو الصحيح، وقيل: إنه كان خارجة بن زيد، وقال الواقدي: هو زيد بن خارجة بن زيد بن أبي','3782','1143','0');
INSERT INTO [t10490] VALUES (1158,'زيد بن لبيد الأنصاري شهد العقبة','3786','1143','0');
INSERT INTO [t10490] VALUES (1159,'زيد بن مربع الأنصاري من بني حارثة، يعد في الحجازيين، حديثه عند: يزيد بن شيبان، قال أحمد بن حنبل ويحيى بن معين: اسم ابن مربع: زيد، سأل يحيى عنه ابن أبي خيثمة، وحكى عن أحمد ابنه صالح','3788','1143','0');
INSERT INTO [t10490] VALUES (1160,'زيد بن جارية الأنصاري الأوسي جد مجمع بن يزيد، شهد خيبر مع رسول الله صلى الله عليه وسلم، فأسهم له، توفي قبل ابن عمر، فترحم عليه لما نعي له','3790','1143','0');
INSERT INTO [t10490] VALUES (1161,'زيد بن ثعلبة بن عبد ربه بن زيد بن الحارث بن الخزرج، روى عنه ابنه عبد الله بن زيد صاحب النداء','3792','1143','0');
INSERT INTO [t10490] VALUES (1162,'زيد بن عبد الله الأنصاري ذكره بعض المتأخرين من حديث يحيى بن بكير عن الليث','3794','1143','0');
INSERT INTO [t10490] VALUES (1163,'زيد بن الدثنة الأنصاري من بني بياضة بن عامر، بعثه النبي صلى الله عليه وسلم في سرية عاصم بن ثابت بن أبي الأقلح، وخبيب، وقتل بمكة بالتنعيم، قتله نسطاس مولى صفوان بن أمية، فقال له أبو سفيان حين قدم ليقتل: أتحب أن محمدا مكانك، يضرب عنقه وأنك في أهلك؟','3796','1143','0');
INSERT INTO [t10490] VALUES (1164,'زيد بن سعنة كان من أحبار اليهود ومن أثراهم مالا، أسلم فحسن إسلامه، شهد مشاهد مع رسول الله صلى الله عليه وسلم، وتوفي في غزوة تبوك','3801','1143','0');
INSERT INTO [t10490] VALUES (1165,'زيد بن ربيعة وقيل: ربعة: قرشي من بني أسد بن عبد العزى، استشهد يوم حنين، قاله ابن لهيعة، عن أبي الأسود، عن عروة وقال ابن إسحاق: هو يزيد بن زمعة بن الأسود بن المطلب بن أسد، جمح به فرس يقال له الجناح، فقتله','3803','1143','0');
INSERT INTO [t10490] VALUES (1166,'زيد بن رقيش حليف بني أمية، استشهد باليمامة، قاله ابن لهيعة، عن أبي الأسود، عن عروة، وقال ابن إسحاق: هو زيد بن قيس، وقال الزهري: يزيد بن رقيش','3805','1143','0');
INSERT INTO [t10490] VALUES (1167,'زيد بن سراقة بن كعب الأنصاري قتل يوم الجسر مع سعد بن أبي وقاص، جسر المدائن سنة خمس عشرة، وأميرهم أبو عبيد بن مسعود الثقفي','3807','1143','0');
INSERT INTO [t10490] VALUES (1168,'زيد بن أسيد بن جارية الزهري استشهد يوم اليمامة، قاله الزهري','3810','1143','0');
INSERT INTO [t10490] VALUES (1169,'زيد بن كعب وقيل: كعب بن زيد، وقيل: سويد بن زيد، له صحبة، ذكر أن النبي صلى الله عليه وسلم تزوج امرأة من بني غفار فرأى بها بياضا، رواه أبو معاوية الضرير، عن جميل بن زيد بن كعب، عن أبيه، وقال بعضهم: عن جده','3812','1143','0');
INSERT INTO [t10490] VALUES (1170,'زيد بن خالد الجهني يكنى: أبا عبد الرحمن، وقيل: أبو محمد، وقيل: أبو طلحة، سكن المدينة وبها مات، شهد الحديبية مع رسول الله صلى الله عليه وسلم، توفي سنة ثمان وسبعين، وله خمس وثمانون سنة، روى عنه من الصحابة: السائب بن يزيد الكندي، والسائب بن خلاد','3813','1143','0');
INSERT INTO [t10490] VALUES (1171,'زيد بن أبي أوفى كان ينزل البصرة','3827','1143','0');
INSERT INTO [t10490] VALUES (1172,'زيد بن معاوية النميري عم قرة بن دعموص، ذكر إسلامه في حديث لقرة بن دعموص','3829','1143','0');
INSERT INTO [t10490] VALUES (1173,'زيد أبو حسن الأنصاري سمع النبي صلى الله عليه وسلم، روى عنه عقبة بن عمرو أبو مسعود. حديثه عند حيوة بن شريح، عن محمد بن عجلان','3831','1143','0');
INSERT INTO [t10490] VALUES (1174,'زيد أبو عبد الله له وفادة إلى رسول الله صلى الله عليه وسلم، روى عنه ابنه عبد الله','3833','1143','0');
INSERT INTO [t10490] VALUES (1175,'زيد الخيل وهو ابن مهلهل بن يزيد بن منهب الطائي، من المؤلفة، أسلم وحسن إسلامه. له ذكر في حديث أبي سعيد الخدري','3835','1143','0');
INSERT INTO [t10490] VALUES (1176,'زيد بن عامر الثقفي من أهل الطائف، حديثه عند عبد العزيز بن قيس، عن حميد، عن أنس، أن زيدا سأل النبي صلى الله عليه وسلم عن النبيذ رواه محمد بن تمام بن عياش عنه','3838','1143','0');
INSERT INTO [t10490] VALUES (1177,'زيد بن كعب البهزي ثم السلمي، أهدى للنبي صلى الله عليه وسلم','3840','1143','0');
INSERT INTO [t10490] VALUES (1178,'زيد أبو عبد الله مجهول','3842','1143','0');
INSERT INTO [t10490] VALUES (1179,'زيد بن خريم مجهول، فيه نظر','3844','1143','0');
INSERT INTO [t10490] VALUES (1180,'زيد بن أبي شيبة أبو شهم روى عنه قيس بن أبي حازم، سماه بعضهم ولا يثبت، نأتي على حديثه في الكنى','3846','1143','0');
INSERT INTO [t10490] VALUES (1181,'زيد الديلمي مولى سهم بن مازن','3847','1143','0');
INSERT INTO [t10490] VALUES (1182,'زيد بن وهب الجهني أسلم في حياة النبي صلى الله عليه وسلم، وهاجر إليه ولم يدركه، وبلغته وفاته في الطريق','3849','1143','0');
INSERT INTO [t10490] VALUES (1183,'زيد بن صوحان العبدي روى عنه شقيق أبو وائل، يكنى أبا عائشة، وقيل: أبا سلمان، قتل يوم الجمل مع علي، أصيبت يده يوم جلولاء، وسبقته إلى الجنة','3851','1143','0');
INSERT INTO [t10490] VALUES (1184,'زيد بن كعابة وزيد بن الأخنس وزيد بن سلمة كلها وهم، وصوابه يزيد وزياد','3854','1143','0');
INSERT INTO [t10490] VALUES (1185,'باب من اسمه زياد','3855','1138','0');
INSERT INTO [t10490] VALUES (1186,'زياد بن لبيد بن ثعلبة بن سنان بن عامر بن عدي بن أمية بن بياضة بن عامر بن زريق بن عبد حارثة بن مالك بن غصب بن جشم بن الخزرج، يكنى أبا عبد الله، شهد العقبة وبدرا روى عنه أبو الدرداء، وعوف بن مالك، وسالم بن أبي الجعد كان أحد عمال رسول الله صلى الله عليه','3856','1185','0');
INSERT INTO [t10490] VALUES (1187,'زياد بن الحارث الصدائي وهو حي من اليمن، نزل مصر، حديثه عند زياد بن نعيم الحضرمي','3863','1185','0');
INSERT INTO [t10490] VALUES (1188,'زياد بن السكن بن امرئ القيس أبو عمارة الأنصاري، قتل يوم أحد، روى عنه عبد الله بن عباس، ومحمود بن عمرو','3867','1185','0');
INSERT INTO [t10490] VALUES (1189,'زياد بن عبد الله الأنصاري يعد في الكوفيين، روى عنه الشعبي','3869','1185','0');
