INSERT INTO [t10490] VALUES (1191,'زياد الغفاري يعد في المصريين، حديثه عند يزيد بن نعيم','3873','1185','0');
INSERT INTO [t10490] VALUES (1192,'زياد بن جلاس يعد في أعراب البصرة','3875','1185','0');
INSERT INTO [t10490] VALUES (1193,'زياد أبو الأغر النهشلي كان ينزل البصرة','3877','1185','0');
INSERT INTO [t10490] VALUES (1194,'زياد بن عياض الأشعري وقيل: عياض الأشعري، مختلف فيه، لا يصح له صحبة، حديثه عند الشعبي','3879','1185','0');
