INSERT INTO [t10490] VALUES (1198,'زياد بن طارق وقيل: طارق بن زياد، وهو الصواب','3887','1185','0');
INSERT INTO [t10490] VALUES (1199,'زياد بن مطرف ذكره الحضرمي في المفاريد، ولا يصح صحبته','3888','1185','0');
INSERT INTO [t10490] VALUES (1200,'زياد بن جارية التميمي','3889','1185','0');
INSERT INTO [t10490] VALUES (1201,'زياد بن الغرد','3892','1185','0');
INSERT INTO [t10490] VALUES (1202,'زياد بن سمية ادعى أبا سفيان فنسب إليه، أخو أبي بكرة لأمه، يكنى أبا المغيرة، ولد عام الهجرة، واستخلفه أبو موسى الأشعري على البصرة حين وفد إلى عمر بن الخطاب رضي الله عنه، وبعثه أبو موسى أيضا رسولا إلى عمر، كان يعد من الدهاة، توفي سنة ثلاث وخمسين','3894','1185','0');
INSERT INTO [t10490] VALUES (1203,'زياد وقيل: زيادة بن الأخرش بن عمرو الجهني حليف بني ساعدة، وقيل: زياد بن عمرو الجهني','3895','1185','0');
INSERT INTO [t10490] VALUES (1204,'زيادة بن جهور اللخمي نزل فلسطين','3897','1185','0');
INSERT INTO [t10490] VALUES (1205,'*','3899','1138','0');
INSERT INTO [t10490] VALUES (1206,'زبيب بن ثعلبة بن عمرو بن سوار بن الفزاع بن عبدة بن عدي بن جندب بن العنبر العنبري، نزل البصرة، مسح النبي صلى الله عليه وسلم وجهه، ودعا له بالعفو والعافية، أمه: كلثمة بنت برثن العنبرية من بلعنبر','3899','1205','0');
INSERT INTO [t10490] VALUES (1207,'زخى العنبري مولى عائشة من ولد قرط بن مناف بن الحارث بن حباب بن جهمة العنبري، برك عليه النبي صلى الله عليه وسلم ومسح رأسه','3903','1205','0');
INSERT INTO [t10490] VALUES (1208,'من اسمه زهير','3905','1138','0');
INSERT INTO [t10490] VALUES (1209,'زهير بن عمرو الهلالي سكن البصرة، روى عنه أبو عثمان النهدي','3906','1208','0');
INSERT INTO [t10490] VALUES (1210,'زهير بن صرد الجشمي أبو صرد، وقيل: أبو جرول، أحد بني سعد بن بكر روى عنه عبد الله بن عمرو بن العاص، سكن الشام','3908','1208','0');
INSERT INTO [t10490] VALUES (1211,'زهير بن عثمان الثقفي حديثه عند الحسن بن أبي الحسن، يعد في البصريين','3911','1208','0');
INSERT INTO [t10490] VALUES (1212,'زهير بن علقمة وقيل: ابن أبي علقمة، بجلي، سكن الكوفة','3913','1208','0');
