INSERT INTO [t10490] VALUES (1214,'زهير بن أمية وقيل: ابن أبي أمية الهاشمي، وقيل: عبد الله بن أبي أمية','3917','1208','0');
INSERT INTO [t10490] VALUES (1215,'زهير بن معاوية الجشمي يكنى أبا أسامة، شهد الخندق','3919','1208','0');
INSERT INTO [t10490] VALUES (1216,'زهير بن عاصم بن حصين له ذكر في حديث حصين بن مشمت، وقد تقدم ذكره','3920','1208','0');
INSERT INTO [t10490] VALUES (1217,'زهير بن عياض الفهري من بني الحارث بن فهر، قتله مقيس بن صبابة','3921','1208','0');
INSERT INTO [t10490] VALUES (1218,'زهير بن عبد الله الشنوي وقيل: زهير بن أبي جبل، وقيل: محمد بن زهير بن أبي جبل','3923','1208','0');
INSERT INTO [t10490] VALUES (1219,'زهير بن خطامة الكناني خرج وافدا إلى رسول الله صلى الله عليه وسلم فآمن، وسأله أن يحمي له أرضه، أخو الأسود، تقدم ذكره مع حديث أخيه الأسود','3926','1208','0');
INSERT INTO [t10490] VALUES (1220,'زهير الثقفي سمع النبي صلى الله عليه وسلم يقول: " إذا سميتم فعبدوا " رواه أبو أمية بن يعلى، عن عبد الملك بن إبراهيم بن زهير، عن أبيه عن جده زهير بن علقمة، وقيل: زهير بن طهفة','3927','1208','0');
INSERT INTO [t10490] VALUES (1221,'زهير بن طهفة وهما واحد','3928','1208','0');
INSERT INTO [t10490] VALUES (1222,'زاهر بن الأسود أبو مجزأة الأسلمي من أصحاب الشجرة، سكن الكوفة، وكان من أصحاب عمرو بن الحمق، قاله الواقدي وقال محمد بن سعد كاتب الواقدي: هو زاهر بن الأسود بن مخلع بن قيس بن دعبل بن أنس بن خزيمة بن مالك بن سلامان بن أسلم بن أفصى','3930','1208','0');
INSERT INTO [t10490] VALUES (1223,'زاهر بن حرام الأشجعي كان ينزل البادية ناحية الحجاز، وقيل: زاهر بن حرام','3933','1208','0');
INSERT INTO [t10490] VALUES (1224,'*','3935','1138','0');
INSERT INTO [t10490] VALUES (1225,'زرارة بن جزي، وقيل: جري روى عنه المغيرة بن شعبة','3935','1224','0');
INSERT INTO [t10490] VALUES (1226,'زرارة غير منسوب أبو عمرو حديثه عند سعيد بن عمرو بن جعدة المخزومي، عن عمرو بن زرارة، عن أبيه','3937','1224','0');
INSERT INTO [t10490] VALUES (1227,'زرارة بن كريم بن الحارث بن عمرو السهمي رأى النبي صلى الله عليه وسلم في حجة الوداع، وقيل: زرارة بن كرب','3939','1224','0');
INSERT INTO [t10490] VALUES (1228,'زرعة الشقري سماه النبي صلى الله عليه وسلم، ذكره في حديث أسامة بن أخدري','3940','1224','0');
INSERT INTO [t10490] VALUES (1229,'زرعة بن خليفة حديثه عند محمد بن زياد الراسبي','3942','1224','0');
INSERT INTO [t10490] VALUES (1230,'زرعة بن سيف بن ذي يزن قيل اليمن، ذكر أولاده عنه كتاب النبي صلى الله عليه وسلم إليه، وقال محمد بن إسحاق: هو زرعة بن ذي يزن مالك بن مرة الرهاوي، بعث إلى رسول الله صلى الله عليه وسلم بإسلامه وإسلام قومه، ومفارقتهم الشرك وأهله، فكتب إليهم رسول الله صلى','3945','1224','0');
INSERT INTO [t10490] VALUES (1231,'زرعة بن ضمرة العامري من بني عامر بن صعصعة، له ذكر، ولا يصح له صحبة ولا رؤية، روى عنه أبو الأسود الدؤلي','3947','1224','0');
INSERT INTO [t10490] VALUES (1232,'زارع بن عامر العبدي سكن البصرة','3948','1224','0');
INSERT INTO [t10490] VALUES (1233,'زبرقان بن بدر التميمي وفد على رسول الله صلى الله عليه وسلم مع عطارد بن حاجب، وقيس بن عاصم يفاخرونه بخطيبهم وشاعرهم فأسلموا، وكان ينزل ناحية المدينة','3951','1224','0');
INSERT INTO [t10490] VALUES (1234,'الزبرقان بن أسلم من آل ذي لعوة، روى عنه أبو وائل، ولا يصح له صحبة','3955','1224','0');
INSERT INTO [t10490] VALUES (1235,'زنباع بن سلامة الجذامي أبو روح كان ينزل فلسطين، حديثه عند ابنه روح، وعبد الله بن عمرو بن العاص','3957','1224','0');
INSERT INTO [t10490] VALUES (1236,'زفر بن أوس بن الحدثان أخو مالك، يقال: إنه أدرك النبي صلى الله عليه وسلم، ولا يعرف له صحبة، ولا رؤية','3960','1224','0');
INSERT INTO [t10490] VALUES (1237,'زفر بن يزيد بن هاشم بن حرملة له ذكر في حديث زمل بن عمرو العذري، وقيل: ربيعة، وقيل: زميل بن عمرو من بني هند بن حزام، أتى النبي صلى الله عليه وسلم فأخبره بصوت سمعه من بعض الأوثان','3961','1224','0');
INSERT INTO [t10490] VALUES (1238,'باب السين','3963','18','0');
INSERT INTO [t10490] VALUES (1239,'من اسمه سعد','3964','1238','0');
INSERT INTO [t10490] VALUES (1240,'سعد بن أبي وقاص وقد تقدم ذكره في ذكر العشرة، وهو سعد بن مالك بن أهيب بن عبد مناف بن زهرة بن كلاب، رضي الله عنه','3965','1239','0');
INSERT INTO [t10490] VALUES (1241,'سعد بن معاذ بن النعمان بن امرئ القيس بن زيد بن عبد الأشهل بن جشم بن الحارث بن الخزرج بن عمرو بن عوف بن مالك بن الأوس، يكنى أبا عمرو، شهد بدرا وأحدا، واستشهد بالخندق، واهتز لموته عرش الرحمن استبشارا لروحه، رمي في أكحله من عضده، رماه ابن العرقة','3966','1239','0');
INSERT INTO [t10490] VALUES (1242,'سعد بن عبادة سيد بني الخزرج، عقبي بدري أحدي، يكنى أبا ثابت، شهد المشاهد كلها، وكان نقيبا صاحب راية الأنصار في المشاهد، توفي بحوران من أرض الشام سنة ست عشرة، وهو سعد بن عبادة بن دليم ٦٣، وقيل: ٦٣ دلهم بن حارثة بن أبي خزيمة ٦٣، وقيل: ٦٣ ابن حزام','3978','1239','0');
INSERT INTO [t10490] VALUES (1243,'سعد بن الربيع بن عمرو بن أبي زهير بن مالك بن امرئ القيس بن ثعلبة بن كعب بن الحارث بن الخزرج، عقبي بدري أحدي، نقيب، استشهد بأحد، كان النبي صلى الله عليه وسلم آخى بينه وبين عبد الرحمن بن عوف','3991','1239','0');
INSERT INTO [t10490] VALUES (1244,'سعد بن مسعود الأنصاري سكن المدينة، له ذكر في حديث أبي هريرة','4002','1239','0');
INSERT INTO [t10490] VALUES (1245,'سعد بن خيثمة بن الحارث بن مالك بن كعب بن النحاط بن كعب بن حارثة بن غنم بن السلم بن امرئ القيس بن مالك بن الأوس، يكنى أبا خيثمة، وقيل: أبو عبد الله، وقيل: إن أبا خيثمة الذي لحق النبي صلى الله عليه وسلم بتبوك هو أخو بني سالم غيره، عقبي بدري نقيب،','4005','1239','0');
INSERT INTO [t10490] VALUES (1246,'سعد بن عمارة الزرقي أبو سعيد وقيل: عمارة بن سعد','4016','1239','0');
INSERT INTO [t10490] VALUES (1247,'سعد بن مسعود الثقفي له صحبة','4018','1239','0');
INSERT INTO [t10490] VALUES (1248,'سعد بن الفاكه بن زيد من بني خلدة بن عامر، لا يعرف له رواية','4020','1239','0');
INSERT INTO [t10490] VALUES (1249,'سعد بن عبيد بن النعمان بن قيس بن عمرو بن زيد بن أمية القارئ الأنصاري، من بني أمية بن زيد، شهد بدرا، لا عقب له، قاله عروة وابن إسحاق. وقال ابن نمير: قتل يوم القادسية وهو والد عمير بن سعد والي عمر بن الخطاب بالشام، وكان يسمى على عهد النبي صلى الله','4022','1239','0');
INSERT INTO [t10490] VALUES (1250,'سعد بن مالك الأشهلي','4029','1239','0');
INSERT INTO [t10490] VALUES (1251,'سعد بن زيد بن سعد الأشهلي بعثه النبي صلى الله عليه وسلم إلى نجد، أفرد له بعض المتأخرين ترجمة، وهو عندي المتقدم: سعد بن زيد بن مالك','4033','1239','0');
INSERT INTO [t10490] VALUES (1252,'سعد بن أسعد الساعدي أبو سهل بن سعد، روى عنه ابن سهل، توفي بالروحاء متوجها إلى بدر','4035','1239','0');
INSERT INTO [t10490] VALUES (1253,'سعد بن سعد الساعدي أخو سهل بن سعد','4038','1239','0');
INSERT INTO [t10490] VALUES (1254,'سعد بن خولة من بني عامر بن لؤي، شهد بدرا، زوج سبيعة الأسلمية، توفي عنها بمكة في حجة الوداع وهي حامل لسبعة أشهر','4040','1239','0');
INSERT INTO [t10490] VALUES (1255,'سعد بن مالك بن سنان وقيل: ابن عبيد بن ثعلبة بن عبد بن الأبجر بن عوف بن الحارث بن الخزرج أبو سعيد الخدري، كان يسكن المدينة، وبها توفي يوم الجمعة سنة أربع وسبعين، وله عقب، ودفن بالبقيع وهو ابن أربع وتسعين سنة، كان يحفي شاربه ويصفر لحيته. روى عنه من','4045','1239','0');
INSERT INTO [t10490] VALUES (1256,'سعد بن زرارة الأنصاري أخو أسعد بن زرارة، روى عنه ابنه عبد الرحمن، ذكره بعض المتأخرين ووهم فيه','4058','1239','0');
INSERT INTO [t10490] VALUES (1257,'سعد بن جارية بن لوذان بن عبد ود الأنصاري استشهد باليمامة','4060','1239','0');
INSERT INTO [t10490] VALUES (1258,'سعد بن المنذر بن عمير بن عدي بن خرشة بن أمية بن عامر بن خطفة الأنصاري، عقبي بدري أحدي شهد المشاهد، اختلف في قتله، كذا نسبه بعض المتأخرين، ونسبه إلى العقبة وبدر وأحد، ولم أر له ذكرا في كتاب الزهري، ولا ابن إسحاق في العقبة وبدر','4062','1239','0');
INSERT INTO [t10490] VALUES (1259,'سعد بن عائذ القرظ الأنصاري مولى عمار بن ياسر، وكان يتجر في قرظ، فسمي به، مسح رسول الله صلى الله عليه وسلم رأسه وبرك عليه، وجعله مؤذن مسجد قباء خليفة بلال في الأذان إذا غاب بالمدينة، ثم استخلفه بلال أيام عمر لما هاجر إلى الشام، ولم يزل الأذان في','4064','1239','0');
