INSERT INTO [t10490] VALUES (1301,'سعد بن إياس وقيل: سعيد الشيباني البكري، أبو عمرو، أدرك النبي صلى الله عليه وسلم ولم يسمع منه، شهد القادسية وهو ابن أربعين سنة وعاش مائة وعشرين سنة','4162','1239','0');
INSERT INTO [t10490] VALUES (1302,'من اسمه سعيد','4165','1238','0');
INSERT INTO [t10490] VALUES (1303,'سعيد بن زيد بن عمرو بن نفيل العدوي تقدم ذكره في العشرة، أوصت إليه أم سلمة زوج النبي صلى الله عليه وسلم أن يصلي عليها، ومروان يومئذ أمير المدينة','4166','1302','0');
INSERT INTO [t10490] VALUES (1304,'سعيد بن عامر بن حذيم بن سلامان بن ربيعة بن حرقوس، وقيل: ابن عويج بن سعد بن جمح، أمه أروى بنت أبي معيط بن أبي عمرو بن أمية بن عبد شمس، توفي بالرقة وبها قبره. وقال الهيثم بن عدي: مات بقيسارية، وهو أميرها سنة تسع عشرة، وقيل: بل مات بعد عياض بن غنم','4168','1302','0');
