INSERT INTO [t10490] VALUES (1306,'سعيد بن سعيد بن العاص بن أمية بن عبد شمس، أمه صفية بنت المغيرة بن عبد الله بن عمرو بن مخزوم، استشهد بالطائف','4174','1302','0');
INSERT INTO [t10490] VALUES (1307,'سعيد بن حريث المخزومي وهو ابن عمرو بن عثمان بن عبد الله بن عمر بن مخزوم، أمه عاتكة بنت هشام بن حذيم بن سعد بن ذباب بن سهم، قتل عبد الله بن خطل يوم الفتح، وشاركه في قتله أبو برزة الأسلمي قاله ابن إسحاق، توفي بالكوفة وبها قبر، حديثه عند أخيه عمرو بن','4177','1302','0');
INSERT INTO [t10490] VALUES (1308,'سعيد بن سعد بن عبادة الأنصاري الخزرجي له ولأبيه سعد ولأخيه قيس صحبة، حديثه عند أبي أمامة بن سهل بن حنيف','4180','1302','0');
INSERT INTO [t10490] VALUES (1309,'سعيد بن حيدة القشيري والد كندير، حديثه عند ابنه كندير','4182','1302','0');
INSERT INTO [t10490] VALUES (1310,'سعيد بن حاطب الجمحي أخرجه بعض المتأخرين، وذكر أن البخاري ذكره في الصحابة','4184','1302','0');
INSERT INTO [t10490] VALUES (1311,'سعيد بن يربوع الصرم المخزومي سكن المدينة، كان اسمه الصرم، فسماه النبي صلى الله عليه وسلم سعيدا، وهو سعيد بن يربوع بن عنكثة بن عامر بن مخزوم، يكنى: أبا هود، وأمه: هند بنت سعيد بن رباب بن سهم، توفي وله عشرون ومائة سنة، سنة أربع وخمسين','4186','1302','0');
INSERT INTO [t10490] VALUES (1312,'سعيد بن زيد بن سعد الأشهلي المهدي إلى النبي صلى الله عليه وسلم سيفه، ذكره بعض المتأخرين وهو وهم، وصوابه: سعد، وأخرج له حديث الحجبي، عن إبراهيم بن جعفر بن محمود بن محمد بن مسلمة، وقد تقدم هذا الحديث فيمن اسمه سعد','4189','1302','0');
INSERT INTO [t10490] VALUES (1313,'سعيد بن نوفل روى عن النبي صلى الله عليه وسلم في الاستئذان، رواه علي بن زيد بن جدعان، عن عمار بن أبي عمار، عنه وهو عندي مرسل','4190','1302','0');
INSERT INTO [t10490] VALUES (1314,'سعيد بن يزيد الأزدي من أزد الغرب، يعد في المصريين، حديثه عند أبي الخير اليزني','4191','1302','0');
INSERT INTO [t10490] VALUES (1315,'سعيد بن ربيعة يروي قدوم وفد ثقيف على النبي صلى الله عليه وسلم، روى عنه عيسى بن عبد الله وأراه مرسلا، ذكره بعض المتأخرين','4193','1302','0');
INSERT INTO [t10490] VALUES (1316,'سعيد بن الحارث بن قيس بن عدي بن سعد بن سهم أمه أم عروة بنت سعيد بن حذيم الجمحي، قتل سعيد باليرموك يوم أجنادين في خلافة عمر، لا عقب له','4196','1302','0');
INSERT INTO [t10490] VALUES (1317,'سعيد بن رقيش وهو ابن ثابت بن يعمر بن صبرة بن مرة بن كبير بن غنم بن ذودان بن أسد بن خزيمة، أخو يزيد بن رقيش، هاجر مع أهله إلى المدينة، ذكره بعض المتأخرين فقال: سعيد بن رقيش الأنصاري من بني غنم بن ذودان، ووهم لأن بني غنم بن ذودان هم من بني أسد بن خزيمة','4200','1302','0');
INSERT INTO [t10490] VALUES (1318,'سعيد بن عبيد الثقفي الطائفي رمي يوم الطائف فأصيب أنفه، روى عنه إسماعيل، ذكره بعض المتأخرين','4202','1302','0');
INSERT INTO [t10490] VALUES (1319,'سعيد بن سويد الأنصاري روى عنه ابناه عقبة، وعبد الملك','4204','1302','0');
INSERT INTO [t10490] VALUES (1320,'سعيد بن بجير الجشمي قدم مع أبيه على النبي صلى الله عليه وسلم، يعد في الحمصيين، حديثه عند أولاده','4206','1302','0');
INSERT INTO [t10490] VALUES (1321,'سعيد أبو عبد العزيز يعد في الصحابة، حديثه عند ابنه','4208','1302','0');
INSERT INTO [t10490] VALUES (1322,'سعيد بن عبيد القارئ','4211','1302','0');
INSERT INTO [t10490] VALUES (1323,'سعيد بن أبي راشد روى عنه أبو الزبير، وعبد الرحمن بن سابط','4213','1302','0');
INSERT INTO [t10490] VALUES (1324,'سعيد بن قيس بن صخر الأنصاري بدري','4215','1302','0');
INSERT INTO [t10490] VALUES (1325,'سعيد بن البختري ذكره ابن خزيمة في الصحابة ولا يثبت','4217','1302','0');
INSERT INTO [t10490] VALUES (1326,'سعيد مولى كبيرة بنت سفيان مسح النبي صلى الله عليه وسلم رأسه','4219','1302','0');
INSERT INTO [t10490] VALUES (1327,'*','4221','1238','0');
INSERT INTO [t10490] VALUES (1328,'سهل بن حنيف بن واهب بن العكيم وقيل: حكيم بن ثعلبة بن مجدعة بن الحارث بن عمرو بن خلاس بن عمرو بن عوف بن مالك بن عمر بن الأوس، شهد بدرا والمشاهد، أحسن القتال يوم أحد، وكان حسن الجسم أبيض، فاعتانه عامر بن ربيعة فلبط به، توفي بالعراق بعد صفين سنة سبع،','4221','1327','0');
INSERT INTO [t10490] VALUES (1329,'سهل ابن الحنظلية الأنصاري من بني حارثة بن الحارث من الأوس، والحنظلية اسم أمه، بايع تحت الشجرة، وكان متعبدا متوحدا لا يخالط الناس، سكن دمشق','4231','1327','0');
INSERT INTO [t10490] VALUES (1330,'سهل بن الحنظلية العبشمي روى عنه أبو العالية قال البخاري: هو غير الأنصاري، وقيل: سهيل','4234','1327','0');
INSERT INTO [t10490] VALUES (1331,'سهل بن أبي حثمة الأنصاري أبو يحيى أحد بني حارثة، وقيل: أبو محمد قال الواقدي: اسم أبي حثمة عامر بن ساعدة بن عامر، كان دليل النبي صلى الله عليه وسلم إلى أحد، وشهد معه المشاهد، وقيل: إن اسم أبي حثمة عبد الله بن ساعدة بن عامر بن جشم من الأوس، وأمه أم','4236','1327','0');
INSERT INTO [t10490] VALUES (1332,'سهل بن سعد بن مالك بن خالد بن ثعلبة بن حارثة بن عمرو بن الخزرج بن ساعدة بن كعب بن الخزرج الساعدي، يكنى أبا العباس، وقيل: أبو يحيى، أدرك النبي صلى الله عليه وسلم، وله يوم توفي النبي صلى الله عليه وسلم خمسة عشر سنة، وتوفي سنة ثمان وثمانين، وقيل:','4239','1327','0');
INSERT INTO [t10490] VALUES (1333,'سهل بن عمر النجاري الأنصاري أخو سهيل، صاحبا المربد الذي ابتناه رسول الله صلى الله عليه وسلم مسجده، وكانا في حجر أسعد بن زرارة توفي في عهد النبي صلى الله عليه وسلم وصلى عليه، وهم بعض المتأخرين فقال: إنه أخو سهيل ابن بيضاء فقال: سهيل ابن بيضاء وأخوه','4251','1327','0');
INSERT INTO [t10490] VALUES (1334,'سهل بن صخر الليثي وقيل: سهيل عداده في المدنيين، سكن البصرة، قال محمد بن سعد: هو سهل بن صخر بن واقد بن عصمة بن أبي عوف بن عبد مناة بن شريح بن عامر بن يسير بن بكر بن كنانة','4255','1327','0');
INSERT INTO [t10490] VALUES (1335,'سهل بن قيس المزني من مزينة','4257','1327','0');
INSERT INTO [t10490] VALUES (1336,'سهل أبو إياس الساعدي الأنصاري','4259','1327','0');
INSERT INTO [t10490] VALUES (1337,'سهل بن عتيك بن النعمان بن عمرو بن عتيك بن عمرو بن مبذول الأنصاري. شهد بدرا والعقبة، وهم فيه بعض المتأخرين فصحفه فقال: سهل بن عبيد، وإنما هو عتيك، ورواه بعقبه فيمن اسمه: سهيل عن هذا أحسبه بهذا الإسناد فقال: سهيل بن عتيك','4261','1327','0');
INSERT INTO [t10490] VALUES (1338,'سهل بن مالك أخو كعب بن مالك، سكن المدينة، روى عنه ابنه يوسف','4264','1327','0');
INSERT INTO [t10490] VALUES (1339,'سهل البلوي الأنصاري صاحب الصاعين، الذي لمزه المنافقون، يقال: سهل بن رافع بن أبي عمرو بن عائذ بن ثعلبة بن غنم، شهد أحدا، توفي في خلافة عمر، وقيل: سهيل','4267','1327','0');
INSERT INTO [t10490] VALUES (1340,'سهل بن جارية الأنصاري سكن المدينة. ذكره ابن أبي عاصم في الصحابة، وقيل: سلمة','4269','1327','0');
INSERT INTO [t10490] VALUES (1341,'سهل بن قيس بن أبي كعب الأنصاري شهد بدرا، واستشهد بأحد','4271','1327','0');
INSERT INTO [t10490] VALUES (1342,'سهل بن عدي الأنصاري بدري','4274','1327','0');
INSERT INTO [t10490] VALUES (1343,'سهل بن عامر بن سعد الأنصاري النجاري استشهد يوم بئر معونة','4276','1327','0');
INSERT INTO [t10490] VALUES (1344,'سهل بن عتيك الأنصاري شهد العقبة، توفي على عهد النبي صلى الله عليه وسلم، وصلى عليه، كرره بعض المتأخرين وهو الذي تقدم ذكره وصحفه فقال: سهل بن عبيد','4279','1327','0');
INSERT INTO [t10490] VALUES (1345,'سهل كان اسمه: حزنا، فسماه النبي صلى الله عليه وسلم سهلا أخرجه بعض المتأخرين، فأفرده ترجمة، وأخرج له هذا الحديث، من حديث علي بن بحر، وهو: سهل بن سعد الساعدي','4281','1327','0');
INSERT INTO [t10490] VALUES (1346,'سهيل ابن بيضاء ابن وهب بن ربيعة بن هلال بن وهيب بن ضبة بن الحارث بن فهر وبيضاء أمه، واسمها: دعد بنت أسد بن حجدم بن أمية بن الحارث بن فهر، شهد بدرا توفي بالمدينة في حياة رسول الله صلى الله عليه وسلم، فصلى عليه في المسجد، حديثه عند أنس بن مالك، وعبد','4283','1327','0');
INSERT INTO [t10490] VALUES (1347,'سهيل بن رافع بن أبي عمرو الأنصاري النجاري أخوه سهل صاحب المربد الذي بناه رسول الله صلى الله عليه وسلم مسجده، بدري','4291','1327','0');
INSERT INTO [t10490] VALUES (1348,'سهيل بن عبيد بن النعمان الأنصاري بدري','4293','1327','0');
INSERT INTO [t10490] VALUES (1349,'سهيل بن عمرو بن عبد شمس بن عبد ود بن نصر بن مالك بن حسل بن عامر بن لؤي، يكنى أبا يزيد، فاصل القضية يوم الحديبية مع رسول الله صلى الله عليه وسلم للمشركين، والد أبي جندل بن سهيل، توفي بالشام في طاعون عمواس سنة ثمان عشرة، وهو الذي تفاءل النبي صلى الله','4295','1327','0');
INSERT INTO [t10490] VALUES (1350,'سهيل بن حنظلة وقيل: ابن حنظلية العبشمي وقيل: إنه أخو سهل بن الحنظلية ولا يصح، وقال البخاري: هو الصحيح','4300','1327','0');
INSERT INTO [t10490] VALUES (1351,'سهيل بن سعد أخو سهل بن سعد الساعدي ذكره بعض المتأخرين، وهو وهم','4302','1327','0');
INSERT INTO [t10490] VALUES (1352,'سهيل بن عتيك من بني النجار، وقيل: سهل، وهو المشهور، وقد تقدم ذكره فيمن اسمه سهل','4304','1327','0');
INSERT INTO [t10490] VALUES (1353,'سهيل بن خليفة أبو سوية المنقري نسيب قيس بن عاصم، عداده في المهاجرين، تقدم ذكره فيمن اسمه محمد','4305','1327','0');
INSERT INTO [t10490] VALUES (1354,'سلمان الفارسي أبو عبد الله: انتسب إلى الإسلام فقال: سلمان بن الإسلام، سابق أهل فارس وأصبهان إلى الإسلام، وقيل: كان اسمه قبل الإسلام مابه بن بودخشان بن مورسلان بن بهبوذان بن فيروز بن شهرك، من ولد آب الملك، وكان مجوسيا قاطن النار، أسلم مقدم رسول','4306','1327','0');
INSERT INTO [t10490] VALUES (1355,'سلمان بن عامر الضبي وهو سلمان بن عامر بن أوس بن حجر بن عمرو بن الحارث بن تيم بن ذهل بن مالك بن سعد بن بكر بن ضبة، نزل البصرة وبها مات، وقال مسلم بن الحجاج: لم يكن في الصحابة ضبي غيره حدث عنه محمد وحفصة ابنا سيرين، والرباب امرأة من بني ضبة يقال لها:','4319','1327','0');
INSERT INTO [t10490] VALUES (1356,'سلمان بن صخر البياضي المظاهر من امرأته , وقيل: سلمة بن صخر، وهو الصواب','4325','1327','0');
INSERT INTO [t10490] VALUES (1357,'سلمان بن ربيعة الباهلي أدرك النبي صلى الله عليه وسلم، وليس له صحبة، هو أول من قضى بالكوفة، ثم قضى بالمدائن، قتل ببلفجر، في خلافة عثمان رضي الله عنه، روى عنه أبو وائل','4326','1327','0');
INSERT INTO [t10490] VALUES (1358,'سلمان بن ثمامة بن شراحيل بن الأصهب الجعفي، غزا مع علي، ونزل الرقة، وله بها مسجد , ذكره بعض المتأخرين، وزعم أن له وفادة إلى النبي صلى الله عليه وسلم، ولم يأت على ذلك ببيان','4327','1327','0');
INSERT INTO [t10490] VALUES (1359,'سلمان بن خالد الخزاعي ذكره سليمان بن أحمد في الصحابة','4328','1327','0');
INSERT INTO [t10490] VALUES (1360,'سليمان بن صرد الخزاعي أبو المطرف شهد مع علي المشاهد ونزل رأس العين، وقتل بناحيتها يوم عين الورد خرج مع المسيب بن نجبة يطلب بدم الحسين بن علي، فسار إلى عبيد الله بن زياد، وذلك مستهل ربيع الآخر، من سنة خمس وستين، فلقوا مقدمته، فقتل سليمان في آخر شهر','4330','1327','0');
INSERT INTO [t10490] VALUES (1361,'سليمان بن أكيمة الليثي','4333','1327','0');
INSERT INTO [t10490] VALUES (1362,'سليمان بن هاشم بن عتبة بن ربيعة وضعه النبي صلى الله عليه وسلم في حجره روى عنه إسماعيل بن محمد بن سعد','4335','1327','0');
INSERT INTO [t10490] VALUES (1363,'سليمان بن أبي حثمة الأنصاري ذكره بعض المتأخرين في الصحابة، وزعم أنه لا يصح، روى عنه ابنه محمد','4337','1327','0');
INSERT INTO [t10490] VALUES (1364,'سليمان بن مسهر أخرجه بعض المتأخرين في الصحابة من حديث معتمر، عن فضيل، عن أبي حريز، عن رفاعة بن قتادة، عن سليمان، عن النبي صلى الله عليه وسلم: " من آمن رجلا على دمه " وزعم أنه وهم، وصوابه: عمرو بن حمق، وسليمان بن مسهر فزاري، من تابعي أهل الكوفة','4339','1327','0');
INSERT INTO [t10490] VALUES (1365,'من اسمه سلمة','4340','1238','0');
INSERT INTO [t10490] VALUES (1366,'سلمة بن سلامة بن وقش الأنصاري من أهل العقبة، شهد بدرا، يكنى أبا عوف، وتوفي سنة خمس وأربعين، وقيل: أربع وثلاثين، وهو ابن سبعين سنة، وهو القائل: تدوي يوم بدر لما سأل النبي صلى الله عليه وسلم عما في بطن ناقته، فقال له سلمة: تدوت عليها ما في بطنها','4341','1365','0');
INSERT INTO [t10490] VALUES (1367,'سلمة بن الأكوع وهو سلمة بن عمرو بن الأكوع، واسم الأكوع: سنان بن عبد الله بن خزيمة بن مالك الأسلمي، يكنى: أبا مسلم، وقيل: أبو إياس، وقيل: أبو عامر، استوطن الربذة بعد قتل عثمان، وتوفي سنة أربع وسبعين، وله ثمانون سنة، وقيل: توفي سنة أربع وستين','4349','1365','0');
INSERT INTO [t10490] VALUES (1368,'سلمة بن أبي سلمة الجرمي أبو عمرو بن سلمة. وقيل: هو سلمة بن نفيع أحد من وفد على النبي صلى الله عليه وسلم من جرم','4365','1365','0');
INSERT INTO [t10490] VALUES (1369,'سلمة بن أمية بن أبي عبيدة بن همام بن الحارث بن بكر بن زيد بن مالك بن زيد مناة بن تميم أخو يعلى بن أمية هاجر مع أخيه يعلى، يعد في المكيين','4367','1365','0');
INSERT INTO [t10490] VALUES (1370,'سلمة بن ذكوان يقال له ابن الأدرع، كان من حرس النبي صلى الله عليه وسلم وهو الذي قال له النبي صلى الله عليه وسلم: " أنا مع ابن الأدرع "','4369','1365','0');
INSERT INTO [t10490] VALUES (1371,'سلمة بن المحبق واسم المحبق صخر بن عقبة بن الحارث بن حصن بن الحارث بن عبد العزى بن وائل بن هذيل بن مدركة بن إلياس بن مضر بن نزار، سكن البصرة، ونسبه البخاري، عن روح بن عبد المؤمن، وروى هو وابنه سنان عن النبي صلى الله عليه وسلم، لهما صحبة، حدث عنه:','4371','1365','0');
INSERT INTO [t10490] VALUES (1372,'سلمة بن يزيد الجعفي وقيل: إنه ابن مشجعة بن مجمع بن كعب بن الحارث، وأمه مليكة بنت مالك بن جعفر بن سعد، روى عنه علقمة بن قيس','4374','1365','0');
INSERT INTO [t10490] VALUES (1373,'سلمة بن صخر البياضي وقيل: سليمان الأنصاري، وهو سلمة بن صخر بن سلمان بن الصمة بن حارثة بن الحارث بن زيد مناة بن خبيب بن حارثة، حديثه عند ابن المسيب وأبي سلمة وسليمان بن يسار','4377','1365','0');
INSERT INTO [t10490] VALUES (1374,'سلمة بن قيس الأشجعي سكن الكوفة، حديثه عند هلال بن يساف','4380','1365','0');
INSERT INTO [t10490] VALUES (1375,'سلمة بن نعيم بن مسعود الأشجعي يعد في الكوفيين، حديثه عند سالم بن أبي الجعد','4383','1365','0');
INSERT INTO [t10490] VALUES (1376,'سلمة بن أسلم الأشهلي شهد بدرا، لا يعرف له رواية','4386','1365','0');
INSERT INTO [t10490] VALUES (1377,'سلمة بن يزيد أبو يزيد، عداده في البصريين','4388','1365','0');
INSERT INTO [t10490] VALUES (1378,'سلمة بن مالك السلمي أقطعه النبي صلى الله عليه وسلم أرضه، وكتب له به عهدا','4391','1365','0');
INSERT INTO [t10490] VALUES (1379,'سلمة بن أبي سلمة الهمداني وقيل: الكندي , يعد في الصحابة','4393','1365','0');
INSERT INTO [t10490] VALUES (1380,'سلمة أبو سنان بن سلمة','4395','1365','0');
INSERT INTO [t10490] VALUES (1381,'سلمة بن نفيل التراغمي وقيل: السكوني، من أهل حمص، له صحبة، حديثه عند ضمرة بن حبيب، وجبير بن نفير، ويحيى بن جابر','4397','1365','0');
INSERT INTO [t10490] VALUES (1382,'سلمة بن هشام بن المغيرة المخزومي شهد مؤتة مع جعفر، وقتل بأجنادين في خلافة عمر، دعا له رسول الله صلى الله عليه وسلم بالنجاة في قنوته','4400','1365','0');
INSERT INTO [t10490] VALUES (1383,'سلمة بن عبد الله بن عبد الأسد المخزومي ربيب النبي صلى الله عليه وسلم، زوج أمه أم سلمة رسول الله صلى الله عليه وسلم، هاجر أبواه أبو سلمة، وأم سلمة إلى المدينة وهو صغير','4404','1365','0');
INSERT INTO [t10490] VALUES (1384,'سلمة بن زهير أخو سمير بن زهير قتله رعاة بني غفار حين هاجر إلى الله ورسوله','4407','1365','0');
INSERT INTO [t10490] VALUES (1385,'سلمة بن سعد بن صريم العنزي الوافد على رسول الله صلى الله عليه وسلم','4409','1365','0');
INSERT INTO [t10490] VALUES (1386,'سلمة بن سلام ابن أخي عبد الله بن سلام','4411','1365','0');
INSERT INTO [t10490] VALUES (1387,'سلمة بن جارية وقيل: سهل، وسلمة بن سحيم، وقيل: نفيع الأسدي، وسلمة الخزاعي','4413','1365','0');
INSERT INTO [t10490] VALUES (1388,'*','4416','1238','0');
INSERT INTO [t10490] VALUES (1389,'سلامة بن قيصر وقيل: سلمة عداده في المصريين , ولي بيت المقدس، حدث عنه: أبو الخير: مرثد بن عبد الله اليزني، وأبو الشعثاء: عمرو بن ربيعة الحضرمي','4416','1388','0');
INSERT INTO [t10490] VALUES (1390,'سلامة بن عمير ابن أبي سلامة بن سعد بن الحارث بن عيسى بن هوازن بن أسلم أبو حدرد الأسلمي. قاله محمد بن سعد الواقدي. له صحبة. توفي سنة إحدى وسبعين، وقال صالح بن أحمد، عن أبيه: اسم أبي حدرد: عبد','4418','1388','0');
INSERT INTO [t10490] VALUES (1391,'سلامة أبو عمرو حديثه عند: ابنه عمرو، لا يصح له صحبة','4420','1388','0');
INSERT INTO [t10490] VALUES (1392,'سلامة أبو قبيصة، وهو الهلب حديثه عند ابنه قبيصة، نأتي على ذكره في باب الهاء إن شاء الله تعالى','4422','1388','0');
INSERT INTO [t10490] VALUES (1393,'سلامة بن عمرو من أصحاب النبي صلى الله عليه وسلم، عن النبي صلى الله عليه وسلم أنه قال: " الكلاب رجس إلا كلب صيد " ذكره بعض المتأخرين، وقال: هو وهم','4423','1388','0');
INSERT INTO [t10490] VALUES (1394,'سلام: ابن أخت عبد الله بن سلام فيه وفي أصحابه نزلت: يا أيها الذين آمنوا آمنوا بالله ورسوله، تقدم ذكره مع سلمة ابن أخي عبد الله بن سلام','4426','1388','0');
INSERT INTO [t10490] VALUES (1395,'سالم بن عبيد الأشجعي من أهل الصفة، يعد في الكوفيين، حديثه عند نبيط بن شريط، وهلال بن يساف، وخالد بن عرفجة','4428','1388','0');
INSERT INTO [t10490] VALUES (1396,'سالم مولى أبي حذيفة بن عتبة بن ربيعة وقال بعض المتأخرين: هو سالم بن عبيد بن ربيعة، ووهم لأنه مولى أبي حذيفة بن عتبة بن ربيعة، وقيل: ابن معقل، يكنى أبا عبد الله، تبناه أبو حذيفة وأرضعته سهلة بنت سهيل بن عمرو بعد البلوغ، وكانت رخصة له، شهد بدرا،','4431','1388','0');
INSERT INTO [t10490] VALUES (1397,'سالم بن حرملة بن زهير بن عبد الله بن خنيس العدوي، وفد على النبي صلى الله عليه وسلم','4439','1388','0');
INSERT INTO [t10490] VALUES (1398,'سالم بن أبي سالم الحجام أبو هند وقيل: إن اسم أبي هند: سنان، روى عنه أبو الجحاف','4441','1388','0');
INSERT INTO [t10490] VALUES (1399,'سالم مولى رسول الله صلى الله عليه وسلم','4443','1388','0');
INSERT INTO [t10490] VALUES (1400,'سالم بن سالم أبو شداد الحمصي شهد وفاة النبي صلى الله عليه وسلم ودفنه، روى عنه معاوية بن صالح، ذكره بعض المتأخرين','4445','1388','0');
INSERT INTO [t10490] VALUES (1401,'سالم بن وابصة مجهول، روى عنه الفضيل بن عمرو','4447','1388','0');
INSERT INTO [t10490] VALUES (1402,'سالم بن عمير بن ثابت من بني عمرو بن عوف، كان أحد البكائين الذين نزلت فيهم: ولا على الذين إذا ما أتوك لتحملهم الآية','4449','1388','0');
INSERT INTO [t10490] VALUES (1403,'من اسمه: سليم','4452','1238','0');
INSERT INTO [t10490] VALUES (1404,'سليم بن عمرو بن حديدة الأنصاري بدري، استشهد بأحد','4453','1403','0');
INSERT INTO [t10490] VALUES (1405,'سليم بن الحارث بن ثعلبة السلمي الأنصاري بدري، قتل يوم أحد','4456','1403','0');
INSERT INTO [t10490] VALUES (1406,'سليم بن جابر الهجيمي وقيل جابر بن سليم أبو جري، تقدم في باب الجيم، فيمن اسمه جابر','4458','1403','0');
INSERT INTO [t10490] VALUES (1407,'سليم بن سعيد بن الجشمي له ولأبيه من النبي صلى الله عليه وسلم لقي، سماه النبي صلى الله عليه وسلم: سليما، ذكره بعض المتأخرين','4460','1403','0');
INSERT INTO [t10490] VALUES (1408,'سليم بن أكيمة الليثي مجهول','4462','1403','0');
INSERT INTO [t10490] VALUES (1409,'سليم أبو حريث العذري يعد في المدنيين','4464','1403','0');
INSERT INTO [t10490] VALUES (1410,'*','4466','1238','0');
INSERT INTO [t10490] VALUES (1411,'السائب بن أبي السائب المخزومي العائذي شارك النبي صلى الله عليه وسلم قبل البعثة، واسم أبي السائب: نميلة، وقيل: صيفي بن عائذ بن عبد الله بن عمر بن مخزوم، له ولأبيه عبد الله صحبة، كان مولى مجاهد بن جبر من فوق','4466','1410','0');
INSERT INTO [t10490] VALUES (1412,'السائب بن خباب أبو مسلم صاحب المقصورة مولى فاطمة بنت عتبة بن ربيعة، وقيل: أبو عبد الرحمن , توفي سنة سبع وسبعين، وهو ابن اثنتين وتسعين سنة، روى عنه: محمد بن عمرو بن عطاء','4469','1410','0');
INSERT INTO [t10490] VALUES (1413,'السائب بن خلاد الجهني والد خلاد حديثه عند ابنه خلاد','4473','1410','0');
INSERT INTO [t10490] VALUES (1414,'السائب بن خلاد بن سويد بن ثعلبة بن عمرو بن حارثة بن امرئ القيس أبو سهل، من بني الحارث بن الخزرج، روى عنه: ابنه، توفي سنة إحدى وتسعين فيما قال الواقدي، وقال أبو عبيد القاسم بن سلام: شهد بدرا، وولي اليمن لمعاوية فيما ذكره المنيعي، عن عمر عنه حدثنا','4476','1410','0');
INSERT INTO [t10490] VALUES (1415,'السائب بن العوام بن خويلد أخو الزبير، استشهد باليمامة','4479','1410','0');
INSERT INTO [t10490] VALUES (1416,'السائب بن يزيد ابن أخت نمر وهو ابن سعيد بن عائذ بن الأسود بن عبد الله بن الحارث الكندي، ويقال: الهذلي. يكنى: أبا يزيد. حليف بني عبد شمس، مختلف في وفاته وسنه، فقيل: توفي سنة اثنتين وثمانين، وقيل: ثمان، وقيل: إحدى وتسعين. توفي وهو ابن أربع','4482','1410','0');
INSERT INTO [t10490] VALUES (1417,'السائب بن يزيد مولى عطاء من فوق، ولده بمروة وبالشام من أرض حوران، كذا قاله بعض المتأخرين، وهو عندي السائب بن يزيد ابن أخت نمر المتقدم وأخرج له هذا الحديث','4495','1410','0');
INSERT INTO [t10490] VALUES (1418,'السائب بن أبي وداعة السهمي وهو ابن ضبيرة بن سعيد بن سعد بن سهم القرشي قال ابن أبي خيثمة: أبو وداعة اسمه: الحارث بن ضبيرة، أسره أبو مرثد، فقال النبي صلى الله عليه وسلم: " تمسكوا به، فإن له ابنا كيسا "، فخرج إليه المطلب ففاداه بأربعة آلاف، وهو أول','4497','1410','0');
INSERT INTO [t10490] VALUES (1419,'السائب بن عثمان بن مظعون بن خبيب الجمحي شهد بدرا مع أبيه عثمان','4498','1410','0');
INSERT INTO [t10490] VALUES (1420,'السائب بن الحارث بن قيس بن وعدان السهمي استشهد يوم الطائف','4500','1410','0');
INSERT INTO [t10490] VALUES (1421,'السائب بن أبي لبابة بن عبد المنذر الأنصاري ولد على عهد النبي صلى الله عليه وسلم وأتي به','4502','1410','0');
INSERT INTO [t10490] VALUES (1422,'السائب بن عمير الأزدي له ذكر في حديث العلاء، ذكره بعض المتأخرين','4504','1410','0');
INSERT INTO [t10490] VALUES (1423,'السائب بن سويد عن النبي صلى الله عليه وسلم حديثه عند محمد بن كعب','4506','1410','0');
INSERT INTO [t10490] VALUES (1424,'السائب بن الأقرع بن جابر بن سفيان بن سالم بن مالك بن حطيط بن جشم الثقفي، ابن عم عثمان بن أبي العاص. أدرك النبي صلى الله عليه وسلم ومسح برأسه، ولي أصبهان، وبها مات، وعقبه بها من ولده: مصعب بن الفضيل بن السائب الثقفي روى عنه: أبو إسحاق السبيعي،','4508','1410','0');
INSERT INTO [t10490] VALUES (1425,'السائب بن عبد الرحمن ذهبت به خالته إلى النبي صلى الله عليه وسلم، فدعا له، فبلغ أربعا وتسعين سنة، وكان جلدا. ذكره بعض المتأخرين من حديث محمود بن آدم، عن الفضل بن موسى، عن الجعيد بن عبد الرحمن، عن السائب، وهو وهم من بعض النقلة وهو السائب بن يزيد،','4509','1410','0');
INSERT INTO [t10490] VALUES (1426,'السائب مولى غيلان بن سلمة الثقفي روى عنه: ابنه نافع','4510','1410','0');
INSERT INTO [t10490] VALUES (1427,'السائب بن أبي خنيس الأسدي قال له النبي صلى الله عليه وسلم: " يا ابن أخي خنيس " روى عنه: سليمان بن يسار، روى حديثه: محمد بن عمر الواقدي، ذكره بعض المتأخرين. . . . عليه','4512','1410','0');
INSERT INTO [t10490] VALUES (1428,'السائب الغفاري سماه النبي صلى الله عليه وسلم عبد الله','4513','1410','0');
INSERT INTO [t10490] VALUES (1429,'من اسمه سفيان','4515','1238','0');
INSERT INTO [t10490] VALUES (1430,'سفيان بن أبي زهير الأزدي الشنوي وقيل: سفيان بن نمير من مرادة بن عبد الله بن مالك بن نصر بن الأزد بن غوث بن نبت بن مالك بن يزيد بن كهلان من أزد شنوءة، وقيل: هو النميري، حدث عنه عبد الله بن الزبير، والسائب بن يزيد','4516','1429','0');
INSERT INTO [t10490] VALUES (1431,'سفيان بن عبد الله بن أبي ربيعة بن الحارث الثقفي الطائفي، روى عنه: عبد الله، وعروة ابنا الزبير، وعبد الله بن سفيان ابنه، ونافع بن جبير، استعمله عمر بن الخطاب على العشور والصدقات , سكن المدينة','4519','1429','0');
INSERT INTO [t10490] VALUES (1432,'سفيان بن الحكم الثقفي وقيل: الحكم بن سفيان','4522','1429','0');
INSERT INTO [t10490] VALUES (1433,'سفيان بن قيس أخو وهب وهو ابن أبان الثقفي، روى عنه: أميمة بنت رقيقة','4524','1429','0');
INSERT INTO [t10490] VALUES (1434,'سفيان بن أسد الحضرمي وقيل: ابن أسيد، يعد في الشاميين , روى عنه: جبير بن نفير','4526','1429','0');
INSERT INTO [t10490] VALUES (1435,'سفيان بن وهب الخولاني يكنى: أبا أيمن، وفد على النبي صلى الله عليه وسلم، وشهد معه حجة الوداع، قيل: إنه ممن شهد فتح مصر وأفريقية سنة ثمان وسبعين، توفي سنة اثنتين وثمانين روى عنه: أبو الخير مرثد بن عبد الله، وأبو عشانة حيي بن يؤمن، ومسلم بن يسار،','4528','1429','0');
INSERT INTO [t10490] VALUES (1436,'سفيان بن معمر بن حبيب الجمحي أبو جنادة وجابر، من مهاجري الحبشة، وقال ابن إسحاق: كان سفيان رجلا من الأنصار، أحد بني زريق بن عامر بن جشم بن الخزرج، قدم مكة فأقام بها ولزم معمر بن حبيب، وتزوج بحسنة أم شرحبيل ابن حسنة، فولد فيها جنادة، وجابرا','4530','1429','0');
INSERT INTO [t10490] VALUES (1437,'سفيان بن همام المحاربي من بني محارب بن خصفة بن قيس بن عيلان','4533','1429','0');
INSERT INTO [t10490] VALUES (1438,'سفيان بن عطية بن ربيعة الثقفي طائفي، ذكر قدوم وفدهم على النبي صلى الله عليه وسلم من ثقيف','4535','1429','0');
INSERT INTO [t10490] VALUES (1439,'سفيان بن سهل وقيل: ابن أبي سهل','4537','1429','0');
INSERT INTO [t10490] VALUES (1440,'سفيان بن مجيب قيل: إن له صحبة، روى عنه: حجاج بن عبيد الثمالي في صفة جهنم','4539','1429','0');
INSERT INTO [t10490] VALUES (1441,'سفيان بن زيد وقيل: ابن يزيد الأزدي، من أزد شنوءة، ذكره البخاري في الصحابة، ولا يعرف، قاله البخاري , روى عنه: ابن سيرين في العتيرة','4541','1429','0');
INSERT INTO [t10490] VALUES (1442,'سفيان بن هانئ بن جبر بن عمرو أبو هانئ الجيشاني، عداده في المصريين، روى عنه: الحارث بن يزيد، وواهب بن عبد الله، مختلف في صحبته','4542','1429','0');
INSERT INTO [t10490] VALUES (1443,'سفيان بن صهبانة المهري وهو الخزنق الشاعر، قاله ابن أبي داود','4543','1429','0');
INSERT INTO [t10490] VALUES (1444,'سفيان بن أبي العوجاء أبو ليلى الأنصاري سكن الكوفة، مختلف في اسمه، فقيل: سفيان، وقيل: أوس، وقيل: بلال، وقيل: داود، ونذكره في الكنى','4544','1429','0');
INSERT INTO [t10490] VALUES (1445,'*','4545','1238','0');
INSERT INTO [t10490] VALUES (1446,'سفينة أبو عبد الرحمن مولى رسول الله صلى الله عليه وسلم، سماه النبي صلى الله عليه وسلم، وقيل: إن اسمه عبس , وقيل: رومان، روى عنه: سعيد بن جمهان، ومحمد بن المنكدر، وعمر ابنه','4545','1445','0');
INSERT INTO [t10490] VALUES (1447,'سويد بن النعمان بن مالك بن عامر بن مجدعة بن جشم بن حارثة بن الحارث الأوسي، سكن المدينة، شهد أحدا والمشاهد كلها، حديثه عند بشير بن سيار','4551','1445','0');
INSERT INTO [t10490] VALUES (1448,'سويد بن مقرن بن عائذ بن منجا بن نصر بن كعب المزني أخو النعمان، حديثه عند ابنه معاوية. يكنى: أبا عدي، سكن الكوفة، وكان من البكائين','4553','1445','0');
INSERT INTO [t10490] VALUES (1449,'سويد أبو عقبة الأنصاري حديثه عند ابنه عقبة، سكن المدينة','4560','1445','0');
INSERT INTO [t10490] VALUES (1450,'سويد بن حنظلة سكن البادية','4563','1445','0');
INSERT INTO [t10490] VALUES (1451,'سويد بن قيس أبو مرحب، وقيل: أبو صفوان، سكن الكوفة','4565','1445','0');
INSERT INTO [t10490] VALUES (1452,'سويد بن طارق الجعفي وقيل: طارق بن سويد، ذكره وائل بن حجر في حديثه، سكن الكوفة','4568','1445','0');
INSERT INTO [t10490] VALUES (1453,'سويد بن زيد الجذامي أخو رفاعة، وفد على النبي صلى الله عليه وسلم مع إخوته، ذكره موسى بن سهل الرملي فيمن نزل فلسطين','4571','1445','0');
INSERT INTO [t10490] VALUES (1454,'سويد بن عامر بن زيد بن جارية الأنصاري سكن المدينة، روى عنه مجمع بن يحيى، لا يعرف له صحبة، ذكره بعض المتأخرين','4572','1445','0');
INSERT INTO [t10490] VALUES (1455,'سويد بن عياش الأنصاري أحد من بعثهم النبي صلى الله عليه وسلم في هدم مسجد الضرار','4574','1445','0');
INSERT INTO [t10490] VALUES (1456,'سويد بن هبيرة من ساكني البصرة، حديثه عند إياس بن زهير','4576','1445','0');
INSERT INTO [t10490] VALUES (1457,'سويد أبو عبد الله الآهلي وقيل: الألهاني العكي، وهم فخذ من الأشعريين','4578','1445','0');
INSERT INTO [t10490] VALUES (1458,'سويد بن معاذ بن علقمة الأنصاري مجهول، لا يعرف له صحبة، عقبه بأصفهان من ولده: إبراهيم بن حيان','4580','1445','0');
INSERT INTO [t10490] VALUES (1459,'سويد مولى سلمان الفارسي ذكره البخاري، وقال: كانت له صحبة، ذكره عن ابن قهزاذ','4581','1445','0');
INSERT INTO [t10490] VALUES (1460,'سويد بن جبلة الفزاري لا يصح صحبته، حديثه عند: لقمان بن عامر، وراشد بن سعد','4583','1445','0');
INSERT INTO [t10490] VALUES (1461,'سويد، غير منسوب وقيل: أبو سويد، وهو الصواب، في الصلاة على المتسحرين، رواه يونس بن يحيى أبو نباتة، عن هشام بن سعد فقال: عن سويد، وهو وهم','4586','1445','0');
INSERT INTO [t10490] VALUES (1462,'سويد بن غفلة بن عوسجة بن جعفي بن مذحج الجعفي أبو أمية، من المخضرمين، أدرك النبي صلى الله عليه وسلم وهو يدفن، كان مولده عام الفيل، توفي وهو ابن ثمان وعشرين ومائة سنة، مات في ولاية الحجاج سنة إحدى وثمانين، وكان ذا ضفيرتين، يصفر لحيته','4587','1445','0');
INSERT INTO [t10490] VALUES (1463,'سواد بن غزية الأنصاري شهد بدرا، حليف بني عدي بن النجار، أقاده النبي صلى الله عليه وسلم من نفسه يوم بدر، وأمره على خيبر','4593','1445','0');
INSERT INTO [t10490] VALUES (1464,'سواد بن قارب السدوسي وقيل: الأزدي، سكن البادية: كان أحد كهان الجاهلية، فأسلم، روى عنه: أبو جعفر بن محمد بن علي، وسعيد بن جبير، ومحمد بن كعب القرظي','4595','1445','0');
INSERT INTO [t10490] VALUES (1465,'سواد بن عمرو الأنصاري وقيل: سوادة بن عمرو بن عطية بن خنساء بن مبذول بن عمرو بن غنم، روى عنه ابن سيرين، والحسن قال المنيعي: سكن البصرة','4600','1445','0');
INSERT INTO [t10490] VALUES (1466,'سوادة بن الربيع الجرمي','4604','1445','0');
INSERT INTO [t10490] VALUES (1467,'سواء بن خالد الخزاعي أخو حبة، وقال شباب: هو عامري من بني عمرو بن عامر بن صعصعة بن عامر حديثه عند سلام أبي شرحبيل، والمسيب بن رافع','4609','1445','0');
INSERT INTO [t10490] VALUES (1468,'سواء بن الحارث النجاري ذكره بعض المتأخرين','4611','1445','0');
INSERT INTO [t10490] VALUES (1469,'سمرة بن معير أبو محذورة الجمحي، مؤذن رسول الله صلى الله عليه وسلم وهو سمرة بن معير بن وهب بن دعموص بن سعد بن جمح، وقيل: سمرة بن معير بن لوذان بن سعد بن جمح أبو محذورة، مؤذن النبي صلى الله عليه وسلم، نزل الشام، وقيل: أوس بن معير، كانت له قصة في','4613','1445','0');
INSERT INTO [t10490] VALUES (1470,'سمرة بن جنادة بن جندب بن حجير بن رئاب بن حبيب بن سوادة بن عامر روى عنه ابنه جابر','4618','1445','0');
INSERT INTO [t10490] VALUES (1471,'سمرة بن فاتك الأسدي من أسد بن خزيمة بن مدركة بن إلياس بن مضر، وقيل: سبرة بالباء وهو الأشهر','4623','1445','0');
INSERT INTO [t10490] VALUES (1472,'سمرة بن عمرو العنبري من ولد قرط بن عبد مناف العنبري، أجاز النبي صلى الله عليه وسلم شهادته لزبيب العنبري في إسلام بني العنبر، وقيل: إن سمرة أبى إقامة الشهادة، وشهد به لغيره','4625','1445','0');
INSERT INTO [t10490] VALUES (1473,'سمرة بن ربيعة العدواني','4628','1445','0');
INSERT INTO [t10490] VALUES (1474,'سمرة بن جندب بن هلال بن فزارة الفزاري وهو أبو عبيد، هو من بني شمخ بن فزارة، قدمت به أمه المدينة بعد موت أبيه، فتزوجها رجل من الأنصار، وكان في حجره إلى أن صارع غلاما بحضرة النبي صلى الله عليه وسلم فصرعه، فأجازه في البعث، وغزا مع رسول الله صلى الله','4630','1445','0');
INSERT INTO [t10490] VALUES (1475,'سبرة بن معبد الجهني أبو الربيع، وهو سبرة بن معبد بن عوسجة بن سحارة بن خديج بن ذهل بن زيد بن جهينة بن قضاعة بن مالك من حمير وقيل: سبرة بن معبد بن عوسجة بن حرملة بن سبرة بن خديج بن مالك بن عمرو بن ذهل بن ثعلبة بن رفاعة بن نصر بن سعد، حديثه عند أولاده','4640','1445','0');
INSERT INTO [t10490] VALUES (1476,'سبرة بن فاتك الأسدي أخو خريم، وهو من أسد بن خزيمة بن مدركة بن إلياس بن مضر، روى عنه: جبير بن نفير وبشر بن عبد الله، وقال عبد الله بن يوسف: سبرة بن فاتك وهو الذي قسم دمشق بين المسلمين','4644','1445','0');
INSERT INTO [t10490] VALUES (1477,'سبرة بن الفاكه ويقال: ابن أبي الفاكه , مختلف في حديثه، روى عنه: عمارة بن خزيمة بن ثابت، وسالم بن أبي الجعد','4646','1445','0');
INSERT INTO [t10490] VALUES (1478,'سبرة بن أبي سبرة الجعفي جد خيثمة بن عبد الرحمن، واسم أبي سبرة يزيد بن مالك بن عبد الله بن ذؤيب بن سلمة بن عمرو بن ذهل، أتى النبي صلى الله عليه وسلم فقال له: " ما ولدت؟ " فقال: الحارث، وعبد العزى، وسبرة','4648','1445','0');
INSERT INTO [t10490] VALUES (1479,'سراقة بن مالك بن جعشم المدلجي قال البخاري: هو ابن مالك بن تيم بن عمرو بن مدلج بن مرة بن عبد مناة بن كنانة، يكنى أبا سفيان، قاله الدارمي، عن أبي غسان الكناني، روى عنه: جابر، وابن عباس، وطاوس، ومجاهد، وعطاء، وسعيد بن المسيب، وعروة بن الزبير،','4651','1445','0');
