INSERT INTO [t10490] VALUES (1523,'سيار بن روح، أو روح بن سيار ذكره بعض المتأخرين، وقال: تقدم ذكره، وذكره في باب الراء فقال: روح بن سيار، أو سيار بن روح من حديث بقية، عن مسلم بن زياد قال: أتيت أربعة من أصحاب النبي صلى الله عليه وسلم منهم روح وجعل هاهنا سيار بن أبي سيار','4767','1445','0');
