INSERT INTO [t10490] VALUES (1559,'شرحبيل الجعفي وقيل: ابن عبد الرحمن، أبو عقبة، وقيل: أبو عبد الرحمن حديثه بالبصرة','4850','1552','0');
INSERT INTO [t10490] VALUES (1560,'شرحبيل بن معدي كرب يعرف بعفيف حديثه عن ابن إسحاق، عن يحيى بن أبي الأشعث الكندي الكوفي، عن إسماعيل بن إياس بن عفيف، عن أبيه شرحبيل، قدم مكة تاجرا في أول الدعوة، والاسم المسمى به عفيف، ذكره بعض المتأخرين، وقال: اسمه شرحبيل، ويعرف بعفيف، نذكر','4853','1552','0');
INSERT INTO [t10490] VALUES (1561,'شرحبيل ذو الجوشن الضبابي حديثه عند يونس بن أبي إسحاق، وقد تقدم في حرف الذال','4854','1552','0');
INSERT INTO [t10490] VALUES (1562,'شرحبيل بن السمط بن الأسود بن جبلة الكندي كان على حمص، وصلى عليه حبيب بن مسلمة سنة ثلاث وستين، يكنى أبا يزيد , ذكره بعض المتأخرين أنه صحابي، وأنه مختلف في صحبته، وأخرج له هذا الحديث','4855','1552','0');
INSERT INTO [t10490] VALUES (1563,'شرحبيل بن عبد كلال له ذكر في حديث عمرو بن حزم','4857','1552','0');
INSERT INTO [t10490] VALUES (1564,'شرحبيل غير منسوب ذكره بعض المتأخرين، وزعم أنه له ذكر في الصحابة، وقال: هو مجهول','4859','1552','0');
INSERT INTO [t10490] VALUES (1565,'شرحبيل بن حبيب زوج بنت الشفاء بنت عبد الله، له ذكر في حديث فيما ذكره بعض المتأخرين، وأخرج له هذا الحديث','4861','1552','0');
INSERT INTO [t10490] VALUES (1566,'شرحبيل أبو مصعب','4863','1552','0');
INSERT INTO [t10490] VALUES (1567,'شراحيل بن مرة الهمداني','4865','1552','0');
INSERT INTO [t10490] VALUES (1568,'شراحيل بن المنقر وقيل: المنقري، يعد في الحمصيين','4867','1552','0');
INSERT INTO [t10490] VALUES (1569,'شراحيل الكندي له صحبة فيما ذكره بعض المتأخرين','4869','1552','0');
INSERT INTO [t10490] VALUES (1570,'شراحيل بن زرعة له ذكر في حديث ابن لهيعة','4871','1552','0');
INSERT INTO [t10490] VALUES (1571,'شريك بن طارق بن سفيان الحنظلي وقيل: المحاربي، أحد بني ثعلبة بن ذبيان بن يفيض بن ربث بن غطفان، وقيل: أحد بني ثعلبة بن عوف بن سفيان بن أسيد بن عامر بن ربيعة بن حنظلة، وقيل: هو ابن سعد من بني تميم بن حنظلة، روى عنه: زياد بن علاقة','4872','1552','0');
INSERT INTO [t10490] VALUES (1572,'شريك بن حنبل العبسي','4874','1552','0');
INSERT INTO [t10490] VALUES (1573,'شريك رجل من الصحابة، غير منسوب','4877','1552','0');
INSERT INTO [t10490] VALUES (1574,'شريك بن السحماء ذكره بعض المتأخرين، وزعم أن له ذكرا في حديث، وقيل: إن هذا لم يكن اسمه إنما كانت بينه وبين ابن السحماء شركة','4879','1552','0');
INSERT INTO [t10490] VALUES (1575,'شهاب بن مجنون الجرمي جد عاصم بن كليب، مختلف في اسمه، فقيل: شهاب، وقيل: شبيب، وقيل: شتير ذكر بعض المتأخرين أنه: شهاب بن كليب بن شهاب الجرمي، ولم يأت عليه فيه تبيان عداده في الكوفيين','4882','1552','0');
INSERT INTO [t10490] VALUES (1576,'شهاب رجل من الصحابة، سمع منه: جابر بن عبد الله، نزل مصر','4884','1552','0');
INSERT INTO [t10490] VALUES (1577,'شهاب مولى قريش سكن حمص، روى عنه ابن عائذ','4887','1552','0');
INSERT INTO [t10490] VALUES (1578,'شهاب بن خرفة سماه النبي صلى الله عليه وسلم مسلما','4889','1552','0');
INSERT INTO [t10490] VALUES (1579,'شهاب والد سعد بن هشام غير النبي صلى الله عليه وسلم اسمه، فسماه هشاما','4891','1552','0');
INSERT INTO [t10490] VALUES (1580,'شهاب بن زهير بن مذعور هاجر إلى النبي صلى الله عليه وسلم روى حديثه عمير بن حاجب بن يزيد بن شهاب، عن أبيه، عن جده شهاب قال: هاجرت إلى النبي صلى الله عليه وسلم فذكره، ذكره بعض المتأخرين فاقتصر على هذا','4893','1552','0');
INSERT INTO [t10490] VALUES (1581,'شريح بن أبي شريح حجازي، له صحبة، حديثه عند أبي الزبير، وعمرو بن دينار','4895','1552','0');
INSERT INTO [t10490] VALUES (1582,'شريح بن هانئ بن يزيد الحارثي أبو المقدام أدرك النبي صلى الله عليه وسلم، ودعا له وبه كنى رسول الله صلى الله عليه وسلم أباه هانئا: أبو شريح','4897','1552','0');
INSERT INTO [t10490] VALUES (1583,'شريح الحضرمي له ذكر في حديث السائب بن يزيد، يعد في الحجازيين','4899','1552','0');
INSERT INTO [t10490] VALUES (1584,'شريح بن أبرهة وقيل: شريح اليافعي له صحبة، وبيعة، زعم أبو سعيد بن عبد الأعلى أنه شهد فتح مصر','4901','1552','0');
INSERT INTO [t10490] VALUES (1585,'شريح بن الحارث الكندي قاضي العراق، ولاه عمر القضاء، وله أربعون سنة، أدرك النبي صلى الله عليه وسلم، يكنى: أبا أمية، وهو شريح بن الحارث بن الرائش بن المنتجع بن معاوية بن جهم بن ثور الكندي، توفي سنة ثمان وسبعين، وله مائة وعشرون سنة، وكان قائفا','4904','1552','0');
INSERT INTO [t10490] VALUES (1586,'شيبان أبو يحيى جد أبي هبيرة يحيى بن عباد بن شيبان , يعد في الكوفيين','4908','1552','0');
INSERT INTO [t10490] VALUES (1587,'شعيب بن عمرو غير منسوب','4910','1552','0');
INSERT INTO [t10490] VALUES (1588,'شبيب بن غالب الكندي له صحبة فيما زعم بعض المتأخرين، وأنه سأل النبي صلى الله عليه وسلم عن المسح على الخفين روى يحيى بن يونس، عن علي بن قرين، عن حميد بن محمد الراسبي، عن شبيب بن حبيب بن غالب، عن عمه، شبيب بن غالب بن أسيد، عن أسيد، عن شبيب، بهذا','4912','1552','0');
INSERT INTO [t10490] VALUES (1589,'الشريد بن سويد الثقفي أردفه النبي صلى الله عليه وسلم، ورآه، واستنشده بشعر أمية بن أبي الصلت','4913','1552','0');
INSERT INTO [t10490] VALUES (1590,'شبيب بن نعيم غير منسوب','4917','1552','0');
INSERT INTO [t10490] VALUES (1591,'شريط بن أنس الأشجعي جد سلمة بن نبيط، ولابنه نبيط ولأخيه صحبة، عدادهم في الكوفيين','4919','1552','0');
INSERT INTO [t10490] VALUES (1592,'شجاع بن وهب الأسدي شهد بدرا، هاجر النبي صلى الله عليه وسلم فبعثه رسولا إلى المنذر بن أبي شمر الغساني، وقيل: إلى جبلة بن الأيهم، وقيل: إلى هرقل','4923','1552','0');
INSERT INTO [t10490] VALUES (1593,'شبل بن معبد المزني وقيل: ابن خليد، وقيل: ابن خالد، أخو أبي بكرة لأمه، وهم أربعة إخوة لأم واحدة اسمها سمية: شبل، وأبو بكرة، وزياد، ونافع، وهم الذين شهدوا على المغيرة','4931','1552','0');
INSERT INTO [t10490] VALUES (1594,'شمعون أبو ريحانة الأنصاري كان بمصر والشام، حدث عنه: كريب بن أبرهة، وعبادة بن نسي، وشهر بن حوشب','4934','1552','0');
INSERT INTO [t10490] VALUES (1595,'شماس بن عثمان بن الشريد المخزومي شهد بدرا، واستشهد بأحد، فيه وفي أصحابه حمزة، ومصعب، وعبد الله بن جحش، نزلت ولا تحسبن الذين قتلوا في سبيل الله الآية','4937','1552','0');
INSERT INTO [t10490] VALUES (1596,'شكل بن حميد العبسي عداده في الكوفيين حديثه عند ابنه شتير','4942','1552','0');
INSERT INTO [t10490] VALUES (1597,'شييم أبو عاصم السهمي وقيل: أبو سعيد روى عنه: ابناه عاصم وسعيد، يعد في الحجازيين','4944','1552','0');
INSERT INTO [t10490] VALUES (1598,'شبرمة له صحبة، غير منسوب توفي قبل وفاة النبي صلى الله عليه وسلم','4947','1552','0');
INSERT INTO [t10490] VALUES (1599,'شفي بن مانع أبو عثمان الأصبحي مختلف في صحبته','4949','1552','0');
INSERT INTO [t10490] VALUES (1600,'شطب الممدود أبو طويل','4951','1552','0');
INSERT INTO [t10490] VALUES (1601,'شويفع غير منسوب','4953','1552','0');
INSERT INTO [t10490] VALUES (1602,'شقران مولى رسول الله صلى الله عليه وسلم واسمه صالح','4955','1552','0');
INSERT INTO [t10490] VALUES (1603,'شبث بن سعد البلوي شهد فتح مصر له صحبة فيما قاله بعض المتأخرين، عن أبي سعيد بن يونس بن عبد الأعلى، وحكى أن له ذكرا في كتاب الفتوح','4957','1552','0');
INSERT INTO [t10490] VALUES (1604,'شبيل بن عوف أبو الطفيل، أدرك الجاهلية، ولم يسمع من النبي صلى الله عليه وسلم','4960','1552','0');
INSERT INTO [t10490] VALUES (1605,'شقيق بن سلمة أبو وائل الأسدي أدرك زمان النبي صلى الله عليه وسلم، ولم يره، ولم يسمع منه، توفي سنة تسع وسبعين، وكان لا يخضب','4963','1552','0');
INSERT INTO [t10490] VALUES (1606,'باب الصاد','4967','18','0');
INSERT INTO [t10490] VALUES (1607,'صهيب بن سنان بن مالك بن عبد بن عمرو بن عقيل بن جندلة بن خزيمة، وقيل: خزيمة بن كعب بن سعد بن أسلم بن أوس بن مناة بن النمر بن قاسط بن هنب بن أفصى بن جزيلة بن أسد بن ربيعة بن نزار، نسبه بهذه النسبة الكلبي، سبته الروم من الموصل صغيرا، كناه رسول الله صلى','4968','1606','0');
INSERT INTO [t10490] VALUES (1608,'صهيب بن النعمان غير منسوب','4975','1606','0');
INSERT INTO [t10490] VALUES (1609,'صفوان بن أمية بن خلف بن وهب بن حذافة بن جمح واسمه: تيم بن عمرو بن هصيص بن كعب بن لؤي أبو وهب الجمحي كناه النبي صلى الله عليه وسلم: أبا وهب، أسلم بعد الفتح، وشهد حنينا وهو مشرك، ثم أسلم بعد ذلك توفي مقتل عثمان بن عفان، استعار منه النبي صلى الله عليه','4977','1606','0');
INSERT INTO [t10490] VALUES (1610,'صفوان بن المعطل السلمي قال محمد بن عمرو: هو صفوان بن المعطل بن رحضة بن المؤمل بن خزاعي بن هلال بن ذكوان بن ثعلبة بن بهثة بن سليم، يكنى: أبا عمرو الذكواني، مات بشمشاط، وهو ابن بضع وستين، قال فيه النبي صلى الله عليه وسلم: " ما علمت منه إلا خيرا "','4981','1606','0');
INSERT INTO [t10490] VALUES (1611,'صفوان بن عسال المرادي من بني الربض بن زاهر بن مراد وكان عداده في بني حمل غزا مع رسول الله صلى الله عليه وسلم اثنتي عشرة غزوة سكن الكوفة، وحدث عنه: عبد الله بن مسعود، وأبو الغريف، وزر بن حبيش، وعبد الله بن سلمة المرادي، وأبو سلمة بن عبد الرحمن،','4986','1606','0');
INSERT INTO [t10490] VALUES (1612,'صفوان بن مخرمة الزهري حديثه عند ابنه القاسم','4990','1606','0');
INSERT INTO [t10490] VALUES (1613,'صفوان بن قدامة المرائي هاجر إلى النبي صلى الله عليه وسلم وبايعه','4992','1606','0');
INSERT INTO [t10490] VALUES (1614,'صفوان ابن بيضاء بن وهب ابن ربيعة بن هلال بن وهيب بن ضبة بن الحارث بن فهر وبيضاء أمه، وهو أخو سهيل، شهد بدرا بعثه النبي صلى الله عليه وسلم في سرية عبد الله بن جحش قبل الأبواء، توفي في طاعون عمواس','4997','1606','0');
INSERT INTO [t10490] VALUES (1615,'صفوان بن عمرو من المهاجرين، قدم المدينة في رهط عبد الله بن جحش الأسدي مهاجرا','5002','1606','0');
INSERT INTO [t10490] VALUES (1616,'صفوان بن محمد وقيل: محمد بن صفوان','5004','1606','0');
INSERT INTO [t10490] VALUES (1617,'صفوان أو ابن صفوان وقيل: أبو صفوان مختلف فيه','5006','1606','0');
INSERT INTO [t10490] VALUES (1618,'صفوان بن عبد الله الخزاعي ذكره بعض المتأخرين فقال: يقال: إن له صحبة حديثه موقوف','5008','1606','0');
INSERT INTO [t10490] VALUES (1619,'صالح بن النحام كان اسمه نعيما فسماه النبي صلى الله عليه وسلم صالحا','5010','1606','0');
INSERT INTO [t10490] VALUES (1620,'صالح مولى النبي صلى الله عليه وسلم يعرف بشقران','5012','1606','0');
INSERT INTO [t10490] VALUES (1621,'صالح الأنصاري السالمي له ذكر في حديث أبي سعيد الخدري','5014','1606','0');
INSERT INTO [t10490] VALUES (1622,'صالح رجل من الصحابة أتى النبي صلى الله عليه وسلم بأخيه','5016','1606','0');
INSERT INTO [t10490] VALUES (1623,'صالح بن المتوكل أبو كثير والد يحيى بن أبي كثير مولى مازن بن الغضوبة، قتل مع مازن بباب برذعة وقبرا به في أيام عثمان بن عفان رضي الله عنه، ذكره بعض المتأخرين وقال','5018','1606','0');
INSERT INTO [t10490] VALUES (1624,'صخر بن حرب بن أمية بن عبد شمس بن عبد مناف أبو سفيان سيد البطحاء، وأبو الأمراء عاش ثمان وثمانين سنة، وقيل: ثلاثا وتسعين، مولده قبل الفيل بعشر سنين، وإسلامه عام الفتح ليلة الفتح، شهد حنينا والطائف مع رسول الله صلى الله عليه وسلم، وأصيبت عيناه،','5020','1606','0');
INSERT INTO [t10490] VALUES (1625,'صخر بن وداعة الغامدي وهو ابن عمرو بن عبد الله بن كعب بن عبد الله بن مالك بن نصر بن الأزد يعد في الحجازيين , حديثه عند عمارة بن حدير','5027','1606','0');
INSERT INTO [t10490] VALUES (1626,'صخر بن العيلة بن عبد الله بن ربيعة بن عمرو بن علي بن أسلم بن أحمس الأحمسي البجلي له صحبة، عداده في الكوفيين، روى عنه عثمان بن أبي حازم','5030','1606','0');
INSERT INTO [t10490] VALUES (1627,'صخر بن سلمان أحد البكائين نزلت فيه وفي أصحابه: تولوا وأعينهم تفيض من الدمع','5032','1606','0');
INSERT INTO [t10490] VALUES (1628,'صخر بن قدامة العقيلي مختلف في صحبته حديثه عند الحسن','5034','1606','0');
INSERT INTO [t10490] VALUES (1629,'صخر الأحمسي يكنى: أبا حازم والد قيس بن أبي حازم، ذكره سليمان بن أحمد في المعجم فيمن اسمه صخر، مختلف في اسمه، وقيل: إن اسمه عوف بن الحارث، وهو ابن الصمة','5036','1606','0');
INSERT INTO [t10490] VALUES (1630,'صخر القعقاعي الباهلي','5038','1606','0');
INSERT INTO [t10490] VALUES (1631,'الأحنف بن قيس مخضرم واسمه صخر بن قيس بن معاوية بن حصين بن عبادة بن نزال بن مرة بن عبيد بن الحارث بن عمرو بن سعد بن زيد بن مناة بن تميم بن مر، تقدم ذكره في حرف الألف','5040','1606','0');
INSERT INTO [t10490] VALUES (1632,'صخر بن لوذان عداده في الحجازيين، بعثه النبي صلى الله عليه وسلم مع عماله إلى اليمن، فيما زعم بعض المتأخرين، ووهم فيه، لأن الذي بعثه عليه الصلاة والسلام إلى اليمن: عبيد بن صخر ابنه، لا صخر وهذا حديث يتفرد به سهل بن يوسف بن سهل الأنصاري عن أبيه عن','5042','1606','0');
INSERT INTO [t10490] VALUES (1633,'صخر بن صعصعة الزبيدي صاحب النبي صلى الله عليه وسلم أبو صعصعة وهو الذي أمره النبي صلى الله عليه وسلم أن ينادي في الناس: " لا يصحبنا مضعف، ولا مصعب " في مخرجه إلى تبوك','5044','1606','0');
INSERT INTO [t10490] VALUES (1634,'الصعب بن جثامة الليثي سكن الحجاز، وكان ينزل في ودان، ثم انتقل إلى المدينة، وهو الصعب بن جثامة بن قيس بن عبد الله بن وهب بن يعمر بن عوف بن كعب بن عامر بن ليث أمه: زينب بنت حرب بن أمية بن عبد شمس، أخت أبي سفيان، وحالف جثامة قريشا','5046','1606','0');
INSERT INTO [t10490] VALUES (1635,'صحار بن عباس وقيل: ابن عائش، وقيل: صحار بن صخر العبدي من عبد قيس روى عنه: ابناه: عبد الرحمن، وجعفر، ومنصور بن أبي منصور، ومصعب بن المثنى','5048','1606','0');
INSERT INTO [t10490] VALUES (1636,'صالح بن الأعسر الأحمسي من بجيلة سكن الكوفة، وقيل: الصنابحي، حديثه عند قيس بن أبي حازم','5050','1606','0');
INSERT INTO [t10490] VALUES (1637,'صنابح وقيل: إنه غير الأحمسي، وهو عندي المتقدم','5052','1606','0');
INSERT INTO [t10490] VALUES (1638,'الصلت أبو زبيد روى عنه ابنه زبيد بن الصلت، حجازي، مختلف في صحبته','5054','1606','0');
INSERT INTO [t10490] VALUES (1639,'الصلت أبو كليب أتى النبي صلى الله عليه وسلم، وأمره أن يحلق عنه شعر الكفر، وهو وهم','5056','1606','0');
INSERT INTO [t10490] VALUES (1640,'صرمة العذري','5058','1606','0');
INSERT INTO [t10490] VALUES (1641,'صرمة بن أنس، وقيل: ابن قيس الخطمي الأنصاري كان شاعرا يكنى: أبا قيس نزلت فيه وكلوا واشربوا حتى يتبين لكم الخيط الأبيض من الخيط الأسود','5060','1606','0');
INSERT INTO [t10490] VALUES (1642,'صرمة بن أبي أنس بن مالك بن عدي بن عامر بن غانم بن عدي بن النجار، أبو قيس أفرده بعض المتأخرين عن المتقدم، وعندي هو المتقدم','5062','1606','0');
INSERT INTO [t10490] VALUES (1643,'صيفي بن قيظي أخو الحباب بن قيظي، قتلا جميعا يوم أحد من الأنصار','5064','1606','0');
INSERT INTO [t10490] VALUES (1644,'صيفي بن سواد بن عبادة الأنصاري من بني سواد بن غنم بن ثعلبة، شهد بدرا فيما ذكره بعض المتأخرين، عن عروة بن الزبير','5066','1606','0');
